INSERT INTO `ci_ming` VALUES ('e647d10f0069df8a00d7f6dc5eace97c','点绛唇·天台','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"千树桃绯，万年药就灵仙侣。芙蓉脂肉。霞袂风前举。\",\"何物凡夫，归向人夸诩。重来处。乱山无数。怅落红如许。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a053ca7178d0dbe93','生查子·除夜守岁','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"淹抑闷青春，拖逗情红泪。铸错作心兵，难把愁城碎。\",\"枕衾余花月，对惯做惺然。醉曾问杜康，无此酒，何名类。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a053ca7179101453f','生查子·除夜守岁','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"待拔旧愁根，忽种新愁换。晓夜两秋成，四大仓都满。\",\"减颜红，摧鬓短。并不因寒暖。曾问越人无，此病何名款。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a053ca717b8f2f0a1','生查子·除夜守岁','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"家在赤城隈，卿住桃源渡。向晚碧云生，迷却天台路。\",\"青鸟不归来，夜夜双星度。花月梦同孤，此恨无人诉。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a053ca717f78e7fb3','生查子·除夜守岁','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"柏酒与辛盘，共作峥嵘景。为惜岁华流，烛跋炉香冷。\",\"红散绮窗梅，翠泻瓷瓯茗。一夜两年人，短鬓双蓬影。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a06a5376988fd874e','渔家傲·本调','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"白板船迎秋浪急。绿蓑衣惹山云湿。清浅滩头容易即。谁似得。长年稳住风波国。\",\"芦叶萧疏枫叶赤。玄真归棹前溪黑。一首佳词吟未毕。无人识。从教误却君王觅。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a07babbaf2f206eec','洞仙歌·小园水陆二洞成，口号志喜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"腹裁口减，措大为山费。次第双峰俨相对。更多情、倩得巨掌仙灵，来一擘，便做金庭地肺。\",\"共惊奇绝处，在水中央，小艇前穿杳无际。漫夸他五泄，具体而微。\",\"应不爽、想见游鱼乐意。但胜侣、心知肯频来，且莫笑愚公，付之一醉。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a08424c7ce509cf10','风中柳春夜听雨，各拈一阕。长卿《雨中花》，幼如《雨霖铃》，不害《少年游》，予得《风中柳》','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"瞥眼春光，不见玉人携手。尽平芜、桃花渡口。酽红浓粉，尽教他欺瘦。这回来、可能还有。\",\"冷落空斋，自是长夜相守。更荧荧、灯残梦剖。霖钤未断，客心愁添否。知多少、杜陵新酒。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a12e5815ab8e7907e','夜飞鹤此调无换头清明','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"岁华到佳丽，寒暖轻纤。桃杏里扬晴帘。和烟暖雾巧妆点，香车骄马相兼。红楼上凝望处，见飞英铺径，舞絮垂檐。电光隙影，料东君、不与人添。\",\"追想去年寒食，携酒拾遗钿，香染脂黏。今日风风雨雨，春城御陌，情意空恹。汉宫蜡炬，甚余辉、能到穷阎。但梨花深夜，清尊好友，险韵频拈。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1317a94f94a46cd3','柳枝张舍人泌有此体游仙','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"曾伴湘灵碾翠軿。下瑶京。贪看碧杜与红蘅。会群媖。\",\"夜香云软霞衣薄。五铢轻。三十六陂空水明。晚峰青。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a13c4e23e59036615','如梦令·离别','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"草绿南原如绣。风软月华云骤。时得晚林香，昨夜缥梅春透。\",\"微嗽。微嗽。惊起避人松鼬。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a13c4e23e677df069','如梦令·离别','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"火样情儿难驻。冰样恓惶难拒。忍死割将来，步步不如归去。\",\"难处。难处。央个梦儿做主。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a13c4e23ef12c24d8','如梦令·离别','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"刚道行期还未。挨到临头无计。手掌大于船，偏少风波做美。\",\"几许。几许。又早隔重江水。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a140754554c919401','长相思·秋夜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"是情儿。总难支。便做铜仙去汉时。也须流泪辞。\",\"竺乾师。大悲慈。语语教人学勿思。如之何勿思。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1407545562f23a48','长相思·秋夜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"那风魔。援池河。只是窝蜂与靥螺。笑伊知甚么。\",\"三棒鼓。一声锣。贼比官人争几多。池河还在么。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a14075455b9a95c30','长相思·秋夜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"枕初惊。酒初醒。窗外寒风断续声。残灯与梦清。\",\"月华凝。露华明。疑有仙娥下玉京。乘鸾捧素笙。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a14306768e19a7c21','绮罗香·史邦卿末句少二字，今更定元夜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"天上融怡，人间和洽，共结王正佳丽。此夜三元，处处风柔月媚。斗奇巧、琢玉雕脂，逞华艳、簇珠攒翠。望云端、绣幕红楼，绮罗香拥神仙队。\",\"寻思屈指人世。几遇清平代，良宵佳会。更有闲愁，来日阴晴难拟。且随分、银烛觥筹，莫厌频、尽堂簪佩。待填词、谱入梨园，一宵传遍清都里。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a15b3355de445e4d5','鹊桥仙·七夕雨','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"珠蕊初翻，银河乍满，风浪其间惯历。须信来宵忆此宵，总迢递、应强寥寂。\",\"无赖封姨，多情乌鹊，毕竟佳期还觅。别离岁岁一般情，又何用、常年悲戚。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a169fb528189be45c','木兰花即前调忆旧游','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"长安冠盔多朋侣。兄弟相看忘尔汝。绛纱携伴紫宸朝，绿酒论文清夜午。\",\"天涯散后徂寒暑。春梦断来无续处。何缘小草到南曹，始觉前时人近古。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a169fb528f5c9d618','木兰花即前调忆旧游','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"野花如靥香车路。娇莺学语行云度。芳时可奈落红多，幽怨肯随流水去。\",\"长短亭标离恨谱。东西陌占销魂处。寸心窝里寸眉尖，容得闲愁千斛贮。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a18dc01e073c9b0da','凤凰台上忆吹箫·月','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"璧影欹悬，冰轮初就，凄清乍人帘钩。想桂宫帝子，甚处空浮。望断青天碧海，今夜里、多在琼楼。微飔动，露华凝玉，雾縠生秋。悠悠。\",\"几多恨也，千百种心窝，一霎眉头。向素娥遥诉，私忖还休。应道关卿何事。偏相望、泪逐莲筹。枉教您，孤眠到今，不解人愁。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1a115bef7c9950b2','瑞鹤仙·天问','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"噫天公忒煞。无好恶、颠倒随时赍发。谁能免庞杂。算从前天理，人情王法。凭伊抑捺。似将来、一笔都抹。\",\"有颜回短命，孔子阨穷，盗蹠华发。道得往因簿注，果报缘悭，影形相答。尤难省达。张公罪，李公罚。许多时隔了，前生夙世，若个于中证察。使先生、佩剑冲星，每教自拔。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1a189fad13b43a6f','醉落魄音托本调','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"青骢紫陌。花丛里戟门深宅。障泥绣锦珊瑚策。问柳章台，折取旧春色。\",\"笙歌日暮能留客。山公沉醉接䍦侧。院西渐隐姮娥魄。拌取明朝，短梦小窗白。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1c95365d247e0d92','水调歌头·为友人赠别','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"简点王正历，大吉丑到从魁。已过开岁旬满，香漏尚残灰。元旦椒花献颂，人日金花剪胜，都作庆新杯。为滞东皇驾，还待土牛催。\",\"稽典礼，戒农事，谨三推。老夫无用，只管问柳与寻梅。梅向阶前破萼，柳在陌头舒眼，如见故人回。定把春婪取，轻肯放伊归。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1c95365d71f14c76','水调歌头·为友人赠别','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"有意凭谁省，把酒对东风。不知泪痕多少，昨夜梦魂中。记取闲亭别馆，谢却舞裙歌扇，璧影挂疏桐。灯暗衣潜解，香冷被初笼。\",\"晚妆却，残粉褪，醉眸慵。两情各诉，相偎常恨不相逢。及见何曾几许，依旧华胥梦断，岐路苦匆匆。马上频回首，此恨有千重。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a1cef1f251f81320f','天仙子·吴本如中丞宅颧剧','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"不惜明珠三斛价。长在深闺，罗在华堂下。尊前撮起殚人娇，莺语乍。花光砑。一幅崔徽金粉画。\",\"歌舞留袖禅悦暇。供顿清饶，雒浦神仙亚。惊鸿过眼彩霞飞，香散罢。寻思那。惟有白云清梦挂。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a23b804bf11f9eaec','齐天乐·万寿节','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"荣光亿道长春节，流虹又逢绕电。万国梯航，八方蹈舞，干羽两阶呈遍。云祥舜旦。更日丽尧年，风清禹甸。紫玉班中，五明宫里仙人现。\",\"遥遥西望长安。把南山乐只，一杯期献。再展芹私，萦怀曝爱，惟有升平堪愿。边尘净扫。尽世界熙熙，太平重见。漫道齐天，后天勤未倦。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a26f089199d47204a','凤凰阁玉兰','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"问花神、何事亭亭素玉。忍抛冷艳在空谷。笑领东风无路，着意拘束。困杀您、霜寒露宿。\",\"年年无计，早把春光占足。此情和恨半含蹙。谁似我，去空回，一样幽独。好记取、待惊郎目。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d5165dd890','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"廉明太守来姊归。千家万家香午坎。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d51d9e5be2','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"黄陵寺里夜闻钟。鸟呜猿啼都为侬。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d51f32035d','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"莫歌月节折杨柳。前溪变声悦郎口。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d5265a6fec','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"巴童爱唱巴渝曲。巫神夜归巫庙宿。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d53883b958','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"蛮烟苦雾最难消。江边蜀客滞兰桡。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d53cf1b6d9','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"千回万转是瞿塘。非关学字学侬肠。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d556513899','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"巴江迎神打鼓鼙。山花红映女巫衣。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d56a8ffe05','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"谁人江畔踏歌词。鹧鸪西飞雄逐雌。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d56c7e1421','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"家家濯锦向江边。传来新样写金笺。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d59653d5f9','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"白盐山下白板扉。锦江城畔锦鸡飞。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d59f94df8c','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"霜高滟滪月空舲。联翩百丈上泷行。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2ab3b8d5f9d1e08c','竹枝孙光宪、皇甫松俱有此体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"不行朝云夜闻钟。子规西来泣相语。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca5324119ae5ce','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"片片残霞紫锦披。蒙蒙星气白榆微。欲弦新月似浓眉。\",\"独上层台余怅望，十年心事恨多违。旄头何日陨天威。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca53246d58852e','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"燕子矶边潮夜生。凤凰台畔柳春晴。双鬟低按小秦筝。\",\"重来南国梅初落，半入凉州月正明。行人何事又关情。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca53247082e0a0','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"风蒲猎猎弄轻柔。欲立蜻蜓不自繇。藕花无数满汀洲。\",\"五月临平山下路，蚕成衣褛麦成秋。人家多住浅溪头。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca532488763377','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"二十余年别帝京。水流花谢两无情。昔时亲友半凋零。\",\"案有黄庭尊有酒，醉闻花气睡闻莺。焚香冼钵过余生。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca53248e807ccb','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"簧暖笙寒调不成。绣鸳鸯底锦衾明。一番花信百愁生。\",\"小立房栊无半语，步移栏干待多情。今宵沉醉也还醒。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca5324c17f8d0a','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"花里寻师到杏坛。养真高静出尘寰。机心还逐白鸥闲。\",\"迸水定浸香案湿，洞门深锁碧窗寒。东来紫气满函关。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca5324d01a5bbc','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"抱得秦筝不忍弹。诗名往日动长安。此中道路古来难。\",\"莫道蓟门书信少，人情反复似波澜。阴符在箧老羞看。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca5324e94dd007','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"欲卷珠帘春恨长。挑花历乱李花香。为谁消瘦减容光。\",\"独见彩云飞不尽，更教明月照流黄。海天愁思正茫茫。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2dca5324fff5f49a','浣溪沙·听筝','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"浸月楼台带月钟。晚风依约到帘栊。踏歌人去书屏空。\",\"宝篆印销金粉她，镜台尘满玉花踪。含思含怨寂寥中。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2f57680517bc47b9','临江仙·元旦','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"卖花声过园林好，唤人沉醉东风。亭前柳映小桃红。称人心意，娇面玉芙蓉。\",\"月云高处迎仙客，绮罗香艳重重。小蓬莱下喜相逢。画堂春满，宝鼎瑞云浓。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a2f576805ed034a14','临江仙·元旦','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"昨夜今朝争几许，共惊年去年来。青阳晨度赤城开。晴光摇草树，佳气入楼台。\",\"想得羲和都不异，人间强自安排。条风初试软金杯。休夸一岁首，明日又相催。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a3246759d408969fa','望江南同周长卿、焦不害咏所见','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"流盼处，相对两峰尖。扇底不防明月上，轻衫微动晚风纤。\",\"聊复为郎淹。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a3246759df0c9db13','望江南同周长卿、焦不害咏所见','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"寻去路，小径竹林边。却似武陵踪自杳，还如雒浦梦相牵。\",\"空来拾翠钿。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a3a274c0c0648d5e7','解语花·咏焚香美人，和长卿','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"日烘帘压，花衬檐牙，别是添情界。沁人无赖。随分例、了却闲中些债。水沉初买。更拂拭、商彝周鼐。唤小鬟、分火熏笼，捻取兰煤待。\",\"乍觉郁纷杳蔼。渐盈盈随处，沾惹襟带。留伊常在。将红袖翠衵，缓笼轻载。昭仪浴罢，谁近得、暗香柔态。较海棠、纵似娇姿，输一般难赛。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a40a13ecf64b482fa','满庭芳·爰园春思','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"春入沁园，天公做美，树老花嫩都芳。酿寒催暖，晴霭泛崇光。淑气融人似酒，微风送、不断林香。池亭畔，时逢艳冶，新样渲梳妆。\",\"六朝金粉地，门临朱桁，路转横塘。喜酒空赊易，诗就吟狂。莫问后庭遗恨，但惜取、烟柳斜阳。烧银烛，夜深花睡，残月下回廊。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a439845bc54641935','三台·咏南都','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"看山围故国如昨，女墙月来还去。吴楚分、地作镇龙盘，江海汇、天成虎踞。前朝事、燕子能相语。总不过、偏安割据。逞浮艳、子夜铜鞮，恣淫乐、后庭琼树。迤千年唐联五代，宋朝南渡尤误。画江守、苟活到钱塘，舍神皋、翻资胡虏。\",\"任潮打、空城晨暮。天留靳、绛节薇垣，待时到、都归真主。见储胥紫气簇拥，未央瑞云浓护。万斯年、重译亘梯航，千八百、共球欢舞。秦淮绕、御苑通仙澍。望翠烟、钟阜凝贮。玉衣举、铁马常趋，祀光辉、夜旋龙驭。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a45280d482d8ba7ac','东风齐着力戊辰除夜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"圣主初元，星回佳候，物意嶙峋。沉香火底，爆竹彻明闻。正值调和玉烛，遍寰宇、尽人阳春。尽今夜，加添甲子，漫守庚申。\",\"笑语逡巡。又早是、一宵两岁平分。赏心乐事，都施少年人。只愿安康履顺，团圞聚、贤子贤孙。还凭仗，东风着力，吹入寒门。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a481eb96592601d84','丑奴儿占赠','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"奴儿天与丰姿占，琢玉团酥。无可形摹。转把奴儿作丑呼。\",\"神仙队里曾偎揣，目眩魂徂。记得模糊。待写云英当丑奴。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a485207c15845180d','念奴娇·早春','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"蕊桃丝柳，带残梅数朵，暗呈春思。雪意霜情犹未断，已觉寒回暖至。绣帐佳人，锦囊才子，简点春衣笥。东皋南陌，酒帘摇飐花肆。\",\"追想前日邀宾，重帘深幕，到今都勿事。水色山光人面映，好景一年须记。待得花秾，上林如锦，春老将归次。流光驹隙，人生行乐何俟。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a494d30deba188d2f','潇湘神·刘宾客体。以上二调俱相似，实不同，宜细辨之','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"湘水神。湘水神。\",\"黄陵歌舞逐时新。日暮江干春草绿，含情含怨复含颦。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a494d30def3c3eb27','潇湘神·刘宾客体。以上二调俱相似，实不同，宜细辨之','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"湘女祠。湘女祠。\",\"靓俨明妆有所思。春尽落花啼鸟候，泪痕齐发去年枝。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a4af498f0c9fd135e','千秋岁引·咏杨花','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"做势漫天，凭空布陌。点画青林好颜色。填平石家金埒下。纷飞柳氏章台侧。都不管，灵和殿，永丰宅。\",\"一望霸陵情脉脉。多少利名途路客。见此茫茫意萧索。妆成慢夸晴雪舞，锦衾篱溷谁能测。雨兼风，泥共土，红和白。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a4fe0168121fc157f','虞美人·夜坛','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"三十年前荣乐事。梦境浑相似。只今留得梦醒人。犹有青灯白发伴残春。\",\"渐无亲戚来情话。间亦微吟罢。天公容我老闲身。更乞三杯软饱梦和神。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a4fe016818378071f','虞美人·夜坛','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"悠悠碧海青天远。目极愁山浅。画眉人去恨恹恹。挽惹嫩丝垂柳舞前檐。\",\"凝销渐烬芳兰麝。掩泪亭皋下。月随云淡晚窗晴。奈可懒妆浓鬓黛眉轻。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a4fe01681a17672b3','虞美人·夜坛','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"醮星坛上雯华湿。露泫光如滴。步虚声里月方中。看取蔚蓝浮翠满晴空。\",\"黄庭内景经初了。宝鼎沉烟小。清微虔叩玉宸君。正是天心子半夜初分。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a4fe01681cdb13310','虞美人·夜坛','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"妖冶风情天与措。梦断知何处。芙蓉如面柳如眉。正是倚栏无语惜芳菲。\",\"燕子衔将春色去。门掩黄昏雨。相思无路莫相思。不似天涯地角有穷时。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a52a246bc66466ccd','醉蓬莱·魏国上公徐六翁招饮园池，同王季重','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"羡蓬莱仙伯，紫府真人，自无烟火。游戏尘凡，驾明霞千朵。小筑瀛山，巧穿环海，向洞天深锁。宝树青葱，金波澹落，琪花婀娜。\",\"玉笋班中，元臣独座，门閤开东，属车回左。此际邀宾，列绣屏雕舸。舞缀行云，歌翻子夜，谈笑珠玑颗。奇俊王郎，挥毫浮白，愧颓唐我。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a57659484c9cbc873','长相思','俞彦','e647d10f0069df8ad1457b72d1457b72','明','这首词笔法简洁细腻，以女子的口吻，写主人公与情人分别后的相思之情，情极深挚，非至情者莫能道出。写法上，运用复杂而微妙的感情交织，在对花的爱与恨以及对于相思的怕而又不得不相思的矛盾交织中，体现其对爱情的忠贞和对幸福的向往。该词化用了范仲淹《御街行》的“都来此事，眉间心上，无计相回避”及李清照《一剪梅》的“才下眉头，却上心头”。\n上片从“花”字生发，鲜花象征着美好的事物，代表着纯真的爱情，并常常被用来比喻美丽的女子。“折花枝”尽管只是写了女主人公的一个动作，但也自然会使人联想到青春少女美丽的面庞及折花枝时的优美姿态，联想起“人面桃花相映红”（唐崔护《题都城南庄》）的艺术境界。“恨花枝”三字接得突兀，词人着一“恨”字，准确地刻划了折花人心灵深处的悲苦，这首词即以盈盈春色反衬人的失意。“准拟花开入共巵，开时人去时”，令人仿佛看到折花人流下相思泪，含情凝睇着伊入离去的方向。女主人公本来打算同恋人在花开时节共同饮酒赏花，可是花开之日却是人去之时，离愁既生，迁恨于花枝也是必然的。其实“花枝”并没有什么过错，“恨花枝”，说到底就是恨自己的恋人，本来说好的事情，为何能轻易违约匆匆离去。不过，这“恨”中也包含着爱的成份。\n下片紧承上片意脉，娓娓道来，生动地写出自己的刻骨相思。这“相思”二字所包容的巨大能量，曾使古今中外的多少有情男女愁苦不已，损心伤神。回想昔日花好月圆共饮时，湖边柳下细和语，卿卿我我蜜意时，女子惧怕孤独、不甘寂寞，“怕相思，已相思”将女主人公怨恨、思念、爱怜的感情，细腻、逼真地表达出来，直抒胸臆。但“怕”又有何用，谁能摆脱这“剪不断，理还乱”的忧愁。但当心里有了“相思”的想法时，就已经在相思了。“怕”和“恨”连起来，正是白居易所描绘的“思悠悠，恨悠悠，恨到归时方始休”的同义语。对一位感情丰富，情窦已开的女子来说，“恨”是“爱”的折光·，“怕”是“恋”的延伸，相思之苦，难遣胸怀。“轮到”，表明主人公过去还未曾品尝过相思之苦，正因为是初尝，才更能体会到相思是那样的折磨人，纠缠人，让人没法回避，无法推辞。心中所思，形诸面容，“眉间”自然会“露一丝”，这“一丝”凝聚着主人公对恋人的无限深情与怀念，其中有恨，有爱，有失望，有希望，有痛苦，有幸福，个中滋味难以表达。以“丝”谐“思”，采用民歌抒情惯用手法，清新含蓄，娇态可怜。\n该词上片写实记事，下片抒情展怀。上下片虚实结合，相映相衬，情味浓郁，完美地塑造了一位多情女的形象。上下片打头的两个三字句，有很别致的形式：第二，三两字相互重复，句首第一字相互对照。例如白居易的“汴水流，泗水流——思悠悠，恨悠悠”，林逋的“吴山青，越山青——君泪盈，妾泪盈”，俞彦这首词颇具匠心地借助了这种形式上的奇突。“折花枝，恨花枝”和“怕相思，已相思”，前者是心理与行为，后者是心理与情势的比照和冲突，两者都不着痕迹地投射出词的主旋律——情绪上的双重体验（眷恋与痛苦），由此超越了语言结构的外壳，成为构成作品内在完整的有机因素。','[\"折花枝，恨花枝，准拟花开人共巵，开时人去时。\",\"怕相思，已相思，轮到相思没处辞，眉间露一丝。\"]',NULL,'折下美丽的花枝，不觉又怨恨起花枝，原来打算花开时我们一起赏花共饮，谁知花开后情人一去不返不见踪影。\n害怕相思折磨自己，相思之情却早愁人，到相思时却事无办法摆脱它，心中稍稍平静眉头又露几分。','这首小令从花枝写到人间的相思。上片写折花枝、恨花枝，因为花开之日，恰是人去之时，已见婉折；下片谓怕相思却已相思，且其情难言，唯露眉间，愈见缠绵。全词清新淡雅，流转自然，富有民歌风味，表达了女主人公因情人远去而犯的离愁别绪以及对爱情的忠贞和对幸福的向往。','[\"准拟：打算，约定。\",\"人共巵（zhī）：指饮酒定婚。巵，古代盛酒器。\",\"辞：躲避。\",\"丝：丝与\\u201C思\\u201D谐音，以双关语既形眉态，又表心绪。\"]'),('e647d10f0069df8a5a3e76d2ef5c203e','何满子宫词','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"草色侵帘入户，月光似水连天。才唱一声何满子，顿抛双泪君前。自是宸游无暇，非关薄命婵娟。\",\"二六栏凭辇路，十三徽隐哀弦。那有闲情买词赋，赋成谁奏甘泉。但祝冈陵岁岁，不妨箧扇年年。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a5ae497a9e089b7a3','西河·竹','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"山窗下归来，雅意相待。杏桃飞尽，恁清阴、始知不改。\",\"倩谁传语报平安，夜深时有天籁。嶰谷种，淇澳派。\",\"仙源宁比萧艾。众芳摇落，翠沉沉、此君独在。化龙栖凤总无凭，最怜月影风态。\",\"成竹意，虽未解。潇湘一幅堪爱。子瞻与可，漫相夸、别传教外。\",\"不如千亩贮胸中，清贫馋守何害。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a65fc4dca5564aeef','水龙吟·闻笛','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"谁家玉笛楼中，遏云声断龙吟水。风清万籁，雾净三更，月明千里。散入雒城，曲中折柳，动人乡思。甚天涯孤客，故园摇落，襟袖有、盈盈泪。\",\"堪叹流商刻徵。断腹声、旧腔谁系。金谷吹残，武陵调苦，萼楼人醉。便欲乘风，钧天学谱，跨鲸云际。待他年，仿佛君山父老，世间游戏。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a674752653e7c58d6','卜算子·贺何若浮举曾孙','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"壮岁子生孙，逾艾孙生子。正合官家极品封，四代都如此。\",\"君到伏生年，又几云仍耳。六世金绯共一堂，妆点清朝史。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a6b0a8b85577a9dd7','内家娇初夏','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"朝来红雨过，房栊静、斜日燕初归。正啼困流莺，病疏浮蚁，昼长人去，璠上双眉。悄心绪，一弯蟾晕小，数叠枕屏欹。山溜渐平，桐阴乍满，稚荷争吐，骄絮还垂。\",\"画图春风里。肠断处、种种密约佳期。堪更臂留韦玉，镜惹潘丝。怕旧事江南，桃根重省，新愁塞北，雁字难飞。无计返魂怀梦，应做情痴。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a6b61cf6436d68d5a','一剪梅·姚太仆世所重九日枉驾小园，偶谈弇州先生小词，即依体口占一首见赠，因效颦寸首奉答','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"与君先后赋还山。我爱江山。君爱湖山。非关褊性合栖山。叵奈冰山。转忆云山。\",\"投琼佳句到青山。仰止高山。弦绝牙山。而今宿草北邙山。梦破鱼山。泪洒牛山。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a6b61cf64e3f983f9','一剪梅·姚太仆世所重九日枉驾小园，偶谈弇州先生小词，即依体口占一首见赠，因效颦寸首奉答','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"为人性僻僻于山。梦里游山。病里思山。莫教一日不看山。淡冶春山。刻露秋山。\",\"吴兴自古擅名山。水有菁山。陆有杼山。劝君莫更恋家山。为尔耽山。故效他山。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a6b61cf64f87cd6a5','一剪梅·姚太仆世所重九日枉驾小园，偶谈弇州先生小词，即依体口占一首见赠，因效颦寸首奉答','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"传闻海上有仙山。道是蓬山。难到蓬山。未如小圃一拳山。水也环山。树也环山。\",\"君来访我到家山。携手登山。节届登山。西风吹落小方山。便是龙山。何必龙山。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a6cebbaeac1644617','春光好·闺思','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"花眼媚，柳腰伸。正芳春。懊恨不曾缘底事，意中人。\",\"无绪独依孤枕。有时愁伴重茵。风起絮飞天欲暮，坐含颦。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a70b217e63a558df6','南乡子·秋闺','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"湖上好花株。花好花残总似奴。夫似沉沉湖底月，虚无。一在萧关一在吴。\",\"愁月月同孤。说与愁花花向隅。愁月愁花愁欲死，糊涂。是谁题作莫愁湖。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a70b217e6547f67d0','南乡子·秋闺','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"风雨下樊楼。玉臂寒生不任秋。除却影儿刚剩我，含羞。总在眉峰半寸头。\",\"断脸复凝眸。挨过黄昏泪未收。待不思量除是梦，还休。梦醒繇来一样愁。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a736577473cbbe6cb','踏莎行·繇黄牛峡至净江，途次忆欧阳公小词，戏效颦二阕，时已量移南枢','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"人鲊瓮甜，鬼门关隘。虎头狼尾滩形怪。夷陵尽处是黄陵，净江又在黄陵外。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a736577477a3687e3','踏莎行·繇黄牛峡至净江，途次忆欧阳公小词，戏效颦二阕，时已量移南枢','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"一水盈盈，亲朋久待。那知尚滞黄陵界。楚天尽处是夷陵，黄陵又在夷陵外。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a77f9fedee1ea9552','江城梅花引·即《江城子》，末多一句，似《行香子》、绝非《梅花引》，不知犯何调，姑俟博雅咏梅','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"古梅芳榭悄含英。冷香凝。满空庭。我共花魂，相对惜孤清。更值众芳摇落后，捻吟髭，霜月下，最关情。\",\"罗浮梦断晓参横。句初成。自丁宁。从此一杯，歌底莫教停。化作玉鳞君便见，小桥边，篱落外，巳星星。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a78bdd4e30db94682','忆王孙·本题','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"王孙踪迹惯天涯。草绿春归不忆家。晚风和月到窗纱。\",\"惜年华。飞絮游丝共落花。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a78bdd4e3f0543e1a','忆王孙·本题','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"晓来春睡未惺惚。报道郎归便整容。窗外那堪薜荔风。\",\"恨添浓。此后何须入梦中。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a798e55c03009dd64','减字木兰花·苦雨','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"春光无主。被底悄寒生夜雨。燕子飞来。不道梅花尚未开。\",\"春江已半。恁把岁华都送断。倩语东皇。此后风尘渐可伤。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a850198702a9e222f','渔父四时词','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"十里平堤古柳中。朝朝暮暮有阴浓。\",\"牵约艇，趁西东。随处荷花面面风。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a850198704f8724b1','渔父四时词','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"暖入冰池云母澌。东风如炙雨如脂。\",\"风笠影，雨蓑丝。妆点玄真一首诗。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a850198708a20fbe8','渔父四时词','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"浪外轻风飐雪花。冻禽两两立寒沙。\",\"云黯澹，树槎牙。小港枯蓬系我家。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a85019870cbca20bf','渔父四时词','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"露白苹香月欲西。人家三五渐闻鸡。\",\"鸣短棹，过前溪。网得鲈鱼蟹正肥。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a8728df795afe20e8','塞翁吟·书所见','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"热局并巍膴，朝屮辈辈炎炎。偶占取，一身兼。猛做出崇严。自称亦是天骄子，安用退步撝谦。君莫恃，浮图好就，遗看封尖。\",\"闲拈。书史事，塞翁旧话，荣辱幻、教人恨添。屈指算、全辽破后，到于今、多少尊官，次第都歼。争如野老，寂寞玄亭，卧起厌厌。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a8bbed9d1749bf005','捣练子','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"庭静峭，月昏黄。捣练声高夜正长。\",\"万绪千丝提不尽。随风和恨过西墙。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a8bbed9d1c735576d','捣练子','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"风峭紧，月模糊。捣练声高忙复徐。欲寄芦河书一纸，边霜莫待染关榆。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a8c36249e51691ef8','满江红·东阿道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"荏苒征途，马首外、黄埃飞灭。渐初夏、麦寒西陇，草熏南陌。平楚澹烟迷云鹜，乱山落照闻啼鴂。只玉骢、飘泊未归来，心慵怯。\",\"名与利，多生劫。劳和恨，他生业。怕镜中不似、去家时节。残月一鞭乡梦断，暮云千里音书歇。想故园、莫道不曾愁，愁今绝。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a907a539a02d0826c','小重山·别思','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"片帆烟雨下孤城。数株新录软、茜红明。春江何似玉人情。风乍起，远渚石桥平。\",\"且自说归程。小楼深夜月、炙银笙。蓟门幽梦恐难凭。虽有泪，寄不到金陵。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a98f49f31f98b6d16','惜余春慢老伎','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"是月常亏，逢花偏悴，奈可无情乌兔。成阴绿叶，子满枝头，谁遣朅来迟暮。闻道寻春有期，丝柳才黄，乳桃含素。怕多时、前日秋娘庭户，蝶迷蜂误。\",\"思往事、天样风情，萍踪沤聚，曾把蛾眉招妒。琵琶写怨，若个青衫，纵有泪痕难赋。闲抚合欢绣衾，挦损凤鸳，消残鸳鹜。古和今、豪富红颜长在，水应西去。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b901a8010b','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"正是恼人时候。春昼。花好奈风颠。\",\"幽襟凄断总堪怜。天摩天。天摩天。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b91ce698a1','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"一夜晓风吹断。红乱。点香泥。\",\"春来何事关情最。花穗。似愁眉。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b9213df60a','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"闻道红梅开遍。郎见。花只到郎家。\",\"妾家那得似郎花。嗟摩嗟。嗟摩嗟。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b952344818','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"兀地心头似火。愁坐。此后不思郎。\",\"思郎空自断人肠。伤摩伤。伤摩伤。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b99a75bbe5','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"减尽当年玉腕。肠断。约莫两心痴。\",\"此情无计与郎知。思摩思。思摩思。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b9aecf301f','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"春色天涯孤馆。悽婉。才到小屏山。\",\"双成不解便相看。还么还。还么还。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b9d9dfcfda','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"碧玉镂成荷叶。周折。赛金杯。\",\"酒鳞风动红光滟。香茜。莫思归。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8a9f2cf6b9ea4ad0d3','荷叶杯·温助教廷筠体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"风急又惊愁雨。无主。倩若报佳期。\",\"便教今日也应迟。知摩知。知摩知。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aa43b480f4eadeac4','赤枣子·欧阳舍人体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"排蕙户，启兰扃。香残雀尾缕烟轻。更泛软金偎软玉，始知人世锦前程。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aa43b480ffcc9c4d0','赤枣子·欧阳舍人体','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"排锦烛，响银笙。桂堂东畔敞云屏。月落星寒嘶骑散，绣帏莲脸待相迎。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aa594cedf4127dc6a','六幺令·冬夜','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"丽谯初永，良夜寂无哗。帘栊逗来寒信，绛蜡有微葩。应是流澌渐合，霜意满蒹葭。此际堪嗟。空庭月冷，瘦着南枝一树花。\",\"不甘萧瑟，强搀和、一半清佳。无奈被余香灺，梦不到天涯。暗想当年此夜，沉醉在他家。几许韶华。都随流水，且听城头咽暮笳。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d115bfbf8','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"深秋叶落疑为雨。何人不道征人苦。林薄又斜阳。寒风与路长。\",\"一声南去雁。泪下浑如霰。天外秣陵城。迢迢魂梦惊。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d20be9da0','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"晴花夜灿楼台锦。月娥流艳笼香寝。相对脸芙蓉。春纤捧玉钟。\",\"传觞残酒共。此酒尤珍重。沾得口脂余。琼浆更不知。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d34a6ff97','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"酒前欢把柔荑手。手荑柔把欢前酒。欢不饮杯完。完杯饮不欢。\",\"落梅吹断角。角断吹梅落。嘶骑夜光微。微光夜骑嘶。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d5aebde3f','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"泪行千叠难书寄。寄书难叠千行泪。无信到关榆。榆关到信无。\",\"小山屏篆袅。袅篆屏山小。寒露月华残。残华月露寒。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d711c8456','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"敛霞红散行云浅。浅云行散红霞敛。中可月庭空。空庭月可中。\",\"砌虫阴语细。细语阴虫砌。灯烬欲残更。更残欲烬灯。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d8647fdae','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"幕帘钩雨花阴薄。薄阴花雨钩帘幕。离别动年期。期年动别离。\",\"远山眉翠软。软翠眉山远。人待不归春。春归不待人。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d9738d439','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"伴人孤月黄昏半。半昏黄月孤人伴。波眼泪时多。多时泪眼波。\",\"怨深萦梦浅。浅梦萦深怨。君似不销魂。魂销不似君。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693d9f072b2e','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"朵云沉户横金锁。锁金横户沉云朵。茫渺信成双。双成信渺茫。\",\"玉如颜素束。束素颜如玉。曾未梦离卿。卿离梦未曾。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693dc40bcc24','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"月晴云散人离别。别离人散云晴月。齐若杜鹃啼。啼鹃杜若齐。\",\"送愁将破梦。梦破将愁送。难会后来单。单来后会难。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693dc79f586d','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"碧烟香袅孤灯夕。夕灯孤袅香烟碧。长夜奈衾凉。凉衾奈夜长。\",\"月明窥牖缺。缺牖窥明月。愁别忆还休。休还忆别愁。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693dd0c2cbb7','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"雪晴飞暗朦朦月。月朦朦暗飞晴雪。妆点额凝香。香凝额点桩。\",\"玉鳞飘地忽。忽地飘鳞玉。疏影晚山孤。孤山晚影疏。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aafbb693dff0aed03','菩萨蛮·临淮道中','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"美人如月花窗绮。绮窗花月如人美。风暖度香丛。丛香度暖风。\",\"未通曾展意。意展曾通未。怜尔得来笺。笺来得尔怜。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ab7307c949c9fcdd3','归田乐本调','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"学秉犁锄，解下腰章，顿抛手板。只悔回头晚。肉眼共盲眼。\",\"馋眼。妒眼。合与君山一时铲。仕途原自坦。谁似尔、忙钻热赶。\",\"老臣无状，只不贪而懒。雪盏更雨盏。月盏。花盏。美满生涯世间罕。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8abb57f3b0de276d7a','玲珑四犯送廿子明还豫章','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"歌罢尊空，奈陌上骊驹，又自催早。握手河梁，极目水天绵邈。孤客荏冉征途，泪染断肠芳草。笑年年、季子裘敝，惟有舌存未老。\",\"暮云春树江东道。任王孙、去来谙饱。中原漫道稀麟凤，故把刘宝恼。寻想翠幄夜阑，醉梦里、狂情杳眇。更甚时，并辔油壁，殢人娇好。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8abbcc596db4b5c755','金人捧露盘·万历庚子，寓极乐寺。丁巳重过，已废坏，感赋','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"二十年，居停处，梵王宫。惯听残暮鼓晨钟。参差台殿，绣幡莲座簇金容。山僧每话，前朝事、中贵豪雄。\",\"笑刘郎，重到此，悲紫陌，忆桃红。向兔葵燕麦春风。惟虚不坏，但教成住尽归空。尚有高梁桥下水，依旧溶溶。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ac5d36ee8909c3c30','谒金门·人日喜晴','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"东风晓。斗帐薄衾寒悄。马日鸡辰都过了。恰晴人意好。\",\"檐外弄春么鸟。数点落梅风小。胜里金花看惯巧。个中催得老。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ac9e3a3cc940e8bef','满路花·晚春','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"残英蝶梦寒。晓被莺呼暖。山光林影换、东风缓。飞红逐水，寂寞无人管。绣帘慵未轴，却被杨花巧穿。特恁柔款。\",\"情悰依约，蓦地添凄惋。从前多少事、心头馆。江淹有恨，欲赋绫笺短。拟向东君问，此际年年。也应清泪痕满。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ad93bed10653878fc','沁园春·寿李伯英太君','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"寿母康哉，婺星晖朗，高傍三台。见西王法驾，双成旌旆，缤纷旖旎，烂熳昭回。仙李蟠根，灵萱擢秀，管领春风着意栽。天都上，正霞生海峤，云满蓬莱。\",\"养堂此日崔嵬。更谁羡、怀清旧筑台。便玉杖扶鸠，金书衔凤，绿尊泛蚁，华发凝鲐。何物斑斓，阶前绣彩，鼎食纶褒次第开。三千岁，待桃花结子，许我重来。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8adc7dbafcd373b5a5','江南春和倪元镇','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"梦破金鸡辞玉笋。门外雀罗看欲静。高楼初旭野销烟，远树依微过人影。\",\"柳颦桃笑怯春冷。梧阴未覆墙东井。肩车便软幂罗巾。\",\"陌头飞丝香惹尘。兔何忙，乌太急。小院回廊月痕湿。\",\"黄昏夜阑次将及。银笙不温钿花碧。我有所思隔都邑。\",\"临风斯须为谁立。蓬山只似海上萍。刘郎遗恨空征营。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8add31ee3595d206de','调笑无题','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"调笑。调笑。树上子规频叫。声声只劝侬归。\",\"归去须凭酒杯。杯酒。杯酒。时事不堪回首。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8add31ee35e9bef8d5','调笑无题','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"芳草。芳草。偷促年光易老。无端陌上萋萋。\",\"惯占西东路岐。岐路。岐路。送却王孙无数。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8addcdd5b7a7b3c877','麦秀两岐','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"抚字多惭德。更苦催科棘。皮骨存，髓空吸。太守有何力。雨岐双穗知无益。感今惭昔。\",\"父老怜愚直。有道投佳什。远相传，图画识。异日山阳笛。吾将从此辞羁的。雪鸿留迹。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8adf2248130efe148a','蝶恋花·柳絮','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"芳树年年春色熳。飞絮粘空，总被东风唤。金茧玉蛾寒食半。永丰坊里天涯畔。\",\"香灺银篝兰烬散。雁柱生尘，侧力鹍弦断。一缕情丝千种乱。并州快剪裁难判。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ae05067dc8d4bc6e3','行香子·和朱少宗伯兰堣','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"富贵轰雷。西市寒灰。后来人、恁不思惟。常扃眉锁，惯织心机。有九牛毛，双虿尾，五羊皮。\",\"因闲细数，多少凄其。恤宗周、嫠妇空悲。昨朝有客，笑我愚痴。道一无能，三不可，七皆迷。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ae05067dcc9f89a43','行香子·和朱少宗伯兰堣','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"石火荣华。有甚争差。到收场、谁擅行家。波波鹿鹿，要博人夸。似鼠拖肠，蛙鼓腹，蚁排牙。\",\"前因若有，造物偿他。更何须、墨水些些。几多才子，终老烟霞。任蜣抱丸，乌吓腐，蟹爬沙。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ae0678c8c1d77e20b','忆长安十首','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"艮安忆，其八忆长班。慨京省官为纳户，四衙门倚作冰山。\",\"口口唤南蛮。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ae0678c8cf8449426','忆长安十首','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"长安忆，最忆是灰尘。地有寸肤皆着粪，天无三日不焚轮。\",\"并作十分春。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ae2a5f4eb5e01fbf6','千秋岁','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"才过重九，此会胡为者。兰麝暖，琼浆写。亲朋齐致祝，奏伎都妖冶。千秋岁，先生始把尘寰舍。\",\"听祝惭颜赭。自分疏狂野。云路拙，投闲且。马牛安足齿，短鬌繁霜惹。宾劝我，一杯聊闰登高也。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8ae6bb2a2bf476ae0d','江城子·同李五岳、周长卿、焦不害、茂潜饮亭亭馆','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"秦淮杨柳澹垂烟。雨余天。弄晴妍。行到画楼，十二锦屏联。人面桃花相映好，金粉地，艳阳年。\",\"曲拦幽径隐亭轩。肆芳筵。倚琼仙。最是殚人，心绪总堪怜。含恨含情低絮语，香冷后，月华前。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aeb55ef46ca7fda15','风入松·唤春','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"唤春春总不回眸。只索休休。燕成翁媪莺成叟。更堪雨惨风愁。一夜香魂无处，原来都在根头。\",\"匆匆因甚逝波流。容易拌丢。艳阳如绣天如酒，恁般下得忙收。赖有情人慰我，绿肥也亚红稠。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aedb94781cbe0acdf','贺圣朝·留春','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"东皇听我多珍重。莫便思飞轻。得得难来，频频要去，匆匆如梦。\",\"梨花欲吐香魂动。待一杯相共。可能张主，免教佳节，雨摧风送。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8aef9ac91a89290d00','玉女摇仙佩佳人','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"凭谁般尔，倩若钩绳，琢就飞琼苕玉。月下当歌，风前凝盻，亦是每常妆束。别具才情处，但芳魂染惹，害人深酷。两相觑、幽襟密意，都是清矑、盻里含蓄。沾些子脂香。堪做人间，千祥百福。\",\"那许雒阳胜苑，太华神州，占断夭红柔绿。背面秋千，相思才诉，引起眉儿全蹙。疑道湘皋女。暂留得踪迹，尘寰芬郁。叹自古、逢权别怨，不禁回首、织乌忙续。从今愿，年年畅好斟醽醁。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8af1a281cf1250005f','江城梅花引·偶成','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"当年陶令赋归兮。悔前非。只嫌迟。能进不如能退勿须疑。君看泰昌天启后，卿与相，争旦晚，便云泥。\",\"未曾无食与无衣。是山溪。总堪栖。若到咸阳市里悔何追。上蔡东门谁禁汝，父和子，黄犬骏，兔儿肥。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8af8a6756d1e36d69f','忆秦娥·梅','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"春寒熟。溪梅已绽枝头玉。枝头玉。照人明媚，殢人芬馥。\",\"东皇为我怜幽独。小楼细雨眠初足。眠初足。一番花信，一番裀褥。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8af8a6756de4fd2305','忆秦娥·梅','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"烧灯毕。凄风楚雨人愁寂。人愁寂。三分春事，二分抛掷。\",\"西园载酒知何日。南楼遣闷闲凭立。闲凭立。乱山无数，暮云遮讫。\"]',NULL,NULL,NULL,NULL),('e647d10f0069df8af9371e65b69f2849','御街行·秋日向张宾王、周长卿、何充符、衷君含、幼醇恭谒旧内','俞彦','e647d10f0069df8ad1457b72d1457b72','明',NULL,'[\"清都福地层城起。集壁翠、凝金绮。凤楼高入五云中，一道片泉接陛。千秋鱼钥，万年虬户，玉殿浑如洗。\",\"路迷兴庆西宫里。回辇路、钩陈迤。铜龙掩射日光寒，疑向承明谒帝。萱荚呈祥，三山献秀，常拥真人气。\"]',NULL,NULL,NULL,NULL),('e647d10f006b93af5be25bb99b2c0979','卜算子·忆昔','秦公庸','e647d10f006b93afd1457b72d1457b72','明',NULL,'[\"忆昔约佳期，花月为盟主。共对花前月下时，幽恨从头数。\",\"拜月月无言，问花花不语。月转阑干花卸枝，双泪多于雨。\"]',NULL,NULL,NULL,NULL),('e647d10f0133b2ed9b02ba6db09ada70','菩萨蛮','焦竑','e647d10f0133b2ed1373b284806beafe','明',NULL,'[\"梅花坐对天如水，月明千里人千里。莫上最高楼，长江生别愁。\",\"百年今夕会，且共樽前醉。明日又离歌，天涯奈尔何。\"]',NULL,NULL,NULL,NULL),('e647d10f0133b2edcc0de6048cf945db','江神子','焦竑','e647d10f0133b2ed1373b284806beafe','明',NULL,'[\"西风吹袂弄新晴，月笼明，小梅横。席上孤琴、又作断肠声。人去青山衰草里，凭谁共，绕花行。\",\"强拈彩笔赋离情，忆平生，到深更。欲写年来、意思苦难成。但把深杯留客醉，知何日，笑相迎。\"]',NULL,NULL,NULL,NULL),('e647d10f01364e5825f7075ad31c9562','山花子·楼瞰','戴澳','e647d10f01364e58d1457b72d1457b72','明',NULL,'[\"楼瞰寒塘古木横。幽禽时对小窗鸣。柳外何人停短棹，理渔罾。\",\"旧雨不来门书掩，疏篱新剪竹枝平。茶鼎白沙泉正熟，听松声。\"]',NULL,NULL,NULL,NULL),('e647d10f019b8c7bf32f182e08aed504','浣溪沙','于儒颖','e647d10f019b8c7bd1457b72d1457b72','明',NULL,'[\"一片心情眼底柔，倦容疏态越风流。未经惆怅不知愁。\",\"鸳谱怪来针线减，工夫强半为梳头。日西初见下妆楼。\"]',NULL,NULL,NULL,NULL),('e647d10f01d465c185f05a6a31e86182','次韵周叔维竹枝歌','郑潜','e647d10f01d465c1d1457b72d1457b72','明',NULL,'[\"东淮时味颇清嘉，莲实累累小胜瓜。雪藕松甜丝万缕，令人吃著便思家。\"]',NULL,NULL,NULL,NULL),('e647d10f01d465c185f05a6a64d197f4','次韵周叔维竹枝歌','郑潜','e647d10f01d465c1d1457b72d1457b72','明',NULL,'[\"月河守闸问船开，闸上新痕长绿苔。怪底篙人相唤急，西江又有使臣来。\"]',NULL,NULL,NULL,NULL),('e647d10f032eb0ba154effbd5367bbac','浪淘沙','陈继儒','e647d10f032eb0ba299210013c1e4bd6','明',NULL,'[\"风雨霎时晴，荷叶青青，双鬟捧著小红灯。报道绿纱窗底下，蕉月分明。\",\"枕簟嫩凉生，茉莉香清，兰花新吐百余茎。扑得流萤飞去也，团扇多情。\"]',NULL,NULL,NULL,NULL),('e647d10f032eb0baa90eaa775dc69501','摊破浣溪沙','陈继儒','e647d10f032eb0ba299210013c1e4bd6','明',NULL,'[\"蜂欲分衙燕补巢，阴阴落叶遍江皋。一阵窗前风雨到，打芭蕉。\",\"惊起幽人初睡午，茶烟缭绕出花梢。有个客来琴在背，度红桥。\"]',NULL,NULL,NULL,NULL),('e647d10f032eb0baac8fd05cf24066aa','添字浣溪沙·初夏夜饮归','陈继儒','e647d10f032eb0ba299210013c1e4bd6','明',NULL,'[\"桐树花香月半明，棹歌归去蟪蛄鸣。曲曲柳湾茅屋矮，挂鱼罾。\",\"笑指吾庐何处是？一池荷叶小桥横。灯火纸窗修竹里，读书声。\"]',NULL,NULL,NULL,NULL),('e647d10f0449c2608d41f38785132c75','西江月','费宏','e647d10f0449c260cec6f62c748ba69d','明',NULL,'[\"舟中夜行，独坐无酒，抚卷作霜月高悬碧汉，画船自泛寒江。银镫独对夜何长，窗外浮光荡漾。\",\"可怪曲生疏阔，闲来冷落琼觞。思量无计助清狂，且与青编相向。\"]',NULL,NULL,NULL,NULL),('e647d10f0451c3383892ecd770651bed','玉楼春·春怀回文','汪廷讷','e647d10f0451c338d1457b72d1457b72','明',NULL,'[\"画图开处飞莺燕，新涨春湖垂柳线。架书淹日尽穷搜，下帏孤坐棋弹倦。\",\"挂檐晴翠山当面，如如悟却忘欣厌。泻玉寒泉绕石台，夜中归鹤栖松院。\"]',NULL,NULL,NULL,NULL),('e647d10f04df0caeee754c589365b24d','采莲曲','张楷','e647d10f04df0caed973132107042ac7','明',NULL,'[\"青绫裙子试新裁，水面风吹拂拂开。舟小身轻波复静，荷花荡里去还来。\"]',NULL,NULL,NULL,NULL),('e647d10f0523c55f1368c91454cad331','临江仙','查维宏','e647d10f0523c55fd1457b72d1457b72','明',NULL,'[\"庭院深深愁几许，落花妒杀东风。天涯人远思无穷。\",\"绿窗梦觉，扶起玉钗松。\",\"漫忆当年行乐处，而今酒懒诗慵。卷帘悄步曲廊东，恼人燕子，双舞夕阳中。\"]',NULL,NULL,NULL,NULL),('e647d10f05b8058b22af7c3e488c7519','虞美人·花原药坞茫锄去','陆宏定','e647d10f05b8058bb49c63fab49c63fa','明',NULL,'[\"花原药坞茫锄去，会底天工意。却移双桨傍渔矶，刚被一轮新月、照前溪。\",\"来霜往露须臾换，都是牵愁案。渐添华发入中年，悔把高山流水、者回弹。\"]',NULL,NULL,NULL,NULL),('e647d10f05b8058b3304e664b6642239','望湘人·记归程过半','陆宏定','e647d10f05b8058bb49c63fab49c63fa','明',NULL,'[\"记归程过半，家住天南，吴烟越岫飘渺。转眼秋冬，几回新月，偏向离人燎皎。急管宵残，疏钟梦断，客衣寒悄。忆临歧、泪染湘罗，怕助风霜易老。\",\"是尔翠黛慵描，正恹恹憔悴，向予低道。念此去、谁怜冷暖，关山路杳。才摧手、教款语丁宁，眼底征云缭绕。悔不翦、春雨蘼芜，牵惹愁怀多少。\"]',NULL,NULL,NULL,NULL),('e647d10f05b8058bc392624ec5933e91','满路花·花朝辑蒲菊繁蔓图悼亡姬','陆宏定','e647d10f05b8058bb49c63fab49c63fa','明',NULL,'[\"刀尺好谁贻？又是中和节。众芳何处也，催鶗鴂。春迟候冷，别院梅花发。抚景堪愁绝。自入春来，风风雨雨才歇。\",\"小庭枯蔓，逗的春消息。新条还护取，穿萝薜。当年记道，纤手亲移植。共倚藤阴月。断人肠，是花期、转眼狼籍。\"]',NULL,NULL,NULL,NULL),('e647d10f068d821edd179f83bfe51180','瑞鹧鸪·湖上上元','卓人月','e647d10f068d821ed1457b72d1457b72','明',NULL,'[\"城中火树落金钱，城外湖波起碧烟。夜夜夜深歌子夜，年年年节度丁年。\",\"玻璃一段湖称圣，琥珀千钟酒号贤。自分懒追儿女队，玉梅花下拾花钿。\"]',NULL,NULL,NULL,NULL),('e647d10f06affb1f165a25021000839e','西湖竹枝','贝琼','e647d10f06affb1f50905d7bbad5f337','明',NULL,'[\"六月玉泉来看鱼，湖头雨过尽芙蕖。芙蕖花开郎更远，玉泉鱼少亦无书。\"]',NULL,NULL,NULL,NULL),('e647d10f06affb1ff5329c51f43fd471','水龙吟·春思','贝琼','e647d10f06affb1f50905d7bbad5f337','明',NULL,'[\"楚天归雁千行，一书不寄相思苦。匆匆过了，踏青时节，更愁风雨。燕子黄昏，海棠春晓，几番凄楚。问谁能为写，重重别恨，算除有、江淹赋。\",\"尚记银屏翠箔，抱琵琶、夜调新谱。芳年易度，沈腰宽尽，白头如许。弱水三千，武陵一曲，重寻何处。奈无情杜宇，年年此日，到淮南路。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97d1986f90b','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"太湖东来沧海西，四十二湾江渐低。源头受得浙间水，不放浑潮淤作泥。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97d1c567fe3','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"江流两岸尽平川，荠麦如云树若烟。不是青龙任水监，陆成沟壑水成田。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97d21e8eeb2','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"沙田疲瘠快秋登，家计浑如水上冰。今日新佥河泊户，阿侬准备学攀罾。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97d2bad55c9','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"沪渎祠荒古垒平，东西芦浦荻芽生。袁嵩向时防海处，何物孙恩敢弄兵。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97d33c1bc28','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"黄浦西边黄渡东，张泾正与泗泾通。航船昨夜春潮涨，百里华亭半日风。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97dc4a4eac9','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"大浦横塘九里湾，蚤潮船上晚潮还。侬心恰似东流水，直到海门无日闲。\"]',NULL,NULL,NULL,NULL),('e647d10f0b089e1bd46ec97ddf08ce4a','海上竹枝词七首','顾彧','e647d10f0b089e1bd1457b72d1457b72','明',NULL,'[\"平川多种木棉花，织布人家罢缉麻。昨日官租科正急，街头多卖木棉纱。\"]',NULL,NULL,NULL,NULL),('e647d10f0b87f59f46eb61fba828bdbf','河满子·客归','方文席','e647d10f0b87f59fd1457b72d1457b72','明',NULL,'[\"帘下杨花似雪，枕边香腻如云。为我飘零踪不定，几年独自黄昏。谁信桃花依旧，只留月印重门。\",\"钿剩龙膏不暖，绣残凤履犹存。日暮碧云空自合，漫教燕语殷勤。简得鲛绡红泪，今朝借作招魂。\"]',NULL,NULL,NULL,NULL),('e647d10f0bd1667ff137049b24283de7','阮郎归','端淑卿','e647d10f0bd1667fd1457b72d1457b72','明',NULL,'[\"园林春到清明节，花柳愁攀折。人生何事轻离别，水远山重叠。\",\"杜宇啼，残更月，银皂照明灭。昨宵有梦轻飞蝶，关心犹未说。\"]',NULL,NULL,NULL,NULL),('e647d10f0c6046e883cfc9c25a9f1db7','竹枝词','朱诚泳','e647d10f0c6046e84dcae38eced556cd','明',NULL,'[\"袅袅万竿秋影，点点二女泪痕。试听鹧鸪声里，满川风雨黄昏。\"]',NULL,NULL,NULL,NULL),('e647d10f0d09fac6e429ae79c5102e1d','如梦令','王世懋','e647d10f0d09fac6b5a1fc20894b77f8','明',NULL,'[\"枝上子规犹闹，门外碧梧谁扫。病起不禁秋，倚尽小楼残照。\",\"寒峭，寒峭，一夜白苹天老。\"]',NULL,NULL,NULL,NULL),('e647d10f0d9f2082ee754c58671d9f10','采莲曲','丘吉','e647d10f0d9f2082d1457b72d1457b72','明',NULL,'[\"细语呼人远不闻，水光摇荡石榴裙。一身肌骨无多重，欲入荷花化彩云。\"]',NULL,NULL,NULL,NULL),('e647d10f0de1295751adcf8fd6383e0f','阿那曲舟中待友','李明岳','e647d10f0de12957d1457b72d1457b72','明',NULL,'[\"几回闲夜停机杼，支枕蓬窗风许许。吹尽苹香不见人，绕塘寒月痈癗语。\"]',NULL,NULL,NULL,NULL),('e647d10f0eaadda4c48c891a89f9fa70','浣溪沙·顾默孙纳姬','茅维','e647d10f0eaadda4d1457b72d1457b72','明',NULL,'[\"四月陂塘水半扉，姬人梳裹入罗帏。佛楼先礼六铢衣。\",\"花午枕抛红印起，月斜炉绕篆烟微。百花深处玉郎归。\"]',NULL,NULL,NULL,NULL),('e647d10f0f13fbd566f3b33c936a0496','满江红·咏竹','陆容','e647d10f0f13fbd5eaa32c9655c567fd','明',NULL,'[\"不种闲花，池亭畔、几竿修竹。相映带、一泓流水，森寒洁绿。风动仙人鸣佩遂，雨余净女添膏沐。未成林，难望凤来栖，聊医俗。\",\"问华胄，名淇澳。寻苗裔，湘江曲。性孤高似柏，阿娇金屋。坐荫从容烦暑退，清心恍惚微香触。历冰霜、不变好风姿，温如玉。\"]',NULL,NULL,NULL,'[\"修竹：长长的竹子。\",\"映带：景物彼此衬托。如：山河相映带，万竹争映带。\",\"一泓：yī hóng，一道。\",\"森寒洁绿：指竹子幽深、阴冷、洁净、翠绿。\",\"风动二句：风一吹像仙人身上的玉佩发出鸣响，雨后像洁净的美女添上了润发的油脂。膏沐，古代妇女润发的油脂：抹膏沐。也指洗沐，润泽：日出雾露除，青松如膏沐；是日值微雨，千林尽膏沐。\",\"凤来栖：传说凤凰非梧桐不栖，非竹实不食。\",\"聊医俗：姑且医治庸俗。苏轼《於潜僧绿筠轩》诗云：\\u201C宁可食无肉，不可居无竹。无肉使人瘦，无竹使人俗。人瘦尚可肥，士俗不可医。\\u201D\",\"问华胄：问竹子是哪个贵族的后代。华胄，huázhòu，贵族的后裔。\",\"淇澳：淇河岸的弯曲处。历史上以盛产竹而闻名。\",\"苗裔：miáoyì，子孙后代。\",\"湘江曲：湘江弯曲处。\",\"孤高：高傲，不合群。此赞竹子的和松柏一样高雅不俗。\",\"阿娇金屋：阿娇，指汉武帝刘彻的姑母（长公主刘娇）的女儿。原指汉武帝刘彻要用金屋接纳阿娇为妇。这里指翠美珍奇的竹子。\"]'),('e647d10f0f1492db1f67150b8791d999','风入松·癸亥端午','许谷','e647d10f0f1492dbd1457b72d1457b72','明',NULL,'[\"薰风吹满旧都城，斜日映钟陵。佳辰恰喜收残雨，正榴花、照眼烘晴。闭户恐辜令节，开尊独坐闲庭。\",\"一年春夏等闲更，冷暖不须惊。瓜棚豆架空寻遍，笑王郎、好梦难成。吊屈添来余恨，江边呜咽潮声。\"]',NULL,NULL,NULL,NULL),('e647d10f10f912a5b253d4d1f769c5cb','柳枝词','章士雅','e647d10f10f912a5d1457b72d1457b72','明',NULL,'[\"长淮渡头杨柳春，长淮市上酒旗新。系船沽酒折杨柳，还是去年西渡人。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0073929857b8ef78c','一丛花·湘南见池上梅花作二首','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"凌波何处见铢衣。池畔月明时。\",\"春风莫作颠狂态，好堪耐、璚雪葳蕤。楼上笛残，陇头书远，人在楚云西。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf007392985feafd1be','一丛花·湘南见池上梅花作二首','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"东风花发小池边。移种是何年。\",\"江南旧日看花处，更回首、多少山川。聊折一枝，瓦瓶斜插，惆怅早春天。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf00b89f96a30797175','木兰花慢·答介溪礼书二首','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"望紫云宫阙，曾出入、更行游。向凤阁挥毫，龙墀曳履，籍甚清幽。千载明良庆会，天地德独抱寸心酬。鲁史专行笔削，虞廷迭和歌讴。\",\"华旃日侍岂番休。殿东头。朱扉黄阁，班行回回，岁月悠悠。遥想而今乐事，西苑畔碧树正清秋。玉醴长沾凤斝，绣袍紧趁龙辀。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf00b89f96ac23060b7','木兰花慢·答介溪礼书二首','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"慨山人老矣，逃涧谷、息朋游。尽细竹成园，寒松夹坞，自占岩幽。天与清风皓月，从受用不费一钱酬。管领匡床石枕，安排野调村讴。\",\"馀生莫更赋行休。眼前头。清闲滋味，无何境界，尽已悠悠。莫笑先生太赸，些个物相伴度春秋。浊酒新烹郭索，闲窗静听钩辀。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf01b3358004324e44a','烛影摇红·攸县山行遇雨','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"微雨空蒙，青山一带如烟隔。绿杨霜后正凋枯，特地蒙恩泽。野草何知喜悦。谓春转、江湘二月。冻云覆水，朔吹鸣条，较来还别。\",\"仄径游泥，马蹄滑处频蹉跌。天时人事好商量，合变峰头雪。大地园林寒彻。尽飞起、滕王蛱蝶。衡山南去，报个丰年，腊前三白。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf022c74f53d7297cd1','如梦令·晓行','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"红日海东乍吐。消尽千山宿雾。无数暗栖鸦，历乱林梢飞去。\",\"安步。安步。锦石碧沙江路。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf04d8b1e98a11acf85','满庭芳·见村社','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"黄菊摧残，霜寒云冷，村墟别有风情。白蘋红蓼，相映水痕清。且喜时和盗息，是人家、仓廪丰盈。击社鼓，迎神答愿，儿女笑歌声。\",\"茅堂开浊酒，橙苞剖密，芋乳堆璚。任相逢，简礼烂醉呼名。爱杀人间真乐，休打算、宪府神京。推恩念，几时酬却，归伴草堂灵。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0584228c7b17790c4','疏帘淡月攸县冬至风雨','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"深沈台院，独自在他乡，怕逢佳节。春尽离家忽地，一阳时月。雁书不到衡阳外，盼关山、几多重叠。满城寒雨，一庭衰草，助人愁切。\",\"想儿女、家园欢笑，道而翁拥旄，持节体国。勤民揭天勋业。吾衰不似当年矣，对大川、怅无舟楫。竹篱茅舍，不如归去，当家温热。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0601722b2e5a12f5e','水龙吟·见水田乐之','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"潺潺碧涧归田，山人自识山家趣。开春阳动，土膏融暖，便修农务。黄犊翻泥，青秧刺水，爱逢时雨。但完官两税，供家八口，万事何、思何虑。\",\"细数古来豪杰。犁锄边、尽多乐处。伊尹深耕，孔明薄种，甚生出处。江左夷吾，终南捷径，岂堪俦侣。判穷达由天，卷舒在我，证却此、生公据。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0751b87fbb9172844','意难忘鹭洲宅赏灯','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"光烂红灯。正珠帘尽下，玉斝高擎。银屏烘夜影，火树迸春星。人散诞、岁丰登。对伟丽神京。雪尚凝、微风料峭，澹月疏明。\",\"那堪主客多情。任铜壶漏水，报过三更。传香添鸭鼎，选曲度鸾笙。连夜醉、不须醒。更告与良朋。尽元宵、都无十日，莫拣阴晴。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf077ee2d07d72db137','望江南离思','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"澄江水，别后几回潮。黄鹤笛声愁里咽，青龙山色梦中遥。秋空正泬寥。\",\"记来时，杨柳暗河桥。箫鼓临风喧祖帐，旌旗和月引征轺。离愁何日销。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf09212f082031f5f87','摸鱼儿·春寒作','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"向小园、花丛飞数蝶，懊恼又飘轻雪。倚画栏、独望湘山影，缥缈半峰残月。\",\"风料峭、罗袂生寒，默默愁难说。家乡万里，想绿草池塘，碧桃院落。\",\"更自伤、离别柔情结。看湘娥、旧恨班班，竹上难灭。\",\"问东皇、何日金乌扑帐，春云热。扁舟独自归去，高堂菽水同欢悦。\",\"班衣笑舞，再莫道、中朝绯袍金印，门前建旌节。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf09a71d82ac1c4373b','蓦山溪·平江马上','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"肩舆谢却，旷野独鞭骢马。县令小家，风似道、桥翁疏野。笑渠岂解，潇洒念本来，旧面目，无虚假，不在官高下。\",\"当年庾亮，玩月荆州夜。酾酒向江楼，顾官属、与之同把。而今遥想，此老兴飘飘，风流尽，堪模写，莫对时人话。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0a3dd46a63042a937','点绛唇·元日阴','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"残雪明阶，扑帘微雨东风峭。狐裘貂帽。可信春曾到。\",\"令节神京，合遣阳光照。天公拗。暖颠寒倒。一任梅花恼。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0b434d21a4cd8a86b','采桑子·甲戌正月十三夜风雨作','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"春城箫鼓无端闹，说近元宵。华烛高烧。偏对良辰更寂寥。\",\"斜风细雨敲窗户，又送萧条。愁病无聊。何日行歌白下桥。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0b434d21ad28e519e','采桑子·甲戌正月十三夜风雨作','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"华灯光射银屏影，斗起春光。月转回廊。罗绮风飘满路香。\",\"金陵自古豪华地，不似殊方。堪笑潘郎。流落天涯独举觞。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0b48609a381f71658','临江仙·雨中柬谭子羽','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"抱病登楼无意绪，满城寒雨蒙蒙。一樽何日与君同。卷帘芳草碧，呼酒夕阳红。\",\"堪恨赏心多不偶，依然枉却东风。扁舟归兴莫匆匆。江梅他自落，别有海棠丛。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0bacc0bde1d7ddce7','蝶恋花·巴陵山行','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"秋色那知行近远。翠竹丹榆，递换撩人眼。导骑才随山麓转。渔舟又渡溪流浅。\",\"数过奇峰千万点。野兴疏狂，转逗身轻健。悬崖绝涧皆平坦。寰中只奈人心险。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0cc8a1258d92842b6','念奴娇·湘山怀古','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"振衣嵂?，洗长空、初过一天新雨。万壑千峰，争耸秀、犹有微云吞吐。扑地闾阎，横江城郭，总是闲尘土。寺前松桧，让渠曾见今古。\",\"漫说衡岳巡游，郁林开拓身，后还谁主。泪竹斑斑，空洒血、玉辇而今何处。水底灵均，江边刺史蔓，草埋荒宇。举觞浮白，竟须烂醉休语。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0d34c8c0761d130f8','玉连环和石亭赏灯','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"彩棚灯障千花簇。影摇红烛。更阑客醉且淹留，月影在、阑干曲。\",\"满座锦衣花幞。雅吟追逐。不须檀板共金钗，如此乐、今生足。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0d948c53556f72921','如梦令·雨怀','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"夜静猛风将雨。落尽紫榆千树。行客忆江南，渺渺碧天穷处。\",\"无绪。无绪。独与寒灯宾主。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0f9706ef9155c7025','潇湘逢故人慢过浏阳石洞岭','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"山城晓发，正初阳微暖，宿雾方消。林涧路迢遥。爱云峰翠滴，霜叶红烧。旌旗宛转，又何知、步入丹霄。回望处、江湖满地，不禁烟浪滔滔。\",\"村墟远，几家烟火道迩来，闾阎生计萧条。试说向当朝。正海晏河清，雨顺风调。知他甚事，掇薪火、漫燎鬓毛焦。且休谈、观风设教，烦公少损征徭。\"]',NULL,NULL,NULL,NULL),('e647d10f1138fcf0ff7834972b9ecb67','诉衷情·桂林徐伯川屡约不至有词见寄作此答之','顾璘','e647d10f1138fcf097275a2389ae0fe2','明',NULL,'[\"瑶笺新调写乌丝。字字说相思。传闻几度来也，又盼过、落花时。\",\"停画舫，驻金羁。枉参差。干天何事，伏雨阑风，故误佳期。\"]',NULL,NULL,NULL,NULL),('e647d10f11a70dc6b253d4d1e61640ea','柳枝词','李汛','e647d10f11a70dc6d1457b72d1457b72','明',NULL,'[\"不唱春风杨柳词，繁华过眼已多时。于今杨柳非前比，无此繁华无此悲。\"]',NULL,NULL,NULL,NULL),('e647d10f11c939487b25fa11980ce606','离亭燕白下怀叶君实师','胡偕行','e647d10f11c93948d1457b72d1457b72','明',NULL,'[\"归雁行行啼哢，唤破一宵秋梦。梦醒封题忙寄与，又不会将书送。怅望碧山头，遥嘱几声珍重。\",\"踏遍长安何用，满目烟云寒冻。檐外铮铮风弄咽，急战雨声还恐。多少客金陵，今夜离愁谁共。\"]',NULL,NULL,NULL,NULL),('e647d10f127dc8348abdd1fef7c86b5f','鹊桥仙·渔父','沈士','e647d10f127dc834d1457b72d1457b72','明',NULL,'[\"澄潭集网，寒江垂线，雪白鲈鱼成串。西风吹送酒花香，促步向、垆头索换。\",\"竹篙枕月，蓑衣遮面，一曲沧浪天半。乾坤水上最清闲，也自分、烟波占断。\"]',NULL,NULL,NULL,NULL),('e647d10f1281ba4c2a4a496e7403aae9','水龙吟·落花','王翃','e647d10f1281ba4cf3173935be3e9d3f','明',NULL,'[\"春阳恰照空园，晓莺啼倦残红冷。芳魂无据，冰帘斜扑，乱抛芳径。力弱丝游，心疏蝶过，召还烟景。问余春未远，悠扬何事，依然就，风中影。\",\"门外晚寒犹逞。正无聊、断肠人醒。新巢燕子，衔来尚湿，小楼初暝。露重晨留，泥香夜葬，沾衣微省。更一溪、暗水吹成罨画，绣萍千顷。\"]',NULL,NULL,NULL,NULL),('e647d10f128a5f9b52c51314c8c716f2','浣溪沙·春闺','吴莫胜','e647d10f128a5f9bd1457b72d1457b72','明',NULL,'[\"午梦谁惊树影摇，玉楼人倦倚红绡。游丝系满杏花梢。\",\"蜂逐窗棂迷去路，燕寻垒草未归巢。风筝线断傍云高。\"]',NULL,NULL,NULL,NULL),('e647d10f130b6c161ac894621084d547','杨柳枝·词四首','黎遂球','e647d10f130b6c16d1457b72e4873aa9','明',NULL,'[\"参差杨柳又如齐，二月雏莺上下栖。怪得多情青眼便，春风常笑雨常啼。\"]',NULL,NULL,NULL,NULL),('e647d10f130b6c161ac8946245391a92','杨柳枝·词四首','黎遂球','e647d10f130b6c16d1457b72e4873aa9','明',NULL,'[\"垂柳垂杨乱著丝，行人都拟为相思。不知原是双双树，生长桥头惯别离。\"]',NULL,NULL,NULL,NULL),('e647d10f130b6c161ac894624bd6e1d5','杨柳枝·词四首','黎遂球','e647d10f130b6c16d1457b72e4873aa9','明',NULL,'[\"树树青青叶叶开，低腰宜浪舞宜台。莫多移到江都种，系得君王不肯回。\"]',NULL,NULL,NULL,NULL),('e647d10f130b6c161ac894624cc2b157','杨柳枝·词四首','黎遂球','e647d10f130b6c16d1457b72e4873aa9','明',NULL,'[\"翠压朱阑十二干，满堤杨柳一般看。如何苏小门边树，偏有游人系玉鞍。\"]',NULL,NULL,NULL,NULL),('e647d10f130b6c1627bca14761aba15f','杨柳枝·词三首','黎遂球','e647d10f130b6c16d1457b72e4873aa9','明',NULL,'[\"轻烟酣醉动长眉，不辨杨枝定柳枝。恰似大堤游冶伴，相逢羞道姓名时。\"]',NULL,NULL,NULL,NULL),('e647d10f130b6c1627bca147a5f33045','杨柳枝·词三首','黎遂球','e647d10f130b6c16d1457b72e4873aa9','明',NULL,'[\"连绵千树俨游龙，不是眠烟便舞风。底笑落残飞絮后，满江秋叶罥芙蓉。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a838501bb908e3d883','杨柳枝·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"罨画楼前雨歇时。千丝万缕绿垂垂。无端却被风吹起，撩乱春心不自持。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a838501bb9a1406a9d','杨柳枝·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"门外春风杨柳枝。去年折柳送郎时。车轮一去无消息，只有长条依旧垂。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a838501bb9acea349a','杨柳枝·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"罩水和烟万叶重。倚风飞絮晓茸茸。莫教吹落长河去，化作浮萍无定踪。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a838501bb9e7de5f14','杨柳枝·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"画帘风动影丝丝。曳绿摇金昼景迟。睡起倚阑看蛱蝶，莺声只在最高枝。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a84341cd28342a778c','调笑词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"明月。明月。今古几回圆缺。天风吹上云端。\",\"琼楼玉宇露寒。寒露。寒露。捣药谁怜顾兔。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a84341cd2855d89548','调笑词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"绿绮。绿绮。写得高山流水。海天烟雾漫漫。\",\"明月松风夜寒。寒夜。寒夜。鹤舞银河初下。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a84341cd2861555951','调笑词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"泥滑。泥滑。道上间关车辖。凄凄切切低飞。\",\"正是行人远归。归远。归远。只恐山头日晚。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a84341cd28e2d1a5c4','调笑词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"精卫。精卫。沧海填来几岁。飞来飞去翩翩。\",\"但见洪涛碧烟。烟碧。烟碧。愁杀孤飞短翼。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a883cfc9c267aa7cc8','竹枝词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"闻郎昨夜下巴东，烟树苍苍山万重。一片阳云飞不定，不知何处有郎踪。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a883cfc9c277c80e54','竹枝词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"荷叶亭亭秋色阑，露珠风荡不成团。自怜颜色非前日，羞把新妆临水看。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a883cfc9c278fa1e2a','竹枝词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"湖上闻郎歌《竹枝》，湖中莲艇便轻移。却言郎度潇湘去，折得荷花空泪垂。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a883cfc9c2fcba2955','竹枝词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"船头烟暝浪花飞，船里风来雨湿衣。独棹兰桡下莲渚，迎郎不见又空归。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a8c4c3c0812d6e0694','三台·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"坐稳锦茵重迭，醉来乌帽欹斜。谁遣青娥斗月，自怜银海生花。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a8c4c3c081e7722492','三台·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"楼上角声呜咽，天边斗柄横斜。酒醒风惊帘幕，漏残月在梅花。\"]',NULL,NULL,NULL,NULL),('e647d10f134fa8a8c4c3c081f55d6b64','三台·词','胡俨','e647d10f134fa8a8cf9a242b8fb5f8be','明',NULL,'[\"一阵霜风乍起，半窗月影初斜。宝匣烧残香篆，银釭落尽灯花。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e4507176fe02b9c58d5','酹江月江湖','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"曾随钓艇，弄秋波，见尽江湖浩漠。两岸白蘋洲渚外，露泠荻花枫落。半夜潮声，中天月色，更转梅花角。推篷试问，故人随处萧索。\",\"发棹牧浦渔村，夕阳城畔，归雁鸣偏数。绿酒黄花烟雨际，几梦故园林壑。坏塔风高，海门山小，春尽垂杨郭。归来林下，振衣高视寥廓。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45131be353bcb05216','沁园春·登真','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"溟涬鸿蒙，肇自先天，无极之初。暨阴阳分判，乾坤定位，循环动静，真宰中居。一点灵明，辉天朗地，亘古圆融无智愚。尘缘断，看碧潭镜净，月莹心珠。\",\"八荒洞照毋隅。中宵永、冰壶玉液酥。正真铅投汞，坎离交姤，火龙水虎，橐籥吹嘘。白雪凝舆，黄芽满鼎，雷震昆仑彻太虚。功成后，俟胎圆神化，同赴天衢。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e451deae78c785a4950','满江红·阅世','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"忆昔少年，行乐处、都非旧景。听海添潮落，绮绣四城春永。歌舞楼头憎日暮，管弦席里嗔酣省。看目前荣悴几光阴，愁难整。\",\"梦魂醒尘虑并。漫堪嗟空自警。慨兰桡荻阁，水寒烟冷。无分江湖中绪断，天心月到澄潭影。念浮生、识破悲欢川波静。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e454cff35c8d046b6a5','苏武慢消闲','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"悟幻寻真，参求几载，自誓顿超生灭。风抄雪纂，夜读朝吟，探究古今贤哲。道妙禅宗，万殊一本，勘破底须分则。这堂堂无碍，真空非与，太阴圆缺。\",\"堪笑处、竹椅蒲团，松窗桂牖，返步便同高洁。尘世相违，水云为伴，高卧故园风雪。骏马貂裘，翠袖红螺，过目浮华闲说。速回头是岸，帘帏光透，性天心月。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45629289874e30952e','风入松·和虞学士韵','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"江城淹病酒难酣。疏鬓讶朝簪。暑消已觉秋光遍，红尘远、斜日回骖。蕙帐累留残宿，青霜犹湿春衫。\",\"秦淮潮定碧如蓝。归燕息呢喃。白云帏幔寒偏早，更谁问、雁字鱼缄。早晚棹声归也，黄花白酒村南。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e4566c85aa901e79974','水调歌头·法海','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"先天无象始，父母未生前。一真独露光明，亘古著灵源。纵闭阴阳，内运颠倒五行，攒簇浩气养三田。发用杀机处，雷电震无边。\",\"既非心，又非法，亦非禅。幽微奥妙千金，不与世人传。莫认符涂咒诀，直悟玄关宗祖，将在自神全。瓦砾兼莎草，济世自超然。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e4573f4853668f9bb1c','水调歌头','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"至道无言说，寂默守规中。杳冥恍惚，周流一气运元宫。采取金精木液，真土内擒铅汞，顷刻显神功。测爻符，鸣橐籥，震雷风。\",\"坎离颠倒，火飞碧海炼真空。太乙含真有象，玉鼎流珠凝结，神化合玄通。归去蓬瀛路，旷劫玩鸿蒙。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45a37c25f6932c12f2','解红·慢了悟','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"本来真性体，虚空遍界圆明现。情尘掩翳心珠暗。溺重渊、灵光不昧，一点辉煌元非远。扫浮云、万象无遮碍，天心见。野花啼乌，闲中舒卷。看沧海桑田几清浅。衰荣代谢常消息，尽愁山苦海，流浪难转。猛省来、乾坤总妙玄。\",\"松筠畔、寂寞细玩真源。曹溪路口无阴树，非法非禅。直待一阳生，坎光腾如波卷。谁会取、静饮刀圭浮虑遣。视无形，听无声，言毋辨。这落魄逍遥真可羡。白云堆里容痴倦。共寒泉枯木，高隐壶天。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45d9a3ce316c0ff707','望梅花·绝交','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"浮世无边尘累。勘破幻缘能几。亿劫冤亲，千生契识，薄似片云情义、空自愧。\",\"奸巧机关逞英雄，徒增业翳。用尽深谋诡计。奔竞是非名利。\",\"造物无私，灵台自昧。苦海沉津如炽、净抛弃。野水疏林绝行踪，月明风细。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45de34648d9ec829e5','无俗念参究','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"麈湖峰下，结云松巢子，动忘昏晓。浮世衰荣无限事，一笑浪沤萍蓼。翠竹黄花，水声山色，此味知多少。湛然莹彻，色空俱自明了。\",\"天光云影徘徊，写长空色，一镜澄清沼。春去秋来心自在，付与野情鱼鸟。海阔江平，月明风细，清籁传音杳。便须飞步，沧溟朗吟天表。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45ec12a477c0fa813c','满庭芳·山居','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"折郤乌藤，便寻茅屋，谁知剩水残山。凿池种树，梅竹任萦环。芳草闲花覆地，烟霞里、藓径柴关。无人到，春风秋月，松菊伴幽潺。\",\"箪瓢随分过，无荣无辱，樵路渔湾。与林猿谷，鸟暮乐朝欢。扫净情尘业垢，披衣坐、真息养还。丹优游处，孤琴只鹤，霜露不凋颜。\"]',NULL,NULL,NULL,NULL),('e647d10f13749e45f5f9268067171cea','风入松·问学','张宇初','e647d10f13749e45d1457b72512c5cad','明',NULL,'[\"十年灯影夜相亲。寒暑迭催频。短窗几度停犀管，残编尽、知味何人。帘外雪深风紧，梅花偏旺诗神。\",\"千经万史足经纶。学业志弥沦。天根月窟问今古，文章事、多少迷津。收敛虚灵莹彻，杖藜随处阳春。\"]',NULL,NULL,NULL,NULL),('e647d10f138b75c7f32f182e737ae5f0','浣溪沙','江膺','e647d10f138b75c7d1457b72d1457b72','明',NULL,'[\"薄薄香云稳称肩，凤头刚衬小红笺。朅来堤畔蹋轻烟。\",\"鸟学新声窥翠幰，蝶知芳气上花钿。个人何处不堪怜。\"]',NULL,NULL,NULL,NULL),('e647d10f138b75c7f57d577a000b9a55','浣溪沙·苦雨','江膺','e647d10f138b75c7d1457b72d1457b72','明',NULL,'[\"宿雨酣花黯未收，绿猗新涨小鳞浮。柳绵欲下不成球。\",\"飞沫卷珠过水面，湿烟拖雨上云头。多情强半为登楼。\"]',NULL,NULL,NULL,NULL),('e647d10f13c22f6cf32f182ec9d499a3','浣溪沙','蒋尔玙','e647d10f13c22f6cd1457b72d1457b72','明',NULL,'[\"剪剪轻云片片霞，玉钩纤月短屏遮。黄昏风睯绣帘斜。\",\"花蕊半含多抱蝶，柳条犹软不胜鸦。一番春梦落天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f1435794620322def1d8145e8','南歌子·新夏','张名由','e647d10f14357946e60e81c4a368b0de','明',NULL,'[\"晚霁收梅雨，轻阴舞楝花。前林烟缕暖残霞。平芜双燕、飞去受风斜。\",\"怅望迷南浦，愁心水一涯。满沟新涨绕田家。不胜聒耳、青草有鸣蛙。\"]',NULL,NULL,NULL,NULL),('e647d10f1435794625eb6af72385f275','清平乐','张名由','e647d10f14357946e60e81c4a368b0de','明',NULL,'[\"绿杨阴里，桃径吹红雨。金勒玉骢何处去，满目天涯飞絮。\",\"别离情绪难禁，余香犹在罗襟。尽日翠钿慵整，菱芳暗袭芳尘。\"]',NULL,NULL,NULL,NULL),('e647d10f145a531711a0546e7da009e8','沁园春·招隐看梅','邬仁卿','e647d10f145a5317d1457b72d1457b72','明',NULL,'[\"十里江城，共向东风，寻到梅花。想三更幽梦，悄移石罅，一枝春影，扶上窗纱。铁骨崚嶒，冰姿修洁，可是神仙萼绿华。今宵醉，且休吹玉笛，只按红牙。\",\"槎枒，绮席横斜。恨知己、难逢好句赊。但已弃零落，金铃莫系，不甘争媚，锦幕休遮。冷欲侵人，清能绝俗，肯让狂蜂坐晚衙。争怜惜，怕角声吹彻，片片飞霞。\"]',NULL,NULL,NULL,NULL),('e647d10f14fd8918c9129ab5320608a7','风中柳春晓','纪映钟','e647d10f14fd8918a2cc63e0f50a6c02','明',NULL,'[\"春尽荒城，白水溶溶一片。小桥横、绿杨如线。幽禽罢浴，相唤平沙软。昼偏长、凭阑人倦。\",\"朱轮宝骑，尽与年光流转。问王孙、归期何晚。连山芳草，遮断天涯面。悄冥冥、珠帘频卷。\"]',NULL,NULL,NULL,NULL),('e647d10f152b525821268583eb84017a','菩萨蛮·送春','倪抚','e647d10f152b5258d1457b72d1457b72','明',NULL,'[\"年年白放春归去，无端又是风和雨。倦态不成妆，春宵恨转长。\",\"一灯红夜午，城上闻更鼓。鱼钥锁双扉，愁他梦里归。\"]',NULL,NULL,NULL,NULL),('e647d10f15ad7eb7fab56b06133aac43','金陵竹枝词二首','柳应芳','e647d10f15ad7eb7d1457b72d1457b72','明',NULL,'[\"御前队子小梨园，长奉千秋万岁欢。一自武宗巡幸后，可怜跳与外人看。\"]',NULL,NULL,NULL,NULL),('e647d10f15ad7eb7fab56b06dd3bced5','金陵竹枝词二首','柳应芳','e647d10f15ad7eb7d1457b72d1457b72','明',NULL,'[\"旧院后门春草新，前门又听叫官身。卢姬已嫁徐娘老，歌舞行中有几人。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639501f3d22d5f7805ea','河传·江上作','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"江上风过水生纹，烟里斜阳半曛。雨声不堪客耳闻，纷纷，梦魂迷断云。\",\"梦断天涯归路杳，天欲晓，残月窥窗小。木兰舟，芦苇秋，汀洲，藕花相伴愁。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639501fbf28c159b2385','小重山·咏月','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"始映西轩似玉钩，想应容不得、一些愁。娟娟斜倚凤凰楼，窥朱户，应自半含羞。\",\"今夜正悠悠，玉□□□□、是□□。人间天上一般秋。\",\"银潢水，何事独西流。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639503dabf010cd00cba','疏影·分韵咏荷花得实字','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"琴薰应律。有细烟罥雨，浓露晞日。洛浦波生，湘渚风交，霓裳水佩争出。姮娥欲共青鸾舞，扬锦幕、香霏纨质。唤海童、齐捧骊珠，艳色荡摇鲛室。\",\"遥想麻姑坛上，翠霞与缟袂，相伴箫瑟。妖冶珊瑚，柔媚昌宗，敢污天然清谧。西峰玉井秋河近，看叶底、蜂房成蜜。便为君、摘向金尊，压倒楚江萍实。\"]',NULL,NULL,NULL,NULL),('e647d10f163263950d9fd01dbbb8961b','风流子·咏草','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雪尽水平津，萋萋处、风景最愁人。想金勒未归，绣帏深闭，玉颜自老，芳意徒新。送日挽烟千万里，迤逦际苍旻。蜀魄叫迷，楚魂思殢，碧云如梦，眉黛空颦。\",\"凄凉长门殿，飞花与清泪，共滴华茵。惟有乱萤时来，夕砌相亲。恨此身不化，柔条弱蔓，暗随灵雨，得到枫宸。窗掩黄昏，一生几度青春。\"]',NULL,NULL,NULL,NULL),('e647d10f163263950e56aa538c6ac5f1','忆旧游·咏灯花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"正良宵过雨，玉漏传声，罗幌无风。梦觉青釭里，有金葩璀璨，琼蕊珑?。翠翘隐映丹凤，摇曳碧云中。似瑞霭含星，轻霞晕月，霁景摛虹。\",\"离人最多感，况四壁萧条，惟有吟虫。想像长门殿，怨佳期冰泮，芳意烟空。强将片影相对，清泪湿铅红。但暗自灰心，朱颜判却成老翁。\"]',NULL,NULL,NULL,NULL),('e647d10f163263950ebc4c4ff90aca99','梁州令','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨过群山翠。杨柳含烟如醉。画檐双燕引雏飞，风动榴花，点点猩红碎。\",\"对花莫把阑干倚。心乱难为理。夕阳江上，满眼清波，总是愁人泪。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639510e09cb75964dc63','满庭芳·二月十一日寿石末公','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"岳气钟嵩，星精降昴，五云光映麟书。绿明红媚，风暖日长初？金翿斜开洞府，群仙拥、翠佩琼裾。收拾尽，乾坤清淑，为瑞在扶舆。\",\"文韬，兼武略，南阳诸葛，东海穰苴。有貔虎龙蛇，深护储胥。制胜不妨谈笑，莺声里、旗影徐徐。行看取，非熊应兆，沙路辗高车。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639512f4d5da4f758773','卜算子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白日下层岑，暮霭生寒水。水远山长不见人，月上风微起。\",\"何许一孤鸿，飞去潇湘沚。欲采蘋花寄所思，无处寻双鲤。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639512f4d5da9b6a2574','卜算子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春去蝶先知，花落蜂难缀。草绿庭空不见人，愁共天无际。\",\"凿沼种荷看，水浅荷钱细。惟有青苔最可怜，欲上人衣袂。\"]',NULL,NULL,NULL,NULL),('e647d10f163263951368c914097175f7','临江仙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"街鼓无声更漏咽，不知残夜如何。玉绳历落耿银河。鹊惊穿暗树，露坠滴寒莎。\",\"梦里相逢还共说，五湖烟水渔蓑。镜中绿发渐无多。泪如霜后叶，摵摵下庭柯。\"]',NULL,NULL,NULL,NULL),('e647d10f163263951368c914e8518f21','临江仙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"楼外西风将雨过，重门又掩黄昏。金凫香息被无温。繁思牵宿恨，愕梦怆离魂。\",\"往事都随流水去，伤心欲共谁论。故家耆旧几人存。相思芳岁尽，霜雪满乾坤。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395154effbd44abc7dc','浪淘沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"门外几多春，特地愁人，红英都作马蹄尘。只有青青河畔草，一似相亲。\",\"极目楚江滨，流水无津，野猿山鸟怨芳辰。叫雨呼云昏复晓，天也伤神。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395187737eaaa8af70f','玉烛新梦归','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"羁魂悲别久，但闷晓忧昏，感新怀旧。任他万壑千峰阻，径度不劳回首。楼台侧畔，记向日新栽花柳。斜照里、一带青山，山前翠浮琼溜。\",\"佳人倩笑来迎，有野舞村歌，龀童鲐叟，故池半甃。风叶动、搅乱数升科斗。周章未了，早画角、吹残更漏。翻惹起、无限愁端，中心自受。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639518918fd9cdf978af','六幺令·杪秋遥夜','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"杪秋遥夜，人卧群喧息。明河渐低庭户，户有蟏蛸织。何许飞来雁阵，冲入寒烟碧。相思何急。悲来空自，目断云霄羡归翼。\",\"青镫独照暗壁，胍胍羁魂寂。堪恨冷梦难长，欲见无由得。年少不知过了，老去徒嗟惜。追寻畴昔。愁如流水，渺渺茫茫趁潮汐。\"]',NULL,NULL,NULL,NULL),('e647d10f163263951ac9c2632b0abde4','满庭芳·咏荷花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"杨柳烟销，梨花云散，瑶台别是风光。翠霞深处，谁舞白霓裳？三十六宫向晓，凤绡拥、红粉成行。珠帘外，风翻瑞葆，惊起紫鸳鸯。\",\"含情，空怅望，衾寒鄂渚，佩冷潇湘。怨凌波尘袜，不度银潢。只恐青娥娇妒，相将见、苇白芹黄。凄凉也，一天坠露，明月在池塘。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639521156199f1180798','帝台春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"凉雨新沐，秋花遍墙角。门对远山，山带斜阳，葱茏相属。明月今宵巳自满，又争柰、素娥幽独。向天涯，望月兴怀，愁多如簇。\",\"年岁促，欢去速。意易足，事难续。镜掩懒重开，纵春风，也不解、染黄成绿。陇首孤云去无际，草上露华白如玉。且深下红帘，倒金樽醽醁。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395239fddeaaa228b92','解语花·咏柳','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"依依旎旎，袅袅娟娟，生态真无比。细腰宫里，和烟重、组绘满园桃李。佳人睡起，画未了、横云妩媚。轻步还怜掌中身，不自炜纨绮。\",\"长想渭城雨后，恨繁丝难绾，陌上征骑。阳关歌罢，香尘远、枉把翠柔折寄。鸦啼向晚，罗幕掩、数行清泪。一任他、化作浮萍，漂荡随流水。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639525665b3633af4b34','水龙吟','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"鸡鸣风雨潇潇，侧身天地无刘表。啼鹃迸泪，落花飘恨，断魂飞绕。月暗云霄，星沉烟水，角声清袅。问登楼王粲，镜中白发，今宵又添多少？\",\"极目乡关何处，渺青山、髻螺低小。几回好梦，随风归去，被渠遮了。宝瑟弦僵，玉笙簧冷，冥鸿天杪。但侵阶莎草、满庭绿树，不知昏晓。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639525eb6af77e8deab9','清平乐','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春风欲到，小草先知道。黄入新荑颜色好，图遣王孙归早。\",\"兴来策杖微行，枝间布谷初鸣。喜见儿童相报，墙根荠菜先生。\"]',NULL,NULL,NULL,NULL),('e647d10f163263952eabdb0f66db7d11','忆秦娥','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"风淅淅，月明何处吹长笛。吹长笛，拂云来雁，连天芦荻。\",\"繁华过眼成今昔，沧波浩渺空潮汐。空潮汐，淡烟衰草，六朝陈迹。\"]',NULL,NULL,NULL,NULL),('e647d10f163263952ee238bf3d06437c','摸鱼儿','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"断肠花、巳随流水，青苔空锁深院。知人不似花重好，莫怪为花留恋。\",\"花不管，任啼鸠悲鸣，霞锦成霜霰。水遥山远，泪滴翠绡寒。\",\"满天风雨，寂寞送昼雁。\",\"伤心事、纵是生来见惯，那堪芳岁先晚。孤云目尽苍梧野，留得竹枝歌怨。\",\"帘莫卷，斜照里、荒烟白草愁何限，情长意短。虫响又黄昏，沈沈夜永，惟有月华满。\"]',NULL,NULL,NULL,NULL),('e647d10f163263952ee238bfdf7b1491','摸鱼儿','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"洒轩窗、数声疏雨，霎时驱退残暑。碧江风过龙鳞起，天际白云如絮。\",\"人老去，但说道秋来，先自伤心绪。故乡何处，望不见渊明。\",\"菊荒三径，尽日漫凝伫。\",\"新凉夜、兔影澄清汉渚，寥寥鸿雁飞度。莎鸡络纬闲相妒，催得鬓丝千缕。\",\"芳草渡，还又是、苍烟绿水连红树，荣名几许。随分莫求多，五湖有路，波浪未应阻。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639532ad97e1a9370653','渔家傲','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"江上秋来惟有雨，江城九月犹炎暑。泉涌中庭苔上柱。深闭户，莎鸡露泣寒螀语。\",\"征戌诛求空轴杼，千村万落无砧杵。玉帐悠悠闲白羽。愁正聚，乱鸦啼破楼头鼓。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395355906997114e6ac','摸鱼儿·晚春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"悄寒生、沉沉院宇，不知春在何处。逢春渐老能多少，惟有暗愁无数。春又暮，但碧草、和烟绿叶漫飞絮。画梁燕语，还是旧音声，乌衣巷陌，转盼几今古。\",\"天涯路，流水青山如故。离魂长在郊树。夜深月暗苍梧远，化作杜鹃归去。人不见、桃花自落空阶上。东风未许，吹梦过蓝桥，关河满目，波浪渺南浦。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639536461c8b3cf116b9','眼儿媚·秋思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"烟草萋萋小楼西，云压雁声低。两行疏柳，一丝残照，数点鸦栖。\",\"春山碧树秋重绿，人在武陵溪。无情明月，有情归梦，同到幽闺。\"]',NULL,NULL,'此写离情，却别深怀抱。「两行疏柳，一丝残照，数点鸦栖」三句写出秋天萧索肃杀的意境。过片转以虚笔写，「无情明月，有情归梦，同到幽闺」，伤心语而以平常语出之，便饶回味。','[\"武陵溪：此用陶渊明《桃花源记》的故事，借指爱人在远方。\",\"归梦：指远游人惦念家里的魂梦。\"]'),('e647d10f1632639538501bb930fc473c','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"长江春水去悠悠，乱絮轻花取次休。落日高楼怨横笛，寒鸦飞尽暮蝉愁。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb93560ea1b','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"枝上秾花吹渐稀，楼前杨柳正依依。春风解释佳人恨，故卷繁阴放燕归。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb9465399b7','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"淮水春晴碧簟纹，杨花杨叶总成云。锦帆零落美人画，枝上流莺谁独闻。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb9582b74df','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"汉将营中万树春，朱旗晓色动星辰。可怜烟雨章台路，翠缕斜萦走马尘。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb96161fb46','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"长千桥边杨柳枝，千条万缕郁金丝。人来人去争攀折，无复青青映酒旗。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb963350a30','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"二月垂杨绿叶齐，无朝无暮子规啼。飞花衮衮向东去，却被东风吹向西。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb97fec6e9f','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"杨叶凄凄隐画楼，杨枝袅袅弄轻柔。鸣鸠叫罢黄莺语，肯信年华有素秋。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb9aba7343a','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"多事垂杨管送迎，长条折尽短条生。不知几许东风里，犹带轻烟罥晚晴。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639538501bb9ca278933','杨柳枝·词','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"忆昔春风白下亭，桃红李白柳青青。繁华一梦今何在，露叶依稀似泪零。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395388c4564752584eb','桃源忆故人','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"渊门篱下黄金蕊，还共空尊憔悴。今古这般滋味，想得都相似。\",\"牛山落日悲风起，回首旧欢无几。长夜清凉如水，独立星河里。\"]',NULL,NULL,NULL,NULL),('e647d10f163263953d5dc132498ee81d','卖花声','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"楼上倚阑干，月在云端，轻烟蜚尽碧绡寒。肠断夕阳芳草外，山海漫漫。\",\"柳嫩不堪攀，欲寄应难，盘龙尘匣怨孤鸾。可惜婵娟今夜月，好共谁看。\"]',NULL,NULL,NULL,NULL),('e647d10f163263953d5dc1329bb53ad5','卖花声','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"门外绿杨堤，有个莺啼，声声传恨入幽闺。闺里佳人惊睡起，泪湿眉低。\",\"莫把此情题，天也悲悽，沧江东下夕阳西。华发不知人别苦，随意萋萋。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870361ec733','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"鼋鼍在水虎在山，登山入木早防闲。别有一般真叵耐，虾蟆生在月中间。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba87056d728fc','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"人间日月去如梭，快活无多辛苦多。欲识江南古来意，请君听唱竹枝歌。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870697420b5','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"杜鹃花开不著忙，石莲花落却恓惶。垆头酒尽休悬望，裁衣无尺莫思量。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba87073347701','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"相思无益莫相思，赢得霜髯换黑髭。明月自圆还自缺，蚌胎瘦减有谁知。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba8708d8d84c3','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"若要开花须护持，花枝怕雨怕风吹。抽条结蕊千辛苦，糁土埋苔一霎时。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870b1f88d4f','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"阳台云雨漫荒唐，巫峡啼猿枉断肠。莫向苍梧山下去，九疑愁色满潇湘。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870bf5f1ad1','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"昨夜星光照湿泥，五更依旧雨凄凄。懊恼人心难测度，说道东方却在西。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870c892fad4','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"弯弯月子似帘钩，挂在梧桐树上头。月里姮娥知何处，人间离别未为愁。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870d69ea126','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"荣华未必是荣华，园里甜瓜生苦瓜。记得水边枯楠树，也曾发叶吐鲜花。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870d7b28a92','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"莫见乾藤道是香，莫见白石道是羊。布帘卖酒齐夸好，甜辣须还到口尝。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870f8f5fa61','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"望帝无端化羽毛，无朝无暮口嘈嘈。见说天高三万物，吻乾舌出也徒劳。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395405ba870ff7c18c6','竹枝歌','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"潇湘江水接天河，第一伤心是汨罗。斑竹冈头兰蕙死，黄茅垄上艾蒿多。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395415d41bdac3c3bfc','谒金门','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"风袅袅，吹线一庭秋草。天际夕阳无限好，断肠芳树老。\",\"尘世茫茫难料，有酒便须倾倒。落叶满阶从不扫，醒来新月皎。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639541835f7f9d2aa10f','浣溪沙·咏鸡冠花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"绛帻鸡人紫绮裘，彤墀欲报五更筹。不胜娇冶立清秋。\",\"日影却穿云气入，虹光犹带露华流。三千红粉总凝眸。\"]',NULL,NULL,NULL,NULL),('e647d10f163263954334969f2cbf0f7b','蝶恋花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"冷雨凄风昏白昼，庭院幽幽，秋草沿墙秀。何处无愁仍有酒，可怜彭泽门前柳。\",\"老去情怀难似旧，貌殊言异，转使人看丑。浸摘黄金花在手，寒烟满眼空回首。\"]',NULL,NULL,NULL,NULL),('e647d10f163263954334969f3e2a3380','蝶恋花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春梦岑岑呼不起，草绿庭空，日抱娇莺睡。暖吹飘香三万里，残丝骨断天沉水。\",\"宝瑟弦僵谁复理，梅子青青，苦在心儿里。满径苍苔花糁地，杜鹃口血闺中泪。\"]',NULL,NULL,NULL,NULL),('e647d10f163263954334969ffbe454b3','蝶恋花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白水茫茫烟渺渺，原野高低，触处生芳草。草绿花红人自老，有情争似无情好。\",\"丧乱馀身欢意少，肠断江山，不肯留残照。门掩黄昏寒料峭，角声吹起双栖鸟。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639546408bd927896d7c','玉楼春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春来触处花成绮，春去可怜花委地。催耕布谷彊知时，去国杜鹃空有泪。\",\"双鱼不见人千里，落絮牵愁和梦起。芭蕉多事惹东风，故作雨声惊客耳。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639546408bd9611ad219','玉楼春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"江城雨熟黄梅后，楼阁清高处永昼。阶前夕露宝珠呈，叶底夜蝉金石奏。\",\"苍苔不受残红覆，市径沿墙欺组绶。老来于物总忘情，漫为青山开北牗。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639546408bd987a4ad10','玉楼春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东风白下桥边道，不见百花惟见草。阴云结暝燕来难，宿雨留寒春去早。\",\"渚蒲汀荇无烦恼，舞叶吹香随处好。相思一夜楚江空，满地绿苔蜂蝶老。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639550ce976c9efc4dcd','御街行·秋夕','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"梧桐滴露鸣金井，夜耿耿、如年永。月明栖鸟数移柯，有似佳期不定。华年迅速，碧霄迢递，别恨空心领。\",\"青鸾怕见单栖影，任网遍，盘龙镜。盈盈一水隔双星，肠断羽迷鳞暝。消愁凭酒，惟应无柰，酒与愁同醒。\"]',NULL,NULL,NULL,NULL),('e647d10f163263955215d61a66ee6cea','最高楼','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"今宵月，还照别离愁，愁上最高楼。九霄无路青冥阔，玉京风露冷于秋。宝刀裁不断，水东流。谁知道，黄粱炊未熟，早过了，洞天三十六。\",\"尘土事，几时休。广寒桂树清香好，圆光恰满又成钩。笑阶前，萱草浪，语忘忧。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639555e86d06e3cca3ee','玉漏迟·初夏','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"海榴花似火，看看又见，麦秋时候。枝上鸣蜩，断续一庭金奏。翠沼风漪未定，看叶底、明珠圆溜。苔径黝，虾须挂处，有人消瘦。\",\"缅想那日欢娱，是鸿雁来初，芙蓉开后。蔓草青芜，但觉暗愁依旧。欲把相思寄与，雾烟惨、不堪回首。凝望久，寒鸦自啼疏柳。\"]',NULL,NULL,NULL,NULL),('e647d10f163263955765948405da7627','长相思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"桃花开，李花开，小白长红越女腮，春风巧剪裁。\",\"蜂徘徊，蝶徘徊，饮遍清香未肯回，莫教飘绿苔。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639557659484949adc1a','长相思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雁南归，人未归，寒信先来家信迟，容颜只镜知。\",\"晓风微，晓星稀，正是愁阑欲睡时，邻鸡莫要啼。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639557659484a5b471b3','长相思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"霜华清，月华清，清逼衾禂冷似冰，那堪砧杵声。\",\"鸡巳鸣，钟又鸣，天若于人还有情，如何不肯明。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639557659484f2f3fe68','长相思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"风飕飕，雨飕飕，风雨凄凄若九秋，酿成千斛愁。\",\"花事休，春事休，漫把花枝插满头，劝春须少留。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639557659484f51305aa','长相思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"烟茫茫，草茫茫，人去人来来去忙，沧江空夕阳。\",\"凫飞翔，雁飞翔，天路冥冥川路长，月明芦苇凉。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639557e8e7b3b81a570e','青玉案·春思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"昨朝出日今朝雨，望不见、愁来路。径滑泥深妨纵步，美人何处，花谢谁为主。\",\"离弦不系鸣筝柱，但满眼相思泪如缕。关塞迢迢音信阻，千重云水，万重烟树，心逐征鸿去。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639558df0d7d12a49cfc','怨王孙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"鳞羽路阻，佳人何处。木落山寒，江空岁暮。明镜飞上青天，照无眠。\",\"海风裂地鲸鱼死，三万里，水击蓬莱徙。不禁清泪，暗里洒向孤灯，结成冰。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639558df0d7d40fff2c9','怨王孙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"兔魄又满，天长雁短。露槿惊寒，夜蝉怨晚。知有多少闲愁，在心头。\",\"白云飞尽秋无际，花满砌，钟鼓重门闭。前年旧事，转盼巳逐东流，水悠悠。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639558df0d7ddcf701b5','怨王孙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"翠被夜冷，碧梧风劲。蛩语将阑，鸦栖不定。开户月在枝头，恰如钩。\",\"烟波缥缈瑶台路，人何处，黄叶连天雨。梦魂此际，绕尽越水吴山，白云间。\"]',NULL,NULL,NULL,NULL),('e647d10f163263955ab97d7e57d259c4','少年游','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"清风收雨，轻云漏月，凉气入幽窗。乱叶吟朝，饥虫啼夜，各自奏新腔。\",\"孤鸿又向天边落，归梦阻苍江。百结愁肠，两行丝鬓，寂寞对清缸。\"]',NULL,NULL,NULL,NULL),('e647d10f163263955c9c77f5e7dc87ba','渡江云','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"西风吹楚甸，四山净绿，万籁起秋声。卷帘当晚霁，倚遍阑干，惨恻望乡情。柴桑旧菊，为谁犹擢金。愁他一江流水，衮衮向东倾。\",\"凄清，天边孤雁，甚处飞来，到中庭落影。云路遥、归程难计，离恨空萦。斜阳冉冉低乔木，但暝色、摇动檐楹。还又见、花间露滴瑶琼。\"]',NULL,NULL,NULL,NULL),('e647d10f163263955f83515cf8705bb9','念奴娇·红树','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"霜风弄巧，把秋林一夜，吹成春苑。炎帝骑龙来绛阙，紫凤三千乘辇。电帜挥云，火旗热雨，霞佩随琼幰。碧山流水，窅然迷却刘阮。\",\"堪笑旧日隋园，绮罗虚剪，蝶与蜂俱怨。对此令人心耿耿，如在郁华宫殿。若木重晖，靡蓱四照，日漾骊珠暖。凭高凝睇，赤城相去不远。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dc0cc151d5','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨声一夜水漫沙，垤鹳长鸣蚁出窠。汀荇叶，石楠花，随意漂流拥钓槎。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dc217fa2df','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"烟雨楼前溪水生，少微山下杂花明。杨柳岸，小舟横，绿阴如恇听啼莺。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dc2dbff1ab','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"漠漠风花扑面飞，萋萋烟草送人归。彭泽菊，首阳薇，白酒苍苔独掩扉。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dc36cd9fff','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"三寸时鱼碧玉鳞，一汀芳草绿罗茵。枫叶渚，荻花津，笑杀黄埃马上人。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dc4c7879b9','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"二月桃花紫锦香，一声啼鴂万蜂忙。歌欸乃，濯沧浪，有酒无愁醉不妨。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dcbbdc2c10','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"麦秀蔪蔪野雉飞，沙头新柳叶如眉。风澹荡，水涟漪，正是溪鱼上钓时。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639560e027dcd3cbbe1d','渔歌子·为赵德怀赋','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"苍岭松花结子成，南明山色翠相迎。游客思，故人情，不是阳关堕泪声。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395619612d6754196a1','喜迁莺·梅花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"冻云初启，看的皪梅花，俯窥清泚。膏泽无加，铅华不御，应与素娥争丽。艳浥蕊宫露冷，香引瑶池风细。夜耿耿，想飞琼欲下，缟衣摇曳。\",\"惊慨犹月榭春宵，锦障开芳砌。倚树联诗，撷英浮酒，左右翠翘珠帨。今日故人何在，肠断白波东逝。向此际，但含情独立，为伊凝睇。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639562ae6715fd2ea06e','夜飞鹊','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"中天旧时月，还照元宵。山叠叠，水遥遥。碧云日暮去无际，玉人何处吹箫。东风尚怜故苑，尽莺含脆管，柳舞纤腰。凭高望断，旋添清泪作春潮。\",\"欲问广寒音信，鲸浪涌鱼蜚，不到层霄。芳径苍苔渐满，琼轩珠缀，空掩蟏蛸。几时再得，海棠花底候鸣镳。但长烟绿树，青芜暝霭，相对魂消。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639567b3607ddb40dd60','一萼红·送别','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白蘋洲，有芦花似雪，人在木兰舟。祖帐方开，骊歌未暌，斜照半入江楼。话不了、缠绵意绪，早归鸿、相唤落沙头。红蓼丹枫，青烟碧草，总为供愁。\",\"此去几时重见，怅秾华易谢，零雨难收，落浦波空，渭城柳尽，欲饮还又回眸。恨只恨、无情海水，趁归风、辄向西流。毕竟难留，一宵长似三秋。\"]',NULL,NULL,NULL,NULL),('e647d10f163263956a10f50809ca32c9','风入松','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"一天烟霭酝愁阴，庭宇沉沉。画梁燕子来何晚，薄寒犹恋罗衾。但道青春未谢，不知芳径苔深。\",\"少年欢笑梦中寻，落得伤心。苍梧凤去幽篁死，断弦空系瑶琴。莫更登高望远，杜鹃声在云林。\"]',NULL,NULL,NULL,NULL),('e647d10f163263956e59c9814a12cdff','蓦山溪','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"檐铃风过，昵昵闺中语。月上海东峰，冷光浸、澄清玉宇。幽人此际，轻步绕回廊；蛙鸣沼，鸦翻树，露滴松梢雨。\",\"芦花粗老，也学杨花絮。衮衮趁流尘，半沾泥、半飘陌路。不须看觑，且自买芳尊；巴觎曲，婆娑舞，醉饮朝还暮。\"]',NULL,NULL,NULL,NULL),('e647d10f163263956e59c981d2a57f1f','蓦山溪','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"三更鸣雨，湿透桃花脸。珠箔未登钩，早飘落、猩红数点。碧云如梦，人去巳无踪；金泥凤，玉蟠龙，寂寞蛛丝检。\",\"玄华柔软，总被秋霜染。永夜拥寒衾，耿青镫、膏凝晕闪。铜壶漏水，应是接天河；一声去，一声来，空把重门掩。\"]',NULL,NULL,NULL,NULL),('e647d10f163263956fa28a8692519638','浣溪沙·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"沙渚烟销翠羽空，楚歌重叠怨兰丛。树头树底觅残红。\",\"三百年间同晓梦，几家台榭凤城东。露桃犹自笑春风。\"]',NULL,NULL,NULL,NULL),('e647d10f163263956fa28a869924ba33','浣溪沙·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"花压阑干春昼长，碧楼红树倚斜阳。入门惟觉一庭香。\",\"绝壁过云开锦绣，冰丝弹月梦清凉。鸾歌凤舞断人肠。\"]',NULL,NULL,NULL,NULL),('e647d10f163263956ffdaf8a76fa1877','望江南','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"黄昏雨，滴沥四檐声。徒为衾裯添宿润，都来肺腑作愁城。惟觉寐难成。\",\"单栖鸟，几度绕枝鸣。金兽有烟飞不起，玉虫无燄翳还明。谁识此时情。\"]',NULL,NULL,NULL,NULL),('e647d10f163263957062abcb34e8479d','诉衷情','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"相思日日上高楼，还是去年秋。无情梁上双燕，弃我忽如雠。\",\"虫啧啧，叶飕飕，夜悠悠。阑干谩倚，塞北江南，何地无愁。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639572d7697a15a0dcd3','浣溪沙·秋思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"细雨斜风拂面来，草黄云白雁声哀。沧江东去几时回。\",\"枫叶有霜才可爱，菊花无酒不须开。百年多病莫登台。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639573f48536285532aa','水调歌头','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨过百花尽，绿叶自成帷。翛然惊破残梦，何许一黄鹂。独上高楼凝望，不见阳春去处，惟见草离离。且喜画梁燕，还傍旧巢归。\",\"理纨扇，清竹簟，试单衣。莫思身外闲事，有酒便须挥。蝼蚁王侯同尽，何用名标竹帛，留与后人疑。坐久暮天碧，月在绿杨枝。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639574f4cdeee4ad3899','浣溪沙·处州叶叔安溪南草堂','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"细草垂杨村巷幽，白沙素石引溪流。青苔矶上有扁舟。\",\"门外好山开罨画，屋头新月学帘钩。窗风一榻似清秋。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639579f146726507c12f','蓦山溪·咏檐铎','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"夜阑人静，鸣玉传声小。历历透纱窗，悄空闺、衾寒梦杳。凄凄切切，如共旅魂言；流水咽，过云停，一日肠千绕。\",\"金茎月转，乌尾讹将晓。欲断又还连，渺烟霄、羁鸿相吊。凭谁细写，此意入朱丝；呼郢客，招湘云，添作江南调。\"]',NULL,NULL,NULL,NULL),('e647d10f163263957c292a93a53af038','金人捧露盘','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"水如蓝，山如黛，草如丝。正去年、双燕来时。衡门昼掩，日长惟有睡相宜。午风吹破，北窗梦、檐影参差。\",\"李花开，梅花谢，榴花放，杏花稀。枉教人、两鬓霜垂。凭阑伫立，空将愁眼盻斜晖。断云迢递，趁征雁、直到天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395804d9eaa468d9df4','醉落魄','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东风太恶，夜来尽把花吹落。馀寒燕子乌衣薄，对语空梁，似叹人离索。\",\"江山满眼今非昨，无情芳草年年绿。雾云不见辽东鹤，野鸟声声，只叫思归乐。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639580d6daafc07acc3e','雨中花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"月入疏松光的皪，淡烟琐、江城夜寂。问何处无愁，人间今古，莫作他乡客。\",\"月里青鸾空有翼，杳不见、姮娥消息。但北斗从横，银河清浅，重露成涓滴。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639582791341dd928880','生查子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"蜘蛛网画檐，一日丝千转。红烬落寒釭，相思无由见。\",\"湖上采莲归，苦意知何限。引领望佳期，征旆天边远。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395844aeea0e7e8f568','淡黄柳·台城秋夜','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"江城夜寂，何处吹羌笛。城上月高风淅淅，翻动林梢败叶，一片琅玕下空碧。\",\"倦游客，乡关暮云隔。谩回首，盼归翼，想柴门、流水依然在。\",\"白发参军，青衫司马，休向天涯泪滴。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958468421a14ce13d0','阮郎归·怨情','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"河梁携手怆离分，悲嘶声入云。长门疏雨滴黄昏，昭阳歌吹闻。\",\"怀故国，叫愁魂，泪花红欲焚。苍梧苦雾压湘筠，猿啼不见君。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639586b9120292b53b58','虞美人','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"红榴花下宜男草，色与人俱好。佳人重色爱新花，只恐东风渐急、夕阳斜。\",\"岂知浮艳难长久，看见花枝瘦。琐窗晨启怨花空，不觉玉颜憔悴、减春红。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639586b91202f129de4b','虞美人','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东风过了南风起，节去如流水。日长门掩燕交加，可惜红葵落尽、雨中花。\",\"人闲只愿花长好，花谢人催老。老来空有爱花心，无柰斜阳不为、驻光阴。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958785150a507d8aa6','六幺令·□□','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"淡云收尽，月在苍龙角。霏霏似烟非雾，空里无声落。汉殿仙人掌冷，桂影筛高阁。玄蝉皓鹤，相呼相唤，嗽咽华滋笑丹药。\",\"□寒螀□分命□，永夜惊离索。络纬也共莎鸡，吟尽伤心曲。只恐韶年易晚，辜负蓬莱约。秋容寂寞，凭君若意，染取枫林赛红萼。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958a94363747715db7','小重山','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"说道多忧解瘦人，眼中双别泪、怕沾巾。白蘋红蓼满江滨，空搔首，何处是归津。感旧却怜新。\",\"情怀都不似、少年春。野花园柳共埃尘。盈尊酒，取醉莫辞频。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958a9436378051fea4','小重山','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"月满江城秋夜长，西风吹不断、桂花香。碧天如水露华凉，人不见，有泪在罗裳。何许雁南翔。\",\"堪怜一片影、落孤房。百年浮世事难量。空回首，天阔海茫茫。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958a943637ffffcbb2','小重山','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"城上高楼接海霞，疏林藏不尽、欲栖鸦。回廊曲槛别人家，征鸿外，风冷夕阳斜。灯暗鼓三挝。\",\"望中烟隐树、月笼沙。白云如梦绕天涯。梧桐雨，和泪滴寒莎。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958aef1b8e7578e3be','满庭芳·重九','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨歇凉生，天高气爽，又是一岁重阳。雁声来处，云白草茫茫？黄菊知人无酒，也不惜、椷死幽芳。龙山上，西风暝霭，落日下牛羊。\",\"登楼，何限兴，一齐分付，络纬寒螀。把古今闲事，莫更思量。坐到银河案户，城角尽、月转空墙。凝眸久，惊乌蜚起，零露湿衣裳。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958bbed9d1108b1622','捣练子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"吴死树，楚江枫，一样秋光雨不同。莫道衰荣长定在，落花终古怨东风。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958bbed9d1d2c1347e','捣练子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"人寂寂，夜沉沉，风满中庭月满林。欲挽银河湔泪眼，银河清浅泪痕深。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958bbed9d1dc66d918','捣练子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"云淡淡，月娟娟，月色朦胧照不眠。宁是不眠休作梦，梦时欢喜觉时怜。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958bbed9d1f3b4ff1c','捣练子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"烟漠漠，日阴阴，山色苍茫草色深。客里不知春又半，倚阑无语听鸣禽。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958d41f387670d83dc','西江月','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"红叶无风自落，黄花过雨犹香。床头尘瓮耻空觞，添了愁城万丈。\",\"阅世身如萍梗，怀人梦绕潇湘。应知此恨不寻常，岂在眉间目上。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958de51e6c049e056e','声声慢·咏愁','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"无踪无迹，难语难言，依依只在心曲。雨冷云昏日暮，海涯天角。轻衾梦回酒醒，夜悠悠、虫响灯绿。事去也，纵相怜、不是那时金屋。\",\"镜里清扬婉婉，凭朱槛、知他为谁颦蹙。凤老桐枯，惨淡九峰青矗。湘江泪痕未尽，有哀猿、相伴幽独。向此际，更那堪怀古送目。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958e914844989867b6','眼儿媚','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"芳草帘栊夕阳红，莺语绿阴中。王孙何处，碧云千里，青鸟无踪。\",\"玉笙吹落天边月，离恨满西风。忍将清泪，化为零雨，滴在兰丛。\"]',NULL,NULL,NULL,NULL),('e647d10f163263958e9cf050a94f6e8e','满路花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"山烟掠草低，江月披云湿。风声宵未了，钟声急。寒鸦何事，独在枝头立。老来诸病集，客里光阴，驶如驰马难絷。\",\"怀新思故，记一长遗十。颓墉围废宇、凭谁葺。阶前冷露，似向离人泣。岁暮蛟龙蛰，干将挂壁，任他苔镛生涩。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959061d51ab166892b','霜叶飞','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"簟凉宵永，纱窗外，琅玕飘坠金井。欲眠深恐寐难成，彊起看星影。悄不觉，天回斗柄，封狼弧矢相辉映。对此默消魂，又露落青冥，渐渐湿透衣领。\",\"河汉有客乘槎，清都非远，可惜鱼羽无定。桂花吹散广寒风，兔老蟾蜍冷。但唧唧饥虫啼暝，中心撩乱谁能整。坐待雄鸡三唱，飒飒霜髭，怕临青镜。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639590bdbd82411e8ffa','满江红','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"风淡寒轻，又还是、清明时节。几处处、莓苔铺绣，碎红堆缬。满树绿阴堪止渴，漫山黄雾松花发。背画阑、独立检韶华，闻啼鴂。\",\"怀往事，空凄切；思不断，肠千结。想繁华一瞬，夕阳明灭。玄武湖边杨柳月，鸡鸣埭上棠梨雪。到如今、何处觅遗踪，泉声咽。\"]',NULL,NULL,NULL,NULL),('e647d10f1632639596e2512a3eee425c','洞仙歌·春梦','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"蒙蒙漠漠，杳不知根蒂，荡漾春光去无际。盼神仙缥缈，烟蔼苍茫，身到处，惟有天风引袂。故人千万里，蓦地相逢，忘了关山路迢递。\",\"聚散未分明，两眩云酣，空回首、巳迷尘世。但拥被、追寻恍如遗。憯不觉、鸡鸣若华东启。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bf18fd05be','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"珠帘暮卷西山雨。离魂不散烟郊树。暝色赴春愁。行藏独倚楼。\",\"王绳回断绝。卧见云间月。悄悄夜寒生。月高风满城。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bf575992e4','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"洞房昨夜秋风起。江清露白芙蓉死。岂是远行时。与君生别离。\",\"千山鸟飞绝。隆想弥年月。思妇夜鸣砧。君听空外音。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bf72cc8787','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东边日出西边雨。孤帆夜发潇湘渚。雨浪细无声。烟斜月转明。\",\"清猿不可听。枫落吴江冷。更欲向芳洲。水深难急流。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bf76f48986','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东风吹绿瀛洲草。去年人到今年老。老去愿春迟。凋伤感所思。\",\"攀枝长太息。倚树疑无力。正是忆山时。愁容镜独知。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bf83a839b6','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"孤云目断苍梧野。无边落木萧萧下。兹岁巳蹉跎。其如芳草何。\",\"玉阶生白露。病骨伤幽素。梦好更寻难。相思锦帐寒。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bfd2077293','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东城江水西飞燕。黄姑织女时相见。见少别离多。浮生能几何。\",\"梧桐相待老。常恐秋风早。万恨在蛾眉。傍人那得知。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395980552bfd312740a','菩萨蛮·七首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"文窗绣户垂罗幕。江南绿水通朱阁。花髻玉珑璁。单衫杏子红。\",\"高楼悲独守。日色浓于酒。不敢怨春风。屏帏向月空。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395999d5a191dba2061','踏莎行·咏游丝','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"弱不胜烟，娇难著雨，如何绾得春光住。甫能振迅入云霄，又还旖旎随风去。\",\"高拂楼台，低黏花絮，如狂似醉无归处。黄蜂粉蝶漫轻盈，也应未敢窥芳树。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959b02ba6dc8687691','菩萨蛮','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"冰澌著树成云朵，林稍白月欹将堕。户下有啼乌，如悲良夜徂。\",\"兰芳销翠被，凄恻惊眠起，起坐待天明，飞霜入鬓清。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959b02ba6df7d40130','菩萨蛮','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"月华泛滟秋塘草，羁虫露泣芙蓉老。空馆夜闻砧，难为客子心。\",\"故乡何处在，云白天黏海，天海共苍茫，何时还故乡。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959b1fb37e66e617c1','惜馀春慢咏子规','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"陇水沉沙，巴猿咽雨。泪尽潇湘竹死。多情怨魄，何处飞来，声在万重云里。肠断行吟放臣，去国佳人，地遥天迩。悄空山，月冷风清，惟见野棠如绮。\",\"记向日、琼户珠帘，樱唇簧舌，吹商呵徵。朱颜尚在，十二阑干，回首不堪重倚。沧海桑田有时，海若未枯，愁应无巳。到明朝、赢得浮花满树，锦毇霞碎。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959b4b8ee26239ada9','玲珑四犯台州作','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白露点珠，明河生浪，秋光看又一半。翠衾知夜永，清梦冷孤馆。南楼数声过雁，西池桂花零乱。岁序如何，江山若此，赢得鬓霜满。\",\"伤心谩回愁眼，见蛩吟蔓草，萤度荒疃。泪随黄叶下，事逐浮云散。沧波滚滚东流去，问谁是、登楼王粲。菊绽篱边，赋归来恐晚。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959c70a6d81b3883f4','踏莎行','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"破暖轻风，做寒细雨，冥冥飞过空江去。客中无物可消愁，绿阴满院黄莺语。\",\"琴断朱弦，香销翠缕，连天碧草人何处。欲乘斜日望长安，伤心烟浪迷南浦。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959c70a6d84184188d','踏莎行','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨过山明，烟生川媚，佳人初把罗衫试。玉骢嘶入柳阴中，蒙蒙淡日蒸花气。\",\"帘幕低垂，阑干独倚，一池春浪鱼鳞起。暮寒寂寞掩重门，怕人问道归期未。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959c70a6d85384c21b','踏莎行','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"冷雨笼晨，凄风催晚，杜鹃哀怨黄莺懒。蛛丝柳絮总轻狂，可怜春去无人管。\",\"碧草连天，青山遮眼，侧身四望肠千断。愁如溪水暂时平，雨声一夜依然满。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959c70a6d895670d29','踏莎行','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"瓶水知秋，池荷怨晚，有人楼上吹清管。月明夜寂却堪听，可怜刚被风惊断。\",\"楚泽吟悲，槐根梦短，江山处处伤愁眼。欲凭青鸟寄殷勤，波涛无地蓬莱远。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959d1265dc434b9c94','浪淘沙·感事','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"天际草离离，鸿雁南归，冷烟凝恨锁斜晖。胡蝶不知身是梦，飞上寒枝。\",\"惆怅倚阑时，总是伤悲，绝怜红叶似芳菲。清露自凋枫自落，没个人知。\"]',NULL,NULL,NULL,NULL),('e647d10f163263959ef5ce70620da7b6','醉花阴','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"楼外斜阳低半树，虽好难留住。树里有鸣蝉，似诉中情，哽咽风前语。\",\"碧云不见蓬莱路，青鸟今何处。莫更倚阑干，愁上心头，无计推他去。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395a07b643e72eded98','兰陵王','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"问明月，何苦才圆便缺。青冥里，风惨露凄，长向人间照离别。玄阴凋暮节，瑶非冰凘暗结。阑干外，河汉渐低，星影辉辉动城阙。重裘冷如铁，看头上春云，都作飞雪。银釭花落铜壶咽。\",\"伤怀巳无柰，暝鸦多事，啼遍寒枝未肯歇。寸心自摧折。忉怛望天末，恨正抵沧江，烟浪空阔。文鱼翼短沉书札，泪滴在衣袂，尽成清血。羁愁恰似庭草，枯了又更茁。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395a51418ea56ab8b7e','隔浦莲','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"朱帘不卷昼雨，弱柳愁千缕。事与孤鸿去，空相忆，同谁语。云水迷翠羽，人何许，玉佩沉湘浦。\",\"悄凝伫，春风到处，白蘋香满洲渚。泪篁自老，目尽野田平楚。未用天涯怅间阻，回顾，斜阳犹在高树。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395a6fd75b08ef68ec5','长相思·晚兴','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"晚风清，晚云轻，雨意将成又不成，断霞天际明。\",\"旧恨盈，新恨生，立到楼头河汉倾，无人知此情。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395a956afbed4494b6a','长相思·嘉兴道中','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"草青青，麦青青，草穗高低麦穗平，黄花相间明。\",\"山禽鸣，水禽鸣，禽鸟与人同有情，不堪闻此声。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395a99230a3ad72104f','青门引·晚春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"数点松梢雨，还被晚风吹住。兔葵燕麦几家春，残花断柳，难记旧游处。\",\"斜阳满目空凝伫，鬓影三千缕。是他枝上，蜂蝶纷纷，带取韶华去。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395aa9b350fe43791f6','满庭芳·清明','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"积雨沉春，昏烟酣昼，不知还又清明。榆钱柳絮，飞逐斗轻盈？芳径莓苔渐满，青蘋与、白芷俱生。疏篱畔，海棠间竹，有个鹁鸠鸣。\",\"宽心，应是酒，酒冲愁阵，彊似奇兵。爱星星白发，知我生平。底用登楼看镜，误身世、只是虚名。空凝伫，落花如雪，云雾琐高城。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ab2172ccc25eff1d','虞美人·有感','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"秋来日日烟含雨，不肯收残暑。小桃错认是春回，尽把枝头红绿、向人开。\",\"白云流水青山远，门掩苍苔晚。草间蜂蝶未须忙，后夜云英和月、结成霜。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395acba6a11a042dc2f','卜算子·咏雨','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"搣搣度莲池，漠漠昏松陇。却过西园拂紫尘，柳媚花矜宠。\",\"泥润燕衣沾，粉腻蜂须重。极目沧江不见人，波浪兼天涌。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ae311e1d434705d4','水龙吟·次韵和陈均从吹箫曲','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"秦台人去无踪，空馀绿树连天表。瑶京路远，缑山夜永，离愁萦绕。露咽玄蝉，霜栖白鹤，月寒烟袅。想孤鸾舞罢，碧桃花尽，长是怨多欢少。\",\"梦里軿车过处，见苍梧、九峰青小。修篁染泪，沧江流恨，年年不了。广莫风悲，昭华玉冷，声沉云杪，又扶桑、日上靡蓱光散，蓬池春晓。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b084eda49ec42d50','贺新郎·愁思','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"碧秀天涯草。怨王孙、雕轮去远，王骢嘶老。风雨馀春无寻处，絮乱花飞梦杳。断魂与、行云萦绕。赤水珠沉迷象罔，暗尘深、不见长安道。今夜月，为谁好。\",\"他乡事事伤怀抱。更那堪、荒烟暝霭，晚虹斜照。千里关山劳回首，苔径残残红未扫。又恐怕、梧桐秋早。王母宫中青鸟使，想归来、巳近蓬莱岛。竹叶酒，且倾倒。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b1c382190f9163c9','花心动','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"烟雨池塘，盻花枝、垂垂向人如泣。燕子乍归，帘幕馀寒，语困画梁双立。晓光摇帐惊眠起，□□□、莺簧犹涩。断肠处、丝繁絮乱，子规声急。\",\"只恐芳时易戢，便不待秋风，粉销香浥。半掩绮窗，深锁重门，天静月华将入。素琴弹尽离凤调，凄风与、愁云交集。夜迢递、罗襟旧痕又湿。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b1c38219594ead72','花心动','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"墙下红葵，向斜阳、犹开那时颜色。半卷绣帘，独倚雕阑，泪眼为谁长滴。九疑烟雾连蓬岛，蜚鱼去、浑无踪迹。旧游处、空馀满地，绿苔堆积。\",\"百岁真如过客，待落蕊重荣，不知何夕。镜掩素尘，香歇罗衣，此恨怎生消得。夜凉月转梧桐影，青灯共、疏萤寥寂。正憔悴、饥虫又啼暗壁。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b2bc6588740fa6bc','金钱子五月五日作','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"云淡风微，江雨欲来还去。倚阑干、鬓霜千缕。白水青芜，是征鸿归处。望尽天涯，夕阳犹在深深树。\",\"艾叶榴花，又上阿谁门户。悄空梁、燕雏自语。王谢亭台，杳不知何许。独立苍茫，乱鸦啼、满城钟鼓。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b328835eb2ee9c26','归朝欢','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"紫燕成雏辞旧宇，红日满庭鸡唱午。披襟起溯北窗风，绿杨叶底闻莺语。故人音信阻，白蘋吹尽沧江雨。倚阑干，悠然独笑，暝色下平楚。\",\"浮生百岁真羁旅，富贵繁华萍梗聚。重瞳隆准等丘墟，紫髯桑盖俱尘土。坐来天欲暮，乌鸦立遍城头树。看云头，金盘涌出，身在广寒府。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b5a6e748e99250f6','浪淘沙·秋感','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"江上晚来风，烟蔼蒙蒙，白蘋吹尽到丹枫。流水落霞衰草外，离恨无穷。\",\"极目楚云东，愁见归鸿，拒霜相倚夕阳中。露重月寒芳意歇，知为谁红。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b5d5549be5ba51b6','生查子·惜花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"东风为爱花，著意吹原野。秾艳正堪怜，何忍轻吹谢。\",\"闷损玉楼人，独立花枝下。微睇欲双蛾，红泪和花洒。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b64f7163078aa29d','玉漏迟·咏雁','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"渺长空万里，西风动地，雁来无数。早发榆关，暮入楚江烟雨。逝水浮花去远，但满眼黄芦飞絮。肠断处，云深梦泽，月寒湘浦。\",\"楼上有个佳人，闷倚遍阑干，翠眉双聚。泪湿鸦鬟，化成素丝千缕。一望一回惆怅，又灯暗、网虫垂户。天路阻，谁知此情愁苦。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b7f1608980db63bc','瑞龙吟','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"秋光好，无柰锦帐香销，绣帏寒早。钩帘人立西风，送书过雁，依然又到。故乡杳，空把泪随江水，梦萦池草。何时赋得归来，倚松对柳，开尊醉倒。衰鬓不堪临镜，镜中愁见，蓬飞丝绕。门外远山青青，长带斜照。\",\"石泉涧月，辜负夜猿啸。伤心处，枫凋露渚，荷枯烟沼，燕去玄蝉老。满天细雨鸣羁鸟，花蔓当檐袅。庭院静，遥闻清砧声捣。拥衾背壁，一灯红小。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b88f1c0d4105c054','一剪梅','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"征雁来时木叶红，淡淡秋光，袅袅西风。江南江北短长亭，烟草低迷落照中。\",\"浮世生涯一转蓬，今日韶颜，明日衰翁。五丁难挽逝川回，千古英雄，此恨都同。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395b88f1c0d878d31ca','一剪梅','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"机杼无声络纬多，天净云收，月在银河。相思迢递隔关山，雁短人遥可柰何。\",\"身世悠悠似逝波，梦里嗟咨，镜里婆娑。欲回西日挂扶桑，惆怅阳公，不解挥戈。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395c2b4f4dc1953c6e6','南乡子·八咏楼作','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"鸣雨过庭除，病骨崚嶒不自扶。强倚阑干成小立，萧疏，立困依然作宰予。\",\"惊觉失华胥，楼外青山落照馀。谁似庄周能化蝶，蘧蘧，心上闲愁一点无。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395c69a825eb5caffd1','二郎神','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"卷帘邀月，渐夜永、凉生珍簟。看露出青莎，烟凝碧树，的皪残萤数点。老景逢秋兼多病，怎免得、长怀短念。辜负却故园，千山松桂，一池菱芡。\",\"门掩，寂寥满地，馀花红糁。眷翠被香收，玉觞尘涴，背壁兰缸自闪。马角不生，乌头难素，心似风旌长飐。休怪他、败叶寒螀相应，替人悽惨。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395c9621037c57a8669','摸鱼儿·金陵秋夜','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"正凄凉，月明孤馆，那堪征雁嘹唳。不知衰鬓能多少，还共柳丝同脆。朱户闭，有瑟瑟萧萧，落叶鸣莎砌。断魂不系，又何必殷勤，啼螀络纬，相伴夜迢递。樵渔事，天也和人较计，虚名枉误身世？\",\"流年衮衮长江逝，回首碧云无际。空引睇？但满眼、芙蓉黄菊伤心丽。风吹露洗。寂寞旧南朝，凭阑怀古，零泪在衣袂。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cc0de6041b4af3c3','江神子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"霏霏轻雨弄秋光，野烟苍，晚风扬。征雁将愁，分付与寒螀。窗外声声啼到晓，人不寐，夜何长。\",\"沧江波浪去茫茫，莫思量，使心伤。篱外黄花，只作旧时香。歌罢归来深对酒，今古事，总凄凉。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cc0de6049a98d802','江神子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春晴杨柳郁金丝，有黄鹂，在高枝。正是佳人，无语倚阑时。不理云鬟寻断梦，消瘦损，雪肤肌。\",\"海棠飘玉燕交飞，草芳菲，水沦漪。怕见轻蜂，小尾扑香归。满镜容颜凭酒染，还惨淡，对斜晖。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cc0de604ac1702bc','江神子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"城头吹角夜沉沉，有栖禽，出疏林。冲落满庭、珠露滴衣襟。身在吴江家在越，南北事，两关心。\",\"人生莫遣二毛侵，病相寻，老骎骎。谩好青山、绿水懒登临。千古钟期今巳矣，空惨怆，对瑶琴。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cc0de604c51f96c1','江神子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"丝丝纤雨织黄昏，掩衡门，黯消魂。赢得满头华发，照乾坤。天上神仙三岛外，空虎豹，守重阍。\",\"河林落日隐昆仑，望中原，杳漫漫。白苇青蒿，无处著啼猿。凤去不来箫管咽，心里事，共谁论。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd88205e4dd4','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"他乡别故人，拭泪攀杨柳。此物最相思，皎皎当窗牗。\",\"留取作秋香，出入君怀袖。万里故园心，天地同长久。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd882756f72b','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"青山欲暮时，暝色生寒树。不那客愁多，复送王孙去。\",\"涧户寂无人，暗谷疑风雨。耿耿夜何长，月落闻津鼓。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd88448a863f','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"开帘满地花，风起春城暮。悲妾似花身，暂向花间语。\",\"落叶不归林，惊鸟从横去。独立傍银床，点点回塘雨。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd88b42c9dbd','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"思君君未归，向夕千愁起。两泪忽成行，滴滴空阶里。\",\"垂柳覆金堤，蕙草生闲地。少壮莫轻年，白日如流水。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd88c1d9be91','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"新年旧燕归，似与羁魂语。不见帐中人，空见相思树。\",\"水上觅红县，片片随风去。薄暮欲如何，背泣西窗雨。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd88c975ab01','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春风能几时，柳色看犹浅。胡蝶梦中飞，迹与孤云远。\",\"明月照花枝，入夜愁难遣。形影自相怜，不及衔泥燕。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cceefd88fe4ed798','生查子·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"愁多梦不成，坐对银釭晓。轻袖拂花妆，惊起双栖鸟。\",\"出户独彷徨，烟里看鸿小。日下四山阴，转转伤怀抱。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740a082cdc92','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"满庭芳草易黄昏。摘索花枝料峭寒。坐见新池落旧痕。\",\"忆王孙。深院无人独倚门。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740a2fe8ff5b','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"远书归梦两悠悠。闲倚南窗尽日愁。手卷虾须上玉钩。\",\"泪难收。红叶相随何处流。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740a3353edc6','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"郎心如妾妾如郎。一度相思一断肠。尘满金炉不炷香。\",\"向斜阳。离恨空随江水长。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740a3369315d','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"乱山凝黛色高低。江雨霏霏江草齐。三户无人自乌啼。\",\"绿杨堤。笑杀山翁醉似泥。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740a769e3d72','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"当时我醉美人家。肯信流年鬓有华。海燕西飞白日斜。\",\"对残花。莫怨春风当自嗟。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740a9e7f02e5','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"花明柳暗绕天愁。赵女乘春上画楼。楼上黄昏欲望休。\",\"月如钩。槛外长江空自流。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740ab1dfd87b','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"水纹如谷燕差池。斜日悠扬在柳丝。故国平居有所思。\",\"倚阑时。却是同袍不得知。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740aba2ce0ac','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"情愁无意整花钿。万事伤心在目前。锦瑟无端五十弦。\",\"月明天。绣被焚香独自眠。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740abb8ad7f0','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"卢家少妇郁金堂。日日春光斗日光。勾引花核笑凭墙。\",\"映垂杨。不是愁人亦断肠。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740abe53d7c0','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"湿云如梦雨如尘。客舍青青柳色新。一片花飞减却春。\",\"劝行人。莫向天涯金绕身。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740ad9a84917','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"为谁零落为谁开。小白长红越女腮。渐老逢春能几回。\",\"看花回。一日须倾三百杯。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395cf66740aeb96d935','忆王孙·十二首集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"郁金苏合及都梁。不是金炉旧日香。懒对菱花晕晓妆。\",\"细思量。欲话因绿恐断肠。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d4b081e40a0c885a','苏幕遮','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白云山，红叶树，阅尽兴亡、一似朝还暮。多事夕阳芳草渡，潮落潮生，还送人来去。\",\"阮公途，杨子路，九折羊肠，曾把车轮误。记得寒芜嘶马处，翠管银筝，夜夜歌楼曙。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d4b081e44a024010','苏幕遮','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨潇潇，风袅袅，柔梦相牵，不觉春窗晓。雁到衡阳回去了，镜里离莺，独对琴台老。\",\"楚天长，秦树杳，山蹙愁眉，江学回肠绕。满地苍烟连碧草，弱水三千，人隔蓬莱岛。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d6127407b99ce718','好事近','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雨过却斜阳，虹外远山犹湿。独据槁梧高坐，符北窗风入。\",\"可人嘉树有清阴，蝉噪一何急。倦暑盼秋来，又恐芳时轻戢。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d65e24c05d437b3d','喜迁莺·残夜','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"画角断，篆杳清，斜月淡疏棂。露寒霜冷鹊频惊，归梦不曾成。\",\"湖上数声沙鸟，河汉渐低木杪。起看北斗待天明，云白日华生。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d7bac2800ad5f50c','蝶恋花·蒋山寺十月桃花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"度朔移来天上种，绛蕊丹跗，王母亲曾弄。青女素娥为侍从，婵娟独擅三千宠。\",\"回首欢娱谁与共，荒草残烟，冷落秦源洞。阆苑风高迷彩凤，断魂飞入韩凭梦。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d7d92007dc8ee980','梅花引','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"晚云凝，晚烟横，烟草茫茫云树平。杜鹃声，不堪听，别泪暗倾，良宵空月明。\",\"冰蚕丝断琅玕折，湘妃竹死青冥裂。短长亭，几千程。\",\"归计未成，愁随江水生。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d84c62bdcb3c2393','千秋岁·淡烟平楚','刘基','e647d10f163263957bb0607670feb62b','明','此词写离别之苦。\n上阕连用花、莺、芭蕉、杨柳、雨、相思树六种物事作渲染、衬托，以柳「丝」谐情「思」，并引进凄婉的爱情传说，表达双方情谊之深，抒发不忍离别的感情。「芭蕉心一寸，杨柳丝千缕」与「百杯桑落酒，三叠阳关句」两联警绝，把饯别时的景色场面与情感交融在一起。一、千、百、三等数量词反复出现，既表现出情绪的激烈，而又变化错落，灵动自如。\n下阕则将时间前拉后伸，见前欢不可再，后会亦无期，从而将这次离别的苦楚，放大到整个人生，整个今古。「百杯」句，即「劝君更进」之意，劝酒以表关切，「三叠」句，即吟诗或作诗以赠别。末尾二句言反复叙别，尚未尽达其情，月已出，潮亦生，船就要开走了。「月明潮上迷津渚」七字融叙事、写景、抒情于一体，寄情于景，以景结篇，而又遥应开头，颇为巧妙。','[\"淡烟平楚，又送王孙去。花有泪，莺无语。芭蕉心一寸，杨柳丝千缕。今夜雨，定应化作相思树。\",\"忆昔欢游处。触目成前古。良宵会，知何许。百杯桑落酒，三叠阳关句。情未了，月明潮上迷津渚。\"]','[\"清·陈亦峰《词则·别调集·卷三》：凄婉纤丽。\"]','平野上淡淡的烟雾，又送贵族公子而去。花儿仍在哭泣，黄莺寂然无语。芭蕉心缩卷不展，依依杨柳愁思千缕。今夜的雨，一定会化为那相思树，双目所过间，都成前尘故事。不知何时能再相会？饮下百杯桑落酒，高唱三叠阳关句，情还未了，渡口边的月已迷蒙了江水。','《千秋岁·淡烟平楚》为明代词人刘伯溫所著。此词描写了离别之苦，前欢不可再，後会亦无期，离别的苦楚放大到整个人生，整个今古。','[\"平楚：即平林。\",\"王孙：贵族公子。\",\"相思树：干令升《搜神记·韩凭妻》载，战国宋康王舍人韩凭妻何氏貌美，康王夺之，罚韩凭筑长城，不久，夫妻相继自杀。康王怒，使二人坟墓相望。隔夜间，两坟头皆长出梓木，旬日间粗可合抱，根交错，枝接杂宋人哀怜，称其木为相思树。\",\"前古：古代；往古。 汉·赵晔 《吴越春秋·勾践入臣外传》：「今大王诚赦越王，则功冠於五霸，名越於前古。」\",\"良会：美好的聚会。三国魏·曹子建《洛神赋》：「悼良会之永绝兮，哀一逝而异乡。」\",\"桑落酒：《水经·河水注》：「河东郡民有姓刘名堕者，宿搜工酿，采拒河流，酿成芳酎。悬食同枯枝之年，排于桑落之辰，故酒得其名矣。」\",\"三叠《阳关》：即《阳关曲》，以王右丞《送元二使安西》诗为辞，送别时唱。\",\"月明句：语本秦少游《踏莎行》：「雾失楼台，月迷津渡。」津诸，即津渡，渡口。 \"]'),('e647d10f16326395d85c7e6220220beb','锦堂春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"墙角含霜树静，楼头作雪云垂。钩帘鹊噪空庭晚，坐看月来时。\",\"异域书迷雁足，幽闺镜掩虫丝。一宵两地肠千转，惟有梦魂知。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395d85c7e628378dda9','锦堂春','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"鹊噪轻阴乍霁，鸠呜冷雨还来。不知春到多时了，浑未见花开。\",\"老景夕阳有限，华年逝水无回。相思一夜兰膏歇，清泪滴琼瑰。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395da5c07190352e4ab','调笑令·四首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"沙燕，沙燕，别去几时重见。汀洲芳草纤纤，细雨微风卷帘。\",\"帘卷，帘卷，人与碧梧俱晚。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395da5c071933c6e475','调笑令·四首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"红树，红树，妆点天涯秋暮。淡烟衰草黄昏，露冷啼螀断魂。\",\"魂断，魂断，明日玉台霜满。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395da5c0719cac495ae','调笑令·四首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"秋雨，秋雨，窗外白杨自语。梦回月在空廊，灯影朦胧夜凉。\",\"凉夜，凉夜，露冷芙蓉花谢。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395da5c0719cad35722','调笑令·四首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白露，白露，点我庭前芳树。树中有个栖鸦，啼到银蟾影斜。\",\"斜影，斜影，照见罗帏梦冷。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395dada40271282e0d3','渔父词五首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"朝辞蓼渚暮芦江，自有沙鸥伴醉翁。尘世里，一浮萍，不著羊裘动客星。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395dada4027151af0b4','渔父词五首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白鹭洲边好月明，赏心亭下暮潮平。红米饭，紫莼羹，自是无愁过一生。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395dada402729903712','渔父词五首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"钓得鳊鱼不卖钱，瓷瓯引满看青天。芳树下，夕阳边，睡觉芦花雪满船。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395dada402777fd443a','渔父词五首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"采石矶头煮酒香，长干桥畔柳阴凉。歌欸乃，濯沧浪，来往烟波送夕阳。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395dada4027fd090107','渔父词五首','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"江花江草雨悠悠，随意东西懒即休。黄犬恨，绿珠愁，定应不上钓鱼舟。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395db8a915ec197c726','过秦楼','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"暖日蒸红，浓烟烘黛，又过一年春半。鸾啼镜掩，桂老蟾孤，芳草为谁如剪。何处燕子飞来，入幕穿帘，似曾相见。问前朝王谢，风流安在，雪消冰泮。\",\"还记得、翠幄笼花，琼阑搭柳，鸣佩响和清管。圆光易缺，急景难追，回首梦迷天远，绿□青蘋是他、玉树亭台，金莲宫苑。且高歌对酒，趁取韶华未晚。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395dd6360ba4d9af4f8','霜叶飞七夕','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"鲤鱼风起，津梁断、盈盈一水难渡。藕花相向自成莲，谁道中心苦。又不觉、明星在户，鹊桥横跨黄姑渚。怕喜极悲生，似那日匆匆，再把欢笑辜负。\",\"堪恨桂阙姮娥，乘云骖雾，便踏龙尾先去。碧鸡啼罢凤楼寒，早漏声催鼓。盼油壁香车驾了，踟蹰欲觅频回顾。但暗滴、真珠落，教向人间，散成飞雨。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ddbef29050926f4e','浣溪沙·即景','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"语燕鸣鸠白昼长，黄蜂紫蝶草花香。沧江依旧绕斜阳。\",\"泛水浮萍随处满，舞风轻絮霎时狂。清和院宇麦秋凉。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ddbef290c909cd0b','浣溪沙·即景','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"云压斜晖照树黄，风生水榭绿阴凉。渴蛙得雨闹池塘。\",\"红焰射人榴火炫，青丝窣地柳条长。卷帘明月满回廊。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395de784639094849b2','菩萨蛮·集句','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"林中独酌邻家酒，门前学种先生柳。灌木自悲吟，风泉清道心。\",\"独行潭底影，蝉噪林逾静。渡口欲黄昏，残阳归掩门。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e015914e3cc0b906','浪淘沙·闺怨','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"白日堕林端，庭宇生寒，黄芦风急雁来难。阶下露珠眉下泪，相赛汍澜。\",\"寂寞掩重关，坐到更阑，月华虽好不堪看。旧恨新愁都有几，地窄天宽。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e3ff76a46368d629','祝英台近','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"问青青，河畔草，能得几时好？春去难留，镜掩鸾空老。凄凉深院黄昏，新愁陈恨，趁风雨、一齐来到。\",\"雾云杳，漫有千丈游丝，那堪系残照。罗幕寒生，哽咽子规叫。断肠只是缘愁，柔肠数寸，怎禁得、许多烦恼。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e3ff76a4bda6a68f','祝英台近','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"翠烟收，珠露下，星汉共潇洒？桂影徘徊，白雪粲檐瓦。故乡渺渺青山，茫茫绿水，又争柰、流年如泻。\",\"嫩凉夜，尚有萱草馀花，芳菲绕台榭。铜篆香残，绛蜡散轻灺。谩说花解忘忧，忧多花少，更不分、被风吹谢。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e429ae7920c660ea','如梦令','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"一抹斜阳沙觜，几点闲鸥草际，乌榜小渔舟，摇过半江秋水。风起，风起，棹入白苹花里。\"]',NULL,NULL,'残阳一抹，闲鸥几点，景极悠远淡雅；秋水荡舟，风起白蘋，更是风韵别具，令人神怡。此词短小精致，意境美，文辞亦美。堪称明词中之佳作。',NULL),('e647d10f16326395e429ae793757b1f1','如梦令','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"昨夜五更风雨，吹尽一汀红树。旷野寂无人，漠漠淡烟荒楚。\",\"日暮，日暮，谁与暝鸦为主。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e429ae799a51ee9d','如梦令','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"莺啄垂圆黄透，蜂抱碎芳红皱。睡起北窗风，浓绿满庭如酒。\",\"回首，回首，想见那人消瘦。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e5aed8f48cdaeb9d','尉迟杯·水仙花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"凌波步，怨赤鲤、不与传缄素。空将泪滴珠玑，脉脉含情无语。瑶台路永，环佩冷，江皋荻花雨。把清魂、化作孤英，满怀幽恨谁诉。\",\"长夜送月迎风，多应被、彤闱紫殿人妒。三岛鲸涛迷天地，欢会处、都成间阻。凄凉对、冰壶玉井，又还怕、祁寒凋翠羽。盼潇湘、凤杳篁枯，赏心惟有青女。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395e88cb595c539171b','菩萨蛮·越城晚眺','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"西风吹散云头雨，斜阳却照天边树。树色荡湖波，波光艳绮罗。\",\"征鸿何处起，点点残霞里。月上海门山，山河莽苍间。\"]',NULL,NULL,'词牌《菩萨蛮》下标明词名为「越城晚眺」，按习惯思维，此词该是刘基在绍兴所作。湘湖有古迹越王城，又叫越王台，诗词中为顺口押韵等原因，偶亦有称越城、越台的，李白在《送友人寻越中山水》诗中的「西陵绕越台」即指此；而作为越国古城，绍兴同样有相同名称的胜迹。但《诚意伯文集》中收入的这首词，确实为刘基在游萧山湘湖越王城时所填。\n秋风吹来，雨过天晴，大地明净，斜阳照远，可以看到远处天边的树林。青青的湖波荡漾着树影，粼粼波光闪亮，比有花纹的绮罗还要艳丽。渐渐消退的残霞里，有几点大鴈飞来。月亮从海口那边的山上升起，整个山河都沉没在没有边际的苍茫之间。这首小词写雨后黄昏到日出的景色变化，生动传神。','[\"波：一作「青」。\",\"征鸿：即「征鴈」。迁徙的鴈，多指秋天南飞的鴈。\",\"海门：指江水入海之口。\"]'),('e647d10f16326395e982ab6faad58eeb','更漏子·咏雁','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"塞门云，湘浦树，毕竟故乡何处。枫叶渚，蓼花矶，水深矰缴稀。\",\"蓬莱阙，扶桑岛，极日茫茫烟草。芦荻老，稻粱空，月明江上风。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ea508a4aced1ada1','花犯·秋夜','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"夜何其，星移漏转，凉蟾照无睡。宝筝弦断，金雁与青釭，相对憔悴。塞鸿过尽人千里。馀香怨绣被。但满眼、碧云红树，阑干空自倚。\",\"阶前暗蛩最殷勤，悽悽似向我、说他情意。莎露冷，相将到、晓霜开蕊。惟应有、素娥未老，曾几见、桑田成海水。任浪语、愁来堪遣，君看明镜里。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ec32cdde242892b7','临江仙·闺怨','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"织女机边如镜月，夜深长照空帏。玉笙簧冷凤孤飞。苔阶行遍，霜露剥裳衣。\",\"华表柱头荏白鹤，不知何日来归。故山今在故人稀。沉思似梦，垂涕掩朱扉。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ec9014706a2f193c','传言玉女·咏蝶','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"为问韩凭，何事化为胡蝶。妒风羞雨，一身轻似叶，园苑昼永，丽日晴烟相接。舞回柳眼，拍翻花颊。\",\"殢粉迷香，困酣犹自未惬。锦云深处，更愁浓露浥。何况素秋，枝上晓霜披鬣。只应无柰，翠销红厌。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395efa712cb5055a3ec','鹧鸪天·冬暖','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"窗外群蛙久不鸣，夜来忽听满池声。客心且喜逢冬暖，天意犹当放晚晴。\",\"尘劳事，莫关情，清风皓月共忘形。夭桃应妒芙蓉色，故发鲜花照眼明。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f02891a4d7148a34','采桑子','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"雁来不带天边信，莫上高楼。新月如钩。一度看来一度愁。\",\"人间无限伤心事，覆水难收。风叶飕飕。只是商量断送秋。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f137049bd13d45bf','阮郎归','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"寥寥庭馆暮寒时，四檐鸣雨垂。博山沉水袅游丝，玉釭花烬微。\",\"蟾影浅，漏声迟，愁多成梦稀。客行虽好不如归，有怀当语谁。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f29ff18e2d3e5727','点绛唇','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"云淡秋霄，夜寒月过轩窗囧。雁声相应。人语长廊静。\",\"欲寄离情，梦短天涯永。休临镜，舞鸾孤影，怕见菱花冷。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f29ff18ed7f2c97c','点绛唇','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"昨夜三更，井梧叶上闻鸣雨。梦魂空去。不到思量处。\",\"为问灯花，底向愁人吐。花无语，星河满树，白发三千缕。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f2e03f9343cafb1e','青门引','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"采采黄金蕊，遥见晚山横翠。重门深掩一庭风，沽来淡酒，能得几回醉。\",\"草黄云白鸿千里，落日寒烟起。相怜自有明月，照人肺腑清如水。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f32f182e0a78955b','浣溪沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"烟惨云黄天雨沙，叶疏枝冷叫栖鸦。峭风凝雾户琪花。\",\"澧浦空捐游子佩，河源不返客星槎。夜冷坐听鼓三挝。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f32f182e6eedfff9','浣溪沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"袅袅西风吹草黄，疏疏零雨泫花妆。惊寒鹦鹉唤人忙。\",\"雁影两行冲月落，虫声一部和更长。博山烟冷思茫茫。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f32f182e7eb4ec79','浣溪沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"不寐凄寒剥瘦肌，闻钟惊起揽裳衣。恰如久戌得归时。\",\"斜汉隔山牵月落，小禽出树掠霜飞。蒙蒙轻蔼晕朝晖。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f32f182e8b086042','浣溪沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"春半江城不见花，也无杨柳可藏鸦。青烟碧草到天涯。\",\"过雨郊原啼布谷，始雷池沼出鸣蛙。清明寒食客思家。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f32f182eadf529ed','浣溪沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"半亩荒园自看锄，雨中时复撷新蔬。不须弹铗叹无鱼。\",\"早息机心劳役少，懒闻世事往来疏。清风明月总赢馀。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f32f182ec63f3b61','浣溪沙','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"布谷催耕最可怜，声声只在绿杨边。夕阳江上雨馀天。\",\"满地蓬蒿无旧陌，几家桑柘有新烟。战场开尽是何年。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f4c799297d531676','水龙吟·夜闻铜瓶汤□作','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"玉釭开尽丹葩，画檐深宿蟾蜍影。门掩清宵，声来何处，堂空人静。如竹梳风，如荷过雨，如泉发井。向罗帏细细，如歌如语，还如暗虫相命。\",\"绣被沉薰正暖，梦云车、紫鹓双并。洞庭旷野，九韶齐奏，殷天笙磬。别有蛾眉，鼓琴弹瑟，江鸣山应，待翛然睡觉，炉存馀灺，鸡啼窗囧。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f5c33edf81ac8c06','南柯子·咏蓼花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"汀荇青丝尽，江莲白羽空。翠蕤丹粟炫芳丛，总把秋光管领属西风。\",\"艳敌秦川锦，鲜欺楚岸枫。鲤鱼却下水仙宫，肯放斜阳更向若华东。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395f7eca3250626854d','南柯子·送别','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"怆别沧江永，伤心落日曛。宝筝弦断雁离群，最苦愁浓酒淡不成醺。\",\"黄叶凄凉月，丹枫索漠云。此来此地却思君，但觉骊歌犹在耳中闻。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395fd3a37704654719a','浣溪沙·槿花','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"可怪西园木槿花，强将孤艳斗轻霞。不知门外夕阳斜。\",\"应有断魂随蛱蝶，岂无悠恨寄寒鸦。那堪横被绿苔遮。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395fe001e4bbc1df438','如梦令·题画','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"草际斜阳红委，林表晴岚绿靡。何许一渔舟，摇动半江秋水。\",\"风起，风起，擢入白蘋花里。\"]',NULL,NULL,NULL,NULL),('e647d10f16326395ff41499b8d4c3ade','忆旧游·闻砧','刘基','e647d10f163263957bb0607670feb62b','明',NULL,'[\"掩重门夜静，庭户幽幽，蓦地闻砧。杳不知何处，是云间过响，空外传音。故乡未卜归日，栖薄愧飞沉。漫对月兴怀，临风起叹，恻怆伤心。\",\"登高盼来雁，想此际孤帏，几度停针。想带绅宽减，向谁人借问，无计追寻。但将数点清泪，滴在紫罗襟。更倚遍朱阑，满头素丝应自今。\"]',NULL,NULL,NULL,NULL),('e647d10f1740306de0be7b16d11cca89','如梦令·九月晦日尚未得归','秦瀚先','e647d10f1740306dd1457b72d1457b72','明',NULL,'[\"亭外桂花香满，秋入芙蓉过半。衰柳不禁风，黄叶乱飘无限。\",\"目断，目断，愁望故乡偏远。\"]',NULL,NULL,NULL,NULL),('e647d10f1841cedaf29ff18e8ec64799','点绛唇','顿文','e647d10f1841cedad1457b72d1457b72','明',NULL,'[\"才长芭蕉，碧纱窗外风和雨。声声杜宇，不解留春住。\",\"欲问春光，此日归何处。春无语，乱红流去，两岸杨花絮。\"]',NULL,NULL,NULL,NULL),('e647d10f1855262232ad97e14d338179','渔家傲','陈琴溪','e647d10f18552622d1457b72d1457b72','明',NULL,'[\"荡漾明波晴昼永，淡烟疏柳湖边景。一叶水云深处艇，天未暝，片帆挂破残阳影。\",\"篷背落霞人半醒，溶溶千顷摇金镜。绣被还愁添暮冷，风不定，芦花飞满双吟鬓。\"]',NULL,NULL,NULL,NULL),('e647d10f1876e0f4937e9419b597b679','蝶恋花·春景','梁木公','e647d10f1876e0f4d1457b72d1457b72','明',NULL,'[\"野草含烟铺紫陌，海燕来时，芳径多凝碧。影月低枝渐渐侧，惜花微雨轻轻滴。\",\"静掩朱扉春院寂，芍药栏边，睡起娇无力。宿鸟不须寻比翼，有人独倚闲亭北。\"]',NULL,NULL,NULL,NULL),('e647d10f18a199b7778add2cc542e31c','河满子','金是瀛','e647d10f18a199b7d1457b72d1457b72','明',NULL,'[\"忆在玉窗金户，弯环晓月西斜。城角数声留不住，暗香携得还家。\",\"梁上双栖燕子，庭中一树梨花。\"]',NULL,NULL,NULL,NULL),('e647d10f1956be2e6475ef58e9c0e693','金缕曲·用陈同甫韵志感','高宇泰','e647d10f1956be2ed1457b72d1457b72','明',NULL,'[\"世事何堪说。叹而今、龙蛇浩劫，粘胶缠葛。试问百年能有几，渐看满头堆雪。早销尽、冲冠旧发。撒手急归茅屋底，且商量、好度闲风月。吾与也，点之瑟。\",\"从兹显晦行藏别。问余生、山樵野牧，尚堪作合。卖卜补锅无不可，剩有凌兢瘦骨。望前路、危冈高绝。一片雄心谁识取，倩笛声、吹彻仙家铁。应不免，石为裂。\"]',NULL,NULL,NULL,NULL),('e647d10f19eb5821317b7d418e61d87a','减字木兰花·邗江归思','范凤翼','e647d10f19eb5821d1457b72cc42acc8','明',NULL,'[\"晴云如絮，霎时飞入银河去。露洗遥空，廿四桥头一笛风。\",\"客窗无暑，片霎芳塘清晓雨。月冷邗沟，梦破狼峰绝顶秋。\"]',NULL,NULL,NULL,NULL),('e647d10f1a49f0da96bb6c50534a21b7','满庭芳·次东坡韵','钱士升','e647d10f1a49f0dad1457b72be3e9d3f','明',NULL,'[\"往事千端，闲愁万觞，世情无数嵯峨。电光石火，变态阅来多。坐见新亭洒泪，空回首、麦秀兴歌。方悟得，金羁脱去，细草落平坡。\",\"桃源何处是，忙呼渔艇，来往如梭。长消受，寒芦皓月银波。更喜苹香菱熟，驾舴艋、闲傍苍柯。何多事，羊裘五月，江上好披蓑。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d0f14d1f3e5047a5c','点绛唇·冬日怀乡','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"千尺京尘，斜阳没处西风弄。小楼夜永。铁马丁当动。\",\"衬贴愁怀，那更寒筹送。泪痕冻。天涯不了，遮断还乡梦。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d318080bb2b7334c4','念奴娇·同费钟石宗伯再和桂洲扈驾南巡','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"六飞南下，大河上、多少祯祥云物。紫气平明浮水面，向晚更穿奎璧。画舫盘龙，楼船彩凤，两岸沙如雪。开头捩柂，长年也是人杰。\",\"休夸碧汉银潢，天上人间，一派灵源发。界断华夷奔大海，岛屿烟波明灭。一曲一千，三门三级，惊起冠中发。圣人康济，太史大书年月。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d3b149e936ed20018','风入松·再和桂洲','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"绿窗午枕睡初酣。梦喜脱朝簪。觉来犹是长安客，垂杨里、空系归骖。恋主心依晓阙，思亲泪湿春衫。\",\"玉河流水碧于蓝。花外燕呢喃。新词谱就凭谁和，重封罢、亲手题缄。欲待天边鸣雁，秋风寄与江南。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d5090dfb360eb3d94','丑奴儿咏阁前芍药','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"问花底事花无语，倚遍南轩。香沁瑶娟。手撚高枝著眼看。\",\"养花天气阴晴半，云翳风喧。默默凭阑。弹指年光作转圜。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d5090dfb366200e96','丑奴儿咏阁前芍药','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"黄扉红药翻阶好，要赏娉婷。日暖风清。几阕新词按谱成。\",\"莫道春光留不住，尚赖啼莺。半醉半醒。起向花前缓步行。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d5090dfb3c1fc04f2','丑奴儿咏阁前芍药','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"白玉阑干红芍药，黄阁前轩。一朵婵娟。封罢词头仔细看。\",\"几番过了风和雨，避却尘喧。红紫将阑。开向钧天倚盖圜。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d5090dfb3d9e9e981','丑奴儿咏阁前芍药','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"红芍开向丝纶阁，袅袅停停。人比花清。倚槛挥毫九制成。\",\"不枉芳名呼近侍，绕殿流莺。槐蚁初醒。傍砌频经敕使行。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d54023706d1d4a2de','天仙子·咏雪','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"闹夜冰花轻不定。扑上窗儿如寄信。灞桥桥外望青山，高一阵。低一阵。霎时白了诗人鬓。\",\"是则是远近都难认。路入蓝关人懒进。青帘斜压酒垆空，描不尽。题不尽。平沙留取飞鸿印。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d56728207c88c933e','念奴娇·秋日怀乡再和介翁','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"秋风客里，消不尽、胸中许多长物。瘦骨棱层才一把，与病战成坚壁。欲造河车，旋营丹灶，赢得头如雪。寻山辟谷，算来他是豪杰。\",\"一生四海交游，浮沉聚散，长啸从风发。惟有钤山严少保，不共世情磨灭。藜阁相辉，木天同上，当日俱青发。绨袍恋恋，多应念我寒月。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d5becba1d8cb8976c','风入松·和桂洲内阁赏芍药用虞文靖公韵','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"绝胜国色带朝酣。偏称胆瓶簪。清严开向丝纶阁，容谁驻、墙外游骖。笑靥露匀胭粉，衣裳霞想罗衫。\",\"黄金屋角绣云蓝。乳燕学呢喃。试把一杯黄票酒，倚阑封、罢紫泥椷。一段太平气象，分明冀北燕南。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d60536291071a6534','青杏儿寒夜斋居','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"炉烬更拈香。小窗里、百结柔肠。天涯踏遍尘千里，仲宣楼远，正平刺没，薄行襄王。\",\"默默护新妆。堪怜处、花老酿蜂忙。万事转头供一笑，萧萧纸帐，圆圆灯晕，短短藤床。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d69e27afe253bd6f9','谒金门·送县侯曹孟辉行取进京','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"秋正好。一路西风吹早。柳条折尽青山小。咫尺长安道。\",\"此去蓬莱三岛。袖里常怀谏草。要看风神惊八表。动摇山岳倒。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d6e901fd7d8e97079','浪淘沙·寒夜斋居','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"寒漏夜迟迟。紧闭柴扉。破窗风透暗丝丝。半灭瓦灯书满案，独自支颐。\",\"惆怅少年时。没个人知。纸屏刚有影追随。滴滴泪痕弹不了，一把相思。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0d85b5d0bdf4d0b7d5','减字木兰花·新居雨后','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"疏窗小阁。银烛摇红帘影薄。院宇深沈。雨送新凉抵万金。\",\"休休蓦蓦。一任那知身是客。茗椀炉薰。云里更筹次第闻。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0da766de280cd136ed','江东竹枝词四首','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"黄浦湾湾东转头，吴淞江下碧如油。不用并州剪刀快，水晶帘下上西楼。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0da766de2867000787','江东竹枝词四首','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"滨口航船一字帮，棹歌和起自成腔。潮来上南潮落北，南到湖南北到江。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0da766de286de4070f','江东竹枝词四首','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"明月垂杨独树桥，桥西熟酒好良宵。红香细剥莺哥嘴，嫩白鲜羹玉面条。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0da766de28a3703453','江东竹枝词四首','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"二月春风满地铺，茅针芦笋一齐粗。海门东来春潮上，春水连潮漫白涂。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0da8ec5830bdf4bb49','念奴娇·叠韵寿桂洲','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"天下奇才，算蛟龙、终不是池中物。妙小文章惊海内，秀气降生东璧。八柱擎天，三长冠世，节操逾冰雪。神仙宰相，元来同是人杰。\",\"同朝瑞霭茏葱，蓬莱阆苑，青鸟翩翩发。报道桑田还变海，铜狄要看磨灭。赤暑全消，金风初动，进贤冠上发。况堪心事，一片长安秋月。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0daaab212ce4c853c1','念奴娇·秋日怀乡','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"大江东去，是吾家、一段画笥中物。襟带五湖吞百渎，说甚黄州赤壁。两岸芦汀，一湾柳浪，海涌潮头雪。苍浪声里，渔翁也是豪杰。\",\"明年准拟归来，轻舟短棹，两腋清风发。春水稳如天上坐，闲看浮沤兴灭。黄歇穿沙，袁嵩筑垒，到处堪睎发。鲈鱼莼菜，一任江天岁月。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0dc54405a5282a2e44','点绛唇·送县侯张八峰行取进京','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"县绕清江，桃花红处春波绿。画栏曲曲。风动帘纹蹙。\",\"镇日鸣琴，有个人如玉。烦推毂。鹤书高荐，选入黄金屋。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0dc96e9220a7e0bb32','念奴娇·与甬川钟石同宿杨园再次前腔','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"半途微雨，为洒净、滚滚风尘中物。一带青山如画里，咫尺层峦叠壁。吏部副卿，南宫少伯，清映如冰雪。白头老我，三十年前争杰。\",\"而今扈驾朝陵，白马银鞍，紫陌联翩发。寻得山庄劳下榻，共剪烛花明灭。香雾扑帘，茶烟隔座，相对频搔发。同胞异姓，真个浑忘岁月。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0dc977a06e32a3007a','蝶恋花·和道州周希旦','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"长安回首香尘杳。红日迢遥，偏爱无情恼。病鹘高风看去鸟。欲奋云霄无利爪。\",\"柳暗溪头荷叶小。淑景方浓，燕子雕梁绕。弹铗计程何日了。萋萋官路王孙草。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0dcb38ec4b5e6c9f4e','木兰花令·初夏即事','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"清明时候催花雨。人早是红英辞树。愔愔情思碧纱厨，睡起槐阴最深处。\",\"衷肠没个人堪语。屈指流年暗中数。无奈雕梁紫燕儿，春社飞来秋社去。\"]',NULL,NULL,NULL,'[\"木兰花令：实为《玉楼春》，错题为《木兰花令》乃为前人误刻所误导。按《花间集》载《木兰花》、《玉楼春》两调，其七字八句者为《玉楼春》体，《木兰花》则韦词、毛词、魏词共三体，从无与《玉楼春》同者。自《尊前集》误刻以后，宋词相沿，率多混填。\"]'),('e647d10f1a9c9f0dcb38ec4bbc3e0113','木兰花令·初夏即事','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"绿油拥盖张槐雨。繁英簇绛烧榴树。无限幽怀午梦馀，忆在江南水风处。\",\"家书题后教传语。新种鱼苗正堪数。荷钱竹粉更关心，恋著君恩未归去。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0dcee93c534734d0ab','念奴娇·和钱文通公小赤壁','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"江山阑入，想安排、真个有那神物。谁遣是非泥印定，天际一般赤壁。马援怀珠，燕人抱石，此意凭谁雪。曹刘成败，旁观输与豪杰。\",\"谩说吴国山川，陆家池馆，征雁和云发。紫岫丹崖高百尺，落日淡烟明灭。二俊才名，长公英迈，一夜俱华发。天公堪诉，照人惟有圆月。\"]',NULL,NULL,NULL,NULL),('e647d10f1a9c9f0dedca3ebf3ab6e9c3','风入松·再填前腔送桂洲','陆深','e647d10f1a9c9f0d78b9cab1f337d999','明',NULL,'[\"离筵高兴付馀酣。暂得解朝簪。路出齐封三宿昼，未须鞭、快著征骖。两岸山横画障，一川水腻罗衫。\",\"终朝不采满筐蓝。话别语喃喃。一把相思无处著，欲凭雁、足寄双椷。自是烟霄有待，怕飞不到江南。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cb007bfae834522e57','清平乐·元正','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"柳颦梅笑。别院东风早。凤吹隔云声缥缈。应是蓬莱仙岛。\",\"吾侪久已沉沦。持杯试祝东君。好趁阳和时节，螭头共听丝纶。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cb119f4d79d61ddfb8','南乡子·七夕嘲女牛','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"一叶井梧飘。玉宇凉生暑渐消。乞巧佳人传报道，今宵。牛女双星会鹊桥。\",\"银汉夜迢迢。风露无声湿翠翘。只恐明朝容易过，何劳。瓜果庭中远见招。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cb3a7f12232f79562c','蝶恋花·人九日','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"迅速光阴乌兔走。寂寞圜扉，两度逢重九。梦里登高还似旧。觉来不管愁眉绉。\",\"圣德仁恩覃九有。白发青衫，准拟蒙宽宥。归去高堂重献寿。金樽满泛黄花酒。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cb3da80fc2f304dab7','西江月·冬至','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"逗暖梅梢破玉，迎长葭琯飞灰。群阴消尽一阳回。云物今朝献瑞。\",\"自叹人生易老，那堪节序频催。东风次第送春来。又是一番光采。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cb79b725a078c7be49','临江仙·中秋','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"云净碧天秋似水，须臾飞上冰轮。山河倒影绝纤尘。婵娟千里共，景物一番新。\",\"老我此身犹是客，蟾宫桂子曾分。今宵见月倍伤神。嫦娥如解，事休照别离。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cb84dac374651c3a58','浪淘沙·七夕遇雨','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"牛女未成欢。别意相关。几回偷把泪珠弹。散作一天凉雨过，秋满人间。\",\"瓜果谩堆盘。夜漏将阑。佳人惆怅蹙春山。孤负穿针良会也，空倚阑干。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cbbc1a80069ea2a694','临江仙·元夕','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"忆昔帝城三五夜，簪缨曾侍宸游。山移鳌背对龙楼。一天星斗下，万文瑞光浮。\",\"箫鼓声中催赐饮，归来月在帘钩。而今白发叹淹留。寒窗灯影里，飞梦绕瀛洲。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cbce43568e19d40536','风入松·拟去妇词','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"落红万点委苍苔。春事半尘埃。满怀愁绪知多少，思量遍、无计安排。好似风中飞絮，时时拂去还来。\",\"当年鱼水正和谐。两意绝嫌猜。谁知命薄多乖阻，箫声远、零落天涯。破镜终期再合，梦魂长绕阳台。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cbde180595d36b451a','浣溪纱正旦代家人','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"何事东风太薄情。愁边忽忽换新正。柳眉梅靥为谁荣。\",\"宝胜羞看肠欲断，同心暗结泪先零。何时重整凤鸾盟。\"]',NULL,NULL,NULL,NULL),('e647d10f1b1287cbf721c2c3d3f3d2a6','鹊桥仙·除夕','黄淮','e647d10f1b1287cba223c6b32dffbc47','明',NULL,'[\"梅英送腊，椒盘迎瑞，争羡屠苏先酌。红炉画阁沸笙歌，谁解道、羁人萧索。\",\"光阴撚指，韶华过眼，万斛清愁如昨。明朝试说向东风，好为我、一齐吹却。\"]',NULL,NULL,NULL,NULL),('e647d10f1ccf350da4930450210c976c','字字双戍妇','陈翼飞','e647d10f1ccf350dd1457b72d1457b72','明',NULL,'[\"长城饮马嘶复嘶，古木寒雅栖复栖。空床独守啼复啼，从军荡子西复西。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0c7630822c7c080366','沁园春·雁','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"木落时来，花发时归，年又一年。记南楼望信，夕阳帘外，西窗惊梦，夜雨灯前。写月书斜，战霜阵整，横破潇湘万里天。风吹断，见两三低去，似落筝弦。\",\"相呼共宿寒烟。想只在、芦花浅水边。恨呜呜戍角，忽催飞起，悠悠渔火，长照愁眠。陇塞间关，江湖冷落，莫恋遗粮犹在田。须高举，教弋人空慕，云海茫然。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0c8cca83e01c856ec8','行香子·芙蓉','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"如此红妆，不见春光，向菊前、莲后才芳。雁来时节，寒浥罗裳。正一番风，一番雨，一番霜。\",\"兰舟不采，寂寞横塘，强相依、暮柳成行。湘江路远，吴苑池荒。恨月蒙蒙，人杳杳，水茫茫。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0ccbfe09423b0b99b7','竹枝歌六首','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"江水出峡过夔州，长流直到海东头。郎应若有思家日，应教江水复西流。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0ccbfe09425d216d9b','竹枝歌六首','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"踯躅花红鸭鸩飞，黄牛庙下见郎稀。大艑摊钱卖盐去，短钗簪叶负薪归。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0ccbfe094269832e70','竹枝歌六首','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"鱼复浦上石累累，恰似侬心无转回。船归莫道上滩恶，自牵百丈取郎来。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0ccbfe0942b15c306e','竹枝歌六首','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"枫林树树有猿啼，若个听来不惨凄。今夜郎舟宿何处，巴东不在定巴西。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0ccbfe0942f301612e','竹枝歌六首','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"蜀山消雪蜀江深，郎来妾去斗歌吟。峡中自古多情地，楚王神女在山阴。\"]',NULL,NULL,NULL,NULL),('e647d10f1e4c4f0ccbfe0942f3abc094','竹枝歌六首','高启','e647d10f1e4c4f0c3d779caee56b06c5','明',NULL,'[\"妾爱看花下渚宫，郎思沽酒醉临邛。春衣未织机中锦，只是长丝那得缝。\"]',NULL,NULL,NULL,NULL),('e647d10f1e58d3332c2336aceb746a79','柳梢青·修武道中','李濂','e647d10f1e58d3331baff70e2d69ed6c','明',NULL,'[\"烂漫春游，人生行乐，山水夷犹。昨夜河阳，今朝修武，明日怀州。\",\"平生雅兴难酬，信辔去、东风紫骝。问酒花村，题诗松寺，飞梦蓬丘。\"]',NULL,NULL,NULL,NULL),('e647d10f1e92f83c5a5c20f60def6ef0','蝶恋花·徐州晚泊','卢雍','e647d10f1e92f83cd1457b72d1457b72','明',NULL,'[\"野树烟生斜日堕，千里帆樯，又向黄昏过。自据胡床船上坐，东山月出浮云破。\",\"细细波流声绕舵，风苇萧萧，两岸明渔火。吹罢洞庭歌楚些，坡仙恍惚相逢我。\"]',NULL,NULL,NULL,NULL),('e647d10f1ecee4e59cf491a1bfeb7de6','忆王孙','赵燕','e647d10f1ecee4e5d1457b72d1457b72','明',NULL,'[\"东风恶劣打残红，帘幕轻阴漾碧空。倚薰笼，梦断香消宝鸭中。\"]',NULL,NULL,NULL,NULL),('e647d10f1ecee4e5b5bd736a3abc7d09','长相思·寄张幼子','赵燕','e647d10f1ecee4e5d1457b72d1457b72','明',NULL,'[\"去悠悠，意悠悠，水远山长无尽头。相思何日休。\",\"见春愁，对春羞，日日春江认去舟。含情空倚楼。\"]',NULL,NULL,NULL,NULL),('e647d10f202582e5faf66bdda64a2365','踏莎行·送友人还山中','赵迪','e647d10f202582e5d1457b72d1457b72','明',NULL,'[\"径转清溪，花飞红雨。武陵风景知何处。当年厌听水声寒，岂知忘却来时路。\",\"水绕人家，烟迷津树。晚峰江上青无数。幽人此别更多情，移家归向山中住。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e7155920c4','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"沙上短蒲绿，赤菱紫芡离披熟。越奴青发艾悬香，踏踏采莲塘上曲。\",\"游龙撇波海水飞，黄头伐鼓吹参差，少年摇曳澹忘归。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e73c0df748','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"沈寒凝海霜斑斑，芦鸿叫叫苍梧间。仙鸡警晓蟠桃乾，天鼓无声泽龙闲。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e73f4bef1e','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"花敛容，柳刷翠。扇薄欲裁纨，佩香思剪蕙。\",\"桐英寂寂沉寒波，粉绿猗猗日影多。六街烟花净如扫，千年古血生青草。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e73ff53fb5','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"清飙激素商，远客生归梦。茱萸委匣香，玉腕胜金重。\",\"幽芳垂晚径，汲绠霜华冻。洞庭木叶空，临水悲相送。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e7401f915c','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"轩辕玉管还初气，彩线迂回报长至。瑶花散落满寥天，万户千门夜明媚。\",\"南山幽翠净如削，内屋红炉春自若。璿玑回斡见天心，姬氏骑龙班岁朔。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e74494389b','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"玉露漙清虚，银云拥明月。空蒙白夜凉，远近商声发。\",\"桂子落秋香，寒娥能孤洁。谁家理素琴，调急弦应绝。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e749440132','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"北斗璿光射幽土，四气缘环若朝暮。壑底惊蛇挽不留，官街力兽驮寒去。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e74b33d8ea','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"皇天高，厚地裂，白日河庭泣鱼鳖。湘神抱瑟愁波涛，颓阳毕化稚阴结。\",\"焉知海上黄金阙，万丈丹梯到寥泬，乔松相携弄《白雪》。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e76829c068','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"嬴皇当极黄杨死，一寸霜皮生不起。峄阳老干青铜根，玉叶排秋十三子。\",\"郁华羿妃缓两辔，迟回二万八千二。春秋九十短长匀，青红白黑上下均，千秋万岁奉明君。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e78fd2a7cd','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"燕梁玄玉湿，蜂蜜花房晓。铜龙啮水微，翡翠含波小。\",\"石间藓蚀菖蒲根，古魂啾啾啼晓昏。红芳碧蕊乱殷殷，蛮女剪烟插翠云。\",\"游丝萦春悬锦绮，渚闲沙白鸳鸯喜。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e7f8dc4855','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"桐风刮帐无春温，赤精解绶佩金神。银浦桥成鹊济津，遥遥帝子度长云。\",\"野芳萋萋日杲杲，十万蛾眉镜中老。锦楼折竹夜迎仙，越秀吴都巧不眠。\"]',NULL,NULL,NULL,NULL),('e647d10f2028138cceb373e7fc2cf3d5','十二月乐章其二二月','李德','e647d10f2028138cd1457b72d1457b72','明',NULL,'[\"碧云屯午风熏熏，五弦曲奏玄霜纹。湘皋野龙拔新玉，拥鳞健角萧萧绿。\",\"蕙香小妓扇轻罗，半掩樱唇试新縠。金丸落落弦红脂，石池青钿溢碧漪。\"]',NULL,NULL,NULL,NULL),('e647d10f202a61e4de0395e089cf9194','醉花阴·雨夜','裘昌今','e647d10f202a61e4d1457b72d1457b72','明',NULL,'[\"窗前疏雨冷潇潇，银灯暗复挑。断肠清漏永，声声叶上，滴碎可怜宵。\",\"半闲衾枕转无聊，相思兴复饶。□□□□□，楚天人远，倩得梦儿娇。\"]',NULL,NULL,NULL,NULL),('e647d10f205466b8a2d534f67f9c7d42','减字木兰花','孟淑卿','e647d10f205466b8d1457b72d1457b72','明',NULL,'[\"月明如昼，料峭黄花寒越瘦。小立阑干，满砌花阴翠袖单。\",\"当年刘阮，重访天台人已远。怪道而今，眼底心头剩月明。\"]',NULL,NULL,NULL,NULL),('e647d10f229d1a87344a87114f5bec75','洞仙歌·题酒家壁','杨循吉','e647d10f229d1a874dcae38eaff0a6a4','明',NULL,'[\"吴郊春满，绿草熏南陌。风弄轻帘小桥侧。瞰荒垣、浓丽几树夭桃，仿佛似，薄醉西施颜色。\",\"酝香飘十里，更着流莺，乱掷金梭向林织。天宇净繁芳，日暖峰游，早拦住、高阳狂客。便典却、罗衫又何妨，算容易飞花、韶光难得。\"]',NULL,NULL,NULL,NULL),('e647d10f23cdd086154effbdc06344c0','浪淘沙','王泰际','e647d10f23cdd08683e8ef5164f1f27b','明',NULL,'[\"高阁掩春残，香冷衾单，满城桃李尽朱颜。自分与花同福命，长是心关。\",\"道远寄书难，盼煞平安，多因身在翠微间。归看双鸾妆镜里，一样春山。\"]',NULL,NULL,NULL,NULL),('e647d10f240d6b7af32f182e6cbf1a63','浣溪沙','张杞','e647d10f240d6b7ad1457b72d1457b72','明',NULL,'[\"苔草无人半入泥，妒风狂雨绿窗西。闲来梦断苦莺啼。\",\"蝶媚常黏飞絮浦，燕娇惯舞落花溪。晓天深夜冷幽闺。\"]',NULL,NULL,NULL,NULL),('e647d10f245e2e0752c5131443d77fe0','浣溪沙·春闺','郭辅畿','e647d10f245e2e07962e56a87437d136','明',NULL,'[\"海比相思尚有涯，轻寒蓦地恼情怀。羞看孤影镜慵揩。\",\"戏向海棠花下过，惹他珠露湿罗鞋。摘双梅子戴金钗。\"]',NULL,NULL,NULL,NULL),('e647d10f252ab2ef4c2d8abe5ea30d3e','巫山一段云','黄氏','e647d10f252ab2efd1457b72d1457b72','明',NULL,'[\"巫女朝朝艳，杨妃夜夜娇。行云无力困纤腰。媚眼晕春潮。\",\"阿母梳云髻，檀郎整翠翘。起来罗袜步兰苕。一饷又魂销。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c4929958a0fc5fb4566','百字令·刘邦彦招饮竹东馆赏桂花','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"竹东庄上，记当年春尽，归舟曾歇。莫雨和风催客去，猛地作时还辍。\",\"小馆茶香，行厨酒尽，执手难为别。归家南望，寸肠几度千结。\",\"谁道买棹重游，主人情重，樽俎依前设。桂树团香纤月露，渐近中秋时节。\",\"试问姮娥，今年才子，谁把高枝折。夜深无语，霏霏满地金屑。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c497434a36025ed2ed9','百字令·赠妓','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"紫薇楼下，记持觞，醉把名花相酹。爱尔玉兰名字好，况乃色香幽媚。\",\"娟净无瑕，清芬离俗，临去犹凝睇。时移事换，梦中频见佳丽。\",\"谁道买笑吴门，美人携手，俨与花相似。气味风标无两样，疑是花神游戏。\",\"娇啭歌喉，明珠一串，散落金盘脆。曲终凝望，碧云犹在天际。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c4977dddb2358a699f4','望江南阎尚温招饮湖中','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"船栊处，恰是藕花洲。别处歌声刚得借，去时春色那堪留。花落水空流。\",\"天渐晚，临去更回头。山翠远如眉黛晓，湖光疑似眼波秋。越女不胜愁。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c49a9aac034b3634801','解连环·送别','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"销魂时候，正落花成阵，可人分手。纵临别、重订佳期，恐软语无凭，盛欢难又。雨外春山，会人意、与眉交皱。望行舟渐隐，恨杀当年，手栽杨柳。\",\"别离事、人生常有。底何须为著，成个消瘦。但若是、两情长，便海角天涯，等是相守。潮水西流，肯寄我、鲤鱼双否。倘明年、来游灯市，为侬沽酒。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c49e1f0b8ffc02efaf7','临江仙·赠余浩','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"秋水芙蓉江上饮，怜渠无限风流，红牙低按小梁州。淡云拖急雨，依约见江楼。\",\"最是采莲人似玉，相逢并著莲舟，唱歌归去水悠悠。清砧孤馆夜，明月太湖秋。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c49ef61a35cc6e6d1da','浣溪沙·夏夕赏莲','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"水面风来晚更宜，酒香荷气水沉微。谁家长笛倚楼吹。\",\"五月梅花今夜落，千门梧叶未秋飞。不知零露湿人衣。\"]',NULL,NULL,NULL,NULL),('e647d10f25be6c49f32f182e07c8ece0','浣溪沙','史鉴','e647d10f25be6c497895fc131415db70','明',NULL,'[\"曲水流通濯锦红，新门移转纳香风。画舟时傍彩云中。\",\"半夜月明歌楚调，双莲波冷泣吴宫。鸳鸯惊散各西东。\"]',NULL,NULL,NULL,NULL),('e647d10f25ce5130f29ff18e60bb8649','点绛唇','吴元泰','e647d10f25ce5130b49c63fab49c63fa','明',NULL,'[\"流水行云，气清奇，将谁依附？烟云名声，留与幽人付。\",\"犬吠天空，鹤唳乘风去，难凭据，八仙何处，演卷从君顾。\"]',NULL,NULL,'这首词是《东游记》的开篇词。',NULL),('e647d10f25d7a7c53b2019ff83183964','行香子·溪上','钱士贲','e647d10f25d7a7c5d1457b72d1457b72','明',NULL,'[\"桃浪溶溶。丝袅晴空。喜盟鸥、独自携筇。春衣催浣，试付奚童。\",\"见苔痕染，酒痕渍，墨痕浓。水流云去，相看无尽，爱吾庐、近隔溪东。晚厨烟翠，归步从容。有柴桑柳，辋川杏，瀼西松。\"]',NULL,NULL,NULL,NULL),('e647d10f2609c050ec5a77297072ad64','点绛唇·寄吴夫人小畹','沈素嘉','e647d10f2609c050d1457b72d1457b72','明',NULL,'[\"隔过墙头，几番同听黄昏雨。别来情绪，向北看春树。\",\"一院藤花，底是临书处。还分取，绿窗朱户，袅袅茶烟缕。\"]',NULL,NULL,NULL,NULL),('e647d10f277fd9683d5dc132e133b8db','卖花声','朱灏','e647d10f277fd968d1457b72d1457b72','明',NULL,'[\"雾递烟邮，初放青螺当户。小池中、鸣蛙两部。花巢风扫，有松涛堪晤。伴伊尼、夕阳闲步。\",\"嫩凉羁叶，柳馆黄鹂常寓。款光阴、全凭酒瓠。一眉新月，在峰尖偷露。恨痴云、又如虫蠹。\"]',NULL,NULL,NULL,NULL),('e647d10f27ae9ece2715c3721885a35b','瑞鹧鸪','京师妓','e647d10f27ae9eced1457b72d1457b72','明',NULL,'[\"少年曾侍汉梁王，濯锦江边醉几场。拂石坐来衫袖冷，踏花归去马蹄香。\",\"当初酒盏宁辞满，今日闲愁不易当。暗想胜游还似梦，芙蓉城下水茫茫。\"]',NULL,NULL,NULL,NULL),('e647d10f28dba86674134ecd057a7bf9','忆少年·怨别','朱一是','e647d10f28dba866d1457b72d1457b72','明',NULL,'[\"沉沉永夜，阑干芍药，娇容沾雨。分明五更梦，也泪流如许。\",\"云水漫漫江北路，盼不到、客帆停处。鸳鸯渡边树，记郎从此去。\"]',NULL,NULL,NULL,NULL),('e647d10f28dba866c8d5d6518106b833','二郎神登燕子矶秋眺','朱一是','e647d10f28dba866d1457b72d1457b72','明',NULL,'[\"岷峨万里，见渺渺、水流东去。指远近关山，参差宫阙，起灭长空烟雾。南望沧溟天边影，辨不出、微茫尽处。叹三楚英雄，六朝王霸，消沉无数。\",\"从古。长江天堑，飞艎难渡。自玉树歌残，金莲舞罢，倏忽飞乌走免。燕子堂前，凤凰台畔，冷落丹枫白露。但坐看、狎鸥随浪，渔父扁舟朝暮。\"]',NULL,NULL,NULL,NULL),('e647d10f290cccb0ee754c585dce73a5','采莲曲','胡侍','e647d10f290cccb0e655c77118ead4c7','明',NULL,'[\"棹讴宛转发中川，队队红妆竞采莲。欲就前溪问名姓，莲花当住木兰船。\"]',NULL,NULL,NULL,NULL),('e647d10f29bb51327a59057ae537511c','满江红·次王昭仪驿壁韵','钱肃乐','e647d10f29bb5132495dabfd7437d136','明',NULL,'[\"长信宫中，留不住、一枝春色。何处觅、风台月榭，玉楼金阙。树底莺声和梦断，帘前燕翅迎风侧。恨红酣、绿醉也年年，今偏歇。\",\"红蜡尽，青蛾灭；隋堤柳，无人识。看胭脂泪落，玉壶凝血。青冢不生边地草，绿珠拚碎高楼月。愿千秋、留取驿中词，应无缺。\"]',NULL,NULL,NULL,NULL),('e647d10f2a6a95d32ee238bf5b17ae18','摸鱼儿','林鸿','e647d10f2a6a95d3d1457b72d1457b72','明',NULL,'[\"记虹桥、少年游冶，无限雨情云绪。金鞍几度归来晚，香靥笑迎朱户。肠断处，是半醉微醒，灯暗宵深语。闲愁几许！恰一似吴蚕，茧缠丝裹，撩乱万千缕。\",\"别离易，淡月乱鸦啼曙，泪痕红袖沾污。深怀遥想何年了，空寄锦囊佳句。春欲去，恨不把长绳，系日留春住。相思最苦！纵说不销魂，衷肠铁石，涕泗也如雨。\"]',NULL,NULL,NULL,NULL),('e647d10f2a784aef51989913dd09f0be','金明池秣陵怀古','殳丹生','e647d10f2a784aefd1457b72d1457b72','明',NULL,'[\"断雁西风，瘦驴黄叶，又是关门日暮。仙梵度、清凉山寺，对凤阙绛云留处。见长干、宝塔翚飞，总阅尽、天界茫茫尘土。任沽酒南冈，垆头人老，莫问少年归去。\",\"裙屐风流叹如许。便画面薰衣，隐囊挥麈。倚司马、长城却敌，空剩春灯燕语。再休言、六代繁华，与半壁乾坤，残棋孤注。想虎踞龙蟠，江山形势，都付冷烟疏雨。\"]',NULL,NULL,NULL,NULL),('e647d10f2af857070060a67894132523','洞仙歌·自寿娄江','顾潜','e647d10f2af857078b0d2689d63fbf8c','明',NULL,'[\"一碧动鲈鱼佳兴，浩荡鸥波放烟艇。过溪桥，十里香稻垂花，秋未晚，远渚芙蕖万柄。野翁能爱我，酌酒烹鸡，何处渔歌更堪听。\",\"醉起试推篷，骤雨初收。斜阳外、山光云影。愿百岁、逍遥瀼西东，任华发星星，换来青镜。\"]',NULL,NULL,NULL,NULL),('e647d10f2af857072eabdb0f4d682655','忆秦娥','顾潜','e647d10f2af857078b0d2689d63fbf8c','明',NULL,'[\"眉山蹙，多愁慵整搔头玉。搔头玉，觑他双凤，暗怜孤独。\",\"阖闾城外春波绿，兰舟载酒欢难续。欢难续，郎归何日，金钱偷卜。\"]',NULL,NULL,NULL,NULL),('e647d10f2af8570787fd27f935289dcf','醉春风','顾潜','e647d10f2af857078b0d2689d63fbf8c','明',NULL,'[\"紫燕归来两，云踪无处访。桃花开后海棠开，想、想、想。落日城边，断霞楼角，绿波兰桨。\",\"锦瑟萦尘网，香篆消罗幌。更阑酒醒梦难成，响、响、响。芳树敲风，空阶滴雨，倍添愁怅。\"]',NULL,NULL,NULL,NULL),('e647d10f2b3400563bce0ef8f688fd7d','潇湘逢故人慢','王秋英','e647d10f2b340056d1457b72d1457b72','明',NULL,'[\"春光将暮，见嫩柳拖烟，娇花染雾，顷刻间风雨。把堂上深恩，闺中遗事，赞火留饧，都付却、落花飞絮。\",\"又何心、挈榼提壶，斗草踏青盈路。\",\"子规啼，蝴蝶舞，遍南北山头、纸灰绿醑，奠一丘黄土。\",\"叹海角飘零，湘阴凄楚。无主泉扃，也难得、有情鸡黍。\",\"画角声、吹落梅花，又带离愁归去。\"]',NULL,NULL,NULL,NULL),('e647d10f2b4ed6b236cecf5c3c8b9bda','清平乐·题眭履道所画桃花送琴士蒯文达归山','程本立','e647d10f2b4ed6b2d1457b7228fc2782','明',NULL,'[\"山翁归去。玉洞花千树。拍手儿童花下路。惊动落花无数。\",\"山中红雨苍苔。人间白发黄埃。借问玄都道士，刘郎何日重来。\"]',NULL,NULL,NULL,NULL),('e647d10f2b8fb825120829712905f74c','阊门竹枝词','李孙宸','e647d10f2b8fb825d1457b72d1457b72','明',NULL,'[\"十五吴儿学弄潮，潮去潮来惯使篙。只为秦淮人不喜，非关风浪怕山高。\"]',NULL,NULL,NULL,NULL),('e647d10f2b8fb825120829713900aa1b','阊门竹枝词','李孙宸','e647d10f2b8fb825d1457b72d1457b72','明',NULL,'[\"昨夜阊门月晕重，今朝定有石尤风。我劝征帆好归去，风波且不到房栊。\"]',NULL,NULL,NULL,NULL),('e647d10f2b8fb82512082971799d2092','阊门竹枝词','李孙宸','e647d10f2b8fb825d1457b72d1457b72','明',NULL,'[\"千钱一箧买吴丝，摐锦桃花竞巧奇。辛苦不辞供杼轴，近来添索解官资。\"]',NULL,NULL,NULL,NULL),('e647d10f2b8fb82512082971a6eb5111','阊门竹枝词','李孙宸','e647d10f2b8fb825d1457b72d1457b72','明',NULL,'[\"谁家游冶紫骝缰，颇似狂夫旧骕骦。何事阊门朝暮过，又教侬误上楼望。\"]',NULL,NULL,NULL,NULL),('e647d10f2b8fb82512082971e2c4f32a','阊门竹枝词','李孙宸','e647d10f2b8fb825d1457b72d1457b72','明',NULL,'[\"生来蟢子解穿罗，生来吴童解打歌。旧曲新腔随意变，枫桥日日画船过。\"]',NULL,NULL,NULL,NULL),('e647d10f2b8fb82512082971edef5016','阊门竹枝词','李孙宸','e647d10f2b8fb825d1457b72d1457b72','明',NULL,'[\"鸦青袷子鹅黄衫，门外买花门内簪。风光非独侬为惜，归船两两又三三。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b1c8851f7','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"园中高树鸟分窠，门外小池钱贴荷。晓散乌鸦千点细，晚归白路一行多。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b42c8dcb8','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"雨过高田水落沟，瓦桥鱼上柳梢头。梅子青酸盐似雪，樱桃红熟酒如油。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b53090446','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"青黄梅气暖凉天，红白花开正种田。燕子巢边泥带水，鹁鸪声里雨如烟。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b659c7d32','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"乌桕红红生稚叶，紫兰茁茁吐新苗。龙须绿折风前笋，凤尾青添雨后蕉。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b688dacae','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"东村西村姑恶啼，家家麦熟黄云齐。春蚕作茧桑园绿，睡起日斜闻竹鸡。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b6ef2c186','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"麦叶蛏肥客可餐，楝花鲚熟子盈盘。家家芃磨声初发，四月江村有薄寒。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7b805b381e','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"门前竹大笋成笆，江上潮来草没沙。村童探緌绿杨树，野艇捞鱼紫楝花。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7bb5c580ad','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"田小三郎唱得工，七姊妹花开欲红。林静三更鹧鸪月，溪腥一阵鸬鹚风。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7bf2094331','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"呼雏逐妇总堪怜，时雨时晴各一天。厨割小鲜来海市，菜添新馔出江田。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e261ff2db7bf8010ee4','萧皋别业竹枝词十首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"越江春水绿如罗，双女祠前发棹歌。大宅北郊横鲍守，野桥南渡接陈婆。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e26f4bfbe5611e35ed1','采莲曲六首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"少女明妆出采莲，双头并蒂独心怜。不知金钏何时堕，空手来归意惘然。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e26f4bfbe561387617a','采莲曲六首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"荷叶莲枝水面齐，采花归去夕阳低。绿芜一道分南北，犹有歌声绕大堤。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e26f4bfbe561dce8529','采莲曲六首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"妾在江东郎在西，采香曾到若耶溪。同行女伴低头说，泪染新红白绩衣。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e26f4bfbe568df0e28b','采莲曲六首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"生长江南惯采莲，棹歌声里斗婵娟。十三十四年相亚，覆额低眉各可怜。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e26f4bfbe56a66741cf','采莲曲六首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"白面红妆二八春，藏羞不肯过东邻。偶从绿水桥边去，谁道荷花妒杀人。\"]',NULL,NULL,NULL,NULL),('e647d10f2bb95e26f4bfbe56b3491c3d','采莲曲六首','沈明臣','e647d10f2bb95e2642d6c7d6309fee4e','明',NULL,'[\"月照波纹似鸭头，一船双桨荡中流。采莲不道罗裙湿，归晒雕栏夜不收。\"]',NULL,NULL,NULL,NULL),('e647d10f2bcea276e2a5f4eb294ee98b','千秋岁','徐有贞','e647d10f2bcea27646771d1fdaa96d96','明',NULL,'[\"引风搅柳丝，雨揉花缬，蚤过了清明时节。新来燕子语何多，老去莺花飞未歇。秋千院，蹴鞠场，人踪绝。\",\"踏青拾翠都休说，是谁马走章台雪，是谁箫弄秦楼月。从前已是无情绪，可奈而今更离别。一回头，人千里，肠百结。\"]',NULL,NULL,NULL,NULL),('e647d10f2bf41656415d41bd0435c53d','谒金门','李端卿','e647d10f2bf41656d1457b72d1457b72','明',NULL,'[\"风乍暖，树底莺声欲啭。窗外小梅香数点，枝头春意浅。\",\"尽日画屏独掩，闲却绣床金剪。宝枕初回香梦远，乱愁天不管。\"]',NULL,NULL,NULL,NULL),('e647d10f2c2c4cb4461ef6b41e74e1b6','黄冈竹枝词','黄锦','e647d10f2c2c4cb4452bf2089d268236','明',NULL,'[\"北郭冈旁马鬣茔，何年春雨绿芜生。可怜翁仲龙钟老，却放蛴螬背上行。\"]',NULL,NULL,NULL,NULL),('e647d10f2c2c4cb4461ef6b4214c7c4f','黄冈竹枝词','黄锦','e647d10f2c2c4cb4452bf2089d268236','明',NULL,'[\"瓮城东望势崔嵬，万顷银波翠嶂开。九月蜑舟收内港，潮婆风报澳南来。\"]',NULL,NULL,NULL,NULL),('e647d10f2c2c4cb4461ef6b4278f3583','黄冈竹枝词','黄锦','e647d10f2c2c4cb4452bf2089d268236','明',NULL,'[\"石壁山头峰插天，石壁山下草如烟。半腰白鹭飞不去，道是岩边漱玉泉。\"]',NULL,NULL,NULL,NULL),('e647d10f2c2c4cb4461ef6b4c2410645','黄冈竹枝词','黄锦','e647d10f2c2c4cb4452bf2089d268236','明',NULL,'[\"黄鱼紫蟹错登筵，丹杮红柑颗一钱。收却落花涂豆子，不劳东作向春田。\"]',NULL,NULL,NULL,NULL),('e647d10f2c424bf05629d7e5a2f165cc','蝶恋花·忙处抛人闲处住','汤显祖','e647d10f2c424bf06b8eba43962e56a8','明',NULL,'[\"忙处抛人闲处住。百计思量，没个为欢处。白日消磨肠断句，世间只有情难诉。\",\"玉茗堂前朝复暮，红烛迎人，俊得江山助。但是相思莫相负，牡丹亭上三生路。\"]',NULL,'离开繁忙的官场来到清闲的地方居住。百般思量，没有一个能让自己心情欢畅的去处。白天看书时细细品味那些哀怨断肠的句子，觉得人世间只有情最难诉说。\n在玉茗堂看书写文章，不觉已从早上到了红烛迎人的傍晚。这里优美的景色使我的文章也为之生色。只要相思不相负，定能迎来牡丹亭上的再世姻缘。',NULL,'[\"忙处抛人：指离开繁忙的官场。\",\"闲处：指闲散的地方。《牡丹亭》在一五九八年完成。汤显祖在这一年罢职回到临川。\",\"世间只有情难诉：《全唐诗··卷十·顾况·〈送李侍御往吴兴〉》：\\u201C世间只有情难说。\\u201D\",\"玉茗堂：汤显祖为自己的住所取的名称，以玉茗（白山茶）花而得名。\",\"红烛迎人：《全唐诗·卷九·韩羽·〈赠李翼〉》：\\u201C楼前红烛夜迎人。\\u201D\",\"俊得江山助：江山之美使我的文章为之生色。俊，相当于今口语的\\u201C美\\u201D，此指文章的秀美。《文心雕龙·卷十·物色》：\\u201C然屈平所以能洞鉴风骚之情者，抑亦江山之助乎！\\u201D \",\"但是：只要。 \",\"牡丹亭上三生路：牡丹亭是约定再世姻缘的地方。传说李源与惠林寺僧圆观（一作圆泽）有很深的友谊。圆观临死时对他的友人李源说，十二年後和他在杭州天竺寺外再见。後来李源如期到那里，看见一个牧童，他就是圆观的後向。（见《太平广记·卷三八七·圆观》）。三生石在杭州灵隐，即李源会见牧童的地方。此指杜丽娘死而復生与柳梦梅团圆的爱情故事。\"]'),('e647d10f2c424bf0d6127407bddb3793','好事近','汤显祖','e647d10f2c424bf06b8eba43962e56a8','明',NULL,'[\"帘外雨丝丝，浅恨轻愁碎滴。玉骨近来添瘦，趁相思无力。\",\"小虫机杼隐秋窗，黯淡烟纱碧。落尽红灰池面，又西风吹急。\"]',NULL,NULL,NULL,NULL),('e647d10f2c424bf0f137049bed6da7aa','阮郎归','汤显祖','e647d10f2c424bf06b8eba43962e56a8','明',NULL,'[\"不经人事意相关，牡丹亭梦残。断肠春色在眉弯，倩谁临远山。\",\"排恨叠，怯衣单，花枝红泪弹。蜀妆晴雨画来难，高唐云影间。\"]',NULL,NULL,NULL,NULL),('e647d10f2c6e347a4ca3105d54553602','卜算子·题凭几图','陈铎','e647d10f2c6e347ad1457b72d1457b72','明',NULL,'[\"病影有灯知，心事无人问。上苑春光过二分，应少同游分。\",\"画障且相隈，曲几聊孤隐。窗外红梅开已齐，似识香闺闷。\"]',NULL,NULL,NULL,NULL),('e647d10f2c6e347af32f182eb1d0d0f0','浣溪沙','陈铎','e647d10f2c6e347ad1457b72d1457b72','明',NULL,'[\"波映横塘柳映桥，冷烟疏雨暗亭皋。春城风景胜江郊。\",\"花蕊暗随蜂作蜜，溪云还伴鹤归巢。草堂新竹两三梢。\"]',NULL,NULL,NULL,NULL),('e647d10f2d11ff9037b81576ecbab54a',' 蝶恋花·镜湖帆影','张岱','e647d10f2d11ff9087ec2f45e6384711','明',NULL,'[\"山似芙蓉青百叠，隔住林峦，穿度轻如蝶。树底疏疏时闪灭，依稀深浅湘裙摺。\",\"伫立高冈随宛折，剡水归帆，犹带山阴雪。遮在人家林外堞，墙头又露他山缺。\"]',NULL,NULL,NULL,NULL),('e647d10f2d8b2ec86a5cbbf7a52a22dc','浣溪沙·题情','张仲立','e647d10f2d8b2ec8d1457b72d1457b72','明',NULL,'[\"浅束深妆最可怜，明眸玉立更娟娟。撩人多在夜灯前。\",\"旖旎海棠初着雨，飘萧江柳乍含烟。倚窗一样受春妍。\"]',NULL,NULL,NULL,NULL),('e647d10f2d970d7083cfc9c2be3ac58e','竹枝词','岳正','e647d10f2d970d70309928d4daa96d96','明',NULL,'[\"郎从月下看吴钩，妾向郎前问所由，闻道敌人今未灭，将欲西去觅封侯。\"]',NULL,NULL,NULL,NULL),('e647d10f2e1e6567211f34653589abb7','蝶恋花·暮春','贺裳','e647d10f2e1e6567d1457b72d1457b72','明',NULL,'[\"薄暮银塘风色静，闲倚雕阑，自赏娉婷影。一簇芙蓉相掩映，唾花落处游鳞竞。\",\"女伴潜呼浑未醒，横睇回波，才讶红妆并。飞尽残霞天又暝，柳梢笑指新悬镜。\"]',NULL,NULL,NULL,NULL),('e647d10f2e1e65677c4c4cae1e6dcb1a','渔家傲·晓别','贺裳','e647d10f2e1e6567d1457b72d1457b72','明',NULL,'[\"啼罢荒鸡衰雁接，荧荧寒焰窗犹黑。欲别重将酥手执，行又立，门前瘦马嘶残月。\",\"递得金鞭红袖湿，回头已被疏林隔。独倚高楼看去辙，铃音绝，纤腰凭久雕阑热。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076200f547f5a06c456f','苏武慢悟俗，用虞邵庵韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"世事如棋，人情似纸，弄巧几多成拙。青春易过，白发无情，转眼光阴消歇。乐在其中，心游物外，此理几人明彻。荡虚空、无去无来，惟有清风明月。\",\"谁似我、剑走青蛇，发冲乌帽，长啸一声天裂。胁生八翼，足跨三天，历遍广寒宫阙。衣剪云霞，佩悬星斗，容我玉清朝谒。挟飞仙、下视尘寰，红日片时残雪。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076201f27cbfeb8d85df','酹江月题陈都宪希冉双玉合藏卷','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"白杨风雨，算天涯、过了几番寒食。谁信客床胡蝶梦，竟做游魂羁魄。杜宇难归，薤歌谁举，云渺稽山窟。攒封无主，土花凝满苍碧。\",\"早是天道非亏，仁人有后，兰玉孙枝出。袍笏不施鳖石拜，来访苍梧荆棘。剑合龙津，鹤归华表，重瘗兰亭墨。空山皎月，物华光射双璧。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762028c400943d46211','清平乐·游春，用宋王介甫韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"香车隘住。笑揭红帘语。浅蹴芳尘罗袜污。堤上未干花雨。\",\"画船载得琵琶。醉中情思无涯。不管湖烟湖水，东风取意飞花。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762034a1a4e398972a4','满庭芳·陶潜抚孤松圃','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"短杖东皋，浊醪西舍，吾生乐在田园。良苗秀雨，生意蔼晴川。长日柴扃不搜，浓阴散、柳外榆前。山林好，意行无阻，琴在也无弦。\",\"为生消几许，区区五斗，未当三餐。算折腰非易，解印非难。落落长松共晚，尘埃事、有梦谁关。无心处，闲云自出，孤鸟自飞还。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076205d6d7d83dad1390','风入松·海天一览','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"乾坤空断海天秋。远水际天浮。望中一发青山小，云涛涌、万里归舟。明月双凫渺渺，西风两鬓飕飕。\",\"布袍长剑走神州。三醉岳阳楼。水晶宫里骑鲸去，星河动、仙泒回流。铁笛叫开闾阖，佩环飞下瀛洲。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076207a15da4f3e485ee','满庭芳·春宵会饮','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"锦障芙蓉，绣帘翡翠，画堂围护春风。宝钗金钏，高会尽仙容。况遇良辰美景，香罗解、乐事无穷。歌讴处，春红照眼，人在万花中。\",\"堪嗟尘世客，天涯飞絮，海角飘蓬。又何须，计较得丧穷通。俯仰今来古往，寻思起、春梦一场空。争如且，开怀畅饮，沉醉倚芳丛。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076208069649b9ede931','酹江月桂岩','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"素空霁雨，耸芙蓉千仞，苍岩高出。凉浴山河风露下，冷浸一枝秋碧。芳影婆娑，天香缥缈，散满诗书室。有人吟讽，恍然身世清特。\",\"闻道绝顶凌霄，梯云有路，相近蟾蜍窟。玉宇琼楼盈尺五，寒气逼人毛骨。金粟侵衣，檀薰着酒，引上登仙屐。嫦娥微笑，手中掷下三色。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707620abab3f9443892fb','摸鱼儿·和晁无咎','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"遍江南、绿苕芳草，春光又回南浦。溪堂夜月无人共，时有钓船移聚。风静处。眼送他、一湾碧玉从东注。村歌带舞。任白眼看天，玉山倒地，鸥鸟自来去。\",\"青鞋底，且向山林稳步。风花勘破虚误。桔槔智巧终何用，抱瓮不妨农圃。还试觑。满江湖、风尘澒洞今如许。身心自语。归得便须归，杜鹃声里，斜日灞陵暮。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707620b046a348d37249d','水调歌头·题扇','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"功名大槐梦，身世钓鱼舟。归来霅川苕水，聊当五湖游。谁计一丝高洁，自喜半生清旷，瓦枕对眠鸥。回首云台事，一笑正堪休。\",\"桃花岸，蘋花渚，荻花洲。蓑衣任君高挂，安用搭羊裘。拍面溪风山雨，满棹江云海月，浊酒注磁瓯。醉后不知处，江上数峰秋。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707620c0adc23b1f71de5','瑞龙吟·春情，和宋周美成韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"天涯路。多少障梦云山，碍眸烟树。蘼芜绿暗春江，断肠流水，双鱼何处。悄凝伫。曾记年时巷陌，绣帘朱户。称时整整新妆，嫣妍花貌，清泠莺语。缥缈凤箫吹断，彩云千里，夜深院落。惟有明月窥窗，照人如故。\",\"红笺小叠。谩写相思句。无因见、风前月底，盈盈仙步。春与啼鹃去。夕阳照出伤离绪。丝柳愁千缕。芳思乱，嫔纷落红如雨。晚风无主，一庭飞絮。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707620d9f3d3ce17324f0','水调歌头·题杏园春色卷','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"恩袍草色动，仙籍桂香浮。三层暖浪，禹门一跃过鳌头。金殿彩云锦覆，玉陛欢声雷动，胪唱出龙楼。仙侣三千辈，平步上瀛州。\",\"马如龙，车似水，恣遨游。曲江春酒，杏花十里锦云稠。看取春风得意，渐入扬州佳境，纤手上帘钩。总是书生事，回首十年秋。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707620dd613b1c07cba38','洞仙歌·庄子观泉','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"飞流直下，泻长空如练。溅沫寒崖玉珠乱。转山腰、万点苍雪侵林，林影外，一带白虹初断。\",\"道人疏懒久，散发披襟，石磴苔茵坐来惯。适意总忘言，斜日双瞳，寒光耀、目花生眩。待横空，一剑上青冥，看飞渡、银河灵源清浅。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076211a784ea5abdccb8','一剪梅·扇梅','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"斜绾香云倚镜台。春已输梅。人欲欺梅。护寒帘蟆手初开。翠羽飞来。白雪飘来。\",\"冷月寒云到玉阶。画角声哀。玉笛腔哀。春幡笑语遍城隈。传道阳回。喜是郎回。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621368c91416f4a962','临江仙','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"落尽樱桃春事减，小园蜂蝶依依。子规啼罢月沉西。曲屏向晓，残烛泪空垂。\",\"楼外绿芜千里道，长亭过骑频嘶。一春无信慰相思。重门寂寞，风雨过花时。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621476df13d2d79c8a','浣溪纱南屏晚钟','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"湖上群山紫翠重。归鸦蓦过夕阳春。南屏烟外一声钟。\",\"瞑色尽随花担返，春游忽逐水云空。明朝车马又匆匆。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076216f0954a70ed8c19','齐天乐·寿吕太守正月二十四日也','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"湖山千里棠阴路。春光又回官柳。花市欢声，茅檐暖气，民俗正歌安阜。收灯未久。转万点红毬，化成南斗。瑞映阶符，寿星现在五云首。\",\"涵藏盛推山薮，尽肉羹翻污，车裀唾呕。霖雨功深，星辰地近，槐棘伫登三九。金章到手。对万本梅花，百壶春酒。醉倚东风，年年开笑口。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762173cf47cc0e82a92','渔家傲·渔舟','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"雨笠烟蓑披带惯。暮归晓出时相伴。村酒醉来无客唤。舟不缆。任风吹着芦花岸。\",\"春水鳜鱼长尺半。秋风菰米浑家饭。为问云台兴甚汉。闲中看。海云江月无边畔。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621ab49895840bc351','蝶恋花·春暮有作','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"燕子归迟春去早。送尽东风，可惜杨花老。人道春来情绪好。争知春去添愁恼。\",\"花节莺期都过了。梅子初肥，却是樱桃小。一片绿阴楼外绕，关山在望征人杳。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621cdbb3caa1876089','翠湘风夏夜','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"轻雷送雨过池塘。烦蒸顿解，柳外水亭凉。手弄冰销新浴起，半堕钗凰。风里芰荷香满，凭栏十里湖光。\",\"流苏垂下绛厨张。水晶枕底，纹簟展清湘。携手月中人似玉，懒上牙床。起向闲阶伫立，紫薇影转回廊。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621d56e4087940d79f','踏莎行·苏堤春晓','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"花露薄红，柳烟迷翠。乱山葱茜非人世。早莺啼送六桥春，绮窗残月人初起。\",\"城錀才开，曙光渐退。湖船尚在停篙处。东风满道湿芳泥，夜来堤上催花雨。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621d72d7cc6526f39d','踏莎行·九莺图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"秋逼银塘，凉生玉井。莲衣破裂西风猛。白蘋深处晚归来，汀洲一片山阴景。\",\"几点云轻，数声烟暝。联拳啄碎澄波影。梦魂清断夜三更，一江明月芦花冷。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707621eae2d7048cb314d','卜算子·冬晚山庄','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"熟水沸铜瓶，向晚书帏静。不见高人领鹤来，寂寞空梅影。\",\"双屐出柴荆，立待昏鸦定。折得寒香独自归，门掩青山冷。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762214a6939c224e4a9','小重山·柳浪闻莺','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一抹晴烟淡又浓。曙鸦飞散尽、树头红。林莺催请晓匆匆。啼声好，唤动少年丛。\",\"纨扇落花风。苏堤三月景、水溶溶。清音度水间歌钟。寻不见，春在绿杨中。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076221a9962b5ae5f08f','摸鱼儿·春恨','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"竟因循、清明过了，依然春恨如旧。落花狼藉重门闭，长是恹恹中酒。从拆手。最苦是、断肠院落黄昏后。风狂雨骤。更燕子无聊，杜鹃有怨，添上个消瘦。\",\"寻春约，惆怅参差难就。韩香犹在襟袖。绿阴啼鴂章台路，疏了傍花随柳。还念否。嗟两地、云轻雨薄空回首。音书未有。莫去问鳞鸿，山长水远，知在甚时候。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076223f056a38494e38e','满庭芳·五头瑞莲，陈司训官舍所产，谓是岁邑士发解之兆','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"香拥红云，影摇青盖，水宫竞斗明妆。天然异种，谁数并头芳。月地恍疑人影，湘妃醉、春散香囊。还相乱，三三两两，交颈紫鸳鸯。\",\"薰风何处起，最宜吟赏。春草池塘。更闲情澹雅，官舍清凉。折桂预期仙步，斜阳里、相衬槐黄。行看好，舟中莲女，马上彩衣郎。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076223f7437df9e8c937','木兰花慢·犊舟','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"把江湖清兴，都分付、与乌犍。向芳草陂塘，驾风移雾，拟泛平川。肩头吟篷书幔，引疏狂、放浪野云前。古道撑低落照，寒林载出长烟。\",\"西湖画舫剡溪船。争似此安然。看月上桃林，歌声欸乃，角枻鞭舷。醉来沧洲入梦枕，蓑衣背上且高眠。一任松涛卷地，不妨麦浪翻天。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762242b1e3b4ca1e140','满庭芳·书赏花卷','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"燕子帘帏，秋千院宇，被花卖到春光。小屏银烛，入夜照红芳。可是东君有意，繁华地、赁与诗狂。三杯后，晚香初罢，檐雨正淋浪。\",\"浮生能几许，坐间屈指，三十风霜。叹彩云易散，春梦虚忙。惟有诗情酒兴，个中事、犹恼愁肠。花应恨，满眶春泪，揾透紫香囊。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076225ba99a64a65bea5','念奴娇·和宋人中秋月韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"淡天垂幂，见银盘转处，余烟收绿。百尺飞楼闲倚久，仙桂时飘香粟。皓鹤无声，金梧有影，寒气生华屋。好风细细，递来何处丝竹。\",\"看取千里婵娟，银屏交映，光射庐峰瀑。今夜嫦娥应过我，卷起真珠千斛。长剑横空，翩然飞迓，底用乘鸾鹄。满身清露，凤笙吹破攒玉。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076225ba99a67413f606','念奴娇·和宋人中秋月韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"冰轮碾上，望山河影里，桂枝横绿。零露侵人秋气重，渐觉玉楼生粟。万里无云，碧天如水，冷浸黄金屋。水晶高卷，簟波微映湘竹。\",\"须信此景无多，樽前拚醉，注流霞如瀑。我爱清光光照我，洗荡尘埃千斛。良夜何如，酒酣兴起，豪气凌云鹄。浩歌催版，唾壶击碎圆玉。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707622819e4740f74f728','蝶恋花·春归','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"池上落花红作阵。池外浓阴，总道垂杨闷。春意自怜芳意尽。风风雨雨归期近。\",\"泪眼惜春还可恨。来不多时，去又无回信。芳草征衫何处认。杨花只乱人方寸。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707622a76d115345a4a10','木兰花慢·双挽','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"算多愁、和天也惨，双股泪、席间流。记花月楼台，扇横鸾影，杖拥鸠头。西风妒他春好，并霜威、作践一场秋。翠减芙蓉夜被，尘生古褐冬裘。\",\"青山今古事悠悠。双冢向人愁。有守宿寒鸦，招魂芳草，代哭鸣蜩。椿萱世难全寿，叹人生、到此意俱休。付与夕阳收管，一杯时奠松楸。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707622c358fc517aaddc1','临江仙·渔舟','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"瓦枕半欹诗梦醒，起来人静江空。丛芦萧瑟战金风。钓舟不系，吹落碧滩中。\",\"坐稳总忘身外事，苍山独对疏慵。夜深清露滴芙蓉。满篷凉月，人在水晶宫。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707622d15cc14e3f3ebac','风入松·江楼秋眺','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"水云微淡侵江楼。山势涌沧洲。黄花欲绽丹枫老，吴霜冷、雁叫清秋。云外离情耿耿，天涯归信悠悠。\",\"乱鸦投树夕阳收。江水向东流。登楼望断平芜影，凭阑久、多少闲愁。风里一声渔笛，浪中数点行舟。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707622f0151ad2caa4f38','临江仙·秋江归隐图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"菰米炊香鲈入馔，吴淞景属三秋。长江潮上水云浮。好风如箭，天际送归舟。\",\"回首功名成一梦，归来松菊应羞。何人相候坐沙头。平林暝处，鸦背夕阳收。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707622f23cef6b135a450','满江红·夜雨','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"夜永香残，疏檐外、潇潇寒雨。正衣篝微润，罗裘无侣。灯焰昏沉孤馆内，梨花寂寞重门里。被斜风、吹折一天秋，敲窗纸。\",\"声点滴，铜壶水。丝散乱，垂杨缕。尽欹眠无寐，屏山遍倚。旅况暗惊南浦别，故人谩想西窗语。况计程、芳草暗天涯，春归矣。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762328d55124b14e923','千秋岁·夏日池亭','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"柳阴亭馆。影落池塘浅。清昼永，琴书懒。绛蟵炎气少，碧簟波纹展。人困也，北窗一梦邯郸远。\",\"推枕西林晚。浴起冰绡软。新月上，珠帘卷。霜纨摇素手，雪藕调冰碗。歌韵响，昼船归处荷香满。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076232f7afa1100763f8','行香子·书泗洲南山石壁','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"野景晴川。白鸟苍湾。向沧浪、一洗尘颜。水拖衣带，山结螺鬟。\",\"听步虚声，钟梵韵，翠微间。市楼一片，客帆千幅，问何人、到老心闲。秘书谢事，司谏归山。见夕阳中，云卷尽，鸟飞还。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623323a4500d8bb79a','满庭芳·和苏东坡','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"战蚁柯边，斗鸡坊口，看来一片虚忙。汉何仁义，楚岂尽刚强。刚道偶然成败，被人笑、持论疏狂。君应看，众人拍手，鲍老独当场。\",\"分明都是戏，尽教涂抹，我又何妨。便将何变得，鹤短凫长。扰扰唇鎗舌剑，是非口、一任苏张。归途好，风光满马，春草雨余芳。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076236e5fa520c694a97','长相思·竹','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"金铁呜。玉雪呜。檐外秋高风日清。苍阴落枕屏。\",\"湘山横。湘水平。两岸暝烟孤棹停。箫声吹月明。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623731e0d45abdec42','蝶恋花·斑竹','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春雨冥迷云委地。节节琅矸，滴滴英皇泪。人倚孤舟愁不寐。鹧鸪啼破湘江翠。\",\"千里苍梧开晚霁。碧水悠悠，不尽临江意。月在粉墙摇凤尾。紫台环佩知何处。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623839aacec8715f32','酹江月癸未春二月中旬之未雪盈数寸','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春云叆叆，被东风、剪做琼裾瑶玦。玉女群游飞素盖，光动青皇宫阙。寒食梨花，清明柳絮，飘向中和节。杏楼人醉，冰容相映红颊。\",\"还想西子湖边，踏青埋路，冷透罗鞋结。谁在画船闲弄水，好似剡溪舟楫。蝶粉都迷，莺黄难至，燕子噤无说。晴光何在，夜庭误认花月。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762387bf9f14fd316df','风流子·送人归吴中','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"天涯孤客梦，西风晚、吹入捣衣秋。念故国情多，书成织锦，敝袍尘满，赋就登楼。寸心急，晓同云北下，夜逐水东流。寒雁数联，暝烟千里，短鞭去马，长笛归舟。\",\"都门樽酒后，相思渺云树，两处悠悠。为遣中宵明月，随到南州。向溪亭苍霭，寄声修竹，野塘春暖，致意沙鸥。备我一蓑烟雨，老景沧洲。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762388732f66c525f39','清平乐·夏','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"余花落尽。蜂蝶还成阵。绿满汀洲芳杜嫩。乳鸭一双娇困。\",\"葛衣缕缕风微。白团扇上新词。欲试碧筒煮酒，琵琶催换春儿。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623a69f79fab60a59e','天仙子·秋景画','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"江上青山山下路。茅屋人家临水住。雨余一片夕阳明，汀草处。鸥来聚。一叶渔舟横野渡。\",\"烟雾纷纷生古树。树底寒流桥欲堕。行人千里未能归，还自苦。愁无数。枫落吴江秋正暮。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623a878a8f393bf789','齐天乐·至六安写怀','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"秋风千里骑黄鹄，翩然白云南去。芳草闲情，枢波醉眼，一枕苍寒留住。清时有味。尽棋局坐忘，茶瓯相对。百首新诗，江山风月助奇思。\",\"功名自来浪许，道炊烟未散，丛阴无处。磨蚁嫌贪，竿鱼讶冷，一笑晚山横翠。闲门静闭。任蝴蝶上阶，雀罗张砌。梦落朝班，日高慵未起。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623b475d02ba58ed8a','浪淘沙·牧子卧牛背晚归','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"牛背稳于舟。身外无忧。醉来一枕梦巢由。惊起不知何处笛，斜日林丘。\",\"古树晚鸦投。暝入西楼。寒烟暮霭织成秋。归路一鞭衰草色，野水桥页。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623c1c1610cc320b8f','水调歌头·许启中使交南','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"银河动清曙，玉宇湛高寒。手招黄鹄，晓骑风露下云端。借我金门仙客，去作三清使者，星佩落人寰。笑指炎荒路，一发认青山。\",\"觑宫袍，风采异，日华鲜。珠霏玉洒，布宣德意笑谈间。独倚横空一剑，俯仰乾坤万里，东望海波闲。白雉看重译，声教讫夷蛮。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707623c2ffa10db3bff54','踏莎行·菊花便面','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"细雨山城，西风酒旆。深秋景物皆诗句。天留老眼看黄花，一年一度重阳醉。\",\"晓撷怜香，夕餐甘味。食贫谁更忧生计。试观富贵满庭秋，金钱委地无人费。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762424ffc7f9088d804','玉楼春·客怀','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"斜阳芳草迷归路。撚指光阴愁里过。梨花晓梦五更寒，杜宇春心三月暮。\",\"天涯望望嗟离阻。那更碧山无尽数。深情只好附双鱼，肠断春潮渺江渡。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076242a5fc7cc8946903','卜算子·断桥梅雪','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"浅水野桥西，饥鸟依林樾。不见逋翁蹋屐过，僧舍茶烟歇。\",\"鹤迹绕寒汀，梅影敧残雪。却被渔舟占尽清，晚钓湖心月。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624334969f41cd6450','蝶恋花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"三十年前真是误。驹隙匆匆，尘土长安路。赢得闲身忙里过。青山白水空烟雾。\",\"梦觉邯郸炊甑堕。钓月樵云，生计从天付。话到忘言成默坐。夕阳一片溪山暮。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624334969f47d10ad0','蝶恋花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"新绿汀洲芳杜暗。渺渺春波，斜日江南岸。去岁送春微有叹。今年春去浑肠断。\",\"蜂蝶无聊惊也倦。冷落西园，飞盖朝来散。落尽庭花人不见。垂杨空锁深深院。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624334969ff46bfda8','蝶恋花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一寸柔肠千里思。倦倚屏山，梦到天涯去。帘外日高花影砌。莺声歇在垂杨里。\",\"半亸香云惊枕坠。起看雕梁，燕子归来未。行过秋千双眼泪。乱红委地春无主。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762458dbd36e1d3bc67','踏莎行·踏雪寻梅','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"密雪和云，层冰匝地。关山岁晏无来使。早芳知有野桥春，吟鞭料理西湖事。\",\"破帽多年，敝袍无絮。酸风故故欺驴背。溪山十里正清寒，梅花却在清寒处。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076245a6897ecf8d1c1f','酹江月夜雪','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"冰花夜糁，悄气侵窗纸，寒灯明灭。平地琼瑶铺欲遍，做我庭前明月。柳絮萦春，梨云搅梦，滚滚无休歇。酸风助阵，绕檐吹响枯叶。\",\"堪笑锦帐羊恙，粗豪风味，全与文人别。细碾凤团镕净片，一洗诗脾清澈。纸帐梅悭，地炉火近，未放衾如铁。闭门高卧，任他天地凝合。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076245cdfc4879f768ea','沁园春·和辛稼轩','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一事无成，百念堪惊，嗟归去来。算山阳笛里，十年风雨，邯郸道上，千里尘埃。贵贱同丘，古今流水，试问荣华安在哉。秋江上，但月生月落，潮上潮回。\",\"天教老向茅斋。把风月、胸襟对客开。更绿阴画舫，醉残坡柳，暗香诗屐，寻遍逋梅。高竹秋声，长松午影，留个山岗次第栽。闲中看，这归休也好，何用徘徊。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076245d48e4bb2cc4bf1','兰陵王·荷花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"野塘碧。云锦天机乍织。垂杨外，红日半酣，翠盖亭亭拥倾国。晚来溪雨息。乱泻明珠苍璧。风帘卷，画阁尽开，一味清香送凉入。携樽，近花侧。问娇艳何如，六郎颜色。\",\"芳洲别浦羞孤特。正一钩新月，画船十里，浣纱人去歌韵寂。望中水云隔。回首，又秋逼。渐香老红衣，飞趁潮汐。寒烟败苇添愁戚。似相对共叹，离怀无极。残阳影里，有宿鹭，黯凝立。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624831e397c6f9034a','沁园春·秋坡','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"买断青山，占住白云，岂不佳哉。尽夜枕商声，苍筠绕梦，晚庭诗景，红叶成堆。老鹤松阴，流泉涧道，付与闲藤日往来。行吟处，见石田齐熟，篱菊新开。\",\"有时落日林隈。听牛背、西风一笛回。更露洗疏桐，乌啼金井，月寒老桂，人在琴台。芋栗供贫，茱萸备酿，生计年来手自栽。清幽甚，笑落花流水，春漏天台。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624901939124c52dd7','临江仙·柳词，用宋许伯阳体','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"不见太师桥下柳，绿阴摇荡雕甍。朱门甲第管弦声。沙堤十里，香絮滚春晴。\",\"惊起胡尘青眼眯，秋风禾黍神京。娇枝嫩叶叹飘零。夜深荒址，时见度流萤。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624901939132c4a859','临江仙·柳词，用宋许伯阳体','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"不见吴王宫外柳，浓阴低映鸥沙。屧廊春响笑仙娃。东风眠起，台榭总芳华。\",\"一夜长堤驰越骑，柔条无力拦遮。荒城留与后人嗟。尚余故态，烟外舞敧斜。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624901939171ec4367','临江仙·柳词，用宋许伯阳体','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"不见汉家营内柳，丝丝轻拂旌旄。将军以逸待人劳。马腾士饱，枝上挂弓刀。\",\"钟鼓令严良夜静，梢头明月初高。翠华临幸事都销。先春芽缕，尚想赭黄袍。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762490193919b8a8eaa','临江仙·柳词，用宋许伯阳体','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"不见章台街上柳，纤匀初画双眉。轻盈婀娜惹游丝。少年走马，花酒恋芳菲。\",\"千里心期归较晚，长条不似前时。东风似与解相思。多情时遣，飞絮点春衣。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076249019391bce26476','临江仙·柳词，用宋许伯阳体','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"不见西湖堤畔柳，弄晴十里依依。暖风搓动绿烟丝。似同苏小，双舞斗腰肢。\",\"忆昔六桥经凤辇，拟为春暗龙池。画船维处恣游嬉。斜阳烟霭，谁管断肠时。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762497ec363f0945494','点绛唇·渔舟吹笛','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"碧水澄秋，丝纶卷尽斜阳影。小舟维定。蓼岸西风冷。\",\"横管孤吹，有调无人听。长江静。眠鸥惊醒。冲入苍烟暝。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624cfef66ad3304173','水调歌头·送褚正郎还朝','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"青山荐行色，碧浪涌蘋洲。红旗棹影，送君骑鹤上扬州。相饯一杯清水，比似一轮明月，三载照高秋。回首棠阴路，父老泪双流。\",\"黯苕南，云杳杳，水悠悠。柳丝残脆，相看无计挽仙舟。一道星河届斗，万里长风破浪，身抵凤池头。从此鸾台峻，鹓鹭阻同游。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624e591856acf9532b','蝶恋花·招友','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"堤柳青丝搓欲就。初试罗衣，帘幕寒仍透。紫陌尘香车马骤。太平箫鼓村村有。\",\"店舍无烟寒食候。小院闲庭，门掩梨花昼。来日阴晴浑未究。及时且罄花前酒。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624f8788b671bf44f0','木兰花慢·贺郑大尹膺奖','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"算江南乐地，山城好、称鸣弦。正桑径雉驯，花村犬卧，麦陇牛眠。天公更怜饥馁，遣我侯、早晚散炊烟。瓜枣顿还幽俗，鸡豚屡庆丰年。\",\"玺书曾记汉旌贤。藩省代天言。道三异化行，七丝政好，六事功全。摩挲旧时凫舄，看相将、飞上五云端。河内空烦借寇，道旁行羡登仙。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707624f8788b67a0592c0','木兰花慢·贺郑大尹膺奖','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"记红芳退后，兰阶下、绿抽簪。渐叶展鸾翎，花含凤嘴，影乱槐阴。宜男自来入咏，早薰风、长养北堂深。好是彩衣寿酒，一枝照映钗金。\",\"中庭无奈雪霜侵。风木忽哀吟。叹光景无多，芳华易殒，冷泪盈襟。伤心药栏西畔，但断机、残月尚如今。总道春晖难答，年年寸草萌心。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762515d59a33e642d41','齐天乐·刘太守之任湖广宪副','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"棠阴才遍行春路，马蹄又辞芳草。兽锦生辉，铁冠增重，好是旧来仪表。朱幡谢了。报蠖屈非宜，莺迁更好。别意依依，樽前一点岘山小。\",\"伤心绿波向晓。念芳洲无主，白蘋空老。借寇难凭，依刘无据，泪眼共看晴昊。岸花飞绕。送千里云帆，长风袅袅。后夜荆台，清霜秋气早。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076251dc6249bc7ed506','水调歌头·己卯初度','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"著书鹖冠子，倚仗鹿门翁。十年正堪一笑，梦觉大槐宫。早是年开五秩，便待人生百岁，双脸再难红。试问输铜臭，何似作诗穷。\",\"道根深，俗缘浅，世情空。仙山久扃，花院名位夙相通。安事舞衫歌扇，自喜薰炉茗碗，况味野僧同。天独与为寿，秋满菊花丛。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762575882bd67f67295','清平乐·玳柄素扇','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春冰乍裂。浅沁生蛟血。截玉裁肪分骨节。软衬凉云弯月。\",\"玉葱夜浸银盆。十枝红甲纤匀。不道啼妆难掩，泪沿湘竹斑痕。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707625cabad4491db0367','行香子·梅未着花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"标格天然。占断风烟。想南枝、犹怯余寒。香苞未点，疏影依前。\",\"称月明时，霜冷夜，雪晴天。西湖屐冷，东垣赋老，问春心、何苦担延。一枝得暖，随分清姘。任玉堂中，茅屋下，野挢边。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707625ea4430c82fd0497','醉花阴·梅花风谢','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"玉堂不闭东风骤。一夜琼枝瘦。香雪乱侵衣，梦醒江楼，羌笛谁家奏。\",\"仙姿自别罗浮后。消损应非旧。莫去倚阑干，冷月清霜，正是愁时候。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707625ebd2bd303b5c15b','清平乐·赠瑞岩观解道士','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"寻真何处。来到桃源地。满路松花金粉坠。时有白云来去。\",\"洞门小扇初开。一双玄鹤迎来。请去先生双屐，莫教踏坏苍苔。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762606a5c153328c4a7','巫山一段云·夏日竹堂','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"午簟湘阴展，晴窗翠葆横。睡余呼水理茶瓶。诗境太清生。\",\"古鼎销香缕，疏帘隔履声。虚檐留客对棋枰。时听子丁丁。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762615ab389b9a2a48d','酹江月挽拙庵潘先生','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一庵藏拙，问乾坤、借得数茎华发。夜半游仙孤枕梦，飞入玉清双阙。山色冥迷，水声哽咽，老鹤愁寒骨。梅花如旧，玉簪无奈销折。\",\"遥想竹下吟笺，山阴清昼，冷淡兰亭墨。十里屐痕吹未散，芳草丛边双迹。华表秋荒，剡舟夜冷，空有当时月。岩花急雨，春山飞遍榆荚。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076263849687eb335fdb','酹江月','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"携壶荷锸，算青山是处，可教埋骨。天地大都成逆旅，黄土何分南北。辽鹤秋沉，蜀鹃春断，回首淮山隔。紫薇何在，月明空伴诗魄。\",\"见说狐兔丛边，树存吴剑，匣保兰亭墨。麦饭不辞千里远，来洒他乡寒食。十里湖烟，两峰残照，记取丹青笔。不禁清泪，空岩春雨时滴。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076265e0d5ff1b654502','贺新郎·记梦','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"兽鼎销沉缕。掩屏山、帘帏低下，昼长门闭。移尽花阴无人唤，春睡沉沉未起。渐梦入、花丛径里。满眼春娇惊旧约，转雕阑、曲槛深深处。相见也，笑偎倚。\",\"贪欢一饷留情住。楚台高、雨云散后，峰峦开霁。素手纤纤寻棋局，共斗绿窗红子。又携向、西厢私语。别久愁多珠泪重，正耳边、聊诉相思意。惊散也，洒窗雨。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626707e37382d25a6d','烛影摇红·送章子仁归吴中','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"泽国秋霜，雁行南去潇湘晚。西风归兴动江湖，一尺鲈鱼短。好是别怀难遣。溅斜阳、青衫泪点。坐间无奈，锦瑟哀弦，玉壶深盏。\",\"三径无成，天涯身世嗟蓬转。新声聊遣唱阳关，红烛摇金剪。还有登高望眼。渺平芜、山遥水远。一声长笛，数点归鸦，倚楼心懒。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626779800a5c72d37b','水调歌头·送黄司训之京','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"断云催落日，寒雁溯西风。一樽别酒青山，去意晚来浓。人道九年教化，好似一天时雨，未论桔槔功。芹水香名在，传诵满儿童。\",\"束图书，装琴剑，买孤篷。今宵明月相看，千里与君同。休管青毡旧物，须有铁冠新命，霜气凛秋空。醉笔点行色，江树向人红。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762690d07898cd6871e','满庭芳·写怀','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"罗帕分香，玉钗留股，东风羞记初盟。残杨剩柳，休问啭春莺。虚向蒲关驻马，淹误了、云路鹏程。伤心处，枕寒衾冷，香梦杳难成。\",\"章台人远，寻春计晚，露蕊凋零。叹落花有意，流水无情。好事从来易阻，御沟里、红叶难凭。空惆怅，夜深人静，辜负月华明。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762691a37c070189698','苏幕遮·风情','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"玉骢骄，金勒富。策柳鞭花，醉向章台卧。宝枕鸳屏留客住。燕子归时，帘卷青楼暮。\",\"柳枝吟，桃叶渡。别语叮咛，回首成虚误。鱼雁又将芳信阻。惆怅深情，梦断年时路。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762699fc739ca957bf8','摸鱼儿·悼鹦鹉','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"向东风、红销翠萎，伤心成甚情绪。护寒帘幕终何用，花病早知来处。春也妒。全不管、画堂冷静人难度。高飞有路。仙梦去多时，玉绳金栅，空锁绮窗雾。\",\"鸳鸯会，惆怅佳期又误。琵琶有语谁悟。香残红稻珊瑚颗，愁与泪珠同堕。还寄意。永别他、陇山千古青青树。梨花院宇。重想念新诗，日高惟有，莺燕自来去。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626a3fb42e5593455b','水龙吟·梨花，和周美成韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"醉颜消尽东风，一枝迥出纷华地。暖香吹雪，淡妆欺粉，群芳羞避。静院无人，闲庭转午，重门深闭。倚阑干无语，玉容寂寞，为滴尽伤春泪。\",\"愁到黄昏时候，漏声沉、隔墙歌吹。疏帘淡月，离怀唤醒，梦魂惊起。细雨帘纤，轻云低娜，不禁春意。算东皇、独付清标，正好与梅花比。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626bd8d8067bdaa3cb','水龙吟·杨花，和章质夫韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"是谁断送春光，又柳絮、满空飞坠。长途飘泊，征衣轻薄，搅人离思。梦熟罗厨，脸消花靥，镜惊尘闭。傍妆台点点，随风散乱，几番欲下还起。\",\"灞水旧时行处。遍长条、雪花如缀。红芳竟老，绿阴成叠，寸心愁碎。晓雨初晴，半归泥土，半随流水。卷珠帘、尽日留春不住，洒东风泪。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626c6bb178a9f6b639','酹江月日休，取山谷四休之意','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"容身何处，有青山、借我一丘一壑。稳坐篮舆横曳杖，十里西湖行乐。春笋斋厨，午茶杯盏，世味从他薄。大都几屐，安闲便是丹药。\",\"随分吟倚西风，醉眠苍石，心与浮云约。回首红尘名利事，勘破蝇头蜗角。万卷藏书，百竿栽竹，有子承当着。乾坤老矣，一簪聊寄华发。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626c83c476bf50cb9f','如梦令·夏日','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"池上繁阴深盖。高柳古槐交碍。晚雨过西楼，洗出月华相待。\",\"凉快。凉快。直面芰荷风洒。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626d0756a641e3faf8','蝶恋花·葡萄','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"凉入秋风惊枕簟。一架藤萝，搅碎闲庭院。虬尾丝丝帘影乱。真珠拂地无人卷。\",\"疑是嫦娥遗宝串。偷挂西窗，月下分明见。浓酒未教醒夜半。醉乡不换凉州远。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626dc67f2cdaad58c9','喜迁莺·送侯倅擢太仆丞','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"采菱风起。正一叶井桐，乍惊秋雨。马首红旗，鸥边画妨，光满白蘋洲渚。点点道旁涕泪，恋恋樽前笑语。伤离处，奈阳春有脚，湖山无主。\",\"堪喜三千路。身上红云，双佩春风里。紫塞防秋，彤廷立仗，马政会看兴举。汉遣属车陪从，周有命书付委。从今后，勋阶台阁，服章金紫。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626e00362a6cfe6ad5','酹江月竹心巷','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"栽云种玉，悄山楹、移入翠微深处。老去闲情无可与，只有此君堪对。空洞无肠，虚明有窍，节节舍清气。竹心如此，我心元与相契。\",\"见说墨妙亭边，爱山台下，剩有烟云地。露叶风梢浑未论，认取岁寒心事。朝爽横琴，秋阴领鹤，付与闲滋味。日高响扣，玉声酬我佳句。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707626e0594f50aad68df','烛影摇红·送二守之任毗陵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春雾空蒙，东风千里湖山晓。红旗画鼓簇花堤，供帐城南早。请看樽前父老。向棠阴、泪流多少。仙舟难挽，湖水空摇，柳丝虚袅。\",\"经济才华，东南列郡都来到。马蹄带取弁山春，散向延陵草。人意新来正好。报前途、天门开了。伫看剑履，快上星辰，五云围绕。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627430b14a368ef5a2','临江仙·扇面','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"妆点吴乡秋意老，夕阳红树离亭。晚江潮退水痕平。两行征雁，相约下寒汀。\",\"钓饵丝纶收拾了，芦花深处舟横。西风吹面酒微醒。起来船尾，坐看远山青。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627556ca3f71a4ec97','蝶恋花·春思','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"芳意柔情千万缕。付与杨花，飘泊江南去。斗帐日高犹熟睡。枕痕蹙损眉峰翠。\",\"人事多违花事退。泪眼残花，日日伤僬悴。试卷珠帘通燕语。绣鞋行到无人处。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627771ceabe16ae1a7','烛影摇红·中秋后三日，与李京兆夜饮投壶，醉后有作','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"修竹闲庭，铿锵金铁秋声满。是谁闲气校输赢，花影频频转。常记雅歌催版。想军中、裘轻带缓。江湖今喜，飞燕名声，插花杯盏。\",\"身世飘零，天涯岁月惊秋晚。百金交谊古难寻，共看松筠暖。可是穿杨技短。早樽前、心寒手软。珊瑚击碎，感慨悲歌，夜虫凄惋。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627a4ad795362b9cac','如梦令·春暮','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"梦里忽惊春去。帘外日高佣起。中酒病恹恹，辜负踏青心绪。\",\"憔悴。憔悴。花褪一庭红雨。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627a4ad79554649678','如梦令·春暮','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"风入井桐声碎。惊起客窗秋思。天阔雁来迟，望断锦机红字。\",\"归去。归去。人在画楼独倚。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627cb5793e4697b1fa','二郎神燕','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"东风帘影，依然是、去年庭院。门外正春寒，试凭旧主，暂寄修梁尘满。借问杨花是何处，有华屋、朱门堪荐。怅绿岸晓迷，红楼暮暝，潇湘云暗。\",\"遥念。天涯冷落，琐窗无伴。软语苦匆匆，梦惊清午，一枕江南思远。恨别云轩，相思绛缕，知惹几多恩怨。望楼香，路杳断肠，无奈落花春晚。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707627d3bfeccfe126abc','沁园春·章子仁频岁远游，诞辰乃秋初也，继又促游岭表，故兼致如此','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"南鬓星霜，两脚风尘，老矣吾生。记燕山晓别，雪中驱马，秦淮夜泊，月下闻筝。独雁辞秋，孤灯听雨，身在江湖轻利名。从今算，又十年心事，屈指堪惊。\",\"天开五十昌龄。人道是、南方一寿星。向虹桥影下，应期乌鹊，冰丝弹处，访旧湘灵。税地栽花，解貂当酒，此去情怀尚可能。明年也，盼梨花消息，岭表归程。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762800a36b4bcee2339','满庭芳·兰','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"窈谷阴崖，香云暖雨，芳蕤初着幽丛。修然世外，此意几人同。回望三湘何许，灵均去、佩冷江空。无人问，断魂千里，相逐落梅风。\",\"平生良自爱，孤标高韵，寂寞山中。算知心独许，三尺焦桐。肠断王孙未返，年时草、又衬残红。春愁满，汀蘋岸芷，落日楚江东。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762800a36b4e8a7f757','满庭芳·兰','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"浓绿和烟，浅青漱雨，春回芳草池塘。绣帘未卷，红影罩西廊。九十光阴苦短，蜻蜓老、蜂蝶争忙。相将近，鸣蛙鼓吹，闾闷满斜阳。\",\"浮生真可笑，区区世路，病懒相妨。护志勤络纬，技转蜣螂。谁道安身有处，苔茵展、风月坛场。三杯好，江花江草，分付与诗肠。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076280a45158aab65d75','摸鱼儿·花港观鱼','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"甚朝来、斜风细雨，观鱼正有佳趣。绿蓑青笠寻常挂，谁道水乡难寄。堪画处。最好是、荻花乱点舟三四。鸥飞鹭起。更落网惊腥，呜榔畏响，潭有老蛟睡。\",\"红尘路，比与烟波名利。寻思总是难事。华亭唳鹤东门大，何似得鱼情味。君试觑。绕平湖、水云十里皆生计。红鳞买醉。世事不相关，柳阴欹枕，湖上晚峰翠。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076280bcc4a627b6eaba','贺新郎·端午','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"梅子成金弹。正池塘、帘纤雨歇，海榴红绽。节序催人又重午，彩索新缠双腕。更艾虎、朱符妆遍。谩对蒲樽开角黍，奈沉湘、旧事经伤感。须一醉，强驱遣。\",\"清江竞渡人争看。绕沙堤、荷香存苒。南薰微扇。两两龙舟斗相向，夺罢锦标方散。又早被、西山催晚。十里平波人去后，但一钩新月蛾眉浅。归棹急，浪花乱。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076281ff18ce6b49e6f4','念奴娇·墨芙蓉','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"孤芳无主，被西风、送与一天愁色。顾影含羞还有恨，彩笔怎生描得。蘋白如矜，蓼红自倚，真意谁能识。影娥池古，晚妆犹剩香墨。\",\"惆怅别浦芳洲，水云静处，佩冷菱歌寂。幽梦偶随归燕化，荏苒乌衣消息。浊世堪惊，细尘易染，清泪空狼籍。江南望断，一痕月照凉夕。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076282e7aaca64e539e0','浣溪纱初夏','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"新笋成竿粉节光。落花红润燕泥香。水亭帘幕小池塘。\",\"萍约翠痕楼影出，柳搏清荫簟纹凉。抛书一枕梦能长。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076283442ca5a8d9b0a2','风入松·旅游图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"青山断送古今愁。落日下林丘。西风古道无人影，行囊薄、尘满征裘。暝色雁投何处，笛声人在高楼。\",\"长途世事两悠悠。羁客倦遨游。秋容诗意知何在，寒鸦外、流水桥头。凭仗丹青会写，为予添个离忧。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762840c6b9563000969','蝶恋花·夏日遣兴','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"昼宇闲闲帘着地。乱匝桐阴，人在清幽处。烟袅画屏沉缕细。绿窗一枕芭蕉雨。\",\"残睡觉来无意绪。雨后斜阳，照在庭前树。浴罢晚凉掀短袂。清香满院荷风起。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076284537dd533d873e8','渔家傲·两牧子骑牛晚渡','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"沙上行行人渐少。一声断角谯门杳。烟霭微茫笼树杪。斜阳倒。乱鸿数点青山小。\",\"人正饥来牛正饱。驱鞭懒顾平原草。两两绝流归去好。寒塘悄。西风吹雪芦花老。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707628997431917493a3b','酹江月送人之南雍','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"都门晓霭，做渭城朝雨，赠人行色。长剑横秋天地小，匹马临风跑立。布袖天香，锦囊诗句，壮观京华客。江南去去，月明应望江北。\",\"天意未赚英雄，试教回首，青史看陈迹。灯火桥门三载事，晨镜尚夸头黑。雕鹗秋霄，鱼龙春浪，气满风云臆。住君一醉，碧山正未西日。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707628bbe27d016c8b85c','满庭芳·中秋月，和人韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"碧海澄秋，遥空霁雨，姮娥起驾飞轮。素光万顷，灏气满乾坤。坐久疏星渐失，枯枝冷、惊鹊南村。闲阶下，桂眠秋影，香味泻金樽。\",\"瑶台当此夜，露零玉佩，风振仙裙。记唾壶残缺，歌韵清温。回首西风易老，流年事、屈指休论。空惊叹，故人千里，相望渺江云。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707628c1e57b12841ccc4','行香子·雷峰夕照','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"敛尽湖烟。淡尽湖天。望西泠、晚尽湖山。林皋烧远，鸦背金寒。看半棱阴，两竿影，落湖间。\",\"残霞遥映，浮岚半掩。透斜红、一缕峰前。何人无事，倚杖闲观。正水云中，莲唱寂，钓舟还。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707628da335c9f45041c7','贺新郎·贺得子','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"天亦从人意。遣黄熊、几宵飞兆，梦中新记。隔帐笙镛啼未了，英物不消多试。更头玉、硗硗奇异。经月异香吹不散，气充闾、郁郁葱葱地。须信道，十分喜。\",\"掌珠一颗才周岁。满华堂、宾朋惊看，食牛豪气。两鬓莫忧秋渐早，万事于人足矣。便整备、传家之计。为报凤池烦接引，看飞冲、五色丹山羽。真个是，应时器。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762903dcbe562762c57','应天长·晚眺','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"楼台落日，渐古树昏烟，乱鸦飞集。渡口归帆，势与寒潮齐急。云山穷眼力。旷千里、楚天凝碧。相上下，孤骛落霞，点成秋色。\",\"俯仰感今昔。嗟旧日繁华，而今萧瑟。剩水残山，惟有梦魂相忆。飞鸿无信息。知谁念、沈腰消瘠。关情处，风里何人，一声长笛。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076290492ed51119989e','沁园春·冷山外号','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"丘壑闲情，松菊旧游，未许相关。笑寻梅蜡屐，云埋一两，藏书茅屋，苔老三间。蕙帐夜空，碧峰朝瞑，争奈愁他鹤与猿。无人管，有洞门寂寂，流水潺潺。\",\"鸣驺催赴朝班。尽京国、缁尘上鬓鬟。任碎叶惊风，一番秋早，落花满地，几度春阑。蚁穴无凭，山灵有待，回首林间倦羽还。君知否，算神仙总在，未老身闲。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076290650a9e514e4c79','风入松·秋怨','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"玉京曾记擅风流。丝竹按梁州。秋波娇滴春山淡，金莲小、肢体轻柔。妆就乌云满镜，舞余红锦缠头。\",\"繁华一去竟难留。门巷不堪秋。雕鞍游冶归何处，相思梦、空绕秦楼。肠断塞鸿北去，伤心江水东流。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076290650a9edc0ccc13','风入松·秋怨','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"宝钗半脱鬓云松。瘦影怯西风。屏山似近阳台路，片时里、幽梦相通。眉黛颦残宫绿，泪痕揾透香红。\",\"小窗锦字背人封。密意寄重重。归期未准惊秋老，黄花淡、离恨偏浓。日暮断云鸿雁，夜寒疏雨梧桐。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076290ad9f7e172acfff','清平乐·春日偶书','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"伤春无绪。连日瞢腾醉。门外落花红满地。愁到杜鹃啼处。\",\"小窗酣碗香茶。日高懒趁朝衙。说与帘前双燕，莫教放进杨花。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076290ad9f7ebb870a64','清平乐·春日偶书','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"绿窗惊睡。说尽天涯事。帘外春寒禁不住。争得重门深闭。\",\"雨昏芳草池塘。度花穿柳心忙。因倚不知情倦，东风梦断昭阳。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076291662abd51c171e4','浣溪纱春睡','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"草绿闲阶日自移。画梁泥冷燕归迟。恹恹春困解罗衣。\",\"枕面正随蝴蝶化，柳阴谁遣杜鹃啼。起来花影到帘帏。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076291cfc7b37b12a131','水调歌头·中秋','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"仙风清玉佩，香雾蔼云鬟。紫台招手，醉骑白凤上高寒。曾记五云深处，缥缈琼楼十二，宝界晃三千。回首人间梦，一落忽经年。\",\"羽衣轻，胡床冷，洞箫闲。肺肝冰雪，孤光一点最相便。可是嫦娥有意，付与婵娟千里，今夜尽人看。起舞答清兴，歌罢唾壶残。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762937441583c859497','满江红·以下四首皆致仕归作，和吕居仁。','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"萧散东归，天应赐、苕溪一曲。江村上、春风酒旆，夕阳渔屋。放浪水云明月地，醉归船重成颓玉。更看他、晓镜照菱荷，双鬟绿。\",\"秋声早，窗前竹。秋香晚，篱边菊。把浮云勘破，世间荣辱。独拥黄紬无别梦，日高春睡方才足。起来时、谁伴立芳洲，沙鸥熟。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762942adc1051bb0a4c','菩萨蛮·闺情','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一帘幽梦随飞絮。庭前数点梨花雨。推枕又斜阳。博山余篆香。\",\"红霞凝困脸。鸾匣菱花掩。芳草近黄昏。无言深闭门。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076295406948ed8c4864','满庭芳·桂花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"露冷铜盘，风清玉宇，一年明月佳时。可人丹桂，金粟满横枝。长记广寒徐步，瑶阶静、碧影参差。夸张处，天香在手，浓透绿罗衣。\",\"孤芳容易歇，十年一梦，回首俱非。早故山招隐，林屋岩扉。比似芙蓉一样，秋风里、幽恨依依。还相慰，仙葩月种，元不在人知。\"]',NULL,NULL,NULL,NULL),('e647d10f30f7076297b5b8daacda0c7e','满庭芳','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"云冷双头，香零九蕊，青春过了难追。凄凉故苑，红影日空移。谁信芳魂解返，娇羞态、依约前时。惊心处，物华如在，世事等闲非。\",\"思量还可恨，年年三月，风雨残枝。谩金瓶斗品，锦障供诗。早是偷春有手，留情地、蜂蝶安知。相看好，芳容并美，造化果吾私。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707629bdb94ba3a3300a6','踏莎行·晚景','陈霆','e647d10f30f70762d1457b72d1457b72','明','作者在写这首词时，时在深秋，人在贬所（他因得罪当时炙手可热的宦官刘瑾而被贬安徽六安），可谓是时亦悲凉，人亦悲凉。\n词的上片写秋日傍晚的景色：流水围绕着孤零零的村落，一座破败荒凉的古城前是一条萧条荒凉的古道。傍晚时分，槎枒参差的古木老树上，归巢的群鸦老鹰在聒噪不停，夕阳斜照穿过了萧疏的树林，在地上拉出了长长的影子，江边一带的枯荷在寒风中索索作响，一片萧瑟衰败的景象。上片主要用了宋秦观《满庭芳》“夕阳外，寒鸦万点，流水绕孤村”的词意，如唐李贺《江楼曲》“鲤鱼风起芙蓉老”诗意，其中“荒城古道”这一意象的增添，点出了作者当时所处之地，暗逗出贬臣的幽怨之情，使羁情之中又添几分贬愁。“射”字下得尤为刻意，较秦观词更显苍劲凄厉。\n下片写登楼所见及所感：登楼远眺，风摇夕烟，暮色渐起；天空寥廓低垂，与无垠的衰草密相连接。在楼上望去，连绵起伏的群山显得那么邈渺低小。我想向远方来的人打听我复归的消息，但在这外人罕到的荒山僻乡，连行人也难觅！过片二句用了秦观《满庭芳》“山抹微云，天连衰草”的词意，来承接上片所写晚景，过渡自然，然后再纵笔写由晚景引发的贬臣之情。结句“青山近处行人少”，出于宋欧阳修《踏莎行》“平芜尽处是春山，行人更在春山外”，却又不囿于欧词，而能翻出新意。欧词是从思妇思游子的角度，言原野的尽头是春山，游子却在更远的春山之外，渺不可寻；而陈词则从游子思乡的角度，言游子在连行人都不到的青山尽处思乡，渺无音信，更倍觉孤独凄苦。这首词多处融化了前人的诗句，几乎是“无一字无来历”，但却自然浑成，一如心中流出，没有东拼西凑之感，这首先得益于作者以真情实意来熔铸前人诗意。特别是结句能在前人名句的基础上翻出新意，表现自己独特的感受，且情意深切，让人读至此眼睛一亮，始味出前面化用前人的词句诗意，非是作者无能，拾人牙慧，而是当时作者写的乃是千载游子贬臣最常见、又最伤感之景，触发的是万古迁客骚人最深沉、又最难以排遣的思乡之情，这里与其说是化用前人的诗句，不如说是融会前人羁情，然后再独出机杼，在积淀的共鸣中表现此刻的独特感受。\n总体来说，此词抒写离情别绪。上片写离别时的情景。情似游丝，泪眼相觑。一溪烟柳，难系兰舟。写尽了离别况味。下片写别后相思之苦。愁绪无数，无法排遣。全词凄迷哀婉，愁思无限。','[\"流水孤村，荒城古道，槎牙老木乌鸢噪。夕阳倒影射疏林，江边一带芙蓉老。\",\"风暝寒烟，天低衰草，登楼望极群峰小。欲将归信问行人，青山尽处行人少。\"]',NULL,NULL,'《踏莎行·晚景》是明代词人陈霆创作的一首词。这首词是通过写秋天傍晚的荒凉景色来抒发沦落天涯的悲凉心情。宋柳永《玉蝴蝶》：“晚景萧疏，堪动宋玉悲凉。”','[\"槎牙：错杂不齐貌。鸢：俗称鹞鹰。\",\"芙蓉老：荷花凋残。\"]'),('e647d10f30f707629cc9da31284301ad','渔家傲·钓月','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"薄晚烟林收紫翠。澄江皓月秋无际。六尺钓竿随手制。丝纶细。流光动处鱼龙避。\",\"试问云台成甚事。直钩自适闲中趣。夜静水寒慵久伫。空归去。西风梦到芦花被。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707629e37e5311be3e755','醉春风子规','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"绿满汀洲暗。天涯芳草遍。年年何事叫东风，怨。怨。怨。满树梨花，半窗残月，梦魂初断。\",\"血染山花片。声动乡关念。玉窗应是数归期，赚。赚。赚。回首衡阳，雁飞不到，寄书人远。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707629e5f1f1a6ff31b9c','烛影摇红·斑竹帘','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"巧碧玲珑，绒痕着处湘箱软。多情应苦禁杨花，春泪斑斑满。不道妨他梁燕。护东风、晓妆庭院。娇柔睡起，鬓影参差，眼花撩乱。\",\"昼永无人，玉纤轻运龟纹卷。断肠心事上悬旌，新月银钩浅。多少幽期难便。俏眉峰、有谁能见。一丝篆缕，数叠花阴，画堂深远。\"]',NULL,NULL,NULL,NULL),('e647d10f30f707629eb5bbccb7ccce4f','画堂春·春院晚景','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"新晴庭院晚香迟。柳梢先有鸦栖。东风特地报花期。吹进帘帏。\",\"芳信未来燕子，春寒犹泊罗衣。落梅砌上月痕移。人立多时。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a042f573532f43fb','烛影摇红·寿沈母八十八，闰八月望后也','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"满百光阴，琼楼再赏银蟾皎。蜕辰刚遇闰中秋，天意分明巧。桂子香风未了。并祥烟、一时清袅。星辉南极，桃熟西池，绮筵开早。\",\"人月双圆，月娥素貌年年好。西风雁外鹤南飞，瀛海云霞绕。报道金莲屡到。拥飞琼、醉酣蓬岛。莱衣对舞，秦管联吹，洞天未晓。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a0edec8400d9fa4b','行香子·水芙蓉','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"露后霜前。绿密红嫣。浅深开、取意争妍。含娇似语，顾影还怜。\",\"点蓼花秋，蘋花景，菊花天。锦城事去，海山梦断，向西风、有恨谁传。芳洲就暝，莲唱回船。但渚烟生，江月上，水云连。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a6e393faa8960858','菩萨蛮·冬景','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"彤云黯淡冰花撒。冻梅枝上缠寒雀。帘蟆密遮风。铜炉兽炭红。\",\"浅斟延小唱。醉倒销金帐。煮雪理茶瓯。清香满碗浮。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a749a6671b40d8a8','水调歌头·寿人六秩，中秋后三日也','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"人间望明月，天上启琼楼。嫦娥劝人，寿斝未把玉盘收。谁谓三光有缺，刚道百年难老，灵药许君偷。笑引仙茎露，玉屑洒冰瓯。\",\"美苕南，猿鹤地，鹭鸥洲。闲情一片，秋水物外有丹邱丘。早是仙台注籍，更报鹤书加算，绿发炯双眸。起舞阅铜狄，抚掌桂花秋。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a74c8b2a31fcb4f9','菩萨蛮·三潭印月','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"采菱歌断遥天晚。碧湖无浪蘋风软。一月漾三轮。秋光被水分。\",\"胜游超秉烛。画舸潭心宿。月恰近双舷。梦魂中夜寒。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a8012ad25aa304ae','西江月·曲院荷风','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"猎猎青蒲弄水，阴阴绿树生凉。南薰吹到藕花庄。柳下正多游舫。\",\"骤雨打穿翠盖，浮萍放出红妆。画阑十二凭湖光。时有暗香来往。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a872fa5739d1e7d5','酹江月云巢云譬道，巢譬天地，道学者号也。','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"乾坤空洞，是何年营构，尽堪栖息。万点飞云凭顿放，大道本来充塞。霁月光风，青山绿树，妙意超形色。絪缊变化，个中示我安宅。\",\"长笑林下山间，无心世故，一枕眠苍石。造化斡旋身上事，天地本吾居室。无去无来，非空非相，泰宇元虚寂。与君勘破，鸢鱼总是粗迹。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a88b89bd7516d9d6','清平乐·牧马图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"千金骏骨。仗下思奇特。露鬣风鬃劳画笔。一顾还倾冀北。\",\"少年驰步长安。骄嘶柳外花边。今日沙场闲放，夕阳秋草连天。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762a9d49b89119e6e65','酹江月题天籁老人像天籁金末词人。','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"滑稽玩世，知胸藏、多少春花秋月。天籁有词人有像，还是遗山风骨。松下巢由，竹闲逸少，气韵真高洁。坐谈抚掌，溪山等是诗诀。\",\"见说多景楼前，凤凰台上，醉帽风吹裂。千古英豪消歇尽，江水至今悲咽。九死投荒，三年坐困，一样成愁绝。寄声知否，酒杯当酹松雪。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762aaec969db95accc0','念奴娇·雪','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"封姨忍冷，把彤云剪碎，满空抛撇。万点青山头忽老，人迹鸟飞俱绝。歌馆迷红，妆楼失翠，四顾成银阙。晚来江上渔蓑，归带香屑。\",\"对此应慕山阴，扁舟兴尽，空载黄昏月。归到溪堂门未掩，别有一般清洁。雪印梅花，梅横月影，三白交相合。纸窗光映，夜明不怕灯灭。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762aaec969dfedec0a5','念奴娇·雪','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"乾坤易老，叹风尘飘荡，河山分裂。名分纲常都扫地，曾有何人提挈。身翊飞龙，气吞胡马，赤手扶天阙。精忠照耀，一时名并日月。\",\"须信天理人心，自来不泯，千载思遗烈。庙貌燕山崇祀典，华表三忠新揭。西北中原，东南王气，回首惊风雪。伤心行路，不堪日暮时节。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762ac014812de699c92','鹧鸪天·春饮','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"新酒鹅黄漾碧樽。眼波相属意殷勤。梨花夜月陪歌扇，杨柳春风荡舞裙。\",\"穷乐事，庆芳辰。人生难得是相亲。仗谁塞却春归路，长使花前醉脸新。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762af8ca1f17015e5ba','摸鱼儿·春寒','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"悄无人、杏花院宇，终朝冷清清地。春阴只在重帘外，春意不知何处。门也闭。早不省、峭寒阵阵从何至。翠帏怕启。鹦鹉噤无声，香篝宿火，知是甚天气。\",\"风头转，窗外纸鸢飞坠。麻家节候难据。天公作意妨游事，罗绮未教轻试。闲议拟。柱教他、垂杨恼乱丝千缕。无情燕子。失了探芳期，风风雨雨，天也会憔悴。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762afe7f86568544b08','渔家傲·江村渔乐图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"万里清江春水碧。杨花乱点撩行客。日午钓篷惊雪白。无人拭。酒醒瓦枕双敧侧。\",\"一笑浮名真外物。五湖谁论云宽窄。香饵纶竿都弃掷。盘双膝。江山晚翠输长笛。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b08c63aac5ef34f4','烛影摇红·贺冯南淮','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"兰玉庭阶，秋风吹绿宜男草。高堂拂晓绣帘开，庆事知多少。云外飞来青鸟。报西池、瑶仙降早。金炉喷篆，宝蜡腾辉，祥烟缥缈。\",\"醉倚蟠桃，洞天春色人难老。屏间笑语簇钗金，膝下宫袍绕。更道莺迁近了。看还朝、风光又好。版舆先路，宝马鸣珂，帝城春晓。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b0be27d92b78851d','酹江月梅月画','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"乾坤扰扰，算人间、换了几番华发。一夜雪痕埋短屐，不见玉田行乐。鹤梦难醒，梅魂未返，诗景谁商略。溪山无主，西风千古寒索。\",\"南望十里龟山，牛眠有冢，草树迷狐貉。一滴重泉知道否，有酒无人同釂。今古斜阳，往来樵牧，枯木寒鸦落。临风怀感，登高时望城郭。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b0be27d92f082420','酹江月梅月画','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"月香水影，记西湖、曾踏六桥残雪。回首江山今在否，诗被梦惊秋铁。鹤老苍苔，屐闲青嶂，僧寺茶烟歇。被花相笑，十年何事轻别。\",\"谁道逋老风流，丹青有手，千里神相合。月又朦胧花又瘦，还是黄昏愁绝。小殿妆奁，寒城昼角，催老花时节。天涯搔首，霜风吹断华发。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b116aa741150701f','蝶恋花·谪所偶成','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"钟鼓楼头昏又晓。驰隙流光，易向忙中了。消得多情天亦老。镜中双鬓成秋草。\",\"白鸟正多人正少。身世江湖，梦里邯郸道。望断平芜江树杳。归心一点孤帆小。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b116aa743f3171cd','蝶恋花·谪所偶成','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"雨雨风风花事退。芳草愁痕，绿遍长亭外。辛苦杜鹃流血泪。碧山春尽行人去。\",\"城上楼高休遍倚。烟柳斜阳，总是伤心处。一点柔情风里絮。遥遥去度长江水。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b116aa749d3b01be','蝶恋花·谪所偶成','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春燕秋鸿惊节序。晓镜朱颜，容易成憔悴。花落花开人世事。闲愁一点无安处。\",\"饱后高眠醒后醉。眠醉工夫，正合闲中味。一笑不知南北异。小屏叠叠吴山翠。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b116aa74dc57b4d3','蝶恋花·谪所偶成','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"流水落花春不管。世路悠悠，回首天涯晚。怅望江南人又远。断魂零落屏山掩。\",\"愁懒渐添欢笑减。病酒经旬，腰带新移眼。燕子日长春梦短。东风门外杨花转。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b2b0f2580dd40470','西江月·红梅','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春色十分相似，清香一种元高。剽枝参出杏花梢。拦住风光多少。\",\"仙骨已经丹换，冰魂仍用莺招。九霞觞底醉春宵。回首月斜天晓。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b2fbf41acb5b3ceb','蝶恋花·题觉海寺壁','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一线功名牵鼻住。百岁闲身，卖向匆忙处。暮鼓才终鸡又起。空教老尽阎浮世。\",\"又是碧山无绝意。白羽青鞋，容我来还去。坐个蒲团抛个偈。世缘都付齁齁睡。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b51e8c5f43c96bfb','蝶恋花·同前','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"客子光阴诗卷里。过了烧灯，又报花朝至。回首小楼听雨处。杏花春意知谁主。\",\"牢落征途行未已。簇簇长亭，官柳摇旌旆。孤闷夜来添不寐。短篷江上风和雨。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b7b4fa55f9acfcc2','章台柳·感旧','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"连环手。分违久。离恨浓如酒。画栏十二锁朱楼，凭高不见章台柳。\",\"从归后。知安否。锦字何曾有。春云阁雨暗阳台，不堪梦里空回首。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b93599873e7816c6','薄幸春怨','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"海棠零乱。点苍苔，锦绣铺遍。破晓梦、子规啼血，蝴蝶一床惊散。恨小楼、昨夜东风，无情卷入闲庭院。便柳絮萦空，梨花翻雪，断送春光一半。\",\"自到得、清明后，罗袖上、泪痕不断。伤心向流水，溯红迎绿，往来觅取相思片。拟今回见。叹青鸾、无准依然，误却桃花面。昼长帘卷，羞睹归来双燕。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762b97952d519316713','行香子·墨芙蓉','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"不向东风。不慕春红。澹无言、意懒妆慵。生香难画，真色谁同。\",\"看浅深愁，浓淡泪，墨痕中。彩云又散，锦城又远，为何人、消损芳容。年华苒苒，昨梦匆匆。望夕阳边，莲棹去，水云空。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762baa48837556dd883','瑞鹤仙·寿词','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"芳尘纤雨过。正紫陌春光，二分才破。缃桃夜深吐。障东风台榭，锦云如堵。庭帏暖和。喜清镜、华年初度。卷珠帘、笑语生春，绛蜡玉花双股。\",\"庆贺。画屏影里，宝篆香中，神仙无数。履侵舄堕。莱衣舞。彩衣歌，蔼高堂喜气，祥烟缭绕，人在蓬莱洞府。愿年年、酒滴花前，醉颜如故。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762bcca6c6523cf3bf2','酹江月梅','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"疏枝冷干，向水边篱落，背人幽独。凭仗东君能慰藉，特遣阳和先属。比雪裁花，临池顾影，百卉皆羞缩。一清相映，白驹人在空谷。\",\"曾记腊雪晴时，瘦藤敝氅，行绕孤山麓。月到横梢钟已静，时有禽翻霜竹。饮散罗浮，信迟驿使，幽梦应难续。小轩孤倚，七弦弹遍冰玉。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762bcca6c6575459a89','酹江月梅','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"暗香浅水，被西山、扶上一痕冰月。瘦鹤伶仃人未寝，坐待茶烟清发。十五年间，四千里外，曾作罗浮客。参横斗转，不堪酒散人别。\",\"天意放我清闲，夜寒鹤氅，重踏西湖雪。醉眼朦朦花惨傪，道是相逢愁绝。林樾闲人，水涯幽士，千古还高洁。和羹休论，笛声且为吹彻。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762bd62e6a00ecd2ce3','念奴娇·咏燕，和人韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"卖花时节，正轻风园榭，弄晴池馆。绪语匆匆言不尽，穿得翠迷红乱。华屋门高，主人恩重，旧垒还堪恋。昭阳何处，望中清梦初断。\",\"长是泪洒东风，天涯无主，谁与寻方便。冷落孤身飘瀚海，倦翼不堪频展。绛缕年深，云轩事渺，老去知人厌。多情帘影，日高为我偷卷。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762c64d165cb487d0b2','清平乐·春朝雨中','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"厌厌夜雨。小院重门闭。无奈春寒推不去。向晓尚侵罗被。\",\"起来点检韶华。东风幸未梨花。独惜胭脂委地，闲庭落遍山茶。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762c76c7dd543470a0b','卜算子·冬夜不寐','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"画角断谯楼，珠斗横仙阙。榾柮无烟午夜长，茗碗分香雪。\",\"漏结翠壶冰，被展青绫铁。毕竟教人懒去眠，立尽梅花月。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762c926c0b04134354e','踏莎行·缃桃鸲鹆','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"旭日亭台，暖风园篽。双双飞上缃桃树。可人鹦鹉隔重帘，春声故遣惊春睡。\",\"金弹偷拈，雕笼半闭。花间别有藏机地。回头相顾各惊飞，画阑簌簌香红坠。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762c9e41a0918d36c7d','摸鱼儿·春游','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"喜朝来、柳烟花雨，寻春问人何处。香车宝马倾城出，坐拥南州歌吹。春好未。但只见、暖云芳草飘香气。鸥边浦溆。两两画船横，一篙深碧，撑向水云醉。\",\"浮生事，勘破蜗牛战蚁。百年希遇良会。五侯七贵貂蝉美，难免鬓丝僬悴。休辞去。最好是、柳条绾镫风牵袂。酒阑众起。归路月明中，严城钟鼓，花影满街碎。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762c9e83a54afcbec07','酹江月和宋人刘改之韵','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"区区世道，算红尘、何似水云宽处。云梦泽南摊睡簟，曾是旧游之路。城老芙蓉，台空箫管，下界多风雨。群仙相笑，问人何事劳苦。\",\"自要天柱峰头，仙人洞口，着登临长句。宝带万钉成甚用，到了终归黄土。篱下黄花，山中白酒，秋意须人主。愁心无奈，江南有梦归去。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762caca2926581436c8','蝶恋花·舟中客怀','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"门掩东风杨柳院。浅绿窗纱，深映宫妆面。隐约吴峰屏里见。梦魂去绕江南远。\",\"梁上飞来双海燕。万语千言，惹起情无限。点点落红心事乱。卷帘无语伤春晚。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762caca2926e9133c57','蝶恋花·舟中客怀','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"春草绿波南浦路。画舸亭亭，载取离愁去。无限江山牵别思。短帆夕照孤篷雨。\",\"罗帕新词对远泪。欲寄江南，未遇梅花使。赖有梦魂知此意。飞归夜夜三更里。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762ce452a8cd96259b3','齐天乐·送刘太守入觐','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"水风吹透菱滩绿，南湖赠人凉意。仙舫登龙，灵槎泛斗，千里明河秋霁。飘飘行旆。瞻紫盖神光，绛都佳气。手捧琅函，嵩呼人在五云际。\",\"湖山暂教无主，报亭空浮，碧峰孤环翠。后土风雷，帝青日月，元气一时交会。明廷眷注，久五裤闻谣，两歧书瑞。鳌抃彤墀，天颜应笑喜。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d127763c5bdb9df8','浣溪纱春日郊行','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"花气薰人似酒浓。野桥东畔草茸茸。睡余无事踏芳丛。\",\"弱柳撚成长短绿，小桃添注浅深红。最多情思是东风。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d15b371434f6726e','水龙吟·七夕，和施西亭','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"水花亭馆初凉，月痕照在葡桃架。星期此夕，针奁未启，蛛丝先挂。虹断东楼，雨收北渚，露盘清泻。望湘帘起处，果筵初设，儿女辈、花相亚。\",\"缥缈行云低度，耿河津、仙軿飞驾。鹊才传喜，虫还催织，欲留无暇。又是匆匆，离肠中断，幽欢空罢。叹天长地久，迢迢一水，两情终夜。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d17d8f15d8b656ed','薄命女长信秋词','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"更漏咽。严禁沉沉金锁合。别殿笙歌歇。\",\"薰余绣被香烬，睡觉银屏凉怯。翠辇不来莲炬灭。月到梧桐缺。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d65e89922ef2304d','长相思·闺情','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"苔满庭。草满庭。雨打梨花户半扃。相思魂梦惊。\",\"计归程。望归程。绿遍汀洲杜若生。天涯杜宇声。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d7bd4156fc2aadb5','烛影摇红·子规','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"落日空山，暝烟赴处行人绝。为谁携恨到江南，泣尽枝头血。最是五更寒彻。一声啼、一声悲咽。纱窗惊散，蝴蝶游魂，梨花香雪。\",\"才见飞来，料应未是经年别。吴山不减蜀山春，何事思归切。回首绿阴成叠。早东风、落红时节。闲情最苦，一春无绪，寸肠千结。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d7dbf6595a22b40a','鹧鸪天·素扇','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"一束清水入手温。华山飞落晓台云。金星散尽难成点，柳絮承来不见痕。\",\"鬟影乱，汗珠匀。清风招动羽车轮。游仙睡熟无人唤，压损羊家白练裙。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762d7f8c63be5c815fc','鹧鸪天·感春','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"九十春光逐逝流。烟花零落冷如秋。云镂宝月残歌扇，尘络纹窗暗酒楼。\",\"蝴蝶懒，杜鹃愁。天涯新绿满汀洲。恼人怕见杨花面，又被纷纷乱扑头。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762dd0f3ed7d2e624a6','水调歌头·庚辰初度','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"紫髯世外客，玄舄地行仙。归来历头屡改，鬓影只青年。那有贵官气象，不脱书生风味，坐榻冷无毡。要识心中事，白眼看青天。\",\"算人生，名易遂，节难全。纷纷金紫，一生歌舞在华筵。留取胸中正气，俯仰人间今古，雷电隐晴川。一笑又初度，聊尔当诗篇。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e14f6b500947e64f','苏武慢下第时作以下共四首','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"手卷珠帘，眼穷云树，楼外远山凝碧。篱菊黄时，水枫丹后，人在天南地北。一日三秋，寸心千里，难借凌空双舄。背斜阳、流水寒鸦，往事黯然今昔。\",\"当此际、罗带分香，红绡封泪，梦绕玉窗瑶室。离恨悠悠，愁怀耿耿，谩有拍樽云液。雁足难凭，马蹄无准，谁与远传消息。对西风、愁损菱花，潘鬓嗟非前日。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e14f6b5042431989','苏武慢下第时作以下共四首','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"竿袅长虹，钩弯新月，几向禹门垂钓。掣碎鳌头，踏翻桃浪，壮志已乖年少。龙虎飞腾，风雷际运，梦想玉堂书诏。挺乌纱、豪气填胸，长剑欲摩天表。\",\"回首处、数亩苍烟，半窗红日，依旧野花啼鸟。登山临水，弄月吟风，舒啸响惊云杪。雪案经书，萤窗灯火，历览古今俱了。看明年、雷动皇都，驰马杏园春晓。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e14f6b509b90cfab','苏武慢下第时作以下共四首','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"涧水流瓢，桃花夹岸，路入神仙寰宇。芍药比容，芙蓉如面，缥缈霞绡云缕。障风袅袅，隔水盈盈，兰麝欲薰芳语。俏多情、天遣相逢，总是飞琼俦侣。\",\"方信有、物外乾坤，壶中日月，身在蓬莱洞府。百鸟啼春，紫箫吹月，留我花间居住。业火未消，尘缘相误，一别顿成今古。待重来、烟水茫茫，惆怅乱红如雨。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e2703b892fc1760e','眼儿媚·暮春','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"点点飞花付东流。新绿染芳洲。数声杜宇，一帘风絮，春去难留。\",\"垂杨故作伤离态，风里舞轻柔。年华负也，梦迷湘水，恨锁秦楼。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e395a4e6dabac534','满庭芳·春草','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"山外斜阳，客边细雨，天涯一望关情。故人何处，春草又还生。长记当时南浦，同携手、别泪双零。音尘断，江淹已老，身世总堪惊。\",\"东风真薄幸，移将新绿，暗满归程。莽烧痕断处，一片愁城。烟霭秦川故道，牛羊外、多少长亭。孤村晚，杜鹃声急，马上酒微醒。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e5686693c511878b','玉蝴蝶·秋怀','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"寥落天涯情景，碧云合处，连日秋阴。人影西风，楼空雁字沉沉。晚楼台、一声长笛，静院宇、几处疏砧。遣寒侵。客尘破帽，如梦孤衾。\",\"沉吟。青铜看罢，年华荏苒，世路侵寻。宝剑埋光，锦囊三尺少知音。青衫湿、别愁不断，黄花老、归思难禁。黯登临。碧山远水，一片伤心。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762e575b801d747b8eb','齐天乐·寿人','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"东风吹醒南湖绿，腊痕放教春早。沁雪梅心，破烟柳眼，湔拂旧寒多少。烧灯未了。报庭院新晴，楼台清晓。银烛光中，绣帘开处篆烟袅。\",\"画堂移入蓬岛。拥群仙舄履，云韶缥缈。南极寿星，西池王母，一对后天不老。膝前围绕。有袍锦画辉，墨香秋饱。醉倚蟠桃，洞天春正好。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762eccca9cbff02c418','西江月·夏日西堂','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"纨扇欲抛还举，水沉将尽重添。南薰微透水晶帘。纹浪忽摇冰簟。\",\"杨柳鸣蜩绿暗，荷花落日红酣。晚凉浴起卸轻衫。新月一钩纤淡。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762ed95b598198d7bd4','巫山一段云·平湖秋月','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"鸦鸟层林晚，菰蒲片雨晴。长烟收尽小云平。湖月十分明。\",\"横笛临风曲，疏钟隔水声。两三渔火近芜汀。时有雁鹅惊。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f218881261ca3562','巫山一段云·飞鸣宿食雁','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"落日边声急，寒云客路愁。西风吹度岳阳楼。木叶正辞秋。\",\"阵乱惊渔火，声寒堕舶舟。月明投足荻花洲。聊缓稻粱谋。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f24091825f2e7c60','画堂春·秋眺','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"烟凝寒碧楚山秋。夕阳敛尽山头。长江无语向东流。目断归舟。\",\"红叶题残旧恨，翠眉蹙破新愁。月明雁字影悠悠。人依西楼。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f2409182c83cb269','画堂春·秋眺','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"井梧翻叶响秋声。玉阶风露凄清。阴虫无数草间鸣。泣尽残更。\",\"红泪暗流宝蜡，碧峰冷浸银屏。梦回帘外月华明。无限伤情。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f3cb7585365b0be6','长相思·两峰插云','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"南高峰。北高峰。南北峰高插太空。春云起夜龙。\",\"烟蒙蒙。雨蒙蒙。三月湖南春树浓。楼台烟雨中。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f4027b957fd21fbc','清平乐·书听雨堂壁','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"清时有味。不涉尘中事。狂舞酣歌高枕睡。更问神仙何处。\",\"从来春梦无多。世途何苦奔波。皱着双眉则甚，放教一笑如何。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f4502eb65530f506','贺新郎·送陈学谕之袁州教任','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"行色秋风里。望遥空、寒云一带，雁行南去。短短烟条溪上柳，欲挽仙舟无计。又总道、夕阳难驻。歌到阳关声渐咽，黯秋光、滴作青衿泪。离有顷，会无据。\",\"九年芳润滋芹水。种门墙、两场桃李，十围丹桂。呜世却须移木铎，吾道又看西矣。谁便说、青霄垂翅。金马他年期待诏，计文园、未减凌云气。重把酒，为伸意。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f7a800bb67232e89','满江红·镇江驿中','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"楼上春寒，倚危祢、杏花消瘦。重门外、檐铃四响，落梅风骤。茶梦做残银烛影，旅魂消尽铜壶漏。早纱窗、虚处月痕斜，三更后。\",\"眠未稳，吟难就。红炉冷，青绫绉。想雕房曲槛，少年时候。锦瑟坐调弦索手，罗衣醉拥薰香袖。到如今、惟只是相思，浓如旧。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f9838d0f55e7b3f3','蝶恋花·春意','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"借问春光今几许。杨柳梢头，一线轻黄缕。残月薄纱人乍起。胭脂冷淡梅妆洗。\",\"试绾香云寒堕指。不道东风，作恶还如此。妆罢开帘呼盥水。笼中鹦鹉传言语。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762f9838d0f5bf56a13','蝶恋花·春意','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"明月满楼霜满瓦。云冷鸳衾，梦里闻檐马。千里征衣谁寄也。相思满纸从头写。\",\"门外平芜迷旷野。落日青山，几见南归者。一一宾鸿飞上下。春山两点和愁画。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762fb481ce34ac7f9df','木兰花慢·题双凤呈祥卷贺冯太守得子','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"喜仙山诞果，百年事、未为迟。正华馆春融，红裁锦褓，翠揭犀帏。天公要成双美，五年间、两送玉麟儿。表表食牛豪气，亭亭玉树风姿。\",\"丹山长养羽毛奇，梧竹岂终栖。看鸣盛朝阳，览辉阿阁，浴采天池。声名直追三凤，笑燕山、落后月中枝。为报青霄鹓鹭，九成伫让来仪。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762fbca995e55234fe8','鹧鸪天·会饮有赠','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"潋滟流霞玳瑁钟。酡颜微映烛花红。两行飞雁瑶筝月，十里芳香锦障风。\",\"情有限，思何穷。天涯离别苦匆匆。梦魂不怕鸳鸯妒，潜作行云到月中。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762fc8c9585b9565a9a','锦堂春杏花','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"风色晚来庭院，雨声晓上帘栊。无言有恨何人会，斜倚粉墙东。\",\"仙苑盛夸年少，孤村遥指春红。京华旧梦江南路，回首夕阳中。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762fcdf84b0e85d895d','满江红·题太原官舍壁','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"归去来兮，青山好、欲归便得。人世事、风前灯焰，梦中槐国。驿马出门尘土路，舫斋听雨江湖客。把十年、忙冗换清闲，嗟何益。\",\"太行岭，孤云没。江南梦，重山隔。有何人、五湖烟棹，洞庭霜笛。宝剑醉看豪气在，银屏晚怯秋风入。向中宵、无意更闻鸡，空伊呖。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762fd7c5cd6aa39ef63','酹江月吴二尹西湖图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"红尘道路，笑西湖虽好，未曾相识。匹马经行天借便，一散松厅羁迹。解帽簪花，携壶贳酒，相约寻芳客。满篙春水，画船荡破晴碧。\",\"好是山色空蒙，水光潋滟，花柳酣风日。一片笙歌云锦地，依约楼台高出。峰北峰南，湖烟湖水，分付花翁笔。锦江何处，归装聊载春色。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762ff6cab516bfbd357','渔家傲·志渔卷','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"七尺顽躯多少事。浮生没个闲田地。借得桐江丝一缕。凭君计。要将白日竿头系。\",\"一笑水云忘世味。五湖是我安身处。梦在沧洲家在水。醒还醉。任他海月空来去。\"]',NULL,NULL,NULL,NULL),('e647d10f30f70762fff5f6ad023e1db9','长相思·风雨归渔图','陈霆','e647d10f30f70762d1457b72d1457b72','明',NULL,'[\"飘雨花。跃浪花。渺渺春江收钓车。东风蓑笠斜。\",\"系芦芽。近蒲芽。一叶轻舟寄浅沙。穿鱼向酒家。\"]',NULL,NULL,NULL,NULL),('e647d10f315aac866ffdaf8a0c3cdc4c','望江南','魏浣初','e647d10f315aac86d1457b72d1457b72','明',NULL,'[\"江南忆，佳味忆江鲜。刀鲚霜鳞娄水断，河豚雪乳福山船，齐到试灯前。\"]',NULL,NULL,NULL,NULL),('e647d10f3192432212f4d5da53006fe0','卜算子','朱无瑕','e647d10f31924322d1457b72d1457b72','明',NULL,'[\"梧叶荐新凉，好梦欺人短。数尽铜壶漏更长，别院砧声远。\",\"疏雨滴芭蕉，肠被西风断。何处寒蛩哀雁中，芦管吟秋怨。\"]',NULL,NULL,NULL,NULL),('e647d10f31bdfa3d8d0a4a07f18b3db3','临江仙·钱唐怀古','魏大中','e647d10f31bdfa3d70efba66a981f2b7','明',NULL,'[\"埋没钱塘歌吹里，当年却是皇都。赵家轻掷兴强胡。江山如许大，不用一钱沽。\",\"只有岳王泉下血，至今泛作西湖。可怜故事眼中无。但供侬醉后，囊句付奚奴。\"]',NULL,NULL,NULL,NULL),('e647d10f32564a6ad79e0aa8cc4ebf92','苏幕遮·惜春','雷迅','e647d10f32564a6ad1457b72d1457b72','明',NULL,'[\"柳含丝，梅破萼。报道东风，一夜穿帘幕。春光依旧人非昨，忽讶春光，不是人情薄。\",\"花前醉，月下乐，只恐韶华，易去难留阁。明朝雨暗胭脂落，莫怨春光，不管人消索。\"]',NULL,NULL,NULL,NULL),('e647d10f3298cbeb4f55865f8089907e','朝中措·平山堂和欧公韵','归庄','e647d10f3298cbeb69d658d0a0f3601d','明',NULL,'[\"山连霄汉草连空，楼阁碧虚中。第五泉边试酌，飒然两腋生风。\",\"千秋胜概，残阳绿树，暮霭疏钟。非复当年栏槛，风流犹想仙翁。\"]',NULL,NULL,NULL,NULL),('e647d10f333143281368c9140ea8f53d','临江仙','薛素素','e647d10f33314328d1457b72d1457b72','明',NULL,'[\"唤起提壶池上饮，春残满地红英。忽闻墙外子规声，不如归去也，终是不分明。\",\"自抱云和弹一曲，曲终还拟湘灵。风前泪眼几时晴，月高星数点，香冷漏三更。\"]',NULL,NULL,NULL,NULL),('e647d10f333d924b5e7b68d9c7862f32','折杨柳枝词戏赠朱文昭','黄哲','e647d10f333d924bd1457b7270feb62b','明',NULL,'[\"绿阴驰道绕隋宫，拂雨撩烟送晚风。万树千条都折尽，不堪回首画桥东。\"]',NULL,NULL,NULL,NULL),('e647d10f337eb827e1cb82e5a8b70d1e','满庭芳·吴江舟中读雪堂新诗寄钱尔斐','黄道周','e647d10f337eb827228499b5e4873aa9','明',NULL,'[\"大研停书，谁似尔，玄徽高寄。把银河曳练，宝幢涌地。青鸟衔笺来阆苑，朱鱼结佩传仙字。忆长干、小巷驻霜蹄，曾摇辔。\",\"早无意，搴赤帜。慵开眼，翻金匮。但谢安高卧，灌夫直视。且喜榻穿身尚健，从知韦绝心逾细。更钓鳌、海上瞰沧洲，调香饵。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3eb874d04d03651e21','西湖竹枝歌四首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"听乐鱼龙出渼陂，学飞凫雁绕昆池。青林半锁瑶台暮，绿浪平铺画舸移。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3eb874d04d10a96a2d','西湖竹枝歌四首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"双鹤徘徊几夕阳，孤山云水似潇湘。重游好探东风信，万树梅花舣雪航。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3eb874d04d2ffda986','西湖竹枝歌四首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"四山楼殿锁清秋，一水萦洄岛屿幽。咫尺蓬瀛休道远，画船箫鼓在中流。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3eb874d04de3be98fb','西湖竹枝歌四首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"倚棹汀洲草色齐，回船涛浪满苏堤。万家杨柳青门外，一片芙蓉绿水西。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d307c96de0','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"郑村渡口风浪多，鱼船来去疾如梭。相邀女伴到梁下，一时同唱采菱歌。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d30ee37fc7','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"两岸清溪中有洲，山花红白遍溪头。闻说西门好风景，大家同上木兰舟。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d31c304999','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"城南回望告天台，衢婺悠悠二水来。清献前身为时出，萧然琴鹤在蓬莱。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d34085fede','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"严陵东头七里滩，家家门户钓鱼竿。良人乍夜趁潮去，将船卖酒入临安。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d346fc59ee','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"渡口狂风掀白蘋，惊波猛浪不愁人。乍宵西崦踏青罢，明到兰阴赛水神。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d378c2986e','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"四柱凌云排六鳌，铁絙高系最坚牢。水母难兴泗洲浪，蛟精不作九江涛。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d37a7f2bef','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"兰阴苍苍北固遥，中洲水面似金焦。欲把湖头比溪上，若个三山在六桥。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d37fcc1535','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"饮水白虹千丈强，来来去去罢褰裳。中流不碍蚤行客，隔水休呼夜渡娘。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d39c7fe510','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"苎萝村中花满蹊，庐江亦有仲卿妻。银钏金钗照溪水，往往来来唱大堤。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d3b7f75ee9','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"溪上浮梁春涨平，蒙蒙烟雾懒回程。风秋潭上踏歌去，水远山高无限情。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d3dbb6c64e','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"三百阑干浮碧空，笙歌缭绕暮云中。天香夜半广寒落，错认银桥在月宫。\"]',NULL,NULL,NULL,NULL),('e647d10f339e6a3ee7a2a7d3e4c1429e','兰江竹枝词十二首','胡应麟','e647d10f339e6a3e4e6cd9521bc0249a','明',NULL,'[\"金台尖高高插天，俯江楼俯夕阳边。当年板筑遁荒野，今日舟梁济大川。\"]',NULL,NULL,NULL,NULL),('e647d10f33cfd25d576594840e32a5b8','长相思','陈洪绶','e647d10f33cfd25d83e8ef51207f8801','明',NULL,'[\"病起迟，懒起迟，蜂子排衙叫午鸡。床头改旧词。\",\"趱归期，办归期，忽值精神恍惚时，归期定复移。\"]',NULL,NULL,NULL,NULL),('e647d10f33cfd25d9b02ba6debe14682','菩萨蛮','陈洪绶','e647d10f33cfd25d83e8ef51207f8801','明',NULL,'[\"小姑居处朱楼起，乌啼声隐杨花里。香气出罗衣，能留蝴蝶飞。\",\"远山青可见，绣领遮团扇。小立看鸳鸯，心怜浴故双。\"]',NULL,NULL,NULL,NULL),('e647d10f33cfd25da6631786531b22a5','更漏子','陈洪绶','e647d10f33cfd25d83e8ef51207f8801','明',NULL,'[\"一重山，一重水，有甚别离情思。开扇面，展屏风，丹青都是侬。\",\"杭州客，并州况，吴越两山相望。茸母发，豆娘飞，望侬还浙西。\"]',NULL,NULL,NULL,NULL),('e647d10f33cfd25de429ae792bdf9b58','如梦令','陈洪绶','e647d10f33cfd25d83e8ef51207f8801','明',NULL,'[\"瓢笠春行无几，望断桃花流水。彻夜雨潺潺，到天明晴矣。\",\"梦喜！梦喜！又是花朝晴起。\"]',NULL,NULL,NULL,NULL),('e647d10f33f11afda490a81a705a01b9','西湖杨柳枝词','孙继皋','e647d10f33f11afd6b8eba43a14ac55a','明',NULL,'[\"湖边无数古禅林，尽是游人与布金。日晚西山钟磬发，随风远近到湖心。\"]',NULL,NULL,NULL,NULL),('e647d10f33f11afda490a81abcd4edf3','西湖杨柳枝词','孙继皋','e647d10f33f11afd6b8eba43a14ac55a','明',NULL,'[\"江南十月早寒催，杨柳千条叶半摧。闲杀六桥堤上路，月明不见踏歌来。\"]',NULL,NULL,NULL,NULL),('e647d10f33f11afda490a81ac6e05762','西湖杨柳枝词','孙继皋','e647d10f33f11afd6b8eba43a14ac55a','明',NULL,'[\"西湖十月暖风吹，游客乘船醉不辞。日落波停山掩黛，却怜西子罢妆时。\"]',NULL,NULL,NULL,NULL),('e647d10f33f11afda490a81afe9f54a5','西湖杨柳枝词','孙继皋','e647d10f33f11afd6b8eba43a14ac55a','明',NULL,'[\"西湖十月烟水平，游船两两趁风轻。不知春满桃花岸，多少女郎连袂行。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fa90bdbd82c855f6df','满江红','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"炎暑方阑，忽惊眼、庭前一叶。报新秋、蝉鸣深树，日暮悲切。叹息人世千年事，空老却英雄豪杰。□□□、□□□□□，□□□。\",\"门掩后，孤灯灭。圜窗照，清夜月。静思量，教人愁肠结。几时恩诏归田里，丝纶随处少遮迾。莫道他、渭水子牙翁，胸臆别。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fac320ed60041005bc','踏莎行·有感六首','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"志扶颠隮，身陷网罗。先忧特为此心多。气吞天际长虹势，手障人间东逝波。\",\"楼上秦曲，军前楚歌。英雄羽客总消磨。惟有忠灵永不散，与君携手吊湘娥。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fac320ed6005de5303','踏莎行·有感六首','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"雾隐棘墙，露低庭树。多难悠悠今未已。百年世事总成空，一笑翻复任眼底。\",\"地角乡关，天涯囹圄。回首君门几万里。豁然芦杖步圜阶，西风吹散燕山雨。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fac320ed6008f2321d','踏莎行·有感六首','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"百年运数，一旦遭逢。天涯长夜叹梦梦。未惜宇宙托蜉蝣，空余禾黍慨王风。\",\"眼前靡靡，心上忡忡。难免幽圜今固穷。仰视青天与白日，冥冥照我此丹衷。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fac320ed602e98e3e4','踏莎行·有感六首','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"黄叶萧萧，西风浩浩。犴中忽讶寒先到。每将心事对晴天，肯因穷老伤怀抱。\",\"浮沤古今，云雨世道。漫把荣辱堪作笑。男儿何事郁襟怀，自有鸢飞与鱼跃。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fac320ed605a310da1','踏莎行·有感六首','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"孤怀炯炯，天畔茫茫。北风日夜此幽窗。秦馆两行七日泪，燕庭一片五月霜。\",\"月临日照，地久天长。不尽当年烈士伤。遗下霄壤千古恨，总出人臣忠义肠。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5fac320ed60d32e95b5','踏莎行·有感六首','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"大道昭明，人心不死。一理绵绵贯终始。消息盈虚总循环，须知万古皆如此。\",\"君父恩德，臣子忠义。存吾之死矢靡已。留取丹怀日浩然，天地无穷永相倚。\"]',NULL,NULL,NULL,NULL),('e647d10f353bb5faeb1c9c8cc3adf827','沁园春','杨爵','e647d10f353bb5fa1714726cc88d8d0a','明',NULL,'[\"金风转律，流光暗易，壮士萧条。存丹天，一点点自陶濯，一身幽困，两岁长宵。知作闲事，乐天委运，念头此外更无劳。静坐间，把宇宙踪迹，闲虑一遭。\",\"生平努力相高，都做了，灰荡与尘消。看世事多般，奇奇巧巧，于人何有，也自逍遥。狂风回乱，浮云翻转，莫休莫歇枉分挠。适怀抱，抬头窗外，秋雨萧萧。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f0bcf8912','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"瑞云渺渺葛山巅，柳软花柔艳雪天。太白谪来尘世里，阳春一曲吐青莲。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f0e8945a2','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"不献蟠桃唱竹枝，愿君富寿更多儿。明春再祝长安道，同上红楼续旧词。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f0fbf9929','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"花枝浓淡映青阳，把酒轻浮琥珀光。愿得年年春色懒，红梅香递九霞觞。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f2541503b','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"金谷春光烂锦丝，和风吹拂万年枝。碧云名谪青云里，玉液恩浓太液池。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f3bda4bb6','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"章台柳色应知改，回首青青可奈何。莫恨瑶台无尔伴，云中好去觅仙娥。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f5b26172a','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"梅花破腊发琼枝，乍转东风淡荡时。天上恰开王母宴，仙郎醉捧紫金卮。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f7cbb0ba5','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"轻烟晴日百年初，五色玄芝产玉除。学得长生丹已熟，蓬莱何事觅仙书。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570f86e5aef7','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"潇洒卢生意自如，樽前今日问何吁。想因素室传书后，天半飞来素女无。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570fa8e40b21','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"何姑曾共泛螺舟，咫尺相思天际悠。恨杀青鸾无处觅，春风醉过岳阳楼。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570fc81b8d7e','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"白玉仙童吹紫鸾，飞来次第落洺干。他年乱舞琼筵上，赢得吴姬笑眼看。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570ff17ae8cb','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"西行怪道懒于云，方朔原来为细君。但得玄珠生老蚌，何劳飞舄渡河汾。\"]',NULL,NULL,NULL,NULL),('e647d10f35808a1dbed0570ffa79117a','赠卢十二竹枝词十二首','申佳允','e647d10f35808a1dd1457b72d1457b72','明',NULL,'[\"云隔巫山十二峰，玄霜独酌醉思浓。莫愁应早归金屋，织女何须七夕逢。\"]',NULL,NULL,NULL,NULL),('e647d10f35c4d7bf1d1a3fc23d16b079','醉花阴·夏日','谢季兰','e647d10f35c4d7bfd1457b72d1457b72','明',NULL,'[\"熏风淡荡吹罗袖。槐柳新蟾骤。池内小荷翻，何处箫声，吹彻梅花透。\",\"如今景物还依旧。茉莉花开后。不坐已愁人，可怪黄鹂，叫得垂杨瘦。\"]',NULL,NULL,NULL,NULL),('e647d10f35c4d7bff9c74a56b91c64a6','清平乐·暮春夜感','谢季兰','e647d10f35c4d7bfd1457b72d1457b72','明',NULL,'[\"怜花惜月。情意凭谁说。正好韶光天又夺。处处子规啼血。\",\"凝思不比春初。空床闲挂流苏。欲睡何曾成寐，支颐无限踌躇。\"]',NULL,NULL,NULL,NULL),('e647d10f35c4d7bffb6b50890951b343','阮郎归·秋夜','谢季兰','e647d10f35c4d7bfd1457b72d1457b72','明',NULL,'[\"数尽更筹梦未成，秋声不忍听。起来且自剔残灯，谁家玉笛声。\",\"风才定，月初升，清光渐渐生。一行花影上窗棂，动人无限情。\"]',NULL,NULL,NULL,NULL),('e647d10f373e1e47ae3b058ea2115446','玉楼春·题柳洲待别图送刘跃如','李日华','e647d10f373e1e47d961e9f242a39645','明',NULL,'[\"轻暖轻寒无意绪，朝来几阵梨花雨。妆成独自倚阑干，暗数落红愁不语。\",\"杜宇一声人欲去，残云片片依沙渚。横塘十里柳烟浓，维舟正在烟深处。\"]',NULL,NULL,NULL,NULL),('e647d10f377338e46e59c981e8fa2d72','蓦山溪','朱茂','e647d10f377338e4d1457b72d1457b72','明',NULL,'[\"云蓝小袖，约素随堤柳。翠黛远山横，描不就、一双眉秀。娇波明盼，著处更留人，情初逗，杯残酒，双陆垂纤手。\",\"芳心半有，玉纽教郎扣。不道解相思，恁花枝、为谁消瘦。幽窗曲槛，灯火二更余，欢未久，宵空负，那正归时候。\"]',NULL,NULL,NULL,NULL),('e647d10f377338e47ee42e547e00dfe3','选冠子闻秋虫有感','朱茂','e647d10f377338e4d1457b72d1457b72','明',NULL,'[\"半枕留寒，一帘凝露，四壁秋虫愁听。凄其诉月，断续吟风，不管孤眠不稳。还似初离别时，絮语喁喁，柔情耿耿。助幽怀作出，悲凉情绪，伴他长恁。\",\"记那日、步袜宫弯，苍苔移印，写尽梧桐清影。回廊笑织，曲槛鸣丝，携手红楼娇俊。谁分今宵对伊，荒馆败篱，唤人孤另。叹行云何日，重逢枉忆，露台玉井。\"]',NULL,NULL,NULL,NULL),('e647d10f37a147cef137049bf0a645a9','阮郎归','魏学濂','e647d10f37a147ced1457b72d1457b72','明',NULL,'[\"去年抛菂种池塘，今年坠粉香。几时得藕便丝长，何曾解断肠。\",\"驱燕子，打鸳鸯，摘莲偷卜郎。擘开多半是空房，羞看枕簟双。\"]',NULL,NULL,NULL,NULL),('e647d10f37a147cef32f182e2684576f','浣溪沙','魏学濂','e647d10f37a147ced1457b72d1457b72','明',NULL,'[\"漠漠微寒到水滨，半秋无恙似初春。鸳鸯来啄影中人。\",\"柳拂浪痕轻似梦，苔沿侬步细于尘。酿成闲恨为谁颦。\"]',NULL,NULL,NULL,NULL),('e647d10f37f6e62138501bb963da69f9','杨柳枝·词','伍瑞隆','e647d10f37f6e621228499b586d7c8a0','明',NULL,'[\"二月江城杨柳春，赠行那得几枝新。闺中只自怜攀折，不见杨花愁杀人。\"]',NULL,NULL,NULL,NULL),('e647d10f37f6e62138501bb9c752b944','杨柳枝·词','伍瑞隆','e647d10f37f6e621228499b586d7c8a0','明',NULL,'[\"城边客舍水边桥，柳色青青送寂寥。无那春风不相惜，夜来吹断最长条。\"]',NULL,NULL,NULL,NULL),('e647d10f37f6e62183cfc9c226eb63c9','竹枝词','伍瑞隆','e647d10f37f6e621228499b586d7c8a0','明',NULL,'[\"蝴蝶花开蝴蝶飞，鹧鸪草长鹧鸪啼。庭前种得相思树，落尽相思人未归。\"]',NULL,NULL,NULL,NULL),('e647d10f37f6e621df2be99c435dcd79','杨柳枝','伍瑞隆','e647d10f37f6e621228499b586d7c8a0','明',NULL,'[\"笼烟带雨绿千般，柳叶初长不耐寒。无限春情与春恨，不知谁为倚阑干。\"]',NULL,NULL,NULL,NULL),('e647d10f3865915410382e459af49dbd','花犯·立夏咏梅子用清真梅花韵','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"亚枝低，累累挂绿，秀才卖风味。绀珠匀缀，忆嚼徵含宫，龋齿妍丽。佳人手摘檐牙倚。吞酸私自喜。记前月、红潮初尽，香篝曾共被。\",\"心中难抛撇仁儿，春山皱蜇得，和愁憔悴。还可惜，双头打，流莺同坠。难苏醒、日长人困，刚脆滴、樱桃浓睡里。巧一阵、楝花风过，吹芳魂似水。\"]',NULL,NULL,NULL,NULL),('e647d10f38659154211d883f29f39a1e','玉漏迟·咏促织','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"向空阶絮语，不知何处，愁人偏切。金井银床，一夜碎啼明月。怪杀天涯荡子，禁得过、凉天时节。声暗咽。暮灯初起，晓钟才歇。\",\"可怜枕上关山，被片晌凄凉，惊回蝴蝶。独院孤衾，此际更和谁说。追忆金笼闲斗，佯输与、一双条脱。空怅别，雕阑桂花如雪。\"]',NULL,NULL,NULL,NULL),('e647d10f386591542c1b9d4c5b81afb6','霜天晓角·卖花用竹山摘花韵','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"睡起煎茶，听低声卖花。留住卖花人问，红杏下、是谁家。\",\"儿家，花肯赊，却怜花瘦些。花瘦关卿何事，且插朵、玉搔斜。\"]',NULL,NULL,NULL,NULL),('e647d10f3865915450f55a1894494e4e','瑶花咏雪','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"先春冰散入，暝花明没，檐牙高啄。篱梢庭卉，折几枝，都似梳翎冻鹤。玉人何处，正暖炙、鹅笙小阁。倚兽炉、纤指频温，字涩红牙新学。\",\"珠帘十二重遮，想此处清寒，不上梅萼。金樽翠袖，生俊煞、呵笔彩毫相角。苦吟句就，忆驴背、旧游如昨。较浅斟低唱今宵，如此风情不恶。\"]',NULL,NULL,NULL,NULL),('e647d10f38659154613b42b9b1baf91b','小楼连苑食新栗作','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"秋园白露初肥，家家拾紫登山麓。金风小罅，吴霜乍点，嫩黄才足。未出瑶房，暗惊纤手，颦眉刺触。喜合欢腰细，于中微束，称鸳鸯比肩名目。\",\"作意春尖笑剥，褪中衣、软温涩缩。漫投欢袖，可堪君口，情甜意熟。度脆饴唇，回甘犀齿，秋魁冠玉。耻雕盘荐饰，红笺私裹，寄缄重复。\"]',NULL,NULL,NULL,NULL),('e647d10f38659154636d90dc0dc8c51b','帝台春春恨用南唐元宗韵','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"花影脉脉，春愁黯无力。成阵乱红，不管人愁，无风飞急。冷落秋千上已后，较愁似、去年今日。问多情，燕子刚来，天涯消息。\",\"无消息，归未得，守月直到灯黑。尽夜夜朝朝泪痕多，准不过一江潮汐。人去今春已非旧，春去那人可如昔。便侬做东风，肯花枝怜惜。\"]',NULL,NULL,NULL,NULL),('e647d10f386591549d284ba0a3e52a78','寻芳草·和稼轩韵','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"这里一双泪，却愁湿那厢儿被。被窝中忘却今夜里，上床时不曾睡。\",\"睡也没心情，搅恼杀雪狸窜戏。怎月儿不会人儿意，单照见阑干字。\"]',NULL,NULL,NULL,NULL),('e647d10f38659154d63f7ccdcc1c54fa','暗香·梅花用白石韵','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"凄凄夜色，且醉扶红袖，倚楼横笛。唤起玉龙，斜月鳞鳞晓星摘。回首故人天际，欲书寄瑶华无笔。谁共我、枕石山中，霜冷露盈席。\",\"南国，音信寂。纵千里相思，方寸堆积，悲吟当泣。不是思君定谁忆？犹记一枝送影，香梦人、罗浮空碧。奈可翠禽叫转，不归也得。\"]',NULL,NULL,NULL,NULL),('e647d10f38659154f89838421be4a513','东坡引·晓起','彭孙贻','e647d10f3865915471a58e8ca0f3601d','明',NULL,'[\"绮窗红日皦，余睡残灯小。水沉宿火烟犹袅，卖花声恁早。\",\"小鬟呼未应，熏炉闲抱，暖玉手、调歌鸟。鹦歌念熟休忘了，春眠不觉晓，春眠不觉晓。\"]',NULL,NULL,NULL,NULL),('e647d10f3870563c154effbd2834c3e9','浪淘沙','查应光','e647d10f3870563cd1457b72d1457b72','明',NULL,'[\"白发乱侵头，懒上高楼，朦胧新月曲如钩。槛外苹风惊乍起，暑气潜收。\",\"宿雨涨回流，喜泛轻鸥，蓼花一望满汀洲。正好开襟延赏处，无限新秋。\"]',NULL,NULL,NULL,NULL),('e647d10f390e4191c2ebbe3b2a14a480','鹧鸪天','林景清','e647d10f390e4191b49c63fab49c63fa','明',NULL,'[\"几字娇蛾恨不开，阳台今作望夫台。月方好处人相别，潮未平时仆已催！\",\"听嘱咐，莫疑猜，蓬壶有路去还来。穆穆一样垂丝柳，休傍他人门户栽！\"]',NULL,NULL,NULL,NULL),('e647d10f391635f890bdbd8206c5517e','满江红','胡介','e647d10f391635f8d1457b72d1457b72','明',NULL,'[\"走马归来，西陵下、斜阳满树。回首处、酒垆犹在，河山非故。久客不知家远近，重来却怪人惊顾。听啼鹃、也道不如归，归何处。\",\"草玄阁，兰台署；扬州梦，秦淮渡。走人间未遍，苍凉日暮。惆怅辽东丁令鹤，当年华表谁为主。但相逢、莫负故人心，三生路。\"]',NULL,NULL,NULL,NULL),('e647d10f3930e72b8e91484446fa6bbb','眼儿媚','许友','e647d10f3930e72bd1457b72d1457b72','明',NULL,'[\"精魂石上忆三生，寒夜与君盟。帘前明月，窗间小饮，楼上残更。\",\"而今闲坐记芳情，庞儿较可憎。亸肩倚案，低头弄笔，斜眼挑灯。\"]',NULL,NULL,NULL,NULL),('e647d10f394c327270333a6224dfafd8','采莲词二首','丽江木知府','e647d10f394c3272d1457b72d1457b72','明',NULL,'[\"弱袂长鬟荡水中，钏文钗影入荷丛。鸣榔惊起韩朋鸟，一个西来一个东。\"]',NULL,NULL,NULL,NULL),('e647d10f394c327270333a62d65a25ac','采莲词二首','丽江木知府','e647d10f394c3272d1457b72d1457b72','明',NULL,'[\"石榴裙卷足如霜，折得红莲满抱香。羞向人前女儿貌，手遮西日看湖湘。\"]',NULL,NULL,NULL,NULL),('e647d10f39b8ea6fa9384cfded8f8f37','浪淘沙·水宿','吴闻礼','e647d10f39b8ea6fd1457b72d1457b72','明',NULL,'[\"枕畔水泠泠，并作秋声，布衾初拥别愁生。风露自寒天自远，梦隔江城。\",\"篷底漏疏星，何处渔灯，桐江钓后一裘轻。宦海不如归去稳，烟渚芦汀。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a1a15b6fe282b6324','点绛唇·早春有感','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"小院黄昏，一庭淡月人声俏。梅花开了。春信知多少。\",\"又是一番，芳草天涯道。伤怀抱。年年憔悴，不似春归早。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a25665b36f5abbecc','水龙吟','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"萧萧风雨江天，凄凉一片秋声逗。香消菡萏，绿催蕙草，烟迷远岫。浪卷长空，云轻碧汉，薄罗凉透。恨西风吹起，一腔闲闷，那胜镜中消瘦。\",\"寂寞文园秋色，这情怀、问天知否。檐铃敲铁，琅玕折玉，听残更漏。淡月疏帘，小庭曲槛，且还斟酒。算从来、千古堪悲，何用空沾衫袖。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a2b56b33e766ba2a9','浣溪沙·同两妹戏赠母婢随春','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"杨柳风初缕缕轻。晓妆无力倚云屏。帘前草色最关情。\",\"欲折花枝嗔舞蝶，半回春梦恼啼莺。日长深院理秦筝。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a36eb580851aeac32','百字令秋怀','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"秋光潇洒，正清江收潦，芙蓉写颊。扇影辞人凉入苎，叹故国经时别。月皎风凄，梦回花谢，多少乡情切。凭高对酒，幽怀几度凄结。\",\"无奈窗户萧条，闲情冷落，篆缕销金鸭。客梦悠悠何日了，旧恨新愁万叠。病骨支离，年华屡换，罗袖长啼血。可怜双鬓，应知一夜堪镊。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a637b4e83889c7bdb','浣溪沙·春恨','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"窗外梅花落素英。隔帘啼鸟弄春晴。断肠芳草又青青。\",\"独倚画鸾愁日暮，半笼金鸭怯寒生。闲思心事暗伤情。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a8383bcb6fc021d4b','玉蝴蝶·秋思','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"惆怅别来岁换，清秋风月，几度悲伤。极目蒹葭，烟水一片微茫。黯魂飞、闲愁空断，还怅望、孤闷偏长。对池塘，红消残碧，绿怨初黄。\",\"凄凉。蛩吟小院，露寒金井，月绕回廊。诗酒潇疏，旧游新恨最难忘。掩重门、卧残清昼，理瑶瑟、烧尽炉香。数流光，秋灯闪淡，无限彷徨。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7a88711f11f6828bd8','浣溪沙·新竹','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"百尺高抽出画墙。娟娟含粉秀冰霜。静临深院日初长。\",\"翠霭蒙空笼曙色，清阴摇月照宵凉。南薰池馆占风光。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7ab987aa4f08cfd7db','水龙吟·次母韵早秋感旧，同两妹作','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"秋来忆别江头，依稀如昨皆成旧。罗巾滴泪，魂销古渡，折残烟柳。砌冷蛩悲，月寒风啸，几惊秋又。叹人生世上，无端忽忽，空题往事搔首。\",\"犹记当初曾约，石城淮水山如绣。追游难许，空嗟两地，一番眉皱。枕簟凉生，天涯梦破，肠断时候。愿从今、但向花前，莫问流光如奏。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7adf35823b61d8b2aa','浣溪沙·前阕与妹同韵，妹以未尽更作再赠','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"翠黛轻描桂叶新。柳腰袅娜袜生尘。风前斜立不胜春。\",\"细语娇声羞觅婿，清胪粉面惯嗔人。无端长自恼芳心。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af29ff18eddf30e1a','点绛唇','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"往事堪伤，旧游绿遍池塘上。闲愁千丈。暗逐庭芜长。\",\"自古多情，偏惹多惆怅。添悽怆。寒宵淡月，一片凄凉况。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e1593d43e','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"风雨闲庭锁寂寥。又看春色一分消。翠屏斜倚思无聊。\",\"梦觉情踪无处问，闷来心绪最难描。殢人残病恨今朝。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e3fafe574','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"几日轻寒懒上楼。重帘低控小银钩。东风深锁一窗幽。\",\"昼永半消春寂寂，梦残独语思悠悠。近来长自只知愁。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e56d08315','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"斗草庭边事已迁。画栏花影只依然。紫箫凄断绿窗前。\",\"寥落春深人病酒，萧条香径柳垂烟。东风回首总堪怜。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e5d7fe509','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"日日枝头堕粉香。东君何事苦匆忙。鸟啼花落送韶光。\",\"泪蹙翠山情杳杳，闷连青海思茫茫。含颦无语立斜阳。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e5e63476a','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"憔悴东风鬓影轻。年年春色苦关情。清魂无奈酒初醒。\",\"啼鸟数声人睡起，催花一霎雨还晴。断肠时节正清明。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e65393740','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"燕子初来垒故巢。晓莺啼恨更添娇。一春都是等闲抛。\",\"不怨满庭风雨恶，只教终日梦魂消。东风空锁绿杨腰。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e6ddb66da','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"清昼沉沉掩碧纱。困慵梳洗髻松鸦。蕙炉间袅篆烟斜。\",\"剪剪轻寒生绣户，霏霏细雨著庭花。一窗新梦晚风赊。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e6ed2ec20','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"正是黄昏欲断凝。不堪帘外雨馀声。倚屏愁绝最难听。\",\"红烛来时朱户掩，篆香消处翠炉清。又看新月照疏棂。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e932a81e9','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"芳草依依道路斜。白云何处是仙家。空馀远碧暮云赊。\",\"红泪滴残清夜月，梦魂长绕淡梨花。几番临镜黯伤嗟。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182e93f82e0c','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"闲闷闲愁不自持。几回消尽又如丝。小庭惆怅日迟迟。\",\"细雨斜风寒食后，子规残月梦回时。此情试问有谁知。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182ec35c6d72','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"一段春慵晓镜中。怕闻花气入帘栊。夜来思遍旧情踪。\",\"粉蝶迷残烟草绿，晚风落尽海棠红。凭栏千里暮云重。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182ecb990fe5','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"寂寂重帘画影沉。网丝牵恨入愁吟。浪痕和梦怨春深。\",\"罗袂暗松金缕扣，惜花潜减旧时心。小楼几日怯登临。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af32f182eda3d03c4','浣溪沙','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"昨夜轻寒透薄罗。晓来微雨忽相过。红英一半已看无。\",\"好句漫成嫌未切，那知总为恨难模。日长双黛奈颦何。\"]',NULL,NULL,NULL,NULL),('e647d10f3a144f7af954bd9b3cd26086','菩萨蛮·代闺人春愁','叶纨纨','e647d10f3a144f7a8f19793bfaafda66','明',NULL,'[\"罗巾拭遍伤春泪。夜长香冷人无寐。独坐小窗前。孤灯照黯然。\",\"关情双紫燕。肠断鸳鸯伴。无奈武陵迷。恨如芳草萋。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c20f96b56a','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"船上女儿画春娥。一叶飘随水上波。左手牵妹右牵板，云冠不动青峨峨。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c211986ded','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"玉娘一曲叫天鹅。此地曾经牙板过。十五年前细腰柳，而今枯瘦十围多。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2197740e9','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"龙洲江口水如空。龙洲女儿挟巨艟。奔涛泼面郎惊否，看我船歌八尺风。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c257f31b3a','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"东街晴雪未消泥。南陌阴?又涨堤。恰似江头娼女面，乍时欢笑乍时啼。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c263caec02','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"雪里山茶取次红。白头孀妇哭春风。自从貂虎横行后，十室金钱九室空。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c268bbf515','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"武溪葱翠独称梁，正望黄山一点苍。三日风头两日雨，谢公昨夜拜梁王。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c26e1c8332','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"泥深一丈怯开门。恶风恶雨恶梦魂。才唤妇来又唤去，斑鸠唤得也顽烦。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2752202fe','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"侬家生长在河干，夫婿如鱼不去滩。冬夜趁霜春趁水，芦花被底一生寒。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c277f64c9b','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"长樯铁鹿破洪涛。木绵子花七尺高。搜时蛮力强三虎，抛掷轻如一鸟毛。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c29831aa15','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"芦花枝上水痕新，南市东村打白鳞。只在梁山山背面，梁山何苦不离人。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2a1537f61','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"陌上相逢尽楚腰。凉州一曲写吴绡。鹍弦拨尽南湖月，更与唱歌到板桥。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2b2cd1d46','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"贾客相逢倍惘然。楩楠杞梓下西川。青天处处横珰虎，鬻女陪男偿税钱。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2d08e67d7','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"吴儿纤晰语音侬。北客高檐项领重。江西贾子面如漆，裤褶行缠伴老佣。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2e64e0b5b','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"百钱一尺买吴丝。焦发龙钟旧玉儿。记得庚寅前后事，万红堆里插花枝。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2ecc29a28','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"一片春烟剪縠罗。吴声软媚似吴娥。楚妃不解调吴肉，硬字乾音信口吪。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ec83cfc9c2ef248b90','竹枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"一溪才顺一溪湾，一尺才过一丈还。船子已愁箭括水，儿童又指帽儿山。\"]',NULL,NULL,NULL,NULL),('e647d10f3a3739ecb253d4d1e31f78b5','柳枝词','袁宏道','e647d10f3a3739ecdfa92d8fa14ac55a','明',NULL,'[\"江南柳絮已纷飞。河北垂杨未折枝。一种青娥娇眼女，南家嫁早北家迟。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb912f4d5daf834eb7e','卜算子','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"酒醒夜堂凉，雨过湘帘卷。时见流萤度短墙，乍近依然远。\",\"欲睡更迟徊，徙倚阑干遍。不觉西楼阙月斜，寂寞桐阴转。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb9606d191eb43aa7be','青玉案','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明','此词着意描绘夏景，抒写闺中人的生活情态。夏日亭午，如火的榴花和满地绿荫给庭院带来特异的气氛，使人如痴如醉。闺中人午睡刚醒，娇眼蒙眬，喃喃自语，情困意慵。一阵清风吹来，扇不摇而自凉。翠竹白苎亭亭玉立，摇曳多姿，丝毫没有感到暑意。作者工画，故以画家之笔，勾勒人物景象，确是词中有画。全词纤丽娟秀，妩媚多姿。 ','[\"庭下石榴花乱吐，满地绿阴亭午。午睡觉来时自语，悠扬魂梦，黯然情绪，蝴蝶过墙去。\",\"骎骎娇眼开仍殢，悄无人至还凝伫。团扇不摇风自举，盈盈翠竹，纤纤白苎。不受些儿暑。\"]',NULL,NULL,NULL,'[\"亭午：正午。\",\"骎骎：形容眼光急迫不安。：滞涩。\",\"苎：苎麻，多年生草本。\",\"些儿：一点儿。 \"]'),('e647d10f3a8c1eb96a10f5084c91193c','风入松','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"简汤子重，汤居碧凤坊西斋睡起雨蒙蒙，双燕语帘栊。平生行乐都成梦，难忘处、碧凤坊中。酒散风生棋局，诗成月在梧桐。\",\"近来多病不相逢，高兴若为同。清樽白苎交新夏，应辜负、绿树阴浓。凭仗柴门莫掩，兴来拟扣墙东。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb96a10f508753dcc05','风入松','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"近来无奈病淹留，十日废梳头。避风帘幕何曾卷，翛然处、古鼎香浮。兴至闲书棐几，困来时覆茶瓯。\",\"新凉如水簟纹流，六月类清秋。手抛团扇拈书册，无情绪、欲展还休。最是诗成酒醒，月明时度南楼。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb990bdbd82005b939d','满江红','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"漠漠轻阴，正梅子、弄黄时节。最恼是、欲晴还雨，乍寒又热。燕子梨花都过也，小楼无那伤春别。傍阑干、欲语更沉吟，终难说。\",\"一点点，杨花雪；一片片，榆钱荚。渐西垣日隐，晚凉清绝。池面盈盈清浅水，柳梢淡淡黄昏月。是何人、吹彻玉参差，情凄切。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb990bdbd82c986ea8b','满江红','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明','词的上片直接点题，夹叙夹议，主要通过史实，引发人们对岳武穆蒙冤受屈产生愤慨。\n「拂拭残碑，敕飞字、依稀堪读。」起首从叙事起，引出以下直至终篇的慷慨。残碑的发掘出土，以铁的事实证明高宗当年褒奖武穆千真万确。这便是「倚飞何重」的证据，可后来为什么又把武穆残酷地杀害了呢？\n「岂是功成身合死，可怜事去言难赎。」词人举古来不合理之事相对照，以见武穆之冤。\n「最无辜、堪恨又堪悲，风波狱。」末二句归结到「后来何酷」的事实。上阕略叙事实，深致感叹，于感叹中连发三层疑议，层层紧逼，引起无限激愤，自然导入下片对事理的分析。\n下片剖析武穆被杀的原因。\n「岂不念，疆圻蹙；岂不念，徽钦辱。」岂不念国家的疆界在敌人侵略下日渐缩小，岂不念徽钦二帝被俘的耻辱。这本不成问题的，作为问题提出来，正在于它出于寻常事理之外，那只能是别有用心了。\n「念徽钦既返，此身何属。」实乃一针见血之论。鞭辟入里，不仅辛辣地诛挞了宋高宗丑恶的内心世界，也是数千年帝王争位夺权史中黑暗内幕的大曝光，读后令人拍案击节。「千载休谈南渡错，当时自怕中原复。」二句揭出高宗必杀武穆的原因。高宗为了保住自己的帝位，可以置徼钦二帝死活于不顾。武穆一贯主张抗金，恢复中原，且到朱仙镇大捷，中原恢复有望，再发展下去，势必直接危及高宗帝位。武穆被杀害，自然也就不足为奇了。\n「笑区区、一桧亦何能，逢其欲。」结尾二句归到武穆悲剧的产生，乃出于君相的罪恶默契。暴露了高宗的卑鄙自私的龌龊心理，武穆之冤狱也可以大白于天下了。\n此词纯以议论着笔，可当作一篇精彩的史论来读。全词以敕碑引发，渐次深入，既对武穆的遭遇表示了深刻的同情，又对宋高宗不以国家人民利益为重，残害忠良进行毫不留情地挞伐，语言犀利。此词犹如一篇宣判词，揭示了虚伪自私的宋高宗的真面目。它痛快淋漓，极具史胆史识，可谓咏史词的杰作。','[\"拂拭残碑，敕飞字，依稀堪读。慨当初，倚飞何重，后来何酷。果是功成身合死，可怜事去言难赎。最无辜，堪恨更堪悲，风波狱。\",\"岂不念，疆圻蹙；岂不恤，徽钦辱。但徽钦既返，此身何属。千载休谈南渡错，当时自怕中原复。笑区区、一桧亦何能，逢其欲。\"]',NULL,'拂拭去残碑上的尘土，当年石刻的宋高宗信托岳武穆时的诏书还可依稀辨读，令人感慨万分地是，皇帝当初对武穆是何等的器重，后来又为什么那样的残酷，难道是功高震主就身当该死，可惜事过境迁高宗依托武穆的诏书难赎惨杀武穆的罪恶，最令人感到可恨可悲而又极为无理的是，秦桧等人一手制造的杀害武穆的风波亭冤狱。\n宋朝的皇帝啊！难道你就不想疆土在逐日散失，难道你就不思念徽钦被俘而去的奇耻大辱，然而徽宗钦宗真正返回之后，赵构的帝位又怎能相属，千年万代的人们啊再不要说不该南渡偏安一隅，当时的赵构啊自己就怕把中原收复，可笑地是区区一个秦桧又有多少能耐，只是他迎合了赵构的心意而已。','此词是由明代词人文征明创作的一首词。整首词将矛头直指皇帝，颇有见地。整首词感情沉着而慷慨，议论痛快而淋漓。','[\"敕飞字：敕，帝王下给臣子的诏命；飞，指南宋民族英雄、抗金名将岳武穆。\",\"难赎：指难以挽回损亡。\",\"疆圻蹙：疆域缩少，指金人南侵，南宋的版图已远小于北宋。\",\"徽钦辱：宣和七年（西元一一二五年），金兵南侵，直逼宋都汴京，宋徽宗赵佶见事不可为，急忙传位给宋钦宗赵桓。靖康二年（西元一一二七年），金兵攻破汴京，掳徽宗、钦宗二帝北还，北宋由此灭亡。\",\"南渡：徽、钦宗二帝被掳后，赵构以康王入继大统，是为高宗。他不知耻，不念父兄，自汴梁（开封）迁都临安（杭州）以图偏安，史称南渡。\",\"桧：指秦桧。\",\"逢：迎合。\",\"欲：愿望，需要。\"]'),('e647d10f3a8c1eb9a991d594e469045c','风入松·行春桥望月','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"夜凉斜倚赤阑桥，天远碧烟消。酒醒忽见花间影，轻云散、月在林梢。野火青山隐隐，渔歌绿水迢迢。\",\"当年曾此醉清宵，共棹木兰桡。白头重蹋行春路，同游伴、半已难招。夜静单衫露冷，玉人何处吹箫。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb9b8bac719f06027d2','念奴娇·中秋对月','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明','此词浮想殊奇，造语浪漫，对月之人似亲昨月宫，月中景物如降落左右，词人貌似仙人，而怀中仍洗不尽尘世烦恼，人间天上浑然写来，仙骨凡心杂错吐露，其主旨全在上下两结拍，上结“欲跨彩云飞起”，有超俗之想，下结“莫负广寒沈醉”，又感伤显，大起大落中，将矛盾的心态淋漓吐出。','[\"桂花浮玉，正月满天街，夜凉如洗。风泛须眉并骨寒，人在水晶宫里。蛟龙偃蹇，观阙嵯峨，缥缈笙歌沸。霜华满地，欲跨彩云飞起。\",\"记得去年今夕，酾酒溪亭，淡月云来去。千里江山昨梦非，转眼秋光如许。青雀西来，嫦娥报我，道佳期近矣。寄言俦侣，莫负广寒沉醉。\"]',NULL,'枝头的桂花像垂着的块块白玉，圆月映照了整个苍穹，夜空好似被洗净了一般。风拂动着眉梢和身躯，人儿仿佛就在水晶宫殿里一样。遥看天际，龙翻偃舞，宫殿如画，能感受到那儿歌舞升平而沸腾的气氛。白霜（月光）铺满大地，（我）愿意乘着缤纷的云朵腾空而起。\n仍记得旧年的今夜，于溪亭酌酒畅饮，望云飘月移。过往的情境如刚消逝的梦，转眼却到了去年此时。自西而来的青雀与嫦娥都告知我佳节（中秋）快到了。（我）寄托旧知好友捎信于你，万万别辜负了这月宫甘甜的香醪。','此词浮想殊奇，造语浪漫，其主旨全在上下两结拍，上结“欲跨彩云飞起”，有超俗之想，下结“莫负广寒沈醉”，又感伤显，大起大落中，将矛盾的心态淋漓吐出。',NULL),('e647d10f3a8c1eb9d8514cafd65fc02f','南乡子','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"香暖透春肌，满眼新娇睡起迟。燕子不来三月尽，依依，手撚残花玉一枝。\",\"风袅鬓云垂，无限闲愁隐翠眉。好梦不成春自去，相思，只有青团扇子知。\"]',NULL,NULL,NULL,NULL),('e647d10f3a8c1eb9d9483e1422523325','新荔篇','文徵明','e647d10f3a8c1eb942ffcf05020c8bfa','明',NULL,'[\"锦苞紫膜白雪肤，海南生荔天下无。\",\"盐蒸蜜渍失真性，平生所见唯萎枯。\",\"相传尤物不离土，畏冷那得来三吴。\",\"顾家传来三四株，桂林翠幄森森殊。\",\"远人无凭未敢信，持问闽士咸惊呼。\",\"还闻累累生数子，绛绡裹玉分明是。\",\"未论香色果如何，只说形模已珍美。\",\"千载空流北客涎，一朝忽落馋夫齿。\",\"白图蔡谱漫夸张，文饰寍如亲目视。\",\"饱啖只于乡里足，鲜尝渐去京师迩。\",\"不须更作岭南人，只恐又无天下痏。\",\"朝来自讶还自疑，事出非常有如此。\",\"虽云远附商船达，不谓滋培遂生活。\",\"始知生物无近远。故应好事能回斡。\",\"卉物聊占地气迁，造化竟为人事夺。\",\"仙人本是海山姿，从此江乡亦萌蘖。\",\"山来沃衍说吾乡，异品珍尝曾不乏。\",\"不缘此物便增重，无乃人心贵希阔。\",\"福山杨梅洞庭柑，佳名久已擅东南。\",\"风情气味不相下，称绝今兼荔枝三。\"]',NULL,NULL,NULL,'[\"石田：文征明的同乡好友沈周，字启南，号石田。他的诗、文、画都很有名。\",\"芃芃：茂盛，旺盛。\",\"盐蒸蜜渍：荔枝的加工方法。这两句是说平时见到的荔枝都是加工后的枯萎了的荔枝。\",\"尤物：本意是指人、物中特别或异于寻常的，这里指荔枝。\",\"三吴：指苏州、常州、湖州（浙江吴兴）。\",\"桂林：因荔枝叶象桂树叶，所以称桂林。\",\"森森：树木茂盛的样子。\",\"闽士：指福建见过荔枝的人士。\",\"\\u201C千载空流北客涎\\u201D句：几千年来北方人只能听到荔枝甘美的传说，空流下涎水。北客，泛指荔枝产区以北人。\",\"馋夫：诙谐话语，指这次尝到了顾家荔枝的人。\",\"漫：满，这里可以解作\\u201C充分\\u201D。这两句是说，白居易的图、蔡襄的谱画得写得尽管好，但又怎么能跟亲眼见到的荔枝相比呢？\",\"迩：近。这两句是说：假如三吴一带能种活荔枝，饱啖荔枝的愿望就能在这里得到满足，京师要尝到鲜荔枝也将比从福建、广东、四川驿马传送要近了。\",\"\\u201C不须更作岭南人\\u201D句：反苏轼\\u201C不辞长作岭南人\\u201D一句而成。\",\"\\u201C只恐又无天下痏\\u201D句：苏轼《荔枝叹》：\\u201C我愿天公怜赤子，莫生尤物为疮痏。\\u201D\\u201C疮痏\\u201D指历代用驿马传送荔枝给百姓带来祸害。\\u201C天下痏\\u201D就出在这首诗。但在这里，\\u201C痏\\u201D是借指荔枝。痏（wěi）：本意是疮、瘢痕。\",\"回斡（wò）：回旋，这里是转变的意思。\",\"卉：草木，指荔枝。\",\"聊：姑且。\",\"\\u201C造化竟为人事夺\\u201D句：老天爷的意志竟被人扭转了。即指荔枝在常熟种活了。\",\"\\u201C仙人本是海山姿\\u201D句：借苏轼《四月十一日初食荔枝》：\\u201C海山仙人绛罗襦。\\u201D因荔枝生长在南海滨，所以称\\u201C海山仙人\\u201D。\",\"江乡：长江一带。\",\"沃衍：平野肥润地区。全句是说三吴一带本是肥润地区。\",\"无乃：无奈，无可如何。\",\"希阔：稀少而路途遥远。\",\"福山：常熟县北的覆釜山，又名福山。\"]'),('e647d10f3b5bcc467aeb68a83181e486','点绛唇·琵琶','陆锡明','e647d10f3b5bcc46d1457b72d1457b72','明',NULL,'[\"三尺冰弦，夜深弹破青天窍。意中人杳，只有清光到。\",\"芦荻萧萧，都入相思调。愁怀抱。嫦娥心照，诉与伊知道。\"]',NULL,NULL,NULL,NULL),('e647d10f3bc6d1fc6ffdaf8a29404e85','望江南','徐?','e647d10f3bc6d1fcd1457b72d1457b72','明',NULL,'[\"城上角，吹动薜萝烟。别意难忘灯下约，归期空向梦中传。消息杳如年。\",\"孤馆客，今夕不成眠。万井寒砧敲夜月，数声黄叶坠秋天。人在碧云边。\"]',NULL,NULL,NULL,NULL),('e647d10f3c4477922793517a347a146d','惜分飞·和陈霞栖韵','杨仪','e647d10f3c447792d1457b72d1457b72','明',NULL,'[\"月户花阶凡几度，尽把芳尊嘱付。转眼浓华故，鸾胶总被秦娘误。\",\"榆荚杨花曾入赋，也解漫天飞舞。梦觉西堂午，琵琶声断闻鹦鹉。\"]',NULL,NULL,NULL,NULL),('e647d10f3c44779270e1038b093b56ae','桃源忆故人访周汝寺','杨仪','e647d10f3c447792d1457b72d1457b72','明',NULL,'[\"单衣晓怯东风冷，忽过湖波千顷。山色倒侵溪影，一路随孤艇。\",\"美人室远飞云迥，遥忆小堂春永。睡起碧纱人静，隔竹敲新茗。\"]',NULL,NULL,NULL,NULL),('e647d10f3c5703fd949794295d27dde2','满江红·春情','季孟莲','e647d10f3c5703fdd1457b72d1457b72','明',NULL,'[\"燕子何时，衔去了、旧年人约。镇帘栊、恹恹春睡，东风遍恶。夜雨忽添窗外草，晓晴乱惹楼头鹊。算赢来、一段好相思，何曾学。\",\"而今事，当初错；别时泪，空流落。怕归来重问，有词难却。天远梦随骢马倦，昼长人并风筝弱。望河桥、万柳织黄烟，愁遮幕。\"]',NULL,NULL,NULL,NULL),('e647d10f3c673fde66b67a6870d890f9','曲游春·和查伊璜客珠江「元」韵','陆钰','e647d10f3c673fdeb49c63fab49c63fa','明',NULL,'[\"问牡丹开未。正乳燕身轻，雏莺声细。共听《霓裳》，看为雨为云，胡天胡帝。与君行乐处，经回首、依稀都记。携来丝竹东山，几度尊前杖底。\",\"鼙鼓东南动地。见下濑楼船，旌旗无际。未免关情，对楚岭春风，吴江秋水。暗洒英雄泪。更莫问、年来心事。又是午梦惊残，歌声乍起。\"]',NULL,NULL,NULL,NULL),('e647d10f3c673fde87c8b53818a56a27','曲游春·三叠韵','陆钰','e647d10f3c673fdeb49c63fab49c63fa','明',NULL,'[\"晓日还升未？正虬箭犹传，兽烟初细。鸣鸟闲关，痛精卫炎姬，子规川帝。千载人何处。笑符谶、何劳悬记。欣然更拓云蓝 ，自写新词窗底。\",\"窗外光阴遍地。才画角飘残，一声天际。竖子成名，念英雄难问，夕阳流水。独下新亭泪。尽寂寞、闲居无事。谁论江左夷吾 ，关西伯起。\"]',NULL,NULL,NULL,NULL),('e647d10f3c673fdea32a05ea911adc0c','浪淘沙·松径挂斜晖','陆钰','e647d10f3c673fdeb49c63fab49c63fa','明',NULL,'[\"松径挂斜晖。闲叩禅扉。故人踪迹久离违。握手夕阳西下路，未忍言归。\",\"此地是耶非。千载依依。采香径外越来溪。碧繶缃绚今尚在，歌舞全稀。\"]',NULL,NULL,NULL,NULL),('e647d10f3c673fdece8ac41be98c2096','浪淘沙·高阁俯行云','陆钰','e647d10f3c673fdeb49c63fab49c63fa','明',NULL,'[\"高阁俯行云。我一相闻。主人几榻迥无尘。世外兴亡弹指劫，一著输君。\",\"回首太湖濆。断霭纷纷。扁舟应笑馆娃人。比拟子阳 西蜀事，话到残曛。\"]',NULL,NULL,NULL,NULL),('e647d10f3c673fdee2d9c56d52a526f8','曲游春·再叠韵','陆钰','e647d10f3c673fdeb49c63fab49c63fa','明',NULL,'[\"渌酒曾笋未？羡肉脆 丝清，宫浮商细。塞耳休听，任佗雄南越，秦称西帝。青史兴衰处，尽简阅、纷纶难记。不如倚杖临风，一任醉□花底。\",\"芳草斜阳藉地。看远树天边，归舟云际。曲里新声，怨羌笛关山，陇西流水。又湿青衫泪。那更惜、阑珊春事。却看杨柳梢头，一轮月起。\"]',NULL,NULL,NULL,NULL),('e647d10f3d31c82390bdbd8245881d1e','满江红','吴朏','e647d10f3d31c823d1457b72d1457b72','明',NULL,'[\"雨抹荷池，添艳了、闹红稠绿。更带着、牵鱼藻鉴，浮波凫玉。榴朵辞枝焚碧藓，蝉声送远翻新曲。向幽轩、一枕梦惊回，风敲竹。\",\"丝雨霁，轻凉足；残照短，纤红续。渐云遮津口，烟黏山麓。凉月照人心似水，此身拟向清虚蹴。记广寒、庭畔素衣中，曾相熟。\"]',NULL,NULL,NULL,NULL),('e647d10f3e766f2f4c71d622b50d4b27','转应曲','王鸿儒','e647d10f3e766f2fd1457b72d1457b72','明',NULL,'[\"金井，金井，梧叶银床露冷。千门万户砧声，北斗城头月明。\",\"明月，明月，偏照旅人华发。\"]',NULL,NULL,NULL,NULL),('e647d10f3e766f2fe9d588e560c078af','踏莎行·赏菊','王鸿儒','e647d10f3e766f2fd1457b72d1457b72','明',NULL,'[\"燕子初归，芙蓉乍老，苍苔院落桐阴小。一帘疏雨晚来晴，繁香不断寒花袅。\",\"著谱人非，餐英事杳，风流未必今时少。且须痛饮读离骚，灵修岂肯捐芳草。\"]',NULL,NULL,NULL,NULL),('e647d10f3e77c9ab50130efc55aad0ec','菩萨蛮·落花','卓发之','e647d10f3e77c9abd1457b72d1457b72','明',NULL,'[\"小玉楼前风雨急，春光一霎都狼藉。桃叶与桃根，谁家最断魂。\",\"尊前回首望，昨夜花成浪。总是雨收时，月明空满枝。\"]',NULL,NULL,NULL,NULL),('e647d10f3eb6894697b5b8da25551b71','满庭芳','吴宗达','e647d10f3eb68946d1457b72d1457b72','明',NULL,'[\"蕉长青笺，笋抽斑管，闷怀欲写无多。鸾台凤阁，心事久蹉跎。小搆数篆茅屋，但傍取、千顷澄波。扁舟系，酒筒茶灶，随意泊烟萝。\",\"知音三两客，朝朝暮暮，百遍相过。判冷吟闲醉，雅谑狂歌。风雨不妨闭户，凭栏处、嫩蕊柔柯。君知否，才情标格，千载忆东坡。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78407184433c3021c75','太常引·连阴书事','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"湿云宿树暗楼前。潮上绿苔砖。烟雨满江天。似画出、王维辋川。\",\"蔓墙虫篆，草池蛙鼓，飞溜聒高眠。海阔晚风颠。最稳是、溪翁钓船。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78407a5b39911ea30c6','西江月·六月二十日初暑书事','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"久雨相连伏日，太阳初变炎天。绿槐绕屋未鸣蝉。浴罢新携团扇。\",\"子病亲临药灶，童归愁问禾田。藤床移近水亭边。卧看星河西转。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7840b09fb3929f2afa8','水龙吟·三首寿青溪主者','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"先生文武长才，五城三岛频来去。绛霄绕室，紫烟炼鼎，青云得路。铜狄摩挲，琳书披阅，玉京游步。想乘牛函谷，飞凫邺郡，今与古、同高趣。\",\"一曲青溪回护。自逍遥、蕊宫深处。黄眉洗髓，洪崖拍手，赤松为侣。桃实千年，芝茎三秀，喜迎初度。任九霞、觞满七星车转，看仙童舞。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7840b09fb3980b02f03','水龙吟·三首寿青溪主者','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"碧天双岫门开，澄澜幽竹环仙境。长庚孕秀，明河借润，孤蟾争炯。叠嶂楼前，桐君山下，几年驰骋。说芹池雨化，杏坛春满，缃卷富、青衿整。\",\"桑柘鹅湖佳景。对清樽、且忘毡冷。紫阳罄欬，春雷响震，秋虫声静。老屋无尘，短檠听雪，寒炉烹茗。看回头、捧得诏黄香墨，出中书省。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7840b09fb39a3b139aa','水龙吟·三首寿青溪主者','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"酒阑和梦登程，秋阴压得征鞍重。似华峰霜隼，禹门雷鲤，丹山云凤。滴露松窗，煮泉术鼎，漉冰齑瓮。向金闺高步，羽林雄论，便写就、中兴颂。\",\"主将远提兵众。细评量、古人言动。太公韬略，萧何图籍，孔明擒纵。茗荚吹香，芝英产秀，荔支修贡。更此行、妙处访求贤俊，助明时用。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707d45d5aabb','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"东鲁圣人后，流派浙江东。一家叔侄兄弟，取俊棘围中。雁荡秀分灵嶂，蟾阙香熏老桂，文燄烂摩空。讲鼓震铅阜，户屦蔼儒风。\",\"一角麟，千里马，九和弓。人材超伟，如此道出紫阳翁。正拟洪钟待叩，便跨征鞍归去，多士诵成功。再踏玉京路，射策大明宫。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707d69819929','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"都城柳丝绿，曾跨锦骢游。玉堂紫薇花发，不听故人留。却忆江东云树，薄采浙西芹藻，毡冷亦风流。移榻谢山下，菡萏碧波浮。\",\"煮茶垆，题诗笔，庋书楼。潇潇官舍，如此疏鬓不胜秋。教雨润流名郡，爱日晴烘归路，未许久林邱。回首五云里，鸣玉凤池头。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707d722a7ffd','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"皇天万物祖，生气本冲和。忍令古今天下，治少乱常多。血溅中原戎马，烟起长江樯櫓，沧海沸鲸波。割据十三载，无处不干戈。\",\"问皇天，天不语，意如何。几多佳丽，都邑烟草莽平坡。苔锁河边白骨，月照闺中嫠妇，赤子困沉疴。天运必有在，早听大风歌。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707d8301a624','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"先生乃儒者，有道出羲黄。袖将攀桂名手，种作杏林芳。江月玻瓈万顷，山雪琼瑶一色，开户挹寒光。清气满胸臆，何况有奇方。\",\"鹤凌云，鹏击水，凤鸣阳。平生济物，心在随寓寄行藏。丹鼎芙蓉紫艳，宝杵芝苓玉屑，坚子避膏肓。神圣可医国，功奏十全良。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707d8497ecd4','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"江上两峰立，门户自天开。香芹翠绕精舍，畅望美人来。麟凤洲中仙骨，龙虎榜中文物，绛帐育英才。椽笔泚银浪，澎湃走风雷。\",\"锦盈机，冰作鉴，玉无埃。笑谈驰骋，今古况是旧经魁。培植三年盛业，修举前时旷典，回首把离杯。老桂吐清馥，飞步上瑶台。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707db35e8cc5','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"良医比良相，活物是奇功。乾坤万古生意，收入药囊中。屋上石城云树，砌下秦淮烟浪，掩映杏花红。仲景有家学，照耀大江东。\",\"悟金丹，传宝诀，契参同。一方共仰，司命台閤誉何崇。谈笑香生兰室，指顾春回茅舍，沉痼扫除空。行矣展高志，寿域蔼仁风。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707db8f0d99a','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"秋高兴何远，爽气掬星河。雨晴山势飞动，楼外雁来多。丹桂香凝幕府，银烛光摇青琐，试问夜如何。天地大无外，老子尽婆娑。\",\"写兵机，修马政，咏铙歌。西风莫添，华发壮志未消磨。眼见帝都龙虎，人似仙洲麟凤，留我共銮坡。把酒暂舒啸，明月借金波。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707dd3710117','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"天地一开辟，日月几东西。古今气化无息，万物岂能齐。春树珍禽韵巧，秋水红鳞影捷，松石伴幽栖。佳景与心会，得句或无题。\",\"碧油幢，苍玉佩，紫金泥。明时文武，勋业我亦弃锄犁。志在螭头直笔，道在床头古易，奏策济群黎。野鹤忽飞到，清梦绕山溪。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78414a5707dfc4c281a','水调歌头·九首送汪教授','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"柯山倚天碧，秀耸浙东南。惟公与山齐德，结屋对巉岩。门外紫芝瑶草，窗下丹炉玉杵，元气此钟含。药镜发灵彩，金匮启玄缄。\",\"凤凰台，龙虎地，驻行骖。佳声飞满，当路毡冷自能谙。手握回生大造，心悟成仙秘诀，神效过苏耽。云外鹤书至，衣袂染柔蓝。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c784196168875700c4f7','太常引·偶述','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"对人无语叙寒暄。多病似文园。学未造渊源。空写到、千言万言。\",\"长江雨歇，高天露下，星绕紫微垣。山友结驯猿。尽占取、清风满轩。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7842e4d1417cfec50c8','太常引·书巢二首','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"苍芸缃轴几周遭。身世占清高。风雨不飘摇。最相称、先生一瓢。\",\"鲲鹏海阔，鹪鹩枝上，各自遂逍遥。经史隽芳膏。似养翮、翱翔九霄。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7842e4d1417ea4740c0','太常引·书巢二首','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"一巢结在万书丛。营葺半生功。稽古豁然通。任踪迹、幽栖此中。\",\"鹤居松顶，龟藏莲叶，意思亦相同。牖户敞玲珑。是理窟、包涵太空。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78434d158a8e4bcabbe','太常引·壬寅季夏即事','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"江城六月雨声寒。河汉倒云端。白浪渺怀山。\",\"笑门巷、撑船往还。龙吟水面，鱼游砌上，田野势漫漫。稼穑本艰难，问何事、天公太悭。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c784413ae4e013839199','太常引·松风','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"怪龙涌起半天潮。惊破月明宵。爽籁自然调。来慰藉、山中寂寥。\",\"瑶琴罢鼓，茶铛息沸，一片奏仙韶。两耳洗烦嚣。且莫效、当时弃瓢。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7845b6261e659109574','木兰花慢·送教授汪处谦','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"羡莲花博士，珠照乘、璧连城。更经笥生香，诗筒寄兴，教铎扬声。故家老成文物，便銮坡宝炬被光荣。来勺姑溪秋水，雅怀一样澄清。\",\"芹宫灯火月华明。时主校文衡。向玉洁堂前，咏归亭上，飞动欢情。堪怜盍簪无几，又梅边归路马蹄轻。此去朝天有日，御炉烟袅瑶京。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c784695e8b0dad0c68e9','竹枝词四首','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"昨夜床头灯结花，朝来浣女立江沙。洗得纻衣。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c784786acd08ef4b2ec3','渔家傲·敬次上所赋','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"驻马坡前观虎踞。金陵都会兴龙处。共沐普天恩似雨。芳草渡。江边营垒人家住。\",\"御柳映街笼翠雾。锦衣银甲青骢驭。文武百官班簉鹭。呼好侣。军门献纳勤来去。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c7847d2df8d4a8145fba','金缕曲·夜宿省中有怀贺久孚','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"庭树秋声冷。夜迢迢、漏传银箭，月明华省。最惜稽山无贺老，短烛照人孤影。做好梦、又还惊醒。风透围屏青绫薄，且披衣、立傍梧桐井。兵卫肃，画廊静。\",\"江湖聚散如萍梗。笑谈间、云霄蹑足，一鞭驰骋。万壑水晶天不夜，人在玉真仙境。说近日、四郊无警。兵后遗民归田里，渐桑麻、绿映鹅湖岭。须再见，好光景。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c78492d7bb76f2d758ed','大江东去送段伯文赴太平帅府经历','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"紫薇香冷，着风生银翰，月筛朱箔。面有长淮清润气，培以老成才学。菡萏波澄，琼花雾敛，梦跨扬州鹤。案尘不染，声华飞动台閤。\",\"天上妙选仙官，牙绯恩重，婉画清油幕。坐对江山雄丽处，依旧太平城郭。令肃貔貅，欢腾鸡犬，千里苏民瘼。云间路阔，一樽试为君酌。\"]',NULL,NULL,NULL,NULL),('e647d10f3f12c784c0e7f31acab8e81f','太常引·晚景','陶安','e647d10f3f12c784dfd7468a81ca0262','明',NULL,'[\"透云鱼尾缕晴红。缥渺水晶宫。人在小桥东。看丝柳、轻摇晚风。\",\"断霞飞练，远烟凝紫，山势活如龙。浴罢倚长松。爱归鸟、孤飞半空。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e12c667e63fa2ed569','贺新郎·六十初度自寿','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"初度今朝又。叹匆匆、年华逝水，岂堪回首。道老而今真是老，花甲已周之候。钟鼎勒、此生何有。圣世虚叨风云会，春卿崇重惭虚谬。只此志，誓无负。\",\"朝来独把梅花嗅。笑尘容、落寞萧条，与花同瘦。俯仰乾坤箕裘事，喜有龙儿凝秀。抱迂拙、从来如旧。欲把年筹重再数，不知天意还从否。聊自适，且斟酒。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e18a0c652656aa11fa','洞仙歌·中秋无月和徐少湖','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"广寒宫冷，望杳蓝桥路。无奈婵娟被风妒。驾长梯、扫开云翳，放出冰毬，又只恐、海底骊龙抱住。\",\"有良朋三五。漫倒芳尊，且教迟回唱金缕。洗梧桐露下、衣袂生凉，漏转层城未归去。\",\"已?此度负佳期，看明岁、今宵会伊何处。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e19a4cda7bc83d1122','百字令和韵赠翟石门阁老','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"奇才间气，应不是、三代后来人物。学海文林，曾览尽、禹穴泰山鲁壁。星斗胸罗，烟云笔涌，不拟赋梁园雪。勋名德望，中朝共仰瑰杰。\",\"此日位列三台，从容弘化理，佐天生发。雅范光仪，愧颛蒙、鄙吝为公消灭。世路纵横，尽风尘扰扰，不侵毫发。碧潭秋水，骊龙独抱明月。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1a760aeae1ac6bd98','题画四首秦楼月','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"江南泽国秋，野水连天净。日落渚沙明，霜老菰蒲冷。\",\"欲下复徘徊，顾侣声相应。带月渡银塘，照见双栖影。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1a760aeae618b1b45','题画四首秦楼月','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"长风发。中宵莽莽吹林樾。吹林樾。疏翎整翮，思游天末。\",\"青山逸态真奇绝。九皋梦想华亭月。华亭月。一声清唳，海天空阔。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1a760aeaeeb6eb002','题画四首秦楼月','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"斜日透疏林，惨惨郊原暮。猛气雄心敛莫施，郁郁如含怒。\",\"紫塞暗秋云，目断阳台路。会见拂拂抟九天，草莽空狐兔。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1a760aeaefa04585f','题画四首秦楼月','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"桄榔云深，生来南国多珍丽。翠花金羽。反得为身累。\",\"弦管歌凝，俯嘿含幽意。秋风起。云山迢递。无计能飞去。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1afab64f746646770','贺新郎·舟中值重午','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"客路风尘恶。叹匆匆、节临重午，不堪牢落。却忆故园当此日，虚馆朋侪对酌。拟胜会、追踪河朔。翘首帝都红日近，望白云、亲舍天寥廓。肠欲断，心飘泊。\",\"江心旧事流传昨。我欲将、兴亡作鉴，悃诚谁托。惆怅灵均今已矣，水底英魂寂寞。又谁辨、醉醒清浊。感事羁怀频吊古，抚乾坤、浩气空盘薄。歌短调，发长噱。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1c0b3078cf7e3136f','洞仙歌·次韵寿顾太恭人','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"薰风解愠，正葛衣轻透。绮席初行争献酒。有令子、金带绯袍两孙，玉立沸笙歌，共向北堂称寿。\",\"荣养谁能似，屈指春秋，更六岁慈龄届九。最喜是孙曾、保姆新擎，娱老眼、男东女右。近王母、传书已名注长生，为报君知，君须信否。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1c87ee541b166288d','瑞鹤仙·寿陆东湖尊堂太君','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"薰风飘绮户，正景属熙明，节当重午。高堂拱贤母，看华发童颜，又逢初度。\",\"遥空昨夜，灿南极、辉联宝婺。寿筵开、杂遝笙歌，古鼎沉檀烟吐。\",\"争睹。有子英贤，纡朱腰玉，作皇家心膂。云霄接武。\",\"满庭前，兰桂森如许。彩绒新绾长生缕。曲按南飞旧谱。\",\"待海筹、一百添齐，从头再数。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1e5f1246f2029bc86','百字令寿夏桂洲阁老和坡翁韵','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"上天纯佑，为社稷、生此中兴人物。巨器宏才更风流，挺立千寻绝壁。冠世文章，包人度量，襟抱同冰雪。试论今日，何人堪比奇杰。\",\"还羡秉笔黄扉，笑谈机务了，英猷横发。仰止皋夔，树芳声、千古谁能磨灭。退食逶迤，开怀延国士，吐餐握发。望公何似，碧天永夜孤月。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1e5f1246fabe1bb4a','百字令寿夏桂洲阁老和坡翁韵','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"林钟律应，又秋早、宵起试占云物。箕昴呈祥灿午夜，南极光联奎壁。浩渺江流，崔巍庐阜，更皎洁瑶台雪。乾坤灵气，一时钟此人杰。\",\"此日寿域宏开，沉檀然兽鼎，瑞烟飘发。看雄据豪吟，染霜纨、龙蛇飞灭。宫酒淋漓，渐玉山微倚，酡颜乌发。祝公遐算，直同天上明月。\"]',NULL,NULL,NULL,NULL),('e647d10f3f7c25e1feafa5b4605b367a','洞仙歌·十六夜对月','孙承恩','e647d10f3f7c25e17fb8ceb3d961e9f2','明',NULL,'[\"银蟾皎洁，万里清辉满。云翳长风尽吹散。想天公、也自知我昨夜无聊，还赐与，通宵尊前吟玩。\",\"海门龙起立，我亦狂来，醉舞婆娑影凌乱。恐嫦娥笑我、华发苍颜，不悟流光暗中换。从言有月即中秋，恁乐事、赏心不须分判。\"]',NULL,NULL,NULL,NULL),('e647d10f40012f1c72d7697ab650037a','浣溪沙·秋思','钱柄','e647d10f40012f1cd1457b72d1457b72','明',NULL,'[\"小立幽阶数绿苔，鸳鸯未绣怕人催。深秋帘幕几曾开。\",\"霜叶半随溪鸟去，寒烟独裹暮钟来。一番消瘦自惊猜。\"]',NULL,NULL,NULL,NULL),('e647d10f401dd7b8b253d4d1383c68c5','柳枝词','谢五娘','e647d10f401dd7b8d1457b72d1457b72','明',NULL,'[\"近水千条拂画桡，六桥风雨正潇潇。枝枝叶叶皆离思，添得啼莺更寂寥。\"]',NULL,NULL,NULL,NULL),('e647d10f401dd7b8b253d4d1fa2b36b5','柳枝词','谢五娘','e647d10f401dd7b8d1457b72d1457b72','明',NULL,'[\"灞水桥头日正长，梳风沐雨是垂杨。可怜千缕黄金线，不与离人系断肠。\"]',NULL,NULL,NULL,NULL),('e647d10f405ee9f977322cec0c6cd3f7','临江仙·春情','钱光绣','e647d10f405ee9f9d1457b72d1457b72','明',NULL,'[\"酒国投时辄醉，砚田荒久慵耕。不堪落叶两三声。病从秋后减，愁向雨中生。\",\"拊剑还歌白石，焚香闲展黄庭。徘徊山寺又钟鸣。鸦啼归晓树，蛩语伴疏灯。\"]',NULL,NULL,NULL,NULL),('e647d10f414c5e1ed8514caf6cb93a78','南乡子','黄正色','e647d10f414c5e1e5cbdfd0daf5afd7f','明',NULL,'[\"清世愧无能，窃得微阶正不胜。天地包容投远塞，相应，投杼慈亲本爱曾。\",\"静坐读伽楞，百念几同二月冰。朔漠荒寒虽特甚，休憎，古训从来只自惩。\"]',NULL,NULL,NULL,NULL),('e647d10f424076764611948be0529485','长相思·秋','张煌言','e647d10f42407676806beafe4f398cb9','明',NULL,'[\"秋山青，秋水明，午梦惊秋醒未醒？乾坤一草亭。\",\"故国盟，故国情，夜阑斜月透疏棂，孤鸿三两声。\"]',NULL,NULL,NULL,NULL),('e647d10f4240767657659484270074d9','长相思','张煌言','e647d10f42407676806beafe4f398cb9','明',NULL,'[\"品瑶笙，按银筝，换羽移宫无限情，秋天不肯明。\",\"几更更，几星星，半是商声与徵声，羁人和梦听。\"]',NULL,NULL,NULL,NULL),('e647d10f4240767676bb6e0dcaf2a504','满江红·步岳忠武王韵','张煌言','e647d10f42407676806beafe4f398cb9','明',NULL,'[\"屈指兴亡，恨南北，皇图销歇！更几个，孤忠大义，冰清玉烈？赵信城边羌笛雨，李陵台畔胡笳月；惨模糊，吹出玉关情，声凄切。\",\"汉苑露，梁园雪；双龙游，一鸿灭。剩逋臣怒击，唾壶皆缺。豪气欲吞白凤髓，高怀肯饮黄羊血！试排云，待把捧日心，诉金阙！\"]',NULL,NULL,NULL,NULL),('e647d10f424076768b1938fbf92c3428','长相思·中夜闻筝','张煌言','e647d10f42407676806beafe4f398cb9','明',NULL,'[\"炙凤笙，弹鹅筝，弄入阳关第几情，铜壶滴漏明。\",\"一更更，一星星，都是商声与羽声，离人不忍听！\"]',NULL,NULL,NULL,NULL),('e647d10f42407676d6944679a9d41930','满江红·示同难宾从罗子慕于武陵狱邸','张煌言','e647d10f42407676806beafe4f398cb9','明',NULL,'[\"萧瑟风云，埋没尽，英雄本色。最发指，酡酥羊酪，故宫旧阙。青山未筑祁连冢，沧海犹衔精卫石。又谁知，铁马也郎当，雕弓折。\",\"谁讨贼？颜卿檄；谁抗虏？苏武节。拚三台坠紫、九京藏碧。燕语呢喃新旧雨，雁声嘹呖兴亡月。怕他年，西台恸哭，人泪成血！\"]',NULL,NULL,NULL,NULL),('e647d10f42407676ece5e1997b5656b7','柳梢青','张煌言','e647d10f42407676806beafe4f398cb9','明',NULL,'[\"无数江山，何人断送，雨暗烟蛮；故国莺花、旧家燕子，一样阑珊。\",\"此身原是天顽，梦魂到处也间关。白发镜中、青萍匣里，和泪相看。\"]',NULL,NULL,NULL,NULL),('e647d10f43c6263cffacb4917eb8d631','蝶恋花·柳影','朱盛藻','e647d10f43c6263cd1457b72d1457b72','明',NULL,'[\"永日三眠颦较妩，困态初舒，莫遗红妆妒。自有明珠生远浦，舞低恍向楼心觑。\",\"拂地长条谁是主，露下芙蓉，只恐难留住。好趁烟光凝地护，交枝漫锁长门路。\"]',NULL,NULL,NULL,NULL),('e647d10f4418284e12f4d5da1028d7f0','卜算子','孟称舜','e647d10f4418284ed1457b72d1457b72','明',NULL,'[\"回首望西陵，隔别入南浦。缥缈孤云自往来，寂寞归时路。\",\"蓼渚小鸿飞，梦断风吹雨。江外峰青似剑，难割愁肠去。\"]',NULL,NULL,NULL,NULL),('e647d10f443a93cfb2b293cfad6ea087','桂枝香古镜','俞琬纶','e647d10f443a93cfd1457b72d1457b72','明',NULL,'[\"张郎一去，君且代郎看，双蛾解理。赠别踌躇，不忍把君分碎。问容颜、君独知憔悴。受多磨，与君无异。广寒三五，嫦娥愁向，却元自己。\",\"晴空里，似丹青点缀。个中小小，洞天深处。背地沉迷，形影都无据。怜君自为分明累，贮尽了、汉宫人泪。架罢妆残，瞥然收却，远山横翠。\"]',NULL,NULL,NULL,NULL),('e647d10f44e4c818539e19b007833985','添声杨柳枝·寄远','王好问','e647d10f44e4c81881c8727cb7109157','明',NULL,'[\"袅袅西风敛暝烟，日衔山。阴阴杨柳暗长川，水如天。\",\"一别玉京成远梦，几经年。锦鱼千里为谁传，思依然。\"]',NULL,NULL,NULL,NULL),('e647d10f44e4c818dd9f54c8d9c4a029','点绛唇·春愁','王好问','e647d10f44e4c81881c8727cb7109157','明',NULL,'[\"九十春光，可怜长日空辜负。别离情绪，最怕黄昏雨。\",\"倚遍楼头，望断春归处。人无语，落花飞絮，又过秋千去。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d803accba50366f8e6','春光好·坐河边新楼','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"山色秀，水纹清。落花轻。沙上鸳鸯泛绿汀。\",\"棹归声。小鸟如啼如话，春光乍雨乍晴。一派霞光催日暮，月东升。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8123b7a7f02fef243','忆秦娥·初春剩国忆子','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"莺声咽。柳梢烟雨梅梢月。梅梢月。谁家玉笛，十分凄切。\",\"迢迢子去伤离别。空亭寂寞愁心结。愁心结。梨花飞碎，香飘尘绝。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d812c351848f5483f3','海棠春·闺中四景词冬','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"朔风剪出鹅毛片。柳絮与、梅花相见。木落万山空，正大江如练。\",\"红炉拨尽寒犹战。况夜夜、玉壶添箭。耐得岁寒心，又苦桃花面。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8232c531b53ff2892','如梦令·怀远，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"寂寞寒深孤枕。风度罗帏梦醒。梦醒却无情，此际离愁漫整。\",\"夜冷。夜冷。花动闲窗月影。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d82df4168158bce7e1','春光好·代姊别妹','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"枫树冷，菊花黄。伴行妆。画舫归途十里塘。\",\"为谁忙。烟水萍丝荇菜，沙汀鸂鶒鸳鸯。多少离情此际有，待佳章。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d831034915ab5c257f','青玉案·即席赋赠友言别','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"一帘萧飒梧桐雨。秋色与人归去。花底双樽留薄暮。云深千里，雁来寒度。客有愁无数。\",\"片帆明日东皋路。送别恨重重烟树。越水吴山知何处。舞移灯影，筝调弦柱。且尽杯中趣。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d832d296d52258e37a','菩萨蛮·坐河边新楼，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"画楼槛外啼黄鸟。山光横断残阳渺。春色杳难留。衔杯花下愁。\",\"绣窗笼晓日。紫燕穿帘急。柳拂水痕清。渔歌渡远汀。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d832d296d5ff55a4c5','菩萨蛮·坐河边新楼，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"春光难驻伤心色。远山暮影烟如织。花气傍高楼。游人在上头。\",\"闲倚阑干敞。湖水平如掌。浪影乱晴空。渔舟荡晚风。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d833b9284e60b6fd0b','长相思·暮归','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"过桥西。花满堤。步印香泥小径归。烟笼万户低。\",\"月初辉。星渐稀。光到窗前知未知。何处夜乌啼。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8394d76e8b6504936','十六字令怀远，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"瓜。今岁须教早吐花。圆如月，郎马定归家。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d83bd8b4ea40e6ae7f','上西楼夜阑闻雨','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"江城云暗星稀。鸟孤飞。帘外绿残红瘦、漏声催。\",\"正风雨。凄凉处。烛花微。警醒梦魂时节、卷罗帏。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d84ee0fd1905880dc3','更漏子·闺中四景词夏','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"湘帘外，池水侧。双燕风前归急。移玉簟，弄双纨。黄梅雨正翻。\",\"薰风起，芙蓉乱。叶底鸳鸯寻伴。一队队，一双双。联翩宿野塘。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d851824f1917e40209','长相思·即事','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"水绕溪。花满堤。鹦鹉声高烟柳迷。愁多恨鸟啼。\",\"香霏霏。泪凄凄。坐看西山虹影低。诗成雪乱飞。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d851824f19b181f303','长相思·即事','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"花影圆。月正妍。隔林啼鸟夜声传。相看情倍牵。\",\"整翠钿。拂朱弦。熏尽罗帏未肯眠。庭前霜满天。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d862a15efe39941487','长相思·赋惜花春起早','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"月刚斜。日影些。一路香风绕碧纱。忙忙贴鬓花。\",\"冷莓■。遍地遮。满院春情委露华。枝头宿鸟喳。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d869f2a10f55267c56','忆秦娥·忆外，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"寒夜冷。终日离愁如断梗。如断梗。日久春长，银瓶落井。\",\"落梧惊梦凭谁醒。窗前窗后花枝影。花枝影。玉漏催残，孤灯半隐。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8753468278048d958','临江仙·题牡丹','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"锦树一阑无气力，随风婀娜枝头。艳阳却被夕阳收。春光有恨，待欲控双钩。\",\"香入美人销不尽，依然别样娇羞。太真西子并风流。小窗倚遍，明月更相留。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8879e24bb5eabbe5d','少年游·冬景','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"梅头未绽，朔风如历，白雪暗飞香。轻寒到枕，愁肠顿起，小胆怯空房。\",\"满天云气，一炉烟烬，迢递此韶光。高唐路杳，楚台无梦，何处写鸳鸯。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d88e8995ffd06095f0','浪淘沙·秋兴','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"窗外雨声催。烛尽香微。衾寒不耐五更鸡。无限相思魂梦里，带缓腰围。\",\"隙月到罗帏。孤雁南归。玉炉宝篆拂轻衣。花气参差帘影动，叶落梅肥。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8978623e111b21446','浣溪沙·送女归，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"唱罢骊驹神暗伤。阑干小月印虚堂。花枝影度隔帘香。\",\"人去空留千里梦，寒深午夜怯银床。独留灯烬照荒凉。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8a36344f5643910ef','眼儿媚·怀远，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"将入黄昏枕倍寒。银汉指阑干。半轮淡月，一行鸣雁，云老霜残。\",\"凭著飘英风自扫，小院掩双镮。离情难锁，迢迢江水，何处关山。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8b0457cd74098aed8','卜算子·初春游寓山看花','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"春半绿茵齐，处处香风透。满目青山点翠苔，景色依然秀。\",\"尽道柳丝黄，不解梅花瘦。回首莺啼深树时，正是秋千候。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8b1149f719a2d2a99','海棠春·闺中四景词秋','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"西风萧瑟梧桐老。闻处处、寒砧夜捣。罗袂拂霜轻，雾髻侵云袅。\",\"绿窗声送孤鸿早。纨扇上、离愁多少。月下桂香浮，限煞秋光好。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8b7d4c886241ada01','海棠春·赠侄女吴子','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"绿窗春透垂杨影。更多少、月明风冷。花枝黄鸟声，绣阁酣初醒。\",\"被底鸳鸯，阶前白璧。都是欢时美景。惭愧白头人，空叹时如锦。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8ba9474544fdd5ea5','忆秦娥·怀远，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"云将暮。寒砧声送愁无数。愁无数。孤鸿只影，极天难诉。\",\"片帆不返金陵渡。相思难道相思路。相思路。千里风尘，一帘霜露。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8ba94745467692cf1','忆秦娥·怀远，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"清秋节。金风陡起悲离别。悲离别。长天月影，常圆常缺。\",\"空阶萧瑟声声叶。霜花点点肠千结。肠千结。云外翔鸿，梦中化蝶。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8bcb4bd6a7a2e55f7','烛影摇红·咏鵻堂怀旧','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"春入华堂，玉阶草色重重暗。寒波一片映阑干，望处如银汉。风动花枝深浅。忽思量、时光如箭。歌声撩乱。环佩叮当，繁华未断。\",\"游赏池台，沧桑顷刻风云换。中宵笳角恼人肠，泣向庭闱远。何处堪留顾盼。更可怜、子规啼遍。满壁图书，一枝残蜡，几声长叹。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8c0775f20b90f75fe','如梦令·寓园有感','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"此地春光如绣。画槛名花依旧。独立悄无言，梅比腰支还瘦。\",\"僝僽。僝僽。林外鸟声拖逗。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8cda31d6ffed22ac4','更漏子·闺中四景词春','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"艳阳天，流水曲。处处莺啼柳绿。初睡足，晓妆迟。帘开燕子飞。\",\"桃如火，梅如豆。添得玉颜消瘦。眉淡淡，鬓星星。王孙草正青。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8d225b28462b7276c','捣练子·秋思','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"风草草，露浓浓。筑起愁城几万重。黄叶满庭人未扫，一声哀雁五云中。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8d2a7f3b4dab56404','洞天春初春同友坐剩国书屋','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"黄鹂槛外声小。曲径残梅未扫。淑气含春遍芳草。正晴光缭绕。\",\"闲庭竟日悄悄。无奈佳人去早。蛱蝶轻飞，风飘花乱，新愁多少。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8f0c6a4da973ddf80','菩萨蛮·忆外，代人作','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"腊花香动烟中影。纱窗半掩罗帏冷。孤雁宿沙汀。寒砧梦里声。\",\"梦到相思地。难诉相思意。夜雨渡芭蕉。怀人正此宵。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8f1a89439f7927afa','捣练子·留别','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"人去也，情难舍。花枝吹散风潇洒。霜天宿鸟静无声，流苏锦帐含愁下。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8f79d0755b78a3889','长相思·春景','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"芳草齐。鹧鸪啼。满院花光傍竹篱。行行日已西。\",\"柳条长，宝马嘶。问尔王孙归未归。楼高望转迟。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8fc55a84c4e14a66c','捣练子·夜坐','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"长相思，久离别。为谁憔悴凭谁说。卷帘贪看月明多，斜风恰打银釭灭。\"]',NULL,NULL,NULL,NULL),('e647d10f4526d1d8fd6cac4cda3bdbc2','卜算子·春日寓山看花','商景兰','e647d10f4526d1d8d1457b72d1457b72','明',NULL,'[\"烟暖碧云楼，楼迥春山秀。风落残红水面飘，池内清波皱。\",\"柳外小莺啼，花鸟声相斗。唤起当年万种愁，泪湿青衫袖。\"]',NULL,NULL,NULL,NULL),('e647d10f453ce63c6e3a58898b6dbab6','忆王孙·和薛萃轩副使','苏世让','e647d10f453ce63cd1457b72d1457b72','明',NULL,'[\"无端花絮晓随风，送尽春归我又东。雨后岚光翠欲浓，寄征鸿。\",\"家在千山万柳中。\"]',NULL,NULL,NULL,NULL),('e647d10f45ebfe72a388ebf008286063','霜天晓角·采石矶蛾眉亭','徐媛','e647d10f45ebfe723a20f62a806beafe','明',NULL,'[\"双峰斗碧，寒玉横秋壁。两道弯环天际，凝无限、青螺色。\",\"断岸涛声急，似奏临邛瑟。鸾镜绿窗人去，惟留取、春山迹。\"]',NULL,NULL,NULL,NULL),('e647d10f466eaec66944d6e752b5dcfd','望江怨','范汭','e647d10f466eaec6d1457b72d1457b72','明',NULL,'[\"兰房晓，络纬缫丝声未了。一霎愁多少，桐阴斜压阑干小。\",\"人悄悄，罗幕手慵开，惟恐惊栖鸟。\"]',NULL,NULL,NULL,NULL),('e647d10f468833c925eb6af7cfcbefeb','清平乐','莫是龙','e647d10f468833c9d1457b72d1457b72','明',NULL,'[\"春光已半，无语空肠断。亭院落花脂粉乱，扫却坐来还满。\",\"玉关消息难凭，机头锦字初成。愁似故园芳草，东风一夜还生。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272005d44e1747912ff','应天长·至日','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"风生觱发，气转葭灰，正喜一阳初集。占云纪瑞更卜，丰和是黄赤。时闭关，方杜客。爱将衰背烘残日。春意着、梅花十分，巳开六七。\",\"一线较初长，香添宿火，暖气薰幽室。正竹坞茶炉待雪，偶喜片云头上黑。看朝来，迷路陌。洒冰花、重重密密。倚寒空，诗裁柳絮，酒分珠滴。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720293cd2d1d18c229','雨霖铃·中秋值雨','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雨混烟迷，听萧萧、声填空阔。湘帘不上银钩，恨寥落、一秋佳节。云外天香，黯黯把、蟾光顿灭。夜深沉、灯火楼台，人寂寥，管弦风月。\",\"天涯有恨成离别。正凄凉、人悲圆缺。是今宵、缺在人间，怕团圆、天边皎洁。泪点雨声厮混，把愁肠万结。叹此夜、千种离情，尽发付蛱蝶。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272034175aa927832a2','高阳台·自述','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"坐倚芝云，摊书霞几，卧缘湘竹为床。鸠藜蒲笠，药帘中有奇方。心阆已断风尘梦，尽幽闲、草色花香。怕人间、多事向炎蒸，抵死奔忙。\",\"一椽仅有南阳。喜窗开照水，竹碍斜阳。抱琴沽酒，醉歌盘涧诗章。鞭骥豢龙些个事，叹冰山、是尔收场。幸吾生、少常欢笑，老不悲伤。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272052fcfd752d57973','虞美人·','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"乌江别冢青青草。色与人俱好。多情指作美人花。怕教娇香嫣紫、委泥沙。\",\"蛱蝶也知春不久。花底闲消受。花瓣落西风。却似帐前拔剑、泣芙蓉。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227205b8c4d0501cdbbe','风入松·对酒','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"仰天把酒更高歌。愁奈余何。生平不断天涯梦，如今无复南柯。何事朱门霄汉，且看茅屋藤萝。\",\"世情无地不风波。休怨蹉跎。乾坤惯妒英雄客，常时草草消磨。况是生年有限，到头占地无多。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720743dd792bf9dff0','早梅芳·梅花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"竹篱风，山店月。更著些儿雪。一枝轻云韵，不受半分尘垢涅。万花难比态，一水堪同洁。笑含情寄思，和雨和烟折。\",\"吐檀心，飞玉屑。羌笛声清绝。石桥湖水，想零落、黄昏时节。分香醒客梦，酿粉笼宫额。更破禅机，自许心如铁。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720ac80d3e83ab9882','锦缠道·蔷薇','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"春色沉酣，朵朵撩风香吐。翠条高架猩红妒。迷蜂困蝶沾飞絮。\",\"帘笼昼永，霞彩含轻雾。\",\"问金谷锦屏，如今何处。风流疑、合同仙侣。燕扶落瓣起晴空，使青楼人怨，昨夜风和雨。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720b718494b7f7cb79','万年欢·除夕','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"次纪其除，喜酒媚椒花，人争献寿。灯焰金莲，那更炭然香兽。今岁今宵去也，看明日、明年还又。总乾坤、岁岁更新，何事人愁如旧。\",\"夜来歌吹千门早，迎春送腊，残更共守。墙角寒梅，相对似怜白首。暗想岁月催人，何异摧枯拉朽。尽狂歌，醉乡潦倒，说与世掸参透。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720b9b99874a49e913','长相思·鸳鸯菊','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"连枝蕊，并蒂开。双双同抱锦心来。鸳鸯金翠钗。\",\"分两色，叠重台。扯弄相思一处栽。结成痴与呆。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720c7dc0dec7cb42d7','秋蕊香·水木樨','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"风露薄秋光暖。柔翠枝头花软。细蕊娇香凝笑眼。分得金波色浅。\",\"木樨开落闲庭院。香风扇。未许西风卷。向露草还舒展。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720cb401a1ecd3a662','醉芦花芦花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"山青青，水绿绿。芦花两岸风簌簌。白云引惹共飞扬，寒霜点缀怜披拂。\",\"牵动行人江上愁，低垂渔父滩头宿。月明洲渚橹咿哑，谁歌白苎西风曲。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22720d1113862d2a48de','锦堂春萱花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"北树朱华日丽，生阶翠叶风长。纤蕤腻蔿舒芳艳，绝似杜兰香。\",\"仙骨瘦知髓绿，道家雅爱衣黄。何因花事关心事，相对隐忧忘。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272101663a606390db5','玉漏迟·通宵','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"夜来人醉也，今宵灯月，恍然畴昔。云洗天空，三匝灯金月璧。香飘越女吴姬，弄春声、秦箫楚笛。闹穰穰、玉宇尘飞，瑶阶人集。\",\"更忘眠怜夜饮，醉月影西沉，漏声残滴。月色灯辉，百岁几番同得。一度春光过了，增一遍、消磨堪惜。况明朝，风景又非今夕。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227211fd065e4a616cd0','长相思·夜坐','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"云黯澹，月朦胧。落叶窗前几阵风。此时愁正浓。\",\"嗔易别，恨难逢。万语千言说梦中。觉来都是空。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22721382b4f31e01ef22','归国遥扶桑','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"迢递。移得根枝自天际。烂熳新红如醉。云艳摇芳砌。\",\"开落长春得意。朵朵还扶日。风露不禁沽滴。苔径空怜惜。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272178ae733aceaedf4','满路花·杨花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"乍因风力起，欲扯飞花住。陌上有行人、愁满路。红亭绿水，岂是怜伊生，飘泊归何许。东君魂梦，似逐飞扬去。留春无处。\",\"却怪铅华误。堕粉泣残妆、风偏妒。燕泥蛛网，收拾应无数。有恨和谁语。幽闺绝塞，故故牵人凄楚。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22721b80a1eb38ce53b4','满江红·芙蓉','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"溪上春酣，惯弄西风夜雨。霞萼仙姿，千万树。盈盈欲语。落晚自怜窥镜水，忍寒谁念凋珠露。胭脂寂寞汉江秋，东君误。\",\"锦城云，秀国雾。水村傍，河桥路。饶得红妆马前住。笑带秋容下夕阳，泪含霜色归尘土。花边把酒莫停杯，成辜负。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22721c3e72176c4cf1b6','望海潮·八月十八日潮生','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"江分吴越，星摇牛女，潮回海跃山倾。怒奋天戈，惊翻地轴，半空万马奔腾。栗烈走风声。俨堆云喷雪，照日层冰。填填隐隐，真疑千里荡雷旌。\",\"沧溟应月亏盈。亘古今消长，暮落朝升。水分一派，浪蟠几曲，苍茫势驾飞鲸，岁岁看潮生。喜凭高纵目，把酒相迎。便想飘然，随潮归去向蓬瀛。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22721cd48a57534b929c','木兰花木笔一名辛夷','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"心吐毫，枝折管。细萼芳苞春日暖。疑醮水，欲书空，写出韶光留著款。\",\"藉斜晖，凝晓露，俨似荷风浮紫碗。怜倚槛，笑含风，缘堤色与芙蓉乱。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22721f3f667e393f7548','杏花天·杏花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"抹红匀粉墙头面。轻烟混、垂杨金线。半落半开春眷恋。\",\"掩映青旗村店。透韶光、初番娇颤。看上苑、酒酣芳宴。\",\"不禁风剪剪，雨带花飞一片。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22721f57859f2d60dd81','烛影摇红·杜鹃','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"咽露啼风，残春留得三更月。红巾绛焰占芳时，迸火风前热。为想巴云楚水，洗不尽、枝头泪血。落日争明，残霞出艳，繁星当夕。\",\"莫问兴亡，洛阳啼处曾悲客。哀魂犹似怨当年，声里含凄切。偏是春宵易晓，更莫遣、歌闲酒歇。满酬坞使，沉醉花王，鹤休占绝。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227222dd8b285d03bd4c','遐方怨·怀友','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"云滚滚，雨霏霏。乡泪灯前，不信今番不湿衣。\",\"声声杜宇唤春归。偏不向、客窗人卧，枕边啼。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227222fe3cf4f8c81f99','清商怨·白鸡冠','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"为怜三唱霜凝白。相对悲愁客。玉羽云翎，雪映书窗色。\",\"不是樊笼标格。俨有意、无声拍拍。飞荡西风，恐升去旌阳宅。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722363ae79c4e1316e','步蟾宫·金桂','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"兔窟分香云外落。碧玉枝、横黄金萼。西风吹雨细如丝，朱户重重卷帘箔。\",\"姮娥为惜人间少。分花月色年年好。漏泄广寒宫殿秋，寒馥金波滴清晓。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722468d4e6729560f3','恋绣衾·夜合','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"红开翠合欲昏天。薰百和、露滑芳妍。夜半合欢庭院，羡鸳鸯、人月同圆。\",\"长条绿蔽朱柯挺，吐芳气、作香烟。雨过还疑濯锦，忘忧处、朝暮花边。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227224c77ce86d7b3458','贺圣朝·红葵','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"薰风未破黄梅雨。叶展花开几许。俨牡丹换紫，蔷薇怯艳，芙蓉知妒。\",\"浅深高下，锦标妍湿，瀼瀼朝露。把金杯浮白，醉面分红，端阳日午。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227224e2d52ec0e6f23c','人月圆·粉团','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"珑璁堆髻光摇雪，初试汉宫妆。谁为擎出，摩尼沧海，滑影团香。\",\"更讶瓣叠江梅，毬攒风絮，色皎青霜。杯倾夜月，牡丹斗艳，芍药联芳。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272253513ad35677fae','风入松·秋思','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"西风一枕雁哑哑。残月窗纱。梦魂惯引心头事，恨牵缠、绕遍天涯。愁共万层山叠，情怜一片云遮。\",\"银釭隐隐落灯花。空自嗟呀。半生未了相思债，到如今、尽属冤家。何日画屏中雀，晓看妆镜堆鸦。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227228d610472b197d13','卜算子·吉祥草','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"嫩叶暗青云，幽花分紫绶。不改阶前霜雪姿，此意君知否。\",\"入坐眼生明，小算人同寿。南华瑞霭绿云铺，白石和悠久。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722962dc28ac65d6c3','巫山一段云·琼花一名玉蕊','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"色莹清秋水，香凝媚服兰。瑚琏璀璨艳禋坛。照月夜生寒。\",\"素蕊珠千点，幽葩玉一环。琳宫仙质寄尘凡。根移去复还。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722a418efcaa4ec11e','相见欢·并头莲','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"同心共蒂红香。效鸳鸯。对对碧波锦浪、喜联芳。\",\"开水面，怜艳女，羡新郎。相看含笑愿学、两情长。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722b6535bc5ee5c2fd','喜迁莺·清明','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"禁烟时序，喜红尘紫陌，花鲜柳软。迁莺学语，客燕初来，丽日暖烘人面。金驮宝勒争驰，画阁珠帘尽卷。最好是，芳草醉裀，桃花歌扇。\",\"相劝。载壶觞，弄水寻山，镇日闲留恋。狂蜂浪蝶，栖香宿雾，摩弄绿嫞红倦。况是韶光易改，那更雨愁风怨。尽今番，日日向花林，沉酣芳宴。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722d5399f127dfb869','武陵春·桃花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"傍柳临溪红拂面，春色醉游人。开径何年惹避秦。云路隔芳尘。\",\"一岁一番相对酒，媚眼似微吨。谢却东风仍托梦，巫峡是前身。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22722edbcbee63fb5276','满庭芳·芍药','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"浥露蒸霞，翻阶压槛，亭亭艳吐韶华。枝头弄雨，似故傍风斜。细剪绛绡金缕，把腰围、巧斗堪夸。把酒怜春去，万花落处，朵朵含葩。\",\"更敧风覆玉，囊簇红纱。汉苑紫云归玉佩，羡扬州、艳赏谁家。喜唱入，歌喉蕴藉，照月醉琵琶。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22723044e9fe12317853','浣溪沙·剪春罗','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"尖尖簇瓣是谁匀。色占猩猩血一痕。相看娇艳更轻温。\",\"罗袖倦风敧弱态，泪痕流血泣残春。不知裁剪为何人。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227230fa6a363a8a4fc1','玉楼春·白芍药','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雕阑堆叠瑶池雪。荡日依云擅奇绝。胭脂一点斗春心，故傍月华轻漏泄。\",\"珠明水白枝头洁。香散荃芜风里爇。佛前擎出玉盘盂，醉看未许轻攀折。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272311ec9d230d22f80','醉春风紫荆','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"细蕊缠青茜。繁葩融醉面。多情扶手捏柔条，颤。颤。颤。绛绡暴雾，红糁笼烟，紫金百炼。\",\"孝友能不倦。荣枯知易变。只愁摇落向风飞，漩。漩。漩。还宜月下，更好水边，笙歌庭院。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272333ad109c319a063','荷叶杯·白莲','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"曾说玉容倾国。堪惜。照水艳毛施。清标原不藉胭脂。为想出尘姿。\",\"占得瑶池玉井。清影。风乱白云低。舞动飘飘白羽衣。鸥鹭逐同飞。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22723b849dcaaf3b08ba','传言玉女·玉兰','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"楚畹兰漪，捧出玉瓯香雪。魂清骨冷，影乱云间月。瓣簇莲台，色共藕花争洁。琪葩瑶树，香清艳绝。\",\"素面轻匀，芳心浅、受红捻。擎云承日，不禁风乱㿱。满空飞舞，荡漾轻舟一叶。载将愁去，向愁人说。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272411240fb98521040','惜分钗怀友','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雨将歇。烟半没。露出些儿山顶月。倚空楼。望归舟。楚天人远，巫峡春愁。悠。悠。\",\"江树杪。离恨杳。最是风尘人易老。面含羞。泪空流。区区富贵，几个长收。休。休。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece71439d5a9ba','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"金霞光映水波红。山吐烟浓。瀼瀼露湛松梢湿，恍人行在乱云中。翠色澹迷柳雾，红香飞落花风。\",\"氤氲忽散两高峰。青插芙蓉。澹黄一抹林峦日，艳湖光、镜净天空。树底鸟嘱风暖，马头草滑泥融。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece7143fbb1109','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"稠烟低压水痕黄。紫绿山光。金霞翠雾双林晚，听哑哑、鸟宿斜阳。衔日远峰的的，吞云高树苍苍。\",\"沾衣寒湿露华香。欸乃沧浪。渡头灯火星沉水，韵昏钟、山角声长。风细金波荡月，夜深银汉生凉。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece71441860aa3','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"融融迟日艳桃花。柳亸金芽。山光掩映层层碧，荡湖波、几点晴霞。燕子争衔翠藻，鸳鸯稳睡青莎。\",\"红亭霁色媚韶华。金勒香车。笙歌声远花闲渡，倒尊罍、残日西斜。芳草眠留处处，醉人扶得家家。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece714476d892d','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"瑶台银阙画图中。琼树玲珑。玉垒长堤跨十里，白茫茫、山色当空。树底红含瘦叶，云边绿剩孤松。\",\"梅花依旧月空朦。清兴谁同。鹤去不还千载梦，冻云深、亭锁阴浓。村店酒香人醉，山楼吟费诗工。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece71470c793e7','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"芰荷十里锦云联。照水争妍。山光四壁青如洗，看奇峰、高插云边。接叶双双白鹭，穿花对对红鸳。\",\"依依杨柳绿参天。竞噪新蝉。南薰馥馥襟怀爽，坐深松、嗽雾披烟。琴弄山间明月，茶烹石上流泉。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece714720841fa','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"轻烟浮白荡山腰。树里红绡。扶苏日晚芙蓉醉，斗金风、桂子香飘。远岫云归片片，平沙雁落嘹嘹。\",\"一竿烟水傲鱼舠。分外逍遥。白蘋红蓼滩头醉，任沉酣、月小天高。黄叶几家茅屋，白杨一带溪桥。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece714822c6980','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"天空云散月千山。四望青蓝。金波涤荡平湖影，照幽林、钟磬声闲。蓑笠何人把钓，楼台有客凭阑。\",\"锦裙同得此时看。雾湿云鬟。玉粲珠辉琼海夜，澹悠悠、水冷烟寒。松顶风摇鹤唳，山头犬吠僧还。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece71488413642','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"阴沉风雨白吞湖。山霭平铺。水云高下苍茫色，惹深烟、树影模糊。蔂蔂花侵雨倦，丝丝柳倩风扶。\",\"沙鸥野鸟自相呼。水涨菰蒲。雾宿湘帘和湿卷，暗空楼、高影扶苏。景在半开半合，妙观如有如无。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece714d69a425c','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"红烟紫雾隐楼台。高下花开。茸茸芳草长堤路，听笙歌、隔水声来。漾漾绿浮沙岸，依依翠霭山崖。\",\"人间何地是蓬莱。休更疑猜。风和树底鸟声细，照碧波、贝锦初裁。扶醉金鞯玉勒，牵情凤钿鸾钗。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227249ece714f8765057','风入松·春湖','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"山衔冻雨树吞霜。黯黯山光。掩映楼台红树里，浴清波、白鸟飞扬。村酒三杯扶醉，梅花几处浮香。\",\"寒空吟倚兴偏长。醉舞徜徉。水沉云冷三千顷，破寒烟、白露微茫。景入洪荒世界，人间蓬岛仙乡。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22724af030f9a9d4ea9f','南柯子·荼蘼','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"学雪飘丛密，穿珠络索长。秦台雨歇粉重妆。谱花推韵友，助诗狂。\",\"午夜东风梦，一庭明月香。媚条无力倦飞扬。残春收拾去，赴高唐。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22724f26a26974b9cb6f','一箩金黄菊','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"轻烟细雨当重九。篱伴孤芳，香为金风透。翠叶黄英怜独秀。弄月欺霜枝影瘦。\",\"三径人归花下酒。一段风流，占却秋时候。最是黄花怜故旧。不嫌茅屋年年有。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227250fbda8b601e8a7a','蓦山溪·山丹','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"幽庭深院，草砌花流电。红就火中百炼。直恁色深香浅。叶柔翡翠，丛压珊瑚，细分霞，低映日，纤瓣巧如剪。\",\"栽向幽窗，风里枝摇颤。把酒喜相看，竟沉醉、花浮人面。蜂蝶休猜，燕莺都不管。且自娱，且自看，长日闲消遣。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227259f049fb5e139c45','西江月·白榴','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"碧蕊似怜星眼，繁花学剪云衣。青霞为骨玉为肌。雅澹萧疏风味。\",\"幽梦白云绕绕，空阶明月依依。相传海上得来奇。分出琼台瑶砌。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22725a318f14f6dc8d05','凤凰台上忆吹箫·中秋','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"满月悬珠，轻云飘绮，苍山烟树横秋。向空中楼阁，高揭帘钩。臂湿夜深玉露，笑声欢、人醉金瓯。逢时候，杯盘清夜，歌舞红楼。\",\"堪酬。阴晴不定，见几番今夕，雾卷云收。喜对此佳景，且豁双眸。猛忆彩鸾何处，便教人、兜地生愁。几时得，月圆人共，学凤凰游。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22725d069601874f1010','望仙门水仙','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"依依楚月傍孤芳。梦魂长。盈盈罗袜忆三湘。恼人肠。\",\"解佩春潇索，携琴夜色凄凉。烟波风月两茫茫。两茫茫。剩有雪中香。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22725e1edbce62fa6067','醉桃源夹竹桃','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"小桃红粉竹边匀。想见酒家春。翠烟低压澹霞痕。照眼色偏新。\",\"溪畔路，误渔人。花底翻令忆避秦。刘郎一去隔芳尘。梦绕渭川云。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272606c416d681f476e','山花子·山棠梨','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"锦葩香吐萼垂垂。飞架东风紫绛帏。露底一枝攀折去，傍蛾眉。\",\"弄影似争春有色，含娇祇怯径无媒。惯是东君妒长好，雨霏霏。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227262f89c85df3cb6d7','醉红妆玫瑰','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"胭脂分影湿玻璃。香喷麝，色然犀。一般红韵百般奇。\",\"休错认，是蔷薇。绣囊佩剪更相宜。匀百和，藉人衣。\",\"把酒对花须尽醉，莫教醒眼，受花欺。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272631a05f3a205d02a','采莲子银莲','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花头碎剪玉玲珑。学白莲开不学红。花意怯春闲帖水，人心逐絮乱随风。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272631a05f3bdc0a7f0','采莲子银莲','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"霜冷汀洲云冷波，水中花撒小银杯。花似水仙人似玉，醉看明月不知回。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227264a66a7e9cb4c9f2','斗百花·紫菊','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"点缀寒烟紫雾。掩映晴霞红树。鲜鲜露浥胭脂，采采风摇燕羽。助却秋容，妆点冷澹时光，秾艳萧条园圃。\",\"篱下闲成趣。胜占春华，争奈青霜偏妒。瑟瑟萧萧，颜色尽教收去。把酒看花，悠然明月黄昏，为惜朝风暮雨。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272659e0dbc2b9e7a60','蕙兰芳引·兰','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"绮石幽香，自一种、天然标格。向红紫春工，不受一些颜色。露滋风泛，但浪迹、湘江楚泽。对静几明窗，时伴云林卷册。\",\"君子同心，美人羞献，岁寒自识。更托意琴声，避世为怜骚客。结好菖蒲，偏嫌荆棘。寄萧斋、著意为君培植。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272659fc33017429426','谒金门·黄葵','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"心倾日。一点孤忠默默。冷艳寒姿香酿蜜。向慕怜朝夕。\",\"一任风欺露涤。愧人剩有心赤。可是不同春有色。开落谁曾惜。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227265b99f9bdc68d887','石州慢·春暮','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花倦惊声，飞共雨肥芳草。一段轻烟，阑截澹白山头，微青树杪。斗红香冷，空教杨柳生愁，点点雪花飞不起，都付与东流，春去知多少。\",\"怀抱。何事当年，花闲柳伴，追欢买笑。萦得柳暗，浓愁花堆烦恼。春归人远，更有无限关心，残更不寄青楼梦，醒眼醉孤灯，此恨和谁道。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227266720fd05d64f853','桃源忆故人碧桃','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"弄玉浮云含晓露。开傍画楼朱户。妖艳秾香偏妒。色悔刘郎误。\",\"吹笙月底轻云度。影侧玉人偷顾。谁惜澹妆衣素。粉带何郎傅。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272673165576679c051','青门引·菖蒲花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"净几延清赏。把卷坐生闲爽。曾闻九节解通灵，幽香绮石，自得同高尚。\",\"枝黏玉屑花轻放。不是风尘相。寄托林泉雅致，流水怜飘荡。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726832225104877d78','东风齐着力花朝','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"膏雨如酥，暖风欲醉，锦绽群芳。郊原蹊径，花气暗浮香。处处红新白嫩，舞丝丝、柳茜微黄。斗春风，枝头蝴蝶，沙上鸳鸯。\",\"景物媚韶光。喜良辰、山融水艳春阳。缓寻芳草，约友载壶觞。扶醉傍花随柳，更豪吟、风月徜徉。愿东君，还怜我辈，收拾休忙。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726c5d3c267e3b296c','青衫湿忆旧','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花边记得相逢处，依旧夕阳斜。空怜一段，青山流水，剩却嗟呀。\",\"恍然人在，湘裙拂柳，宫袖飞花。别离天远，寒烟红树，暮雨朝霞。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726d9b8402a5df5f65','春光好·金盏','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"青青叶，矮矮丛。斗春工。竹叶香浮金盏色，醉千钟。\",\"黄菊较输春艳，金钱比怯秋红。好胜百花开更早，占东风。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726d9fc7370a66d919','月华清·白牡丹','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花剪云绡，种移兔窟，占得玉清琼素。一段萧疏，傍夜来、帘箔香初吐。雪魄蟾魂幽韵，别一种、风流天付。看庭阶弄影，光寒玉露。怕教胭脂耽误。\",\"向风尘夸红斗紫，撩云托雨。一片冰心，甘冷澹，轻烟薄雾。潇然趣。待托霓裳，天上曲、清扬歌句。奈春为爱君开，恨摇落春还妒。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726e88ea5b204ba9cd','满庭芳·元宵','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"宝月当空，湘云飘彩，六街灯火初悬。笙歌四起，争驰人语喧阗。借天长夜，天须放、玉漏绵绵。大展开，三山碧海，万井漾红莲。\",\"楼台人燕赏，歌怜皓齿，酒压青年。恍世如蓬岛，人似神仙。喜是岁当大有，净尘氛、四海风烟。庆丰登，千门欢洽，万寿祝初筵。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726f09ef3e7d7cb746','画堂春·长春一名月月红','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"为怜恶雨与憎风。一春摇落芳丛。春残不逐春归去，占住春工。\",\"矮架短屏色艳，笼烟笑日香秾。一枝不断四时中。月月长红。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22726fe5af16dfa7b452','感恩多·题情','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"独焰流红泪。更筹搅心碎。愁锁思沉吟。病儿深。\",\"怕向枕边帐里，梦中寻。梦中寻。不如一见，了却霎时心。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22727032b52562203fda','梅花令茶梅','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"澹粉轻匀。微红浅带嚬。叶较茶枝更绿，花却似、与梅浑。\",\"傲霜开小春。轻霞浮翠云。最好此时花尽，喜相对、共温存。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227270da30f454ee74ee','爪茉莉莱莉','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"香吐黄昏，不是风尘味。清影弄、月痕轻缀。袅袅扶风，联珠颗、枝条柔翠。几多番、落尽还开，直恁秾情幽媚。\",\"绣窗绮阁，惯是怜娃惜婢。晚妆残、湘云堆髻。一枝偏倚，珊枕玉温香腻。夜深沉、梦醒残鸡，暗麝尤侵鸳被。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22727120f8a0c5d52fed','三字令·五九菊','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"一种菊，两浮香。当夏日，开梅雨，为端阳。荡金风，摇玉露，惜重阳。\",\"延清赏，对秋光。英可餐，花可插，酒可尝。不须愁，不须怨，不须忙。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272712be396145ba8f5','醉落魄黄荼蘼','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"一般风味。为惜残春偏着意。涂黄额试学宫娃媚。改艳添香，幽韵怜棠棣。\",\"芳名原向酒中得。风流彻骨镕金液。东君不是忙收拾。数自梅花，开到君应惜。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22727195555208dd1cbb','点绛唇·红榴','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"笑日烧空，霞蒸艳色红然火。风翻电转，雨滴流珠颗。\",\"映水珊瑚，透影穿帘箔。云鬟亸。一枝钗伴，爱杀双头朵。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227278d35b3e9a163896','祝英台近·金灯','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"一枝擎，花数朵，烛影摇莲炬。夜静闲庭，冷焰空凝炷。丛丛草伴林间，都无人惜，任莺老、燕闲来户。\",\"怜幽趣。试将花插罗帏，相对黄昏语。影落灯花，灯下红偏愈。为惜连枝并蒂，月暗西厢，还须好、照人幽处。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227279b79185faca554a','四园竹石竹','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"茜绯枯紫，更著粉边轻。花如刻缯，叶疑缀玉，色醉还醒。\",\"盈盈开日，爱向闲庭绣户，密翠簇繁英。落还生。偏怜常占春艳，可人闲意幽情。\",\"无那一朝霜露，不堪憔悴付飘零。根枝犹在，待春回，依旧订花盟。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22727a2aecca10e224d6','鹤冲天·白菊','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"分露色，弄霜妍。花叠月痕圆。倦将红紫斗春鲜。冰魂空自怜。\",\"黄木冠。白羽扇。醉卧花边篱畔。满空干叶作秋声。惜花心为惊。\"]',NULL,NULL,NULL,'[\"鹤冲天：词牌《喜迁莺》的别名，起于唐人，《太和正音谱》注\\u201C黄钟宫\\u201D。双调四十七字，前段五句四平韵，后段五句两仄韵、两平韵。因韦庄词有\\u201C争看鹤冲天\\u201D一句而留下《鹤冲天》的别名，读者宜注意其与正格双调八十四字的《鹤冲天》词牌之间的区分。\"]'),('e647d10f46cb22727ab15dd52e944736','江月晃重山蓼花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"照水枝含浅绿，凝霜穗散轻红。汀沙风急雨蒙蒙。香含千古，澹暮烟中。\",\"黯澹还依杨柳，凄凉为惜芙蓉。半红半白媚秋容。晚来堪画处，傍渔翁。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227282ba50a9ad9ccdf5','女冠子·玉簪','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"金风璧月。幻出花偏白。占幽芳。\",\"含露簪拖蕊，开风玉散香。斜插停云鬓，重添向晚妆。多情共摇落，枕边旁。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272845400bc99cba557','眼儿媚·笑眼花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"闲倚东风笑未休。巧倩抵轻柔。云衣雪面，月眉星眼，白玉搔头。\",\"长条无计撩春住，朵朵似含羞。飞共丁香枝上，杨花风里，乱惹闲愁。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288687ef26d3f9bc5','忆王孙·金丝桃','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"为惜春风去渺茫。不堪回首忆刘郎。慢裁金缕作衣裳。\",\"剩残妆。吐出丝丝惹恨长。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f51105c6abd2','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"荷花十里水浮香。夏日初长。披襟散发云深处，能教肌骨生凉。折得碧筒劝酒，还堪荷叶为裳。\",\"举杯浮白醉红妆。潦倒壶觞。山青不改年年色，无心云出云藏。懒逐风尘炎日，且看烟树斜阳。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f51119228f7c','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"开帘华月满空楼。天净云收。咿哑雁落青山外，萧萧树泠风秋。霜叶惯红客泪，芦花乐白人头。\",\"年来灯里已无油。颖悟掸修。且看生灭眼前景，何须别有参求。生向青枝艳色，灭随黄叶东流。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f5111938cf3f','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"浓烟稠白望中深。雪色云阴。梅花剩有西泠约，携尊不负追寻。荡日冰澌万顷，摇风玉瘦千林。\",\"寒空倚阁费孤吟。坐有知音。一鸟不飞天影黑，微茫山树俱沉。歌出阳春白雪，情怜远水遥岑。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f5111bbfda36','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"一春常为看花忙。载酒扶航。阴晴不断西湖路，偏怜山水情常。杨柳桥边醉月，桃花堤上飞觞。\",\"撩人芳草带斜阳。绿软红香。朝来风雨春无主，眼前雕谢韶光。烧烛漫浮烟水，高歌且着颠狂。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f5111c574936','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"残棋未了夕阳边。结静中缘。如今翻覆人间事，争敲觑劫牵缠。坐下清香苦茗，床头断简残编。\",\"一钩新月印前川。柳雾花烟。朝朝暮暮催千古，风风雨雨堪怜。白眼且看变幻，清尊不负芳年。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f5114a6c58ce','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"幽闲芳草闭柴门。绝似乡村。桑绿鸣鸠花拂燕，新晴帘卷残春。草上深红浅白，阶前剩雨残云。\",\"溪头水满且垂纶。学做闲人。心地不存寒暑念，由他谁富谁贫。载酒客来明月，裁诗人醉黄昏。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f51157ddf478','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"青襟耽误十年余。宇宙身迂。翰墨虚为湖海客，前津始悟迷途。寄傲且看山水，消忧漫托琴书。\",\"倦将短发为人梳。世故休羁。身学偷闲心学懒。萧然物外闲居。建业何妨饮水，武昌不羡多鱼。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f511a6cf706c','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"青蒲白日自铺排。新筑幽斋。白云不碍窗前冷，香分花草亭台。藏帖藏书藏画，栽松栽竹栽槐。\",\"区区俗虑早忘怀。一任痴呆。但得清闲穷也好，风尘鱼鸟休猜。无怪黄梁托梦，何人碧眼怜才。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f511c39f2bce','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"白云堆里有衡茅。近水临桥。有竹有松兼有鹤，更多短短红桃。翠壁丹霞景媚，青山绿树楼高。\",\"门前常有小渔舠。不待人招。载酒穿云湖上晚，醺醺弄月吹箫。个里不关荣辱，其间尽自逍遥。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227288e9f511f36cd871','风入松·闲适十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"芙蓉花落水痕收。山冷云稠。蒹葭风露苍苍晚，深烟荡破渔舟。有待一林红叶，相亲几队沙鸥。\",\"能歌白苎锦缠头。秋色明眸。十二桥边花月夜，凤钗雀舫长游。谈笑自怜空阔，萧骚兴落抢洲。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22728ac8d62f459fd84e','青玉案·玉璁','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"轻烟低拂游丝倦。门掩昼长庭院。绿暗红残帘幂静。\",\"夕阳影里，芳草丛中，一种花娇颤。翠铺锦簇蝶身裁，看轻盈、还怜飞燕。舞向东风争得意，玉剪玲珑，枝横缭绕，满砌开应遍。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22728bb83f6d4439934a','蝶恋花·蝴蝶花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"蝶倦枝头飞不去。醉色迷香，抵为春留住。翅染青青陇头雨。联翩斗影扶风舞。\",\"为惜花开蜂蝶妒。花幻蝶飞，幻蝶看犹误。梦断夕阳芳草路。春深花蝶归何处。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22728f4af88dadcc4c86','贺新郎·七夕','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"风洗云痕净。展藤床、天街傍晚，罗衫较冷。银烛画屏萤拍扇，最好今宵夜景。\",\"看黄姑、试开妆镜。姱容为怯经年病。漫匀霞、腮颊填桃杏。\",\"把眉月，鬓云重整。痴呆银汉窥桥影。鹊声喧、香车七驾，彩鸾双逞。\",\"玉殿瑶阶传密意，宝枕华灯欢庆。且莫话、别来孤另。\",\"此夕人间心上事，指双星、暗把盟言订。更乞与，红丝胜。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227291b2e2049fb1fde7','蝴蝶儿·秋海棠','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"万千娇，绕丛开。轻盈疑是阿娇来。桃花填两腮。\",\"怯雨垂垂湿，羞风故故歪。多情偏喜傍幽斋。秋色满苔阶。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22729209b8fc62190940','高阳台·重九','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"黄菊擎霜，紫萸带雨，年华又早重阳。风喧病叶，夜来声满书窗。晓看日放些儿影，渐云收、青吐山光。为怜戏马应弦，还忆穿杨。\",\"芒鞋竹杖荷裳。快扳萝扪壁，峻岭崇冈。纵目流观，平沙雁落长行。糗糕菊酒迎凉脯，且鲸吞、沉醉何妨。尽幽闲，佩萸力健，落帽人狂。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272926a71e45db6cd44','春从天上来凌宵','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"袅娜枝条。傍山头屋角，树杪松梢。红轻翠飐，萦绕凭高。软软回风带雨，更藏烟隐雾，向春深、布叶敷苗。看荧荧，朱华烘日，绿蔓凌霄。\",\"直欲飞英百尺，使茶蒿萧艾，仰艳钦翘。松风霞彩，疏影微香，幽人昼梦寥寥。恐木老风枯，必朽草芜同凋。想青囊、曾教采摘，苦口堪调。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227293af97763232c2a8','醉公子·朱槿','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"路口西风媚。夕阳红欲醉。才放又惊残。荣枯朝暮间。\",\"含露篱边泣。何人问颜色。百年如梦中。一晨花较同。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272961a1dab9a74c740','少年游·十姊妹','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"桃腮共染，粉面同匀，挈伴斗残春。朝阳宫里，赵家姊妹，雨露洽新恩。\",\"休错认，蔷薇红澹，芍药未招魂。日晒脂香，风生笑脸，芳意自温存。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272989f5d7afed16bee','小重山·映山红','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"谁将颜色斗春工。珊瑚分细蕊、满山红。枝枝含笑向东风。偏著意，乔木与孤松。\",\"花瓣剪玲珑。山矾开作伴、结芳丛。踏青南陌恣游骢。人攀折，低压鬓髼松。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272994e783d905d07a9','调笑令·含笑花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"含笑。含笑。半吐半开芳抱。有意无声倦容。\",\"唇吐莲花澹红。红澹。红澹。雨过花稀叶暗。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb227299f6e5ad9eccbd7b','探春令·迎春','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"和风咋夜报窗前，道东君来也。洒繁枝、翠羽黄金屑。又喜是、春回节。\",\"些小芳心先引惹。占东风台榭。看从今撩乱，千红万紫，都为伊漏泄。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22729bf0487b356b23d1','绣带子锦带','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"翠软颤芳丛。粉匀深浅红。恍惚飞琼花外，绣带舞香风。\",\"开向雨蒙蒙。翻翠袖、似泣芳容。佳人著意，晓来攀折，为惜飘蓬。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb22729f4884047c061b38','梅花引·红梅','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"丹换骨，酒凝脂。向暖南枝似杏枝。澹丰姿。不禁寂寞，春意惹红丝。\",\"幽香不减月中韵，清标犹似水边时。寄相思。寄相思。罗浮梦醒，残醉怯腰肢。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6dbac9a1f39dfcc','海棠春·海棠','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"蜀锦新裁枝上蕊。笑粲粲、含烟照水。枝垂猩面湿，萼吐樱唇美。\",\"月痕扶醉雕阑倚。情脉脉、匀红蕴紫。烧烛醉忘眠，惜花人未起。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a056e9edb','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"恋霭山光几上摊。心胸写出水云寒。天机意匠许多般。\",\"销白昼，寄清闲。泉石襟期在笔端。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a4e11a0da','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"芸阁牙签散几筵。残灯夜雨竹炉边。坐来心到几千年。\",\"怜雪案，叹韦编。残卷生平未了缘。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a64926c74','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"静院迟迟落子声。风回花雨点楸枰。斜阳残局更留停。\",\"怜蜩甲，叹蚁兵。一笑人间胜负情。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a8460322b','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"嫩白娇红手自栽。枝枝叶叶满庭台。怜香蜂蝶为飞来。\",\"看竹牖，覆苔阶。一花零落一花开。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a86cc5ac1','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"千日何能醉不回。莫教鲸吸耻尊罍。花间长得玉山颓。\",\"叹醒眼，笑痴呆。人间剩有酒怜才。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a90814fe3','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"砚池新水涤晴窗。笔底生花墨带香。山阴书法拓开皇。\",\"惊凤翥，骇龙翔。宵雾朝烟纸上光。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7a9ce3e88f','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"玉指朱弦湿露华。湘帘月挂一钩斜。满庭香雾落梅花。\",\"操白雪，调烟霞。水云声断雁哑哑。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7aab39bfd8','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"联玉挥珠思入神。泠泠句字迥阳春。于今风雅更何人。\",\"修月窟，露天根。诗外真风若与论。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7aba7e15b7','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"柏子兰芽几上焚。松窗烟霭散清芬。残炉隐隐梦回闻。\",\"通紫雾，袅青云。一帘花影荡湘纹。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a6fc4b7ae66eceef','天仙子·读书','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"竹坞青烟一缕浮。半铛黄叶煮云头。龙芽香霭泛瓷瓯。\",\"消午梦，了闲愁。寂寞诗脾渴正投。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a80e96ae39cba068','金人捧露盘·黄蔷薇','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"厌红妖，欺紫媚，占鹅黄。向东风、瑟瑟金装。翠条袅娜，笼烟带雨暗浮香。醉眠金屋开斗帐，轻拆麝香囊。\",\"更萧疏，槐借叶，菊为裳。笑盈盈、雅澹梳妆。露翻影动，打不开蝶醉蜂狂。不受东君脂粉误，别是行藏。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a997104d80a59f15','瑞龙吟·端午','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"月当午。更时值离明，罡填鬼户。萧斋炼草成丹，扶急持危，遵生济度。随时俗。还将酒泛菖蒲，庭栽葵菽。臂悬彩线灵符，衣纫兰佩，门悬艾虎。当轩海榴喷火，池莲艳日，垂杨宿雾。且自散发披襟，腾吟吊古。\",\"竞渡声喧，何处争旗鼓。叹流光，半生过隙，等闲如许。岁逐良辰去。看新月，又催日暮。抵事虚歌舞。扶沉醉，更向玉人低语。满捧瑶卮，莫停金缕。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272a9e14532f38727ca','惜分钗金雀','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花叶碎。枝条媚。软软扶风欲成醉。郁金香。剪鹅黄。钗横鸾雀，枝插鸳鸯。双。双。\",\"枝头羽。风前舞。个个轻盈欲飞去。睡朦胧。鬓髼松。淋漓夜雨，飘泊东风。茸。茸。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272ace3c57c456f261f','乌夜啼紫鸡冠','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"翠凤分翎耸耸，紫丝刷羽层层。势逞风摇浑欲斗，雨急听疑鸣。\",\"朱顶慢教鹤并，丹砂未许蜂争。啼霜不报人间晓，何意拟苍蝇。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272ae497fcbbe1551e5','绕佛阁·四月八日浴佛','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"教法西来，万天秋洗，千江月静。三乘印证。大破群迷、悟即为贤圣。镕金冶铁，妙相庄严，烧灯爇鼎。慧观顿觉，圆明不灭，方寸涵宗镜。\",\"四月正逢八日，看浴佛都人争胜。净土莲台，握圣水濯清影。昙花飞绕，俨悉达初生风景。无论贤愚，何分贵贱，是法平等。愿洗涤、尽皈清净。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272b1071ee8df4ee33e','江城梅花引·蜡梅','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"移来蜡国小黄香。似梅妆。胜梅妆。月中弄影，嫩色澹鹅黄。较密更鲜溶腊润，妖冷艳，倚琼枝，立水傍。水傍。水傍。\",\"傲雪霜。抱紫囊。幻行藏。化作黄姑，好颜色、金缕衣裳。一段清幽，雅素道家妆。炼出庚辛丹灶火，金丹就，色吐金丹，满树香。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272b56fa762f8cef201','绛都春·元日','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雪色初晴，看海月流红，楼台清晓。鼓吹千门，和气催促春回早。桃符爆竹都门道。太平风景依然好。朱屋笙歌，青楼人醉，红尘欢笑。\",\"吾老。四十年来，见人世、风雨阴晴多少。独有梅花，清寒不改当轩好。良辰相对怜倾倒。任散澹、无烦无恼。总红尘有事相干，道胡涂醉了。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272b58df1951ec92536','满江红·春感','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"柳弄鹅黄，又一番、春来时候。景物重新催趱，岁华驰骤。肯将花柳著青春，何故风波催白首。悲欢算定看人忙，牛马走。\",\"燕语还，依旧主。莺啼却，怜青昼。莺莺燕燕弄人愁，天曾知否。梦寐牵缠人世事，死生笼络乾坤手。看破来、两字兴亡，吾何有。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272b6fe1698522e5209','天仙子·风仙','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"茸茸花颤秋深浅。金凤斜飞满庭院。摇弄西风故敢开，解桃愁，分杏怨。不让春光红一片。\",\"玉人松却黄金钏。绕丛攀折黄昏倦。捣向金盆色更奇，傍夜深，争笑卷。朝看玉指猩红撚。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272b77f1a41b74bd619','小庭花罂粟一名丽春','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"一种根枝几色开。轻盈疑似蝶飞来。却从芍药竞翻阶。\",\"纤纤嫩白随风软，袅袅鲜红傍日歪。雨余隋苑锦初裁。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272bea8af98646e0f3f','雨中花·槐花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"十二珠帘阑曲曲。昼阴浓、叶分密绿。满地屯云，当空蔽日，枕箪敧寒玉。\",\"澹金花落疑残菊。怕西风、惊蝉簌簌。乱影疏星，碎摇凉月，庭院怜歌曲。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272bec8f769e3e1a26a','一斛珠·木香','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"人间春尽。林园花落红成阵。千条细蕊摇香粉。惹笑增怜，月底偏温润。\",\"影玉浮霜檀麝喷。恨教飘雪风成阵。独令芳草知丰韵。惜玉怜香，门外无人问。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272bff66d68d6dc46ab','如梦令·白槿','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"笑展荼蘼一撚。色共梨花细剪。开落不经秋，寂寞玉容粉面。\",\"含怨。含怨。鬌与青霜同变。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272c223b1e128a1f76b','新荷叶红莲','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"歇雨吞风，锦云一片浮香。翻霞照水，轻盈历乱残妆。波光月色，净如洗、碧袜红裳。隐隐撩人，丰度洛浦行藏。\",\"娇怜西子，红新还忆张郎。远浦歌声，晚天烟隔清杨。柳边落日，溪边醉、野水孤航。折得一枝，归去冰簟生凉。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272c6c533e85cda79ab','桃源忆故人怀友','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"望入烟云天际杳。风战绿杨如扫。鸡塞雨淹芳草。路隔知多少。\",\"离情解妒腰肢小。梦里此情偏好。唤醒数声啼鸟。花落春归早。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272c896fc5129391fda','洞天春蕉花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"山乐雨鸣清晓。凤翥倚垣风扫。展簟梦回凉更好。怕入秋声早。\",\"密掩书窗悄悄。避却炎蒸多少。叶如界纸，心似柚书，聊供挥草。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272cbdb55d29e05e271','行香子·山矾一名七里香','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"蕊撒冰沙。叶亸金芽。托根枝、喜傍山家。春风倚笑，香雾秾花。\",\"白婆娑，深涧雪，洒松丫。常开二月，清芬七里，澹悠悠、月衬青霞。芝兰结伴，踯躅同华。韩寿香，何郎面，许同夸。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272ccf799e7c8b818b6','鹊桥仙·牵牛','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"金风初动，银汉低垂，传说鹊桥曾度。碧罗蓝缕俨仙衣，乞与人间晓来睹。\",\"木樨开后，芙蓉巳老，占却秋光无数。竹篱摇蔓短长风，欲飞去、紫云高处。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272ceb1901c33e3a8d2','花心动·瑞香','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"露湿烟凝，簇繁英、镂红雕翠。外着轻霞，中含澹玉，开向残冬天气。夹径低垂青步障，倚风俨动鸾凤尾。锦萼包香待放，还须春雨。\",\"梦醒午窗凝思。恨愁结丁香，情怜兰蕙。剪裁鸡舌，剖碎麝脐，浓馥满襟蓓蕾。喜弹压芳菲，暖烘人醉。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272d0b336fa1bcc9b9b','滴滴金滴滴金','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花镕腊瓣春铺地。衬残红，伴草碧。最是金莲香印迹。把笑能相惜。\",\"晓来泪染露华湿。怜去马，分行色。醉眠曾为春留客。梦里长相忆。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272d2dd3d9c2bb3d145','小重山·棠棣','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"朵朵春风碎剪金。长条垂巧叶、绿森森。蔷薇常共结芳林。屏锦绣、红嫩著黄侵。\",\"傍晓惜花心。开帘携凤侣、共追寻。一枝斜压伴瑶簪。想约略、仙掌露华深。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272d8598e32d89176cd','四园竹秋怨','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雨扶黄叶，夜半打空窗。罗衾较冷，鹤焰知寒，蟋蟀依床。萧瑟西风摇落，树枯山瘦，朝来减却秋光。\",\"断人肠。有约梦迷巫峡，空怜雁出潇湘。无那枕边醒耳，听彻沉沉玉漏长。一宵难过，何况是，无日不思量。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272d9e716af57c2510b','隔浦莲金莲','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"浪迹碧湖葑草。羡金铺翠藻。俨似水中莲，比叶细，较花小。柔枝擎袅袅。谁幻巧。开落知多少。\",\"自幽悄。一片秋容，更有黄叶白蘋红蓼。举属匏尊，落日放船偏好。野水寒烟共鱼鸟。容老。弄花波怜醉倒。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272dcea3776ffdf4123','更漏子·紫薇','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"倚南风，开长日。占炎蒸三月力。笼浅碧，荡深红。星垣名与同。\",\"泛远香，延凉月。雨时行飘绛雪。栽上苑，伴朱衣。草诏凤凰池。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272dd6a9642a2ff32e8','豆叶黄菜花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"春满郊原水满堤。阴阴麦长菜花肥。丛丛蝴蝶斗香飞。\",\"洒金泥。浪叠黄云望欲迷。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272de0a45780ab77df3','清平乐·春','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"湖烟幂䍥。山白云堆叠。千顷冰花寒皎洁。蹊径银铺曲折。\",\"兴来沽酒山家。吟看玉树含葩。策蹇断桥残雪，孤山折得梅花。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272de0a457867445915','清平乐·春','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"树红云白。秋冷山衔碧。十二桥边霞影赤。摇落芙蓉堪惜。\",\"孤舟荡破深烟。日日看花水边。花下闲容白发，杖头剩有青钱。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272de0a457875ce1d53','清平乐·春','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"桃花杨柳。试问春来否。水上笙歌花下酒。兴托百篇八斗。\",\"华裾翠袖翩跹。香车宝马联骈。芳草醉扶游客，夕阳鼓打回船。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272de0a4578af03d6ba','清平乐·春','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"荷花十里。鲜妍裁锦绮。翠叠红铺香盖水。零乱轻雷过雨。\",\"阴阴柳暗长行。习习荷风荐凉。短棹人归烟月，笛声吹满沧浪。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272df2dcf61869c4818','夜游宫·银木樨','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"澹白枝头弄影。分异种、西来鹫岭。月冷银河占清景。散香风，横玉宇，照冰镜。\",\"人间秋色胜。夜凭阑、自怜幽兴。清虚之府何孤另。独不念，少年人，许相并。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e0e3c2ed131828b4','醉花阴·垂丝海棠','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"萼吐丝丝红衮衮。颤雨生轻韵。袅袅柳争妍，无力随风引。\",\"妆成云白拈霞衬。残醉含微哂。都只为东君，牵愁惹恨，缕缕萦方寸。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da8592a322573','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"门外柳条黄。细草生香。落梅风里雪飞扬。春暧冰消溪上水，陇麦初长。\",\"山色澹斜阳。暮霭苍苍。树头新月露微茫。人醉草堂呼不起，梦熟羲皇。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da85931ad962a','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"山冷白云秋。翠暖烟浮。黄花红叶满林邱。或驾轻车以寻壑，或棹孤舟。\",\"禾黍刈田畴。芋栗全收。村村赛社竞歌讴。饱暖不知朝市贵，何忮何求。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da8593c323fd7','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雪月澹梅花。翠竹欹斜。纸窗松影弄枝丫。万籁不闻尘世隔，谁似仙家。\",\"万事且由他。断却嗟呀。如今觉是悔前差。笑指白云吾老处，占住烟霞。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da85944bdd608','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"秋水浸寒花。溪上人家。高低茅屋傍蒹葭。剥枣食瓜村酿熟，醉月西斜。\",\"残夜雁咿哑，露冷汀沙。空山无梦到天涯。屋角三竿人未醒，树杪饥鸦。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da85948cd5955','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"山径绿阴肥。香冷红稀。午窗梦醒子规啼。流水落花春去也，柳絮沾泥。\",\"把钓坐临溪。乐矣忘饥。得鱼沽酒自相宜。眼底风波看已破，何是何非。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da8597c5bc378','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"露冷月痕凉。落叶敲窗。烧鏺人坐夜初长。半榻残经三尺剑，一炷清香。\",\"无事到心忙。草草行藏。世缘何必更思量。看破七无成一笑，正此时光。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da8599f6c111e','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"爽气荐荷风。葵紫榴红。绿沉云冷树阴浓。几度阴晴梅子雨，花径苔封。\",\"静境趣无穷。领略谁同。云头蟹子火初烹。香袅篆烟窗下坐，一曲丝桐。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da859bf6494f1','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"风雪掩空斋。僵卧云偎。先春香吐水边梅。茶煮竹炉煨骨拙，清溢吟怀。\",\"策蹇几人来。山径幽哉。荆扉半月不曾开。眼界一空心地寂，身在蓬莱。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da859ced57281','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"披雾葛衣轻。树底凉生。阴阴修竹翠为屏。池浸紫薇和月冷，几点流萤。\",\"花影湿棋枰。北牖风清。酣酣梦醒夜三更。最好深山更深处，避却炎蒸。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e13da859db94df5c','浪淘沙·山居十首','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"红紫覆亭台。贝锦天裁。芹泥旧垒燕飞来。唤妇鸠鸣风酿雨，云湿苍苔。\",\"无事掩萧斋。远矣襟怀。从教鸥鹭莫疑猜。一任春风都管领，花落花开。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e166c4cad7fa6fba','惜分飞·怀友','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"山暖浮烟云吐月。春树江天人别。猛教心地热。却怜鸠计当初拙。\",\"那竟如今音信绝。试问行踪车辙。何处堪悲咽。深闺此际愁千结。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e5784468f59da746','重叠金金钱','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"花镌红玉开日午。长恨宵分付尘土。模铸竟谁成。金钱旧得名。\",\"人作樗蒲竞。共喜得花胜。旧说解通神。争怜不济贫。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e7b40383f5fd187c','月照梨花梨花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"翠袖撩人，玉容无语。深院闲亭，黄昏夜雨。晓来风乱秾香。洗残妆。\",\"压树白云飞不起。盈盈照水。纱窗春梦里。洛浦俨摇琼佩，月色溶溶。晚烟笼。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e984493fb72e2f33','明月棹孤舟茗花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"香浮碧月花浮玉。蕊抱檀、心枝扑簌。偏傲霜寒，为怜露白，更不较开迟速。\",\"水畔依依林畔竹。自不染、些儿尘俗。待得春回，香芽抽叶，煮蟹子松烟熟。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272e9ea9707621cf2cc','长相思·怀人','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"雨声长。雁声长。信隔三秋泪两行。听来应断肠。\",\"见时忙。别时忙。会少离多天一方。说来空感伤。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272ecaf08e63b45a5af','红林檎近山茶','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"碎锦铺寒日，烂红偷早春。芳葩知耐雪，浓艳欲撩人。暮烟山雨添色，重更精神。摇动微微霜月，巫峡近前身。\",\"似嫌梅冷澹，若共杏温存。天教禁冷，生成雪骨冰魂。喜闲门住傍，有花山下，对花日日开尊。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272ee06f3a88e2937db','鹧鸪天·自述','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"打伙红尘四十年。空将一线为人牵。如今准给山林帖，作正支销风月钱。\",\"红满面，白盈颠。朝朝花柳傍湖边。从教占定人间乐，不向风尘计可怜。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272eeda66565fd4c583','汉宫春·牡丹','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"翠软红肥，瑶台上沉香，百宝妆束。酣风醉日，佩缕俨摇金玉。艳夺香偏，春色丽、神妖动欲。笑轻盈，东风待嫁，铺叠锦衾绣褥。\",\"试听清平歌曲。倒尊罍畅饮，高张银烛。花如解意，向人前摇红颤绿。风风雨雨，惯愁人、休教催促。愿东君，怜香惜媚，为念牡丹荣辱。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272efc4b0516af07d41','惜分飞·杨妃茶花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"沉醉东风花不语。斗帐香销金缕。酒色能多许。剩将残醉枝头吐。\",\"铅华占得杨家女。挽不住春归去。莫伤心事，总为胭脂误。马嵬山下香凝土。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272f2c132302e612601','西江月·题情，代作','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"有恨不随流水，闲愁惯逐飞花。梦魂无日不天涯。醒处孤灯残夜。\",\"恩在难忘销骨，情含空自酸牙。重重叠叠剩还他。都在淋漓罗帕。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272f2c13230368e6eb3','西江月·题情，代作','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"地久天长有尽，愁消恨减何年。秋深病惹是春前。恩怨厮成一片。\",\"暗地情怜锦字，困人妆倦花钿。要知了却好姻缘。不在梦中峡畔。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272f82832b40a5348cf','南乡子·素馨','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"素质艳名姬。露屑风香著意培。不逊寒梅羞御粉，依依。蟠向云鬟香独奇。\",\"密翠似攒眉。月底轻扬玉缕衣。烟冢荒城迷路草，萋萋。兴寄幽花抵自知。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272fb02d303a1f17a41','玉树后庭花李花','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"亭亭树绕春丝茧。玉华细前。苇绡万朵摇青艳。桃蹊柳堰。\",\"晓看妒杏蒸霞面。偏怜莺燕。为爱东风闲倚遍。九标堪羡。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272fe119267ed890721','声声令鼓子','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"马嵬香散，羯鼓尘生。花枝解惜旧时声。把皮腔幻出，日边急，雨中鸣。\",\"俨风走、渔阳甲兵。恨到无声。方是怨，几时平。鼓催刻漏梦魂惊。\",\"有形无调，打不出，别离情。都付与、东风战争。\"]',NULL,NULL,NULL,NULL),('e647d10f46cb2272fe516c51674f6315','浪淘沙·薝葡一名栀子','高濂','e647d10f46cb2272a1afc58c806beafe','明',NULL,'[\"鼻观妙香通。雪里云封。自甘冷澹不求荣。六出一花重叠瓣，净土家风。\",\"觅句对芳丛。为问掸宗。相看气味道和同。枝叶岁寒青不变，实傲霜红。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b3252baffba10e4bcc','东风第一枝·游峡山','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"六寺烟深，双峰黛冷，嬉游时际暄暖。曲塘画癋参差，曦来荻芽沙浅。娇春罗绮，映绿净、波纹吹软。尽瘦生、掌上谁擎，漫认倚风轻燕。\",\"帘乍卷、垂杨拂眼，门半掩、小桃窥面。歌云日趁新妆，泪竹几巡古苑。微茫林角，挂一抹、残杨如线。怕后游、晴雨无凭，有约也应难见。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b3429aead0dd61d2a6','定风波·客园看桂','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"蟋蟀声寒黯淡风，谢家池馆九秋中。丛桂四山香满院，一片，妆成金屋画阑东。\",\"对酒不知身是客，堪惜，夕阳容易散晴空。却待夜深明月好，须早，扁舟隔岸采芙蓉。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b34334969fd2aba7c0','蝶恋花','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"芳草生烟横野渡，绿到春山，山色深于树。离恨年年萦别绪，桃根桃叶秦淮路。\",\"眼底韶华容易度，燕子斜阳，说与归期误。婪尾余花春已暮，柳丝不解牵伊住。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b35ceac47515c163b0','东风第一枝·春日','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"南浦拿舟，东郊列幕，中旬准拟携酒。恁教压雨欺烟，几时小桃红透。鱼笺讯远，要修褉、山阴人旧。倩好风、为扫苔茵，池水尽他吹皱。\",\"更明日、垂杨绕堠，早今日、芳兰密牖。丽词一曲歌成，那寻雪儿绣口。无多春色，总莫负、燕前莺后。可怜是、几个黄昏，多半忆君时候。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b38279134144f36d54','生查子','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"径转翠屏开，一片荼蘼雪。不待晚妆成，自下香阶折。\",\"莺是故园莺，月是当时月。谁分比来心，只觉看承别。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b382791341c38f4c06','生查子','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"切切乱蛩悲，暗与羁魂语。深夜苦怀人，卧听芭蕉雨。\",\"烟雾障林峦，秋梦行何处。孤雁落西风，声咽寒芦渚。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b39c70a6d89446e52a','踏莎行','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"芦月凝霜，蕉风作雨，飘飘叶落梧桐树。蛩吟底事动哀音，不知人在愁深处。\",\"鹤怨空鸣，鸿孤远度，伤心又被秋光误。怪他有梦不归来，云山岂碍魂归路。\"]',NULL,NULL,NULL,NULL),('e647d10f46e846b3e429ae79476d0d29','如梦令','周珽','e647d10f46e846b3d1457b72d1457b72','明',NULL,'[\"深院一番风起，狼藉残红满地。点点露珠浓，应是别离人泪。\",\"无寐，无寐，闲却半床鸳被。\"]',NULL,NULL,NULL,NULL),('e647d10f475eb15b63950a63fc071e7c','如梦令·春风','支如增','e647d10f475eb15bd1457b72d1457b72','明',NULL,'[\"又见东风吹遍，岁岁扰侬庭院。皱了绿萍纹，又皱却桃花面。\",\"埋怨，埋怨，忽地暗将人换。\"]',NULL,NULL,NULL,NULL),('e647d10f4871c676154effbd2a6633a1','浪淘沙','郑妥','e647d10f4871c676d1457b72d1457b72','明',NULL,'[\"日午倦梳头，风静帘钩，一窗花影拥香篝。试问别来多少恨，江水悠悠。\",\"新燕语春秋，泪湿罗裯，何时重话水边楼。梦到天涯芳草暮，不见归舟。\"]',NULL,NULL,NULL,NULL),('e647d10f497dc40d87fd27f92ae6d785','醉春风','尹春','e647d10f497dc40dd1457b72d1457b72','明',NULL,'[\"池上残荷尽，篱下黄花嫩。重阳还有几多时，近、近、近。曾记旧年，那人索句，评香斗茗。\",\"望断萧郎信，懒去匀宫粉。虾须帘外晚风生，阵、阵、阵。双袖生寒，一灯明灭，博山香烬。\"]',NULL,NULL,NULL,NULL),('e647d10f4a3ac22a2f044e1bc516f09d','蝶恋花·赠妓','祝允明','e647d10f4a3ac22a07042ac75cce8ded','明',NULL,'[\"闹蝶窥春花性浅。未了妆梳，小颗唇朱点。玉絮吹寒飞力软。深深绣户珠帘掩。\",\"厮放临时仍泥恋。一把风情，错认徐娘减。略绰晕香红半片。阑干回首东风远。\"]',NULL,NULL,NULL,NULL),('e647d10f4a3ac22a9e74a2a53115f5f1','题沈山人藏王舍人竹枝','祝允明','e647d10f4a3ac22a07042ac75cce8ded','明',NULL,'[\"舍人清真同右军，金銮退直谢鸡群。千金不肯卖一叶，今日凤毛留赠君。\"]',NULL,NULL,NULL,NULL),('e647d10f4a64294c8e8d6549048779f8','临江仙·同杨魏村少参登桐君山','万士和','e647d10f4a64294c490640b48f19793b','明',NULL,'[\"睡里钓台相失，寻仙且上桐山。乱峰环合碧波寒。笑携黄鹤伴，来坐白云间。\",\"二十年前游处好，趋庭犹忆红颜。而今狼藉鬓垂斑。西风衰草外，长啸下松关。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f0e2a3bcf4bfac379','扬州竹枝词四首','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"往年歌管不曾闲，遮莫商船到北湾。今日樵儿吹野曲，犹传一曲汉东山。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f0e2a3bcf8157d448','扬州竹枝词四首','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"隋家杨柳古扬州，杨叶无多野水流。莫说小红楼畔路，琼花观里少人游。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f0e2a3bcfcf071025','扬州竹枝词四首','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"十里珠帘卖酒旗，一时都逐火云飞。伤心欲问前朝事，白发淮人即渐稀。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f0e2a3bcffd51ece9','扬州竹枝词四首','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"小年曾入镇南家，回首西风白日斜。独抱胡琴理胡曲，白菱飞上野菱花。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c203387828','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"新黄河接老黄河，河上船同蚁阵多。一派棹歌天上落，分明口口叹阿㗻。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c225f77df7','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"南客新来北地居，莫因莼菜忆三吴。椹子酒胜桑落酒，槎头鱼赛细鳞鱼。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c23e9cc6b3','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"前船夫问后船夫，尔在河南运米无。九十石粮车十辆，几多牛疋几人驱。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c24294d501','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"挂帆挝鼓响耽耽，南船北船密如蚕。往日南人齐赴北，如今北客尽朝南。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c27700f1e1','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"金龙王庙在河干，刲羊烧酒上杯盘。乞得好风行半月，归来庙下赛衣冠。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c27dc85e99','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"为防潋子向中流，外倒风多里倒忧。莫道路途行不顺，邳州徐州又济州。\"]',NULL,NULL,NULL,NULL),('e647d10f4a8e992f83cfc9c2ffa1659c','竹枝词','唐之淳','e647d10f4a8e992f861dc9bd9701a1c1','明',NULL,'[\"春来日日起狂风，不分南北与西东。行客怕风兼怕水，几时上得吕梁洪。\"]',NULL,NULL,NULL,NULL),('e647d10f4c497233a857eeea7c206f75','渭城曲','靳学颜','e647d10f4c49723383f255037bd28f15','明',NULL,'[\"春风送客渭城西，折柳亭前落日低。骢马渐随尘影没，黄鹂飞上戍楼啼。\"]',NULL,NULL,NULL,NULL),('e647d10f4d0013cd25eb6af79bc7bac3','清平乐','吴子孝','e647d10f4d0013cdfa1e9c964d6e4749','明',NULL,'[\"韶光易变，凉露霏如霰。翠卷香消憔悴遍，一夜池塘秋晚。\",\"芦枯苇折波中，凫栖雁宿芳丛。尽日倚阑如画，白苹红蓼西风。\"]',NULL,NULL,NULL,NULL),('e647d10f4d0013cd5afe145aea499403','点绛唇·二月八日夜泛射渎归作','吴子孝','e647d10f4d0013cdfa1e9c964d6e4749','明',NULL,'[\"花信风轻，为花日日芳游早。曲溪缭绕，归棹情多少。\",\"月映清波，良夜春寒悄，长空皎。歌声缥缈，醉里愁如扫。\"]',NULL,NULL,NULL,NULL),('e647d10f4d0013cd7062abcb250ccd11','诉衷情','吴子孝','e647d10f4d0013cdfa1e9c964d6e4749','明',NULL,'[\"嘉靖癸丑、甲寅，东南倭乱韶光都过乱离中，登眺觉心慵。青山城外望断，愁绝黛痕浓。\",\"闲把酒，倚楼东，小梅红。馆娃烟草，香径风兰，长记游踪。\"]',NULL,NULL,NULL,NULL),('e647d10f4d0013cd8eb43d8f07f19d34','风流子·登虎丘致爽阁','吴子孝','e647d10f4d0013cdfa1e9c964d6e4749','明',NULL,'[\"雨痕消已尽，长空静、皎日映江流。看寒草都腓，衰林渐薄，翠荷遍沼，黄橘连洲。新晴好、抱琴遵竹浦，载鹤共松舟。近市炊烟，隔林樵唱，苍茫望远，窈窕寻幽。\",\"晚来还著屐，穷岩壑、更上绝顶飞楼。何事西来爽气，偏对吟眸。指青峦万叠，澄川百道，绕阑分列，似画难收。方信三吴佳景，尽属清秋。\"]',NULL,NULL,NULL,NULL),('e647d10f4d0013cdf02891a4692bbe66','采桑子','吴子孝','e647d10f4d0013cdfa1e9c964d6e4749','明',NULL,'[\"纤云尽卷天如水，芦荻风残。松竹霜寒，更看前溪月满山。\",\"画船红烛金尊酒，子夜歌阑。缓吹轻弹，得意人生且尽欢。\"]',NULL,NULL,NULL,NULL),('e647d10f4d117d3da2d534f63532f89e','减字木兰花','赵宽','e647d10f4d117d3d01d8bae20c9ebb2d','明',NULL,'[\"姚江阻雨，寒风吹水，微波皱作鱼鳞起。白雨横秋，秋色萧条动客舟。\",\"疏钟何处，知在前村黄叶树。茅屋谁家，荒径无人菊自花。\"]',NULL,NULL,NULL,NULL),('e647d10f4d1d4eccd4b081e45c15836e','苏幕遮','刘胜','e647d10f4d1d4eccd1457b72d1457b72','明',NULL,'[\"恨桃花，憎柳絮。何事春来，浪逐东风去。邂逅从君花下语。无那情痴，梦里还凝伫。\",\"楚台风，巫峡雨。暮暮朝朝，无计重相遇。背处偷弹双玉箸。自笑多情，翻为多情误。\"]',NULL,NULL,NULL,NULL),('e647d10f4d4a8215415d41bd6e35509f','谒金门','邵圭洁','e647d10f4d4a8215d1457b72d1457b72','明',NULL,'[\"江草碧，又见一年寒食。风雨落花春可惜，卷帘人独立。\",\"十里青山咫尺，两岸绿杨如织。酌酒赋诗俱未得，浪将光景掷。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3c3d58f24c217ad812','临江仙·林见素赴云南宪副过镇远赠予','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"滇海云深逢老象，时于沙底埋牙。知君归兴入春赊。凤山烟雨里，数尽旧时花。\",\"闻道天家收放逐，玉堂新草黄麻。仙源好去问灵槎。未应同老我，风雨漫思家。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3c3d58f24caaf1e97a','临江仙·林见素赴云南宪副过镇远赠予','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"记得高斋相对坐，孤灯疏雨寒宵。玉骢一去杳难招。天涯芳草路，转入望中遥。\",\"百岁光阴秋梦短，鹿埋何处芭蕉。旧情偏傍故园饶。酒杯江上饮，吟镫月中敲。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3c49ad836f9663995a','浪淘沙·林见素赴云南宪副过镇远赠予临江仙','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"樽俎识英雄。玉立山嵷。孤怀不与俗人同。一疏汪洋忧国泪，非少非翁。\",\"楚水挂烟蓬。路入黔中。青天似念远人穷。有脚阳春行处好，暂尔劳公。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3c49ad836ffe62d8e3','浪淘沙·林见素赴云南宪副过镇远赠予临江仙','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"夜雨几番情。短梦长更。一壶春酒待君倾。白马不来花落尽，对景心惊。\",\"记得旧时盟。鲈脍莼羹。欲摇晚辔听春莺。只恐东山归计早，有负苍生。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3c86dc8340f81c4c76','满江红·寓南都题西园池亭用宋僧晦庵警世韵','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"宇内寓形，何须问、足与不足。天生物、五行均赋，有嬴有缩。譬如车轮三十辐，迭为上下交翻覆。履前途、孰谓皆夷平，无砾碌。\",\"荒山下，一间屋。败壁底，一瓶粟。少有人于此，肯着双目。器小不能胜大受，命穷岂足膺多福。请从今、消释此生心，休多欲。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3c88cccc041507ffad','归朝欢·漳守陈公洪谟有捕寇功膺奖劳之典僚属乞词以贺','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"太守胸藏孙武子。十万甲兵盘肺腑。汀州鼙鼓正纵横，用权制变谈笑取。汀纷方如许。漳南随处种禾黍。这太平，左撑右柱，太守用心苦。\",\"喳喳喜鹊鸣前署。报道褒书来宪府。蛟龙不作泥涂蟠，凤凰终展云霄羽。化工善陶铸。谁云翻覆无凭据。吹寒炉，石烹五色，看补青天去。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3cb9a77d2ca287aae5','浪淘沙·再用前调书怀','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"宦思与羁情。惯见频更。丈夫泪不等闲倾。得丧路头勘破久，宠辱谁惊。\",\"诗社订新盟。玄酒大羹。鹿声鸟语共呦嘤。只因昨夜思亲苦，白发齐生。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3cdfafe3a877433b1c','临江仙·再用前调书怀','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"高阁日长人语静，风铃时动檐牙。故乡道路望中赊。只凭寒夜梦，和月到梅花。\",\"春事阑珊双鬓短，襟怀半在桑麻。落英无意恋枯槎。东君犹自错，留我美人家。\"]',NULL,NULL,NULL,NULL),('e647d10f4e3fac3ceae61d81de7c9ee9','水调歌头·郡守张白斋入觐僚属乞词为赠','周瑛','e647d10f4e3fac3c411ae1bf42d6c7d6','明',NULL,'[\"马首红尘暗，道路苦多岐。远追鸾凤俦侣，结驷入京圻。袖有万言长疏，朝修夕改，墨汁尚淋漓。仰面青天近，欲进翻自疑。\",\"披云雾，睹日月，伏轩墀。皇情悦豫，赐问及鳏嫠。四海卧同一榻，万物视同一体，尧舜此襟期。花外春衫薄，归骑莫迟迟。\"]',NULL,NULL,NULL,NULL),('e647d10f4e476ca526c185909ba8bc99','万年欢·赠别陈都事','林弼','e647d10f4e476ca5d1457b72d1457b72','明',NULL,'[\"问讯东君，又何事、一岁一番归去。杜宇声中，撩乱落花飞絮。漫向江头凝伫，想无计、可能留住。江亭上、况有离人，不禁千种愁绪。\",\"还思泽国风尘，更谁能、草檄名重藩府。旧业都荒，茅屋稻田瓜圃。说与风流谢傅，且休把、云山自许。应回首、人在丹霞，满庭榕叶莺语。\"]',NULL,NULL,NULL,NULL),('e647d10f4e476ca53c4b02f8208e0b77','木兰花慢·送孔子远少府归东瓯','林弼','e647d10f4e476ca5d1457b72d1457b72','明',NULL,'[\"羡千年泗水，流圣泽、尚淙淙。远西引河源，东连岱岳，南下瓯江。如君一门簪绂，更风流文彩世无双。柳外藏书楼阁，竹间放鹤轩窗。\",\"弓刀暂尔拥旌幢。清誉蔼南邦。喜烟净妖狼，滩飞瑞鹅，月静惊厖。归去吴钩锦带，想高堂春酒正盈缸。菽水清欢未极，风云壮志休降。\"]',NULL,NULL,NULL,NULL),('e647d10f4e476ca5899388ed578ada72','木兰花慢·为允元学录寿时戊戌中秋南城溪楼即席分得微字','林弼','e647d10f4e476ca5d1457b72d1457b72','明',NULL,'[\"秋江水上，花外岸、柳边矶。喜满眼晴光，溪云摇曳，山日熹微。无限庾楼清兴，一瓢春酒，今夜莫相违。幸对月明风细，况逢莼美鲈肥。\",\"寸心此际念庭帏。称寿舞斑衣。笑薄宦孤踪，塞鸿海燕，北去南归。万顷澄江如练，咏新诗却忆谢玄晖。但愿诸公长健，共看千里清辉。\"]',NULL,NULL,NULL,NULL),('e647d10f4e84fdb8e2d4a9c308db9892','临清柳枝词二首','董谷','e647d10f4e84fdb8d1457b72d1457b72','明',NULL,'[\"春早持斋一月余，罗衣初试觅香车。丁宁夫婿严斋禁，顶上归来始破除。\"]',NULL,NULL,NULL,NULL),('e647d10f4e84fdb8e2d4a9c3d51723e1','临清柳枝词二首','董谷','e647d10f4e84fdb8d1457b72d1457b72','明',NULL,'[\"结束腰肢细可怜，女奴相送上秋千。风光又是清明近，堕却金钗记去年。\"]',NULL,NULL,NULL,NULL),('e647d10f4ead9b7f8d41f387ac7213ff','西江月','黄尊素','e647d10f4ead9b7f277281aabc573864','明',NULL,'[\"疏星一一如洗，朗月明明欲波。高台夜听竹声过，热心被他吹破。\",\"最喜良田满满，还看远岫峨峨。风前一枕漫颜酡，却笑从前总错。\"]',NULL,NULL,NULL,NULL),('e647d10f4f00b6d52d9a88c0f8e5301d','蝶恋花·苏堤春晓','莫璠','e647d10f4f00b6d5d1457b72d1457b72','明',NULL,'[\"十里楼台花雾绕，宜雨宜晴，山色笼春晓。杨柳梢头残月小，海棠枝上犹啼鸟。\",\"兰帐玉人初睡觉，试问楼前，画舫开多少。报道寻芳人起早，紫骝嘶过香尘道。\"]',NULL,NULL,NULL,NULL),('e647d10f4f00b6d582b709f8334da90d','蝶恋花·曲院荷风','莫璠','e647d10f4f00b6d5d1457b72d1457b72','明',NULL,'[\"五月凉风来曲院，绿水芙渠，红白都开遍。风递荷香情不断，采莲舟过歌声缓。\",\"醉折碧筒供笑玩，翠盖红绡，高下翻零乱。向晚新凉生酒面，六铢衣薄停纨扇。\"]',NULL,NULL,NULL,NULL),('e647d10f4f4f641a7008933bd53ff6c0','渔歌子·钓鱼效张志和体','陈献章','e647d10f4f4f641a0663a4ddcfa53013','明',NULL,'[\"红蕖风起白鸥飞，大网拦江鱼正肥。微雨过，又斜晖，村北村南买醉归。\"]',NULL,NULL,NULL,NULL),('e647d10f4fd08c11a12cd0efe3911ca5','摸鱼儿·苏堤春晓','瞿祐','e647d10f4fd08c11d1457b72d1457b72','明',NULL,'[\"望西湖、柳烟花雾，楼台非远非近。苏堤十里笼春晓，山色空蒙难认。风渐顺，忽听得、鸣榔惊起沙鸥阵。瑶阶露润，把绣幕微搴，纱窗半启，未审甚时分。\",\"凭阑处，水影初浮日晕，游船未许开尽。卖花声里香尘起，罗帐玉人犹困。君莫问，君不见、繁华易觉光阴迅。先寻芳信，怕绿叶成阴，红英结子，留作异时恨。\"]',NULL,NULL,NULL,NULL),('e647d10f5074757760a985f688fb4e3c','满江红·题圣月兄归来图','钱肃图','e647d10f50747577297fa7774921f95b','明',NULL,'[\"断水残云，留不住、并州羁客。且收拾、新亭孤泪，江滨吟魄。下榻漫淹徐孺子，归来好赋陶彭泽。把柴门、遥向夕岚开，餐山色。\",\"水绕屋，渔灯白；云满袖，诗筒碧。更斗茶僧舍，寻□村北。半卷阴符摩醉眼，一编心史图秋壁。有袁安高卧在东邻，堪投迹。\"]',NULL,NULL,NULL,NULL),('e647d10f50dfe06920c60fc867c48510','梁州令·谢公岭','朱谏','e647d10f50dfe069ad71c82b1373b284','明',NULL,'[\"着屐登山客，历尽云霞天壁。屐痕犹在白云中，峰回路转，千古无人识。\",\"春来秋去成尘迹，一代风流歇。斜阳影落溪外，长松几树连天碧。\"]',NULL,NULL,NULL,NULL),('e647d10f514b501f7062abcb564551ac','诉衷情','周用','e647d10f514b501f97275a23204da255','明',NULL,'[\"人间何处有丹丘，曾到太湖头。日高主人犹卧，花影满重楼。\",\"寻画史，接诗流，老沧洲。一村烟树，数家茅屋，几个渔舟。\"]',NULL,NULL,NULL,NULL),('e647d10f514b501f8e9cf050afab0d9a','满路花','周用','e647d10f514b501f97275a23204da255','明',NULL,'[\"风前满地花，雨后连天草。今年三月里、春归早。低云薄雾，犹自怜清晓。金尊须臾倒。无奈离愁，为他转伤怀抱。\",\"绣帘斜卷，昼静闻啼鸟。韶华刚九十、勾销了。绿波无赖，点点青荷小。寄语春知道。桃李多情，莫教惜春人老。\"]',NULL,NULL,NULL,NULL),('e647d10f534f34ae1001914f2235842b','风流子·西山晚眺','汤传楹','e647d10f534f34ae806beafe89f03f7d','明',NULL,'[\"东风细雨，斜阳外、芳草未曾休。奈锦缆牙樯，香车宝马，还春旧例，谁事清游。聊尔尔、去携金谷酒，来泛木兰舟。云脚垂青，烟梢涨渌，吟魂欲断，望眼频收。\",\"长堤共搔首，看晚山无数，围住闲愁。度尽篮舆径里，柳岸桥头。看三分诗料，一肩林影，两行归客，几曲春洲。人逐飞鸦争渡，月在中流。\"]',NULL,NULL,NULL,NULL),('e647d10f534f34ae9b02ba6d14691fdc','菩萨蛮','汤传楹','e647d10f534f34ae806beafe89f03f7d','明',NULL,'[\"垂杨解带围青阁，暖风微拂金铃索。燕子不知愁，衔花上小楼。\",\"璧车牵梦去，江草萦心住。酒困未全苏，春光一半无。\"]',NULL,NULL,NULL,NULL),('e647d10f534f34aec2ebbe3b91baa750','鹧鸪天','汤传楹','e647d10f534f34ae806beafe89f03f7d','明',NULL,'[\"一片伤心花影重，美人初出晓云宫。帘前泥落常憎燕，鬓侧花摇数避蜂。\",\"钩月翠，晕潮红，倚烟欺雨咒东风。碧纱窗掩喁喁处，塞北江南春梦中。\"]',NULL,NULL,NULL,NULL),('e647d10f534f34aef137049bea5e5e74','阮郎归','汤传楹','e647d10f534f34ae806beafe89f03f7d','明',NULL,'[\"玉台晓镜试轻凉，薄施秋水妆。空庭对影怯昏黄，微风吹雾裳。\",\"佩洛浦，髻巫阳，凭阑问海棠。夜深多露倚谁旁，折来伴绮窗。\"]',NULL,NULL,NULL,NULL),('e647d10f538d7f299c70a6d88642c6cb','踏莎行','梁云构','e647d10f538d7f29d1457b72d1457b72','明',NULL,'[\"烟锁朱楼，霜铺白屋，墙边透出东篱菊。荷池尚见泻残红，芳郊犹喜堆余绿。\",\"天外鸿孤，灯前影独，月明常把金钱卜。蛩吟错认报佳音，芙蓉笑倚屏山曲。\"]',NULL,NULL,NULL,NULL),('e647d10f53ba2acacdc0c295ec70281e','西湖竹枝歌','周南老','e647d10f53ba2acad1457b72d1457b72','明',NULL,'[\"苏公堤上草离离，春尽王孙尚未归。风度珊瑚帘影直，一双紫燕近人飞。\"]',NULL,NULL,NULL,NULL),('e647d10f53ba2acacdc0c295ed9cb732','西湖竹枝歌','周南老','e647d10f53ba2acad1457b72d1457b72','明',NULL,'[\"采菱女儿新样妆，瓜皮船小水中央。郎心只如菱刺短，妾情谩比藕丝长。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4018db3b248002e4d','西江月·观蜀史梼杌王宗衍事','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"舞影金波月浸，歌声玉叶云留。益州风物似扬州。卓女烧春绿酹。\",\"琼树何如锦树，缯楼却赛迷楼。露桃花下不知秋。正是酒悲时候。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4030a98b1faa1a15e','喜迁莺·寄胡在轩且约高峣之会','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"东风如剪。早见芍药努牙，牡丹生卵。雕楹深笼，芳屏曲护，珍重春工犹浅。回首上林，何处金蕊，玉房心懒。吟笔健，认香山白傅，徘徊华馆。\",\"看花君具眼。击钵诗成，刻烛无妨短。目送孤鸿，手提双鲤，一水盈盈未远。何日棠舟，兰棹飞下，云涛千转。日暮也。望昆明天外，碧云初卷。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d40375651621ef46c3','河传·咏隋堤','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"东楚。南浦。隋堤游处。龙脑飞霙，明珠溅雨。彩女殿脚三千。青娥吴绛仙。\",\"梅黄雨细枫香老。江都好。忘却长安道。不堪回首，芳魂已断迷楼。怨扬州。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4048789c9090ce164','忆王孙·九月八日邀客赏菊','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"西风庭院咽玄蝉。薄雾浓雰采菊天。老去悲秋强自宽。\",\"假婵娟。且向樽前醉管弦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d406d05a682bcdef48','木兰花慢·春日闲居，寄简西峃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"重三今日是，寒食过，又清明。念故国关心，归期难卜，望远愁生。天留烟霞胜处，汇平湖、列岫碧纵横。花里笔床茶灶，松间渔影樵声。\",\"可人咫尺喜逢迎，来往片云轻。看柳埧长绡，交塘归棹，双塔高城。长安水边不记，想绿朱碧玉、事堪惊。笑弄豚儿犬子，饱餐燕麦鱼羹。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d408e2cf92d82f2b67','误佳期壬辰元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"今夜风光堪爱。可惜那人不在。临行多是不曾留，故意将人怪。\",\"双木架秋千，两下深深拜。条香烧尽纸成灰，莫把心儿坏。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d40ba7ff3098809991','鹧鸪天·雨中凉甚独酌','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"为喜新凉入酒杯。自斟自劝自徘徊。暑才溽处云先合，诗欲成时雨早催。\",\"吟草阁，赴兰台。江山金碧画图开。天教一片滇南景，逐我多情万里来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d40ba7ff30db46d629','鹧鸪天·雨中凉甚独酌','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"灌口沙坪摘小春。素馨茉莉荐香尘。要知贮月金波味，只有餐霞玉洞人。\",\"云叶嫩，乳花新。冰瓯雪瓯却杯巡。清风两腋诗千首，舌有悬河笔有神。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d40faef5a04c103c83','西江月·廿一史弹词第一段总说开场下场词二首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"滚滚龙争虎斗，匆匆兔走乌飞。席间花影坐闲移。百岁光阴有几。\",\"说古谈今话本，图王定霸兵机。要知成败是和非。都在渔樵话里。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d40faef5a04d2287ba','西江月·廿一史弹词第一段总说开场下场词二首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"天上乌飞兔走，人间古往今来。沉吟屈指数英才。多少是非成败。\",\"富贵歌楼舞榭，凄凉废冢荒台。万般回首化尘埃。只有青山不改。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41368c91403bb5ae4','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明','这是一首咏史词，借叙述历史兴亡抒发人生感慨，豪放中有含蓄，高亢中有深沉。\n从全词看，基调慷慨悲壮，意味无穷，令人读来荡气回肠，不由得在心头平添万千感慨。在让读者感受苍凉悲壮的同时，这首词又营造出一种淡泊宁静的气氛，并且折射出高远的意境和深邃的人生哲理。作者试图在历史长河的奔腾与沉淀中探索永恒的价值，在成败得失之间寻找深刻的人生哲理，有历史兴衰之感，更有人生沉浮之慨，体现出一种高洁的情操、旷达的胸怀。读者在品味这首词的同时，仿佛感到那奔腾而去的不是滚滚长江之水，而是无情的历史；仿佛倾听到一声历史的叹息，于是，在叹息中寻找生命永恒的价值。\n在这凝固地历史画面上，白髮的渔夫、悠然的樵汉，意趣盎然于秋月春风。江渚就是江湾，是风平浪静的休闲之所。一个“惯”字让人感到些许莫名的孤独与苍凉。幸亏有朋自远方来的喜悦，酒逢知己，使这份孤独与苍凉有了一份慰藉。“浊酒”似乎显现出主人与来客友谊的高淡平和，其意本不在酒。古往今来，世事变迁，即使是那些名垂千古的丰功伟绩也算得了什么。只不过是人们茶馀饭后的谈资，且谈且笑，痛快淋漓。多少无奈，尽在言外。\n大江裹挟着浪花奔腾而去，英雄人物随着流逝的江水消失得不见踪影。“是非成败转头空”，豪迈、悲壮，既有大英雄功成名就后的失落、孤独感，又暗含着高山隐士对名利的淡泊、轻视。既是消沉的又是愤慨的，只是这愤慨已经渐渐没了火气。面对似血的残阳，历史仿佛也凝固了。“青山依旧在”是不变，“几度夕阳红”是变，“古今多少事”没有一件不在变与不变的相对运动中流逝，从“是非成败”的纠葛中解脱出来，历尽红尘百劫，太多的刻意都可以抛开，太复杂了倒会变得简单，在时、空、人、事之间的感悟中，别是一般滋味在心头。\n历史固然是一面镜子，倘若没有丰富的甚至是痛苦的残酷的人生体验，那面镜子只是形同虚设，最多也只是热闹好看而已。正因为杨慎的人生感受太多太深，他才能看穿世事，把这番人生哲理娓娓道来，令无数读者产生心有戚戚的感觉。\n既然“是非成败”都如同过眼烟云，就不必耿耿于怀、斤斤计较；不如寄情山水，托趣渔樵，与秋月春风为伴，自在自得。作者平生抱负未展，横遭政治打击。他看透了朝廷的腐败，不愿屈从、阿附权贵，宁肯终老边荒而保持自己的节操。因此他以与知己相逢为乐事，把历代兴亡作为谈资笑料以助酒兴，表现出鄙夷世俗、淡泊洒脱的情怀。无论过去，当下，还是以后，追逐名利似乎总是一些人的生存方式，然而名缰利锁又往往令人痛苦不堪，难以自拔。\n当然要建功立业，当然要展现英雄气概，当然要在无情的流逝中追求永恒的价值。但是既要拿得起，进得去；还要放得下，跳得出。要想看清历史发展的必然趋势，看清自己在历史中的位置和可能起到的作用，深度和远见都必须在生活中不断磨炼。\n浪奔浪流，万里滔滔江水永不休，任凭江水淘尽世间事，化作滔滔一片潮流。历史总要不断地向前推进，不以人的意志为转移。逝者如斯，谁也留不住时光的脚步。可是人们却不甘就这样顺其自然，随波逐流。\n青山不老，看尽炎凉世态；佐酒笑语，释去心头重负。任凭江水淘尽世间事，化作滔滔一片潮流，但总会在奔腾中沉淀下些许的永恒。与人生短暂虚幻相对的是超然世外的旷达和自然宇宙的永恒存在。宇宙永恒，人生有限，江水不息，青山常在。\n下阕展现了一个白髮渔樵的形象，任它惊骇涛浪、是非成败，他只着意于春风秋月，在握杯把酒的谈笑间，固守一份宁静与淡泊。而这位老者不是一般的渔樵，而是通晓古今的高士，就更见他淡泊超脱的襟怀，这正是作者所追求的理想人格。','[\"滚滚长江东逝水，浪花淘尽英雄。是非成败转头空。青山依旧在，几度夕阳红。\",\"白发渔樵江渚上，惯看秋月春风。一壶浊酒喜相逢。古今多少事，都付笑谈中。\"]',NULL,'滚滚长江向东流，不再回头，多少英雄像翻飞的浪花般消逝，争什么是与非、成功与失败，都是短暂不长久，只有青山依然存在，依然的日升日落。江上白髮渔翁，早已习惯于四时的变化，和朋友难得见了面，痛快的畅饮一杯酒，古往今来的纷纷扰扰，都成为下酒闲谈的材料。','此词是明代文学家杨慎所作《廿一史弹词》第三段《说秦汉》的开场词，后毛宗岗父子评刻《三国演义》时将其放在卷首。央视版电视剧《三国演义》将其作为主题歌歌词。\n从全词看，基调慷慨悲壮，意味无穷，令人读来荡气回肠，不由得在心头平添万千感慨。在让读者感受苍凉悲壮的同时，这首词又营造出一种淡泊宁静的气氛，并且折射出高远的意境和深邃的人生哲理。作者试图在历史长河的奔腾与沉淀中探索永恒的价值，在成败得失之间寻找深刻的人生哲理，有历史兴衰之感，更有人生沉浮之慨，体现出一种高洁的情操、旷达的胸怀。','[\"淘尽：荡涤一空。\",\"渚（zhǔ）：水中的小块陆地。\"]'),('e647d10f540153d41368c91423701ec4','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉水堤旁千树柳，仙宫还在人天。是谁驴背耸诗肩。不如来饮酒，倾倒醉吟边。\",\"鸟踏画檐时点缀，珠帘向晚风穿。依稀竹上尽婵娟。黄昏图画出，云尽月重圆。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41368c914789842e5','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"江国梅花千万朵，白云乡里温柔。笛声几度怨梁州。梦中云满树，醒后月当楼。\",\"正是相思春信早，桑禽啼破香愁。一枝难寄陇西头。燕姬钗上见，应是更风流。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41368c914806ac0ca','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"水上生尘罗袜，风前束素纤腰。陈王瞥见已魂消。妒云开翠幄，笑电闪红绡。\",\"此去但留今夕，重来定约何朝。西陵南浦路迢迢。徘徊花上月，空度可怜宵。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41368c914d39545d6','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红尘鞍马红亭路，天涯一望无穷。长安日下楚云东。鸣鞭杨柳陌，分袂杏花丛。\",\"离拍易终人易散，佳人玉箸啼红。今宵明月与君同。愁心闻唳鹤，倦寝听归鸿。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41368c914e735a481','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"池北池南新绿，树头树底残红。多情休怨雨和风。东君元是客，归去自匆匆。\",\"怅望鸳鸯别浦，萧条翡翠芳丛。梦回香冷酒微中。杜鹍声不断，帘外月朦胧。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41368c914f3fc47d0','临江仙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉蕊娟娟花灼灼，银釭背解明珰。澹云轻雨拂高唐。不言惟有笑，多媚总无妆。\",\"却怪汝南鸡唱早，行踪不辨回廊。青苔幽径落红香。宝钗寒挂月，罗袜冷侵霜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d414719992ef072c65','菩萨蛮·楚雄春归','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"来来去去无千里。屏风万叠青山里。流水赛流霞。桃花夹杏花。\",\"芜平芳草短。一望春如剪。回磴是回程。莺声报一声。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4154effbd3b0b1487','浪淘沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"鸦晚又鸡晨。最恼离人。添花补柳为谁春。飞鹊镜中妆态减，窗锁朱尘。\",\"璧月几回新。惯照眉颦。青山隐隐水粼粼。万水千山经岁别，远似星津。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4154effbd6ed72dd1','浪淘沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"去燕又来鸿。节序匆匆。秋声半夜搅梧桐。惊起南窗千里梦，满地西风。\",\"欹枕听寒蛩。离思无穷。归期又误菊花丛。遥想玉人肠断处，屈遍春葱。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4154effbd7f9b23ff','浪淘沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"灵鹊噪重檐。几度虚占。锦江春水隔鹣鹣。怕见雕梁双燕子，常下珠帘。\",\"离恨恼江淹。彩笔慵拈。碧云离合暮山尖。伴我归心和别梦，只有银蟾。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4154effbdc35e9ef7','浪淘沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"春梦似杨花。绕遍天涯。黄莺啼过绿窗纱。惊散香云飞不去，篆缕烟斜。\",\"油壁小香车。水渺云赊。青楼珠箔那人家。旧日罗巾今日泪，湿尽韶华。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4154effbdf749619e','浪淘沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"归思满琴心。幽恨难禁。月儿楼下海棠阴。想见纤纤弦上手，羽咽高沈。\",\"青鸟寄归音。一字千金。离鸾别凤谩沉吟。花发月圆须寄取，好在春深。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4162522f4155cb4e5','鹧鸪天·落叶','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"袅袅西风锦树新。一年芳景属流尘。莫辞雨滴寒鸣夜，曾抱花枝暖过春。\",\"蒲柳岸，柙枫津。琅玕摧折感离人。天津休问题红怨，香阁回文翠黛颦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d417f5bc316594eca8','柳梢青·杏花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"晕雪融霞。若烟非雾，何处人家。宋玉墙东，文君垆下，占断韶华。\",\"梅梢已罢横斜。柳条犹未藏鸦。锦树烘春，琼枝枭月，留醉仙娃。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41ae1215bf1119d15','西江月·欧海蟾楼上','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"夜合庭前月照，夕佳楼上春宵。青灯的的夜迢迢。欹枕荒鸡未晓。\",\"杜曲愁浓酒恼，沈园梦断香消。倚门红袖不须招。不见卢郎年少。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41b3b2c142f2f0e2e','巫山一段云·绍途遇雪','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"梅柳春相近，关山雪正飞。寒光朔气冷侵衣。玉塞隔金微。\",\"久戍人将老，长征马不肥。帛书欲寄雁声稀。秃节几时归。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41bf94b1c0a610242','一七令·舟中阅唐诗纪事王起李绅张籍令孤楚于白乐天席上各赋一字至七字诗以题为韵遂效其体为花风月雪四首宋人名','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"花。摛锦，铺霞。邀蝶队，聚蜂衙。珠璎姹女，宝髻宫娃。\",\"风前香掩冉，月底影交加。绿水名园几蔟，青楼大道千家。\",\"谢傅金屏成坐笑，陈朝琼树不须夸。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41bf94b1c797bbc78','一七令·舟中阅唐诗纪事王起李绅张籍令孤楚于白乐天席上各赋一字至七字诗以题为韵遂效其体为花风月雪四首宋人名','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"风。偃草，飘蓬。过竹院，拂兰丛。柳提摇绿，花径飞红。\",\"青缸残燄灭，碧幌嫩凉通。漆园篇中竽籁，兰台赋里雌雄。\",\"无影迥随仙客御，有情还与故人同。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41bf94b1c91a409b9','一七令·舟中阅唐诗纪事王起李绅张籍令孤楚于白乐天席上各赋一字至七字诗以题为韵遂效其体为花风月雪四首宋人名','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雪。凝明，澄彻。飞玉尘，布琼屑。苍云暮同，岩风晓别。\",\"深山樵径封，远水渔舟绝。南枝忽报梅开，北户俄惊竹折。\",\"万树有花春不红，九天无月夜长白。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d41bf94b1c96ea8ca5','一七令·舟中阅唐诗纪事王起李绅张籍令孤楚于白乐天席上各赋一字至七字诗以题为韵遂效其体为花风月雪四首宋人名','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月。霜凝，冰洁。三五圆，二八缺。玉作乾坤，银为宫阙。\",\"如镜复如钩，似环仍似玦。兰闺少妇添愁，榆塞征人怨别。\",\"汉家今夕影娥池，穆穆金波歌未阕。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d421904631b2628c03','醉蓬莱·代寿黔国上公','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"看金波遥映，玉镜高飞，昆明池沼。近天上中秋，人间蓬岛。建节堂深，筹边楼迥，正雨晴风袅。青鸟喉歌，红鸾掌舞，仙音缭绕。\",\"白鹤南来，锦书为报。云动三山，星飞五老。汉节唐符拜，重华天表。玉尘谈诗，金钗压鬓，向琼枝齐祷。愿岁岁长春，年年此日，恩波难老。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d421cda200ea287217','满庭芳·感通寺赠承道玄、杨伯清','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"环海烟霞，冠山楼阁，天涯万里凭阑。瑶台高处，携手蹑飞鸾。病起带围宽尽，朱颜改、青镜羞看。风情减，停歌罢笑，愁对酒杯宽。\",\"清欢。听牛背，一声牧笛，云散漫漫。问玄谷冰壶，禅伯仙官。庭下丁香树子，花开后、又见花残。从吾好，玉毫光里，一粒现金丹。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4225cc7980413acb3','天净沙·题画惜花春早起','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"良宵一刻千金。水晶宫里沉沉。月姊休教妒影。离情别恨。\",\"人间天上同心。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4225cc79808be2b3c','天净沙·题画惜花春早起','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"霜天晓角声残。霞绡雪缕衣单。依约宫黄画浅。小梅开遍。\",\"咏花人倚阑干。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4225cc79825717813','天净沙·题画惜花春早起','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"桂花影里金波。瑶池倒映银河。玉臂清辉无那。纤纤摊破。\",\"分明掌上嫦娥。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4225cc798fe0c2317','天净沙·题画惜花春早起','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"晓风香露楼台。长红小白齐开。笑整鲛绡双带。拈来慵戴。\",\"满怀蜂蝶争来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d422b207e4beb0181c','水龙吟·咏梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"汉宫娇额涂黄，风流早露春消息。铅华莹雪，冰肌照水，盈盈无力。弄影轻颦，向人微笑，芳心谁识。对珠帘不掩，绮窗半露，嫣然一笑倾国。\",\"最恨昏黄月黑。迷花丛、枝南枝北。寿阳妆减，罗浮梦断，春宵一刻。皴玉谁温，凝脂自暖，空劳相忆。正香残漏俏，灯昏酒冷，此情何极。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42464e9431b7ee3bd','阙调名中秋夜小酌即席示二弟小词二首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月照三更。鹊绕南枝雁亦惊。庾楼高兴，休教辜负今夜十分明。\",\"凄清何处管弦声。仿佛燕台紫陌行。便从今夕话离情。\",\"明年此景，又隔凤凰城。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42464e94324b04db4','阙调名中秋夜小酌即席示二弟小词二首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"江树无烟。清辉寒影在中天。谢池家宴，休教辜负今夜十分圆。\",\"一樽相对静娟娟。分得金波漾酒船。更阑酒尽不成眠。\",\"一枝仙桂，好事说明年。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4257723f864175e45','荷叶杯','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"远寄音书一纸。千里。一字一徘徊。\",\"泪眼愁眉不忍开。来么来。来么来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4257723f8ef799db2','荷叶杯','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"枕上一声鸡唱。天亮。好梦忽惊残。\",\"锦帐香消翠被单。寒么寒。寒么寒。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425cb77c58e17db1a','太平时溯流纳溪舟，宿玉林寺。忆昔刘参之与弟承之、梁腾实相送至此，参之、腾实墓已宿草，感怆赋','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"回首徂征出塞前。甲申年。故人相送蜀江边。共留连。\",\"廿载旧游非旧雨。销魂处。晨星朝露各风烟。泪潸然。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425d6c8bb36991863','蝶恋花·廿一史弹词第九段说宋辽金夏开场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"检尽残编并断简。细数兴亡，总是英雄汉。物有无常人有限。到头落得空长叹。\",\"富贵荣华春过眼。汉主长陵，霸主乌江岸。早悟夜筵终有散。当初赌甚英雄汉。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425eb6af73c09722b','清平乐','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"金炉尘满。绣被熏香懒。此恨破除须酒盏。争奈愁深酒浅。\",\"多时弦断瑶琴。空余孤凤悲吟。谩道鸾胶能续，续来不是前音。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425eb6af773b1e771','清平乐','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"扁舟解缆。春满江南岸。灯火街头月正半。客里年华又换。\",\"倚遍阑干影斜。行人回首天涯。沙市桥南草色，章台寺里梅花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425eb6af7d6177480','清平乐','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"灯红酒绿。山掩围屏曲。何处萧萧风动竹。好梦惊回难续。\",\"月华冷浸霜华。寒林惊起寒鸦。道是天明尚早，小窗横影花花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425eb6af7e852644a','清平乐','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"君王未起。玉漏穿花底。永巷脱簪妆黛洗。衣湿露华如水。\",\"六宫鸾凤鸳鸯。九重罗绮笙簧。但愿君恩似日，从教妾鬓如霜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d425eb6af7f977b549','清平乐','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"倾城艳质。本自神仙匹。二八承恩初选入。身是三千第一。\",\"月明花落黄昏。人间天上销魂。且共题诗团扇，笑他买赋长门。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4277509f42de292dd','昼夜乐中秋董太守席上','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"螳螂川上清秋节。爽襟怀、消烦热。香醪桂馥兰熏，官妓舞琼歌雪。五马风流华筵设，金屏燕颃莺颉。软语劝飞觞，有如花连舌。\",\"座中豪兴称三绝。高阳并，斜川埒。且偕仙侣逍遥，肯叹群儿圆缺。未害广平心似铁。看霜鬓、半成耄耋。倩横玉叫云、把寥天吹彻。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42b0444610e521401','南乡子·荆州元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"官柳动新枝。万缕黄金万缕丝。\",\"折赠行人千里去，回期。莫待清霜落叶时。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42b0444612e24b23e','南乡子·荆州元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"芳草被金堤。野烧红余绿正齐。\",\"恰翠踏青新雨后，萋萋。裙拂秋千彩架底。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42b04446135f06497','南乡子·荆州元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"细雨妒芳姿。禁住莺翁与燕儿。\",\"待得晴明好天气，迟迟。花絮飞残水满池。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42b04446173e12bb9','南乡子·荆州元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉笛送残梅。片片随风入镜台。\",\"台下新妆传玩处，徘徊。腊尾春头恨几回。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42b044461c312afb2','南乡子·荆州元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"枉渚与回蹊。楚雾秦烟望转迷。\",\"相伴王孙千里去，东西。蝴蝶成团杜宇啼。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42b044461d53ed983','南乡子·荆州元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"丝雨湿香街。禁住花灯不放开。\",\"闷上紫姑香火会，遥猜。红豆音书甚日来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d42c4366d922e6fa7f','菩萨蛮·大理普宁寺中秋','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"花宫露下秋如水。月明千里人千里。莫上最高楼。天际添别愁。\",\"一时今夕会。且共尊前醉。踪迹似飘蓬。明年何处逢。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4311182f33ff228eb','酒泉子·避暑江山平远亭，招简西峃不至','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"西峃不来。负却红墙垂手。懒当歌，慵对酒。倦登台。\",\"石城艇子又空回。溪?一双惊散。藕花汀，桃叶岸。独徘徊。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d432ad97e17098b345','渔家傲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"水日闲亭谁与共。吸残寒露冰壶冻。倒影云峰潭影空。湘箔动。荼蘼架底清香送。\",\"篆缕熏残衣雾重。黄梅润透红绡缝。枕上醒回池上梦。开雪洞。瑶琴一曲江南弄。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d432ad97e175850fbf','渔家傲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"千里有家归未得。可怜长作滇南客。愁见陌头杨柳色。伤远别。多年故国曾攀折。\",\"望断乡山音信绝。那堪烽火连三月。夜夜相思头欲白。心似结。五更梦破闻啼鴂。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d432ad97e1ff390720','渔家傲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"水晶帘外炎歒永。纱厨斗帐神仙洞。酪粉蔗浆分碧瓮。琼斝奉。玲珑弦上传啰唝。\",\"歌罢酒阑行雨送。银盆沐水煎金凤。懒髻䰒松云乱拥。凉意动。暗惊团扇生秋梦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d435b9090b0440756e','锦缠带','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"谁家红袖倚江楼。手卷珠帘上玉钩。眼波娇溜满眶秋。\",\"笑抬头。头上花枝颤未休。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4364ddc29d8529cec','临江仙·寄刘建之','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"寄语东桥东畔柳，且教留住行人。与君相见即相亲。忍看携手把，分作断肠津。\",\"莫唱阑干花雾曲，温泉剩有余春。明朝关索岭头尘。相思千里外，回首一沾巾。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d43723b25c2d2f6d93','忆王孙·重宿禄品','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"落梅风里王鳞皴。牛背寒鸦过别村。樵笛声中暮霭昏。\",\"掩重门。翠被香寒酒正温。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d43723b25c7300bfb8','忆王孙·重宿禄品','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"满城开遍粉西施。节近重阳客未知。袅袅凉风搅鬓丝。\",\"倚楼时。长笛声声莫遣吹。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d438501bb9bf752014','杨柳枝·词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"汉东门外柳新栽，拂晓长堤露眼开。野老攀条相向语，去年曾被贼烧来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d438501bb9fde06f52','杨柳枝·词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"临水临风漾碧漪，含烟含雾一枝枝。战尘收后无离别，又见长条到地时。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4387b5d5a956d55ba','临江仙·廿一史弹词第七段说隋唐开场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"一片残山并剩水，年年虎斗龙争。秦宫汉苑晋家茔。川原流恨血，毛发凛威灵。\",\"白首诗人闲驻马，感时怀古伤情。战场田地好宽平。前人将不去，留与后人耕。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d439c1fb2434601ce2','小秦王立春','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红穗金花落绛台。画楼银烛晓光催。钗梁小燕双双颤，春自玉人头上来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d43ed0824808ca1471','月中行','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月华静夜思悠哉。夜静灭纷埃。窥人不待画堂开。圆影隙中来。\",\"细轩朱缀香尘满，重门掩、露滴金苔。洞房未晓且徘徊。清漏不须催。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d43f2aa3c7cbfebbdd','人月圆·泛大理海子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"好风雨日相迎送，渺渺碧波平。玉几云凭，金梭烟织，宝刹霞明。\",\"邀散神仙，寻闲洲岛，上小蓬瀛。海流东逝，海天南望，海月西生。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44284954086670464','风入松·马上喜晴','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"刺桐花里咽新蝉。方响落寒泉。远山凝黛修蛾敛，谁妆点、淡粉浓胭。嫩水靴文蹙浪，娇云卯色烘天。\",\"碧池荷叶又田田。藕在阿谁边。莲子擘开须见意，香丝到底相牵。留取团团宝月，西楼共醉婵娟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d442a2089233c3fe49','西江月·廿一史弹词第八段说五代十国下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"千古伤心旧事，一场谈笑春风。残编断简记英雄。总为功名引动。\",\"个个轰轰烈烈，人人扰扰匆匆。荣华富贵转头空。恰似南柯一梦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44334969f2dc90059','蝶恋花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"夭似花枝轻似雾。香烬灯昏，梦入瑶台路。枕上眉山重碧聚。偎人若把离情诉。\",\"乞巧楼中携手处。夜夜相思，今夕重相遇。最恨五更留不住。航帆风送高桥渡。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44334969f3ee4404b','蝶恋花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"好梦难成云易散。玳瑁筵前，当日亲曾见。金杯满捧香醪劝。朱唇半吐樱桃绽。\",\"别后佳期天样远。夜夜相思，寸寸柔肠断。徙倚画楼千万遍。落梅庭院飘银霰。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44334969fbb88fd2b','蝶恋花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉貌合教金屋贮。天上仙姿，谪在人间住。九霞山下曾相遇。瓠犀笑破樱桃注。\",\"别来青鸟如何处。岂有仙踪，好寄相思句。海月团团生碧树。梦随惊鹊南飞去。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d443b08a9147a25428','临江仙·寄简西峃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"西峃襟怀元洒落，近来踪迹何疏。高峣一水不斯须。应同渔父饮，醒眼看三闾。\",\"侍立小僮清似玉，少陵诗兴何如。遥岑远目几踟蹰。碧云停杳霭，明月几盈虚。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4440934eacdd4fa27','归朝欢叙南李将军园海棠','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"鶗鴂声中春已尽。蜀国海棠犹有韵。已将淡粉换龙涎，渐看绣缬成香晕。脸霞红半隐。紫锦经雨胭脂润。正清欢，云歌柳舞，聊解朱炎愠。\",\"十二金钗花作阵。不怕周郎回顾问。薛涛临镜意先慵，黄荃渲笔愁难近。绿樽倾翠酝。青蘋风起波文粼。望高城不见，遥寄香霏阁信。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d445110ef8e11ebb4d','满江红·十一月六日，鲁泉董太守过宿高峣','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"王子回船，还只是、戴公无兴。试思之、雪中林壑，琼辉玉映。佳客冲寒来半夜，主人扫雪开三径。便拥炉、命酒炙车螯，才相称。\",\"前古事，休重咏。翻旧案，行新令。唤官奴秉烛，肉屏围定。画角吹梅天未晓，金钗剪烛人初静。更新词、一曲满江红，君须听。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4456588430277eee6','鹧鸪天·壬子元夕前，高峣海庄，与王云岩、丘鸿夫、张子中、李继培小饮','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"戎旅今年四处家。故乡咫尺是天涯。寻巢未定身如燕，览镜曾经鬓似鸦。\",\"愁对酒，懒看花。青衫老泪感琵琶。侧身天地无刘表，徙倚阑干日又斜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d445658843b00e5d38','鹧鸪天·壬子元夕前，高峣海庄，与王云岩、丘鸿夫、张子中、李继培小饮','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"淼淼春波碧海边。淹留佳客夜开筵。未临元夕灯先试，虽是贫家月也圆。\",\"歌宛转，舞蹁跹。劝郎行乐驻华年。白头未定西归计，愁听箜篌蜀国弦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d445dc10ca4f312d0b','御街行·柳花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"城西杨柳千千树。风起飞花絮。青楼珠箔斗轻盈，还绕名园幽处。满地香毬，满天晴雪，卷却春归去。\",\"游人歌吹堤边路。芳景须珍护。眠开青眼舞慵腰，无计使风流住。百啭残莺，一双新燕，九十春光暮。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44849e323372067d1','浣溪沙·落梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"径转梅桥燕尾分。疏枝冷蕊照江濆。玉龙鳞甲点波纹。\",\"谢女黄昏吟作雪，襄王清夜梦为云。余香留与醉红裙。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44b507b12f212d1fe','朝中措','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"经霜原草未全黄。绿叶映红芳。野客争夸稔岁，行人自笑炎方。\",\"却思京国，狐裘貂帽，雪洞毡房。不识琼楼高处，曝暄谁献吾皇。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c0711d9a3baa13e','落灯风正月十七日留简西峃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"柳外落灯风乍起。杏靥梅钿纷堕蕊。彩架阁秋千，红绳紧、香尘满地。春一分休矣。\",\"银塘初暖湔裙水。催莫愁、兰舟遥舣。沽酒趁梨花，听双歌、温柔乡里。且住为佳耳。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c2d8abe259b173a','巫山一段云','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"背壁羞娇影，回灯脱薄妆。暗中惟觉绣鞋香。解佩玉丁当。\",\"好梦云将结，幽欢夜正长。迟迟懒上郁金床。故意恼檀郎。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c2d8abe5d501ecd','巫山一段云','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"星的妆金靥，云梳挽宝鬘。长蛾新月斗弯环。风度佩珊珊。\",\"小玉香煤烬，飞琼扇影闲，鳞霞练霭锦班班。画出晚来山。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d6220ca6702b','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"青鸟。青鸟。来自十洲三岛。树头梅蕊春催。\",\"枕上残妆梦回。回梦。回梦。城角晓闻三弄。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d6224e60d530','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"采菊。采菊。且醉尊前醽醁。百分觥棹船空。\",\"万虑销沈酒中。中酒。中酒。又度客边重九。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d6229a5fffb1','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"江水。江水。昨夜风惊浪起。眼看估客归舟。\",\"肠断征人泪流。流泪。流泪。离恨江沙共碎。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d622a2e24a79','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"归路。归路。遥隔铁桥铜柱。楼头秋霁无尘。\",\"陌上愁看去人。人去。人去。心头千山碧树。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d622a7fdfdf0','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"促织。促织。声近银床转急。熏残百合衣香。\",\"消尽兰膏夜长。长夜。长夜。露冷芙蓉花谢。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d622c4e4f3fd','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"双燕。双燕。金屋往来长见。珠帘半卷风斜。\",\"何处衔来落花。花落。花落。日莫长门寂寞。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44c71d622f279312f','转应曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"黄鸟。黄鸟。飞过花枝袅袅。绿阴青子初新。\",\"流水斜阳晚春。春晚。春晚。望断东城上苑。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44dcc3575eabf6442','唐多令·中秋与夺客泛舟','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"飞镜露云头。金波水面浮。水晶宫、今夜中秋。唤取官奴吹玉笛，香雾湿，锦云留。\",\"二客亦风流。螂川泛小舟。问何如、赤壁黄州。坡老有灵应鼓掌，天地寄浮蝣。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d44f314ed59b2de71f','凤栖梧送薛治曲泉之镇雄勘夷手卷','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"借寇歌廉春有脚。皂盖朱幡，辉映江阳郭。一喢清泉斟且酌。候人无事闲铃阁。\",\"瘴域岚方劳锁钥。静柝沉锋，虎凶成鸾鹤。风飐归旌诗满橐。休迟竹马儿童约。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d451c9ce1db84fd70e','薄幸','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"碧鸡催晓。正话别、匆匆未了。又咫尺、销魂桥上，南浦绿波芳草。记昨宵、醉褶罗襟，玉山拚为金樽倒。爱皎皎当窗，盈盈隔水，那个人儿窈窕。\",\"自油壁香车去后，路远西陵月悄。恨同心带缓，露兰啼歇，多情却被无情恼。最萦怀抱。把双双骰子，重重喝采占佳兆。桃花有意，前度刘郎未老。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45272664e76e00926','临江仙·四会柬亩','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"高宴诸侯张乐地，锦堂华烛蟠烟。当筵歌舞假婵娟。病吟终少味，老醉不成颠。\",\"高隐无言应念我，白头奔走山川。熏梅染柳又催年。人归落雁后，思发在花前。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d452c43f8222847453','江城子·丙戌九日','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"客中愁见菊花黄。近重阳。倍凄凉。强欲登高，携酒望吾乡。玉叠青城何处是，山似戟，割愁肠。\",\"寒衣未寄早飞霜。落霞光。暮天长。戍角一声，吹起水茫茫。关塞多愁人易老，身健在，且疏狂。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4537d2a481d70261b','好事近·煮茶进，和蔡松年韵','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"彩线利如刀，解破团圆明月。兰薪桂火筠炉，听松风翻雪。\",\"唤取眠云跂石人，赛十洲三绝。焚香朗诵黄庭，把肺肝清彻。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45561c22377ca5c97','临江仙·送刘珥江洎杨许二子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"官柳野梅西郭路，昆桥直接丰桥。相思百里不辞遥。春山同载酒，月地共联鏕。\",\"南去北来人自老，怜予心旆摇摇。短亭暂别亦魂销。生烟寒漠漠，飞霭晚潇潇。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4570721ec2edf26d8','鹧鸪天·魏南山将军别墅蜡梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"窄窄红渠艳袜罗，盈盈一笑满黎涡。小槽春滴金华酒，纤月风林玉树歌。\",\"香掩冉，影婆娑。载花归路似东坡。相逢不尽疏狂兴，其奈星星白发何。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4570721eca8393c49','鹧鸪天·魏南山将军别墅蜡梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"融蜡融栀褪粉光。晕眉晕脸学蛾妆。惊回花底三更梦，别是风前一种香。\",\"桃杏妒，艾兰防。几番孤负月昏黄。枝头要见青青子，且放银灯伴海棠。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45765948421285a82','长相思','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雨声声。夜更更。窗外萧萧滴到明。梦儿怎么成。\",\"望盈盈。盼卿卿。鬼病恹恹太瘦生。见时他也惊。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4584252783188d012','江月晃重山四阕壬寅立春','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"屏翳烘云靺鞨，羲和敲日玻瓈。韶风丽景画桥西。春色早，南国且黄鹂。\",\"潇洒蕙楼菌阁，繁华杏坞桃溪。多情杨柳露新荑。堪一醉，骑马欲鸡栖。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4584252785f15cb42','江月晃重山四阕壬寅立春','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"腊尾金杯滟滟，春头彩胜翩翩。裁红晕碧动嫣然。风日好，偷眼艳阳天。\",\"一曲玉箫明月，几弦锦瑟华年。故乡迢递水云连。归未得，思发在花前。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d458425278d6fce9b0','江月晃重山四阕壬寅立春','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"金马九重恩谴，碧鸡二十春风。江花江草每年同。君不见，憔悴已成翁。\",\"白发带花休笑，紫髯说剑犹雄。欲笺心事向天公。赊美酒，长醉玉壶中。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d458425278e6fd71f2','江月晃重山四阕壬寅立春','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"龙马山前草长，螳螂川上花开。霜天晓角报春回。传菜手，先进荡寒杯。\",\"金线丝丝染柳，玉英片片熏梅。流光容易把人催。吾老矣，何日赋归来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45977063928d78e11','鹧鸪天·杜鹍花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"水蝶岩蜂总不知。艳红凝露万千枝。石家锦帐遮金谷，王母霞幢照羽旗。\",\"香雨歇，彩云移。暖风晴日醉胭脂。山深春晚无人赏，又是啼鹃叫落时。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d459770639d64c2a37','鹧鸪天·杜鹍花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"高阁遥岑送远青。倚阑高咏有人听。子弦乍咽新双调，卯醉难消软半瓶。\",\"烟漠漠，雨泠泠。留君且住会心亭。谢公棋墅思同乐，渔父江潭笑独醒。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d459d6629e5cb1fa79','百字令病中起登楼填词一首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"遥峰青染，见天外树杪，余霞红闪。小榭轻埃喜屏翳，层阴才收有渰。钟鼓新晴，楼台暮景，快听渔阳掺。酒市旗亭，何处青帘飞飐。\",\"惆怅枕病凋年，棐几尘生，荆扉昼掩。药里关心诗总废，谁问东皋西崦。过却重阳，篱边都不见，玉镶金锬。峭寒无寐，沉沉细数更点。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45ab97d7e19a29793','少年游','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红稠绿暗遍天涯。春色在谁家。花谢蝶稀，柳浓莺懒，烟景属蜂衙。\",\"日长睡起无情思，帘外夕阳斜。带眼频移，琴心慵理，多病负年华。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45b79454016b46f8b','沁园春·己丑新正','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"劝汝一杯，关山迢递，我马崔隤。想玉堂金马，曾同富贵，竹篱茅舍，也共尘埃。事过眼前，老来头上，卿不见吾白发哉。故园好、更彩衣称寿，何日同回。\",\"天涯劝我宽怀。欠酒债、拔卿金雀钗。笑孔肩薄首，夫人书法，嵇心羊体，仙子琴才。道韫家声，黔娄夫婿，此怨休将造化埋。六如偈，棒打开梦幻，新拜如来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45b7945406adcb7ab','沁园春·己丑新正','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"愁汝休来，且天边高寄，地下深埋。自戊申吾降，酒星正照，辛壬师授，酒诰蒙开。若下乌程，兰生桑落，行辈都将兄弟排。二豪者，便螟蛉蜾蠃，如我何哉。\",\"酒中兴岂无媒。要月户、风亭白雪阶。况鸾腔凤调，浮丘按拍，霞浆金醴，王母添杯。洞里春浓，壶中夜永，童子休将鹤驾催。玄真侣，烦诸君为我，筑醉仙台。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45b7945407627c906','沁园春·己丑新正','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"自寿一杯，休行吟泽畔，兀坐山隈。笑穷兵独舞，干戈载戢，却胡长啸，铅椠何才。归去来兮，呜呼老矣，生入玉门何日哉。新春好，且提壶沽酒，共赏花开。\",\"催花击鼓如雷。酒色似、蒲桃初发醅。任天河水泻，流干银汁，月轮桂老，撑破珠胎。岛上红云，洞中香雪，醉倒花娘锦被堆。与君约、这醉乡深处，不饮休来。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d45b7945409dc5d736','沁园春·己丑新正','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"甚矣吾衰，叹天涯岁月，何苦频催。奈霜毫种种，三千盈丈，丹心炯炯，一寸成灰。三径秋荒，五湖天远，儒术于吾何有哉。君知否，盼行云不住，流水难回。\",\"寂寥谁与徘徊。好事者、惟输曲秀才。恁昏花老眼，底须窥牖，支离病脚，最怯登台。思发花前，人归雁后，百感中来强自裁。狂歌好，只清风和我，明月休猜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46301ad9a53f1e3e2','无俗念游仙二首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"十年奔走，向红麈、何处可寻蓬岛。一枕青瓷惊梦起，幻迹都如扫。七返还丹，千龄大药，昧者难精讨。餐霞伴侣，飘然遥在天表。\",\"长生须住仙村，诛茅贺厦，迥出层云抄。翠水蓝岑浑不夜，紫府丹丘常晓。玉豉金盐，璚英珠液，种种供难老。乖龙莫懒，为我先耕瑶草。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46301ad9acca802da','无俗念游仙二首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"匏瓜河上，问津时、半夜朝先归晚。鹤倦鸾慵行且住，谁遣昌容迎幰。朝采帘掀，夜光杯举，留醉青霞苑。玉颜掩樗，笑看泓水清浅。\",\"谪来尘世何年，青精紫倩。全县侣怀同搴。灵境壶天飙霭隔，座上玉书尘满。赤鲤琴高，青鸾阿母，问讯行当反。五潢似镜，白榆历历秋剪。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4636003c1aa9efc31','西江月·廿一史弹词第五段说南北朝下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"飒飒西风渭水，萧萧落叶长安。英雄回首北邙山。虎斗龙争过眼。\",\"闲看灞桥杨柳，凄凉露冷风寒。断蝉声里凭阑干。不觉斜阳又晚。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d463f2b2c6866fc1e3','殿前欢感通寺赠承冰壶炼帅、董西羽徵君','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"问冰壶。洞天还似此中无。留春长在和春住。月冷山孤。\",\"愿指瑶台路。好把朱颜住。不续玄都赋。吾今忘我，我亦忘吾。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d463f2b2c6a800468c','殿前欢感通寺赠承冰壶炼帅、董西羽徵君','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"枕西阿。仙洲玉几对金梭。苍山九点空中堕。影蘸烟波。\",\"避暑云端卧。爱月天边坐。沽酒墙头过。狂歌谁和，谁和狂歌。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d464cf21ba5bdb2b79','点绛唇·廿一史弹词第六段说十六国开场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"暮鼓晨钟，春花秋月何时了。七颠八倒。往事知多少。\",\"昨日今朝，镜里容颜老。千年调。一场谈笑。几个人知道。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46712884601072140','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"正月滇南春色早。山茶树树齐开了。艳李夭桃都压倒。妆点好。园林处处红云岛。\",\"彩架秋千骑巷笊。冰丝宝料星毬小。误马随车天欲晓。灯月皎。碧鸡三唱星回卯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846073a6cb8','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"七月滇南秋已透。碧鸡金马山新瘦。摆渡村西南坝口。船放溜。松花水发黄昏后。\",\"七夕人家衣襮绣。巧云新月佳期又。院院烧灯如白昼。风弄袖。刺桐花底仙裙皱。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846274579b9','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"四月滇南春迤逦。盈盈楼上新梳洗。八节常如三月里。花似绮。钗头无日无花蕊。\",\"杏子单衫鸦色髻。共倾浴佛金盆水。拜愿灵山催早起。争乞嗣。蛛丝先报钗梁喜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846348cac22','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"十二月滇南娱岁晏。家家玉饵雕盘荐。安息生香朱火焰。槟榔串。红潮醉颊樱桃绽。\",\"苔翠氍毹开夜宴。百夷枕粲文衾烂。醉写宜春情兴懒。妆阁畔。屠苏已识春风面。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846431b2f9f','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"十月滇南栖暖屋。明窗巧钉迎东旭。速鲁麻香春瓮熟。歌一曲。酥花乳线浮杯绿。\",\"蜀锦吴绫熏夜馥。洞房窈窕悬灯宿。扫雪烹茶人似玉。风动竹。霜天晓角肌生粟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4671288465de7c8d5','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"八月滇南秋可爱。红芳碧树花仍在。园圃全无摇落态。春莫赛。玫瑰彩缕金针繲。\",\"屈指中秋餐沆瀣。遥岑远目天澄派。七宝合成银世界。添爽快。凉砧敲月胜竿籁。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4671288467aa8550d','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"五月滇南烟景别。清凉国里无烦热。双鹤桥边人卖雪。冰碗啜。调梅点蜜和琼屑。\",\"十里湖光晴泛蝶。江鱼海菜鸾刀切。船尾浪花风卷叶。凉意惬。游仙梦绕蓬莱阙。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846868cd507','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"九月滇南篱菊秀。银霜玉露香盈手。百种千名殊未有。摇落后。橙黄菊绿为三友。\",\"摘得金英来泛酒。西山爽气当窗牖。鬓插茱萸歌献寿。君醉否。水晶宫里过重九。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4671288468883d0b3','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"二月滇南春嬿婉。美人来去春江暖。碧玉泉头无近远。香径软。游丝摇拽杨花转。\",\"沽酒宝钗银钏满。寻芳争占新亭馆。枣下艳词歌纂纂。春日短。温柔乡里归来晚。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846e361258a','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"十一月滇南云幕野。漕溪寺里梅开也。绿萼黄须香趁马。携翠斝。墙头沽酒桥头泻。\",\"江上明蟾初冻夜。渔蓑句好真堪画。青女素娥纷欲下。银霰洒。玉鳞皴遍鸳鸯瓦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846e5fc254d','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"三月滇南游赏竞。牡丹芍药晨妆亲。太华华亭芳草径。花饾饤。罗天锦地歌声应。\",\"陌上柳昏花未瞑。青楼十里灯相映。絮妥尘香风已定。沈醉醒。提壶又唤明朝兴。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d467128846fa7c91d2','渔家傲·滇南月节','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"六月滇南波漾渚。水云乡里无烦暑。东寺云生西寺雨。奇峰吐。水椿断处余霞补。\",\"松炬荧荧宵作午。星回令节传今古。玉伞鸡㙡初荐俎。荷芰浦。兰舟桂楫喧箫鼓。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4681c696b8778a92f','酹江月和坡仙韵，赠陈广文致政归','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"阳关休唱，且须尽、陶令杯中之物。宦海无津喜君归，仙叟紫房青壁。泉石新盟，烟霞旧梦，怀抱皆冰雪。清风堪挹，斜川栗里争杰。\",\"缅想松菊门庭，田园幽事，兴头先发。情话琴书消遣处，笑看浮沤生灭。棋局酒樽，桑榆境相对，朱颜玄发。行歌坐啸，一任抹风批月。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46a10f508ad4612b6','风入松','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"喷香瑞兽小妆台。咫尺天台。芭蕉不展丁香结，闭春心、眉锁难开。银蜡烧灯过后，金钗斗草归来。\",\"徘徊花月可怜宵。天近人遥。有情芍药含春泪，梦回时、一枕无聊。王母池边翠水，云英家住蓝桥。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46a10f508ce970c08','风入松','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月窗花院好风光。谢女檀郎。朝朝暮暮遥相望，殢人娇、罗带留香。青鸟解传消息，银河不隔红墙。\",\"绣罗红嫩抹酥胸。此夕重逢。妒云恨雨腰肢袅，晕眉心、獭髓分红。蜡烛寒笼翡翠，麝香暖度芙蓉。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46a281fe8499b5e45','一剪梅·戏简西峃宿杏花楼','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"宋玉墙头杏子花。香也堪夸。艳也堪夸。东风鸟外一枝斜。问是谁家。江上人家。\",\"肯信流年鬓有华。诗咏红霞。酒泛流霞。低声昵语似雏鸦。人在天涯。忘却天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46d7e4311ef769009','鹧鸪天·元宵后独酌','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"千点寒梅晓角中，一番春信画楼东。收灯庭院迟迟月，落索秋千翦翦风。\",\"鱼雁杳，水云重，异乡节序恨匆匆。当歌幸有金陵子，翠斝清尊莫放空。\"]',NULL,NULL,'此词写元宵节后的独酌思乡。「迟迟月」与「翦翦风」，点缀出早春夜晚的清寒，也烘托出怀乡的愁绪。末两句以歌酒故作宽解，更见乡愁的深挚婉曲。','[\"落索：冷落萧索。\",\"翦翦：形容风轻微而带寒意。\",\"鱼雁：指书信。\",\"金陵子：歌女。\",\"斝（jiǎ）：古代青铜所製之圆口三足酒器。《康熙字典》 ：「《说文》：『玉爵也。从叩从斗，冂。象形，与爵同意。或说斝受六升。』《诗·大雅》：『洗爵奠斝。』《周礼·春官·司尊彝》：『裸用斝彝黄彝。』《礼·明堂位》：『爵夏后氏以盏，殷以斝，周以爵。』注：『斝画禾稼也。』《正字通》：『两柱交似禾稼，故曰「斝」。』」\"]'),('e647d10f540153d46db60e91627b41fc','花犯·念奴','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"云軿不辗地，仙居多丽谯。湖海念年龙卧，锦涟清霅苕。甫里笔床茶灶，山阴楸枰方罫，香籀记昏朝。醉乡无畔岸，北斗挹天瓢。\",\"楼中人，谁是伴，有松乔。灵文绿帙，齐物与逍遥。肯念草玄寂寞，暂遣喜公缩地，风御蓦琅霄。阑干凭到景，共和晓仙谣。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46e59c981f0c905db','蓦山溪','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"送君南浦。系马垂杨树。攀折赠行，忍听道、一声别去。愁眉泪眼，一步一回头，团山路。高桥渡。\",\"总是销魄处。清风明月，好景成虚度。重会在何时，算只有、梦中相遇。衾寒枕冷，和梦也难成，思千缕，愁无数。乱似风中絮。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46e76ebc7916e1017','玉蝴蝶·梁生镇园亭有兰名玉瑚蝶，即席赋一首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"蝴蝶不随春去，玉英瑶蕊，犹自娟娟。潇洒水边林下，洗净朱铅。嫦娥来、梁园月下，青女降、楚畹霜前。更山阴、婆娑一老，觞咏群贤。\",\"当年。紫茎绿叶，纫华杂佩，故事陈篇。何似今宵，长歌石磴泻红泉。醉归去、香携满袖，似相逢、解佩江仙。散尘缘、猗猗一曲，琼轸冰弦。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46fa28a867b165fbd','浣溪沙·集句','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"洞里仙人碧玉箫。一渠春水赤栏桥。绿窗虚度可怜宵。\",\"沈麝不烧金鸭冷，障泥未解玉骢骄。长亭回首短亭遥。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a18e8d913','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"霜景霁，何处远钟撞。扣月渔榔寒轧轧，祈年社鼓夜逢逢。\",\"幽梦起山窗。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a314399dc','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"松槛外，鹭渚与凫汀。茅屋不愁经夜雨，草堂还喜带春星。\",\"歌咏满林坰。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a32e707e9','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"螺涧底，呜玉漱清泠。千里清风来曲几，一方明月挂疏棂。欹枕酒初醒。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a3b5b5f40','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"来往惯，蓬户不教扃。山屐过时穿嫩藓，野航移处破浮萍。\",\"炬火晚荧荧。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a444d83af','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"明月好，流影浸亭台。金界三千随望远，雕阑十二逐人来。\",\"只是欠传杯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a5517c0a8','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"长夏日，独往亦消停。仙酝一壶倾绿醑，蛮笺十样写黄庭。落魄尽忘形。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a77d8bb38','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"故园好，最忆世耕亭。樵斧丁丁惊鹳雀，钓丝袅袅立蜻蜓。\",\"百曲建长瓴。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8a85ffc66e','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"梅蕊好，冰玉出烟尘。袅袅孤芳尘外色，盈盈一朵掌中春。\",\"只少似花人。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8aa5a9a352','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"茅屋小，斜枕碧山阿。蕙冷松寒闻夜鹤，藻香萍暖下天鹅。\",\"不羡懒云窝。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8aa6665c71','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"晴雪好，万瓦玉鳞浮。照夜不随青女去，羞明应为素娥留。\",\"只欠剡溪舟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8aabbaabd5','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"凉吹满，六月早知秋。仙侣泛花曾共醉，野人刻竹记同游。\",\"何日问归舟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d46ffdaf8ad1cd5004','望江南','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"登顿处，萝磴几回扪。檐葡林中消永日，松篁影里荫芳尊。\",\"归路已黄昏。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47074e095c60dcce5','南乡子·廿一史弹词第二段说三代开场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"携酒上吟亭。满目江山列画屏。赚得英雄头似雪，功名。虎啸龙吟几战争。\",\"一枕梦魂惊。落叶西风别换声。谁弱谁强多罢手，伤情。打入渔樵话里听。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d472131a7fbd3fbf96','南歌子·羁怀','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"黄鹤蓬莱岛，青凫杜若洲。愁人寂夜梦仙游。不信一身流落，向南州。\",\"万里家山路，三更海月楼。离怀脉脉思悠悠。何日锦江春水，一扁舟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d472e75d084a426938','黑漆弩谢曾少岷题扇','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"梁尘飞绕停教住。有哲匠坐上原父。早梅时、新月弯弓，喜见遥天收雨。\",\"尉迟杯、满酌休辞，半臂急催忙去。写蒲葵，一字千金，付阿买、珍藏好处。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d472e75d08a2b7c516','黑漆弩谢曾少岷题扇','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月仪蠹简留君住。索靖是右军师父。笑藏真、酗酒狂僧，说甚龙蛇风雨。\",\"戏鱼堂、响榻重镌，莫道影留形去。熊朋来，鼎韵钟铭，鉴赏到、西台妙处。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d477a1e4c6be50b8ee','西江月·廿一史弹词第九段说宋辽金夏下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"山色消磨今古，水声流尽年光。翻云覆雨数兴亡。回首一般模样。\",\"清景好天良夜，赏心春暖花香。百年身世细思量。不及尊前席上。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d477a1e4c6c90c17f7','西江月·廿一史弹词第九段说宋辽金夏下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"细想三皇五帝，一般锦绣江山。风调雨顺万民安。不见许多公案。\",\"后世依他样子，齐家治国何难。流芳百世在人间。万古称扬赞叹。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d477a1e4c6d016f927','西江月·廿一史弹词第九段说宋辽金夏下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"三百余年宋史，辽金西夏纵横。争强赌胜弄刀兵。谁解倒悬民命。\",\"富贵草梢垂露，英雄水上浮萍。是非成败总虚名。一枕南柯梦醒。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d478ea78cb214f2b84','临江仙·可渡桥喜晴','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"可渡新晴韶景媚，不愁寒透征衣。滇阳渐近雪霜微。旧游如梦寐，旅思欲纷蜚。\",\"时序惊心催候雁，嗈嗈鸣向朝晖。多情何事帛书稀。明年春色里，领取一行归。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d478ea78cba38664f1','临江仙·可渡桥喜晴','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"万里云南可渡，七旬老叟华颠。金羁翠眊杏花鞯。还家剑锋画，出塞马蹄穿。\",\"旧店主人争羡，升翁真是神仙。东征西走几多年。风霜知自保，穷达任皇天。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2e0bec69a6','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"江头秋色换春风，江上枫林青又红。下水上风来往惯，一年长在马船中。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2e0eddf02a','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"最高峰顶有人家，冬种蔓菁春采茶。长笑江头来往客，冷风寒雨宿天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2e1010187d','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"无义滩头风浪收，黄云开处见黄牛。白波一道青峰里，听尽猿声是峡州。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2e3e1af47e','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"青江白石女郎神，门外往来祈赛频。风飐青旗香雨歇，山桂花开瑶草春。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2e591ecd34','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红妆女伴碧江濆，蓪草花簪茜草裙。西舍东邻同夜烛，吹笙打鼓赛朝云。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2ed6372fc6','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"神女峰前江水深，襄王此地几沉吟。?花温玉朝朝态，翠壁丹枫夜夜心。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2edba903e8','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"日照峰头紫雾开，雪消江面绿波来。鱼腹浦边晒网去，麝香山上打柴回。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2ee395e596','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"夔州府城白帝西，家家楼阁层层梯。冬雪下来不到地，春水生时与树齐。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4790c3c2ee6a3ce9e','蜀中竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"上峡舟航风浪多，送郎行去为郎歌。白盐红锦多多载，危石高滩稳稳过。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d479617df205e26ab8','汉宫春·菊席','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"采采黄花，向龙山高处，浅泛金卮。秋容老圃堪赏，插鬓参差。杨妃沈醉，苎罗更比西施。可笑群儿，凡目品题，污却高姿。\",\"千古陶家清兴，有何人提著，冷落东篱。炎皇曾书，本草寿域仙蕤。明堂月令，挺孤芳、四字标奇。歌一阕，悠然自适，无弦琴是心知。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d479bb00138f2f8ff8','江城子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"滇南春似锦江春。水鱼鳞。柳蛾颦。千树梨花，花底草如茵。晴日暖风浓胜酒，熏媚眼，醉游人。\",\"柔丝弱絮软红尘。惜芳辰。倦游身。啼鸟惊心，何事唤归频。不是不归归未得，愁望远，泪沾巾。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47b2869b2c9c58905','折桂令','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"枕高冈、坐占鸥沙。看晓渡帆樯，晚市鱼虾。红叶园林，黄花篱落，白水蒹葭。望东寺、双浮佛塔。\",\"指高峣、一片人家。隐趁归槎。低岸乌纱。满酌春醪，闲话桑麻。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47c1952dd6eb4b5b0','蝶恋花·二阕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"璧月琼枝风韵在。皎皎盈盈，自拗梅花带。一笑倾城迷下蔡。暗香疏影春难赛。\",\"年纪无多情性快。翠袖金杯，满劝深深拜。禅室散花元不碍。道人还了鸳鸯债。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47c1952dddf07df7c','蝶恋花·二阕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月白珠玄成一派。灼灼红灯，光映猩猩菜。天女从来银色界。肌肤冰雪牵人爱。\",\"护粉羞黄如有待。镜约钗盟，只恐旁人怪。弦上分明传意态。何时许解香罗带。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47c1dd9b6f3c5dd5e','瑞鹧鸪·咏柳','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"垂杨垂柳管芳年。飞絮飞花媚远天。金茧抱春寒食后，玉蛾翻雪暖风前。\",\"别离江上还河上，抛掷桥边与路边。游子魂销青塞月，美人肠断翠楼烟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47c52391600c601fe','鹊桥仙·七夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"冰盘荐巧，珠帘堆莹，半卷生绡光致。嫦娥妒眼便西沈，又早倩、羲和催辔。\",\"多情瑞鹊，填桥度我，何不晓来回避。但教三万六千年，也不若、人间百岁。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47ee3ff4421eb5d7b','鹧鸪天·斗山陂遇雪','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"去岁渝州赏雪时。桂舟兰楫泛清漪。握松旧令传琼腕，冠柳新词侑锦卮。\",\"香遍满，夜何其。三更醉舞赛西施。岂知饥走荒山道，百结鹑衣履冻澌。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47f1fd287c67f919a','鹧鸪天·乙酉九日','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"早岁辞家赋远游。东西南北任萍浮。熟知津路无劳问，惯听阳关不解愁。\",\"临远水，望归舟。流波落木又惊秋。多情黄菊休添泪，且向尊前泛玉瓯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47f37d1f2329efe91','踏莎行·甲午新春书感','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"异国光阴，暮冬羁旅。鬓丝禅榻今如许。羞将白发对青铜，慵把流霞烘绿醑。\",\"旧侣雕零，新欢间阻。东风杨柳萦愁绪。天寒独立咏苍茫，销魂正在斜阳处。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d47f37d1f2cb6011a2','踏莎行·甲午新春书感','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"白雪墙头，红云水面。匆匆花事看看半。去年花对断肠人，今日有肠无处断。\",\"萧寺双文，巫山一段。旧游回首空长叹。晓屏灯烬峭寒天，梦为远别啼难唤。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d480ad360c5560822a','西江月·廿一史弹词第七段说隋唐下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"追想千年往事，六朝踪迹茫然。隋唐相继统中原。世态几回云变。\",\"杨柳凄迷汴水，丹青惨淡凌烟。乐游原上草连天。飞起寒鸦一片。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4827913411e648bf0','生查子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"金匮失玄参，无约何时会。争道破揪枰，心为佳期碎。\",\"杨柳困三眠，终日常如醉。露泣缠枝花，满眼娇情泪。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48279134161dae179','生查子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"两朵活莲花，一对相思卦。裙底耍鸳鸯，巧笔难描画。\",\"春困步苍苔，背立秋千下。心事倩谁传，花落西厢夜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4835e5dcb4adf9179','山花子·咏软枝条同心山茶花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"袅袅同心巧笑分。粉霞红绶藕丝裙。软翠柔蓝擎不定，晓妆匀。\",\"醉露屏前双朵朵，舞风庭下一群群。不肯嫣然回一顾，为思君。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4835e5dcb832080b5','山花子·咏软枝条同心山茶花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"瑞雪晴林暮霭消。锦云香坞彩霞飘。绝代佳人空谷里，路迢迢。\",\"鹤顶研砂添赤髓，猩唇滞酒晕红潮。小朵分心堪采掇，当琼瑶。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48380e4183510fa58','鹧鸪天·戊申初度','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"绿粉名园暑燕开。池红水碧两徘徊。竹西仙叟藏春坞，莲北诗人半月台。\",\"三凤兆，八龙才。不论燕桂与王槐。丹霞夹月华星出，长照金樽映宝罍。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48380e418c8898f5f','鹧鸪天·戊申初度','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"宦海风波记昔年。天涯青镜感华颠。过堂未悟钟将衅，睨柱谁知壁偶全。\",\"周卦气，卜先天。日斜鼓缶月明前。笑看太白成三影，便是维摩不二禅。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d486602b9163ca55e3','瑞鹧鸪·集句咏巫山高','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"望中孤鸟入销沈。宿处津亭枫树林。江草江花岂终极，巫山巫峡气萧森。\",\"为云为雨襄王梦，愁水愁风商妇心。客去客来天地老，无朝无暮有猿吟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4867733ac40b2eb18','鹧鸪天·巧夕咏剪红罗','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"一片红云簇帐天。翠绡双卷出婵娟。露凝狐塞燕支紫，日炙鸿门楚锦嫣。\",\"香馥郁，影联翩。金风璧月斗明蠲。散花天女同心结，莫恼维摩丈室禅。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4867733ac9013dc80','鹧鸪天·巧夕咏剪红罗','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉露金风撼翠柯。园芳开到剪红罗。灿如天女琼花树，丽夺仙郎瑞锦窠。\",\"秋已透，夜如何。穿针楼上巧云多。七襄共爱天机好，其奈流年一掷梭。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d486b912020541c708','虞美人','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"檀槽凤尾龙香拨。一串骊珠落。风流傅粉媚何郎。解唱春风一曲杜韦娘。\",\"溶溶阴锁梨花院。宋玉愁空断。虚无只少对潇湘。却似满城风雨近重阳。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d486d49d7b7b1fa2a4','滩破浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"滟滟波光绿似醅。茸茸草色嫩如苔。只有轻寒浑不定，晚还来。\",\"落梅村里昏鸦远，摆柳风前候雁回。明日新晴堪眺望，上春台。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4880cda016140a3ab','六州歌头·吊诸葛','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"伏龙高卧，三顾起隆中。割宇宙，分星宿，借江东。祝东风。\",\"端坐舌战徂公。激公瑾，连子敬，呼翼德，挥白羽，楚江红。\",\"乌韵惊飞，虎踞蚕丛地，炎焰重融。吞吴遗恨在，受诏永安宫。\",\"尽悴苍穹。鉴孤忠。念行营草出师表，心匪石，气凌虹。\",\"岁去志，年驰意，早成翁。目断咸潼。出五丈，屯千井，旗正正，鼓冬冬。\",\"天亡汉，将星陨，卯金终。巾帼食槽司马，生魄走、死垒遗弓。\",\"遣行人到此，千古气填胸。多少英雄。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48a3f3b57b8772669','赛天香妓宴','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"芙蓉屏外，倒金尊、满座艳歌凝咽。半面新妆香透幌，环佩珊珊步怯。接黛垂鬟，低声小语，问采香仙妾。柳袅花停，莺莺燕燕标格。\",\"媚眼射注檀郎，双鸳全露，裙底凌波袜。万斛胭脂倾在水，染就银河一色。天作红墙，山为翠幕，生把伊侬隔。离魂牵梦回，南浦凉月。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48a94363714d0b970','小重山','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"番马屏风睡燕梁。更筹何太促、酒杯长。花明月暗袅余香。瞢腾里，醉眼怯灯光。\",\"相忆阻横塘，今宵凭伏梦、到伊行。玉山无赖隔巫阳。天又晓，蝴蝶为谁狂。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48b3905182b9e7ddf','柳稍青李菊亭将军席上','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雨歇仙家。香尘随马，晚霭迎车。玉树歌春，金屏笑夜，银烛烘霞。\",\"流年两鬌霜华。且烂醉、东篱菊花。不学江州，青衫司马，泪满天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48bbed9d13a5cef34','捣练子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"春梦浅，夜筹添。懒唱新翻阿鹊盐。黛角歌眉颦翠滴，绣尖绒舌唾红甜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f38720525ea7','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"自有嫩枝柔叶，何须补柳添花。低声昵语似雏鸦。肠断东桥月下。\",\"香雾清辉何处，春风今夜谁家。五花骢马七香车。趁取小乔未嫁。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f3872fba5487','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"酝造一场烦恼，只因些子恩情。阳台春梦不曾成。枉度雨云朝暝。\",\"燕子那知我意，莺儿似唤他名。消除只有话无生。除却心头重省。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f38738fabeea','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"漫道革囊盛血，休言炭帚污人。禅机仙术两成尘。输与花门柳阵。\",\"被欲生棱夜永，带翻成结灯昏。东园桃李片时春。不是少年风韵。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f387395ddc1b','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉指管生弦涩，朱唇语颤声羞。动人一味是温柔。为甚两眉长皴。\",\"不惯秋娘渡口，乍离阿母池头。临邛太守最风流。肯许凤求皇否。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f387a46706c5','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"客里家家美酒，人生处处扬州。娇歌聒帐醒扶头。年少风流还有。\",\"世事今成白首。归心已付东流。替人憔悴替人愁。笑杀长亭古柳。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f387c6b1eb15','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"紫塞朝朝烽火，青楼夜夜弦歌。一朝明月雁声多。带得边愁无那。\",\"云阙九重阊阖，家山万里岷峨。独愁乡思满蹉跎。梁甫狂吟谁和。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f387c7d3e21a','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"寒食清明过了，牡丹芍药开残。春光晼晚兴阑珊。寂寂重门独掩。\",\"秉烛谁同夜饮，添衣尚怯春寒。欲寻芳草向江干。榆荚杨花满岸。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d41f387d865f5da','西江月','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"倚醉深关朱户，娇羞怕捧金觥。背人弹泪绕花行，唱尽新词懒听。\",\"总是为郎调护，当初枉道无情。英雄摩勒肯重生。赎取佳人薄命。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48d45dd28a8b08f56','西江月·画观音寿意','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"金粟如来神影，青莲居士新词。普陀岩瞑八功池。宛与昆明相似。\",\"瑞霭锦云团盖，祥光宝月圆规。醍醐乳酒注军持。听取长生莂记。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d48ef937c9d60f1d76','青玉案·昆明邝尹升万州守歌','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"昆明春水盈盈渡。咫尺又、销魂路。金马碧鸡遗爱处。卧辙攀辕，留綦解佩，望望仙尘去。\",\"鹏海鲸波天一柱。紞紞鼓声催欲曙。金紫重来还肯许。帝加三锡，民歌五裤，早沛清时雨。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d490bdbd82f123829a','满江红','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"覆雨翻云，纷纷轻薄何须说。眼见儿曹富贵，灰消烟灭。百事有隆还有替，一毫无玷原无缺。把浊醪、粗饭任吾年，看豪杰。\",\"也莫羡，炎如热。也莫笑，凉如雪。自扫雀罗门巷，世情都隔。借问东华尘与土，何如南浦风和月。报山涛、不用问嵇康，交情绝。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d490c5efd41fe0a2f5','于中好己酉新春试笔','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"早岁登龙气吐虹。花砖红采映青葱。西昆纶綍都经手，东壁图书尽在胸。\",\"惊退鹢，悔雕虫。烟波万里一丝风。桃夭杏艳村醪嫩，方丈蓬莱是此中。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4932153e2cae43cd4','江城梅花引·夕宿江门','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"漏声沈。雨声沈。滴滴空阶碎客心。拥单衾。拥单衾。病骨寒侵。\",\"争教不断魂。静闻枥上嘶羸马。巴童熟睡荒鸡哑。旅游难。白首衰残。何日歇征鞍。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d494bf12ce630ef31a','渔家傲·秋雨','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"云掩遥山山掩翠。雨声急战荷声碎。绿锦离披红锦坠。花叶背。波间惊起鸳鸯睡。\",\"潋滟芳尊人共对。碧筒凉沁初消醉。湿烟香雾笼归袂。联玉辔。南风马上闻蛙吹。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4951479bd91701304','鹧鸪天·高峣岸梅，句用鸟兽字','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"鱼尾红霞海影摇。碧鸡腊信上梅梢。涂黄巧学蜂腰细，匀粉俄惊蝶翅飘。\",\"龙竹瘦，鹤松翘。相和兰麝绮香销。龟城春色添乡梦，想见枝头绿凤么。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d496d30f4293f3aa92','归朝欢亨衢陟明歌','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"秋尽汀洲苹未歇。掩映荻花相向折。一枝好赠朝天人，还如鳷鹊楼前雪。琼凹连玉凸。五城边银为宫阙。墀声寒，非烟丛里，卿蔼瑞云缬。\",\"五马骄嘶虬漏彻。鞘静佩鸣鹓簉列。朱衣引队奏彤帏，天言清问尧阶切。俞音传衮鷩。赐金增秩干旄孑。把勋名，昭回青简，身许稷和契。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d497b5b8daf11e3080','满庭芳','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"天地侧身，风尘回首，何年梦落南荒。家山千里，烟月记微茫。萍水蓬风无定，随处倘佯。葛藤话、浮生半日，且卧老僧房。\",\"门前来往路，叹迷踪羁旅，混迹渔商。看海波渺渺，曲似回肠。玉关外、多愁易老，算不如沉醉为乡。旗亭下，千金斗酒，一掷买春芳。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49982ac9560acac2f','渔家傲·夏日晓起','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"鸡唱未圆天已旭。伯劳杜宇声相续。残月笼花风袅竹。眠未足。千金难买临明宿。\",\"叶上露华纷缀玉。青松雨后如膏沐。思爽神清来往独。幽径躅。休教踏破苔钱绿。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49982ac95f48154a9','渔家傲·夏日晓起','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"春色已随银树去。点苍千里行云暮。四颗明珠行复聚。歌舞处。鹤桥便是秋娘渡。\",\"青镜红颜容易去。老年芳景须珍护。服食求仙多错误。君记取。九州大错休教铸。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49ae8cc4017770e7e','木兰花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"晓寒倦倚相思枕。丝雨织愁愁欲稔。绿杨桥畔觅谁家，油壁香车迎阿沈。\",\"莫教错送回文锦。密地重将青鸟审。流萤庭户嫩凉天，月许再圆花再品。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49ae8cc403d4a8521','木兰花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"遥钟促漏难成寝。残月疏灯寒凛凛。镜添华发似愁潘，带减腰肢如病沈。\",\"玉箫尘琐无心品。故国经年书断锦。欲寻石上一柯棋，共醉山中千日饮。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49ae8cc40be8f74b7','木兰花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"弓鞋一掬凌波迥。冉冉盈盈羞顾影。擎茶步缓扎花凝，斗草归迟苔露冷。\",\"雪皱云松倩郎整。罗帐灯昏莲瓣瞑。掌中无力搦琼枝，渴思半消残酒醒。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49ae8cc40ea99fcfe','木兰花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"枝头百舌塞犹噤。病起心情无赖甚。乍看南浦月名娟，记得东园花姓沈。\",\"枕肱不枕鸳鸯枕。倚醉偎人偏越恁。如今憔悴背花眠，只有余香留燕寝。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49b02ba6d0088887d','菩萨蛮','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"袅袅腰肢浑似柳。碧花茗椀劳纤手。清昼小横陈。阳台梦未真。\",\"一声鶗鴂暮。两浆催人去。新月曲如眉。黄昏怅望时。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49b02ba6ddf3f7372','菩萨蛮','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"梦云溶曳巫阳夜。兰台风起春寒乍。花鸟莫深愁。笙歌聊写忧。\",\"蛾眉梳堕马。象口熏残麝。浓睡带余酲，翠窗啼晓莺。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d49da1655f6033745d','清平乐·廿一史弹词第五段说南北朝开场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"闲行闲坐。不必争人我。百岁光阴弹指过。成得甚么功果。\",\"昨日羯鼓催花。今朝疏柳啼鸦。王谢堂前燕子，不知飞入谁家。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a0fa02f88e53b5ee','满江红·梨花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"露重风香，韶华浅、玉林无叶。谁剪碎，遍地琼瑶，满园蝴蝶。娇泪一枝春带雨，粉英千片光凝雪。伴秋千、影里月明中。伤离别。\",\"花在手，肠如结。人对酒，情难说。忆故园游赏，清明时节。今日相逢滇海上，惊看烂熳开正月。更收灯、庭院峭寒天，啼鹃歇。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a15124a10f9f55e1','鹧鸪天·莲池','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"断角残钟半掩门。谁招雪魄与冰魂。罗浮梦里更三点，花信风前第一番。\",\"溪绿净，月黄昏。留连檀板共金樽。袜尘波底香难赛，轸粟灯前玉易皴。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a15124a1609b4fc7','鹧鸪天·莲池','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红玉花开绿锦池。碧筒金醴泻芳卮。十三餍足传情遍，二八蛾眉度曲迟。\",\"云散后，月明时。接䍦颠倒袂参差。良辰佳会应难续，风雨连宵恼别离。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a15124a1dde3b755','鹧鸪天·莲池','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"翠斝青樽旅思消。酒楼斜映绿杨桥。瓮头霞色如妆面，帘脚风流似舞腰。\",\"同醉处，可怜宵。障泥未解玉骢骄。青衫不湿江州泪，已许狂歌托圣朝。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a2d534f677db8872','减字木兰花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"乌衣椎髻。龙女改妆歌薜荔。立近梅花。弄粉团香衬脸霞。\",\"真真唤醒。便出墨池登雪岭。醉写乌丝。齐唱东风第一枝。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a414265369aed5d6','西江月·廿一史弹词第三段说秦汉下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"落日西飞滚滚，大江东去滔滔。夜来今日又明朝。蓦地青春过了。\",\"千古风流人物，一时多少英豪。龙争虎斗漫劬劳。落得一场谈笑。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a41426538420d75c','西江月·廿一史弹词第三段说秦汉下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"豪杰千年往事，渔樵一曲高歌。乌飞兔走疾如梭。眨眼风惊雨过。\",\"妙算龙韬虎略，英雄铁马金戈。争名夺利竟如何。必有收因结果。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a4142653a7e5d4d6','西江月·廿一史弹词第三段说秦汉下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"道德三皇五帝，功名夏后商周。英雄五霸斗春秋。秦汉兴亡过手。\",\"青史几行名姓，北邙无数荒丘。前人田地后人收。说甚龙争虎斗。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a6bf4c1b47a1d797','浪淘沙·芝山以其慧中所提团扇赠行，侑以蜀锦一幅，词以寄谢','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"李愿本诗仙。列屋争妍。肯分团扇翠帷边。想见清香凝燕寝，秋水明蠲。\",\"丝繂系兰船。回首江天。浣花春水腻鱼笺。何以报之青玉案，锦字遥传。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a7840d4f7b430c2c','塞垣瑞鹧鸪檃括唐诗以补唐曲','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"秦时明月玉弓悬。汉塞黄河锦带连。都护羽书飞瀚海，单于猎火照甘泉。\",\"莺闺燕阁年三五，马邑龙堆路十千。谁起东山安石卧，为君谭笑静烽烟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4a9bbcc4bcbd5d4fb','浣溪沙·雪中，萧东淳自安宁至，二姬自滇来，小饮成醉','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雪里佳人特地来。红裙翠袖小弓鞋。杨花飞处牡丹开。\",\"佛氏解为银世界，仙家幻作玉楼台。王猷访戴棹休回。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4ab9d9b34aa3598df','个侬艳情','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"恨个侬无赖，娇卖眼、春心偷掷。苍苔落花，一双先印下。月样春迹。闻气不知名，似仙树御香，水边韩国。罗襦襟解闻香泽，雌蝶雄蜂，东城南陌。何人轻怜痛惜。窥宋玉邻墙，巫山宁隔。寻寻觅觅。\",\"又暮云凝碧。良夜千金，繁华一息。楚宫盼睐留客。爱长袖风流，钟情何极。唱道是、凤帏深处附素足。颤袅周旋恶。怜伊尽倾侧。叫檀郎枉春夕。恐佳期别后青天样，何由再得。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4acbc992b43ead99e','何满子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"夜夜遥钟促漏，朝朝急莞清笳。行见月圆还月缺，乡关犹隔三巴。\",\"客里形容憔悴，愁看匣里菱花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4acbc992b8c1c5373','何满子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"韪鴂催成绿雨，鹧鸪啼破红霞。马上韶华真似梦，恼人春思交加。\",\"记得晓妆临镜，愁看映水桃花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4acbc992bd38eff91','何满子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玄灞长松系马，青门高树藏鸦。红雨秋千香径软，乐游原上谁家。\",\"客里心情缭乱，愁看风外杨花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4acbc992bfd17b629','何满子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"欹枕梦回春晚，倚阑人在天涯。迢递关山千万里，佳期水渺云赊。\",\"记得罗巾别泪，愁看带雨梨花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b1d5b7d77319a3ee','折桂令·昭君出塞图','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"乱纷纷，玉蕊冰花。气结愁云，泪湿腮霞。高阙千寻，停骖一顾，漠漠黄沙。只见三队五队，槊旌旗，舞风番马。\",\"千点万点，绕穹庐，成阵寒鸦。一曲琵琶。几拍胡笳。目断飞鸿，恨满天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b2801a2e7170c7d2','天仙子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"浪飐桃花风滚絮。春色可能留得住。四眸相顾两埋冤，金齿路。金马路。总是销魂离别处。\",\"红雀蜻蜓难共树。紫燕伯劳相背去。一番新恨又从头，他也误。我也误。孤馆乱山愁日暮。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b2801a2ed5cc17bf','天仙子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"忆共当年游冶乐。小小池塘深院落。相亲相近不相离，花下约。柳下约。一曲当筵金落索。\",\"回首欢娱成寂寞。惊散鸳鸯风浪恶。思量不合怨旁人，他也错。我也错。好段因缘生误却。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b3cf035023e0e03a','定风波·沾益冬至','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"客中冬至夜偏长。寒炉坐拥待晨光。心似死灰飞不起。愁里。路迷心折望吾乡。\",\"玉几由来天北极。曾直。五更三点入鹓行。何事白头犹道路。谁诉。天高难问只苍苍。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b4f0dde96c4629f6','滇春好寄李南夫、钱节夫、毛东镇','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"滇春好，韶景媚游人。拾翠东郊风袅袅，采芳南浦水鳞鳞。能不忆滇春。滇春好，百卉让山茶。海上千株光照水，城西十里暖烘霞。能不忆滇花。\",\"滇春好，翠袖拂云和。雅淡梳妆堪入画，等闲言语胜听歌。能不忆滇娥。滇春好，最忆海边楼。渔火夜星明北渚，酒旗风影荡东流。早晚复同游。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b5629da02f78b82a','西江月·廿一史弹词第二段说三代下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"阅尽残编断简，细详千古英雄。功名富贵笑谈中。回首一场春梦。\",\"昨日香车宝马，今朝禾黍秋风。谁强谁弱总成空。傀儡棚中搬弄。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b62c61ba141b57ed','西江月·泸州发船，王云巢携翠容相送','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"莫道留春无计，君来有计留春。翠容烟黛隔花颦。隐隐遥山远映。\",\"载酒春江画舫，题诗红泪罗襟。江头离别一何频。醉倒柳昏花瞑。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4b991c6b33951ad29','天仙子·梦作','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"莲叶为舟丝作索。渡口梅风歌扇薄。一声留得满城春，金凿落。银凿落。醉里不知离绪恶。\",\"别后那回华表鹤。一点芳心无处着。云中谁寄锦书来，春寂寞。人寂寞。绿遍汀洲生杜若。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4bc1992a02af3ea28','沁园春·自寿兼谢钝庵','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"初度今年，遥集句东，丹壶洞天。记京国俊游，筑金台上，乡亭旧话，濯锦城边。两地萍蓬，十年烟月，欲往从之如绊然。今何夕，又满堂花醉，重对华筵。\",\"新杨窈窕春妍。正绿萼、梅花开最先。况凤侣潘杨，兆呈琼齿，龙媒秦晋，应在冰泉。画里萧郎，瓮中毕叟，绛帐风流群桂仙。上云乐，倾壶箫管，白发重玄。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4bd032e556d84cdae','定风波·廿一史弹词第八段说五代十国开场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雨淋漓滩赴选场。秀才落得甚干忙。白发渔樵诸事懒。萧散。闲谈今古论兴亡。\",\"虞夏商周秦楚汉，三分南北至隋唐。看到史官褒贬处。得趣。不摇纨扇自然凉。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4bd5d0fb2a985f7f6','生查子·款残红','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"花径款残红，风沼萦新皱。有意惜余春，无计消长昼。\",\"香醪泻玉洼，瑞脑喷金兽。谁与共温存，寂寞黄昏后。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4bd5d0fb2bf16c435','生查子·款残红','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"频移带眼空，只恁恹恹瘦。不见又思量，见了还依旧。\",\"为问频相见，何似长相守。天生并头莲，好结同心藕。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4bdb0af344d7a1de7','满江红·檃括潜夫词，忆李中溪种玉园梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"角寂钟沈，清梦到、罗浮翠麓。种玉园中同醉处，暗香犹扑。月落参横增怅望，天寒日暮悲幽独。讶陇头、不寄一枝来，萦心曲。\",\"茅屋稳，辞金屋。藜烛焰，羞银烛。叹出尘风韵，背时妆束。争羡东邻姬傅粉，岂知空谷人如玉。问扬州、何逊有新诗，谁堪续。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4be9f737c15e73ed7','瑞龙吟·杏花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"东风峭。谁猜水剪余霞，日烘残照。妆成窥宋东墙，倾城倾国，嫣然一笑。轻笼罩。叶底烟开冉冉，枝头春闹。燕泥香里，斜阳酒旗映水，天涯一棹。消息繁华谁报。记曲江池上，长安古道。\",\"愁落愁开，雨横风暴。沉吟无语，时把朱阑靠。知谁伴、单衫露冷，醉红簪帽。倚醉狂歌浩。悠悠尽是伤离调。花艳为谁好。春又晚、可惜年华空老。关山迢递，故人难到。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4bf03e4728bc8be10','浪淘沙·富顺县罗汉洞诸公相送','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"良会阻天涯。水渺云赊。归途喜见腊梅花。又醉当时罗汉洞，一曲琵琶。\",\"浮客似浮槎。到处为家。鲁峰高兴更南沙。有约肯来无宿诺，细酌流霞。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c0a43bc6e8b63a6e','西江月·唐宫守岁图','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"五柞宫中腊尽，万年枝上霜清。沉香火底坐吹笙。玄圃楼台不瞑。\",\"蕊女金钗剪烛，花奴玉导挑灯。红儿酒渴嚼春冰。忽报景阳钟应。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea07c3a87d','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红叶随流水，青梅出短墙。雅意属檀郎。可怜人二八，好风光。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea225b692a','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"窗面秋千月，墙头蓓蕾风。春事转头空。酒醒人散后，拾残红。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea239abbbf','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"丝品娟娟玉，钗梁灼灼花。妙语似雏鸦。有心来恼我，莫应他。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea45a17bb2','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月是巫娥伴，花为宋玉邻。远似隔星津。早知离恨苦，莫相亲。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea6ff8663b','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"片玉连城价，千金一刻宵。仙梦隔蓝挢。意中人不见，为谁娇。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea7183fcc1','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"灯下蠲红纸，花间擘彩笺。肠断写新篇。画粱新燕子，倩他传。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4ea9aa70f59','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"青鸟飞相逐，乌龙卧不惊。镜约与钗盟。月沈星欲堕，奈何情。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c267d4eace25f958','南歌子·八首','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"粉泪盈纤手，春愁占两眉。细雨阻佳期。昨宵当此际，恰来时。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c2ebbe3b74803bd7','鹧鸪天','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"曲阁飘萧一夜风。名花零落五铢红。卜欢有信占今夕，买赋多情出后宫。\",\"朝暮朵，浅深丛。翠帏丝障锦帘栊。汉江游女愁多露，春在昭阳日影中。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c2ebbe3b8edc6f43','鹧鸪天','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"宝树林中碧玉凉。西风又送木犀黄。开成金粟枝枝重，插上乌云朵朵香。\",\"依绣阁，傍银塘。广寒宫里白云乡。迥砧横笛声初断，坠露流风夜正长。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c2ebbe3b8f6a38ec','鹧鸪天','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"恰喜高秋爽气新。却愁秋月解伤神。酒阑二十年前事，梦醒三千里外身。\",\"枫叶岸，菊花津。且须勤买阿宁春。帐头傀儡元来假，枕上邯郸岂是真。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c2ebbe3bfc198024','鹧鸪天','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"锦地香霏织雨丝。蟠桃腻叶染春姿。薛涛红粉临妆怯，周昉丹青点笔迟。\",\"同醉处，旧游时。卷中似欠海棠词。酒徒云散佳人远。孤馆残灯恼梦思。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c364f8d7784707b6','浣溪沙·永宁病起将归','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"听彻晨鸡又暮鸦。病来帘外即天涯。明朝才得问归槎。\",\"客路纵遥终会到，他乡须好不如家。江门春水照江花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4c707c2135d8de682','鹧鸪天·刘岷川母寿词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"王母西来青鸟迎。瑶池翠水浅还清。何如萱草庭前茂，又见猗兰砌下生。\",\"罗甸国，锦官城。望云就日两关情。锦江化作长春酒，笑对东风北斗倾。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4cc55a1da41697868','霓裳中序第一雨中刘珥江送鸡头','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"青霞裁雾縠。正锦潋瑶潘碧澳。水底鲛人织女。当翠房深处，新妆乍沐。尘消罗袜，冶步盈盈出鳞屋。向何许，满身璎珞，云辙转风毂。\",\"皎镜全澄独漉。相掩映、荷裳芰服。昨夜龙宫失晓也，养天鸡、启明传旭。啄粮无半菽。算只有明珠满腹。江妃把、金针穿玩，递与素娥掬。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4cfd4fa47477c9bf5','蝶恋花·足范文正公遗句','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"香颈垂云眉倒晕。珠翠丛中，没个他风韵。浪蝶狂蜂遍有分。为谁偷把红销印。\",\"锦字花笺难问讯。一寸相思，只与黄昏近。薄幸负人终不忿。何时枕上分明问。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d18e5c4c6c1013a6','滇海竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"罗汉孤峰祇树林，梁王辇道海中心。海垠青青堪牧马，海眼只今无处寻。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d18e5c4ce0fa7c24','滇海竹枝词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"东浦彩虹悬水椿，西山白雨点寒江。烟中艇子摇两桨，空里鹭鸶飞一双。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d3b7e8052b1c8af5','浣溪沙·高峣带雨','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"满目风光户不关。李成骤雨郭熙山。吾卢宛在画图间。\",\"修竹墙头过绿醑，丁香棚底醉红颜。晚来一笑破天悭。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d4efeb8be22a2cb8','喜迁莺·贺薛曲泉抚台旌奖','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"青阳有脚。喜五马重临，江阳城郭。南定云开，西岷波静，帘卷风清幕。旱魃化为甘雨露，祷不须泥鹤。兆万宝，咏千箱四野，丰年如约。\",\"斟酌。曾见说，老手剧郡，利器无盘错。犬吠花村，鱼游春水，桴鼓长闲却。伫闻考绩薇垣，复报荐名荷橐。计晨夕，莺迁燕贺，金明紫渥。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d8514caf1d01bd10','南乡子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"晴虺索春饶。垂柳长亭学舞腰。昨夜惊鸿还入梦，妖娆。已隔扬州念四桥。\",\"濯锦露花朝。曾泛兰舟拾翠翘。云散风流何处觅，难描。浅黛遥山雪半消。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d8514caf98f6ae1f','南乡子','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"油壁香车。来自钱塘苏小家。行雨行云曾未惯，先夸。双带同心结不差。\",\"酒泛红霞。银蜡烧残燕尾斜。小语低声归去也，啼鸦。风袅垂杨月隐花。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4d9e4ad8d7d6192c9','翠楼吟·代送大理蔡太守','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"月晃苍山，风清黑水，花满原鳞城雉。燧松明夜火，歌来暮似城都市。五华楼合，看五马迎恩，双鸿送喜。迟迟晷行春金碧，照春金紫。\",\"此邦宜有循良，拥玉麟铜虎，旖旎叶榆难借寇，望登仙香尘遥起。攀轩处、正红颊窥帘，华颠卧轨。画图外、甘棠阴里，心驰千里。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4de82ffebb932b39d','忆王孙·落花集句','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"绣葩红蒂堕残芳。蝶醉蜂痴一簇香。卢家少妇郁金堂。\",\"对斜阳。云雨巫山枉断肠。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4df371f6ee476a899','雨中花·春寒','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"寒透水边沙际。暖在花心叶底。扫雪楼台，收灯庭院，向晚朱门闭。\",\"丝额画帘低拂地。舞袖弓鞋浑未试。指冷金刀，香温翠被，懒剪宜春字。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e0174890171b98bc','捣练子·集句伤乱','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"长寂寞，独徘徊。抱病起登江上台。万户千门春色闭，谁家桃李乱中开。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e01748905aae8790','捣练子·集句伤乱','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"愁对酒，懒看花。病入新年感物华。万里寂寥音信断，山楼粉蝶隐悲笳。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e017489072ae4728','捣练子·集句伤乱','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"金碧岸，锦春丛。红白花开烟雨中。今日乱离俱是梦，六街尘起鼓冬冬。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e0174890910814a5','捣练子·集句伤乱','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"风料峭，月栊明。滴破春愁压酒声。犹有夸张年少处，醉闻花气睡闻莺。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e017489093fab8ff','捣练子·集句伤乱','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"花亚朵，柳垂丝。楼上风和玉漏迟。春困苦多无处卖，日长惟与睡相宜。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e0174890cb647566','捣练子·集句伤乱','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"销午梦，减春愁。上尽重楼更上楼。日暮乡关何处是，寒鸦飞去水悠悠。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e020418294c9e334','莺啼序·高峣海庄十二景圃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"碧鸡唱晓霞散绮，重关征画。环村步、几族生烟，空翠幻作仙界。茭草荡湾环洲渚，轻风送幅蒲帆快。看舞鸥、飞鹭联翩，向狖寥外。晴兆须臾，雨信顷刻，问水椿山带。树羽飐风展青帘，驳云销睨日晒。浪花平、洛神衬袜，红妆涌出青罗盖。浮修眉，约略黛螺，映盘鸦髻。海花汀藻，文石锦沙，纶组迎船絓。\",\"渔火焰、涟漪倒影，归棹穿方罫。蒲牢昏吼，栖鸦结阵，八村九寺鸣萧籁。提筠蓝、金线穿鱼卖。松灯点点互月彩。卧玉塔浮澜，素娥窈窕千态。翠岩深霭，禅室僧归，杖锡微径隘。月黑白林明处，香软寒轻薏苡，缸中梨梦萦呗。南蛮老松，遥看晴雪，似银龙下斟玉瀣，洗尘襟、着得乾坤大。辋川何似吾庐，海变春醪，偿风月债。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e429ae7939c470e1','如梦令','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"云影月华穿过。雨意钟声敲破。洞户卷帘时，飞透流萤一个。\",\"孤坐。孤坐。白雪金徽谁和。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e429ae7975422081','如梦令','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"屏底歌残金缕。座上花催蜡炬。春枕酒醒时，又是绿波南浦。\",\"春去。春去。帘外潺潺微雨。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e66a51c559b70019','风入松·途中遇雪','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"梦回灯烬旅衾单。鸡唱五更寒。一夜里、雪花飞遍，晓来时、千里漫漫。野色遥连玉树，清光炯映银鞍。\",\"行人休道路歧难。酒盏自须宽。一种风流堪画处，溪桥两两渔竿。楼上笛声吹起，天涯梅蕊飘残。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e770c0a2f5a5f5c9','浣溪沙·丙午十二月，碧鸡关路旁梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"为访寒梅过野塘。一枝斜出宋家墙。团情团思媚韶光。\",\"游女弄珠临汉水，画师绰绛学吴妆。新词休咏旧昏黄。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e770c0a2f8141c8e','浣溪沙·丙午十二月，碧鸡关路旁梅','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"琼稷金盘六齿红。玉人美酒赛新丰。凭陵大叫小楼中。\",\"临岸梅花惊照水，隔江桃叶唱回风。明朝重醉宋家东。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4e9e73dae4ba96187','浣溪沙·高峣雨中喜简西峃至','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"短短轩窗小小斋。行吟坐啸独悠哉。爱闲能有几人来。\",\"佳客新从云里至，好花多向雨中开。隔篱呼取尽余杯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4eb13cba2b2ff2328','浣溪沙·高峣晚晴招简西峃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"玉斧修成宝月团。月中仍有女乘鸾。钗横鬓乱不胜寒。\",\"白兔楼头风露迥，碧鸡祠下水云宽。相思两地共凭阑。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4eb13cba2cc615c16','浣溪沙·高峣晚晴招简西峃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"云露山尖水见沙。生烟漠漠树横斜。晚来晴景属诗家。\",\"天碧远粘千里草，霞红低缀一丛花。谪仙桥上望仙槎。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4eb27a740b7b3f5c8','满江红·咏菊，效稼轩词论体','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"唤醒灵均，为问餐、秋菊落英消息。秋英元不落，妙筌谁识。要悟灵均言外意，此花珍重殊难得。待英蕤、零落始供餐，休轻摘。\",\"九月律，当无射。黄落尽，无颜色。惟兹独秀，冷露寒霜侧。不肯悠悠随宿莽，只将凛凛争松柏。把九章橘颂，试同观，方奇特。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4ece5e199ec6fcfee','柳梢青','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"酥雨才收。猩红谢馆，蛾经秦楼。忙杀东君，牡丹缠脚，芍药梳头。\",\"藏鸦杨柳风流。正好系、班骓紫骝。窈窕洲边，温柔乡里，莫负歌喉。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4ed55aaf80eb17395','千秋岁·壬寅新正二日寿内','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"瑶池阿母。本是神仙侣。谪向人间官府。住摄提，贞孟月，中子逢初度。\",\"琅霄宴，春回北斗斟云醑。麟笔春秋谱。彤管毛诗语。\",\"女洙泗，闺邹鲁。五福中天聚。百秩从今数。最难得，芝兰庭下斑彩舞。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4ed826c606ecf25a7','鹧鸪天·棋姬','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"红袖乌丝罢写诗。翠娥银烛笑谭棋。雁行布阵穿花垒，虎穴临冲拔绣旗。\",\"烽火劫，羽书持。东山樽俎卷淮淝，紫囊儿辈元能办，况有婵娟出六奇。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4ed826c608a7ef727','鹧鸪天·棋姬','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"仙梵潮音水月堂。昙花犹映墨花香。旧围红袖题诗处，曾指银瓶索酒尝。\",\"追幻梦，忆疏狂。春风宿草满平冈。浮山夜壑藏舟夜，槛外长江自渺茫。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4ed826c60b4616e00','鹧鸪天·棋姬','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"篱下黄花早已雕。镜中华发不相饶。清霜白露烟光晚，去雁来鸿客路遥。\",\"香篆冷，烛花消。佳宾同醉可怜宵。都将璧月琼枝句，付与樽前薛小桃。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4efbb1cd3ed57d72d','鹧鸪天·西庄','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"秋水澄清胜酒醅。野烟笼榭似楼台。弹声林鸟山和尚，写字寒虫水秀才。\",\"乘兴去，兴阑回。夕阳影里记徘徊。正思修禊明年约，无奈鸣驺得得催。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f1beab1a2585d97b','临江仙·题春夜宴桃李园图','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"桃李名园开夜宴，樽前烛影摇红。璚枝碧月锦帘栊。香寒心字篆，花暖肉屏风。\",\"百岁光阴真过客，千金一刻难逢。高谈幽赏兴何穷。玉箫歌一曲，金谷酒千钟。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f1ecc77a3e744275','灼灼花','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"谁把纤纤月。掩在湘裙褶。凤翠花明，猩红珠莹，蝉纱雪叠。颤巍巍一对、玉弓儿，把芳心生拽。\",\"掌上呈娇怯。痛惜还轻捻。戏蕊含莲，齿痕斜印，凌波罗袜。踏青回露湿、怕春寒，倩檀郎温热。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f29ff18e7521c6da','点绛唇','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雪暗江郊，小灯孤馆长宵半。真珠酒艳。冻作红冰片。\",\"晓梦惊回，画角声初断。南枝畔。梅花开遍。谁见春风面。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e0019fcda','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"楚峡云娇宋玉愁。汀花海藻系兰丹。晕灯荧泪五更头。\",\"桃叶桃根双姊妹，江南江北两风流。佳期好在月明楼。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e3d4aec3c','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"脚上鞋儿血色罗。掌上无力斗弯靴。月牙新样不争多。\",\"芍药栏边穿窈窕，柘枝筵下舞婆娑。芙蓉帐里奈君何。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e41e0a813','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"南国何年桂女笺。蕊黄金粉斗芳妍。风流应自锦江传。\",\"佛幌烟煤犹自馥，蛮方文物也堪怜。为君重染古苔篇。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e4ee9df94','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"步彻香尘倚画阑。丛头合凤尾交鸾。金莲并蒂月双弯。\",\"宋玉东邻芳草软，江淹南浦落花干。抱云勾雪近灯看。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e6c725b23','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"滇海明珠照永昌。纤罗豆蔻减浓香。蚁穿红线结盘囊。\",\"已信春金仍应玉，不愁转紫复回黄。星横洞户月侵廊。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e6d37b31c','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"珠树三花惜共攀。尊前又唱小阳关。雏莺学语唤春还。\",\"桃叶渡头流恨水，绿杨亭上画眉山。酒醒人在梦魂间。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182e75b4a94d','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"燕子衔春入画楼。猧儿撼晓动帘钩。一场残梦五更头。\",\"彩凤琴中弹别调，锦麟书里诉离愁。相思相忆几时休。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182eba18f53c','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"风里桃花一阵红。一年春色又成空。可怜光景闭门中。\",\"绝塞不知登览兴，故乡惟与梦魂通。倚楼病眼送归鸿。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182ebfeae2b9','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"首夏偏宜淡薄妆。铜青衫子紫香囊。清歌一曲送霞觞。\",\"罗袜凌波回洛浦，淡云轻雨拂高唐。纱厨今夜贺新凉。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182ed0c63272','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"何处春娇晚晕多。分梢新月浅檀蛾。千金一斛带青螺。\",\"情泪乱抛红瑟瑟，梦云深锁碧蛾蛾。细腰肠断掌中歌。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182ed3e31f3e','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"巫峡行云洛浦风。秦楼西畔宋家东。滴春娇泪雨蒙蒙。\",\"龟甲歌屏朝掩翠，蛾眉妆镜晚窥红。寂寥门闭杏花丛。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182edb0f87ca','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"小岁新阳好物华。句东春信到梅花。断钟残角又天涯。\",\"酒面浮金添嫩蚁，钗头屈玉艳雏鸦。暗香疏影认林家。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182ee54066fa','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"解唱隋家昔昔盐。离亭别宴柳毵毵。远山颦映翠眉尖。\",\"霅上恰嫌苕子小，江都还笑宝儿憨。断肠春色在江南。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f32f182eeb8510e4','浣溪沙','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"远道明朝万壑冰。华堂今夕九枝灯。娇歌姹管醉难胜。\",\"幻玉风流南北院，飞琼仙梦短长亭。青楼回首十三层。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f43435d1b028cde9','庆春泽·安宁元夕','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"鱼市笙歌，螳川灯火，又看滇海元宵。剪剪轻风，绮罗十里香飘。彩云影罩冰壶地，人家在、月户星桥。恣经过、一刻千金，玉漏迢迢。\",\"柳金梨雪催春早，叹他乡异节，回首魂销。今夜相思、玉人何处吹箫。锦江烟水迷归望，绿琴心愁恨慵调。梦回时、酒醒灯昏，月转梅梢。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f4dc866303c85cdb','浣溪沙·游感通寺赠冰壶','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"柳绿槐黄放晓晴。天公知我欲山行。峰峦相顾亦多情。\",\"玉树临风无奈爽，冰壶见底有余清。长生仙客话无生。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f4dc866399d1e484','浣溪沙·游感通寺赠冰壶','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"病起思乡太剧愁。荒城无地可登楼。仙人要我感通游。\",\"绿水似环萦岛屿，白云如带绕林丘。凉风先借一分秋。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f581877b59ca6c40','西江月·廿一史弹词第六段说十六国下场词','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"六代瓜分世界，五胡云扰中原。纵横三百有余年。几度交锋索战。\",\"马过生灵齑粉，血流河洛腥膻。耳闻犹自不堪言。有眼休教看见。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4f5a64d11054288b0','满庭芳·效东坡作','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"归去来兮，半生歧路，天涯南北西东。弋人何慕，造化任冥鸿。曾是先朝执戟，今衰矣、白首扬雄。休点检，并游英俊，五相一渔翁。\",\"丹衷。举头望，长安万里，一朵云红。把致君尧舜，付与诸公。赢得老身强健，尽驱使、明月清风。浣溪畔，先生醉也，拍手笑儿童。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4fc1496f0aecbaf67','金字经二首江上晚归','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"窗纸风偏透，衾裯冷不胜。欹枕挑灯诗又成。听。\",\"更鼓最无凭。同云暝。檐花滴到明。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4fc467c907f369e27','锦鹧鸪三更一笛一联，胡仲父落梅之句也。词人所称，予惜不见其全篇，遂为足之','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"梦断罗浮绰约丛。玉龙鳞甲缀帘栊。自孤花底三更月，却怨楼头一笛风。\",\"寒料峭，晓葱珑。劝君莫放酒杯空。雪儿清唱山香舞，要把霜毛晕醉红。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4fc8b0ba27986d05a','浣溪沙·欧园离席，与刘珥江同赋','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"雾鬓风鬟晚雨收。石城艇子似新秋。弯弯月出挂城头。\",\"桃叶桃根牵别恨，垂杨垂柳绾离愁。丽词都付雪儿讴。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4fd437b39ecd3f423','临江仙·丁未新正寄简西峃','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"元日晴明人日接，韶光淑气相仍。梅钿柳带似相矜。花游谁与共，绮语老犹能。\",\"一水盈盈遥望处，团团宝月初升。山阴剡曲兴须乘。十千沽绿酒，三五醉红灯。\"]',NULL,NULL,NULL,NULL),('e647d10f540153d4feb8bb1d61d69e35','卖花声别金治卿','杨慎','e647d10f540153d48a146f1a020c8bfa','明',NULL,'[\"骤雨打新荷。绿涨清波。风蒲猎猎映金沙。池上玉尊移晚兴，共倚林柯。\",\"莫惜醉颜酡。听我离歌。长亭回首碧云多。千里怀人思命驾，何日重过。\"]',NULL,NULL,NULL,NULL),('e647d10f5402fecedb16221545afdef7','浣溪沙·再赠梁姬文正','范文光','e647d10f5402feced1457b72207f8801','明',NULL,'[\"夙世刚修半面缘，西风吹上五湖船，秋来瘦骨倍堪怜。\",\"痴想只教魂孟浪，闲情空对影留连，相思同入药炉煎。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f640beaab05','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"江水东流更不回，行人白发苦相催。只恐明朝风雨恶，夜深灯烛亦须来。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f6429683694','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"黄家渡西多好春，黄家渡上酒能醇。看花吃酒唱歌去，如此风流有几人。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f643916bdb8','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"千株云锦照江沙，沙上青旗卖酒家。莫怪狂夫狂得彻，吴姬玉手好琵琶。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f643af98811','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"吴淞江上好春风，水上花枝处处同。得似鸳鸯与鸂鶒，时时来往锦云中。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f6453ade793','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"寒食清明正好春，看花须着少年人。少年看花花自喜，白发看花花亦嗔。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f646240cc71','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"水上花枝日日开，行人头白不能来。请看门外东流水，流向沧溟更不回。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f64645cc3cd','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"吴姬玉手好琵琶，少小声名到处夸。但使主人能爱客，年年来此看江花。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f649c797cd4','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"日日花前金叵罗，百年能得几经过。花开花谢人还老，狂客狂吟莫厌多。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f64b726fe74','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"江花红白最堪怜，莫惜看花费酒钱。他时白发三千丈，纵使频来不少年。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f64d6961fcf','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"松江水碧碧如天，水上行人坐画船。记得吴儿《竹枝》调，为君高唱百花前。\"]',NULL,NULL,NULL,NULL),('e647d10f560b919fb3bd5f64e8966a28','马益之邀陈子山应奉秦景容县尹江上看花二公竹枝歌予亦作数首','时太初','e647d10f560b919fd1457b72d1457b72','明',NULL,'[\"寄语看花江上人，清明时节好来频。清明节后多风雨，风雨颠狂恼杀人。\"]',NULL,NULL,NULL,NULL),('e647d10f567e3c338958f93e3672bdb2','南乡子·秋怀','褚附凤','e647d10f567e3c33d1457b72d1457b72','明',NULL,'[\"忽地秋催，又见黄花满径开。箕踞科头无个事，三杯，林下清风信快哉。\",\"何必徘徊，收拾天涯宦迹回。艺圃自存经济意，归来，重把门前五柳栽。\"]',NULL,NULL,NULL,NULL),('e647d10f568357d4d71a006029c123ea','蝶恋花·咏蝶','沈静专','e647d10f568357d4d1457b72d1457b72','明',NULL,'[\"舞向低檐依嫩绿，翠冷天涯，影断吹愁续。幻就云衣飞态足，粉烟浥露枝头浴。\",\"风动犹疑翻紫玉，引得佳人，误入花阴扑。静敛香须还自宿，蜂媒觅伴仍相促。\"]',NULL,NULL,NULL,NULL),('e647d10f56fb6068405ba8701a576021','竹枝歌','祁顺','e647d10f56fb60687895fc1355c567fd','明',NULL,'[\"朝出采菱日暮归，掘头艇子荡斜晖。无端惊起双鸂鶒，直傍南塘深处飞。\"]',NULL,NULL,NULL,NULL),('e647d10f56fb6068405ba870a9ef35b4','竹枝歌','祁顺','e647d10f56fb60687895fc1355c567fd','明',NULL,'[\"断岸平沙水绕洲，小溪都入大溪流。渔翁贩得鱼虾卖，日日鸣榔船步头。\"]',NULL,NULL,NULL,NULL),('e647d10f57aeaae7415d41bd83d1d88e','谒金门','赵金','e647d10f57aeaae7d1457b72d1457b72','明',NULL,'[\"湖天渺，一片水云沙鸟。俯景茫茫空懊恼，孤吟秋色老。\",\"自爱琅玕芝草，茅屋碧山环绕。高卧长松心尽了，年来机事少。\"]',NULL,NULL,NULL,NULL),('e647d10f5873909f1ce2ab6be807db40','西湖竹枝歌其一','马琬','e647d10f5873909fd1457b72d1457b72','明',NULL,'[\"湖头女儿二十多，春山两点明秋波。自从湖上送郎去，至今不唱江南歌。\"]',NULL,NULL,NULL,NULL),('e647d10f58a30f0d9cf491a1491e7832','忆王孙','葛一龙','e647d10f58a30f0d18d10dc684f0f204','明',NULL,'[\"春风吹后满天涯，系马高楼春日斜。归梦悠扬隔柳花，不如他，一路青青直到家。\"]',NULL,NULL,NULL,NULL),('e647d10f598e43ab6ca2965daa3aa707','南歌子·游仙词','王锡爵','e647d10f598e43abd63fbf8ca14ac55a','明',NULL,'[\"月色依微照，云光浅淡流。卷帘同上最高楼，试看海天万里、好清秋。\",\"酌酒金螺小，调笙玉指柔。更深鹤背冷飕飕，劝我今朝且住、莫归休。\"]',NULL,NULL,NULL,NULL),('e647d10f59a758dd154effbd4d2870a2','浪淘沙','顾同应','e647d10f59a758ddd1457b72d1457b72','明',NULL,'[\"生小教人怜，娇擘瑶笺，春来独自在窗前。谢女帘栊飞柳絮，秀句先传。\",\"香梦逗春烟，一晌留连，吟情多半为花牵。还忆从郎徵险韵，谱入冰弦。\"]',NULL,NULL,NULL,NULL),('e647d10f59a758dd34280826ef274c0d','柳梢青·萤','顾同应','e647d10f59a758ddd1457b72d1457b72','明',NULL,'[\"六曲窗纱，夜凉如许，秋在谁家。帘外星移，屋梁月堕，逗得些些。\",\"玉阶悄忆年华，曾几照、钗横鬓斜。长信宫间，摩诃池冷，光黯秋花。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea413db5dada62dc9df','西江月·咏雪','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"柳絮空中吹早，梨花槛外开迟。红炉翠幌玉堂西，欲堕因风扶起。\",\"望去一山明月，踏来几寸银泥。春光寒峭暮烟低，小立早梅香里。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea46ffdaf8acc6a4558','望江南','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"春光好，芳草绿初齐。新雨翠摇雕槛外，轻潮红涨画楼西。约伴著罗衣。\",\"何处去，桃李更芳菲。凤额帐深垂柳带，鸦头袜染落花泥。憔悴踏青期。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4791f17e573ffad56','忆汉月咏萤','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"芳草谁知前度，正值寒塘欲暮。因风零乱玉阶前，偷照绮罗香处。\",\"怜他垂柳外，千万点、长堤碧树。黄昏细雨情无人，管取夜深来去。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4972bc085cf5dedec','柳梢青·春雨','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"昨夜今宵，东风不断，细雨丝飘。暗锁银塘，微沾玉砌，草绿兰皋。\",\"落红一溜江潮，香断处、平芜画桥。杨柳烟低，梨花雪重，多半春销。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea49ef5ce70ab760162','醉花阴','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"病起闲愁消永昼，香钮松金扣。时节近清明，燕子双来，翠幄轻寒透。\",\"杏花新雨池塘后，莫道春依旧。昨夜更东风，一树海棠，零落胭脂瘦。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4a787a8dc69c46c23','秋蕊香·落叶','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"门外秋声不绝，簌簌空阶吹彻。寒枝影乱鸦啼歇，满院清霜斜月。\",\"和风带雨难分别，还凄切。绮窗敲处灯明灭，梦醒三更时节。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4b2f6a8bcfcf50c13','点绛唇·上元','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"车马三更，香风一带笙歌路。月华初度，万点春星曙。\",\"曾记当初，今夜相逢处。君休去，玉箫声住，帘卷梅花树。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4b85c36264f69b7db','少年游·晓别','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"五更天晓渐鸡鸣，斜月半床横。树杪风疏，马蹄霜滑，银汉带疏星。\",\"看他渺渺垂鞭去，望里不分明。一线江流，几层山色，千里隔长亭。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4bdf1fbc778ee852f','风中柳落花','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"一夜东风，南陌落红吹急。正芳时、画桥寒食。秋千庭外，挂游丝百尺。怎凄凉、粉愁香泣。\",\"半启朱扉，又是青阴如织。更凝眸、有谁怜惜。但教飞去，向铜沟流出。及早透、三春消息。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4c7167020777957bc','南柯子','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"杏雨新沾砌，梨花冷闭门。几重映色上湘纹，断送墙腰长日、又黄昏。\",\"澹澹斜阳下，微微碧树分。楼高莫倚数归人，望去浮云千里、怨王孙。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4f32f182ef9acc658','浣溪沙','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"水殿凉生咽暮蝉，亭台百尺映清泉。数声雨过小荷钱。\",\"纨扇轻摇圆似月，冰绡斜映薄如烟。鬓云撩乱堕香肩。\"]',NULL,NULL,NULL,NULL),('e647d10f59c47ea4fd66e8e6a47874ad','眼儿媚·咏燕','邵梅芳','e647d10f59c47ea4d1457b72d1457b72','明',NULL,'[\"东风一剪过池塘，曾识旧纱窗。乌衣未改，红妆如昨，稳上雕梁。\",\"年年花下相逢处，何事更商量。多应是诉，来时春短，别后秋长。\"]',NULL,NULL,NULL,NULL),('e647d10f59fd8948c0395c4d66f4b6e2','浣溪沙·红桥即事','杜浚','e647d10f41a62bc87a6a74cb7fea637f','明',NULL,'[\"曲曲红桥涨碧流，荷花荷叶几经秋。谁翻水调唱凉州。\",\"更欲放船何处去，平山堂下古今愁。不如歌笑十三楼。\"]',NULL,NULL,NULL,NULL),('e647d10f5a13513a7062abcb744e9293','诉衷情','金大镛','e647d10f5a13513ad1457b72d1457b72','明',NULL,'[\"西窗枯坐梦难成，憔悴对孤檠。今夕不知何夕，枕上有秋声。\",\"风淡淡，露盈盈，最无情。楼头短笛，江上寒潮，花外残更。\"]',NULL,NULL,NULL,NULL),('e647d10f5c3de5f215758c6c6be97662','满庭芳·有序','韩雍','e647d10f5c3de5f20ed9422383cdcec0','明',NULL,'[\"庆衍恭襄，贤追庄敏，天生一代忠良。早膺封拜，承命下南荒。尽展六韬三略，收蛮夷、尽数归王。守家法，清贫如故，四海姓名香。\",\"圣明忧国计，简司漕运，特授金章。总水兵，百万作镇淮阳。定缵先公勋业，足京储、永固封疆。还儗进，六师大帅，丹悃佐吾皇。\"]',NULL,NULL,NULL,NULL),('e647d10f5c8e5b661d7c000561193e64','减字木兰花·题日涉园','翁孺安','e647d10f5c8e5b66d1457b72d1457b72','明',NULL,'[\"白云深处。帘卷画楼含暮雨。玉树歌残。野鸟心闲独自看。\",\"门摇杨柳。高士纶竿今在否。尚想分题。林外烟岚翠不齐。\"]',NULL,NULL,NULL,NULL),('e647d10f5c8e5b66c2ebbe3b4981a9f7','鹧鸪天','翁孺安','e647d10f5c8e5b66d1457b72d1457b72','明',NULL,'[\"三月听莺绿已肥。香车常趁落红归。道旁蝴蝶能相逐，梦里鸳鸯不肯飞。\",\"惜别去，思依依。绣床犹剩舞时衣。博山熏透无人语，院落沉沉冷夕晖。\"]',NULL,NULL,NULL,NULL),('e647d10f5c8e5b66ee35f8b019e401f2','浣溪沙·秋日','翁孺安','e647d10f5c8e5b66d1457b72d1457b72','明',NULL,'[\"雁阵才过字一行，碧天沉影散秋光。凉风初沁白罗裳。\",\"堕叶井梧旋静砌，含英篱菊护低墙。声声何处捣衣忙。\"]',NULL,NULL,NULL,NULL),('e647d10f5d0518c61368c91442d3e9e8','临江仙','陆垹','e647d10f5d0518c6f414a83818ead4c7','明',NULL,'[\"病后衣裳慵对客，镜中双鬓鬖鬖。青山何处著茅庵。向来经济意，都只是空谈。\",\"世事悠悠那复论，三杯且放微酣。小窗过午睡还贪。无端春梦绕，犹自在江南。\"]',NULL,NULL,NULL,NULL),('e647d10f5d1119fa512ec1554b583eeb','玉漏迟·元宵奉陈静斋宪长','王韦','e647d10f5d1119fad1457b72d1457b72','明',NULL,'[\"今宵春正好，卷帘无那，客中情绪。连日眉梢，暂放片时还聚。薄宦辞家几许，叹骨肉、水萍风絮。留不住。纷纷客泪，两行如澍。\",\"缅想此日家园，正儿女团栾，烧灯时序。明月依人，空照一樽清醑。深谢东君慰我，喜投赠、玉堂佳句。高唱处。乳鸦乱翻庭树。\"]',NULL,NULL,NULL,NULL),('e647d10f5d1119faaffa1dfa79e4324b','柳枝词二首','王韦','e647d10f5d1119fad1457b72d1457b72','明',NULL,'[\"却笑梁园事已非，梁王去后燕空归。可怜园上多情树，风絮纷纷学雪飞。\"]',NULL,NULL,NULL,NULL),('e647d10f5d1119faaffa1dfaca4f4828','柳枝词二首','王韦','e647d10f5d1119fad1457b72d1457b72','明',NULL,'[\"渭水西来万里遥，行人归去水迢迢。垂杨不系离情住，只送飞花过渭桥。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf0053504b32d6ccb1','摸鱼儿·辛词烟柳斜阳之句宜其悲也乃尤有甚于彼者复用韵写之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"向西园、花飞一片，早已伤心春去。残红落尽更如今，难把流光追数。留不住。征鸿影、黄沙紫塞秦关路。从谁寄语。道有人独对，雨打梨花，看黏泥飞絮。\",\"倩流水，欲觅芳踪还误。津头风雨深妒。凄凉庾信江南赋，难向无情天诉。为楚舞。流不尽、楚歌血溅阴陵土。寸心知苦。望万里荒烟，一蓑渔艇，渺渺无归处。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf006231765d7b7368','点绛唇·碧桃','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"碧海凝霜，问谁唤作春光住。绿烟轻处。月染幽香聚。\",\"缥缈云魂，旧厌霓裳舞。天台路。山空月冷。不管刘郎误。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf020b445feda280d6','蝶恋花·秋感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"檐帽风轻收早稻。半卷黄云，半拥绿烟岛。雪鲫如银膏满脑。爪肪切玉铺霜早。\",\"袅袅长竿争打枣。靺鞨珠圆，颗颗香甜饱。受用苍天今已老。掀髯一笑邯郸道。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf03133a7830e7a813','鹧鸪天·杜鹃花','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"锦国春从恨里裁。云安涪万浅深开。山头万片□芳影，枝上三更结怨胎。\",\"红泪滴，血函埋。他时化碧有馀哀。伤心臣甫低头拜，为傍冬青一树栽。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf037463245e638883','春云怨春雨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"一宵风雨。将小窗曙色，朦胧勒住。惊起数声啼鸟，多在碧烟深重处。肥绿欺人，瘦红欲尽，流水无情向南浦。红药多憨，翠胎未吐，还遣花期误。\",\"问春拟向何方去。怕芳草天涯，萋萋非故。记得年时踏青路。燕子重来，梦里楼高，望迷烟树。桃叶难凭，流莺不管，自绾柳丝低诉。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf040065ff835a6327','捣练子·晚春','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"云似梦，雨如尘。花泪红倾柳黛嚬。也算人闲春一度，明年莫更不还人。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf05a649dd8310f08a','望湘人·归雁','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"到君生寒里，寒尽春边，惺惺还自分晓。烟水无涯，关河何处，长是梦魂飞绕。任不归来，也堪信宿，柰他幽悄。尽江南、草长鱼肥，难把寸心灰了。\",\"历尽墟烟晚照，更天低月暗，风凄云窅。脉脉自孤征，冷觑垂杨栖鸟。随处生缘，暂时留恋，只爱桃英娇小。问他日、万片秋声，谁领取江天缥渺。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf06dbf1d5a1032172','摊破浣溪沙·始春新月','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"嫩柳梢头挂一分，玉痕松浅绿烟轻。空里流霜荡漾，逼天清。\",\"瘦影徘徊还似梦，朦胧雪鬓未惺惺。却恼人间菱镜，太分明。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf075ce24e38df9bc3','东风齐着力忆别峰修竹为冰雪摧折','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"一片绿云，千条寒玉，亭亭孤上，非想有情天。记得雨馀残照，趁啼禽即栗横肩。迎眸处，捎云垂露，袅娜芉眠。谁道别、经年怨湘妃不语，我见犹怜。\",\"鲸波吞岭，欲遣变桑田。魆地一声去也，挽不住、环佩珊然。空想像，纸窗清影，淡写之玄。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf08b368ba7d1e6ded','南浦·惊秋','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"片云堆雪，却无端、飞影欲掠斜阳。回首西风起处，几叶上轻黄。莫问藕花凋未，看蜻蜓、点点度银塘。有前时双鬓，而今似否，青镜自商量。\",\"极目神州杳杳，只归禽、无数点微茫。销尽残虹，半折短剑蚀清光。便遣吾庐三径，在他时、枫叶不禁霜。祝掠云双燕，好将归梦绕空梁。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf0a3d5cf0fbc32f46','点绛唇·月桂','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"蟾馆飞英，随他月在秋长在。冻云如黛。幻出香严界。\",\"曾订青娥，有誓深如海。春光改。清芳缥缈。还与山矾赛。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf0b606fb2c1265d53','风流子·自笑','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"老夫无藉处，问今古、更有几人知。把红霞揉碎，挼成火枣，玉露团合，酿就冰梨。饱餐罢、擎篮盛夜月，添炭煮冰凘。一掐中天，星随指落，还从残腊，花促春归。\",\"秋风落叶里，扪碧霄、敲响玻瓈。大笑天翁白雀，输我偷骑。金弹惊开，幽窗啼鸟，玉笙唤起，茅店荒鸡。且殷勤属望，绝调钟期。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf0d6c46fe7863495b','菩萨鬘桃源图','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"桃花红映春波水。盈盈只在沅江里。湘水下巴邱。湖西是鼎州。\",\"停桡相借问。咫尺花源近。三户复何人。长歌扫暴秦。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf0f76f8da6b0d116a','点绛唇·牡丹二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"春未分明，觌面始知韶日晓。朱凝粉袅。费尽春多少。\",\"人在绿窗，艳影朦胧绕。碧天杳。瑞云回含。锦襭空青表。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf0f76f8dab1427655','点绛唇·牡丹二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"不道人闲，消得浓华如许色。有情无力。殢着人相识。\",\"阅尽兴亡，冷泪花前滴。真倾国。沉香亭北。此恨何时释。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf10a978a62a08901f','满江红·写怨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"离亭人散，折不了、柳丝垂绿。尽桃花、飞尽故枝，缘终难续。雁影更沈湘岸月，鹍弦谁奏燕台筑。只空山、剩得老青蓑，掘黄独。\",\"汗青照，文山福。紫芝采，商山禄。但荒草侵阶，修藤覆屋。井底血函空郑重，知音谁与挑镫读。问杜鹃、何日血啼乾，商陆熟。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1156a639bba87df3','菩萨蛮·遣愁','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"半生只伴闲愁住。如今却待驱愁去。归鸟没遥天。云横斜照边。\",\"乾坤看一笑。愁到何时了。抛掷与征鸿。霜宵唳晓空。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf12f6b39d4a54e5b0','渔家傲·为好送鱼苗谢之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"连雨前溪新涨弥。垂纶不上双双鲤。刚叹直钩钩不起。青蓑委。果然甚矣吾衰矣。\",\"雪脊霜鳞春燕尾。殷勤不待桃花水。曲沼逍遥萍叶紫。秋风里。熟炊香稻烹银髓。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf137f5edb36a3f1db','女冠子·慈竹','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"萧疏不可。簇簇绿烟深锁。一枝枝。\",\"乳凤梳翎细，游鳞蜕甲迟。天高难借问，风横怕相欺。且自团圞住，要谁知。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf179b0d6e13d2350a','生查子·秋感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"藕花艳银塘，玉露凋零尽。素蝶不知愁，波影弄金粉。\",\"飞飞飞不去，日暮西风紧。为惜白蘋香，怕受微霜损。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf17c31be9b6be3bcd','减字木兰花·忆旧二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"春溪水满。月向桃花香处暖。几叶芭蕉。绿影斜侵嫩草苗。\",\"碧烟日罩。遥听江城歌管闹。小步闲吟。一径苔阴万里心。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf17c31be9f874cff0','减字木兰花·忆旧二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"江湖短剑。醉卧不知谁野店。笑傲刀兵。月落猿啼客梦惊。\",\"寒更历尽。孤雁孤飞栖不稳。为问青天。锦瑟谁人续断弦。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf18260b4b766452a3','念奴娇·柳影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"半塘春水，早光透玻瓈，绿鬟倒亸。曲岸苔平铺径净，还惹晴风轻簸。叶叶衣凉，垂垂佩委，碎剪绿阴破。柔情不定，翠眉偏映双锁。\",\"为问庭院沈沈，画垣几曲，却放春愁过。巷陌斜阳偷送出，款款碧烟萦里。乱点征衫，横拖玉勒，似惜垂鞭堕。犹愁未稳，黄鹂更着一个。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf189e210d9cea107b','浣溪沙·过熊男公夜话','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"炊稻烟阑煮笋香。溪流萦岫半斜阳。闲宵不觉剪灯长。\",\"鱼计向春元得水，蝶魂入梦不惊霜。无劳濠上讯蒙庄。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf18db6d7df9179c3b','长相思·春夜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"花影低。月影微。一夜迢迢已半非。问天知不知。\",\"风满扉。露满衣。炉冷残香恋素帏。栖禽尚未归。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf19c492a30d93a287','沁园春·梅花道人题骷髅图澹归嗤其鄙陋为别作七首乃词异而所见亦不相远反其意作四阕正之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"为问蒙庄，卮言枉吊，笑尔何知。既使我其然，焉能免此，如君之说，抑又奚为。幸未凋零，先为飘荡，究竟鱼还死水湄。早辜负，郤桃花春水，杨柳秋堤。\",\"欲抛抛付伊谁。真避影、银镫只浪吹。便一枕蝶轻，还黏粉趐，三眠蚕稳，仍惹缫丝。去则难留，留原难却，一线纹生玩月犀。唯片晌，耐板桥霜迹，茅店荒鸡。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf19c492a32fed404a','沁园春·梅花道人题骷髅图澹归嗤其鄙陋为别作七首乃词异而所见亦不相远反其意作四阕正之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"当汝无时，原无消息，逗此风光。到云生月吐，旋相圆满，山支水派，不爽针铓。桂斧谁修，玉砂难碾，琢就玲珑七宝装。曾倩汝，为日轮炫紫，寒夜凝霜。\",\"成功底事难量。仍掷与、乾坤自主张。尽雪里梅开，凭谁蕴藉，风中柳摆，非汝轻狂。百折如新，一丝不乱，烟草迷离总不妨。珍重好，教大钧裁剪，鹤短凫长。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf19c492a35a1f3974','沁园春·梅花道人题骷髅图澹归嗤其鄙陋为别作七首乃词异而所见亦不相远反其意作四阕正之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"白日难欺，青天不爽，只此骷髅。到排场戏毕，尽停边鼓，熏炉烟散，郤剩香篝。无想有天，也须扣算，放自当年到此收。终不道，泛秋波一叶，随处芳洲。\",\"思量惭愧难酬。曾顶戴、春霖起白沤。忆香蒸云子，从伊饱满，轻裁霞绮，护汝温柔。莫倚无知，瞒他有眼，总付梧桐一片秋。应认取，者下回分解，别有风流。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf19c492a3fb26921a','沁园春·梅花道人题骷髅图澹归嗤其鄙陋为别作七首乃词异而所见亦不相远反其意作四阕正之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"毕竟还他，晓风残月，正好惺惺。看太白占星，显开玉色，黄钟应律，敲作金声。揖让筵终，征诛局罢，渠不增加汝不轻。堪爱处，为元龟受灼，枯槁皆灵。\",\"西园片片落英。也妆点、东风媚晚晴。任血洒虞兮，原非战罪，肠回康了，不碍文名。万石洪钟，一丝残纽，止此冰霜骨几茎。夫谁暇，怨华亭鹤唳，蜀道淋铃。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1a978e1896a80cc3','多丽·别恨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"悄年华，偏是流光难掷。梦回时、分明眼底，离亭杨柳初折。浑相忘、金微路远，与扳留、青组珠勒。天涯何处，漫生芳草，归来珍重怕逢啼。鴂重思、省元来是，梦生死关河。隔今生，永迢迢良，夜如何拚。\",\"得祗当、年华镫影，里鸳鸯绣带轻。拆怨落、花浪随流，水消尽、西园旧春。色孤馆黄，昏雨丝云，片苍苔满地无人。迹问青、天何意留，住孤鸾只教。空辜负，当年无限，山海恩德。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1b2075960b8dedc4','如梦令·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"花影红摇帘缝。苔影绿浮波动。风雨霎时生，寒透碧纱烟重。\",\"如梦。如梦。忒杀春光调弄。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1b207596ce9dbd2b','如梦令·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"如梦花留春住。还梦春随花去。一片惜春心，付与游丝飞絮。\",\"无据。无据。不觉梦归何处。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1b8b583e6c810b8f','摸鱼儿·咏霜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"向深秋、芦花风起，吹散一天珠露。六铢仙袂新裁薄，剪落冰丝千缕。轻盈舞。还只恐、素娥镜里深相妒。欲留教住。柰晴色窥人，断云送暖，蓦地催归去。\",\"更谁念，暗壁吟蛩正苦。遥向征鸿低诉。平沙野水朦胧影，难认白蘋香处。春期误。却早遣、啼鹃血泪沾江树。暝烟休护。又何似明朝，天低日淡，散作萧疏雨。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1e1a27bed5644241','念奴娇·竹影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"纸窗僧院，记湿云初散，秋山如洗。缭绕茶烟轻袅外，荇藻澹生空水。檐雀低飞，松鼯偷窜，欲压还扶起。兰风微展，泠然斜亸仙袂。\",\"不道活现金身，寻根拨叶，问几茎叠翠。断续晴岚飞片片，总入虚澄镜里。题字遮残，疏棂界破，历乱毵毵尾。闲心窥破，阑干何事重倚。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1f20e5092e0fc3fb','贺新郎·用韵寄题翠涛山居','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"君未同余老。任叔夜、崟欹磊落，玉山奚倒。偶借渔矶垂直钓，不许问律人造。岂但爱、清溪回抱。万叠碧簪青带绕，遍天涯、总是王孙草。笑一室，何须扫。\",\"平畴良稼怀新好。多种秫、黄云酿熟，香熏龙脑。画骏不临松雪谱，自宝思陵鹰爪。煮冰雪、自寻垆灶。闲写溪云山雨句，终不教、举俗怜丁卯。虽寒瘦，非郊岛。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf1f7e50f2af7a694d','念奴娇·梅影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"香魂近远，却早离枝上，暗黏芳草。为惜清姿全似水，幻作碧膏笼罩。侧坠蜂须，半凋珠颗，非被冰霜恼。侵阶零乱，素痕未许人扫。\",\"遥想萼绿仙姿，绣妆剪彩，那似晴光好。斜转回塘清浅里，脉脉相看微笑。冻蝶低飞，疑非疑是，无柰阴寒峭。暄风动后，浓阴任遣莺闹。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf20eeca3dc3c1637c','薄幸午睡觉问渠','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"当年是你，兜揽下、个侬来此。更不与，分明道止，竟如何安置。但随流，荡漾云痕，归鸿水底成人字。便俐齿嚼空，金睛出火，都则不关渠事。\",\"但惜取刹那顷，忍不得、秋瓜藤坠。逗杀人，为霜禁冷，为风禁泪，镇柳丝、轻摆摇春水。到历头垂杪，半酣不睬难驱使。无端薄幸，付与乌鸢蝼蚁。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf23cfaba47b3815c4','阮郎归·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"桃花流水紫云深。瑶京路易寻。人闲何事苦关心。匆匆下碧岑。\",\"才燕语，又螀吟。飞霜两鬓侵。玉壸鸳语信空沈。藤萝锁翠阴。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf23cfaba4c2c5cb13','阮郎归·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"当年只解爱桃开。寻芳得得来。仙娥容易许仙才。霞裳浪与裁。\",\"愁弄月，怕驱雷。繇来非圣胎。巫山别有楚阳台。行云梦不回。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf25665b364f3b1d58','水龙吟','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"轻舠直上潇湘五。湖载取风光去。兰汤初浴，绛罗轻解，鸡头剥乳。腻粉肌丰，苞香乍破，芳心暗吐。待笑他、石家赪面杨家，病齿虽天冶还。\",\"含醋曾倩绿窗，深护全、不教香泥微污。莫愁秋老侬家，自有杏金丹驻。唯应少府，无妻空老，长怜樊素。愿年年、岁岁相期，解佩蘋花洲渚。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2697b9c7727a1490','踏莎行·与李治尹夜话致身录事有感而作','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"几许兴亡，凭谁料理。血痕一缕留青史。从来白刃杀英雄，恹恹儿女丛中死。\",\"霜气飞空，星光堕水。闲宵半吐伤心字。他年莫问草堂荒，萧萧落叶随风起。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2713117535a90dfd','贺新郎·自题草堂','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"狼籍成衰老。唯馀此、数茎瘦骨，随风颠倒。满目江山无熟处，一曲林峦新造。何敢望、松萦竹抱。新绿半畦荒径侧，怕萋萋、仍是黏天草。钁头在，还须扫。\",\"东墙幸有冰轮好。到秋来、暖雪生眉，琼浆灌脑。人道森寒清彻髓，也是龟毛蛇爪。总拚与、寒灰冷灶。万顷烟岚窗纸暗，恰昏昏、?睡忘寅卯。阿鼻狱，蓬箂岛。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2755f58614ad57c7','黄莺儿·苦雨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"昏昏昏晓忘春曙。几尺幽窗，半掩苍苔，一抹青山，平铺飞絮。似酒醒乍扶头，镫灺难留炷。沈沈天海蒙蒙，早把韶光，暗里消去。\",\"拚与。白鸟荡银波，清猿隐高树。芭蕉几叶，莫有绿肥，轻寒依然勒住。想流水小桥东，密筱浓阴处。应有怨老莺声，细把闲愁诉。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2883f3043f2247d0','虞美人·问月','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"乍圆还缺相调弄。也是如春梦。有形毕竟有消时。只是较人差久、少人知。\",\"迟回闲恋花闲影。便道秋宵永。侬今何遽不风光。一晌清灵不散、万年长。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc910c1ec2bc','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"鸬鹚衔鱼只道饥，鵁鶄运目也孤飞。江花笑水郎不去，白浪掀天郎不归。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91227a976a','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"巫山欲出三峡中，狼山还隔海门东。何人不打开船鼓，何人解使斗帆风。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc917ca38237','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"嫩鹌鹑斗不相降，野鸳鸯飞不作双。昔年挑冰上雪岭，今年贩水下长江。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc917f7cf2f3','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"江边寒梅自着花，江上女儿自斗茶。浪向花前爇片脑，浪疑茶里点脂麻。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91c7d6aea9','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"黄陵庙前班竹丛，巴邱湖上水连空。生受鹧鸪啼夜雨，生成江兔拜秋风。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91d2a90ac2','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"暮雨朝云神女峰，三朝三暮黄牛踪。入峡来回看不厌，出峡对面不相逢。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91e64909d0','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"巫山不高瞿塘高，铁错不牢火杖牢。妾意似水水滴冻，郎心如月月生毛。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91ee143f60','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"杨柳湾头?舸开，杨花飞雪逐船来。郎爱杨花随舵转，侬怜杨柳倒根栽。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91f2a49b93','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"荷花有日绽金须，荷叶无心系水珠。浪打萍开从恼妾，萍留浪去恰怜渠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2997dc91f5f12e13','竹枝词十首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"洞庭湖北汉水横，青草湖南湘水清。两道湖光争潋滟，两般艇子各分明。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf29996ea68dd94715','醉春风咏蝶','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"红影纷无数。偷眼斜回觑。阑干十二绣帘垂，住。住。住。乱蕊繁花，熏人无奈，非侬留处。\",\"绿影轻风度。芳草天涯路。翩翩清梦自逍遥，去。去。去。纵使飘零，依然不似，轻狂柳絮。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2a78cd1ebe2011a9','风流子·春感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"春光又到也，全不惜、霜鬓不禁愁。问归飞双雁，江南塞北，冰融风软，何处堪留？莫轻弃、鱼波翻縠皱，鹭影挂银钩。但爱风高，黄沙眯目，为贪月冷，白草含秋。\",\"垂杨摇荡处，绾不住、绿水去悠悠。饶有荇牵翠带，难系浮沤。看彻踏青，眉皆黛蹙，相逢折柳，泪总珠偷。叹千金春价，一刻难酬。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2b828e6789a0b8c7','满庭芳·初夏','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"颗颗梅珠，条条菖叶，不须更怨春归。垂杨风软，练鹊一双飞。自爱芳塘素影，与波光、上下争晖。闲日永，新来病浅，相赏莫相违。\",\"当年还似此，年华虽老，不道今非。问灵均，去后谁剪荷衣。日暮轻云凝绿，遥天回、笑揖湘妃。还应有，芙蓉出水，妆点钓鱼矶。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2d783add26d647b4','满江红·家兄倾背后诸君见慰重叠恤其衰病有逾量之奖含泪作此答之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"丁宁千遍，教绾住、一枝飞絮。柰伶仃、孤燕归来，黄昏自语。纵使长条堪系马，栖鸦风冷斜阳暮。问前时、流水绕桃花，今何处。\",\"沤已散，难重聚。镜已破，难重觑。听子规唤道，不如归去。他日天台花再发，人间自有刘郎遇。便痴迷、蝶梦不教醒，终无趣。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf2de656fa5eda405e','水调歌头·惊梦','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"昨夜喧雷雨，一枕血潮奔。千红万紫，撩乱浪唤作芳春。大抵白蜺婴茀，更有玉蟾偷药，蓦尔弄精魂。宝剑在侬手，闪霍动乾坤。\",\"尽今生，梨花雨，打黄昏。历历悄悄，山寺钟声曙色分。待我铁眉刷翠，罩下金睛点漆，弹指转晶轮。十里杏花发，人道是样云。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf33cf46bdfc6633b6','诉衷情·近秋望','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"遥天一碧，回望西飞白鸟。乍临镜水摇空，又向莲峰弄影。风起素光斜映，薄袂萧清，悄觉凉襟迥。双眸炯。\",\"今古寸心孤另。亭皋一叶，坠响枫林静。还重省、闲愁元鸟，归迟幽绪，藕花香冷。梦到今宵永。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf351d4e7e3f45cb25','苏幕遮·翠涛以新诗见怀作此答之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"老犹惭，愁不死。燕子衔来，无限伤心字。春色三分还似此。和雨和烟，了郤韶光事。\",\"有如痴，仍似醉。短剑光销，红蜡倾珠泪。一片潇湘东下水。谁遣无情，长惹飞花坠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf371d240ef75bb4f0','沁园春·翠涛六□每句戏用彩色字','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"步碧萝湾，寻紫芝英，寿翠涛翁。忽逢赤松子，赴青女约，染千树绿，作小春红。微启朱唇笑余白，发黍米丹砂岂未逢。若会得，则翠涛偕尔，稳住绛云。\",\"中蔚蓝天色清，空但净洗黄，尘即阆蓬。看漫天黑瘴，徒然黮?，当头皂帽，自许从容。素书宵授元文密，印彤云原自擭雏龙。凭寄语，展双眉黛锁，霜老苍松。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf39e099dacafaa0b0','双双燕·除夕忆家兄','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"荒山百里，想残雪初晴，应同消受。莫还似我，祇共寒垆相守。重叠山河冷泪。更梦对、团圞春昼。读书帷里华镫，献寿堂前椒酒。辜负。\",\"当年人说。道仙苑芝英，一时三秀。山移海涸，别是人间花柳。还听金鸡玉漏。怕天也、妒人长久。但念今夕相怜，莫问明年健否。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf3a2fc915fb5471fd','潇湘逢故人慢赠李简尤初度简尤方五十髭髯如雪已十馀年矣','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"潇湘一曲，记前时相念，此地相逢。掀髯一笑中。问霜雪因甚，早上青松。多情自昔，阅桑田、沧海重重。有吴市、洞箫孤奏，碧天吹裂晴空。\",\"酌君酒，为君舞，怕长沙、袖短举手难工。把袂且从容。但五弦挥罢，目送归鸿。随时梅酒，又何有、今古英雄。莺花在、年年三月，绿杨柔亸春风。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf3d4023f3548cb02f','喜迁莺·元夕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"龟纹云展，逗月影、照人清善。徼幸春光，等闲换郤，银海玉山万片。不道残梅，香尽似惜柳条绿浅。更念我一茎瘦骨，风丝欲剪缱绻。\",\"笑年年岁岁，长伴韶华转。野烧烘朱，疏星炫采，一点山镫光颤。良夜人闲似此，莫问琼壸近远。痴来自笑衰翁，受用东风消遣。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf3eaf4659dab4f86f','清平乐·鹧鸪','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"但南无北。费尽丁宁舌。说与天涯行不得。也似欲啼清血。\",\"空山烟雨霏微。离披败叶低飞。乳燕莫夸轻俊，人间何处乌衣。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf3f9832239c6b23c1','念奴娇·咏蝶','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"流莺啼遍，唤不醒芍药，丛中双蝶。待把红香留住稳，无待雨狂风劣。流水浮萍，垂杨飞絮，都是闲枝节。殷勤为甚，飘零了不孤怯。\",\"纵好绣带云裳，来回弄影，荡芳情一捻。日暮帘垂归燕语，池上晚风猎猎。渺渺天涯，归魂何处，便是烟花劫。无人说与，阳关梦里三叠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf40a92aa3b9cf75bd','念奴娇·帘影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"莺啼报曙，早惊觉绣帷，星眸欲炫。起看瓶花香渐喷，的皪深红如颤。衣剪轻罗，参差三五，恰透冰肌现。钗光闪烁，翠鬟怕有撩乱。\",\"恰好细数湘纹，无端搅动，柰飞来双燕。还忆夜香烧罢后，妒杀玉蟾光满。何似疏筠，半笼晓色，一尽来回便。檐阴未半，银钩且莫高卷。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf40d1e45615299775','哨遍广归去来辞〇苏于瞻檃括归去来辞陶公之馀潘也吾自有大归大去而大来者为期未知远近然知迟迟之不如接淅久矣因借其韵以自抒己怀','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"一领青蓑，一柄长镵，也是闲牵累。归去来何处，可言归，旧家山目。前即是知，者稀谁堪就，问津路莫将黄叶。迷童稚凭冷，觑春花闲，看秋月苍天伎。俩止此笑乾坤两扇。半开扉任柳絮穿帘。扑面飞既，不黏泥自，然轻脱唯。吾，之意噫归。去来兮纵横万里。人间世嚼匫。\",\"{匸内龠}橄榄来回甘。苦知味弄二，月轻雷散，一天暮霭倾倒银。河香水酿就，蜂蜜惊回，蚁梦丈夫当如。此矣昭，昭白日亭午。时驾玉虬停骖一问。之向虞渊可容转。计烛龙今在何，处料也难酬。答但斟北斗天，浆满斝恣。我花前沈。醉归来斩尽一团，疑胡不归漫留止。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b0036a650e','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"启琳宫、㬉回溪畔，江南共道春早。桃花新雨溶溶后，谁把琼浆酿造。壸天老。正望中、茶烟几线萦僧橑。燕泥香扫。快翠泛铜瓶，膏凝玉盏，鱼眼调香脑。\",\"添胜迹，百道奔泉回抱。暗萦绿荇芳藻。泠泠碎玉夜声中，花院晨钟轻捣。君莫恼。君不见、玉矾落尽瑶京道。王孙芳草。纵百丈络丝，万条罗带，难系春光好。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b00ab092d2','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"向苍崖、蓬声吹裂，斜阳一片危岸。江流北泻雁南征，洞里春光无算。星灿烂。都应是、云中剑舞珠光按。花蹊棋馆。留满地苍苔，数峰烟树，掷与人间看。\",\"仙户启，石乳倒垂银蒜。空山翠杳天半。百花桥阻玉壶远，谁倩鸳鸯抵唤。君莫叹。君不见、彤云故锁三山断。罡风吹散。想华表鹤归，天台人返，怕见人民换。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b014b9dcc0','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"翠屏遥、眉横七二，空青一派如洒。闲登危榭凭阑眄，垂幂同云覆野。银河泻。道飞来、杨花玉树都疑假。望迷鸳瓦。问酒拥销金，茶烹凤乳，若个偿春价。\",\"赏心事，不在酒旗歌社。梅痕暗偷萼赭。冻髭笑指祝融君，故骑火龙未下。君莫惹。君不见、胡沙似雪催征马。笳声怨也。把塞北冰天，江南春色，都付渔樵话。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b0435aa1c9','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"插青天、俯临图画，一壁翠光欲滴。炎风吹断阳禽影，认得孤峰回翼。如相识。记寒声、萧萧咽尽霜华夕。望中何极。尽帘压千丝，窗飞一缕，垂幕笼轻碧。\",\"回首处，犹记当时踪迹。危亭斜倚南陌。满城春滑笙歌腻，消尽银缸夜色。君莫惜。君不见、黄沙汉使无消息。秦关坐隔。听沙岸残更，野塘晓阵，总似三生笛。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b08fe8b158','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"剪中流、白蘋芳草，燕尾江分南浦。盈盈待学春花靥，人面年年如故。留春住。笑浮萍、轻狂旧梦迷残絮。棠桡无数。尽泛月莲舒，留仙裙在，载取春归去。\",\"佳丽地，仙院迢遥烟雾。湿香飞上丹户。醮坛珠斗疏镫映，共作一天花雨。君莫诉。君不见、桃根已失江南渡。风狂雨妒。便万点落英，几湾流水，不是避秦路。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b0a300bf31','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"瀁平塘、绿钱初展，正是乳鸦啼后。桃尖渐放朱霞晕，芳惹游鞭征袖。真如绣。点波纹、红绡青幛团绒皱。田田歌奏。问苦菂寻莲，萦丝觅藕，几许多情逗。\",\"莫浪语，西子湖头难又。锦屏十里香透。繁华满目江南梦，约略送愁时候。君莫嗅。君不见、清香销尽酸风瘦。秋容如旧。只莎草吟螀，蓼花飞?，露冷金飙骤。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b0a93a1cb5','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"瞰蒸湘、曲影双清，流下洞庭秋远。危崖突兀玉峰寒，界破苍流一线。谁许见。只鲛宫、金绳夜拥鱼龙怨。画船歌扇。对笑水江花，窥楼晕月，惹尽流霞片。\",\"行乐地，记取韶光迅转。画阑彩笔题遍。云杳潇湘千顷碧，瞥眼武陵溪畔。君莫羡。君不见、渔阳挝断霓裳宴。沧桑已变。想眉黛娇青，眼波凝绿，不是旧时面。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41b6f8b0dadfafc5','摸鱼儿·潇湘小八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"截流澌、飞虹侧挂，长空流月孤影。斜收返照蔚蓝天，蘋末一江风冷。更漏静。罢鸣桹、停桡沙觜星光耿。疏镫夜永。似说向人间，流光易谢，夜烛游须秉。\",\"歌舞罢，追赏良宜此景。木兰牵动绿荇。樵青唱阕斗参横，残月又斜西岭。君莫省。君不见、芦中人老成萍梗。载愁舴艋。向牛饮溪边，羊裘滩上，别把丝纶整。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf41d6eb9770711358','尉迟杯·冬景','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"夕阴凝。分一半霜月空霄影。危柯风吼鼍吟，料得栖禽难定。眉云黛蹙。问何事、带浓愁不醒。也应他、倦看天涯，万里平芜烟暝。\",\"休道柳㬉花娇。便秋色、碧梧犹恋金井。早似十分，摇落也谁，待怨玉露当时。薄幸但、随风几片。丹枫还、逞飘零情性。听孤鸿、暗度三更，幽魂脉脉孤省。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf473d1e1d2cc8ead4','摸鱼儿·东洲桃浪，潇湘小八景词之三','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"剪中流，白苹芳草，燕尾江分南浦。\",\"盈盈待学春花靥，人面年年如故。\",\"留春住，笑几许浮萍，旧梦迷残絮。\",\"棠桡无数。\",\"尽泛月莲舒，留仙裙在，载取春归去。\",\"佳丽地，仙院迢迢烟雾。\",\"湿香飞上丹户。\",\"醮坛珠斗疏灯映，共作一天花雨。\",\"君莫诉。\",\"君不见桃根已失江南渡。\",\"风狂雨妒，便万点落英，几湾流水，不是避秦路。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf4793f9414c345313','念奴娇·姜斋影〇衰病弥月一切尽遣拥火枮坐心无所寄因戏作诸影词引半缕活气令不分散孤镫下忽见婆娑在壁因念人知非我之无彼不知非彼之无我也留连珍重旋与评唱一阕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"孤镫无奈，向颓墙破壁，为余出丑。秋水蜻蜓无着处，全现败荷衰柳。画里圈叉，图中黑白，欲说原无口。只应笑我，杜鹃啼到春后。\",\"当日落魄苍梧，云暗天低，准拟藏衰朽。断岭斜阳枯树底，更与行监坐守。勾撮指天，霜丝拂项，皂帽仍黏首。问君去日，有人还似君否。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf4cad725ed2dbf053','小重山·讯游人','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"闻道韶光色色新。青骢骄玉勒、踏芳尘。河桥嫩柳折殷勤。全不管、叶叶翠眉嚬。\",\"莺语咒含嗔。子规催不转、未归人。东皇若肯惜馀春。红药在、重染茜香匀。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf4d57d10acff1e206','点绛唇·和林和靖韵咏草','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"旧日青山，霜风吹后谁为主。招魂何处。棠杜春皋雨。\",\"杜宇声中，休怨芳年暮。凝眸去。萋萋无数。仍满天涯路。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf4e10dbf5d375a246','谒金门·春怨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"云无数。遮断春光来处。更着廉纤千嶂雨。罩住和烟树。\",\"社燕佳期已误。他日归来应诉。旧识垂杨芳草路。总被深寒妒。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf4f65d12ed263ed8f','瑞龙吟·别恨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"天涯远，凝望迢递层阴，夕阳一线。几番拚不相思，曾种情苗，待谁割断。初相见，也似轻薄杨花，随风飘转。无聊且付闲心，灯下香前，自寻幽愿。漫道天台桃径，重逢刘阮，残红一片。又随流水飘零，凋残故苑。\",\"盈盈脉脉，烟锁芙蓉巘。劳想像、扇影低回，佩声缱绻。梦里啼莺，似把游人唤。又恐春期浅，辜负却、翩翩画梁双燕。此情谁欣，一帘春晚。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf4ff9cb30d5fb2bb0','一剪梅·答须竹所问','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"北海残碑数字传。前日烽烟，今日苍烟。湘灵雁柱鼓湘川，欲扣清弦，还恐惊弦。\",\"闲心遥寄水云边。莫问湖天，自有壶天。忘忧春暖锦堂萱，好种芝田，且看桑田。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf50a91141db723a53','念奴娇·波影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"晴江春晓，但摇曳落花，溶溶东下。谁送素绫千皱褶，直上画檐鸳瓦。镜面回镫，月轮浮雪，一色飞光下。游丝婀娜，吹来似欲相惹。\",\"一霎汞走珠流，应有渔舟，兰棹纵横把。迸散一天星斗乱，不觉银河西泻。惊碎还圆，轻匀稍淡，移向荼蘼架。日高风静，潇湘依旧如画。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf52f01f580594903a','渔家傲·翠涛作煨榾柮诗索和以词代之六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"炉影圆红烟尾曳。汤瓶渐送松声碎。不是村醪无米兑。难成醉。酸风昨夜新伤肺。\",\"青镜古今多少泪。无缘却向寒崖坠。明月在天霜满地。愁也未。两茎铁脊舒腰睡。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf52f01f582cd62c5c','渔家傲·翠涛作煨榾柮诗索和以词代之六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"膏腻孤镫花蕊褪。寒灰渐缩霜绞印。坐待一丝红影烬。眠不稳。梅花梦也无凭准。\",\"十九年来毡啮尽。归鸿望断长安信。莫叹苏卿霜染鬓。渠非困。多他一倍还添闰。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf52f01f5870b47a95','渔家傲·翠涛作煨榾柮诗索和以词代之六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"残火星星容易灺。鹿头虎爪相撑架。渐次囫囵低复亚。难消夜。凭谁细数兴亡话。\",\"正闰参差王与霸。妖狐也把骷髅挂。肉馅馒头人瓮鲊。都休也。猎人不怕猩猩骂。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf52f01f58903d9189','渔家傲·翠涛作煨榾柮诗索和以词代之六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"薄日渐消霜径粉。荷锄细把枯株认。惜取山?休蹙损。春事近。香风遣透幽窗喷。\",\"橡斗藤花诛已尽。杜鹃无蕊盘根稳。斸得归来刚一畚。煨教烬。明年莫更啼春恨。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf52f01f58a8590bab','渔家傲·翠涛作煨榾柮诗索和以词代之六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"鹁鸽桃纹干炼熟。枣膏胡粉和香筑。凤翅狻猊填绛绿。红光簇。貂珰门外神茶矗。\",\"残梦京华难再续。空山雪子埋牛目。几橛枯株鹰爪秃。谁人斸。跛奴运斧还伤足。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf52f01f58a8fe7613','渔家傲·翠涛作煨榾柮诗索和以词代之六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"双髻峰头千树缟。轻松钟乳玲珑罩。相聚团圞围土灶。愁欲倒。而今郤忆愁时好。\",\"不怨融和春不早。春云怕障邯郸道。旧梦不成人愈老。流莺恼。凭谁唤醒粘天草。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf5539dec232429c4e','临江仙·水仙花','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"道雪无香都不是，峭寒一点春融。昨宵烟月水涵空。粉肥略滞影，绿弱不禁风。\",\"遥与碧窗人似玉，檀心深锁重重。倚阑初日照憨慵。春纤珠串坠，仙袂素罗松。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf566de1397f812dc8','谒金门·小除夕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"年华骤。闪闪孤灯依旧。半盏春前村酝酒。病里还消受。\",\"一剑天风孤吼。千里暮云铺就。不惜镜中人易朽。只笑恒河皱。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf56a55f6e347090a0','粉蝶儿·咏霜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"问天涯，有几多寒情冷绪。怕春来、游丝飞絮，更因风，黏住了，江天红树。弄轻盈，勾引教飞去。\",\"悄不知，小桥西荒鸡催曙。月斜时、揉碎一天珠露，苦恁将，酸风勒住。尽凄凉，拚与寒鸦低诉。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf58c92cb4c57b4c7f','添字昭君怨友人刘懿庵营虎塘颇胜没后鞠为茂草赋此寄叹','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"燕垒粉垣香坠，螀馆蓼花红碎。行人莫唱水东流，水西流。\",\"柳外画船箫鼓，帘外梅龙烟雨。当年原倩遣闲愁，惹闲愁。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf59c82a57818925eb','浣溪沙·冬望','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"缥渺云丝展碧空。孤峰峰影影孤松。天南天北雁来风。\",\"极目江山无止竟，伤心日月太从容。霜枫依旧半林红。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf5a6de74bce9b7a43','长相思·落日','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"海天宽。暮烟残。才得团栾对面看。还沈山外山。\",\"人倚阑。鸟飞还。回首东风一碧寒。婵娟欲上难。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf5bde19711626eb4f','菩萨鬘除夕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"相移不觉春前岁。相连不断风前泪。泪巳到今残。乾坤醉梦闲。\",\"紫山横白霭。幂历天如海。花月有时新。难留霜鬓人。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf5d415bf92838d5cd','归朝欢春晚','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"不道春光消不得，风风雨雨落红积。馀芳一片尚嫣然，倡条冶叶谁相识。幽情还自惜，向他时，断虹残雨，留取韶华迹。游丝袅袅娇无力，难凭远觅春消息。\",\"也知春只在天涯，杜鹃莫诉春归急。柳带烟如织，从容绾住东流疾。任桃花路杳，天台不道仙源隔。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf60836054115a236c','长相思·春恨二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"莺乱飞。花乱飞。总爱春光去不违。谁知春事非。\",\"风满扉，雨满扉。坐待元禽暮不归。孤云绕翠微。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6083605447fd82b2','长相思·春恨二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"待寻芳。懒寻芳。褪粉梨花背夕阳。清明未断霜。\",\"游丝长。柳丝长。欲绾春光春又忙。辛夷早绽香。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf628ea83fc03a7565','高阳台·蛛丝落叶','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"拚却无情，更谁厮惹，西风檐角萧萧。留看人闲，斜阳一晌光摇。蘋花应笑归期误，又争知、孤绪无聊。被殢人，轻蝶憨憨，片影相撩。\",\"天涯一梦迢迢，任露侵衰草，月冷危桥。便随尘土，离魂不倩人招。前时难忆藏鸦处，有归飞、孤雁低邀。望谁为，并刀一剪，剪断情苗。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf62e9fdce82f4aef5','十二时慢蟋蟀','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"问古今，闲愁有几，长与秋来相惹。到月暗、梧桐疏影。木叶泠泠初下。便遣清宵，都无万感，犹自难消夜。偏旁砌、侧近西窗，冷絮闲叨，不管残镫花灺。从伊谁，问知幽恨，长在眉梢萦挂。\",\"似诉风凄，如悲露冷，斗转银河泻。莫念人幽悄，殷勤特相慰藉。不分明，伤心句里，听得又还争差。待拥孤衾，灭镫塞耳。可放离忧罢。未多时更被，荒鸡数声啼也。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf63214d830c41e607','忆王孙·蜂投窗纸掇遣飞去戏祝之寓意','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"白云送望转霏微。别径人间去不违。好逐桃花一片飞。\",\"阮郎归。莫问天台路是非。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6461414e5a16f1a9','念奴娇·影戏影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"笑啼俱假，但绰约风流，依稀还似。半壁粉墙低映月，卖弄佳人才子。情丝牵引，清光回照，漫道伤心死。猛然觑破，原来情薄一纸。\",\"应是缥渺飞仙，当年窃药，落在银蟾里。半面人闲高处望，传与霓裳歌吹。有意留仙，难禁夜短，还怕镫花坠。迷楼吐燄，倩谁挽住香袂。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6809859bb332af5d','水调歌头·放言','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"我自非卿法，何事为卿愁。非卿之法，卿抑可不为吾忧。若汝三更半觉，而肯一参万岁，月也应含羞。弄影浮云上，圆缺总清秋。\",\"数花须，黏柳絮，汝风流。差排蚁国，虾岛立地凤麟洲。既道尼山出世，又召东山入梦，镜里是真头。烱烱明看汝，更上一层楼。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6c47a49fe6097f26','天香·馀冬雪霁念早梅应开病中不得寻访怅然有作','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"霜薄含冰，云轻弄雪，未遣珊珠红褪。夕坞回塘，谁借问早，把琼心幽酝也。应待我向，月下微吟相讯自。惜自怜孤影半，吐半垂清晕来。\",\"时早莺啼破几，点点玉钱绿衬。便与寻芳已晚，粉残香损。犹自不成辜负，却只恐、东风垂柳困。梦里依然，蝶魂相趁。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6d9de6f6aa199a32','摊破浣溪沙·病中与刘李二生夜话','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"曾把榆钱买少年，少年已是柰何天。为忆杨花未落，已啼鹃。\",\"阅尽闲愁都是梦，不知残梦在谁边。赢得如今蚕老，已三眠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6e091bd889d199eb','碧芙蓉阙题','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"风定小窗明，半睡初阑，双眉乍展。追数馀寒，浪过春半。花有恨、一枝不稳，柳如人、三眠巳倦。莫更惜几日韶光，催取玉矾香满。\",\"还愁轻暖处，更一丝云茧。横掠山腰，虽则暂时消散。怕黄昏、玉绳低挂，又参差、碧绡斜卷。青天如梦，倩取百啭啼莺唤。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf6efd0d0e3e32d9a9','生查子·春感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"梨花一片飞，飞落春衫袖。莫漫爱馀香，春去君知否。\",\"青郊骢马儿，浪醉旗亭酒。不是不催归，宝鞭在君手。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7002f938740cb0b0','点绛唇·飞花','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"不是人闲，定然无个藏春处。和风和雨。抵死惊飞去。\",\"暖日晴风，犹自难留住。天涯路。金勒驹骄。踏作红尘土。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7121d770e1c90f2e','苏幕遮·暮春月夕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"雨初收，春已尽。多幸婵娟，还照烟霄暝。弱柳摇波波不定。绿影层层，蹙损金枢晕。\",\"尽闲愁，谁借问。花落花开，难向今宵认。犹有馀香吹陈阵。珍重清辉，莫映残云隐。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf71e0a93b2813cbcd','西江月·春日野兴','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"草绿黄犉归晚，溪平白鸟飞低。小池春藕半沈泥。渐近梅酸天气。\",\"昨日社逢雨止，今年闰放春迟。寒深茸母短芽稀。未与山厨做美。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7337cf865e71d9d4','摸鱼儿·病后作','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"问还馀、莺花几许，容吾酬尽春债。向来淡墨写虚空，也须添朱着黛。真狡狯。拈一片、落英欲揉韶光碎。泼天无赖。拚古庙香炉，荒村迓鼓，社夥从新赛。\",\"痴绝处，瘦骨披襟潇洒。倚阑目断天外。归禽谁戢双双翼，栖稳垂杨烟霭。空惭愧。喷热血、心肝摘出从人卖。寸丝留在。便棠杜云寒，白杨风起，不道馀香坏。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf73b7b72d1a223393','望江南本意一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"江南忆，钟阜杳霏微。佛子献来金堵窣，功臣长侍玉罘罳。\",\"缭绕五云飞。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7478c0d95a988e6a','玉连环述蒙庄大旨答问者二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"彀中游羿，莫漫惊、宠辱浪生规避。原自有、万里清空，可无影而藏，不飞而至。黑白两端，算都是龙泉轻试。但涂中曳尾，刃发新硎，全牛皆废。\",\"无涯有涯交累，唯饵香药作，不黏滋味。消彼此、百种聪明，向白日青天鼾?熟睡。侧足焦原弄獶虎，不殊豚彘。笑弈秋着着争先，居然钝置。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7478c0d9bd24c064','玉连环述蒙庄大旨答问者二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"生缘何在，被无情、造化推移万态。纵尽力、难与分疏，更有何闲心，为之偢睬。百计思量，且交付天风吹籁。到鸿沟割后，楚汉局终，谁为疆界。\",\"长空一丝烟霭，任翩翩蝶翅，泠泠花外。笑万岁、顷刻成虚，将鸠鸴鲲鹏随机支配。回首江南看烂漫，春光如海。向人闲到处逍遥，沧桑不改。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf75da712d7da11fc0','浣溪沙·冬景','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"款冻额黄晕浅檀。金罂颊醉半腮丹。妒他临老更芳颜。\",\"落叶泠泠清影瘦，败荷剪剪绿云乾。争教不怯晓来寒。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf77ffaefe235db9e6','玉楼春·归雁','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"秦关楚水天涯路。唯有归鸿知住处。经时巳换蓼花洲，依旧难忘芳草渡。\",\"南天回首苍烟暮。寄语元禽归也误。垂杨千树乱啼鸦，谁听呢喃清昼语。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf78704347893709f1','女冠子·卖姜词○余旧题茅堂日姜斋此更称卖姜翁非己能羡聊以补人之不足尔戏为之词且卖且歌之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"卖姜来也。谁是能酬价者。不须悭。\",\"老去丝尤密，酸来心愈丹。垂涎休自闷，有泪也须弹。最疗人闲病，乍炎寒。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7c5951ec339243b5','浣溪沙·秋感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"风剪芙蓉坠晚香。冲波难认旧鸳鸯。秋宵渐永尽思量。\",\"几度相逢唯梦里，疑非疑是不端详。鹭鸶空带满头霜。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7c817fe66752b45a','二郎神七夕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"秋生处还，只恐佳、期无据俯。杳杳人、闲几点疏，镫乱流萤低度。莫有多情应似我，向蛛网、含愁轻诉。方信得经年，此夕带水，桥成堪渡。\",\"休误。荏苒凌波，迢迢西浦。费乌鹊高飞，心力倦奈，尘世荒鸡催曙。回首盈盈，青女下似，笑我凄凉庭户。算自有，银潢几许年华，玉颜非故。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7c8577d99aa6c0e9','忆秦娥·本意怀仙','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"骖鸾去。还来太华峰头住。峰头住。不远人闲，迷蒙烟雾。\",\"神山无蒂飞鲸渡。迢迢紫海清都路。清都路。天酒花倾，云韶鹤舞。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf7d1e454e36c13280','捣练子·咏愁','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"刚有绪，又无端。细雨千丝月半弯。一寸眉棱千障压，不教人放片时闲。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf80b765396dc9340e','扫地游忆旧','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"微霜碾玉，记日射檐光，小窗初透，夜寒深否？问素罗新裁，熨须铜斗。闲揽书帷，笑指砚冰，蹙皱香篝，有黄熟篆销，芳膏结纽。\",\"自惹闲愁后，对莲岳云压，苔潭珠溅，垆烟孤瘦。叹渺渺京华，不堪回首。碧海人归，雄剑谁怜孤吼。空凝望，绕湘流、暮云荒岫。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf85001596e01b722a','二郎神咏镜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"碧潭清浅，又早被、蘋风吹动。惊鬓影参差，归来欲倦，试向菱铜闲弄。何处相逢似曾相，识一面、闲愁偷送。多情甚白祫，朱颜直至，而今相共。\",\"还恐。尘生碧海，元云暗涌。也似我年来，桃花春水，咫尺烟迷秦洞。总不分明，犹应记得，笑语当时恩重。谁忍见、半规流落人间，别邀新宠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf883916d6c615a000','贺新郎·上巳前一日雪','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"春自无愁耳。笑无端、一枝浅绛，半林新翠。便拟东风相宠惜，暗约柳丝萦系。乔相赚、落霞片紫。斜日烟笼沈海底，早蛟风、乱剪云皴碎。鲛泪冷，弥天坠。\",\"寒凘渐结流觞滞。便拚得、兰亭休赏，龙门堪醉。糁径不遮春草绿，略染西峰螺髻。删不了、朗清天气。乍与碧桃争粉素，柰铅华、俄顷成流水。今视昔，应如是。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf887192daf62a300f','忆江南·寒月四首一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"寒月迥，霜气护婵娟。眼晕乍临秋水镜，眉黄初学远山烟。不尽使人怜。\",\"清赏处，犹自忆华年。移几当轩添兽篆，卷帘呵手拂鸾笺，人在玉山前。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf89726254231815d0','蝶恋花·衰柳','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"为问西风因底怨。百转千回，苦要情丝断。叶叶飘零都不管。回塘早似天涯远。\",\"阵阵寒鸦飞影乱。总趁斜阳，谁肯还留恋。梦里鹅黄拖锦线。春光难借寒蝉唤。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8b5dfc6e7e7db963','长相思·石榴','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"蕊珠宫。碧霞封。风裂云痕一线空。繁星数点逢。\",\"素绡重。隔帘栊。丹荔新餐玉液浓。杨妃病齿红。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8c0af19e1edd191f','点绛唇·矮桃','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"绛襭盈盈，墙头怕惹游人顾。幽情谁诉。芳草萋萋处。\",\"燕眼偷窥，早倩绿云护。勤嘱付。低飞蛱蝶。莫惹香泥污。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8c26f86b3fe308bf','忆秦娥·子规','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"幽魂咽。蜀天泪洒春江血。春江血。东下湘灵，哀弦夜月。\",\"春归还诉春前别。天荒地老情无歇。情无歇。唤不归来，他生此劫。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8c384f1738b94241','忆秦娥·病中寒卧二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"酸风恶。寒梅不管丹枫落。丹枫落。当年也似，梅含朱萼。\",\"侵窗云暗晴珠夺。压肌衣重腰弓弱。腰弓弱。曾闻衰老，而今方觉。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8c384f176562daad','忆秦娥·病中寒卧二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"寒无那。冻云乍裂晴难果。晴难果。纸窗低觑，庞眉深锁。\",\"年华容易催人过。寒威荏苒留人卧。留人卧。乍梦春生，鸡声啼破。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8c86cc8c56604466','诉衷情令·春景','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"东风亭午摆垂杨，丝丝日日长。吹落梅花无数，不解惜幽香。\",\"落霞紫，暮烟黄，送斜阳。无凭天气，一霎融和，蜂蝶空忙。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8d3b8280a7bfee6e','添字昭君怨春怀','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"茸草偏生南浦，桃叶半遮芳树。东风何用苦相催，缓缓来。\",\"刚倩柳绵黏住，又被落红勾去。麟洲闻有返魂香，海天长。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8e31d62ee8bfee92','天仙子·元夕','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"垂垂冻雨凌珠滴。阿谁唤作镫花夕。当时有梦到今圆，孤烟幂。残膏腻。寒山不着东风力。\",\"才领取归鸿嘹呖。又消受残梅狼籍。人间昨日是元宵，人非昔。天难识。明年消尽中原历。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8e5d06edf3889967','十六字令落花影二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"落花影，款款映春江。终相就，贴水不成双。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf8ee8eb136fe3a4ef','玉女摇仙佩霜叶','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"云容初皱，鹦老梳翎，微露狸红丹咮。迤逦金风，轻寒不耐，心绪还如中酒。莫是怜秋瘦，贳韶光薄媚，春归还又。念一自、红药凋零，杜宇三更，啼血而后。拖逗得、伤心染就残痕，到如今消受。\",\"还想拒霜木末，蓼花江岸，晚艳于今未久。半折残虹，一湾夕照，郤早送人僝僽。零落还知否，唯应伴、几叶淡黄衰柳。又恐怕、青娥娇妒，不肯留侬住，朱颜非旧。重回首，雨馀荒草堆红绣。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf907a0c2f4735f892','念奴娇·松影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"擎云缥渺，有五鬣仙姿，凭谁细数。半顷黄茸茵藉软，移上素琴冰柱。缕缕丝丝，断纹交映，细写双清谱。疏光逗漏，幽香几许暗度。\",\"一霎云散西清，迟迟送上，到回峦高处。垂霭霏微深黛转，似把归禽低擭。雾冷山空，一枝斜闪，犹在寻香路。夕阳易没，轻阴且趁归去。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf937e9419333e2eea','蝶恋花·春景','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"薄日蒸云光影碎。白霭蒙蒙，罩住春山睡。一树梨花轻雾里，玉山斜倚冰绡醉。\",\"半吐山礬香孕蕊。小步寻春，好了莺花事。莫待楝花花信至，子规难挽东流水。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf94c17f7f158eda08','如梦令·小游仙二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"天酒玉童斟送。秋水小鬟低诵。今日与他生，一径琼壶花动。\",\"非梦。非梦。波动月原不动。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf94c17f7f87e1c264','如梦令·小游仙二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"一色花飞莺报。五色烟轻云罩。梦里屡骖鸾，只是今生未到。\",\"谁道。谁道。只有人闲春好。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf94c9094d4ebf6381','凤凰台上忆吹箫·忆旧','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"楚塞天遥，漓江雨冷，烟云湿透征衣。指数峰残雪，候雁先归。堪叹生生死死，今生事、莫遣心违。家山里，一枝栖稳，碧草春肥。\",\"依依。旧家枝叶，梦不到岘山，风雪霏微。念镜中霜鬓，人老渔矶。指点棠梨春雨，犹应化、白蝶双飞。孤飞也，寒烟幂䍥，镫火荆扉。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf96ccf1577c945eac','烛影摇红·十月十九日','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"瑞霭金台，琼枝光射龙楼雪。群仙笑指九阊开，朱凤翔丹穴。云暗雁风高揭。向海屋、重标珠阙。彩鹓飞舞，日暖霜轻，小春佳节。\",\"迢递谁知，碧鸡影里催啼鴂。骖鸾不待玉京游，难挽瑶池辙。黄竹歌声悲咽。望翠瓦、双鸳翼折。金茎露冷，几处啼乌，桥山夜月。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf97f813e564f03837','谒金门·示意有授','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"幽绪悄。欹枕梦回分晓。俯仰乾坤霜月皎。此际愁多少。\",\"欲洗长空天杳。袖里青虹光袅。相倩一挥云汉表。莫打闲之绕。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf99d292e78bd508e6','满江红·直述','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"泪冷金人，渭城远、酸风痛哭。君莫笑、痴狂不醒，口如布榖。堕地分明成艮兑，通身浑是乾坤肉。耿双眸、黑白不模糊，分棋局。\",\"千钟粟，谁家粟。黄金屋，谁家屋。任锦心绣口，难忘题目。为问鹤归华表后，何人更唱还乡曲。把甲辰、尧纪到如今，从头读。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf9cb94e171a10e69d','如梦令·春闺','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"抛郤瓶花蒂软。坐待盘香线断。不见也还休，门外莺忙蝶乱。\",\"谁卷。谁卷。一轴画帘银蒜。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cf9cc424a3b5fb6b48','谒金门·待须竹','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"闲不得。闲得也应相即。除却镜中霜鬓识。更谁通消息。\",\"多病年来无力。翠嶂层层相隔。珍重杨花风起日。觅浮萍踪迹。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfa1c7a74a65fd9632','念奴娇·雁影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"关河迢递，恨塞日昏黄，难传标格。沙净水澄清澈好，点点萧萧历历。晓镜修眉，清波皱影，淡写层层碧。分明瞥见，孤舟莫弄渔笛。\",\"贴水浴鹜争飞，凌乱波纹，未放千双直。风裂云痕开夕照，还与芦花相即。野岸霜前，南楼月下，应恨无人识。晓来无据，教人空望天北。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfa1d36e75f2bac859','如梦令·春后寒雪不已','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"粟起镜中珠饱。光射窗中花扰。消遣一衰翁，鳊项缩来欲槁。\",\"休恼。休恼。昨夜春山先老。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfa28a087f64fa3941','祝英台近·残梅','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"有馀香，无别意，难向东风说。前夜朦胧，一晌邀霜月。元来却上柳梢，平沈碧海，拚不管、幽芳凄绝。\",\"情无歇，苦禁断雨斜阳，望寻香冻蝶。漫无消息，归燕空饶舌。便判取艳坠珠楼，烟飞紫玉，也难待、杜鹃啼血。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfa471626569ff1ac6','尉迟杯·闻丹霞谢世遥为一哭','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"离愁远。恨漓水不逐湘流转。萧萧寒雨天涯，南雁一声惊断。闲悰无数。都付与、似水并刀剪。忍今生、死死生生，总难片语分判。\",\"追忆云暗苍梧。也则是、风光本色消遣。裸戏谷泉，雷电里莫，更有耶娘生面。今且向、垂杨暮雨。鹃啼处、咒残春一线。想依然、还我伤心，归舟天际相见。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfa596d2a86505bba6','菩萨鬘早春','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"梅花绽锦催寒去。霜花铺粉留寒住。去住两悽然。衰翁惜岁年。\",\"烧根红茁草。巳报春生早。莫惹晓莺啼。啼时花乱飞。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaae81c4e332db08c','生查子·咏史六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"龙凤是何年，人间瞒不得。空谷无人行，且喜似人迹。\",\"可怜松雪翁，不惜天水碧。马腹君自投，芳草嘶南陌。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaae81c4e44f1f8b5','生查子·咏史六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"阿姨骂不瞋，为怕鹦哥骂。猫儿杀鹦哥，才卜归魂卦。\",\"堂堂灵隐僧，桂子香清夜。五字万年碑，竟是谁天下。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaae81c4eae882deb','生查子·咏史六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"青衣抱玉觞，独向苍天哭。天有无情时，历乱双鹅扑。\",\"杜鹃啼不休，商陆子难熟。流泪一千年，血迹西台续。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaae81c4eb13c2ad3','生查子·咏史六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"沙中奋一椎，飞影不知处。知非赌命场，不下千金注。\",\"蒲山电眼儿，约略知其趣。豪气未能降，长揖关朗去。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaae81c4ee307a48a','生查子·咏史六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"长平十万人，一夜秦坑杀。鱼死浊流中，不祭乘时獭。\",\"死坑未是愁，唯有生坑恶。眢井埋蟾蜍，欲跳只三脚。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaae81c4ef6b0b800','生查子·咏史六首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"千秋铜雀台，肠断西陵妓。谁念故园空，豆蔻含胎死。\",\"清漳自东流，粉黛愁难洗。分得馀香归，骄杀邯郸子。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfab891ffda9f3ee60','十六字令元夕见月','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"雪面女，耐冷卸罗衣。盈盈出，争赛粉香肥。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba257440c26b2fd','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"打鼓津头知野戍。万里归舟，认得云中树。日落长沙春已暮。寒烟猎火中原路。\",\"何处停桡深夜语。江黑云昏，莫向天涯去。旧是杜陵飘泊处。登山临水伤心句。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba257442c3b363f','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"谁倚磨崖题彩笔。记得中兴，仙李蟠根密。万里湘天开白日。晶光长射蛟龙室。\",\"欲泛扁舟寻往迹。路隔丹梯，水弱罡风疾。日暮湘灵空鼓瑟。猿声偏向苍湾出。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba257444b096863','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"瑶草难寻仙苑道。洞里春生，一霎韶光好。圆牖茏葱迎始照。天涯一点红轮小。\",\"无那云煓迷海峤。断送金乌，闷损飞光倒。纵有晶荧开霁昊。斜阳又被寒烟罩。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba257448545067d','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"九岭参差无定影。泪竹阴森，回合青溪冷。一片绿烟天际迥。迷离千里寒宵暝。\",\"香雨飞来添碧凝。认是当年，望断苍梧恨。东下黄陵知远近。西崦落日回波映。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba257449b832796','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"日落天低湘岸杳。迎目茏葱，独立苍峰小。道是昭王南狩道。空潭流怨波光袅。\",\"绿影寒澄春放棹。记得当年，渌水歌年少。明月南枝乌鹊绕。登楼何处依刘表。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba25744a135d3f5','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"为锁湘流东下缓。两岸芦梢，片片蒲帆短。寒水茫茫渔火乱。唯应曲里春风㬉。\",\"万古沅湘秋思满。六六湾头，绾住愁无算。无那湖光都个管。鼍风吹浪连天卷。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba25744b58516e5','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"湘水自分漓水下。曲曲潺湲，千里飞哀泻。冰玉半湾尘不惹。停凝欲挽东流驾。\",\"百尺危崖谁羽化。一捻残香，拈插莓苔罅。忆自寻香人去也。寒原夕烧悲馀灺。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba25744c94a21ee','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"蒸水东流湘水北。一曲沧浪，映带青山色。旧是朱陵仙洞客。鹤归不向乌衣国。\",\"江树迷离潭影侧。画槛筠帘，梦断春消息。击鼓冯夷寻未得。馋龙怪舞云生墨。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba25744d98f395a','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"见说随帆瞻九面。碧藕花开，朵朵波心现。晓日渐飞金碧颤。晶光返射湘江练。\",\"谁遣迷云生绝巘。苍水仙踪，雾锁灵文篆。帝女修眉愁不展。深深未许人间见。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaba25744f8882dd6','蝶恋花·潇湘十景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"渺渺扁舟天一瞬。极日空清，只觉云根近。片影参差浮复隐。琉璃净挂青螺印。\",\"忆自嬴皇相借问。尧女含嚬，兰佩悲荒燐。泪竹千竿垂紫晕。宾鸿不寄苍梧信。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfadc9e82ea7eee322','金人捧露盘·和曾纯甫春晚感旧韵','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"古嵩台，双阙杳无踪。忆潮平、细浪溶溶。龙舟渡马，依然先帝玉花骢。冲冠发指，旗挥星落，血斩蛟红。\",\"怨苍梧，班管泪，沈白日，瘴云中。更背飞，孤影飘蓬。今生过也，魂归朱邸旧离宫。苔残碧瓦，鸳鸯碎，蔓草春风。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfae66f7c801a36c7e','昭君怨·本意','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"千古英雄一泪。只在琵琶声里。冷笑看功臣。画麒麟。\",\"娇面胡风吹皱。拚与红颜消受。赤凤不知愁。汉宫秋。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfaf69c0d07b66b1a5','念奴娇·蝶影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"纨衣试暖，乍圆印苔钱，旋添花晕。药径日高风力软，细逐落花低陨。微掩香须，别裁黄袂，金粉应初褪。雕阑重叠，玉阶未遣全认。\",\"小院午睡方醒，扇罗乍觑，似扑芳魂损。侧转忽如秋叶舞，又向碧纱窗隐。垂柳参差，红芽掩映，梦也无凭准。殢人凭槛，凝眸无那娇困。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfafa7440b19c10cb5','西江月·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"湘水悠悠北去，章江渺渺东流。清光拂剑碧天秋。情寄一杯浊酒。\",\"落月倩谁留住，长江又送新愁。小孤潮阻散花洲。露冷长堤衰柳。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfafa7440b2278b6d9','西江月·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"曾忆龙沙孤泊，将军祠下霜寒。凉辉浅浅挂西山。半破金枢拂岸。\",\"羌管声中鸟梦，藤花影里渔湾。婵娟不管泪阑珊。还送数行归雁。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfb0d5d0d55c4cd6d4','西江月·重过续梦庵','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"残梦当年欲续，草庵一枕偷闲。无端幻出苦邯郸。禁杀骑驴腐汉。\",\"几度刀兵膋血，十年孤寡辛酸。潭龙?睡太痴顽。欲续衰年已懒。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfb30be8ccdf804b5e','念奴娇·南岳怀古','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"井络西来，历坤维万叠，丹邱战垒。万折千回留不住，夭袅龙骧凤起。云海无涯，岚光孤峙，绾住潇湘水。何人能问，问天块磊何似。\",\"南望虞帝峰前，绿云寄恨，只为多情死。雁字不酬湘竹泪，何况衡阳声止。山鬼迷离，东皇缥渺，烟锁藤花紫。云璈无据，翠屏万片空倚。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfb4d9dcf9ccfb0960','御街行·上巳','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"迷烟迷雨教春困。不道是、清明近。轻寒曾忍柳风狂，只待东君花信。红药芽娇，山丹胎小，依旧无凭准。\",\"兰亭今昔何须问。消不尽、新亭恨。强将客泪付流觞，逝水难酬春怨。莺莺燕燕，花花草草，目送韶光褪。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfb5ae204b023d1007','江城梅花引·病中口占示刘生','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"和镫和影一双双。耐凄凉。尽凄凉。天也难教，病骨老来降。还欲其天争旖旎，空碧里，弄轻狂、竞飞霜。\",\"飞霜。飞霜。夜何长。有难忘。自难忘。梦也梦也，还认得、烟水微茫。待把疏星，斜月与分张。一叶暗飘风不定，飞去也，尽飘零、在回塘。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfb5ce73970a2bbd47','贺新郎·寒食写怨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"绵上飞乌恨。更龙蛇、追随四海，一时惊散。回首五云金粟地，不见玉骢珠汗。但扑地、苍烟撩乱。酒冷饧香休道也，梦石泉、槐火成虚幻。棠杜雨，离肠断。\",\"岳云回望莲花巘。尽凄凉、延陵十字，难酬幽愿。昨岁杯浆浇泪后，拚付寒螀莎馆。正夜雨、松杉绿满。一径苍苔行迹杳，想鸺鹠、夜哭鼯鼪窜。寸草尽，春晖短。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfb8c41329adc02d48','怨王孙送春','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"野水，苍树。落红飞絮，芳草长堤。垂杨古渡，只是无人。解惜春归雁。\",\"欲传云里字将谁。寄拚与。闲愁死天远。水远山远何处。相逢梦魂中。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfbb0604736e3a0a12','沁园春·浑天毬','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"黍子匀黄，吉贝衔绒，莫问因谁。但既在于中，自堪细数，未超其外，不得旁窥。暗去明来，偷寒送暖，无情还作有情痴。相怜甚，教碧绡帘幕，翠拥珠围。\",\"知恩今古人稀。犹自把、双镫照路迷。更划断鸿沟，不随毂转，调成玉律，一任清吹。彩缕交萦，金梭互掷，珍重天孙织锦机。莫辜负，听毬门箫鼓，对打争驰。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfbd80146386c7155f','卜算子·咏傀儡示从游诸子','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"也似带春愁，却倩何人说。更无半字与关心，吐出丁香舌。\",\"红烛影摇风，斜映朦胧月。铅华谁辨假中真，皮下无些血。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfbe7b673e79a423cd','霜天晓角·怀旧','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"平湖春水。日落扁舟舣。话到伤心深处，双泪落、青樽里。\",\"天不。留愁绪。拚遣愁人死。刚有一丝春怨，又花落、鹃声止。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfbe7b673ed8f8349f','霜天晓角·怀旧','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"清秋晚角。斜日横云脚。剑射灯花坠紫，双影瘦、征衣薄。\",\"今日。梦中语。当时难卜度。唯有丹枫霜叶，点点血、还如昨。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc340a11a09b2e5e3','浣溪沙·李花二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"前日冰花万树飞。晴风初解晓寒威。谁铺琼蕊更霏微。\",\"却怪乍来双燕子，涎涎尾掠素光肥。应如雪夜待人归。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc340a11abfbe5145','浣溪沙·李花二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"流水前湾晓镜清。粉香玉润素绡轻。欲邀月姊伴飞琼。\",\"不可久留仙佩冷，天桃争赛妒颜赪。江梅残梦正相迎。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc5acca8c140c8646','摸鱼儿·自述','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"当年事、也随风起，片帆一晌轻挂。云闲江树霏微处，早爱青山如画。停桡也。又郤有、蘋花菰米香低亚。难消良夜。且月载金枢，波分素练，饱看银河泻。\",\"入佳境，茹蘖居然啖蔗。千金难酬春价。娟娟蛱蝶花闲戏，不怕黄莺絮骂。谁真假。已早似、光风霁月连床话。千蹊万岔。则堪信堪疑，欲歌欲泣，狂谱从人打。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc6cb568cb970b534','念奴娇·云影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"层峦飞映，似缥渺神山，因风离合。簇簇青莲轻度过，却绕孤峰三匝。极目空明，遥天无际，瑶镜微笼匣。平沈远树，翠光略吐尖甲。\",\"曳仗闲倚亭皋，微飙下拂，便凉生一霎。荷芰半塘新绿暗，忽染素罗轻袷。回首吾庐，午烟遥接，一幅青绡裛。数竿修竹，凤梢还与低压。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc6ee2212ed250714','金明池新柳','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"多幸烟和，无妨风细，袅袅自怜春软。娇小甚、鹅黄绽处，但一叶两叶初剪。拚多情、拖逗波光，映岸侧、镜影苔茸深浅。总冉冉相依，摇摇不定，约约匀匀低转。\",\"不问花期今近远。只着意韶华，殷勤欲绾。忍微寒、待莺已久，向落日、藏鸦不惯。定谁知、无限柔情，对残月晓风，翠眉难展。只片晌停凝，经时荡漾，独自来回消遣。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc8368325aa7c9c68','惜馀春慢本意','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"似惜花娇，如怜柳懒，前日峭寒深护。从今追数，雨雨风风，总是被他轻误。便与挥手，东风闲愁，抛向绿阴深处。也应念、曲岸数枝，新柳不禁飞絮。\",\"争遣不、烧烛留欢，暗邀花住，坐待啼莺催曙。怕燕子归，来定巢栖，稳不解商量细。语未拟扳，留长久乍，雨乍晴繇来无。据待荷珠，露满梅丸，黄熟任伊归去。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc8c183310e395069','清平乐·咏雨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明','这哪里是在咏雨，分明是词人抚世伤心的热泪！过片假写池中鱼儿，骂尽天下随波逐流浮沉之辈。结句既是富有哲理的人生感悟，更是对世态炎凉的深沉感喟。顾炎武曾有“亡国”与“亡天下”之辨，这首词即为“亡天下”之音。','[\"归禽向暝。隔断南枝径。不管垂杨珠泪迸。滴碎荷声千顷。\",\"随波赚杀鱼儿。浮蘋乍满清池。谁信碧云深处，夕阳仍在天涯。\"]',NULL,NULL,'《清平乐·咏雨》是明朝文学家王夫之创作的诗歌。作者假借咏雨来抚世伤心的热泪，假写池中鱼儿，骂尽天下随波逐流浮沉之辈。','[\"暝：指空漾灰暗的天色。\",\"赚杀：赚煞。意谓逗煞。言雨滴水面，鱼儿疑为投食，遂被赚接喋。\"]'),('e647d10f5d7f17cfc9791961ac0fedec','行香子·游珍珠岩','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"流水淙淙，涧草茸茸。转苍湾午日霞烘。赤城围玉，紫盖擎空。试问仙翁，今何在，绛云封。\",\"芒鞋几两，青山踏偏，乍桃溪一曲相逢。平田下望，雾霭烟蒙。谁人知我，极远目，送归鸿。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfc9c770b266e3fbf9','丑奴儿令和李后主秋怨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"当年谁送江南怨，云树悲秋。舴艋含愁。月影消沈玉一钩。\",\"无数蜻蜓飞晚照，红蓼梢头。款款嬉游。水冷蘋花带影流。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfcab6372b7389b2bb','水龙吟·莲子二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"平湖渺渺波无际。难认旧时青盖。荻絮横飞，蓼红斜炫，秋光无赖。拚不含愁，韫香密裹，泠泠珠佩。伴江妃、泪颗盈盈怕谁，厮恼幽房里深。\",\"深缀十斛明珠，谁买空、望眼悬愁碧海。露冷昆明霜凋，玉井兰舟罢采。自抱冰魂，海枮石烂，千年不坏。莫抛掷、一点孤心，苦留得秋容在。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfcab6372bedbe32c3','水龙吟·莲子二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"白蘋红蓼秋江岸。早有蜻蜓斜睨。须褪蜂黄，乳含菽玉，恹恹倦起。乍历金风，谁知渐老，蜜房深闭。应只似、天宝鸡坊皂衣，人在空记得霓。\",\"裳事零零玉露，含愁漫、阁住泪珠难坠。无情忍见蹴碎，菱花啄残菰米。自惜残香，又逢劈破，辰离烟水。问多情、碧藕抽丝，可得经时重系。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfcd32913448d005c8','鹧鸪天·白莲','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"敛束檀心吐半丝。迟回妆靓暗香吹。绿窗独倚珠千泪，团扇斜窥玉一规。\",\"清露浅，碧烟微。闲心自照镜光知。鹿门漫作无情句，月冷风清欲坠时。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfce2b005835a75ad8','清平乐·问刘存孺索香橙','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"秋光已尽。月也幽香褪。只有霜膏团紫晕。仙雾兰浆深酝。\",\"小窗读易初晴。药炉风软烟轻。莫遣先生午睡，凭教鼻观惺惺。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfce378e427efdd592','忆秦娥·蓼花','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"秋江渺。秋心独展幽芳悄。幽芳悄。护臂纱轻，注唇丹小。\",\"芦花风乱汀洲绕。采芳人远知音少。知音少。几叶渔船，一轮残照。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfcfda91816d7b475c','眼儿媚·春深始见梅花','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"不知深浅几春宵。犹自见春娇。暗藏红粟，斜分绿影，全罩冰绡。\",\"当时曾有孤山约，客梦已迢遥。馀香应惜，东风渐老，野水平桥。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfd2745e70aea16014','望海潮·本意','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"红尘如此，茫茫沧海，吾将谁与言归。蜃雾腾虹，龙珠炫紫，波光天外霏微。宝日涌初晖。经烟霾万里，云锁千围。依然不改，晶轮激火夹天飞。\",\"吹箫人鼓馀威。将吴宫旧怨，血洒灵衣。怒遣天吴，滥驱海若，长风奋驾支祈。淫姣责江妃。将平沙尽洗，仙草滋肥。属目轩然一笑，人在钓鱼矶。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfd3c0486eb6207428','摊破浣溪沙·夏景','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"隐隐轻雷挂断虹，平田高下与溪通。溪外罩鱼人影，落波中。\",\"屐齿泥融双印直，葛衣风漾一襟松。回看西峰峰外，半青红。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfd5bf3d168dbacf38','如梦令·写恨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"恩重玉簪金管。愁尽玉鱼金椀。莫道只今生，万岁难消春恨。\",\"泪眼。泪眼。题颗零零珠串。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfd61a14de3eb765e3','菩萨鬘述怀','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"万心抛付孤心冷。镜花开落原无影。只有一丝牵。齐州万点烟。\",\"苍烟飞不起。花落随流水。石烂海还枯。孤心一点孤。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfd968aef56693bb3d','满江红·藤蓑词','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"一幅藤蓑，遥领取、江门风月。钓竿把、孤舟独泛，沧波喷雪。倚棹腾腾吹笛去，冲风直犯金鳌窟。有些些、闲事不关心，同谁说。\",\"镫一点，渔歌歇。天一碧，归禽没。买村醪自酌，醉梳华发。扑岸芦花飞已尽，雁声不咒西风劣。又何妨、拳足橛头中，霜衾铁。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfda2e3b665a5f34a8','浣溪沙·山行','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"曲曲屏山翠幕垂。啼禽不拣浅深枝。逗人斜转竹林西。\",\"绿玉竿柔愁露浥，翠烟叶密耐风欺。溪光一半染玻瓈。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfdb2c10f8a906f7d6','念奴娇·对镜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"闲愁自昔，到如今当得，雪欺霜负。云亚天低抬不起，随意白衣苍狗。剑跃双龙，笔摇五岳，也是闲筋斗。蝶黏蛛网，丝毫动得还否。\",\"别有一线霏微，轻丝杪忽，系蟠泥秋藕。一恁败荷凋叶尽，自有玉香灵透。眉下双岩，电光犹射，独运枯杨肘。无情日月，也须如此消受。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfdb9bc4ddbe9332c2','念奴娇·帆影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"西风落照，看渺渺沧洲，烟波无际。一段轻阴相趁转，不许兰桡惊碎。略带神鸦，时飘芦叶，曲映江枫紫。凭遮客鬓，怕教照见憔悴。\",\"有人独倚危楼，沙际迟回，疑是归舟系。已过轻鸥飞起处，不见旗悄燕尾。荡入中流，斜侵隔岸，依旧萦沙嘴。回头北渚，唯馀塔影孤峙。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfdd14d3872f98f017','一剪梅·春晚','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"妒花风雨在花前。开也堪怜，落复何言。愁魂斜亸泪珠悬，弱蒂难坚，红影难圆。\",\"何事迷香双蛱蝶。浪向东园，魂梦相牵。谁知不是养花天，试听啼鹃，春在谁边。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfddbef29056a06e6d','浣溪沙·即景','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"幸草犹馀几段残。烧痕斜插野鸡斑。灰堆无数米家山。\",\"双眼瞢腾疑梦觉，一天晴雨两阑珊。不愁也索带愁看。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfdf9c2190cd3c6d4a','长相思·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"宝刀分。宝钗分。绣带分开坠画裙。馀香犹自温。\",\"锦鸳群。野鸳群。三十六双认得真。旁人莫漫嗔。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfdf9c2190ceab65ed','长相思·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"不思量。也思量。密语何曾得话长。惊鸿夜渡湘。\",\"真成双。假成双。侬似莲心苦薏藏。怜他菡萏香。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe01ff3f8501cc731','添字昭君怨秋怀','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"谁染千山乌桕，欺杀一株衰柳。微霜何似镜中霜，万丝长。\",\"蜡屐从今几两，冰级当年屡上。夜阑星汉耿清空，挂残虹。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe07db50eff92ba72','摊破浣溪沙·偶兴','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"为讯南朝燕子飞，人间何处有乌衣。不是旧家庭院，也争归。\",\"低啄絮泥防易落，不知疏柳坠残晖。还与一双蝙蝠，傍檐飞。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe2f8faa9e5208ddf','昭君怨·咏柳','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"莺啭上林春软。蝉噪隋堤秋晚。一样系兴亡。碧丝长。\",\"夜雨盈盈千颗。点点清波滴破。不但翠眉嚬。泪珠匀。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe49a5f4c273be2bd','捣练子·咏霜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"孤月上，夕风微。忍得寒生玉粟肥。征雁千双空里过，可能带得向南飞。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe49a5f4ccb92a69f','捣练子·咏霜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"空无际，月无痕。悄悄盈盈上草根。蜻蛚逼窗镫影绿，愁人不道不消魂。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe4ac03aca9f37194','醉太平·冬夜','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"霜枫坠丹。风窗送寒。心知明月光残。转墙西小湾。\",\"巍巍豹关。迢迢玉山。梦回还在人闲。听鸡声夜阑。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe4d19d8e444cecf3','八六子·花朝夜窗中见月','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"荡春寒，一轮娟娟，初上烟暖云柔。柰镫穗影含金粟，药垆声泣寒螀，凄然似秋。\",\"无端还与绸缪。几线疏棂界破，半襟白袷光浮。想溪外梅花，低垂瘦影，斜窥流水，香魂欲绝，应共我遥向素蛾写怨，青天碧海悠悠。\",\"不禁愁。灭镫拥衾去休。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe585d5aa5ce860c6','望江南本意五','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"江南望，渺渺似云中。五色秣陵芝作盖，三山北固海吞虹。\",\"今古几英雄。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe730d05f3dea4470','渔家傲·樵歌二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"煨芋喷香斟卯酒。扳萝不怕寒崖陡。黄叶飕飕麇鹿走。君知否。夜来野烧霜枯后。\",\"林外炊烟青一绺。斜阳又转苍溪口。莫怪逢仙柯已朽。眈棋久。人闲残局难丢手。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfe730d05f6395ed28','渔家傲·樵歌二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"雨歇轻阴苔满地。莺声引入云深际。谷口回风惊扬起。芫花碎。芒鞋点点红芳缀。\",\"满径山?香蔼腻。杜鹃铺锦簪螺髻。掉首行歌歌不已。腰镰坠。枝枝不忍伤新翠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfead6de3d2e890866','浣溪沙·望归雁','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"病眼遥天认不真。一双双影未全分。空花摇曳乱春云。\",\"香篆欲消拖蔂尾，平波初皱起鱼鳞。乌丝阑染素笺纹。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfec6db5cb03f85fb4','减字木兰花·冬尽','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"烟笼残月。遮屋五更霜似雪。小爱东窗。逗漏玻瓈数点黄。\",\"梅风疏缓。冻蝶乍苏幽砌暖。生怕春生。生雨生风困早莺。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfed37480b4d09361a','蝶恋花·早秋','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"落日青空分燕尾。一片残云，犹作孤峰紫。翦翦白蘋风歘起，藕花露滴清波里。\",\"为问贴天双燕子。底事能忘，辛苦春前垒。回首垂杨无限意，无情也应悲秋死。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cfef45a8225d9cc5c7','青玉案·秋海棠','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"雕阑玉露凝珠屑。长只恐、芳魂折。日暮碧烟相护切。翠鬟低亸，胭脂淡染。了不愁孤怯。\",\"含情静解丁香结。浅笑偷窥清夜月。自惜断肠谁与说。金风蝶困，归云燕去。唯有寒蝉咽。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff096f358a9c3481d','解语花·鸳鸯梅','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"罗浮月下，豆蔻胎中，许结同心侣。渐启朱唇。含浅笑、不倩绿衣歌舞。\",\"暄风疏雨。红袖敛、芳心自吐。一双双、永不分离，何事还含醋。\",\"照影银塘低觑。向香泥玉藕，暗中增妒。彩禽欲唼。\",\"还应惜、自趁桃波飞去。绿阴深处。尽抛与、多情细数。\",\"不差池、三十六双，作宜男绣谱。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff10322865535a924','瑞鹤仙·寿李为好','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"早觉秋清也，是年华通，闰露晶莹。也霏微香浅。处知月中金，粟遥飞英。也双星炯。也良宵、映人闲永。也祝其、人如玉颉，颃姑射在瑶京。\",\"也轻。也邺侯仙，骨凌虚上，御风泠也。梦鹤迥也。何知蚁，梦醒也。笑向来蛮触，交争何事，冷看闲看定也。尽海波、干度桑田，吾心静也。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff230a86fa6d86e85','蝶恋花·候雁不至','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"曾倩归飞双燕子。换取宾鸿，来渡潇湘水。云绵乌丝阑画纸。素绡好写关河字。\",\"斜日寒鸦飞接翅。为底迷留，白草黄沙地。搔首问天天似醉。南楼霜冷丹枫坠。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff285f0f14c3c849d','谒金门·偶感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"千秋岁。只似而今滋味。落日黄花衰草地。有英雄残泪。\",\"片月亭亭西坠。不管幽虫吟砌。未审碧霄何意绪。恁茫茫无际。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff3d32bcb6004394a','瑞鹤仙·寿刘庶仙','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"桃花开也未，有酥雨微晴，烂熏春醉。何须倾绿蚁，弄嫩柳莺背。黄醅香腻，彩衣新试，更昨岁、兰开双穗。此芳辰不乐，何如尘世，原同儿戏。\",\"曾记牡丹月下，琴轩春早，笑歌声沸。年华弹指，渐两鬓，霜华缀。看江山，一晌蜃楼闪烁，烟波杳霭无际。只壶公壸里，韶光一色，彤云多丽。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff3e18a1069a21e6f','更漏子·本意','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明','“斜月横，疏星炯”是环境描写，烘托出悲凉的氛围，也为下文作铺垫。有漏壶滴水声、霜叶坠落声和秋虫的鸣叫声。衬托秋夜的漫长和寂静，借以表达作者内心的孤寂难平。表达了忧国忧民之情。“双眸未易扃”则可以看出此时作者的状态是心绪不宁，难以入睡的。“双眸未易扃”与“不道秋宵真永”前后呼应，正是作者彻夜难眠，才感到秋夜漫长。\n结构上，承上启下，与上文的外在环境的凄清相应和，为下文抒壮志未酬和忧国忧民之情作铺垫。\n“薄酒何曾得醉！”这句话是定语后置句，词人借酒消愁，却不能醉。原因在于他心忧天下。通过“天下事，少年心，分明点点深”这些诗句，表达了忧国忧民情，表达了反清复明壮志未酬之情。通过“薄酒何曾得醉”可以看出词人愁之重、深。\n全词格式工整对仗，上片写景，下片言志。作者秋宵长夜难眠，以酒求醉、求眠不得，其根本原因在于忧国忧民的情怀，作者借“更漏”以抒情怀。本词中写长夜不能入睡，为更漏声所恼。末三句感怀家国身世，更觉情意深挚，含蕴不尽。','[\"斜月横，疏星烱。不道秋宵真永。声缓缓，滴泠泠。双眸未易扃。\",\"霜叶坠。幽虫絮。薄酒何曾得醉。天下事，少年心。分明点点深。\"]','[\"近代叶恭绰《广箧中词》：\\u201C船山词言皆有物，与并时批风抹露者迥殊，知此言可以言词旨。\\u201D这阕小令写长夜秋声，听在耳中，击入心头，\\u201C天下事\\u201D三字使\\u201C点点深\\u201D力重千钧。结三句平直中回漩郁勃气，反卷上片则清丽缠绵之境顿觉峭然萧然，清劲见骨。\"]','一轮弯月挂在西边，稀疏的星星闪烁着明亮的光，可是秋夜却显得格外漫长。漏壶滴水的声音回荡在耳边，使人难以入睡。\n下了霜的叶子弯了下来，幽暗角落的虫子在鸣叫，秋天的一切都是显得如此悲凉，薄酒一杯，怎么能解我心中愁？满腔热血地想要为国效力，却始终不能如愿。天下大事，儿女情长，只能深埋在心中。','《更漏子·本意》是清代词人王夫之创作的一首词。词中作者写长夜不能入睡，为更漏声所恼。眼看着“斜月横，疏星炯”是环境描写，烘托出悲凉的氛围，也为下文作铺垫。该词表达了忧国忧民之情。辗转反侧之状，溢于言表，末三句感怀家国身世。“天下事，少年心”表达出想要回报国家，为国效力的迫切愿望。全词格式工整对仗，上片写景，下片言志末三句感怀家国身世，更觉情意深挚，含蕴不尽。','[\"炯（jiǒng）：明亮。\",\"永：漫长。\",\"滴泠（líng）泠（líng）：指漏壶滴水之声。\",\"扃（jiōng）：门上钮环，喻闭门，引申为闭眼。\",\"幽：阴暗的角落\",\"絮（xù）：鸣叫\",\"薄：少许\"]'),('e647d10f5d7f17cff478d9d856579909','兰陵王·秋感','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"伤心事。今日从何说起。剑光冷，血溅潭龙，落叶风高云际寺。宾鸿传锦字。向道。海云孤峙。天涯远，欲托传情，不怕关河阻迢递。露坠芙蓉死。问秋藕可能，将丝重系。\",\"吹箫人老吴阊市。向夜阑人静，闲提半语，也怕吟虫相调戏。拥孤衾独睡。凭梦。将愁寄。更天海悠悠，望断烟水。纵然有梦成差异。难寻觅酒伴，同垂珠泪。想天□□，□也了，无惭愧。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff4efc802a2bf11d6','永遇乐·纸窗日影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"笼月云轻，开萍水碧，问还似否。落叶旋飞，雁声空度，未计闲情生受。乍暖熏肌，残红上颊，睡眼半醒相逗。却猜疑、荔枝初剖，紫晕冰绡微透。\",\"镜匣斜开，枕痕淡染，粉面如中卯酒。含笑迎眸，回光相映，应念人消瘦。乳雀低飞，岭云横度，巳怨暂时僝僽。更只恐、午梦将阑，未许偎人长久。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff531b8e363d82a76','贺新郎·中秋大病不得与从游诸子觞月吟此慰之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"海门孤月上。是人间、平分秋色，桂香新酿。一曲草堂东岭对，延尽碧天清爽。窗影照、吟虫幽响。鹭足倒拳衾似水，笑清狂、到此无能强。镫燄薄，摇孤幌。\",\"一丸冰玉含惆怅。付伊谁、划破青天，御风孤往。擒取妖蟆三足怪，铺满银魂千丈。问窃药、当年欺罔。玉宇能禁寒彻骨，但有情、不怕银河广。宝剑在，英雄掌。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff6a5feb1e510b813','念奴娇·走马镫影','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"炎光未谢，竞的卢飞跃，争先赤兔。才转危坡还注坂，横戟无心回顾。汗血追风，怒髯奋臂，总被流光误。暗中毂转，蚁磨几时停住。\",\"儿童莫笑来回，半针尖里，走英雄如鹜。终是虾跳难出斗，渐有荒鸡催曙。五夜光残，一丝气冷，敲罢边腔鼓。勋名半纸，无人重与偷觑。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff810e105e2c52a85','风中柳水桂','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"不惜轻盈，取次浓妆珠蕊。似凭阑、绿云倦倚。低鬟微笑，喷兰襟香细。敛幽情、闲心如水。\",\"道是江妃，还似月中仙子。向秋清、露凝芳髓。冷然孤秀，怕微霜风起。索细挽、柔条重觑。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e64308b732b0','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"转汀洲、回峦斜拥，曲径松杉堆黛。落帆人语寒潭静，幡影风摇星彩。涵轻霭。渐潮声、鱼音梵呗喧金界。霜林惊籁。更百八绵连，噌吰奰发，流响空青内。\",\"深院锁，谁放鲸吟出海。澄江歘腾漰湃。唤回客梦清宵永，寂历闲心如洒。君莫骇。君不见、景阳旧恨台城改。佛镫绿叆。共窣堵铃鸣，相轮铎语，长夜悲兴败。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e6430a6bdcbd','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"问阳禽、北发天山，尺帛为谁长系。当年不作关山怨，但爱江南佳丽。秋无际。拥长汀、银膏玉屑堆光腻。横空如砌。宛一抹修眉，千行淡墨，欲坠还容曳。\",\"菰米熟，千顷元珠丰脆。何须更寻归计。丹枫染紫蘋花白，飞舞一江清霁。君莫睇。君不见、三更月落催哀唳。春光无蒂。蚤炎焰燔空，狂涛拍岸，羽驾何方税。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e6434523de7f','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"舞廉纤、不知是雪，还是沙明波素。同云返映晶光凝，暝色遥笼烟树。双无据。颤寒空、微霄极浦相回互。芦洲古渡。有孤艇篷窗，挑镫酌酒，唱彻梁围句。\",\"知此夕，一派瑶峰玉宇。朦胧半函银兔。清晖的皪蛟冰瀁，疑是东方已曙。君莫觑。君不见、迥波难挽流澌住。珠摧玉仆。向帝女祠东，昭王潭北，直下长江去。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e64354e2607b','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"接长江、西上三巴，东下海门万里。高楼思妇天涯梦，昨日金钱送喜。春归矣。倩啼莺、遥催彩鹢乘风起。危阑闲倚。望碧浪参天，斜阳低树，片影浮空水。\",\"相认处，青练双飞旗尾。夕风吹送旖旎。银缸对照江楼眼，收拾寄愁蠲纸。君莫盻。君不见、征人已老兰舟委。艨艟浪起。尽大帽白衣，摊钱打鼓，不向湘皋{舟羲}。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e64371f58561','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"俯江浔、滩临危磴，屏拥青蓉回抱。小桥流水平田迥，绿浪风生畦稻。当晴昊。散溪云、轻松一抹飞烟巧。青帘缭绕。有白笋黄鱼，红虾绿酒，装点旗亭好。\",\"兰舟泊，正及江南春早。玉山何惜倾倒。桃花留客红垂晕，幂䍥轻绡笼罩。君莫恼。君不见、鲛人蜃客迷三岛。韶华易老。但棠杜花边，鹧鸪声里，瘴雨迷衰草。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e6437a90096b','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"卷残虹、隔岸芦梢，低挂一团赪玉。紫金光聚明霞闪，雪洗鹭飞鸥浴。江一曲。舞轻桡、橛头车子相随逐。荻湾六六。听短笛横吹，棹歌遥答，共趁沙汀宿。\",\"投竿处，牵动绡金波蹙。溅珠县洒红粟。暝空欲敛西清色，一派炊烟凝绿。君莫瞩。君不见、羲轮无系西流速。云昏极目。听哀雁啼更，孤篷打雨，难拟明朝旭。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e643a8f9f073','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"展平湖、一片玻瓈，何处天围四野。金风轻卷千波雪，阵阵落晖低亚。真潇洒。渐西晶、连天接住东光射。冰轮上也。见镜吸空明，练飞霜影，一荡清无罅。\",\"凭槛处，百尺丽谯飞榭。玉楼闪冉光乍。清魂摇曳浑无定，灵肉欲随羽化。君莫诧。君不见、南来猿鹤悲清夜。天孤月寡。叹吹笛王孙，朗吟仙客，倦理云軿驾。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff830e643f210c99d','摸鱼儿·潇湘大八景词其一','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"望九疑、垆烟飞黛，远送元云千里。斜阳枫岸平沈后，木末惊飙拂水。兰舟舣。荐新凉、银缸影颤篷声起。推窗闲盻。看幅幅轻绡，层层珠瀑，骀宕空青里。\",\"清绝地，故是蕙汀兰沚。浅碧旧含芳蕊。朝来润浥灵苗发，共载天香云髓。君莫拟。君不见、楚骚歌阕兰蘅死。灵修邈矣。怕碎玉鏦铮，金铃淋沥，吹入愁人耳。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff87edc0c55058fc8','蓦山溪·听雨','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"闲抛数点，似洒无情泪。一晌悄无声，尽教疑、轻狂调戏。山溪蓦度，更不许人猜，浑似醉，全无谓，滥簸银河水。\",\"元云空腻，无力支天坠。荷盖小亭亭，应难留、得琉璃脆。孤镫昏闪，瘦影未分明，愁不寐，情谁系，梦也心魂碎。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff8bb8cb33cca5d98','水调歌头·咏怀','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"钓竿落吾手，意不在鱼边。六鳌何处，一艇缥渺凌孤烟。耐可乘流直上，不避回风吹转，蹴破浪花圆。一曲棹歌里，星斗落帆前。\",\"白蘋洲，芳草渡，扣哀弦。落花细数，坐久目送荇丝牵。出入无肠国里，烂漫无愁天上，鼎鼎度华年。唯有千丝雪，镜里自相怜。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff96eb763cfdef4b5','忆王孙·本意','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"芦花谁覆钓鱼船。宝玦藏腰未敢言。落叶惊乌月上弦。\",\"夜如年。春梦无人与再圆。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cff9ebd3158264255e','凤凰阁咏风','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"镇来回不审，何时是了，问天也难分晓。可惜一江绿净，半林红袅，都被轻狂消缴。\",\"年来双耳，长似蝉吟林表，那堪鼍吼还相嬲。便两鬓、几茎霜，也来萦绕。\",\"到长夏、又还幽悄。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffa583a1babc5a69a','玉楼春·白莲','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明','莲，出淤泥而不染，淡雅清幽，摇曳生姿。且又是白莲更给人增添人贞静纯美之感。\n“娟娟片月涵秋影，低照银塘光不定”，“娟娟”有好些意思，其一，长曲貌，《文选·鲍照<翫月城西门廨中>》诗：“始出西南楼，纤纤如玉钩。末映东北墀，娟娟似娥眉。”月亮开始出现在西南楼头，纤巧的就像挂帘的玉钩。最后映照在台阶之上，长长弯曲的样子就像美人的峨眉。“片月”也就是弦月。故而可用长曲来形容。其二，姿态柔美貌，形容弯弯的弦月正恰当。其三，明媚貌。宋 司马光 《和杨卿中秋月》：“嘉宾勿轻去，桂影正娟娟。” 桂影就是月亮代称，娟娟正是说月亮的明媚。 “涵”，包含包容，有映照之意。“秋影”，秋天里物像的阴影。秋天是凋零的季节，万物的摇落显出尘世的萧瑟苍凉与衰微。曲长柔美又明媚的弦月，映照着大地依然是一片肃杀的景象，改朝换代之后血腥的战事虽远去但清朝著名文字狱时时威胁着前朝的遗民，若文字中呈现对前朝的怀恋又对当朝的不满，这必引来身首异处的灾祸。尽管如此一个人仍要表示对故国的忠诚，遣词必须杳渺幽微却又能给人意蕴深远的联想。同时这也是词这一种文学体式美感特质所在。王夫之将这两者非常微妙融合在一起。\n“秋影”蕴含的摇落，也意味着变节。像云间三子之一宋征舆，清初女词人徐灿的老公陈之遴这些明朝的才子士人都在清朝做了官。娟娟片月映照下的尘世种种事态，这里面是有一个对比，片月的纯净与尘世暗淡的对比，前者是王夫之的持守后者世网的险恶与人心的易变。\n“低照银塘光不定”，“银塘”，月光照下来，池塘一片银白。“光不定”，出自李商隐《当句有对》“池光不定花光乱，日气初涵露气干。”池塘闪烁的亮光摇晃不定花影零乱迷蒙，朝阳升起照射下来，花草上的露珠很快都干了。这是李商隐写自己思绪的不宁与被人漠视的才华。王夫之引用来别有深意，月光低低的映照着池塘光亮摇晃不定，“光不定”所写正是时代的凝重与人心的思变。清初文字狱的高压，前朝士人多有投清谋取高官，自己在这样处境中如何自处这正是这首词后半部分所要表达的，而开始两句词写白莲所处环境也是自己所处时代大环境的介绍。正有着这样的时代背景才有了自己的特立独行与卓尔不群。故得以白莲自喻。\n“绿云冉冉粉初匀，玉露泠泠香自省。”，“绿云冉冉”出自盍西村的《［越调］小桃红·客船晚烟》，“绿云冉冉锁清湾，香彻东西岸。”，“绿云”出自南宋词人曹冠《夏初临》“绛榴细蹙香罗，绿云初展圆荷。”红色的石榴花就像飘着香泽的罗裙微微收束在一起，白色的荷花在圆圆的荷叶丛中初露风姿。“冉冉”，渐进的样子，形容事物缓慢变化。“绿云冉冉粉初匀”，白莲渐渐绽放开来，就像刚刚匀就脂粉的美人。这里潜藏着一个没有被说出来的“香”字。白莲逐渐开放出来锁住了清清的河湾，而那悠远的清香遍及东西两岸。这句词所包含的香草美人，在屈原《离骚》中就是一个人高贵品质的象征。在此可看成王夫之的自喻。但写得即含蓄又意味隽永。“玉露”，让人联想到杜甫秋兴八首之一“玉露凋伤枫树林，巫山巫峡气萧森。”玉露成霜伤残着枫树林，巫山巫峡云气阴晦萧森。这是写景也在写安史之乱后唐皇朝未来的风雨飘摇与昏暗不明。“泠泠”，清凉、凄清的样子。在玉露成霜凉意袭人的凄清氛围中，白莲知道自己依旧是清香如故。不论外界风物如何改变自身持有的幽香始终如一。这一点对于王夫之是对前朝如一的忠贞，对于屈原是对楚王不变的忠诚，对于芸芸众生只要一个人认识到自身的美好并能一以贯之这份美好，自身之外一切改变对你又能怎样呢？一个人若能将“绿云冉冉粉初匀，玉露泠泠香自省。”潜玩含咏千遍也许就能让人生进入另一种境界。\n上半阙从对比及环境背景暗示性写法作为铺垫引出白莲或作者自己对自身高贵的认识与持守，下半阙是对此作更深入阐述。\n“荻花风起秋波冷，独拥檀心窥晓镜。”“荻花”出自白居易《琵琶行》：“浔阳江头夜送客，枫叶荻花秋瑟瑟。”秋风从水塘边的荻花丛中吹来，阵阵寒意侵袭着冷冷水波中的白莲。这一句仍是对现实状况隐喻，清朝建立明朝留下来文人纷纷变节投靠清廷，王夫之曾投奔桂王以期恢复明朝，可是朝臣的倾轧荒淫终使他失望隐退。正如瑟瑟秋风吹得人心怎不寒冷？“檀心”，指赭色花蕊，可比喻对前朝赤胆忠心。“晓镜”，指清澈水面，但出自李商隐《无题》之一“晓镜但愁云鬓改，夜吟应觉月光寒。”因此“晓镜”这一语词有流光易逝年华老去的慨叹。独自呵护着自身的忠贞赤诚，当清晓来临悄悄看那波光中的自己，真无奈于对时光不再的深深叹息。如屈原《离骚》中所说，“惟草木之零落兮，恐美人之迟暮。”美人迟暮所惋惜并不只是匆匆年轮催人老，而是老去同时自身的理想与志意的落空。现实恢复明皇朝的理想已不可实现，而自身也已是垂垂老者。“窥晓镜”对于王夫之是有很深哀愁在里面。而写年华老去恰好引出最后两句词。“他时欲与问归魂，水碧天空清夜永。”这里所呈现幽微隐约的情感与似断实连的笔法，令人赞叹。\n将来想要问我魂归何处，清凉的长夜里一碧如洗的天空就是我灵魂的归宿。这很自然让人想起李商隐《嫦娥》中两句诗“嫦娥应悔偷灵药，碧海青天夜夜心。”“碧海青天夜夜心”与“水碧天空清夜永”，都写出一个人虽然身处永世的孤独中但内心持守的纯净与高洁也将与天地共长久。就像白莲总是出淤泥而不染，洁白的花瓣中蕴藏着永远是一颗赤子之心。','[\"媚媚片月涵秋影。低照银塘光不定。绿云冉冉粉初匀，玉露泠泠香自省。\",\"荻花风起秋波冷。独拥檀心窥晓镜。他时欲与问归魂，水碧天空清夜永。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffc081c7cd37212cb','绮罗香·读邵康节遗事属纩之际闻户外人语惊问所语云何且日我道复了幽州声息如丝俄顷逝矣有感而作','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"流水平桥，一声杜宇，早怕雒阳春暮。杨柳梧桐，旧梦了无寻处。拚午醉日转花梢，甚夜阑风吹芳树？到更残月落西峰，泠然胡蝶忘归路。\",\"关心一丝别挂，欲挽银河水，仙槎遥渡。万里闲愁，长怨迷离烟雾。任老眼月窟幽寻，更无人花前低诉。君知否，雁字云沈，难写伤心句。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffc67a8b03c34e3fa','望梅忆旧','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"如今风味，在东风微劣，片红初坠。早已知、疏柳垂丝，绾不住春光，斜阳烟际。漫倩游丝，邀取定巢燕子。更空梁泥落，竹影梢空，才栖还起。\",\"阑干带愁重倚，又蛱蝶粘衣，粉痕深渍。拨不开、也似难忘，柰暝色催人，孤镫结蕊。梦锁寒帷，数尽题愁锦字。当年酝就，万斛送春残泪。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffcac05446ab55a36','浣溪沙·病起春日小步','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"水浅平田碧几丛。出胎初叶剪娇红。匀匀流柳半溪风。\",\"小雪去年门外影，如今还落少阳中。春光不道不从容。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffd51bd669cd63dfc','浣溪沙·桃花二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"春恨拚教柳带长。朱颜贪耍醉霞觞。临风临水笑双双。\",\"欲洗儿容争采摘，郤随蝶梦尽飘扬。爱他娇艳柰他狂。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffd51bd66cb525059','浣溪沙·桃花二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"初破灵砂颗题肥。绿烟轻绕护霏微。晴烘休漫怕丹飞。\",\"结子他年千岁熟，如今刚半过春晖。莫教轻放阮郎归。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffd5c9febf4baeaeb','更漏子·本意二首','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"嵩台泊，漓江柝。剑吼匣中如昨。刘备垒，马殷坟。闲愁夜几分。\",\"灯烬灭。寒衾铁。只有归鸿悽切。檐溜雨，远鸡声。心知是五更。\"]',NULL,NULL,NULL,NULL),('e647d10f5d7f17cffec94425967c9099','摸鱼儿·辛幼安伤春词悲凉动今古惜其蛾眉买赋之句未忘身世为次其韵以广之','王夫之','e647d10f5d7f17cfd1dc3a824921f95b','明',NULL,'[\"总繇他、闲愁不管，才来又还催去。悠悠一派东流水，载得落花无数。人长住。却笑伊、来回奔走天涯路。凭阑无语。终不似黄莺，苦爱东风，百啭迎人絮。\",\"今古事，莫待怨谁相误。可但月来云妒。伤春未已伤秋赋，重倩吟螀寒诉。琼花舞。又早见、玉山瑶井填黄土。无为自苦。待人散月斜，日长山静，侬自有归处。\"]',NULL,NULL,NULL,NULL),('e647d10f5e1522a6cf44578d7c9b1126','三五七言','李梦阳','e647d10f5e1522a6ac796a52aa486f25','明',NULL,'[\"望云海，陟高京。秋风令百物，天地何冥冥。黄河岸头能覆车，何况羊肠诘曲行。\"]',NULL,NULL,NULL,NULL),('e647d10f5e1522a6cf44578da21a34d6','三五七言','李梦阳','e647d10f5e1522a6ac796a52aa486f25','明',NULL,'[\"天秋暮，月如素。金窗隔烟纱，花槛流萤度。良人远戍玉门西，谁念空闺玉箸啼。\"]',NULL,NULL,NULL,NULL),('e647d10f5e1522a6e429ae795bd0b0bf','如梦令','李梦阳','e647d10f5e1522a6ac796a52aa486f25','明',NULL,'[\"昨夜洞房春暖。烛尽琵琶声缓。闲步倚阑干，人在天涯近远。\",\"影转。影转。月压海棠枝软。\"]',NULL,NULL,NULL,NULL),('e647d10f5e30f3ba8d43bb4932aef774','江南好','陈子升','e647d10f5e30f3bae9fd7c2c2538a61e','明',NULL,'[\"炎夏过，夜坐迥凄清。千个竹风摇草阁，一规桂魄浸桃笙。\",\"多少玉关情。\"]',NULL,NULL,NULL,NULL),('e647d10f5e30f3bac71670206f7eb28e','南柯子','陈子升','e647d10f5e30f3bae9fd7c2c2538a61e','明',NULL,'[\"羽曲清三叠，痫弦碎六幺。绿珠绛树总魂销。何必凤凰台上忆吹箫。\",\"鬓影腾蝉翼，钗光颤翠翘。歌残眉语暗相撩。莫负可怜人度可怜宵。\"]',NULL,NULL,NULL,NULL),('e647d10f5eac4c019c70a6d8dbca1973','踏莎行','徐远','e647d10f5eac4c01d1457b72d1457b72','明',NULL,'[\"零雨初收，金波乍转，捣衣双袖临风卷。银床玉簟陡生凉，迟眠且扑流萤扇。\",\"草绿长门，荷残别院，如丝情绪如云乱。无端蟋蟀响西堂，井梧叶叶关心颤。\"]',NULL,NULL,NULL,NULL),('e647d10f5eb06cdaae2baf97145bc34d','浣溪沙·来青阁','程可中','e647d10f5eb06cdad1457b72d1457b72','明',NULL,'[\"复道悬空踏翠微，芳林过雨湿胭脂。卷帘岚气扑人衣。\",\"半榻午阴销剩暑，一窗寒碧送斜晖。曲阑十二绿成围。\"]',NULL,NULL,NULL,NULL),('e647d10f5ee2da8bee754c5835fa82ed','采莲曲','苏民怀','e647d10f5ee2da8bd1457b72d1457b72','明',NULL,'[\"荷弱风来不自持，那堪水面受风吹。金鞭横截垂杨过，调妾珊瑚知是谁。\"]',NULL,NULL,NULL,NULL),('e647d10f5ee2da8bee754c584a51c37a','采莲曲','苏民怀','e647d10f5ee2da8bd1457b72d1457b72','明',NULL,'[\"轻舟日日傍芳菲，南港花繁北港稀。贪折高茎伤弱腕，却翻圆露湿罗衣。\"]',NULL,NULL,NULL,NULL),('e647d10f5ee2da8bee754c58729dc8be','采莲曲','苏民怀','e647d10f5ee2da8bd1457b72d1457b72','明',NULL,'[\"空明秋水浸红霞，一带横塘十里花。落日回桡游子去，个边杨柳是侬家。\"]',NULL,NULL,NULL,NULL),('e647d10f5ee2da8bee754c58f6eaa5d7','采莲曲','苏民怀','e647d10f5ee2da8bd1457b72d1457b72','明',NULL,'[\"一曲菱歌恨未终，所思人隔若耶东。秋风昨夜湖南过，多少荷花怨落红。\"]',NULL,NULL,NULL,NULL),('e647d10f5fc8424f9b501991a2fc5714','浪淘沙令','王直','e647d10f5fc8424fbad5f337ad71c82b','明',NULL,'[\"风暖翠烟飘，残雪都消。游丝百尺堕晴霄。可惜春光容易过，又近花朝。\",\"驱马第三桥，芳意萧条。竹林浑未放夭桃。瘦尽城南千树柳，不似宫腰。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bb095f41a0117c6b9c','木兰花慢·奉呈介翁阁老依来韵','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"天留宰辅侍宸旒。开史局、庆重游。信彩笔花生，白云篇净，尘断境偏幽。伫待玉人来也，熙帝载喜，起入赓酬。千古堪传盛节，四民满听歌讴。\",\"元寮黑发讵容休。试抬头。看海阔天高，鹏抟鲲运，襟度许悠悠。那须个、兴风使浪，皮里著阳秋。藉好上林晴日，载醪随处停辀。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bb0c4d6735205aa57d','减字木兰花·调送杨原山次桂翁韵','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"还来暂去。海天踪迹男儿志。射影难妨。子夜歌残急就章。\",\"行吟吊古。忠悬霄汉心同苦。济险惟贞。周道还看似砥平。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bb46408bd90f91b852','玉楼春','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"天街密信传青鸟。恍出尘寰会瑶岛。错莫春霄风雨声，繁枝迸落长门晓。\",\"阳台梦杳凭谁道。松萝误惹荣怀抱。拟将幽限托朱弦，他生何处同谐老。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bb53e2d90024152330','画堂春·山桐花盛开壬午暮春','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"柴门过雨春融液。月色桐花香藉。梦断晓莺山更寂。可谁寻觅。\",\"曾嫌紫陌同尘，岂畏红芳避迹。朝阳鸣凤无消息。也应抛掷。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bb56b949d635a1f7d5','凤栖梧次韵奉答俨山阁老','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"黛眉暗锁针停缕。半卷珠帘，默听流莺语。往事不堪秋梦断。无情几点芭蕉雨。\",\"鱼雁漫凭传尺素。镜掩妆台，枉避纤腰妒。机札夜阑忙倒屦。懵然回想身何处。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bb99302016a8792a7c','西江月·次韵答前人','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"春去漫增闲恼，病馀更爱孤眠。浮名浮利总如然。生只被他萦管。\",\"囱草不除夜雨，盆榴半吐朝烟。寻常打破鬼门关。何是何非堪免。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bbafe820c12d7933d8','醉花阴·送希召赴洛次韵','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"绿杨深处游缰并。听莺啼不尽。红日又西飞，骊驹歌罢，恼乱离人兴。\",\"叹浮生蜂衙蚁阵。聚散何时定。愿潘郎去也，花满河阳，回首长安近。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bbc5660a1ca4074173','帝台春为府学师生送徐太守北觐并引代君家作','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"亭亭物表。器宇秋天杳。廉明太守，说向吾人，古来稀少。清风两袖朝天去，转眼是、凤池春晓。好敷陈，兽锦袍新，御炉香袅。蓬莱岛。\",\"莺花绕。霜月皎。鸡人早。伫献绩天颜，渥恩重，谩回首、石城云杪。舟楫川途莫道烦，乾坤整顿还须了。记取旧门墙，几人归鉴藻。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bbe4a7ccf879cf9926','玉蝴蝶·怀友','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"举目云遥雁杳，玉人何在，不尽悲凉。萧萧风雨，秋期又近重阳。井梧飘、生憎待凤，篱菊把、孤负传觞。黯相望。山花山鸟，分付谁行。\",\"思量。南来北往，几翻风浪，几度星霜。襟期皓首，素衣何处惹尘黄。谢醴筵、先几罔测，抱芳芷、旧恨难忘。悄更长。关山月冷，梦断潇湘。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bbec3266c8d2f02a00','喜迁莺·庆江明府先生荣膺奖擢并引','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"垂帘清昼。倩妆点溪山，春明如绣。这户弦歌，那村花柳。民物总归仁囿。禾传九穗登场，麦道两岐呈秀。观风使，问循良说甚，汉家卓茂。\",\"庇佑。天锡廉明贤宰父，富贵谦多受。荐剡频闻，福星高照，顷见徵车来召。平陵谩拟留宽，河内空期借寇。君去也，想遗爱难忘，棠阴重覆。\"]',NULL,NULL,NULL,NULL),('e647d10f5ff2e4bbf607c63dc393ea89','喜迁莺·驾明府谢君荣膺旌奖并引','符锡','e647d10f5ff2e4bbd1457b72d1457b72','明',NULL,'[\"瘴雨蛮烟。伫西风□尽，月满前川。水色柔蓝，山光列画，断崖芳树连天。试问带牛佩犊，何如凿井耕田。争柰得，老布道东西，征讨频年。\",\"堪怜。赖得琴堂分黑白，馀喘并周全。犬卧桑阴，雉驯麦垄，古来胜事空传。且喜鸮书高荐，行看骥足骧腾。从今去，但仙凫迹远，魏阙心悬。\"]',NULL,NULL,NULL,NULL),('e647d10f5ffc6f7c3ce786fa4466373f','摊破浣溪沙小春','项兰贞','e647d10f5ffc6f7cd1457b72d1457b72','明',NULL,'[\"淅淅寒风撼玉钩。起来斜日照红楼。帘外一声鹦鹉唤、唤梳头。\",\"花发小春情脉脉，笛吹长夜恨悠悠。多少泪珠弹不断、倩谁收。\"]',NULL,NULL,NULL,NULL),('e647d10f5ffc6f7c84b26c5b02fc6723','点绛唇·忆外茂仲','项兰贞','e647d10f5ffc6f7cd1457b72d1457b72','明',NULL,'[\"柳眼舒青，宫桃着露烟如织。凭栏目极。无语心凄恻。\",\"风度箫声，凤去空相忆。愁寒食。薄罗初试。杏脸消春色。\"]',NULL,NULL,NULL,NULL),('e647d10f5ffc6f7cb3e74e6ef5190759','醉花阴·闻莺','项兰贞','e647d10f5ffc6f7cd1457b72d1457b72','明',NULL,'[\"袅袅东风欺弱柳。碧沼波纹皱。寂寞倚雕栏，呖呖啼莺，几处春光透。\",\"千回百转离人后。更怕春归骤。记得去年间，花下踟蹰，正是愁时候。\"]',NULL,NULL,NULL,NULL),('e647d10f5ffc6f7cc5406a006d05b8b7','捣练子·秋夜','项兰贞','e647d10f5ffc6f7cd1457b72d1457b72','明',NULL,'[\"新雁唳，叶纷飞。砧杵声催露湿衣。独坐空庭更漏永，一天明月散清辉。\"]',NULL,NULL,NULL,NULL),('e647d10f5ffc6f7cfe4e4b8ea4b63f1d','梅花引·秋闺','项兰贞','e647d10f5ffc6f7cd1457b72d1457b72','明',NULL,'[\"晚云攒，晚风寒。叶落霜飞篱菊残。不堪看。不堪看。侬瘦似花，倚栏谁个怜。\",\"哀蛩泣遍衰杨岸，哀鸿叫遍疏桐院。恨漫漫。恨漫漫。明月又圆，那人何日还。\"]',NULL,NULL,NULL,NULL),('e647d10f6024424a9b64f4cee2538176','水龙吟·咏莲','张邦奇','e647d10f6024424a729c6888f337d999','明',NULL,'[\"芙蓉开遍芳塘，薰风倍觉湖天好。爱花心性，北窗簟滑，起来偏早。王母瑶池，麻姑琼海，更谁窈窕。枉惹他游女，暗增羞妒，频低首，临波照。\",\"一段氤氲芳气，画阑前、紫云萦抱。忽思泰华峰头，玉井搴来寒峭。欲去无因，步虚声里，梦云空绕。看层霄、鹤起凌风，何日尽收烦恼。\"]',NULL,NULL,NULL,NULL),('e647d10f60837db22eabdb0f34512cbf','忆秦娥','翠微','e647d10f60837db2d1457b72d1457b72','明',NULL,'[\"杨枝袅，恩情无限天将晓。天将晓，漏穷难唤，教人烦恼。\",\"邮亭一夜风流少，匆匆后会应难保。应难保，最伤情处，残云风扫。\"]',NULL,NULL,NULL,NULL),('e647d10f6119f058ee754c58d0ad3714','采莲曲','沈野','e647d10f6119f058d1457b72d1457b72','明',NULL,'[\"解道芙蓉胜妾容，故来江上采芙蓉。檀郎何事偏无赖，不看芙蓉却看侬。\"]',NULL,NULL,NULL,NULL),('e647d10f61e3c58c154effbd8bbe301d','浪淘沙','杨继礼','e647d10f61e3c58cd1457b72d1457b72','明',NULL,'[\"锦席绮筵重，兰炷香浓，隔帘鹦鹉唤秋风。六曲阑干颓玉处，梦到巫峰。\",\"金缕翠云中，宝髻轻松，绿窗月色正朦胧。说到飞觞传永夜，怕听飞鸿。\"]',NULL,NULL,NULL,NULL),('e647d10f627e2af990bd670e46da67b0','武城道中夜闻作吴语而歌者仿佛竹枝遗响因为二章','陆完','e647d10f627e2af94dcae38ee60e81c4','明',NULL,'[\"数间茅屋隐平沙，隔岸微闻唤酒家。若道消愁全仗酒，也停双橹倩人赊。\"]',NULL,NULL,NULL,NULL),('e647d10f627e2af990bd670e574c28d7','武城道中夜闻作吴语而歌者仿佛竹枝遗响因为二章','陆完','e647d10f627e2af94dcae38ee60e81c4','明',NULL,'[\"不奉君欢又一年，背人啼向落花前。当初自爱侬如玉，今日如花更可怜。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb11096da799f99cf2','卜算子·和湘蘋徐夫人','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"病起惜花残，云向何边住。会绕繁华拙政园，道韫幽吟处。\",\"倩梦访瑶台，雨阻犹难去。似隔云渊莫问奇，惆怅空无据。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb3cdc48e80f61b8e6','忆秦娥·暮春','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"春光骤。惜花憔悴蛾眉皱。蛾眉皱。碧天依旧，翠筠新茂。\",\"草茵花落鲜如绣。鸳鸯蹴水荷钱逗。荷钱逗。柳丝分线，莺梭织就。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb514d4a2279a80802','满宫花初夏','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"暖风和，柔情悄。一瞬春光易老。绿阴花瘦红香少。拂柳丝飞絮早。\",\"倚栏看，荷钱小。碧水盈盈波渺。荼蘼架上争开绕。又是采桑时了。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb567440d0f99fc237','卜算子·惜花，和黄月辉韵','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"睡起卷疏帘，春帚还停扫。细细苍苔片片红，燕觅香泥绕。\",\"新绿满枝头，蜂蝶无端闹。有意随流却送春，风雨摧残早。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb6125e44563c546f3','踏莎行·春日永丰村庄访女','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"不算寻芳，非游南浦。行舟只把津梁数。御河风景尚依然，园林宛似柴桑里。\",\"水畔鸥迎，莺呼启户。草堂相聚殷勤语。倾心正诉隔年愁，柳条怎绾斜阳暮。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb635fcf2b12b7f0b7','东坡引·泛舟访皆令闺友','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"樱桃红已足，日觑苔痕绿。纶竿钓罢清溪曲，一村桑落熟，一村桑落熟。\",\"闲愁莫展，翠蛾频蹙，强抱清琴对修竹。池边有侣堪联续，山儿画半幅，山儿画半幅。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb74ac58cab9d02a13','绕佛阁·喜参同庵新建大悲楼','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"法云普覆，双溪水绕，新筑璀璨。高阁孤耸，一灯远映、梵音到花畔。桂香又遍。招隐作伴，深坞幽境，时卧游玩。梦魂缭绕，鸿飞少芳翰。病客最疏懒。\",\"暮唤莲莲涤古砚。还是检书、咿唔常目眩。待腊尽春来，重赴禅院。此时登殿。缓步女丛林，积怀颇展。绿阴堪听黄莺啭。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb86a99f6eb70f251a','深院月闺怨','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"黛眉蹙，减容光。历尽风霜苦未央。斛剩馀愁动斗量，还须车载付沧浪。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fb9cb94e1720b20d09','如梦令·春闺','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"数日珠帘慵卷。槛外初来新燕。绣袂倚朱栏，春暖风吹人倦。\",\"人倦。人倦。且向海棠消遣。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fba77f9cd4448b6af9','菩萨蛮·秋水','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"湖光万顷澄如镜。白蘋荡漾菱花盛。一色映长天。峰青江上烟。\",\"鸳鸯相对浴。鱼跃溪边曲。不碍木兰舟。枫飘万点秋。\"]',NULL,NULL,NULL,NULL),('e647d10f62f542fbb2a59e30d57a5d41','点绛唇·晨妆','归淑芬','e647d10f62f542fbd1457b72d1457b72','明',NULL,'[\"漫展香奁，镜前巧画青山远。绿鬟粉面。宝髻添珠钿。\",\"绣袂红绡，玉臂双金钏。轻罗扇。花羞难见。落雁低声啭。\"]',NULL,NULL,NULL,NULL),('e647d10f634fcdefd4ff83e64d3f66d8','浪淘沙·秋','朱栴','e647d10f634fcdef8d9a0adbdb6ebd05','明',NULL,'[\"塞下景荒凉，淡薄秋光，金风淅淅透衣裳。读罢安仁《秋兴赋》，憀栗悲伤。\",\"廿载住边疆，两鬓成霜，天边鸿雁又南翔。借问夏城屯戍客，是否思乡？\"]',NULL,NULL,NULL,'[\"这首词选自《宣德宁夏志》，《嘉靖宁夏新志》、《弘治宁夏新志》、《万历朔方新志》、《朔方道志》亦收录此词，题目作《浪淘沙·塞垣秋思》。浪淘沙，词牌名。\",\"安仁《秋兴赋》：指西晋文学家潘岳，字安仁，工诗赋，曾作《秋兴赋》。\",\"憀栗：同\\u201C寥栗\\u201D，凄怆貌。\",\"夏城：即今宁夏回族自治区首府银川市老城。宋为西夏都城，明为九边重镇宁夏镇城，故称镇城为夏城。屯戍客：驻守边疆的人。 \"]'),('e647d10f6419bb5ff8a7a77e7bdb4852','桂殿秋','朔朝霞','e647d10f6419bb5fd1457b72d1457b72','明',NULL,'[\"秦淮渡，碧波流，冷月斜明花外楼。仙人掌满金茎露，桂子香寒玉殿秋。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da6600052ba0c8e895e3','桃源忆故人暮春','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"楼前花逐东风舞。惟有杨花堪妒。一味入帘穿户。不管愁人顾。\",\"枝头杜宇声偏苦。叫得斜阳欲暮。门外残红无数。零落横塘路。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da66358275b682aab6be','醉桃源早春','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"疏帘不卷早春寒。残梅倚石栏。碧天无际路漫漫。孤云独去闲。\",\"丝添鬓，意阑珊。频将双泪弹。中庭明月又团团。愁人不要看。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da6686f20684f5710115','小重山·端午','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"闲窗独坐病馀身。惊闻画鼓闹，在河津。始知节届佩符辰。彩丝系，输与少年人。\",\"一岁一番新。年光催短鬓。暗伤神。感时怀古欲沾巾。湘江渺，何处吊灵均。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da66d0863ecc429b209b','传言玉女·咏残梅','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"疏梅枝上，昨夜又经春雪。依依窗外，恰对人寥寂。冷淡孤芳，一种幽情殊绝。休吹长笛，好留馀馝。\",\"瘦影横斜，正冰蟾好时节。乱点苍苔，无奈东风劣。梦断高楼，有恨无言可说。早听帘际，莺调新舌。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da66e4215d0c72d5d067','南歌子·秋思','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"翠菊娇秋色，金蓉衬晚霞。倦来无力倚红纱。恨杀孤鸿一点、叫天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da66e676ebf0625b8cbf','小重山·秋闺','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"萧瑟幽闺更漏长，庭前丛桂发、暗飘香。月明露白渐生凉，轻风起、时拂郁金裳。\",\"远雁一行行，相看还伫立、怯空房。幽怀几许总难量，兰缸灺、花影欲窥窗。\"]',NULL,NULL,NULL,NULL),('e647d10f6504da66fb77f320c31d9c9b','鹧鸪天·有感','纪映淮','e647d10f6504da66297fa7778303a79b','明',NULL,'[\"落尽红香绿满枝。韶光如驶去难追。春归过去鹃啼血，小阁帘垂乳燕窥。\",\"情脉脉，意孜孜。无言常自费寻思。连天草色和烟碧，何事东风着意吹。\"]',NULL,NULL,NULL,NULL),('e647d10f65392f84405ba87040992909','竹枝歌','徐庸','e647d10f65392f84d1457b72d1457b72','明',NULL,'[\"银钗女儿脂粉香，竹枝歌里唱悠扬。数声传去郎听得，惹起相思空断肠。\"]',NULL,NULL,NULL,NULL),('e647d10f65392f84405ba8708700aeab','竹枝歌','徐庸','e647d10f65392f84d1457b72d1457b72','明',NULL,'[\"九疑山下暮云深，二女祠前竹满林。稚子虽然翻锦箨，要知节节有虚心。\"]',NULL,NULL,NULL,NULL),('e647d10f65392f8440c0991d0d44d478','转应词二首','徐庸','e647d10f65392f84d1457b72d1457b72','明',NULL,'[\"秋梦。秋梦。梦觉笛声三弄。空堂蟋蟀频鸣。\",\"正是窗前月明。明月。明月。曾见几番圆缺。\"]',NULL,NULL,NULL,NULL),('e647d10f65392f8440c0991d68d1fd2a','转应词二首','徐庸','e647d10f65392f84d1457b72d1457b72','明',NULL,'[\"疏树。疏树。黄叶乱飘江路。西风吹鬓飕飕。\",\"景色浑如去秋。秋去。秋去。塞外远人归未。\"]',NULL,NULL,NULL,NULL),('e647d10f65392f84b253d4d157bca023','柳枝词','徐庸','e647d10f65392f84d1457b72d1457b72','明',NULL,'[\"章台一种异随堤，带雨含烟万树齐。不惜赠郎扳拆去，任随征旆走东西。\"]',NULL,NULL,NULL,NULL),('e647d10f65392f84b253d4d168c534f8','柳枝词','徐庸','e647d10f65392f84d1457b72d1457b72','明',NULL,'[\"学画修眉两叶长，美人晓起斗清扬。花飞如雪随风舞，江北江南总断肠。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fd07a9d936ba855f2d','踏莎行·春日会友人梁氏园游赏','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"鹭破青林，凫分碧藻。江村景致天然好。一尘不到万缘空，人生只合闲中老。\",\"古树巢云，落花绣草。四山弦管啼幽鸟。醉来白眼望青天，人间别有乾坤小。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fd437988e68bd5fb5f','酹江月题情慰奚原启下第','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"佳人薄命，叹红粉、几多黄土。岂是老天浑不管，好恶随人自取。既赋娇容，又全慧性，却逍随凡苦。不平如此，问天天竟无语。\",\"可惜国色天香，随缘流落，飘泊今如许。借问繁华何处在，多少楼台歌舞。紫陌春游，绿窗晚绣，过客惊眉妩。人生失意，从来无问今古。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fd4461f5b10c5ef16d','酹江月和东坡韵题赤壁图','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"黄州迁客，意翩翩、不是风尘中物。一叶扁舟凌万顷，气盖乌林赤壁。孟德雄才，周郎妙算，到此俱销雪。横江一笑，眼中谁是英杰。\",\"一自两赋成来，山川胜概，倍增辉发。鹤梦箫声随水去，只有声华难灭。静对新图，闲歌古句，竖起冲冠发。何时载酒，江心重溯流月。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fd50ba223ba5fbe5e7','卜算子·秋思','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"云散岭头光，叶落山形瘦。目断遥空雁不来，正是悲秋候。\",\"雨点水痕圆，风蹙波文皱。顾影徘徊落小池，顿觉人非旧。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fd856287e2103a5d15','沁园春·寄题岳王庙','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"为国除患，为敌报仇，可恨堪哀。顾当此乾坤，是谁境界，君亲何处，几许人才。万死间关，十年血战，端的孜孜为甚来。何须苦，把长城自坏，柱石潜摧。\",\"虽然天道恢恢。奈人众、将天拗转回。叹黄龙府里，未行贺酒，朱仙镇上，先奉追牌。共戴仇天，甘投死地，天理人心安在哉。英雄恨，向万年千载，永不沉埋。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fdcabc783241576f41','鹧鸪天·江村','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"城阙峨峨禁籞重。梁家池馆闹春风。韶光尽在花畦畔，生意都来草径中。\",\"天广大，日从容。十分春色万枝红。醉中记得西杨语，莫待银蟾生海东。\"]',NULL,NULL,NULL,NULL),('e647d10f65ad30fdf739adab8b651134','清平乐·昔高槎轩作水龙吟词咏红竹子诵而喜之欲拟作一词未有以起其意者适余友志昂奉常以松雪翁钩勒竹卷求题故为作此词','丘浚','e647d10f65ad30fd309928d4fa1e9c96','明',NULL,'[\"佳人空谷。陨落猗猗菉。泪尽粉乾销腻玉。翠袖仅馀边幅。\",\"吴兴公子清狂。临池不学钟王。垩帚扫成飞帛，雕锼金薤琳琅。\"]',NULL,NULL,NULL,NULL),('e647d10f65e7911fbe3b826228a2e10d','鹊踏花翻落梅','关键','e647d10f65e7911fd1457b72d1457b72','明',NULL,'[\"澹沲琼妆，悠缟袂，临期肠断风流尽。怜伊似水年光，和梦心情，难忘林下风前韵。飘萧絮扑玉楼遥，迷离雪拥金炉近。借问玉貌，几时消损。\",\"笛声三弄空贻恨。未见杏靥梨涡，柳绵桃绶，早与春风趁。罗浮仙梦不堪追，夜寒月冷谁传信。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc70c1cddde','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"春尽田家郎未归，小池凉雨试絺衣。园桑绿罢蚕初熟，野麦青时雉始飞。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc70cf0cab1','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"渚兰汀芷不胜春，极浦遥山岂解颦。谁在长安褷花柳，山中闲煞采芳人。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc73fe5d3f1','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"银烛金杯映绮堂，呼儿击鼓脍肥羊。青衫黄帽插花去，知是东家新妇郎。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc74153fb7a','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"拍拍东风燕子寒，卷帘花絮若为看。夜深雨脚何曾睡，春水平于养鸭栏。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc76f779a92','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"杨柳深深桑叶新，田家儿女乐芳春。刲羊击豕禳瘟鬼，击鼓焚香赛土神。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc773eebe5a','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"白纸黄坟野草生，柳烟桐火照清明。楚娥不识秋千戏，两两沙头接臂行。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc786b38afa','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"侬饷蒸藜郎插田，劝郎休上贩茶船。郎在田中暮相见，郎乘船去是何年。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc7894b0963','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"绿鬓荆钗双髻螺，青裙高系小红靴。阿?旧是茶城女，教得娃儿能楚歌。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc794a79986','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"溪上春流乱石多，劝郎慎勿浪经过。莫道茶陵水清浅，年来平地亦风波。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed5607191bc7e29035ef','茶陵竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"溪南溪北树萦回，洞口桃花几度开。枫子鬼来天作雨，云阳仙去水鸣雷。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56658cbfb8f4f96ef7','雨中花·题画','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"正爱月来云破，那更柳眠花卧。帘幕风微，秋千人静，酒尽春无那。\",\"迢递高楼孤寂坐，缥渺笛声飞堕。恨曲短宵长，院深墙迥，凭仗风吹过。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8f02fd98f6','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"湘江女儿愁落晖，湘江江上鹧鸪飞。行人试看君山竹，竹不成斑君始归。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8f04621ed1','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"戎门旌节拥高台，军士南边戍未回。红巾小儿齐击鼓，知是官船江上来。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8f2792a581','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"潭州城边多野田，黄茅白苇远连天。莫言楚国无生理，亩地如今倍直钱。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8f30ad5143','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"三十六湾湾对湾，人家多住白茅间。直过洞庭三百里，长沙城北是彤关。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8f4bee8a50','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"湘江水深天下清，何如陇头秋月明。离人到此不得醉，况是高楼吹笛声。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8f900b2f1f','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"长沙少年无奈春，青衫白面不生尘。劝君莫向湘潭住，江燕衔泥解涴人。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8fa0525d66','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"南船北船满洞庭，萧公祠前牲酒馨。二妃枉作君王后，君看君山山自青。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8fbce08558','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"汨罗江头春水生，汨罗江上楚歌声。人间若解三闾苦，水底鱼龙亦有情。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8fcba1c4e6','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"马殷宫前红水流，定王台下暮云收。有井犹名贾太傅，无人不祭李潭州。\"]',NULL,NULL,NULL,NULL),('e647d10f66a6ed56e2545d8ff9929798','长沙竹枝歌十首','李东阳','e647d10f66a6ed56c930eecd490640b4','明',NULL,'[\"江头彩旗耀日明，船上挝鼓不停声。湖南乐事君记取，五月五日潭州城。\"]',NULL,NULL,NULL,NULL),('e647d10f66fc77224887c58457e07e68','清江裂石','于未','e647d10f66fc7722d1457b72d1457b72','明',NULL,'[\"忽见推帘起暮鸦，风逼小灯斜。又被打窗雪片，巡檐问、可压梅花。正开缸、初试屠苏酒，举杯谁劝、强半惜年华。纵博得、玉壶香暖，毕竟是天涯。\",\"浑一片云山，四围隐隐飞峦，带不起烟霞。驴背叹行频，垆头暂醉，料醒来孤踪、依旧尘沙。正醒醉含愁，猛听征鸿嘹亮，哀怨过谁家。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c3507804fc30a8d45cd','竹枝词五首','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"门前杨柳岸边花，几度逢郎来吃茶。日晚莫□□□□，月明直送到侬家。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c3507804fc3285761f9','竹枝词五首','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"前年粜米上瞿塘，今岁装盐过岳阳。伴侣相逢终日醉，有钱不似在他乡。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c3507804fc367af1026','竹枝词五首','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"荷花浦头春月明，问郎何处唱歌声。月明易缺似郎意，花开不断是侬情。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c3507804fc39cab8348','竹枝词五首','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"五月瞿塘无客过，黄陵庙前春水多。但使黄金随处有，任他湖海足风波。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c3507804fc3b6e18124','竹枝词五首','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"妾貌如花朝暮新，郎心似水荡青春。相逢好是回塘路，荷叶深深不见人。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54d45d4ed8c','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"旭日照芳林，莺啭春风早。一片红云暗不开，无奈春声搅。\",\"乘兴且闲游，莫待韶华老。随意飞红点绿苔，休着家童扫。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54d6bccdc69','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"绿竹绕清流，草舍人家远。几处牛羊晚下来，烟外闻鸡犬。\",\"禾稼满秋原，路向桑麻转。箫鼓从教乐社神，岁岁常相见。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54d7136f359','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"积玉映空青，蓬岛人间近。珠树瑶华满眼开，缥缈仙台影。\",\"便欲跨青鸾，直上三山顶。鹤氅披云看下方，月白银河冷。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54d723e45cd','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"宿雨涨春流，晓日红千树。几度寻芳载酒来，自与东风遇。\",\"弱水与桃源，有路从教去。不见西湖柳万丝，满地飞风絮。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54d905b3c15','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"斜日照疏帘，雨歇青山暮。白鸟鸣边一半开，香霭和烟度。\",\"楼上见平湖，影隔青林雾。吹断鸾箫兴未阑，月照芙蓉露。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54da5526ee1','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"惊雪喷高崖，雷响青天晓。刚道吴胥驾海来，势压沧溟小。\",\"满岸是渔舟，拨乱飞春鸟。谁信神鱼去不留，五色祥云绕。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54ddbc37313','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"淅沥带秋烟，两岸蒹葭响。何处渔舟暝未还，隔浦闻清唱。\",\"撩乱下枯槎，一夜苕溪涨。天目应添翠几重，明日看晴嶂。\"]',NULL,NULL,NULL,NULL),('e647d10f671f3c35a07ec54ddcb6480f','卜算子·夹城八景夹城夜月','王洪','e647d10f671f3c35bad5f33790db9da4','明',NULL,'[\"孤月泛江秋，露下高人静。期着佳人夜不来，坐转霜梧影。\",\"吹彻紫鸾箫，宝篆烟销鼎。桂子飞香下广寒，银汉秋波冷。\"]',NULL,NULL,NULL,NULL),('e647d10f6789d5be9804dfdc1c73068d','百字令中秋','李福谦','e647d10f6789d5bed1457b72d1457b72','明',NULL,'[\"空庭秋晚，趁花阴把盏，临风独适。醉眼昏沉潦倒甚，今夕又逢佳夕。桂子飘香，银河敛影，秋意平分坼。凄凉往事，不堪回首重忆。\",\"堪笑苍狗白衣，人间幻境，转盼成陈迹。记得去年今夜语，都在心头历历。壮志空存，此身依旧，漂泊江南客。明朝酒醒，又添一段于邑。\"]',NULL,NULL,NULL,NULL),('e647d10f68b61e444c072692edbaa8ad','山花子·初夏','唐世瀚','e647d10f68b61e44d1457b72d1457b72','明',NULL,'[\"寂寂闲庭雨乍晴，绿阴如幄午风轻。几转柳塘迷断岸，野桥平。\",\"满架蔷薇花欲谢，趁风燕子掠浮萍。慵卧小窗惊唤醒，被流莺。\"]',NULL,NULL,NULL,NULL),('e647d10f6913016a25eb6af75b6d78e5','清平乐','杨基','e647d10f6913016ac70daf248d9a0adb','明',NULL,'[\"欺烟困雨，拂拂愁千缕。曾把腰肢羞舞女，赢得轻盈如许。\",\"犹寒未暖时光，将昏渐晓池塘。记取春来杨柳，风流全在轻黄。\"]',NULL,NULL,NULL,NULL),('e647d10f6913016a5ecb6fa10a5b3ce1','多丽','杨基','e647d10f6913016ac70daf248d9a0adb','明',NULL,'[\"问莺花，晚来何事萧索。是东风、酿成新雨，参差吹满楼阁。辟寒金、再簪宝髻，灵犀镇、重护香幄。杏惜生红，桃缄浅碧，向人憔悴未舒萼。念惟有、淡黄杨柳，摇曳映珠箔。凭阑久，春鸿去尽，锦字谁托。\",\"奈梦里、清歌妙舞，觉来偏更情恶。听高楼、数声羌笛，管多少、梅花惊落。鸳带慵宽，凤鞋懒绣，新晴谁与共行乐。料应在、楚云湘水，深处望黄鹤。天涯路，计程难定，长恁飘泊。\"]',NULL,NULL,NULL,NULL),('e647d10f6913016a8899da327c1c730c','夏初临','杨基','e647d10f6913016ac70daf248d9a0adb','明',NULL,'[\"瘦绿添肥，病红催老，园林昨夜春归。深院东风，轻罗试著单衣。雨余门掩斜晖。看梅梁、乳燕初飞。荷钱犹小，芭蕉渐长，新竹成围。\",\"何郎粉淡，荀令香销，紫鸾梦远，青鸟书稀。新愁旧恨，在他红药栏西。记得当时。水晶帘、一架蔷薇。有谁知。千山杜鹃、无数莺啼。\"]',NULL,NULL,NULL,NULL),('e647d10f6913016a8a947f0918579f22','浣溪沙·花朝','杨基','e647d10f6913016ac70daf248d9a0adb','明',NULL,'[\"鸾股先寻斗草钗，凤头先绣踏青鞋。衣裳宫样不须裁。\",\"软玉镂成鹦鹉架，泥金镌就牡丹牌。明朝相约看花来。\"]',NULL,NULL,NULL,NULL),('e647d10f6913016ad45a6aa8377edbd4','烛影摇红·帘','杨基','e647d10f6913016ac70daf248d9a0adb','明',NULL,'[\"花影重重，乱纹匝地无人卷。有谁惆怅立黄昏，疏映宫妆浅。只有杨花得见。解匆匆、寻芳觅便。多情长在，暮雨回廊，夜香庭院。\",\"曾记扬州，红楼十里东风软。腰肢半露玉娉婷，犹恨蓬山远。闲闷如今怎遣。看草色、青青似剪。且教高揭，放数点春，一双新燕。\"]',NULL,NULL,NULL,NULL),('e647d10f6913016af3a8f0979bfa5c2b','菩萨蛮·水晶帘外娟娟月','杨基','e647d10f6913016ac70daf248d9a0adb','明',NULL,'[\"水晶帘外娟娟月，梨花枝上层层雪。花月两模糊，隔窗看欲无。\",\"月华今夜黑，全见梨花白。花也笑姮娥，让他春色多。\"]',NULL,NULL,'这是一阕以机趣见巧的小词。似若咏物，实係喻理。其所表现的「理」，初读再也简单不过，梨花在与皎月争胜鬭艳。上阕说因为有月光，雪样白的梨花被淹化了，分不清「花月」。诚然，这也可谓以月色比衬花容。下阕推过一层，月华被梨花淹化，梨花冷艳之色全显，那可就独佔了「春色」了。细一想，有了上阕月色相比，那「花月两模糊」之「模糊」其实已写足了梨花之洁白，则不必再赘述「今夜黑」之背景衬托。这种「让他春色多」的虚荣好胜心性彷彿有点病态。再往里想，不免悲哀，杨基似乎傻气十足地在「笑姮娥」，「让他」独佔春色，自我表现，其实是在讲：不是「花」太白，那是因为「夜」太黑。这很惊悚。表现才华，逞露个性，不一定得在夜色如墨的背景前。在黑夜中多几分春色，难说就值得骄傲。不必深究词人是否有意「寄托」，也许他在逞才炫巧。但即使如此，词在客观效果上表述了与「疾风知劲草」相类的命题，衹是「劲草」悲壮，「梨花」则悲苦味浓。','[\"菩萨蛮：词牌名。近水杨宁益《零墨新笺》考证《菩萨蛮》为古缅甸曲调，唐玄宗时传入中国，列于教坊曲。变调，四十四字，两仄韵，两平韵。\",\"娟娟：美好貌，言月光皎洁，月色妩媚。\",\"姮（héng）娥：卽嫦娥。\"]'),('e647d10f692b1b50b670aa7b58d602b3','浪淘沙·闺思','顾众','e647d10f692b1b50d1457b72d1457b72','明',NULL,'[\"生小弄冰弦，未拨先怜，涩莺娇燕语春烟。一点幽情传未得，残月窥帘。\",\"铜雀锁婵娟。惊数流年，小桃窗下背花眠。谁唱西陵肠断句，梦到君边。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa3026f4789b126852a','汉宫春·题半身停箫仕女','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"金缕裁衣，更腰系霓裳，内家妆束。蛾眉淡扫，高绾烟鬟凝绿。隔窗遥见，倚东风、海棠春足。还堪恨、被遮罗袜。凌波步，莲双蹙。\",\"一枝楚江寒玉。露纤纤十指，春兼轻掬。秦楼何处怅望，凤鸾追逐。何当拂琯启朱唇，暗留芳馥。想只待、赏音佳伴，为吹个、洞天仙曲。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa31759764d713efae1','浣溪沙·题夏景','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"紫燕营巢语画梁。海榴喷火散清香。困人天气日偏长。\",\"夹道槐阴张翠幄，满池莲艳斗红妆。薰风殿阁送微凉。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa32f802ecc8b22f7c4','喜迁莺·词赠南京太常少卿莆田陈公赴户部侍郎之召时李立之少卿尚留南寺','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"帝城清晓。见彩凤衔来，九重丹诏。宠溢弓旌，光生轮帛，云有圣恩宣召。伟器惜居闲散，雅望合登枢要。典三礼，有一夔自足，何须双妙。\",\"争道旧曾向。粉署蜚英，久矣推清操。政贰民曹，班联常伯，看展经邦才调。趣驾曹参乍别，前席贾生初到。敷奏处，听薰弦阜物，世跻熙皞。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa33b9768da686cb5bd','小重山·题斑竹白扇','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"十二江妃白练裳。微风吹不散、郯藤香。斑斑清泪洒英皇。离魂断、金佩冷潇湘。\",\"暑气正探汤。堪怜一握入、手相将。翩翩凤羽近人翔。全凭仗、付与透襟凉。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa33eb219a8e507c023','临江仙·题观泉图','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"桧壑泉垂朝雨后，冰帘一片谁收。两髯闲坐古矶头。回头频指点，飞雪洒清秋。\",\"白羽停挥凉意透，冷光晴射双眸。夕阳不尽古今愁。伯牙弦上趣，曾遇赏音不。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa355a27d45fb43c493','踏莎行·题扇','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"湘竹披烟，剡藤裁雪。敛舒蹙就棕榈摺。动摇一握展开时，半弯恍讶瑶空月。\",\"赤日低焚，火云高结。人间何处逃炎热。便将入手不停挥，凉风满面真清绝。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa35770b7533a2425bd','好事近·题踏雪图','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"江路晓风清，满地乱堆琼屑。谩策蹇驴闲步，采梅梢香雪。\",\"小桥流水不知寒，春意已潜泄。望隔岸何处，有珠宫瑶阙。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa36e16b24daeb289cc','满江红·题把酒问月','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"灵籁无諠，良夜永、冰轮初上。秋如许、金风荐爽，素波清泳。白玉台高天咫尺，褰裳览眺吟怀畅。便浩歌、把酒问嫦娥，遥相向。\",\"如汉水，葡萄酿。似鲸吸，江湖量。乾坤两白眼，任渠豪放。想是长庚来采石，金銮乍别青藜杖。待更阑、抱阮好归休，林鸡唱。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa388e822406f95ed70','明月棹孤舟题月下抚琴图','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"露下天坛秋月皎。闲来此、豁舒襟抱。细拂冰弦，闲调玉轸，弹曲广陵仙操。\",\"思入冥冥尘虑扫。绕十指、鹤鸣鸾啸。流水高山，子期何在，不见赏音人到。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa38a39febe0a7aed62','鹊桥仙·次韵贺费彦升寿六十','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"庄椿甲子，费壶日月，历象也难推候。瑶池阿母向西来，特地把、蟠桃荐酒。\",\"竹梧标格，鹤鸾风采，但见年年依旧。称觞莫道我来迟，喜和得、阳春添寿。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa3a484c8b58251592a','沁园春·送程司马还新安','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"能武能文，禄位功名，始终保全。想身依凤阙，亲承恩露，手提虎旅，汛扫蛮烟。镇守南都，命参机务，司马兼收将相权。也只为，把险艰历遍，足疾缠绵。\",\"一封疏乞归田。俄优诏、遥飞下九天。许暂返柴桑，优游泉石，勉亲药物，颐养天年。当宁思贤，人惟求旧，早晚徵书来日边。从今去，遇家山风景，且莫留连。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa3a7a09f66d7b615d0','昼锦堂送都宪高公致仕','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"黄甲高登，乌台峻擢，其奈亲老谁将。几载白云入望，搅乱柔肠。一杯菽水怀桑梓，两封章奏达岩廊。吾皇道，争似此人，忠孝暂许还乡。\",\"欢声。心事遂，便卸却，朝簪整顿归航。一水家山咫尺，况遇秋凉。都门出祖离觞滟，画堂献寿舞衣长。兹归也，好看椿萱并茂，乔梓同芳。\"]',NULL,NULL,NULL,NULL),('e647d10f69376fa3f6b7c16b07ce89e7','菩萨蛮·题小景画','倪谦','e647d10f69376fa3a60937ebdc09c97f','明',NULL,'[\"短篷载雨随流下。木兰画桨无人把。两岸碧波平。群山似马行。\",\"浮沉飘一叶。远水长天接。欹枕惬吟情。有诗成未成。\"]',NULL,NULL,NULL,NULL),('e647d10f693ebe290960a67825fe1398','蝶恋花·题武夷太虚岩','邱四可','e647d10f693ebe29d1457b72d1457b72','明',NULL,'[\"古壑流连寻趣步，淅沥泉飞，寒石留云护。数折曲房罗翠雾，幽怀可是人能悟。\",\"风月江山悬客慕，为惜芳年，聊取柔情付。自昔金兰多陌路，而今管鲍休相误。\"]',NULL,NULL,NULL,NULL),('e647d10f69cf74e886b9120265a93ed1','虞美人','吴邦桢','e647d10f69cf74e8d1457b72d1457b72','明',NULL,'[\"西溪十里渔村路，犹记看梅处。暗香疏影最关情，更怜雪外一枝横，雨初晴。\",\"而今别去梅花久，梅好应如旧。风霜愧我渐苍颜，长教老鹤怨空山，几时还。\"]',NULL,NULL,NULL,NULL),('e647d10f6a0fb8d3af5d11abaf5cb420','千秋岁·送远山李夫人南归','顾横波','e647d10f6a0fb8d3d1dc3a824f398cb9','明',NULL,'[\"几般离索，只有今番恶。塞柳凄，宫槐落。月明芳草路，人去真珠阁。问何日，衣香钗影同绡幕。\",\"曾寻寒食约，每共花前酌。事已休，情如昨。半船红烛冷，一棹青山泊。凭任取，长安裘马争轻薄。\"]',NULL,NULL,'这是明末清初秦淮八艳之一的女才子顾媚的一首颇有情致的送别词。词为送别自己闺中密友远山李夫人而作。李夫人即吉永兵部侍郎李元鼎之妻朱中楣，远山系朱氏的字，朱为明宗室之女，俞陛云《清代闺秀诗话》谓其“恒有故国之思。”从这首词的内容看大约是顾媚于京城送朱氏南归故乡江西而作。\n词上阕主要写惜别时的所见所感。首起二句“几般离索，只有今番恶”言人生或许有多次离别的愁苦凄怨，然而只有这一次最厉害。一个“恶”字，不但以低抑的情调笼罩全篇，而且交待了词人与被送别者极不寻常的关系，同时，也点明了只此一别，恐怕重聚匪易，道出了词人异常沉重的心情，接着以“塞柳凄，宫槐落，月明芳草路，人去真珠阁”四句从客观上点明送别李夫人的环境和时间是在芳草萋萋、柳絮飘飞、槐花殒谢的暮春明月之夜。而“凄”“落”二字又从主观上渲染了惜别时的悲凉氛围。真珠阁，即珍珠阁，泛指华丽的闺阁，这里特指词人与李夫人经常相处同聚的居室。“问何日，衣香钗影同绡幕？”照应开关，叹息这次离别分手之后，不知何日再得一起乘车盛装携手同游。一个问号，表明相分之难。“衣香钗影”即衣香鬓影，多用来形容女子盛装出游。上阕绾结二句通过具体事件表达对与李夫人之间昔日相处的美好生活的眷念，显得情感真挚。\n下阕主要设想别后情景。换头“曾寻寒食约，每共花前酌”二句紧承上阕，回忆当年曾经相约寒食节踏青寻芳，每每花前举杯开怀共饮的欢快往事，这里袭用“以喜衬悲”的手法，接着哀叹“事已休，情如昨”，此二句在作一大的顿折中留连昔日美好交往的无限深情。“半船红烛冷，一棹青山泊。”是词人设想此一别后，友人将解舟南下的情景：孤伴青冷的烛光，泊舟在异地青山之下。虽是词人推想之辞，但是一个“冷”字却合情合理地刻画出别后闺友的凄凉境况，表现了对友人的关切。歇拍“凭任取，长安裘马争轻薄”二句仍是设想之辞，不过前两句是设想友人别后，这两句是设想自己，在友人离去之后任凭京城中那些豪华显贵者争着恣意游玩去吧，未尽之意是，友人一走，无知音相伴，我将再也无心去参加了。秦淮八艳活动于明末，时与一些民族志士交往。入清后，常怀民族故国之情，加之朱中楣本为明宗室朱民后裔。所以煞尾二句不仅是词人对友人挚情的流露，而且未尝不含弦外之音，颇值得玩味。\n这首词拾取琐事，追怀友情，以作临别赠言，情感细腻，真切动人，同时通篇叶入声韵，诵读起来更增加了低抑哀回的情味。',NULL),('e647d10f6a450b2f25eb6af799b20e01','清平乐','吴山','e647d10f6a450b2f42ffcf05d010396c','明',NULL,'[\"秋光如许，雁字排空去。十二阑干凝眺处，帘卷西山暮雨。\",\"白沙翠竹柴门，落霞孤鹜江村。况是亭皋叶下，萧萧最怆吟魂。\"]',NULL,NULL,NULL,NULL),('e647d10f6a4b94da1368c9148e4c17f2','临江仙','闵如霖','e647d10f6a4b94da0c8ce551020c8bfa','明',NULL,'[\"万里碧天清似洗，银河珠斗回杓。秋千院落月儿高。柳阴墙外转，花气户边飘。\",\"春色恼人应不寝，何人更品鸾箫。灵鸡未唱夜迢迢。玉阶清露湿，金鼎碧烟销。\"]',NULL,NULL,NULL,NULL),('e647d10f6a6689341bfd0d9ae5c86354','菩萨蛮·次尔斐韵','顾朝桢','e647d10f6a668934d1457b72d1457b72','明',NULL,'[\"笼烟弱柳丝如带，莺梭织就朱阑外。芳草意绵连，王孙醉欲眠。\",\"新萝荫古壁，牛背斜阳笛。杜宇月三更，凄清分外明。\"]',NULL,NULL,NULL,NULL),('e647d10f6b690e54060635da2df8bf72','念奴娇·用东坡赤壁怀古韵','孙楼','e647d10f6b690e54bc731692277281aa','明',NULL,'[\"红尘深处，转愁肠、却忆江南景物。翳云开，梅雨霁、一片苍崖翠壁。竹里鸣淙，松间泻瀑，衣袂飞珠雪。流觞递咏，山中谁是奇杰。\",\"而今归路迢遥，宦途迤逗，弹铗悲歌发。去住无凭，眠未稳、好梦随生随灭。过雁牵情，吟蛩送泪，听里堪华发。青山不负，有时还醉萝月。\"]',NULL,NULL,NULL,NULL),('e647d10f6bdca15450d8b9202363e3dc','点绛唇·忆琼章姊','沈宪英','e647d10f6bdca154d1457b72d1457b72','明',NULL,'[\"帘外轻寒，谢娘风絮无人见。桃花如面，肠断春归燕。\",\"人去瑶台，只觉东风贱。湘帘卷，绿杨千线，烟锁深深院。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d9695e8b0d1ed8a51c','竹枝词四首','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"湖船来往惯风波，尾后黄旗飞虎图。一夜王程三百里，枇杷明日进留都。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d9695e8b0d7f04807b','竹枝词四首','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"南濠有客寄书还，夫婿黄柑已趁钱。几日不来湖上棹，休教重上赣州船。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d9695e8b0dd0a37477','竹枝词四首','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"青山是处锁蛾眉，日日湖边有别离。却怪烟波三万顷，扁舟只许载西施。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d9695e8b0de956530b','竹枝词四首','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"江南一雪苦非常，湖上空余橘柚乡。万树千头零落尽，于今那得洞庭霜。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d986b912021d3ba524','虞美人','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"海棠不碍窗纱影，鬓亸鸳鸯领。绣棚吹落唾花红，但见余香一点、袅晴风。\",\"柳眠无力轻烟惹，色撩人也。罗衫闲搭画阑边，日暖融融好个、洗头天。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d9b253d4d1547f8515','柳枝词','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"绿阴如雨万条斜，啼罢朝莺又晚鸦。尽日春风无别意，只吹花点过西家。\"]',NULL,NULL,NULL,NULL),('e647d10f6c01f9d9c8ede8f26b866bc3','惜分飞·闺情','吴鼎芳','e647d10f6c01f9d9b71091571c54985e','明',NULL,'[\"红界枕痕微褪玉，唤起那禁幽独。好鸟啼春足，归期不准花须卜。\",\"尽日眉峰双凝绿，十二绣帘低轴。人在阑干曲，杨花却羡无拘束。\"]',NULL,NULL,NULL,NULL),('e647d10f6cb4da25b20ce92e6cbf4ac3','重叠金惜别','吕希周','e647d10f6cb4da25d1457b72d1457b72','明',NULL,'[\"粉痕未褪梅妆雪，骖鸾忽度梨花月。灯影小红楼，角声起暮愁。\",\"孤篷怜倦客，绣被余香歇。料倚枕鸳单，屏风六曲山。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f488a679c00cd3ede','杂题上元竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"瓦坼龟文一例看，断连横竖有悲欢。一声脆响笼灯看，暂作排愁事一端。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f488a679c1b7f88a3','杂题上元竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"腰身十七正娇慵，珠凤鞋帮一捻红。不奈小桥春露滑，阿娘扶过石栏东。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f488a679c65842867','杂题上元竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"风雨元宵意倍伤，画檐低拜扫晴娘。若教扫得天边雨，为扫离人泪两行。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f488a679ccb283303','杂题上元竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"绣佛前头结好因，上元香火肃凌晨。悬幡一色灯笼锦，名氏亲书善女人。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f488a679ce44a4518','杂题上元竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"夜筵花果绕香炉，心事年年问紫姑。灯后踏青挑菜起，不知堪会那人无。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f488a679cf62f51e8','杂题上元竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"白縠单衫绝点埃，经年皎洁似初裁。何须更取园蔬识，偶趁同袍一摘来。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd1716b6f073','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"春朝岁旦古难并，十九年来见两巡。记得簸钱时未还，对门除夕看迎春。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd171fb47992','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"佛阁前头照水梅，半晴天气暖催开。娇憨小妹惊看见，一日来扳四五回。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd1721423174','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"吟魂易放早春天，风物江南倍可怜。底事兰成能作赋，感怀偏在戊辰年。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd172960cf51','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"笔床砚匣本长闲，每到新年说掩关。宾友到门俱谢却，夜来相遇看鳌山。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd178e0cbb1b','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"道北宾筵夕夕张，也呼朋社一猖狂。怪来罗列珍羞甚，人日曾扳水部郎。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17970a63a4','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"明欢姻阀互经过，背后绫纹刺几何。邂逅肩舆不相问，奚奴私说客名多。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17a0c5d204','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"莫怪河豚价不廉，桥南酬过五千钱。谁知太宰归田日，只与屠沽意气鲜。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17a4f34f0e','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"蒋侯旌骑出西村，姊妹同看倚戟门。笑恨芳香吴望子，不留冰玉永承恩。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17a588a556','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"闲行坊曲看春联，暗露婵娟屈戍边。小户岂曾窥邸报，也随人写太平年。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17a58b012c','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"旧家姑娣别经年，岁月围炉剖橘筵。侍女却防欢喜炭，笑花飞上画裙缘。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17bd051712','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"休宁药炮旧知名，俊健游郎满路擎。一炷挂香浑未了，手中抛过百千声。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17c5aa1c71','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"盼得灯兴趁踏歌，县门无榜奈愁何。可知巷陌萧条夜，成就欢期事转多。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f805ccd17db67ff4e','新岁竹枝词','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"多少蓝衫聚邑门，提灯捧盒酒盈尊。须臾看点红单出，个个佥名有沐恩。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f887e805460e3cc38','满江红·二首','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"眼角相勾，谁道有、这场抛散。怕向那、定情帘下，诉愁窗畔。几度卸妆垂手望，无端梦觉低声唤。猛思量此际正天涯，啼珠溅。\",\"欲寄语，加餐饭。难嘱咐，鱼和雁。隔云山牵挽，寸心如线。善病每逢春月卧，长愁多向花前叹。况如今憔悴去侬边，何曾惯。\"]',NULL,NULL,NULL,NULL),('e647d10f6da6972f887e8054f5b2682b','满江红·二首','王彦泓','e647d10f6da6972fd1457b72d1457b72','明',NULL,'[\"春雨霢霂，正狼藉、落花堪哭。无聊赖，客窗滋味，几宵残烛。眼底乍抛人一个，眉尖压上愁千斛。问断肠词为阿谁吟，楼东玉。\",\"银屏后，阑干曲。偎素脸，频叮嘱。爱明眸秋剪，翠娥娇蹙。福薄若无欢笑分，病身甘守孤单宿。望天公鉴念一心人，成金屋。\"]',NULL,NULL,NULL,NULL),('e647d10f6dc5f4732625ade05b23b8c0','蝶恋花·残春风雨','薛廷宠','e647d10f6dc5f473d1457b72d1457b72','明',NULL,'[\"绿杨枝上黄鹦小，长路关情，花鸟三春了。云水迢迢乡梦杳，夭桃秾李空开笑。\",\"江笛一声天正晓，雨色愁人，征骑忙多少。紫荇风牵罗带绕，晚来顿觉轻寒峭。\"]',NULL,NULL,NULL,NULL),('e647d10f7047ed8de429ae799822a63c','如梦令','武氏','e647d10f7047ed8dd1457b72d1457b72','明',NULL,'[\"画阁闲吟玉案，寂寂薰风庭院。微闷坐花前，独把阑干倚遍。\",\"帘卷，帘卷，秋在桐阴一半。\"]',NULL,NULL,NULL,NULL),('e647d10f7072b63a804d9eaa5573b915','醉落魄','夏树芳','e647d10f7072b63ad1457b72d1457b72','明',NULL,'[\"团冰握雪，天公幻个人孤洁。最怜误向风尘撇，貌似春花，命似晚秋叶。\",\"酒边灯下曾相洽，重逢不耐匆匆别。新来真个音书绝，欢似浮云，愁似半残月。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c1037d81ca1f29ef719','声声慢','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"雪晴山邬，月冷江皋，岁寒解后相逢。携手归来，轻盈一样春容。行行□鸣环，暗香霏、缥缈东风。弄花手，与安排金屋，共贮芳。\",\"雅淡暗通心素，笑桃根桃叶，冶艳妖红。试问韶华，樽前若个情浓。想是乔家姊妹，可人处、清致皆同。春正好，淡眉山、愁减几重。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10999ad4460e295d96','浪淘沙·令咏莎滩','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"雨过碧云秋，占断滩头，沧浪翻处湿纤柔。谁展翠茵平似剪，宿鹭眠鸥。\",\"沙尾远凝眸，雨惨烟愁，萋萋不共水东流。几度渔人来傍宿，绿映孤舟。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10a62095b49bc3b1ee','买陂塘赋东郭草堂','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"爱草堂、带村连郭，门前碧水千顷。秋波泠浸遥山晚，青镜净涵眉影。沙浦迥，怪隔水渔歌，鸥梦都惊醒。时乘小艇，向柳外花边，载诗载酒，独尽漫游兴。\",\"谩重省，且得身闲心静，乐事自甘瓢饮。好怀谁道中年减，佳趣更多吟咏。□转径，门正对朝阳，不怕梅花冷。流年暮景，肯容我为邻，相依卜筑，共尔两家隐。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10d5ea437baee1b365','选冠子','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"袅袅芙蕖，平铺断港，路入锦云处。香浮绿水，浪卷晴舟，宛在翠红香里。湘妃馀酣未醒，拥盖藏羞，含娇欲语。想淩波尘远，遥鸣佩，风飘艳绮。\",\"望中宛似若耶溪，隔水只欠，小艇采莲女。最怜芳意，佳藕难寻，肠断寸丝千缕。叶老房空，漫嗟此际，一点春心更苦。且休歌水调，恐惊起，文鸳双侣。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10ea6fc6e36f5c00e3','如梦令·咏石','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"径庭掩尘踪静悄，雨长苔痕缭绕。行处不妨花，容我醉眠偏好。\",\"休扫，休扫。待积落红多少。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10ed0c56ecc3e51b85','瑞鹤仙','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"盈盈罗袜。移芳步淩波，缓踏明月。清漪照影，玉容凝素，鬓横金凤，裙拖翠缬。渺渺澄江半涉。晚风生，寒料峭，消瘦想愁怯。\",\"我僭为兄，山矾为弟，也同奇绝。馀芬剩馥，尚熏透、霞绡重叠。春心未展，闲情在、两鬓眉叶。便蜂黄褪了，丰韵媚粉颊。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10ee8f75444d4ea034','暗香疏影','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"冰肌莹洁。更暗香零乱，淡笼晴雪。清瘦轻盈，悄悄嫩寒犹自怯。一枕罗浮梦醒，闲纵步、风摇琼玦。向记得、此际相逢，临水半痕月。\",\"妖艳不同桃李，淩寒又不与、众芳同歇。古驿人遥，东阁吟残，忍与何郎轻别。粉痕轻点宫妆巧，怕叶底、青圆时节。问谁人、黄鹤楼头，玉笛莫教吹彻。\"]',NULL,NULL,NULL,NULL),('e647d10f70ef4c10fb969d6c08fb8a26','醉落魄咏苔','张肯','e647d10f70ef4c10d1457b72d1457b72','明',NULL,'[\"径翠钿狼藉，绿圆点点浓如积。芳痕涨雨凝寒碧，一片浓阴，休扫坐来石。\",\"径深不教残阳入，茸茸不似春红色。芳尘净洗无纤迹，吟客来时，只恐印行屐。\"]',NULL,NULL,NULL,NULL),('e647d10f715ac4b17315b746b5d767c0','千秋岁引·丽谯','夏允彝','e647d10f715ac4b1d1457b72c1fea270','明',NULL,'[\"泽国微茫，海滨寥廓，万堞孤城逼天角。云外龙车碧树悬，霜前雁字当窗落。苎城花，秦山月，都萧索。\",\"刺史风流推琴鹤，暇日高吟倚轩阁，酾酒新亭几忘却。三泖沙明绕郡楼，九峰岚翠扶城郭。铜壶响，晓更催，宛如昨。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01098a05854e6546d2','醉蓬莱·寿陈子宣父七十','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"初日上珠帘，乳燕双飞，垂杨风软。游丝卷尽，有莺声娇啭。绿遍东城，红稀南陌，偏称人游衍。花外楼台，柳边歌管，乍开芳宴。\",\"况老景欣逢太平时，满目韶华，莱衣缱绻。麻姑相讯，道蓬莱水浅。人生七十，古来真鲜。愿沧海桑田，从今再变。寿域无期，华筵常展。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01158ff5a4d9fb14d2','乌夜啼寿茹母七十词','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"二月春添富贵，千秋人对芳菲。朱颜绿发金尊里，笑道古来稀。\",\"柳外歌传金缕，花前舞衬斑衣。瑶池宴是承欢处，此日报春晖。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a011e16a1a3edafbaf8','醉蓬莱·冯尹父母双寿词','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"喜金鱼乍绾，玉节更颁，功名都就。归老田园，松菊浑如旧。往日中元，恰逢初度，又小春时候。天降长庚星，垂宝婺齐膺眉寿。\",\"堪羡君家，满庭玉树，数枝丹桂，谁论谢窦。牛刀试割，百里贤声骤。片片白云，东去乡园，迢递频回首。彩衣遥拜，两袖清风，一杯春酎。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a013f4f574fe24ffe5c','临江仙·吴尚质寿母','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"芙蓉露滴秋江冷，黄花欲满东篱。雁行历历海云低。想重闺明月，正屈指归期。\",\"春风初喜兰芽茁，齐眉燕婉芳菲。锦屏银烛映仙卮。年年欢此会，应不羡瑶池。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01ae3f3cdfe941547d','瑞鹤仙·寿谈守教母六十','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"薰风初入幕。禁城隈，缭绕朱栏画阁。杨柳枝枝弱。\",\"绿阴中，遥映满阶红药。萱花吐萼。爱北堂、春光如昨。\",\"清昼永，人在瑶池深处，安排春酌。\",\"恰有蓬瀛仙客。羽服翩跹，云车煜爚。玉壶丝索。玳筵前，觥筹错。\",\"芳尘动，正见妙舞清歌，尽日承颜为乐。想莱彩潘舆，兹事古今寥廓。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01bf59dc413bbedd01','临江仙·寿姚申甫父','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"渊明未老赋归来，赢得清风两袖。田园三径浑如旧。桂子满中度，已擢双枝秀。\",\"长君鸾诰喜重荣，绣服光辉清昼。黄花秋晚浮新酎。驷马入高门，愿上千春寿。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01c3315194282c09e1','浣溪沙·代寿陈乡人','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"花满江城月满楼。玉卮金缕共相酬。主人华发未盈头。\",\"心远不妨游市井，身闲端可傲王侯。人间何处是丹丘。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01da0c877c28452766','满庭芳·题来雁词两首','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"绛阙冬初，瑶阶月满，仙宫恰喜新成。螭碑鸿笔，帝命属春卿。石上丹书珍重，许微臣、并勒御名。缟衣人，来从何处，恍惚梦魂惊。\",\"听斋宫漏彻，家童踯躅，特地传声。有翩翩鸣雁，夜集前楹。可道事都非偶，皇恩重、物兆先迎。自而今，禁垣清切，日见被光荣。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01da0c877c78059bec','满庭芳·题来雁词两首','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"沙塞风凄，潇湘云暖，横空多少成行。随阳南去，待春更回翔。何事偏离故侣，含声戢、翼下君堂。堪爱是，月明三五，烛影对寒光。\",\"况翩翩缟服，依依清梦，歘达君傍。道群分，类聚二语分明。那时刚承恩数，穹碑书、托姓名香。渐陆重，看今日兆，染翰待君王。\"]',NULL,NULL,NULL,NULL),('e647d10f71a48a01f44f9e445dcba6ac','浣溪沙·顾隆吉索寿周君六十','王立道','e647d10f71a48a0141a60377204da255','明',NULL,'[\"杨柳风柔燕子斜。江南春色正繁华。画楼弦管是谁家。\",\"冉冉歌声迎落日，盈盈舞影乱流霞。千年重见碧桃花。\"]',NULL,NULL,NULL,NULL),('e647d10f71ac327bad9fc917ccc49c91','杨柳枝·辞','朱阳仲','e647d10f71ac327bd1457b72d1457b72','明',NULL,'[\"广陵杨花辞故枝，各自空条雨后垂。羌笛不知春自去，分明一曲月中吹。\"]',NULL,NULL,NULL,NULL),('e647d10f721cd7bf57659484664cf2cc','长相思','刘荣嗣','e647d10f721cd7bfd1457b7224409ec4','明',NULL,'[\"山悠悠，水悠悠，水远山长湘渚秋，衡阳天尽头。\",\"风满舟，雨满舟，细雨斜风生暮愁，谁登江上楼。\"]',NULL,NULL,NULL,NULL),('e647d10f732df82412f4d5da0cdf190d','卜算子','聂大年','e647d10f732df82428fc27825e76bef6','明',NULL,'[\"杨柳小蛮腰，惯逐东风舞。学得琵琶出教坊，不是商人妇。\",\"忙整玉搔头，春笋纤纤露。老却江南杜牧之，懒为秋娘赋。\"]',NULL,NULL,NULL,NULL),('e647d10f732df82412f4d5da12a9d525','卜算子','聂大年','e647d10f732df82428fc27825e76bef6','明',NULL,'[\"粉泪湿鲛绡，只恐郎情薄。梦到巫山第几重，酒醒灯花落。\",\"数日尚春寒，未把罗衣著。眉黛含颦为阿谁，但悔从前错。\"]',NULL,NULL,NULL,NULL),('e647d10f732df82476faee874687a4c6','临江仙·半道春红','聂大年','e647d10f732df82428fc27825e76bef6','明',NULL,'[\"记得武林门外路，雨余芳草蒙茸。杏花深巷酒旗风。紫骝嘶过处，随意数残红。\",\"有约玉人同载酒，夕阳归路西东。舞裙歌扇绣帘栊。昔游成一梦，试问卖花翁。\"]',NULL,NULL,NULL,NULL),('e647d10f7364f6b54334969f2938bf75','蝶恋花','黄鸿','e647d10f7364f6b5d1457b72d1457b72','明',NULL,'[\"着意留春春不许。一阵东风，吹落花无数。记取等闲花落处。重游怕是桃源路。\",\"门外青丝垂日暮。偏惹离肠，不系征帆住。两两画梁新语燕。飞飞又入花间去。\"]',NULL,NULL,NULL,NULL),('e647d10f7364f6b55f606d213be5e93f','菩萨蛮·梦中咏梅','黄鸿','e647d10f7364f6b5d1457b72d1457b72','明',NULL,'[\"雕栏十二幽香腻。分明睡在罗浮里。风动晓妆前。残云冷翠钿。\",\"花花何处问。向晓愁来近。横枕一张琴。琴心曲里凭。\"]',NULL,NULL,NULL,NULL),('e647d10f7364f6b5f47b5afe26c1e4aa','长相思·秋怨','黄鸿','e647d10f7364f6b5d1457b72d1457b72','明',NULL,'[\"倚栏杆。步栏杆。鹈鴂三更枝上寒。如何桂影圆。\",\"记难难。话难难。那堪清漏唤邯郸。啼红烛未残。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab503930e78e5cebf3f','洞仙歌·江都恨','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"珠帘乍卷。漏春光一半。廿四桥烟花恨满。久伤故国，鸿雁来稀，吴江畔。古艳阳琼花观。望隋堤一抹，杨柳依依，明月迢迢隔河汉。\",\"露满玉衣秋，夜漏沉沉，催刀尺，伤心肠断。泪滴金壶红粉怨。偶一梦，到南朝，乱敲银蒜。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab51c5f62ca1aa61141','一剪梅·咏柳','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"无限伤心夕照中，故国凄凉，剩粉余红。金沟御水自西东，昨岁陈宫，今岁隋宫。\",\"往事思量一晌空，飞絮无情，依旧烟笼。长条短叶翠蒙蒙，才过西风，又过东风。\"]',NULL,'残阳如血，令人伤情，斜射着无垠的山川。故国覆亡，到处是一派凄凉。如今只剩南明朝廷残破狭小的宫殿，御苑中的溪流，自西向东流去，日夜不息。就像陈后主荒淫误国，昨日的陈朝宫殿，今年又变作隋朝的宫庭。\n不堪回首，往事就像这轻浮的柳絮，刹那成空。柳絮不知人心，依旧似烟知雾地漫天飙飞。柔软的柳枝，青翠的柳叶，像细雨蒙蒙，西风刚刚吹过，东风又来侵袭，几番风雨。','夏完淳以一腔忠贞，把短短十七年的有限生命，完全投入抗清复国的大潮之中，所写的诗、文、赋、词，无不悲歌激烈，血泪泪交融。辞风师承陈子龙。此首《一剪梅》，也同样是借咏柳来抒发一腔亡国之痛。 ','[\"一剪梅：词牌名。此词牌以周邦彦所作起句有\\u201C一剪梅花万样娇\\u201D句，故取为调名。又名\\u201C玉簟秋\\u201D、\\u201C腊梅香\\u201D。重头六十字，平韵。\",\"金沟：御沟，御河，上有金鳌玉蝀桥，故简言之，水即沟中水。\",\"陈宫：陈朝宫殿。\",\"隋宫：隋朝宫殿。隋炀帝曾筑长堤植万柳。\",\"一晌（shǎng）：一转眼。晌：不多久，读若\\u201C赏\\u201D。\"]'),('e647d10f738ecab525eb6af7dd5aaa1c','清平乐','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"盈盈花落。无奈东风恶。泪滴花魂花命薄。颇似愁人漂泊。\",\"珠帘半卷窗开。惜花懒傍妆台。梦到画桥深处，黄莺唤得春来。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab52715c37289f40e4c','瑞鹧鸪','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"断肠春信自年年，烟雾珠帘掩翠钿。红滴杜鹃花下雨，绿回杨柳院中烟。\",\"重重春恨凭谁说，两两黄鹂唤欲眠。独有多情明月在，隔墙花影动秋千。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab52eabdb0f874572bd','忆秦娥','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"伤离别，相思又值清明节。清明节，蓟门衰草，汉宫红叶。\",\"愁怀万种凭谁说，边鸿不到音书绝。音书绝，长安何处，晚山重叠。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5420d0018c4d47107','卜算子·秋色到空闺','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"秋色到空闺，夜扫梧桐叶。谁料同心结不成，翻就相思结。\",\"十二玉阑干，风动灯明灭。立尽黄昏泪几行，一片鸦啼月。\"]',NULL,NULL,'描写的是闺中人思今意中人的情怀。从夏完淳的经历看，词中所写的闺中人，其实是他自己心怀的化身，而所思念的意中人，也是故国、或者是理想的寄托。',NULL),('e647d10f738ecab55977126716460981','潇湘夜雨','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"莺啭花更，蝶园香国，重阑朝暮愁归。坠鞭人瘦，怎禁小风吹。不为春酲懒起，分香处，燕子来窥。芙蓉帐，馀烟已渺，红泪染离袿。\",\"相逢何处也，难寻丹棘，欲寄啼规。恐萧郎别后，浅淡愁眉。剩有菱花一半，已不是那日崔徽。长堤路，桃花带笑，柳眼自含悲。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab55a482337f33a700b','一剪梅·柳','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"无限伤心夕照中。故国凄凉，剩粉馀红。金沟御水日西东。昨岁陈宫，今岁隋宫。\",\"往事思量一饷空。飞絮无情，依旧烟笼。长条短叶翠蒙蒙。才过西风，又过东风。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab56ffdaf8a3831e284','望江南','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"江上望，郎在木兰舟。远水孤帆天漠漠，斜风细雨水悠悠。\",\"归去不胜愁。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab56ffdaf8aaaabcb31','望江南','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"期已过，不见暮帆归。寂寞孤灯寒枕簟，徘徊残月挂帘钩。\",\"肠断五更头。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab58f77d39a8638144e','烛影摇红','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"辜负天工，九重自有春如海。佳期一梦断人肠，静倚银釭待。隔浦红兰堪采，上扁舟伤心欸乃。梨花带雨，柳絮迎风，一番愁债。\",\"回首当年，绮楼画阁生光彩。朝弹瑶瑟夜银筝，歌舞人潇洒。一自市朝更改，暗销魂、繁华难再。金钗十二，珠履三千，凄凉千载。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab590bdbd82dd7b788d','满江红','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"无限伤心，吊亡国云山故道。蓦蓦地，杜鹃啼血，棠梨开早。愁随花絮飞来也，四山锁尽愁难扫。叹年年春色倍还人，谁年少！\",\"梨花雪，丝风晓。柳杨枝，笼烟袅。禁三千白发，镜花虚照。襟袖朱颜人似玉，也应同向金樽老。想当时罗绮少年场，生春草。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5b3e858c6ccebc3c6','江城子·病起春尽','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"一帘病枕五更钟，晓云空，卷残红。无情春色，去矣几时逢？添我几行清泪也，留不住，苦匆匆。\",\"楚宫吴苑草茸茸，恋芳丛，绕游蜂。料得来年，相见画屏中。人自伤心花自笑，凭燕子，驾东风。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5b88f1c0dc76cc2ff','一剪梅','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"黄鹂啼处绿阴遮。红杏枝斜。碧柳枝斜。和风和月梦魂赊。风乱杨花。月乱梨花。\",\"春风漂泊梦为家。花也天涯。人也天涯。花光人面绿窗纱。红是桃花。瘦是梅花。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5bc942d1b46705e23','两同心有梦','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"梦到扬州，依然南浦。绮罗如梦锦如尘，香车宝马知何处。翠烟笼，十里隋堤，茫茫飞絮。\",\"举目魂消肠断，春光如许。琼花消息竟飘零，荒台古苑春无主。想莺花，十二楼前，迷离烟雨。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5c1024b4247b7bf9d','玉蝴蝶','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"春日恼人花鸟，香车宝马，陌上成行。相思满地，芳草绿到西江。旧衣香。留他荀令，新曲误，顾了周郎。枉思量。金壶滴博，银箭丁当。\",\"茫茫。玉人何处，寻伊消息，瘦了容光。只剩些些，影儿作对梦儿双。恨多情、雁传银字，愁不语、花落纱窗。对斜阳。珠帘卷起，又是昏黄。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5cb2b2155856f8e6c','金明池','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"似雨江梅，如烟飞絮，细细香风欲送。江上梦回青琐闼，佩声却是谁家凤？几番空对紫薇花，而今悔、虚害也黄粱梦。正宿雨初醒，春眠未起，一派画楼钟动。\",\"叹谪仙先朝供奉。看春色流光，雨梢花重。西楼倚，锦筝低按，东郭卧、玉樽休空。莽游仙、白了人头，但芝草连天，丹霞封洞。待饮马榆关，拂衣黄阁，古月梅花三弄。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5cf9bc03e0b30b35b','寻芳草·别恨','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"几阵杜鹃啼，却在那，杏花深处。小禽儿，唤得人归去，唤不得愁归去。\",\"离别又春深，最恨也，多情飞絮。恨柳丝，系得离愁住，系不得离人住。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5d28a69ab630acfcc','满江红·惆怅','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"渌水三年，小桥畔，相逢曾赋。长凝伫，青骢有影，章台无路。月下凭肩衣尚暖，楼头密约书成蠹。访城西、惟有画檐蛛，丝如故。\",\"晓云意，人空慕。题诗客，愁应诉。奈绿阴满径，乍逢崔护。芍药已付春梦远，杨花有被东风误。想雕栏、不追却沾泥，言还齼。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5e80d57f19c8d7d0b','婆罗门引·春尽夜','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明','在明末文坛上，夏完淳是一位少年诗人，抗清志士，其诗词作品多寓时事之叹，慷慨悲壮。这首以“春尽夜”为题的词，表面全是写景，但联系明朝将亡的形势，细味“辞却江南三月，何处梦堪温”诸句，工丽婉曲，语极含蓄，当是有所寄托。 ','[\"晚鸦飞去，一枝花影送黄昏。春归不阻重门。辞却江南三月，何处梦堪温？更阶前新绿，空锁芳尘。\",\"随风摇曳云。不须兰棹朱轮。只有梧桐枝上，留得三分。多情皓魄，怕明宵、还照旧钗痕。登楼望、柳外销魂。\"]',NULL,NULL,NULL,'[\"不阻重门：即不为重门所阻。\",\"兰棹朱轮：指游船、游车。\",\"皓魄：指月亮。 \"]'),('e647d10f738ecab5f32f182e79da8ae8','浣溪沙','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"柳色深深听晓莺，玉阑愁依梦分明，闲花斜落小钗横。\",\"蝴蝶前生原夜合，杨花身后作浮萍，东风轻薄误多情。\"]',NULL,NULL,NULL,NULL),('e647d10f738ecab5f32f182ee42ce005','浣溪沙','夏完淳','e647d10f738ecab56ba3af5d8d420fa3','明',NULL,'[\"剪剪猩狨试晚霞，斜风轻送七香车，倚阑低亸鬓云斜。\",\"难道郎心风外絮，可怜妾命梦中花。月华流恨到天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f739f14665b02a5224634bda2','柳枝词四首','郭谏臣','e647d10f739f1466471c75eedc5c768b','明',NULL,'[\"雨织金丝绿未敷，隔窗偷眼看模糊。画眉京兆今何处，试问双眉得似无。\"]',NULL,NULL,NULL,NULL),('e647d10f739f14665b02a5225c15f0e7','柳枝词四首','郭谏臣','e647d10f739f1466471c75eedc5c768b','明',NULL,'[\"袅袅柔条锁翠烟，随风乱舞绿窗前。楚王宫里人多少，如此腰肢亦可怜。\"]',NULL,NULL,NULL,NULL),('e647d10f739f14665b02a522d4ba2d21','柳枝词四首','郭谏臣','e647d10f739f1466471c75eedc5c768b','明',NULL,'[\"陌上丝丝带雨微，遥知春傍柳条归。一年一度常如此，昔日章台今已非。\"]',NULL,NULL,NULL,NULL),('e647d10f739f14665b02a522f85a5052','柳枝词四首','郭谏臣','e647d10f739f1466471c75eedc5c768b','明',NULL,'[\"河桥青眼竞窥春，淡著烟光别样新。留却长条休浪折，春来多少远行人。\"]',NULL,NULL,NULL,NULL),('e647d10f739f1466dd6ad433eb75fb27','戏为柳枝词赠别二首','郭谏臣','e647d10f739f1466471c75eedc5c768b','明',NULL,'[\"岸头柳色近鹅黄，丝短那堪别恨长。寄语东风休作态，天涯到处断人肠。\"]',NULL,NULL,NULL,NULL),('e647d10f739f1466dd6ad433ff3337f4','戏为柳枝词赠别二首','郭谏臣','e647d10f739f1466471c75eedc5c768b','明',NULL,'[\"东风昨夜消残雪，弱柳丝丝渐堪折。上林春色自多情，莫向河桥频送别。\"]',NULL,NULL,NULL,NULL),('e647d10f73a3749d4334969fa3b0ee50','蝶恋花','许肇篪','e647d10f73a3749dd1457b72d1457b72','明',NULL,'[\"雨饯云邮春去矣，燕嘴泥香，暗度疏帘里。底事玉楼人未起，泪痕染透鸳鸯被。\",\"约腕金环空自委，花瓣无情，尽日随流水。唤到侍儿何处使，秋千架外寻梅子。\"]',NULL,NULL,NULL,NULL),('e647d10f73b419618dfd1984591481c6','次韵杨柳枝词','杭济','e647d10f73b419616786f3c6d63fbf8c','明',NULL,'[\"东风袅袅拂朱阑，万树千条露未干。玉涘声中人不见，倚楼烟雨正春寒。\"]',NULL,NULL,NULL,NULL),('e647d10f73b419618dfd19847c786ccb','次韵杨柳枝词','杭济','e647d10f73b419616786f3c6d63fbf8c','明',NULL,'[\"玉沟萦绕水烟凉，嫩叶垂垂窣地长。百尺宫墙遮不尽，飞花犹自入昭阳。\"]',NULL,NULL,NULL,NULL),('e647d10f745ff36d1be338d8c5e4a3a0','昭君怨·柳色','江广','e647d10f745ff36dd1457b72d1457b72','明',NULL,'[\"低映垂帘庭院。蝉语不知深浅。断续好风牵。几曾闲。\",\"侵晚翠筠同茜。浑了云山一片。欹影几条妍。夕阳边。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd0001582c9463700c0a','阙题送司徒叶公致仕','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"三朝元老，忽赋归来，未了君王眷瞩。平生事满丹青，有何不足。乡里争看天上人，笑声喧动西湖曲。从今后、山南山北，到处随吾所欲。\",\"曾读汉唐宋，许多年，只一本名臣录。试与从头点检，几人全福。天留绿野旧规模，清风吹醒红尘目。任教他、桃李开遍，公门不如松菊。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd000d90426e30d89da3','玉烛新送陆宪长之江西','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"今岁春王正。报道大江西，宪台增胜。寒生万壑，冰壶里、更明月相辉映。\",\"浙江今日，清风动处秋潮定。卷山雨、百鉴堂前，为万姓新观听。\",\"平生志在经纶，看案牍如山，等闲酬应。尚馀高兴。\",\"吊孺子、东湖独穿云径。霜严江净。古柏枝岁寒逾劲。\",\"有天书、行召君还，庙堂执政。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd001d42a12b01b14e3b','水调歌头·送吕宪副之云南','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"晴色豁双眸，玉节向南游。乍离天上宫阙，声名已到遐陬。试看平生名节，好似迎风寒露，冰在玉壶秋。万世朝廷法度，一一请从头。\",\"笑谈里，使化外，慕中州。皇仁随处，宣布车骑肯淹留。民有衔冤茹苦，吏有甘刑黩货，凭我使君筹。天子有明见，不日召咨诹。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd004334969f0b308f78','蝶恋花','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"三年卧病今年好。二月春寒，又被东风恼。古人诗语今人道。傍柳随花方未了。\",\"闲学少年还叹老。击节长歌，不恨知音少。流水桥边亭子小。古琴时有幽人抱。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd004334969f0e85e125','蝶恋花','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"屈指一阳今九九。绿起郊原，草色无还有。燕子归来应不久。主人病后初开酒。\",\"次第春光诗管取。论清景刚，道是才黄柳。半醒半醉花开后。几多人等闲白首。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd004334969f56b6a437','蝶恋花','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"相逢谁谓吟诗瘦。闻道春来，筋力全胜旧。曾将春酒遥称寿。今年准拟明年又。\",\"梦里周公应不复。笔底春秋，莫问何时就。老眼摩挲凌宇宙。一声啼鸟西堂昼。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd004334969f6cd2d786','蝶恋花','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"故人千里书中意。前日江山，今日还增气。呼童为扫门前地。观风使者春将至。\",\"行空天马非凡骑。第二泉头，酌水能心醉。漫道荐贤名已遂。桃李笑东风多事。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd005b1fb09ba5087f44','满庭芳·送林方伯考绩','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"春满吴山，花深薇省，徵书将下天边。画船开处，人道似登仙。不待天官书考，御屏上、姓字高悬。最好是，甘棠蔽芾，重赋召南篇。\",\"江湖千万里，一骢五马，犹记当年。豸冠依旧在，袍锦红鲜。请看都台松柏，冰霜后、老翠含烟。重整顿，胸中舟楫，风日爱前川。\"]',NULL,NULL,NULL,NULL),('e647d10f7478cd00d1229ff98a7b4122','钓鱼词','邵宝','e647d10f7478cd0007042ac75cce8ded','明',NULL,'[\"芦花秋水浅，不用系扁舟。便是被风飘去，不到海东头。莫问蓬莱何处，莫问瀛洲何处，适意是真游。乾坤千万古，遗下这风流。\",\"一竿三尺线，轻扬钓鱼钩。更爱相亲相近水中鸥。不看溪风山月，即看渚蒲汀草，自识春秋。沧洲吾道在，江汉日悠悠。\"]',NULL,NULL,NULL,NULL),('e647d10f7480977fa5bec3a360916313','满江红·和岳忠武韵','李文缵','e647d10f7480977fd1457b72d1457b72','明',NULL,'[\"狩泣苍麟，叹盲左、书传未歇。空痛绝，王师帝佐，忠魂惨烈。白草黄沙鸣塞雁，青霜紫电看秦月。望湖滨、顿首鬣封祠，同仇切。\",\"国已破，耻谁雪；乱未已，恨难灭。幸孙曾尚在，蒸尝无缺。灵武旋颁哀痛诏，干将犹带模糊血。卷纶竿、闲钓小桥西，心依阙。\"]',NULL,NULL,NULL,NULL),('e647d10f74f6b4d5154effbd265a2921','浪淘沙','沈宗','e647d10f74f6b4d5d1457b72d1457b72','明',NULL,'[\"倦绣到如今，睡思沉沉，推琴罢阮怯繁音。香梦不知何处去，帘月偏深。\",\"待把坠欢寻，夜夜孤衾，瑶阶清影倍伤心。忆着喃喃私语际，月也难禁。\"]',NULL,NULL,NULL,NULL),('e647d10f74f6b4d5d9f3990638aa4514','诉衷情·近江行有感','沈宗','e647d10f74f6b4d5d1457b72d1457b72','明',NULL,'[\"露浓霞淡，说是严冬尚早。迷离一望丹枫，无复青青芳草。伴我秋容寂寂，野服疏疏，漫道风光好。\",\"孤帆悄，回首凤城春老。人生如寄，怎得常年少，金尊倒。剑光锈涩，雄心濩落，望中残照，时被浮云绕。\"]',NULL,NULL,NULL,NULL),('e647d10f750f585ae2a2f8922d70e797','南歌子·塞上','杜陵生','e647d10f750f585ad1457b72d1457b72','明',NULL,'[\"草暖鸳鸯泊，沙寒鸡鹿城。芦管一声声，故乡千里月，梦难成。\"]',NULL,NULL,NULL,NULL),('e647d10f75a235c21993a21e323924f2','满江红·移居严庄有感见《林下词选》、《众香词》、《今词初集》、《昭代词选》','顾道善','e647d10f75a235c2d1457b72d1457b72','明',NULL,'[\"禾黍斜阳，村皋外、红霞飞灭。回首处、依稀风景，繁华销歇。沼冷鸳鸯荷泣雨，台荒麋鹿松摇月。忽送来、野寺断钟声，疏篱缺。\",\"清晓怨，帘钩揭。王孙梦，鹃啼血。问天涯何处，铜仙金阙。猿鹤三秋淹短草，风云万里悲高碣。料明朝、好镜不相瞒，霜侵发。\"]',NULL,NULL,NULL,NULL),('e647d10f75a235c2717ea037cd07f018','蝶恋花·闺情见《林下词选》、《众香词》','顾道善','e647d10f75a235c2d1457b72d1457b72','明',NULL,'[\"日射纱窗花影透。满镜春山，雪面妆成候。立遍苍苔寻草斗。鸾针凤线抛残绣。\",\"拜佛朝持般若咒。脉脉含情，徙倚消长昼。闻道知愁还未久。去年学得吹箫后。\"]',NULL,NULL,NULL,NULL),('e647d10f75a235c2e429ae79ed5458e7','如梦令','顾道善','e647d10f75a235c2d1457b72d1457b72','明',NULL,'[\"高柳蝉声哽咽。四壁蛩吟悲切。丹桂发天香，疑似广寒宫阙。\",\"八月。八月。又是中秋佳节。\"]',NULL,NULL,NULL,NULL),('e647d10f7661d21c9b02ba6d902eb603','菩萨蛮','沈窠','e647d10f7661d21cd1457b72d1457b72','明',NULL,'[\"朦胧倦蝶和花睡，一钩新月衔山翠。鹦鹉梦偏惊，小鬟帘外灯。\",\"画图披粉墨，树树留春色。忽忆旧时情，双眉柳样青。\"]',NULL,NULL,NULL,NULL),('e647d10f77588490f1bb4799a80492f7','早梅芳','吴洪','e647d10f77588490e94f63f58c00dee2','明',NULL,'[\"柳将眠，桃欲笑，又是清明到。玉河冰泮，无数金鱼弄池藻。联车油壁软，并马丝鞭袅。喜凤城丽景，游览趁春晓。\",\"望瀛洲，纷翠葆，花满琼华岛。玉睷西去，金碧觚棱耸云表。散衙常过午，下直须归早。更相期，提壶同藉草。\"]',NULL,NULL,NULL,NULL),('e647d10f77a1e3059ef5ce704676b53f','醉花阴','宋存标','e647d10f77a1e305d1457b72d1457b72','明',NULL,'[\"香散飞花衣袖窄，浪浴双鸳翼。明月爱随人，一缕红云，暗里争颜色。\",\"独立回廊闻太息，梦里曾相识。玉指映柔荑，欲折花枝，睡起娇无力。\"]',NULL,NULL,NULL,NULL),('e647d10f77a4694383cfc9c202717dd7','竹枝词','陈子壮','e647d10f77a46943309fee4e8d420fa3','明',NULL,'[\"积岁官租此岁偿，青青无土复抽秧。知他莂尽禾中草，若个丁儿出手强。\"]',NULL,NULL,NULL,NULL),('e647d10f77a4694383cfc9c20bb26b75','竹枝词','陈子壮','e647d10f77a46943309fee4e8d420fa3','明',NULL,'[\"淮北抛荒已渐多，争乾争雨奈时何。东庄秫薥西庄麦，愁向南人说稻禾。\"]',NULL,NULL,NULL,NULL),('e647d10f77a4694383cfc9c2268b3933','竹枝词','陈子壮','e647d10f77a46943309fee4e8d420fa3','明',NULL,'[\"屋冷鸦飞村路长，大姑行嫁细姑忙。茜红不耐单衫子，撞著前头铁裲裆。\"]',NULL,NULL,NULL,NULL),('e647d10f77a4694383cfc9c285e05dd2','竹枝词','陈子壮','e647d10f77a46943309fee4e8d420fa3','明',NULL,'[\"遍地连蔓瓜色新，啖瓜休问种瓜人。道傍纵有高轩过，遮莫柴门野草茵。\"]',NULL,NULL,NULL,NULL),('e647d10f77a4694383cfc9c2976352fd','竹枝词','陈子壮','e647d10f77a46943309fee4e8d420fa3','明',NULL,'[\"片片蜻蜓尔但嬉，逾田跨野拣新枝。蜻蜓未解将身化，撼树西风忆尔时。\"]',NULL,NULL,NULL,NULL),('e647d10f78444a6e698f44876b9a452d','花深深闺怨','顾媚','e647d10f78444a6ed1dc3a824f398cb9','明',NULL,'[\"花飘零。帘前暮雨风声声。风声声。不知侬恨，强要侬听。\",\"妆台独坐伤离情。愁容夜夜羞银灯。羞银灯。腰肢瘦损，影亦伶仃。\"]',NULL,NULL,NULL,NULL),('e647d10f78444a6e90e8c169561d86b1','虞美人·答远山李夫人寄梦','顾媚','e647d10f78444a6ed1dc3a824f398cb9','明',NULL,'[\"春明一别鱼书悄。红泪沾襟小。却怜好梦渡江来。正是离人无那、倚妆台。\",\"朱栏碧树江南路。心事都如雾。几时载月向秦淮。收拾诗囊画轴、称心怀。\"]',NULL,NULL,NULL,NULL),('e647d10f78444a6eaf5d11ab1e8d040f','千秋岁·送远山李夫人南归','顾媚','e647d10f78444a6ed1dc3a824f398cb9','明',NULL,'[\"几般离索。只有今番恶。塞柳凄，宫槐落。月明芳草路，人去真珠阁。问何日、衣香钗影同绡幕。\",\"曾寻寒食约。每共花前酌。事已休，情如昨。半船红烛冷，一棹青山泊。凭任取、长安裘马争轻薄。\"]',NULL,NULL,NULL,NULL),('e647d10f787e41be90bdbd828859e0f2','满江红','吴易','e647d10f787e41bed1457b72d1457b72','明',NULL,'[\"斗大江山，经几度、兴亡事业。瞥眼处、英雄成败，底须重说。香水锦帆歌舞罢，虎丘鹤市精灵歇。尚翻来、吴越旧春秋，伤心切。\",\"伍胥耻，荆城雪；申胥恨，秦庭咽。羞比肩种蠡，一时人杰。花月烟横西子黛，鱼龙沫喷鸱夷血。到而今、薪胆向谁论，冲冠发。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94054443a2f3865cc3','柳梢青·题具庆堂','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"素发严亲，酡颜慈母，眉寿双全。屋润家肥，儿恭妇顺，孙更能贤。\",\"夫妻快活团圆。这想是、前生福缘。更积阴功，仍行好事，长享耆年。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb941fce7dc80f179f4c','木兰花慢·庆寿','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"过元宵五日，灯山罢、寿筵张。喜盛集亲朋，争喧鼓吹，满泛壶觞。八仙共教童子，送蟠桃封寄与翁尝。甘比玉杯云液，冷如金碗冰浆。\",\"佳儿拜捧上高堂。擘破散奇香。恰半枚入口，身田温暖，腑脏清凉。管取延年却老，转颜红发黑更瞳方。此去才周花甲，从头再数何妨。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb943e3fa5e665abf02d','玉漏迟·庆表兄陈安行初度','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"水风萍末起。三庚乍退，新凉堪喜。玩竹吟翁，此日瑞呈弧矢。阿母亲来庆寿，赐玉液、清香滑美。端的延年却疾，颐神健体。\",\"快饮不敢推辞，试跪啜才吞，六尘如洗。换骨更形，只消数瓯而已。自是长生有分，便可在地仙班里。却说道，人中再谁如你。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb945039ec154ec4f5c7','玉烛新寿舍弟昌明初度','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"春光忙似箭。又三月初三，牡丹开遍。卯君初度，当今日、祝寿惟深缱绻。八人同气，但只睹五人颜面。我一个、分外龙钟，难似四人强健。\",\"如今骨肉无多，但有兴能来，且休辞倦。更教儿侄。愿个个、努力圣经贤传。空词一阕，笑写当、袖中鹅面。期岁岁生朝，持杯乾劝。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94526aba52c26297e4','风流子·书舍卷题周参政南峰','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"钱塘最胜处，龙井畔、此地是君家。有垂柳引莺，古松巢鹤，石栽蒲草，池种荷花。客来访、绿衣铺座席，红袖过汤茶。评史讲经，赋诗观帖，竹间棋局，窗下琵琶。\",\"况湖山清雅，僧房占、不尽水竹烟霞。赢得老辞画省，荣赐乌纱。便重寻旧隐，歌当细听，酒当满泛，休问其他。人道昔时书屋，今更光华。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94573f4581f3d0f012','水龙吟·题都帅冯侯乃子二乐堂','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"豫章徐孺湖边，粉墙画戟元戎第。高闳昼静，疏帘风淡，博山香细。椿树长荣，萱丛镇茂，荆花连蒂。有珍殽脆滑，纯绵轻暖，凡奉养、都加意。\",\"先祖竭忠帝室。到如令、子孙承继。将门有将，非徒英勇，仍能孝义。百战成功，万钟为养，也知容易。愿斯堂、八柱坚如铁拄，永留人世。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb945ef4101d1216899d','天仙子·题赵仲穆图','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"公子才华偏蕴藉。九畹春光生笔下。幻成花叶恰如真，他看罢。咱看罢。不信根苗原是画。\",\"醉眼摩挲惊复讶。端的人能移造化。风不谢。雨不谢。一任卷将堂上挂。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb945ef4101daff3a3ce','天仙子·题赵仲穆图','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"忆昔维舟湘水曲。露浥芳蕤飘远馥。褰裳徐步踏晴沙，东一簇。西一簇。尽日徘徊看不足。\",\"叹息光阴如转毂。老去观图揩病目。花绝俗。叶绝俗。想像王孙清似玉。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb946f2860f5039f1cbf','摸鱼儿·辛丑生朝自寿','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"笑吾生、为缘凡骨，神仙亲遇难度。随群逐队埃尘里，年少遽成衰暮。忙退步。这凤翼、龙鳞老拙难攀附。况当末路。亟纳禄投闲，养疴延寿，随意做诗赋。\",\"悬弧旦，罔极亲恩已负。持身惟谨清素。繁华易歇同朝露，叹几个人能悟。非义富。非义贵、石崇元载何须慕。乘除有数。全晚节尤难，粗衣淡饭，天自管分付。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94818f91817a856687','鹊桥仙·自述','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"儒冠相误，虚名相误，衰病又还相误。自怜身世总无成，乾枉了、青春虚度。\",\"书生曾做，京官曾做，方面也曾亲做。颠来倒去竟如何，只落得、一贫如故。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94893ef5d3679b0037','玉漏迟·送费同知考满之京','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"螺川清彻底。地灵人杰，俗敦廉耻。阖郡民家，十有九家诗礼。幸得循良二守，共抚字、政平讼理。多少贫穷感戴章，逢称美倏。\",\"忽乐及瓜期，看课最天官，上闻丹扆。不次超迁，位联六卿班里。此际同寅北望，比别个倍增欢喜。端的是。人生似公有几。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb949cc3a70804806325','长相思·春雨','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"怕倾盆。要倾盆。伞笠都无强出门。满身泥水痕。\",\"行厌闻。坐厌闻。溜响从朝达夜分。聒聋双耳根。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb949cc3a7087b86df31','长相思·春雨','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"朝淋漓。暮淋漓。到处墙倾土屋欹。放晴知几时。\",\"湿花枝。洗花枝。平地弥漫浸作池。蓑衣终日披。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb949e3a70e21162f837','摸鱼儿·怀旧','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"记昔年、十分轻健，秦淮白下游遍。结交尽是高怀侣，多伴舞衫歌扇。心眷恋。纵雨阻、泥妨幽兴何曾倦。曲房小院。有鲫鲙银丝，蚁浮绿酝，畅饮醉仍劝。\",\"分离也，恰似云鸿海燕。音书特寄无便。风流杜牧今憔悴，谩想柳眉花面。江万转。山万叠、怎生能勾重相见。光阴似箭。回首是衰翁，思量前事，清泪落如霰。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94a7485558f978f8c6','石州慢·咏青娥眉淡竹草','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"异草从来花别，另一般样颜色。丛间开遍，既非红紫，又非黄白。闲园旷野，多少黛嫩螺娇，冰盘带露侵晨摘。磁钵放轻研，翠脂凝光泽。\",\"奇特。最风流处，宝镜台前，绣香奁侧。想未欣匀面，慵将填额，懒施粉饰。且付描扇描图，底须去问倭夷国。谁制与佳名，也端然消得。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94b44ddc8a7e607d4d','沁园春·山庄即事','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"山是何名，族为甚姓，地本荒凉。水远土硗田狭，众人弃置，天教与我，卜筑成庄。岭月为朋，溪云当客，更有林风时送凉。养衰病，惟静窥周孔，默契程张。\",\"升沈宠辱都忘，且懵懵腾腾，醉几场。到东门叹犬，华亭叹鹤，悔之晚矣，徒自悲伤。幻梦轩裳生涯笔，砚陵谷从他海复桑。幽闲处，谢天留老眼，坐阅兴亡。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94c300dba17c7efd3d','满庭芳·贺人生日四月年六十','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"梅弹垂金，柳绵飘雪，绿阴清昼偏长。画堂初晓，庆寿沸笙簧。花甲从头再起，任教他、乌兔奔忙。悬弧旦，才登耳顺，痛饮又何妨。\",\"年年逢此日，新茶新笋，件件堪尝。喜子能，干蛊孙战文场。那更闲身自在，且休论、紫绶金章。人都道，天留老眼，安坐阅沧桑。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94c45ce16c1a01e291','贺新郎·新婚','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"冬暖天晴霁。灿三星、居然在户，焕辉庭砌。照见堂前荆花树，一朵忽成双蒂。听花底、笙歌声沸。传道仙娥辞洞府，跨青鸾、喜会文章婿。态窈窕，容端丽。\",\"知郎早有凌云志。小科场、探花手段，好教先试。仍把风流京兆笔，巧画翠眉纤细。却慢慢、大登科第。况是妙年谐燕尔，且亲前、齐著斓斑戏。众喝采，好兄弟。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94d6742260f8f6a005','临江仙·题墨菊送陈知府行','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"一段秋光新写就，盛开不待重阳。枝梢花叶互低昂。巧将篱下艳，幻作画中芳。\",\"为问渊明何独爱，爱他晚节寒香。从今移种上林旁。时时承雨露，岁岁傲风霜。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94df2be99c080f5d32','杨柳枝','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"春来婀娜袅烟青，花落春江化作萍。随水东流飘泊去，比郎踪迹更无凭。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94df2be99c2ded74bc','杨柳枝','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"摇青曳绿汴河边，浓比征袍色更鲜。谩有柔条千万缕，如何不解系郎船。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94df2be99c94facc0a','杨柳枝','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"参差几树瞰长河，树树垂金拂绿波。不是秋来易零落，自缘人世别离多。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94df2be99c9e5dbbee','杨柳枝','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"细叶如眉绿未匀，修眉浑似带愁颦。输他桃杏东风里，不管南游北去人。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94f58a21111ef48cc0','归朝欢题送陈知府考满','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"灏气渐消三伏暑。商吹送凉侵白苎。驿亭张宴饯邦侯，管弦低处闻金缕。明月映鹤渚。殷勤频劝杯中醑。这九年，用心抚恤，百姓似儿女。\",\"青云万里期高举。第一功名端的取。曾覃惠泽及疲民，仍将忠爱酬明主。临别听我语。岩廊接武夔皋侣。把仙瓢，好教泻尽，天下望霖雨。\"]',NULL,NULL,NULL,NULL),('e647d10f7893eb94f8bc3e098d90c4f6','南乡子·送人之京','李昌祺','e647d10f7893eb9479a49b3e6786f3c6','明',NULL,'[\"北上别家山。韦布趋朝识圣颜。水路七千端的远，艰难。了却公家事早还。\",\"暑退夏初残。拜别红云虎豹关。归日正逢粳稻熟，加餐。受用乡园暮景闲。\"]',NULL,NULL,NULL,NULL),('e647d10f798a11f94f7dc79e2b085331','浣溪沙·为侍女随春作','叶小纨','e647d10f798a11f9d1457b72d1457b72','明',NULL,'[\"髻薄金钗半亸轻。佯羞微笑隐湘屏。嫩红染面太多情。\",\"长怨曲阑看门鸭，惯嗔南陌听啼莺。月明帘下理瑶筝。\"]',NULL,NULL,NULL,NULL),('e647d10f7b5ec1caa3e8d36213509eae','瑞龙吟·送司马祭酒省亲','张弼','e647d10f7b5ec1caa424ed4b6d3a1e06','明',NULL,'[\"秋光子。正遇月窟香清，天街凉早。诸生云会都门，斯□□□，今朝出道。为亲老。常念承颜无便，陈情有表。□□暂许南归，捧觞戏彩，增延寿考。金带绯袍趋拜，古□如此从来少。司马相公曾孙，全尽忠孝。\",\"越山涑水，千载相辉耀。娱亲暇、松风萝月，幽情倾倒。只恐丹山鸟。口衔一纸天风晓。催入蓬壶岛。东阁下，还将丝纶来草。燮调二气，万年熙皞。\"]',NULL,NULL,NULL,NULL),('e647d10f7b5ec1cab3ef6e2d78b99cb8','鹊桥仙·为马宗海同年作','张弼','e647d10f7b5ec1caa424ed4b6d3a1e06','明',NULL,'[\"琼林宴罢，萍踪无定，空自传消寄信。天涯海角此相逢，这段旧、情怀难尽。\",\"交情似海，宦途如梦，后会安知何处。大家尽力报君亲，且莫计、东西去住。\"]',NULL,NULL,NULL,NULL),('e647d10f7c958bcef6ea81d253479c36','风中柳湖上','陈鎏','e647d10f7c958bceb5488aef70efba66','明',NULL,'[\"万顷平湖，胜在吴山高处。两峰间、芦汀鸥渚。随波荡漾，见渔舟无数。云帆外、几村烟树。\",\"茶磨郊台，千古浮云去住。越溪中、五湖东注。桥边杨柳，已条条金缕。且留连、莫教春去。\"]',NULL,NULL,NULL,NULL),('e647d10f7d4649501c7ad911351c86b0','稔冈竹枝词','梁朝钟','e647d10f7d46495066ee84298d420fa3','明',NULL,'[\"四月江头尽种秧，一春无事此时忙。几多筐馌大堤语，辛苦长途为送将。\"]',NULL,NULL,NULL,NULL),('e647d10f7d4649501c7ad911632b720a','稔冈竹枝词','梁朝钟','e647d10f7d46495066ee84298d420fa3','明',NULL,'[\"清明前后雨如丝，压纸人家庭未归。桥上点残乌桕烛，烛花粗滑泪如泥。\"]',NULL,NULL,NULL,NULL),('e647d10f7d4649501c7ad911cc7b16ed','稔冈竹枝词','梁朝钟','e647d10f7d46495066ee84298d420fa3','明',NULL,'[\"微风疏雨走村翁，秃老无枝几树枫。儿童昨日迎春骑，照见门前溪水红。\"]',NULL,NULL,NULL,NULL),('e647d10f7dc9e109d988e434b9ebbb04','金明池金陵怀古','魏允楠','e647d10f7dc9e109d1457b72d1457b72','明',NULL,'[\"燕子矶边，凤凰山上，又是狂风骤雨。金砌玉阶何处问，尽燕麦兔葵满路。问秦淮、桂楫兰桡，却只是、衰草垂垂将暮。叹绛阙蛛萦，纱窗尘渍，一片晚鸦来去。\",\"想王谢风流何处？便紫燕黄莺，一时无主。看当日、词臣狎客，空剩得、白杨黄土。最伤心、景物三春，教苑里蛙声、公私相诉。但渡口寒烟，亭中皓月，兀自依依芳树。\"]',NULL,NULL,NULL,NULL),('e647d10f7dff73435c1daf6fd8019638','失调名','陈鹄','e647d10f7dff73437949e456b147a61c','明',NULL,'[\"莫待柳吹绵。吹绵时杜鹃。\"]',NULL,NULL,NULL,NULL),('e647d10f7e3501382eabdb0f366ad85c','忆秦娥','方以智','e647d10f7e3501387a6a74cb647c722b','明',NULL,'[\"花如雪，东风夜扫苏堤月。苏堤月，香销南国，几回圆缺。\",\"钱塘江上潮声歇，江边杨柳谁攀折。谁攀折，西陵渡口，古今离别。\"]',NULL,NULL,NULL,NULL),('e647d10f7e79a113b739a3bfdc3e7b61','古调笑·令','陆宝','e647d10f7e79a113d1457b72d1457b72','明',NULL,'[\"竹坞，竹坞，坐荫空阶无数。有时弄影斜阳，个字淋漓短墙。\",\"墙短，墙短，分得秋声一半。\"]',NULL,NULL,NULL,NULL),('e647d10f7e92b3ab8f5d6093a45c82dd','向湖边','范兆芝','e647d10f7e92b3ab93279690c0560792','明',NULL,'[\"芳草洲前，波皴细縠，冷浸贺家遗宅。一曲清流，是狂奴曾乞。任当年鼓震渔阳，铃霖蜀道，不改湖光澄碧。水底眠来，缅高风未息。我亦生平，雅抱烟霞癖。\",\"又千条柳线，漾三分春色。鸭绿粼粼，更新添几尺。荡兰桡，欲与浮鸥敌。恰船似天上坐，来将汰击。渺渺予怀，喜青山犹昔。\"]',NULL,NULL,NULL,NULL),('e647d10f7f2142f283cfc9c21b50f38d','竹枝词','汪广洋','e647d10f7f2142f2d1457b72bad5f337','明',NULL,'[\"三百六十滩水清，桃花春浪近来生。催归不待临歧语，夜夜子规啼到明。\"]',NULL,NULL,NULL,NULL),('e647d10f7fff383038501bb9024db12c','杨柳枝·词','黎景义','e647d10f7fff383066ee8429480a0425','明',NULL,'[\"拂水含烟舞态轻，河边桥畔总无情。若教生长荒山里，不解行人唱渭城。\"]',NULL,NULL,NULL,NULL),('e647d10f802f4ba04c71d6222135e2c4','转应曲','姚广孝','e647d10f802f4ba09cb67ffb309928d4','明',NULL,'[\"斜日，斜日，门外马蹄声疾。林栖鸟尽飞还，霞彩红衔远山。\",\"山远，山远，莫怪行人归晚。\"]',NULL,NULL,NULL,NULL),('e647d10f81116faf107d9a1f860e1cb1','鹧鸪天·酬孝峙','钱继章','e647d10f81116fafd1457b72d1457b72','明','此词上阕起句以“发短”而起，不但具有很强的时代感，而且暗示了词人不屈的性格。据历史记载，满清贵族入主中原后，立即颁布了强制汉人梳长辫的政令，在清初顺治二年六月，江南民众还曾因满清的“剃发令”而奋起浴血抗战。词人以“发短”来暗示了对满清强制汉人梳长辫的不满。同时，词人以“髯长”反衬了“发短”，并借此描绘了汉族男子传统的壮美风度和潇洒气概。特别是“眉有棱”，表面是在写眉毛，其实词人通过棱棱眉毛暗示眼睛炯炯眼神。这正如鲁迅所说，刻画人物，最好是画眼睛。眼睛是心灵的窗户，可以借此透视词人的内心的明亮。到此，一位头发很短、胡须很长、眉毛高耸、怒目睁睁的形象跃然纸上。在明朝灭亡之后，不少怀故国之思的遗民墨客纷纷遁入僧门。然而，这些抱亡国之恨的“僧人”不同于那四大皆空的僧人。所以，词人写道：“病容突兀怪于僧。”这是描写自己的容颜衰弱。其实，词人虽说“怪于僧”，实际上暗示了自己难以诉说的复杂情感。接着“霜侵雨打寻常事，仿佛终南石里藤”中的“终南山”一词，化用陶渊明“采菊东篱下，悠然见南山”诗意，暗含了“隐居”之意。上阕结尾，词人通过有着很强的生命力的“石里藤”一任“霜侵雨打”而表现出“寻常事”，暗示了内心的坚强。前两句是词人面容表象的大特写镜头，充分显示其怪异；“霜侵”两句则是词人心态意象的比拟式描摹，着力传达其坚强。\n下阕以闲游、戏钓的飘逸气度，通过清峻自立的形象，进一步申足了上阕遗世独立、傲岸不羁的个性，同时自然地生发为“折腰久矣谢无能”的词意。首先，词人描写了自己不为官的闲游，戏钓的惬意。接着说“折腰久矣谢无能”，其中，“折腰”代替“做官”。此意源于东晋陶渊明“吾不能为五斗米折腰，拳拳事乡里小人邪”而毅然辞官归隐的典故。这里，更好地表现了词人坚决不与清朝统治者合作的气节。接着写道：“熏风未解池亭署，捧出新词字字冰。”词人借“熏风未解池亭署”来暗示了当时的社会环境的险恶，借“字字冰”暗示词人自己内心的凄凉。“熏风”跟上阕“霜侵”词意前后对照，上下呼应，共同渲染着象征着残酷苦涩的社会现实，同时也反激着、映衬着词人骨骼硬、心意冷，不畏险恶，不慕势禄（不屑于清政府笼络汉族士子的高官厚爵），宁可隐居深山穷壤、苦熬栉风淋沐雨的凄楚生活，却始终铮铮然清峻自立的高兀形象。成功的艺术形象正是诗词的生命所在。\n在艺术上，这首词主要表现在以下几方面：首先，前后照应。比如下阕“熏风”跟上阕“霜侵”的照应。其次，形象鲜明。词作由表入里，由近致远，由风貌到气质，从而丝丝入扣地熔铸出词人的立体塑像。第三，运用典故。词作中，词人暗用了东晋陶渊明“采菊东篱下，悠然见南山”和“不为五斗米折腰”的语典，含蓄表现了词人不愿为清朝效力的爱国品质。\n作为柳洲词派之中坚作家的著名词人，钱继章以此词的高超造诣，为柳州派赢得了词史上的不朽地位。','[\"发短髯长眉有棱，病容突兀怪于僧。霜侵雨打寻常事，仿佛终南石里藤。\",\"闲倚杖，戏临罾。折腰久矣谢无能。熏风未解池亭暑，捧出新词字字冰。\"]',NULL,'此人头发很短，鬍鬚很长，眉毛高耸，俨然正怒目睁睁地睥睨环宇；一副面带“病”容又颧骨凸起的奇特形状，比那光头垂眉、闭目参禅的和尚还怪模怪样。而他好似终南山石缝中生长出来的一根蔓延的青藤，一任严霜侵袭、暴雨击打，却等闲视之。\n他时或轻拄手杖闲适地慢步远瞻，时或戏把渔网以临水捕鱼而消遣光阴。他为官很久了，要他再出任官职，他以没有能力为借口推辞了。阵阵炎热的东南风呵，没有消解池亭中暑气；而他新作的词曲竟一个个字都是冰凉的，一派冷气。','《鹧鸪天·酬孝峙》是清代词人钱继章的作品。此词作者通过对自己形象的写照为中国词史留下的一幅爱国词人的肖像画。上阕通过描写外貌，突出了词人病态怪异形象，表现了他坚忍不拔的性格特点；下阕以闲游、戏钓的飘逸气度，进一步表现了词人遗世独立、傲岸不羁的品格，同时自然地生发为“折腰久矣谢无能”的词意。全词形象鲜明，用典贴切，以简洁劲拔的白描手法，由表入里，由近致远，由风貌到气质，层层推进，步步深入，描绘了一个富有立体感的，富有气节的人物形象。','[\"鹧鸪（zhègū）天：词牌名。双调，五十五字，押平声韵。也是曲牌名。南曲仙吕宫、北曲大石调都有。字句格律都与词牌相同。北曲用作小令，或用于套曲。南曲列为\\u201C引子\\u201D，多用于传奇剧的结尾处。\",\"孝峙：王屋，字孝峙，浙江嘉善人，明末文学家。\",\"发短：这里诗人剃短发，他有意与清廷明令留长发、结辫子的规定公然对抗。\",\"怪于僧：清初年许多士人遁入佛门，不与统治者合作。此句意思是说自己的模样怪得连和尚都不如，作者通过对自己的这种描写发泄心中极度的愤怒。\",\"寻常：即经常，或者平时。\",\"终南：终南山，在今西安市南。\",\"临罾（zēng）：指水边网鱼。罾，用竹竿做支架的方形渔网。\",\"折腰：指出任官职。这里用东晋文学家陶渊明\\u201C不为五斗米折腰\\u201D而辞官归隐的典故。\",\"熏风：即东南风。\"]'),('e647d10f81116faf154effbdf1a844d2','浪淘沙','钱继章','e647d10f81116fafd1457b72d1457b72','明',NULL,'[\"云意压山尖，故故相黏，绕篱败叶夜重添。不见黄花开一点，孤负陶潜。\",\"闷坐了无欢，旧恨新拈，西风一霎卷茅檐。欲买秋光无计可，笑拾榆钱。\"]',NULL,NULL,NULL,NULL),('e647d10f81116faf415d41bde993ebbd','谒金门','钱继章','e647d10f81116fafd1457b72d1457b72','明',NULL,'[\"斜阳促，弥望暮烟修竹。弱柳堤边芳草屋，一汀云影簇。\",\"趁取晚凉新浴，碧槛薄垂冰谷。小院落花香断续，月明风簌簌。\"]',NULL,NULL,NULL,NULL),('e647d10f81116faff32f182e01b9f4f9','浣溪沙','钱继章','e647d10f81116fafd1457b72d1457b72','明',NULL,'[\"睡损眉黄澹未添，凤屏残麝透花尖。下阶幽梦背人占。\",\"柳外疏莺声睨睆，竹边归燕语呢喃。斜风细雨隔重帘。\"]',NULL,NULL,NULL,NULL),('e647d10f81116faff32f182e825a138f','浣溪沙','钱继章','e647d10f81116fafd1457b72d1457b72','明',NULL,'[\"春尽园林褪闹红，阴阴静绿意从容。须眉俱碧住山翁。\",\"花卷半阶通履舄，柳开一面出帘栊。管人闲事是东风。\"]',NULL,NULL,NULL,NULL),('e647d10f814ee17f57659484f4f40530','长相思','袁彤芳','e647d10f814ee17fd1457b72d1457b72','明',NULL,'[\"风满楼，月满楼，月白风清动客愁。征鸿声且留。\",\"灯半篝，香半篝，灯烬香沉残梦悠。归舟天尽头。\"]',NULL,NULL,NULL,NULL),('e647d10f82079cf7d1941b67b5f284e8','拂霓裳','徐石麒','e647d10f82079cf795d309f0c1fea270','明',NULL,'[\"望中原，故宫锦树障烽烟。惊坐起，凉宵梦断蒋陵前。金人倾宝露，玉女绣苔钱。问当筵，有谁能、醉鼓渐离弦。\",\"西台哭罢，三户里、识遗贤。欹皂帽，吹箫乞食总堪怜。英雄身未死，屠钓技常连。又何颜，许青门、瓜种故侯田。\"]',NULL,NULL,NULL,NULL),('e647d10f8256af3049250e56dea2a071','满庭芳·题画','董其昌','e647d10f8256af30b2dd14031c54985e','明',NULL,'[\"宿雨初收，晓烟未泮，浮云都逐飞鸿。文君翠黛，一霎变颦容。多少风鬟雾鬓，青螺髻、飘堕空蒙。频骋望，征帆灭处，远霭与俱穷。\",\"古今来画手，谁如庄叟，笔底描风。有江南一派，北苑南宫。我亦烟霞骨相，闲点染、蒙懂难工。但记取、维摩诘语，山色有无中。\"]',NULL,NULL,NULL,NULL),('e647d10f82e82a2e4334969f9db23e05','蝶恋花','仁宗昭皇帝','e647d10f82e82a2ed1457b72d1457b72','明',NULL,'[\"烟抹霜林秋欲困，吹破胭脂，便觉西风润。翠袖怯寒愁一寸，谁家庭院黄昏信。\",\"明月修容生远恨，旋摘余娇，簪满佳人鬓。醉倚小阑花影近，不应先有春风分。\"]',NULL,NULL,NULL,NULL),('e647d10f838e3b175986fc9324774742','柳梢青·春情','欧阳铉','e647d10f838e3b17d1457b72d1457b72','明',NULL,'[\"春去杨花，无情有思，闲到窗纱。暮雨急催，峭寒轻透，香屑慵加。\",\"小楼孤倚堪嗟，风一剪、云欹鬓斜。燕子飞来，青梅如豆，不奈天涯。\"]',NULL,NULL,NULL,NULL),('e647d10f83f54f3b65999f98f7e938f5','玉楼春·夜坐','呼举','e647d10f83f54f3bd1457b72d1457b72','明',NULL,'[\"一灯半灭愁无数，河畔清蟾凉印户。闲庭细草乱蛩鸣，似共离人分泣露。\",\"玉楼遥隔湘江浦，黯黯离魂寻得去。秋钟夜半远随风，短梦惊回忘去路。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb30b440b53','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"九月吴江空水鲜，菊花篱落晚霞天。诗中尽说斜川好，不道斜川在眼前。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb334c2d5fb','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"八月吴江泊钓船，芙蓉花下绿杨边。鸳鸯一双堪入画，只可遥看莫近前。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb3427d7e42','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"冬半吴江水未坚，芦花枫叶尚依然。沙头一派天书字，知道鸿飞若个边。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb34b5b3727','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"十月吴江大有年，耞声两岸夕阳天。竞舂白粲输官里，不遣乡胥恼夜眠。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb3506ae799','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"二月吴江燕子飞，河豚欲上荻芽肥。侬家艇子前洲里，贪看春波忘却归。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb3544cacad','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"三月吴江柳正青，柳花飞去半为萍。蔬畦麦陇蔷薇架，妆点田家作画屏。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb355237ea6','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"腊月吴江更自妍，梅花开近竹林边。王猷可惜空归去，不见新晴雪后天。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb371975e82','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"六月吴江锦作天，青蒲绿柳间红莲。渔郎日见不知爱，空在江边住百年。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb38e93075b','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"四月吴江正插田，青秧白水暖生烟。回桡转入深村里，只见垂杨不见天。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb3b435bf96','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"正月吴江好放船，雪消冰泮水如天。江头杨柳千千树，记得东风似去年。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb3e5500907','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"五月吴江赛屈原，红旗画楫满晴川。鸱夷漂泊谁家事，寂寞胥门一炷烟。\"]',NULL,NULL,NULL,NULL),('e647d10f845293814f3b8bb3f268a321','旧有诵十二月吴江竹枝歌者戏效之得三首而止十一月廿三夜不寐因足成之诗成梦乘马上曲磴地名湖塘遇小儿杜姓者同行论处世之道甚悉','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"七月吴江斗巧天，鸡头菱角彩楼前。青裙荡桨谁家女，独自中流唱采莲。\"]',NULL,NULL,NULL,NULL),('e647d10f8452938168bdd1cdf1704db3','满江红·乙巳元宵','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"云月澄鲜，重城夜启。满目繁华，正江南春早，红灯初试。帘影千门摇粉黛，香尘十里随罗绮。论今宵、何止刻千金，堪游戏。\",\"欢未已，愁还继。暗想今年，遥怜往岁。四时相代如流水。上林春色三千里。望鳌山、凤辇是何年，心摇曳。\"]',NULL,NULL,NULL,NULL),('e647d10f8452938178e4e43a4a8c6f13','法驾导引·读蕳斋','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"雷隐隐，雷隐隐，列缺挟飞车。一掷火铃三万里，人间何处着妖邪。\",\"归路海天霞。\"]',NULL,NULL,NULL,NULL),('e647d10f8452938178e4e43a65b0309c','法驾导引·读蕳斋','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"歌万寿，歌万寿，稽首玉宸尊。下视九州云五色，群龙济济会天门。\",\"一气俨长存。\"]',NULL,NULL,NULL,NULL),('e647d10f8452938178e4e43a98ef7ee8','法驾导引·读蕳斋','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"金风袅，金风袅，飞佩度银泓。仙袂六铢凉似水，抟桑东望火云蒸。\",\"回首指蓬瀛。\"]',NULL,NULL,NULL,NULL),('e647d10f8452938178e4e43aa5222705','法驾导引·读蕳斋','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"骖龙驾，骖龙驾，龙驾引鸾旌。罗列三光俱在下，青云回首玉皇城。\",\"风露杳冥冥。\"]',NULL,NULL,NULL,NULL),('e647d10f8452938178e4e43ad31854a1','法驾导引·读蕳斋','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"祥云起，祥云起，云起四天明。宝月宫中光气满，流黄一道坠明星。\",\"万寿圣人生。\"]',NULL,NULL,NULL,NULL),('e647d10f84529381e8d9e02bde08a9e6','浣溪沙·庚午二月廿二日书感时水后疫疠盛行守臣美迁去部使者方加税督责甚严','顾清','e647d10f8452938107042ac7109d2dd3','明',NULL,'[\"陌上风花半欲飞。楼头更著筦弦催。伤心自起探斜晖。\",\"燕子作双翻水去，蜂儿成阵采香归。园林谁为管芳菲。\"]',NULL,NULL,NULL,NULL),('e647d10f84e2c91331c59f20781f7163','念奴娇·西湖','张文秀','e647d10f84e2c913d1457b72d1457b72','明',NULL,'[\"平湖秋霁，见银蟾、光闪波纹摇绿。天上飘来，风韵细、桂子香生金粟。玉户玲珑，瑶台皎洁，影落嫦娥屋。夜深云际，一声谁弄横竹。\",\"醉倚六曲雕阑，望中悬白练，两峰垂瀑。净洗尘肠，摛秀句、一唾珠玑千斛。更欲凌空，铿锵环佩，仙驾骖鸾鹄。凤楼人远，独怜不见秦玉。\"]',NULL,NULL,NULL,NULL),('e647d10f8506c90ec2ebbe3b18dd4197','鹧鸪天','杨范','e647d10f8506c90ed1457b72d1457b72','明',NULL,'[\"东海东头看月升，秋风一叶井梧零。闲云飞尽天如洗，今夜星河分外明。\",\"舒倦体，坐残更，微闻促织砌间鸣。萧萧凉气侵人骨，为爱吟诗睡未成。\"]',NULL,NULL,NULL,NULL),('e647d10f854af6fd3185841046bb5d6f','山花子','支大纶','e647d10f854af6fdd1457b72d1457b72','明',NULL,'[\"枫叶霜催暮色残，相思人在碧云间。何处琼箫吹月下，倚阑干。\",\"梦断雨声鸡塞远，愁移花影凤楼寒。莫恨王孙消息断，万重山。\"]',NULL,NULL,NULL,NULL),('e647d10f85e3fc986cd45f88b4cda03e','江城子·村居','沈泓','e647d10f85e3fc98d1457b72d1457b72','明',NULL,'[\"长堤细柳隐虹桥，弄笙箫，伴渔樵。醉倚枫林、仰面挂诗瓢。释卷正逢秋水涨，临古渡，放轻舠。\",\"夕阳西下暗窗绡，雁声高，笛声遥。碧簟凉生、细雨燕辞巢。隔岸篝灯孤艇泊，沽浊酒，短扉敲。\"]',NULL,NULL,NULL,NULL),('e647d10f8667228c3769e8be836f2d5d','虞美人·春雨','沈钟鹤','e647d10f8667228cd1457b72d1457b72','明',NULL,'[\"春光九十谁分取，断送晨昏雨。桃花和泪湿胭脂，检点残红一半、未开时。\",\"长空拟把愁云扫，天梦浑难晓。柔肠不断却回环，咫尺相思人隔、万重山。\"]',NULL,NULL,NULL,NULL),('e647d10f872aba26e3acc968c41118f4','临江仙·纪游','姜实节','e647d10f872aba268d420fa352d080a3','明',NULL,'[\"梦断罗帏春睡浅，小楼风竹频敲。溪流一曲抹山腰，雨香初拂柳，日气渐烘桃。\",\"笑解金龟同买醉，故人雅集金宵。阄诗量酒兴还饶，晓钟齐女墓，残月大姑桥。\"]',NULL,NULL,NULL,NULL),('e647d10f8861f54f7d091d72dfaffdf6','蝶恋花·夏雨','郑溱','e647d10f8861f54fd1457b72d1457b72','明',NULL,'[\"过了黄梅寒尚重，出水新荷，不耐擎珠弄。几许愁怀难遣送，闭门回首都成梦。\",\"雨后还将蘘薜种，着破春衫，暂把衣空。一卷残编何所用，江干多少风波涌。\"]',NULL,NULL,NULL,NULL),('e647d10f886e55c2ba4cb7894c4a6c46','清江裂石西湖','屠隆','e647d10f886e55c2d010396cf18a6d1c','明',NULL,'[\"淼淼重湖背郭斜，永日坐蒹葭。四面山青不断，楼阁外、乱水明霞。有画船锦缆载词客，金翘杂佩，强半挟吴娃。水穷处、长林古寺，夏木绿阴遮。\",\"回首望空明，白鸥隐隐飞来，带一片轻沙。把酒问西湖，今来古往，都不管兴亡、旧恨年华。且与君、棹扁舟，听取哀弦急筑，散发弄荷花。\"]',NULL,NULL,NULL,NULL),('e647d10f88f6c4e99fe73ae0b35ee9d5','眼儿媚·过华湖','丁奉','e647d10f88f6c4e9d1457b72d1457b72','明',NULL,'[\"芦苇萧萧奏轻，烟雨隔南洲。小航冒冷，孤帆披湿，一路吟讴。\",\"闲人俨似天随子，幽兴五湖秋。相知只在，渡头渔叟，沙嘴眠鸥。\"]',NULL,NULL,NULL,NULL),('e647d10f89441ed5415d41bd3e4dccc3','谒金门','孙绍祖','e647d10f89441ed57fb8ceb3e60e81c4','明',NULL,'[\"春雨足，香嫩枝头微绿。渭水清清寒漱玉，客来茶正熟。\",\"午倦不堪重读，点就乳花飞瀑。七碗可人凉气肃，潇潇风助竹。\"]',NULL,NULL,NULL,NULL),('e647d10f895bb1585ecb6fa17cbc6b80','多丽','王肯堂','e647d10f895bb158c88d8d0a69d658d0','明',NULL,'[\"步花阴，满园春色飘零。过横塘、曲阑斜折，崎岖转入荷亭。坐无聊、起看画障，行还倦、闲拨炉薰。暗觉声喧，微窥影乱，映门一线露罗裙。双扉启、缟衣翠袖，相簇度银屏。霎时见，虚窗雪艳，素壁霞明。\",\"细看伊、莲随步起，果然兰并肌芬。脸盈盈、疑开蛤帐，腰细细、堪试香尘。腻发盘云，修眉偃月，珊瑚一颗蹙朱樱。擎桨处、春纤如削，浑是玉雕成。临别去，流波回觑，障面笑狂生。\"]',NULL,NULL,NULL,NULL),('e647d10f8994fdd59c70a6d8c3fabddc','踏莎行','李标','e647d10f8994fdd5d1457b721c54985e','明',NULL,'[\"砚点飞花，檐沾弱絮，炉烟小误湘帘暮。春风不解动人情，人情怕送春归去。\",\"细草粘天、斜阳挂树，天涯离别人何处。休休莫更倚危栏，远山正是愁人路。\"]',NULL,NULL,NULL,NULL),('e647d10f8a2b4a3971a62c1146e91a11','满江红·题碧梧翠竹送李阳春','张宁','e647d10f8a2b4a39d1457b72d1457b72','明',NULL,'[\"一曲清商，人别后、故园几度。想翠竹、碧梧风采，旧游何处。三径西风秋共老，满庭疏雨春都过。看苍苔、白石易黄昏，愁无数。\",\"峄山畔，淇泉路；空回首，佳期误。叹舞鸾鸣凤，归来迟暮。冷淡还如西草，凄迷番作江东树。且留他、素管候冰丝，重相和。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba534862caaee9194df45','如梦令·次黄山谷旧作','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"此日郊原花柳。暗度昨年时候。心绪强言欢，讨得梦中消瘦。\",\"生受。生受。南国江山如绣。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba5348c5dca9105f5f707','长相思·次刘青田先生作','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"时悠悠。宦悠悠。时宦相催不断头。般般件件愁。\",\"欲归休。未归休。菊花松蔓老春秋。岁月肯吾留。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba5349b02ba6d2ccc8726','菩萨蛮','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"绿阴深处迷花谷。谷花迷处深阴绿。春尽不留人。人留不尽春。\",\"燕来归路远。远路归来燕。帘卷怯轻寒。寒轻怯卷帘。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba534d24999954ac005bb','秋闺仿刘青田叠字叠句','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"相思相盼何时已。闲把闲愁理。玉人人倚玉阑杆，新月新秋新怯寒。\",\"日阴阴，夜深深，漏更更。叶叶声声响到明。梦儿成不成。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba534d9c635ea24adb528','西江月·春闺次秦少游作','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"裁就弓鞋样浅，绣成鸳枕针残。坐沉红烛有馀闲。日九回肠非懒。\",\"别馆疏棂风细，孤帏绣榻香寒。昔时云雨梦中难。欲觅佳期已晚。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba534f772b19f17365e93','渔家傲·丁丑重九日猎边外龙安山次先贤范希文词','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"搔首问天摩巨阙。平生有恨何时雪。天柱孤危疑欲折。空有舌。悲来独洒忧时血。\",\"画角一声天地裂。边风撼树惊魂掣。绝影骄骢看并逐。真捷足。将军应取燕然勒。\"]',NULL,NULL,NULL,NULL),('e647d10f8a3ba534f772b19f3eb31a2e','渔家傲·丁丑重九日猎边外龙安山次先贤范希文词','卢象升','e647d10f8a3ba5349e984c103c1e4bd6','明',NULL,'[\"怪是重阳风雨恶。东篱把菊寒酸作。醉掾当筵吹帽落。皆寂寞。人间漫说登高乐。\",\"何似今朝开眼目。秋山万叠尖如削。绝顶连云张锦幕。天摸著。昆仑下看阆风阁。\"]',NULL,NULL,NULL,NULL),('e647d10f8a591ae2f1d6044e76aa339f','和沈愚阊门柳枝词三首','苏平','e647d10f8a591ae2d1457b72d1457b72','明',NULL,'[\"拂水摇烟积翠深，阖闾城外昼阴阴。花飞便作浮萍草，恰似侬家荡子心。\"]',NULL,NULL,NULL,NULL),('e647d10f8a591ae2f1d6044eb664cf23','和沈愚阊门柳枝词三首','苏平','e647d10f8a591ae2d1457b72d1457b72','明',NULL,'[\"此地吴王旧市朝，空遗衰柳日萧萧。行人不用频回首，炀帝宫前更寂寥。\"]',NULL,NULL,NULL,NULL),('e647d10f8a591ae2f1d6044ee099f51a','和沈愚阊门柳枝词三首','苏平','e647d10f8a591ae2d1457b72d1457b72','明',NULL,'[\"袅袅烟丝拂画桥，纷纷春日上长条。馆娃宫女能相妒，莫向东风学舞腰。\"]',NULL,NULL,NULL,NULL),('e647d10f8c61f798e7114410da79c44f','小重山·湖上春游','裘律','e647d10f8c61f798d1457b72d1457b72','明',NULL,'[\"山绕平湖镜绕山，堤边千树柳、绿毵毵。杏花娇倚玉阑干，秦楼畔、小鸟语绵蛮。\",\"红粉试轻纨，笙歌吹画舫、漾飞鸾。酒旗撩乱系雕鞍，春色暖、芳草弄晴湾。\"]',NULL,NULL,NULL,NULL),('e647d10f8c91809e82cad9b5247fcb3c','长相思·寄友','解缙','e647d10f8c91809e0e3a37aaa60937eb','明',NULL,'[\"吴山深，越山深，空谷佳人金玉音。有谁知此心。\",\"夜沉沉，漏沉沉，闲却梅花一曲琴。高松对竹林。\"]',NULL,NULL,NULL,NULL),('e647d10f8cc4a40b83cfc9c2d6e02e5d','竹枝词','陈荐夫','e647d10f8cc4a40bd1457b72d1457b72','明',NULL,'[\"荷叶田田柳叶垂，千船万船多女儿。与郎暗约花间去，不唱《竹枝》知是谁。\"]',NULL,NULL,NULL,NULL),('e647d10f8d74940ec1377267a6981282','惜分飞·握扇美人图','李伊玉','e647d10f8d74940ed1457b72d1457b72','明',NULL,'[\"花雨缤纷迷小院，莲步踏残香片。粉汗摇纨扇，轻纱微露黄金钏。\",\"夕照山横天一线，水影分光上面。锦带随风缠，钗斜为逐双飞燕。\"]',NULL,NULL,NULL,NULL),('e647d10f8dca2f1e1b5e9b4ee387819b','望海潮·送顾漱玉入雍','陆楫','e647d10f8dca2f1ebc731692351b3358','明',NULL,'[\"江帆飞雨，汀莎没雁，玉人方上皇州。酒泛霞觞，潮生烟浦，那堪泪洒苹洲。云树两悠悠。蓬窗迷岸草，一任夷犹。羡尔词源、倒倾三峡楚天秋。\",\"清时肯卧林丘。看诗题杏苑，名彻宸旒。朔月关心，燕风扑面，谁云悔觅封侯。壮志奋吴钩。故园芳草合，鱼鸟含愁。何日京尘夜酤，清醑泻床头。\"]',NULL,NULL,NULL,NULL),('e647d10f8e0ae5daf8eaf3ffb2d8b489','菩萨蛮·江游','沈懋德','e647d10f8e0ae5dad1457b72d1457b72','明',NULL,'[\"江声汹汹鱼龙老，云情烟色从空绕。千里一扁舟，看完无限秋。\",\"苹花随浪急，白鹭迎风立。天外倚高楼，有人添暮愁。\"]',NULL,NULL,NULL,NULL),('e647d10f8e15531218b72500d7bd197d','浪淘沙·夜雨','梁寅','e647d10f8e155312d282ef262bd7f907','明','首二句“檐溜泻泉声，寒透疏棂”，不蔓不枝破题而入，从“夜雨”写起。“泉声”可见雨势之大，“寒透”标明天气之寒。雨水顺着屋檐流下，如山泉泻地，哗哗作响，寒气透入稠密的窗棂，四处洋溢。雨声喧哗，寒气逼人，词人不由满怀愁绪，难以成眠，乃写下一句“愁如百草雨中生”。以草喻愁是古典诗词中常见的表现手法。一场春雨，催生百草，乃是自然中常见之景，词人的愁绪如百草在雨中萌发，妙在即景言情，情形相生，生动自然。接下来“谁信”二句继续写愁情。夜雨滂沱，声声入耳，这一夜词人犹如作客旅店，睡不安枕，勉强入梦后，恍惚间又被惊醒。“谁信”，谓难以置信。“好梦”指什么，词人为罕救蓑雨而惊梦？这些都没有明说，从而为读者设置了一个悬念，耐人寻味。\n下片“花发恐飘零，只待朝晴”二句承惊梦而来，一语破的，点明题旨。原来词人之所以会“愁如百草”“好梦先惊”，其缘由就在于担忧无情的夜雨使花儿凋谢，零落成泥。结果如何，只要等到天亮后才晓得。所以他急迫地盼望拂晓，盼望天色转晴。从“只待”二字中，可见词人这一夜是如何朝思暮想、魂牵梦萦的了。夜雨过后，现象如何呢？“彩霞红日照山庭”一句呈现出另一番光景，红日东升，霞光万道，绚烂的阳光洒满山庭，不用说，这时词人的心情肯定也会随之一亮，愁绪顿消了。值得留意的是，词人关于担忧了一夜的花儿却只字不提，以不言言之，一笔晃过，花儿怎样样了，飘零了没有？令人深长思之，咀嚼不已。结句“曾约故人应到也，同听啼莺”，仍没有一字提及花儿，但有关花儿的内容尽在其中，含蕴丰厚。“曾约故人”进一步提醒了词人因雨生愁产生“花发恐飘零”的缘由，先前他曾约友人来此原来是为了赏花，而一夜倾盆大雨，花儿飘零势在难免，题中应有之意已无须明说。既然花已凋谢，不用伤情，愁亦无用，好在雨过天晴阳光绚烂，花儿谢了，还有“啼莺”，友人来了就和他一道观赏黄莺在枝头悠扬婉转的鸣叫吧。结尾二句振起词意，笔法迂回，摇曳多姿，流显露一品种似于“行到水穷处，坐看云起时”（王维《终南别业》）的旷达、随意和萧散，含不尽之意见于言外，神韵无量。\n此词构造紧密，层次井然，上片描写“夜雨”惊梦的愁情，下片交待愁情产生的缘由，结句一笔宕开，另辟新境，如抽丝剥笋，层层深化，明晰地展现了词人情感活动变化的轨迹。而这一轨迹又与景物的转换相联合，景生情，情生景，巧妙自然，令人称道。清况周颐《蕙风词话》云：“吾听风雨，吾览江山，常觉风雨江山外有万不得已者在。此万不得已者，即词心也。”读罢此词，本人们不难把握住作者的“词心”，并深深为之感染。','[\"檐溜泻泉声，寒透疏棂。愁如百草雨中生。谁信在家翻似客，好梦先惊。\",\"花发恐飘零，只待朝晴。彩霞红日照山庭。曾约故人应到也，同听啼莺。\"]',NULL,NULL,'《浪淘沙·夜雨》是明代学者梁寅所作的一首作品，这首小令写“夜雨”，表达了词人浓烈的惜花之情，情形融合，风格悠远。',NULL),('e647d10f8e1553122ea51f6458d89361','浪淘沙·冬景','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"锦树分明上苑花。晴花宜日又宜霞。碧烟横处有人家。绿似鸭头松下水，白于鱼腹柳边沙。一溪云影雁飞斜。\"]',NULL,NULL,NULL,NULL),('e647d10f8e15531233f3f9a7646335e0','金菊对芙蓉秋思','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"玉刻奇峰，蓝拖秀水，秋光浑似耶溪。渺苍烟十里，白鸟孤飞。恨无越女芙蓉艳，兰舟小、桂棹轻移。西风残照，樵人渔子，结伴尤宜。\",\"无柰物理难齐。叹鱼虾苦瘦，雁鹜多肥。望茫茫江海，今更何之。溪头绿树亲曾种，耐寒暑、应笑人衰。青山千仞，白云万顷，须理荷衣。\"]',NULL,NULL,NULL,NULL),('e647d10f8e1553123bacb8da3a79ae43','临江仙·舟中','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"十日江程春过半，渚青新长芦芽。李花几树间桃花。嫩黄烟际柳，远白水边沙。\",\"老子老来心似水，肯教愁绪如麻。扁舟来往似仙槎。还家今有日，那得更思家。\"]',NULL,NULL,NULL,NULL),('e647d10f8e1553124b5f673a7a8da3b8','采桑子·孟夏','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"舍南舍北多桃李，子满青枝。曲径冥迷。新竹增高旧竹低。\",\"扫苔坐石何妨久，行步常迟。伫立移时。云起东峰日坠西。\"]',NULL,NULL,NULL,NULL),('e647d10f8e1553125d23668185c5a73b','天仙子·苦热','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"六合似炉云似火。热气蒸肌烟雾锁。此时那得羽翰生，冰壑过。风岩坐。瀑布溅衣珠万颗。\",\"未旱先愁愁怎躲。如在颠崖惟恐堕。急须霖雨慰苍生，名誉播。江之左。谁在东山深处卧。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312646c2498b3810c80','玉蝴蝶·丙午元夕','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"霁景烟霞五色，黄金柳袅，碧玉桃开。再观升平气象，处处春回。且追随、村歌里巷，休耽恋、绮席楼台。独徘徊。人看月上，月趁人来。\",\"因怀。金陵旧曾游玩，御街灯火，远照秦淮。胜友同欢，醉听箫鼓闹春雷。几年间、风驰云往，千里外、水复山回。是仙才。飙轮许借，重访蓬莱。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312714471fde80ce8d1','永遇乐','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"年少羡君，有如琼树，相见何晚。虎瞰山前，轻船同载，正桂花香满。封溪一夕，孤篷听浪，又趁朔风遄返。计行藏、只堪一笑，怕令白鸥惊见。\",\"君如管辂，聪明夐异，能道山翁奇蹇。绛阙蓬莱，人间天上，翘首仙凡远。何时访我，竹溪松壑，尽有白云堪玩。写长怀、且寄南飞秋雁。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312954b6e00c62ac7d6','忆江南','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"山深处，豺虎纵跳踉。但爱体肥贪肉食，那知腹饱是身殃。天网甚恢张。\",\"千里外，魑魅可同乡。三尺正当严律令，四凶那得诧强梁。稽首谢君王。\"]',NULL,NULL,NULL,NULL),('e647d10f8e1553129c8c27dc139e4b7b','木兰花慢·贺彭子寿伯埙叔侄新居','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"羡楼台有地，不改换、旧林塘。想叔父东山，郎君玉树，筹画非常。云霄正期勋业，见先恢、庭户揽风光。苍霭昼生翠牖，丹霞晓映雕梁。\",\"春莺求友候春阳。应恋木千章。宜永日图书，凉飙丝竹，皓月壶觞。蓬莱宛然异境，爱琪花、瑶草近人香。老矣犹夸能赋，忆君梦到华堂。\"]',NULL,NULL,NULL,NULL),('e647d10f8e1553129daa1a4e5acd0695','阮郎归·自洪还家','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"归来长啸碧山河。结茅牵翠萝。衰颜借问近如何。吴霜侵鬓多。\",\"衣裋褐，卧行窝。眼前随分过。一溪风月与烟波。闲中宜钓蓑。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312a2d82a8259b412b8','忆秦娥·为南溪廖氏题古梅','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"湖山曲。山头花照湖波绿。湖波绿。盈盈仙子，镜中颜玉。\",\"百千年树繁英簇。春光却在幽人屋。幽人屋。自然富贵，海珠千斛。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312a7ee9b4232799f4e','苏幕遮·秋旱喜雨','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"白萍乾，红蓼悴，日减溪流，尘拥浑如雾。一日云凝千嶂雨。黄叶潇潇，却又添新翠。\",\"垦蒿烟，耕草露。种麦栽菘，生计那嫌暮。预羡登高欢会阻。门掩黄花，也自多幽趣。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312b079a9b74145826e','诉衷情令','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"李花当户间桃花。妍景雪兼霞。春风送将春色，照耀野人家。\",\"回蝶使，罢蜂衙。日初斜。双莺窗外，双燕帘间，共惜秾华。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312bf15506c5041d8de','人月圆·春夜','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"三春月胜三秋月，花下惜清阴。锦围绣阵，香生革履，光动兰襟。\",\"棠梨枝颤，乍惊栖鹊，夜久寒侵。明朝风雨，休孤此夕，一刻千金。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312cbe8708962cabde1','八声甘州·赠易自然祷雨有感','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"喜神龙飞雨遍秋郊，黄河自天来。是蟠溪逸士，胸中造化，掌上云雷。坛峙吟峰东畔，稽首望仙台。绛节霓衣，拥阊阖朝开。\",\"凭仗小心风送，绿章上奏，咫尺瑶阶。念鱼头赤子，濈濈困炎埃。赖皇穹、恩波滂沛，便千村万落总春回。人都道先生功行，山岳崔嵬。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312d5852d9d9b812099','鱼游春水','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"家临千峰翠。幽径重开荆棘里。小桃花艳，春日盈盈霞绮。香入骚人碧玉杯，色映游女青螺髻。带露更娇，迎风尤媚。\",\"古有墙东避世。况似武陵风光美。时时独酌花间，别有天地。不教扫径看尤好，意欲寻仙从兹始。岩前白云，石边流水。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312d7f1b775296ae868','雨霖铃·夏景','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"螺峰堆绿。夜来经雨，浑似膏沐。飞泉怒泻崖谷，悬霜练□鸣苍玉。虎迹岩前过处，踏破翠苔褥。听啼鸟，山北山南，树杪残云自相逐。\",\"蓬门昼掩稀来躅。称幽人、独步看新竹。移床松下零露，三四点、怎沾缃轴。日永如年，况是身闲不受拘束。休妄想、鹓鹭朝班，聊且伴麋鹿。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312dcea72a65c2232d8','八声甘州','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"记年时波荡两鸳鸯，雌雄各分流。恨郎情似水，妾心如石，此恨难休。自古恩深沧海，富贵等云浮。何忍轻离别，翻爱为仇。\",\"君看江头枯树，纵春风虚过，根干仍留。且牵萝空谷，蓬户自绸缪。想秋胡、未忘故态，怕无金、相赠却怀羞。归来日，郎嗔妾忿，都合冰消雾收。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312ec3144aba014273d','金缕曲·泊南浦','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"南浦归帆暮。喜重看、螺江烟柳，鹤汀云树。画栋朱帘歌舞地，风景已非前度。只浩荡、江涛如故。相望飞楼鹏翅展，羡雄城、防卫多貔虎。又喜免，乱离苦。\",\"旧时犹记登临处。共诗朋、赋友同欢，咏今怀古。两鬓星星今老矣，却似荼蘼孤注。叹桃李、不知春去。独有洪崖青不改，似于人、恋恋能相顾。招我隐，有佳趣。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312f165ae8fb0da5892','木兰花慢·桃源','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"爱山中日月，春渐去，又还来。望水绕人家，云生窗户，岫转峰回。层层绛桃千树，似丹霞、散绮映楼台。世上从教桑海，人间自有蓬莱。\",\"渔郎未必是仙才。偶尔到天台。喜相问相邀，山中殽簌，树里尊罍。何便寻归路，是风波险处未心灰。要似秦民深隐，桃花只好移栽。\"]',NULL,NULL,NULL,NULL),('e647d10f8e155312f453a8d685083bde','玉蝴蝶·闲居','梁寅','e647d10f8e155312d282ef262bd7f907','明',NULL,'[\"天付林塘幽趣，千章云水，三径风篁。虽道老来知足，也有难忘。旋移梅、要教当户，新插柳、须使依墙。更论量。水田种秫，辟圃栽桑。\",\"荒凉。贫家有谁能顾，独怜巢燕，肯恋茅堂。客到衡门，且留煮茗对焚香。看如今、苍颜白发，又怎称、紫绶金章。太痴狂。人嘲我拙，我笑人忙。\"]',NULL,NULL,NULL,NULL),('e647d10f8e45967c164effff51039335','渔歌子','吴兖','e647d10f8e45967cd1457b72d1457b72','明',NULL,'[\"千顷蒹葭一钓翁，家居南浦小桥东。桃花水，鲤鱼风，短笛横吹细雨中。\"]',NULL,NULL,NULL,NULL),('e647d10f8e84eb582db6254db924c43d','浪淘沙·海棠','杨宛','e647d10f8e84eb58d1457b72d1457b72','明',NULL,'[\"尽日若含愁，别样娇羞，晚凉香散上帘钩。带露摘来斜插鬓，一段风流。\",\"蛩语玉阶幽，又是深秋，相携闲对小妆楼。不解断肠伊似我，我似伊否。\"]',NULL,NULL,NULL,NULL),('e647d10f8e84eb58347577918a4c0f3c','太平时','杨宛','e647d10f8e84eb58d1457b72d1457b72','明',NULL,'[\"袅袅疏枝带露轻，隔帘棂，丝丝牵缀别离情。最难胜。\",\"幽恨只凭羌管诉，调凄清。临风半是断肠声。不堪听。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef202362b828e6777efd377','满庭芳·初夏','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"树染橙香，花燃榴火，曲池莲盖初张。槐阴半亩，引起竹床凉。门静闲花自落，小园中、无数新篁。凭栏久，闲调鹦鹉，摘叶戏螗螂。\",\"日长，真似岁，轻施冰簟，闲梦羲皇。正孤蝉吟树，乳燕依梁。且喜黄梅过也，建兰开箩岕茶香。东斋晚，一壶村酒，新月上松窗。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef20236415d41bd25d2bda0','谒金门','施绍莘','e647d10f8ef20236d1457b72d1457b72','明','施绍莘是个隐逸之士，但较少明末山人气。他的词曲中每多冷眼看世态的意蕴，生际明末，哀伤情常在心底。这阕小令从题面看是“伤春”，就词心言则是伤时。上片冷峻，写出危颓之世，迷惘如梦，而“墙里”人依旧“笑语”如常。不知是麻木，还是醉生梦死的荒唐。下片将视线收敛内观自照，“无计”是最大的悲哀。最大的悲苦永远属于清醒人，这又是一种难解之谜。“万种消魂”只能化为诗句，实也就是“国家不幸诗家幸”。然而此种“幸”，仿佛是天公的特定惩处。用断肠之苦换取若干文字，能说不是大不幸。此词警策之句首在“如梦一庭空絮”，将醉者以及醒而“无计”者全溶进了梦游般的境界。那种时代的悲剧性的深刻，由此凄婉情韵中毕见。 ','[\"春欲去，如梦一庭空絮。墙里秋千人笑语，花飞撩乱处。\",\"无计可留春住，只有断肠诗句。万种消魂多寄与，斜阳天外树。\"]',NULL,NULL,NULL,'[\"谒金门：词牌名。原唐教坊曲名。重头四十五字，仄韵。又名\\u201C醉花春\\u201D、\\u201C出塞\\u201D、\\u201C空相忆\\u201D、\\u201C花自落\\u201D、\\u201C垂杨碧\\u201D等。　②\\u201C如梦\\u201D句：柳絮飞空，茫茫如梦，人间众生亦各自在梦境里。　\",\"断肠：形容伤心之至。痛致肠断，典出《搜神记》：\\u201C人杀猿子，猿母悲啼死，破其肠，肠皆断裂。\\u201D　\",\"消魂：悲哀情，亦作\\u201C销魂\\u201D，语出江淹《别赋》：\\u201C黯然销魂者，惟别布局矣。\\u201D\",\"\\u201C斜阳\\u201D句：用辛弃疾\\u201C斜阳正在，烟柳断肠处\\u201D句意。 \"]'),('e647d10f8ef202368bf7bd43c52a2742','浣溪沙·月夜','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"如镜窥妆逗小楼，真珠帘外半痕收。倒簪花影上人头。\",\"品得秦筝初度曲，花前和露耍千秋。柳丝浓翠拂鞋钩。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef202369b02ba6de7022483','菩萨蛮','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"春深加倍心情恶，慵歌懒笑梳妆薄。独自掩窗纱，莺啼诉落花。\",\"梦回刚拭泪，偷立秋千背。风起乱红飞，不知双泪垂。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef20236e429ae7975b1d747','如梦令','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"日约楼阴初整，风暖烟消人静。早起未梳头，闲扫小园芳径。\",\"清净，清净，只剩数枝花影。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef20236f32f182e8765cf25','浣溪沙','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"愁卧寒冰六尺藤，懒添温水一枝瓶。乱鸡啼雨要天明。\",\"等得梦来仍梦别，甫能惊觉又残灯。西江别路绕围屏。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef20236f32f182ea171cecc','浣溪沙','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"半是花声半雨声，夜分淅沥打窗棂。薄衾单枕一人听。\",\"密约不明浑梦境，佳期多半待来生。凄凉情况是孤灯。\"]',NULL,NULL,NULL,NULL),('e647d10f8ef20236f36ae02e531a4b94','点绛唇·泖桥次韵','施绍莘','e647d10f8ef20236d1457b72d1457b72','明',NULL,'[\"寺枕荒塘，时时雪浪吞僧屋。桥头路曲，废井当枯木。\",\"如此幽闲，恰好闲人宿。窗敲竹，酒醒茶熟，天水鹦歌绿。\"]',NULL,NULL,NULL,NULL),('e647d10f8f382e4197b5b8da9c7aa557','满庭芳','陈㛃','e647d10f8f382e41d1457b72d1457b72','明',NULL,'[\"湛碧池塘，空青庭院，清霜又已深秋。绕篱香韵，黄菊伴人幽。怪是重阳佳节，凭阑处、吹帽风稠。思前古，渊明此际，淡漠几曾愁。\",\"江山无限感，悲深语咽，志壮身柔。剩图书经卷，夙业堪修。忆昔云鬟藕覆，菱镜里、未展眉头。时易暮，数声哀雁，叶落满沙洲。\"]',NULL,NULL,NULL,NULL),('e647d10f8f3fabf112f4d5da97fd8a9c','卜算子','蒋冕','e647d10f8f3fabf1fe70c36835309226','明',NULL,'[\"斜日坠荒山，云黑天垂暮。时见空中一雁来，冷入残芦去。\",\"惊起却低飞，有意同谁语。啄尽枝头数点霜，还向空中举。\"]',NULL,NULL,NULL,NULL),('e647d10f8fc3c0e6955a2a83deb14599','双双燕·秋日过横山堂','张玮','e647d10f8fc3c0e6536a76f981c8727c','明',NULL,'[\"披蓬探隐，到幽草溪头，碧阴山曲。竹韵松姿，媚却吟眸诗腹。是处翠峰成簇，更新秋、雨凉如沐。深林宛转芳禽，浅涧琤琮漱玉。\",\"孤独，简编娱目。有纫佩芳兰，供餐茂菊。藤床闲憩，梦入华胥应乐。好是月明帘轴，恰一枕、清酣睡足。小小花童低报，瓦鼎烹茶刚熟。\"]',NULL,NULL,NULL,NULL),('e647d10f8ffa6cd831f4a545a5d68d21','菩萨蛮·蜂蝶','钱涓','e647d10f8ffa6cd8d1457b72d1457b72','明',NULL,'[\"窥红惹绿身儿小。飞飞惯入芳林扰。伊也点鸦黄。个兮浅淡妆。\",\"树头春意闹。翻逐邻家悄。风度远花香。知他飞那厢。\"]',NULL,NULL,NULL,NULL),('e647d10f8ffa6cd87410f665430465e0','秋波媚芙蓉','钱涓','e647d10f8ffa6cd8d1457b72d1457b72','明',NULL,'[\"艳质谁教托蓼洲，寂寞更谁俦。半竿落日，几行征雁，一叶归舟。\",\"那堪风雨频相妒，红粉逐波流。深深绮阁，盈盈罗帐，多少清愁。\"]',NULL,NULL,NULL,NULL),('e647d10f8ffa6cd8b7ad359e67d0f194','清平乐·腊梅','钱涓','e647d10f8ffa6cd8d1457b72d1457b72','明',NULL,'[\"峭寒方甚。蕊破枝头嫩。独喜傲霜无瘦损。另是一家风韵。\",\"游蜂酿蜜来赊。翻疑葛染成花。日映隔帘金碎，暗香透入窗纱。\"]',NULL,NULL,NULL,NULL),('e647d10f8ffa6cd8d4f9f218a17cca74','相见欢·山园','钱涓','e647d10f8ffa6cd8d1457b72d1457b72','明',NULL,'[\"阶前云补苍苔。衬花开。窈窕朱阑碧户、似天台。\",\"山一带。水一派。足徘徊。满目画耶诗也、费安排。\"]',NULL,NULL,NULL,NULL),('e647d10f903ecbce2eabdb0fc0ea6692','忆秦娥','王微','e647d10f903ecbced1457b72d1457b72','明',NULL,'[\"多情月，偷云出照无情别。无情别，清辉无奈，暂圆常缺。\",\"伤心好对西湖说，湖光如梦湖流咽。湖流咽，离愁灯畔，乍明还灭。\"]',NULL,NULL,NULL,NULL),('e647d10f903ecbce3102910a285b0eca','如梦令·怀谭友夏月到','王微','e647d10f903ecbced1457b72d1457b72','明',NULL,'[\"闲庭如昼，修竹长廊依旧。对影黯无言，欲道别来清瘦。\",\"春骤，春骤，风底落红僝僽。\"]',NULL,NULL,NULL,NULL),('e647d10f903ecbce8bbed9d1d5615733','捣练子','王微','e647d10f903ecbced1457b72d1457b72','明',NULL,'[\"心缕缕，愁踽踽，红颜可逐春归去。梦中犹殢惜花心，醒来又听催花雨。\"]',NULL,NULL,NULL,NULL),('e647d10f90541cc3ceeb4466e81b62d4','苏武慢江亭远眺','韩守益','e647d10f90541cc3d1457b72d1457b72','明',NULL,'[\"地涌岷峨，天开巫峡，江势西来百折。击楫中流，投鞭思济，多少昔时豪杰。鹤渚沙明，鸥滩雪净，小艇鸣榔初歇。喜凭阑、握手危亭，偏称诗心澄彻。\",\"还记取、王粲楼前，吕岩矶外，别样水光山色。烟霞仙馆，金碧浮图，尽属楚南奇绝。紫云箫待，绿醑杯停，咫尺良宵明月。拚高歌、一曲清词，遍彻冯夷宫阙。\"]',NULL,NULL,NULL,NULL),('e647d10f9073da833f8d69ab7be14d4e','点绛唇·与冯太君话旧','张鸿逑','e647d10f9073da83d1457b72d1457b72','明',NULL,'[\"相见空怜，许多心事难传与。不如归去，又被黄昏雨。\",\"明月多情，还照横塘路。添愁绪，幽闺深处，重忆连床语。\"]',NULL,NULL,NULL,NULL),('e647d10f917520020e43f23da0a1ff5a','潇湘神·零陵作','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"潇水长，湘水长，三湘最苦是潇湘。无限泪痕斑竹上，幽兰更作二妃香。\"]',NULL,NULL,NULL,NULL),('e647d10f917520020e43f23dac3e85cf','潇湘神·零陵作','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"潇水深，湘水深，双双流水逐臣心。潇水不如湘水好，将愁送去洞庭阴。\"]',NULL,NULL,NULL,NULL),('e647d10f917520020e43f23db331af4d','潇湘神·零陵作','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"潇水流，湘水流，三闾愁接二妃愁。潇碧湘蓝虽两色，鸳鸯总作一天秋。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002164effffcbfb7d4e','渔歌子','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"素馨红，素馨绿。素馨红绿看难足。穿茉莉，贯芙蓉，持作玲珑花屋。\",\"旖旄香，宜新浴，荔枝膏滑惭非玉。捐麝片，屏笺沉，怕乱冰肌真馥。\"]',NULL,NULL,NULL,NULL),('e647d10f9175200223f85bbfbd575161','念奴娇·秣陵吊古','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"萧条如此，更何须，苦忆江南佳丽。花柳何曾迷六代，只为春光能醉。玉笛风朝，金笳霜夕，吹得天憔悴。秦淮波浅，忍含如许清泪。\",\"任尔燕子无情，飞归旧国，又怎忘兴替。虎踞龙蟠那得久，莫又苍苍王气。灵谷梅花，蒋山松树，未识何年岁。石人犹在，问君多少能记。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002293cbaa2ce641196','长亭怨慢','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"记烧烛、雁门高处。积雪封城，冻云迷路。添尽香煤，紫貂相拥、夜深语。苦寒如许，难和尔、凄凉句。一片望乡愁，饮不醉，垆头驼乳。\",\"无处，问长城旧主，但见武灵遗墓。沙飞似箭，乱穿向，草中狐兔。那能使、口北关南、更重作，并州门户。且莫吊沙场，收拾秦弓归去。\"]',NULL,NULL,NULL,NULL),('e647d10f9175200234876f22290d1da2','蝶恋花·风流未满愁蚤满','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"风流未满愁蚤满。满到天边，暮雨还添满。不识是愁将水满，不知是水将愁满。\",\"分付东风吹稍缓。莫使春潮，只送愁先返。愁返江南人未返，不如潮水从今断。\"]',NULL,NULL,NULL,NULL),('e647d10f917520023b0cd599c78b96c6','渔歌子·二首·其一','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"开到棠梨不是花。莺啼莫忆旧繁华。朝雨急，暮风斜。春光催去去谁家。\"]',NULL,NULL,NULL,NULL),('e647d10f917520023dcdf2bb0bf799ca','蝶恋花·春情','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"似雨如晴春乍暖。漠漠烟青，未肯含愁浅。怅望不知人已远，踏红似向花间转。\",\"乱落杜鹃馀几片。付与游丝，莫被风吹断。紫燕衔香知有怨，怨他情与东风短。\"]',NULL,NULL,NULL,NULL),('e647d10f917520024c2d8abefb857920','巫山一段云','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"片片瑶姬影，飞来最有情。朝朝暮暮不分明，愁与梦魂凝。\",\"云湿碍行雨，峰开似列屏。髻鬟染得一天青。一朵一仙灵。\"]',NULL,NULL,NULL,NULL),('e647d10f917520025a1a7ef4dc8d0190','渔歌子·二首·其二','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"夹浦夭桃树树斜。一湾不见一湾花。云带岸，水侵沙。\",\"白鸥多处有人家。\"]',NULL,NULL,NULL,NULL),('e647d10f917520028205ad4207326bbe','紫萸香慢·送雁','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"恨沙蓬，偏随人转，更怜雾柳难青。问征鸿南向，几时暖返龙庭。正有无边烟雪，与鲜飙千里，送度长城。向并门少待，白首牧羝人，正海上，手携李卿。\",\"秋声，宿定还惊。愁里月，不分明。又哀笳四起，衣砧断续，终夜伤情。跨羊小儿争射，恁能到，白蘋汀。尽长天遍排人字，逆风飞去，毛羽随处飘零，书寄未成。\"]',NULL,NULL,NULL,NULL),('e647d10f9175200286b912021673b408','虞美人','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"无风亦向朱栏舞，情为君王苦。乌江不渡为红颜，忍使香魂无主独东还。\",\"春含古血看犹暖，巧作红深浅。花前休唱楚人歌，恐惹英雄又唤奈虞何。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002926cb47a356c0849','蝶恋花·题唐宫扑蝶图','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"凤子翩翩纷似雪。画扇低扬，惊入深深叶。博得君王开笑靥，闻香忽复穿裙褶。\",\"秦女剩鸾颠倒绝。捉得黄须，腻粉教轻捻。收向镜奁成媚蝶，承恩好待华清月。\"]',NULL,NULL,NULL,NULL),('e647d10f917520029a31912b5b8e8f2f','蝶恋花　· 立春','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"开岁炎天频下雪。梅蕊迟开，冻得南枝彻。几数夭桃先漏泄，春光已被蛮娘夺。\",\"黄鸟数声娇欲绝。一夕东风，只转缠蛮舌。处处壶觞闲可掣，嬉游莫待芳菲月。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002a948acca5f7d360f','浣溪沙·杜鹃','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"血洒青山尽作花，花残人影未还家。声声只是为天涯。\",\"有恨朱楼当凤阙，无穷青冢在龙沙。催还不得恨琵琶。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002c0dc2a6e092a973e','梦江南','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"悲落叶，叶落落当春。岁岁叶飞还有叶，年年人去更无人。\",\"红带泪痕新。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002c0dc2a6eb8428f3c','梦江南','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"红茉莉，穿作一花梳。金缕抽残蝴蝶茧，钗头立冬凤凰雏。\",\"肯忆故人姝。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002d56911fa871c34c6','绛都春','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"庞妻赵女，本酒泉丽质，小字娥亲。阴市好刀，磨砻犀利，鹿车中、嬉春日日施红粉。\",\"卷帏人道佳人。挟长持短，诛雠报父。不顾娇身。遂杀凶豪李寿，任娥眉溅血，玉骨成尘。\",\"孝媛帝怜，香名人播，已千春。曹娥与尔争英烈，更同缑玉芳芬。\",\"子胥渐汝，吹箫一片苦辛。\"]',NULL,NULL,NULL,NULL),('e647d10f91752002f32f182e378ccfea','浣溪沙','屈大均','e647d10f9175200246031b3d5cf21ce3','明',NULL,'[\"一片花含一片愁，愁随江水不东流。飞飞长傍景阳楼。\",\"六代只余芳草在，三园空有乳莺留。白门容易白人头。\"]',NULL,NULL,NULL,NULL),('e647d10f9197aa15f2e03f93ef3e1235','青门引','张卿玉','e647d10f9197aa15d1457b72d1457b72','明',NULL,'[\"水曲红波冷，几树小桃摇影。怜春情性未分明，天台人近，一线香风引。\",\"青阳踏破芳魂醒，十二栏杆静。口脂红印鹦鹉，窗前难数春归恨。\"]',NULL,NULL,NULL,NULL),('e647d10f91ceea748bcc96424eef70cd','竹枝词三首','江源','e647d10f91ceea74d1457b72d1457b72','明',NULL,'[\"郎游巴西复巴东，雪消江涨舟淩空。昨来寄得平安字，更说艰难愁杀侬。\"]',NULL,NULL,NULL,NULL),('e647d10f91ceea748bcc9642bc688187','竹枝词三首','江源','e647d10f91ceea74d1457b72d1457b72','明',NULL,'[\"八月秋风汉使槎，忆郎身世在天涯。三巴三峡莫云远，四海梯航今一家。\"]',NULL,NULL,NULL,NULL),('e647d10f91ceea748bcc9642fe6c026f','竹枝词三首','江源','e647d10f91ceea74d1457b72d1457b72','明',NULL,'[\"闻道襄阳物物春，一从郎去无音尘。劝郎莫向大堤去，女儿花艳能迷人。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4038bfe651ea37b71','怨朱弦和王明佐新声慰其不遇名曰怨朱弦','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"谱朱弦，一片秋声。万壑松涛，银汉初倾。挟遍侯门，沈深似海，雕栏十二娇莺。流苏帐底，小按银筝。哑哑轧轧嘤嘤。骂书生煞横，捻冷装酸，搅破春情。我道高山流水，渠道香云暖雨，两语难凭。怎能勾、师涓老子，证个分明。\",\"酒人哉，匕首荆卿。宋意悲歌，高渐沾缨。应徵飞霜，倚天雌霓，长河倒泻珠绳。葡萄尚暖，蝴蝶犹萦。陡被杜宇啼醒。道不如归去，归去来兮，任汝纵横。鱼为洞庭清乐，凤为苏门长啸，飞跃争听。何须借、天公两耳，枉施瑶京。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd405d9b02356dd9cf3','虞美人·别思','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"摩诃池上金丝柳。惯爱纤纤手。折来将表片时心。记取浅黄柔绿、泪痕深。\",\"博山香细银灯吐。乍识黄昏雨。娇花欲展半蔫红。错道褪残春事、骂东风。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd407a38bc39ace58a3','解语花·题美人捧茶','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"中泠乍汲，榖雨初收，宝鼎松声细。柳腰娇倚。薰笼畔、斗把碧旗碾试。\",\"兰芽玉蕊。勾引出、清风一缕。颦翠蛾、斜捧金瓯，暗送春山意。\",\"微袅露鬟云髻。瑞龙涎犹自，沾恋纤指。流莺新脆。\",\"低低道、卯酒可醒还起。双鬟小婢。越显得、那人清丽。\",\"临饮时、须索先尝，添取樱桃味。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd41330c0ea0b89fd88','临江仙·詹簿兄遗子鹅鲟鱼','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"有客青州常从事，雨中相对留连。吾兄折简赤须传。鹅儿黄似酒，鲟鼻大如船。\",\"故国风光俱入眼，眼中偏爱偏怜。欲因弹铗问当年。先生何所有，苜蓿满新盘。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd41368c914f8dce309','临江仙','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"迟日三眠浑似柳，起来徐步闲庭。中年风物易关情。不知因个甚，撩乱没支撑。\",\"我笑残花花笑我，此时憔悴休争。来年春到便分明。五原无限绿，难染鬓千茎。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd419c4ad610470ab65','天仙子·酷暑','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"万叠火云堆落照。一抹青山连晚烧。不教蘋带吐微飔，天转峭。星仍闹。长安炙就红垆小。\",\"枝上忽闻蝉语噪。为问西风何日到。男儿失足堕京尘，炎也噪。凉也噪。不如销夏湾中好。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd41c76242e047ae719','三五七言春泛','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"兰为楫，桂为桨。春波既摇裔，春日仍骀荡。归到横塘市欲收，平林一抹苍烟上。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd41df52ddf7d656601','满庭芳·傍有何山亦佳闰六月初七夜戏为天孙赋此','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"玉露初零，金飙微送，银浦垂静还波。七襄人倦，纤手掷鱼梭。记得年时此夜，双星聚、乐事还多。生绡底，天鸡唱醒，低语别牛哥。\",\"黄昏肠断处，无情乌鹊，忘却填河。渐转低、瑶斗蹙损青蛾。方悟朱明宰闰，排年月、恨杀羲和。常记取，人间天上，要好便多磨。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd41f3ee7138ebdd724','长相思·闲情','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"东陌头。西陌头。陌上香尘粘碧油。见花花自羞。\",\"南高峰，北高峰。两处峰高愁杀侬。行云无定踪。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd425cee7e2a1f860ca','望江南病后戏作','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"闲嘱病，慎莫忌多才。夜雨松它温锦带，霜天负我软金杯。\",\"怕汝又安排。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd427f9117c09c1b8e8','浣溪沙·春闷','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"窗外闲丝自在游。隔山花鸟弄?辀。一庭芳草怨清幽。\",\"权把束书钩午梦，起沽村酒泼春愁。放教残日过墙头。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd42bc9a19ebd80a7f7','满路花·初春游目即事','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"穿芽径字青，压水冰文绿。山城初雪罢、添妆束。谁家猎骑，揉碎千崖玉。貂裘斜半揭。马尾捎生，问人何处堪宿。\",\"青帘高写，美酒新丰熟。春色偏占早、花如簇。珠帘十二，处处调丝竹。夜阑金缕曲。唱道明朝，扶头依旧醽醁。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd432197434bb85528e','桂枝香','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"东风一骑。几日发长安，欲留无计。猛见柔黄，嫩绿压冰成水。青山处处青螺小，隐孤村、夕阳鸦背。那时离别，除非醉后，不教愁死。\",\"记岁暮停桡淮沚。急难甥舅，穷途兄弟。雨妒霜骄，了却半生滋味。临岐不尽心头事，怯乡愁、难更提起。渭城人酒，点点滴滴，渭阳人泪。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd43238bd45fa4b43fc','贺新郎·谢袁履善惠酒','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"春意归风雨。到如今、缓红舒翠，一番重起。况更索居无一事，镇日琴书而已。待醉也、如何得醉。多谢白衣能远致，把葛巾、忙却科头倚。胸磊块，故应洗。\",\"琼膏慢入清尊细。似当年、掌分茎露，雪消春水。欲折筒荷充泛驾，凭借曲生为驭。直引到、华胥路里。遮莫归来问名姓，道清真、袁粲频为主。天下事，任公耳。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd432ad97e16649c473','渔家傲','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"细雨轻烟装小暝。重衾不耐春寒横。袅尽博山孤篆影。闲自省。天涯有个人同病。\",\"十二巫峰围昼永。黄莺可唤梨花醒。两点芳波揩不定。临晚镜。真珠簌簌胭脂冷。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd43581e5a35cb64f24','如梦令·偶题','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"韦杜去天尺五。日炙锦蔫红腐。占尽洛阳春，不放东君做主。\",\"何处。何处。归梦一蓑烟雨。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd43ce75d6c6f0389cd','满江红·题高宗赐岳武穆诏后次文徵仲待诏','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"御墨淋漓，到飞字、百身难赎。弹指罢、遗黎梦断，旧都沦覆。十二金牌丞相诏，风波片纸君王狱。恨当时、巧放两人归，乾坤蹙。\",\"翘首地，青衣辱。回马地，朱仙哭。笑大江东去，一夔兹足。北面生看臣构在，南枝死望中原复。痛他年、降表出皋亭，鸱夷目。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd43d5b022d483cf9e7','望江南有感','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"无限事，断送在长安。只见前人前事好，后人谁道后人难。\",\"青史不胜寒。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd442ad36318f8d27d6','如梦令·秋思','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"残月碧梧金井。屈戍绣霜初冷。徙倚不成眠，红泪一双珠迸。\",\"孤影。孤影。长自伴人宵永。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd443d312051f2568b4','如梦令·感怀','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"刚是子规催去。又被鹧鸪留住。行不得哥哥，烟雨江南何处。\",\"难据。难据。央个醉乡为主。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd44cd6517e3a992177','洞仙歌·伤乱','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"金钱磨破，难迓江南信。巴得书来更添闷。道姑苏台畔、留却几丛，麋鹿草，约略秋风又尽。\",\"向时佳丽地，结绮临春，酝酿韶华付寒烬。便长洲陌上、有日言归，家已破，废井荒烟无准。剩几个遗民、怕都来，不是旧时人、向谁寻问。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd44cdb6e1809ced997','木兰花秋思','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"金堂凤䗶匀红泪。帘外一钩霜满地。鸳鸯被冷不成眠，两点瞳人剪秋水。\",\"千回万结心头事。熨贴相思裁两字。不须支枕盻天明，十二时愁从此始。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd44f03f93c7f69b8e3','解语花·题美人捧觞','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"檀槽细压，紫溜泠泠，滴碎珠千斛。鹔鹴初赎。谁揩醒、卓女远山黛绿。\",\"朱樱小蹙。风袅处、山香几曲。捧屈卮、徐露春芽，一样纤纤玉。\",\"何事锦围翠簇。只枝头一点，买断金谷。灵犀轻嘱。\",\"微酣后、记取夜来题目。双鬟趁逐。扶俺向、碧纱厨宿。\",\"夸醉乡、还傍温柔，此际平生足。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4503cfd9aded2e075','一剪梅·登道场山望何山作','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"小篮舆踏道场山。坐里青山，望里青山。渐看红日欲衔山，湖上青山，湖底青山。\",\"一弯斜抹是何山。道是何山，又问何山。姓何高士住何山，除却何山，更有何山。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd456482e7e69dd5939','百字令青州阅武偶成','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"竹符铜虎，是何人管领，东方千骑。画戟雕戈霜万叠，独让些些麈尾。璧月辞櫜，莲花离匣，马足寒飙起。雷奔电击，一麾波静如洗。\",\"屈指斧钺行边，楼船横海，济甚人间事。涂抹支吾天地老，眼底英雄而已。九合诸侯，一匡天下，自是真男子。向微管仲，吾其被发人矣。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd45b567571f52720c7','鹧鸪天·寄怀','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"峭雨零霜舶棹归。玉颜初醒带宽垂。阁残吴苑千金笑，低损成都十样眉。\",\"能几日，又分离。别来疑梦复疑非。莫将心向天公语，天上双星会几时。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd45e4c507b7efcb47c','双双燕','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"廿年恩养，念暖幌雕笼，一时勾了。低声软语，泣别殢情京兆。无柰春山顿老。况冷断、如簧雅调。辞君岂为无鱼，叹汝难同凡鸟。\",\"惊起，钩帘浅照。见妩月初生，似他波俏。飘黄摧粉，总博绿愁红躁。谁破芸窗乍晓。怎忘得、双弯纤妙。知否御爱开元，鹦鹉墓傍新旐。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd460af069f0c458b06','两山竹枝歌','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"橘绿橙黄香满校，瓮头篘玉鲙鱼丝。山中历日由来少，知是江南九月时。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd460af069f952e1b4b','两山竹枝歌','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"峨舸大艑碧波间，遍截玲珑稛载还。最是三吴多好事，家家分得洞庭山。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd460af069fc27420ff','两山竹枝歌','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"白雪缫成茧子绵，黄云剪就稻花天。千家村里无开市，二尺溪头有系船。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd460af069ffc1605f4','两山竹枝歌','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"短短钗银压鬓鸦，围腰群捉木绵花。莫嫌村坞行人少，夫婿经商不在家。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd464e9342fe681bea8','小诺皋','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"阖辟以前，乾坤之外，到了不堪穷际。尽追求、尽教胡突，依形附气。何处小儿谵语，羞杀井蛙蛭蚁。说甚么、出世住世治世。黄面瞿昙，青牛老子。更有那、我家司寇，伎俩一般而已。赚人处，还在此。\",\"稷下万言，河间千卷，毕竟没些巴鼻。莫轻容、古人瞒过，常谈俗事。妆点几张故纸，无柰藏头露尾。不采他、上计中计下计。浊酒三杯，清琴一几。别饶借、淡风微月，受用剩山残水。五更事，休办取。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd472408eb812ba03ae','多丽·晚春雪后即景','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"醉飞琼，恼乱瑶池清宴。吉玲琤、凤钗敲碎，荡成满地飘霰。送天桃、武陵几树，伴梨花、杜曲千片。妒我春衫，妆他寒食，乍惊蜂蝶又猜莺燕。五陵畔、杂和烟雨，宝马踏青转。樊楼上、银筝细炙，红垆重暖。\",\"怯寒色、条桑小妇，幕䍦轻护娇面。展纤弯、一双莲瓣，屐齿香泥粉痕浅。冷落流光，萋迷馀景，东风无计玉山软。天公老、被伊瞒过，红日几时见。空担阁、无主野芳，满目愁怨。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd473f485369371accc','水调歌头','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"迟日卷残雪，蒲柳弄新晴。满城儿稚，欢笑为我报清明。花破青楼冶女，草媚上阑游骑，金粉出辎軿。几处上坟返，香泪湿盈盈。\",\"对新景，追往事，叹飘零。十年回首，一梦今日负平生。依旧禁烟时月，也解来朝修褉，憔悴不胜情。满眼夕阳色，都在汉宫城。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd475ea1df1fbc03027','南乡子·怨欢','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"薄行总难熬。刚把黄昏醉玉箫。妾似凤凰桥下月，空捞。郎似初三十八潮。\",\"猛见旧鲛绡。几点飞红晕翠涛。点起那筹和泪滴，心苗。火片真情冷地消。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd47c5239165c83e873','鹊桥仙·七夕','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"冰盘荐巧，珠帘堆莹，半卷生绡光致。嫦娥妒眼便西沈，又早倩、羲和催辔。\",\"多情瑞鹊，填桥度我，何不晓来回避。但教三万六千年，也不弱、人间百岁。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4802f99108cf343a1','临江仙·封太史韩友兰寿','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"多少神仙官府在，少无拘束仙官。此翁仙福渐来完。庭前饶玉树，海上有青鸾。\",\"为问青鸾消息地，一韩曾使三韩。安期持底与传言。如瓜双火枣，捧日一金丹。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd480411587f935e576','贺新郎·谢汝钦侄惠酒','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"酒债寻常有。闭闲门、落花芳昼，独吟搔首。可怪袁君相赠后，镇日唯寻绿友。到骄惹、传杯之手。从事督邮何足较，但醉乡、别业吾能受。君不见，洗愁帚。\",\"清狂阮籍天应厚。醉醒时、漫开青眼，阿咸为寿。犹有焦生风格旧，试问无功知否。待徙倚、临风频嗅。曲沼游鱼堪投馔，况隔帘、歌鸟来行酒。洗盏罢，酹杨柳。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4851f66f239d12b7f','虞美人·寄怀','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"浮萍只待杨花去。况更廉纤雨。鸭头虚染最长条。酝造离亭清泪、几时消。\",\"珊瑚翠色新丰酒。解醉愁人否。薄寒撺送汝南鸡。偏向碧纱厨畔、醒时啼。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd490bdbd82273f3e06','满江红','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"五岳先生，天原付、一双高屐。端只为、几篇诗句，漏伊消息。空自挂冠神武外，依然乞食铜驼陌。记玉鞭、环佩夜朝天，空陈迹。\",\"青鬓在，还垂白。血泪洒，都成碧。叹帝城宫树，夕阳秋色。明日酬他迁谪恨，西风战尽英雄策。唤白云、随我共归来，人方识。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4934bc0178d32edd4','凤凰台上忆吹箫·愁','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"经雨斜阳，压云西岭，冉冉欲堕还浮。渐谯楼三点，唤起千愁。与醉平分此夜，猛地里、醉去难留。纱窗上，微风撼醒，揩拭双眸。\",\"悠悠。殢人住也，千百处攒心，几处堪休。似粘天衰草，贩浪残舟。悔杀太平风景，等闲过、抛掷东流。寻思遍，何年向人，觅换眉头。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd497b5b8da1a0dee8a','满庭芳','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"碧屿遥攒，绿波新剪，淡月微晕梨花。薄罗香袖，十指秀琼芽。任不寒暄半语，背人处、犹自怜他。那堪更，朱唇皓齿，锦字走龙蛇。\",\"瞥然还暂去，司空见惯，老子难挨。强将来、对盏眉语交加。怕被旁人看杀，洛城里、难坐羊车。临岐约，幂䍦过访，休许露些些。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd49c70a6d8a51d3bbb','踏莎行','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"玉琢娉婷，津含旖旎。青丝细绾双鸳尾。当时一寸暗同心，临岐总结秋波内。\",\"雁返吴枫，鱼游淮沚。那堪山岳仓皇起。便寻桃叶到天台，教予懒向空林醉。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4a4a1aa5ae71aa17f','浣溪沙·闺思','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"金博山头半吐烟。玉凌波底未舒莲。韶光悄悄恨绵绵。\",\"柔似女蚕春再浴，困如人柳日三眠。细笺心语衬胸前。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4aec5b03196f2041c','青玉案·闺怨','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"鸭头波软浓于酎。风起处、青初皱。乳燕双双帘幕透。梨花未雪，绿杨犹絮。蚕饱桑阴瘦。\",\"无端酿出清明候。忽忆城南乍分手。十二雕栏寒更陡。踏青人远，斗茶时近，滋味如中酒。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4b0848176da8cea5c','夏初临苦热简王参军','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"燕诉馀愁，蝉鸣新怨，千唆万弄斜阳。斗大书斋，无端夏日偏长。碧纱单挨流光。觑残榴、褪尽红妆。孤松犹瘦，清阴正稀，难据胡床。\",\"常时河朔，避暑传觞，醉乡天远，归路仓皇。隐囊纱帽，故人别号清凉。但过何妨。对弹棋、散帙焚香。逞疏狂，高歌羽声，片片飞霜。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4b4f35a933f5b6a30','春云怨饮酒','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"风僝雨僽。渐柳眠无力，花如中酒。睡怯象牙寒悄，幽梦几回浑不就。燕搊华弦，莺调清管，细谱新词杜鹃噣。行路方难，归期无据，愁与闷相守。\",\"芳醪点出天公手。解翻寒作暖，撺辰成酉。枕畔华胥暂拖逗。青眼朦胧，一任长门，送来银漏。未举尊前，乍停杯后，半刻也堪白首。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4b6b591a83d4fd030','满庭芳·春夜不寐','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"尖侧东风，迷离烟雨，只解排比黄昏。一灯清映，炯炯泪珠痕。薰尽铜垆香灺，相思被、熨帖难温。那堪更，穿花玉漏，点点出长门。\",\"无端千万种，新愁旧事，来往纷纷。总成就、天涯一病身。挨得邻鸡报也，权撇下、几件销魂。还禁架，杨花燕子，迟日悄庭阍。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4b7ede68aaaf7786d','望江南·梦故乡作','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"无个事，湘枕睡初酣。青织晚潮萦似带，碧攒春树小于簪。遮莫是江南。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4bf7b2c6d04b1b0a5','鹧鸪天·书怀','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"中酒朝来仗酒医。爱花身老妒花飞。扶持白发无长计，点染朱颜有片时。\",\"悲落魄，笑支离。世情原与世人宜。青山不用金钱买，只碍当头一小儿。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4c1a31b54c31c232d','百字令','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"柳骄花横，未全受眼底，封姨拘束。不为章台春做主，为有王维新曲。翡翠帘垂，郁轮袍就，字字敲金玉。樱桃微绽，一声扶醒红绿。\",\"堪羡凤缕瑶筝，鸾镌牙版，羯鼓催相逐。换羽移商才不尽，那怕周郎回瞩。和雪人稀，奏薰弦断，昨夜东皇恧。罚教尘世，白头长自空谷。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4c2ebbe3b5ea57f0f','鹧鸪天','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"度索山头根未枯。天台移作美人都。霏微映水霞千片，旖旎含风锦万株。\",\"穿屈戍，点屠苏。千年结子解偷无。若教王母而今见，唤作徐卿第一雏。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4c67f46d93d2f8874','鹧鸪天·渔舟','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"蘋末风吹舴艋舟。荡寒村酒两三瓯。蔚蓝天起鱼鳞皱，罨画溪穿燕尾流。\",\"无一事，不知愁。绿蓑衣垫卧船头。相逢莫诧无鱼卖，自是平生用直钩。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4cf20b5656135572d','怨王孙寄思','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"愁似中酒。难禁厮守。织雨黏云，困花酣柳，猛见双雀金翘。暗魂消。\",\"那人偏自沾情绪。丢不去。只傍眉峰住。倩他梦里，相会无那娇莺。两三声。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4d0ea6a970b766f62','苏幕遮·病中夜坐闻隔席欢笑有感','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"翠垆烟，红烛雨。雨底铜壶，滴到难挨处。欲作新诗心自语。身入中年，怕作关情句。\",\"酒如油，花似雾。谈笑风流，一霎抛人去。病与穷愁相伴住。笺恳天公，残日休如许。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4d17345bc72825281','望江南思家','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"回首处，薄业太湖干。半艇春洲芦笋绿，一楼风雨杏花寒。\",\"此景放谁看。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4d31d035f3a2813fe','生查子·离恨','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"频馀翡翠簪，渐缓鸳鸯带。十二铸愁垆，百炼芳年改。\",\"人是此生人，债是前生债。精卫一丸泥，空去填东海。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4d60ba50b821d8205','长相思·闺怨','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"风满陂。雨满陂。雨雨风风无尽时。孤帆天外迟。\",\"莺儿飞，燕儿飞。燕燕莺莺双翅低。愁他掠翠帏。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4d62fdb26d432e295','望江南书所见','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"春睡足，钗玉自欹斜。立傍镜台还胍胍，枕痕纤甲印桃花。\",\"来试雨前茶。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4d7c8abb69c6f1926','法曲献仙音·题双湖卷赠周君寿','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"剩水分江，柳堤梅垞，属玉联拳鸣唤。浸玉濆珠，钓童浣女，芙蕖并头凌乱。\",\"看皓月金波散。俱涵一轮满。\",\"水晶馆。问东君、酒卮无恙，长日里、兰舫绣帆相绾。\",\"掩映翠琉璃，似佳人、一对纤眼。碧漱红酣，瀼东西、止隔花岸。\",\"待年年岁岁，倒着接䍦还健。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4dbfeedef4cc82e1f','水调歌头·上已日','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"三月又三日，上已复清明。问君几许，高兴儿女队中行。数点洗尘芳雨，一脉养花天气，信马出郊坰。年少五陵子，金弹惹流莺。\",\"过油璧，低粉面，按银筝。管弦丝竹，何限应自胜兰亭。共酌几杯春醑，也插一枝杨柳，归袖任纵横。听取九门钥，隐隐下西清。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4dc06352015caf41e','望江南晚步','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"随意步，芒屦破苍苔。半壁雨收残日去，满江风送晚潮来。\",\"此景亦悠哉。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4def143cd55a71894','玉蝴蝶·拟艳','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"记得秋娘，家住皋桥西弄，疏柳藏鸦。翠袖初翻金缕，钩月晕红牙。启朱唇、含风桂子，唤残醉、微雨梨花。最堪夸。玉纤亲自，浓点新茶。\",\"嗟呀。颠风妒雨，落英千片，断送年华。海角山尖，不应飘向那人家。惹新愁、高楼燕子，赚人泪、芳草天涯。况浔阳，偶然江上，一曲琵琶。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4df2be99c1b4db762','杨柳枝','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"十月寒轻叶未凋，淡黄疏绿短长条。无情有态堪怜处，日脚云头雨半腰。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4df2be99c70fc2618','杨柳枝','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"飞花欲卷翠蛾颦，浪打萍飘恼杀人。犹胜洛阳东陌上，北风吹作马蹄尘。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4df2be99c8bbdd4b8','杨柳枝','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"西蜀竹枝君莫听，且听东吴杨柳枝。是处春阴堪画舫，何村树杪不青旗。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4df2be99cef2a9e0b','杨柳枝','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"杨柳吴阊千万枝，横塘风绾曲尘丝。不缘游子无攀折，总为情人少别离。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4e18d578b13969e4d','满庭芳·登道场山作','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"一雨催凉，千崖吐秀，万壑争效潺湲。暂停篮笋，携杖翠微间。点点龙鳞玉甃，堪怜处、屐齿敲悭。朱栏外，粘天万顷，七十二峰间。\",\"呼尊夸更有，松飙凤吹，云岫鸦鬟。又谁送、黄鹂巧唱绵蛮。漫问何山最好，争应道、还有何山。能移兴，新晴月色，飞步上孱颜。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4eb310fbabc1dec40','浣溪沙·江南词','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"一夜春波酿作蓝。晓桑柔叶绿鬖鬖。鸦鬟十五太骄憨。\",\"织就双鱼成比目，偷将百草斗宜男。更无心绪喂春蚕。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4ec1325c07c8557bf','木兰花一名玉楼春','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"是谁约勒东君去。枝上晓声寒杜宇。柔绿犹能抵死留，妖红不解逡巡住。\",\"灞陵一阵飘香雨。宛转玉骢蹄下土。记他含涩拆苞时，总有千金无赎处。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4ef80274c87e282f3','望江南·即事','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"歌起处，斜日半江红。柔绿篙添梅子雨，淡黄衫耐藕丝风。家在五湖东。\"]',NULL,NULL,'这是一首轻快活泼的短歌。起句以悠扬的歌声，抓住读者的听觉，先声夺人，引出明丽的画面：斜日半江红。斜日正是落日，落日的晚霞洒在江面上，碧绿的江水出现了“半江瑟瑟半江红”（白居易诗）的瑰丽画面。接着词人又以“柔绿的竹篙”和“黄梅细雨”点缀画面，夕阳之下，细雨，江水新涨，着一“添”字，使人似乎看到竹篙吃水加深，使撑船之人更需奋力，情绪渐渐高涨，推出词中的主人公：穿着淡黄衣衫撑篙的舟子。他那随藕丝般微风飘动的黄色衣衫，不仅给画面增添了新的色彩，也给画面增添了生气。“藕丝风”三字把无形之风写得富有质感，“藕”字透出柔和粉嫩的色彩，造成优美的视觉效果。最后，以“家在五湖东”收尾。这是舟子的家乡，也是词人的家乡太仓的所在地，轻描淡写的陈述句式，却有十分丰富的意蕴，首先是说明家乡之所在，其次点《忆江南》之题，洋溢着舟子，也是词人对家乡的热爱。\n全词写景如画，色彩和谐，折射出词人心灵的平静。境界的开阔和景物的明丽，又透露了他强烈的思乡之情。摹景抒情，诗中有画，表现了诗与画的同步造诣和审美情趣，达到了唐代诗人“诗中画趣”的优美境界。','[\"篙：竹篙。\",\"梅子雨：即夏天梅子黄熟时的连绵细雨。\",\"耐：宜、适宜。\",\"藕丝风：形容风雨细如藕丝。\",\"五湖：指太湖。\"]'),('e647d10f92194bd4f377977df482b930','三五七言秋江辞','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"风江秋，风叶愁。孤云阁远屿，一雁落汀洲。贪看十五焦山月，帆到金陵不肯收。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4f7b691f5ea328f02','河满子·春郊独行','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"卵色遥垂别浦，鱼鳞浅甃平沙。小拂东风无甚力，悠扬自在杨花。轻暖轻寒天气，半村半郭人家。\",\"碧贮莲花露酒，香分谷雨前茶。呼取自斟还自劝，乌纱任汝欹斜。返照一行归骑，隔林几点残鸦。\"]',NULL,NULL,NULL,NULL),('e647d10f92194bd4f7c9f55f9493ac70','临江仙·癸丑元日醉题','王世贞','e647d10f92194bd4e60e81c4bcb41ccd','明',NULL,'[\"拨乳酴酥新绿泛，金花巧胜初裁。东风殢雨印泥苔。腊随残漏尽，春逐烧痕来。\",\"昨岁贪杯今岁病，病时依旧贪杯。欲填新令雪儿排。小园梅未吐，先报一枝开。\"]',NULL,NULL,NULL,NULL),('e647d10f92eacc49f32f182e209e35f6','浣溪沙','汤允','e647d10f92eacc49d1457b72d1457b72','明',NULL,'[\"燕垒雏空日正长，一川残雨映斜阳。鸬鹚晒翅满鱼梁。\",\"榴叶拥花当北户，竹根抽笋出东墙。小庭孤坐懒衣裳。\"]',NULL,NULL,NULL,NULL),('e647d10f93132c755f1a5286be4ecb9d','意难忘雨夜','霍达','e647d10f93132c75d1457b727d12b66d','明',NULL,'[\"雨颤风摇，正百花时候，夜截春梢。挑灯惊鼠梦，拭泪掩绞绡。钗钿冷、佩环遥，玉鉴远山消。更可怜、心香烬夜，幼子啼宵。\",\"当年携手游遨，似丁香思结，心展芭蕉。寻花才减绣，话雨暗添膏。思往事、付春潮，赚杀旧魂飘。誓今生、梦长睡短，暮暮朝朝。\"]',NULL,NULL,NULL,NULL),('e647d10f932420e583cfc9c2e5deabd6','竹枝词','金銮','e647d10f932420e5d1457b72d1457b72','明',NULL,'[\"春雨春风花意狂，谁家溪上浣花娘。水流不管花心乱，花落能教水面香。\"]',NULL,NULL,NULL,NULL),('e647d10f9353a4c8f22e8d7ad1247238','西湖竹枝词','瞿佑','e647d10f9353a4c833ebd5b0970af30e','明',NULL,'[\"西子湖边杨柳枝，千条万缕尽垂丝。东风日暮花如雪，飞入雕墙两不知。\"]',NULL,NULL,NULL,NULL),('e647d10f93c0441da2d534f6f97c7795','减字木兰花','阮大铖','e647d10f93c0441d9fb22219e4873aa9','明',NULL,'[\"春光渐老，流莺不管人烦恼。细雨窗纱，深巷清晨卖杏花。\",\"眉峰双蹙，画中有个人如玉。小立檐前，待燕归来始下帘。\"]',NULL,NULL,NULL,NULL),('e647d10f94da4364841ab7d114a99d1f','蝶恋花·天香馆寄陈湖山','马守贞','e647d10f94da4364d1457b72d1457b72','明',NULL,'[\"阵阵东风花作雨，人在高楼，绿水斜阳暮。芳草垂杨新燕羽，湘烟剪破来时路。\",\"肠断萧郎纸上句，深院啼莺，撩乱春情绪。一点幽怀谁共语，红绒绣上罗裙去。\"]',NULL,NULL,NULL,NULL),('e647d10f95447a1b31858410d1d73c68','山花子','白旃香','e647d10f95447a1bd1457b72d1457b72','明',NULL,'[\"桁垂杨拂画桡，碧桃花处泛轻舠。六曲纹窗春水碧，赤阑桥。\",\"绿酒红灯风细细，碧天明月夜迢迢。何事春来频载酒，听吹箫。\"]',NULL,NULL,NULL,NULL),('e647d10f954a62de4c7429bfc60d63f1','南浦·浦滕王阁怀古','喻综','e647d10f954a62ded1457b72d1457b72','明',NULL,'[\"天高气爽，看平楚、落叶动秋声。南国惊霜征雁，带影下寒城。漠漠轻舟过雨，湿帆飞远、几点微星。更萧条鸦集，陌头衰柳，残照万峰青。\",\"瑟瑟江澄染练，忆从前、何事不凝情。日月销磨今古，不见旧旗旌。青雀黄龙何在，也阶除、惟有树长生。叹去来、月上栏杆，空际水波明。\"]',NULL,NULL,NULL,NULL),('e647d10f95bcb64c38e6d255f19d933e','菩萨蛮·秋闺','徐灿','e647d10f95bcb64cf60bb6bb9fe97fff','明',NULL,'[\"西风几弄冰肌彻，玲珑晶枕愁双设。时节是重阳，菊花牵恨长。\",\"鱼书经岁绝，烛泪流残月。梦也不分明，远山云乱横。\"]',NULL,NULL,'这是一首闺怨词，是作者早起与其夫陈之遴分居两地时所作，将独守空闺女子的落寞凄苦之情，表现的细腻而又蕴藉。\n上阕由景生情。秋风吹得女主人公冰肌寒透了，它却那般不解人意，只顾一味恼人。身体感知的是时节的变迁，时节物侯的变迁不免令人顾影生怜。因为「玲珑晶枕愁双设」——即使美丽的水晶枕也徒为虚设。看着它们双倚的样儿怎不使人感念自身的孤单。此处点明的是「愁」，「双」乃愁缘起。重阳节是登高的好日子，亲朋友伴原该簪上菊花，喜悦地相偕而出。而此时此刻，菊花着吉祥之物，却牵出了几多对远人的思念，让人恨恨难消！\n下阕自道心事。恨的却是远方人久无鱼书，音信全无。独守闺中人的深情，怕只有深夜时长燃的蜡烛和拂晓前的淡月相知吧。「烛泪流残月」，见出从傍晚到深夜，又从深夜到天明，主人公彻夜不能成眠。想拖个吉梦在梦中见他，却也恍恍惚惚，醒转来只见得一抹远山、乱云簇簇。这首小词，将独守空闺女子的落寞凄苦之情，表现的细腻而又蕴藉。','[\"西风：秋风。\",\"鱼书：喻指书信。\"]'),('e647d10f95bcb64c74765d2d912b30d2','菩萨蛮·春闺','徐灿','e647d10f95bcb64cf60bb6bb9fe97fff','明',NULL,'[\"困花压蕊丝丝雨，不堪只共愁人语。斗帐抱春寒，梦中何处山。\",\"卷帘风意恶，泪与残红落。羡煞是杨花，输它先到家。\"]',NULL,NULL,'《菩萨蛮·春闺》是明末清初女词人徐灿所创作的一首作品。这是一首闺怨词，描写了人在愁绪中，雨是借以诉情的最佳友伴。\n这是一首闺怨词。人在愁绪中，雨是借以诉请的最佳友伴。首句「困花压蕊丝丝雨」，真切地写出春雨的绵长轻润。但这丝雨「困」花「压」蕊，原来也像是了解了闺中女子的愁肠，一任默默地飘洒相伴。「斗帐抱春寒」，夜深了人在红罗绣帐里却不能成寐，便更觉得春夜的清寒。「抱」，写出闺中人之孤单无助，惟有抱「春寒」而已。「梦中何处山」，独自寻梦，梦里山重水远，并不能遇见自己所思念的人。下阕，「卷帘风意恶」，因心事凄迷，便觉得连吹卷起珠帘的风也欺悔人。这是以「我」之眼观物的写法，此语看似极无理，实则极含情。惟究词人心绪何以如此之坏，「泪与残红落」拖出了并不难猜的谜底：原来是看流水逝花，叹岁月流转，恐年华不再，故而泪与残红落。结句乃把一段心事，付与自由飘舞的杨花，盼着远人也如杨花般早日回家。全词格调清新秀雅，虽写一般闺情，而不一味抑郁不欢，能于词中独赋一种绵渺深情，令人味之而意愈长。','[\"斗帐：小帐。形如覆斗，故称。\",\"春寒：指春季寒冷的气候。\",\"卷帘：卷起或掀起帘子。\",\"残红：凋残的花，落花。\"]'),('e647d10f95ceee70e429ae79eeaf1fed','如梦令','张婉','e647d10f95ceee70d1457b72d1457b72','明',NULL,'[\"陌上莺啼绿柳，楼上花飞宝甃。晴日晚春天，画箧香消豆蔻。\",\"消瘦，消瘦，又是断肠时候。\"]',NULL,NULL,NULL,NULL),('e647d10f960692dda21feb0c31a7ee8f','菩萨蛮·答何用明','桂华','e647d10f960692ddd1457b72d1457b72','明',NULL,'[\"帝城三月莺花绕，笙歌沸彻千家晓。半壁夕阳明，醉眠人未醒。\",\"城头鸣画角，惊起南飞鹊。回首上林枝，行人肠断时。\"]',NULL,NULL,NULL,NULL),('e647d10f961502103b8c897deb2e2af4','贺新凉雁声','邵嶙','e647d10f96150210d1457b72d1457b72','明',NULL,'[\"睡觉秋窗罅，正闲阶、商飙萧飒，明蟾低亚。嘹唳数声天际响，直透茆檐竹瓦。这凄怨、凭谁堪话。辛苦塞垣千万里，又明星、几点湘烟挂。稻粱好，究须怕。\",\"娟娟凉露横空洒。好似听、哀弦密拍，楚歌悲咤。难比闲鸥和野鹜，随意鲈乡蟹舍。任沙觜、沿缘上下。今夜重帏寒信紧，又豆棚、络纬萧萧者。铜仙泪，泫然泻。\"]',NULL,NULL,NULL,NULL),('e647d10f97105bceb253d4d1fed5addb','柳枝词','胡安','e647d10f97105bced1457b72d1457b72','明',NULL,'[\"惜取杨枝暮复朝，未经攀折欲魂消。闲中喜得无离别，缓步春风灞水桥。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf15cde133c09afd957','应天长','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"星驰一线，宝炬腾空，千花一时开遍。万斛金莲，恍似红妆映波面。香风转，云半卷。见舞袖、翩翻轻旋。想胜景、即是仙宫，蓬莱阆苑。\",\"欢乐意留恋。岁岁年年，长沐赐宴。五色红云，瑞气祥氛互葱茜。移銮辂，开雉扇。仙仗拥、九华春殿。向黼扆、更祝南山，寿觞频劝。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf197b5b8dae61a9929','满庭芳','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"玉殿传宣，金吾弛禁，皇都春意争妍。暝烟初敛，华月吐婵娟。紫陌香尘冉冉，六街上、车马骈阗。争来看，鳌山灯火，滉漾九重天。\",\"圣皇端拱处，千官鹄立，玉笋班联。有歌喉，宛转舞袖蹁跹。清漏迟迟夜永，不妨对、绮席琼筵。嵩呼愿，齐天万寿，同乐太平年。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1ab4022b254e7797e','瑞鹤仙·元宵词五阕','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"三山浮海上。忽六鳌移来，高耸万丈。祥云护仙掌。\",\"看峰峦几叠，洞天相向。烟霞滉漾。势凌空、奇姿异状。\",\"见丹霄、翠色崔嵬，特起十分雄壮。\",\"凝望。琼装万树，绣簇千花，倚天屏障。群仙庆会。三岛上，共欢赏。\",\"有飙轮鸾鹤，飘飘天际，羽盖缤纷彩仗。向金门、宴乐良宵，太平气象。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1dd40a6f116afdc22','画堂春','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"春空云尽月华明。雪消才放新晴。元宵游赏遍都城。处处欢声。\",\"丹禁九重宫阙，彩鳌三岛蓬瀛。万方玉帛会瑶京。同乐升平。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1e19966474a45f76f','西江月·端午赐观击毬射柳五阕','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"箫鼓教坊间奏，锦袍玉佩联翩。彩毬击罢听传宣。竞挽雕弓飞箭。\",\"柳外争夸中的，花前捷报飞传。香尘起处正回鞭。万岁嵩呼舞抃。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1e19966474d2354d4','西江月·端午赐观击毬射柳五阕','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"御座正临仙苑，禁林大敞琼筵。臂间长命彩丝缠。何必灵符丹篆。\",\"处处龙舟竞渡，家家箫鼓喧阗。万方无事乐丰年。仰荷圣明恩眷。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1e199664753b88135','西江月·端午赐观击毬射柳五阕','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"芳树海榴吐艳，碧池荷叶铺钱。端阳佳节太平年。百辟咸沾恩宴。\",\"仙仗遥迎瑞日，炉香细袅祥烟。香蒲泛酒艾旗悬。拜舞金銮前殿。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1e199664799f9a5d6','西江月·端午赐观击毬射柳五阕','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"内苑场开紫禁，瑶台佩列群仙。两朋骏马总腾骞。玉勒金羁光炫。\",\"左右分曹决胜，毬飞仗击争先。流星一点影回旋。挥霍纷纭万变。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1e1996647c32bd7c7','西江月·端午赐观击毬射柳五阕','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"令节新颁宝扇，嘉言挥洒云笺。香罗细葛叠相鲜。剑佩趋陪金殿。\",\"臣庶欣逢舜世，华夷共祝尧年。皇图巩固福绵绵。磐石尊安永奠。\"]',NULL,NULL,NULL,NULL),('e647d10f97576bf1ef0327652d42702d','醉蓬莱','杨荣','e647d10f97576bf181ca0262f4a331b7','明',NULL,'[\"近天阶咫尺，云拥千官，动摇环佩。仙乐声高，似飘飖天外。罗绮香秾，珠㙨光粲，看红妆千队。银汉低徊，月临仙掌，星垂华盖。\",\"睹此繁华，古今应最，禁苑中此，夕同欢会。海上三山，与金茎相对。宝马香车，九街驰逐，遍五城欣快。圣寿齐天，年年岁岁，嵩呼罗拜。\"]',NULL,NULL,NULL,NULL),('e647d10f97f2068a5ab97d7ee678e8bb','少年游','马洪','e647d10f97f2068ad1457b72d1457b72','明',NULL,'[\"弄粉调脂，梳云掠月，次第晓妆成。鹦鹉笼边，秋千墙里，半晌不闻声。\",\"原来却在瑶阶下，独自踏花行。笑摘朱樱，微揎翠袖，枝上打流莺。\"]',NULL,NULL,NULL,NULL),('e647d10f97f2068a6899ff201d113fe9','东风第一枝·梅花','马洪','e647d10f97f2068ad1457b72d1457b72','明',NULL,'[\"饵玉餐香，梦云惜月，花中无此清莹。俨然姑射仙人，华佩明珰新整。五铢衣薄，应怯瑶台凄冷。自骖鸾、来下人间，几度雪深烟暝。\",\"孤绝处、江波流影。憔悴也、春风销粉。相思千种闲愁，声声翠禽啼醒。西湖东阁，休说当时风景。但留取、一点芳心，他日调羹翠鼎。\"]',NULL,NULL,NULL,NULL),('e647d10f97f2068ac80915a6e80844cd','满庭芳·落花','马洪','e647d10f97f2068ad1457b72d1457b72','明',NULL,'[\"春老园林，雨余庭院，偏惹蝶骇莺猜。蔫红皱白，狼藉满苍苔。正是愁肠欲断，珠箔外、点点飘来。分明似，身轻飞燕，扶下碧云台。\",\"当初珍重意，金钱竞买，玉砌新栽。更翠屏遮护，羯鼓催开。谁道天机绣锦，都化作、紫陌尘埃。纱窗里，有人怜惜，无语托香腮。\"]',NULL,NULL,NULL,NULL),('e647d10f97f2068ae78f81303a82f4ed','行香子','马洪','e647d10f97f2068ad1457b72d1457b72','明',NULL,'[\"红遍樱桃，绿遍芭蕉，锁窗深、春思无聊。双飞燕懒，百啭莺娇。正漏声迟，帘影静，篆香飘。\",\"惜月前宵，病酒今朝，有谁知、臂玉微销。封题锦字，寄与兰翘。恨树重重，云渺渺，水迢迢。\"]',NULL,NULL,NULL,NULL),('e647d10f98b456f9bef28b91dd18e542','百字令题赵仲穆越山图','张鈇','e647d10f98b456f9d1457b72d1457b72','明',NULL,'[\"浙江东岸，是越王勾践，旧时封国。尝胆卧薪成底事，惟有荒台凝碧。万壑争流，千岩竞秀，宛宛无今昔。兔葵燕麦，中间多少遗迹。\",\"遥想东晋风流，兰亭停禊，空自留残墨。何用登临伤往事，堪叹鬓须垂白。且觅扁舟，贺家湖上，载酒寻春色。季真归后，四明还有狂客。\"]',NULL,NULL,NULL,NULL),('e647d10f9a0f536f2445039e50ff3928','摘红英','李　鄂','e647d10f9a0f536fd1457b72d1457b72','明',NULL,'[\"潮头白，峰头碧，落梅香暗渔翁笠。西风透，东风骤，待得晴明，莺花时候。\",\"莺声涩，花枝寂，春来已蜡看山屐。山如旧，春如绣，层楼望处，苍寒红瘦。\"]',NULL,NULL,NULL,NULL),('e647d10f9a29eb081ae8e4e46317eef0','念奴娇·茉莉','于玉班','e647d10f9a29eb08d1457b72d1457b72','明',NULL,'[\"檐栊午寂，正阴阴窥见，后堂芳树。绿遍长丛花事杳，忽见琼葩丰度。艳雪肌肤，蕊珠标格，消尽人间暑。还忧风日，曲屏罗幕遮护。\",\"长记歌酒阑珊，微闻暗麝，笑觅衣沾露。月没阑干天似水，相伴谢娘窗户。浴后轻鬟，凉生滑簟，无限风流处。惹人幽梦，枕边零乱如许。\"]',NULL,NULL,NULL,NULL),('e647d10f9b4768b77874e331731792bc','如梦令·纪事','计南阳','e647d10f9b4768b7d1457b72d1457b72','明',NULL,'[\"长乐晨钟初动，睡熟紫绡娇凤。翠辇一时移，十骑红妆云拥。\",\"如梦，如梦，城上乌啼相送。\"]',NULL,NULL,NULL,NULL),('e647d10f9b4768b79513cd83f87d71d6','花非花','计南阳','e647d10f9b4768b7d1457b72d1457b72','明',NULL,'[\"同心花，合欢树。四更风，五更雨。画眉山上鹧鸪啼，画眉山下郎行去。\"]',NULL,NULL,NULL,NULL),('e647d10f9b9a3ba497b5b8da569f33de','满庭芳','吴承恩','e647d10f9b9a3ba49b1fc1894748b616','明',NULL,'[\"观棋柯烂，伐木丁丁，云边谷口徐行。卖薪沽酒，狂笑自陶情。苍径秋高，对月枕松根，一觉天明。认旧林，登崖过岭，持斧断枯藤。\",\"收来成一担，行歌市上，易米三升。更无些子争竞，时价平平。不会机谋巧算，没荣辱，恬淡延生。相逢处，非仙即道，静坐讲《黄庭》。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9b8a5cd4ae4ee46f48','柳枝歌三首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"苏小门前万缕垂，白家园内两三枝。听歌看舞人何在，惟有东风展翠眉。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9b8a5cd4ae90abf6e8','柳枝歌三首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"宛转千条罥晚风，拖烟带雨渭城东。征衫点得轻轻絮，寄入《阳关》曲调中。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9b8a5cd4aedd3bcdd3','柳枝歌三首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"三月风和散曲尘，枝枝垂地每伤神。为君系得春心住，忍折长条送远人。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9bd721e6fadee420f6','鹧鸪天·赋绣鞋','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"花簇香钩浅浣尘，轻风微露石榴裙。金莲自是悭三寸，难载盈盈一段春。\",\"仙已去，事犹存，阳台何处更为云。相思携手游春日，尚带年时草露痕。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9be34996c98acf775b','竹枝歌二首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"春风满山花正开，春衫女儿红杏腮。侬家荡桨过江去，为问阿郎来不来。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9be34996c9bf530560','竹枝歌二首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"巴山后面竹鸡啼，巴水前头沙鸟栖。巴水巴山到郎处，闻郎又过石门溪。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9be70fbd251ae73e72','杨柳枝·二首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"莫折柔丝五尺金，一丝丝系别离心。春风吹到无情处，乱滚香绵结昼阴。\"]',NULL,NULL,NULL,NULL),('e647d10f9c07dc9be70fbd25a40efbf7','杨柳枝·二首','周宪王','e647d10f9c07dc9bd1457b72d1457b72','明',NULL,'[\"春来折尽更逢春，旧折长条又复新。谁有行人相送别，今年不是去年人。\"]',NULL,NULL,NULL,NULL),('e647d10f9cb71fd7bd6fb1c72a0e49e5','霜天晓角','黄承圣','e647d10f9cb71fd7d1457b72d1457b72','明',NULL,'[\"春日小窗独坐，闻鹧鸪声有感凄凉晓色，衣带云英湿。芳草春来破冻，阑干外、一痕碧。\",\"鹧鸪声正急，绿窗人怨泣。休向东皇诉恨，狂风在、女墙侧。\"]',NULL,NULL,NULL,NULL),('e647d10f9ccfff346e0b45c784668b31','西楼子初月','顾文婉','e647d10f9ccfff34d1457b72d1457b72','明',NULL,'[\"雕阑漫倚凝眸，月如钩。一段晚香薄雾、织成愁。\",\"怀人意，思乡味，几时休。除是梦中暂借、广寒游。\"]',NULL,NULL,NULL,NULL),('e647d10f9ccfff34f32f182e12586525','浣溪沙','顾文婉','e647d10f9ccfff34d1457b72d1457b72','明',NULL,'[\"晓日凝妆上翠楼，恼人春色遍枝头。湘帘风细荡银钩。\",\"燕子未归寒恻恻，梅花初落恨悠悠。重门深锁一天愁。\"]',NULL,NULL,NULL,NULL),('e647d10f9ccfff34f32f182e9a65b9b8','浣溪沙','顾文婉','e647d10f9ccfff34d1457b72d1457b72','明',NULL,'[\"风雨妨春苦不宽，开帘怕见嫩红残。锦屏深护早春寒。\",\"新嫩一身扶不起，愁痕万点镜慵看。空拈斑管写长叹。\"]',NULL,NULL,NULL,NULL),('e647d10f9d31238373f48536e82339bc','水调歌头','张一如','e647d10f9d312383d1457b72d1457b72','明',NULL,'[\"落月下春苑，啼鸟别花曹。无端凑与离怨，难咽割肠刀。玉貌雪肤何处，只剩无情有恨，隐隐怕逢挑。空态忽沾臆，暗影自栖膏。\",\"华畿邈，巫峡渺，汉津涛。此情终古，直教同是一忉忉。送断韶华一瞬，赚杀芳心一寸，船往不留篙。回忆可堪话，无计放萧骚。\"]',NULL,NULL,NULL,NULL),('e647d10f9d943eac533420ce3f100b64','竹枝词四首题陈侯东华送别图','尹台','e647d10f9d943eacb5488aefed422773','明',NULL,'[\"东华晓日照江楼，冠盖云屯拥画舟。借问南阳诸父老，谁能挽取旧岑侯。\"]',NULL,NULL,NULL,NULL),('e647d10f9d943eac533420ce6f885af7','竹枝词四首题陈侯东华送别图','尹台','e647d10f9d943eacb5488aefed422773','明',NULL,'[\"三年为政似君无，人道莱芜釜有鱼。留得家携青色犊，升朝自驾旧来车。\"]',NULL,NULL,NULL,NULL),('e647d10f9d943eac533420ce9d9a3321','竹枝词四首题陈侯东华送别图','尹台','e647d10f9d943eacb5488aefed422773','明',NULL,'[\"蓼花新涨动孤城，千尺虹梁截岸横。欲系仙帆无那远，中流箫鼓望双旌。\"]',NULL,NULL,NULL,NULL),('e647d10f9d943eac533420cecf983368','竹枝词四首题陈侯东华送别图','尹台','e647d10f9d943eacb5488aefed422773','明',NULL,'[\"竹枝词和古桐乡，去思浑牵江水长。遥忆东华松独倚，残株犹足比甘棠。\"]',NULL,NULL,NULL,NULL),('e647d10f9d9b87082c58277c14ae65d9','忆秦','陆卿子','e647d10f9d9b8708d1457b72d1457b72','明',NULL,'[\"砧声咽，梅花梦断纱窗月。纱窗月，半枝疏影，一帘凄切。\",\"心前旧愿难重说，花飞春老流莺绝。流莺绝，今宵试问，几人离别。\"]',NULL,NULL,NULL,NULL),('e647d10f9d9b8708dd40a6f118161377','画堂春','陆卿子','e647d10f9d9b8708d1457b72d1457b72','明',NULL,'[\"晴空烟袅柳丝微，乱红风定犹飞。杏花零落燕空归，门外鸦啼。\",\"慵病不禁宽带，讳愁无那尖眉。香消斜倚画屏时，此恨谁知。\"]',NULL,NULL,NULL,NULL),('e647d10f9de4c9b89611d7512eb5803a','江城子·秋日行舟','沈麖','e647d10f9de4c9b8d1457b72d1457b72','明',NULL,'[\"浮萍潆曲小桥边，似荷钱，似花钿。秋艇宜人、一路棹添烟。三径高情来访菊，无事尔，此身前。\",\"新生野水碧涓涓，跃鱼渊，钓丝牵。疏柳依依、坡岸弄轻妍。日落残霞分夜影，钟断处，塔铃传。\"]',NULL,NULL,NULL,NULL),('e647d10f9de4c9b8efcefce865ddc68b','如梦令·醉后题画兰赠陈姬','沈麖','e647d10f9de4c9b8d1457b72d1457b72','明',NULL,'[\"月枕纱笼酒后，打点玉容柔瘦。洒墨乱春妆，香雨沾人衣袖。\",\"成就，成就，今夜风情消受。\"]',NULL,NULL,NULL,NULL),('e647d10f9de7cbb30aac85029ebc775a','应天长·春日田家乐','周世臣','e647d10f9de7cbb3d1457b72d1457b72','明',NULL,'[\"溪南溪北桥横木，赖得行人影孤独。柳数围，传新绿，刚覆三间茆屋。\",\"床头栖野鹜，闲个篱边眠犊。黄土矮墙坚筑，安顿初添竹。\"]',NULL,NULL,NULL,NULL),('e647d10f9de7cbb3dd4e1909470f8d2a','蝶恋花·舟中有怀','周世臣','e647d10f9de7cbb3d1457b72d1457b72','明',NULL,'[\"今夜相思有几许，又怕他宵，无计安排去。野岸萧萧芦自语，各天默默人无据。\",\"好事岂教长久聚，强把鸳俦，一霎分何处。睡眼朦胧多恶绪，披衣坐觑东方曙。\"]',NULL,NULL,NULL,NULL),('e647d10f9e2268b3e4215d0c087aba90','南歌子·秋思','方大猷','e647d10f9e2268b3d1457b729a440050','明',NULL,'[\"蓬鬓惊堆雪，衰容艳拒霜。莫持琼管弄清商，吹得万千林叶、一时黄。\",\"谁道春如剪，裁成百样芳。那知秋色是干将，天外群峰处处、割愁肠。\"]',NULL,NULL,NULL,NULL),('e647d10f9ec3bfade429ae796c88e91c','如梦令','陈淳','e647d10f9ec3bfadd1457b72d1457b72','明',NULL,'[\"吟罢小池杨柳，酌尽玉壶春酒。水面正风来，吹乱一天星斗。\",\"消瘦，消瘦，愁在凭阑时候。\"]',NULL,NULL,NULL,NULL),('e647d10f9ec702b7f29ff18e159447d4','点绛唇','蔡宗尧','e647d10f9ec702b7d1457b72d1457b72','明',NULL,'[\"寒雨溪桥，卷帘半醉春风好。早莺来了，疏影梅花老。\",\"吟破狂愁，燕落江天渺，山城小。马蹄多少，露湿王孙草。\"]',NULL,NULL,NULL,NULL),('e647d10f9f23de7ac2ebbe3b0eb90731','鹧鸪天','杨玉香','e647d10f9f23de7ab49c63fab49c63fa','明',NULL,'[\"郎系闽南第一流，胸蟠星斗气横秋，新词婉转歌才毕，又遂征鸿了碧楼。\",\"拉锦缆，由兰舟，见郎欢喜别郎忧，妾心正似长江水，昼夜随郎到福州。\"]',NULL,NULL,NULL,NULL),('e647d10f9f6f5f201368c91421c301a6','临江仙','夏皀','e647d10f9f6f5f20d1457b72d1457b72','明',NULL,'[\"榴叶竹屏闲处好，香街雨梦无缘，听人说著指琼鞭。玉楼嗔短舞，书枕惬高眠。\",\"几夜晚眠还早起，城中一半风烟，画桥架水绿阴连。柳眉伤老大，莺口弄清圆。\"]',NULL,NULL,NULL,NULL),('e647d10f9f6f5f20f137049b0987b111','阮郎归','夏皀','e647d10f9f6f5f20d1457b72d1457b72','明',NULL,'[\"赌酒明湖未放船，时节欲开莲。壁后茶灶几点烟，听雨小窗眠。\",\"云解冻，水平田，蛙声又一年。晚晴花草落愁边，呼童问酒钱。\"]',NULL,NULL,NULL,NULL),('e647d10fa0fb94eb8fa3815f0bee2e88','踏莎行·过金山寺','董剑锷','e647d10fa0fb94ebc7af0926375c7134','明',NULL,'[\"一拳浮玉，中流砥柱，茫茫烟水迷云树。年来两过未曾登，那知张祜题诗处。\",\"南北横分，古今几度，拿舟三老随歌渡。歌声幸莫感伤人，江流一任人来去。\"]',NULL,NULL,NULL,NULL),('e647d10fa1d3dc71b253d4d12beb23c4','柳枝词','郭浚','e647d10fa1d3dc71d1457b72d1457b72','明',NULL,'[\"送郎西上别魂消，却道纤身似柳条。只恐别魂销远客，柳条翻似阿郎腰。\"]',NULL,NULL,NULL,NULL),('e647d10fa22fc3e3267f59cfbd34d8d6','潇湘逢故人慢拟王和甫','韩洽','e647d10fa22fc3e3d1457b72d1457b72','明',NULL,'[\"园亭晴敞，正梁飞旧燕，林唱新蝉。望清景无边，有青峰回合，碧渚相连。葛衣纱帻，对南薰、一曲虞弦。起无限、乡心别恨，潇湘夜雨朝烟。\",\"曲终也，余韵在，见游鱼浴鹭、出没波间。爱褥草芊绵，更秾柳垂池，翠柏参天。日长人倦，向北窗、欹枕高眠。愁魂绕、沧浪云梦，片时行尽三千。\"]',NULL,NULL,NULL,NULL),('e647d10fa22fc3e39b02ba6dbb578767','菩萨蛮','韩洽','e647d10fa22fc3e3d1457b72d1457b72','明',NULL,'[\"疏帘淡月和烟入，琵琶深夜愁弦涩。莫奏踏罗裳，沙寒闻雁娘。\",\"重衾宵梦午，梦忆莲花浦。归棹带星澜，隔船窥舞鸾。\"]',NULL,NULL,NULL,NULL),('e647d10fa22fc3e3bd4058eed6d1f29a','菩萨蛮·王氏园','韩洽','e647d10fa22fc3e3d1457b72d1457b72','明',NULL,'[\"玉鳞狼藉残梅片，轻禽倒挂垂杨线。春沼绿波融，香吹蝶翅风。\",\"峰峦横小翠，竹径通幽邃。缩取六桥山，移来绣陌间。\"]',NULL,NULL,NULL,NULL),('e647d10fa22fc3e3f7e31f9ac90d3fbc','法曲献仙音·拟周美成','韩洽','e647d10fa22fc3e3d1457b72d1457b72','明',NULL,'[\"迟日笼晴，温飙荐暑，入夏炎光初烁。扇剪轻纨，裙裁白苎，松吹半掀华幕。看槐柳、浓阴满，翛然似岩壑。寥寞。\",\"恨佳游、星分雨散，流年换、憔悴几孤旧约。仿佛绿琴声，记秦楼、欢笑如昨。岁月欺人，惧芳华、先秋摇落。任花开花谢，谁叩草玄亭阁。\"]',NULL,NULL,NULL,NULL),('e647d10fa2a1ccae212685831fdbf77b','菩萨蛮·送春','锁懋坚','e647d10fa2a1ccaed1457b72d1457b72','明',NULL,'[\"晓钟才到春偏度，一番日永伤迟暮。谁送断肠声，黄鹂知客情。\",\"山光青黛湿，仍带伤春泣。绿酒泻杯心，卷帘空抱琴。\"]',NULL,NULL,NULL,NULL),('e647d10fa2afb64ff02a865886086f22','浣溪沙·清明','梁希声','e647d10fa2afb64fd1457b72d1457b72','明',NULL,'[\"满径残花衬履行，暖风吹扬薄罗轻。绿杨拖覆小桥横。\",\"日午绣帘酣睡燕，雨余芳树啭流莺。暮春天气更关情。\"]',NULL,NULL,NULL,NULL),('e647d10fa33c2f74415d41bd0c301c49','谒金门','崔嫣然','e647d10fa33c2f74d1457b72d1457b72','明',NULL,'[\"风萧瑟，远听寒砧声急。应念征鸿无信息，夜凉吹玉笛。\",\"几许红楼岑寂，梦断楚江兰泽。明月梧桐清露滴，暗蛩吟败壁。\"]',NULL,NULL,NULL,NULL),('e647d10fa3640a3a83cfc9c2ff59afe7','竹枝词','浦源','e647d10fa3640a3ad1457b72d1457b72','明',NULL,'[\"红漆车儿驾白羊，吴盐空洒竹枝香。不知羊角如心曲，才听车轮欲断肠。\"]',NULL,NULL,NULL,NULL),('e647d10fa36c047d0c5c2e7da8cee466','念奴娇','郑婉娥','e647d10fa36c047dd1457b72d1457b72','明',NULL,'[\"离离禾黍，叹江山似旧，英雄尘土。石马铜驼荆棘里，阅遍几番寒暑。剑戟灰飞，旌旗鸟散，底处寻楼橹。喑呜叱咤，只今犹说西楚。\",\"憔悴玉帐虞兮，灯前掩泪，双靥流红雨。凤辇羊车行不返，九曲愁肠漫苦。梅瓣凝妆，杨花翻雪，回首成终古。翠螺青黛，绛仙慵画眉妩。\"]',NULL,NULL,NULL,NULL),('e647d10fa3b55301fbe8f989d56ab202','辊绣球','冠皑如','e647d10fa3b55301d1457b72d1457b72','明',NULL,'[\"柳外袅晴丝，正晴日、迟迟停午。墙头曾见，去年携手，花前欸语。\",\"为伊忘却，绣窗鹦鹉。不奈韶光如许。对帘箔、几回凝伫。\",\"绿酒银灯，甚时欢聚。归来燕子休迟到，画桥飞絮。\"]',NULL,NULL,NULL,NULL),('e647d10fa3d02ad1f02891a4092e0ccd','采桑子','单恂','e647d10fa3d02ad1d1457b72d1457b72','明',NULL,'[\"画帘微雨春风暮，最苦今番。罗袖痕斑，留待归时逐点看。\",\"桃花门巷无人到，蹙损蛾弯。手约云鬟，斜倚残红第几阑。\"]',NULL,NULL,NULL,NULL),('e647d10fa3d02ad1f32f182e1ec4ec76','浣溪沙','单恂','e647d10fa3d02ad1d1457b72d1457b72','明',NULL,'[\"豆蔻花红满眼明，小帘贴燕雨如尘。踏青时节又因循。\",\"倦蝶有情随鬓亸，远山无赖学眉颦。冷清清地奈何春。\"]',NULL,NULL,NULL,NULL),('e647d10fa3fc9261e6b7dc14814bc41b','蝶恋花·丙寅寒夜与宛君话君庸作','张倩倩','e647d10fa3fc9261d1457b72d1457b72','明',NULL,'[\"漠漠轻阴笼竹院，细雨无情，泪湿桃花面。落叶西风吹不断，长沟流尽残红片。\",\"千遍相思才夜半，又听楼前，叫过伤心雁。不恨天涯人去远，三生缘薄吹箫伴。\"]',NULL,NULL,NULL,NULL),('e647d10fa4482ebf9ef5ce702ecdae86','醉花阴','焦源溥','e647d10fa4482ebfd1457b72f670ef5d','明',NULL,'[\"红缀繁枝莺语巧，弱柳和烟袅。玉勒慢寻芳，踏遍东皋，满眼青青草。\",\"烟岫重重霞气表，无数山花绕。古寺梵音沉，曲径疏篱，才信郊居好。\"]',NULL,NULL,NULL,NULL),('e647d10fa52d7a42c2ebbe3b218e5d19','鹧鸪天','沈静筠','e647d10fa52d7a42d1457b72d1457b72','明',NULL,'[\"一片春光遍九霄，这回风月也全消。重来绣阁吟残句，不数缑山弄玉箫。\",\"身外事，等闲抛，万层云路碧迢迢。香南雪北何由见，直比人间午梦遥。\"]',NULL,NULL,NULL,NULL),('e647d10fa55af92e506fea9b4e0eff66','鹊桥仙·感悟','董斯张','e647d10fa55af92ed1457b72d1457b72','明',NULL,'[\"轻暖轻寒，不晴不雨，辜负韶光九十。落花飞絮两无情，仗千尺、游丝作合。\",\"涂抹东西，飘零南北，从此扁舟一叶。梦回始觉未归休，恨一点、窥人明月。\"]',NULL,NULL,NULL,NULL),('e647d10fa689489082bd35cf37f51db5','阮郎归·秋雨','杨士聪','e647d10fa689489087ec2f457437d136','明',NULL,'[\"风吹黄叶客心惊，暗云秋雨生。青山飞霭入楼轻，摽摇连暮声。\",\"沾垄润，洒溪清，疏烟藏橘橙。江天千里一樽倾，萧萧白发更。\"]',NULL,NULL,NULL,NULL),('e647d10fa6bc49724111727a02ba91d5','南乡子·闻雁感怀','李因','e647d10fa6bc4972962e56a85d79099f','明',NULL,'[\"嘹呖过南楼，字字横空引起愁。欲作家书何处寄，谁投，目送孤鸿泪暗流。\",\"忆昔共追游，荻岸渔汀系小舟。又是那年时候也，休休，开到黄花知几秋。\"]',NULL,NULL,NULL,NULL),('e647d10fa75fafb776e4cfaf874cbfdf','渔父词','文彭','e647d10fa75fafb7ced556cda1afc58c','明',NULL,'[\"又夜深结网一灯红，吹笛邻舟搅睡浓。云夜月，雨晴风，都在渔家烂醉中。\"]',NULL,NULL,NULL,NULL),('e647d10fa75fafb7ae8c60741f33f9f7','笠泽渔父词四首','文彭','e647d10fa75fafb7ced556cda1afc58c','明',NULL,'[\"吴淞江上是侬家，每到秋来爱荻花。眠未足，日初斜，起坐船头看落霞。\"]',NULL,NULL,NULL,NULL),('e647d10fa75fafb7ae8c60744979ce80','笠泽渔父词四首','文彭','e647d10fa75fafb7ced556cda1afc58c','明',NULL,'[\"无利无名一老翁，笔床茶灶任西东。陆鲁望，米南宫，除却先生便是侬。\"]',NULL,NULL,NULL,NULL),('e647d10fa75fafb7ae8c607450c03e13','笠泽渔父词四首','文彭','e647d10fa75fafb7ced556cda1afc58c','明',NULL,'[\"钓得鲈鱼不卖钱，船头吹火趁新鲜。樽有酒，月将圆，落得今宵一醉眠。\"]',NULL,NULL,NULL,NULL),('e647d10fa75fafb7ae8c60749ccc9c64','笠泽渔父词四首','文彭','e647d10fa75fafb7ced556cda1afc58c','明',NULL,'[\"夜深结网一灯红，吹笛邻舟搅睡浓。雪夜月，雨晴风，都是渔家一醉中。\"]',NULL,NULL,NULL,NULL),('e647d10fa7bbac88f32f182e3ffa5596','浣溪沙','周兰秀','e647d10fa7bbac88d1457b72d1457b72','明',NULL,'[\"雨过池塘万绿生，微风吹满绣帘轻。远山一角夕阳明。\",\"静里荷香来扇底，梦余蕉影倚窗横。关情何处玉箫声。\"]',NULL,NULL,NULL,NULL),('e647d10fa7bc19e30453759f32efaf92','蝶恋花·九月海棠','仁宗皇帝','e647d10fa7bc19e3d1457b72d1457b72','明',NULL,'[\"烟抹霜林秋欲褪。吹破胭脂，犹觉西风嫩。翠袖怯寒愁一寸，谁传庭院黄昏信。\",\"明月羞容生远恨。旋摘余娇，簪满宫人鬓。醉倚小阑花影近，不应先有春风分。\"]',NULL,NULL,NULL,NULL),('e647d10faa481a264f60f0886293d639','西江月·秋泛','吴桢','e647d10faa481a26d1457b72d1457b72','明',NULL,'[\"极目烟波渺渺，停舟新秫家家。橹声咿轧战风鸦，寂寞寒潮西下。\",\"呼我扫愁红友，看残篱畔黄花。人生适意不须他，竹屋蓬窗聊且。\"]',NULL,NULL,NULL,NULL),('e647d10fab00097a6493c3ce3781d46f','水龙吟·写怀','叶绍袁','e647d10fab00097adca5672f7437d136','明',NULL,'[\"寂寥苹渚蘅皋，道书读罢香销篆。松声入坐，药栏红亚，美人蕉钏。极目平芜，天高水远，莲歌游遍。且藤壶汲煮，青衣小袖，点取凤团香片。\",\"自把秋棠手洗，粉墙西、翠梳新钿。开帘风动，画屏琴石，轻阴满扇。挹酒盈尊，摘花侵暝，夜凉迎面。更堪娱、又有椒篇，絮句丽词常见。\"]',NULL,NULL,NULL,NULL),('e647d10fab2b23fad612740752ed69ae','好事近','徐士俊','e647d10fab2b23fad1457b72d1457b72','明',NULL,'[\"剪乱海棠丝，抛却春心不管。杨柳那知人意，惹莺儿啼怨。\",\"脸红眉翠不堪销，拥着半床懒。二十四番花信，数春宵愈短。\"]',NULL,NULL,NULL,NULL),('e647d10fab372b92e58f34a0587f98e3','玉楼春·春日','姚绶','e647d10fab372b920ed94223fa1e9c96','明',NULL,'[\"东风寒悄人何处，百里幽闲犹未遇。看春不觉又清明，槛外梨花开几树。\",\"相思心逐东流去，老天肯把韶光驻。陌头杨柳正青青，莫教容易飞花絮。\"]',NULL,NULL,NULL,NULL),('e647d10fab9e6c60eeb04e9d7c5bc65d','唐多令','王称','e647d10fab9e6c60d1ee59e2a60937eb','明',NULL,'[\"湖水接天流，君山一点浮。倚丹青、画出层楼。欲吊湘君何处也，烟草色满汀洲。\",\"把酒记重游。相看渐白头。赋青蘋、谁更悲秋。却恋晚峰云外笛，沧海意付扁舟。\"]',NULL,NULL,NULL,NULL),('e647d10fac4d1b3f8ace97838860e367','鹤冲天·题莼魰小像和葆馚原韵','钱澄之','e647d10fac4d1b3f52947e0ab59a51a3','明',NULL,'[\"云沙月渚，梦里曾游处。春草夜来生，催佳句。为金门待诏，总交付，渔家住。赖诗朋酒侣，唱和新声，谱入舞巾歌苎。\",\"扁舟晓暮，难趁秋风去。觅取故乡鲈，和烟煮。又飞觞刻烛，且同听、燕山雨。吾衰心未许，扶醉追欢，不减旧年情绪。\"]',NULL,NULL,NULL,NULL),('e647d10fac5df9910d05ccff3ce01513','风入松·漫兴','戈止','e647d10fac5df991d1457b72d1457b72','明',NULL,'[\"生涯应作浣花翁，事事疏慵。结庐恰喜尘嚣外，栽花地、雨碧霜红。家在乱云深处，人来流水声中。\",\"年年相识旧东风，排我小帘栊。榆钱落尽萍钱落，奚囊在、不畏诗穷。夜泛试移短棹，晓眠常过高舂。\"]',NULL,NULL,NULL,NULL),('e647d10fac644253c28abc409baee9af','桂枝香寄友村居','张逸','e647d10fac644253d1457b72d1457b72','明',NULL,'[\"天高气肃，正一派秋声，悲吟万木。潇洒远山抹翠，澄溪漾玉。故人家住残阳外，小枫村、低低茅屋。烟生芦渚，霜沾菊圃，酒香茶熟。\",\"疏篱畔、山中野服，想竹栏琴韵，松窗棋局。四壁清幽，闲挂云林几幅。鲤鱼风起天横雁，待一叶寻他剡曲。开樽长啸，池边蟹紫，墙头橘绿。\"]',NULL,NULL,NULL,NULL),('e647d10fac874f2825eb6af7efb533ee','清平乐','谢小眉','e647d10fac874f28d1457b72d1457b72','明',NULL,'[\"良辰初到，春色随时闹。妆点蛾眉因甚早，人在咸阳古道。\",\"当年泣别长亭，今朝嘶马萦情。寂寞黄昏时候，琐窗夜夜风生。\"]',NULL,NULL,NULL,NULL),('e647d10facd352e886b912025edd7524','虞美人','韩曾驹','e647d10facd352e8d1457b72d1457b72','明',NULL,'[\"落花不为随春去，别有闲情绪。寻花偶过野人家，偏是菜花开尽见桃花。\",\"无情每恨良宵月，圆少翻多缺。上弦数到下弦时，问有几多明月上花枝。\"]',NULL,NULL,NULL,NULL),('e647d10fae19763c2a2b5d43b1b6e07e','菩萨蛮·连城山房','张娴倩','e647d10fae19763cd1457b72d1457b72','明',NULL,'[\"风卷落红愁不歇。枝头燕剪裁桃叶。花气沁帘香。游丝挂绿窗。\",\"蕉青鸾翅影。草碧龙须冷。无语对瑶琴。闲花落胆瓶。\"]',NULL,NULL,NULL,NULL),('e647d10fae19763c679e90d81e012160','忆秦娥·听杜鹃','张娴倩','e647d10fae19763cd1457b72d1457b72','明',NULL,'[\"春去矣。蘼芜绿遍苔痕紫。苔痕紫。暗叶啼风，老红泣雨。\",\"燕解人愁已不语。杜鹃犹在寒烟里。寒烟里。道不如归，何时归去。\"]',NULL,NULL,NULL,NULL),('e647d10fae19763c71c76fad23f3e6f3','忆秦娥·秋夜听雨','张娴倩','e647d10fae19763cd1457b72d1457b72','明',NULL,'[\"寒雨涩。寒风风响寒檐铁。寒檐铁。叮叮点点，萧萧瑟瑟。\",\"寒蛩趯趯吟寒叶。寒衾寂寂寒灯灭。寒灯灭。嘈嘈唧唧，凄凄切切。\"]',NULL,NULL,NULL,NULL),('e647d10fae19763c8be25d4d9ab48796','菩萨蛮·问海棠','张娴倩','e647d10fae19763cd1457b72d1457b72','明',NULL,'[\"海棠昼睡胭脂冷。杜宇枝头呼不醒。想是怯东风。沉沉春梦中。\",\"闲来花槛外。漫把花愁解。切莫更朦胧。轻将锦被笼。\"]',NULL,NULL,NULL,NULL),('e647d10faeb0e2debc4c63513dbc654f','满庭芳·送友人还会稽','黄周星','e647d10faeb0e2de7a6a74cb11d0e628','明',NULL,'[\"新绿方浓，残红尽落，多情正自凝眸。不堪南浦，又复送归舟。便倩江郎作赋，也难写、别恨离愁。消魂久，斜阳芳草，天际水悠悠。\",\"问君何处去，若耶溪畔，宛委山头。有千岩竞秀，万壑争流。愧我江湖迹遍，到如今、仍坐书囚。迟君至，开襟散发，咏月醉南楼。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde211c8c4a','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"白盐生井火生畬，女子行商男作家。撞布红衫来换米，满头都插杜鹃花。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde263b1072','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"绿酒娟娟白玉瓶，酴絪花发语猩猩。《竹枝》歌断人无那，十二峰头暮雨青。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde27e1d0ab','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"月出江头半掩门，待郎不至又黄昏。夜深忽听巴渝曲，起剔残灯酒尚温。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde28b77398','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"兰桡小小叶为船，荡漾红妆下锦川。泪落清波君不见，秋风吹老并头莲。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde444b2e27','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"阴阴桃李晚妆迟，万里桥头叫子规。拜月归来人语静，金钗失在竹廊西。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde511bd0da','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"白帝城高秋月明，黄牛滩急暮潮生。送君万水千山去，独自听猿到五更。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde7c162f82','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"避人低语卜金钱，侵晓焚香拜佛前。见说嘉陵江水恶，莫教风浪打郎船。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde8b48fd3b','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"生年十五棹能开，那怕瞿唐滟滪堆。郎今晒网桃花渡，奴把鲜鱼换酒来。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde918b7e2a','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"青桑老尽茜花开，新妇看蚕婆不来。织得西川宫样锦，机头先与小姑裁。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302ddde9eefbe74','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"点点流萤送落花，春风寂寞断琵琶。人来寄与菖蒲叶，说是成都造纸家。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302dddebdd2bb66','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"杨柳青青酒店门，阿郎吹火妾开樽。千金卖得文章去，不记当时犊鼻裈。\"]',NULL,NULL,NULL,NULL),('e647d10faf7441491302dddec13e0c7b','竹枝词十二首','王叔承','e647d10faf744149d1457b72d1457b72','明',NULL,'[\"一从荡子客长干，怕说风波五百滩。忽接家书心暗喜，更于封外写平安。\"]',NULL,NULL,NULL,NULL),('e647d10fb0e77f1ef32f182e501003e6','浣溪沙','钱继登','e647d10fb0e77f1ed1457b72d1457b72','明',NULL,'[\"蝉避浓炎静不哗，东怜伊轧缲丝车。豆棚瓜架野人家。\",\"翠荚嫩堪浮茗气，黄鸡肥欲待姜芽。闲搔短发日西斜。\"]',NULL,NULL,NULL,NULL),('e647d10fb123f78ff8d0b4ba4f722829','沁园春·戏策毛颖为学士效稼轩体','储懋端','e647d10fb123f78fd1457b72d1457b72','明',NULL,'[\"咨汝毛锥，誉擅中书，胡思乞骸。念望崇灵兔，南山感月，名高绣虎，北海惊雷。久效驰驱，积成劳悴，畴昔锋芒逐渐摧。嘉乃绩，俾守中山郡，其往钦哉。\",\"吾将以礼为媒，更别选循良颖也才。令蜀江裁楮，先生静待，淇园截竹，君子蒙灾。合就管城，移封即墨，锡汝龙宾水一杯。兼策汝，试端明学士，速驾前来。\"]',NULL,NULL,NULL,NULL),('e647d10fb16562a2436a3bf9defab009','点绛唇·光泽寺','王慎中','e647d10fb16562a21cd38823020c8bfa','明',NULL,'[\"门掩青山，空庭竹影门长扫。一溪斜绕，水气香花草。\",\"木石幽殊，禽鸟传昏晓。谁知道，白云锁了，恰有人寻到。\"]',NULL,NULL,NULL,NULL),('e647d10fb19ded6c102097c00d4b6810','临江仙·春尽','徐尔铉','e647d10fb19ded6cd1457b72d1457b72','明',NULL,'[\"春光不奈人烦恼，夜来也动归心。轻纱敲雨湿窗明，风多花有恨，林暗蝶无情。\",\"竹杖闲携随径去，斜阳一派松声。茶旗酒阵破愁城，借云邀片月，倩鸟唤新晴。\"]',NULL,NULL,NULL,NULL),('e647d10fb1c2a2969c70a6d8dca49556','踏莎行','无名氏','e647d10fb1c2a296d41d8cd9d41d8cd9','明',NULL,'[\"佳约偏乖，韶华难驻，柳丝点点江头树。朝来为甚不钩帘，残花飞满帘前路。\",\"春赏才阑，春归何遽，问春归向何方去。有情燕子不同归，呢喃独伴春愁住。\"]',NULL,NULL,NULL,NULL),('e647d10fb1c2a296be7c41fff05b9718','柳梢青·咏萤','无名氏','e647d10fb1c2a296d41d8cd9d41d8cd9','明',NULL,'[\"碧焰疏篁，斜飞秋水，轻点莲香。忽影风檐，半明苔壁，又入绳床。\",\"夜深露冷清光，度残月、乱点苍茫。罗幌眠云，松窗幽磬，心地空凉。\"]',NULL,NULL,NULL,NULL),('e647d10fb1c2a296c1994b5e663189dc','秋波媚','无名氏','e647d10fb1c2a296d41d8cd9d41d8cd9','明',NULL,'[\"垂杨袅袅映回汀，作态为谁青；可怜弱絮，随风来去，似我飘零。\",\"蒙蒙乱点罗衣袂，相送过长亭；叮咛嘱汝，沾泥也好，莫化浮萍。\"]',NULL,NULL,NULL,NULL),('e647d10fb1f3fa5779bb001364ac9b1a','江城子','徐之垣','e647d10fb1f3fa57d1457b72d1457b72','明',NULL,'[\"梨花着雨晚来晴，月胧明，泪纵横。绣阁香浓，深锁凤箫声。未必人知春意思，还独自，绕花行。\",\"酒兵昨夜压愁城，笑狂生，枉心惊。浇尽胸中，块磊未曾平。却与评量珠玉价，拚醉里，锦囊倾。\"]',NULL,NULL,NULL,NULL),('e647d10fb1f91feed2917020359f57fc','踏莎行·春寒','万日吉','e647d10fb1f91feed1457b72d1457b72','明',NULL,'[\"芍药香凝，海棠丝袅，东风吹透相思草。绿纱深处自调筝，一弦一柱寒声早。\",\"苔满阶庭，月留昏晓，萋迷碧砌残红少。画梁燕子未归来，黄昏春锁花关小。\"]',NULL,NULL,NULL,NULL),('e647d10fb22ce8619102429d1535ec1d','竹枝词二首','刘溥','e647d10fb22ce861d1457b72d1457b72','明',NULL,'[\"微风细雨初至，碧水遥天未分。借问黄陵远近，江心一片春阴。\"]',NULL,NULL,NULL,NULL),('e647d10fb22ce8619102429d563da984','竹枝词二首','刘溥','e647d10fb22ce861d1457b72d1457b72','明',NULL,'[\"江心濈濈秋影，烟外亭亭绿痕。帝子祠前别思，鹧鸪声里黄昏。\"]',NULL,NULL,NULL,NULL),('e647d10fb26958bc9c70a6d8270a2038','踏莎行','张嘉','e647d10fb26958bcd1457b72d1457b72','明',NULL,'[\"绿锁朱阑，红翻碧沚，韶光蓦地清明矣。愁多好梦已难寻，那堪又被莺呼起。\",\"妆懒临鸾，期愆乘鲤，当年盟誓都虚耳。有情燕子解怜香，衔将落瓣营新垒。\"]',NULL,NULL,NULL,NULL),('e647d10fb34b465340d5e8bec1576f42','临江仙·村居','吴熙','e647d10fb34b4653d1457b72d1457b72','明',NULL,'[\"醉里衡门聊自适，短垣松竹萧森。日斜罗幕掩清阴。心孤闻远磬，愁重理瑶琴。\",\"几点落英寒著地，行来寂寞空林。有人闲坐爱微吟。平桥新涨水，一径野云深。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a0011cbf2f2f7e00413','感皇恩·送陈大参德修升云南右方伯','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"金榜早蜚英誉，两京扬历剧。郡分符声赫奕。超佐薇垣，茂着旬宣嘉绩。黎元沾惠爱，咸欢怿。\",\"入觐王庭，遂迁岳牧。万里滇南烟树碧。西江父老，空想甘棠行迹。还期霖雨，遍沛寰域。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a00252dead44bb7ca99','归朝欢·送张天瑞都宪巡抚北畿','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"三伏炎曦真烁石。使君何事行匆剧。王畿分镇简贤能，君命有严遑自适。西江人叹息。福星移照天之北。旧同寅，暌违矩范，翘首情何极。\",\"使君早蕴匡时策。中外贤劳名赫奕。此行绣斧带霜威，当道豺狼看敛迹。九重方侧席。寤寐求贤敷德泽。看他年，进秉钧轴，霖雨苏寰域。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a002933e2bac78b25d0','归朝欢·送都宪秦公巡抚北畿','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"使君才器由天赋。骅骝早聘青云路。京华扬历政声隆，藩郡翱翔仁泽溥。九重深眷顾。玉节煌煌明绣斧。到京畿，扬清激浊，千里民安祚。\",\"东风吹散西山雨。冠盖纷纷朝出祖。黎元深感使君恩，车前卧辙人无数。圣主思贤辅。姓名定入金瓯覆。怅明朝，相思两地，空望云间树。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a006aa3f9f99c83c6e0','水龙吟·送方伯陈公大玉之任闽藩','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"使君抱负不凡，少年文采称无敌。云霄直上，龙墀夺锦，金闺通籍。计省发硎，政无疵厉，民沾惠泽。喜受知圣主，擢参薇省，经行处、皆春色。\",\"天上忽颁宠命。又荣迁、闽南牧伯。九重宵旰，恢弘治化，还资辅弼。棠树阴中，旧时父老，空思行迹。况同寅交契，江云渭树，此情何极。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a0072a0445f5d3bdb29','玉烛新送沈大参朝贺之京','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"诗礼传家旧。喜早骋骅骝，康庄驰骤。十年纷署，声华着、简拔旬宣陇右。\",\"才转闽藩，旋复承宠恩隆厚。等闲间、金带生花，海国歌諠清昼。\",\"兹逢圣主龙飞，正玉烛新调，群生咸囿。君函笺奏。\",\"朝丹陛、带得天香盈袖。华封祝后。须念及苍生颙候。\",\"亟旋旆、收取功名，鹓班重簉。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a0093955eaed3471646','感皇恩·送李方伯之任山西','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"采石古名区，秀钟英杰。早际风云朝北阙。谏垣名重，超佐西江节钺。襟怀清彻底，如冰雪。\",\"政迹着闻，帝心简在。又擢晋阳新牧伯。轺车到处，枯槁渐回春色。故人千里，空望明月。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a00a4aee46e3155790b','忆旧游·送宪副汪从仁之任云南词并引','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"喜清和时节，梅酿微酸，榴吐新葩。祖席酒三行，把离情搅动，乱似杨花。忆着英年分陕，美誉赤城霞。看白璧坚贞，青蝇虽点，本自无瑕。\",\"悯起佐西江，臬㥃吏弊民情，一一梳爬。九重思俊杰，抚滇南赤子，帝曰汝嘉。旧日夷民识面，重见喜无涯。待边境又宁，归朝还拟殿邦家。\"]',NULL,NULL,NULL,NULL),('e647d10fb3832a00eb9ec8b34f333473','感皇恩·送同寅罗象州迁云南宪副','黄仲昭','e647d10fb3832a001f3202d8ebd6d2f5','明',NULL,'[\"南浦祖筵开，骊歌将阕。彩鹢乘风江上发。便道乡关，山水应增秀色。两地相思处，共明月。\",\"金紫辉煌，青骢蹀躞。万里风云随玉节。冰霜满面，散作阳春德泽。他年金鼎，还资调燮。\"]',NULL,NULL,NULL,NULL),('e647d10fb386030f1368c9148ca4afcc','临江仙','方于鲁','e647d10fb386030fd1457b72d1457b72','明',NULL,'[\"曹载信来访山中，偕饮溪上酒家小市桥横堪系马，双溪水暖浮鸥。青帘风亚酒垆头。垂杨当户牖，芳草带烟流。\",\"千里故人鸡黍约，百钱挂杖登楼。竹林把臂及春游。休嗟逆旅客，且作醉乡侯。\"]',NULL,NULL,NULL,NULL),('e647d10fb42bd2f897b5b8da74a5f7ff','满庭芳','钱氏（唐世济室）','e647d10fb42bd2f8d1457b72d1457b72','明',NULL,'[\"一片松阴，半庭梧叶，纵横占满空阶。授衣时节，砧响隔墙来。渐老懒亲刀尺，褰帘望、秋色佳哉。真难料，千馀里外，来看木樨开。\",\"叹阎浮狭小，斗争坚固，利嫁名媒。怕胸中块垒，辜负新醅。但使逍遥鹏鴳，知何事、荏苒萦怀。高楼晚，孤鸿别浦，乡思忽相催。\"]',NULL,NULL,NULL,NULL),('e647d10fb42bd2f897b5b8da97c305d0','满庭芳','钱氏（唐世济室）','e647d10fb42bd2f8d1457b72d1457b72','明',NULL,'[\"古树阴浓，新篁翠浅，乍阴过雨还晴。方塘对岸，留得小红英。消受茶芽蕨笋，髹几净、云母窗明。薰风至，钓丝摇曳，无处立蜻蜓。\",\"喜灌园君志，辟纑吾职，两意相并。把当前，好景直拟蓬瀛。怎便尘劳尽谢，长无事、心境双清。家园好，藕花如锦，波面想盈盈。\"]',NULL,NULL,NULL,NULL),('e647d10fb443f4d1fc56a781c2950fb1','贺新郎','费元禄','e647d10fb443f4d1d1457b72d1457b72','明',NULL,'[\"瑞脑烧金鼎，卷重帘、庭阴正午，画堂人静。春落天涯知何处，糁地杨花飞尽。印玉枕、流莺催醒。梅雨纱窗寒欲透，更长年、三月厌厌病。愁不语，懒窥镜。\",\"江南旧事须重整，隔花阴、传消寄息，月娥难倩。梦断秦楼相思久，芳草佳期未准。倚遍了、梧桐金井。蝴蝶已归煤麝冷，枉教人、揉碎丁香影。谁共我，步香径。\"]',NULL,NULL,NULL,NULL),('e647d10fb4b51b72892c2924372fed69','山花子·夏夜','周永年','e647d10fb4b51b72b71091578d420fa3','明',NULL,'[\"低髻拢花梦半醒，重携枕簟向闲庭。何处玉箫风送远，倚阑听。\",\"趁着萤光看络纬，卷开珠网放蜻蜓。不是夜长愁不寐，候双星。\"]',NULL,NULL,NULL,NULL),('e647d10fb4bbaf3c574e3d03fa234077','人月圆','龙辅','e647d10fb4bbaf3cd1457b72d1457b72','明',NULL,'[\"凭栏却讶春归早，辜负养花天。青黏天外，绿啼雨后，红褪风前。\",\"墙围蕉叶，径抽粉箨，窗簸榆钱。懒寻双陆，冷抛蹴鞠，闲挂秋千。\"]',NULL,NULL,NULL,NULL),('e647d10fb4bbaf3c6d5b28c58004ccee','阮郎归·署斋晚眺','龙辅','e647d10fb4bbaf3cd1457b72d1457b72','明',NULL,'[\"幽禽避雨噪虚檐。风掀半卷帘。落花如梦草微酣。远山柔绿黏。\",\"棋懒赌，韵慵拈。博山金灺添。小鬟偷粉启香奁。学人眉晕尖。\"]',NULL,NULL,NULL,NULL),('e647d10fb4bbaf3cc06ac9f2e6e4dd49','南歌子','龙辅','e647d10fb4bbaf3cd1457b72d1457b72','明',NULL,'[\"绿沁琉璃带，红摇玳瑁钗。下阶无语立苍苔。昨夜银塘雨洗、露桃开。\",\"回脸遮星靥，偎人泥粉腮。真珠帘卷有谁来。两两旧巢宫燕、掠泥回。\"]',NULL,NULL,NULL,NULL),('e647d10fb4bbaf3cd5cf24b11042f189','相见欢','龙辅','e647d10fb4bbaf3cd1457b72d1457b72','明',NULL,'[\"小鬟衫着轻罗。发如螺。睡起钗偏髻倒、唤娘梳。\",\"心上事，春前景，闷中过。打叠闲情别绪、教鹦哥。\"]',NULL,NULL,'此词着意描写人物情态。小鬟睡起，钗偏髻倒，娇憨之态可掬。神情逼真，如在眼前。全词委婉含蓄而又新巧自然。 ','[\"螺：螺髻，古代女子的发式。\",\"打叠：收拾，安排。 \"]'),('e647d10fb4bbaf3cd5cf24b1d1553434','相见欢','龙辅','e647d10fb4bbaf3cd1457b72d1457b72','明',NULL,'[\"雏鹂啄下红樱。曲栏晴。笑取泥金小扇、扑蜻蜓。\",\"牵得住，推不去，是春情。多少柔肠嘱咐、护花铃。\"]',NULL,NULL,NULL,NULL),('e647d10fb4bbaf3cf137049b1ce0d84c','阮郎归','龙辅','e647d10fb4bbaf3cd1457b72d1457b72','明',NULL,'[\"东风吹送隔溪箫。柳丝红板桥。淡云推月上花稍。桥门树影摇。\",\"凭碧槛，待春潮。圆沙小浪高。船头漾水弄长篙。心惊猧犬嗥。\"]',NULL,NULL,NULL,NULL),('e647d10fb4d6a9c93d5dc132966fa95d','卖花声','陈冉','e647d10fb4d6a9c9d1457b72d1457b72','明',NULL,'[\"愁压远山低，此恨谁知，吴绫浅碧湿胭脂。无限秋光憔悴意，残叶疏枝。\",\"多病损腰肢，檀袖双垂，纱窗月上影迟迟。漏冷莲花人静也，寒雁来时。\"]',NULL,NULL,NULL,NULL),('e647d10fb512647ec07d4bfe05eaf32c','一剪梅·红满苔阶绿满枝','唐寅','e647d10fb512647e42ffcf05df12ecd0','明',NULL,'[\"红满苔阶绿满枝，杜宇声声，杜宇声悲！交欢未久又分离，彩凤孤飞，彩凤孤栖。\",\"别后相思是几时？后会难知？后会难期？此情何以表相思？一首情词，一首情诗。\"]',NULL,NULL,NULL,NULL),('e647d10fb512647edae8a486ce3c7110','一剪梅·雨打梨花深闭门','唐寅','e647d10fb512647e42ffcf05df12ecd0','明','「闺怨」之作在历代词人笔下堪称汗牛充栋，愈是习见的题材愈难出新意，从而所贵也尤在能别具心裁。唐寅这阕《一剪梅》的佳处不只在于词句之清圆流转，其于自然明畅的吟哦中所表现的空间阻隔灼痛着痴恋女子的幽婉心态更是动人。空间，既无情地拉开着恋者的距离，而空间的阻隔又必然在一次次「雨打梨花」、春来春去中加重其往昔曾经有过的「赏心乐事」的失落感；至若青春年华也就无可挽回地在花前月下神伤徘徊之间被残酷地空耗去。时间在空间中流逝，空间的凝滞、间距的未能缩却花开花凋落，人生便在等待中渐渐消逝。活过之物终将凋零，只可在行也思君坐也思君中，愁聚眉峰尽日颦。上片的「花下销魂，月下销魂」，是无处不令「我」回思往时的温馨；下片的「行也思君，坐也思君」则写尽朝暮之间无时不在翘首企盼所恋者的归来，重续欢情。唐寅轻捷地抒述了一种被时空折磨的痛苦，上下片交叉互补、回环往复，将一个泪痕难拭的痴心女形象灵动地显现于笔端，诚无愧其「才子」之誉称。','[\"雨打梨花深闭门，忘了青春，误了青春。赏心乐事共谁论？花下销魂，月下销魂。\",\"愁聚眉峰尽日颦，千点啼痕，万点啼痕。晓看天色暮看云，行也思君，坐也思君。\"]',NULL,NULL,'此词是明代词人唐寅的作品。这首词的佳处不只在于词句之清圆流转，其于自然明畅的吟哦中所表现的空间阻隔灼痛着痴恋女子的幽婉心态更是动人。唐寅轻捷地抒述了一种被时空折磨的痛苦，上下篇交叉互补、回环往复，将一个泪痕难拭的痴心女形象灵动地显现于笔端。','[\"一剪梅：此词牌以周邦彦所作起句有「一剪梅花万样娇」句，故取为调名。又名《玉簟秋》、《腊梅香》。重头六十字，平韵。\",\"「雨打梨花深闭门」句：用宋·李重元《忆王孙·春词》结末成句。\",\"忘了：一作「孤负」。\",\"误了：一作「虚负」。\",\"销魂：黯然神伤。\",\"「愁聚眉峰尽日颦」句：整日眉头皱蹙如黛峰耸起。颦，皱眉。\",\"「晓看天色暮看云」句：两个「看」字实係无意义举止，乃特定心态的外现行为。\"]'),('e647d10fb767e4c0c5823def024941c5','满庭芳·书虫','冯惟敏','e647d10fb767e4c0ebb71045bcb41ccd','明',NULL,'[\"蚕鱼虽小，咬文嚼字，有甚才学。绵缠纸里书中耗，占定窝巢。\",\"俺看他一生怕了，你钻他何日开交。听吾道：轻身儿快跑，捻着你命难饶！\"]',NULL,NULL,NULL,NULL),('e647d10fb7922456e21730dc3f728570','破阵子','王月','e647d10fb7922456d1457b72d1457b72','明',NULL,'[\"宫粉香消睡鸭，仙衣尘满红绒。寂寞画屏春月白，冷淡花裀夜烛红。\",\"谁家玉笛风。\"]',NULL,NULL,NULL,NULL),('e647d10fb7c3f97ff0c4be29a7f26771','满江红·和王昭仪','吴本泰','e647d10fb7c3f97fd1457b72d1457b72','明',NULL,'[\"白雁南飞，摇落尽、汉宫秋色。笳吹起、霓裳声断，绛河仙阙。翡翠巢空金殿里，鸳鸯瓦碎瑶台侧。忆春风、拂槛露华浓，都销歇。\",\"尘黯淡，灯明灭；蛩语似，支离说。怨琵琶空抱，杜鹃啼血。戍柝惊回鸡帐梦，玉容羞照龙沙月。莫悲伤、环子系罗衣，君恩缺。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d1ac894622c690091','杨柳枝·词四首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"千载姑苏送客时。金昌亭畔柳如丝。于今一树无人种，怕与春风管别离。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d1ac89462b39bdba3','杨柳枝·词四首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"白马金羁人未回。翠条仍自挂妆台。黄鹂莫作藏身处，一入丝中遣不开。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d1ac89462cf8cbfe4','杨柳枝·词四首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"去岁今时别泪飘。夫君留向玉关遥。春风似为相思妾，吹动天涯万柳条。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d1ac89462e8533c72','杨柳枝·词四首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"巫峡巫山锦树春。垂杨垂柳著行新。东风不是贪飞絮，为觅天涯未见人。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d3c17c04b1316c945','采莲曲三首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"团扇遥将明月裁。莲舟掩映一花开。风前羞杀芙蓉色，莫是阳云感梦来。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d3c17c04b1e30bc70','采莲曲三首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"缃裙新借彩云裁。正值芙蓉玉沼开。不为名花同妾色，画船宁泛月明来。\"]',NULL,NULL,NULL,NULL),('e647d10fb7e8548d3c17c04b4ecaa039','采莲曲三首','黄省曾','e647d10fb7e8548df187a23ccda72177','明',NULL,'[\"怀人朝把紫笺裁。暂向荷花锁黛开。正是妾心愁剧处，鸳鸯两两莫教来。\"]',NULL,NULL,NULL,NULL),('e647d10fb86bc4b5a752ef3d16ba4fcc','如梦令·春暮别张允升','吴宗儒','e647d10fb86bc4b5d1457b72d1457b72','明',NULL,'[\"花落春归时节，满目杨花飞雪。杯酒几多情，愁对东风话别。\",\"声咽，声咽，去后相思啼癊。\"]',NULL,NULL,NULL,NULL),('e647d10fb93bfc15a2a27eabc1ee2c6c','浦口竹枝','袁凯','e647d10fb93bfc15d1457b72d1457b72','明',NULL,'[\"浦上荷花生紫烟，吴姬酒肆近人船。更将荷叶包鱼蟹，老死江南不怨天。\"]',NULL,NULL,NULL,NULL),('e647d10fb95eb3d7c6a6616f10263e38','金菊对芙蓉书壁','刘侗','e647d10fb95eb3d7d1457b72d1457b72','明',NULL,'[\"华紫移枫，荷香在桂，又成一种风光。正轻寒气候，好整衣裳。几时送却堂前燕，计云程、故国烟茫。游人何事，独胜秋色，犹殢他乡。\",\"空有回文锦句，尽听残白露，挨过重阳。任青楼赏遍，酒永歌长。千里平安慵启帙，都忘却、家在湖湘。芦蓼池塘。芙蓉亭沼，橘柚垣墙。\"]',NULL,NULL,NULL,NULL),('e647d10fb988819e90bdbd828fb80476','满江红','建文皇帝','e647d10fb988819ed1457b72d1457b72','明',NULL,'[\"三过吴江，又添得、一亭清绝。刚占断、水光多处，巧依林樾。漠漠云烟春昼雨，寥寥天地秋宵月。更冰壶、玉鉴暑宜风，寒宜雪。\",\"臞庵右，山岚缺；虹桥左，波涛截。正三高堂畔，旧规今别。何但渔翁垂钓好，谩将柳子新吟揭。信登临、佳兴属彭宣，能挥发。\"]',NULL,NULL,NULL,NULL),('e647d10fb9fbe1aa16fa14bb368daa78','临江仙·秋日','黄媛介','e647d10fb9fbe1aad1457b72d1457b72','明',NULL,'[\"庭竹萧萧常对影，卷帘幽草初分。罗衣香褪懒重熏。有愁憎语燕，无事数归云。\",\"秋雨欲来风未起，芭蕉深掩重门。海棠无语伴销魂。碧山生远梦，新水涨平村。\"]',NULL,NULL,NULL,NULL),('e647d10fb9fbe1aa20a8f0129db526f1','长相思·春暮','黄媛介','e647d10fb9fbe1aad1457b72d1457b72','明',NULL,'[\"风满楼。雨满楼。风雨年年无了休。馀香冷似秋。\",\"卖花声。卖花舟。万紫千红总是愁。春流难断头。\"]',NULL,NULL,NULL,NULL),('e647d10fb9fbe1aa43737804d10d87ec','菩萨蛮·秋思见《林下词选》、蒋重光《昭代词选》','黄媛介','e647d10fb9fbe1aad1457b72d1457b72','明',NULL,'[\"芙蓉花发藏香露。白云惨淡关山路。愁思惹秋衣。满庭黄叶飞。\",\"绣阁帘初卷。梦与离人远。秋雨又如烟。魂销似去年。\"]',NULL,NULL,NULL,NULL),('e647d10fb9fbe1aa4fdf9bc83024934c','长相思·春日饮闲靓','黄媛介','e647d10fb9fbe1aad1457b72d1457b72','明',NULL,'[\"花满栏。叶满栏。人醉言归情未阑。都来相见难。\",\"道如兰。气如兰。随在云山日一竿。离心江上看。\"]',NULL,NULL,NULL,NULL),('e647d10fb9fbe1aad2e9aa8634c4e2ba','金菊对芙蓉答宗姊月辉见怀之作','黄媛介','e647d10fb9fbe1aad1457b72d1457b72','明',NULL,'[\"五易星霜，两迁村塾，思君几许魂消。看燕来雁去，梦断音遥。兵戈路绝空相念，惟虚却、月夕花朝。还家一载，城隅轻隔，似阻江潮。\",\"感伊投我琼瑶。羡珠光溜彩，玉韵含韶。恨未能携手，愁寄纤毫。君家梅竹犹堪赏，待相逢、斗酒重浇。春光未老，花香正美，离思空劳。\"]',NULL,NULL,NULL,NULL),('e647d10fba2ed2b92ba4f6ee484c5f83','公无渡河','詹同','e647d10fba2ed2b9d1457b72d1457b72','明',NULL,'[\"江上春来风雨恶，大浪小浪江中作。一苇之航何足云，荡漾中流几飘泊。\",\"人鲊瓮，鬼门关，下隔深渊咫尺间。饥蛟食人骨如山，公无渡河当早还。\"]',NULL,NULL,NULL,NULL),('e647d10fba34dedd85fa6dc2ba29ebc3','蝶恋花·夏日','王九思','e647d10fba34deddcec6f62c4e6cd952','明',NULL,'[\"门外长槐窗外竹，槐竹阴森，绕屋重重绿。人在绿阴深处宿，午风枕簟凉如沐。\",\"树底辘轳声断续，短梦惊回，石鼎茶方熟。笑对碧山歌一曲，红尘不到人间屋。\"]',NULL,NULL,NULL,NULL),('e647d10fba8f2352dd420519d466ebbc','归自谣','张天湜','e647d10fba8f2352d1457b72d1457b72','明',NULL,'[\"更漏歇，一夜珠帘风自揭。寒鸦影碎梧桐月。\",\"玉关征戍音尘绝。当年别，江南莺语花时节。\"]',NULL,NULL,NULL,NULL),('e647d10fbaf56b130a9ba7be802f9dce','酷相思','支如玉','e647d10fbaf56b13d1457b72d1457b72','明',NULL,'[\"新柳月痕初坠，恨春鸟、催人起。想盟言、花片随波逝，欲住也、留无计，欲去也、留无计。\",\"印旧添新衣上泪，左右拚憔悴。问九峰、桃李曾开未？\",\"人远也，书难寄，春尽也，愁难寄。\"]',NULL,NULL,NULL,NULL),('e647d10fbb36936552c513145be99a81','浣溪沙·春闺','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"淡薄轻阴拾翠天。细腰柔似柳飞绵。吹箫闲向画屏前。\",\"诗句半缘芳草断，鸟啼多为杏花残。夜寒红露湿秋千。\"]',NULL,NULL,NULL,NULL),('e647d10fbb3693655a6f2247476299af','江城子·西风自古不禁愁','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"西风自古不禁愁，奈穷秋，思悠悠。何似长江，滚滚只东流。霁景萧疏催晚色，新月影，挂帘钩。\",\"芙蓉寂寞水痕收，淡烟浮，冷芳洲。断霭残云，犹自倚重楼。纵有茱萸堪插鬓，须不是，少年头。\"]',NULL,NULL,NULL,NULL),('e647d10fbb3693657630e3cfb1fc955f','玉蝴蝶·思張倩倩表妹','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"蓦地流光惊换，画阑一带，烟柳初齐。乍暖轻寒，庭院尽日帘垂。送愁来、数声啼鸟，牵梦去、几树游丝。忆当年，情含宝帐，未解春思。\",\"堪悲。盈盈极目，几多江水，隔若天涯。恨结丁香，也应还、自怪香綦。漫思量、花前旧约；空怊怅、虚负芳期。又谁知，夜窗魂断，晓镜低眉。\"]',NULL,NULL,NULL,NULL),('e647d10fbb369365a714a12a0e188954','凤凰台上忆吹箫·代人恨别','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"好事多磨，重云掩月，晓风惊梦难留。又一番春色，恼乱枝头。\",\"湘浦佩沉波冷，花影里、枉自凝眸。添怊怅，燕来鸿去，锁尽闲愁。\",\"休休。楚台已远，生惹得情深，何处忘忧。叹幽怀几许，总付东流。\",\"那更栏前芳草，萦人思、恨满银钩。空回首，从今风雨，两地悠悠。\"]',NULL,NULL,NULL,NULL),('e647d10fbb369365d106fded14893097','蝶恋花·感怀','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"犹见寒梅枝上小。昨夜东风，又向庭前绕。梦破纱窗啼曙鸟，无端不断闲烦恼。\",\"却恨疏帘帘外渺。愁里光阴，脉脉谁知道？心绪一砧空自捣，沿阶依旧生芳草。\"]',NULL,NULL,NULL,NULL),('e647d10fbb369365d14d764cea3303dd','蝶恋花·小婢寻香，婀娜有致，楚楚如秋棠，可怜年十二而死，怆然哀之赋此','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"巫女腰肢天与慧。浅发盈盈，碧嫩红阑蕙。满地莺声花落碎，春茸翦破难重缀。\",\"蝴蝶寻飞香入袂。不道东风，拍断游丝脆。最是双眸秋水媚，可怜雨溅胭脂退。\"]',NULL,NULL,NULL,NULL),('e647d10fbb369365e3906cb0c30cd510','凤凰台上忆吹箫·步月','沈宜修','e647d10fbb369365bcb41ccd42a39645','明',NULL,'[\"帘影横阶，翠？垂榥，花阑芳径苔肥。看小庭烟醉，白月澄漪。\",\"帘外将舒玉药，寻萼梦、雪汗凝蕤。金壶送，回廊静悄，墨洒花筛。\",\"微微。罗衣耐冷，双繶向轻阴，夜渐阑时。想夕阳初下，云树参差。\",\"又是婵娟千里，长相见、淡景霏霏。还待去，浓香软叠，绣幕重帏。\"]',NULL,NULL,NULL,NULL),('e647d10fbb5153d283cfc9c2d36867f3','竹枝词','王佐','e647d10fbb5153d2d1457b722dffbc47','明',NULL,'[\"竹林塘西越女归，双双采莲歌夕晖。兰舟荡桨忽分去，两岸风花高下飞。\"]',NULL,NULL,NULL,NULL),('e647d10fbb5153d2dc6339945380905f','竹枝词赠两墩为乐老之章四首','王佐','e647d10fbb5153d2d1457b722dffbc47','明',NULL,'[\"碌碌红尘四十年，归来仍被世情牵。昨朝闲到桐墩下，信有人间快活仙。\"]',NULL,NULL,NULL,NULL),('e647d10fbb5153d2dc63399471950518','竹枝词赠两墩为乐老之章四首','王佐','e647d10fbb5153d2d1457b722dffbc47','明',NULL,'[\"两朵能行白牡丹，妒花风雨忍相残。主人不吝东风面，明日还来一倚栏。\"]',NULL,NULL,NULL,NULL),('e647d10fbb5153d2dc633994766df8a8','竹枝词赠两墩为乐老之章四首','王佐','e647d10fbb5153d2d1457b722dffbc47','明',NULL,'[\"试遣云英唱我词，我词便是璞墩诗。璞墩风月无边景，还是青春年少时。\"]',NULL,NULL,NULL,NULL),('e647d10fbb5153d2dc6339948c5fafe6','竹枝词赠两墩为乐老之章四首','王佐','e647d10fbb5153d2d1457b722dffbc47','明',NULL,'[\"莫笑风前两鬓丝，老来偏爱竹枝词。若将年纪方彭祖，八十方才是小儿。\"]',NULL,NULL,NULL,NULL),('e647d10fbc527a6625eb6af7c24e0c8e','清平乐','徐元端','e647d10fbc527a66d1457b72d1457b72','明',NULL,'[\"绣窗无那，自卷帘儿坐。羞睹黄莺枝上卧，抛去青梅数颗。\",\"东风阵阵相摧，胭脂零落苍苔。春色依然归去，为谁留下愁来。\"]',NULL,NULL,NULL,NULL),('e647d10fbc527a66d8514caf03563c90','南乡子','徐元端','e647d10fbc527a66d1457b72d1457b72','明',NULL,'[\"独坐数归期，花影重重日影低。无计徘徊思好句，迟迟，除却春愁没个题。\",\"闲倚画楼西，芳草青青失旧堤。犹记当时人去处，依依，红杏花边一酒旗。\"]',NULL,NULL,NULL,NULL),('e647d10fbcc7141a6513b611845ee0a9','桃源忆故人舟次瓜步怀徐姬石莲','陆亮辅','e647d10fbcc7141ad1457b72d1457b72','明',NULL,'[\"桃花碎影江如靛，隔断相思一半。昨夜曾题纨扇，镜里莲如面。\",\"无边春草江南怨，好梦被谁偷换。柳外老莺啼遍，报道看花晚。\"]',NULL,NULL,NULL,NULL),('e647d10fbd1f699596d11dc2fec7123e','满江红·送友致仕','郑满','e647d10fbd1f6995d1457b72d1457b72','明',NULL,'[\"归去来兮，有几个，生年满百。好寻取、陶家荒径，贺家故宅。一抹斜阳铺水靓，千重晚岫排云碧。料乡园、翠竹与黄花，还如昔。\",\"心早叹，为形役；更暮景，休虚掷。看候门稚子，欢歌笑拍。荣辱只今都不管，烟波且作逍遥客。更到时、戏采旧庭前，娱朝夕。\"]',NULL,NULL,NULL,NULL),('e647d10fbd4c4e7adf2be99c98eeac91','杨柳枝','区怀年','e647d10fbd4c4e7ad1457b72d1457b72','明',NULL,'[\"酒帘风劲碧梢寒，南陌花光媚眼看。惆怅谷莺呼不起，腰枝赢得倚阑干。\"]',NULL,NULL,NULL,NULL),('e647d10fbd4c4e7adf2be99cf57df067','杨柳枝','区怀年','e647d10fbd4c4e7ad1457b72d1457b72','明',NULL,'[\"映水长条故故柔，彩虹桥畔系扁舟。轻阴似管离人意，一夜回塘带雨愁。\"]',NULL,NULL,NULL,NULL),('e647d10fbda5a07f9102429d4522d420','竹枝词二首','孙一元','e647d10fbda5a07f729c6888db191505','明',NULL,'[\"郎到家时春正忙，愿郎看蚕奴采桑。去年蚕死犹自可，今年蚕死愁杀郎。\"]',NULL,NULL,NULL,NULL),('e647d10fbda5a07f9102429dceb5e111','竹枝词二首','孙一元','e647d10fbda5a07f729c6888db191505','明',NULL,'[\"湖日初明湖水涯，门前鹊鸣郎到家。折得草花还自喜，插向阿奴双髻丫。\"]',NULL,NULL,NULL,NULL),('e647d10fbdef3872360d11880204661a','蝶恋花·婺州席上','张纲','e647d10fbdef3872d1457b72d1457b72','明',NULL,'[\"风动飞霙迎晓霁。银海光浮，宴启群仙会。骑省流芳谁可继，尊前看取连枝贵。\",\"华发衰翁羞晚岁。未报皇恩，尚忝专城寄。酒入愁肠应易醉，已拚一醉酬君意。\"]',NULL,NULL,NULL,NULL),('e647d10fbdef3872684e8dc43b378da1','蝶恋花·安人生日','张纲','e647d10fbdef3872d1457b72d1457b72','明',NULL,'[\"五日小春休屈指。花发西轩，早已传春意。应为高堂催燕喜，一枝得得来呈瑞。\",\"绮席初开云幕邃。兰蕙腾芳，人生蓬壶里。寿酒满斟那惜醉，锦囊行拜恩封贵。\"]',NULL,NULL,NULL,NULL),('e647d10fbdef3872684e8dc4e7097327','蝶恋花·安人生日','张纲','e647d10fbdef3872d1457b72d1457b72','明',NULL,'[\"雨洗轩庭迎晚照。黄菊才芳，未觉秋光老。怪底烘堂添语笑，姮娥此夜来蓬岛。\",\"献寿新词谁解道。满眼儿孙，著语皆争巧。潋滟金杯休惜釂，追欢不怕霜天晓。\"]',NULL,NULL,NULL,NULL),('e647d10fbdef38729c808f029df505ab','蝶恋花·丁宅二侍儿','张纲','e647d10fbdef3872d1457b72d1457b72','明',NULL,'[\"缓带垂红双侍女。彩凤衔来，秀色生庭户。转蕙光风香暗度，回眸绰约神仙侣。\",\"寡和清歌声激楚。夜饮厌厌，劝我杯频举。只恐酒阑催暮雨，凭谁约断阳台路。\"]',NULL,NULL,NULL,NULL),('e647d10fbdef3872a491a63fe48de1ad','蝶恋花·癸未生日','张纲','e647d10fbdef3872d1457b72d1457b72','明',NULL,'[\"老去光阴惊掣电。生长元丰，试数今谁健。多谢天公怜岁晚，清时乞得身闲散。\",\"忆昔生朝叨睿眷。台馈颁恩，内酒当筵劝。今日衰残欢意鲜，举杯目断尧天远。\"]',NULL,NULL,NULL,NULL),('e647d10fbdef3872b6020e87f58e05b6','人月圆·壬午生日，时初遇会庆节，至翌日小集','张纲','e647d10fbdef3872d1457b72d1457b72','明',NULL,'[\"望尧云了，归路蔼欢声。何妨明日，开筵笑语，聊庆初生。\",\"官闲岁晚身犹健，兰玉更盈庭。持杯为寿，从教夜醉，谁怕参横。\"]',NULL,NULL,NULL,NULL),('e647d10fbdf16db848b09950db4ed6ec','踏莎行·园居','钱棻','e647d10fbdf16db8d1457b72d1457b72','明',NULL,'[\"万乘徒空，一丘壑足，小园但种千竿竹。苍阴深处掩高斋，黄鹂啼过山头屋。\",\"壁挂嵇琴，村疑阮曲，白云邀我同歌宿。幽花绕砌不知名，柴门秋水添新绿。\"]',NULL,NULL,NULL,NULL),('e647d10fbeb049e0f32f182e9c2db86a','浣溪沙','花梦月','e647d10fbeb049e0d1457b72d1457b72','明',NULL,'[\"梳罢鸣蝉浅画眉，习池香鸭篆烟迟。一窗日影到罘罳。\",\"只说频来桃叶渡，不期犹隔柳花堤。数声风外啭春鹂。\"]',NULL,NULL,NULL,NULL),('e647d10fbede7cc046408bd9bfedc449','玉楼春','程馞','e647d10fbede7cc0d1457b72d1457b72','明',NULL,'[\"子规啼过杨花路，缄泪函情无寄处。君如南浦往来潮，妾似西陵朝暮雨。\",\"当年只恨传金缕，何必相逢成别苦。夜深蜀国十三弦，紫女青皇私自语。\"]',NULL,NULL,NULL,NULL),('e647d10fbede7cc08de75d1a7957ade1','锦堂春秋怨','程馞','e647d10fbede7cc0d1457b72d1457b72','明',NULL,'[\"玉簟寒生暮雨，冰绡泪染朝霞。多情记得还家未，丛桂已无花。\",\"谱入银筝恨切，窥来宝镜愁加。无端雁去书难寄，飞尽总由他。\"]',NULL,NULL,NULL,NULL),('e647d10fbede7cc0bf2159102b40943b','西江月·秋兴','程馞','e647d10fbede7cc0d1457b72d1457b72','明',NULL,'[\"尽日荷锄治圃，有时捉杖寻泉。山翁招我坐桥边，笑指菊花开遍。\",\"隔岸重重竹树，近溪点点潭烟。滩头流下小鱼船，转过芦花不见。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce480b45d37fbae95049','古调笑令','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"芳草，芳草，生向江边太早。连天一片春愁，不管王孙泪流。\",\"流泪，流泪，点点桃花吹坠。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce48277272a226b263aa','摸鱼儿·与友人别','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"放兰舟、溯江西上，渺茫云树何处。天涯相见频相别，同作水边风絮。春欲去，恨故苑、莺花总付烟和雨。离情罢诉！教夜半啼鹃、声声向月，为写此愁苦。\",\"思重会，定在吴阊古渡，吹箫城下应许。英雄失路皆无策，渐使浣纱怜汝。君莫取，鸿鹄在、他时自有凌霄羽。功名粪土！且满酌霜醪，还携渔艇，共醉白苹渚。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce4868c07e9f3519c0ee','离亭燕','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"渐到鹧鸪多处，愁作一天风雨。芳草可怜千万里，长共夕阳无主。且莫返乡关，已有断魂飞去。\",\"云际片帆难驻，江上一竿谁许。空妒白鸥来复往，笑我飘零倦羽。掩泪似春山，终日蒙蒙洲渚。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce487c1ce45668dcd140','浪淘沙·春草','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"嫩绿似罗裙，寸寸销魂，春心抽尽为王孙。不分东风吹渐老，色映黄昏。\",\"蝴蝶不留痕，飞过烟村，红藏几点落花魂。雨过苔边人不见，湿欲生云。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce489a1e372363a2aeea','一落索','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"杜宇催春从汝，莫催人去。人留即是好春留，更一任、风和雨。\",\"怕见游丝飞絮，为伊无主。落花争似泪花红，只滴在、分襟处。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce489a21a1f6b3e48c08','江城梅花引','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"黄花和我满头霜，怕重阳，又重阳。不分早梅，还与斗寒香。老去看花如雾里，被花恼，一枝枝、总断肠。断肠，断肠。\",\"苦参商，夜已长，天已凉。一叶一叶，落不尽、悲似潇湘。那得罗浮，清梦到兰房。明月笑人眠太早，飞去也，影徘徊、尚半床。\"]',NULL,NULL,NULL,NULL),('e647d10fbf01ce48e05423a433db38d0','满庭芳·蒲城惜别','一灵','e647d10fbf01ce48d1457b72d1457b72','明',NULL,'[\"金粟堆边，冰蒲水畔，紫骝迢递迎来。月中惊见，光艳似云开。桑落沾唇半醉，将长笛、弄向秦台。天明去，鞭挥岸曲，愁杀渡人催。\",\"徘徊。空叹息，桃花易嫁，凤子难媒。和香雨氤氲，飞作尘埃。坠井银瓶永绝，谁复取、仙液盈杯。应知尔，三春绣阁，幽寂委苍苔。\"]',NULL,NULL,NULL,NULL),('e647d10fc0d3ec92b8a625f1976391c9','阮郎归·立夏','张大烈','e647d10fc0d3ec92d1457b72d1457b72','明',NULL,'[\"绿阴铺野换新光，薰风初昼长。小荷贴水点横塘，蝶衣晒粉忙。\",\"茶鼎熟，酒卮扬，醉来诗兴狂。燕雏似惜落花香，双衔归画梁。\"]',NULL,NULL,'绿阴遍野，风暖昼长，横塘新荷，花丛蝶忙，一片初夏时节的景象宛然在目。仰看雏燕惜花，双衔归梁，恍觉物各有情，不禁酒后诗兴大发。词以写景为主，而作者兴会，亦在其中。 ','[\"点：点缀。\",\"晒粉：蝴蝶在阳光下扇动双翼，如晒翅粉。\",\"酒卮：酒杯。 \"]'),('e647d10fc0d3ec92fd80e610e216e4cc','少年游·秋思','张大烈','e647d10fc0d3ec92d1457b72d1457b72','明',NULL,'[\"萧瑟秋风古渡桥，江风壮晚潮。夕阳衰柳，何堪轻拆，瘦损小蛮腰。\",\"碧云澹远澄波静，惨怨散林皋。山海情深，石尤风急，留住远征桡。\"]',NULL,NULL,NULL,NULL),('e647d10fc175652e8bcc964220c9cab6','竹枝词三首','罗万杰','e647d10fc175652e69d658d05f73a27a','明',NULL,'[\"小姬酿酒唤人尝，玉盌擎来扑鼻香。门前五尺垂丝柳，系尽江南年少肠。\"]',NULL,NULL,NULL,NULL),('e647d10fc175652e8bcc96426496eef9','竹枝词三首','罗万杰','e647d10fc175652e69d658d05f73a27a','明',NULL,'[\"轻纨五丈细如絺，剪就单衫着地垂。江滨日暖浑无事，火伴相邀赌荔枝。\"]',NULL,NULL,NULL,NULL),('e647d10fc175652e8bcc9642eacce27d','竹枝词三首','罗万杰','e647d10fc175652e69d658d05f73a27a','明',NULL,'[\"木兰花映木兰舟，花自妖娇水自流。贪看堤间杨柳色，不知舟落小滩头。\"]',NULL,NULL,NULL,NULL),('e647d10fc2ec5b3e2c86ec10da936ca5','西河驿','庞尚鹏','e647d10fc2ec5b3ed1457b724748b616','明',NULL,'[\"去岁淮楼坐明月，海色山光白如雪。今岁扬旌拂塞尘，黄沙白草埋青春。\",\"听鸡握发事长路，东渡辽海西入秦。临岐拊髀长叹息，愧我碌碌非能臣。\",\"会当早觉渔樵伴，买舟结屋称芳邻。\"]',NULL,NULL,NULL,NULL),('e647d10fc30b82489ef5ce70894f05a8','醉花阴','沙宛在','e647d10fc30b8248d1457b72d1457b72','明',NULL,'[\"翡翠楼头风几阵。断送残红尽。薄暮掩罗帏，睡鸭香寒，冷却沉檀印。\",\"梦回宝枕垂云鬓。愁压蛾弯损。窗外雨声疏，响入芭蕉，又是黄梅信。\"]',NULL,NULL,NULL,NULL),('e647d10fc31842a75ecb6fa189c3c396','多丽','吕福生','e647d10fc31842a7d1457b72d1457b72','明',NULL,'[\"夜未央，蛩声一片侵床。漏滴沥、云颓雨咽，谱成半枕凄凉。空怅望、纤林带月，不堪闻、孤雁啼霜。灯泪红残，香心字冷，难将樽酒倩疏狂。恨人去，天涯路远，抛却好春光。向只道，相思有梦，毕竟荒唐。\",\"破愁烦、蝇头绣句，花笺印叠鸳鸯。更关情、楼西倚闷，最消魂、灯下凝妆。月不长圆，花原易落，一时也抵百年长。终凝恋、斜阳衰草，别袂共离觞。一点点，鲛衫欲湿，界破檀黄。\"]',NULL,NULL,NULL,NULL),('e647d10fc35614dc405ba870036d602a','竹枝歌','龚诩','e647d10fc35614dc4e0d67e5991de292','明',NULL,'[\"朝见浮云飞出山，暮见浮云飞入山。浮云自是无心物，郎既有心胡不还。\"]',NULL,NULL,NULL,NULL),('e647d10fc3c358b2eb949e3e4b3ab170','望江东','李元组','e647d10fc3c358b2d1457b72d1457b72','明',NULL,'[\"一幅春光明更美，莺满院，啼花碎。飞絮落红相点缀，人立在、秋千背。\",\"长思短忆供憔悴，算只有、花前泪。已将心事随流水，阑干下、金钗坠。\"]',NULL,NULL,NULL,NULL),('e647d10fc4becf68535d7c51c5d52eff','满江红·贺江阴王尹筑城御寇','洪贯','e647d10fc4becf68d1457b72d1457b72','明',NULL,'[\"海宇清宁，甚蓦地、飞传羽檄。回首处、萑苻啸聚，烽烟南北。百雉先几雄版筑，一方从此安磐石。渺长江、天堑绕城流，谁之力。\",\"守则固，疆圻画；战必胜，车徒饬。看狐鼠穴扫，了无遗迹。日月未容尘雾翳，东南用壮河山色。信王公、设险在承平，千秋绩。\"]',NULL,NULL,NULL,NULL),('e647d10fc511928f1f506892e13d558e','南乡子·花朝','冯鼎位','e647d10fc511928fd1457b72d1457b72','明',NULL,'[\"晓雾如烟，轻寒细雨嫩晴天。红杏枝头莺对语，飞去，昨夜玉人春梦处。\"]',NULL,NULL,NULL,NULL),('e647d10fc511928f679aaabca90e0a0c','盐角儿春半','冯鼎位','e647d10fc511928fd1457b72d1457b72','明',NULL,'[\"春分过也，小楼昨夜，棠梨花谢。章台折柳，西园拾翠，暗香随马。\",\"午风轻，莺声乍，盈盈侣秋千初罢。再相约、黄昏踏月，窗下绿蛾重画。\"]',NULL,NULL,NULL,NULL),('e647d10fc511928fa0b448a49c37f4bf','子夜歌','冯鼎位','e647d10fc511928fd1457b72d1457b72','明',NULL,'[\"上西楼、曲屏暮霭，记得烛花红处。自心字香消、彩云飞散，苹踪难聚。\",\"春水画桥，烟溪芳草，夜夜相思路。怅重来、情事依然，惟有半帘淡月，照人离绪。\",\"酒阑后、无聊短梦，尚逐紫箫声去。杨柳梢头，寒梅影里，曾看弓弯舞。\",\"想章台信杳，凄凉京兆眉妩。啼帕遗红，唾裙留碧、憔悴莺花主。\",\"倩谁传、桃叶桃根，断肠新句。\"]',NULL,NULL,NULL,NULL),('e647d10fc511928fa2d534f6f6f7d737','减字木兰花','冯鼎位','e647d10fc511928fd1457b72d1457b72','明',NULL,'[\"长亭凄绝，去岁伤秋曾送别。杨柳湾头，一曲烟波一曲愁。\",\"高城何许，正在归鸿欲没处。满袖西风，人倚朱阑第几重。\"]',NULL,NULL,NULL,NULL),('e647d10fc511928fdb6e3ee675f1bcad','摊破浣溪沙郊行','冯鼎位','e647d10fc511928fd1457b72d1457b72','明',NULL,'[\"迟日青骢缓辔行，轻寒轻暖弄春晴。细草平沙花夹道，有啼莺。\",\"嫩绿翻风烟样软，小溪过雨镜般明。归近黄昏新月起，晚霞生。\"]',NULL,NULL,NULL,NULL),('e647d10fc5353fc6c80dd3f82edb6729','念奴娇·别意用辛稼轩韵','宫伟','e647d10fc5353fc6d1457b72d1457b72','明',NULL,'[\"萧娘楼畔，恰匆匆过了，菊花时节。帆影拖霜逢小至，画舫水嬉微怯。绣阁春心，芳洲密约，执手何曾别。玉楼人去，此情谁与传说。\",\"漫道青邸朱帘，彩毫长擅，对引金波月。旧梦燕山看不尽，新梦涛声千叠。喜得今宵，锦屏瞥见，展放双眉折。悄声低问，多情付与华发。\"]',NULL,NULL,NULL,NULL),('e647d10fc569b37925583e10895b4227','南歌子·见《众香词》','王毓贞','e647d10fc569b379d1457b72d1457b72','明',NULL,'[\"浴罢明肌雪，妆残亸鬓鸦。娇怯欲扶花。花枝扶不得、倚风斜。\"]',NULL,NULL,NULL,NULL),('e647d10fc569b3797f5a44346a9a8c55','生查','王毓贞','e647d10fc569b379d1457b72d1457b72','明',NULL,'[\"鸂鶒翠钿飞，翡翠榴裙嵌。含笑入罗帏，人影灯光蘸。\",\"昵枕听晨鸡，点点铜壶勘。亏得种芭蕉，日闪红窗暗。\"]',NULL,NULL,NULL,NULL),('e647d10fc569b37982791341ee8577db','生查子','王毓贞','e647d10fc569b379d1457b72d1457b72','明',NULL,'[\"兰沐绮窗凉，红汗酥胸溅。倦倚玉栏杆，蜂咂娇花颤。\",\"红沁被池香，翠滑眉心现。挥麈扑青蝇，佯拂檀郎面。\"]',NULL,NULL,NULL,NULL),('e647d10fc569b379e429ae7959abe47f','如梦令','王毓贞','e647d10fc569b379d1457b72d1457b72','明',NULL,'[\"月影被花摇碎。掩映隔帘红袂。倚醉殢春娇，狼藉满头珠翠。\",\"难记。难记。人在画屏秋水。\"]',NULL,NULL,NULL,NULL),('e647d10fc60db73f345a9fdcf398e2a6','风入松·次虞邵庵韵别友','黄润玉','e647d10fc60db73fd1457b72d1457b72','明',NULL,'[\"先生和气似春酣，一见盍明簪。太平无讼霜台静，榕阴里、时与联骖。瓜代早装行李，兰熏重整朝衫。\",\"雨余山色净于蓝，秋燕更喃喃。凄凉风月阳关道，和离思、摹入诗缄。一曲骊歌人去，月明千里闽南。\"]',NULL,NULL,NULL,NULL),('e647d10fc6acb49c9b02ba6d7b24c4a6','菩萨蛮','赵大佑','e647d10fc6acb49c41a60377655ea4bd','明',NULL,'[\"画梁双燕春江曲，翠楼掩映垂杨绿。往事几凭栏，关山风露寒。\",\"彩云无觅处，回首桃源路。惆怅月华明，新欢旧恨并。\"]',NULL,NULL,NULL,NULL),('e647d10fc7db14d46aedc3eb2ce674e2','小重山·题东川','陈如纶','e647d10fc7db14d46490791e351b3358','明',NULL,'[\"绿涨粼粼接碧空，可人鱼鸟境、著闲翁。蓬莱只在海山东，烟霞迥、杖履任从容。\",\"无分学屠龙，有时移短棹、泛春风。归来篷底夕阳红，同酒伴、烂醉水云中。\"]',NULL,NULL,NULL,NULL),('e647d10fc7db14d496be16cb349010f1','眼儿媚·答铭泉春怀','陈如纶','e647d10fc7db14d46490791e351b3358','明',NULL,'[\"罗袖轻盈怯风寒，飞梦到长干。蜂慵蝶倦，红稀绿暗，春色将残。\",\"三月家书犹未见，锦瑟几抛闲。相思无奈，千重烟树，万叠云山。\"]',NULL,NULL,NULL,NULL),('e647d10fc7db14d4a7468bb7b2b205ae','踏莎行·答贾竹亭春行','陈如纶','e647d10fc7db14d46490791e351b3358','明',NULL,'[\"杨柳溪桥，桃花野渡，十年车马同游处。联诗曾对月华明，伤心只见春光暮。\",\"迢递双鱼，浮沉尺素，相思辗转愁无数。东风听彻子规啼，声声诉尽空归去。\"]',NULL,NULL,NULL,NULL),('e647d10fc84ff779f1598899239d95fc','如梦令·七夕风雨','张凤翼','e647d10fc84ff7795cce8ded69d658d0','明',NULL,'[\"狼藉芳筵瓜果，妒煞无情风雨。拆了鹊边桥，怎把佳期留住。\",\"知否，知否，一夜泪痕深注。\"]',NULL,NULL,NULL,NULL),('e647d10fc88361f8154effbd035d68ec','浪淘沙','王越','e647d10fc88361f8856fc816ced556cd','明',NULL,'[\"远水接天浮，渺渺扁舟。去时花雨送春愁。今日归来黄叶闹，又是深秋。\",\"聚散两悠悠，白了人头。片帆飞影下中流。载得古今多少恨，都付沙鸥。\"]',NULL,NULL,NULL,NULL),('e647d10fc88aef6f164effffd2d090e5','渔歌子','李玉照','e647d10fc88aef6fd1457b72d1457b72','明',NULL,'[\"秋思萦怀懒赋诗。黄昏深院月来迟。\",\"人静处，漏残时。一片幽情只自知。\"]',NULL,NULL,NULL,NULL),('e647d10fc88aef6f4f09b9f5aa2b66e0','如梦令·夜坐','李玉照','e647d10fc88aef6fd1457b72d1457b72','明',NULL,'[\"梦入愁乡初醒。犹有残灯相映。铁马寂无声，金鸭沉烟已烬。\",\"清冷。清冷。谁念绣衾孤另。\"]',NULL,NULL,NULL,NULL),('e647d10fc88aef6f9cf491a11936117a','忆王孙','李玉照','e647d10fc88aef6fd1457b72d1457b72','明',NULL,'[\"幽闺深闭日如年。临镜无心整翠钿。赢得愁肠两处牵。\",\"思淹淹。裙带闲拈花柳边。\"]',NULL,NULL,NULL,NULL),('e647d10fc88aef6fead7f6b073d1f953','醉公子·忆梦中美人','李玉照','e647d10fc88aef6fd1457b72d1457b72','明',NULL,'[\"无意拈花片。有恨抛针线。细想梦中人。芳姿记未真。\",\"默坐还相忆。珠泪和香滴。月色到窗纱。寻思暗抵牙。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c300ee3cfc8deaf4db','法驾导引·曲上建保祥斋，仁寿宫中灵幡结瑞，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"朝北极，朝北极，斗转帝车回。历历珠星联五纬，高高华盖护三台。\",\"寿域万年开。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c300ee3cfcc264bb4e','法驾导引·曲上建保祥斋，仁寿宫中灵幡结瑞，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝幡结，宝幡结，神巧示天工。锦带盘盘双舞凤，彩旒曲曲九蟠龙。\",\"缥缈逐灵风。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c300ee3cfcd3e889bd','法驾导引·曲上建保祥斋，仁寿宫中灵幡结瑞，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"炉香爇，炉香爇，秘祷九天灵。结固玄珠封玉鼎，保和金液注黄庭。\",\"十月证仙婴。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c300ee3cfce3fa7a18','法驾导引·曲上建保祥斋，仁寿宫中灵幡结瑞，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"帝神降，帝神降，天乐下丛霄。十灵霓幡苍雾结，九华羽盖翠云飘。\",\"肃肃万灵朝。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c300ee3cfcf9b6488e','法驾导引·曲上建保祥斋，仁寿宫中灵幡结瑞，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"瑶坛上，瑶坛上，宝盖缀华幡。凤尾交交彩霞舞，龙鳞宛宛紫霓盘。\",\"织女降云端。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c302cbc508a9d907e8','壶中天慢初归述怀，次赖云翁韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"解组归来，拟种秫、荒田采芝空谷。散诞已无名利累。\",\"野性颇同麋鹿。抵冒风波，淹留岁月，岂为千钟粟。\",\"久怀丘垄，几度伤心欲哭。\",\"谁想今日溪山，重寻游钓，里社仍分肉。三十余年尘土梦，勘破人间荣辱。\",\"夏赏青池，春游芳野，秋饮冬篱菊。烟霞深处，高卧不嫌矮屋。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c304c90b8e16ca5606','大江东去次苏韵，赠槐川侍御','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"骢马巡行，赖高贤、轸念一方民物。风力霜棱行道避，震动匡庐一壁。名重璠玙，文腾星斗，雅操凝冰雪。紫霄鸾凤，鹰鹯未是材杰。\",\"闾阎疾苦偏知，蠲除账货，仓廪随发。人仰槐川真御史，所至豺狼迹灭。野老林间，惟知歌咏，长笑舒眉发。大江列郡，人在玉壶秋月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db051f8785','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"两岸垂杨，一湾芳草，深护龙洲。想夜雨当年，主人云卧，雷门春水，忽占鳌头。白简凝霜，铁冠峨豸，十载登朝侍冕旒。人争避，行行骢马，按部洪州。\",\"大江章贡交流。有卷雨、飞云帝子楼。看霜雪威棱，豺狼遁野，云霄风力，雕鹗横秋。白首怀乡，丹心恋阙，客梦时时到旧丘。烦君去，把穷檐民瘼，子细咨鲰。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db095fbf34','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"紫阁名公，玉堂词伯，仗节行边。计策马云中，关山春日，回车辽左，海戍秋烟。嘉峪关前，贺兰山下，几夜星营鼓角喧。临戎处，喜宫袍座拥，赐带腰悬。\",\"怜公将相双权。算文武、才猷世罕全。况司马壮游，驰驱万里，韩公雄略，功业千年。当宁虚怀，故人远念，几见军中羽檄传。早归来，共高歌雪曲，酒醒朝天。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db0b416ac6','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"天上归来，重寻旧隐，桂树芳洲。喜骢马东巡，梅花岁晚，画船西下，柳色江头。把赠春词，留连夜酌，尚忆前时侍衮旒。又谁料，华堂绿酒，共醉南州。\",\"如君海内名流。况气压、西江第一楼。看执法芒寒，乌台清夜，文昌辉映，蟾窟高秋。白发闲身，故人青眼，长笑何妨守一丘。山林里，有渔樵可话，风月堪诹。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db0f6c7c4b','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉女潭头，张公洞口，好段烟霞。记往日尊前，长安有约，何年此地，同炼丹砂。飞舄云间，拂衣天上，几阵清风送到家。忽报道，有仙翁驻节，太守公衙。\",\"相逢一笑天涯。指玄圃、沧洲路未赊。把万种丹经，从头参透，玄关一窍，认取无差。犬吠白云，莺啼修竹，策杖春江送去槎。更看取，待银蟾秋满，丹桂生花。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db1af6c261','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"三十年来，看花旧侣，特地相过。念南浦挐舟，遥遵江路，龙冈策马，直访岩阿。经济长才，风云壮节，揽镜容颜鬓未皤。却谁遣，东泽一曲，早占烟萝。\",\"平生凤阁鸾坡。愧独受、天边宠渥多。奈相业无堪，久宜休避，圣恩未许，几度蹉跎。丹桂堂边，白鸥园里，此日兰舟泛芰荷。喜相逢，须教尽醉，莫遣停歌。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db1c66deeb','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"南郡趋庭，重闱戏彩，文采英英。正夜雨篝灯，黄堂斋阁，秋风掇棹，绿水江城。华岳云开，秦关树绕，仗剑携书壮此行。还家好，看豹文早变，鹏翮初成。\",\"而翁五马尊荣。况政事、文章着盛名。羡紫绶绾章，飞腾宦辙，青云有子，振起家声。桂折蟾宫，花簪鳌禁，秋榜春闱取次升。鸥园老，喜亲提彩笔，助尔修程。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db1facf7a3','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉瓒清仪，朱弦逸韵，文采超群。羡早岁登科，英华振发，名区作宰，惠爱流闻。简召秋台，班趋晓陛，乡曲衣冠共五云。惊老眼，念一时佳士，谁更如君。\",\"当年扈跸南巡。记春日、相看袂忍分。正尔向山中，养成经济，我从天上，辍却丝纶。丹桂堂中，灵溪桥畔，绿树清江日未曛。相送好，喜游山得伴，别酒成醺。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db27e4a1c3','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"仁寿宫前，豳风亭畔，帝社坛东。正晴日高天，烟销雾敛，远山秋水，拥翠涵空。玉带金貂，麟袍绣蟒，咫尺丹霄扈六龙。镇日里，珍羞络绎，天语从容。\",\"底须驾鹤崆峒。又何必、寻仙海上峰。念际会风云，身都将相，一时文武，总是英雄。愧我迂疏，岂堪畴伍，自分长辞紫阁中。却谁料，今朝一笑，还共诸公。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db32468895','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"濲水偶逢，西湖久客，今日君来。喜锡杖能携，破除烦恼，荷衣初制，摆脱尘埃。八角池边，白鸥园上，一曲春词对客裁。念林翁，似山阴野客，兴尽船回。\",\"城南有个茅斋。爱竹里、柴门临水开。更细路穿松，最宜踏雪，小桥跨涧，偏称观梅。王氏堂槐，宝家庭桂，一任天公为我裁。且约君，向武夷九曲，长笑徘徊。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db41f47b2d','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"鳌禁英流，龙头妙选，久识奇才。羡玉署春坊，争夸独步，词垣讲幄，荐历华阶。腰带垂金，印章绾紫，简命留都绛帐开。记当时，离尊易尽，别句难裁。\",\"蹇予谢政归来。喜日伴、鸥群碧水隈。念江国梅花，曾劳问讯，山城春酒，又共衔杯。箫鼓林塘，笙歌院落，沉醉花前月满台。叹人生，相逢乐处，即是蓬莱。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db449f81fd','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"山入五羊，江通八桂，仗钺临戎。正贡篚新来，交蛮面革，楼船才返黎寇功。柏府风裁，辕门号令，南仲威存北伐名。况一时，三移节镇，两转中悉。\",\"大江月白风清。伫相送、旌旄岭外行。念诸葛沉机，南人慑服，仲淹英略，西贼闻惊。天子中兴，庙谟神武，直挽天河洗甲兵。早归朝，更将经济，报答升平。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db4c807084','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白日青天，绣衣骢马，归自钱塘。正快睹中台，法星明润，忽看南徼，宪节回翔。碧海三山，武夷九曲，迢递风烟接故乡。怜君处，仙帆秋水，那得相将。\",\"嗟予素食朝堂。畏满目、波涛日夜狂。念相业无成，鬓毛先老，君恩未报，身世都忘。按辔观风，停车问俗，好为闾阎作主张。秋空里，尚远瞻雕鹗，横击豺狼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db507b2491','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"道院寒灯，儒宫夜榻，犹记髫年。念京第相逢，堪惊岁月，都亭分手，又隔风烟。乌帽新笼，白袍袍换，莫厌青青座上毡。庐江畔，有门墙可爱，桃李争妍。\",\"看君潘鬓依然。美腹笥、藏经不让边。叹少日云霄，霜蹄蹭蹬，暮年江海，云翮蹁跹。老我非才，际时何幸，多病偏蒙圣主怜。凤池里，正迟回恋阙，惆怅归田。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db52a9a972','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"花满河阳，棠阴蔽野，久着贤声。记京第相逢，仙凫书接，山楼久住，藜火宵明。笔擅三长，才堪八面，每见挥毫对客成。白鸥园，平生老眼，为尔偏青。\",\"谩夸词翰纵横。还自愧、文章浪得名。念此日编摩，真劳旧宰，它年流播，尽付来英。宝泽楼前，琼恩堂上，几度笙歌共醉醒。沧洲远，鳌峰乌石，目送君行。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db7491ea80','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"春日相逢，春山如画，不共登临。想象麓新堂，棠花旧馆，池生碧草，莺语空林。前烛蓬窗，振衣野岸，中夜徘徊碧树阴。忘情处，任朱弦调古，绿酒杯深。\",\"何须万壑千岑。记明月、孤帆此夜吟。念行李萧萧，驰驱北上，仙槎泛泛，迢递南寻。报主关情，忧时改鬓，漫结心盟碧水浔。百年里，看双龙宝剑，不负千金。\"]',NULL,NULL,NULL,NULL);
INSERT INTO `ci_ming` VALUES ('e647d10fc8d190c30ed186db7c27cc23','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白玉桥东，碧山楼畔，昼锦堂开。正冠盖盈门，笙歌满座，红莲才放，绿酒初醅。紫禁朝回，凤池吏散，特为先生庆寿来。献新词，嗣音大雅，敬祝台莱。\",\"共看廊庙奇才。更星斗、文章逼上台。况海岳精神，壮腾龙马，云霄气概，时动风雷。圣主思贤，明时求旧，旦日征书下玉阶。愿从头，把经纶再试，花甲重排。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db7d93e2e7','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"节操冰清，丰标玉立，名重司空。正江左分曹，才归省署，浙西持宪，更理兵戎。约束诸生，按行五郡，秋水楼船七里陇。最喜是，乡关入望，民俗相通。\",\"富春山色笼嵷。念昭德、新祠梦寐中。但有疏辞天，君恩未许，无繇缩地，仙舫难从。酒醒长安，梦固海峤，目送云鸿万里风。归朝早，双龙阙下，一笑还同。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db7f65d4a6','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"春月才圆，春花欲放，春宴初开。喜春省仙卿，高跻眉寿，春曹诸彦，共赋台莱。留赏春灯，醉酣春酒，一任春宵鼓角催。春堂上，蔼十分春色，光映三台。\",\"主人昔占春魁。有赞治、春官礼乐才。况雅量春融，清颜春盎，化行春雨，名震春雷。春殿宣麻，春宫定策，春日还看拜玉阶。春风里，好调和玉烛，照耀春台。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186db94d916eb','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"短棹乘流，轻轩度岭，风雨兼程。念走马看花，当年识面，避人焚草，往事关情。三十年来，四千里外，廊庙江湖白发生。喜今朝，有故人远到，春满山城。\",\"后园丹桂堂成。正露冕、扶筇踏晓晴。看竹径烟浓，春迷鹤梦，横塘水静，晚结鸥盟。佳客难留，好怀未尽，又向江头话送行。凝望处，爱高楼云阁，青眼偏明。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbaa77bc79','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝带垂金，铁冠峨豸，玉节东行。正木落关山，霜清海岱，送君尊酒，无限离情。灯火东泉，风云北阙，青鬓相看白发生。向天涯，念同袍有几，又隔春明。\",\"人间青史功名。难得是、明良共太平。计老我此身，当年许国，受知圣主，敢爱余龄。一道风霜，万家霖雨，洗耳闾阎起颂声。思君处，望楼台蜃结，雕鹦秋横。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbadc9912a','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"数载怀乡，几番抗疏，此日归休。喜紫阁彤庭，拂衣长谢，丹山碧水，策杖闲游。楼起青云，堂开绿野，丹桂依然香满洲。烟霞里，诗盟酒约，日日堪酬。\",\"寻花问柳优游。更不管、人间岁月流。念千载遭时，真逢尧舜，百年致主，独愧伊周。绿鬓词臣，乌须元宰，又得青山对白头。北窗下，园闻啼鸟，不听鸣驺。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbbca5b17c','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"骢马翩翩，荐冠矗矗，丰采超群。记阊阖排云，当年封事，楼台得月，此夜清尊。旧话偏长，好怀难尽，笑口平生得共君。灵峰麓，喜谩劳车马，特驻柴门。\",\"五云北望天阍。正迢递、江湖引梦魂。念老我余龄，自宜田野，看君壮气，久负经纶。边塞烟氛，闾阎疲瘵，须向明廷仔细论。兹行好，愿匡扶圣主，早建奇勋。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbbd9d4e8e','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"枢府尚书，储宫宾客，文武全才。正海国秋风，楼船遥下，沙场春日，斧钺初回。凤阙颁恩，龙墀锡宴，马首旌旗万里开。天声震，看指麾云鸟，号令风雷。\",\"炎荒朔雪周回。总只为、天王辟草莱。想定远封侯，玉关迢递，伏波为将，铜柱崔嵬。百国销兵，诸蛮纳土，干羽从容列两阶。归来日，早图形麟阁，晋位三台。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbc28fd0de','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"骢马循行，玉堂话别，屈指经年。正南浦怀人，绿波春水，西风送客，落木秋烟。我到东山，君居北阙，塞雁江鸿又各天。频问讯，望钟陵明月，相忆娟娟。\",\"茅斋日晏高眠。时步屧、支筇野水边。喜林鸟如呼，归来可乐，沙鸥作伴，相对堪怜。惟有丹心，浑忘白发，春梦长依黼房前。愿故人，好力持舟楫，弘济商川。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbc63d1a93','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"银烛华堂，金尊绿酒，今夕长安。漫白首思亲，丹心恋主，满怀情事，兀坐更阑。节序堪惊，勋庸未建，况是明良际会难。中宵立，望台阶平正，斗柄阑干。\",\"一生窃禄朝端。历几度、冰霜到岁寒。念琐闼封章，时挥彩笔，玉堂视草，日侍金銮。庭下三槐，阶前五桂，春雨秋风次第看。升平世，一人有庆，万国同欢。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbc789111c','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"千里故人，一尊江上，笑口同开。念毛坞新巢，梦魂空到，麓堂旧约，舟楫仍回。骢马南行，彤扉北望，恋阙心悬碧水隈。清宵立，见中天奎璧，光动三台。\",\"如公海内名魁。有七步、风流八斗才。况此日词林，真堪妙选，他年麟阁，预卜先阶。沙燕留人，潭花送客，把手高歌一快哉。苍生望，好大施霖雨，遍沃蒿莱。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbd1703f2f','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"虎观词臣，鸾坡学士，昭代英儒。记宝带垂金，光生北阙，玉堂振绎，名重南都。梦绕桥门，书来铃阁，又道文旌返旧庐。经年隔，望海云缥缈，岭树模糊。\",\"镜中鬓发萧疏。却自笑、形容非故吾。但赖得天恩，罢归田里，获从初服，依旧耕锄。后乐园中，堂开丹桂，草长池塘春雨余。谱新词，托惠连远寄，聊写区区。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbd334a416','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"尚记当年，春闱较士，见子看花。念路入钱塘，君方作县，林藏象麓，子亦还家。白简霜威，皂囊风力，独步南台众所夸。乘骢日，遇风波满地，流滞天涯。\",\"相思梦绕长沙。更怅望、苍梧岭树赊。笑侧席师垣，滥竽座主，无能荐引，空抱咨嗟。经济高才，江湖壮气，竹帛勋名未有涯。葛溪上，有白头知己，拭目烟霞。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbe639fcbc','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝墨楼前，赐麟堂上，一别三年。喜学有师承，文章丕变，士多矜式，礼义相先。振起儒风，激昂晚进，洙泗门墙得正传。共道是，有苏公条教，白日青天。\",\"蹇予谢政归田。正欲访、匡庐五老烟。奈白鹿洞中，难留地主，紫薇花下，忽送神仙。便省东齐，旧游西晋，一点文星入紫躔。重分守，记华堂丹桂，绿酒歌筵。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbeae3bf2c','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"万仞冈头，平临四望，人世奇观。正宿雨初收，流云散乱，浑如沧海，浪涌涛翻。时见群峰，参差隐现，仿佛蛟鼍没巨澜。太空里，长烟幕幕，积雾漫漫。\",\"下方尘土歊烦。到此处、方知宇宙宽。看壁立中开，剑光犹迸，古苔剥落，鹤迹仍完。身在层霄，分明上界，何用凌风跨玉鸾。昨宵卧，神游八极，不觉超凡。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c30ed186dbed04ab46','沁园春·港口别方思道二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白玉阶前，黄金殿侧，多士充庭。爱贾谊敷陈，经纶蕴藉，董生条对，礼乐纵横。老子当时，已曾括目，识尔文章有法程。果然是，家传夙学，海内知名。\",\"拜官远别神京。有恋恋、相知一日情。想蹑屐钟陵，看山力健，连床伯氏，梦草诗清。旧里柴门，新开竹径，得子相过眼倍明。春夜宴，爱芳园桃李，把酒频倾。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bda21aba480','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"谏闼封章，司农会计，久着贤声。忆奉使西川，赈荒全陕，风裁霜厉，节操冰清。朝论同归，帝心简在，督饷云中壮此行。指挥处，看三军嬉饱，万马腾鸣。\",\"天寒紫塞龙城。正坐拥、貔貅百万兵。念虎帐风严，辕门月冷，敌声夜撼，杀气朝横。诸葛神机，酂侯英略，刍粟如山转运平。策勋日，定功同汗马，麟阁书名。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bda288de398','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"二十年前，恍然如昨，初过君家。爱门对青山，桥横绿水，一川乔木，数里烟霞。白发而翁，红颜阿姊，予亦青衿鬓似鸦。到而今，追寻往事，何限堪嗟。\",\"京麈荏苒年华。但明发、趋朝暮放衙。谩玉带麟袍，彤闱紫阁，门施长戟，路拥新沙。桑梓关心，风波激目，安得山巅与水涯。挥彩毫，写清词一曲，相送归槎。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bda549c5343','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"琐闼英僚，杏园仙侣，廿载交承。念霄汉飞腾，冠裳争羡，风波独立，梦寐堪惊。圣主怜才，老天留我，玉带今朝送子行。离亭酒，也须拚尽醉，莫惜深倾。\",\"丈夫有志功名。愿白首、丹心答圣明。喜节钺东征，远山云静，楼船南下，新水河平。问讯闾阎，观风阙里，白日青天岱岳横。望中原，好徐徐揽辔，早见澄清。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bda8710d33f','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"石壁溪头，富春山下，画舫新来。正雨过青山，波生碧渚，千峰日照，两岸花开。北郭池塘，东门杨柳，二十年前几往回。重登眺，爱风烟如画，临水楼台。\",\"追思少日情怀。长记在、先人旧郡斋。向范老祠前，春风走马，客星亭上，雪夜观梅。往事分明，故交零落，叹息光阴一瞬哉。伫立久，念白云芳草，欲去徘徊。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bdaa377141a','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"紫殿宣麻，彤墀赐宴，仗钺专征。看白日辞天，玄冬出塞，金戈寒映，宝剑雄鸣。雪满居庸，山横碣石，北去龙沙几日程。据鞍处，正旌旗蔽野，鼓角连营。\",\"丈夫有志功名。好戡乱、扶危定太平。况圣主恩深，清时望重，试凭全策，早下坚城。王浚平吴，裴公破蔡，勋业真堪带砺盟。登坛处，愿指麾诸将，一战功成。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bdad931218f','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"屡疏求闲，殊恩优老，昼锦还乡。看晓日都门，秋云锦席，楼船未发，冠盖相望。白首乌纱，绯袍玉带，却笑神仙是渺茫。归途好，览楼台北固，箫鼓钱塘。\",\"君家庆泽深长。喜金榜、相传四世芳。况春省贤郎，玉堂少弟，声名并起事方张。乌石岩头，金鳌谷口，此地宜开绿野堂。高斋卧，有三竿红日，一曲沧浪。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3130d1bdadfd419eb','沁园春·送蔡都御史巡抚山东','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"问讯溪山，好秋风里，何处登临。爱清湖新筑，天然融结，眼中佳胜，不让横林。楼起青云，堂连绿野，篱巷斜通碧树阴。最难得，重湖沙绕，隔岭潭深。\",\"门前几个青岑。合每日、开窗对酒吟。况隐隐花源，从人借问，萧萧竹径，许客相寻。我劝先生，多栽杨柳，遍插芙蓉夹水浔。更须买，青娥画舫，莫吝黄金。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c316140b9e43383600','酹江月次韵答顾东桥翁见四阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"黄扉十载，掌丝纶、补衮调羹非手。自愧才疏如袜线，小器岂堪大受。一代赓歌，百年礼乐，赖明明元首。格天帝业，毫发赞襄何有。\",\"独惭凤阁鸾台，挥毫载笔，忝窃夔龙后。平地风云遭圣主，未羡磻溪钓叟。犬马衰龄，山林倦翼，莫报君恩厚。稽首焚香，中夜遥瞻北斗。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c316140b9e7149710d','酹江月次韵答顾东桥翁见四阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"清词曾寄，楚江头、俯仰又成今昔。想见当年挥彩笔，摇动三江七泽。凡桂洲前，青云楼下，剩有烟霞迹。灵岩玉洞，真是神仙窟宅。\",\"何时双舄飞来，一筇相伴，共领看山敕。却把平生诸翰墨，留记名山石室。我爱桥翁，江南才子，一代文章伯。山中无事，芳尊但少佳客。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c316140b9eb334aa3b','酹江月次韵答顾东桥翁见四阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"灵山山下，葛溪滨、风景不殊畴昔。乍睹城西新赐第，突兀琼恩宝泽。玉带雕麟，绯袍绣蟒，不类山林迹。花开客到，休笑平章旧宅。\",\"自怜多病微躯，放归田里，独奉君王敕。共道熊罴当入梦，莞簟已安家室。多谢桥翁，瑶篇远寄，丽藻真词伯。筵开汤饼，传邀江左仙客。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c316140b9ebded8acb','酹江月次韵答顾东桥翁见四阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"谁将秀句，谱新词、堪羡桥翁妙手。多谢高情传密意，一笑开怀领受。岁月频经，风波尽历，此日方回首。功名勘破，毕竟于吾何有。\",\"自怜少日风流，登临赋咏，肯落他人后。碌碌红尘三十载，不觉渐成老叟。南谷松篁，北山猿鹤，相见颜殊厚。从今稳卧，一室修然如斗。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31cd295c88a11aea5','水调歌头·奉旨诣文华殿，代拜先圣先师','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"雨霁苍龙阙，月明虎豹关。虚阁遥闻清漏，鸟宿禁林安。左掖金铺半启，西内钟声初动，玉殿上香还。草承瑶佩湿，花映鹤袍丹。\",\"正高城，击柝罢，小星残。身绕凤凰池上，归步信安闲。忝窃孤卿重任，沾沐圣师余泽，真愧草茅寒。遭逢千载异，君德重于山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c0019dbe5','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"富春向来碧溪长。昭德祠前草树香，劳君三载护甘棠。\",\"闻道儿童留别驾，谁教风雨送归航。眼中清浊付沧浪。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c0d40e24e','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"大廷曾见董生文。丹凤楼前已识君。果然雕鹗出风尘。\",\"两浙乘骢豺虎遁，三山揽辔岛夷闻。好将经术策奇勋。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c1c40f982','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"结庐吾已对灵山。西望鹅峰咫尺间。沙头鸥鹭喜人还。\",\"莫向红尘嗟踯躅，也知碧岭可登攀。何须大药驻朱颜。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c1c968ac9','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"草阁萧萧对碧山。野翁中夜梦龙颜。一庭春雨长苔斑。\",\"溪上群鸥堪作伴，林间倦鸟自知还。怜君两度叩柴关。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c33bf5dbc','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"莫厌西南道路长。丈夫膂力正方刚。之官仍买过家航。\",\"桂岭春烟笼橡槲，鹅溪秋水荫桄榔。怜君青鬓坐黄堂。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c3c165b18','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"纤纤细雨润花枝。袅袅和风拂柳丝。醉春台上立多时。\",\"准办杖头挑绿酒，安排屐齿踏红泥。明朝郭外去寻诗。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c3dc8ddc5','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"当年挥翰凤凰池。青琐翱翔又一时。怜君江海独栖迟。\",\"虹井卧龙今作雨，方塘野鹤少人知。怜君寄我送兄诗。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c4588b718','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"先公作宦古齐东。此地当年见汝翁。恂恂敦朴古人风。\",\"六十四龄闻抱汝，今朝见汝与翁同。昨宵偶尔梦罴熊。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c47d6d034','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白石青沙海屿连。吴门秋月映娟娟。美人家住太湖边。\",\"潞渚波光涵日月，幔亭峰影度风烟。豸袍画舫羡登仙。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c5496171f','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"天上归来卧野堂。劳君为买葛溪舫。萧萧风雨话偏长。\",\"折桂当年夸鬓绿，看山今日羡腰黄。且将岁月付诗囊。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c60434882','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉署春坊树影重。御沟流水岸西东。也须著个后渠翁。\",\"风细层轩修竹里，雨凉深院绿槐中。黄昏来访语匆匆。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c660f2574','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"尔也今为溧水丞。春洲芳草暖烟生。县衙相近石头城。\",\"老去扶吾惟药裹，兴来相伴只鸥盟。题诗怜尔廿年情。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c7332d8cd','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"凫趋丹陛御炉边。鹄立桥门壁水前。十年京国共称贤。\",\"单父琴清庐阜月，河阳花满大江烟。几时飞舄早朝天。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c89f6988d','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"去年京国一相逢。道上行人已避骢。果然雕翮振秋风。\",\"老我一区依葛水，劳君两度访灵峰。春波画舫又匆匆。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c97bdc27d','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"三十年前听鹿鸣。怜君文采冠时英。几回同赏上林莺。\",\"独对云林开草阁，漫劳风雨驻江城。沙头别酒看春晴。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c9d63a2ca','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"池阁山亭竹树凉。南风时送菱荷香。故人相对倒清觞。\",\"胜负已看棋局罢，浮沉谁著钓丝长。耳边孺子咏沧浪。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757c9e7e8243','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"记得当时过邺城。后渠亭上漫题名。万竿修竹绕檐楹。\",\"一字不曾通政府，十年始得见先生。玉堂相对有余情。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757cbf44bb2b','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宫漏沉沉夏日长。雨余殿阁昼生凉。南风微送院荷香。\",\"草阁抛书移白日，竹床欹枕听沧浪。梦魂时复到江乡。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757cc5c6c139','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"昔日少年今老翁。长安花下一尊同。不堪分手又匆匆。\",\"我欲归来寻旧隐，有谁相伴卧云松。怜君作宰万山中。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757cca49178d','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"闭门连夕雨声寒。晓起萧萧更倚阑。清如茅屋住江干。\",\"天际阴云横作阵，泥中莎草乱成团。长安大道几时干。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757ce08c4380','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"庭院沉沉白日斜。绿阴满地又飞花。岑岑春梦绕天涯。\",\"帘幕受风低乳燕，池塘过雨急鸣蛙。酒醒明月照窗纱。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757ce5dce1a3','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"老子今年六十龄。三千里路庆初生。一家兄弟见君情。\",\"初寿堂前槐影密，三台楼下桂香清。乡心先与白鸥盟。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757ce61a30f0','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"瀛海波涵秋气清。白沙如雪夜盈盈。月明紫蟹阵横行。\",\"春瓮酒初浮绿蚁，小园景巳到香橙。百枚遣赠谢高情。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757cf0162eff','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"归来几度望燕山。白日红云缥缈间。自怜倦鸟合知还。\",\"葛水有谁同上下，云岩先已得跻攀。酒杯何日对开颜。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757cf114168a','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"三十年前梦竹楼。楚云湘水忆黄州。天涯今日送君游。\",\"漫道贾生年最少，不应王粲独悲秋。江湖难解范公忧。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31eaa757cf4286261','浣溪沙·答序庵惠蟹','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"浮云逝水自纵横。白日青天老眼明。怜君万里独南征。\",\"雨过前溪添草色，花飞上苑自莺声。野翁无限惜春情。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c31ebe257dab0d853c','卜算子·夏日阁中','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"红药映阶翻，墙角黄薇谢。又见盆荷出水圆，容易春还夏。\",\"漏永麝烟销，昼静朱帘下。高阁清凉午睡浓，半饷挥毫罢。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c326c540fc31cf4616','临江仙·贵州东坡书院，次林见素，戊寅年作','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"今日西风初送暑，碧梧已动秋声。高楼一望暮山明。白云飞处，迢递客愁生。\",\"目断征帆江上尽，几人遥挂归笙。天涯芳草绿波平。柴门何日，拄杖小桥横。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c326c540fc57436776','临江仙·贵州东坡书院，次林见素，戊寅年作','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"百尺清泉声断续，半岩松竹青青。丹崖翠壁列云屏。入山行解带，扫石坐翻经。\",\"忆过西湖杨柳院，六桥载酒扬舲。天涯离恨不堪听。萍踪随处寄，尘梦几时醒。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c326c540fcbbd029eb','临江仙·贵州东坡书院，次林见素，戊寅年作','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"山谷老人年七十，百花潭上优游。石亭草阁枕江流。携琴随野鹤，移席傍沙鸥。\",\"东浙甘棠思邵父，角极画省名留。一瓢春酒醉林丘。神仙行陆地，何用访瀛洲。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c32a75ab93bd0c4719','风流子·用顾东桥韵，寄史恭甫','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"阳羡溪山好，吾将老、曾见昔贤心。念此语空留，买田未遂，桃源一别，旧路难寻。叹人生，几回王子棹，徒付伯牙琴。争似山人，烟霞占断，洞天深隐，高卧长吟。\",\"向仙台佛窟，看紫烟敛散，白日升沉。时听端流激玉，岩溜淙金。更健步堪游，底须策杖，直钩可钓，不用敲针。何日扁舟滆水，独访云林。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c333a2476c3fb4d4c4','鹧鸪天·天西山灵岩寺，和元耶律丞相韵，劖洞中石壁','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"人世沧桑几变迁。灵岩玉洞自岿然。朝衣几共游山日，佛界犹存刻石年。\",\"嗟岁月，惜风烟。等闲花发又啼鹃。只将彩笔题僧壁，玉带留来朝日边。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c33663d1063d0057b9','感皇恩·送钦赐书院诸扁出城','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"御榻降丝纶，楼堂书院。次第嘉名出宸翰。更令华扁制司空，金碧云龙光烂。遣使送江南、还乘传。\",\"鼓吹喧阗。人裳拥塞，夹道千人万人看。城东小驻，日午离筵方散。盛事记今朝、蒙天眷。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c33663d106eb5e2091','感皇恩·送钦赐书院诸扁出城','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"今夕是中秋，朝来中使。宫醪玉馔传宣至。白煠羔羊，玉雪如双，月饼黄金似。更五尊秋露、连翩赐。\",\"小臣何幸，蒙恩非次。佳辰况复逢熙事。皇女挝周，宝玉带中三试。有一函御札、亲批示。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c336da1fab43748f58','三台·令西湖感旧','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"犹是西泠桥畔，难寻春柳春花。只有婆娑老树，夕阳依旧栖鸦。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c33eb5ebf7b04b360f','行香子·答杨正郎仪惠古今词抄','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"独坐灯前。清兴修然。览新词、尽是遗编。风流谷老，豪宕坡仙。最爱它、气格高，辞锋健，意机圆。\",\"太白临川。一代称贤。欧阳子、千古争妍。词场三味，妙理难传。真个是、下诗坛，入画品，出文筌。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3456029098512d1ba','蝶恋花·次李蒲汀秋日之作','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"高楼半饷看云睡。枕簟凉生，不觉西风至。屈指明年六十二。巾中白发先秋坠。\",\"造物小儿真剧戏。反复无端，堪笑人间事。履险从来吾有备。安身只是心无累。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3457a6cf42ae480a7','丹凤吟端午次周美成韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"荏苒端阳节届，梅雨蒸轩，荷风薰阁。绿槐庭院，飞燕乍穿帘幕。\",\"朝来雨霁，微寒犹袭，宫扇纨轻，朝衫罗薄。喜值天家开宴，玉醴仙蒲，沾醉凤楼东角。\",\"因念君恩深重，此际顿忘怀抱恶。笑渠薄伎俩，似洪炉点雪，禁甚销铄。\",\"春花过眼，岁岁自开还落。只愧元非调燮手，这机衡难握。\",\"浮云富贵，却几曾恋著。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34648e7eb13d47d36','西江月·次朱希真三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"对景不妨头白，逢春且放眉开。莫教闲事恼心怀。容忍些儿何碍。\",\"尘土难寻道侣，山林应属奇才。朱衣翠袖两边排。不似柴门自在。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34648e7eb2f6d2437','西江月·次朱希真三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"举世昏昏醉梦，几人心眼惺惺。山窗春睡正冥冥。想见高斋唤醒。\",\"江上云雨滕阁，湖边烟水徐亭。莫将喧寂挠虚灵。便是黄庭内景。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34648e7eb38c4da8f','西江月·次朱希真三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白日如如兀坐，中宵栩栩酣眠。兴来临水更看山。怀抱了无萦绊。\",\"不要许多荣利，只图些子清闲。麻绦草履布衣衫。学个道人打扮。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34648e7eb55a96138','西江月·次朱希真三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝泽楼前月满，琼恩堂后花明。耳边箫鼓动江城。元夕故园风景。\",\"去岁今宵内苑，承恩赐宴观灯。人间天上最关情。却笑萍踪无定。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34648e7eba12769d1','西江月·次朱希真三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"岁月急如流水，功名虚似浮云。何须劳苦百年心。行止繇来是命。\",\"岂得人情胜旧，从教世事更新。笑谈且与俗相亲。好恶胸中自定。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34648e7ebd02e7bbf','西江月·次朱希真三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝泽楼前明烛，琼恩堂后焚香。金盘瓜果侑瑶觞。等待团圆月上。\",\"端拜广寒仙姊，遥瞻北斗星皇。朱颜不改寿年长。更愿麒麟早降。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c347f214f285ba1d75','汉宫春·乙巳元夕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"元夕今朝，喜春风才到，臈雪仍飘。正是连天灯火，满地琼瑶。楼高宝泽，望朱栏、迥出云霄。画堂里，灯屏锦幛，一派笙箫。\",\"不是山翁好事，要与民同乐，莫负良宵。况有贤明太守，楚楚英僚。太平歌吹，这风光、无福难消。齐祝愿、吾皇万寿，万方玉烛和调。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c347f214f2e15259b2','汉宫春·乙巳元夕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝月流虹，正元宵新霁，雪满瑶空。灯火楼台灿烂，香蔼帘拢。筵开罗绮，拥群姬、珠翠重重。一品夫人初度，年年占断春风。\",\"紫诰名高八座，羡翟冠霞帔，叠受褒崇。曾侍采桑西苑，亲谒三官。蓬莱深处，瑶池宴、首饮黄封。但愿全膺五福，生儿拊凤攀龙。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34bb769040172dd4c','丑奴儿观阁前芍药有感二阙','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉堂何用栽红药，秾萼娉婷。那似人清。搅乱梅花梦不成。\",\"自有看花人道好，爱听流莺。醉眼偏醒。掉臂花前挟弹行。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34bb769040f5eeb13','丑奴儿观阁前芍药有感二阙','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"阶下几株红芍药，晓起开轩。露萼㛹娟。错认蓝花著意看。\",\"道人已罢看花兴，燕雀休喧。不去凭阑。收拾闲心学坐圆。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34bb76904a3326cf3','丑奴儿观阁前芍药有感二阙','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"昨日盛开今日少，一朵当轩。靓色娟娟。却比繁英更耐看。\",\"明岁而今花又发，蝶嚷蜂喧。玉砌雕阑。物态人情似转圆。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c34bb76904e43b11e3','丑奴儿观阁前芍药有感二阙','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉阶暖日翻红药，绰约娉婷。色艳香清。独倚薰风画不成。\",\"铃索昼闲宫漏静，绿树啼莺。午梦初醒。自起看花绕阁行。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3581549664e2a446f','苏幕遮·夏日阁中','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白玉堂，黄金阁。帘卷薰风，阶下翻红药。墙角黄薇花半落。\",\"宫漏沉沉，昼静喧檐雀。\",\"算人生，何处乐。遇主遭时，日日沾恩渥。定知无福也难消，合把丹心，向日同葵萼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c358483b3f68034d8c','诉衷情·次荆公','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"龙楼凤阁五云间。每日一登攀。自念平生遭际，敢忘却、寸心丹。\",\"年渐老，性贪闲。鸟知还。琵琶千仞，灵峰万叠，梦在家山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35918d2a2cd70c1d4','渔家傲·次欧阳文忠公韵二十首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"池上春深花尽绽。阑干曲曲闲凭遍。蝶攘蜂喧晴日暖。归来晚。罗衣两袖花香满。\",\"小院黄昏帘半卷。林端月出烟光远。风凫炉熏时欲断。更漏短。琐窗春梦梨云乱。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35918d2a2d9ccd17e','渔家傲·次欧阳文忠公韵二十首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"花香送酒莺声劝。醉归斜日花间晚。楼头调玉管犹眷。相思满。风前凝伫情无限。\",\"北书懒寄春空雁。惊回午梦梁间燕。芳草萋萋天共远。春不见。虚教杨柳垂青眼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35918d2a2e35074f9','渔家傲·次欧阳文忠公韵二十首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"一夜雨添池水涨。红亭绿树深相向。晓日兰舟吾独上。堪偃仰。天光云影波摇漾。\",\"起坐船头神思爽。柳边花底斟春酿。有客不来聊自赏。山童唱。时时鼓吹穿林响。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35918d2a2f3f454bd','渔家傲·次欧阳文忠公韵二十首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"艳蕊娇红秾叶翠。天生异种如留意。国色嫣然真绝丽。谁堪比。杨妃扶醉差相似。\",\"美人劝酒牵罗袂。红颜厮映枝头蕊。纤手捧尊如玉腻。客自醉。不因人面花枝媚。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35c69d4c3316087ab','减字木兰花·灯','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"仁和宫里。祈雪瑶坛清似水。碧瓦霜明。满殿炉烟逼太清。\",\"千门万户。宝炬花灯排辇路。漏下三更。夜清月辉斗柄横。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35c69d4c3981c7c24','减字木兰花·灯','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宫门乍入。月满瑶阶星露湿。陛上天灯。玉兔金乌相对明。\",\"长街窈窕。不奉传宣那得到。安喜仁和。延寿宫前一带过。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35d87c0fc21e06680','桂枝香次荆公韵，送费钟石少宰之南都','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"登楼纵目。正云黯长空，天地凝肃。遥想金陵佳丽，锦围绣簇。玄武湖头司寇第，对三山、天外横矗。朱雀桥边，乌衣巷口，春游兴足。\",\"念昔日、杏园追逐。叹南北萍踪，音书断续。白首还山，高谊每惭虚辱。岁晚江湖冰雪后，喜岩下、桂枝仍绿。寄语浅翁，好怀都付，白鸥园曲。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c35d87c0fc78eb078a','桂枝香次荆公韵，送费钟石少宰之南都','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"烟尘满目。正木落燕山，天寒气肃。北望居庸叠翠，万峰云簇。黄金台上当时月，见几度、征旗矗矗。冰合滹沱，风寒易水，不堪驻足。\",\"笑车马、少年追逐。叹慷慨悲歌，古调难续。青史功名，只是一场荣辱。七国英雄俱寂寞，但岁岁、花香草绿。禁苑除夕，挥毫聊和，桂枝旧曲。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c361ee953eda7a9666','万年欢·西苑贺上祷雪有应','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"瑞满长空，见彤云密布，琼花飘雪。坛上炉烟，馥馥沉檀和屑。风里宝幡双结。瞻缥缈、霓旌素节。导引皓鹤千群，遥下玉楼银阙。\",\"铜龙漏咽。正九关鱼錀沉沉，夜光皎洁。平晓迷漫，迤逦瑶台相接。圣主忧民念切。对此际、龙颜开悦。况正属、腊内春前，一白胜于三白。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3656dc3e16ce0636b','望江南西苑除夕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"禁中夜，宫漏箭频催。玉剑云寒祈雪罢，霞衣露湿醮星回。\",\"金殿紫坛开。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3656dc3e1f2e1903b','望江南西苑除夕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"岁云幕，除夕是明朝。襆被未离西苑直，佩环方候紫宸朝。\",\"秉烛醉宫醪。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd2d2f7983','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"解政归来，新营别墅，小结数椽衡宇。白昼看云，清宵对月，酌酒听歌金缕。满院花香，一庭草色，无数莺啼燕语。更有个、小小池亭，日与白鸥为侣。\",\"感皇恩、赐第城中，琼堂宝阁，何异洞天仙府。东壁图书，西苑花木，尽勾老臣居住。鱼水遭逢，云龙庆会，俯仰有何今古。但自惭、调燮无功，虚负商家霖雨。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd3f78c6ec','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"物换星移，春来秋去，荏苒光阴流转。白露丹枫，青霜落木，又是江空岁晚。静里娱情，闲中遣兴，惟有酒杯诗卷。爱吾庐、城市山林，真觉地偏心远。\",\"念区区、一介寒生，躬逢尧舜，千古升平重见。玉殿宣麻，黄扉掌制，紫塞手提龙剑。仁寿宫中，谨身殿上，圣主亲曾锡宴。到而今、追想当年，福分也应非浅。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd554bc6df','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"淮口洪涛，海门巨浸，几度片帆飞过。北上太行，南游越巂，鸟道林深箐大。楚水吴山，长途倦客，愁对江枫渔火。笑平生、岐路风波，此日依然还我。\",\"觉今来、梦寐无惊，心神俱定，麋鹿任兴吾左。醉后放歌，闲中度曲，聊学吴歈楚些。却杖登山，挥毫对客，白首有谁能那。不问他、秋月春花，适意四时俱可。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd6d45ac64','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"晚节亭中，赐闲堂上，长日从容笑语。出郭寻春，过挢送客，翠竹白沙江路。芳草长堤，绿杨古渡，隔岸几声杜宇。道相公、久已归来，喜对家山如故。\",\"忘形处、相伴渔樵，沙头同醉，村酒溪鱼旋取。宠辱无惊，是非不管，此乐也应天与。返照入林，断云堆墨，倏忽晚来风雨。望城南、灯火攒星，独棹一舟归去。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd7c3f9d0e','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"万岁山高，广寒殿古，缥缈九重深处。太液波容，承光树色，便是阆风玄圃。银瓮宫醪，雕盘玉馔，味胜琼浆麟脯。半空中、羽盖云旓，恍惚昼逢王母。\",\"蒙圣恩、特赐传宣，遍教游览，宴罢归来日暮。城转西华，桥连东阁，迤逦蓬莱仙路。紫禁宸居，青山隐墅，中夜梦魂来去。睡觉来、日出东方，鸟语花香在户。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd83bbd171','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"十载长安，半生禁闼，饱历燕山冰雪。下石危机，铄金谗口，幸保此身孤洁。天子神明，重瞳朗鉴，邪正辨如胡越。自归来、圣眷犹存，厚禄音书未绝。\",\"屈指间、两听春莺，三瞻秋兔，镜里未添白发。无虑无营，无嗔无喜，此是长生要诀。北郭池亭，东山花鸟，闲处风光自别。任逍遥、物外烟霞，不管人间岁月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cd95933579','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"怀玉东来，冰溪西下，百里水清沙白。灵岫参天，芙蓉万朵，屏倚三台楼北。坐枕渔矶，起扶鸠杖，行处靸双凫舄。寻旧游、某水某丘，风景未殊畴昔。\",\"也休问、玉井高花，瑶池仙果，曾见几番华实。把握灵根，参通玄窍，饱咽霞浆玉液。青鸟难逢，丹砂空煮，采药竟无消息。道英雄、回首神仙，却是老夫今日。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cdac64bd9d','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"林下行吟，山中打坐，此是老翁风格。草履麻绦，布袍箬笠，不受尘埃物色。玉带金鱼，锦麟绣蟒，多少同袍贵客。到头来、回首田园，几个欲归便得。\",\"小池中、跨水虹桥，临流飞阁，相望蓬莱咫尺。雨后排青，烟中凝紫，朝暮灵峰秀特。静抚孤松，闲随野鹤，一片白云无迹。傍渔矶、斜日晚风，时共海鸥欢剧。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cdb7ffb87e','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"绿树芳园，白鸥野水，一曲渔矶堪钓。取乐偷闲，傍花随柳，不是学他年少。辍却丝纶，放归田里，深荷九重优诏。喜天边、倦翼翩翩，飞向夕阳林表。\",\"自归来、新买池塘，旋裁杨柳，勾引水禽沙鸟。竹底茅斋，松间草阁，藜火夜辉林杪。拄杖闲游，支颐打坐，行住万缘都了。念道人、非佛非仙，说与傍人不晓。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cdd0b17309','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"青琐玉堂，黄扉南省，久荷圣皇恩惠。九庙仪章，四郊典礼，西苑肇兴蚕事。千古中兴，一人制作，不过赞襄而已。却胜于、尼父当年，空叹吾衰衰矣。\",\"中岁时、种秫南村，读书象麓，自分放情山水。感激明时，遭逢圣主，不揣草茅微细。利害甜弹，是非廷诤，每事直穷到底。幸保全、未老归来，赢得熟眠酣醉。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cdd10381c8','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"筑室灵峰，买田葛水，休笑巢鸠计拙。青琐十年，黄扉七载，辛苦此时暂歇。丹桂洲前，白鸥池上，照眼碧波澄澈。更新添、宝泽楼高，邀取大江明月。\",\"愧迂儒、经国无能，避贤宜早，敢谓退身决烈。畎亩情深，江湖忧在，瞻恋九重天阙。仁寿高玄，奉天华盖，几度梦中朝谒。长记得、西苑观灯，踏遍御街春雪。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cdd9665a21','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"临水推窗，迎风倚槛，长忆玉楼琼宇。碧岭堆螺，银塘铺练，檐柳细垂金缕。坐待明蟾，修然池阁，幽意有谁共语。舞婆娑、月色满庭，顾影三人成侣。\",\"夜深时、露湿星榆，波摇海屋，如在清虚之府。冷浸瑶台，寒生贝阙，应是广寒难住。庾亮层楼，坡公短棹，几度照人今古。且徘徊、今夕清光，休管明朝风雨。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cde82e8bc3','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"龙口岩前，白芒洲畔，长忆往时游钓。一别青山，十年紫禁，蓦地位跻三少。琐闼封章，玉堂视草，金殿屡承丹诏。首鹓班、玉带朱衣，无日不瞻天表。\",\"荷君恩、报答无能，勋庸未建，敢学归云倦鸟。晏下彤闱，明登紫阁，独立太清天杪。大旱为霖，巨川作楫，整顿乾坤都了。那时节、拂袖还山，高卧海天分晓。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3666bb3cdf4103140','苏武慢次虞伯生韵，咏白鸥园','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"灵鹫山前，葛阳溪上，野老园开后乐。花木平泉，风烟绿野，不减迂翁西洛。前辈高情，浮生旧梦，回首千年如昨。叹乾坤、元气长存，吾道未应萧索。\",\"耳边闻、边境无虞，丰年多黍，扶杖岂胜雀跃。篱下鸡肥，瓮头酒熟，唤取比邻共酌。有客相过，兰舟夜泛，箫鼓笙歌间作。醉归时、斗柄横斜，月转宝楼西角。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c372fd40f71b5dcaf8','浪淘沙·令次荆公韵，赠闵星士','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白发闵山翁。休论穷通。我今不改旧书佣。试问古来诸将相，几个英雄。\",\"鱼水偶遭逢。拊凤攀龙。横飞直上廿年中。却喜风波身早退，有甚奇功。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3740c6ddac82ea70f','大江东去扈跸渡河日，进呈御览','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"九曲黄河，毕竟是天上，人间何物。西出昆仑东到海，直走更无坚壁。喷薄三门，奔腾积石，浪卷巴山雪。长江万里，乾坤两派雄杰。\",\"亲随大驾南巡，龙舟凤舸，白日中流发。夹岸旌旗围铁骑，照水甲光明灭。俯仰中原，遥瞻岱岳，一缕青如发。壮观盛事，己亥嘉靖三月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c376520ce84b2e1a52','永遇乐·题松石遐龄圆，寿宫詹陆俨翁','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"矗矗乔松，岩岩巨石，苍颜古色。长共东风，不随流水，贞心自识。年年岁岁，沾濡雨露，厌饫烟霞冰雪。摩层汉、百尺龙鳞，俯千寻壁立。\",\"有个仙翁，俨如山岳，独挺岁寒标格。我爱丹青，分明画出，妙手真难得。节近中秋，会逢初度，北斗光瞻南极。但愿翁、柱石明堂，光华寿域。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618100e37f808','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"黄金明魏阙，白日照燕关。岁岁花香柳媚，春色满长安。只怪鹦声太巧，底事燕巢无定，昏暮马蹄还。为渠双鬓白，添我寸心丹。\",\"问苍生，多困苦，甚凋残。独使至尊忧切，忍放一身闻。感叹明良相遇，说到圣贤出处，风雨对床寒。寄与浚川老，莫学谢东山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618101483f5eb','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"昔年芗水上，终日掩柴关。相伴碧空猿鹤，无复梦长安。几见林花开谢，坐数鸥群朝暮，时共野云还。不愁生白发，那用觅金丹。\",\"到如今，芳草远，桂枝残。只为波涛浩荡，舟楫怎容闲。谁作甘霖济旱，安得盐梅调鼐，广厦庇酸寒。但须垂竹帛，底羡勒燕山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618101c1590f6','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"太极原无象，梦觉亦非关。天性浑然心地，定静自能安。只为一团私欲，便有许多驰逐，长往不知还。洪炉难着雪，点铁要须丹。\",\"叹言湮，嗟教弛，慨经残。可怪支离末学，弃实尚虚闲。须辨小人君子，方识霸图王道，水火异炎寒。斯文天未丧，万古仰尼山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810280657bf','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"隽才名翰苑，雅望重贤关。照眼玉壶冰鉴，性度自安安。曾向湖南问讯，咫尺龙门迢递，万里使车还。寻仙游五岳，分我玉盘丹。\",\"望钟陵，春草碧，杏花残。几度寄书无雁，彩笔画堂闲。追忆舟樯北上，怅望林丘高卧，只恐旧盟寒。那知开口笑，今日共蓬山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618103f2c0459','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"朝回望行盖，离思绕江关。正喜云龙相聚，公去岂心安。莫学尧夫康济，且辨杜陵药饵，聊选马卿还。底须访仙灶，自有活人丹。\",\"问年龄，才五十，未衰残。况有九重眷注，能便早投闲。从古圣君难遇，海内苍生无数，犹未免饥寒。相看临潞水，有梦到樵山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618105897ec1a','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"今夜中秋月，独坐对高天。休问古今圆缺，心赏自年年。更上小楼南望，千里故园何处，风露九霄寒。把尽长安酒，不似在林间。\",\"傍花吟，随鹤舞，伴鸥眠。但得身闲无事，何用月长圆。少日功名富贵，老去江湖丘壑，出处道须全。丹心将皓魄，秋水共娟娟。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37966181071f67e3c','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"戏彩南衙日，篝灯北郭年。回首富春山色，几度梦依然。记得长安走马，携手琼林归醉，又隔廿年前。却惭黄阁老，相对柏台贤。\",\"念交承，论往昔，重惊怜。莫惮驰驱波岭，豸绣映青天。久识外台风采，行睹中丞节钺，开府大江边。甘霖滋大旱，巨楫济长川。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618107a2c8ab0','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"微飙生竹院，凉月照松关。避暑南宫仙署，藤枕石床安。望切琼楼玉宇，思入雪山冰壑，八极驭风还。雨添荷叶碧，露洗海榴丹。\",\"坐来深，香烬冷，漏声残。静看银河清浅，天阔水云闲。想象水晶帘箔，恍惚琉璃宫阙，潇洒玉壶寒。更欲骑黄鹤，吹笙近碧山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618108c275411','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"漫顶伊川帽，徒扣紫阳关。不见宫墙数仞，何处把身安。着眼岐路差错，举步草茅梗塞，古道竟难还。原无钻孔铁，空慕点颜丹。\",\"大音微，玄酒浊，太羹残。只为多添注脚，翻使六经闲。信口谈何容易，下手便成颠倒，都不疗饥寒。蹄涔诧沧海，蚁垤笑丘山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810966dae5a','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"飞锡还归定，蒲团且闭关。借问九年面壁，四大怎生安。都落黑风鬼国，那是慈航彼岸，谁手买珠还。我佛原无法，众生自有丹。\",\"一棒打，千相灭，万灯残。才被风幡勘破，寂寂镜台闲。水上莲花稳坐，芦叶江心径渡，圆影浸波寒。离诸烦恼障，尔是清凉山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3796618109da54eec','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"绿槐阴合匝，黄鸟语间关。檐外数竿修竹，日午报平安。朝退簿书多暇，睡起吏人初散，林暮鸟仍还。兴来时命酒，何暇问烧丹。\",\"夜堂深，凉雨后，烛花残。简点平生心事，云在意俱闲。浪说朝阳鸣凤，漫道中流砥柱，宝剑照霜寒。总为君恩重，当年别故山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810a766d7e2','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"我有先天气，日日透三关。走上昆仑绝顶，奔入绛宫安。飞洒鸦桥甘露，凝结玄珠黍米。八九互回还。风雷起中夜，龙虎护初丹。\",\"又何妨，铅汞老，药苗残。静里调停心息，呼吸自闲闲。出入原通橐龠，烹熏不须垆灶，水热火偏寒。兴来游八极，醉里度三山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810c4e2bb84','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"鸣雨歇深院，高门闭重关。虚室凉生枕簟，昼寝梦魂安。天外好气时至，林际断霞孤映，鸦带夕阳还。庭交莎草绿，砌拥蜀葵丹。\",\"暑气销，暝阴敛，角声残。起步檐楹徙倚，聊得簿书闲。却忆年时故里，曾唤小舟载酒，草阁枕江寒。月明歌绿水，云静看青山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810c73060dc','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"平皋松十里，紫气接函关。覆野乱枝如盖，上有鹤巢安。落日嵩云片片，秋水洛川渺渺，林黑雨龙还。昔闻广成子，曾此炼神丹。\",\"慨经罹，秦火后，六经残。我爱两程夫子，守道乐安闲。一自神龟呈瑞，天启中原文物，奎璧夜芒寒。白发司农老，筑室占青山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810d0f08d65','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"清霜飞画楝，柏府昼长关。炯炯台端孤月，林静夜乌安。我爱汉庭老吏，本是玉堂词客，江海召初还。冠峨金豸角，袍绣鹤头丹。\",\"酒杯深，檐花纤，剑歌残。几度笑谈今古，坐久藉余闲。总是杜陵高格，只有谪仙豪兴，郊岛太癯寒。知音谁大雅，文藻落江山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c379661810d46b78e3','水调歌头·答王浚川司马','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"汉兵朝出塞，胡马夜临关。咫尺云中烽火，羽檄满长安。幸赖君王神武，报道天声不杀，黠虏望风还。庙谟皆帝力，仁义炳如丹。\",\"是何心，甘战斗，恣贪残。忍使农耕荒却，机杼尽抛闲。堪叹沿边饶将，可惜连营劲卒，冻苦雪霜寒。果谁驱铁骑，直捣贺兰山。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37c516ae3c97a07d7','减字木兰花·壬寅正月六日，奉圣谕，今日皇四女试岁周，一取钟、二取银、三取黄水晶石，示卿等知。恭述此词，用纪嘉事','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"正元六日。西苑五臣同召入。喜溢龙楼。皇女今朝试岁周。\",\"详看御札。三取盘中皆吉物。去岁今辰。彩段宫花拜赐新。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a262b09937d','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"大河东下，走万里、包纳许多灵物。推荡中原无砥柱，那问全城半壁。静影涵空，洪涛浴日，巨浪翻银雪。蛟鼍出没，神龙羞与争杰。\",\"曾谁醉卧三山，飞游八极，独驾天风发。毕竟此河经万古，看尽人间生灭。老子胸中，久吞云梦，乌帽今华发。圆通朗照，心知天上明月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a262bc28b5f','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"源分天上，算黄河、不是人间之物。横亘中原三万里，失却乌江赤壁。雷雨掀腾，鱼龙变化，桃浪翻红雪。乾坤流峙，泰山堪与争杰。\",\"昔年奉使遐方，马首东风，见洛阳花发。诸将提兵淮海上，群盗未曾驱灭。关塞崎岖，风尘澒洞，梦寐惊毛发。太平今日，重来又是春月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a2631f0c934','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"一卧经旬，喜百事无营，只须药物。病起渺然思泽国，兴在黄楼赤壁。五老烟空，九华云静，涛涌三山雪。长江如堑，阅来多少英杰。\",\"却想江左风流，五陵豪贵，过眼春花发。屈指千年如一日，歘忽风休电灭。对镜慵看，倚楼长叹，一任添华发。寸心炯炯，清觞坐引秋月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a2648eb7c59','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"一撮峰儿，障长江、真是天生神物。海国江门涛阵涌，玉迭雄开两壁。孤柱乾坤，双擎日月，万古头如雪。地灵江左，从来几个人杰。\",\"玉堂天上仙翁，画里新词，彩笔云烟发。品隙人才高与下，勘破世间兴灭。二十年前，门生座主，不觉俱华发。丹心炳炳，敢负中兴岁月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a264dd92579','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"大河南北，数千里、一望中原民物。迤逦江山开锦幛，尽是名城坚壁。楚汉纷争，曹吴割据，战甲明霜雪。一时闲气，弄来有甚豪杰。\",\"青春三月南征，柳色连天，满路花争发。醉后彩毫吟丽句，不管古今兴灭。圣主中兴，太平有道，德泽沾穷发。何年沧海，钓竿时弄烟月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a26990a464a','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"铁豸峨冠，真御史、不负清朝人物。标格冰壶涵玉树，文彩上腾奎璧。骢马巡行，动摇山岳，长夏飞霜雪。大江西望，山川偏称英杰。\",\"独怜棨戟遥临，明年秋日，桂阙天香发。坐使英才都入彀，列郡氛尘销灭。老我非才，愧调商鼐，谩握周公发。玉堂杯酒，送君相对明月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a269beffb88','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"凤阁鸾台，受丝纶重寄，敢忘民物。欲挽江河无气力，空羡岩岩立壁。边塞烟麈，闾阎疾苦，赢得头颅雪。凭谁整顿，济时全仗才杰。\",\"天生圣主中兴，聪明神武，号令风雷发。赤日中天腾照耀，多少爝沉燐灭。补衮何功，调羹非手，勋业无毫发。太平有象，不妨吟弄风月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a26ae743c33','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"一种灵株，细摩挲、不似人间之物。偃蹇虬枝连密叶，风雨暗生墙壁。的的悬珠，汇景如贯，颗颗凝霜雪。交梨火枣，品题未许称杰。\",\"绝怜白玉盘行，黄金笼贮，瑶席清辉发。凉沁诗脾甘露爽，坐使襟尘消灭。玉女盆头，仙人掌上，直欲披玄发。夜深苍海，骊龙吐出明月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a26b02063e4','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"夜雨秋尊，新烹蟹、又见一年风物。秉烛西堂聊自酌，时听蛩吟四壁。满壳膏肥，双钳肉嫩，味胜经霜雪。遍尝海错，还输此种为杰。\",\"芳鲜充溢雕盘，真堪咀嚼，清兴翛然发。却笑持螯人去后，风韵到今难灭。更待黄橙，也须紫菊，取醉娱华发。中秋在望，小楼酌共看月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a26c028b8cc','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"昔试南宫，荷明公、收作药笼中物。彩笔玉堂干气象，瞻望文章奎璧。席上春风，尊前夜雨，几对程门雪。飞英振藻，一时真愧诸杰。\",\"独怜万里驰驱，三王六诏，使节连翩发。琐闼十年曾抗疏，旧草万行明灭。霄汉飞腾，风波震骇，白尽巾中发。长安今夕，芳樽且醉华月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c37fee7a26e83a8cfb','大江东去和东坡韵病起闲述','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"病渴相如，喜故人、遗此盘中珍物。却想碧山楼阁下，双桧幽亭西壁。满架明珠，一林苍玉，六月涵冰雪。移来绝域，张骞也是人杰。\",\"水晶颗颗匀圆，才教入口，两腋清风发。洗我胸中千磊块，万火一时俱灭。便欲骑鲸，乘风直上，手挽骊龙发。广寒宫里，冷然掇出秋月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3824bcea80fee4d7b','水龙吟·次辛稼轩，贺未斋阁老霖雨堂落成','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"名花坐对嫣然，玉杯满泛休停手。相府风光，帝城春色，可人依旧。真是天香，堪称国色，群芳魁首。喜夜深、银烛高烧，问红妆艳质，曾如否。\",\"檐外渐移星斗。况今夕、月明如昼。雀锦屏开，麟袍座列，朱衣庭走。占断人间，梨园法曲，黄封御酒。愿年年、共赏霖雨堂中，放歌为寿。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3824bcea83f27e1e1','水龙吟·次辛稼轩，贺未斋阁老霖雨堂落成','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"文章独占龙头，当年已识丝纶手。身历三朝，官登极品，当今耆旧。晚入黄扉，平生青眼，相看白首。喜长安、霖雨堂成，道神仙宰相，人知否。\",\"甲第高连北斗。又何必、锦袍明昼。昼寝凝香，朱门列戟，紫衣奔走。灯火楼台，笙歌院落，年年春酒。庇苍生、更作万间广厦，为相公寿。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3860455261e29c219','阮郎归·送族弟臣赴广德守','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"楼前杨柳雨初晴。长河新水平。南风五月彩舟轻。天涯送尔行。\",\"杨子渡，石头城。大江天际横。萋萋芳草不胜情。遥天一雁明。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c38604552623fc0406','阮郎归·送族弟臣赴广德守','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"雨过红云覆苑墙。龙池草色芳。隔花宫漏午偏长。人闲白玉堂。\",\"绿树静，碧荷香。葵心赤向阳。五弦天上舜琴张。南薰殿阁凉。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c387c91ea286ba03db','法驾导引·曲上赐金符金仙人银太平牌应制三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"从天降，从天降，宛宛两仙人。万炼黄金开面目，九还玉液铸精神。\",\"白日现真身。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c387c91ea2ad0490a0','法驾导引·曲上赐金符金仙人银太平牌应制三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"礼大道，礼大道，太上降真符。琼液丹清龙虎篆，紫泥金碧凤鸾书。\",\"鸿宝锡天衢。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c387c91ea2fd3cfb55','法驾导引·曲上赐金符金仙人银太平牌应制三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"蒙恩赐，蒙恩赐，银傍太平牌。万象同春迎百福，四方如水应三台。\",\"寿域自天开。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3888ea5cb331e3628','越溪春·送吴少参希盂乃尊院使公还毗陵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"阳谷仙翁沧海客，踪迹满天涯。御榻当年曾入梦，赐袍带、名动京华。白首辞官，青云有子，拂袖还家。\",\"天风吹上星槎。春日别烟霞。金尊银烛长安夕，玉堂里、坐对檐花。我欲买田阳羡，与翁同炼丹砂。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3888ea5cb6a7bd4e8','越溪春·送吴少参希盂乃尊院使公还毗陵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"紫袍金带归来早，高卧旧江乡。羡汝东溪溪水上，风烟好、乔木苍苍。隔断红尘，坐移白日，一曲沧浪。\",\"长安数载相望。秋日送归航。我欲留君君不住，故园远、乡话偏长。无限青山怀抱，空惭白首岩廊。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3895c768359af42f0','卜算子·次苏子瞻韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"明月满长空，云散江天静。缥缈宾鸿江上来，秋色涵孤影。\",\"顾影忽长鸣，幽怨凭谁省。渚暗芦花梦不成，月落霜空冷。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c38be4505d67a08964','齐天乐·夏日阁中','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"层城尽日宫云覆，高阁靓深无暑。埤竹阴清，垣梧影静，昨夜凤池新雨。香凝燕寝，喜愠解朱弦，凉生白苎。时拂锦笺，彩毫随意赋诗句。\",\"自念寒生一介，平步陟丹霄，遭逢英主。渭水纶竿，萃郊耒耜，何必当年伊吕。有怀自许。但勋业无成，光阴易去。揽镜婆娑，听梁间燕语。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c38d3e19f8c3d749a6','碧空月词寿周泉坡司寇六十','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"汉廷真老吏，中台旧豸冠。威仪风节重朝端。自是庙堂器宇，足人看。\",\"鬓带星星白，胸藏炳炳丹。六旬寿酒醉长安。正值碧空霜月，净团团。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3909e35b1ecd5124d','贺圣朝·影柬李蒲汀约饮碧山楼','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"东邻学士正青春。脱风尘。碧山终日坐朝昏。足闲身。\",\"秋风吹散西山雨，月华新。葡萄架底醉醺醺。共何人。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39198838d31f06a13','鹤冲天·初夏二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"临水阁，倚风轩。细雨熟梅天。一池新水碧荷圆。榴花红欲燃。\",\"薄罗裳，轻纨扇。睡起绿阴满院。曲阑斜转正闲凭。何处玉箫声。\"]',NULL,NULL,NULL,'[\"鹤冲天：词牌《喜迁莺》的别名，起于唐人，《太和正音谱》注\\u201C黄钟宫\\u201D。双调四十七字，前段五句四平韵，后段五句两仄韵、两平韵。因韦庄词有\\u201C争看鹤冲天\\u201D一句而留下《鹤冲天》的别名，读者宜注意其与正格双调八十四字的《鹤冲天》词牌之间的区分。\"]'),('e647d10fc8d190c39198838dc986fdb0','鹤冲天·初夏二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"黄梅雨，绿杨风。徙倚画楼东。疏疏槐影映帘笼。蔷薇满架红。\",\"小池台，深庭院。正是日长人倦。碧纱厨里睡忺时。流莺枝上啼。\"]',NULL,NULL,NULL,'[\"鹤冲天：词牌《喜迁莺》的别名，起于唐人，《太和正音谱》注\\u201C黄钟宫\\u201D。双调四十七字，前段五句四平韵，后段五句两仄韵、两平韵。因韦庄词有\\u201C争看鹤冲天\\u201D一句而留下《鹤冲天》的别名，读者宜注意其与正格双调八十四字的《鹤冲天》词牌之间的区分。\"]'),('e647d10fc8d190c39221875f30125754','鹊桥仙·中秋白鸥园赏月，次陈大尹三首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"丹桂婆娑，银蟾晃漾，蓦地渐升沧海。看到中天万国明，谁道是、素娥风采。\",\"玉宇琼楼，龙池凤阁，相伴清光廿载。今夕山中月更圆，似相识、当年元宰。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39221875f4c1997d9','鹊桥仙·中秋白鸥园赏月，次陈大尹三首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白首辞天，锦袍入里，离却茫茫宦海。碧岭堪樵，沧波宜钓，更篱下、黄花可采。\",\"麋鹿为群，鹭鸥作伴，林卧忽惊三载。赢得身闲百不忧，万事有、天公主宰。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39221875f5437af9f','鹊桥仙·中秋白鸥园赏月，次陈大尹三首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"横翠亭前，望宸楼下，秋水澄清彻底。此夜蟾光分外明，照大地、平铺如纸。\",\"彩笔题诗，清尊对客，画妨笙歌盈耳。却忆长安几度看，那得似、青山故里。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39221875f744cd624','鹊桥仙·中秋白鸥园赏月，次陈大尹三首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"宝鉴初呈，冰轮乍辗，迤逦光生池面。记得西岩欲堕时，喜今夕、来年重见。\",\"一曲霓裳，千年玉斧，谁向广寒游遍。十二琼楼皓魄圆，应独照、丹心一片。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39221875fc1afc229','鹊桥仙·中秋白鸥园赏月，次陈大尹三首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"杨柳烟疏，芙蓉露冷，丹桂香风扑面。池上芳尊向夕开，喜千里、故人相见。\",\"金缕高歌，霓裳慢舞，宛转六幺初遍。何处清砧响更哀，都并作、秋声一片。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39221875ff38f1eda','鹊桥仙·中秋白鸥园赏月，次陈大尹三首','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"草阁观书，兰舟载酒，尽日竹间花底。二客新从海上来，忽投赠、云烟满纸。\",\"山谷新词，杜陵好句，瑶瑟清音振耳。可惜中秋节后逢，不共对、月明千里。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c392c0f63b9b4b6ccf','浪淘沙令二阕（其一）','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"世事信难凭。堪叹人情。一团笑里喑藏兵。只为区区微得失，丧了平生。\",\"日夕苦蝇营。开阖纵横。等闲蜗角便相争。开口向人谈义利，那个分明。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f641c0b8603','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"转眼上林春色到。洗耳春雷一声早。文章自信有家传，为君预卜龙头兆。\",\"灵鹊双巢林阁杪。巢成似出天工巧。愿得生儿拟凤毛，凤池异日仍衔诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f64631d9789','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"雪湖湖妨乘春到。渺渺春波江上早。彩毫和我玉楼词，为贺宫庄双喜兆。\",\"迢迢北上云天杪。长安春日莺声巧。朝士相逢问野翁，山林感荷君王诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f6463bb1726','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"有约期君君不到。惜春惟恐春归早。旧雨遥怜隔岁欢，清词并入新年兆。\",\"江上云连春树杪。鹊声日日檐端巧。何日来同池上游，闲情莫问天边诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f64658d0b3b','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"西园日日烦君到。喜信仍能报吾早。翚翼应占莞簟祥，鹊巢自是熊罴兆。\",\"仙楼高出云林杪。每藉经营心力巧。会当荐尔隶司空，岁晚还膺天上诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f647f895ca6','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"沐恩堂上春重到。隔屋张筵相过早。南楼高与北楼齐，西家喜接东家兆。\",\"灵鹊来巢华栋杪。连营并构非人巧。预占跨灶足传芳，更报乘龙先应诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f64e1a5d9ee','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"小园昨夜东风到。雪里梅花开放早。华堂灯火庆元宵，宫庄又喜添佳兆。\",\"高楼画栋支林杪。灵鹊双双结巢巧。定知麟阁产麟驹，更看凤池传凤诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c393ee4f64e61cb802','玉楼春·宫恩庄上元日，鹊营四巢于楼栋志喜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"元宵准拟翁先到。宝泽楼前试灯早。春酒初看春瓮浮，新庄喜见新年兆。\",\"灵鹊飞来云栋杪。两两巢成相对巧。愿学徐卿引二雏，敢思汉殿传三诏。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d061f2fb8','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"衡岳湘川，产名公、真是玉堂人物。紫禁彤庭携手旧，何日同游赤壁。镜里朱颜，花前青鬓，又早巾中雪。非才宜退，匡时赖有英杰。\",\"自从身入黄扉，仰惭白日，归兴时时发。彩笔新词劳远寄，满纸云烟明灭。花甲初周，世途饱历，长笑空搔发。钟陵南望，故人千里明月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d15de888a','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"砥柱乾坤，镇长江、万古中流独立。横遏惊涛排骇浪，直负擎天巨力。吞吐云烟，奔腾月日，壮观东南壁。三山海上，孤根不与同植。\",\"蛟宫贝阙玲珑，海色浮空，日抱鼋鼍出。人代光阴如一瞬，只有沧波不息。吞海亭前，江天阁上，目送飞鸿翼。振衣长笑，墨笔醉洒岩石。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d2e88c0a1','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"水落蒹葭，白露冷、长养横行之物。夜雨疏灯照蜗涎，晓起枯黏满壁。物色堪嗟，人情可慨，愁鬓添新雪。飘然霞外，神仙真是豪杰。\",\"故园酒熟鸡肥，相对青山，有约黄花发。但得天恩容拂袖，眼底风波自灭。宠辱闻惊，是非妄恼，胸次无毫发。寸心皎皎，争光天上秋月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d37a7c78c','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉削湘筠，藤缕细、巧手掣成名物。多谢山人遥遗赠，列置高堂四壁。清映水帘，凉分石枕，助炎天霜雪。银屏绣榻，看来未算雄杰。\",\"有时徙倚轩窗，听雨听风，幽兴偏多发。坐对灵峰七十二，朝暮云烟兴灭。携向园亭，白鸥相伴我，披襟散发。南楼弄笛，不用胡床待月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d46245264','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"新春前后，见三白、真是皇家瑞物。万里江山同一色，晃漾银城铁壁。田父增欢，都人咸喜，相庆今年雪。琼楼玉宇，望中掩映雄杰。\",\"长安景色堪夸，指瑶林琪树，依稀梅发。寂寂千山飞鸟静，野径人踪稀灭。素夺鸥群，皓迷鹤屿，翠失长松发。元宵节近，交光应待明月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d468dd24e','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉光阁上，凭阑处、下有蜿蜒神物。窈窕洞天烟雾里，深锁千岩万壁。鸟埭云间，龙渊石峭，虬峡涛翻雪。山人占断，江左地灵人杰。\",\"凌虚台倚清冥，碧树沧洲，高兴时时发。极目斜阳飞鸟外，杳雾断霞明灭。缥缈亭边，回阳洞口，长笑披玄发。玉梁倒影，古潭冷浸秋月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d4a0ff265','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"百尺危楼，面阳峰、览尽楚天云物。西望武昌东夏口，遥对黄州赤壁。清簟疏帘，投壶散帙，炎夏生冰雪。玉堂惟构，未许元龙称杰。\",\"读书夜感青黎，画楝雕檐，星斗寒光发。秋水长空涵雁影，时见断霞明灭。学士当年，倚栏凝睇，把镜悲华发。丹心岁晚，咫尺身依日月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d4cd34dc1','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"海岳道人，向白云深处、静中观物。百尺瑶台清似水，仄倚丹崖翠壁。台下梅花，虬枝铁干，点缀千年雪。三山咫尺，神仙原是人杰。\",\"老我而今自归来，天上豪兴时时发。笑傲烟霞，看人间、蠛蠓瓮中生灭。白石樵翁，沧波渔父，相伴俱黄发。蓬莱小阁，相对灵峰山月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d6b432197','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"秋意满前，正衔杯、又见江乡风物。同巷高人应念我，伏枕萧条四壁。许大忧怀，无边归思，捻断髭须雪。自怜迂叟，岂堪相伴朝杰。\",\"去岁今辰，想莼羹鲈绘，高兴徒发。几度长空南望，尽雁字千行明灭。揽镜堪悲，壮颜非旧，秃尽冲冠发。危楼昨夜，坐到西岩堕月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d6f78dda0','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"南极老人，龟鹤鹿、都是长生之物。范取精镠成妙质，罗列锦屏东壁。朱李沉冰，红桃绚日，瓜剖雕盘雪。黄金爵驻，寿山千仞高杰。\",\"玳筵水陆交陈，银烛瑶觞，满座笙歌发。帘动水晶风乍入，不觉炎氛销灭。杯饮长春，堂开初寿，乌帽笼玄发。佳辰欢对，凤楼东畔凉月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d7bc61b9c','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"瑞鹤灵龟，双白鹿、谁献寿筵珍物。一簇笔歌围锦障，杂遝南厢西壁。黄阁元公，春卿太宾，玉质涵冰雪。周廷朋寿，不数汉家三杰。\",\"麟袍蟒绣辉煌，玉带联翩，高兴尊前发。银箭不闻更漏永，红烛千行明灭。却笑吾生，今朝初度，鬓有星星发。年年同醉，管领壶中日月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d973e60e1','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉阳山水，入丹青、真足世间奇物。曲曲山房围八卦，四面苍崖翠壁。峭峡穿云，回潭浴日，绝壑藏冰雪。洞天六六，此中堪与争杰。\",\"画图遥谢山人，妙品新词，把玩光辉发。祝我与山长不老，阅尽万缘生灭。玉女潭深，白鸥池静，照见飘萧发。寸心耿耿，两乡千里明月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6d9d739f35','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"小坐秋轩，谢蒲翁、遣赠名园佳物。自启筠笼唤纤手，携置麟堂东壁。碧水含晶，繁星焕彩，寒映冰盘雪。如珠似玉，果中应是魁杰。\",\"总忆年时咏葡萄，一体新词三发。几对黄花烹紫蟹，久悟世间生灭。汉阁丹青，商山杖屦，毕竟俱华发。清尊架底，且醉藤萝夜月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6dc1715247','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"大药难求，看人间、那有长生之物。沧海桑田经几变，说甚乌江赤壁。麟阁图形，燕然勒迹，博得头如雪。古今代谢，几多贤圣豪杰。\",\"小子今日生辰，醉后狂歌，聊为诸公发。富贵功名都是梦，只有芳名不灭。男子头颅，丈夫意气，要有冲冠发。寸心耿耿，冰壶凉沁秋月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c394af3f6de787c1e0','大江东去庚子初度，石门少傅、松皋太宰、介溪宗伯治具来贺，即席和答二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"水晶宫里，撒明珠、散作园林珍物。恍若老龙行雨罢，倦卧藤轩西壁。影乱潭星，光分海月，清照瑶峰雪。碧山楼下，主人真是人杰。\",\"摘来玉乳累累，把赠题缄，每为比邻发。况复年年劳赋咏，满纸墨华明灭。丘壑情深，江湖忧在，相对惊须发。如公经济，未许平章风月。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3975576af73e0797e','忆旧游·送淳安邵主簿号藻川','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"记石佛岩前，乌龙山下，王氏新庄。柴门临水曲，有一林修竹，数亩池塘。几共萧萧夜雨，坐对短檠光。叹如流岁月，从头屈指，四十经霜。\",\"相望。念先公，有遗祠尚在，往事堪伤。惜藻川旧学，试问淳一簿，白首为郎。却愧纡朱拖玉，素食坐岩廊。爱富春山色，桐江烟水近吾乡。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39975e554daab9c72','减字木兰花·送陶御史钦夔按滇南','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"行行且止。骢马人看真御史。劲翮凌空。一鹗秋横万里风。\",\"昆明池上。春日旌旗同大将。太华峰头。醉后曾将翰墨留。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39cdeed317f608286','锦堂春次未斋韵，送陆侍御瑚便道寿母','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"腊日慈颜初度，绣衣北阙驰情。凤凰台下烟花簇，计是过家程。\",\"鹤发鸾章日映，锦堂绿酒春生。雪毫写罢江南曲，紫阁正平明。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39cdeed31b24dda03','锦堂春次未斋韵，送陆侍御瑚便道寿母','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"太液池冰欲泮，迎和门柳初柔。内家园苑春光早，花满凤凰楼。\",\"风暧金鳌桥畔，日高万岁山头。五云深锁红尘隔，随处是瀛洲。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39e08c06ca3edec2a','瑞鹤仙·内苑醮坛鹤降应制','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"碧空云敛尽，正晚霞、红映凤楼东角。星坛朝谒罢，方诵彻琼章，乍呜天乐。翘首睇层霄，飞来玄鹤。驾灵风、万仞翱翔，两两盘旋寥廓。冲漠。\",\"鉴我皇诚，昭兹圣孝，祥繇善作。九天现瑞，这感应，如然诺。想虚空、宝幢羽盖参差，导引仙禽依约。帝教迎取，二圣回銮。紫宫快乐。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39f67f4b926b9adfd','法驾导引·曲辛丑夏，仁赛宫醮坛鹤降，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白鹤舞，白鹤舞，云翔恣蹁跹。瑶水乍离王母畔，霓裳先到玉皇前。\",\"凤苑集群仙。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39f67f4b93a653914','法驾导引·曲辛丑夏，仁赛宫醮坛鹤降，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白鹤举，白鹤举，振翮驾天风。周览六虚升紫府，直从三岛陟玄空。\",\"白日过崆峒。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39f67f4b9430f90fd','法驾导引·曲辛丑夏，仁赛宫醮坛鹤降，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白鹤导，白鹤导，仙仗拥天衢。翠羽翩翩九芝盖，霓旌渺渺五云车。\",\"遥下太清居。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39f67f4b951c1e413','法驾导引·曲辛丑夏，仁赛宫醮坛鹤降，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白鹤下，白鹤下，遥自九天来。圆顶丹砂千岁火，修胫紫甲万年苔。\",\"玉骨本仙胎。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c39f67f4b9964a8cc9','法驾导引·曲辛丑夏，仁赛宫醮坛鹤降，应制五阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"白鹤唳，白鹤唳，明月炯清霄。响人高云凝碧落，吸来凉露滴松梢。\",\"缑岭玉笙遥。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3a0eb21b397bfa3ab','喜迁莺·赠汪东峰中丞巡抚江西','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"冠峨豸角。暂坐辍中台，行临方岳。虹井烟霞，东峰花鸟，江海十年离索。青琐先朝交契，白首此时欢谑。对尊酒，正枥马初喧，骊歌又作。\",\"抛却。念今日，处处民穷，不见闾阎乐。洗涤疮痍，挽回醇俗，须仗大贤经略。铁柱浮渊旧梦，画栋飞云高阁。大江畔，恐节钺难淹，庙堂有约。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ad903568245cd3ab','玉楼春·次周美成','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"何年不踏长安道。整顿乾坤济时了。水边时伴鹭鸥群，竹里日教宾客到。\",\"南园日暖寻芳草。坐爱溪荷摇翠葆。日长无事即神仙，未信朱颜容易老。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ad9035686ffb16c0','玉楼春·次周美成','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"霖雨堂成增气象。麟袍敕赐来天上。共羡龙头老状元，雅称凤池真宰相。\",\"鹓班著处人瞻仰。衣被新恩夸浩荡。更看高阁画仪形，要使苍生谐属望。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3b0b7b76ad54d451c','醉桃源送黄四府之黄州','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"黄州西望楚山青。楼头秋月明。冰溪十里送君行。遥通湘水清。\",\"新雁过，晚蝉鸣。撩人离思生。春风谷口听迁莺。重来慰我情。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3b69eedab4e7fbf09','绮罗香·和答李蒲汀','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"雨过盆池，风吹荷叶，绝胜银塘幽意。几朵仙葩，绰约杨家姊妹。斗红妆、映水朱妍，洗素容、凌波玉腻。小亭中、琥珀杯浓，相看不觉同花醉。\",\"却羡东邻老子，散发碧山楼，顿添芳思。自谱新词，一曲与花争媚。恐西风、容易惊秋，对南薰笑歌休弃。况光阴、暮景如驰，只索牢牵辔。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3b86896a633de6e40','画堂春·春思','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"春来长是为春愁。无端脉脉悠悠。萋迷芳草遍汀洲。怕上高楼。\",\"千里碧云南望，一湾绿水东流。与谁约买木兰舟。载酒同游。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3b996a62e1bc8d4c7','满江红·次东坡韵，题严陵钓台','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"岸柳窥人，东风里、似曾相识。指长堤、朱箔青楼，还知旧日。前度刘郎今又到，落花流水成陈迹。但萋萋、芳草满江湄，清如织。\",\"风月夜，思畴昔。云雨梦，无消息。正一雁南来，今夕何夕。仙鸟不传云外信，谁家苦弄风前笛。倚蓬窗、搔首对黄昏，空追惜。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3b996a62e79b3f708','满江红·次东坡韵，题严陵钓台','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"七里滩头，几度过、渔矶傍侧。爱江乡、山阔云闲，水清沙白。蓑笠当年甘寂寞，仪容此日增颜色。激颓风、轩冕等浮尘，先生力。\",\"故人意，君王识。天度大，凡襟窄。只足腹相加，几人容得。深浅交情贫富见，古今人品天渊隔。叹江湖、落落少相逢，知音客。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3b996a62e984dfe75','满江红·次东坡韵，题严陵钓台','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"象麓草堂，何处在、白芒洲曲。有负郭东泉，当年书屋。碧岭千层盘翠黛，清溪一带流寒玉。对萋萋、芳草一川平，烟中绿。\",\"南山下，东篱菊。北窗外，西潭竹。静隔断红尘，那知荣辱。少日尽多林壑趣，中年游遍天涯足。到于今、把世态人情，看教熟。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ba28c1bb1c808cf6','法驾导引·曲纪上祀方泽三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"方丘上，方丘上，午夜御香浮。列宿光依龙衮静，明蟾影逐翠华流。\",\"爽气似高秋。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ba28c1bb5e739d52','法驾导引·曲纪上祀方泽三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"朝泰折，朝泰折，玉辂自天临。五岳百灵齐献瑞，川侯海伯尽输琛。\",\"后土鉴皇心。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ba28c1bb6c089826','法驾导引·曲纪上祀方泽三阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"六龙驾，六龙驾，晚出凤凰楼。辇路无尘甘雨霁，海波不动卿云流。\",\"天子祀方丘。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3baa89a59b5008f9a','汉宫春·壬寅元夕上召观灯仁寿宫','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"醮罢星坛，正华灯满架，宝炬盈盘。高下彩霞摇曳，珠斗斓斑。黄金台殿。光掩映、白玉雕栏。有中使、传宣丹诏。\",\"赐臣特地来看。感激皇恩浩荡，念几人能得，天上奇观。况是阳春降泽，玉帝回銮。祥光瑞色，护九重、宝月团团。喜今年，太平嘉庆，良宵上奉宸欢。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3bf21c8ecaaa18645','浪淘沙·令二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"镇日抱瑶琴。独坐沉吟。出门何处觅同心。弹尽高山流水调，没个知音。\",\"岁月苦侵寻。华发盈簪。迂疏原合住山林。只为君恩深未报，惆怅而今。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c2b9f8f114e67064','喜迁莺·三首次康伯可贺费清湖初度','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"韶年淑气。正梅吐寒香，柳含春意。老稚欢呼，闾阎歌颂，白昼江城鼓吹。翘首中天昨夜，南极一星吴瑞。朝来报、道宴启黄堂，悬弧佳会。\",\"堪记。羡使君，年少飞腾，五马人生贵。彩服怡亲，青云有子，满眼天伦乐事。已睹龚黄政迹，伫看夔龙遭际。他年纵、老手挥毫，大书遗惠。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c2b9f8f185528241','喜迁莺·三首次康伯可贺费清湖初度','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"凉生秋早。正七夕才过，清湖日晓。白苎衣轻，青藜杖稳，林下又添一老。从教岁月优游，休管江湖廊庙。任傍人、说束帛重征，蒲轮再召。\",\"表表。羡龙驹，海鹤精神，屹立如嵩少。春酒开筵，锦袍归里，增得寿星光耀。况是功名到手，满眼向平愿了。但醉来、对葛岭天高，鹅峰月小。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c2b9f8f196b46395','喜迁莺·三首次康伯可贺费清湖初度','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"天空海阔。正一鹤南飞，高翔碧落。风卷沧溟，云收岛屿，万里烟涛喷薄。多少蛟鼍出没，满眼鱼龙变作。却争如、任劲翮飞腾，盘回廖廓。\",\"难学。羡先生，道骨仙风，缥缈人间鹤。明月三山，寒云五岭，几度秋风庐岳。八极遨游将遍，应入太清女漠。愿他年、一品当朝，画图忆昨。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c2c00ba624f1a1ea','御带花寄南京杨四泉光禄','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"昔日少年今渐老，追忆旧游，而今难得。朱雀桥边，乌衣巷口，前朝旧宅。秦淮畔、走马听莺，几醉笙歌月。二十年浑似梦，尘土风波，尽曾经历。\",\"画省黄扉，但碌碌无庸，枉教头白。江左故人，时寄我、锦笺盈尺。却怜几个同心，又隔天南北。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c7b64f5a71430036','摸鱼儿·次晁无咎韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"忆少年、作客洪都，几度西山南浦。从倚滕王高阁上，落日风帆遥聚。凝望处。爱匡庐、烟中瀑布如川注。鸟歌花舞。向孺子孤亭，云卿旧宅，时复携游去。\",\"悔当时，错把蟾宫高步。荏苒青春虚误。朱衣玉带黄金殿，忘却丹丘玄圃。笑匆匆、绿鬓巳成丝，弹指能几许。春来自语。且酌酒花前，放歌溪上，不管青山暮。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c88daa0bbc6cc9ab','菩萨蛮·回文秋夜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"落膘寒汀烟漠漠。漠漠烟汀寒膘落。流水野塘秋。秋塘野水流。\",\"露幽藏古树。树古藏幽露。明月伴人行。行人伴月明。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c88daa0be9140b66','菩萨蛮·回文秋夜','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"碧空秋浸凉云夕。夕云凉浸管空碧。微月澹辉辉。辉辉澹月微。\",\"露含花湿雾。雾湿花含露。幽阁傍萤流。流萤傍阁幽。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc2a538d25','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"思悠悠。梦悠悠。多梦多思并入秋。相看人白头。\",\"蘋花洲。蓼花洲。红蓼白蘋生远愁。凭高独上楼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc3d2c06c5','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"天悠悠。水悠悠。天水相连万里秋。佳人楚水头。\",\"忆沧洲。隔沧洲。渺渺烟波生暮愁。那堪独倚楼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc4f9d6085','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"荼蘼香。蔷薇香。庭院轻风白昼长。人闲春草芳。\",\"望潇湘。隔潇湘。天远楼台人一方。相思萦寸肠。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc51c056c3','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"情悠悠。事悠悠。往事多情不耐秋。无端白了头。\",\"花满洲。草满洲。草暗花明总是愁。衔杯懒下楼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc7e912dd9','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"山悠悠。水悠悠。水远山长湘渚秋。衡阳天尽头。\",\"风满洲。雨满洲。细雨斜风生野愁。休登江上楼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc838d61ad','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"黄鹂啼。晓梦疑。迢递江南人未归。春愁双鬓知。\",\"雁南飞。乡思迟。长忆江村细雨时。闲看鸥鹭矶。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc8dfd6e3c','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"晓盈盈。玉池萍。老鹤松梢惊露鸣。石坛花气清。\",\"泉泠泠。绕涧声。山鸟相呼如引朋。道人幽兴生。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dc8e81a517','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"风萧萧。雨萧萧。独坐虚堂夜寂寥。诗成蜂蜡销。\",\"山迢迢。水迢迢。回首江南归梦遥。频将短鬓搔。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dcadc3a721','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"岸悠悠。水悠悠。枫叶芦花瑟瑟秋。孤帆天际头。\",\"白鹭洲。鹦鹉洲。一带澄江销客愁。醉登黄鹤楼。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dcb8d84e61','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"水盈盈。一池萍。晓雨初收林鸟鸣。园亭秋意清。\",\"风泠泠。戛竹声。独鹤孤云为友朋。道心随处生。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3c89c60dcf5f17d24','长相思·和浚川韵六阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"杜鹃啼。君莫疑。日日思归未得归。乡心有梦知。\",\"鸿雁飞。音信迟。又是黄花白露时。西风吹钓矶。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3cc6b686c0cc64f8a','万年欢·和答白下史恭甫庆予六十之作二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"千古高情，爱王氏堂槐，陶公门柳。说甚补天扶日，只凭赤手，廿载龙颜左右。嗟绿鬓、而今白首。喜年华、花甲才周，正是还山时候。勋名何有。\",\"看世间、几个诗人，也堪不朽。留取精神，做太平迂叟。春酒年年为寿。旋采摘、野肴山蔌。更浩歌、一曲沧浪，长舆溪翁为偶。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3cc6b686c5d5367de','万年欢·和答白下史恭甫庆予六十之作二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"昨夜西堂，见南斗光中，文昌辉映。晓启泥缄，锦轴瑶篇称庆。念子清才谁并。真江左、诗仙草圣。惜壮年、早辍经纶，惭愧调元造命。\",\"托根自正。原不比、和露夭桃，倚云红杏。傲雪青松，独立烟霞僻境。千里同声相应。况高谊、此时堪敬。寄心期、玉女峰前，约访孤山和靖。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3cd8d76baaa6e978d','法驾导引·曲书葛仙飞升台','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"丹崖上，丹崖上，立壁倚青冥。赤雾蒙蒙迷翠壑，白云杳杳漾沧溟。\",\"岩下走雷霆。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3d107e84877bcc87c','瑞鹤仙·贺吴宜人七十员外春母也','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"南山开锦绣。尚菊有黄花，芙蓉簇秀。日映华堂，锦席帘幕，重重晓寒初透。宾鸿来后，恰才过、小春时候。正墙头、梅蕊舒琼，香入玉堂春酒。\",\"那有。七帙宜人，彩衣满眼，佳儿佳妇。今辰届寿。喜朝来，青镜里，绕珠翠，乍添鹤发丝丝，转觉朱颜胜旧。看云霄、三桂联芳，孙枝齐茂。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3edfa5626ad028b81','南山寿贺吴山泉宪副六十','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"望南躔一星，光映三阶。有仙翁、紫袍金带，佳辰海上筵开。数流年、才周花甲，论风采、独步霜台。晚岁功名，诸郎科第，占却人间五福偕。遥瞻祝、青天岱岳，翁寿并崔巍。还歌咏、日升川至，山有台莱。\",\"羡吾乡、苏山甘水，钟灵辈出奇材。念当年、东山灯火，幸此日、北阙风雷。契重金兰，情深葭玉，廊庙江湖共此怀。念同庚初度，与翁不得共衔杯。伫看取、乔迁赴阙，丹诏飞来。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ef459f894b214a94','千秋岁·贺渐斋生辰','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"西山雨后。枕簟新凉透。华月满空如白昼。锦堂明绛蜡，瑞蔼飘金兽。听金缕，千秋一曲为公寿。\",\"六七丈天儿，不数燕山宝。长安夜，今朝酒。莫惜醉如泥，杯到休停手。如公少，人间五福兼而有。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ef459f89b224d789','千秋岁·贺渐斋生辰','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"南山秋老。黄菊都开了。节近重阳天气好。金尊浮绿蚁，玉鼎熏龙脑。蟠桃熟，西池王母来青鸟。\",\"丹桂两枝芳，兰茁灵芽早。称家儿，真国宝。共羡太夫人，福履人间少。宴蓬莱，海山日上扶桑晓。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3f2ccf4428d1973fb','念奴娇·人日答李蒲汀，用山谷韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"东风忽到，问天涯、芳草渐生新绿。白发又添人一岁，何日向平愿足。晏下彤闱，明登紫阁，谩衣朱腰玉。何如画舫，春江满载醽醁。\",\"帝城迤逦风光，宝楼西畔，淡月笼高木。简点灯前十年事，此夜一杯谁属。入岁占云，经冬望雪，倚遍危栏曲。朝阳彩凤，几时飞上庭竹。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3f2ccf4428ed8da31','念奴娇·人日答李蒲汀，用山谷韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"南楼独倚，悄无人、唯见五湖烟绿。桂树香生吹欲下，疑是九天零粟。万里无尘，长空一色，处处袁安屋。数声羌笛，青鸾飞在庭竹。\",\"人在璚楼玉宇，指点江山，几处飞泉瀑。安得沧江都是酒，洗我愁思千斛。天将老我，鹤发成仙，月下跨黄鹄。嫦娥今夜，共谁谈笑如玉。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3f2ccf4429f6aeeaa','念奴娇·人日答李蒲汀，用山谷韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"隔岁佳期，向此夜、试把金尊斟绿。据我胡床三弄笛，簌簌桂华零粟。光满璚楼，寒生银瓦，是处神仙屋。虚檐影转，坐夜风动庭竹。\",\"露华冷浸衣巾，银潢低泻，倒挂长空瀑。何处萧声惊鹤梦，涤尽尘襟千斛。步雪归来，乘风飞去，底用骖鸾鹄。广寒宫殿，个人应念如玉。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3f2ccf442c5a312f1','念奴娇·人日答李蒲汀，用山谷韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"朔风一夜，搅霜空、丹却千林寒绿。帘卷碧山楼阁静，镇日看山未足。我羡高人，萧然林洞，鹤骨清如玉。园收芋栗，拥炉时泻醽醁。\",\"严冬物色荒凉，欹枕萧萧，落尽燕山木。长忆东邻闲学士，那得一尊相属。病起挥毫，朝回隐几，漫写江南曲。柴门溪上，犹锁万竿修竹。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3f2ccf442c846670d','念奴娇·人日答李蒲汀，用山谷韵','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"素娥风采，怡精神、万顷琉璃深绿。狂把金尊眠未得，管甚玉楼寒粟。共讶清光，万间同照，谁更歌茅屋。鳷鹊无依，三绕一庭修竹。\",\"醉后青眼摩挲，清流万丈，看银河横瀑。记得嫦娥曾许我，金粟秋香千斛。水晶宫里，环佩声清，不用乘黄鹄。明年此夜，谁共一轮寒玉。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3f7d8ead3e6c3ceac','桂枝香·次荆公韵，送费钟石少宰之南都','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"长空极目。见几度冥鸿，南飞肃肃。剧想钟陵山色，烟霞如簇。石头城外长江水，趁西风、乱帆遥矗。杨柳秦淮，蒹葭鹭渚，旧游未足。\",\"念十载、红尘驰逐。叹松菊幽盟，易寒难续。白发盈巾，总为是非荣辱。桂树洲边乡水畔，但芳草、年年增绿。何时携手，归钓水溪一曲。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3fa230fb692aa8e1c','浣溪沙·壬寅正月十六日见东宫踏冰过金海作二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"高阁春云护九重。日华晓色映笼葱。新年今日见东宫。\",\"龙凤姿容天日表，圣神根器帝王风。老臣欢喜万方同。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3fa230fb6a2528726','浣溪沙·壬寅正月十六日见东宫踏冰过金海作二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"迎和门外日初晴。玉树琼楼照眼明。胡床隐坐度层冰。\",\"此日人间占瑞雪，昨宵天上看华灯。朱衣凌晓禁中行。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3fb56367a037cf5ea','少年游·壬寅端午柬李蒲汀二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"多谢山翁，园收瓜豆，馈我过端阳。饭煮香梗，酒清浮蚁，风物似吾乡。\",\"无奈感时添客虑，欲语向谁行。入阁平明，挥毫尽日，心口自思量。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3fb56367a10b973b0','少年游·壬寅端午柬李蒲汀二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"旧筑草堂，周遭碧水，宛在象山阳。二十四岩，花明竹媚，真个是仙乡。\",\"洲渚玲珑江草碧，鸥鹭自成行。静倚吟筇，闲移钓艇，万事罢思量。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3fb56367ab0a863e6','少年游·壬寅端午柬李蒲汀二阕','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"蒲泛金觞，黍包玉粒，节序又端阳。草阁临流，龙舟竞渡，几度梦江乡。\",\"却笑衰翁长卧病，犹自恋朝行。经国无能，君恩未许，终日费思量。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ffdf22b875b9d1d2','风入松·次虞文靖公韵，赏阁前芍药','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"玉阶红药弄晴酣。高阁对华簪。朝回便是看花处，雕阑畔、不著游骖。应诏频挥彩笔，承恩叠赐罗衫。\",\"凤池新雨水如蓝。梁燕昼喃喃。唤醒一枕还乡梦，情脉脉、千里空缄。何日乞身归去，看云水北山南。\"]',NULL,NULL,NULL,NULL),('e647d10fc8d190c3ffdf22b8c62b7b20','风入松·次虞文靖公韵，赏阁前芍药','夏言','e647d10fc8d190c3ab541d8735464c84','明',NULL,'[\"绿窗午枕睡初酣。梦喜脱朝簪。觉来犹是长安客，垂杨里、空系归骖。恋主身依晓阙，思亲泪洒春衫。\",\"玉河流水碧于蓝。花外燕呢喃。新词一曲凭谁和，泥封罢、亲手题缄。试待天边鸿雁，秋风寄与江南。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b0d6cbf0b87571757','感皇恩·寿故大司马白夫人编修秉德之母','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"每岁到花朝，正当称寿。青鸟将书报金母。一周花甲，重见春杓回斗。喜看华发映，朱颜久。\",\"白傅盟勋，窦郎腾茂。有福能如太君否。凤冠霞帔，笑指东风蒲柳。满听新曲度，长生酒。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b0f93d225d02ee6a3','卜算子·题汪汝温便面','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"草长沙露尖，风急波生绉。两个渔郎任意行，不问谁先后。\",\"纶竿腥乍收，饭甑香初透。回首相呼好放船，正是潮生候。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b1210dae4703766b7','浪淘沙·十月十四日病中经宿不寐枕上作','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"风叶走庭阶。月上书斋。隔墙寒影舞松钗。数尽残更人不寐，无奈幽怀。\",\"酾酒断溪涯。题遍苍崖。病来耽误几青鞋。不待一分林下福，也共时乖。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b1ad3cef7085d13cc','金缕曲·月正元日寿董都督','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"喜遇元正节。想会合贞元，挺生材杰。曾仗雕戈提虎旅，荡扫狼烟灰灭。\",\"问旧将、谁优谁劣。七十年来勋业遂，置阴符、却访还丹诀。\",\"庆初度，华筵设。萧萧两鬓飞晴雪。看膝前、桂枝秀颖，兰芽香茁。\",\"冠盖盈庭丝管沸。银瓮椒花酒冽。更休问、乌圆兔缺。\",\"但祝年年春不老，比乔松、晚翠根盘铁。奏一调，长生阕。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b55192902b55dff0f','沁园春·寿少詹学士镜川杨公','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"天启家筵，连开寿域，总值芳辰。喜位进储端，新悬印绶，恩加阃正，久奉丝纶。霜拂华㒹，霞明丹颊，甲子而今才一巡。鸣珂巷，见长庚彻夜，瑞霭凌晨。\",\"膝前双玉，也何用、斑衣能悦亲。看才压秋曹，方恢宦辙，名需春榜，更冠儒绅。曲度南词，酒传内酝。珠履纷纷来众宾。偕老处，问几番蒲柳，得似松筠。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b634cd852685cea70','点绛唇·元夕大雨','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"满意鳌山，一天星月交相射。春霖无藉。误却千金夜。\",\"院落沉沉，坐看灯花谢。情难写。佳期再也。须问天公借。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b637393320ea72caf','木兰花慢·赠通守娄公致政还乡有序','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"喜年方耳顺，何遽尔赋归田。想十载红尘，题舆佐岳，连宵清梦，先到林泉。抽身急流而退，任攀辕无计可留延。万里蓬瀛身近，一州父老情牵。\",\"故园松菊正依然。晚景乐无边。看满载图书，芸霏别舸，倾城冠盖，云拥离筵。酌杯起、为公寿，问几人舒卷似公贤。海上耆英会启，日边循吏名传。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b6c70b6376fe8bf30','浪淘沙·令四景乐道辞','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"庭满冻苔斑。农事都闲。尖风不到水云间。曝背茅檐调鹤舞，一任疏顽。\",\"名利几人还。雪拥关山。金丹留我镜中颜。起索梅花同一笑，紧闭柴关。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b6c70b637a6b030fb','浪淘沙·令四景乐道辞','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"秋意到郊亭。水褪沙汀。金茎香泻瓮头青。柱杖西畴看稻了，细检丹经。\",\"蒲柳最先零。羲驭难停。旧时交友似晨星。惟有黄花开更好，多谢园丁。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b6c70b637f41e8ba4','浪淘沙·令四景乐道辞','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"竹院好风凉。洗尽炎光。呼童闲试水沉香。数卷南华看未了，且困藤床。\",\"官道暗尘黄。四体流浆。可能知有翠寒堂。尽日茶瓜留客坐，一调沧浪。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b70f386b56db27302','念奴娇·寿叶文庄公夫人耿清惠公女上舍晨母也','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"奉萱堂上，人争道、七十古来稀有。满面春潮双鬓雪，天教乐寿。清惠家风，文庄壸政，宦族传来久。喜逢诞日，婺星光，映台斗。\",\"好是一颗明珠，三秋高捷，拟占青云首。膝下诸孙森立，彩服相辉左右。东海桃红，洞庭橘熟，同荐松花酒。愿期千岁，瑶池重会金母。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b8697e334f7230152','一剪梅·庚戌元夕饮文远家','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"传柑节候雨初晴。灯满山城，月满山城。画堂围坐夜三更，墙外歌声，席上歌声。\",\"可人添送紫金罂。未解春酲，又犯春酲。不辞扶醉卧前楹，客也多情，主也多情。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11b9a3ff4c668a469be','青玉案·哀郭用章佥宪','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"高才无愧霜台佐。荆楚地人相贺。谁遣阳由滩下过。烟水迷茫，风花飘堕，不及茶毗大。\",\"清流肯受黄尘涴。空怅望、吴山恨无奈。岁月纷纷如蚁磨。夕阳芳草，断蓬孤柁，忍读招魂些。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11ba00b6bf82d72c302','风入松·草桥阻风雨作','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"萧萧阴雨渐经旬。有意暗摧春。林鸠来往缘何事，声不住、故恼征人。没脚泥壅岸尾，打头风断河漘。\",\"篷推蓬、偏感异乡身。归梦夜来频。江南江北家千里，心随舵去住无因。兀坐悄然自数，半生多少苦辛。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11ba379c1ec05c171fd','庆宣和行住坐卧卷','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"五更风雪候趋朝。玉漏迢迢。纸窗明月上梅梢。睡著。\",\"睡著。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11ba379c1ec4510ca98','庆宣和行住坐卧卷','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"香车宝马洛阳街。满面尘埃。芙蓉秋水钓鱼台。坐来。\",\"坐来。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11ba379c1ec78815f87','庆宣和行住坐卧卷','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"银屏金屋逞骄奢。利锁堪嗟。杏花村落酒中赊。行些。\",\"行些。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11ba379c1ecd7c5f402','庆宣和行住坐卧卷','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"三边玉帐拥雕戈。汗马奔波。竹篱茅舍小行窝。住么。\",\"住么。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11ba99e415abfe1fb6a','浪淘沙·八月九日早至南山庵','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"沙口系兰桡。初退溪潮。石成山路树成桥。又见一番新稻熟，犹未趋朝。\",\"野寺晚钟敲。声到书巢。此身何日伴渔樵。少答君恩寻旧隐，细和山谣。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11bc6821a7bb6ff6534','人月圆·为人寄寿钱塘王嘉瑞故孔目天与之孙','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"钱塘江绕吴山翠，相望隔天涯。镇海奇观，泛浙佳兴，尽属谁家。\",\"闻说王郎，翰林孙子，四十年华。遥想同人，一回相寿，几醉流霞。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11be1623ad3c3e13c0d','满江红·南山道中晚秋','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"九月南山，犹未觉、园林秋意。无远近、丹枫黄叶，翻成佳丽。风外磬声知有寺，云中犬吠疑无地。见孤峰、淡淡扫蛾眉，当空际。\",\"修竹里，柴门闭。古木下，渔舟系。更牛背两两，儿童相戏。素练摇江明一鹭，黄云漫陇飞双雉。喜农家、细麦早纤纤，青如罽。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11be5356f565eb3853d','一剪梅·庆汪本亨六十寿词并引','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"稠木阴阴一里春。松喜津津，梅喜津津。画堂开宴庆佳辰。主也精神，客也精神。\",\"花甲才周第一巡。岁又更新，曲又更新。愿期遐算比灵椿。不是尧人，谁是尧人。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11be947af81144d521e','浪淘沙令·四景乐道辞','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"春雨夜如何。花靥红多。窗前黄鸟数声过。斜挂氅衣初睡起，日上庭柯。\",\"人世苦奔波。两鬓霜皤。争如溪上理渔蓑。笑问东风来几阵，酒满香螺。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11bebeac070e6ecfa5f','一剪梅·庆郑君存良七十寿','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"乔木江村映锦沙。尚义人家，积善生涯。画堂开宴鼓频挝，砌拥兰芽，鬓点霜华。\",\"辉辉晴日上檐牙。词吐天葩，酒泛流霞。百年冰玉两无瑕，醉眼生花，仙枣如瓜。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11becf57701d1f49db3','鹧鸪天·寿费司业兄药轩君六十','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"花甲才周又一年。药轩初度敞华筵。句分池草千金重，春试阶蓂四叶全。\",\"娱彩服，听朱弦。主人如鹤酒如川。愿言铁瓮金山景，百岁常供几杖前。\"]',NULL,NULL,NULL,NULL),('e647d10fc9bca11bfa87c8b2b80999c2','满江红·寿盛都宪七十','程敏政','e647d10fc9bca11bb265ce606490791e','明',NULL,'[\"七十高年，冰壑老、重逢华旦。喜江上、白鸥新社，初分一半。句曲道人朝送酒，武夷仙子晴张幔。望弧南、一点老人星，当空烂。\",\"抚桑柘，无羁绊。有松竹，堪娱玩。更埙篪迭奏，儿童争看。爱国久惊霜鬓短，怡年未觉朱颜换。愿自今、花甲两周来，从头算。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c04e83e1afe3fe4a2','鹊踏花翻美人解','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"锣鼓声频，街坊眼慢，不知怎上高高骑。生来少骨多筋，软陡腾翻，依稀略借鞍和辔。作时鹘打雪风天，停犹燕掠桃花地。\",\"下地不动，些儿珠翠，堪描耐舞军装伎。多少柳外妖娇，楼中笑指，颠倒金钗坠。无端归路又逢谁，斜阳系马陪他醉。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c0cd5399c1d8b7e17','鹧鸪天·闻张子荩捷报呈学使公二首有序','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"措大?楚醉一场。猛?典却破衣裳。非关双眼看人做，自胜千金许赠将。\",\"因好事，累粗觞。断鸿数点在斜阳。欲呼细问长安事，争带泥金几处忙。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c0cd5399cae8fedc7','鹧鸪天·闻张子荩捷报呈学使公二首有序','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"试选蛾眉几许长。纤纤侵入鬓云黄。天边夺得初三月，镜里描来第一双。\",\"真国色，好天香。安排枕席待君王。越溪多少莲舟女，老却失颜不嫁郎。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c25accaf09e9b38bc','浣溪沙·鉴湖','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"浅碧平铺万顷罗，越台南去水天多，幽人爱占白鸥莎。\",\"十里荷花迷水镜，一行游女惜颜酡，看谁钗子落清波。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c2702450c054ca80f','菩萨蛮·闺人纤趾','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"千娇更是罗鞋浅。有时立在秋千板。板巳窄棱棱。犹馀三四分。\",\"红绒止半索。绣满帮儿雀。莫去踏香堤。游人量印泥。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c4b475d968fa2d45f','竹枝词袁宏道评曰：刘荪州无此奥','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"风前烛焰片时红。马首西时马尾东。两只鸳鸯睡不醒，一只相思愁杀侬。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c4b475d96ec9b9ae6','竹枝词袁宏道评曰：刘荪州无此奥','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"笼中鹞子不得出。笼外要入将奈何。一边虫粘蜘蛛网，一边窗打扑灯蛾。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c50064b40949c4eaa','竹','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"一斗醉来将落日，胸中奇突有千尺。急索吴笺何太忙，兔起鹘落迟不得。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c789fca58d0ef6af4','如梦令·宝珠斋饭罢箸响椀寂为作一偈时宿东天目','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"两只脚挨转磨。一副牙关嚼错。连日施药医人，大似把船放舵。\",\"错过。错过。莫被宝公瞧破。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21c9bb7d0e155888014','鹧鸪天·竹炉汤沸火初红','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"客来寒夜话头频。路滑难沽曲米春。点检松风汤老嫩，退添柴叶火新陈。\",\"倾七碗，对三人。须臾梅影上冰轮。他年若更为图画，添我炉头倒角巾。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21ca2220e3b263923af','鹧鸪天·蒋三松风雨归渔图','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"芦长苇短挂青枫。墨泼毫狂染用烘。半壁藤萝雄水口，一天风雨急渔翁。\",\"蓑笠重，钓竿蒙。不教工处是真工。市客误猜陈万里，惟予认得蒋三松。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21cbf42d74f10298024','自马水还道中竹枝词','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"湾湾曲曲几山溪。眼眼腮腮泪落丝。立到马遥人影没，更谁知尔下山时。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21cd338cc69619e1c41','凤凰台上忆吹箫·画中侧面琵琶美人','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"湖石阴中，栟榈影外，天然一个宫娃。悄无人与共，自弄琵琶。拨扫忽成抖擞，恍摇却、钿翠鬟雅。如花畔，蜂撩未定，战杀其花。\",\"匀搽梨腮双靥，那半面，刚被这半面相遮。问何时展过，得见些些。除是递将红叶，应回流水之涯。俄成讶，缘来画也，一笑看差。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21cf2424731a395e7f7','眼儿媚·书唐伯虎所画美人','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"吴人惯是画吴娥。轻薄不胜罗。偏临此种，粉肥雪重，赵燕秦娥。\",\"可是华清春昼永，睡起海棠么。只将秾质，欺梅压柳，雨罢云拖。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21cf889bf9a436d8e33','自燕京至马水竹枝词','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"沙浑石涩夹山椒。苦刺桑乾水一条。流出芦沟成大镜，石桥狮影浸拳毛。\"]',NULL,NULL,NULL,NULL),('e647d10fcc2ea21cfc150acec3fa6fe9','贺新郎·继闻廷对之捷复制一阕','徐渭','e647d10fcc2ea21c253f7b5d06a81a4f','明',NULL,'[\"真圣主龙目。握新符、策士临轩，两科收录。暗卜今年谁最好，刚得齐贤名族。亲认取、青宫叮嘱。今日马蹄催宴去，记官袍、有领偏深绿。特赐与，浇?醁。\",\"男儿到此平生足。却惹起、愁人一醉，消他万斛。手取尘冠弹欲碎，不为要君推毂。正沉吟、断虹生北。却似有情相照映，奈无言、难与传心腹。几时更，谈夜烛。\"]',NULL,NULL,NULL,NULL),('e647d10fcc576b5e57659484478d82d4','长相思','顾若璞','e647d10fcc576b5ed1457b72d1457b72','明',NULL,'[\"梅子青，豆子青，飞絮飘飘长短亭。风吹罗袖轻。\",\"恨零星，语零星，正是春归不忍听。流莺啼数声。\"]',NULL,NULL,NULL,NULL),('e647d10fccca6b8c2666ddd058217232','鹧鸪天·题村叟屋壁','袁黄','e647d10fccca6b8cd1457b72d1457b72','明',NULL,'[\"数叠烟林散翠鬟，莫嫌此地少青山。酒逢社日添酬应，花到开时费往还。\",\"松老大，竹平安，柴门虽设不曾关。旁人方讶茅斋窄，鹤栅蜂房割半间。\"]',NULL,NULL,NULL,NULL),('e647d10fcd060d6ea7f82641d46083ae','生查子·腊梅','顾志','e647d10fcd060d6ed1457b72d1457b72','明',NULL,'[\"幽香度月迟，瘦骨凌霜老。潇洒自天然，驿路花开早。\",\"从教纤手栽，眉黛晨妆巧。独有岁寒心，别笛声吹晓。\"]',NULL,NULL,NULL,NULL),('e647d10fcd2957d3e176a89db6b80d74','虞美人·春晓忆长沙作','桑悦','e647d10fcd2957d3c930eecdd1457b72','明',NULL,'[\"细雨梨花春梦破，梁燕将愁诉。薄阴小阁日瞁瞁，宝篆轻烟、一缕袅东风。\",\"星沙旧占游歌处，清景难留住。王孙芳草最多情，万里和烟、随我到边城。\"]',NULL,NULL,NULL,NULL),('e647d10fcd3737287894f83245196fba','南乡子·华亭吊古','天随子','e647d10fcd373728d1457b72d1457b72','明',NULL,'[\"风雨满长亭，鹤唳滩头梦未平。故国王孙何处去，伤情，烟月空蒙白苎城。\",\"往事恨飘零，江上青山云自横。闻说莼鲈秋更好，凄清，漫忆风流张步兵。\"]',NULL,NULL,NULL,NULL),('e647d10fcda8e1ea996e44a26513dd28','访黄茂才伯守留饮偕乃兄弟同赋体得三五七言','李英','e647d10fcda8e1ead1457b72d1457b72','明',NULL,'[\"白鹤身，白纶巾。偶访云霄客，偏怜草莽人。西池聚首情何洽，北海芳尊醉是真。\"]',NULL,NULL,NULL,NULL),('e647d10fcde55ab234b87dfff5bdacca','临江仙·顾四城北新居','王屋','e647d10fcde55ab2d1457b72d1457b72','明',NULL,'[\"独访柴门深竹里，板桥流水斜通。渔舟泊处暮云重。数家横浦上，一径入烟中。\",\"丛木暗妨樵路远，鹭鹚飞破霞红。轻雷隐隐小池东。藕花鲜着雨，菱蔓弱牵风。\"]',NULL,NULL,NULL,NULL),('e647d10fce102666cf44578d8f3029bf','三五七言','释函可','e647d10fce102666d1457b72d1457b72','明',NULL,'[\"塞草白，塞日黄。埋沙无老骨，挂树有饥肠。安得长风生此夕，尽吹残魄返其乡。\"]',NULL,NULL,NULL,NULL),('e647d10fd0a4ab994334969f2a56c81b','蝶恋花','王化龙','e647d10fd0a4ab99d1457b72d1457b72','明',NULL,'[\"小院幽窗风弄晓，柳织烟笼，开罢棠梨了。枝上流莺啼更早，惜花人共春花老。\",\"金鸭香深檐外袅，绣到鸳鸯，转惹侬情恼。镜掩孤鸾羞独照，眉山一点愁多少。\"]',NULL,NULL,NULL,NULL),('e647d10fd0cfcfe68fd26bbfcaf274dc','清平乐·春雪','储昌祚','e647d10fd0cfcfe6d1457b72d1457b72','明',NULL,'[\"雪眠风扫，飘泊知多少。盼断天涯春色杳，一夜青山都老。\",\"金猊桂橐重燃，烟笼修竹千竿。只有梅花添恨，几枝早落阶前。\"]',NULL,NULL,NULL,NULL),('e647d10fd0d89396faccb7348aa1e34b','一剪梅·秋感','胡文焕','e647d10fd0d89396d1457b72d1457b72','明',NULL,'[\"云叶银床落井柯，望断关河，肠断关河。年华冉冉去如梭，秋影婆娑，鬓影婆娑。\",\"越女含情竹上歌，娇敛双蛾，愁敛双蛾。芭蕉冷语咽声多，客里风波，梦里风波。\"]',NULL,NULL,NULL,NULL),('e647d10fd0f42410568b36d834f219cc','临江仙·秋兴','王凤娴','e647d10fd0f42410d1457b72d1457b72','明',NULL,'[\"珠帘不卷银蟾透，夜凉独自凭阑。瑶琴欲整指生寒。鹤归松露冷，人静井梧残。\",\"天际一声新度雁，翱翔似觅回滩。浮生几见几多欢。三秋今已半，枫叶醉林丹。\"]',NULL,NULL,NULL,NULL),('e647d10fd0f42410adecdf19de91a4fc','浣溪沙·再同乔夫人郊行','王凤娴','e647d10fd0f42410d1457b72d1457b72','明',NULL,'[\"曲径新篁野草香，随风闪闪蝶衣忙。柳绵飞堕点衣裳。\",\"人在镜中怜影瘦，燕翻波面舞春长。小桥古渡半斜阳。\"]',NULL,NULL,NULL,NULL),('e647d10fd10182324334969f16733748','蝶恋花','张綎','e647d10fd1018232d1457b72d1457b72','明',NULL,'[\"紫燕双飞深院静，簟枕纱厨，睡起娇如病。一线碧烟萦藻井，小鬟茶进龙香饼。\",\"拂拭菱花看宝镜，玉指纤纤，捻唾撩云鬓。闲折海榴过翠径，雪猫戏扑风花影。\"]',NULL,NULL,NULL,NULL),('e647d10fd10182324b26e627fea64bef','风流子','张綎','e647d10fd1018232d1457b72d1457b72','明',NULL,'[\"新阳上帘幌，东风转、又是一年华。正驼褐寒侵，燕钗春袅，句翻词客，簪斗宫娃。堪娱处、林莺啼暖树，渚鸭睡晴沙。绣阁轻烟，剪灯时候，青旌残雪，卖酒人家。\",\"此时因重省，瑶台畔、曾遇翠盖香车。惆怅尘缘犹在，密约还赊。念鳞鸿不见，谁传芳信，潇湘人远，空采苹花。无奈疏梅风景，碧草天涯。\"]',NULL,NULL,NULL,NULL),('e647d10fd1178d9af137049bf677f4b4','阮郎归','王鏊','e647d10fd1178d9ac5cc17e3471c75ee','明',NULL,'[\"行年六十鬓斓斑，皇恩特放还。高堂设席对青山，秋来正未闲。\",\"阴忽霁，暑初残，笙歌夜未阑。傍人莫笑老嗤顽，人生到此难。\"]',NULL,NULL,NULL,NULL),('e647d10fd2fd17c115c25df3ee4069b0','汉宫春·客中九日','陆嘉淑','e647d10fd2fd17c1d1457b72d1457b72','明',NULL,'[\"极目平芜，见疏林萧飒，远水弥漫。举头一声哀雁，别泪空弹。秋容惨淡，但心伤、行路艰难。何况是、重阳时节，天涯风景凄然。\",\"长恨征途憔悴，叹敝裘风雨，疲马关山。为问故园黄菊，知有谁看。风吹破帽，漫凭高、一醉尊前。浑未解、远游何意，白云回首江天。\"]',NULL,NULL,NULL,NULL),('e647d10fd3b8e75032197434091323f9','桂枝香','王廷相','e647d10fd3b8e7507d6044e9f337d999','明',NULL,'[\"朝霞飞散，正微借清霜，林外烘染。摇落年华，宋玉又伤幽怨。小楼悄悄阑干倚，最浮云、不堪情恋。玉书难寄，楚天空阔，梦迷人远。\",\"叹双鬓、年来较短。算病旅中秋，不禁登玩。只见青山，谁见古时人面。乡心渺渺随流水，更不待、商歌魂断。新愁顿起，半窗暝雨，一声归雁。\"]',NULL,NULL,NULL,NULL),('e647d10fd3b8e750ef8dba3e34443cfe','巴人竹枝歌四首','王廷相','e647d10fd3b8e7507d6044e9f337d999','明',NULL,'[\"郎在荆门妾在家，年年江上望归查。荼蘼种得高如妾，纵有春风枉却花。\"]',NULL,NULL,NULL,NULL),('e647d10fd3b8e750ef8dba3e4ea5edff','巴人竹枝歌四首','王廷相','e647d10fd3b8e7507d6044e9f337d999','明',NULL,'[\"江草江花满眼新，不知郎处几多春。愁来欲上东峰望，上到东峰愁杀人。\"]',NULL,NULL,NULL,NULL),('e647d10fd3b8e750ef8dba3ea1aa1ea3','巴人竹枝歌四首','王廷相','e647d10fd3b8e7507d6044e9f337d999','明',NULL,'[\"蒲子花开莲叶齐，闻郎船已过巴西。郎看明月是侬意，到处随郎郎不知。\"]',NULL,NULL,NULL,NULL),('e647d10fd3b8e750ef8dba3ef57f1b72','巴人竹枝歌四首','王廷相','e647d10fd3b8e7507d6044e9f337d999','明',NULL,'[\"野鸭唼唼一双飞，飞到侬池不肯归。莫共鸳鸯斗毛羽，鸳鸯情性世间稀。\"]',NULL,NULL,NULL,NULL),('e647d10fd4ea2f5383cfc9c21bf915fd','竹枝词','郭文','e647d10fd4ea2f53d1457b72d1457b72','明',NULL,'[\"金马何曾半步行，碧鸡那解五更鸣。侬家夫婿久离别，恰似两山空得名。\"]',NULL,NULL,NULL,NULL),('e647d10fd4eb508e50470da09cdb16bf','汉宫春·题美人画轴','李堂','e647d10fd4eb508ead71c82b471c75ee','明',NULL,'[\"月减梅妆，又春深燕雏，双飞帘幕。惊啼梦醒，懒动秋千彩索。危楼欲上，奈腰围、瘦来非昨。凝伫久，广寒宫殿，微闻数声瑶鹤。\",\"伊人惯寻欢乐。正骄驰玉勒，笑搴珠箔。谁怜眉翠远岫，遥岑映着。苍苔绿润，雨初晴、闲阶步却。添新恨、蹊成桃李，敛黛对人依约。\"]',NULL,NULL,NULL,NULL),('e647d10fd50cca7062a81a1da646fc4d','蝶恋花·徐州晚泊用师邵韵','卢襄','e647d10fd50cca704e8412ad99adff45','明',NULL,'[\"城上危楼惊欲堕，北楫南樯，日日城边过。犹记昔年楼上坐，楼前景物多题破。\",\"今日长年催捩舵，万井萧条，落日稀烟火。坡老不归招楚些，云山隔水如留我。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f1e57383cfc9c22f12ec2e','竹枝词','何吾驺','e647d10fd5f1e573d1457b72d1457b72','明',NULL,'[\"稚子门前唱竹枝，花下佳人送和词。只是闻声听不得，依稀犹似怨将离。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f1e57383cfc9c236af4dfc','竹枝词','何吾驺','e647d10fd5f1e573d1457b72d1457b72','明',NULL,'[\"欢爱莲花紫艳妆，侬爱莲花白雪光。一夜东风吹雨急，虽同落地各衣裳。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f1e57383cfc9c29cacf5d2','竹枝词','何吾驺','e647d10fd5f1e573d1457b72d1457b72','明',NULL,'[\"朝来东望火云生，一霎回头送雨声。乍暖乍寒无住著，十二时中总世情。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f1e57383cfc9c2b9208e04','竹枝词','何吾驺','e647d10fd5f1e573d1457b72d1457b72','明',NULL,'[\"四月秧青五月齐，稻花初雨照前堤。大家载酒商农务，莫遣西风到夏畦。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f1e57383cfc9c2ce84bc75','竹枝词','何吾驺','e647d10fd5f1e573d1457b72d1457b72','明',NULL,'[\"东家女儿食兼珍，西壁含饴但素羹。待各长成十五六，食贫嫁得好良人。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f1e57383cfc9c2d428b58f','竹枝词','何吾驺','e647d10fd5f1e573d1457b72d1457b72','明',NULL,'[\"贵人高纛引褰帏，团扇交摇污满衣。只见长松箕踞者，也无是处也无非。\"]',NULL,NULL,NULL,NULL),('e647d10fd5f9cba89b02ba6d8794319c','菩萨蛮','邱浚','e647d10f4d0c18a6309928d4fa1e9c96','明',NULL,'[\"销魂别处何寥寂，感情含思愁生极。倦睡困方深，更阑夜正沉。\",\"沉檀烧细炷，香冷帏空处。寒光月影斜，横透碧窗纱。\"]',NULL,NULL,NULL,NULL),('e647d10fd6218941387047c7acbf1359','虞美人·春晚','沈际飞','e647d10fd6218941d1457b72d1457b72','明',NULL,'[\"阶前嫩绿和愁长，坐忆眠还想。花红破梦似相怜，起望小林残萼、损容颜。\",\"双莺又向愁人絮，春也知归去。个人只是不思家，生却杨花心性、落天涯。\"]',NULL,NULL,NULL,NULL),('e647d10fd6a878a6231bbcd7f43c63c9','风入松·元州画赠素安联床夜话图','彭年','e647d10fd6a878a60c9ebb2d18d10dc6','明',NULL,'[\"雨余山阁洗炎嚣，绛烛频烧。故人住近携琴至，岂烦折简相邀。新瓮景山斟酌，小团鸿渐烹调。\",\"画檐风动响芭蕉，梧竹翛翛。石床凉思清肌骨，纨扇都抛。辨马极谈申旦，闻鸡起舞中宵。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab19c49915','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"阿侬家住第三桥，白粉墙低翠竹高。春光一日老一日，怕见花间飞伯劳。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab1feca9ac','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"劝郎莫上南高峰，劝郎莫上北高峰。南高峰云北高雨，云雨相随愁杀侬。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab4f27bed2','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"小姑拟郎去不归，为郎打瓦复钻龟。青山尚有飞来日，岂有人无相见时。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab6ac5fa0b','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"十五十六月正圆，楼头买卜问青天。月如照到吴江水，郎在吴江第几船。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab73e4dda6','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"郎去从征不见来，门前小径已生苔。愿得海门高万丈，倒流江水送郎回。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab73fb4b72','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"望郎一朝又一朝，萧郎信似浙江潮。钱塘潮信有时失，臂上守宫无日销。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062ab9fb91ea3','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"郎在潮东侬在西，临鸾愁画远山眉。梅花月冷珊瑚□，□□□□□□□。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062aba625f618','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"葛岭东头是相门，当年甲第入青云。楼船东入里湖去，何曾望见岳王坟。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abb8b332ca','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"湖上儿女宫样装，小舟荡漾芰荷香。逢人便唱相思曲，不道侬家有阿郎。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abbedfd01b','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"阿侬随郎上钓舟，郎作钓丝侬作钩。钓丝无钩随风扬，钓钩无丝随水流。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abbfe96586','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"钱塘儿女不学针，月明楼下理胡琴。逢人但道新番曲，不识春愁海样深。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abc9154855','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"十八嫁郎眉未开，郎轻离别重求财。扁舟一逐江潮去，日日潮来郎未来。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abd8d44c14','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"郎心恰似江上篷，昨日南风今北风。妾心恰似七宝塔，南高峰对北高峰。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abd9d71cc0','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"湖草青青湖水深，画船撑出断桥阴。画船无柁是郎性，断桥有柱是侬心。\"]',NULL,NULL,NULL,NULL),('e647d10fd6edb91f4a4062abf87bf9fc','竹枝词十八首','朱同','e647d10fd6edb91fd1457b72d1457b72','明',NULL,'[\"郎去从君二十年，花开花落任春妍。门前有个垂杨树，不着游人系画船。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847017ac11eba6d267f','旅况卜算子','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"深闺白露寒，古驿丹枫暮。雁叫芦花四五声，衰草天涯路。\",\"不见相思人，空见相思树。剩水残山无限程，立马斜阳渡。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847025065e14feed2d6','别意柳梢青','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"莺语声吞。蛾眉黛蹙，总是销魂。银烛光沉，兰闺夜永，月满离尊。\",\"罗衣空湿啼痕。肠断处、秋风暮猿。潞水寒冰，燕山残雪，谁与温存。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484708e8c8950c9d0535','春思点绛唇','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"金谷香飘，绿杨枝啭莺声巧。残红轻扫。满径胭脂绕。\",\"蜂蝶无情，苦被东风恼。春归早。佳期渐杳。镜里铅华老。\"]',NULL,NULL,NULL,NULL),('e647d10fd73548470b7bc93b65a9d626','秋思南柯子','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"柳叶含愁黛，菱花照泪妆。些儿闲事恼人肠。不忍灯前重忆、解罗裆。\",\"铁马嘶风紧，铜龙咽漏长。梦魂随月到伊傍。无奈醒来依旧、恶思量。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484711252a7ccf357ad9','闺情阮郎归','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"愁云残雪满燕关。深闺频倚栏。镜中谁与画春山。啼痕损玉颜。\",\"憎画舸，怨雕鞍。郎行何日还。湘江千古泪潺潺。谁留竹上斑。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e19137ecfe3d','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"丹实满林馣。耀日红酣。由来佳品压江南。汉苑杨梅应避色，卢橘香惭。\",\"沁齿有馀甘。玉液中涵。钗头一朵美人簪。记得乐天曾有句，映我绯衫。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e1918ca40b10','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"白玉莹肌肤。轻衬罗襦。凤凰冈上锦千株。任是崇龟工墨妙，香味难图。\",\"百果更谁如。羞杀杨卢。品题犹说蔡君谟。蠲渴延年还补髓，一罐醍醐。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e1918d296171','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"树树火连空。绿叶芃芃。朱颜妖丽玉肌丰。传说琅邪王少女，十八娘红。\",\"分摘满筠笼。锦绣成丛。半林香气度微风。却笑杜陵诗句好，只忆泸戎。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e1918d3e34f4','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"高树锦蒸霞。朱实清华。一丸寒玉裹红纱。万颗累累闽海上，不数三巴。\",\"西域枉乘槎。马乳休夸。剖开琼液碎丹砂。异品即今谁第一，犹说江家。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e19199c89073','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"驿骑走红尘。一笑华清。炎方何用献朱樱。天宝梨园新度曲，小部音声。\",\"鼙鼓动西京。妃子心惊。梨花魂断不胜情。翠袖红缯俱是梦，水绿山青。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e191aff80f1e','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"庭静午风凉。荔子盈筐。小姬纤手解罗囊。玉腕冰肌相掩映，百步闻香。\",\"含笑问檀郎。何似侬妆。红颜薄命总堪伤。因忆骊山当日事，闲说明皇。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e191b039311e','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"十里锦云乡。傅粉凝妆。红裙争看绿衣郎。黑□稍头朱柿小，玳瑁丁香。\",\"延寿品非常。尤胜陈江。绣鞋一种记闽娘。风送瑞堂香百步，结绿硫黄。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484732d1e191b29a3674','荔枝浪淘沙','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"一品状元红。金线金钟。麝囊吹散桂林风。黄玉紫琼真胜画，江绿丛丛。\",\"双髻翠云松。兰寿香浓。绿珠魂在玉堂东。五岭三巴无此种，独擅闽中。\"]',NULL,NULL,NULL,NULL),('e647d10fd73548473a54922651bf3993','感旧传言玉女','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"泪眼朦胧，滴损桃花双颊。起傍朱阑，听东风啼鴂。旧欢如梦，隔断云山干叠。恨柳嗔花，怨蜂愁蝶。\",\"沉水香酣，倚薰笼、肠万结。绿波春草，记去年离别。那忍春归，又见梨梢飞雪。褪尽铅华，镜中妆靥。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847705a6ad0c577a60a','春怨如梦令','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"一夜风狂雨妒。孤枕寒灯难度。杜宇太无情，苦唤东君归去。\",\"春暮。春暮。飞絮落花无数。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484780158dec497c240c','闺情卖花声','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"远信阻青禽。幽怨难禁。寒更孤枕半边衾。铁马声残金鸭冷，月在花阴。\",\"双靥泪痕深。湿透罗襟。当初谁信有于今。衣带渐宽腰渐减，空结同心。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484783cfc9c2861c366b','竹枝词','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"忽听楼头鼓乱敲，模糊月色上花梢。郎今去住无消息，暗掷金钱卜一爻。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484783cfc9c2a5a42eb2','竹枝词','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"美人门对钱塘江，水透苏堤月透窗。莫怨裴航仙梦晚，种来玉杵未成双。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484783cfc9c2ca5ee64b','竹枝词','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"妾住西湖旧狭邪，孤山深处是郎家。郎若重来须细认，湖边新种碧桃花。\"]',NULL,NULL,NULL,NULL),('e647d10fd735484783cfc9c2da4f6c3c','竹枝词','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"杨柳堤边去采莲，柳枝低拂采莲船。分付儿童休浪折，留待郎来作马鞭。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1ea0e3da375','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"湖心歌管遏春云，水底榴花六幅裙。转过苏堤欢不见，停桡齐上岳王坟。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1ea4ab2f9c9','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"桃溪春色武陵同，落尽残英阵阵风。暗出袖中花一片，问郎何似夜来红。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1ea833e65f4','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"石屋云开淡复浓，青山微露半芙蓉。一时游女红妆湿，身在高唐第几峰。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1ea9033c96d','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"霞光片片映香台，花下红妆笑靥开。预卜明朝天色好，愿郎游骑得重来。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1eaa9853319','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"啼罢天鸡曙色微，鹫峰高处见朝晖。傍人不用歌行露，夜与郎游尚未归。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1eaad6c2a19','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"三月寻春到锦塘，青青垂柳一丝长。折来戏作同心结，不是同心不赠郎。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1eab5ca0ef7','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"骊珠不动小龙眠，井水无波玉镜鲜。携手与郎同一照，忽惊花发并头莲。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1eaf0e97e58','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"柳外春烟午未消，烟深何处认郎桡。小鬟不用频相问，已约桃花第五桥。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1eaf16610a0','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"虬龙千尺翠烟凝，九里涛声浪几层。油壁青骢堤上过，同心犹记结西陵。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847aa6cf1eaf7879b88','西湖十景竹枝词六桥烟棹','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"梅花十亩映湖塘，历乱幽魂处处香。淡月一轮疏影见，青铜镜里寿阳妆。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847acd28e0fbf4d54a1','西蜀竹枝词','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"杜鹃啼上海棠枝，赤甲山前月落迟。斗帐夜寒春梦醒，笛声吹过竹郎祠。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847bbe960ce73852e78','闺思忆秦娥','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"游人别。几回梦断花间月。花间月。关山两地，一般圆缺。\",\"青楼雁尽音书绝。可怜空带同心结。同心结。灯前偷看，寸心都折。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847d87cd1a0b7708ca3','青楼曲少年游','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"绿云梳罢，碧纱窗里，方自按箜篌。青衣报道，才郎到也，忙步上妆楼。\",\"酒沽?醁，杯传鹦鹉，花下递相酬。檀板轻敲，清歌未阕，月上帘钩。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847e181aa7214de3578','春情苏幕遮','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"篆烟微，灯燄小。罗帐香酣，一夜春多少。钟漏沉沉人悄悄。含醉含醒，梦只巫山绕。\",\"枕云浓，梁月皎。笑语叮咛，此意休忘了。无那邻鸡声太早。顾影仓皇，莫待纱窗晓。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847ee23480db615f4ce','春怨卖花声','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"春雨夜潇潇。目断蓝桥。梦中犹忆翠云翘。觉后泪痕空自湿，无限魂销。\",\"瘦损沈郎腰。虚度良宵。何时金屋贮妖娆。灯下凝眸羞不语，同解罗绡。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847ef89d704d5d62eda','寄远诉衷情近','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"孤灯细雨，枕畔泪痕无数。幽期难托琴心，愁绪怕传尺素。暗想香肌艳质，放诞风流，肠断朱弦暮。\",\"天台路。遥在桃花深处。芳闺冷落，只恐青春误。凭谁诉。而今佳会，蹉跎后日，相逢无据。早遂蓝桥遇。\"]',NULL,NULL,NULL,NULL),('e647d10fd7354847efd0eba98c369b99','闺怨阮郎归','徐熥','e647d10fd7354847d1457b72d1457b72','明',NULL,'[\"琴调别鹤镜分鸾。孤灯清夜阑。龙钟双袖不曾乾。无人空暗弹。\",\"红袖褪，翠眉残。肌消玉钏宽。燕山莫道客衣单。闺中寒更寒。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8d4a7b76a22c97073','湖州竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"荷叶浦北菱湖东，清溪曲曲与天通。莫将铁笛弄小海，大风倒卷冯夷宫。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8d4a7b76a50fda96b','湖州竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"沤波春涨腻如油，坐看青山船倒流。桃花欲开杏花落，打鼓吹笛上杭州。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8d4a7b76aa85e9cdd','湖州竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"黄姑浣花织锦衣，留郎不住送郎归。一色银河三万里，手携石子是支机。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8d4a7b76aabded4ee','湖州竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"月子湾湾照船头，阿侬听我唱湖州。侬心不似双溪水，才出城东各自流。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8e53955ab4c2fdfff','水调歌头·宴顾仲瑛金粟影亭赋桂','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"山横眉黛浅，云拥髻鬟愁。天香笑携满袖，曾向广寒游。素腕光摇宝钏，金缕声停象板，歌罢不胜秋。十指露春笋，佯整玉搔头。\",\"记钱塘，朝载酒，夜藏钩。青衫断肠司马，消减旧风流。三百六桥春色，二十四番花信，重会在苏州。水调按新曲，明月照高楼。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8f22e8d7a3c229011','西湖竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"山上有山未还家，日日望断金犊车。湖阴剩种宜男草，直待郎归始著花。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8f22e8d7a49db20c2','西湖竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"侬家少长住西泠，门对里湖莲叶青。妾心如藕丝不断，郎意忽作随波萍。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8f22e8d7a5fa5fc39','西湖竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"昨夜忆郎开绮窗，平湖月白水如江。妾似两峰日相望，纵有飞来不作双。\"]',NULL,NULL,NULL,NULL),('e647d10fd7f81ea8f22e8d7aae4e7a2b','西湖竹枝词','袁华','e647d10fd7f81ea8d1457b72d1457b72','明',NULL,'[\"南山起云北山阴，暮暮朝朝晴未深。公荷拳似小儿手，虽不成莲已有心。\"]',NULL,NULL,NULL,NULL),('e647d10fd95fa3ee01457a7a153efd05','泛舟江上戏作采莲曲三首','欧大任','e647d10fd95fa3eed1457b72d1457b72','明',NULL,'[\"浦口齐歌出荡边，叶残花败未回船。风吹罗袖沾微雨，道是无情亦可怜。\"]',NULL,NULL,NULL,NULL),('e647d10fd95fa3ee01457a7a6bdf4385','泛舟江上戏作采莲曲三首','欧大任','e647d10fd95fa3eed1457b72d1457b72','明',NULL,'[\"兰舟忆昔下横塘，莲花初开莲叶长。谁道今朝鶗鴂至，回桡愁见两鸳鸯。\"]',NULL,NULL,NULL,NULL),('e647d10fd95fa3ee01457a7a9374a75c','泛舟江上戏作采莲曲三首','欧大任','e647d10fd95fa3eed1457b72d1457b72','明',NULL,'[\"雨过蘼芜绿满洲，一双鸂鶒对沉浮。王孙画舫归何晚，日暮湘江无那愁。\"]',NULL,NULL,NULL,NULL),('e647d10fd982c1c5236ed42fb256c5c8','少年游·重游','柳如是','e647d10fd982c1c5f60bb6bb4f398cb9','明',NULL,'[\"丝丝碧树何曾卷，又是梨花晚。海燕翻翻，那时娇面。做了断肠缘。\",\"寄我红笺人不见。看他罗幕秋千。血衣着地，未息飘扬，也似人心软。\"]',NULL,NULL,NULL,NULL),('e647d10fd982c1c564db81c1a9f968b2','南乡子·落花','柳如是','e647d10fd982c1c5f60bb6bb4f398cb9','明',NULL,'[\"拂断垂垂雨，伤心荡尽春风语。况是樱桃薇院也，堪悲。又有个人儿似你。\",\"莫道无归处，点点香魂清梦里。做杀多情留不得，飞去。愿他少识相思路。\"]',NULL,NULL,NULL,NULL),('e647d10fd982c1c568aabd0c57f5eec2','梦江南·怀人（十首）','柳如是','e647d10fd982c1c5f60bb6bb4f398cb9','明',NULL,'[\"人去也，人去凤城西。细雨湿将红袖意，新芜深与翠眉低。蝴蝶最迷离。\",\"人去也，人去小池台。道是情多还不是，若为恨少却教猜。一望损莓苔。\",\"人去也，人去绿窗纱。赢得病愁输燕子，禁怜模样隔天涯。好处暗相遮。\",\"人去也，人去碧梧阴。未信赚人肠断曲，却疑误我字同心。幽怨不须寻。\",\"人去也，人去小棠梨。强起落花还瑟瑟，别时红泪有些些。门外柳相依。\",\"人何在？人在小中亭。想得起来匀面后，知他和笑是无情。遮莫向谁生。\",\"人何在？人在月明中。半夜夺他金扼臂，殢人还复看芙蓉。心事好朦胧。\",\"人何在？人在木兰舟。总见客时常独语，更无知处在梳头，碧丽怨风流。\",\"人何在？人在玉阶行。不是情痴还欲住，未曾怜处却多心。应是怕情深。\",\"人何在？人在枕函边。只有被头无限泪，一时偷试又须牵。好否要他怜。\"]',NULL,NULL,NULL,NULL),('e647d10fd982c1c5d577b4e2edc5d906','江城子·忆梦','柳如是','e647d10fd982c1c5f60bb6bb4f398cb9','明',NULL,'[\"梦中本是伤心路。芙蓉泪，樱桃语。满帘花片，都受人心误。遮莫今宵风雨话，要他来，来得么。\",\"安排无限销魂事。砑红笺，青绫被。留他无计，去便随他去。算来还有许多时，人近也，愁回处。\"]',NULL,NULL,NULL,NULL),('e647d10fd982c1c5dfcde3bf48a6f691','金明池·咏寒柳','柳如是','e647d10fd982c1c5f60bb6bb4f398cb9','明',NULL,'[\"有怅寒潮，无情残照，正是萧萧南浦。更吹起、霜条孤影，还记得、旧时飞絮。况晚来、烟浪斜阳，见行客、特地瘦腰如舞。总一种凄凉，十分憔悴，尚有燕台佳句。\",\"春日酿成秋日雨。念畴昔风流，暗伤如许。纵饶有、绕堤画舸，冷落尽、水云犹故。忆从前、一点东风，几隔着重帘，眉儿愁苦。待约个梅魂，黄昏月淡，与伊深怜低语。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6021c65ff572e3ff8','沁园春·重阳','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"木叶初飞，重楼雁过，又是重阳。有瓶开清洌，樽中酒绿，枝含瘦冷，篱下花黄。佳节难逢，茱萸细看，剧饮高歌总不妨。君听取，但此身粗健，莫更思量。\",\"古今总是亡羊。漫得失、输嬴梦一场。看龙山事往，夕阳明灭，楚台人去，烟草荒凉。半瞬流光，百年浮世，回首西风易断肠。对知音，且颓然一醉，山月苍茫。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6323aed90d9846d29','踏莎行·韩信庙','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"高岭连云，繁烟带雨。长杨满路悲风起。将军墓上草萧萧，荒祠白日眠狐鼠。\",\"九里山前，未央宫里。凄凉往事烦胸臆。乌江汾水两悠悠，东流不尽英雄泪。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d63edac90adadd5ede','西江月·易水','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"西望远山落日，南来暮柳繁烟。鸟啼花发自年年。成败兴亡几变。\",\"白水河边壮士，黄金台下英贤。到头惟有斩燕丹。干古令人哀叹。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d64593f10dda06b1a8','踏莎行·赵氏二子仲典仲礼皆以奇童称仲典十七秋试归病于途死仲礼十九秋试归病于途死二子生时其母梦紫龙入室即飞去人见二子奇特谓必大成皆夭殁异矣','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"绿柳沙迷，白楼烟袅。莎青花落鹃声杳。紫龙两两没遥天，双双玄鹤归华表。\",\"子建才高，甘罗年少。论经纶、那有君怀抱。竟一抔黄土掩英贤，想九原，痛恨乾坤老。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6478f3191ca90f325','祝英台近·妾薄命','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"夜光阑，纱窗晓，啼鸟正当户。春梦岑岑，遍绕天涯去。恨来还自寻思，五更风雨，应不到、东君去处。\",\"东君处。如有这个风光，应思我难度。况又东风，早晚萦飞絮。看看太半韶华，匆匆过了，是谁把、青春孤负。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6560a0ea3faa9e26e','金菊对芙蓉阅兵雁门登城','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"楼起层城，城蟠绝岫，旌旄上拂云天。看龙蛇阵绕，虎豹关严。班声一喏桑乾沸，青霄外、震裂恒山。有英雄骁将，黄金绣铠，白玉雕鞍。\",\"北望杀气腾腾，更飞霜漠漠万里行。遥把壮心，引动鼙鼓填然。合当痛饮黄龙府，纵兵十日，蒐畋好生擒开罕。招徕冒顿，约束楼兰。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d658076f1511b6a1c7','西江月·春思','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"残雪已消往事，东风又报春愁。珠帘不卷玉香钩。庭院迟迟清昼。\",\"细雨繁花上院，轻烟碧草汀洲。一声啼鸟水东流。春在小桥杨柳。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d659354b8f23c1e2ca','满江红·广武道中','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"漠漠霜天，孤城下、九秋时节。飘零处、寒灯独照，荒山几叠。渺渺长空哀雁叫，凄凄野戍悲笳咽。望秦川、今夜到明朝，头应白。\",\"来时候，柳可折。才回首，流芳歇。叹玉关人老，岁光奔迫。代马踏残青海草，塞风吹落云中月。愧浮沉、今已负平生，应悲切。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6789a46c580f5a657','满江红·吴中秋','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"时序惊心，庭皋下、梧桐一叶。才回首、春光几许，早来秋色。遥忆去年燕市酒，忽看今夕吴中月。最飘零、无定似浮萍，功名客。\",\"江千折，山万叠。壮志寒，归心切。算人间业帐，何时了绝。百岁奔忙愁里过，楚台汉冢君知得若。待黄粱、梦醒始知休，真痴惑。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d68208519b30651559','满江红·客思','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"雪浪连天，还几阵、阴风怒作。正此际、潮生越海，吴江枫落。万里孤舟渔火对，满山溟雨滩声恶。纵瓶中、有酒洌如泉，那堪酌。\",\"愁似海，心如烁。多病体，难支阁。说海棠开了，便酬初约。七夕重阳都已过，楚台空负朝云合。望长安、云树阻归程，成萧索。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d68d40e971eb94a75b','玉楼春·客中春','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"槛外桃花红似血。岸上柳条青可折。美人何处春醉眠，独倚高楼看明月。\",\"楼上玉箫声断绝。江上琵琶还惨切。夜深风送海潮来，回首秦关肠欲结。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d69e0795a873b01fb0','踏莎行·春思','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"梦转红英，愁生绿绮。小窗独对应无语。雨馀花气细侵帘，画梁燕子双飞去。\",\"情绪千端，家山万里。乡心不为功名系。沉云重隔华山青，长河不断空流水。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6a8b75c3d8a66a7e6','永遇乐·妾薄命','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"莺舌轻调，燕泥新缀，蝶梦先醒。篱落谁家，夭桃临水，横放一枝红影。此时无奈，青楼音信，却似瓶沉玉井。叹青春、容易抛掷，早过二分光景。\",\"珠帘半卷，银屏独倚，还把云妆重整。检点韶华，娇柳夭桃，谁占东君穷。此君还似，杨花无定，点点和烟芳径。但无言、凭栏悄悄，此心自省。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6af50d59f0672ee17','鹧鸪天·镇虏台宴诸将','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"戈戟丛中玳宴开。正同诸将捉生回。十年高卧希夷峡，此日还登镇虏台。\",\"长剑舞，巨觥催。帐前金鼓震风雷。李陵碑外天骄遁，遥见旌旗报捷来。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6b893e1d8e45c6ca4','踏莎行·盟台','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"落日荒荒，停云脉脉。行人共指盟台说。相如曾此挫强秦，汗青万古称豪杰。\",\"韩信兴刘，陶朱霸越。英雄自有谋王策。当时一怒顾长刀，将军颈上空流血。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6cce0297d9b47fd30','满江红·哀仲礼','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"四野停云，哀猿哭、潇潇渭水。伤心处、青山雨歇，白杨风起。十二楼中谁是主，三千门下空珠履。想当时、雪夜渡冰舟，成何济。\",\"五经义，十九史。班马才，关闽志。但萋萋荒冢，寒莎遍地。长夜沉沉何日晓，绵绵此恨何时已。看穹碑、历历写衷肠，千行涕。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6d4ca590ab255371c','临江仙·重阳','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"蓝水经霜清彻底，玉山遥送青来。寒云浮雨过庭槐。白衣携酒至，黄菊几枝开。\",\"盛友还逢佳节赏，独怜多病形骸。登临未减昔年怀。西风齐著力，送我上高台。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6d8c9cf92a753c69e','谒金门·春楼','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"珠帘卷。楼外春愁无限。雨过荼蘼春色减。落红惊满院。\",\"枝上燕慵莺懒。谁与韶光为伴。烟柳丝丝迷望眼。阑干空倚遍。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6e9bce9d5ae987978','西江月·同前','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"台下纵横铁骑，筵前错落金卮。风烟不警太平时。正是赏心乐事。\",\"流水落花片片，小桥垂柳丝丝。韶光那觉是边陲。此日须抛一醉。\"]',NULL,NULL,NULL,NULL),('e647d10fd9aa45d6edf04f55a9ab8398','临江仙·三月三十日','韩邦奇','e647d10fd9aa45d6dc09c97fb2dd1403','明',NULL,'[\"芍药牡丹开已罢，明朝不是春光。莺儿撩乱送斜阳。繁华应不尽，蜂蝶且休忙。\",\"几架蔷薇依碧槛，新荷更满芳塘。水晶帘外细飘香。推枰听玉树，移座倒金觞。\"]',NULL,NULL,NULL,NULL),('e647d10fd9b3680e55f751a86344d521','浪淘沙·三首','汤珍','e647d10fd9b3680ed1457b72d1457b72','明',NULL,'[\"淮河一道达清河，如此风波可奈何。东岸沙崩西岸长，南船来较北船多。\"]',NULL,NULL,NULL,NULL),('e647d10fd9b3680e55f751a8bddafaad','浪淘沙·三首','汤珍','e647d10fd9b3680ed1457b72d1457b72','明',NULL,'[\"滟滪瞿塘险未平，狂沙恶浪见堪惊。相逢尽劝公无渡，蜀道那闻断客行。\"]',NULL,NULL,NULL,NULL),('e647d10fd9b3680e55f751a8f58cb3df','浪淘沙·三首','汤珍','e647d10fd9b3680ed1457b72d1457b72','明',NULL,'[\"后浪争催前浪来，前沙汰去后沙堆。乾坤日在淘沙里，风雾冥冥总可哀。\"]',NULL,NULL,NULL,NULL),('e647d10fdaa5c53657659484335f0606','长相思','李攀龙','e647d10fdaa5c53683f255037949e456','明',NULL,'[\"秋风清，秋月明。叶叶梧桐槛外声，难教归梦成。\",\"砌蛩鸣，树鸟惊。塞雁行行天际横，偏伤旅客情。\"]',NULL,NULL,NULL,NULL),('e647d10fdbf3c6d679ff1d1a7d42d528','浣溪沙·红桥','张纲孙','e647d10fdbf3c6d6d1457b72d1457b72','明',NULL,'[\"白舫青帘映碧流，西风初透芰荷秋。玉箫声里度扬州。\",\"二十四桥今寂寞，隋堤芳草莫添愁。如淮美酒醉红楼。\"]',NULL,NULL,NULL,NULL),('e647d10fdc01296f6ba503b6b6c4f108','南乡子·感怀','李炜','e647d10fdc01296fd1457b72d1457b72','明',NULL,'[\"谈笑解吴钩，客满西园花满洲。记得河桥分手路，悠悠，哀笛山阳几度秋。\",\"洒落向神州，极浦遥天不尽愁。花月春江浑是梦，休休，老作人间马少游。\"]',NULL,NULL,NULL,NULL),('e647d10fdc01296ff32f182e70da94bb','浣溪沙','李炜','e647d10fdc01296fd1457b72d1457b72','明',NULL,'[\"修竹天寒倚翠娥，鸳鸯霜重雁声过。更长无寐倚云和。\",\"桃叶渡头频问信，石尤江上怨风波。人生憔悴是情多。\"]',NULL,NULL,NULL,NULL),('e647d10fdc1fa6a2c0395c4dc778ab61','浣溪沙·红桥即事','蒋平阶','e647d10fdc1fa6a2d1457b72d1457b72','明',NULL,'[\"柳外高楼一带遮，门前偷下六萌车。玉环双臂绾红纱。\",\"十二阑干闲倚遍，黄莺啼上内人斜。隔江愁听后庭花。\"]',NULL,NULL,NULL,NULL),('e647d10fdd19b5835978d4c21e32658a','眼儿媚·赠卞校书','万寿祺','e647d10fdd19b583f3173935207f8801','明',NULL,'[\"花弄香纹春满楼，桃叶引江流。个人何事，斜阳独倚、曲曲肠柔。\",\"垂帘淡淡扑杨球，私心好处投。侍儿敛态，闭门作意，不上金钩。\"]',NULL,NULL,NULL,NULL),('e647d10fdd9e4fcb9b02ba6d7222e516','菩萨蛮','贺颜登','e647d10fdd9e4fcbd1457b72d1457b72','明',NULL,'[\"绿窗拥被莺呼醒，红云微皱愁难整。檀炷袅薰笼，罗衣三两重。\",\"流苏休便揭，欲起愁无力。杨柳小腰肢，春来瘦不支。\"]',NULL,NULL,NULL,NULL),('e647d10fddaacfc7a6496c9fa3a4234c','八声甘州·留别阿字无兄','释今释','e647d10fddaacfc7d1457b72d1457b72','明',NULL,'[\"叹离情一往一重来，可似海门潮。已千秋期许，十年建立，两地忧劳。又道如形伴影，彼此不容招。底事扁舟上，回首迢迢。\",\"待向梅关北去，倩宾鸿寄语，云阔天高。想闻声欲下，转眼失鹪鹩。更有时、连床续梦，怕冰霜、折断岁寒梢。晨钟起、添些风雨，相助萧萧。\"]',NULL,NULL,NULL,NULL),('e647d10fddaacfc7b42c654f04b782ee','小重山·得程民部诗却寄','释今释','e647d10fddaacfc7d1457b72d1457b72','明',NULL,'[\"落落寒云晓不流，是谁能寄语、竹窗幽。远怀如画一天秋，钟徐歇、独自倚层楼。\",\"点点鬓霜稠，十年山水梦、未全收。相期人在别峰头，闲鸥意、烟雨又扁舟。\"]',NULL,NULL,NULL,NULL),('e647d10fdf0851365a7b7cdb7799e43b','苏武慢秋日旅感','包尔庚','e647d10fdf085136d1457b72d1457b72','明',NULL,'[\"玉分红，金橙剖绿，渐报江南秋老。正磋摇落，况复清砧，空外又闻频捣。佩剑磨残，唾壶击碎，耗却壮心多少。怅年来、病翮缡褷，逊他飞鸟。\",\"念从古、赋擅凌云，笔能摇岳，一例天涯潦倒。质衣酒肆，贷粟监河，不及侏儒长饱。潘鬓将凋，沈腰增瘦，留得一囊玄草。胜寒灯如豆，蛩吟四壁，伴人幽悄。\"]',NULL,NULL,NULL,NULL),('e647d10fdf8be79e1e453053297b9cdf','长相思·寄于夫人','瞿寄安','e647d10fdf8be79ed1457b72d1457b72','明',NULL,'[\"朝含颦。暮含颦。风动疏帘疑是君。相思欲断魂。\",\"试罗裙。褪罗裙。鸳瓦霜飞病又新。天涯两地人。\"]',NULL,NULL,NULL,NULL),('e647d10fdfa392116c78744f6a79e564','蝶恋花·贺友新婚','沈家恒','e647d10fdfa39211d1457b72d1457b72','明',NULL,'[\"莫道好春无处讨，岭上琼花，开向金闺早。酒醒香浓天未晓，一丸璧月窥窗悄。\",\"初整晨妆眉样巧，笑问檀奴，秀色餐多少。更喜今年春色好，阶前渐绿宜男草。\"]',NULL,NULL,NULL,NULL),('e647d10fe20d0a2271789482a86383cf','浪淘沙·新秋','刘碧','e647d10fe20d0a22d1457b72d1457b72','明',NULL,'[\"昨夜雨绵绵。寒涩灯烟。薄衾萧索不成眠。晓起床头看历日，换了秋天。\",\"绿叶尚新鲜。犹想争妍。教他知道也凄然。眼底韶光容易改，树且堪怜。\"]',NULL,NULL,NULL,NULL),('e647d10fe20d0a22ce405a22a4e7d251','苏幕遮·客里送春','刘碧','e647d10fe20d0a22d1457b72d1457b72','明',NULL,'[\"乱云中，残照里。又送他归，懒唤莺儿起。山径落红谁是主，别恨离情，目断韶华矣。\",\"擘涛笺，飞翠尾。每到关心，逗得愁如绮。岁岁劳人长徙倚，一似杨花，暗去随江水。\"]',NULL,NULL,NULL,NULL),('e647d10fe2c807f125eb6af7f7a2c197','清平乐','韩纯玉','e647d10fe2c807f1a981f2b7375c7134','明',NULL,'[\"春来消息，早过三之一。乍暖余寒无气力，二十四番风急。\",\"日长花事相宜，只愁开到荼蘼。偶向晴窗弄墨，拈诗又是无题。\"]',NULL,NULL,NULL,NULL),('e647d10fe2c807f179bb0013f430e5c2','江城子','韩纯玉','e647d10fe2c807f1a981f2b7375c7134','明',NULL,'[\"眉峰烟锁未分明，过秦青，恨零星。一树樱桃、微雨梦难醒。月在当头杯在手，愁似海，总填平。\",\"春宵三五好心情，绕红亭，笑声盈。促席传觞、倚醉听搊筝。嫌煞铜壶银漏短，香灺烬，烛花横。\"]',NULL,NULL,NULL,NULL),('e647d10fe2c807f186b9120283414117','虞美人','韩纯玉','e647d10fe2c807f1a981f2b7375c7134','明',NULL,'[\"一帘花影春风夜，月到蔷薇架。绮疏一半晚犹开，只为画梁双燕、未归来。\",\"挑灯约莫黄昏过，犹自薰香坐。星河耿耿傍阑干。\",\"留取小窗相对、晚妆残。\"]',NULL,NULL,NULL,NULL),('e647d10fe2c807f1ccc9a8aa2ceacb4a','菩萨蛮·夏日访潘子见归舟作','韩纯玉','e647d10fe2c807f1a981f2b7375c7134','明',NULL,'[\"夹山一带山平远，湘溪问字归家晚。荷气晚来多，斜阳雨半蓑。\",\"吾生何所寄，泛宅浮家去。苕水碧于天，孤舟独往还。\"]',NULL,NULL,NULL,NULL),('e647d10fe2c807f1e2dbf0ec4e18a13c','小重山·和稼轩韵','韩纯玉','e647d10fe2c807f1a981f2b7375c7134','明',NULL,'[\"万顷玻璃水接空，雨蓑烟笠下、一渔翁。荷香断续柳西东，秋光好、两岸插芙蓉。\",\"十里水晶宫，有时随浪去、笑儿童。三秋几阵鲤鱼风，斜阳外、沉醉荻花中。\"]',NULL,NULL,NULL,NULL),('e647d10fe2c807f1f32f182ea82a973f','浣溪沙','韩纯玉','e647d10fe2c807f1a981f2b7375c7134','明',NULL,'[\"手卷虾须上玉钩，疏疏急雨下梧楸。韶光生怕付东流。\",\"红叶窗中无俗事，白云乡里有温柔。好留双鬓莫教秋。\"]',NULL,NULL,NULL,NULL),('e647d10fe3258057e429ae7955648025','如梦令','冯琦','e647d10fe325805729921001f3173935','明',NULL,'[\"竹外瑶华千顷，更与素梅掩映。双雀噪寒枝，蹴落飞花无影。\",\"相并，相并，明月惊栖不定。\"]',NULL,NULL,NULL,NULL),('e647d10fe3c9e68ed629981b3f813cb3','南乡子·夏日游竹隐寺','骆文盛','e647d10fe3c9e68e1415db7098986c00','明',NULL,'[\"古寺隐丛篁，石径逶迤碧藓荒。夹道松枝低拂幰，苍凉，清露沾衣湿不妨。\",\"缓步上回廊，老衲相邀入竹房。旋汲山泉供茗碗，悠扬，风送闲云下石床。\"]',NULL,NULL,NULL,NULL),('e647d10fe3df1d0ef137049b723aeb65','阮郎归','徐阶','e647d10fe3df1d0e0c8ce551076023ed','明',NULL,'[\"一春归思羡冥鸿，青山在眼中。无端秋色又丹枫，沧洲路未通。\",\"搔白首，望长空，徘徊意万重。持杯看尔去匆匆，心随江水东。\"]',NULL,NULL,NULL,NULL),('e647d10fe4818f80154effbddb66cc08','浪淘沙','俞汝言','e647d10fe4818f80e9fd7c2ce6384711','明',NULL,'[\"芳草遍天涯，几引流霞，小楼宛转暮云遮。记得当时歌舞地，眷柳怜花。\",\"归路一鞭斜，断送年华，红阑几曲碧窗纱。酒醒更残灯又灺，梦落谁家。\"]',NULL,NULL,NULL,NULL),('e647d10fe48916b65e4c507b6a86201d','双双燕','张琨','e647d10fe48916b6d1457b72d1457b72','明',NULL,'[\"披蓬探隐，到青草溪头，碧阴山曲。竹韵松姿，争媚吟眸诗腹。是处翠峰成簇，更秋雨、新凉如沐。深林宛转鸣禽，幽涧琤琮漱玉。\",\"孤独，简编娱目。有纫佩芳兰，供餐茂菊。藤床闲憩，梦入华胥仙国。又早月明帘轴，恰一枕、清酣睡足。小小花童低报，瓦鼎烹茶刚熟。\"]',NULL,NULL,NULL,NULL),('e647d10fe4bb8083ee754c5833267df3','采莲曲','魏学礼','e647d10fe4bb8083d1457b72d1457b72','明',NULL,'[\"烟中一叶采莲舟，两岸香风正早秋。瞥见江南明月上，玉箫吹断紫云愁。\"]',NULL,NULL,NULL,NULL),('e647d10fe64278a8576594847f82b112','长相思','周恭','e647d10fe64278a8d1457b72d1457b72','明',NULL,'[\"阻佳期，盼佳期，欲寄鸾笺雁字稀。新词和泪题。\",\"怕分离，又分离，无限相思诉与谁。此情明月知。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea900bf33ff5fdabe40','唐多令·寒食','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明','坐落在北京天寿山的明十三陵，从某个角度来说是朱明王朝的象征。当清兵的铁蹄踏上十三陵之后，忠于朱明王朝的人上无不痛心疾首、难以接受此事实。当 时，因抗清而身陷囹圄的作者听到这个消息后，就在狱中含泪写下这首被人称作“绝笔’的词作。【疏星注：陈子龙从未进过监狱，这首词是和朋友倡和时所填。】 故国之思，优愤之情溢于言表，结句更明确表达了期复明室的不可动摇的意念。【疏星注：陈子龙填此词时，对复国已经不存希望，也已失去不可动摇的信念，此词 只是哀叹故国沦亡，亲友凋零。】全词凄怨激楚，悲愤填膺，与国变之前的作品显然大异其趣，足见作者词风在国破家亡后的转变。 ','[\"碧草带芳林，寒塘涨水深。五更风雨断遥岑。雨下飞花花上泪，吹不去，两难禁。\",\"双缕绣盘金，平沙油壁侵。宫人斜外柳阴阴。回首西陵松柏路，肠断也，结同心。\"]',NULL,NULL,NULL,'[\"选自《陈忠裕公全集》。先朝：前朝。此处指朱明王朝。陵寝：帝王陵墓建筑。\",\"遥岑：远山。唐韩愈《孟郊联句》：\\u201C遥岑出寸碧，远目增双明。\\u201D\",\"双缕：双丝线。盘金:季咸用诗：\\u201C盘金束紫身属官，强仁小德终无端。\\u201D\",\"油壁：车名。妇人所乘之车，车身饰以油漆，故名。\",\"宫人斜：唐代宫女的坟墓。宋张侃《宫人斜》诗：\\u201C万古宫人斜上望，淡烟衰草为凄然。\\u201D此处泛指宫人之墓。\",\"西陵：此指坐落在北京天寿山的明十三陵。 \"]'),('e647d10fe6b62ea90b31fada27855da1','蝶恋花·金屋珠楼多遍了','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"金屋珠楼多遍了，试问东风，着意知多少。一种艳阳浑欲扫，飞烟浅破丁香小。\",\"初放柳条明月晓，为写相思，惯绣双双鸟。莫说轻红何处好，催人连夜伤春早。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea90beb2b68572c95b5','诉衷情令·春游','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明','上片“小桃枝下试罗裳”，是说出游前的准备。天气渐暖，要换上春天的罗裳，不是一换就得，所以要试，试衣于小桃枝下，很可能是普通农家的女孩子。站在小桃枝下，映衬唐人崔护《题都城南庄》中的“人面桃花相映红”。不仅如此，鲜艳的桃花和女子衣上的香泽又引来了翩翩的蝴蝶。不说蝴蝶环绕飞舞，却说“蝶粉斗遗香”，蝶翅上扑落的粉末和女子遗落的香泽比赛，哪一种更香，这就婉转曲折深有意趣了。在花丛中翩翩起舞的蝴蝶，本来也是美的化身，所以梁山伯祝英台的故事中，才有化蝶之说。\n游春的人自然很多，有乘宝马香车的，“玉轮碾平芳草”，从芳草地上忽忽驶过，车中的贵妇娇娃，也只在车窗中露出了半面。但“半面恼红妆”，这半面娇容却引起这位游春少女的不快。可能是因为她们乘坐的香车对于芳草的摧残，也可能因为她们不暇他顾的高傲姿态。李商隐《南朝》诗云：“地险悠悠天险长，金陵王气应瑶光。休夸此地分天下，只得徐妃半面妆。”讽刺南朝苟安于半壁江山，犹如徐妃之半面妆，如取用李商隐诗意，则“半面恼红妆”当指南明福王朱由崧即位南京，只图苟安一时，如同南朝梁、陈末年故事。这种时事使人烦恼，故云：“半面恼红妆。”惟此，前面的“玉轮碾平芳草”也可解释为暗指南明弘光朝排斥忠良压制贤才，这样来解释这两句词，可能显得牵强一些，但是，“半面恼红妆”又似乎不是一般语言，很值得探究。\n下片“风乍暖，日初长，袅垂杨”三句，写的是初春景象。风才开始变暖，白天才开始变长，垂挂的柳枝才缀上新绿而随风飘荡。三句非常紧凑。从几种事物中观察，感受表现了初春的特点。而以下的三句“一双舞燕，万点飞花，满地斜阳”却大有春事已过的景象。关于燕子，在子龙词中多次提及，如《山花子·春恨》“惟有无情双燕子，舞东风”，《眼儿媚》“只愁又见，柳绵乱落，燕语星星”、《蝶恋花·春日》“燕子乍来春渐老，乱红相对愁眉扫”、《江城子·病起春尽》“凭燕子，骂东风”。几乎都是与暮春春尽联系着。此处“一双舞燕”恐怕也不是早春景象，“万点飞花”更是暮春特有之景，“满地斜阳”也平添了一份凄清。这后面的三个四字句，与前面的三个三字句，意思上有承接，时间上有发展，似是感叹着“九十韶光如梦短”（《渔家傲》），由初春写到春夫，时间上的跨度是为了表现情感上的跨度。\n这首词描写春游所见，情景相生，处处透着词人盎然的游兴。词的开始两句用烘托法。桃下试裳能让人产生“桃之天天，灼灼其华”的联想。而蝶粉斗香，又可想见女子春服艳丽如花的美好形象。接下来轮碾芳草，红妆半露，也是情态毕现，鲜明如画。整个上片着笔于人，浓墨重彩。下片则转而写景。风暖日长，垂杨袅袅，双燕翩翩，一派生机蓬勃的景象。景润人心，词人想必也是逸兴满怀。结尾“万点飞花，满地斜阳”微微露出美景不能永驻的淡淡春愁。正如杜甫《曲江》“一片花飞减却春，风飘万点正愁人”及梅尧臣《苏幕遮》“落尽梨花春事了。满地斜阳，翠色和烟老”所歌咏，在这样的时刻，文人总不免会透出一丝纤柔的情怀。','[\"小桃枝下试罗裳，蝶粉斗遗香。玉轮碾平芳草，半面恼红妆。\",\"风乍暖，日初长，袅垂杨。一双舞燕，万点飞花，满地斜阳。\"]','[\"清代文学家王士祯：清真能作景语，不能作情语。至大樽而情景相生，令人有后来之叹。\",\"安徽大学中文系副教授惠淇源《婉约词全解》：桃下试裳而至蝶粉斗香，可见其春服艳丽。所绣花朵几能乱真；轮碾芳草而红妆半露，情态如画。风暖日长，垂杨袅袅，正是纵情逸兴之时；双燕飞花，满地斜阳，又暗示良辰难久，美景不长。全词工丽婉媚，情景交融。\"]','有人在桃树下试穿丝衣，蝴蝶在残余的花间飞舞。芳草被华美的车子辗平，花枝上，花落已半。\n风变得薰暖，白天开始长起来，杨柳的枝条柔美细长。一双燕子在风中飞舞，杨柳絮飘飘扬扬，地上铺满了落日的余晖。','《诉衷情令·春游》是明末文学家陈子龙所作的一首词。上片写一位女子在春天的出游，着笔于人，浓墨重彩。下片则转而写景，写了所见所感的春天的风物。全词采用以景寄情手法，使作品具有很深的意境，写时节由初春转入暮春，语言明丽，格调凄婉，亡国之悲见于言外。','[\"诉衷情令：又名《一丝风》、《渔父家风》。正体双调四十四字，前段四句三平韵，后段六句三平韵。注意不应与唐教坊曲名《诉衷情》混淆。\",\"罗裳：织锦的裤裙。裳，下身的衣服，裙装。\",\"蝶粉：指桃花初放，蕊粉末褪。斗：比试。遗香：指罗裳和少女身体散发出的清香。\",\"玉轮：华贵的游车。\",\"半面恼红妆：即\\u201C恼半面红妆\\u201D之倒装句。指因花瓣凋零而心生懊憾。半面：指凋残。红妆：指花瓣。\",\"乍：开始。\",\"袅（niǎo）：细柔摇曳的形态。\"]'),('e647d10fe6b62ea90eae24c8cac821fa','丑奴儿令·春水','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"赤栏桥下烟波急，欲问西洲。莫寄东游，千里清江一线浮。\",\"落花乱点湘纹皱，昨日琼楼。今日兰舟，为送多情晓夜流。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea91f0d3d86d2789a7c','蝶恋花·春闺','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"紫燕香泯归画栋。卷上帘钩，杨柳笼烟重。窗外巧莺啼一弄，飞花只有东风送。\",\"锦瑟瑶琴都不动。倦倚阑干，白日耽幽梦。金鸭微温红袖拥，芙蓉半掩鞋头凤。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9235c06702c470d45','蝶恋花·落叶和舒章','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"金井雕栏蛩语歇，独上空阶，簌簌惊罗袜。满院西风人影绝，乱鸦啼断寒枝月。\",\"今夜霜花楼外滑，一曲屏山，遮过灯明灭。几阵纱窗声不辍，梦中又到愁时节。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea925eb6af74830982d','清平乐','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"绣帘花散，难与东风算。拈得金针丝又乱，尚剩檀心一半。\",\"几回黛蹙双蛾，斜添红缕微波。闲看燕泥欲堕，柳绵吹满轻罗。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea93185841038e1542f','山花子','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"杨柳凄迷晓雾中，杏花零落五更钟。寂寂景阳宫外月，照残红。\",\"蝶化彩衣金缕尽，虫衔画粉玉楼空。惟有无情双燕子，舞东风。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea939955a28ce32ca46','虞美人·镜','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"碧阑囊锦妆台晓，泠泠相对早。剪来方尺小清波，容得许多憔悴暗消磨。\",\"海棠一夜轻红倦，何事教重见？数行珠泪倩他流，莫道无情物也替人愁。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea94334969f3d7b90da','蝶恋花','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"袅袅花阴罗袜软，无限芳心，初与春消遣。小试娇莺才半转，海棠枝上东风浅。\",\"一段行云何处剪，掩过雕阑，送影湘裙展。隔著乱红人去远，画楼今夜珠帘卷。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea94334969f527b2f64','蝶恋花','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"雨外黄昏花外晓，催得流年，有恨何时了。燕子乍来春又老，乱红相对愁眉扫。\",\"午梦阑珊归梦杳，醒后思量，踏遍闲庭草。几度东风人意恼，深深院落芳心小。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9443d63999c878d6c','如梦令·本意四首','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"【其一】\",\"烟景霏微阴洞，堤上紫骝骄鞚。贪看两鸳鸯，行过金鳌玉蝀。如梦，如梦，花月十年情重。\",\"【其二】\",\"绮阁沉沉烟重，倒映绿波风动。悄立晓寒侵，依旧雕阑画栋。如梦，如梦，玉树数声残弄。\",\"【其三】\",\"天上仙裾无缝，环珮飘飖风送。倚遍小栏干，咫尺烟迷云冻。如梦，如梦，瀛海玉箫双凤。\",\"【其四】\",\"醽醁宜春瑶瓮，门外青丝云拥。今夜好思量，总教玉人珍重。如梦，如梦，满地落红催送。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea946e45e89a80cd51b','踏莎行·寄书','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"无限心苗，鸾笺半截，写成亲衬胸前折。临行检点泪痕多，重题小字三声咽。\",\"两地魂销，一分难说，也须暗里思清切。归来认取断肠人，开缄应见红文灭。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea94f3704de10304c62','念奴娇·春雪咏兰','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"问天何意，到春深、千里龙山飞雪？解佩凌波人不见，漫说芷珠宫阙。楚殿烟微，湘潭月冷，料得都攀折。嫣然幽谷，只愁又听啼鴂。\",\"当日九畹光风，数茎清露，纤手分花叶。曾在多情怀袖里，一缕同心千结。玉腕香销，云鬟雾掩，空赠金跳脱。洛滨江上，寻芳再望佳节。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea958df03984da22196','长相思·西湖雨中','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"黛眉收，翠鬟流，厌损芳波一段愁，愁时梦未休。\",\"山浮浮，水悠悠，欲问行云何处留？有人天际头。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9606d191e58609459','青玉案','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"海棠枝上流莺啭，试小立、春风面。细草凌波红一线。碧云凝照，绿杨零乱，重锁深深院。\",\"甘蕉翠滴当心卷，遍写相思空自遣。归去枕函曾梦见。一天星月，满庭风露，吹落梨花片。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea963225ddfa801a97f','蝶恋花·才与五更春梦别','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"才与五更春梦别。半醒帘栊，偷照人清切。简点凤鞋交半折，泪痕落镜红明灭。\",\"枝上流莺啼不绝。故脱馀绵，忍耐寒时节。慵把玉钗轻绾结，恁移花影窗前没。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea96ffdaf8a01d060f6','望江南','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"无限意，消息更悠悠。弱柳三眠春梦杳，远山一角晓眉愁，无计问东流。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea96ffdaf8a08d3cf6f','望江南','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"思往事，花月正朦胧。玉燕风斜云鬓上，金猊香尽画屏中。\",\"半醉倚轻红。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea979bb001331b1f0b1','江城子','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"一帘病枕五更钟，晓云空，卷残红。无情春色，去矣几时逢。添我千行清泪也，留不住，苦匆匆。\",\"楚宫吴苑草茸茸，恋芳丛，绕游蜂。料得来年、相见画屏中。人自伤心花自笑，凭燕子，驾东风。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea97e24f1f3a5fbd98b','忆秦娥·杨花','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"春漠漠，香云吹断红文幕。红文幕，一帘残梦，任他飘泊。\",\"轻狂无奈春风恶，蜂黄蝶粉同零落。同零落，满池萍水，夕阳楼阁。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea986b912024b91de0a','虞美人','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"枝头残雪余寒透，人影花阴瘦。红妆悄立暗消魂，镇日相看无语，又黄昏。\",\"香云黯淡疏更歇，惯伴纤纤月。冰心寂寞恐难禁，早被晓风零乱、又春深。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea98bf7b078dc8e4b89','点绛唇·黄昏','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"帘卷斜阳，东风罗带吹芳径。月华初映，人影阑干并。\",\"又怯空房，花下双鬟等。青灯冷，碧纱烟静，半晌愁难定。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9b2801a2e0c32ec04','天仙子','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"古道棠梨寒恻恻，子规满路东风湿。留连好景为谁愁，归潮急，暮云碧，和雨和晴人不识。\",\"北望音书迷故国，一江春雨无消息。强将此恨问花枝，嫣红积，莺如织，侬泪未弹花泪滴。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9b2801a2e22d8a4ec','天仙子','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"十二画屏围楚岫，一缕水沉携满袖。小桃纤甲印流霞，听玉漏，人归后，两点横波微晕透。\",\"豆蔻梢头春雨瘦，云腻暖金灯下溜。镜台斜背解罗衣，芙蓉绣，丁香扣，宝袜酥温红影皱。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9b6bd02e5c7e6108e','谒金门·五月雨','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明','这首词的标题是“五月雨”五月雨也就是梅子黄时雨，柳宗元诗云：“梅实迎时雨，苍茫值晚春。”词中所写，正是这种景象。　\n上片写景，在那黄莺儿啼叫的地方，满天正飘洒着雨丝，“疏雨”是间歇的雨，“摇荡”形容雨丝飘洒的状态，正所谓“雨丝风片”，再加上间断的莺啼，再准确不过地写出了梅雨的特点。极目远眺，平野之上没有行人，只见残剩的红花缀在绿树上，经过雨的冲洗，显得格外鲜亮。“极目平芜人尽去”，可能取欧阳修《踏莎行》“平芜尽处是春山，行人更在春山外”的意境。雨天行人稀少，树是那样绿，花是那样红，词人的观察总是十分细腻、具体的。　\n下片写情，主要写词人的感受。江南梅雨季节，湿度大，衣物易生霉，本来古代上层家庭就好在室内薰香，此时此地更要点燃炉香来除湿。所以说： “费得炉烟无数”此亦从周邦彦《满庭芳·夏日溧水无想山作》“地卑山近，衣润费炉烟”化出。梅雨季节，江南正处于冷暖空气交会地带，故而多雨，并且气温多变，早晚甚至还会感到轻微的寒意，词中说“只有轻寒难度”，正是指的这种气候，“难度”则属于诗人的感受了。最后两句由雨写到转晴，忽雨忽晴也止是梅雨季节的特点，诗人强调的是由雨转晴完全在不知不觉之间，“疏雨”似乎一直在下着，然而忽然看见西楼边上露出了花影，天已放晴了。阳光照射下，才有花影。这花影摇曳着，摆弄着阳光。时间已接近傍晚，花影随着太阳光线移动，就好像在催促着薄暮的到来。这里“弄”字“催”字都用得很巧妙。在词中“弄”是一个很有表现力的动词，与张先《天仙子》“云破月来花弄影”有异曲同工之妙。张词写月光照射下，花影在轻轻摇动，于寂静中显出动态，所以王国维《人间词话》说：“着一‘弄’字而境界全出矣。”这首词取法这一 “弄”字，也用得相当精彩，不说“花弄影’，而说“花影弄晴”，晴是阳光给人的感觉，就是说花影摇动引起光线的明暗变化，这样就有了新的创造。而接言的“催薄暮”，用一个动词“催”字，赋予花影以感情的色彩。薄暮是自然到来的，在花影轻摇中阳光渐渐移去，给人的感觉似乎是催促傍晚的到来，感觉十分细腻，表达则十分精彩。\n前人评陈子龙词“婉丽、清艳”、“情深一往，情韵凄清”。他的词属于“婉约”派，这一派认为词的本质就应该是婉转缠绵、含而不露的。在陈子龙的词中一些细腻曲折的表达，确实体现了这种特点。但他所处的时代和他本人的归宿，却是与柔情蜜意、轻歌曼舞的生活不甚相容的，所以前人又特别强调他的“艳”不是浓得化不开的艳，而是“清艳”。情韵更不是“婉媚”，而是“凄清”。在他存世的数十首词中，大都是抒写节候变化和自然风物的，其中也颇多批评时事的寓意和忧时伤世感情的曲折表达，至于本篇，主要用赋体写成。既然找不到什么寄托的根据，我们且将它作为一篇描写节候风物的佳作来欣赏吧。','[\"莺啼处，摇荡一天疏雨。极目平芜人尽去，断红明碧树。\",\"费得炉烟无数，只有轻寒难度。忽见西楼花影露，弄晴催薄暮。\"]',NULL,NULL,NULL,'[\"凉雨：一作\\u201C疏雨\\u201D。\",\"平芜：草木丛生之旷野。\",\"断虹：一作\\u201C断红\\u201D。\",\"费得炉烟无数：宋周邦彦《满庭芳·夏日溧水无想山作》词：\\u201C地卑山近，衣润费炉烟。\\u201D炉烟，指炉火。 \"]'),('e647d10fe6b62ea9c28a97da578435c8','小重山·忆昔','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"晓日重檐挂玉钩，凤凰台上客，忆同游。笙歌如梦倚无愁，长江水，偏是爱东流。\",\"荒草思悠悠，宫花飞不尽，覆芳洲。临春非复旧妆楼，楼头月，波上对扬州。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9c5a46287061d30dd','画堂春·雨中杏花','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明','首二句“轻阴池馆水平桥，一番弄雨花梢”，勾勒出“池馆”的气象景物：天色微阴，春雨绵绵，池塘水涨，已与桥平，细雨又把杏花浸洗一番。这二句似乎实写南园桥边雨中杏花，却分明是虚写风雨摧残中的柳如是，以实写虚，从而得到虚实相生的艺术效果。\n紧接着二句“微寒着处不胜娇，此际魂销”，描绘了一幅杏花娇弱，好象承受不住春寒风雨的景象，这让伤名的诗人黯然销魂。\n上片一反诗词中用杏花装点热闹繁荣场面的惯常手法，用重笔描写它在春寒冷雨之中的“不胜娇”。在作者渲染的这种令人黯然神伤的情景中，作为虚影叠印的柳如是，则已绰约其中了。\n下片则借杏花的“零乱’’写柳如是的飘零身世，以抒发一己的“无那”心绪。\n“忆昔青门堤外，粉香零乱朝朝。玉颜寂寞淡红飘。”一个“忆”字，引出城门外长堤边这一派红颜憔悴、玉蕊凋零的残败景象。几句中三次写花，三次写法各不同：一曰“粉香”；二曰“玉颜”；三曰“淡红”。从“味”、“形”、“色”描绘之，美的确美矣。只可惜红颜薄命，生不逢地，只能用“寂寞”打发日子。\n这是一首咏物词，词中所咏的，是春雨微寒中的杏花。但吟味再三，雨中花间，总依稀晃动着一位佳人的倩影：她就是江南名妓柳如是。词人笔下的“雨中杏花”，就是他心中的情人柳如是：当年的卖笑生涯，留下了“青门堤外，粉香零乱朝朝”的记忆；如今，与词人同居，承受种种欢爱，却也遭遇无边的压力，仿佛“奔雨花梢”，“微寒著处不胜娇”。她以“寂寞”抗争，在无声中忍受巨大的精神摧残，任凭“玉颜”“淡红飘”，让青春年华流逝。词人对柳氏的遭际黯然“魂销”，却也无可奈何，只能徒唤“无那”。','[\"轻阴池馆水平桥，一番弄雨花梢。微寒著处不胜娇，此际魂销。\",\"忆昔青门堤外，粉香零乱朝朝。玉颜寂寞淡红飘，无那今宵。\"]','[\"清初文学家王士稹《陈忠裕全集》：\\u201C嫣然欲绝。\\u201D\"]','清明前后，云淡天阴，池塘和小桥边上，只见那毛毛细雨滋润着盛放的杏花，在微寒的天气下更显娇艳，也更为婀娜多姿。\n想当年在那京师门外的堤岸上，一阵狂风暴雨过后，几日之间，杏花身殒香灭。花朵已经落尽的杏花，连蜂蝶都不来光顾了，只好守着寂寞过日子，它哪里还有一刻千金的春宵？','《画堂春·雨中杏花》是明代词人陈子龙所写的一首词。该词上片通过描绘眼前的景象来表达自己内心的情感，下片借杏花的“零乱’’写柳如是的飘零身世来表达自己的无奈情绪。全词表达了词人对外妇柳如是的怀念之情以及两人无法在一起的黯然情感。','[\"画堂春：词牌名。最初见于《淮海居士长短句》。双调，有四十六字至四十九字四格，前片四平韵，后片三平韵。\",\"青门：汉长安东南门，本名霸城门，因其色青，故俗称为青门。\",\"玉颜：指杏花。\",\"无那：无奈。\"]'),('e647d10fe6b62ea9d07d559641a460bc','虞美人·杂咏','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"夭桃红杏春将半，总被东风换。王孙芳草路微茫，只有青山依旧对斜阳。\",\"绮罗如在无人到，明月空相照。梦中楼阁水湛湛，撇下一天星露满江南。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9d0f3f9ad2c3ef436','浣溪沙·杨花','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明','上阕“百尺章台撩乱飞，重重帘幕弄春晖”，是对柳如是处于名妓生活的写照。这里明写杨柳，暗喻柳如是之姓，语意双关。“百尺”，指柳如是当时居住高楼，名望甚高，难以攀折。“撩乱飞”，有思绪撩乱纷纷之意。“重重帘幕”，指柳氏所居之深宅大院，甚为豪华。“弄春晖”，指舞弄着“春色”，正在走红。整个两句，描写柳如是当时在妓女界的名望、地位及其豪华生活。对这位功名未就、生活拮据的年少书生陈子龙来说，自然既存爱慕之心，又有怜惜之意。遂有“怜他漂泊奈他飞”的感慨。柳如是幼年即为周道登玩弄于股掌之上，年十五流落风尘，漂泊的命运犹如杨花柳絮，引起陈子龙的怜惜与同情；加以对柳如是深情相爱，但由于自己生计尚不富裕，家有嫡妻把持，无法将柳如是救出水火，结为鸾风，在这无奈之中，只有像杨花一样，任他随风飘荡了。\n下阕“澹日滚残花影下，软风吹送玉楼西”，这里将柳如是的未来生涯，继续比作杨花柳絮，作动态的描述加以警告。此处“淡日”，指暗淡的日子。“软风”，指柔和的微风。就是说，不须要强劲的风，即可轻轻地将花下的絮球吹到楼边墙角，了它残生。这里有着深刻的含义，作者向他的恋人柳如是提示：不要只看到在这青春盛日，被那名流学士竞相追逐的繁华似锦的生活（指上阕“重重帘幕弄春晖”的盛况），还须想到日后年老色衰，渐渐为人所弃的暗淡日子里的悲惨处境。上阕一个“弄”字，下阕一个“残”字，上阕渲染“春晖”，下阕揭示“淡日”，一前一后，遥相呼应，一喜一悲，相互映照，很是值得玩味。最后以“天涯心事少人知”作结，含蓄有致，给读者留下了想像的馀地。就是说，我的这一番心事，是很少有人知道的，您可知否？真是语重心长，含情脉脉，其中蕴藏着多少感人的肺腑之言！至于此处的“天涯”，究竟指的是：人在天涯，寄语情怀；还是人在咫尺，心怀天涯之事？又，这番“心事”，究竟指的是：恋情堪虑的心事，还是事业抱负的心事？都未明言。所有这些，留给读者去品味吧！\n陈子龙在这短短的小令中，感情丰富真挚，笔调高雅蕴藉，非大手笔莫能为之。由此即可看出他真情实感及其文学才华。','[\"百尺章台撩乱飞，重重帘幕弄春晖。怜他飘泊奈他飞。\",\"澹日滚残花影下，软风吹送玉楼西。天涯心事少人知。\"]','[\"清·王士祯《陈忠裕全集》云：不著色相，咏物神境。\"]','在高高的专门送别的章台旁缭乱飞舞，在重重的帘幕前的春天阳光中翻腾，可怜啊，它这样无境止地飘泊，但又有怎样的办法不让它飞扬。\n在淡淡的日光中慢慢飘摇洒落于花丛阴影下，又被温熙的和风吹拂到华美的高楼的西边，如那在天涯飘泊流离的旅人一样，它的心事少有人知道。','《浣溪沙·杨花》作于崇祯八年（公元1635年）。是年春季至夏初，作者与柳如是同居于松江生生庵别墅南楼。在此期间陈子龙写了大量情词，以寄托他们真挚热烈的爱情。柳如是为明末负有盛名的才女和美女，又是难得的爱国志士，与陈子龙情投意合，有着一段美好的爱情生活。她本姓杨，陈词中凡与杨、柳有关的词作，大都与柳如是有关联。\n本词是陈柳二人离异后，陈以杨花为题来表达其对柳的思念之情。作者把杨花撩乱飞舞的情景与漂泊者沦落天涯的遭际有机地结合在一起，寄寓了自己的身世之恨。','[\"重重帘幕：喻指陈柳婚姻中的重重障碍。\",\"春晖：指春光。\",\"\\u201C怜他飘泊奈他飞\\u201D句：眼看杨花（喻柳如是）随风飘零，却又无可奈何。\",\"\\u201C澹日滚残花影下\\u201D句：意为杨花之飘零，使天空迷蒙失色，似乎黯淡的太阳是在花影中滚动。\",\"玉楼：华丽的楼房；亦指仙人居处。西汉·东方朔《十洲记·崑崙》：\\u201C其一角有积金为天墉城，面方千里，城上\\u2026\\u2026玉楼十二所。\\u201D\",\"天涯心事：相隔天涯，彼此间的情意。\"]'),('e647d10fe6b62ea9d2917020f7ee5288','踏莎行·春寒','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"墙柳黄深，庭兰红吐，东风着意催寒去。回廊寂寂绣帘垂，残梅落尽青苔路。\",\"绮阁焚香，闲阶微步，罗衣料峭莺啼暮。几番冰雪待春来，春来又是愁人处。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9dd40a6f1b61db1fc','画堂春','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"艳阳深染杏花梢，晓烟初注樱桃。满堂春思卷红绡，昨夜春消。\",\"拾翠绿云斜亸，门棋红子闲敲。无边芳草玉骢骄，人去迢迢。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9e26212490e393b5b','醉桃源·题画','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"朱阑清影下帘时，泠泠修竹低。满园空翠拂人衣，流莺无限啼。\",\"莲叶小，荇花齐，雨馀双燕归。红泉一带过桥西，香销午梦微。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9e2a5f4eb4a190890','千秋岁','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"章台西弄，纤手曾携送。花影下，相珍重。玉鞭红锦袖，宝马青丝鞚。人去后，箫声永断秦楼凤。\",\"菡萏双灯捧，翡翠香云拥。金缕枕，今谁共。醉中过白日，望里悲青冢。休恨也，黄莺啼破前春梦。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9e429ae79c39c3291','如梦令','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"红烛逢迎何处，笑倚玉人私语。莫上软金钩，留取水沉香雾。\",\"难去，难去，门外杏花春雨。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9f32f182e8f8f3d30','浣溪沙','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"半枕轻寒泪暗流，愁时如梦梦时愁。角声初到小红楼。\",\"风动残灯摇绣幕，花笼微月澹帘钩。廿年旧恨上心头。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9f6220974f9c9ce85','少年游·春情','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明',NULL,'[\"满庭清露浸花明，携手月中行。玉枕寒深，冰绡香浅，无计与多情。\",\"奈他先倾离时泪，禁得梦难成。半晌欢娱，几分憔悴，重叠到三更。\"]',NULL,NULL,NULL,NULL),('e647d10fe6b62ea9ff57a048e427ffc8','点绛唇·春闺','陈子龙','e647d10fe6b62ea9faafda668d420fa3','明','此词原题为“春闺”，後代编者王昶等人为拔高陈子龙这首词的思想而作了修改，并题为“春日风雨有感”。\n词之起二句，先扬後抑。此时词人举目所见，是“满眼韶华”，一片春光。继而东风乍起，落红遍地。这一顿挫，表现了自然界的变化，从而也折射出时代的变化。陈子龙生当明清易代之际，对明王朝怀有深厚感情。在他看来，明代江山无限美好，正如满眼韶华。可是清兵南下，“扬州十日”、“嘉定三屠”，犹如骤起狂风，将万紫千红摧残殆尽。在这里，词人用的是比兴手法。“韶华”（春光）和“红”（花），代表美好事物，代表他所热爱的明代江山和明代人民；而“东风”则是邪恶势力的象征，也隐喻清兵的南下，“东风”一辞作贬义者，古已有之，如陆游《钗头凤·红酥手》“东风恶，欢情薄”，此处只是移用于词人所憎恶的事物罢了。下面二句，以“几番”照应前面的“惯”字。说明东风之摧残百花非止一次，而是经常如此。“烟雾’二字，补足前句未及写出的“雨”字。春天的风雨连绵无尽，常常呈现烟雾迷蒙的状态。在东风肆虐、烟雨茫茫的天气中，百卉凋残，一片凄凉，于是词人不禁发出由衷的慨叹：“只有花难护！”前几句造足蓄势，至此词人的感情迸發而出，力抵千钧。在生活中，他奔走呼号，出生入死，力求挽救明朝的危亡，结果毫无效果。因此这一句正是反映了词人内心深处的亡国之痛。\n下阕宕开一笔，径写对明王朝的系念，但在词的意脉上仍与上片紧密相连。词人在白天看到风雨摧残的落花，到了晚上便自然联想到惨遭践踏的故国。“梦里相思”一句，为艳词中常语，然而此处用以表达爱国之情，却非常深刻而又贴切。“王孙”一辞，通常被理解为贵族子弟，如《楚辞·招隐士》“王孙游兮不归，春草生兮萋萋”，但这里的本意却更接近杜甫《哀王孙》中所说的“可怜王孙泣路隅”。在清兵南下之际，朱明的宗室子弟，或流离道路，或辗转沟壑，唯有少数人如唐王朱聿键、鲁王朱以海等仍在企图反抗。此处作者对明代王孙魂牵梦萦，实际上是将复兴明代的希望寄托在他们身上。可是梦醒之後，依然风雨如磐，落红成阵。面对如此残酷的现实，他不得不发出“春无主”的哀叹。结二句进一步渲染出这种悲哀情绪，哭出了国家将亡的忧思。句中的“杜鹃”，又名杜宇，相传是古蜀国的君主望帝之魂所化，它隐于西山，日夜悲啼，口吻常常出血。後人常用杜鹃啼血借指失国之痛。这里说“泪染胭脂雨”系由“啼血”转化而来，则杜鹃悲鸣时流出血泪，洒在飘飏落花的风雨中，红雨满天，景象壮丽而又悲惨。词人若非怀有深仇惨痛是写不出这样的句子的。用“胭脂”形容雨中落花，前人有杜甫的《曲江对雨》“林花着雨胭脂湿”；而用以兼喻泪水的有李煜的《相见欢·林花谢了春红》“胭脂泪，留人醉，几时重”；陈子龙则将这些故实融会贯通，自铸伟词，赋予新意，令人读来便觉有更深刻的意蕴和更强烈的美感。\n陈子龙比较重视诗词的寄托，他曾说过他之作诗是为了“忧时托志”（《六子诗序》）。此词形式上虽“风流婉丽”，但词人借以“忧时托志”则与其诗作是一样的，在绮丽的表面下，蕴含着深永的内涵。','[\"满眼韶华，东风惯是吹红去。几番烟雾，只有花难护。\",\"梦里相思，故国王孙路，春无主！杜鹃啼处，泪染胭脂雨。\"]',NULL,'美丽的春光映入眼帘，春日东风总是将繁花吹散。几重烟雨朦胧，只有这些娇弱的花朵难以庇护。\n梦里总是时不时浮现起旧时芳草萋萋的道路。春天沉默不语，在那杜鹃泣血的地方，分不清落花染红的是春雨还是眼泪。','这首词全用比兴手法写景寄意。上阕通过春光被雨打风吹去、只留得落红无数的景象，暗寓明王朝的倾覆；下阕借怀人与杜鹃啼血的描写，实抒复国希望与亡国哀痛。通篇由景生情，又融情于景，委曲以达其幽深哀婉之情，令人低徊难尽。','[\"点绛唇：词牌名。此调因梁江淹《咏美人春游》诗中有\\u201C白雪凝琼貌，明珠点绛唇\\u201D句而取名。双调四十一字。\",\"韶华：美好的时光。常指春光。\",\"东风：春风。\",\"红：指春天的花。\",\"\\u201C几番烟雾\\u201D句：指清兵入关後的一系列事变。\",\"故国：一作\\u201C芳草\\u201D。\",\"王孙：对尊礼、思慕者的称呼，如淮南小山《招隐士》的\\u201C王孙游兮不归\\u201D。这里疑指南明的鲁王、唐王。\",\"无主：一作\\u201C无语\\u201D。\",\"泪染：一作\\u201C泪洒\\u201D。\",\"胭脂：一种用于化妆和国画的红色颜料，亦泛指鲜艳的红色。\"]'),('e647d10fe6ff949a5a1bd09d904c1f3c','水调歌头·送苏伯城词','邹智','e647d10fe6ff949a6d9cb7de226d1f15','明',NULL,'[\"微雨歇烦暑，轻风迎晚凉。携手伏波桥上，平水正苍茫。千峰归鸟纵横，两岸飞花下上，恰好是斜阳。把酒为君舞，君当倾几觞。\",\"道路难，功业远，岁华忙。莫负良天美景，终古恨空长。先生玉府神仙，小子石城居士，烂醉两何妨。阿真何处在，焚起紫猊香。\"]',NULL,NULL,NULL,NULL),('e647d10fe72c76e6f29ff18e33e3eeba','点绛唇','王赛玉','e647d10fe72c76e6d1457b72d1457b72','明',NULL,'[\"水院妆残，桐阴满地新凉夜。湘帘轻下，墙角疏更打。\",\"渐近黄昏，明月移花榭。愁偏惹，佩环休卸，绣鸭熏香麝。\"]',NULL,NULL,NULL,NULL),('e647d10fe79dc9236e27628215468904','金缕曲·登滕王阁','李麟','e647d10fe79dc923d1457b72d1457b72','明',NULL,'[\"风送漳江渚。漫登临、东流一瞬，渺然终古。物换星移凡几度。漠漠愁云欲雨。听欸乃、渔舟南浦。芳草王孙何处也，只西山、帘卷还如故。思往事，遽如许。\",\"王郎作赋真翘楚。便扫空、豪华百辈，腐儒章句。看取荒凉容膝地，畴昔曾酣歌舞。且采采、蘋香延伫。世路驱人明日又，爱今来、水阁清无暑。对斜照，淡忘侣。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b300c54c7e1e3d9168','碧芙蓉秋思','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"曲径绕苍苔，秋意平铺，萧散无数。澹日移阶，几度寻花误。西风怨、露草寒螀，夜天凄、霖林烟树。隔桥杨柳，翠减长条，犹自依依故。\",\"青山云去杳，红楼日、又催暮。罗簟生寒，漫引新桐句。池塘映、芙蓉一叶，帘幕惊、海棠疏雨。阑干凭遍，妆台渐冷，黯黯无语空凝伫。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b300f887225f8ba44c','鹧鸪天·春怀','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"日上花梢睡未醒。绣衾香暖梦留人。依依柳眼天边碧，澹澹山眉镜里青。\",\"无意绪，惜娉婷。绿阶芳草伴愁生。东风吹梦知何处，空听流莺槛外声。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b306b77126f34601a7','杏花天','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"翠烟无意撩书幌。带芳草、侵云渐长。晚风初作落花声，九十将阑未赏。\",\"南园路、风光暗想。听唤雨、鸣鸠两两。小池水皱萍漪绿，泛得红香惝惘。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3129e2dc77af2e7fe','浣溪沙·春夜','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"柳絮飞残不见春。近来闲煞惜花心。无聊独自步庭阴。\",\"紫燕未归馀画栋，黄昏先到怯囊琴。灯花月影两深深。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b314d2938cf8167bcb','点绛唇·夏日雨景','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"竹径深深，流云晓度罗帏静。雨丝几阵。满地桐花冷。\",\"湿翠侵眉，纤晕苍苔影。看无尽。绮屏人映。一片潇湘景。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3154effbdca0fcada','浪淘沙','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"薄暮峭寒分。罗簟香焚。粉墙留影弄微曛。一缕茶烟和梦煮，却又黄昏。\",\"曲曲画湘文。静掩巫云。花开花落负东君。嫌取花开花又落，都是东君。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b31f0d3d866643bacd','蝶恋花·春闺','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"春半馀寒犹未褪。雨雨风风，赚得清明近。杨柳垂腰消酒困。海棠点靥藏春晕。\",\"一树桃花红雨阵。片片飞来，铺尽苍苔印。燕子不归难借问。东风易去还因甚。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3275ca02d2a239423','水龙吟·次父六月二十四日作','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"昼长人静沉沉，绿杨正袅深深院。画帘低映，薄罗无暑，汗消珠钏。兰畹香消，湘筠影瘦，翠阴遮遍。听莲歌处处，悠扬逸韵，半入水风烟片。\",\"一霎雨馀明净，晚云如黛花如钿。舟移萍乱，芳香袖惹，媚风轻扇。一色红妆，千重翠盖，参差江面。更堪怜、归路平波杳杳，夕阳斜见。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b32b611b60e2064098','踏莎行·闺情','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"意怯花笺，心慵绣谱。送春总是无情绪。多情芳草带愁来，无情燕子衔春去。\",\"倚遍阑干，斜阳几许。望残山水蒙蒙处。青山隔断碧天低，依稀想得春归路。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b32e2d80a2dd2d8728','疏帘淡月秋夜','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"窗纱欲暮。渐暝色朦胧，暗迷平楚。断雁凄哀点点，远天无数。苍烟染遍西风路。剪江枫、飘红荻浦。画栏东角，疏帘底畔，徘徊闲伫。\",\"漫赢得、长宵如许。又锦屏香冷，绣帏寒据。满耳秋声，长向树梢来去。萧萧竹响还疑雨。悄窥人、嫦娥寒兔。壁摇灯影，空阶露结，怨虫相语。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b339978c6f961ee6f5','蝶恋花·立秋','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"屈指西风秋已到。薄簟单衾，顿觉凉生早。疏雨数声敲叶小。小亭残暑浑如扫。\",\"流水年华容易老。秋月春花，总是知多少。准备夜深新梦好。露虫又欲啼衰草。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b33d542b9ec83c66b9','河传·秋景','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"风澹。疏阴凉浅。碧汉悠悠。玉钩。柳边挂斜西画楼。初秋。井梧先自愁。\",\"小幔轻云澄练绮。清光美。好把镜奁比。倚阑干。亸鬓鬟。远山。秋烟点黛弯。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b34183bffd10c37a81','踏莎行·秋景','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"悴叶零愁，清香入绣。王孙桂小涂黄就。画帘日影上银钩，松风一枕消清昼。\",\"片雨凉生，小风波皱。疏疏翠玉琅玕逗。断云飞尽碧天长，数枝烟柳斜阳瘦。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b343f21acf635cce79','浣溪沙·送春近作','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"春色三分付流水。风风雨雨送花休。韶光原自不能留。\",\"梦里有山堪遁世，醒来无酒可浇愁。独怜闲处最难求。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b350b5d4d8df3d31fc','点绛唇·暮景','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"薄卷红绡，断霞西角斜阳远。昼长无伴。闲去题花扇。\",\"独倚阑干，看尽归鸦遍。轻云乱。凉风吹散。新月中天见。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b352c513142a33a071','浣溪沙·春闺','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"曲榭莺啼翠影重。红妆春恼澹芳容。疏香满院闭帘栊。\",\"流水画桥愁落日，飞花飘絮怨东风。不禁憔悴一春中。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b352c513148192ec3a','浣溪沙·春闺','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"几日东风倚画楼。碧天清霭半空浮。韶光多半杏梢头。\",\"垂柳有情留夕照，飞花无计却春愁。但凭天气困人休。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b352e7d53b1204dc58','水龙吟·秋思，次母忆旧之作，时父在都门之二','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"芭蕉细雨潇潇，雨声断续砧声逗。凭栏极目，平林如画，云低晚岫。初起金风，乍零玉露，薄寒轻透。想江头木叶，纷纷落尽，只馀得、青山瘦。\",\"且问泬寥秋气，当年宋玉应知否。半帘香雾，一庭烟月，几声残漏。四壁吟蛩，数行征雁，漫消杯酒。待东篱、绽满黄花，摘取暗香盈袖。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b353e6ae5fd7ae76b3','后庭花·破子夜思','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"朝来烟雨繁。金炉香缕翻。\",\"坐久还慵立，眠多愁梦烦。掩重门、落花流水，依稀随断魂。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b35cf7181b62e8cb69','浪淘沙·春闺','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"终日掩重门。莺燕纷纷。昼眠微醒觅残魂。强起亭亭临镜看，重整双云。\",\"倦倚碧罗裙。又早黄昏。侵阶草长旧愁痕。惟有垂杨千尺线，绾住馀曛。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b360c03f9501d3f88e','浣溪沙·初夏','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"香到酴醾送晚凉，荇风轻约薄罗裳。曲阑凭遍思偏长。\",\"自是幽情慵卷幌，不关春色恼人肠。误他双燕未归梁。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b363129e8a29ac0ec1','鹧鸪天·壬申春夜梦中作五首','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"野径春来草放齐。碧云天晓乱莺啼。紫笙吹彻缑山上，清磬敲残鹫岭西。\",\"红馥馥，绿萋萋。桃花杨柳共山蹊。遥看一抹烟云处，带雨春帆近日低。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b363129e8a34ad3c27','鹧鸪天·壬申春夜梦中作五首','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"一卷楞严一炷香。蒲团为伴世相忘。三山碧水魂非远，半枕清风梦引长。\",\"依曲径，傍回廊。竹篱茅舍尽风光。空怜燕子归来去，何事营巢日日忙。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b363129e8a4597f2cd','鹧鸪天·壬申春夜梦中作五首','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"春雨山中翠色来。萝门攲向夕阳开。朝来携伴寻芝去，到晚提壶沽酒回。\",\"身倚石，手持杯。醉时何惜玉山颓。今朝未识明朝事，不醉空教日月催。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b363129e8a80ae5a62','鹧鸪天·壬申春夜梦中作五首','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"雨后青山色更佳。飞流瀑布欲侵阶。无边药草谁人识，有意山花待我开。\",\"闲登眺，莫安排。啸吟歌咏自忘怀。飘飘似欲乘风去，去住瑶池白玉台。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b363129e8ac4accf47','鹧鸪天·壬申春夜梦中作五首','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"西去曾游王母池。琼苏酒泛九霞卮。满天星斗如堪摘，遍体云烟似作衣。\",\"骑白鹿，驾青螭。群仙齐和步虚词。临行更有双成赠，赠我金茎五色芝。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b36642920484f19a21','浣溪沙·春思','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"红袖香浓日上初。几番无力倩风扶。绿窗时掩闷妆梳。\",\"一向多慵嫌刺绣，近来聊喜学临书。鸟啼春困落花疏。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b367b8b64655105d67','浣溪沙·春暮','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"一瞬春光最可嗟。东风老去夕阳斜。断肠芳草遍天涯。\",\"粉蝶眠春随梦去，游丝系树带花赊。可知春思在谁家。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b367b8b646f5e3d428','浣溪沙·春暮','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"曲曲阑干绕树遮。半庭花影带帘斜。又看暝色入窗纱。\",\"楼外远山横宝髻，天边明月伴菱花。空教芳草怨年华。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b36a0fb940278a11d9','虞美人·残灯','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"深深一点红光小。薄缕微烟袅。锦屏斜背汉宫中。曾照阿娇金屋、泪痕浓。\",\"朦胧穗落轻烟散。顾影浑无伴。怅然一夜漫凝思。恰似去年秋夜、雨窗时。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b36da61d859060f2f9','浣溪沙·小窗即事','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"竹径烟迷薜荔墙，好风摇曳弄垂杨。无情啼鸟向人忙。\",\"一曲瑶琴消午梦，半炉沉水爇春香。倚阑无语又斜阳。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b36e79a9e117b929fe','蝶恋花·兰花','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"碧玉裁成琼作蕊。馥郁清香，长向风前倚。楚畹当年思帝子，紫茎绿叶娟娟美。\",\"自道全无脂粉气。笑煞春风，红白匀桃李。幽谷芳菲谁得比，猗猗独寄琴声里。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b36ef2810833909d00','浣溪沙·前夕','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"风透疏棂景色清。凄凄四壁怨蛩鸣。夜深微湿露无声。\",\"砌上落花和月落，帘前明月近花明。又看河汉半斜倾。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b376a603098ded1a04','南柯子·秋夜','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"门掩瑶琴静，窗消画卷闲。半庭香雾绕阑干。一带澹烟红树、隔楼看。\",\"云散青天瘦，风来翠袖寒。嫦娥眉又小檀弯。照得满阶花影、只难攀。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b379ee3c68c16a0488','如梦令·辛未除夕','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"风雨帘前初动。早又黄昏催送。明日总然来，一岁空怜如梦。\",\"如梦。如梦。惟有一宵相共。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b37c113ada679b2df3','满宫花咏游春人','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"日融和，花媚妩。粉蝶摇枝娇舞。轻风吹落小桃红，燕子衔归绣户。\",\"草芊绵，人容与。共羡春光如许。紫骝金勒系垂杨，拾翠寻芳伴侣。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3855c4461d03dcb16','点绛唇·戏为一闺人代作春怨','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"新柳垂条，困人天气帘慵卷。瘦宽金钏。珠泪流妆面。\",\"凝伫凭阑，忽睹双飞燕。闲愁倦。黛眉浅淡。谁画青山远。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3858784c501439f93','浪淘沙·秋怀近作','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"青女降枝头。已解添愁。暮蝉声咽冷箜篌。试看夜来多少露，草际珠流。\",\"身世一浮鸥。岁月悠悠。问天肯借片云游。袅袅乘风归去也，直上瀛洲。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3869dcbc87a658fee','小重山·晓起','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"春梦朦胧睡起浓。绿鬟浮腻滑、落香红。妆台人倦思难穷。斜簪玉，低照镜鸾中。\",\"徐步出房栊。闲将罗袖倚、立东风。日高烟静碧绡空。春如画，一片杏花丛。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b386b9120225f758c3','虞美人','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"一杯淡茗聊相赏。莫怪人惆怅。近来多病损红妆。不耐萧条清昼、卧琴床。\",\"侍儿漫把胭脂扫。委地还馀俏。春风著意半蹉跎。燕子不知花事、已无多。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b386b91202d03f20b0','虞美人','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"看花日日寻春早。捡点春光好。轻罗香润步青春。可惜对花无酒、坐看茵。\",\"昨宵细雨催春骤。枕上惊花瘦。东君为甚最无情。只见花开不久、便飘零。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b389ca0f2a3edc91f6','生查子·送春','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"风飘万点红，零落胭脂色。柳絮入帘栊，似问人愁寂。\",\"凭阑望远山，芳草连天碧。深院锁春光，去尽无寻觅。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b38a927f9978fc24ee','水龙吟·秋思，次母忆旧之作，时父在都门','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"井梧几树凉飘，满庭景色仍如旧。啼鸦数点，斜阳一缕，挂残疏柳。有恨林花，无情衰草，风吹重又。看轻阴带雨，天涯万里，楼高漫频搔首。\",\"记泊石城烟渚，落红孤鹜常如绣。轻舟画舫，布帆兰枻，暮云天皱。水静初澄，蓼红将醉，早秋时候。对庭前、萧索西风，惟有寒蝉高奏。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b38b0476d0750361b8','南柯子·九日','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"日暖茱萸好，霜飞菡萏衰。碧云山外夕阳催。自有竹篱斜径、菊花开。\",\"烟重迷疏柳，阴浓笼湿苔。画楼时送暗香来。且去待看明月、倒金杯。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b39850cee98cc25f6c','捣练子·暮春月夜','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"春寂寂，月溶溶。落尽红香剩绿浓。明月清风同翠幕，夜深人静小窗空。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b39b56c27c5c6328bd','临江仙·端午','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"团扇新裁明月影，珠帘半上琼钩。榴花红到玉钗头。彩丝宜续命，绿砌绕忘忧。\",\"酒泛菖蒲香玉碎，嫩红双靥横秋。画船何处闹歌楼。萧萧烟雨外，还锁楚江愁。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b39c70a6d82ee1e871','踏莎行','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"昨夜疏风，今朝细雨。做成满地和烟絮。花开若使不须春，年年何必春来住。\",\"楼外莺飞，帘前燕乳。东君漫把韶光与。未知春去已多时，向人犹作愁春语。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b39ed63a428c2306d9','凤来朝春日书怀旧作','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"小院闲无事。步花阴、嫩苔雨渍。弄明光、几叠琴弦腻。曲槛畔、情何似。\",\"静对圣贤书史。一炉香、尽消梦思。翠幕外、东风起。不觉又、欲瞑矣。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3a1091a8c659a270d','踏莎行·紫薇花','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"细剪胭脂，轻含茜露。芳菲百日浓辉聚。红妆懒去斗春妍，薰风独据珊瑚树。\",\"翠叶笼霞，琼葩缀雾。湘帘影卷猩姿雨。仙郎禁院旧传名，亭亭好伴西窗暮。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3a271149611d6ad09','虞美人·看花','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明','少女心性，自然格外清纯，故而少女写作的文艺作品，也就显得十分纯真可爱。那春寒的夜色中匆匆而去的娇小身影，那寂静的院落里踽踽而归的零乱脚步，那生闲气而找不到对象的娇憨神情，全由那清丽畅达的文字给记录了下来，读之真让人忍俊不禁。','[\"阑干曲护闲庭小。犹恐春寒悄。隔墙影送一枝红。却是杏花消瘦、旧时风。\",\"海棠睡去梨花褪。欲语浑难问。只知婀娜共争妍。不道有人为你、惜流年。\"]','[\"陈维崧《妇人集》评叶小鸾：\\u201C吴江叶进士（绍袁）三女，长昭齐（纨纨），次蕙绸（小纨），三琼章（小鸾），俱有才调，而琼章犹英彻，如玉山之映人，诗词绝有思致。\\u201D\"]',NULL,NULL,'[\"曲护：弯弯曲曲的阑干回廊。\",\"悄：悄然（来临）。\",\"\\u201C隔墙影送一枝红\\u201D句：这句与\\u201C满园春色关不住，一枝红杏出墙来\\u201D说的内容相近，意境却不同：此处\\u201C一枝红\\u201D\\u201C消瘦\\u201D不堪，远没有后者富有生命力。\",\"\\u201C海棠睡去梨花褪。欲语浑难问\\u201D句：这两句是说海棠花闭合了，梨花也从枝头褪去，难觅踪影，想问候她们却没有一个与我说话的。\",\"惜流年：关注时光的流逝。\"]'),('e647d10fe7ca96b3a49491d4544ca8aa','踏莎行·早春即事','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"檐畔梅残，堤边柳细。暖风先送游人意。流莺犹未弄歌声，海棠欲点胭脂醉。\",\"鸟踏风低，烟横云倚。湘帘常把春寒闭。无端昨夜梦春阑，丝丝小雨花为泪。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3a6752ebbb81fee26','鹊桥仙·题画山水','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"柴扉不掩，翠微欲滴，断岸芦花风寂。远峰云树两朦胧，曲径杳、崎岖难觅。\",\"平波澹澹，长松历历，玉洞仙床咫尺。闲来看尽思悠然，恨不得、将身飞入。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3a9e872c6a728a901','河传·七夕','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"鸾扇。星钿霞钏。暂撇残机。步移。思量去年今夕时。凄其。未期先惨离。\",\"借得嫦娥初月镜。窥瘦影。拂试翠眉整。驾云霓。河汉西。凄迷。只愁鸡暗啼。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3af6c3daddcaa84c5','浣溪沙·早春','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"灯夕初过冷未平。乍看今日试微晴。东风已解向人迎。\",\"梨蕊几时飘弱韵，柳条如欲荡柔情。隔墙何处按歌声。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3b4afae6d5cf728c3','蝶恋花·七夕','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"飞鹊年年真不误。机石停梭，掩映河边渡。清露未消杨柳暮，落花借点疏萤度。\",\"月色风光都莫负。酒酌芳樽，不把佳时错。女伴随凉池上路，海棠花畔吹箫坐。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3bc28c620d00c87e4','浪淘沙·春景','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"杨柳弄柔黄。缕缕纤长。海棠风醉艳红妆。折取一枝归绣户，细玩春光。\",\"春日对春妆。莺燕笙簧。横塘三月水流香。贴水荷钱波动处，两两鸳鸯。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3bc7517b6b0985ec0','点绛唇·咏采莲女','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"粉面新妆，澹红衫子轻罗扇。昨宵邻伴。来约莲塘玩。\",\"棹泛扁舟，影共莲花乱。深深见。绿杨风晚。空载闲愁返。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3c1b0c9739c4cc0e9','虞美人·影春日','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"海棠睡惹流莺恼。又是清明过了。杨柳水边多少。愁绪萦芳草。\",\"杜鹃枝上东风悄。碧玉栏前人杳。一夜绿娇红老。只恐春归早。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3c4db067535181578','蝶恋花·秋海棠','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"浅绿嫣红开几许。谁料西风，也解倾城妩。酒晕盈盈娇欲伫。檀心半吐轻含雨。\",\"剪向屏山深处贮。似笑如愁，旖旒怜还怃。低亸对人浑不语。断肠应恐人无绪。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3c7167020c437ed93','南柯子','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"庭际花枝袅，帘前汉渚横。断肠何必问吹笙。但见海棠叶上、露星星。\",\"墙月深深照，炉香细细零。谁家闺妇又砧声。搅得梦魂无赖、不堪情。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3c9e5c636db031d1c','玉楼春·春寒','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"南园蝴蝶飞无数。满院春寒帘幕护。深深人在小楼中，悄悄花开寒食路。\",\"映阶日色时将午。添得罗衣窗影暮。寂寥孤馆闭闲春，但见斜阳窥绣户。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3cf1cdd675914b1eb','千秋岁·即用秦少游韵','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"草边花外，春意思将退。新梦断，闲愁碎。慵嫌金叶钏，瘦减香罗带。庭院悄，只和镜里人相对。\",\"过了秋千会。荷叶将成盖。春不语，难留在。几番花雨候，一霎东风改。肠断也，每年赚取愁如海。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3d41fbdc9e0153eb6','河传','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"人坐。佳时瓜果。气朗长空。月宫。断霞半天衫袖红。重重。紫薇花影浓。\",\"曲沼芙蓉香满院。人正宴。数点晚萤见。倚针楼。看女牛。莫愁。今宵犹未秋。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3e0989396317db2ed','踏莎行·忆沈六舅父','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"枝上香残，树头花褪。纷纷共作春归恨。十年客梦未曾醒，子规莫诉长离闷。\",\"回首天涯，愁肠萦寸。东风空递双鱼信。几番归约竟无凭，可怜只有情难尽。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3e429ae7996bb7927','如梦令','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"雁唳西风天际。槛外梅花香细。今夜与明朝，试共相看不睡。\",\"且睡。且睡。守岁何如别岁。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3e53be13d350c405b','唐多令·秋夜','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"灯晕伴残更。萧萧落叶轻。诉穷愁、草际虫声。栏外芭蕉新嫩绿，仍做出，旧秋声。\",\"罗被夜凉清。凄然梦亦惊。透纱窗、月影纵横。几遍鸡声啼又晓，空蹙损，两山青。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3e7bcbd1618ea46d2','玉蝴蝶·春愁','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"梦破晓风庭院，粉墙花影，睡起恹恹。几日双娥，愁损镜里春尖。看尽他、莺梭柳线，都织就、雾锦云缣。最难忺。催花小雨，依旧廉纤。\",\"堪怜。韶光淑景，芊芊芳草，寂寂钩帘。燕子归来，花香都向绿琴添。散闲愁、流红泛去，消酒困、湿翠飞黏。怯春衫。香烘袅袅，袖护掺掺。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3efedbd6079f77d24','雨中花·梨花','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"泪雨琼姿娇半吐。又一夜、风摇鬟雾。绣陌啼莺，画梁归燕，莫便催春去。\",\"脉脉柔情慵未足，叹寂寞、玉容难赋。今夜黄昏，明朝庭院，空锁重门暮。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3f1cbffd247366a2f','鹧鸪天·夏日','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"处处蝉声咽柳亭。隆隆日午正当庭。莲香有水红妆倩，竹粉无风翠影停。\",\"挥扇子，候凉生。疏帘小簟却银屏。南熏日暮无行雨，唤煞啼鸠不耐听。\"]',NULL,NULL,NULL,NULL),('e647d10fe7ca96b3f64a37231fad8530','蝶恋花·春愁','叶小鸾','e647d10fe7ca96b3d1457b72faafda66','明',NULL,'[\"蓦地东风池上路。绿怨红消，竟是谁分付。不断行云迷楚树。闭门寒食梨花雨。\",\"雨后斜阳芳草处。闲把情怀，付与东君主。便向西园飘柳絮。不能飘散愁千缕。\"]',NULL,NULL,NULL,NULL),('e647d10fe7e9e3bc5d925dcb32a501af','虞美人·春景','沈自炳','e647d10fe7e9e3bcd1457b72e4873aa9','明',NULL,'[\"竹篱阵阵飞花雨，栖燕衔香语。孤云细草小溪晴，攀梅拾豆打流莺，短桥横。\",\"杨花一似郎情薄，相见还飘泊。空余三月断肠春，万重山外楚江滨，有行人。\"]',NULL,NULL,NULL,NULL),('e647d10fe7e9e3bc5f94879e4a93ba3e','中兴乐','沈自炳','e647d10fe7e9e3bcd1457b72e4873aa9','明',NULL,'[\"芙蓉池上露初凉，桐花月转回廊。秋满纹帘，孤灯漏长。\",\"梦入红楼画墙，见萧娘。觉来枕畔，玉钗犹响，无限思量。\"]',NULL,NULL,NULL,NULL),('e647d10fe7e9e3bca6631786d7c15f72','更漏子','沈自炳','e647d10fe7e9e3bcd1457b72e4873aa9','明',NULL,'[\"忆情人，南浦去，携手向人低语。眉样浅，翠衣轻，莺啼画舫行。\",\"香闺掩，珠帷敛，春剩杨花千点。愁不禁，黯销魂，黄昏独倚门。\"]',NULL,NULL,NULL,NULL),('e647d10fe90ee1be3fc2477e2b1785d5','水龙吟·杨花用章质甫韵','赵南星','e647d10fe90ee1be6b8eba4397d01458','明',NULL,'[\"春闺忒恁愁人，已看尽落红翻坠。杨花更惨，连空映日，撩人情思。飞过高城，寻来小院，从教门闭。偶苹风乍定，商量暂住，低飞燕，还扶起。\",\"何处疑花乱玉，几曾堪、髻簪衣缀。兰闺人倦，多愁牵梦，难成易碎。小玉声喧，晴天雪下，香阶无水。忆辽西何处，神魂荡漾，暗抛红泪。\"]',NULL,NULL,NULL,NULL),('e647d10fe93ef3c4ce5f35d219ff17f4','踏莎行·施公村居九日','沈龙','e647d10fe93ef3c4d1457b72d1457b72','明',NULL,'[\"水满江天，沿堤钓艇，空村客远风烟冷。丹枫消翠草堂开，天晴衬出重阳景。\",\"竹扫茶烟，鹤窥香鼎，芙蓉照面潮红醒。一痕淡月上峨眉，松台人散乌纱影。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e141bed120cc71919','凤凰台上忆吹箫·丁已中秋感旧','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"银汉无声，澹云笼月，汀烟落木萧萧。怅西风万里，江上停桡。玉砌雕阑何处，霓裳曲、凤管鸾箫。休重问，伤心往事，都付寒潮。\",\"河桥。当年惜别，翠雁湿啼痕，敛衽魂销。感新愁磊磈，有酒难浇。行径红兰香歇，低颦念、暮暮朝朝。银屏悄，定应晚妆，宫黛慵描。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e1592f97baee83edf','水龙吟·己巳端午','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"海榴庭院初长日，梅羽弄阴弄霁。惊心节序，艾枝悬绿，菖蒲浮碧。衣赐香罗，忆陪宫宴，御炉烟细。愧相如多病，归来鬓痕如缕。\",\"往事风流莫记。黯江山、旧游凝睇。湘波吊雪，楚峰悽黛，荆云愁垒。鱼腹沉冤，众人皆醉，一樽谁酹。独登临词客，兴亡多恨，洒青衫泪。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e2604cf98efaed82a','浪淘沙·惜春','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"年少为花愁。珠箔青楼。竹西歌吹古扬州。翠袖舞低杨柳月，醉倒还留。\",\"投老忆狂游。往事都休。繁华如梦水东流。飞絮落花春去也，白了人头。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e2c92d94a7acf6e76','壶中天慢','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"年少疏狂，儗晞发扶桑，濯缨旸谷。北适幽燕东通越，争道中原逐鹿。抗节登车，悲歌倚剑，太华青如粟。闻鸡中夜，秦庭曾有人哭。\",\"谁忆万里关山，尘凝箭羽，慷慨嗟髀肉。白发青衫归故里，多少泥涂之辱。却羡渊明，休官彭泽，三径连松菊。迎门稚子，残书老砚茅屋。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e41a6e6dbb4171839','忆秦娥·送别','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"溪头柳。青青折赠行人手。行人手。最伤心处，西风重九。\",\"阳关一曲长亭酒。停鞭欲去仍回首。仍回首。少年离别，老来依旧。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e573c10a3ef67e8a2','卜算子·春晚','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"帘幕楝花寒，啼鴂催春去。芳草天涯绿渐迷，烟树无重数。\",\"老骥蝢长缨，犹忆云霄路。流水孤村独掩门，白日东风暮。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e5f0c9815cab9535d','满庭芳·杨焕文赴召还','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"象阙星辰，凤台春色，觚棱仙掌金茎。琼林侍宴，天语记叮咛。日近龙颜开霁，红云拥、雉扇鸾停。金沟暖，万年宫树，时听晓莺声。\",\"赐归优诏许，剑悬蛟影，箭委雕翎。对寒食，飞花流水柴荆。自笑投林倦鸟，繁华梦、谁问功名。溪山好，船头载酒，醉里钓丝轻。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e66596b0d8759d3e0','虞美人·汴梁怀古','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"春风芳草梁园路。玉辇今何处。香销珠翠旧妆楼。惟有胭脂井畔、水东流。\",\"伤心太液池头月。清影圆还缺。万年枝上野花开。肠断年年不见、翠华来。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e7d4b0ad750300e87','木兰花慢·辛酉九日','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"写长空一雁，又景物、是重阳。甚雨暗东篱，数枝青蕊，未放秋香。支筇倦登高处，掩柴门衰柳映横塘。日暮江声滚滚，西风鬓影苍苍。\",\"当年歌舞翠红乡。一曲杜韦娘。曾击碎珊瑚，玉人扶醉，银烛成行。回头已成陈迹，早归来茅屋石田荒。谩把清樽遣兴，都非少日疏狂。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14e7d9365dab3810539','水调歌头·禁城春闱元夕','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"凤苑东风早，柳眼已催春。御沟冰泮，微绿宿雨浥芳尘。燕子归来时候，寒勒小桃未放，初试薄罗新。九市闹弦管，万户庆灯辰。\",\"对鳌山，驰宝马，竞朱轮。绮罗丛中，香蔼争道漏声频。台榭花阴如昼，巷陌月华如海，心事翠眉颦。举酒强歌笑，独忆去年人。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14ea01cedaf9b92d890','满庭芳·寿许得夫先生','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"白露横空，金风荐爽，银河凉阴西流。长庚一点，文彩照南洲。悬弧当年壮志，过庭训、铅椠灯篝。青云梦，黄槐丹桂，东井劫灰浮。\",\"钩陈迷太液，紫驼蒙雪，玉马嘶秋。怅黍离之感，多少闲愁。短褐归来老砚，生涯旧、茅屋沧洲。蟠桃熟，朱颜鹤发，绿酒满金瓯。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14eb3dc92bbbbb5403c','春草碧芜城怀古','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"平山堂下东风，何处海棠春、琼花月。桥上十里珠帘，金钗翠袖歌声彻。\",\"醉倒玉人扶、银灯揭。莫问旧日江山，几多离别。却忆锦帆游、迷宫阙。\",\"回首故苑繁华，豪气五陵都销灭。断柳锁寒烟、潮声咽。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14ebd020c9420a614e8','念奴娇·寄徐指挥','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"中天日丽转洪钩，紫凤黄龙呈瑞。附翼攀鳞登庆会，谁是晚成之器。击楫中流，闻鸡而起，多少澄清志。斗牛南射，寒光炯炯垂地。\",\"报道玉塞秋高，虎皮雕羽箭，西征辽水。铭勒燕然头欲白，壮矣风云英气。他日归来，堂开绿野，富贵功名遂。故园松菊，邻翁酿酒同醉。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14ed072d68defd6c7bb','浪淘沙·寒食','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"石径土墙斜。桃李桑麻。纸钱飞处乱啼鸦。祭馀携酒去，寒食野人家。\",\"苑树忆天涯。遗恨琵琶。铜驼衰草卧龙沙。汉寝唐陵无麦饭，暮雨梨花。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14ed30b920b3ee59126','水龙吟·题御沟红叶','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"御沟暖透温泉柳，浑不觉西风早。红叶飞来，珠帘搭处，始知秋老。信步瑶阶，雁沙香褪，袜罗娇小。谩宫袖微揎，玉纤轻拾，胭脂湿、露华清晓。\",\"宝砚香熏煤麝，拭霜毫、龙蛇萦绕。芳题锦字，翠嚬红怨，带真连草。天上人间，为凭流水，春心多少。谩殷勤寄去，无情有恨，彩云青鸟。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14eef032765a2722627','醉蓬莱','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"正清秋绛阙，华盖天高，金风销暑。海霁鲸波，喜瑞流虹渚。黄道星辰，紫微云气，拥蓬莱深处。万国衣冠，四夷玉帛，两阶干羽。\",\"雉扇初开，嫔妃鱼贯，九奏箫韶，凤凰咸舞。内使传宣，记叮咛天语。喜溢龙颜，大地丰登，九衢甘雨。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14efb58a13231b0b7bc','满江红·寄水北山人徐宗周','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"水北幽居，抱柴门、一溪寒玉。山万叠、青林当户，白云连谷。乳燕落红春又晚，柴桑三径多松菊。喜新晴、布谷又催耕，秧分绿。\",\"床头酒，何时熟。谋诸妇，明朝漉。谩黄鸡炊黍，共邀邻曲。旧梦风云销侠气，繁华看破知荣辱。系扁舟、钓罢晚归来，书还读。\"]',NULL,NULL,NULL,NULL),('e647d10fe9acd14efff133b1ca074a93','虞美人·重游三山感旧','刘炳','e647d10fe9acd14ed1457b72d1457b72','明',NULL,'[\"信陵门下簪缨客。寂寞头多白。重来无复旧交游。上马台边烟草、不胜秋。\",\"风流云散繁华去。犹指将军树。长歌何处吊荒丘。衰泪凄凉不尽、与江流。\"]',NULL,NULL,NULL,NULL),('e647d10fea89251bcf44578da67d69f4','三五七言','沈周','e647d10fea89251b970af30e1cd38823','明',NULL,'[\"君不来，来何时。三年一书札，万里独相思。瑶芳满把凭谁寄，明月伤情只自知。\"]',NULL,NULL,NULL,NULL),('e647d10fea89251be1dc8a6ddf938bcf','鹧鸪天·柳','沈周','e647d10fea89251b970af30e1cd38823','明',NULL,'[\"惯得轻柔绮陌中，几枝斜映驿亭红。微烟啅雀金犹懒，细雨藏鸦绿未浓。\",\"攀傍岸，折随风，管人离别思无穷。开花更是无聊赖，一片西飞一片东。\"]',NULL,NULL,NULL,NULL),('e647d10feaade4b01cb943c103dfa54e','采莲曲四首','李云龙','e647d10feaade4b0d1457b72d1457b72','明',NULL,'[\"花前相见即相呼，日暮连舟渡碧湖。含笑问郎何处住，西来能过妾家无。\"]',NULL,NULL,NULL,NULL),('e647d10feaade4b01cb943c1a3d5d443','采莲曲四首','李云龙','e647d10feaade4b0d1457b72d1457b72','明',NULL,'[\"柳塘风起荻花飘，竹坞烟消江水遥。闲过隔船探女伴，飞上木兰桡。\"]',NULL,NULL,NULL,NULL),('e647d10feaade4b01cb943c1a48ea6df','采莲曲四首','李云龙','e647d10feaade4b0d1457b72d1457b72','明',NULL,'[\"江风潇潇生白波，荷叶拍水飞鹭多。兰棹莲舟自来去，女郎时唱采菱歌。\"]',NULL,NULL,NULL,NULL),('e647d10feaade4b01cb943c1f702bc99','采莲曲四首','李云龙','e647d10feaade4b0d1457b72d1457b72','明',NULL,'[\"日暮期郎杨柳津，小姑相妒阿婆嗔。扁舟欲去不得去，江水连天愁杀人。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e616020fa0','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"山桃花开红更红，朝朝愁雨又愁风。花开花谢难相见，春色无边愁杀侬。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e61accbbb0','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"劝郎水底莫铺绵，劝郎石上莫栽莲。水底铺绵绵易烂，石上栽莲根不坚。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e64341674a','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"蜀江西来一带长，江水无波镜面光。长恨人心不如水，等闲好恶最难量。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e68ea0a35e','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"石头城外是江滩，滩上行舟候潮难。潮信有时还又至，郎舟一去几时还。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e6ae56b2e3','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"家住东吴白石矶，门前春水浣罗衣。朝来系著木兰棹，闲看鸳鸯作队飞。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e6bd0857c8','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"油壁寻芳拂翠蛾，春衫半臂试轻罗。海棠花娇香不露，遮莫狂蜂蛱蝶多。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e6deb50527','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"劝郎切莫上巫峰，劝郎切莫往临邛。临邛少妇解留客，巫山峰高云雨浓。\"]',NULL,NULL,NULL,NULL),('e647d10feadfa1cf4b3391e6ec54ea92','竹枝辞','童轩','e647d10feadfa1cfa424ed4bced556cd','明',NULL,'[\"西湖荷叶翠盈盈，露重风多荡漾轻。荷叶团团比侬意，露珠不定似郎情。\"]',NULL,NULL,NULL,NULL),('e647d10feaf4435f1a459ffdd32ce2cd','点绛唇·次沈素嘉韵','庞蕙','e647d10feaf4435fd1457b72d1457b72','明',NULL,'[\"十载芳邻，自怜一别还如雨。看云愁绪，隔个江天树。\",\"佳句曾题，小楷红笺处。频看取，相思难据，一瞬情千缕。\"]',NULL,NULL,NULL,NULL),('e647d10feb2b3561f32f182e71217cc3','浣溪沙','周思兼','e647d10feb2b35615607fe88d961e9f2','明',NULL,'[\"天上青鸾月下逢，玉窗斜掩影重重。吹箫人在画楼中。\",\"问道绿阴今几许，两竿长日舞春风。潇湘秋色总无穷。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c186a007b12d75f705e','念奴娇·雨中','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"荼䕷初谢，又山丹开过，蔷薇零落。二十四番花信了，清暑绿阴帘幕。五月陂塘，熟梅风雨，病怯春衫薄。重门静掩，客怀无奈萧索。\",\"惆怅绿绮弦空，紫箫声断，好事翻成恶。睡起无聊还欲睡，梦绕白云丹壑。怨别伤离，新愁旧恨，一醉都抛却。数声山鸟，醒来人在西阁。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c187d1407b8241e143a','西湖竹枝词五首','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"阿侬家住湖水傍，菰米莼丝野饭香。猫头紫笋尺围大，沙角红菱三寸长。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c187d1407b82c2d0362','西湖竹枝词五首','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"郎从湖上打鱼虾，妾在湖边只浣纱。生长不离湖水上，涌金门外是侬家。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c187d1407b83d323950','西湖竹枝词五首','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"麦岭风吹小麦花，古藤乔木路三叉。千年玉骨湘累墓，万里坚城少保家。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c187d1407b88a0f241b','西湖竹枝词五首','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"谁家女儿金缕衣，银瓶庙里插香归。素罗团扇纤纤手，笑指鸳鸯作对飞。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c187d1407b8a33648d1','西湖竹枝词五首','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"湖头春水绿于苔，滉漾绿酒浮金杯。瞰湖楼子知多少，扇扇红帘影倒开。\"]',NULL,NULL,NULL,NULL),('e647d10feb3a0c18e5603ec7596ccbcb','风入松·次宋人题西湖','朱朴','e647d10feb3a0c18d1457b72d1457b72','明',NULL,'[\"武陵春酒不论钱，红杏竹篱边。画桥一带青青柳，兰舟系、玛瑙坡前。隔树数声鹦鹉，谁家一簇秋千。\",\"朱唇玉板鹧鸪天，歌罢鬓云偏。杜鹃啼歇清明雨，楼台锁、几处新烟。沈醉折花归去，路傍拾得遗钿。\"]',NULL,NULL,NULL,NULL),('e647d10febe98262e34996c9788deb74','竹枝歌二首','张元凯','e647d10febe98262d1457b72d1457b72','明',NULL,'[\"燕子高楼水上开，楚江风雨竹枝哀。贾帆未向门前落，孤负春潮日日来。\"]',NULL,NULL,NULL,NULL),('e647d10febe98262e34996c9d485ce3d','竹枝歌二首','张元凯','e647d10febe98262d1457b72d1457b72','明',NULL,'[\"水落寒江露钓矶，春时去路已全非。郎今无那舟行远，匹马先从间道归。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab701663a9fb275b483','踏莎行·春情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雨过疏帘，苔荒小院。年年病骨逢春软。莺啼白日弄轻阴，无聊卧看杨花转。\",\"衣冷谁添，书成未遣。琐窗魂梦都寻遍。为伊消瘦也甘心，镜中莫放檀痕浅。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7022927426c7d5ea7','牡丹枝上祝英台武原舟中值雪','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"漠漠湖云白。卷起浪花千尺。独倚孤舟，满袖泪珠偷滴。可堪冻雪凝须，层冰堕指，甚时候、要人行役。\",\"有何益。漫把芳年掷。淹留水村山驿。远记佳人，拥衾此际怜惜。料应为我情痴，柔肠萦损，恹恹地、懒将笙炙。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab702bd3095c42fe917','满庭芳··题金虎文峤庵词','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"树合清圆，波澄潋滟，算来几日新秋。风帘自上，虹见雨初收。肠断花间妙句，桐阴下，引满金瓯。都休论，移宫换羽，一味是离愁。\",\"高楼。窥淡月，鹦哥影动，茉莉香幽。教雪儿缓唱，试谱箜篌。翻忆历城淮海，千年后、再见风流。空筵伫，芙蓉未老，白鹭下汀洲。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70342139bdc11e523','山溪满路花·故鄣山中访妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"溪回罨画，漠漠寒烟锁。垂柳隔虹桥，新月起、乱莺啼破。草香沙暖，此路是天台，环绕峰千个。屡下雕鞍坐。抚景寻思，几时梦里来过。\",\"朱扉半启，小立双鬟舞。仙饭杂松云，低问取、阮郎曾饿。玉笙吹罢，万树碧桃开，人照红窗火。醉醒愁无那。春夜厌厌，参旗楼角斜簸。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab703df8e3c91dfff49','青玉案·美人足','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"双拘巧叠蝉纱腻。须掩在、罗裙翠。半夜潜踪因底事。月堤苔滑，碧天露冷，满地蔷薇刺。\",\"盈盈最是多风致。掌上何时看详细。可惜浓香闲绣被。醉醒轻蹴，梦回低挽，搅杀心儿遂。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab704b12047e5b83a92','鹊桥仙·五日对酒作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"钗符袅玉，臂丝约彩，惊叹流光如矢。雪头堆上海榴红，吾老矣、颠狂如此。\",\"茂弘喻布，孔群比肉，毕晚谁非谁是。一樽无处酹零均，也只合、当年醉死。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab705b829584e7c9a89','点绛唇·孜孜','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"香暖衾窝，背他好梦孜孜地。刺桐花底。小语东风细。\",\"步步提防，难把离魂系。重门闭。雨偷云替。元在罗帏里。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7060035df0682626b','满江红·读沈丰垣新词，次洪昉思韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"落魄谁怜，才几日、鬓中堆雪。则除是、猧儿曾见，鹦哥能说。过眼花随流水去，断肠人向西风别。助凄凉、枕上笛声悲，灯明灭。\",\"情已尽，犹啼血。言不尽，空存舌。似残莺宛转，冷泉幽咽。梦醒忽惊时序改，愁来不信乾坤阔。再休将、醉墨写相思，生绢裂。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab707f6c75b92f06eb0','浣溪沙·晓思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"恻恻春寒近五更。半窗斜月叫新莺。梦儿难好却无凭。\",\"倚柱有期空盼影，隔花相唤不闻声。最无情处最牵情。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70866a31de6af623b','点绛唇·美人鼻','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"篆袅金猊，妆成独坐怜清昼。下阶闲走。自摘青梅嗅。\",\"信断音沉，嚏是何人咒。分离久。总因谗口。教掩春衫袖。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70ca2de021eb76e99','东风','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"翠密红疏，节候乍过寒食。燕冲帘，莺睨树，东风无力。正斜阳楼上独凭阑，万里春愁直。\",\"情思恹恹，纵写遍新诗，难寄归鸿双翼。玉簪恩，金钿约，竟无消息。但蒙天卷地是杨花，不辨江南北。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70cb34f61c8781322','醉花阴·重阳即事用李易安体','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"永昼空阶虫语咽。趱得头如雪。无奈又重阳，不见黄花，瞒过愁时节。\",\"佳人浪作经年别。便梦来江截。何处最关情，凉透纱厨，酒暖鹅笙热。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70cf36819eed4edd0','鹊桥仙·喻风','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"柳腰全困，花头半坼，迤逦春光如绣。东风快似剪刀儿，把淡绿深红簇就。\",\"流莺搅睡，晴丝系恨，正是销魂时候。漫将金斗比东风，熨不醒、两眉长皱。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70f16cf04761db367','满江红·咏灯','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"独对银缸，听街鼓、不堪愁绝。却又是、春阴陡暗，晚风偏劣。耿耿未成虚枕梦，摇摇转觉空房怯。镇凄凉、流泪湿黄昏，花重叠。\",\"云髻拥，香肌瞥。徒想像，增呜咽。怎照人欢会，照人离别。欲简私书还剔起，怕看孤影将吹灭。恰才馀、一燄解罗衣，翻成结。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab70fa7f64b404ec3fb','醉花阴·闺情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"西窗斜日花阴下。正梳头才罢。对镜几徘徊，未到黄昏，又把云鬟卸。\",\"屏风自掩深寒夜。剪银台灯灺。收取入香奁，狼藉春山，留待檀郎画。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7136d6d18298cc755','比目鱼·偶见','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"春游东城陌。懊恨无端逢艳质。偏荷半倒，不动湘裙襞积。娇滴滴。掩映得、山花水柳俱无色。欢容笑靥。敢未解春愁，临波听鸟，故意调行客。\",\"当面红墙万尺。堪恨此身无双翼。怜春春不怜人，雨荒云黑。情漫切。真个是、多情枉把无情惜。不堪再忆。斜日野桥西，几番翘首，千里暮烟碧。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab714c66c768db862f4','蝶恋花·春游即事','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"一寸斜阳非可恃。柳外啼鹃，真是无才思。欲笑还颦因底事。销魂记得南朝寺。\",\"酒罢春衫红泪渍。花不看人，人愿看花死。归去小楼仍独自。半湖烟草如何是。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab714d08baef861cdb2','醉花阴·用李易安体春雨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"听尽潺潺帘外雨。一夜愁如许。绣帐隐残灯，炯炯双眸，没个安排处。\",\"莺啼侥幸天将曙。见落红无数。泪点更多些，弹与东风，总把春交付。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab71572c3b62579cb0d','丹凤吟·答洪昉思梦访之作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"别后相思一样，目断城云，魂销江树。玉帘深锁，愁似乱莺狂絮。\",\"沉沉落照，半明还暗，野烧回春，寒山催暮。梦里何曾怕险，滚雾翻风，为我连夜飞度。\",\"也有镇常相见，见时不免含嫉妒。道我眉儿翠，又身轻过汉，腰细如楚。\",\"那知憔悴，不复再行多露。关黑枫青君自爱，更休将愁诉。\",\"但须纵酒，看石榴半吐。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7170e75ba33e7c9e8','一萼红··春日东园','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"漫窥帘。爱桃花万树，新雨拆红缄。近水无言，随风有恨，欹斜直恁娇憨。栖不稳、画梁双燕，为营巢、终日语呢喃。似喜仍忧，乍暄还冷，情绪恹恹。\",\"尽道西郊堪赏，便倡条冶叶，谁再沾粘。泪岂因花，病非中酒，羞称宋玉江淹。受尽孤眠况味，眠又起，真欲似春蚕。忽见隔楼山影，想杀眉尖。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab717c0c766ba39df62','满江红··书恨，用张安国韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"一笑回头，可便是、留心有意。频想像、巫山荐雨，银河隔水。笑我枉推卿意表，料伊未晓侬心里。算便是、说破舌尖儿，谁倾耳。\",\"残梦醒，东风细。闲踏遍，花阴碎。伊家曾晓得，这般情味。花暖罗窗依约坐，莺啼绣幕瞢腾睡。空教我、目断粉墙高，如千里。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab719ce064f57e15221','师师令·除庭酴醾栀子盛开凄然赋此','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"胭脂日晒。剪轻罗刀快。天然真色谢铅华，更脉脉、檀心无奈。一样背时春不待。问要谁怜爱。\",\"看花心闷因花害。镇啼痕沾带。落霞斜照玉屏红，正雨过、远山如黛。只道凤帏人尚往，恁晚妆时戴。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab71a81c36fc183806e','摸鱼儿·夏日同郎晋扬饮商霖宅','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"爱凉风、雨声拦截，披襟坐对林樾。倚亭况有萧萧竹，烟里海榴如血。壶漫挈。银烛下、玉盘瓜果堆红雪。蝉声带咽。恁萧瑟如秋，垂垂珠露，何处有炎熟。无穷事，准拟樽前诉说。\",\"欲言又恐凄切。鱼肠屈作同心结。安得尽交英杰。谁最劣。君不见、景升儿子皆烟灭。悲歌未阕。人醉月西倾，客中又是，深夜听啼鴂。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab71d65fade198d1fa9','八声甘州··听唐韩如弹琴','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"自钟期死后碎瑶琴，古调向人难。正张华石上，苏公泉侧，泪血斑斓。打叠移宫换羽，袖手且盘桓。谁在东江市，霹雳重弹。\",\"最苦长门秋怨，可为予改操，白雪幽兰。奈无端五岳，膈上心间闻。但从来、读骚饮酒，问傍人、此意不相关。祇博得万山俱响，风急云翻。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab71ddf0609977efd43','点绛唇·秋病','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"起了还眠，须知不是侬贪懒。银筝象板，听着惟长叹。闻道新来，你也梳头慢。\",\"乌云散。不茶不饭。人瘦黄花绽。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab71ea30b3a1f909d98','师师令·风情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"花茵宝褥。更龙涎馥馥。春宵会合凤鸾俦，怀抱着、娇香柔玉。再四央求松耙复。怪檀郎恁促。\",\"含羞几度嫌明烛。把红鸳轻蹴。郎言留着看娇容，云髻亸、芙蓉肌肉。勉强因循还闭目。怪檀郎恁毒。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7227244c23da2b4b7','醉花阴·客夜咏枕用沈会宗体','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"鸳鸯绣出偏成对。衬荷钱叠翠。街鼓报三更，怪冷嫌高，欹着何曾睡。\",\"无端要拍珊瑚碎。又暗垂清泪。臂玉恐销香，低首寻思，蓦地灯花坠。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab723841ba2ef2db590','探春慢··孙无言徵刻予词于扬州，遥有此寄','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"一树琼花，二分明月，扬州自古佳丽。杜牧曾游，何郎不再，试问风流谁继。才子飘零尽，还喜得、词编玳瑁，知音千古寥寥，能识高山流水。\",\"念我朱颜易老，奈江梦少花，洒笔成泪。浪许金荃，羞称玉树，何处更将愁讳。从此然脂夜，免冻了、春纤十指。虞生不恨，相逢竟须沉醉。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7251a83848ac4a8d3','醉花阴··睡起','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雾障云屏春睡重。玉枕烟鬟拥。何处晓莺啼，翠黛轻扬，又觉星眸动。\",\"粉痕狼籍燕支冻。强起心烦冗。对镜懒梳头，日上纱窗，犹是思残梦。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab726105c6d5b08ec8f','双燕笑孤鸾·旅思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"正残梦醒，见斜月窥窗，晚钟初撼。披衣坐起，仍在乱山孤店。嘹嘹断鸿过也，宿芦花水村烟敛。空说远游豪迈，止博些伤感。下箬。\",\"泛来酒酽。若比着离愁，酒儿全淡。西风凄紧，独自把灯长点。几番寄书不到，料佳人怪侬抛闪。争信为他垂泪，有罗巾为验。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7266a126a5f15c21c','销夏·雨窗读巢青阁词翻「风入松」，用仄韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"窗外葡桃珠一架。苏苏雨下。把卷科头怯晚寒，映斜阳、玉虹遥挂。香凝楚簟重铺润，逼吴笺嫩砑。\",\"忽忆东头销永夏。湖山如画。唱遍旗亭绝妙词，有按拍、双鬟低亚。峰临高阁青来，珠滴小槽红榨。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7292eca079f228da5','鹊桥仙·咏轿','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"教莺先促，逢花又住，帘幕有时偷控。芙蓉小褥衬香绵，想行处、柳腰微动。\",\"层层碧嶂，迢迢翠陌，回忆旧欢如梦。常将飞燕比身轻，敢归路、愁多增重。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72973acca2f83812b','沁园春·美人','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"梦想容辉，彩云出崖，芙蓉出波。算太白休增，空忧粉薄，斜红任绕，岂怪朱多。宝靥含星，琼姿映月，有无限春风隔扇罗。真奇艳，早脂开北地，菊曜东阿。\",\"酒阑人带微酡。怕易化、红酥不敢呵。为密语防猜，羞添两颊，佳期不误，喜动双窝。界处娇啼，頩时薄怒，似天半朝霞艳雪和。平生愿，愿身为明镜，日近嫦娥。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72a08ad4257205ab4','行香子·赋恨次彭金粟韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"眠底谁亲。销尽离魂。书难寄、白雁红鳞。镜儿扑碎，钿合空存。倒系人思，牵人恨，惹人嗔。\",\"深盟厚约，搏酥拌蜜。奈而今、远隔河津。愁须有分，梦也无因。记水边花，窗边月，枕边云。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72a2e426b434f8230','蝶恋花·闻声','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"料峭东风花树暝。月过窗纱，犹见残灯影。香梦初回知害冷。彩云低唤何曾应。\",\"玉漏迢迢嫌夜永。一点春莺，趱上相思症。半晌隔楼还寂静。娇娥睡去檀郎醒。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72a661a3e774f3129','柳梢青·咏柳寄妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"野驿逢春，荒城傍晚，对语寒乌。才见摇金，又惊飞雪，几日萧疏。\",\"为伊目断长途。正雨后、斜阳翠芜。眉小常颦，眼重未醒，腰细谁扶。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72a80f63d066c7ccc','鹊桥仙·题昭时二儿画羽阶三兄小像','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"古松百尺，悬流千丈，挥扇解衣盘礴。玉楼金埒逞豪情，总不想、而今丘壑。\",\"昭儿图景，时儿图面，孺子丹青能作。老来应识少游心，当念此、及时行乐。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72b1a27d95845b360','渔家傲·记得','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"记得琼筵春意泄。背灯偷把丹唇洁。天样红墙成间别。须为彻。名花不护非豪杰。\",\"欲诉东风难自说。子规为我长啼血。静夜倚楼谁玩月。肠寸截。碧云来去雕栏热。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72cce7cd61f8e4150','鹊桥仙·杜鹃','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"江南花落，楼西月冷，啼到不堪闻处。年年含血骂东风，恰似我、呕心奇句。\",\"云低铁峡，烟荒玉叠，漫说离情最苦。天涯多少断肠人，只是你、不曾归去。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab72d7b2ad952849ce6','西江月·春夜','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"别后何曾有梦，见时又说多情。玉楼银烛坐吹笙。赢得半床春病。\",\"欲去愁魂袅袅，难干泪眼盈盈。落花风紧月三更。帘外鹦哥叫冷。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab730df74e6bc5a72ca','水晶帘外月华清秋夜东园席上赠紫儿','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"怕听催银箭。早垂地、河光如练。同在瑶台，爱月里天香，画帘低卷。私语傍人都不会，要灭烛、蘋风方便。流盼。又悟得幽情，欲言仍咽。\",\"醉来也应倦。奈狂朋怪侣，满斟频劝。任欹鬟接黛，红牙重按。竹脆丝娇空旖旎，怎赛过、肉音清转。肠断。任相逢就去，争如休见。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab73127a961c425876f','遍地雨中花雨夜感怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"昔日鸳帏春睡足。有此夜、睡难熟。蕉叶窗西，藕花池上，雨冷浑如哭。\",\"怕他人、笑我痴迷，恨和愁、都藏在腹。倚枕无言，停杯不饮，坐看秋灯绿。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7317782023f7bc2d2','鹊桥仙·友人纳姬戏赠','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"玉帘不卷，翠屏深锁，花气着人如醉。纷纷蜂蝶太贪春，才一夜、粉黄都退。\",\"鬓云全倒，眉山轻蹙，楼上日高犹睡。新欢未审有何愁，看枕上、盈盈珠泪。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab73348373931280a1c','多丽··忆吴门旧游','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"记当时，多情初会金昌。但盈眸、都称佳丽，输伊真色生香。况知音、能翻旧曲，偏爱好、屡换新妆。几岁抛家，今年避客，半垂银袖倚迥廊。喜偶遇、无因却走，含笑道胜常。参差见、如嫌蝶粉，未退蜂黄。\",\"漫携樽、溪山销暑，含愁曾吊真娘。翠鬟倾、人簪茉莉，红裙脱、自裹鸳鸯。月底牵裾，河干解缆，风流赢得断柔肠。想楼外、重重烟树，依旧下斜阳。空回首、五湖人远，风雪苍茫。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab73446986976978e92','西河··同袁令昭先生集湖上','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"春事晚。落红一夜飞散。蜻蜓蛱蝶漫踌躇，东君意懒。柳烟深锁玉楼空，画船犹把歌按。雨俄霁，寒又暖。浪花日影同灿。\",\"匆匆过客爱西施，髻鬟双绾。小姬随意弄琵琶，青衫愁泪俱满。游丝百丈正宛转。恨斜阳、终自难挽。回首总成虚诞。典春衣、共卧垆头，休惮赏遍湖山，无人管。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab734d18a5cc0ba0daa','浪打江城闺怨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"愁病亸犀簪。犹带宜男。恁样分离，滋味不曾谙。枕上朦胧才片刻，知何处，是江南。\",\"料得恋娇憨。燕搅莺探。人在绿杨烟里唤停骖。舞蹴地衣歌却扇。沉醉也，污银衫。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7370229ffd0274a82','扶醉怯春寒·春晓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"花晓玉帘，红莺声百转，偏相混。睡眼瞢腾，见向壁灯初烬。强扶馀醉怯春寒，奈又是、东风几阵。心头闷。将宝枕推斜，绣衾压损。\",\"记得绸缪初印。别易会何难，真如转眼韶光尽。验取瑶簪，怎上有通长璺。春来倒喜梦糊涂，全不是、旧时帮衬。难思忖。待得见他时，将他细问。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7385cd6a7bd0a6d97','氐州第一·送邹程村之江西','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"万古钱唐，波浪涌雪，滔滔日夜东注。别酒淋漓，孤舟摇漾，残照低云满路。执手方悽恻，人说潮平可渡。野鸭鸣沙，林蝉噪柳，更听柔橹。\",\"十载相思能一晤。有无限、幽情难诉。流水空弹，凌云初就，怕蛾眉嫉妒。豫章城、星子县，堪纵目、襟吴带楚。倘遇秋鸿，寄书来、水天朝暮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab738b3ac85716fc955','蝶恋花··幽会，用李后主韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"半夜瑶阶?细步。避月随花，雨送巫山暮。说道□间难久住。未明须放奴回去。\",\"渐渐花阴和月度。帐□更深，啮袖低低语。千万莫忘封绛缕。明宵候你原来处。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab73a7b114523df7606','浪淘沙·次沈丰垣韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"见面不如初。绣带银涂。双鸾截断总成孤。离合从来俱是命，我害卿乎。\",\"烛泪晓窗枯。梦里惊呼。湘山云雨竟荒芜。当日风流天也妒，莫恨情疏。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74050393ac67d9c68','玲珑四犯·闰六月初七日作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"小月窥人，早半枕新凉，客睡初醒。珠泻高荷，怅望碧池烟暝。枉了盼着佳期，今夜依然孤另。问几年、积下馀剩，算出恁般节令。\",\"西窗立尽梧桐影。雨苏苏、隔河微映。锦机料得无心织，空把云鬟整。屈指残暑易消，且按着、离情待等。不似人天畔，羁旅归，期无定。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7415a35355c6a3703','望江南元夜即事','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"桥上月，终夕自凭栏。只怕粉云香阁闭，从教珠露碧衫寒。\",\"人道看灯山。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74212a4ca4b3bc4e9','高阳台··次韵答陆荩思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"日黑山寒，天低草合，高城不阻离魂。握手相看，曾经几度论文。残莺叫醒西窗晚，奈依然、隔着江云。叹流光，开遍玟瑰，难寄芳芬。不须懊恨轻分。\",\"喜新词再读，气逼星辰。欲付雪儿，翻教泪洒罗巾。放歌作达真吾事，怎柔肠、转尽车轮。正西陵。竞赏新荷，莫负双樽。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74537a34a4a686ef6','鹧鸪天·夜怨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"肠断西园月满林。无聊空自拥寒衾。垂将珠箔从春去，挑尽银灯觉夜深。\",\"期屡负，梦难寻。不知流泪湿梦巾。口衔莲子兼红豆，尝遍相思苦在心。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74a9c2703c0b65d98','菩萨蛮·慢·答友人寄怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"夹城华月。问浮光美影，几番圆缺。记小阁危栏，有鹦鹉时呼，石榴新结。画烛高烧，人欲醉、清歌未阕。爱携舟仙侣，又把藕丝，为我亲雪。\",\"书来怪侬久别。早西风残照，蛩语悲咽。纵盼得、一纸书来，谅恁小银笺，愁恨难说。梦想机云，终不负、黄花佳节。漫挑灯、幽窗冷雨，寸心偏热。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74bcf15c45ad2d31a','鹧鸪天·赠妓宛儿','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"香阁题诗写白罗。玉栏携手奈情何。春寒飞雨浑疑梦，夜静游云欲听歌。\",\"含笑靥，锁愁蛾。泥人佯醉几摩挲。水村明日催花发，抱取红裙细马驮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74c9d04f6f59bcfe3','蝶恋花·睡起','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"窗满斜阳初睡觉。庭院无人，不信鹦哥叫。强起扶头还脱帽。杏花零落春愁闹。\",\"无限愁颦因一笑。情死情生，拚为情颠倒。两袖啼痕曾不燥。漫柔乡里邯郸道。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74d3b127955d71632','鹧鸪天·怀妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"翠馆花深路欲迷。暗中绣手记曾携。独怜锦带人如燕，恰喜琼筵客似泥。\",\"闲处想，梦中啼。小楼深坐两眉低。纷纷蜂蝶春如画，不肯梳头下玉梯。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab74f572b88c0dbbb71','月笼沙除夕','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"酒满香浮盏面，雪晴光动檐牙。凤灯双照鼓频挝。赚得儿童成老大，爆竹梅花。\",\"莫恨无情岁月，何妨痼疾烟霞。醉乡高枕即为家。四十八年堪一笑，蚁阵蜂衙。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7512d2f9acbbd0d23','洞仙歌·咏尘','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"如无似有，恁厌厌漠漠。几日牵愁暗珠箔。当时留、宝瑟脆断朱丝，轻拂取，随拂随生难却。\",\"尚思同宴赏，窄袖弓鞋，叠在空箱更谁着。有醉尽生前，人到千秋，总也是、北邙寂寞。但制得、新歌付莺喉，向簌簌春寒，画梁飞落。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab751f6b62efb1538fe','青玉案·美人手','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"柔荑腻滑明如雪。曾得见、湘帘揭。巧弄机关春意泄。轻翻小镜，暗题密字，蓦把魂灵捏。\",\"泪珠弹尽相思血。倦抵牙龈自微啮。绣带慵拈人代结。玉筝弦断，翠奁脂冷，重杀针儿铁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab751fd44a2fc33733b','如梦令·佳人对镜','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"曾共粉郎私语。独立从来自许。谁学远山青，两靥胭脂微注。\",\"回顾。回顾。错把镜中人妒。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab752c51314fe3bbf9e','浣溪沙·春闺','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"絮扑游丝不放飞。燕巢花满落香泥。无人春冷教添衣。\",\"妆罢乱莺啼阁外，梦回斜日照窗西。此情除是那人知。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab753ca535a8e3c38e1','喜迁莺··寄俞生士彪','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"水鸠鸣屋。见春阴不散，渐飞霢霂。小燕初来，闲花又落，故把馀香频蹴。烟迷半林深翠，风剪一池轻縠。忆旧岁，正此时相见，掌杯盈绿。\",\"幽独。衰更甚，云拥乱山，咫尺欢难续。王粲情多，江淹才尽，晚节真成碌碌。途穷笑吾增恸，雅奏须君终曲。春难待，怕南城握手，樱桃将熟。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7555855faaf3697fe','御街行·美人斗叶子','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"桃笙滑腻花纹漾。疏竹里、开烟幌。曾输斗草捏绣腰，又道簸钱欺诳。莫愁新谱，争奇角胜，侬把犀筹放。\",\"低鬟半侧蝤蛴项。心用在、秋波上。小奴持扇扑兰风，也自抬身偷望。玉环不肯，罗巾不要，拔下钗儿当。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7564a020c49ed7dde','万年枝·吴门春思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"昔日高台，恨听歌未了，已见荆棘。野堑低云，人道锦帆遗迹。实袜花钗土蚀，记笑倚、东窗无力。而今安在，乱峰荒寺，古碑谁识。\",\"天涯寒食。正当那、落红万点，旅思凄恻。门对垂杨，住久黄鹂认得。愁泪不须沾臆，恐添注、太湖深黑。醉时独啸，苍波东逝，斜阳西匿。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab757659484b43c6390','长相思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"人初静。风偏横。落叶秋窗眠不定。一床浑是病。\",\"愁魂冷。悲啼凝。瘦得新来羞对镜。对时心要硬。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7593e47d4a76b05ee','渔家傲·立春晓起偶作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"新岁昏昏愁只睡。日光照入帘栊内。六幅素屏闲似水。因底事。晓来题遍银钩字。\",\"砌草庭梅徒自媚。白头惯洒多情泪。醉里不知春已至。重审视。彩蛾金燕分明是。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7595de7820427ce1e','玉烛新·读茀庵填词，寄王德威','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雨馀残暑退。记吹过鸾音，绛河垂地。篱花霜倒，新梅破、转眼小春天气。金徽懒按，端只为思君憔悴。挑灯坐、手展新词，不知泪沾衣袂。\",\"也思纵酒豪吟，待骏马翻蹄，苍鹰鞲臂。屡稀欢会。奈愁在、两眉难讳。川途迢递。问何日兰舟重系。怕还是、十里莺啼，桃花又坠。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7598986fbcce5acd5','厌金杯吴山雪望','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"山接荒城，沙昏断浦。倚危栏、江天欲暮。六花纷绕，扑面更沾衣，难挥去。似我离情最苦。\",\"兽烟不断，貂锦添寒，恰又是、潮生风怒。素车白马，人道子胥魂，公莫舞。一醉愁销万古。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab75a99aca7a7a3f3d1','六幺令··次晏叔原韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"隔帘疏雨，又被晚云阁。病馀转添腰细，羞把柳枝学。此恨都无人晓，花底鹦哥觉。粉笺香匝。新词在手，记取红幺指尖掐。\",\"夜少蜂愁蝶怨，缕缕难酬答。多少水远山长，密密重签押。似恁凄凉况味，端为风流霎。月如银蜡。却曾照过，私语喁喁画楼角。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab75c4f15e9525d15a5','点绛唇·美人耳','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"巧鬓轻笼，月痕半吐真珠莹。惺憽不定。莺语俄惊醒。\",\"蓦地心烦，唤着何曾应。提名姓。不教安静。难道真真冷。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab75ea182294e2d8d0f','蝶恋花·感旧次沈丰垣韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"花雨纷纷山影媚。春岂愁人，人把春牵累。马上垂头元不醉。为谁流满青衫泪。\",\"水馆月明仍独睡。一寸榴笺，细语胸前佩。好梦惊残空自悔。杜鹃啼破三更嘴。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76055fbc256d16d34','青玉案·美人目','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"满堂佳丽秋波凝。偏属意、真徼幸。烛底孜孜人未省。隔纱如笑，传杯欲语，都做相思证。\",\"登垣窥玉花枝暝。似梦佳期奈无定。泪湿红绵空寄赠。小窗坠月，残灯隐暮，炯炯寒宵永。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab760972c0348d50eb4','夏云峰晚登南楼观云作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"水远帆低，山围树合，云起小楼日暮。惊夭矫、青鸾并下，爱绰约绛莲齐吐。更重重、蕴火含烟，似欲借、向风乱烧平楚。问何处阳台，连朝傍晚，幽期宋玉曾赋。\",\"香阁深深看不见，况顷刻溟蒙，竟迷江路。停杯望、一心辗转，垂带坐、双眸凝注。总莫怪、换狗更衣，却全似伊行，言语无据。就倚遍阑干，轻阴不散，又作半天疏雨。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab761f8958b69cc0a38','点绛唇·夏日闺情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"小院无人，罗窗玉枕清如水。鬓横钗坠。汗透酥胸腻。\",\"疏雨凄风，翠竹琅玕碎。俄惊起。定晴斜睨。寂寂帘垂地。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab762c283ad8311e3c9','夜合花·同毛稚黄湖心亭眺望','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"复嶂笼烟，孤城却月，中堆万顷琉璃。惊魂炫目，空花忽现离奇。山钟断，水禽啼。倚危栏、独振吾衣。哀筝未阕，一声长啸，风起云飞。\",\"休将帘幕低垂。待向浪痕高处，漫泼金卮。东吴南宋，都成野马游丝。头欲白，泪频挥。算将来、不饮真痴。酒酣起舞，君休捉住，黄鹤同骑。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab762ea8857e54d9272','踏莎行·后段多一字。旅梦','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"孤影长羁，双眉时结。魂灵不怕关河截。野桥南去不逢人，朦胧一片杨花雪。\",\"问有谁应，愁难自说。杜鹃窗外空啼血。道不如归去怎生归，银骢又秣三更月。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76336ee720c7ac3ea','满江红·咏柳','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"几个莺梭，早织就、千丝万缕。最苦是、苏堤欲晓，灞桥将暮。媚眼未醒开又合，纤腰半倒扶难住。重沉沉、搭在玉栏杆，和烟雨。\",\"还记得、长亭路。曾折送，行人去。恁牵缠似我，别时情绪。帘暗梦回应有泪，楼高目断浑无语。隔青山、不见紫骝归，蒙天絮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab763e477f66b85835c','离鸾·陆荩思忆婢喜儿，次干臣韵；予亦翻此调和之','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"春去渐疏杯酌。无奈晚风偏虐。吹散柳绵空外转，尚解乱投池阁。双燕教雏飞，竞衔花、巢泥香落。镇日手披团扇，恁腰围如削。\",\"咫尺音书难托。况是连宵梦恶。桃叶桃根何处问，泪尽春江都涸。空把喜为名，倒误人、半生行乐。不许灯花发燄，又闷来弹鹊。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab764b6138d3ac05018','夏云峰·五日有怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"淡日烘葵，微风展艾，愁里又逢佳节。冰簟滑、鸳衾香在，朱门销凤箫声咽。托妆成、钗颤金符，爱臂玉、凝光五丝亲结。怎正拟为欢，连朝风雨，无端顿成离别。\",\"强自随人看竞渡，便粉白雅黄，满前都劣。愁不断、偏遭酒困，期屡误、重将蓍揲。也待托、鸲鹆传情，奈句字含糊，未通人说。只暗里呼名，榴花鬓底，料得耳轮频热。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab764ba52a78a5d39ac','倦寻芳·饮合涧桥酒楼，同诸虎男暨及门张台柱','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"寺桥压雨，峰馆凌空，宛转相属。处处褰帘，一带小溪如玉。幽恨不随流水去，离魂偏遇游丝触。爱村姬，任银瓶笑指，芳名原熟。\",\"痛往事、存亡荣落，不意欢娱，此际能续。人到中年，怕听断肠新曲。兴过难留头上黑，愁来且尽杯中绿。记岩西，抱花眠，夜深烧烛。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab766c2f5d50fcfb958','凤衔杯怨别','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"浴罢慵将花冠整。还共酌、碧梧金井。总唱惯阳关，银筝未按香喉应。翠黛小，低鸳颈。\",\"忆欢娱，重悲哽。全不管、有人难听。正烁石流金，火云突兀炎威盛。却怪你，心偏冷。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76832b49d41cae21c','满镜愁','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"烟草凄迷，风袅画帘不定。楼上斜阳人独凭。残醉醒。春老黄鹂静。\",\"雨覆云翻，岂是红颜心硬。病起谁怜腰带剩。嫌瘦影。愁满菱花镜。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76873ceaf9fc63c47','华胥引·见张祖定新诗有寄','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"云窗春暖，月地宵寒，乱花似锦。京兆当年，深青浅绿曾细审。何故多病多愁，想粉坑脂窨。却似黄鹂，欲啼还被寒噤。\",\"故剑新缣，总因他、蛾眉谗谮。频移带眼，须知细腰姓沈。还怕玉帘风细，人愁单枕。清夜然脂，日高为你犹寝。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76ad4d654be91edee','雨中花·春梦','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"月冷红梨浑欲谢。没甚事、和衣睡罢。宝帐灯明，翠屏香袅，已在花阴下。\",\"独行小径心儿怕。遥望见、玉帘低挂。寂静无人，分明有路，隔着秋千架。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76ca57ee3fe50ac64','荷叶杯·春闷偶成','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"都道春来愁闷。不信。日晚上危楼。\",\"落花飘尽水东流。愁摩愁。愁摩愁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76ccde3794723ba4b','鹊桥仙·闰七夕','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"琼云乍卷，金风渐冷，谁把鹊桥重驾。玉人携手隔帘看，堆雾鬓、晚妆初卸。\",\"更深灭烛，凉生却扇，咫尺明河欲下。阑干私语告檀郎，总输你、朝朝夜夜。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76d6dfc22843d5f68','苏幕遮·闺病','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"燕声娇，花影碎。日过窗西，犹自厌厌睡。一线情丝常似醉。九十春光，半拥鸳鸯被。\",\"靥销红，眉敛翠。便到沉身，总是多情泪。说与东风都不会。镜子裙儿，晓得人憔悴。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab76f932c59ea43ebc3','浪淘沙·夜怨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"人静雨潇潇。帘影风摇。残杯独酌转无聊。翠袖啼痕红烛泪，同洒春宵。\",\"烟水隔斜桥。魂断谁招。带围宽尽沈郎腰。到得见时卿试比，不让苗条。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab77278a64821a4be26','鹊桥仙·早梅','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"粉痕微坼，檀痕尚浅，迤逦因香寻着。一枝偷采雪中春，敢燕莺、全然未觉。\",\"银瓶水暖，翠鬟手腻，又把软帘垂却。溪桥烟冷月初斜，怎消受、五更寒角。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab773910e592e6d08f0','西河··感旧用周美成韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"伤心地。重经往事休记。人非物换，两茫茫、不堪提起。歌停舞歇断行踪，望中烟草无际。阑干曲，空徙倚。旧因何故牵系。朝思暮想，更添修、愁城怨垒。\",\"他生未卜此生休，忆君清泪如水。雕屏绣榻今还在，但那人对面千里。业债想因前世。故今番、会少离多，冤对生死，总由奴命里。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7745843233ee0380a','花犯··夏夕闻隔楼弦弄有感','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"簟波平，残灯半灭，三更梦初醒。两眸炯炯。见斜月穿窗，闲弄花影。蓦然坐起罗衣冷。隔楼笙管竞。遥想像、翠帘红烛，空房真寂静。\",\"回思少年爱追凉，槐阴携艳质，香肩曾并。消薄醉，记亲煮、凤团双饼。歌懒奏、笑垂素颈。今隔着、银河千万顷。都不道、那时轻诺，海榴花外等。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab775a4b07dfc455988','苏幕遮·题竹青女郎祠','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"殿云鲜，庭树绿。幡袅溪风，溪水明如玉。游女穿花高髻矗。踏破红裙，争唱迎神曲。\",\"梦来犀，春去速。月照苍梧，夜夜啼湘竹。何似小姑居处独。紫燕双飞，时把晴丝触。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab77996bac4d8860a0e','宣清·重晤潘德延述感','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"二十年前，事类浮云，偶聚长江不返。雪山低、并逐银骢，月楼深、共挥金椀。击筑悲歌，弹棋逞艺，雄心难按。都不记，是何时，蓦地与君分散。\",\"昔日风流，而今老丑，始信浮名幻。又意外奔波，就中羁绊。正当落花飘漫。喜得重逢，到其间、几声长叹。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab77b6510aaa765c483','点绛唇·美人颈','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"捏粉搓香，烟鬟斜亸蝤蛴嫩。绣床垂顿。半为东风困。\",\"不许轻搂，意拗心原顺。低声问。齿痕偷印。又道多情狠。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab77cdcfd670b3b9e6e','西江月·小楼','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"疏雨尚沾池草，轻风暗扫庭梅。小楼灯火画帘垂。帘外月华如水。\",\"被冷真愁香髻，枕欹却忆颦眉。忽闻金斗簸残灰。夜静原来未睡。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab77d79788ef68f1df7','宝鼎现·月夜泛临平湖，同三兄羽阶、潘生云赤、男圣昭赋','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"烟涛暗拥，远岫微露，深幽无极。谁忽把、纤阿推上，皓彩团团生几席。吹锦烛、正徐开画舫，莫奏中流哀笛。喜寂寞、鱼龙夜静，上下水天俱碧。宝鼎今日犹争唱，赤乌人、尽去无迹。叹尔汝、一杯相劝，青盖如烟何处觅。\",\"唤不醒、醉临春千尺，降作佛奴何益。便痛剪、娇娥云髻，怎救胭脂狼藉。词客为欢，且游赏、漫辞涓滴。羡沙边鸥鹭，不管人间今昔。露下翠幕轻寒隔。山玉颓无力。拥绣被、早是鸡鸣，斗柄垂垂堪摘。\"]',NULL,NULL,NULL,'[\"吴赤乌十二年，临平湖出宝鼎。孙皓时出石函，占曰青盖入洛之祥，后降晋。陈后主至簿中，湖开敕度，国中妇女二百八十九人，自卖于佛寺骂奴以厌之。\"]'),('e647d10fec1beab77e25bb162fff67f9','望梅·早春长桥探梅作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"南村古渡。况弥漫野水，天寒日暮。傍溪桥、一树亭亭，未全放，多应雪欺风妒。缟袂斜舒，倚修竹、佳人凝伫。正香浮月淡，梦醒参横，无限凄楚。\",\"孤芳漫将愁诉。恨催人朝夕，头鬓非故。想纷纷、荣落何常，看身外浮云，眼前岐路。潦倒情怀，拼一醉、难成佳句。遣磐金小蕊，莫也怪侬不语。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab77f8f2a1f095e88b8','九重春色·春闺','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"正金钩低控绣帐，绛台尚摇银烛。莺睍睆、飞上碧桃枝，画帘薄、微通朝旭。又隐隐、递钥声相续。惊好梦、鬟敧堆线，争如我、怕见菱花，才一夜、暗销香玉。妆成小立屏山矗。树荫朱栏，燕飞华屋。教人两眉频蹙。看晴丝露蕊，时暗相触。\",\"芳池水正溢。垂杨外、任倒了秋千，慵蹴泥素袜。石上红苔，倚翠袖、粉沾新竹。漫书题、鱼茧半纸，泪湿凤罗千幅。岂伤春、常苦病缠绵，似三起三眠蚕熟。斜日坠、愈增幽独。登小阁、目断云山万里，归期难卜。倩杜宇、为我催促。蝶和蜂、尚惜韶光晚，抱花自宿。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab780ce0f9642332b30','满江红·题三子西湖竹枝词','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"生长钱唐，忍负却、莺年燕岁。看几度、湖流微涨，山云低坠。帘卷更添眉上恨，酒阑都化心头泪。唤桃根、闲按竹枝歌，声声脆。\",\"怪万叠，玻璃翠。淘不尽，愁肠胃。任生绡共剪，真珠同缀。古迹漫夸南宋乐，残妆犹爱西施媚。似寒乌、相对语斜阳，无人会。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab783f4a52fa10f9c2f','最高楼春愁','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"从今后，春去不须留。都付与东流。风回絮卷花堆砌，莺啼人醒月当楼。最难堪，三月尽，五更头。\",\"但听着曲儿心又闷。但对着、酒儿身又困。常只是，靠香篝。病中也要寻排遣，闲时却又记漫柔。细思量，全不是，为春愁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7842a5961194047d2','踏莎行··恨情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"竹叶香丝，莲花金瓣，月华初满花头绽。纵然端正又风流，十分好处无人看。\",\"钿盒难分，连环不断，相思还寄垂杨岸。前生果是没因缘，如何空积愁千万。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab785a3467c9d9b6042','浪淘沙·春恨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"弹泪湿流光。闷倚回廊。屏间金鸭袅馀香。有限青春无限事，不要思量。\",\"只是软心肠。蓦地悲伤。别时言语总荒唐。寒食清明都过了。难道端阳。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7865d4822ace8ce3f','花心动··怨词用谢无逸韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"石阙口中悲不语，笙炙眼儿空热。三尊佛儿，四座莲台，这位怪侬多设。去心莲子谁知苦，冲阵马蹄痕流血。乱头发、只消不理，理时休结。\",\"没有笔儿怎撇。笑雀见笼糠，空生欢悦。桃核鸳鸯，那个敲开，空把两仁磨折。大刀底事误佳期，谎破镜、天边残月。你何苦，寸寸竹儿生节。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7865d4822e034c359','花心动··怨词用谢无逸韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"蕉叶千层心却少，煮蟹难教肠热。你做车儿，做了方轮，这样机关空设。雨多便把晴忘却，伊不肯心边添血。风筝断、半空飞去，料难重结。\",\"新着鞋儿休撇。也巴到初三，暂时心悦。一带垂杨，日夜添丝，丝动便遭摧折。胸前小镜手三翻，曾猜着团团明月。花隔院，谁晓这些根节。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab786714de76b4c04f2','渔家傲·新秋','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雨过回塘波细涌。水精帘外残霓控。半亸犀簪眉懒纵。谁惊梦。猧儿撼起金铃动。\",\"病着轻绡还道重。藕丝倦雪银盘冻。七夕又来谁与共。心怕恐。柳梢新月窥西弄。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7868a20ac139db306','望江南晚思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"天已晚，还自怕登楼。山底淡云轻似梦，灯前寒雨细如愁。\",\"人问嫌抬头。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab786a2e2dbca09deb8','六州歌头··凤凰山吊南宋行宫','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"烟销艮岳，一马却浮江。南渡事，真草草，寓钱唐。正苍黄。怎爱湖山秀，新歌竞，离宫起，将二帝，冰天苦，竟相忘。恸哭朱仙三字，成疑狱，自弃封疆。反半湖灯火，蟋蟀当平章。播越堪伤。遂销亡。\",\"空馀五寺。山钟歇，悲辇路，草荒荒。子规叫，精灵出，景凄凉。泪沾裳。回忆骑驴笑，厓山远，断归航。西湖上，却依旧，奏笙簧。闻道鹤归华表，城郭是，人去何方。恨东风一夜，吹变几沧桑。满地斜阳。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab788aaaa6d27054c2c','念奴娇··寒食悼亡','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"隔帘深雨，昼沉沉、做出凄凉寒食。况是东风，连夜紧、吹得残花无力。绣幕斜穿，雕梁并语，旧燕如相识。蒙蒙飞絮，搅将天气昏黑。\",\"因念昔日南楼，幽明长判，岁久花钿蚀。浪说珊珊，非又是、寻记竟无消息。莺老歌慵，春寒酒懒，难把愁城克。生何如死，黄泉不用悽恻。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab78e41039b6385098f','归去难离情，用周美成韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"向道伊至诚，谁想更变。那夜阑干语、情非浅。云香雨嫩，蓦地巫山远。你莫将侬怨。你试思量，还因甚事离散。\",\"玉碎珠沉，魂断情难断。何故伊家毫不垂念。红窗翠月，就是重相见。还要将侬难。粉颈斜回，娇嗔不肯抬眼。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab78e758f73fb205308','如梦令·送春','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"病起忍将春送。弱草依花风动。犹自泥残莺，不唤春回何用。\",\"如梦。如梦。但觉翠颦增重。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab78e816fc402a4836c','昼夜乐·亡妇遗钗，有火珠一颗。今失所在，怅然赋此','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"清和风暖樱桃醉。记合卺、成佳会。玉楼向晚妆成，亲见珠钗低坠。椽烛光寒新月晦。红影动、斜飞襟袂。回首漠阜空，竟难寻仙佩。\",\"香奁狼藉孤灯背。但留些、断金翠。几番悔恨当时，不与蝶裙同施。簸土扬灰休细觅，怎再上、别人头髻。只道落怀中，却原来是泪。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab78e936cdc4814fc20','月中柳湖上春行，同毛稚黄作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"少年曾共踏青游。酒满十三楼。香尘不断吼骅骝。芳草蓝深，夭桃红浅，白了人头。\",\"山如屏障周遭在，难抵住、一段闲愁。停杯无语思悠悠。飞絮蒙天，游丝著地，不要遮留。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab79062d6ffb787f1a1','垂丝钓寒夜','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"灯明翠幄。离愁今夜偏恶。坐看结双花、镇寂寞。成担阁。教侍儿剪却，何曾着。\",\"把薰笼自摸。睡时又怕，被儿也不单薄。模糊旧约，揉碎花笺嚼。听尽三更柝。鸡声咿喔，又萧萧雪落。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7925ee81de3a1e3e8','沁园春·美人腰','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"轻要云支，纤愁烟袅，擅名楚宫。似垂堤欲断，月临细柳，穿花易损，春在游蜂。拜跪须扶，欠伸又困，持履真忧掌上风。曾亲见、拾将裙带，两结心同。\",\"舞时翩若惊鸿。真不负、陈王小赋工。被邻姬捏取，悔教赌赛，情人抱过，恰便裁缝。莲锦拴愁，云英系恨，反类东阳病后容。天生就，岂减厨能学，休恨肌丰。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab79461d96376531437','金明池·春恨，用秦少游韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"花压重门，莺啼别馆，一望阴阴苑路。春渐老、云昏日淡，更晓雾结做昼雨。有心情、怕上红楼，翻绣被、记起玉人何处。怎带缓鲛绡，筝零雁字，寂寞画堂歌舞。\",\"午梦沉沉浑无主。算梦里相逢，那能长住。我应受、此愁且耐，你不解、此情空诉。悔当年、浪说风流，似梅子含酸，莲房带苦。看湖水溟蒙，春心荡漾，待要一时东去。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab795f72689837d3e5f','解佩令·寄丁象严','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"白龙湖畔，黑龙山上，记当初、柳吟花醉。善写伤心，强做出、湘潭憔悴。到中年、以愁为讳。\",\"江鱼不上，江鸿不度，便相思、芳馨难寄。唱彻新歌，杯在手、都成愁泪。趁西风、洒君衣袂。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab799732bba00e12dec','蕙兰芳引咏杜鹃','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"勒使春归，忙杀了、乱红愁绿。更有甚伤心，向我似啼还哭。血痕在口，又何用、此间思蜀。到三更月冷，不许愁人眠熟。\",\"便去何如，如何不去，是谁拘束。怎似我羁栖，守定暗窗孤烛。玉人何处，佳期未卜，倩伊行、到彼再三催促。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab79d7e81a90c056881','蝶恋花·咏簟','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"树咽疏蝉嫌昼永。自展琅玕，寂寞何人共。睡起懒将眉黛纵。\",\"汗轻微印香肌莹○玉枕频欹衾怕拥。不肯梳妆，垂泪思残梦。\",\"帘幕无风波细涌。荧荧湮得湘痕重。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab79da32cd8c5a98db1','鹊桥仙·春恨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"黄莺不语，绿阴渐满，犹见蝶翻金翅。半林斜日卷帘栊，生怕惹、轻寒着体。\",\"慵拈翠管，惟亲珊枕，病里思君如水。春蚕春柳本无情，也学我、三眠三起。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab79fba000ab3965f1a','西江月·雨中坐泛雪庵作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"壮士红旗较猎，佳人翠馆徵歌。目成花底认横波。忽又鼻端出火。\",\"自笑尘埃落魄，谁知岁月蹉跎。一龛春雨伴弥陀。回转头来是我。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7a1860c5178ab0459','月笼沙恨别','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"悄嘱栏西执手，轻呼帐底沾胸。杨花漠漠水溶溶。隔水隔花看不见，恨杀银骢。\",\"旅店催将鞍卸，孤衾尚觉香浓。晓莺啼过玉楼空。料得那人眠未稳，斜靠薰笼。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7a2242d3cd64838ad','小重山·饮张祖望从野堂','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"细草如烟野渚平。草中云突兀、是秦亭。小堂开处日初晴。花满地，新竹叫残莺。\",\"邀客坐吹笙。美髯都化雪、不须惊。黄河天上赋新成。堪下酒，试诵与侬听。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7a2afd04a78b451e4','西施元夜独酌','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"新年晴日暖溶溶。尚蹙损眉峰。元宵令节，无奈月朦胧。勉强凭栏，但说乌云里，倒全见灯红。\",\"鸾筝调苦春绣冷，人未醉、被先烘。金莲欲谢、雪又打窗栊。伴我无聊，只有梅花在，早香绽东风。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7a35eab503f5e6580','青玉案·幽期','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"望中渺渺相思路。便咫尺、难来去。幽梦虽轻吹不度。画堂南畔，玉栊西面，谁是无人处。\",\"隔墙花暝春风暮。青鸟仙书无一句。总是伊家真个许。晚云笼罩，重门深闭，又下黄昏雨。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7a4bdfe758babf098','后庭宴再咏蜂','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"日晚闹窗，风暄游阁。檀痕腻嘴红沾脚。贪春何故更防春，藏针暗里无人觉。\",\"园林取次花残，袅袅细腰难托。欲讳幽情，无奈黄俱落。还记旧衣香，绕过韆鞦索。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7aa25135c564a89f6','沁园春··寄赠王扬州阮亭，即用其偶兴韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"羡尔高情，濯足淮河，振衣蜀冈。便眺览烟云，何妨薄宦，沉酣典籍，不愧清狂。骏马穿花，佳人雪藕，摘遍朱荷水一方。新歌奏，早声传北里，色艳东皇。\",\"墨痕锦袖淋浪。但怅望、星桥有报章。任岁月驱驰，宫中磨蝎，风尘黯黯，路上亡羊。一日不斋，百年浑醉，携手频游翰墨场。休回首，纵兰陵酒美，何处吾乡。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7aa25135c9c48d4ce','沁园春··寄赠王扬州阮亭，即用其偶兴韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"不断长江，滚雪翻云，日夜东流。怪万里烟花，终沉伍剑，二分明月，先照隋钩。郡县劳人，文章绝世，斗大如何困一州。偏豪迈，在词中拜将，醉里封侯。\",\"琼华寄我难酬。奈水满、芜城夜色浮。道执卷跨牛，其中有乐，腰钱骑鹤，此外何求。素札空传，玉箫谁教，泛泛还同沙际鸥。虚名误，但浓斟玉液，暖被貂裘。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ab1073b8727bea6f','鹊桥仙·歌妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"朱唇欲启，翠眉先蹙，掩袖俄延半歇。玉箫低度趁香喉，我倒喜、宫商不协。\",\"逞娇似媚，言愁如诉，裙底红鸳暗跌。误多频顾耐端相，方便杀、灯前皓月。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ad12bcaa38ba0ea9','惜秋华·秋思次彭金粟韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"暝色沉山，正帘子、斜开灯儿未炙。俯首凝思，无聊指甲空剔。一年几个中秋，夜酝酿暮云如墨。岑寂。愁人断送、要这般天色。\",\"帐底馀香息。甚柔情一段，浪作经年忆。还似伤春卧病，看朱成碧。乱蛩更助凄凉，渐闻疏雨梧桐滴。通夕。怎比得、镜潮镜汐。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ae0a29831914cc7b','虞美人·偶成','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"珠帘夜卷瑶台雪。初上团团月。潜身偷看董双成。却恨翠烟红雾太纵横。\",\"归来孤枕和衣倒。冷被灯儿笑。赚人肠断是蛾眉。隔着花阴又把玉箫吹。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ae22d6a89a852cfb','小重山·八月初九日风雨寒甚昼眠有作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"江上秋涛卷黑云。朦胧天未晓、雨翻盆。棘闱灯火正纷纷。青衫湿，都是旧啼痕。\",\"衰鬓已如银。久将年少事、让他人。午鸡啼过不开门。风太紧，拥被惜馀温。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ae7ab7a7c4cd42f8','浪淘沙·中秋夜雨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"暝色暗高楼。懒上帘钩。残荷吹尽水悠悠。两袖泪珠多似雨，偏洒中秋。\",\"笙管记同游。庭桂香浮。胭脂零落小坟头。便有玉栏今夜月，总是闲愁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7aea557eae9c576ae','十二时·闰怨，用耆卿韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"问多情，如何下得，扫却旧恩如洗。子细想、真无意思。\",\"撞住吃亏忍气。万种禁持，千般啰唣，见了谁提起。\",\"都是我、惯了伊家，做出这般，我也无如何耳。\",\"凭着伊，十分怪我，我是至今牵系。人也劝奴，为何守这，冷冷清清地。\",\"奴须丢不下，死生只在这里。\",\"算将来，真成痴想，只为旧时情意。清夜无眠，翻来覆去，泪满鸳鸯被。\",\"是我难割舍，料伊也拼奴弃。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7b0642663e11e0f5e','满江红··夏夕','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"翠幕香寒，推枕起、昼栏重凭。镇一片花昏柳暗，夜深人静。几阵凉风吹过雨，黑云捧出黄金饼。笑从前、长道此身孤，俄添影。\",\"何处笛，声悲横。乌鹊噪，明河净。见双鬟低说，今宵差冷。玉貌云衣都化土，非心所爱无心应。便杯儿、斟满不沾唇，常推病。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7b091e7d36de38e51','点绛唇·寒夜不寐','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"玉漏迢迢，听来却是三更绝。枕儿难贴。今夜何曾热。\",\"乱打西窗，冷雨都成雪。和谁说。孤灯明灭。暗把鸳衾啮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7b3dd46b4077194f4','摸鱼儿··游飞来峰','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"怪摩霄、万重苍壁，玲珑深杳难测。神娲妙手真奇诡，抟就宛如埏埴。春寂寂。仿佛见、金光玉乳纷红碧。人间未隔。似梦境迷离，相看一笑，那处记曾识。流年换，今古荒唐漫忆。\",\"探幽此度须力。桃花路暗难寻觅。吟罢酒船浮白。归未得。应让我、诗能飞去君无翼。淹留共惜。莫上最高峰，水云烟草，落日半湖赤。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7b4a967a28e65d058','念奴娇·东园感','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"柳稍雪尽，正东风淡迤沱，金芽初绽。望得春来人又去，空镇翠苔深院。镜匣微开，帘钩反挂，尽日销香篆。早梅开了，隔窗鹦鹉惊唤。\",\"教我换取春衣，小衿秃袖，端的谁裁剪。原是沈腰，今更瘦、宝玦频移带眼。暗想佳人，罗衾昼拥，也自看春懒。待将书寄，问他眉黛深浅。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7b4dbfeb1e00064c0','齐天乐··二月二十日风雨感怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"碧桃风雨何时歇，春阴半楼低结。懒脱罗襦，聊偎角枕，宝鸭频烧不热。柔情欲绝。但梦后关心，影边饶舌。蓦地风来，帐儿却似有人揭。\",\"西窗犹自未晚，只兰灯一燄，才上还灭。柳压浓烟，莺飞浅浪，空负艳阳时节。欲言又咽。甚芳泽沾衣，残书留箧。盼得来时，玉肌须痛啮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7b69b0da64a320a64','蝶恋小桃红秋思','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"多情却被秋僝僽。冷落西风，正在重阳后。别泪淋漓淹锦袖。漫将人憔悴比黄花，比黄花还瘦。\",\"三年不鼓求凰奏。愁压眉山，镜里时时斗。剔尽残灯挨玉漏。要纱厨重照影儿双，问今生能彀。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ba7a29c928967c07','菩萨蛮·幽怨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"柳丝拂地愁如织。娇莺啼露东风湿。烟草碧重重。迷离似梦中。\",\"无人知此意。腰比前春细。莫去倚危楼。斜阳花更愁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7bd0e99f015b7bb44','岁寒三友夜雨留别张祖望毛稚黄','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"南楼月夜宝灯红。一饮千钟。诗成镂版，曲就上弦，春似情浓。醉卧锦屏，满身花影重。\",\"流年谁信太匆匆。南北西东。雨黑今宵话别，衰鬓如霜左耳聋。记取后期，桃花黄鹤峰。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7be8f82da1a6a45f3','御街行·和佳人减膳','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"娇多似病非贪睡。日照银屏内。侍儿低语促朝餐，反怪殷勤无为。丽情难遣，离愁不解，填满人肠胃。\",\"擎瓯少力知何意。元自腰儿细。隔花催唤女医人，问取药方禁忌。玉羹嫌淡，金齑道苦，不是相思味。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c20f2c4dbdac9d21','念奴娇·用彭羡门韵，留别毛玉斯','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"解维还住，正烟江、雁冷月堤花碎。离别须臾，时不再、肠断河梁都尉。玉椀香生，金簧调苦，酒尽何曾醉。悲君肮脏，赠言羞借毛遂。\",\"更爱一串骊珠，风流酝藉。用意何奇肆。沦落天涯，俱是客、应笑季鹰思脍。露白荒城，枫青古岸，夜出含沙鬼。星光惨淡，与卿深语休睡。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c48b67a5c69087de','玉楼春·秋夜独宿有怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"湘帘一片秋声碎。残月窥人人不寐。心儿似你最无情，此时也合频垂泪。\",\"寒风摇曳孤灯背。落落星光如欲坠。思量还是影多情，夜夜伴侬愁里睡。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c4f7d13cf015e0e8','东湖月·己酉生日，潘生云赤以自度曲寿余，览次有感，依韵答之','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"甚钟灵。便珊瑚百丈老重溟。问谈天邹衍，凌云司马，此日敢纵横。骨带铜声敲不响，有黄金伏枥难行。只好衲衣持钵，绣幕闻笙。\",\"山青兼水绿，对萧疏、白发转多情。笑磨厓天半，沉碑潭底，身后枉垂名。雾里看鸢非我事，尽优游下泽同乘。漫道出，堪作雨处亦关星。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c52d1090c91f914e','蝶恋花·秋日题西轩素屏','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雨过荒亭闻促织。不管人愁，抵死声啾唧。诗写素罗浑未黑。欹斜惨淡疑乌贼。\",\"往事不堪重记忆。梦醒斜阳，已照银屏侧。古镜沉沉钿翠蚀。唾茸不褪深红色。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c677cd2a1f990c98','胜常再过薛姬','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"黄柳堆烟，别才半月，如今渐可藏乌。却喜幽期不爽，隔花鹦鹉惊呼。系马闲庭，见水帘风过，拘控珊瑚。\",\"西窗卧病，含羞强出，脸晕红酥。道罢胜常侧坐，知他有意生疏。记得书来，说魂迷别馆，目断征途。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c701bc3826a90025','归田乐引怨情用黄庭坚暮雨蒙阶砌体','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"眉细元非缴。脸如花、天然娇媚，只是芳年小。许了又悔了。去了来了。弄得人心喜还恼。\",\"寄书都不报。却傍花屏低声笑。柔肠萦损，争是无情好。是了已悟了。罢了丢了。蓦地腮边泪珠掉。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c7c0c936fce5599f','蝶恋花··晓起有怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"金鸭香寒搴绣帐。帘外鸦啼，帘内灯还亮。待要起来仍勉强。又偎珊枕低垂项。\",\"残梦暗思心怅怏。湖雨湖云，并在心儿上。冻勒小梅红未放。无言澹日高三丈。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c7d6d03b3f02aa8d','夜飞鹊·嘉兴晓发','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"前江月初上，喧动邻舟。渔火远映津楼。从来客睡不曾着，况听旅雁啾啾。征衫露华暗满，恨孤身冲晓，衰鬓惊秋。吴歌欸乃，动离情、一样声柔。\",\"宛转寒溪数里，回首见孤城，宿雾徐收。遥忆兰闺昨夜，残灯欹枕，也恁闲愁。苦无消息，拟缄书、又怕沉浮。奈鸳鸯湖水、再三嘱付，不肯西流。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c84b577552b4f79c','沁园春·美人发','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"掠雾轻笼，盘云低亸，百万难偿。记灯前夜冷，金钗坠响，屏间春晓，玉枕馀香。堕马堪羞，抛家有恨，谁似新兴独擅场。多情甚，把并刀剪寄，密意深藏。\",\"偷看亦自斟量。料我见、犹怜也不妨。奈秋蝉影薄，有时仿佛，灵蛇样巧，蓦地形相。病起曾梳，愁来早卸，膏沐无心意转伤。知何日，在水晶帘下，卧看新妆。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c8bc0f4490c7d83a','天仙子·寄妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"绿树阴阴春欲尽。寂寞园林终日闷。杏花零落已无多，风一阵。雨一阵。抵死黄蜂还要趁。\",\"却忆双钗低压鬓。绣幕生寒沉水烬。红笺叠就又开看，横不认。竖不认。靴脸尚留鞋底印。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c92137d46e02c94d','风流子··代闻元亮悼亡，用秦少游韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"风流今已尽，冰弦断、难觅凤麟洲。枉宝袜偷看，频移带眼，金钱暗掷，浪卜刀头。见伊处、书中心切切，帐底梦悠悠。夕泣朝啼，泪多流被，南来北往，愁重沉舟。\",\"萧条朱门里，不堪回首处，华屋西州。怕见尘生遗挂，粉剩空楼。倘指上玉环，他生再见，腰中绣裹，今世真休。若是伊心未泯，鬼也须愁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7c92137d4fb71c2ad','风流子··代闻元亮悼亡，用秦少游韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"风流难再得，寻聚窟、何处问仙洲。想别后寄情，曾经剪发，病中永诀，不肯回头。岂知道、黄泉声寂寂，碧落恨悠悠。桂殿初奔，敲残兔臼，桃源再访，赚杀渔舟。\",\"繁华消磨尽，三年真薄幸，一觉扬州。最苦难求毒石，未得神楼。似乱攀荷藕，千丝不断，对烧蜡烛，两泪难休。反羡伊家解脱，免却闲愁。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7cab9fd43f87cbec8','浪淘沙·寄毛稚黄','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"别路柳含烟。恨杀啼鹃。片帆高挂水连天。无数青山遮不住，似箭离弦。\",\"对酒擘银笺。碧树红泉。胜游还道不如前。把烛重看肠断句，夜雨绵绵。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7cb49b9d455fc6814','苏幕遮·为潘云赤悼亡','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"篆烟微，花烛烂。月缺花残，咫尺幽明判。綷縩罗裙鬟髻乱。似有如无，风静垂帘蒜。\",\"梦无凭，愁不惯。眉妩青颦，谁举梁鸿案。遗挂尘生丝脆断。菡萏鸳鸯，不合愁人看。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7cefeb26c862fb264','玉楼春·旅情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"碧桃花谢东风湿。病里看春无气力。愁来不断水如烟，梦去仍回云似墨。\",\"玉窗人背孤灯泣。泪洒榴裙应变色。子规啼罢鹧鸪啼，终日踌躇楼上立。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7cf50a9fcc6efd70a','月笼沙九日题南楼壁间','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"帘外潺潺暮雨，江边隐隐寒潮。乱鸿缥缈寄书遥。满地黄花人病酒，蜂蝶空劳。\",\"自惜明珠离掌，谁怜小带垂腰。画楼西畔是虹桥。只恐粉云遮不断，怕杀登高。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d1b67cbd17f6a0d0','浪淘沙·戏赠幼妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"眉小发初齐。游戏偏宜。唱歌频误拍频移。掌上有风休试舞，怕逐花飞。\",\"殢酒更娇痴。款抱轻携。晓寒深护绣帘垂。烟里一丝金线柳，认做黄鹂。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d1e697e2d069570d','鹊桥仙·春夜','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"绮窗月转，银台灯灿，绣袜斜窥金胜。暗闻莺语隔东风，越显得、夜阑人静。\",\"粉容薄醉，兰心小倦，唤着有时低应。泥人怀里绿云香，料不为、鸳鸯枕硬。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d30842afa7d6a885','弄珠楼·弄珠楼宴集，赠陆嗣端司马自度曲','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"烟涛万顷，看九龙、奔赴弄珠楼。重帘高枕，水天日夜悠悠。白雁风驱，黄花露濯，描画十分秋。莫负佳人劝饮，玉杯似手。翠蚁香浮。\",\"搔头。灯前敲断，应为节轻讴。星移物换，沦亡古迹难求。锦障催诗，红衣扶醉，改席上兰舟。嘱付青衫司马，襟怀磊落，漫学江州。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d69a5d3808b5567f','神女·书高唐赋后','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"草香竹暗巫山曙。记宸游、空凝伫。疏疏雨自何来，霭霭云无处所。此日登台，当年荐枕，闻道佳期在朝暮。怎想像梦里、姚姬不管，深宫细腰苦。\",\"侍臣宋玉偏能赋。动淫思、牵愁绪。岂知惆怅中宵，赢得悲哀万古。浪说神人，鸣鸾易返，又逢薄怒。今但有、杜宇啼红，花落高唐路。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d712c31445d86bdc','殢人娇别情','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"斜月笼花，恨杀邻鸡无赖。人倦起、强持裙带。褰帏絮语，道残灯犹在。留不住、便缓些见何害。\",\"云亸垂鬟，山低接黛。弹粉泪、传觞心懈。紫骝嘶紧，在绿杨烟外。全不管、此际殢人娇态。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d7984c5c21ab2d64','金门贺呈朝','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"炎威逼。火云万朵俄成墨。电掣金蛇江树黑。天河直下夜深寒，把烦嚣都涤。\",\"半掩罗帏暂息。帘外碧桐如拭。隐隐残雷无气力。暗思玉簟亸烟鬟，料今宵眠得。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d8ca4f7178e91613','露下滴新荷','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"忆年时，长桥畔、水微波。正邀他、同赏新荷。彩舟银烛词成，先付雪儿歌。缠头锦、醉中亲与，十丈红罗。欢呼不觉夜深，影动星河。\",\"怪今宵、风兼雪，人不见，恨如何。待题诗、手冷时呵。凤笺仍卷，自将金鸭暖衾窝。小楼深巷，应相念、皱损双蛾。常拟策蹇再寻，奈不肯晴和。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7d9c4cf54499fa5ba','卖花声晓起买花有感','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"风雨连朝，春色不知馀几。见筠篮、粉红香腻。高呼低唤，奈绣帘深闭。料多娇、惺忪犹未。\",\"惜花人早，买得酴醾半蕊。手擎来、燕随莺睨。兰闺昔日，有翠螺新髻。今教我、寄将谁是。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7dacfc5242794041a','鸾啼序陈眉公先生题娱园图，家君命谦绩填此词用杨升庵碧鸡唱晓体','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"功成解组开别业，桐鱼早占。虽通市、车马无喧，亭榭半隔村店。盟白鹭、陂塘旋买，水环沙嘴多菱芡。更千章、乔木撑空，骇日光暗。栋接山云，帘低野月，爱闲凭曲槛。独啸夜、深似鸾鸣，银河珠斗影淡。曲房幽、道书闲读，丹成九转神光湛。忆飞鸢、浪掷岁华，被浮名赚。广南文竹，衔碧嵌金，空翠摇青簟。常把钓、东湖荡漾，舟傍垂杨缆。\",\"良朋老衲，焚香煮茗清谈，直至疏钟撼。入春来、花满流莺瞰。荷钱叠叠又菡萏。任一叶惊秋，万峰雪彩奇艳。四时闲适，颐养天和，已觉无俗念。咫尺红尘不到，苔卧绿枪，月挂玉弓，江走龙剑。诗题辋川，吴笺时砑，怪篇篇、连城无玷。饮松醪、肯负兰灯焰。漫言绿野优游，醉月迷花，补平日欠。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7dad6cbcb7ecb4997','浪淘沙·春怨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"河流尽含颦。也学愁人。隔窗莺语泥斜曛。满眼落花多似泪，终日纷纷。\",\"宽带小腰身。怕杀黄昏。月来还共影寒温。最是今年情兴好，睡过三春。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7db4728558097d51f','空亭日暮寄洪昉思时客蓟门','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"空亭日暮。记声断骊歌，摇鞭欲去。沙草半连云，雪花时带雨。梦难凭，期漫许。但相看无语。才转眼，散发披襟，江南酷暑。\",\"我有离情怎诉。想望月芦沟，也思旧侣。斫地为谁哀，谈天何自苦。妙文传，芳信阻。正金台吊古。愁多少，骏骨如山，寒烟宿莽。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7dc243db9d90f0435','浪淘沙·清明','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雨过小窗前。几处莺声。纷纷花柳障春城。我自愁多如中酒，人道清明。\",\"箫鼓画船轻。游当何曾。倚帘终日泪盈盈。风里落花浑不响，蓦地心惊。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e0b209237a388c44','西江月·西阁有怀','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"水选孤村寂静，山连小阁清虚。坐来空翠湿衣裾。一霎溟蒙似雨。\",\"傍柳时移宿鸟，衔花欲上游鱼。窥帘新月暮寒馀。也照飞琼伴侣。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e2160a8b7f52ec7c','念奴娇·咏冰','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"玉壶一夜，把青霜皓月，团团结就。怪道绣衾寒似铁，向晓不闻莲漏。映日狐疑，衔花鱼上，遥想融和候。滹沱正合，可怜车马驰骤。\",\"还忆翠阁佳人，晓窗垂泪，珠玉沾襟袖。挑得口脂，呵又冷、红入小桃偏厚。镂管微含，金簧旋炙，闷把无情咒。东风早至，寒江吹变醇酎。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e4ad7b021591fdac','兰陵王··春夜病中听雨','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"晚风厉。十二帘拢垂地。灯忽暗，不许结花，镇日撩人有何喜。小鬟熏素被。耿耿全无睡意。帘纤雨，却又打窗，问道三更过还未。寻思转无味。怎偏我逢春，愁病相继。红桥曲巷迷征骑。\",\"曾持酒低劝，写词偷和，宝钗拨火人半醉。恁时节将睡。憔悴。更腰细。那枕上莺声，何日相慰。枝头春色今馀几。怕粉蕊湮透，檀心滴碎。苍天何恨，也有恁，许多泪。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e4ed552b48a87073','采桑自度曲不寐','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"淡月明。孤灯背。\",\"梦难成。暗啮鸳鸯被。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e613eb825ddb89b3','玉楼人·醉杏花天江上别妓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"停杯掩袖都无语。容易逢君侬便去。孤雁叫秋风，乱不成行，似解分离苦。寒山一抹低平楚。泪满钱唐古渡。\",\"眉峰未蹙情先露。弦上传情浑似诉。一字一俄延，错乱宫商，误则随他误。丝丝烟柳愁千缕。柳上斜晖未暮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e6f2744debfc97d6','苏幕遮·立春','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"树莺藏，兰雪拥。浪说春回，又被东君哄。面药口脂呵复冻。一样愁颦，便觉朝来重。\",\"负深盟，思好梦。懒簇酥山，花影帘钩控。此夜歌楼谁与共。火照堆鸦，胜里金人动。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e7e7b3d4270e6e77','玉女剔银灯夜阅倚声集怀邹程村','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"天气初寒，楼外月华如雪。孤灯弄影，展卷空悲咽。词唱金荃，歌翻玉树，谁似风流英绝。梅花堪折。记分手、樱桃时节。\",\"万丈庐山，梦来时、怕阻截。素书题就，见晓星窥闑。马去关河，人稀驿路，谁信雁鸿能说。神交但切。岂畏远离长别。程村时客江西。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e8e948e2325d4593','江城子·春日感旧','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"玉楼深雨湿兰荃。记当年。倚香肩。髻坠偏荷，铖戴夜飞蝉。白日泥人常自病，和泪语，最堪怜。\",\"愁魂落瘗荒原。草如烟。听啼鹃。剩粉残脂，寄不到黄泉。万缕千丝江畔柳，都学我，恨绵绵。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e9d645243fd2cfca','青玉案·美人眉','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"卷帘深坐苔痕浅。知心里、将谁怨。袅袅蛾儿新出茧。何段京兆，自胜虢国，十样随人换。\",\"镜中巧抹春山远。病起无端又重看。有限青颦愁不断。暂时佳会，此生薄福，消得梁鸿案。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7e9f32ad14459409b','踏莎行·述怀兼呈金振公','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"不受饥驱，何忧看杀。逢君几度更裘葛。愁时蝶梦苦难轻，老来姜性偏增辣。\",\"病莫多情，穷须作达。醉歌要把行云遏。蜀山雪亮坠猢狲，钱唐风晦惊罗刹。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ea1d7d0ca7aa5bfd','玉楼春·游明因寺折桃花一枝贮之胆瓶是夜风雨大作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"胭脂淡抹香肤腻。万树绯桃偏映水。可怜含蕊便遭风，一向冤他贪结子。\",\"重帘护烛花如醉。却忆小桥通野寺。摧残不到五更头，化作枕边红粉泪。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7eb698a9c8a33da2a','锦帐留春夜起浮月池上作','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"花竹阴森，路通幽沼。楼角银河低绕。度流萤，帘乍卷，海牛轻掉。微月朦胧故窥人，更不用、兰灯照。\",\"玉簟凉生，画屏人悄。愁听哀蝉微噪。记年时，曾在此，香肩斜靠。手执齐纫暗思量，闷只把、荷风舀。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ec5ef29bfb0a6db8','风中柳代闺人咏珠','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"掌上时看，只道一生如意。露沾花、岂能常缀。月满星孤，漫多方比拟。泪盈盈、线儿穿起。\",\"一斛恩深，不是轻躯佳丽。倒长拼、玉楼春碎。从头细数，有许多心事。曾几日、怎都忘记。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ec760cacd80fb5f8','点绛唇·美人肩','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"忆昔垂髫，削成软衬香云拥。玉楼春冻。倚处秾纤中。\",\"私语阑干，回首真如梦。相思横。强挨不动，担有千斤重。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ecbb37dca0b21da4','惜分飞··堂堂','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"别后情怀何处诉。门外柳迷烟路。绿鬓俄成素。光阴负我堂堂去。\",\"风急桃花浑似雨。恼得黄莺不语。长日人难度。兰灯炙耳斜阳暮。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ecbbc9c6103fffb5','爪茉莉春晓','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"彻夜踌躇，五更反睡了。疏帘外、乱花啼鸟。千回百转，烦絮得、梦颠魂倒。醒来见、红日当楼，却与绛台争照。\",\"惊啼谁唤，枕头边、几曾燥。还强起、玉屏斜靠。双鬟不解，说买花、最宜早。岂知我，一向黛痕慵扫。劝梳洗，添懊恼。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ee058eb84cf53f4b','庆春宫··答徐野君','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"烟草沉山，蘋风蹙水，天涯又是残春。唤友莺儿，寻家燕子，那堪花雨纷纷。心惊物候，空目断、江东暮云。愁来不见，梦去仍迷，此地逢君。\",\"那堪踪迹沉沦，豪气成虹，短发如银。名重词坛，春波妙曲，几番吹雪萦尘。凄凉旧事，漫提起、教人断魂。只须付与，月底红牙，掌上青樽。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7ef8b9d7a89fe79ff','浣溪沙·忆旧','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"绣领垂髫不解愁。小眉如画眼波秋。翠裙风定袅香毬。\",\"窗下悔棋曾握臂，帐前临镜强梳头。粉云今日障红楼。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7f06824a4a1ab41f7','粉蝶儿··自恨和毛泽民韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"自恨多情，为何这样难绝。闷昏昏、没些情节。到如今、诸事废此心不灭。尚牵缠、想着迎风待月。\",\"病起穿衣，带儿却倩人结。瘦将来、臂才一捻。这离愁多簇在，翠眉两叶。便欢时、却是与愁同活。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7f3313b828430385c','苏幕遮·秋千','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"草烟低，池水浸。一带帘栊，几处莺声唤。绣线休挑歌莫按。催上秋千，挽脱泥金襻。\",\"翠眉颦，云髻乱。愿做游丝，不放桃花瓣。归骑踌躇杨柳岸。柳外斜晖，蓦地香风散。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7f44c82bae83436b9','念奴娇··春晴用李易安韵','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"莺残花老，拚一春孤负，绣帘深闭。恼地烦天人事恶，总没半分争气。枉说相思，真成孤负，细想毫无味。如今拚了，不堪重把书寄。\",\"只是多病多愁，云山满目，把红楼频倚。拚是如今真拚了，曾奈不时记起。锦被香消，翠奁尘积，都是伤人意。不知薄幸，近来曾解情未。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7fba36a770b271e7e','沁园春·美人口','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"染袖争怜，洗妆不褪，微绽樱桃。见藏阄席上，强沾金椀，倚声窗里，渐暖琼箫。唾井无嫌，街碑自苦，懒度丁香分外娇。时偷笑，奈深藏巧扇，半掩轻绡。\",\"画帘月黑风摇。喜鹦舌、偷尝是昨宵。想甘饴微啮，舍人非妄，玉鱼半吐，妃子真妖。龋齿增妍，反唇何害，蜜约如瓶要守牢。相逢处，怕看伊羞面，吹灭兰膏。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7fc56a781aaca3494','贺新郎','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"雪满亭阜路。正西窗、小梅数蕊，冲寒欲吐。独酌无聊人也醉，一霎天低日暮。蓦地把兰灯偷炷。睡鸭浓香熏素被，命如丝、怎把长宵度。帘外冷，叫鹦鹉。\",\"旧游零落凭谁诉。忆池亭、疏莲摇瞑，断虹截雨。钗坠桃笙呼不起，暗里冰肌亲拊。奈天样红墙间阻。错是他年真错了，便六州、聚铁应难铸。眸炯炯，听街鼓。\"]',NULL,NULL,NULL,NULL),('e647d10fec1beab7fcf51b82218e64ef','踏莎行·夜坐有感','沈谦','e647d10fec1beab7806beafe14851003','明',NULL,'[\"对酒心慵，看花泪湿。多情自是天生得。灯来照出影儿单，夜深一任纱窗黑。\",\"往事难留，重思无益。真心不为新欢惑。要人肠断有何难，雨中又是高楼笛。\"]',NULL,NULL,NULL,NULL),('e647d10fec1d66c71e6de415857b475f','浣溪沙·寄衍公','道源','e647d10fec1d66c7d1457b72d1457b72','明',NULL,'[\"故侣湖南不肯归，水洲贪看野凫飞。竹房辜负绿蕉肥。\",\"垆冷博山清话隔，簟开湘水倦眠稀。信音莫待寄秋衣。\"]',NULL,NULL,NULL,NULL),('e647d10fec58a19d2913f277be2f4ff5','八声甘州·晓发','陆释麟','e647d10fec58a19dd1457b72d1457b72','明',NULL,'[\"晓风清、滟滟待潮生，潮落始行舟。正鸡声茅店，孤城断角，欹枕悠悠。偶尔掀篷一笑，起舞也风流。问谁人领取，此景清幽。\",\"极目长空杳霭，但寒烟衰草，江练明楼。羡双双白羽，天水共沉浮。待乘桴、渐穷湖海，莫思量、锦缆与鸣驺。菰蒲里、烟波钓叟，与子为俦。\"]',NULL,NULL,NULL,NULL),('e647d10feccc8d0764e18d0ec490bbce','采莲曲二首','顾闻','e647d10feccc8d07d1457b72d1457b72','明',NULL,'[\"兰舟终日漾莲溪，少女如花锦袖低。含笑折来流水畔，红妆两两镜中啼。\"]',NULL,NULL,NULL,NULL),('e647d10feccc8d0764e18d0ec9d76c63','采莲曲二首','顾闻','e647d10feccc8d07d1457b72d1457b72','明',NULL,'[\"岸上金羁白马郎，溪边红粉断人肠。临风背摘双头蕊，笑入荷花万点妆。\"]',NULL,NULL,NULL,NULL),('e647d10feceb26c168631375f74ee4a8','凤凰台上忆吹箫·阅古今名媛诗集','沈自徵','e647d10feceb26c1d1457b72d1457b72','明',NULL,'[\"雾锁春风，烟埋秋月，一生心事全休。恰雨窗吊古，检点鸳俦。多少名姝珠泪，虚染就、锦笔银钩。伤情处，金环不见，玉叶空留。\",\"抬头，斜阳荒冢，原来是绮阁妆楼。怅尘缘虽破，梦境难收。说甚裁云好手，也几度、惊彻鸡筹。沉吟久，知音罕嗣，若个能酬。\"]',NULL,NULL,NULL,NULL),('e647d10fecf85ed77e6c1e64e2b15138','思帝乡','郭㻬','e647d10fecf85ed7d1457b72d1457b72','明',NULL,'[\"红烛冷，碧钗留。烛冷钗留人去，恨悠悠。\",\"拚得一分憔悴，一分秋。试问眉间心上，几多愁。\"]',NULL,NULL,NULL,NULL),('e647d10fedaac1a00c5c2e7d8ccbdbf2','念奴娇','张红桥','e647d10fedaac1a06c1da886d9731321','明',NULL,'[\"凤凰山下，恨声声玉漏，今宵易歇。三叠阳关歌未竟，城上栖乌催别。一缕情丝，两行清泪，渍透千重铁。重来休问，尊前已是愁绝。\",\"还忆浴罢描眉，梦回携手，踏碎花间月。谩道胸前怀豆蔻，今日总成虚设。桃叶津头，莫愁湖畔，远树云烟叠。剪灯帘幕，相思谁与同说。\"]',NULL,NULL,NULL,NULL),('e647d10fedd501d51ce2ab6b4f80980c','西湖竹枝歌其一','强珇','e647d10fedd501d5d1457b72d1457b72','明',NULL,'[\"湖上女儿学琵琶，满头都插闹妆花。自从弹得《阳关曲》，只在湖船不在家。\"]',NULL,NULL,NULL,NULL),('e647d10fedfb0278d8514caf1eecced9','南乡子','顾士林','e647d10fedfb0278d1457b72d1457b72','明',NULL,'[\"风雨满汀洲，极目帆樯带急流。酒兴诗怀无冷暖，休休，一任凉飙发素秋。\",\"何事皱眉头，且读离骚赋远游。逆数归期归未得，悠悠，千顷烟波一叶舟。\"]',NULL,NULL,NULL,NULL),('e647d10fee18d18ab253d4d100b0a542','柳枝词','王野','e647d10fee18d18ad1457b72d1457b72','明',NULL,'[\"江上春深万万枝，含枝带雨尽低垂。不知此物关何事，今古令人怨别离。\"]',NULL,NULL,NULL,NULL),('e647d10fee2372d5e9ce3983cc12e2c5','风入松·闲居','崔桐','e647d10fee2372d5d1457b72d1457b72','明',NULL,'[\"亭台帘幕锁重重，韶景又东风。竹床习懒闲春梦，唤莺声、醒眼犹慵。短发乌巾斜裹，寻幽日傍花丛。\",\"碧流桥外小桃红，徙倚画栏东。银鲦玉笋新来美，好情怀、尽付杯中。莫笑海翁醉也，醉时百感成空。\"]',NULL,NULL,NULL,NULL),('e647d10fefdba3aaee754c58b123ac06','采莲曲','李觉斯','e647d10fefdba3aa277281aad1457b72','明',NULL,'[\"杨柳依依锁六桥，鵁鶄飞处渚莲飘。儿家生长西湖里，不识烟波万里遥。\"]',NULL,NULL,NULL,NULL),('e647d10fefdba3aaee754c58b441a7f1','采莲曲','李觉斯','e647d10fefdba3aa277281aad1457b72','明',NULL,'[\"木兰轻舫荡平沙，双宿鸳鸯并蒂花。不学浔阳江上女，贩茶船上奏琵琶。\"]',NULL,NULL,NULL,NULL),('e647d10ff000daa505a989f307c84f79','蝶恋花·春暮','孙茂芝','e647d10ff000daa5d1457b72d1457b72','明',NULL,'[\"柳絮卷将春色去，芳草多情，别作娱人具。宝马香车忙不住，秋千正在阴浓处。\",\"蝶趁蜂交随满路，画扇轻衫，逐处人争聚。若个醒然归可据，酒香一路青松树。\"]',NULL,NULL,NULL,NULL),('e647d10ff022667646408bd91e26c296','玉楼春','来集之','e647d10ff0226676d1457b72d1457b72','明',NULL,'[\"窗外松篁初过雨，半天爽气开烟雾。狂怀无计奈花飞，倚楼独自和莺语。\",\"偏是春来无意绪，只身没个安排处。溪流一派送愁来，山围四面裹愁住。\"]',NULL,NULL,NULL,NULL),('e647d10ff03c61814673f510d30d83ad','凤凰台上忆吹箫','马如玉','e647d10ff03c6181d1457b72d1457b72','明',NULL,'[\"清夜无眠，湘帘不卷，潇潇雨打蕉窗。听秋声几点，滴碎秋肠。望断天涯芳讯，人寂寞、偏觉更长。灯花落，枕屏斜倚，睡鸭销香。\",\"思量。旧欢团扇，只等闲抛却，尘冷兰房。数楼头征雁，影去潇湘。隔院吹来玉笛，声幽咽、渐入伊凉。桐叶响，金风阵阵，寒透罗裳。\"]',NULL,NULL,NULL,NULL),('e647d10ff048b76c3ca1a8441b54411d','蝶恋花·元旦','沈宣','e647d10ff048b76cd1457b72d1457b72','明',NULL,'[\"接得灶神天未晓，爆仗声喧，催要开门早。新画钟馗先挂了，大红春帖销金好。\",\"苍术堆炉香气绕，黄纸神牌，上写天尊号。烧过纸灰都不扫，日斜人醉和衣倒。\"]',NULL,NULL,NULL,NULL),('e647d10ff048b76cc0e864452e1fcda7','蝶恋花·除夕','沈宣','e647d10ff048b76cd1457b72d1457b72','明',NULL,'[\"锣鼓儿童声聒耳，傍早关门，挂起新帘子。爆仗满街惊耗鬼，松柴烧在乌盆里。\",\"写就神荼并郁垒，细马送神，多著同兴纸。分岁酒阑扶醉起，阖门一夜齐欢喜。\"]',NULL,NULL,NULL,NULL),('e647d10ff04cd5470c5c2e7d69372674','念奴娇','施耐庵','e647d10ff04cd5472812e5cfd1ee59e2','明',NULL,'[\"天南地北，问乾坤，何处可容狂客？借得山东烟水寨，来买凤城春色。翠袖围香，绛绡笼雪，一笑千金值。神仙体态，薄幸如何消得？\",\"想芦叶滩头，蓼花汀畔，皓月空凝碧。六六雁行连八九，只待金鸡消息。义胆包天，忠肝盖地，四海无人识。离愁万种，醉乡一夜头白。\"]',NULL,'普天之下，请问这个世界，什么地方可以使我容身？暂栖身在水泊梁山，今日来观赏京城之春。翠绿的衣袖散发着香气，红色的绡绢笼罩着洁白的肌肤，真是一笑值千金，美如仙女的体态，与薄情人没有缘份。\n想那芦叶萧萧的滩头，开满蓼花的岸边，纵然有月色如水如银。鱼在水中雁飞天上，只盼金鸡报晓的佳音。虽有包天的义胆，盖地的忠肝，有谁能理解我的心。满怀的离愁千万种，醉乡里一夜霜染双鬓。','小说《水浒传》第七十二回《柴进簪花入禁院，李逵元夜闹东京》中，浪子燕青引宋江再次见到名妓李师师，李师师以酒食款待他们。席间，李师师低唱苏东坡《念奴娇·赤壁怀古》词，宋江乘着酒兴，填写了这首词，呈给李师师。\n该词上片前面四句描述了宋江自己曾为世道所不容纳，竟然到了无处安身的地步，后来投奔了梁山，做了梁山泊的寨主，现在来到东京，观赏灯景。接着后面的五句，笔锋转向李师师，表述了宋江对这位名妓的倾慕之情。先是描述了李师师的衣着服饰，“翠袖围香，绛绡笼雪”，衣袖散发着淡淡的香气，红色绢纱里笼罩着的是如雪的洁白肌肤。面对这样一位佳丽，是值得掷干金来买她的一笑的。像这样美如仙女的人，是薄幸的男人无缘消受的。\n这首词下片前面的五句，回述了宋江身在粱山水泊的寂寞情怀，色沉雁杳，日夜盼望朝廷降旨招安的消息。结尾五句描述了他满怀忠义之心，却不能够被理解，为此常只能是借酒浇愁，把头发都愁白了。宋江这一次冒险来到东京观赏花灯的真实目的便是设法打通关系，想要通过名妓李师师能让宋徽宗体察到他宋江的”忠肝义胆”，殷切希望朝廷能够对梁山水泊的起义军进行招安。这首词集中披露了宋江投奔梁山只是暂时栖身，最终将投降朝廷的用心。','[\"天南地北：指代普天之下。\",\"狂客：狂放不羁之人。此处为宋江自谦之词。\",\"山东烟水寨：指梁山泊。\",\"凤城：旧时京都的别称，谓帝王所居之城。\",\"翠袖：青绿色衣袖，泛指女人的装束。\",\"绛绡：红色绡绢。雪：比喻李师师洁白的肌肤。\",\"一笑千金值：值得千金买一笑。\",\"神仙体态：意谓美如仙女。\",\"薄幸如何消得：薄幸，薄情；负心。消得：消受得了。\",\"芦页滩头，蓼（liǎo）花汀畔：指梁山水泊。\",\"六六：六六三十六，暗指天罡星数，八九七十二，暗指地煞星数。\",\"金鸡消息：黄鸡报晓的声音。指朝廷招安。\",\"四海：泛指大下。\",\"醉乡：喝醉酒时神志迷离的状态。\"]'),('e647d10ff04cd5478d41f3878e950c33','西江月','施耐庵','e647d10ff04cd5472812e5cfd1ee59e2','明','“自幼曾攻经史，长成亦有权谋”二句显示出宋江对自己文才武略的自信心。“恰如猛虎卧荒丘，潜伏爪牙忍受”，我在过去的生活中逆来顺受，循规蹈矩，就好似那藏卧荒丘中的猛虎，收起爪牙，暂时隐藏自己的锋芒，显示出作者博大的胸怀和审时度势智慧的头脑。“不幸刺文双颊，那堪配在江州”，宋江因杀死阎婆惜，被发配江州，此句是说现在我遭到不幸两颊刺文金印，怎么能忍受被发配到遥远的江州充军？我绝不会善罢甘休！“他年若得报冤仇，血染浔阳江口”，这一句充分体现了作者的勃勃雄心，他对未来充满希望，想要和当权者大干一场，其政治抱负和牺牲精神跃然纸上。\n宋江获罪刺配江州，郁积在心中的愤懑因酒醉喷发而出，豪情纵横地抒发了他深藏于心中的不凡怀抱，被当政者称为“反诗”。词中流露的“血染浔阳江口”的想法正是他后来作为农民起义领袖的思想基础。短短的几十个字，就让读者感受到他的不平、怨气和将要有所作为的雄心壮志。\n上片自述身世抱负、语句通俗直言不讳。“自幼曾攻经史，长成亦有权谋”是说自己文通经、史，自有经邦济世之才；武晓韬略，知以奇用兵，先计而后战的应变之术。然而北宋徽宗昏暗不明、贤愚不辨，重用蔡京、童贯等奸佞小人，致使豺狼横行、忠贤被黜、黎民受压。“恰如猛虎卧荒邱，潜伏爪牙忍受”两句采用比喻手法，以猛虎卧于深山荒丘比喻自己之不得志，只能暗中收敛起尖牙利爪忍受屈辱等待着时机到来。反映出踌躇满志的词人不向恶势力低头、敢与命运抗争的叛逆性格。应当注意到以“猛虎”自喻，所抒发的非同寻常之志，虎为百兽之王，可以呼啸生风。所以此处已表达了词人有叱咤风云、改朝换代的志向。\n下片写遭受迫害的词人，原本具有的反叛意识便有了进一步的升华。“不幸刺文双颊，那堪配在江州”两句，记述词人受到官府的酷刑后，又变成了流放犯，被发配到江州（今江西九江市一带）。“刺文双颊”，指古代的黥刑，又叫墨刑，即以刀刺纹于犯人的面颊、额头后以墨涂之，墨生于肉，则刺文不去，留下做终生的耻辱。这对于一般人尚且不堪忍受，何况是一个文武全才、胸怀大志、以猛虎自比的人！所以“他年若得报冤仇，血染浔阳江口”便是该词的必然结尾，也是词人多年壮志、满腹积恨如山洪般地爆发，鲜明地表现了“官逼民反”、“要生存就要反抗斗争”的主题。“浔阳江口”，便在江州，是他流放服役之处；“血染”之义，便是真刀真枪地大干一场，对大大小小的奸臣贼子决不宽恕。这是铮铮铁骨的七尺男儿复仇的怒吼，不愧是后来纵横江湖、驰骋数州、经历十郡，一时之间宋军不敢抗拒的义军领袖应有的气魄。\n该词格调高昂激越。写作手法是由低到高、由柔到刚循序渐进地陈述与抒发，虽然语言通俗明白如话，毫无文饰，但难得的是真情实志发自心底，没有丝毫矫揉之气，读其词如见其人。','[\"自幼曾攻经史，长成亦有权谋。恰如猛虎卧荒丘，潜伏爪牙忍受。\",\"不幸刺文双颊，那堪配在江州。他年若得报冤仇，血染浔阳江口。\"]',NULL,NULL,'宋江是北宋农民起义的领袖。在《全宋词》中，宋江词存二首，一首是感伤、惆怅的《念奴娇》，另一就是这首充满豪情的《西江月》。该词格调高昂激越。写作手法是由低到高、由柔到刚循序渐进地陈述与抒发，虽然语言通俗明白如话，毫无文饰，但难得的是真情实志发自心底，没有丝毫矫揉之气，读其词如见其人。','[\"刺文：古代刑罚的一种，在犯人脸上刺刻文字或标记，押送边疆服役或充军。\"]'),('e647d10ff0930eb2a4bda43b190f3b85','蝶恋花·舟舣津亭何处树','周孚先','e647d10ff0930eb2d1457b72d1457b72','明',NULL,'[\"舟舣津亭何处树。晓起珑璁，回首迷烟雾。江上离人来又去，飘零只似风前絮。\",\"倦倚蓬窗谁共语。野草闲花，一一伤情绪。明日重来须记取，绿杨门巷深深处。\"]',NULL,NULL,NULL,NULL),('e647d10ff0e07ad497b5b8da4d7d9d0d','满庭芳','马晋','e647d10ff0e07ad4d1457b72d1457b72','明',NULL,'[\"雪渍疏髯，霜侵衰鬓，去年犹胜今年。一回老矣，堪叹又堪怜。思昔青春美景，除非是、月下花前。谁知道，金章紫绶，多少事忧煎。\",\"侵晨骑马出，风刚暴横，雨又凄然。想山翁野叟，正尔高眠。更有红尘赤日，也不到、松下林边。如何好，吴淞江上，闲了钓鱼船。\"]',NULL,NULL,NULL,NULL),('e647d10ff0fbba507eb7f9b87090e404','西江月·南丹戍所','顾悫','e647d10ff0fbba50d1457b72d1457b72','明',NULL,'[\"玉垒黄尘黯黯，香闺清梦恹恹。何时重结画眉缘，应比秋山翠减。\",\"回首西风落日，断肠衰草寒烟。塞鸿飞不到南天，消息都无半点。\"]',NULL,NULL,NULL,NULL),('e647d10ff125a5ce2eabdb0fc5245a12','忆秦娥','钱应金','e647d10ff125a5ced1457b72d1457b72','明',NULL,'[\"珠帘揭，东君欲别莺声咽。莺声咽，乱堆柳絮，平铺荷叶。\",\"玉人午梦残妆怯，落花愁杀双飞蝶。双飞蝶，可怜风雨，送春时节。\"]',NULL,NULL,NULL,NULL),('e647d10ff125a5ce3bce0ef84620a12c','潇湘逢故人慢','钱应金','e647d10ff125a5ced1457b72d1457b72','明',NULL,'[\"深秋村落，夸青菱香熟，素芋甜和。擘紫蟹，蒸黄雀，知己团聚，笑语婆娑。\",\"浓烟淡雪，剪湘湖、几尺渔蓑。纵消受、白蘋红蓼，生平未免情多。\",\"空怀古，时悱恻，十年来、可偿文债诗魔。叹世路蹉跎。\",\"恐心费参熊，眉费松螺。风期阔绝，喜今夕、重话云窝。\",\"寒潭月、皎然见底，问君不醉如何。\"]',NULL,NULL,NULL,NULL),('e647d10ff125a5ce9c70a6d8dc389e7c','踏莎行','钱应金','e647d10ff125a5ced1457b72d1457b72','明',NULL,'[\"铜雀春深，纸鸢书暖，绣床无力抛针倦。朝来不是懒看花，羞颜怕与花相见。\",\"粉颊啼痕，罗消裙襕。红泉脉脉流松涧。湘琴一曲美人愁，云连猿路秋连雁。\"]',NULL,NULL,NULL,NULL),('e647d10ff12644b8531a7c3762350555','江神子送医官石仲铭摄邵伯镇巡检得代','张以宁','e647d10ff12644b82df45244d1ee59e2','明',NULL,'[\"谢公埭上绿成围，栋花飞，子规啼。簇簇弓刀、白马拥骄嘶。一树棠梨开透也，春正好，又分携。\",\"草萋萋，望中迷。衣锦归欤、家在海云西。种杏明年功又满，还捧诏，上金闺。\"]',NULL,NULL,NULL,NULL),('e647d10ff12644b8d621ac2cfce811e6','明月生南浦广州省治南汉主刘银故宫铁铸四柱犹存周览叹息之馀夜泊三江口梦中作一词觉而忘之但记二句云千古兴亡多少恨总付潮回去因檃括为明月生南浦一阕云','张以宁','e647d10ff12644b82df45244d1ee59e2','明',NULL,'[\"海角亭前秋草路。榕叶风清，吹散蛮烟雾。一笑英雄曾割据。痴儿却被潘郎误。\",\"宝气销沉无觅处。藓晕犹残，铁铸遗宫柱。千古兴亡知几度。海门依旧潮来去。\"]',NULL,NULL,NULL,NULL),('e647d10ff1e285871397788acd40c615','醉公子','陈氏（平生室）','e647d10ff1e28587d1457b72d1457b72','明',NULL,'[\"绿窗残梦醒。雾重花如瞑。晓起宝帘垂。湿风吹雨丝。\",\"镜台梳洗懒。罗胜同心绾。低语嘱双鬟。香残箫局寒。\"]',NULL,NULL,NULL,NULL),('e647d10ff1e28587257723f80d1f757f','荷叶杯','陈氏（平生室）','e647d10ff1e28587d1457b72d1457b72','明',NULL,'[\"不料檀郎欲去，私期。愿作燕双飞。他生与尔画梁栖。\",\"痴么痴。痴么痴。\"]',NULL,NULL,NULL,NULL),('e647d10ff1e28587415d41bd913eb158','谒金门','陈氏（平生室）','e647d10ff1e28587d1457b72d1457b72','明',NULL,'[\"春欲暮，帘外落红无数。斜倚曲阑浑不语，笑看双燕舞。\",\"惆怅夜来风雨，吹散满城花絮。一段夕阳留不住，马嘶芳草去。\"]',NULL,NULL,NULL,NULL),('e647d10ff1e28587e429ae7923c51029','如梦令','陈氏（平生室）','e647d10ff1e28587d1457b72d1457b72','明',NULL,'[\"欲访桃源有渡。要上瑶台有路。浑似小蜘蛛，却被柔丝牵住。\",\"难去。难去，惆怅落花微雨。\"]',NULL,NULL,NULL,NULL),('e647d10ff240608c154effbd31560dd2','浪淘沙','冯恺章','e647d10ff240608cd1457b72d1457b72','明',NULL,'[\"新月透窗纱，篆缕烟斜，谁家闲弄玉琵琶。却向夜深人静后，自剔灯花。\",\"谯鼓听频挝。惊噪栖鸦，孤衾寂寞负年华。暗里销魂缘底事，梦断天涯。\"]',NULL,NULL,NULL,NULL),('e647d10ff240608cc2ebbe3bb0d5d6f9','鹧鸪天','冯恺章','e647d10ff240608cd1457b72d1457b72','明',NULL,'[\"小阁沉沉闭麝烟，楝花风过画帘前。弄晴弱柳垂金缕，贴水新荷撒翠钱。\",\"闲伫久，只耽眠，愁怀懒托十三弦。困人天气无聊景，风送残红落枕边。\"]',NULL,NULL,NULL,NULL),('e647d10ff29b4541e3ff76a45c63dccf','祝英台近','顾德基','e647d10ff29b4541d1457b72d1457b72','明',NULL,'[\"饮将阑，天欲曙，无奈逼人去。唱罢阳关，折柳惹飞絮。断魂画角尊前，锦帆江上，但私祝、东风留住。\",\"更相觑，且问甚日归来，叮咛不多句。把袂踟蹰，欲判泪如雨。长年不管人愁，扬舲方遽，知此夜、棹停何处。\"]',NULL,NULL,NULL,NULL),('e647d10ff3854aef9ef5ce709706d68a','醉花阴','沈士锐','e647d10ff3854aefd1457b72d1457b72','明',NULL,'[\"老尽芙蓉霜叶吼，顿觉秋容瘦。曳杖试行吟，竹里青帘，高倚疏疏柳。\",\"远眺停云怀故友，泪落杯中酒。惨惨复惨惨，风雨三更，谁念人僝僽。\"]',NULL,NULL,NULL,NULL),('e647d10ff3f755316ffdaf8a2965399c','望江南','周积贤','e647d10ff3f75531d1457b72d1457b72','明',NULL,'[\"遥想忆，残泪湿轻纱。斜倚画楼帘半卷，东风微雨落杨花。\",\"肠断七香车。\"]',NULL,NULL,NULL,NULL),('e647d10ff3f75531c06ac9f2263ce371','南歌子','周积贤','e647d10ff3f75531d1457b72d1457b72','明',NULL,'[\"玉篆沉凫永，金铺小凤斜。杜娘无力绣春纱，闲倚绿萍池畔，数杨花。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e713df7197ac3723ec7','菩萨蛮·代人贺周指挥称号晋野有引','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"雪花飞入朱帘里。锦堂一派笙歌委。瑞气满高筵。浓香送酒船。\",\"晋野谁程号。声华从此茂。顾名思义时。东君知未知。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e715ef82d417825a0e8','凤凰阁代诸生贺程近斋受奖有引','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"秋月冰壶，纤埃俱绝。真个是风流人杰。看他八斗深藏，三江倒泄。论声华、日星昭列。\",\"秉铎东州，远绍尼山命脉。有多少游杨立雪。眼见得、凤口衔书，蒲轮动辙。要先生、那时正说。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e719e7a6a7a9ba5249f','喜迁莺·代谢二尹贺张立庵受奖有引','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"关西人杰。有对日丹心，凌霜老节。鹏起秋风，龙腾春浪，早岁声华奇绝。多少苍生命脉，些子王章机诀。全仗赖，有脚阳春，无情冷铁。\",\"堪悦。真个是，民困方苏，百里弦歌彻。子厚情思，九龄风致，才望齐贤并列。休羡名高豸府，更看声流龙阙。有时节，凤诏飞来，岩廊调燮。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e71b9c8558ddf260439','减字木兰花·道中','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"孤云野水。一声长笛芦花里。明月江天。搔首西风懒待眠。\",\"萍踪何处。片篷飞下沧浪去。子夜吴歌。万里征夫奈若何。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e71bdf1d580ed03f6de','减字木兰花·吴淞','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"翻来覆去。淋漓秋雨何时住。江上兵船。破浪冲锋已半年。\",\"祲氛未扫。多愁不觉容颜老。仗剑西风。慷慨悲歌樽酒中。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e71c93a108e6a83fe17','千秋岁·代人寿岳翁八十冠带有引','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"堂开烟外。绿野轻寒退。清昼永，祥云霭。春风荡箫鼓，淑气邀蓬海。更有那，青禽黄鹤怜相对。\",\"北阙恩颁赉。白首青袍带。屈指处，谁能再。俊声归大老，眉寿齐华泰。准备著，安车稳上磻溪载。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e71d9bea1fc537a0762','贺圣朝·代谢二尹送张立庵入觐有引','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"山亭折柳携君手。离思浓如酒。二天人远，霜寒雪冻，谁能禁否。\",\"琴鹤萧然，囊无馀物。奏草怀中有。此行看取霖雨，苍生功名白首。\"]',NULL,NULL,NULL,NULL),('e647d10ff4811e71e3c6cbefdc9e6290','长相思·别浦杨子','任环','e647d10ff4811e715607fe8829921001','明',NULL,'[\"黄叶翻。红叶翻。行人归旆出阳关。天高烟水寒。\",\"别君难，见君难。从今留得梦中欢。相逢知几年。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafae27c5f4e721f589ff','惠阳竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"侬家住在西湖边，暮暮朝朝得见莲。恨杀昨宵无赖雨，水深寻藕总无缘。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafae27c5f4e73489be52','惠阳竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"床下别郎何草草，欲送郎行怕兄嫂。自从郎渡合江头，侬鞋不踏门前道。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafae27c5f4e7959aa603','惠阳竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"惠阳城南浪似雷，惠阳城东江水来。嘱郎莫学双江水，一合千年去不回。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafae27c5f4e7cf07be85','惠阳竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"烟霞桥出鳄湖边，一道垂杨入野田。郎骑白马来殊好，鳄怪伤人是浪传。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafae27c5f4e7e6a2dba9','惠阳竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"此去广州三日水，此去罗浮日半山。算来亦是无多路，只要郎心乐往还。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa2174930a9','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"月泉寺口月明时，一叠秋风一叠词。今夜思君不得见，坐看孤月上高枝。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa23d52fe6f','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"坡山渡口春草齐，番山谢豹山中啼。可怜一夜潇潇雨，犹向东风忆蜀西。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa24dadca73','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"河南十五采菱女，相约踏歌踏早潮。木兰为楫沙棠桨，日出东方烟雾消。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa26fa493da','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"虎头日出尚曈昽，艓子齐开上大通。借问狂夫何处去，锦帆都趁五更风。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa2d9a56f57','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"妾家旧住青枫桥，日见王孙马饮潮。何事王孙别妾去，早潮仍长暮潮消。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa2f480211b','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"送郎莫过琵琶洲，琵琶本出韩凭愁。妾自有心防触着，那知郎意在东流。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa2f6e27071','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"一亩花田倚郭边，鸟啼花发自年年。昌华冷落诸陵坏，空有香魂尚俨然。\"]',NULL,NULL,NULL,NULL),('e647d10ff48fafaefd50baa2f8aa0027','广州竹枝辞','何绛','e647d10ff48fafae97d01458a51c896c','明',NULL,'[\"蟛子春肥百舌啼，老渔歌唱白鹅西。莫倚近年波浪稳，蚩尤风起石门低。\"]',NULL,NULL,NULL,NULL),('e647d10ff673eb740cc74f9d70a29391','一斛珠','王坊','e647d10ff673eb74d1457b72d1457b72','明',NULL,'[\"落花翻空红雨，多随春信纷纷去。万千绿叶无情绪，连夜生新，占尽花开处。\",\"一霎辞根无定所，伤心红粉成黄土。怜香只有莺啼树，似劝东风，略做些儿主。\"]',NULL,NULL,NULL,NULL),('e647d10ff6825480180b2747b8f68cda','浪淘沙·暮春闺思','路迈','e647d10ff6825480d1457b72d1457b72','明',NULL,'[\"花柳漫凝眸，谁与同游，晓妆无力倚珠楼。望断天涯眉黛锁，减尽风流。\",\"燕舞触帘钩，絮语轻柔，纤纤闲整玉搔头。怕听子规啼不住，搅动离愁。\"]',NULL,NULL,NULL,NULL),('e647d10ff6b2a5518bbed9d1f6bf4c6e','捣练子','钱继振','e647d10ff6b2a551d1457b72d1457b72','明',NULL,'[\"春睡足，落花平，草架荼睸送晚晴。梅子弄黄茶炙翠，梢头犹剩一声莺。\"]',NULL,NULL,NULL,NULL),('e647d10ff6c307922eabdb0fa0db3fbb','忆秦娥','王达善','e647d10ff6c30792d1457b72d1457b72','明',NULL,'[\"愁如缕，谁家落日敲秋杵。敲秋杵，淡烟衰柳，故人何处。\",\"别来每恨关山阻，征鸿影落芙蓉渚。芙蓉渚，短蓬孤棹，几声疏雨。\"]',NULL,NULL,NULL,NULL),('e647d10ff705455a0eede1382029dabf','河满子·中秋','张草','e647d10ff705455ad1457b72d1457b72','明',NULL,'[\"月破秋寒洒背，霜沾露影侵床。玉笛几声今夜起，记来曲子霓裳。孤杵愁连村落，吹笳梦入潇湘。\",\"铅水泻如清泪，江枫绚似红妆。啼雁半行芦荻老，晶晶波下横塘。往事思君一夜，明朝直欲回肠。\"]',NULL,NULL,NULL,NULL),('e647d10ff705455a613e7e2871684949','金菊对芙蓉闺怨','张草','e647d10ff705455ad1457b72d1457b72','明',NULL,'[\"寒漏初更，枕函虚冷，恼来索性思量。见临风败叶，月下清光。樽前恨满孤帏寂，灞陵路、烟水茫茫。竹声动处，个人不见，影到纱窗。\",\"空房几遍回肠，想寻闲斗草，遣闷吟香。奈燕僝莺僽，空误年芳。生盟念念犹难冷，轻离别、屡变星霜。最伤情是，春花啼癊，秋雨鸣穷螀。\"]',NULL,NULL,NULL,NULL),('e647d10ff705455ad8514cafeb2afd32','南乡子','张草','e647d10ff705455ad1457b72d1457b72','明',NULL,'[\"芳草翠烟浓，蕉叶零零夜雨中。无数浮萍风皱转，情同，残梦天涯断远钟。\",\"独立在帘栊，双燕新扶柳岸风。鶗鴂冲寒花事晚，繁红，滴湿胭脂落几丛。\"]',NULL,NULL,NULL,NULL),('e647d10ff705455aeadf4f2ee7029ef6','生查子·秋怨','张草','e647d10ff705455ad1457b72d1457b72','明',NULL,'[\"寒井下梧桐，叶叶秋风绕。何处寄相思，月落灯残了。\",\"泪界脸波红，梦怪巫山杳。记得别郎时，鸡唱千林晓。\"]',NULL,NULL,NULL,NULL),('e647d10ff7757cc238bd347f41aae9b8','更漏子·咏啼','林章','e647d10ff7757cc2411ae1bf41a60377','明',NULL,'[\"春山愁，秋水怨，斗下两行珠串。梅子雨，蓼花霜，长亭十里妆。\",\"和月说，因风咽，一一莺喉燕舌。帕上点，枕边声，声声点点情。\"]',NULL,NULL,NULL,NULL),('e647d10ff7757cc24a094b5cce05f294','孤鸾','林章','e647d10ff7757cc2411ae1bf41a60377','明',NULL,'[\"为谁抛撇，似海燕初分，林莺乍别。回首天涯，满目云山愁绝。东风不怜春色，把一枝、杨花吹折。直恁粘烟带雨，更盈盈似雪。\",\"奈梦儿、相隔恨难说。想昨夜孤衾，今朝双颊。比这青衫上，有几重啼血。一声晚钟动了，又送人、断肠时节。莫把琵琶乱拨，正春江潮咽。\"]',NULL,NULL,NULL,NULL),('e647d10ff7addaad5396357b43d919c1','浣溪沙·柬瞿夫人','韩知玥','e647d10ff7addaadd1457b72d1457b72','明',NULL,'[\"玉箸双垂薄袖寒，十离诗就背人看。闲愁赢得许多般。\",\"月转空阶天欲曙，香萦倦枕梦初阑。相思一夜小梅残。\"]',NULL,NULL,NULL,NULL),('e647d10ff7d6945c9102429d89fcab44','竹枝词二首','郑善夫','e647d10ff7d6945c7fb8ceb3df12ecd0','明',NULL,'[\"梨岭遥于枫岭遥，小关高比大关高。佣夫过岭如平地，一岁来回一百遭。\"]',NULL,NULL,NULL,NULL),('e647d10ff7d6945c9102429d99e6d6ff','竹枝词二首','郑善夫','e647d10ff7d6945c7fb8ceb3df12ecd0','明',NULL,'[\"西涧西边东涧东，千山不断万山通。谢豹见春啼出血，王孙上树捷如风。\"]',NULL,NULL,NULL,NULL),('e647d10ff84c70e1fa6eb55308143762','阊门柳枝词二首','沈愚','e647d10ff84c70e1d1457b72d1457b72','明',NULL,'[\"黄金枝软翠烟浓，几缕柔丝扬晚风。莫向尊前争婀娜，细腰人在馆娃宫。\"]',NULL,NULL,NULL,NULL),('e647d10ff84c70e1fa6eb5539a73e7d7','阊门柳枝词二首','沈愚','e647d10ff84c70e1d1457b72d1457b72','明',NULL,'[\"小蛮能唱白家词，笑把纤腰斗柳枝。愁绝尊前春未老，风流太守鬓成丝。\"]',NULL,NULL,NULL,NULL),('e647d10ff8c2d6490dfad02c4d9f6dd6','浣溪沙·途次有感','彭绍贤','e647d10ff8c2d649d1457b72d1457b72','明',NULL,'[\"晓发离亭春日迟，暖风阵阵弄游丝。嫩黄吹上去年枝。\",\"别恨陡随芳草远，归心先被杜鹃知。落花无赖点征衣。\"]',NULL,NULL,NULL,NULL),('e647d10ff8f2507d97b5b8dac8f910f5','满庭芳','戴琏','e647d10ff8f2507dd1457b72d1457b72','明',NULL,'[\"竹隐寒烟，菊凝晚露，空阶霜月微明。小窗寂静，四壁响虫声。风细金炉香袅，穿花影、数点飞萤。良夜永，闷无情绪，独坐对长檠。\",\"玉人音声断，巫山云锁，洛浦烟横。奈鱼沉雁杳，谁诉衷情。新酒难降旧恨，佳期误、檐鹊无凭。愁人处，更阑酒醒，孤枕梦难成。\"]',NULL,NULL,NULL,NULL),('e647d10ff927a7c18a943637f648ef6c','小重山','齐之鸾','e647d10ff927a7c1bffc9834d63fbf8c','明',NULL,'[\"清角悲笳响遏云，平催凉月上、白纷纷。塞垣砧杵小城闻，心铁石、谁解叹离群。\",\"银烛水沈熏，引杯嫌独醉、但微醺。参辰何事各天分，弹铗起、台斗气氤氲。\"]',NULL,NULL,NULL,NULL),('e647d10ff99d466d386b4bde08b8f13d','喜迁莺·送朱思斋词并序代作','张羽','e647d10ff99d466dff49cc4086e8f7ab','明',NULL,'[\"琴堂新霁。正凉入薰弦，潜消残暑。帝日中天，郎星分野，望极碧穹如洗。蒲境讴歌满路，潘县春风桃李。相看取，问东君，政绩前贤传里。\",\"刚喜。应只是。柏府严霜，解作花封雨。竹马迎来，花骢催发，江郭放教容与。屈指瓜时易即，棠树垂阴曾几。频向说，绣衣行，部争留好语。\"]',NULL,NULL,NULL,NULL),('e647d10ff99d466ddd48490f0c99133b','金菊对芙蓉寿沐国公词','张羽','e647d10ff99d466dff49cc4086e8f7ab','明',NULL,'[\"黄缀庭槐，碧收营柳，朝来画戟霜凝。更烟霏瑞脑，日丽雕甍。华阶鹤舞南飞曲，降阿母、青鸟低鸣。又何须用，金貂换酒，银甲弹筝。\",\"相共祝取长生。记当年此日，光岳钟灵。喜干戈将印，不负蜚英。旧家燕子归来惯，见滇海、万里波澄。群仙道是，人间元老，天上长庚。\"]',NULL,NULL,NULL,NULL),('e647d10ff9a8332600d1ca942099c657','西湖竹枝歌五首','李昱','e647d10ff9a83326eb86d510bb04af0f','明',NULL,'[\"芙蓉花面藕丝裳，柳叶眉尖恨最长。嗔道王孙金弹子，朝朝来打野鸳鸯。\"]',NULL,NULL,NULL,NULL),('e647d10ff9a8332600d1ca944ef43e16','西湖竹枝歌五首','李昱','e647d10ff9a83326eb86d510bb04af0f','明',NULL,'[\"总宜船中载酒波，雁儿舞罢近前歌。日斜莫拗青荷叶，心似藕丝头绪多。\"]',NULL,NULL,NULL,NULL),('e647d10ff9a8332600d1ca9473232eb9','西湖竹枝歌五首','李昱','e647d10ff9a83326eb86d510bb04af0f','明',NULL,'[\"菱角钩衣为出尖，莲心苦口却回甜。妾生不恨郎缘薄，自恨春波日夜添。\"]',NULL,NULL,NULL,NULL),('e647d10ff9a8332600d1ca9492aa5b1e','西湖竹枝歌五首','李昱','e647d10ff9a83326eb86d510bb04af0f','明',NULL,'[\"湖上春来郎未归，青青杨柳想郎衣。郎今走马章台路，切莫颠狂作絮飞。\"]',NULL,NULL,NULL,NULL),('e647d10ff9a8332600d1ca94a9745b6c','西湖竹枝歌五首','李昱','e647d10ff9a83326eb86d510bb04af0f','明',NULL,'[\"家住西湖湖岸头，十三吹得乐官愁。连环欲结同心事，只怕郎心似水流。\"]',NULL,NULL,NULL,NULL),('e647d10ff9a833267699acaa5eea7f72','嘲鸠三五七言','李昱','e647d10ff9a83326eb86d510bb04af0f','明',NULL,'[\"双鹁鸠，毛斑斑。阴雨逐妇去，晴天呼妇还。嗟哉尔妇良不易，爱憎只在须臾间。\"]',NULL,NULL,NULL,NULL),('e647d10ffb3f72101801ded56b88a764','大江东去和东坡词三首忆旧游呈陆俨山学士','严嵩','e647d10ffb3f7210e816c63518d10dc6','明',NULL,'[\"记得当年，携客棹、曾览吴淞风物。黄浦矶边芳草径，因叩朱门素壁。宛似游仙，真同忆戴，夜泛山阴雪。挥觞觅句，不让一时才杰。\",\"只今遥指江云，重吟海树，高兴依然发。四十年来同宦侣，不觉飙驰星灭。槐省垂鱼，凤池鸣玉，相对俱华发。君恩报了，五湖重访烟月。\"]',NULL,NULL,NULL,NULL),('e647d10ffb3f72101801ded5ce53c6bd','大江东去和东坡词三首忆旧游呈陆俨山学士','严嵩','e647d10ffb3f7210e816c63518d10dc6','明',NULL,'[\"蚤岁相逢，知风骨、自是玉堂人物。二十年来官未调，兴在丹崖青壁。云海为家，柴门拥褐，高卧袁安雪。青宫华省，一时登用才杰。\",\"好似鹏鹗乘风，骅骝骋步，得路争先发。共喜虞廷逢圣治，关塞虏尘新灭。幸接仙寮，欣披藻翰，森爽清毛发。寅供三礼，光近九天日月。\"]',NULL,NULL,NULL,NULL),('e647d10ffb3f72101801ded5e1074159','大江东去和东坡词三首忆旧游呈陆俨山学士','严嵩','e647d10ffb3f7210e816c63518d10dc6','明',NULL,'[\"玉署仙翁，缘底事、身病偶须药物。杜老吟诗因太瘦，却忆寻山面壁。世事浮云，利名蜗角，去住怜鸿雪。荆州争夺，空叹昔时豪杰。\",\"只须满引金尊，高弹锦瑟，四座燕歌发。秉烛观游聊此夕，莫问人间兴灭。松柏凌寒，桑榆收晚，勋业看黄发。木天兰省，正要平分风月。\"]',NULL,NULL,NULL,NULL),('e647d10ffb3f7210d4d5c4470d286d7a','木兰花慢·月夕期浚翁文会叠前韵','严嵩','e647d10ffb3f7210e816c63518d10dc6','明',NULL,'[\"笑萍踪南北，赖陶谢、与同游。正凤阁紬书，乌台执简，地峻人幽。却喜自公退食，多暇日、心赏若为酬。但得高朋嘉话，底须赵瑟秦讴。\",\"眼中世事醉中休。向城头。见宿雨清尘，明蟾出海，良夜悠悠。巳办金鱼玉佩，沽美酒、高咏蓟城秋。聊共西堂秉烛，且须南陌停辀。\"]',NULL,NULL,NULL,NULL),('e647d10ffb3f7210dffa214dad761596','木兰花慢·答张少宰甬川叠前韵予往岁奉使湘南与少宰会衡岳下登览弥旬','严嵩','e647d10ffb3f7210e816c63518d10dc6','明',NULL,'[\"念楚天衡岳，弭使节、奉山游。正天柱开云，朱陵飞瀑，洞古峰幽。元是金兰契谊，重会合、纻缟递相酬。人道玉堂仙侣，诗成四野传讴。\",\"别来梦想未能休。却转头。看世事如棋，年华似水，鸿燕悠悠。何意仍携末路，笑老圃、黄菊耐深秋。共向彤庭结绶，还来紫陌联辀。\"]',NULL,NULL,NULL,NULL),('e647d10ffb3f7210e1881fe06d9a1c8e','木兰花慢·宫保王公浚川忆留都旧游制木兰花一阕见赠辄次韵答之','严嵩','e647d10ffb3f7210e816c63518d10dc6','明',NULL,'[\"记当年携手，陪夜醉、爱春游。看鹭渚波晴，凤台花暗，览胜寻幽。屡枉佳人投赠，青玉案、琼玖愧难酬。郢客调高白雪，巴歈堪息吟讴。\",\"尘劳奔抗几时休。重回头。叹往事成陈，风流云散，烟浪悠悠。不知今夕何夕，重来此、燕月对清秋。谩忆丹山碧水，却怜高盖华辀。\"]',NULL,NULL,NULL,NULL),('e647d10ffbc22b282eabdb0facdcaeb4','忆秦娥','景翩翩','e647d10ffbc22b28d1457b72d1457b72','明',NULL,'[\"秋萧索，西风一夜吹香阁。吹香阁，挑灯独坐，半垂帘幕。\",\"满阶明月梧桐落，满窗凉露吴衫薄。吴衫薄，菱花闲对，鬓云斜掠。\"]',NULL,NULL,NULL,NULL),('e647d10ffd0cb9800d4d0f19fc348a69','蝶恋花·送春','刘铉','e647d10ffd0cb980f016e59c4dcae38e','明',NULL,'[\"人自怜春春未去。萱草石榴，也解留春住。只道送春无送处。山花落得红成路。\",\"高处莺啼低蝶舞。何况日长，燕子能言语。会与光阴相客主。晴云又卷西边雨。\"]',NULL,NULL,NULL,NULL),('e647d10ffd0cb98084388b0305131b56','少年游（戏友人与女客对棋）','刘铉','e647d10ffd0cb980f016e59c4dcae38e','明',NULL,'[\"石榴花下薄罗衣。睡起却寻棋。未省高低，被伊春笋，拈了白玻璃。\",\"钏脱钗斜浑不省，意重子声迟。对面痴心，只愁必局，肠断欲输时。\"]',NULL,NULL,NULL,NULL),('e647d10ffd0cb98097e847ae05226534','乌夜啼·石榴','刘铉','e647d10ffd0cb980f016e59c4dcae38e','明','春去夏来，落红无数。而石榴花却在此时怒放，暮雨晓露，绿叶愈加玲珑剔透，新花更是如茜初染，勃发出无限生机。末句“初染”与首句“残红”相对应，时序荏苒之意宛在。\n','[\"垂杨影里残红。甚匆匆。只有榴花、全不怨东风。\",\"暮雨急。晓鸦湿。绿玲珑。比似茜裙初染、一般同。\"]',NULL,NULL,NULL,'[\"乌夜啼：即词牌《相见欢》，初为唐教坊曲名，双调三十六字，\\u201C乌夜啼\\u201D为其别名。\",\"不怨东风：言春日百花为东风吹落，石榴入夏而开，故不怨。\",\"比似：好象，犹如。\",\"茜裙：用茜草根染成的红裙。 \"]'),('e647d10ffe675dd2370284d56f02684e','生查子·北平驿秋夜','金俊明','e647d10ffe675dd21bc0249a64f1f27b','明',NULL,'[\"逼暝转深林，瑟瑟松涛沸。日落旅魂惊，嘶马停还未。\",\"灯萦独夜情，剑吼清秋气。凉月照无眠，应见征人泪。\"]',NULL,NULL,NULL,NULL),('e647d10ffeac27f34e1273e3dc12339c','如梦令·红梅','董守正','e647d10ffeac27f3d1457b72d1457b72','明',NULL,'[\"春意浓如醇酒，缀上玉颜酡首。曳杖隔篱看，半吐峭寒香纽。\",\"知否，知否，却似倚阑红袖。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b927bca147915096ec','杨柳枝·词三首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"媚眼正窥春色好，舞腰无奈暖风多。莫教攀折赠行客，爱尔轻丝拂玉珂。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b927bca147b6ff5f80','杨柳枝·词三首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"百花开处明如锦，雨打风飘色乍消。谁似青青堤畔柳？轻盈长学楚宫腰。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b927bca147c08a8397','杨柳枝·词三首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"万缕千丝绕大堤，寻春衣马望中迷。清阴不独摇春酒，留与娇莺自在啼。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b951adec07104bb177','拟竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"剑阁连云欲插天，人心险恶更堪怜。凭君寄谢临邛女，又被风吹过别弦。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b951adec07ba9dac15','拟竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"狂夫一去动经秋，日日祈神锦水头。每下鸣机逢七夕，几回肠断为牵牛。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b951adec07dc7d8a09','拟竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"溪女蓬鬟日召负，担头十指尚纤纤。路逢年少轻调笑，连袂清歌望酒帘。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b951adec07f5a414a2','拟竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"薛涛坟畔尽桃花，犹忆新笺锦作霞。云雨竟随巫峡散，空余明月落谁家。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b9695e8b0d3251cb03','竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"峡口维舟月落时，黄陵苦竹宿羁雌。西风一夜吹归梦，霜满篷窗客未知。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b9695e8b0d4b260fea','竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"汉江对岸夹青楼，蜀客连樯溯上流。借汝一条龙竹杖，轻风半夜到夔州。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b9695e8b0d6594aada','竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"黄牛白马上艰辛，崖下盘涡泡里身。齐唱竹枝牵百丈，烧钱浇酒赛江神。\"]',NULL,NULL,NULL,NULL),('e647d10ffee033b9695e8b0d96938766','竹枝词四首','邓云霄','e647d10ffee033b9d1457b72d1457b72','明',NULL,'[\"三年怨妇望廊归，买卜频停织锦机。谁料行人阻风水，巫山夜雨正沽衣。\"]',NULL,NULL,NULL,NULL),('e647d10fff35aa2e279421be19114f4a','蝶恋花·留别吴白楼','边贡','e647d10fff35aa2e97275a2308040837','明',NULL,'[\"亭外潮生人欲去，为怕秋声，不近芭蕉树。芳草碧云凝望处，何时重话巴山雨。\",\"三板轻船频唤渡，秋水疏杨，欲折丝千缕。白雁横天江馆暮，醉中愁见吴山路。\"]',NULL,NULL,NULL,NULL),('e647d10fff35aa2e9c70a6d867b006bd','踏莎行','边贡','e647d10fff35aa2e97275a2308040837','明',NULL,'[\"露湿春莎，草生芳甸，渔舟迤逦依山转。斜阳明灭照村墟，绿槐深护幽人院。\",\"小栅鸣鸡，古梁巢燕，柴门不锁蓬蒿遍。问渠莫是武陵源，一溪流水桃花乱。\"]',NULL,NULL,NULL,NULL),('e647d10fffc1b06301095b54c0b522d6','点绛唇·答母','张引元','e647d10fffc1b063d1457b72d1457b72','明',NULL,'[\"细雨初晴，暖风早入芭蕉院。归期日盼，松尽黄金钏。\",\"病起南楼，愁睹梅梁燕。无由见，白云天远，十二阑干遍。\"]',NULL,NULL,NULL,NULL),('e647d10fffc1b06339d9e676310fb714','念奴娇·春日怀家寄母','张引元','e647d10fffc1b063d1457b72d1457b72','明',NULL,'[\"燕舞莺娇，看韶光又是，清明时节。乍卷湘帘春昼永，病体素罗犹怯。恨写孤桐，书传只雁，字字伤离别。栏杆徙倚，一腔心事难说。\",\"繁华瞬息当年，旧游回首，惟有西楼月。亲老北堂违菽水，望里暮云遮叠。事业无成，红颜易改，风景摧心折。珠沉璧委，恐惊明镜容发。\"]',NULL,NULL,NULL,NULL),('e647d10fffc1b06340490bea1c3ad66a','浪淘沙·忆母','张引元','e647d10fffc1b063d1457b72d1457b72','明',NULL,'[\"新月冷雕檐。离思绵绵。椒花未举已潜然。可惜清光如昼也，两地愁看。\",\"拨尽玉炉烟。兰麝慵添。瑶草三复转情牵。目断征帆何处是，锦字谁传。\"]',NULL,NULL,NULL,NULL),('e647d10fffc1b0639b02ba6d38d9f6da','菩萨蛮','张引元','e647d10fffc1b063d1457b72d1457b72','明',NULL,'[\"落红飞尽翻风片。芭蕉初放轻葵扇。掩袂立斜阳。归来双燕忙。\",\"金尊空自举。不破愁千缕。生计转无凭。风波何日清。\"]',NULL,NULL,NULL,NULL),('e647d10fffd47a010d5c117213e7feb2','百字令自述用东坡韵','郑士奇','e647d10fffd47a01d1457b72d1457b72','明',NULL,'[\"先生拂袖，赋归来、曾见义熙人物。老去懒摇白羽，只爱青林石壁。坐引桐风，书研竹露，诗净峨嵋雪。山中皓首，那知世上豪杰。\",\"自笑痴兴还浓，紫芝采就，高咏林间发。午后松涛翻雪乳，片缕茶烟明灭。世事如棋，臣心似水，老濯沧浪发。寒梅吹绽，一林香影明月。\"]',NULL,NULL,NULL,NULL);
