INSERT INTO `ci_yuan` VALUES ('c16655df00c92c25f22e8d7a40252cdc','西湖竹枝词','吴礼','c16655df00c92c25d1457b72d1457b72','元',NULL,'[\"湖上鸳鸯相对飞，春寒著人郎未归。莫卷珠帘看行路，杨花撩乱扑人衣。\"]',NULL,NULL,NULL,NULL),('c16655df00cb970a695e8b0d3bd55623','竹枝词四首','姚文奂','c16655df00cb970ad1457b72d1457b72','元',NULL,'[\"晚凉船过柳洲东，荷花香里偶相逢。剥将莲肉猜拳子，玉手双开各赌空。\"]',NULL,NULL,NULL,NULL),('c16655df00cb970a695e8b0d5a84bf1e','竹枝词四首','姚文奂','c16655df00cb970ad1457b72d1457b72','元',NULL,'[\"家住西湖第四桥，自从丱角学吹箫。年来愁得两鬓雪，吹尽春风那得消。\"]',NULL,NULL,NULL,NULL),('c16655df00cb970a695e8b0de11529f0','竹枝词四首','姚文奂','c16655df00cb970ad1457b72d1457b72','元',NULL,'[\"侬家只在断桥边，劝郎切莫下湖船。湖船无柁难轻托，白日风波在眼前。\"]',NULL,NULL,NULL,NULL),('c16655df00cb970a695e8b0df53f8ca2','竹枝词四首','姚文奂','c16655df00cb970ad1457b72d1457b72','元',NULL,'[\"冰肌玉骨自清凉，藕花十里锦云香。一朵红衣初脱卸，那将莲子打鸳鸯。\"]',NULL,NULL,NULL,NULL),('c16655df01602127dc41f87c269a9778','木兰花慢','郑禧','c16655df01602127d1457b72d1457b72','元',NULL,'[\"恃平生豪气，冲星斗，渺云烟。记楚水湘山，吴云越月，频入诗篇。皎洁剑光零乱，算几番、沉醉乐花前。□种仙人瑶草，故家五色云边。\",\"芙蓉金阙正需贤。诏下九重天。念满腹琅玕，盈襟书传，人正韶年。蟾宫近传芳信，□姮娥、娇艳待诗仙。领取天香第一，纵横礼乐三千。\"]',NULL,NULL,NULL,NULL),('c16655df01602127dc41f87c3919c8dd','木兰花慢','郑禧','c16655df01602127d1457b72d1457b72','元',NULL,'[\"任东风老去，吹不断，泪盈盈。记春浅春深，春寒春暖，春雨春晴。都来杀诗人兴，更落花、无定挽春情。芳草犹迷舞蝶，绿杨空语流莺。\",\"玄霜着意捣初成。回首失云英。但如醉如痴，如狂如舞，如梦如惊。香魂只今迷恋，问真仙、消息最分明。后夜相逢何处，清风明月蓬瀛。\"]',NULL,NULL,NULL,NULL),('c16655df01602127dc41f87c794381b9','木兰花慢','郑禧','c16655df01602127d1457b72d1457b72','元',NULL,'[\"望斜杨袅翠帘，试卷小红楼。想鸾佩敲琼，鸾妆沁粉，越样风流。吟怀自怜豪健，洒云笺、醉里度春愁。有唱还应有和，纤纤玉映银钩。\",\"犀心一点暗相投。好事莫悠悠。便有约寻芳，蜂媒才到，蝶使重游。梅花故园憔悴，揖东风、让与杏梢头。况是梅花无语，杏花好好相留。\"]',NULL,NULL,NULL,NULL),('c16655df028bcedaafb9d93c1f99ecd0','和西湖竹枝词二首','杨椿','c16655df028bcedad1457b72d1457b72','元',NULL,'[\"郎去天涯妾在楼，西湖杨柳又三秋。郎情莫似湖头水，城北城南随处流。\"]',NULL,NULL,NULL,NULL),('c16655df028bcedaafb9d93cba497266','和西湖竹枝词二首','杨椿','c16655df028bcedad1457b72d1457b72','元',NULL,'[\"侬家生长在西湖，暮管朝弦随处呼。早听当初阿姨语，免教今日悔狂夫。\"]',NULL,NULL,NULL,NULL),('c16655df0325b4cc617f15b9e6a937e9','和西湖竹枝词','李廷臣','c16655df0325b4ccd1457b72d1457b72','元',NULL,'[\"杨花飞尽荷花开，南人北人湖上来。荡舟自唱黄陵曲，载得山头月子回。\"]',NULL,NULL,NULL,NULL),('c16655df0364f22202fc79d5f4772824','满江红·偶睹春事阑珊谨用兄韵以写所怀','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"点检花枝，风雨外、雪堆琼矗。春去也、朱丝弦断，鸾胶难续。眼底光阴容可惜，旧游回首寻无迹。对青山、一饷倚枯藤，滩声急。\",\"人巳老，身犹客。家在迩，归犹隔。纵语音如旧，形容非昔。芳草绵绵随意绿，平波渺渺伤心碧。到愁来、惟觉酒杯宽，人闲窄。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2220902eadfa65312ae','满江红·遁庵兄以闵菊乐府见示三复之馀倚歌和之','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"谁把秋香，偏著意、植根姑射。尘土外、鲜鲜元有，可人容质。日久渐随芜共没，岁寒还与松同洁。傲新霜、还有两三枝，天应惜。\",\"巾自漉，醅微白。人已老，欢犹昔。爱此花不负，渊明清节。醉里不知人世换，悠然独倚西风立。问一中、时复慰穷途，何如泣。\"]',NULL,NULL,NULL,NULL),('c16655df0364f22219280d76fc75cef3','月上海棠·谨次遁庵兄继玉清韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"酒杯何似浮名好。一入枯肠泰山小。唤醒梦中身，鶗鴂数声春晓。昂头处，几点青山屋杪。\",\"人生得计鱼游沼。视过眼光阴、向来少。须卜一枝安，笑月底、惊乌三绕。无穷事，毕竟何时是了。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2222e6b1be166b36c02','满江红·新春敬用遁庵韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"料峭东风，吹醉面、向人如旧。凝伫立、野禽声里，无言搔首。庭下梅花开尽也，春痕巳到江边柳。待人闲、事了觅清欢，身先朽。\",\"菟裘计，何时有。林下约，床头酒。怕流年不觉，鬓边还透。往事不堪重记省，旧愁未断新愁又。把春光、分付少年场，从今后。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2223debabae899d3389','望月婆罗门引仲坚复见和文势亹亹殊觉逼人可谓不负忍穷矣而其言若有所感因取旧韵述己意以答之虽知荒于辞章犹贤于无所用心也','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"长安倦客，不堪重整旧朝衣。天香尚带馀霏。盖世虚名何用，政尔畏人知。爱青山屋上，面面屏围。\",\"十朋弗违。好事外、卜皆非。办取残年香火，暇日歌诗。东涂西抹，笑颠狂、如我向来稀。休看镜、鬓发如丝。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2224391146131509aae','江城子·季春五日有感而作歌以自适也','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"百年光景霎时闲。镜中看。鬓成斑。历遍人闲、万事不如闲。断送馀生消底物，兰可佩，菊堪餐。\",\"功名场上税征鞍。退时难。处时安。生怕红尘、一点污儒冠。便恁归来嗟已晚，那更待，买青山。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222439114613aef659c','江城子·季春五日有感而作歌以自适也','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"阶前流水玉鸣渠。爱吾庐。惬幽居。屋上青山、山鸟喜相呼。少日功名空自许，今老矣，欲何如。\",\"闲来活计未全疏。月边渔。雨边锄。花底风来、吹乱读残书。谁唤九原摩诘起，添画我，辋川图。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222448fce381524cb69','月上海棠·重九之会彦衡赋词侑觞尊兄遁庵公与坐客往复赓歌至于再四语意益妙殆不容后来者措手彦衡坚请余继其后勉为赋之二首','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"黄花未入渊明手。日搅空肠几回九。山色绕吾庐，犹是当年明秀。忘言处，此意何尝在酒。\",\"等闲莫把良辰负。恨不见平生、旧亲友。三径甫荒凉，东篱下、落英谁嗅。伤时泪，不觉沾襟渍袖。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222448fce38f911faed','月上海棠·重九之会彦衡赋词侑觞尊兄遁庵公与坐客往复赓歌至于再四语意益妙殆不容后来者措手彦衡坚请余继其后勉为赋之二首','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"老来还我扶犁手。想豪气、十分已无九。都把济时心，分付与、一时英秀。还自笑，潦倒犹堪殢酒。\",\"从前枉被虚名负。何似尊前贤圣友。纤手斫金齑，一嚼不妨时嗅。颓然醉，卧印苍苔半袖。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2225408fed882633f92','大江东去赠答杨生彦衡','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"暮年怀抱，对水光林影，欣然忘食。推手功名非我事，闲处聊为闲客。世故多虞，人生如寄，一榻容安息。鬓丝千丈，谁家机杼堪织。\",\"三径松菊犹存，诛茅薙秽，时借邻翁力。酒满芳尊山满眼，此意无今无昔。平地风波，东华尘土，不到幽人席。兴来独往，溪南还有溪北。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2225555f8dc42e86356','望月婆罗门引翼日封生仲坚见和因复用前韵以答','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"小窗睡起，晓寒特地怯春衣。篝灯坐拥残霏。衮衮愁来何处，独有两眉知。倩一尊聊尔，为解重围。\",\"赏心莫违。桃李事、转头非。几曲升平旧谱，数首新诗。花开正好，算一番、风雨未应稀。舒醉袖、惹住游丝。\"]',NULL,NULL,NULL,NULL),('c16655df0364f22259c6b28bb2e09175','望月婆罗门引晨起与仲坚偶坐少焉雨作其声洒洒然绝似文场下笔时因借前韵戏成一篇','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"蹉跎岁晚，床头铁研已生衣。破窗雨送轻霏。暗里随风入夜，花上早先知。听一时下笔，鏖战文围。\",\"天心不违。更问甚、是和非。便著春衫换酒，醉墨题诗。逢场作戏，觉赏心、乐事未全稀。还自叹、虫吐馀丝。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2225cef5f295432a8ab','诉衷情·史仲恭寿','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"芹溪清浅舞涟漪。堕钓锦鳞肥。黄花一尊芳酒，万事觉俱非。\",\"留晚景，惜香霏。醉时归。最关情处，迎门稚子，一笑牵衣。\"]',NULL,NULL,NULL,NULL),('c16655df0364f22260ecf4e8c489f47c','水调歌头·山中偶成用遁庵兄韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"人生等行旅，能费几春秋。元龙漫矜豪气，百尺卧高楼。昨日青青双鬓，今日星星满镜，转首岁华流。归去便归去，何处觅菟裘。\",\"一枝筇，一壶酒，寄真游。姑山玉立千仞，直下看神州。欲语幽情谁可，赖有白鸥知我，尘世尽悠悠。一笑对妻子，出处不须筹。\"]',NULL,NULL,NULL,NULL),('c16655df0364f22270085b97407d77d6','月上海棠·冬至后一日独居无悰用前韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"光阴输与闲人手。屈指穷冬又初九。风雪拥柴关，竹外一枝梅秀。醅瓮熟，似笑渊明止酒。\",\"溪山旧约吾无负。便结无情岁寒友。世味尽醇醲，掩鼻向伊慵嗅。蓬茅底，有手何妨且袖。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2227dc2fc58a7a4d4d9','江城子·幽怀追和遁庵先生韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"昔年兄弟共弹冠。转头看。各苍颜。千古功名、都待似东山。慷慨一杯风露下，追往事，叙幽欢。\",\"晨霞翠柏尚堪餐。养馀闲。未全悭。十丈冰花、况有藕如船。醉里忽乘鸾鹤去，尘土外，两癯仙。\"]',NULL,NULL,NULL,NULL),('c16655df0364f2229aa4a58aff6d76b1','大江东去寄卫生袭之','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"干戈蛮触，问渠事直有，几何而已。毕竟颠狂成底事，谩把良心戕毁。坐穴藜床，磨穿铁砚，自有人知己。摩挲面目，不应长为人泚。\",\"过眼一线浮华，辱随荣后，身外那须此。便恁归来嗟已晚，荒尽故园桃李。秋菊堪餐，春兰可采，免更烦邻里。孙郎如在，与君共枕流水。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222a6e92723e230155e','江城子·东园牡丹盛开二三子邀余饮花下酒酣即席赋之','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"水南名品几时栽。映池台。待谁开。应为诗人、著意巧安排。调护正须宫样锦，遮丽日，障飞埃。\",\"晓风吹绽瑞云堆。怨春回。要诗催。醉墨淋漓、随手洒琼瑰。归去不妨簪一朵，人也道，看花来。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222a8620dd38b7200ed','望月婆罗门引清明后醉书于史氏之别墅二首','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"繁华梦断，吹花风起却添衣。满庭红雨霏霏。独倚绳床微叹，此意竟谁知。怅西园转眼，翠幕成围。\",\"事无我违。觉四十九年非。便好忙开蒋径，细和陶诗。风流巳置，抚遗编、三叹赏音稀。人不见、弦断朱丝。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222a8620dd3da5006d0','望月婆罗门引清明后醉书于史氏之别墅二首','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"东风袅袅，飞花一片点征衣。等闲耗损香霏。春去春来无迹，静里几人知。问沈郎何事，带减腰围。\",\"功名愿违。算此计、未应非。剩把闲愁殢酒，幽兴裁诗。溪山好在，怅眼中、渺渺故人稀。回首处、清泪如丝。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222ab1754b445ea23b7','大江东去送杨国瑞西归','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"西风汾浦，雁初飞，秋水渺茫无际。有底忙时来复去，泛若虚舟不系。篱菊将开，村醪初熟，且住为佳耳。笑言相答，个中吏隐无愧。\",\"岁月不贷闲人，君颜非少，我发白如此。好把金杯休去手，万事惟消沈醉。日转山腰，马嘶柳外，歌阕行人起。凭高西望，相思目断烟水。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222ad08feb4f2ba1179','蝶恋花·卫生袭之生朝，吾兄作歌词以寿之。余独无言，生执巵酒坚请不已，勉用兄韵以答其意','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"点检东园花发未。蝶绕芳丛，馥馥香浮蕊。买酒酬春君有地，不妨日涉聊成趣。\",\"身世虚舟元不系。浮利浮名，是甚闲情味。花下一杯方得意，人闲万事宜姑置。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222ce165c30fa13ee36','木兰花慢·元宵感旧','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"金吾不禁夜，放箫鼓、恣游遨。被万里长风，一天星斗，吹堕层霄。御楼外香暖处，看人闲平地起仙鳌。华烛红摇醉勒，瑞烟翠惹吟袍。\",\"老来怀抱转无聊。虚负可怜宵。遇美景良辰，诗情渐减，酒兴全消。思往事今不见，对清尊瘦损沈郎腰。惟有当时好月，照人依旧梅梢。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222d1f7bca2014cf449','蝶恋花·明日卫生见和，复次韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"燕子归来寒食未。脉脉桃花，微露胭脂蕊。回想旧游歌舞地，狂诗颠酒当时趣。\",\"白日长绳谁可系。老去情怀，事事都无味。倦鸟知还非有意，忙时宜用闲时置。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222d5869b658b24998f','满庭芳·斋居有感敬继遁庵兄韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"鏖战文场，横挥笔阵，万言一策平边。青云稳步，逸气盖贤关。致主尧虞堂上，真儒事、直欲追前。回头错，闲中风味，一笑觉都还。\",\"百年都几日，何须抵死，著意其闲。寻一邱一壑，此固无难。遁迹月萝深处，风吹梦、不到长安。浑无事，床头睡起，檐日已三竿。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222ec8c799a1b065465','行香子·书舍偶成','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"自叹劳生，枉了经营。到而今、一事无成。不如闻早，觅个归程。向渭川渔，东市卜，富春耕。\",\"眼底浮荣，身外虚名，尽输他、时辈峥嵘。得偷闲处，且适闲情。有坐忘篇，传灯录，洗心经。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222edefb24bea36f357','清平乐·送张君美经历之任安西幕','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"鸡声戒晓。催上长安道。宾幕雍容年正妙。人物风流温峤。\",\"政成海泳江涵。门阑剩有馀闲。公事不妨行乐，春风韦杜城南。\"]',NULL,NULL,NULL,NULL),('c16655df0364f222fd4d2dae37c4d418','满江红·张丈信夫林亭小酌感事怀人敬用遁庵先生韵','段成己','c16655df0364f222d1457b72d1457b72','元',NULL,'[\"光景催人，还又是、西风吹袂。青镜里、满簪华发，不堪憔悴。一月几逢开口笑，十年滴尽伤时泪。倩一尊、相对说清愁，花前醉。\",\"初未识，名为累。今始觉，身如寄。把闲情换了，平生豪气。致主安民非我事，求田问舍真良计。看野云、出岫却飞回，元无意。\"]',NULL,NULL,NULL,NULL),('c16655df03e30fd59c7c45ec89deb9a6','水龙吟·细字韵','周景','c16655df03e30fd58fb21ee7fa1e9c96','元',NULL,'[\"人生能几相逢，百年四海为兄弟。旧时青眼，今番白发，年华陨涕。春更无情，抛人先去，杨花无蒂。况江程渐短，别期渐紧，须重把、兰舟系。\",\"幸自清江如带，指黄垆、流莺声细。沧波如许，平芜何处，明朝迢递。何预兴亡，不如休去，墙阴挑荠。且相期共看，蓬莱清浅，更三千岁。\"]',NULL,NULL,NULL,NULL),('c16655df03f037db4334969f55ef471c','蝶恋花','孙景文','c16655df03f037dbd1457b72d1457b72','元',NULL,'[\"净洗胭脂轻扫黛。斗草亭边，自拗梨花戴。一段心情空自爱。风流那得时常在。\",\"屈指春光归已快。不掩珠帘，又恐东风怪。花影低将新月碍。小阑干外深深拜。\"]',NULL,NULL,NULL,NULL),('c16655df03fd5858beda27da68633138','清平乐·题碧梧苍石图','竹月道人','c16655df03fd5858d1457b72d1457b72','元',NULL,'[\"寸肠愁断。目送斜阳岸。枫落吴江秋水漫。盼杀南来征雁。\",\"绮窗好梦初成。梦回相见卿卿。明月西风夜冷，苍梧乱影多声。\"]',NULL,NULL,NULL,NULL),('c16655df0457f873f22e8d7a0637dd6e','西湖竹枝词','黄公望','c16655df0457f873e0040614396781bd','元',NULL,'[\"水仙祠前湖水深，岳王坟上有猿吟。湖船女子唱歌去，月落沧波无处寻。\"]',NULL,NULL,NULL,NULL),('c16655df049a64054bf158b5cbd66c6b','卜算子·谱太白诗语','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"明月在青天，借问今时几。但见宵从海上来，不觉云间坠。\",\"流水古今人，共看皆如此。惟愿当歌对酒时，长照金尊里。\"]',NULL,NULL,NULL,NULL),('c16655df049a64056e59c981489aa53d','蓦山溪','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"云房西下，天共沧波远。走马记狂游，正芙蕖、平铺镜面。浮空阑槛，招我倒芳尊，看花醉，把花归，扶路清香满。\",\"水枫旧曲，应逐歌尘散。时节又新凉，料开遍、横湖清浅。冰姿好在，莫道总无情，残月下，晓风前，有恨何人见。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405b222c01bd898cd89','望云间代州南楼','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"云朔南陲，全赵宝符，河山襟带名藩。有朱楼缥缈，千雉回旋。云度飞孤绝险，天围紫塞高寒。吊兴亡遗迹，咫尺西陵，烟树苍然。\",\"时移事改，极目伤心，不堪独倚危阑。惟是年年飞雁，霜雪知还。楼上四时长好，人生一世谁闲。故人有酒，一尊高兴，不减东山。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405bc32a2a54e4e0caf','凤栖梧','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"霜树重重青嶂小。高栋飞云，正在霜林杪。九日黄花才过了。一尊聊慰秋容老。\",\"翠色有无眉淡扫。身在西山，却爱东山好。流水极天横晚照。酒阑望断西河道。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405c2ebbe3b1b15eaae','鹧鸪天','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"十顷平波溢岸清。草香沙暖水云晴。轻衫短帽垂杨里，楚润相看别有情。\",\"挥彩笔，倒银瓶。花枝照眼句还成。老来渐减金钗兴，回施春光与后生。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405c2ebbe3b7aca8c3a','鹧鸪天','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"金络闲穿御路杨。青旗遥认醉中香。可人自有迎门笑，下马何妨索酒尝。\",\"春正好，日初长。一尊容我驻风光。归来想像行云处，薄雨霏霏洒面凉。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405d002cbd347767bbf','望海潮·发高丽作','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"云垂馀发，霞拖广袂，人间自有飞琼。三馆俊游，百衔高选，翩翩老阮才名。银汉会双星。尚相看脉脉，似隔盈盈。醉玉添春，梦云同夜惜卿卿。\",\"离觞草草同倾。记灵犀旧曲，晓枕馀酲。海外九州，邮亭一别，此生未卜他生。江上数峰青。怅断云残雨，不见高城。二月辽阳芳草，千里路旁情。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405d61274071ef6ee38','好事近','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"密雪听窗知，午醉晚来初觉。人与胆瓶梅蕊，共此时萧索。\",\"倚窗闲看六花飞，风轻止还作。个里有诗谁会，满疏篱寒雀。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405f32f182e00e36c5f','浣溪沙','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"抬转炉熏自换香。锦衾收拾却遮藏。二年尘暗小鸳鸯。\",\"落木萧萧风似雨，疏棂皎皎月如霜。此时此夜最凄凉。\"]',NULL,NULL,NULL,NULL),('c16655df049a6405f32f182e0579b045','浣溪沙','赵可','c16655df049a6405d1457b72d1457b72','元',NULL,'[\"火冷熏炉香渐消。更阑拨火更重烧。愁心心字两俱焦。\",\"半世清狂无限事，一窗风月可怜宵。灯残花落梦无聊。\"]',NULL,NULL,NULL,NULL),('c16655df049db2e90d4d0f196ec88ad4','蝶恋花·送春','何可视','c16655df049db2e9d1457b72d1457b72','元',NULL,'[\"金井啼鸦深院晓。扬尽东风，柳絮吹难了。燕子多情相识早。杏梁依旧双双到。\",\"一缕沉烟帘幕悄。满眼飞花，只觉人怀抱。十二玉楼春树杪。天涯不断青青草。\"]',NULL,NULL,NULL,NULL),('c16655df049db2e946408bd938bb8634','玉楼春','何可视','c16655df049db2e9d1457b72d1457b72','元',NULL,'[\"邻鸡喔喔晨窗白。檐树深沉初辨色。鸳鸯瓦上露溶溶，翡翠帘边风瑟瑟。\",\"小楼一半屏山隔。不灭银缸通照夕。还乡好梦却忘愁，梦破那堪仍在客。\"]',NULL,NULL,NULL,NULL),('c16655df04badb1725eb6af7271c524d','清平乐','陆行直','c16655df04badb17d1457b72d1457b72','元',NULL,'[\"楚天云断。人隔潇湘岸。往事悠悠江水漫。怕听楼前新雁。\",\"深闺旧梦还成。梦中独记怜卿。依均相思碎语，夜凉桐叶声声。\"]',NULL,NULL,NULL,NULL),('c16655df04be2dbd58a703402dba7032','阮郎归·寄李都运有之','胥鼎','c16655df04be2dbdd1457b723210ddbe','元',NULL,'[\"人情冷暖共高低。疏慵非所宜。老夫碌碌本无机。闲教造物疑。\",\"形木槁，鬓丝垂。山林先有期。故人只道挂冠迟。此心应不知。\"]',NULL,NULL,NULL,NULL),('c16655df04ce5f82ca55c4ccd55a3121','菩萨蛮·蒙岩石刻','契玉立','c16655df04ce5f82d1457b72d1457b72','元',NULL,'[\"蒙岩几日桃花雨。依稀流水章桥去。只恐到天台。误通刘阮来。\",\"玉堂开绮户。不隔尘寰路。休认避秦人。壶中别有春。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a047dff2460706706','齐天乐·送马德昌','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"人生南北如歧路，相逢自怜不早。倾盖班荆，分灯并壁，吟卷笔床茶灶。交情古道。怕催诏连翩，好风吹到。聚久别难，砌蛩那更碎怀抱。\",\"临行谁劝驻马，待将尘土事，妨我吟啸。小住虽佳，还堪就否，催得云帆缥缈。官梅正好。比前度孤山，剩开多少。两处心旌，倚楼同晚照。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a0f11395295cfaab7','贺新郎·次韵','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"回首章台路。又一番春事阑珊，满帘风絮。郭外谁家闲院落，别是壶天意趣。拚一日、来游一度。除却忧愁风雨外，肯容他、野马埋双屦。行乐地，更何处。\",\"人生忍把佳期误。况今朝满坐春风，怎禁不去。莲社兰亭当日话，便合从今委付。算此语、非缘要誉。饮少欢多还觉醉，看归途、叠嶂青无数。情未足，日催暮。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a1a16d9ba6c31bb67','木兰花慢·赠弹琵琶者','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"爱当垆年少，将雅调，寄幽情。尽百喙春和，群喧夜寂，老凤孤鸣。都来四条弦里，有无穷、旧谱与新声。写出天然律吕，扫空眼底秦筝。落红。\",\"天气暖犹轻。洗耳为渠听。想关塞风寒，浔阳月色，似醉还醒。轩窗静来偏好，到曲终、怀抱转分明。相见今朝何处，语溪乍雨初晴。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a445489f86e8b0db3','木兰花慢·寿张可与','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"对黄花烂漫，能几日，又重阳。问眼底湖山，新□□□，别是风光。三台近临吴地，映奎躔、明处紫微郎。人道当年豸角，这回收敛秋霜。笑谈。\",\"幕府昼偏长。何事最为忙。但宽取一分，一分方便，亦足流芳。山人敢攀宗衮，为东南、民命秉心香。更借南阳菊水，殷勤满注瑶觞。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a47773c794312d3cc','木兰花慢·寿陈介轩','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"问家传旧物，有清节，与高风。任野色充庭，俗尘扫迹，谈笑从容。生涯但随分足，比孟尝、襟度只差穷。曾是贞元朝士，本来湖海元龙。旦评。\",\"推分许轩翁。万□一词同。喜渐近稀年，从教发白，目炯双瞳。蟾钩又还乍吐，对荷香、莫放酒尊空。砌下芝兰竞爽，年年家庆图中。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a577b47f0f1ce57ce','临江仙·寿程雪楼','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"白雪楼前清昼，新来喜事连绵。朱明绿暗麦秋天。绣衣何日去，丹荔已香传。\",\"前夜团圆明月好，清光流照华筵。锦囊随处地行仙。庭椿关望眼，同庆八千年。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a7d0d37f5a0795130','柳梢青·赋枯梅寄张郎中马同知','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"冷淡根荄。小春时候，两蕊三花。栽向西湖，移来东阁，一任安排。\",\"绝怜瘦影横斜。但宜在、山巅水涯。花里平安，岭头孤秀，荣悴争些。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a7f06c42210ba48ee','唐多令·寄吴间','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"移住还瀛洲。天槎去莫留。数归期、已过中秋。上界群仙官府足，云不碍，水长流。\",\"酒令与诗筹。依然记旧游。倚斜阳、分付羁愁。应与鳌峰人共语，还不减，去年不。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a8fab1b891f2e315e','木兰花慢·送季治书','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"羡高标雅量，窗八面，更玲珑。每风日佳时，湖山清处，袍锦从容。不把云霄自隔，向尊前、看我啸吟中。人世流光易老，古来知己难逢。青骢。\",\"来往太匆匆。呵护有纱笼。想当道豺狼，先声到处，胆落英风。南楼暂容横榻，算览观、有限兴无穷。别后相思何处，心期付与飞鸿。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766a9c42c8c195067006','唐多令·寿王肯堂','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"前日是中秋。婵娟为我留。画图间、主劝宾酬。忙处偷闲谁得侣，真意度，淡交游。\",\"薇省老参谋。三台一举头。鲁祥麟、憎重儒流。今岁彩衣还有伴，方衮衮，庆公侯。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766aa155768fa8a59686','玉漏迟·寿马右丞','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"浙江回棹处。急流勇退，冥鸿高举。出昼迟迟，不为虎丘留住。姑待春和水涨，旋乘个、月明船去。天未许。云归又出，依然霖雨。\",\"熟路小队轻车，想司马重来，聚观如堵。暇日西湖，点检旧题诗句。冷落江南倦翼，但惟有、心香一缕。长记取。每岁仲春端午。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766ad0a5eee9cf5677d8','木兰花慢·寿刘东厓','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"记当年客里，是今日，奉霞觞。任厨传荒凉，风林震撼，乐自难量。别来岁华电走，又都门、我北尔南塘。我半百年已老，兄多四岁偏强。玉堂。\",\"满袖惹天香。凤阁样词章。看采帖题春，椒盘草颂，蓬矢开张。金瓯御屏简记，赋□钧、寿域奏明光。后十五年再见，伴兄绿野徜徉。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766adbcb9d8aff55b3bf','摸鱼儿·次韵抱瓮','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"采黄花、自斟清醑，南山人在何许。浮生聚散云萍似，消得几番寒暑。些个路。□不断情悰，惟有春天树。停歌罢舞。更说甚悲欢，从教白首，心事付崔橹。\",\"钧天梦、忘却当年宦谱。吟蛩休怨休诉。如今世味更尝惯，但见青山多妩。清对苦。是我误儒冠，还是儒冠误。西湖胜处。且趁取时佳，不寒不暖，同泛小舟去。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766adc41f87c960c284e','木兰花慢','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"载西山爽气，添不重，月船轻。记前度今朝，琼花烂漫，管领歌声。今岁秾华深处，羡衮衣、还看彩衣荣。人世云萍相遇，岁寒松柏长青。行行。\",\"催觐朵云明。晓色上觚棱。看春去春来，依然黄阁，移近家庭。浮云傥来轩冕，算古今、久远是功名。尚有寒厓枯卉，东君也解留情。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766ae28180fa4ef66a5d','玉漏迟·寿张右丞','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"太平元夜好。鳌山宴彻，祥烟凝晓。端正婵娟，为我玳筵留照。曾待龙潜旧邸，更欣际、鸿图初造。人尽道。两宫柱石，元真廊庙。\",\"天转右辖星躔，尽向上勋名，历阶须到。商鼎周彝，时奉退朝吟啸。剩有埙篪韵雅，况庭户、纤尘如扫。经济了。长醉遂初春早。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766ae67e621befcfd5fb','木兰花慢·寿抑斋','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"尽江南江北，谁不慕，抑斋名。羡梦笔才华，粲花谈论，乔木家声。重寻玉堂旧步，喜今番、天语重丁宁。云是儒家领袖，居然史笔权衡。相迎。\",\"便觉眼常青。相对话羁情。正绿暗园林，朱明时候，瑞霭腾腾。期公久长名节，太羹中、还著五侯鲭。此去明堂一柱，当年玉树阶庭。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766af46fb48986158f47','齐天乐·寿王伯起','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"庭柯一叶炎曦淡，秋光宦情相似。驷马争驰，千帆竞送，不羡纷纷时辈。随缘赋禄，庆官府清明，纪纲兴起。暇日吟鞍，湖山公案更兼理。\",\"将军犹自未老，旧时供奉曲，还有风致。拄笏西山，蟠螭北海，却是君家盛事。佳辰雅聚，且满引霞觞，坐看芝砌。富贵长年，四时谈笑里。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766af583fbbfd05a9751','木兰花慢·次唐格斋韵','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"尽交游满眼，岁寒者，果谁欤。羡锦绣为心，冰霜作操，仁义蘧庐。门前扫清俗轨，自熙然、瓮牖与绳枢。记我蓬弧时候，寓情翰墨欢娱。驱车。\",\"特地致函书。高义渺江湖。悄梦断鹓鸾，盟深鸥鹭，闲适从渠。槐阴渐成翠幄，看庭前、莺过引新雏。岁岁期君一醉，相忘非我非鱼。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766af67617ec7e7dab2f','齐天乐·次韵谢倦翁','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"班荆倾盖当时事，回头屡更寒暑。倦翼才还，苍颜易得，零落江陵千树。相逢冷署。且莫讶儒冠，解将人误。计我生朝，采笺新调映红炬。\",\"投闲谁道太早，人生行乐耳，何地非旅。绿暗书眸，红生醉脸，聊对檐花细雨。移尊共语。问何日扁舟，嫩荷香处。坐待蟾光，四更犹未吐。\"]',NULL,NULL,NULL,NULL),('c16655df04d3766af77f361e63bde089','望月婆罗门引送徐容斋','张伯淳','c16655df04d3766ad1457b72d1457b72','元',NULL,'[\"容斋平日，一身用舍系安危。儿童走卒皆知。谁料鲈鱼江山，忽忆故山薇。任西风别酒，月正圆时。\",\"性斋有诗。道扫舍，待吾归。二老相招如此，公论畴依。人生行乐，对佳水佳山何必归。公笑曰、归去来兮。\"]',NULL,NULL,NULL,NULL),('c16655df05006b4183cfc9c2c84586e2','竹枝词','王立中','c16655df05006b41d1457b72d1457b72','元',NULL,'[\"孤山梅花开雪中，恰似阿侬冰雪容。不学画桥南畔柳，春来容易嫁东风。\"]',NULL,NULL,NULL,NULL),('c16655df050b5e941ce2f87b0bd80d72','金字经','徐失名','c16655df050b5e94d1457b72d1457b72','元',NULL,'[\"犀箸丝鱼脍，象盘冰蔗浆。池阁南风红藕香。将。\",\"紫霞白玉觞。低低唱。唱著道、今夜凉。\"]',NULL,NULL,NULL,NULL),('c16655df050b5e941ce2f87b6c4d3d7f','金字经','徐失名','c16655df050b5e94d1457b72d1457b72','元',NULL,'[\"紫燕寻旧垒，翠鸳栖暖沙。一处处、绿杨堪系马。他。\",\"问前村、沽酒家。秋千下。粉墙边、红杏花。\"]',NULL,NULL,NULL,NULL),('c16655df05ecf8cffa8e8f34265a4a10','西湖竹枝词二首','无名子','c16655df05ecf8cfd1457b72d1457b72','元',NULL,'[\"苏公堤上杨柳青，人来人去绾离情。东风为尔丁宁道，折断柔条莫再生。\"]',NULL,NULL,NULL,NULL),('c16655df05ecf8cffa8e8f3492b421bf','西湖竹枝词二首','无名子','c16655df05ecf8cfd1457b72d1457b72','元',NULL,'[\"天竺寺前开翠微，长年流水白云飞。流水入湖无日歇，白云出岫有时归。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba0146773e8029973f','摸鱼儿·春日西湖泛舟','张翥','c16655df05f199ba4079016d6c1da886','元','这首词写春日泛舟西湖所引发的感慨。上片写景，下片抒情，不胜今昔之感。上片是景中情，好风好雨好天气，一片喜气洋洋；下片是情中景，昔日繁华风流云散，气象萧索。上片一开头词人用寥寥数笔勾勒出雨后西湖的美丽清新，湖水涨满，微风轻拂。在乍暖还寒的天气中，人们结伴出游。用“嫩”字、“轻”字形容江南仲春天气，可称绝妙。这也就是所谓的“通感”手法。歌女轻脆而嘹亮的歌声从半卷的帘中飘出，与婉转的莺声，呢喃的燕语相配合，组成了美妙的春之歌。在这样的风光声色中，春色如酒，春情如酒，谁不是酒不醉人人自醉呢？无人不暂时忘却了平日的拘谨和烦恼。下片写今日的情绪。如今春光虽好，但是这里已经没有了昔日的繁华。杨柳岸边不见了红亭翠馆，歌舞之地满目荒凉，野草丛生，那亭中、馆中、花中的歌女们早已成过眼云烟国，四散而去。在一片残照下，流水落花春去也；词人极目水天，空余惆怅；也使读者在篇终处低回想象，寻绎无穷。全词运用对比手法非常突出，同样是春天，前者是“波外风软”，“天气嫩寒轻暖”；后者是“青芜满目成秋苑”，“落絮飞花”。前者的人物“兰舟同上鸳鸯浦”，“任狂客无肠，王孙有恨，莫放酒杯浅”；后者的人物是“游兴全懒”。前者歌舞之地热闹非凡，后者是“绮罗云散”。总之上下片之间几乎都可以一一对照。生长在那个时代的张翥，不一定对前朝有很深的感情。他的感慨也许并像宋遗民那样会有故国之思，或许是另有原因，但他对人情物景的变迁还是相当敏感的，因此面对美丽的西湖之春，发出了如此深沉的叹息。','[\"涨西湖、半篙新雨，曲尘波外风软。兰舟同上鸳鸯浦，天气嫩寒轻暖。帘半卷，度一缕、歌云不碍桃花扇。莺娇燕婉。任狂客无肠，王孙有恨，莫放酒杯浅。\",\"垂杨岸，何处红亭翠馆。如今游兴全懒。山容水态依然好，惟有绮罗云散。君不见，歌舞地、青芜满目成秋苑。斜阳又晚。正落絮飞花，将春欲去，目送水天远。\"]',NULL,NULL,NULL,'[\"曲（qū）尘：曲上所生菌，色淡黄如尘土，故称。曲是酿酒或制酱用的发酵物。\",\"兰舟：木兰船，船的美称。\",\"\\u200B歌云句：宋晏几道《鹧鸪天》：\\u201C舞低杨柳楼心月，歌尽桃花扇底风。\\u201D歌云：指歌声响遏行云。桃花扇：歌舞时用的扇子。\",\"无肠：古人称蟹为无肠公子。因其横行，故称狂客。\",\"王孙有恨：《招隐士》：\\u201C王孙游兮不归，春草生兮萋萋。\\u201D王孙此指游子。因离家故有恨。\",\"莫放句：五代王衍《醉妆词》：\\u201C莫厌金杯酒。\\u201D\"]'),('c16655df05f199ba016e8c43d742f887','浣溪沙·临川别席','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"昨夜花前送玉钟。绿鬟歌罢落梅风。不知离思为谁浓。\",\"醉语低回银烛背，梦云重叠绣帏中。只愁归路见芙蓉。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba0314357dd88a2500','东风第一枝·忆梅​','张翥','c16655df05f199ba4079016d6c1da886','元','这首词的字面意思如题所示是“忆梅”，是否另有寄托，不详；但看全词，似非单纯的咏物词。词的开首五句只说梅花初绽。唐人齐己《早梅》诗云：“前村深雪里，寒梅一枝开。”而此词说梅花只开得一半，北枝未开南枝开。然而仅仅这半点春色，已经烘动芳心，佳人起来梳妆打扮了。春色恼人眠不得啊。“是月斜”三句，都是写梅边景象，古诗中常用此类景物衬托梅花，如北宋林逋《山园小梅》“暗香浮动月黄昏”，南宋姜夔《暗香》“旧时月色，算几番照我，梅边吹笛”，“但怪得竹外疏花，香冷入瑶席”，《疏影》“苔枝缀玉，有翠禽小小，枝上同宿”。此词中的“老树浑苔”与“苔枝缀玉”同一意思。苔梅是梅树中的一种，“其枝樛曲万状，苍藓鳞皴，封满花身”（见南宋范成大《梅谱》）。又，“是月斜”三句也许用典，《龙城录》“赵师雄醉憩梅花下”条载，隋开皇中，赵师雄迁罗浮，一日，天寒日暮，于松林中见一女子，淡妆素服，出迓师雄，语极清丽。因与共叩酒家门，得酒相饮，笑歌戏舞。后醉寝。但觉风寒相袭。久之，东方已白，师雄起视，乃在大梅花树下，上有翠羽啾嘈相顾，月落参横，但惆怅而已。“从下片的”叩门喜伴金尊“来看，应有用典的可能。果真如此，那么下片”云淡淡“数句，也较容易理解：这是梦醒后的一种失落感。又因为唐大角曲中有《大梅花》、《小梅花》，乐奏花落，所谓”黄鹤楼中吹玉笛，江城五月落梅花“，惊破了好梦，所以说”倚栏怕听画角\"。画角，一种有花纹的角，吹奏乐器。接下去，又是对梦的朦胧回忆。“半面”的出典，原是恶谑，但用在这里，却有佳人“回眸一笑百媚生​”的美感，是作者不能忘情的再一次显现。然而，这一切都是一场春梦罢了，都是消逝了的影子，空剩下无法抹去的回忆。相忆至深，不免寄意未来，希望有朝一日，重寻旧踪，去访梅寻梦。词用了一连串的比喻拟人手法，将花比人，将人比花，人花糅合，表达出作者对逝去的美好人事的向往；而全词又从姜夔《暗香》、《疏影》脱胎，遣词命意，无不沾带姜词的痕迹。','[\"老树浑苔，\\u200B横枝未叶，青春肯误芳约。\\u200B背阴未返冰魂，\\u200B阳梢已含红萼。佳人寒怯，谁惊起、晓来梳掠。\\u200B是月斜花外幺禽，\\u200B霜冷竹间幽鹤。\\u200B\",\"云淡淡、粉痕渐薄，风细细、冻香又落。叩门喜伴金尊，\\u200B倚栏怕听画角。\\u200B依稀梦里，\\u200B记半面、浅窥珠箔。恁时得、重写鸾笺，\\u200B去访旧游东阁。\\u200B\"]',NULL,NULL,NULL,'[\"浑：满是。\",\"\\u200B冰魂：喻梅花高洁的品格精神。宋苏轼《松风亭下梅花盛开》诗：\\u201C罗浮山下梅花村，玉雪为骨冰为魂。\\u201D\",\"幺：小。\",\"半面：《南史·徐妃传》载，南朝梁元帝眇一目，徐妃知帝将至，常饰半面妆以待。\",\"珠箔：珠帘。\",\"恁时：什么时候。\",\"鸾笺：彩笺，指代书信。\",\"东阁：唐杜甫《和裴迪》诗：\\u201C东阁官梅动诗兴，还如何逊在扬州。\\u201D此指梅花所在地。\\u200B\"]'),('c16655df05f199ba08028d4a33e0f247','金缕词送王季境还广陵','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"西子湖边路。看依然、水光山色，自宜晴雨。天上归来重载酒，惟有旧盟鸥鹭。笑鬓影、星星如许。公子华筵凉似水，更绿鬟、窈窕歌金缕。留晚醉，看眉妩。\",\"三生书记真豪举。把平生、香奁软语，锦囊佳句。君到淮南明月夜，为问崔娘安否。□翻作、锦筝新谱。只恐惊鸿花外起，趁行云、直过沧江去。飞不到，断肠处。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba0a359014bcf86fc7','凤凰台上忆吹箫·听沈野云吹箫醉后有赋','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"琪树锵鸣，春冰碎落，玉盘珠泻还停。渐一丝风袅，悠扬青冥。疑把红牙趁节，想有人、记豆银屏。何须数，琵琶汉女，锦瑟湘灵。\",\"追思旧时胜赏，醉几度西湖，山馆池亭。惯依歌花月，按舞娉婷。岁晚相逢客里，且一尊、同慰漂零。君休惜，吴音朔调，尽与吹听。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba0dc2c2ea1d285bf2','婆罗门引','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"暮天映碧，玻璃十顷蕊珠宫。金波涌出芙蓉。谁唤川妃微步，一色夜妆红。看光摇星汉，起舞鱼龙。\",\"月华正中。画船漾，藕花风。声度鸾箫缥缈，雁柱玲珑。酒阑兴极，更移上、琼楼十二重。残醉醒、烟水连空。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba11d097aff6168cb1','惜分飞·写梦','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"相见依然人似旧。比似年时较瘦。笑问平安否。不言低掩罗衫袖。\",\"便欲窗前推枕就。无柰红僝绿僽。惊起空回首。半床斜月疏钟后。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba1368c91473871039','临江仙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"羡杀渔村无畔岸，茫茫杨柳蒹葭。雨余秋涨没汀沙。惊鸿投别渚，浴鸟坐沉槎。\",\"残日篱头闲晒网，垂髫来卖鱼虾。得钱沽酒径归家。一声横笛外，烟火隔芦花。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba1d60f74b18ec1b45','洞仙歌·辛巳岁燕城初度','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"功名利达，任纷纷奔竞。纵使得来也侥幸。老眼看多时，钟鼎山林，须信道、造物安排有命。\",\"人生行乐耳，对月临风，一咏一觞且乘兴。五十五年春，南北东西，自笑萍踪久无定。好学取、渊明赋归来，但种柳栽花，便成三径。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba1f13bc462596b50e','忆旧游·重到金陵','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"怅麟残废井，凤去荒台。烟树敧斜。再到登临处，渺秦淮自碧，目断云沙。后庭谩有遗曲，玉树已无花。向宛寺裁诗，江亭把酒，暗换年华。\",\"双双旧时燕，问巷陌归来，王谢谁家。自昔西州泪，等生存零落，何事兴嗟。庾郎似我憔悴，回首又天涯。但满耳西风，关河冷落凝暮笳。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba20f39fe211060d32','水龙吟·听房氏自然歌，求词为赋','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"春风琼树香中，数声恰似流莺啭。歌尘飞下，落花起舞，骊珠脱串。豆蔻珠帘，牡丹雪岭，小桃人面。是自然绝艺，天然书谱，霓裳序，六幺遍。\",\"独占二分月色，向尊前、几番曾见。赏音如此，不辞醉墨，为题纨扇。浪雨闲云，剩香残黛，莫论恩怨。看秾华又老，情缘未断，寄楼中燕。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba25eb6af7f69b65ae','清平乐','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"先生醉也。甚矣吾衰也。万物不如归去也。陶令真吾师也。\",\"篱边菊蕊初黄。为花准备携觞。只恐不如人意，风风雨雨重阳。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba26d09894c21e7b82','鹧鸪天·为朱氏小妓绣莲赋三首','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"半臂京绡稳称身。玉为颜面水为神。一痕头导分云绾，两点眉山入翠颦。\",\"丹杏小，碧桃新。雏莺恰啭上林春。平生惯是听歌耳，除却莲儿只一人。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba28f55ee67615df35','行香子·山水便面','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"佛寺云边。茅舍山前。树阴中、酒旆低悬。峰峦空翠，溪水清涟。只欠梅花，欠沙鸟，欠渔船。\",\"无限风烟。景趣天然。最宜他、隐者盘旋。何人村墅。若个林泉。恰似敧湖，似枋口，似斜川。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba29a33b4916e32cc8','行香子·止酒五首','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"酒量无多。不饮从他。看黄垆、似隔山河。渊明自止，醉尉谁呵。也莫豪吟，莫狂舞，莫高歌。\",\"鸥外风波。蜗角干戈。算百年、一梦南柯。阅人传舍，随处行窝。便富熏天，气盖世，待如何。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2cf4e55594524b74','谒金门·酒后偶忆','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"春几许。红透数枝花雨。管领风光谁是主。酒边人楚楚。\",\"好与写将乐府。剩与画教眉妩。醉后不知庭院午。隔帘双燕语。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2eabdb0f5334f9b0','忆秦娥','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"金杯侧。东风易醉多情客。多情客。送君南浦，送春南陌。\",\"落花巧妒罗裙色。舞红变尽休轻拆。休轻拆。待他归看，旧时宽窄。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2eabdb0fb8bf286d','忆秦娥','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"江南北。金鞍一去空消息。空消息。鸡声月店，雁声霜驿。\",\"春醪谁道浓无滴。十分满引浑无力。浑无力。离愁如海，怎生乾得。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2ee238bf43860292','摸鱼儿','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"过花朝、淡烟轻雨，东风还又春社。客怀不断还家梦，只泥酒杯陶写。孤馆夜。甚浓醉、无人知道归来也。兰灯半灺。任赋就鱼笺，弦抛玉轸，谁念倦司马。\",\"长安市，几度携尊命驾。空惊游兴衰谢。醉乡天地无今古，争得一襟萧洒。春纵冶。便不饮、从教团雪揉花打。觥筹已罢。笑蜾嬴螟蛉，吾今真止，为报独醒者。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2ee238bf53233a84','摸鱼儿','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"记苏台、旧时风景，西楼灯火如画。严城月色依然好，无复绮罗游冶。欢意谢。向客里相逢，还又思陶写。金尊翠斝。把锦字新声，红牙小拍，分付倦司马。\",\"繁华梦，唤起燕娇莺奼。肯教孤负元夜。楚芳玉润吴兰媚，一曲夕阳西下。沉醉罢。君试问、人生谁是无情者。先生归也。但留意江南，杏花春雨，和泪在罗帕。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2ee238bf88039574','摸鱼儿','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"正匆匆、楚乡秋晚，孤鸿飞过南浦。同来桃叶堪惆怅，一舸载春先去。愁绝处。问那曲阑干，曾听人低语。今宵最苦。向枫树溪桥，芦花野馆，剪烛卧听雨。\",\"吴霜鬓，破帽西风怎护。丝丝都是离绪。旧情顿冷新愁重，总付坠鞭词谱。君记取。待雪夜、相思乘兴柴冈路。唱予和汝。要款段随车，轻盈唤酒，重为国香赋。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba2ee238bfeb3c2ecd','摸鱼儿','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"问西湖、旧家儿女，香魂还又连理。多情欲赋双蕖怨，闲却满奁秋意。娇旖旎。爱照影、红妆一样新梳洗。王孙正拟。唤翠袖轻歌，玉筝低按，凉夜为花醉。\",\"鸳鸯浦，凄断凌波梦里。空怜心苦丝脆。吴娃小艇应偷采，一道绿萍犹碎。君试记。还怕是、西风吹作行云起。阑干谩倚。便载酒重来，寻芳已晚，余恨渺烟水。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba32197434631110fa','桂枝香','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"天香万斛。尽贮入魏台，辟寒金粟。谁唤仙娥睡起，露妆烟沐。翠云裙袖黄云袜，倚秋风、乍惊郎目。恨无明月，高烧蜡炬，分阴丛绿。\",\"深照见、凉禽并宿。爱摇滟琼杯，花影堪掬。寸梦丝缘旧约，尚堪重续。何时卜隐西湖上，葺缃荷、芳杜为屋。小山人远，魂招不来，谩歌遗曲。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba32ad97e158c515ed','渔家傲','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"红白芙渠千万朵。水仙恰试新梳裹。缟袂霞衣争婀娜。香霞堕。凌波忽载行云过。\",\"正好玩芳停画舸。尊前自唱无人和。惟有沙鸥三两个。飞近我。夜凉同向花间卧。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba34121d2facae148c','六州歌头·孤山寻梅','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"孤山岁晚，石老树查牙。逋仙去。谁为主。自疏花。破冰芽。乌帽骑驴处。近修竹，侵荒藓，知几度。踏残雪，趁晴霞。空谷佳人，独耐朝寒峭，翠袖笼纱。甚江南江北，相忆梦魂赊。水绕云遮。思无涯。\",\"又苔枝上，香痕沁，幺凤语。冻蜂衙。瀛屿月，偏来照，影横斜。瘦争些。好约寻芳客，问前度，那人家。重呼酒。摘琼朵。插鬓鸦。唤起春娇扶醉，休孤负锦瑟年华。怕流芳不待，回首易风沙。吹断城笳。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba36659e4869843fc3','真珠帘','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"银蟾半露婵娟影，西风早、次第中秋天气。凉透小帘栊，乍夜长迟睡。见说灵岩山色好，甚也不浓如归意。归未。趁西泠载酒，南园寻桂。\",\"还又客里生朝，把金尊绿酒，与谁同醉。烟雨隔垂虹，望美人秋水。桃叶妆楼团扇曲，但小草、鸾笺相寄。传示。送白萍一剪，碧云千里。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba36e61b743521f161','唐多令·寄意箜篌曲','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"花下钿箜篌。尊前白雪讴。记怀中、朱李曾投。镜约钗盟心已许，诗写在，小红楼。\",\"忍泪上云兜。断魂随彩舟。等闲闲、惹得离愁。欲寄长河鱼信去，流不到，白鹭洲。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba37d81ca1452acece','声声慢','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"西风坠绿。唤起春娇，嫣然困倚修竹。落帽人来，花艳乍惊郎目。相思尚带旧恨，甚凄凉、未忺妆束。吟鬓底，伴寒香一朵，并簪黄菊。\",\"却待金盘华屋。园林静、多情怎禁幽独。蛱蝶应愁，明日落红难触。那堪雁霜渐重，怕黄昏、欲睡未足。翠袖冷，且莫辞、花下秉烛。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba37d81ca149ca07f8','声声慢','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"金銮学士，天上归来、兰舟小驻芜城。供奉新词，几度惯赋鸣筝。相逢沉郎绝艺，为尊前、细写余情。问何似，似秦关雁度，楚树蝉鸣。\",\"我亦从来多感，但登山临水，慷慨愁生。一曲哀弹，只遣髯变魂惊。行期买花载酒，趁秋高、月明风清。须尽醉，听江头、肠断数声。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba40a65b98e10d037e','满江红·次韵耶律舜中樟亭观潮','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"望入西泠，乍一线、涛头涌白。疑海上、鳌翻山动，鹏抟风积。银汉迢遥槎有信，秋光浩荡云无迹。快醉挥、吟笔倒琼瑰，冯夷宅。\",\"沙草远，迷烟碛。云树老，敧宫壁。叹潮生潮落，几时休息。事往空遗亡国恨，鸟飞不尽吴天碧。正销凝、何处夕阳楼，人横笛。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba4264f9c6306bffd1','鹧鸪天·赠泉南琵琶妓','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"玉手琵琶半醉中。从容慢拈复轻拢。青衫司马情偏感，翠袖红莲艺更工。\",\"花淡泞，月朦朦，归来无语立东风。汗巾红渍槟榔液，错认窗前唾绣绒。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba4878c5522bbf3ac3','恋绣衾·春晴中酒','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"醉乡残梦莺唤醒。见柳梢、日影弄明。又误了、寻芳伴，减东风、庭院笑声。\",\"玉儿扶起仍娇困，索樱桃、芳露解酲。纵病也、心情好，比别离、滋味较轻。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba49bf36671be92bf6','南浦','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"花落楚江流，过西山、雨涨渔村无路。双浆载愁来，苹沙外、惟有盟鸥相觑。春波碧草，送君曾是伤情处。依旧朝云飞画栋，秋满鹤汀凫渚。\",\"斜阳三两人家，□青旗影里，炊烟一缕。弦索夜深船，凄凉听、还似西风湓浦。征鸿去尽，梦回明月生烟树。如此山川无限恨，都付一尊怀古。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba4ac42544a52189f9','水龙吟·次韵王本中赋楼子芍药','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"宝楼十二玲珑，仙家只在云间住。金盘舞罢，罗裙襞皱，乘风欲去。画槛移春，彩鸾衔信，几番延伫。看钗莛叠萼，天然富贵，妆台近，有人妒。\",\"句引广陵遗恨，倩流莺、为花低诉。年年长是，芳菲时候，满城烟絮。春色三分，落红千片，总成尘土。向月明空羡，双双睡蝶，宿花房露。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba4b507b129af69328','朝中措','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"梅花处处满枝开。酒力荡吟怀。烟染藏鸦万缕，东风扶起春来。\",\"幽禽啼树，戏鱼跳日，水碧如苔。若个仙翁画得，翠微倒影楼台。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba50b63f4ac88dbf99','昭君怨','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"队队毡车细马。簇拥阏氏如画。却胜汉宫人。闭长门。\",\"看取蛾眉妒宠。身后谁如遗冢。千载草青青。有芳名。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba5231174768e3f3af','一枝春闹蛾','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"雾翅烟须，向云窗斗巧，宫罗轻剪。翩翩鬓影，侧映宝钗双燕。银丝蜡蒂，弄春色、一枝娇颤。谁网得、金玉飞钱，结成翠羞红怨。\",\"灯街上元又见。闹春风簇定，冠儿争转。偷香傅粉，尚忆去年人面。妆楼误约，定何处、为花留恋。应化作、晓梦寻郎，采芳径远。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba57a28c9f1f9cb0f7','谒金门·效前人首句用山人字醉中答友','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"山人坐。把酒歌呼相和。起舞尽教乌帽堕。归来头不裹。\",\"浮世人情历过。身外虚名参破。除却醉乡无别个。神仙天地我。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba57fe3aae2f69356c','摸鱼儿·钱万户宜之邀予赋瑶台景','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"甚瑶台、翠鸾雏小。风流占断妍景。数声何处啼春胜，帘卷晓窗人静。天气困。梳未稳、绿鬟倭堕临妆镜。懒羞倦整。待铰个金花，眉夫双雁，还怯剪刀冷。\",\"仙家好，十二行云路迥。牢笼芳梦不定。多情正要人拘管，无柰绿昏红暝。乘酒兴。携两袖、天风飞上蓬莱顶。阑干独凭。为唤玉笙来，霓裳按舞，和月醉花影。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba65636ec78d9cc479','临江仙·次韵山村先生赋柳','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"摇荡春光湖上路，多情偏识倡条。画船系在赤阑桥。花飞人别处，绿暗雨休朝。\",\"恼乱东风扶不起，空怜燕姹莺娇。舞衣香冷董娇娆。相思无限恨，犹似旧宫腰。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba69c74f569d50075e','陌上花','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"关山梦里，归来还又、岁华催晚。马影鸡声，谙尽倦邮荒馆。绿笺密记多情事，一看一回肠断。待殷勤寄与，旧游莺燕，水流云散。\",\"满罗衫是酒，香痕凝处，唾碧啼红相半。只恐梅花，瘦倚夜寒谁暖。不成便没相逢日，重整钗鸾筝雁。但何郎，纵有春风词笔，病怀浑懒。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba6a10f5086269b919','风入松','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"东风巷陌暮寒骄。灯火闹河桥。胜游忆遍钱塘夜，青鸾远、信断难招。蕙草情随雪尽，梨花梦与云销。\",\"客怀先自病无聊。绿酒负金蕉。下帷独拥香篝睡，春城外、玉漏声遥。可惜满阶明月，更无人为吹箫。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba6ad6b8209fc2d957','高阳台·题赵仲穆作陈野云居士山水便面','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"染黛浮空，凝妆伫远，数峰底事含颦。十样新眉，从他雨抹烟匀。龙绡便面宜歌舞，看亭亭、玉骨冰神。几销魂，翠被余香，锦瑟清尘。\",\"如今归去湖山畔，对一川平野，一片闲云。两两渔舟，相过桂渚兰津。谁将玉斧修明月，柰琼楼、高处无人。忆王孙。芳草江南，暗鴂残春。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba73e98347f9ecce14','露华玉簪','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"瀛洲种玉。总付与花神，月底深斸。琢就瑶笄，光映鬓云斜矗。几度借取搔头，别试汉宫妆束。风露冷，幽香半襟，淡伫阑曲。\",\"亭亭雪艳愁独。爱粉沁冰筒，须拈金粟。石上那回磨断，争忍轻触。一自楚客归来，珠履旧游谁续。秋梦起，残妆半簪坠绿。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba73f48536e403cd10','水调歌头','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"三十九年我，老色上吟髭。生辰月宿南斗，正合退之诗。今岁两逢正月，准算恰成四十，岁暮日斜时。腊彘剐红玉，汤饼煮银丝。\",\"炷炉香，饮杯酒，赋篇词。萧然世味，前身恐是出家儿。天下谁非健者，我辈终为奇士，一醉不须辞。莫问黄杨厄，春在老梅枝。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba762dc6362641611c','望海潮','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"扶桑何许，蓬莱何处，沧海一望漫漫。精卫解填，鼋鼍可驾，凌波直度三韩。云气有无间。只是天是水，无地无山。赑屃鳌掀，飓风俄起书生寒。\",\"从今不数鲵桓。羡秦人采药，龙伯垂竿。槎信未来，珠光暗徙，群仙约我骖鸾。长啸壮怀宽。且振衣绝顶，酾酒长澜。挥手相招，片帆飞趁暮潮还。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba76a72c344d64b9a2','水龙吟·广陵送客，次郑兰玉赋蓼花韵','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"芙蓉老去妆残，露华滴尽珠盘泪。水天潇洒，秋容冷淡，凭谁点缀。瘦苇黄边，疏萍白外，满汀烟穟。把余妍分与，西风染就，犹堪爱，红芳媚。\",\"几度临流送远，向花前、偏惊客意。船窗雨后，数枝低人，香零粉碎。不见当年，秦淮花月，竹西歌吹。但此时此处，丛丛满眼，伴离人醉。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba794cbc4b4a465f1d','齐天乐','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"阑干十二东风外，春藏画楼鸳琐。蜡炬光浓，氍毹坐软，宝鼎旋培沉火。玻璃盏大。但有酒须倾，有歌须和。剧饮淋浪，万金良夜虚过。\",\"中年情绪易恶，风流青镜里，销减些个。闽雨程赊，吴云驿远，别恨料应如我。先拚醉卧。任杨柳烟销，海棠月堕。明日江头，倩谁留画舸。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba79ec099ac11818d3','绮罗香·雨中舟次洹上','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"燕子梁深，秋千院冷，半湿垂杨烟缕。怯试春衫，长恨踏青期阻。梅子后、余润留寒，藕花外、嫩凉消暑。渐惊他、秋老梧桐，萧萧金井断蛩暮。\",\"薰篝须待被暖，催雪新词未稳，重寻笙谱。水阁云窗，总是惯曾听处。曾信有、客里关河，又怎禁、夜深风雨。一声声、滴在疏篷，做成情味苦。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba7ef85e45496c3539','解连环·留别临川诸友','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"夜来风色。叹青灯素被，早寒欺客。想寂寞、人在帘栊，望鸿雁欲来，又催刀尺。秋满关河，更谁倚、夕阳横笛。记题花赋月，此地与君，几度游历。\",\"江头楚枫渐赤。对离尊饮泪，难问消息。趁一舸、千里东归，眇天末乱山，水边孤驿。晼晚年华，怅回首、雨南云北。算今古、此情此恨，甚时尽得。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba804cc5f0fdc49caf','水龙吟·郑兰玉赋蜡梅，工甚，予拾其遗意补之','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"玉人栀貌堪怜，晓妆一洗铅华尽。此花应是，菊分颜色，梅分风韵。萼点驼酥，口攒金磬，心凝檀粉。甚女贞染就，仙女绝胜，蜂儿童，鹅儿嫩。\",\"说与玉龙莫品，怕宫波、一般流浪。故人堪寄，折枝代取，江南春信。沉水全熏，檗丝密缀，额黄深晕。乍燕姬未识，是花是蜡，笑偎人问。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba86b91202986af602','虞美人','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"千林白雪花间谱。价重黄金缕。尊前自听断肠词。正是江南风景落花时。\",\"红楼翠舫西湖路。好写新声去。为凭宫羽教歌儿。不道峰居才子鬓如丝。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba8788b08e16348160','沁园春·次韵李元之听董氏双弦','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"谁唤娇娆，斜插双弦，华筵乍开。爱玉纤轻轧，半笼翠袖，歌喉缓引，暗点鸳鞋。胡部新声，乐工巧制，写出龙沙马上哀。哀何似，似离鸾惊起，白雀飞来。\",\"丁宁击节金钗。要细听、春风且慢催。正宫商分犯，拽归双调，伊州入破，攧遍三台。画扇香收，罗巾汗湿，愁是云兜醉后回。花闲客，任鸾绡缠髻，更尽余杯。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba898fc40fc61f02a4','水调歌头·御河舟中','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"中夜正无寐，何处橹声来。河声不堪强聒，更听雁声哀。月色依依偏照，霜气萧萧渐紧，何似解离怀。明发吾无策，惟有快衔杯。\",\"过重阳，都未见，菊花开。遥知数丛篱下，破蕊映书斋。三十六陂烟水，二十四桥风月，天遣几时回。传语闲鸥鹭，相望莫惊猜。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba89be613a533b723a','水龙吟·傅渊道宅上赏紫牡丹','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"紫云何处飞来，仙家别有藏春洞。刻缯纹皱，镂檀色腻，薰脐香重。谷雨初晴，榆烟新换，楝风微动。是花姑养就，韩仙染出，还分与，人间种。\",\"好与密笼绣幄，护玉环、三生妖梦。只愁今夜，绿丛月老，珠房露冻。便唤秋娘，重浇卯酒，缓歌低送。倩红鸾与约，韵华且住，作尊前供。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba89beed267e26a8c5','清平乐·盛子昭花下欠伸美人图','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"阶前昼永。绕石芭蕉影。半亸云鬟慵不整。寂寞朝酲乍醒。\",\"湘裙翠被风流。背人无限娇羞。玉腕一双跳脱，欠伸浑是春愁。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba8ec6222e325b0a0e','鹊桥仙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"功名一饷。风波千丈。已与闲居认状。平生一步一崎岖，也趱到、盘山顶上。\",\"梅花解笑，青禽能唱。容我尊前疏放。从今甘老醉乡侯，算不是、麒麟画像。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba8ec6222eeb07aaca','鹊桥仙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"生朝戊子。今朝戊子。五十八年还是。头童齿豁可怜人，也召入、词林修史。\",\"前生偶尔。后生偶尔。但喜心头无事。从来不解学神仙，怎会得、长生不死。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba8f1903787edb359b','定风波·昆山路漕席上','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"舞袖歌鬟簇画堂。就中偏是展家娘。待道无情还有思。恰似。昆山日暖凤求凰。\",\"海上潮生人尽醉。催起。兰舟分散不成双。回首玄都春梦里。从此。桃花应自怨刘郎。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba90fd222216290f41','金缕词送上官子东之昆山州幕官','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"烟草长洲苑。渺姑苏、旧游麋鹿，岁华云晚。多少吴宫花月恨，春去春来不管。只付与、行人凄断。君去风流宾幕里，把今情、古意供裁剪。珠唾湿，玉烟暖。\",\"相逢尽看金杯满。信人生、好怀有几，梦长缘短。白发峥嵘三千丈，底用云揉雨染。且斗取、尊前强健。为问浮槎还到否，便乘之、直上三山远。看瀛岛，水清浅。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba927964384aac367c','多丽·为友生书所见','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"小庭阶。帘栊婀娜蓬莱。恨匆匆、归鸿度影，东风摇荡情怀。不多时、见他行过，霎儿后、依旧回来。银铤双鬟，玉丝头导，一尖生色合欢鞋。麝香粉、绣茸衫子，窄窄可身裁。偶回头、笑涡透脸，蝉影笼钗。忆疏狂、随车信马，那知沦落天涯。豆蔻初、可怜春早，菖蒲晚、难见花开。红叶波深，彩楼天远，浪凭青鸟信音乖。等闲是、这番迷眼，无处可安排。行云断、梦魂不到，空赋阳台。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba930efea4a8e1f56f','木兰花慢·题红犀扇面','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"记西湖送别，曾共绾，绿杨丝。怅水去云回，佳期杳渺，远梦参差。重来访邻寻里，爱卿卿、不减旧风姿。不着银筝清怨，难题纨扇相思。\",\"暗香销尽合欢枝。留在锦囊诗。又越北闽南，秋随雁影，花老莺儿。应缘采春情重，便鉴湖、春色恋微之。扶起晓窗残醉，潮平月落多时。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba95e617f32b4c9b09','菩萨蛮·赠雁','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"人随雁雁俱南去。雁应先到凭传语。若问锦书无。人归不得书。\",\"归期还信否。准在梅花后。烟树短长亭。只争三四程。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba961a311db172c5a5','花心动·剑浦有感','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"花信风寒，绮窗深、匆匆禁烟时节。燕子乍来，宿雨才晴，满树海棠如雪。黛眉准拟明朝画，灯花剪、妆奁双叠。负佳约、鹊还误报，燕应羞说。\",\"宝镜将圆又缺。从涩尽银簧，怕吹呜咽。一霎梦魂，也唤相逢，依黯断云残月。古来多少春闺怨，看薄命、无人如妾。软绡帕、凭谁寄将泪血。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba968bbddfec061e8f','踏莎行·江上送客','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"芳草平沙，斜阳远树。无情桃叶江头渡。醉来扶上木兰舟，将愁不去将人去。\",\"薄劣东风，夭邪落絮。明朝重觅吹笙路。碧云红雨小楼空，春光已到销魂处。\"]',NULL,NULL,'这是一首暮春送别词。上片写江头送别情景。扶醉登舟，人去之后，只有“愁”留了下来。下片写别后情景。落花飞絮，东风薄劣。春光将尽，人去楼空。全词凄婉缠绵，余意不尽。 ','[\"平沙：旷野。\",\"将：送。 \"]'),('c16655df05f199ba9b02ba6d6ac04b79','菩萨蛮','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"郎情秋后萧疏叶。妾心陌上悠扬蝶。何处望归鞍。春云山外山。\",\"梨花新月下。独自烧香罢。惟有梦相寻。惊鸟啼夜深。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba9b501991178913a7','浪淘沙令','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"醉胆望秋寒。星斗阑干。小窗人影月明间。客里不知归是梦，只在吴山。\",\"行路自来难。长铗休弹。黄尘到底涴儒冠。一片白鸥湖上水，闲了渔竿。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba9c70a6d84c496b2e','踏莎行','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"雨涧天寒，孤山雪后。美人空谷谁为友。香林有路玉烟深，瀛洲无梦朝云瘦。\",\"照影冰壶，含情翠袖。写生合在徐黄手。仙家花月镇长春，与君岁晚同三寿。\"]',NULL,NULL,NULL,NULL),('c16655df05f199ba9d1ade56d0284e69','木兰花慢·次韵陈见心文学孤山问梅','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"压西湖千树，曾几度，为携尊。向柳外停桡，苔边待鹤，酒熟诗温。瀛洲旧时月色，怅荒凉、惟有数枝存。天上梨花成梦，江南桃叶移根。\",\"如今憔悴客愁村。难返暗香魂。甚岁晚春迟，角寒笛晓，雪暗云昏。登临不甚寄目，但青山、隐隐月纷纷。再约与君同醉，从他啄木敲门。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baa2562017192f393b','疏影·王元章墨梅图','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"山阴赋客。怪几番睡起，窗影生白。缥缈仙姝，飞下瑶台，淡伫东风颜色。微霜恰护朦胧月，更漠漠、暝烟低隔。恨翠禽、啼处惊残，一夜梦云无迹。\",\"惟有龙煤解染，数枝入画里，如印溪碧。老树枯苔，玉晕冰圈，满幅寒香狼藉。墨池雪岭春长好，悄不管、小楼横笛。怕有人、误认真花，欲点晓来妆额。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baa49525402664c5ce','定风波·商角调西江客舍酒后闻梅花吹香满窗醒而赋此','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"恨行云、特地高寒，牢笼好梦不定。婉娩年华，凄凉客况，泥酒浑成病。画阑深，碧窗静。一树瑶花可怜影。低映。怕月明照见，青禽相并。\",\"素衾正冷。又寒香、枕上薰愁醒。甚银床霜冻，山童未起，谁汲墙阴井。玉笙残，锦书迥。应是多情道薄幸。争肯。便等闲孤负，西湖春兴。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baac8a4d39ed8b6b84','太常引','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"素娥风韵自天真。似回首、趁行云。眉黛任长颦。正宜看、啼妆未匀。\",\"扇犀轻按，袖罗低掩，一曲广陵春。飞尽画梁尘。曾数到、梨园几人。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baae454f3ddcd53096','水龙吟·赋情云','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"无心却恁多情，闲愁长向眉尖聚。牢龙不定，为谁留恋，为谁归去。半饷花阴，霎儿月暝，几番日暮。被东风搅散，离愁惹断，又还趁，歌声驻。\",\"只恐佩环卧冷，好重将、绣帷调护。何人得似，晓妆鬟髻，春娇态度。缥缈尊前，朦胧眼底，非烟非雾。把柔情一缕，都随好梦，作阳台雨。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baaeb4c37e735f090a','喜迁莺·琼花','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"东风吹尽。但一片绿阴，空留春恨。后土祠荒，飞琼谪久，还喜玉容堪认。二十四桥夜月，二十四番花信。便载酒，怕芳菲易老，阴晴难稳。\",\"娇困。羞起晚，伫立画阑，净洗闲脂粉。沉水浓熏，蜂黄淡染，自有绝尘香韵。也知世间无对，肯许浮花相近。凤箫远，待数枝折与，玉峰人问。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baaec2d75411f399af','水龙吟·西池败荷','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"水宫仙子归来，为谁独立西风背。凌波梦断，可怜零落，一奁环佩。雨叶敲寒，露房倒影，秋声惊碎。问西亭翠被，将愁何处，空留得，余香在。\",\"最爱双飞白鹭，镇相依、蓼边萍外。舞衫歌扇，有人绣出，水情云态。西子湖边，越娘舟上，忆曾同采。甚人今未老，花应依旧，约明年再。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baaf2824ae3737d3a5','摘红英春雨惜花','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"莺声寂。鸠声急。柳烟一片梨云湿。惊人困。教人恨。待到平明，海棠应尽。\",\"青无力。红无迹。残香剩粉那禁得。天难准。晴难稳。晚风又起，倚阑争忍。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bab77d90ad134c2eeb','百字令燕城晚望','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"碧天向晚，远云开疑是、江南山色。渺渺孤鸿残照外，独上高城望极。鸡散台空，萤沉苑废，龙去沟无迹。英雄安在，千秋恨血凝碧。\",\"我欲携酒重来，佛狸祠下，字暗苍苔石。社鼓神鸦浑不见，一片青青荠麦。夜月琼枝，春风水调，肯慰淹留客。翩然归去，天风扶下双舄。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bab7f16089883ffcb8','瑞龙吟','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"鳌溪路。潇洒翠壁丹崖，古藤高树。林间猿鸟欣然，故人隐在，溪山胜处。久延伫。浑似种桃源里，白云窗户。灯前素瑟清尊，开怀正好，连床夜语。应是山灵留客，雪飞风起，长松掀舞。谁道倦途相逢，倾盖如故。\",\"阳春一曲，总是关心句。何妨共、矶头把钓，梅边徐步。只恐匆匆去。故园梦里，长牵别绪。寂寞闲针缕。还念我、飘零江湖烟雨。断肠岁晚，客衣谁絮。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bab8932b26a20c9077','谒金门·寒食临川平塘道中','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"溪水漫。岸口小桥冲断。沽酒人家门巷短。柳阴旗一半。\",\"细雨鸣鸠相唤。曲港落花流满。两两睡红鸂鶒暖。恼人春不管。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baba64fbd15622f3db','点绛唇·舟行书见','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"风起云飞，兰舟竞入横塘住。恼人何处。隔岸花篱雾。\",\"一水盈盈，难送凌波步。空相觑。正如牛女。隔断银河路。\"]',NULL,NULL,NULL,NULL),('c16655df05f199babbdeb3add125b013','孤鸾题钱舜举仙女梅下吹笛图','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"江皋空阔。更半霎轻风，些儿微雪。倚树仙姬，翠袖暮寒应怯。闲拈玉龙自品，爱冰姿、与花争洁。一阕霓裳乍了，又落梅初叠。\",\"怕曲终人去彩云绝。便梦断瑶台春思愁结。□□□□、□□□□□□。那堪绿毛幺凤，向苔枝、数声啼咽。留得余香满袂，已西山斜月。\"]',NULL,NULL,NULL,NULL),('c16655df05f199babcc41a60022180f3','多丽·西湖','张翥','c16655df05f199ba4079016d6c1da886','元','这是一首即景赋咏、流连光景之作。开首几句，用淡淡的色彩勾勒湖上景色：暮霭沉沉，青山隐隐，四围树色幽深。晚烟把一切染上了青紫的色调，唯南边的南屏山峰上，犹存一抹斜阳，留下了几许明丽。这里作者运用了明暗对比的手法，画出了湖上的光景。然而江山永恒，人事代谢，当此暮色苍茫之际，“江晚正愁予”，不由得作者思发怀古之幽情，想起昔日苏州馆娃宫一带，当吴王夫差全盛时期，是何等的热闹。帝王嫔妃醉生梦死，但不久之后，西施出走，姑苏台上一片冷清清景象，野鹿出没。又想起当年汉武帝为求长生，铸造金人捧露盘，曾几何时，铜人被迁走，昔日繁华的汉苑荒芜冷落，流萤飞度，就拿眼前的杭州来说，当年林逋隐于孤山，妻梅子鹤，一时传为佳话，如今风流云散，人去山空，只剩下六桥烟柳，几座荒岛，几个废亭，冷清地伫立在晚风斜日中。作者怀古抚今，深深地感到世事如梦，扑朔迷离，不可捉摸。念天地之悠悠，望故乡而不见，湖海飘零，感慨万千，他只得浊酒一杯，以消愁肠。下片对上片而言，是时间的顺延，写西湖的夜景。这时苏堤上歌声散尽，人们携妓向西泠走去。荷花在夜气中呈暗红色，深碧的荷叶经过雨打平添了几分凉意。微风过处，菰蒲摆动，欲立蜻蜓不自由。世界是这么宁静。澄澄秋夜，年光偷换，唯有这欣欣向荣的荷花留驻着即将逝去的良辰美景。采菱人儿悠扬的歌声传来，那么婉转，那么动听。接着作者又将目光投向远处，但见水天一色，几点渔火一闪一闪，闪烁在沉沉的暗夜。不久，云破月来，流光徘徊，照耀人间，如有无限依恋之情。全词以船的行进作为视角的转换（此词原有题“西湖泛舟夕归，施成大席上，以‘晚青山’为起句，各赋一词”），因此词中写景多变，就像摄像机似的不断切换镜头，有时推近，有时拉远，将西湖秋日傍晚至晚间的景象一一映现出来。细微时，连蜻蜓弄风的细小画面也被放大特写；拉广角时，南屏一角夕阳、六桥烟柳、两三星火，都隐隐约约。但是词中夹入一段怀古文字，写得那么感伤，似与全词基调不大相称，有因文造意之嫌。','[\"晚山青，一川云树冥冥。正参差、烟凝紫翠，斜阳画出南屏。馆娃归、吴台游鹿，铜仙去、汉苑飞萤。怀古情多，凭高望极，且将尊酒慰飘零。自湖上、爱梅仙远，鹤梦几时醒。空留得、六桥疏柳，孤屿危亭。\",\"待苏堤、歌声散尽，更须携妓西泠。藕花深、雨凉翡翠，菰蒲软、风弄蜻蜓。澄碧生秋，闹红驻景，采菱新唱最堪听。见一片、水天无际，渔火两三星。多情月、为人留照，未过前汀。\"]',NULL,NULL,NULL,'[\"南屏：山名，在杭州南。\",\"馆娃：馆娃宫，春秋时吴王夫差为西施所筑宫馆，遗址在今江苏吴县西南灵岩山上。\",\"吴台：姑苏台，在今江苏吴县西南。其邻长洲苑，是围猎之所。\",\"铜仙句：汉武帝于神明台上作承露盘，立铜人舒掌接甘露，和玉屑饮用，以为可以长寿。三国魏明帝时，下诏迁承露铜人至洛阳。\",\"爱梅二句：宋初诗人林逋，居于杭州孤山，不入城市二十年，妻梅子鹤。\",\"六桥：杭州苏堤上六座桥。\",\"西泠（líng）：桥名。在杭州孤山。\",\"菰（ɡū）蒲（pú）：水生植物，俗称茭白。\"]'),('c16655df05f199bac049873af43b4718','最高楼为山村仇先生寿','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"方寸地，七十四年春。世事几浮云。躬行斋内蒲团稳，耆英社里酒杯频。日追游，时啸咏，任天真。\",\"喜女嫁男婚今已毕。便束帛安车那肯出。无一事，挂闲身。西湖鸥鹭长为侣，北山猿鹤莫移文。愿年年，汤饼会，乐情亲。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bac2ebbe3b849dab8a','鹧鸪天','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"乍学琵琶已断肠。锦绦银甲玉悬珰。春风琼树声逾稳，秋水芙蓉字亦香。\",\"微敛笑，浅句妆。何须重觅杜韦娘。休教月底清歌去，怕趁行云上凤凰。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bac2ebbe3ba2e92263','鹧鸪天','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"一曲吴歌酒半酣。声声字字是江南。书凭仙苑青鸾递，花助妆楼粉蝶衔。\",\"飞燕瘦，宝儿憨。已妍还慧更岩岩。无因剪得湘江水，与蘸春云作舞衫。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bac3e0f1c33a8727ac','摸鱼儿·赋湘云','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"问湘南、有云多少，不应长是为雨。平生宋玉缘情老，赢得鬓丝如许。歌又舞。更一曲琵琶，昵昵如私语。闲悲浪苦。怪旧日青衫，空流泪满，不解画眉妩。空凝伫。\",\"十二峰前路阻。相逢知在何处。今朝重见春风手，仍听旧弹金缕。君且住。怕望断、蘅皋日暮伤离绪。新声自谱。把江北江南，今愁往恨，尽入断肠句。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bac7c592b3b1dbfadb','感皇恩·题赵仲穆画凌波水仙图','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"湘水冷涵秋，行云平贴。时见惊鸿度萍末。雾鬟烟佩，微步一川凉月。软波擎不定，龙绡袜。\",\"楚楚绀莲，愔愔瑶瑟。照影明珰两清绝。汜人何处，起舞为谁轻别。数峰江上晚，和愁叠。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baccd5cd8aaa6b6e8b','齐天乐·临川夜饮滏阳李辅之寓所','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"江霜一树凄凉叶，惊鸟夜深啼落。客里相逢，尊前细数，几度雨漂风泊。微吟缓酌。渐月影斜欹，画阑东角。只怕梅花，无人看管瘦如削。\",\"江湖容易岁晚，想多情念我，归信曾约。尘土狂踪，山林旧隐，梦寄草堂猿鹤。离怀最恶。是酒醒香残，烛寒花薄。一段销凝，觉来无处着。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bacd6c7cf087067961','意难忘妓杨韵卿以善歌求赋','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"高韵天成。问当时爱爱，得似卿卿。江梅风致别，楚蕙雪香清。花旎旎，月盈盈。写不尽才情。把旧游，名讴试数，谁解新声。\",\"诗家只有杨琼。向吴姬丛里，转更分明。金闺春思怯，翠被暮寒生。人欲去，酒还醒。黯此际销凝。待剪将，江云数尺，与染丹青。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bacdbd12bc20463f20','摸鱼儿·题熊伯宜藏梅花卷子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"记西湖、水边曾见，查牙老树如此。冰痕冷沁苔枝雪，的皪数花才试。天也似。爱玉质、清高不久闲红紫。孤山处士。总赋得招魂，烟荒雨暗，寂寞抱香死。\",\"春风笔，休忆深宫旧事。添人多恨多思。墨池雪岭三生梦，唤起缟衣仙子。仍独自。伴瘦影、黄昏和月窥窗纸。声声字字。写不尽江南，闲愁万斛，诉与绿衣使。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bad8514caf0cb6773a','南乡子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"秋色照波明。夹岸芙蓉似锦城。罨画楼台红粉面，轻盈。未许黄徐写得成。\",\"一舸载杨琼。共醉花前玉笛声。犹记青鸾和月跨，三生。我是仙家石曼卿。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bad8514caf9d792025','南乡子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"野唱自凄凉。一曲孤鸿欲断肠。恰似竹枝哀怨处，潇湘。月冷云昏觅断行。\",\"离思楚天长。风闪青灯雨打窗。惊起小红楼上梦，悠扬。只在佳人锦瑟傍。\"]',NULL,NULL,NULL,NULL),('c16655df05f199badcea72a681532b54','八声甘州','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"向芙蓉湖上驻兰舟，凄凉胜游稀。但西泠桥外，北山堤畔，残柳依依。追忆莺花旧梦，回首冷烟霏。惟有盟鸥好，时傍人飞。\",\"听取红筵象板，尽歌回彩扇，舞换仙衣。正白萍风急，吹雨暗斜晖。空惆怅、离怀未展，更酒边、忍又送将归。江南客、此生心事，只在渔矶。\"]',NULL,NULL,NULL,NULL),('c16655df05f199badd7077d955ecfd61','破阵子·七夕戏咏','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"此夕天孙河鼓，多情呆女痴儿。鹊驾年年仍远渡，蛛合家家长巧丝。星期莫怨咨。\",\"迢递金钗私语，凄凉纨扇宫词。奔月姮娥催去路，行雨巫山空梦思。都无重会时。\"]',NULL,NULL,NULL,NULL),('c16655df05f199badf224813ce4a95e4','蝶恋花·柳絮','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"陌上垂杨吹絮罢。愁杀行人，又是春归也。点点飞来和泪洒。多情解逐章台马。\",\"瘦尽柔丝无一把。细叶青颦，闲却当时画。惆怅此情何处写。黄昏淡月疏帘下。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae04945ea6f175289','满江红·钱舜举桃花折枝','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"前度刘郎，重来访、玄都燕麦。回首地、暗香销尽，暮云低碧。啼鸟犹知人怅望，东风不管花狼籍。又凄凄、红雨夕阳中，空相忆。\",\"繁华梦，浑无迹。丹青笔，还留得。恍一枝常见，故园春色。尘世事多吾欲避，武陵路远谁能觅。但有山、可隐便须归，栽桃客。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae5902f435b39d642','苏武慢','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"岁晚江空，雪飞风起，老境若为聊赖。家人解事，准备深尊，旋遣夜窗寒解。萍梗孤踪，幻影浮生，万里喜还闽海。但囊中留得，诗篇烂写，水情山态。\",\"真比似、一个冥鸿，南来北去，阅尽几重关塞。名缰利锁，绊杀英雄，都付醉乡之外。惟不能忘，一舸吴淞，鲈脍豉羹莼菜。且今宵还我，冰壶天地，眼空尘界。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae78f81300ffae058','行香子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"扰扰阎浮。清浊同流。费精神、补喜填忧。岁云暮矣，卿可归休。有板支颐，书遮眼，被蒙头。\",\"蝼蚁王侯。华屋山丘。待他时、老去优游。筑间茅屋，买个黄牛。种芋成区，瓜作圃，稻盈畴。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae78f8130e073f55d','行香子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"水远天低。雪意垂垂。火炉头、煨芋燃萁。蒲团稳坐，纸帐低围。且放些慵，补些拙，学些痴。\",\"休惹群儿。唱起铜鞮。笑山翁、醉倒如泥。谁分蜾嬴，莫近鸱夷。把独醒人，沉醉者，两忘机。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae78f8130e86aa9df','行香子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"谢董糟丘。罢醉乡侯。更开除、从事青州。长瓶尽卧，大白休浮。本欲成欢，翻引病，不销愁。\",\"今日空喉。明日扶头。甚窦中、瓮下堪羞。客应嗔断，妇不须谋。指水为言，山作誓，有盟鸥。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae78f8130f292a514','行香子','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"传癖诗逋。野逸山臞。是幽人、平日称呼。过如饭袋，胜似钱愚。尽我为牛，人如虎，子非鱼。\",\"石铫风炉。雪碗冰壶。有清茶、可润肠枯。生涯何许，机事全疏。但伴牢愁，盘礴赢，鼓咙胡。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae820c827cac8dc9e','眉妩·七夕感事','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"又蛛分天巧，鹊误秋期，银汉会牛女。薄命犹如此，悲欢事，人间何限夫妇。此情更苦。怎似他、今夜相遇。素娥妒、不肯偏留照，渐凉影催曙。\",\"私语钗盟何处。但翠屏天远，清梦云去。纵有闲针缕，相怜爱、丝丝空缀愁绪。窃春伴侣。问甚时、重画眉妩。谩铅泪弹风，都付与洗车雨。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bae98d293ca60f7cf9','苏武慢对雪','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"冻雨跳空，朔云屯地，陡觉夜寒无赖。谁从蕊阙，宴罢群仙，一样佩零珠解。应唤冯夷，起舞回风，搅碎渺茫银海。倚南窗清思，盈襟看尽，整容斜态。\",\"君试问、白羽鸣弦，青貂束锦，千骑猎归烟塞。何如倦客，蜡屐枝筇，乘兴竹边梅外。随处堪寻，卖酒人家，春渚水乡挑菜。趁湖山晴晓，吟魂飞上，玉峰瑶界。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baea341c97261a98f5','春从天上来同王继学宪使赋','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"十里红楼。问声价如今，谁满扬州。白发书记，此日重游。听取席上名讴。拥冰弦斜伫，更为我、敛笑凝眸。觅黄骝。\",\"看端端怎比，楚楚风流。殷勤砑绫小草，写不尽宫妆，一段春柔。淡水疏花，知谁消受，几度帘卷香收。怕巫娥归去，空惆怅、梦断情留。把离愁。付行云行雨，楚尾吴头。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baea7ff99d28bfbfe3','春从天上来','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"袅袅秋风。听响彻云间，彩凤啼雄。嬴女飞下，玉佩玲珑。肠断十二台空。渺霜天如海，写不尽、楚客情浓。烛销红。\",\"更锵金振羽，变徵移宫。扬州旧时月色，叹水调如今，离唱谁工。露叶残蛾，蟾花遗粉，寂寞琼树香中。问坡仙何处，沧江上、鹤梦无踪。思难穷。把一襟幽怨，吹与鱼龙。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baeb1c9c8c5b0878a9','沁园春','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"何许登临，路绕芜城，冈连楚皋。爱流云低响，歌催琼树，微波照影，人艳仙桃。松院移尊，柳桥携袖，随处兰舟且暂捎。秋无际，望空江雁远，落木天高。\",\"不妨左手持螯。更右把、金尊送浊醪。叹鸡台草暗，凄然兴废，龙山烟冷，老矣英豪。白发宁饶，黄花任插，要里西风破帽牢。刘郎醉，把吴笺笑擘，试与题糕。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baeb1c9c8c796c687b','沁园春','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"客汝知乎，载酒轻舟，看花小车。胜炎州出使，瘴浮征旆，禁门待漏，霜满朝靴。岁去堂堂，老来冉冉，瓶雀飞时手怎遮。平生事，叹山林迹远，霄汉程赊。\",\"从渠梦蝶疑蛇。得放懒、还须自在些。甚天荒地老，铜台歌舞，水流云在，金谷豪奢。客问先生，归宜早计，醉后之言可信耶。鸥盟在，任渔蓑江上，雨细风斜。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baeb1c9c8ce541aab7','沁园春','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"天上玉堂，海外瀛洲，山中蜕岩。甚六十四岁，出持使节，八千余里，来驻征骖。香火缘深，功名意薄，梦觉仙家雪满簪。桐花社，喜酒边莺燕，诗外云岚。\",\"锦堂容我清酣。拥画烛、金钩手屡探。怪朗吟御史，笑回红粉，送归司马，泪湿青衫。蜀魄春多，塞鸿秋远，无限离情老不堪。空留意，在水光山色，江北江南。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baebaf08376d0d446b','清平乐·寄山居道人约看杏花','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"东风阵阵。第几番春信。呆李痴桃消息近。写取鸾笺试问。\",\"君家杨柳墙东。杏花初吐生红。好唤一床金雁，明朝来醉春风。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baed9d93f1e67e6924','临江仙·梁山舟中二首其二','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"羡杀渔家生处乐，隔湾数点青灯。蓑衣忘在石矶层。蓼花秋酿酒，枫树晚垂罾。\",\"时向瓦鸥篷底醉，往来鹭侣鸥朋。老翁倚棹坐瞢腾。有鱼吾欲买，摇手不能膺。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baf32f182e4bea9943','浣溪沙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"一点芳心两翠蛾。恼人离绪不胜多。尊前忍听渭城歌。\",\"花落鸟啼春去也，水长天远客愁何。此情何处托微波。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baf32f182e9e16832c','浣溪沙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"数载相看欲话难。酒边失口却成欢。空添别恨与眉端。\",\"流水有声传锦瑟，行云无梦赴青鸾。关河秋雨客窗寒。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baf32f182ec49c89e7','浣溪沙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"偶约尊前已目成。琵琶私语更分明。如今翻作断肠声。\",\"彩扇旧歌怜楚楚，青楼薄幸怨卿卿。海枯石烂古今情。\"]',NULL,NULL,NULL,NULL),('c16655df05f199baf32f182ef71d3d78','浣溪沙','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"珍重千金一诺同。小红楼上舞筵中。谁知别路太匆匆。\",\"愁杀二分无赖月，凭将万里有情风。为传消息宋家东。\"]',NULL,NULL,NULL,NULL),('c16655df05f199bafbd20036fe49bcab','清平乐·酒后二首','张翥','c16655df05f199ba4079016d6c1da886','元',NULL,'[\"先生醉矣。是事忘之矣。欲友古贤谁可矣。严子真其人矣。\",\"问渠辛苦征鞍。何如自在渔竿。终办一丘隐计、西湖鸥鹭平安。\"]',NULL,NULL,NULL,NULL),('c16655df0602bb8c617f15b951930b1d','和西湖竹枝词','同同','c16655df0602bb8cd1457b72d1457b72','元',NULL,'[\"西子湖头花满烟，谩郎日日醉湖边。青楼十丈钩帘坐，箫鼓声中看画船。\"]',NULL,NULL,NULL,NULL),('c16655df06363fe27af29cc1ddfe902e','虞美人·别鲁道源','何景福','c16655df06363fe2d1457b72d1457b72','元',NULL,'[\"三年奔走荒山道。喜说苕溪好。苕溪秋水漫悠悠。载将离恨上杭州。\",\"干戈未已身如寄。安乐知何处。青溪溪上钓鱼矶。纵使无鱼，还有蟹螯肥。\"]',NULL,NULL,NULL,NULL),('c16655df071bf040617f15b9f9d27ead','和西湖竹枝词','马稷','c16655df071bf040d1457b72d1457b72','元',NULL,'[\"与郎别久梦相思，不作西园蝴蝶飞。化作春深鶗鴂鸟，一声声是劝郎归。\"]',NULL,NULL,NULL,NULL),('c16655df0840c04a7ba7ded073b0151c','小桃红','盍志学','c16655df0840c04ad1457b72d1457b72','元',NULL,'[\"杏花开后不曾晴，败尽游人兴。红雪飞来满芳径。问春莺。\",\"春莺无语风方定。小蛮有情，夜凉人静，唱彻醉翁亭。\"]',NULL,NULL,NULL,NULL),('c16655df09720e2593f30947a40873ff','点绛唇·楼下清歌','李祁','c16655df09720e25b49c63fab49c63fa','元','上片首句以一个“清”字为全词感情上定下了幽清的基调。“水流歌断春风暮”，这句是说那流水般的一曲清歌，春风吹拂的暮霭中结束了。“春风暮”，景语，一字一景，词中以下诸景，皆缘此三字而来；这里也同时点出了这首词的特定节候，这正是一个怀人的季节，怀人的天气，怀人的时刻。“水流”，字面上自然是写“清歌”的缠绵婉转，由此，作者的笔触转入怀人。\n作者写怀人，非用泛泛之笔，而是借助于一个梦境，把怀人念远的思想情绪写得深刻入微。“梦云烟树，依约江南路”以及下片的“碧水黄沙”云云，皆是梦境，用笔上又极见层次。“梦云”、“依约”两句是入梦之境。“云”，是“梦云”，“树”是“树”是“烟树”，“江南路”是“依约”（由“云”而“树”而“路”，由飘忽而实，梦中寻找知音的足迹甚明。\n下片写梦中寻觅和对月怀人。“碧水黄沙”，紧承上片结句之意，进一步写对知音的寻觅。如果说上片“依约江南路”是朦胧中辨认知音去路的话，那么，“碧水黄沙”所表现的则是到处寻觅，水中陆上，无所不至，大有“上穷碧落下黄泉”的工夫了，且四字属对工稳，色彩鲜明，为本词的唯一亮色，这正是作者用笔变幻处。“梦到寻梅处”是穷尽“碧水黄沙”辗转寻找的结果，笔法由面到点，然后由“寻梅处”引出“花无数”，再由花而人，向花打听知音之所。\n这几句，用笔如剥茭，一步一层，层层转深，转愈深而情愈切，及至问花无语，寻觅无着，顿挫之下，不禁怅然若失，愁绪茫茫，不知所之，转见明月，也好像已随那人运去，而失去了它那固有的光辉。“明月随人去”一句所展示的空间既大且空，读之令人如置身于一个广漠而暗淡的世界，进而想到作者于此所寄寓的感情必然是悲凉而空虚的。此时的作者，是醒是梦，已难分难辨之际，这真是以景传情的神来之笔。','[\"楼下清歌，水流歌断春风暮。\",\"梦云烟树，依约江南路。\",\"碧水黄沙，梦到寻梅处。\",\"花无数。问花无语。明月随人去。\"]',NULL,NULL,'《点绛唇·楼下清歌》，北宋李祁词作，词曰：“楼下清歌，水流歌断春风暮。梦云烟树。依约江南路。碧水黄沙，梦到寻梅处。花无数。问花无语。明月随人去。”此词明写春景，暗抒离情。','[\"断：终了\",\"水流：即流水，暗寓知音。\",\"依约：隐约、朦胧的，极是迷离惝恍的梦境。\"]'),('c16655df09f6d4d979ffa211d53e894b','意难忘','宋远','c16655df09f6d4d9d1457b72d1457b72','元',NULL,'[\"鸡犬云中。笑种桃道士，虚费春风。山城看过雁，春水梦为龙。云上下，燕西东。久别各相逢。向夜深，江声浦树，灯影渔篷。\",\"旧游新恨重重。便十分谈笑，一样飘蓬。元经摧意气，丹鼎赚英雄。年未老，世无穷。春事苦匆匆。更与谁，题诗药市，沽酒新丰。\"]',NULL,NULL,NULL,NULL),('c16655df0a6dca67771edcecde1b5509','浣溪沙·上巳','洪翼','c16655df0a6dca67d1457b72d1457b72','元',NULL,'[\"软翠冠儿簇海棠。砑罗衫子绣丁香。闲来芳径踏青阳。\",\"风暖有人能作伴，日长无事可思量。水流花落任匆忙。\"]',NULL,NULL,NULL,NULL),('c16655df0a6dca678a947f0993f860c3','浣溪沙·花朝','洪翼','c16655df0a6dca67d1457b72d1457b72','元',NULL,'[\"鸾股先寻斗草钗。凤头新绣踏青鞋。衣裳宫样不须裁。\",\"雕玉镂成鹦鹉架，泥金镌就牡丹牌。明朝相约看花来。\"]',NULL,NULL,NULL,NULL),('c16655df0a840fb5617f15b90346d564','和西湖竹枝词','陈聚','c16655df0a840fb5d1457b72d1457b72','元',NULL,'[\"茜红裙子柳黄衣，花间采莲人不知。唱歌荡桨过湖去，荷叶荷花风乱吹。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d912dc39104d68d994','木兰花慢·寿砀山蒋令','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"孟春才过了，看两叶，坼尧蓂。正杏蕾包红，梅花谢白，杨柳回青。东风此时须记，是当年、河岳降英灵。刀笔名香禁署，丝纶宠佩天庭。\",\"夜来南极老人星。光彩照前厅。见厅上高人，鸣琴清坐，阖境安宁。因来捧杯添寿，指庄椿、直到八千龄。更有无垠福禄，汪洋别比沧溟。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d91368c91401052527','临江仙','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"梦里还家喧笑语，觉来春色他乡。潇潇风雨入离肠。有诗还洒壁，无酒不传觞。\",\"试问凌烟辛苦地，争如畎亩南阳。谁教山鬼伯龙旁。人间无限事，歌罢倚绳床。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d91e63f075c7b22a42','木兰花慢·寿陈公望','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"问东原豪杰，先屈指，到元龙。看潇洒风标，汪洋学海，磊落词锋。沂川几回来往，把乾坤、歌入舞雩风。妙理鸢飞鱼跃，尘心雪化冰融。\",\"梅花香外岁寒松。高节谢春容。有千岁神膏，扶君寿质，阅世无穷。仍呼九天鸾鹤，与王乔、相约叩琼宫。但使飞霞佩在，宁愁白玉堂空。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d91f2a47797bb1afe6','水龙吟·中秋和人韵','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"西风万卷堂空，卧听箫鼓谁家宴。多情惟有，碧霄明月，肯来相见。因记当年，南楼老子，座前宾满。把清谈当却，弹丝吹管。谁更问，霓裳按。\",\"梦里仙游惊断。怅天涯、故人难面。空留玉斧，修轮斫桂，又成衰晚。水调歌残，壮心都付，一声长叹。对清光不寐，呼儿取酒，不妨重暖。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d92382af34903f7d04','临江仙·寿张都运父','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"瑞气充闾朝不散，欢声浮动庭槐。九天邀下寿星来。歌翻白云调，酒泛紫霞杯。\",\"八十康疆谁得似，仙风摆落尘埃。斑衣人是栋梁材。他时黄阁上，同看碧桃开。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d923ac60f4d82cf364','大江东去离豫章舟泊吴城山下作','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"南游三载，只江山、不负中原诗客。万里行装无别物，满意风云泉石。牛斗星边，灵槎缥缈，鬓影银河湿。哀歌谁和，剑光摇动空碧。\",\"回首帝子长洲，洪崖仙去，风雨鱼龙泣。海外三山何处是，黄鹤归飞无力。天下佳人，袖中瑶草，日暮空相忆。乾坤遗恨，月明吹入长笛。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d967c5d2444ff2db00','临江仙·寿李长卿','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"十月小春天气好，尧蓂五叶初开。长庚曾记梦中来。襟灵元洒落，风骨不尘埃。\",\"瑞霭云生香满室，安排北海尊罍。仙人来献紫霞杯。共添山海寿，东阁胜蓬莱。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d973f485362a6353ab','水调歌头','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"鲸川四重午，岁月若飘风。诗书万卷何事，白首课儿童。试把楚词高咏，更取清尊细酌，醒醉竟谁同。俯仰百年了，求足不求丰。\",\"愧吾身，犹未脱，世尘中。还丹有诀谁悟，回首忆仙翁。我欲乘云归去，独与山灵晤语，修道倘成功。笑谢醯鸡瓮，白日看长空。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d97a8cd44cdecf5dbd','木兰花慢·扬州寿纪子周','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"皇天忧世难，生俊乂，佐时来。有忠孝诚心，文章大手，经济雄才。他年汉庭三策，要青云高步出尘埃。儿女徒惊气岸，丹青莫状灵台。\",\"诗人清骨即仙才。何处觅蓬莱。便唤取何郎，一尊同醉，东阁官梅。充闾郁葱佳气，挽春风、都入紫霞杯。休道寿君无物，滔滔万里江淮。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d983bcb8241cd85842','木兰花慢·听姜惠甫摘阮','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"想高情千古，谁得似、仲容贤。把山海遗音，写归玄璧，妙绝当年。清风竹林人去，被浮哇、迷却性中天。不有黄台公子，宁闻清庙朱弦。\",\"博山香底坐臞仙。幽兴想飘然。笑袅袅繁声，三生儿女，恩怨流连。回头月明千里，正松风、岩壑和流泉。座上神游八表，知音不在言传。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d989f5ce3d229215dc','大江东去登鲸川楼','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"飞楼缥缈，疑行云、势压鲸川雄杰。宾主落成登眺日，正是炎蒸时节。把酒临风，凭阑一笑，忘尽人间热。四围烟树，万家金碧重叠。\",\"休问去棹来帆，南商北旅，欢会并离别。且向尊前呼翠袖，歌取阳春白雪。千古兴亡，百年哀乐，天远孤鸿减。酒阑人散，角声吹上明月。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d99f80deccbecef183','踏莎行·雪中看梅花','王旭','c16655df0a96e3d9d1457b72d1457b72','元','这是一首咏物之词，运用了托物言志的手法。词一开篇，就是指点江山、直抒胸臆。与众不同的是，他不是只咏雪或只咏梅，而是花开两朵、两朵俱美。一会是一个平台上的比较，一会是各自舞台上的辉煌；既各有高低，又各有所长。从篇章角度分析，上下阕的结构是一致的：一二句，先立主旨、总领全段；三四五句，以鲜明意象，比较二者异同；把这异同的典型特征，完全袒露在作者笔下、袒露在读者眼前；其中的对比手法，恰好也照应了主旨。这样的结构意图，就是抓住雪与梅的同与不同：上阕的“全似”，下阕的“不似”，这就全面的比较了两种风流。　　从段落到全篇，这首词大体可以分出起承转合的结构特征。起：两种风流，一家制作。承：雪花全似梅花萼。细看不是雪无香，天风吹得香零落。转：虽是一般，惟高一着。合：雪花不似梅花薄。梅花散彩向空山，雪花随意穿帘幕。　　具体看来，梅雪争春，同样风流，这就是不可避免的梅与雪的不同。不过，异中寻同，词人偏要说“雪花全似梅花萼”。这里，雪花与梅花同在雪花仿佛就是梅花的花瓣。是啊，梅花的美丽，在于不仅有形，而且有香。雪花有梅之形，于是就输了一段香　　词人的智慧，就在于丰富的想象力。风流，像风一样流芳天下，因而是世间万物的最佳典范。作者在梅与雪的映衬中，明知雪花有弱点，但却把她的弱点，放在险恶的处境中，想象为是狂风夺去了雪花能与梅花媲美的资格，这是极其高明的想象力，正是这样的想象，既说明了“全似”中的真实距离，也说明了这确实情有可原。这既是替雪花找托词，又是进一步展示了梅花的风采。　　这基础上，作者归纳说，雪梅的风流看似一样，但梅花其实更高一着。前面是雪花与梅花异中之同，这里是二者终有高下、毕竟不同的现实状态。“雪花不似梅花薄”，一个“薄”字，意味深长。按古代汉语，薄通泊，淡薄即淡泊，这是一解；另一解则是薄的本义，薄即草木丛生。是啊，雪是水气的精华，而梅花是草木的精华、百花的精华、生命的精华，不愧是花中之花。所以，雪花的“不似”，表现了略逊一筹。是啊，梅花把自己的生命、把生命的色彩，装点向草木凋零、万花俱谢的雪山；雪花虽无生命，却依然善解人意的飞向人们的身边。　　这就是词人最重要的比较。两种美丽的花朵，一是梅花，她挑战着寒冷的世界，以自身的豪气，呼唤着天地中生命的色彩、呼唤着天地中美丽的春天，也呼唤着天地中美丽的百花；另一是雪花，她把单调的冬天变成飞花溅玉的世界，这就是异中有同。那雪与梅各展所长，只要都是一流，又何必在意第一第二呢。这就是美丽的雪花，不卑不亢，绝无对梅花的嫉妒，只有与梅花的互补。即使不得第一，绝不与第一对立。这就是雪花，甘做背景、甘做配角，恬淡中显示雪花的美丽。　　可见，词中梅雪并举，映衬之妙、拟人之巧、想象之高，就让梅与雪刚柔相济、共迎春光。其中那个“薄”字，引出了太多的争议：其一，薄字取贬义时，即意指雪花比梅花更薄一点。其二，用作褒义时，作为和严冬战斗的宠儿，怎么能比雪花更厚呢？然而词人不会取“薄”字的贬义，因为那与词人笔下的“风流”形象太不相称。用一个不恰当的比喻，武松打虎，不能是武松打猫，贬低斗严冬的“战友”，实际就是贬低自己。因此薄字在此词为第二种说法，这也就是把“薄”理解成褒义的最好依据。','[\"两种风流，一家制作。雪花全似梅花萼。细看不是雪无香，天风吹得香零落。\",\"虽是一般，惟高一着。雪花不似梅花薄。梅花散彩向空山，雪花随意穿帘幕。\"]','[\"厦门大学教授黄拔荆说：这首词是抑雪扬梅，通过雪花与梅花的对比，着意歌颂梅花的品格。\",\"中国社会科学院研究生院教授王学泰说：\\u201C雪与梅花相同之点是颜色、形状和俱在寒天开放，但梅花毕竟不如雪花那样耐寒。这首词虽然题作《雪中看梅花》实际上是\\u2018梅花零落之时看雪花\\u2019。天风吹得梅花四处飘落，它的惟一的优势\\u2014\\u2014香，也零落殆尽，而雪花比梅花更厚实、更能随意飞舞、傲啸于天地之间。\\u201D\"]',NULL,'《踏莎行·雪中看梅花》是元代词人王旭所写的一首咏物词，运用了托物言志的手法。词一开篇，就是指点江山、直抒胸臆。与众不同的是，他不是只咏雪或只咏梅，而是花开两朵、两朵俱美。','[\"风流：风度、标格。\",\"一家制作：意指雪和梅都是大自然的产物。\",\"萼：花萼，这里指的是花瓣（因押韵关系用\\u201C萼\\u201D字）。\",\"细看不是雪无香：仔细一看，不是雪花，因为雪花没有香气。\",\"着（zhāo）：等次。\",\"薄（báo）：厚度小的。\",\"散彩：放射出光彩。\",\"帘：窗帷。最后两句说：梅花开在空山，放射出光辉异形，雪花却在人家帘幕下低飞。\"]'),('c16655df0a96e3d9aac7e1d9e50cf613','临江仙·为宋太守寿','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"莫怪今年秋事晚，黄花不在重阳。天公留泛九霞觞。故教争十日，风露寿华堂。\",\"豪杰如公谁得似，平生义胆刚肠。功名回首付诸郎。灵椿长不老，桑梓有余光。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9b8354d457d2d5e0b','木兰花慢·扬州寿瓜尔夹士常','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"醉西湖寿酒，歌旧曲，已三年。喜万里湖山，归来相见，淮海楼边。春风绣衣无恙，唤竹西歌吹共留连。世事浮云千变，灵台孤月长圆。\",\"一官聊办买书钱。行橐故萧然。有梦里青山，词中白云，徽外鸣弦。悠悠紫台归路，乐因循、诗酒堕凡缘。借问蓬莱官府，何如平地神仙。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9c6d9d307cd53123f','大江东去寿高伯川','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"月临南吕，正庭阶、开到尧蓂三叶。风露清明钟气质，诞作人间豪杰。器宇宏深，襟灵阔远，物我俱融彻。滔滔欲海，回头一棹超越。\",\"试看南墅开尊，西池垂钓，此个情怀别。记得婆娑亭上酒，还到寿君时节。我梦登天，群仙相对，曾写长生牒。见君姓字，分明非是虚说。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9c7cbaced435fb293','木兰花慢·寿泰安石监州','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"泰山雄胜地，人物出，必豪英。看衍庆堂中，使君才气，磊落高明。春风又临初度，正梅花、香满腊嘉平。唤取茅仙送酒，尊前共祝长生。\",\"青云居第筑初成。燕雀亦欢声。伫梦叶熊罴，祥占弧矢，兰玉春荣。山城岂能淹滞，佩飞霞、终上紫霄行。留着兰轩老笔，他年歌颂功名。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9cb747e7c2a96b05f','大江东去为张可子郎中寿','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"九秋风露，洗尘埃、人似华峰独立。兰省东南经济地，正赖风流筹画。湖海胸襟，冰霜气节，邈矣公难及。苍生望重，故园休梦泉石。\",\"且对得意江山，登临一笑，香满黄花席。更把西湖都酿酒，醉取白云诗客。乘兴为公，遍游仙府，检下长生籍。妙毫浓墨，书公寿禄无极。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9dcc651b05f18f435','临江仙·为子周兄寿','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"童稚相看今白首，情因儿女尤深。霜天昨夜卷层阴。舟中明月照，华屋寿星临。\",\"气敛风云归寂寞，谁知经济雄心。南阳烟雨卧龙吟。行藏安所遇，有酒且同斟。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9f4a18be024639ed5','水调歌头·端午','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"漱齿汲寒井，理发趁凉风。先生畏暑晨起，笑语听儿童。说道今年重午，节物随宜稍具，还与去年同。己喜酒尊冽，更觉粽盘丰。\",\"愿人生，常醉饱，百年中。独醒竟复何事，憔悴佩兰翁。我有青青好艾，收蓄已经三载，疗病不无功。从此更多采，莫遣药囊空。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9f5cf19e035eadfa8','临江仙·寿高伯川','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"蓂荚生三秋八月，气漙风露清明。诞来人世作豪英。性天元广大，心地尽宽平。\",\"书院兴秋谁得似，燕山窦氏齐名。一身长向善中行。松筠同不老，龟鹤共长生。\"]',NULL,NULL,NULL,NULL),('c16655df0a96e3d9fca9a820f80b21c4','水调歌头·和张都运李氏柳塘赏荷韵','王旭','c16655df0a96e3d9d1457b72d1457b72','元',NULL,'[\"我爱此塘好，碧水映红蕖。垂杨袅袅烟笼，绿发倩风梳。医却尘埃俗病，唤起沧浪幽兴，怀抱一时舒。更把直钩钓，得意不须鱼。\",\"忆当年，吟北渚，醉西湖。兰舟撑入云锦，未必画中如。世事春风一梦，回首欢游安在，烟海隔蓬壶。清赏有今日，题壁记来初。\"]',NULL,NULL,NULL,NULL),('c16655df0c118c3f1368c914aeec683d','临江仙','谢醉庵','c16655df0c118c3fd1457b72d1457b72','元',NULL,'[\"淮海东南佳丽地，古今画品诗题。羡君去意拂晴霓。腰钱期跨鹤，舞剑岂闻鸡。\",\"自笑病来成老懒，飞沉杳隔云泥。他时相忆此分携。月明归雁过，花落子规啼。\"]',NULL,NULL,NULL,NULL),('c16655df0c118c3f1368c914df989094','临江仙','谢醉庵','c16655df0c118c3fd1457b72d1457b72','元',NULL,'[\"白发壮心还未减，春风梦绕扬州。青山隐隐水悠悠。征帆从荡漾，行李亦风流。\",\"向日侍郎今右相，元龙豪气横秋。月明千里镇淮楼。依然青眼旧，应不负依刘。\"]',NULL,NULL,NULL,NULL),('c16655df0c118c3fc2ebbe3bac4de82c','鹧鸪天','谢醉庵','c16655df0c118c3fd1457b72d1457b72','元',NULL,'[\"睡思才消赖有茶。老怀刚慰奈无花。花随流水三春尽，柳疑东风一向斜。\",\"怜病久，怯春多。莫云庭院噪归鸦。碧云草就关心句，信到吟诗解叹嗟。\"]',NULL,NULL,NULL,NULL),('c16655df0c118c3ff32f182ed96ca63e','浣溪沙','谢醉庵','c16655df0c118c3fd1457b72d1457b72','元',NULL,'[\"沉屑微熏睡鸭金。朱弦还解解芳心。盈盈桃李未春深。\",\"天上鸾胶须着意，人间凤曲有知音。莫教风雨绿成阴。\"]',NULL,NULL,NULL,NULL),('c16655df0c8822026c832f1d4dd203ac','好事近·次蔡丞相韵','元德明','c16655df0c882202d1457b72d1457b72','元',NULL,'[\"梦破打门声，有客袖携团月。唤起玉川高兴，煮松檐晴雪。\",\"蓬莱千古一清风，人境两超绝。觉我胸中黄卷，被春云香彻。\"]',NULL,NULL,NULL,NULL),('c16655df0d219f63f22e8d7ac7ffefd2','西湖竹枝词','李介石','c16655df0d219f63d1457b72d1457b72','元',NULL,'[\"春晖堂上挽郎衣，别郎问郎何处归？黄金台高倘回首，南高峰顶白云飞。\"]',NULL,NULL,NULL,NULL),('c16655df0e35ef1b2a99a30b18d86e31','朝中措·游灵源山','冀国公主','c16655df0e35ef1bd1457b72d1457b72','元',NULL,'[\"倦游踪迹查无凭。寥落过山城。客馆潇潇夜雨，披襟凤烛青荧。\",\"追思往事，十年一梦，堪笑堪惊。冉冉隙驹光景，依依嚼蜡心情。\"]',NULL,NULL,NULL,NULL),('c16655df0e35ef1bc03640c147c5fd73','蓦山溪·游灵源山','冀国公主','c16655df0e35ef1bd1457b72d1457b72','元',NULL,'[\"楚峰远眺，山岚凝芳树。禾黍正离离，立西风、云烟向暮。青松瑟瑟，风送短笛声，寒溪畔，翠微间，依约人间住。\",\"灵源待月，襟袖凉微度。把酒问青天，广寒宫，知何处。中元吟赏好，预作中秋归兴逸，欲乘鸾，竟得神仙趣。\"]',NULL,NULL,NULL,NULL),('c16655df0eb55077617f15b9bd5a51b3','和西湖竹枝词','张师贤','c16655df0eb55077d1457b72d1457b72','元',NULL,'[\"孤山脚下路三叉，孤山墓上好梅花。不似马塍桃李树，随春供送别人家。\"]',NULL,NULL,NULL,NULL),('c16655df10bd80c00a4cff894312c8ae','雨霖铃','宋德方','c16655df10bd80c00e095e05e6d8545d','元',NULL,'[\"高山流水。叹知音者，世间能几。终南万里，烟霞归去也，岁云暮矣。拄杖药炉经卷，除此外、有何行李。乐恬淡、清静家风，一片灵台莹如洗。\",\"就中妙处因师指。下工夫、战退无常鬼。匣藏三尺神剑，霹雳响、火龙飞起。天下升平无事，白云间、笑傲而已。名利客，不信长生，奔走红尘里。\"]',NULL,NULL,NULL,NULL),('c16655df10bd80c00a4cff896391286f','雨霖铃','宋德方','c16655df10bd80c00e095e05e6d8545d','元',NULL,'[\"金莲七朵。自甘河等闲参破。丘刘谭马，孙王郝太古，许来同坐。两个披毡真侣，更漏泄、祖师因果。绽玉蕊、万朵齐芳，香满人间瑞烟锁。\",\"天元甲子休空过。正群仙出世交相贺。全真户牖深奥，端的处、要忘人我。枝派后分十九，住丹台、姓字名播。功与行，但欠丝毫，上界未容我。\"]',NULL,NULL,NULL,NULL),('c16655df11e01ee873f4853691f773c8','水调歌头','徐逊','c16655df11e01ee8d1457b72d1457b72','元',NULL,'[\"盘马万山顶，弹禽群木中。追思年少行乐，减尽旧豪雄。三十六峰青矗，三百六滩碧逝，归路杳重重。拱手问仙伯，谪宦岂天公。\",\"奉慈亲，携稚子，访琳宫。太虚点雪高处，万虑一杯融。但得挈家归隐，何用举家拔宅，鸡犬亦升空。长啸下山去，脚底响松风。\"]',NULL,NULL,NULL,NULL),('c16655df1204c47dcd903d02332bac62','和西湖竹枝词三首','康瑞','c16655df1204c47dd1457b72d1457b72','元',NULL,'[\"燕子来春雁来秋，曾见钱王衣锦游。英雄漫说八百里，只管东西十四州。\"]',NULL,NULL,NULL,NULL),('c16655df1204c47dcd903d0288875d99','和西湖竹枝词三首','康瑞','c16655df1204c47dd1457b72d1457b72','元',NULL,'[\"狮子峰头插将旗，凤凰山下草离离。三宫去后宫门闭，恰似钱王献土时。\"]',NULL,NULL,NULL,NULL),('c16655df1204c47dcd903d02c0f3f8de','和西湖竹枝词三首','康瑞','c16655df1204c47dd1457b72d1457b72','元',NULL,'[\"大船捶鼓银酒缸，小船吹笛红绣窗。鸳鸯触棹忽惊散，荷花深处又成双。\"]',NULL,NULL,NULL,NULL),('c16655df12de3e2c4611e154a2c386f2','渔父','吴瓘','c16655df12de3e2cd1457b72d1457b72','元',NULL,'[\"波平如砥小舟轻。托得纶竿寄此身。\",\"忘世恋，乐平生。不识公侯有姓名。\"]',NULL,NULL,NULL,NULL),('c16655df12de3e2c4611e154a9ec93fc','渔父','吴瓘','c16655df12de3e2cd1457b72d1457b72','元',NULL,'[\"野色山光水接天。云烟缥缈思长川。\",\"收此景，老梅仙。万顷湘江笔底传。\"]',NULL,NULL,NULL,NULL),('c16655df12de3e2cece5e199af4f6a3e','柳梢青','吴瓘','c16655df12de3e2cd1457b72d1457b72','元',NULL,'[\"墙角孤根，株身纤小，娇羞无力。蟹眼微红，粉容未露，不禁春色。\",\"待东君汨没芳姿，渐迤逦、檀心半坼。缓步回廊，黄昏淡月，那时相得。\"]',NULL,NULL,NULL,NULL),('c16655df133d91db617f15b9ff4e529b','和西湖竹枝词','高克礼','c16655df133d91dbd1457b72d1457b72','元',NULL,'[\"第四桥头第一湾，看鱼直上玉泉山。大鱼已逐龙飞去，留得当年旧赐环。\"]',NULL,NULL,NULL,NULL),('c16655df13e338d01ce2ab6becfc8394','西湖竹枝歌其一','宋元禧','c16655df13e338d0d1457b72d1457b72','元',NULL,'[\"十三女儿不出门，父娘坟在葛岭根。同携女伴踏青去，不上道傍苏小坟。\"]',NULL,NULL,NULL,NULL),('c16655df13e338d0617f15b95f12ecd6','和西湖竹枝词','宋元禧','c16655df13e338d0d1457b72d1457b72','元',NULL,'[\"湖光照侬双画眉，鬓边照见一茎丝。东家女伴多年别，昨日携来十岁儿。\"]',NULL,NULL,NULL,NULL),('c16655df13e338d0617f15b98da605e6','和西湖竹枝词','宋元禧','c16655df13e338d0d1457b72d1457b72','元',NULL,'[\"湖上采薪春复春，养蚕长见茧丝新。老蚕不识人间事，犹趁东风了此身。\"]',NULL,NULL,NULL,NULL),('c16655df13e338d0617f15b9c6a90639','和西湖竹枝词','宋元禧','c16655df13e338d0d1457b72d1457b72','元',NULL,'[\"十三女郎不出门，父娘墓在葛山根。同携女伴踏青去，不上道旁苏小坟。\"]',NULL,NULL,NULL,NULL),('c16655df13ef7851766b4378a2b5d224','十六字令','周玉晨','c16655df13ef7851d1457b72d1457b72','元',NULL,'[\"眠。月影穿窗白玉钱。无人弄，移过枕函边。\"]',NULL,NULL,NULL,NULL),('c16655df140c7a3a7b9daed765d4badc','和王左司柳枝词二首','马祖常','c16655df140c7a3a9956656405311655','元',NULL,'[\"郎君巧歌《杨柳枝》，柳眉初出学月支。隋堤千树烟光暮，不如柳眉初出时。\"]',NULL,NULL,NULL,NULL),('c16655df140c7a3a7b9daed7793b80ac','和王左司柳枝词二首','马祖常','c16655df140c7a3a9956656405311655','元',NULL,'[\"凤城三月草色青，池塘飞絮相飘零。风吹宛转低扑人，长空白日流榆星。\"]',NULL,NULL,NULL,NULL),('c16655df140c7a3a835274fc15fbc287','和王左司竹枝词二首','马祖常','c16655df140c7a3a9956656405311655','元',NULL,'[\"金炉宝熏留篆云，花间百舌鸣早春。五坊戏马赛争道，传声催赐十流银。\"]',NULL,NULL,NULL,NULL),('c16655df140c7a3a835274fc7cf61dec','和王左司竹枝词二首','马祖常','c16655df140c7a3a9956656405311655','元',NULL,'[\"日边宝书开紫泥，内臣珠帽辇步齐。君王视朝天未旦，铜龙漏转鸡人啼。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748111831518f95ecec','八声甘州·和孔瞻怀信国公，因念亦周弟','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"是去年、春草又凄凄，尘生缕金衣。怅朱颜为土，白杨堪柱，燕子谁依。谩说漫漫六合，无地著相思。辽鹤归来后，城亦全非。\",\"更有延平一剑，向风雷半夜，何处寻伊。怪天天何物，堪作玉弹棋。到年年、无肠堪断，向清明、独自掩荆扉。何况又、禽声杜宇，花事酴醾。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748132c9b442d1f3324','阮郎归·其二惜春用前韵','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"舞红一架欲生衣。残英辞旧枝。雨声自唱惜春词。行人应未知。\",\"新火后，薄罗时。君归何太迟。镜中失却少年姿。年随花共飞。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a7481ca7d3964e7405bd','听儿童歌船作竹枝助之','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"田舍儿童走似獐，踏歌椎鼓斗颠狂。元宵乞汝闲几日，元宵过后种田忙。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748296ce1335be8ceef','疏影','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"寒泉溅雪，有环佩隐隐，飞度霜月。易水风寒，壮士悲歌，关山万里离别。杨花浩荡晴空转，又化作、云鸿霜鹘。耿石壕，夜久无言寂历，如闻幽咽。\",\"云谷山人老矣，江空又岁晚，相对愁绝。玉立长身，自是胎仙，舞我黄庭三叠。人间只惯丁当字，妙处在、一声清拙。待明朝、试拂菱花，老我一簪华发。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a74841cc018a70c67720','莺啼序·其二有感','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"秋风又吹华发，怪流光暗度。最可恨、木落山空，故国芳草何处。看前古、兴亡堕泪，谁知历历今如古。听吴儿唱彻，庭花又翻新谱。肠断江南，庾信最苦，有何人共赋。天又远，云海茫茫，鳞鸿似梦无据。怨东风、不如人意，珠履散、宝钗何许。想故人、月下沉吟，此时难诉。吾生已矣，如此江山，又何怀故宇。不恨赋归迟，归计大误。\",\"当时只合云龙，飘飘平楚。男儿死耳，嘤嘤昵昵，丁宁卖履分香事，又何如、化作胥潮去。东君岂是无能，成败归来，手种瓜圃。膏残夜久，月落山寒，相对耿无语。恨前此、燕丹计早，荆庆才疏，易水衣冠，总成尘土。斗鸡走狗，呼卢蹴鞠，平生把臂江湖旧，约何时、共话连床雨。王孙招不归来，自采黄花，醉扶山路。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748552373101ae30de4','侧犯·夜饮海棠下','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"恨花开尽，夜深自敛胭脂颗。雨过。绕曲曲花蓬锦围裹。浮空烧蜜炬，香雾霏霏堕。无那。\",\"倚滴滴娇红笑相亸。歌俦饮伴，花底围春坐。念满眼、少年人，谁更老于我。岁岁花时，洞门无锁。莫负东君，酒盟诗课。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a7485d736b23504cf93b','大酺感春','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"正宝香残，重帘静，飞鸟时惊花铎。沉思前梦去，有当时老泪，欲弹还阁。\",\"太一宫墙，菩提寺路，谁管纷纷开落。心情浑何似，似琵琶马上，晓寒沙漠。\",\"想筝雁频移，钏金度瘦，素肌清削。\",\"相思无奈著。重访旧、谁遣车生角。暗记省、刘郎前度，杜牧三生，为何人、顿乖芳约。\",\"试把菱花拭，愁来处、鬓丝先觉。念幽独、成荒索。何日重见，错拟扬州骑鹤，绿阴不妨细酌。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a74883cfc9c26fa27cf4','竹枝词','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"江南女儿善踏歌，桑落酒熟黄金波。洗壶日日望君至，君不来兮可奈何。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a74883cfc9c2a8e8156a','竹枝词','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"终日望君君不来，为谁犹住郁孤台。幸自一年无梦到，谁教昨日有书回。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a7488883d298bf3623c2','绮寮怨题写韵轩','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"绛阙珠宫何处，碧梧双凤吟。为底事、一落人间，轻题破、隐韵天音。当时点云滴雨，匆匆处、误墨沾素襟。算人间、最苦多情，争知道、天上情更深。\",\"世事似晴又阴。罗褥甲帐，回头一梦难寻。虎啸崎嵚，护遗迹、尚如今。斜阳落花流水，吹紫宇、澹成林。霜空月明，天风响、环佩飞翠禽。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748ba89382d7b0adc6c','塞翁吟黄园感事','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"又海棠开后，楼上倍觉春寒。绿叶润，雨初干。爱远树团团。当时剩买名花种，那信付与谁看。十载事，土花漫。\",\"但青得阑干。悲欢。思人世、真如一梦，留不住、城头日残。看眼底、西湖过了，又还见、赵舞燕歌，抹粉涂丹。凭君更酌，后日重来，直是晴难。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748bb4594d0189351ba','乌夜啼秋兴','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"院静槐阴似水，雨馀蝉语先秋。熟残梅子无人打，金弹满红沟。\",\"又送行人归去，谁怜倦客淹留。画船旗鼓江南岸，人倚夕阳楼。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748bd409dba0e7b2d93','石州慢','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"京浙尘埃，闽峤风霜，不觉催老。封侯事付儿曹，懒把菱花频照。明光赋笔，那知白首山中，年年管领闲花草。叩角夜漫漫，问何时能晓。\",\"堪笑。空有传世千篇，正似病呻饥啸。欲傲王侯，早被王侯相傲。盖棺事定，即今老子犹龙，荣枯得丧浑难料。无酒不须愁，问黄花知道。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748bf2b3656b1fc5f9e','瑞鹤仙·刘氏园西湖柳','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"绿杨深似雨。西湖上、旧日情丝恨缕。风流似张绪。羡春风依旧，年年眉妩。宫腰楚楚。倚画阑、曾斗妙舞。\",\"想而今似我，零落天涯，却悔相妒。痛绝长秋去后，杨白花飞，旧腔难谱。年年暗度。凄凉事，不堪诉。记菩提寺路，段家桥水，何时重到梦处。况柔条老去，争奈系春不住。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748ca4f3a04642f7be8','苏幕遮·春情','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"绿秧平，烟树远，村燕声喧，凫雁归来晚。自倚阑干舒困眼。一架葡萄，青得池塘满。\",\"饮先愁，吟又懒。几许闲情，百计难消遣。客路不如归梦短。何况啼鹃，怎不教肠断。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748cdffea226651ed1d','望海潮·次龙有章韵','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"云外梅阴，雨馀苔晕，嫩寒初沁罗裳。书几凝尘，琴丝带润，小窗幽梦生凉。新水涨银塘。恨王孙去后，烟草茫茫。记得湖山胜处，相对拆封黄。\",\"情笺思墨犹香。奈当时两鬓，都是吴霜。兔颖吟苦，鹴裘解尽，何意此□游梁。旧话不堪长。便倩薰风吹去，本地看风光。惟有青山，伴我耕钓老村庄。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748e95e0594b4b2ab9c','阮郎归·其一梨花','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"冰肌玉骨淡裳衣。素云生翠枝。一生不晓谪仙诗。雪香应自知。\",\"微雨后，禁烟时。洗妆君莫迟。东风不解惜妍姿。吹成蝴蝶飞。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748ed10eb46dfc8583b','法驾导引·其三','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"公度我，公度我，我是汉铜仙。惜我玉龙为觳觫，为公锄雨种芝田。\",\"留眼看千年。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748eff41a76d7d268c5','望海潮·云外梅阴','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"云外梅阴，雨余苔晕，嫩寒初沁罗裳。书几凝尘，琴丝带润，小窗幽梦生凉。新水涨银塘。恨王孙去后，烟草茫茫。记得湖山胜处，相对拆封黄。\",\"情笺思墨犹香。奈当时两鬓，都是吴霜。兔颖吟苦，鹴裘解尺，何意比□游梁。旧话不堪长。便倩薰风吹去，本地看风光。惟有青山，伴我耕钓老村壮。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748f46a65262aea0dc8','法驾导引·其一寿云岩师','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"云漠漠，云漠漠，云拥紫皇家。岩上神仙无一事，幅巾临水看桃花。\",\"点点是丹砂。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748f6391cb61702812d','莺啼序·其一春晚','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"东风何许红紫，又匆匆吹去。最堪惜、九十春光，一半情绪听雨。到昨日、看花去处，如今尽是相思树。倚斜阳脉脉，多情燕子能语。自怪情怀，近日顿懒，忆刘郎前度。断桥外、小院重帘，那人正柳边住。问章台、青青在否。芳信隔、□魂无据。想行人，折尽柔条，滚愁成絮。\",\"闲将杯酒，苦劝羲和，揽辔更少驻。怎忍把、芳菲容易委路。春还倒转归来，为君起舞。寸肠万恨，何人共说，十年暗洒铜仙泪，是当时、滴滴金盘露。思量万事成空，只有初心，英英未化为土。浮生似客，春不怜人，人更怜春暮。君不见、青楼朱阁，舞女歌童，零落山丘，便房幽户。长门词赋，沉香乐府，悠悠谁是知音者，且绿阴多处修花谱。殷勤更倩啼莺，传语风光，后期莫误。\"]',NULL,NULL,NULL,NULL),('c16655df14c5a748f7b4b14ed61089bd','凤凰台上忆吹箫·转官球','赵文','c16655df14c5a748b3ba8f1bdfd7468a','元',NULL,'[\"白玉磋成，香罗捻就，为谁特地团团。羡司花神女，有此清闲。疑是弓靴蹴鞠，刚一踢、误挂花间。方信道，酴醾失色，玉蕊无颜。\",\"凭阑。几回淡月，怪天上冰轮，移下尘寰。奈堪同玉手，难插云鬟。人道转官球也，春去也，欲转何官。聊寄与、诗人案头，冰雪相看。\"]',NULL,NULL,NULL,NULL),('c16655df16e1b22d1074a8914b8f6600','品令','王行','c16655df16e1b22de077e1a514d9e800','元',NULL,'[\"飞琼环佩。立缥缈、香云影里。冰丝萦蹙霞绡帔。瑶阶玉砌。雪月看初霁。\",\"不待夭妍相妩媚。任天然丰致。绰约仙姿真绝世。众芳无地。先得东风意。\"]',NULL,NULL,NULL,NULL),('c16655df16e1b22d89c75661c92ba1e6','清风八咏楼','王行','c16655df16e1b22de077e1a514d9e800','元',NULL,'[\"远兴引游踪，漫遍踏天涯，萋萋芳草。偏爱双溪好。有隐侯旧迹，层楼云表。碧崖丹嶂，看缥缈、凭阑吟啸。偶佳遇、留捣元霜，岁星旋又周了。\",\"归期谁道无据，几回首兴怀，故林猿鸟。拟待春空杳。与鸳俦鸿侣，共还池岛。川途迢递，纵南翔、仍诉幽抱。莫轻负、今日相看，但得翠尊同倒。\"]',NULL,NULL,NULL,NULL),('c16655df1730f95f65594cf41692fdb7','上平西泰和南征作','刘昂','c16655df1730f95fd1457b72d1457b72','元',NULL,'[\"趸锋摇，螳臂振，旧盟寒。恃洞庭、彭蠡狂澜。天兵小试，百蹄一饮楚江干。捷书飞上九重天。春满长安。\",\"舜山川，周礼乐，唐日月，汉衣冠。洗五州、妖气关山。已平全蜀，风行何用一泥丸。有人传喜，日边路、都护先还。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c9022a570320458535','春草碧','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"几番风雨西城陌。不见海棠红、梨花白。底事胜赏匆匆，正自天付酒肠窄。更笑老东君、人间客。\",\"赖有玉管新翻，罗襟醉墨。望中倚阑人，如曾识。旧梦回首何堪，故苑春光又陈迹。落尽后庭花，春草碧。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c91368c9146155dc3f','临江仙','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"倦客更遭尘事冗，故寻闲地婆娑。一尊芳酒一声歌。卢郎心未老，潘令鬓行皤。\",\"醉向繁台台上问，满川细柳新荷。薰风楼阁夕阳多。倚阑凝思久，渔笛起烟波。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c94611e1542c07eeb1','渔父','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"杨柳风前白板扉。荷花雨里绿蓑衣。\",\"红稻美，锦鳞肥。渔笛闲拈月下吹。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c94611e154355434e0','渔父','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"钓得鱼来卧看书。船头稳置酒葫芦。\",\"烟际柳，雨中蒲。乞与人间作画图。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c9606d191e30b695d8','青玉案','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"冻云封却驼冈路。有谁访、溪梅去。梦里疏香风似度。觉来惟见，一窗凉月，瘦影无寻处。\",\"明朝画笔江天暮。定向渔蓑得奇句。试问帘前深几许。儿童笑道，黄昏时候，犹是帘纤雨。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c98d41f387d7b12a57','西江月','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"一百八般佛事，二十四考中书。山林城市等区区。著甚由来自苦。\",\"过寺谈些般若，逢花倒个葫芦。少时伶俐老来遇。万事安于所遇。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c98effc56e115aa83f','朝中措·襄阳古道灞陵桥','完颜璹','c16655df175d65c9d1457b72d1457b72','元','这是一首感今追昔之作，但又不同于一般的此类作品，并不拘泥于一时一地一物，而是笔势跳跃，地域转换涉及颇广，古今上下，纵横多变。既表达了作者内心澎湃激荡的感情，又是刻意而为的婉转的笔法。因为词人虽然忧念国事，对金朝衰势焦灼悲苦，但迫于政治环境的险恶，此种心情，却不敢直接表白，只能委婉地透露，故在此词中用典颇多，但其用典浑化无痕，意深而笔曲，颇为耐人寻味。\n上片首句以灞陵古道起，气势颇为宏大。灞陵桥，即霸桥。《三辅黄图》载：“霸桥在长安东，跨水作桥。汉人送客至此桥，折柳赠别。”此处以此地起首，当然不是写离别，而是要缅怀英雄业绩之意。因为在历史上，这一带曾发生过无数次激烈的战斗，涌现出了众多威名赫赫的英雄人物：雄才大略、一统中华的秦始皇、“按剑清八极，归酣歌《大风》”的汉高祖刘邦；汉初功臣萧何，韩信、张良；汉武帝时的封疆大将卫青、霍去病、射虎南山的飞将军李广，开创贞观之治的唐太宗李世民等等，他们在这里都留下了许多惊天地、泣鬼神的动人事迹。词作者由古迹“灞陵桥，想到了这些英雄人物，又不禁联想到金朝国势衰落，却无人能象这些英雄那样，担当起挽救国家于危亡中的大业，不禁感慨万千，诗兴大发，故有”诗兴与秋高“句。”千古风流人物，一时多少雄豪“虽是化用苏轼《念奴娇。赤壁怀古》”千古风流人物，一时多少豪杰“句，但却毫无突兀之感，实在是作者真情实感的自然流露，既抒发了他追念前代英雄豪杰的真情实感，同时也流露出他对国家缺乏栋梁之才，不知前途如何的深切的忧虑。\n下片，沿上片起首之意，依然以地名入词。玉塞，即玉门关，又称玉关，“霜清玉塞”意玉门关外，霜清月冷，“云飞陇首着，风落江皋”则更显凄清。\n后两句，出自南粱柳恽《捣衣诗》“亭皋木叶下，陇首秋云飞。”这三句都是写秋景，皆对应了上片“秋高”二字。同时也婉转地表露了作者的生活窘况。《金史》本传曾载词人“客至，贫不能具酒肴。”可见，此三句不同地域的秋景描写，也未尝不是作者孤凄，愤懑心境的曲折表露。后两句“梦到凤凰台上，山围故国周遭”则化用李白，《登金陵凤凰台》及刘禹锡《石头城》“山围故国周遭在，潮打空城寂寞回”诗句，充溢着强烈的感伤之情，表达了词人对故都燕京昔日繁华、昌明盛世的深切怀念。往日国家兴旺，才人辈出，而今冷落凄凉，缺少栋梁之才；两相对衬，词人是多么的焦灼，心中的悲愤、忧虑向谁倾述？读罢此句，那种对国家前途命运的深切忧思宛如感同身受。叹寥寥数句，能达此立意，可见作者是个极具才华的词人。','[\"襄阳古道灞陵桥，诗兴与秋高。千古风流人物，一时多少雄豪。\",\"霜清玉塞，云飞陇首，风落江皋。梦到凤凰台上，山围故国周遭。\"]',NULL,NULL,'《朝中措·襄阳古道灞陵桥》是金代词人完颜璹的作品。是一首感今追昔之作，但又不同于一般的此类作品，并不拘泥于一时一地一物，而是笔势跳跃，地域转换涉及颇广，古今上下，纵横多变。既表达了作者内心澎湃激荡的感情，又是刻意而为的婉转的笔法。','[\"瀑陵桥：即灞桥，：整今陕西省西安市东灞水上。\",\"玉塞：郸玉门关。在今甘肃省敦煌市东。\",\"陇首：泛指陇山一带地区。\"]'),('c16655df175d65c99c8166744383017b','秦楼月','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"寒仍暑。春来秋去无今古。无今古。梁台风月，汴堤烟雨。\",\"水涵天影秋如许。夕阳低处征帆举。征帆举。一行惊雁，数声柔橹。\"]',NULL,NULL,NULL,NULL),('c16655df175d65c9eb1c9c8c32fdaac2','沁园春','完颜璹','c16655df175d65c9d1457b72d1457b72','元',NULL,'[\"壮岁耽书，黄卷青灯，留连寸阴。到中年赢得，清贫更甚，苍颜明镜，白发轻簪。衲被蒙头，草鞋著脚，风雨潇潇秋意深。凄凉否，瓶中匮粟，指下忘琴。\",\"一篇梁父高吟。看谷变陵迁古又今。便离骚经了，灵光赋就，行歌白雪，愈少知音。试问先生，如何即是，布袖长垂不上襟。掀髯笑，一杯有味，万事无心。\"]',NULL,NULL,NULL,NULL),('c16655df17b1dadc5ec4508117e410f2','梧叶儿','吴西逸','c16655df17b1dadcd1457b72d1457b72','元',NULL,'[\"韶华过，春色休。红瘦绿阴稠。花凝恨，柳带愁。泛兰舟。\",\"明日寻芳载酒。\"]',NULL,NULL,NULL,NULL),('c16655df17b1dadc5ec45081f4cd6377','梧叶儿','吴西逸','c16655df17b1dadcd1457b72d1457b72','元',NULL,'[\"香随梦，肌褪雪，锦字记离别。春去情难再，更长愁易结。\",\"花外月儿斜，淹粉泪微微睡些。\"]',NULL,NULL,NULL,NULL),('c16655df17cc4b28c782f582a0509209','柳梢青·和杨元咎梅词四首','柯九思','c16655df17cc4b2870222949674bfc5f','元',NULL,'[\"懊恨春初，飘零月下，轻离轻隔。重酝梨云，乍舒柳眼，羞人曾识。\",\"已堪索笑寻檐，早准备、怜怜惜惜。莫是溪桥，才先开却，试驰金勒。\"]',NULL,NULL,NULL,NULL),('c16655df17cc4b28ece5e1990c3b07ae','柳梢青','柯九思','c16655df17cc4b2870222949674bfc5f','元',NULL,'[\"璚散残枝。点窗款款，度竹迟迟。欲诉芳情，曲中曾听，画里重披。\",\"春移别树相期。渐老去、何须苦悲。人日酣春，脸霞清晓，复记当时。\"]',NULL,NULL,NULL,NULL),('c16655df17cc4b28ece5e1993d5d9e75','柳梢青','柯九思','c16655df17cc4b2870222949674bfc5f','元',NULL,'[\"翠苔轻搭。南枝逗暖，乍收渐霎。乱插繁花，快张华宴，绕衣千匝。\",\"玉堂无限风流，但只欠、些儿雪压。任选一枝，折归相伴，绣屏花鸭。\"]',NULL,NULL,NULL,NULL),('c16655df17cc4b28ece5e199d3c68351','柳梢青','柯九思','c16655df17cc4b2870222949674bfc5f','元',NULL,'[\"姑射论量。渐消冰雪，重试新妆。欲吐芳心，还羞素脸，犹吝清香。\",\"此情到底难藏。悄默默、相思寸肠。月转更深，凌寒等待，更倚西廓。\"]',NULL,NULL,NULL,NULL),('c16655df17ffda25f37b2457607464be','绮罗香·斗草','黄澄','c16655df17ffda25d1457b72d1457b72','元',NULL,'[\"绡帕藏春，罗裙点露，相约莺花丛里。翠袖拈芳，香沁笋芽纤指。偷摘遍、绿径烟霏。悄攀下、画阑红紫。扫花阶、褥展芙蓉，瑶台十二降仙子。\",\"芳园清昼乍永，亭上吟吟笑语，妒秾夸丽。夺取筹多，赢得玉珰瑜珥。凝素靥、香粉添娇，映黛眉、淡黄生喜。绾胸带、空系宜男，情郎归也未。\"]',NULL,NULL,NULL,NULL),('c16655df184f84c336ec17faef8c08c0','如梦令·乍晴见月','王瞒','c16655df184f84c3d1457b72d1457b72','元',NULL,'[\"檐际犹余残溜。帘外淡云月逗。风雨漫经旬，依旧姮娥眉皱。\",\"晴否。晴否。早是落花时候。\"]',NULL,NULL,NULL,NULL),('c16655df19a657d88c06979217864d05','天净沙','严忠济','c16655df19a657d8d1457b727c82fab8','元',NULL,'[\"宁可少活十年，休得一日无权。大丈夫时乖命蹇。\",\"有朝一日天随人愿，赛田文养客三千！\"]',NULL,NULL,NULL,NULL),('c16655df1a6ddaf325eb6af7ae7781f1','清平乐','李仁山','c16655df1a6ddaf3d1457b72d1457b72','元',NULL,'[\"瑶英轻洒。姑射飘仙驾。巧夺孤山能变化。夭娇飞来白下。\",\"绝怜玉骨清姿。不随红紫芳时。要识天然标格，竹篱茅舍横枝。\"]',NULL,NULL,NULL,NULL),('c16655df1c74e3c72c4cde1629eae1eb','木兰花慢·西湖送春','梁曾','c16655df1c74e3c72de5d166dc960c46','元',NULL,'[\"问花花不语，为谁落，为谁开。算春色三分，半随流水，半入尘埃。人生能几欢笑，但相逢、尊酒莫相催。千古幕天席地，一春翠绕珠围。\",\"彩云回首暗高台。烟树渺吟怀。拚一醉留春，留春不住，醉里春归。西楼半帘斜日，怪衔春、燕子却飞来。一枕青楼好梦，又教风雨惊回。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d4138852a8','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"宫车调得马蹄匀，细草轻风不动尘。织成翠羽垂珠幕，缕就黄金结绣茵。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d42a04de28','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"露下穹庐暑气苏，卧看织女映黄姑。山泉响似江南雨，林下不闻啼鹧鸪。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d42f027b12','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"沙岭风清宿雨多，白云如雪夜陂陀。涧泉十里九曲折，北向天边作御河。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d43d602e6b','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"霓旌移仗蕊珠宫，云母屏门不隔风。花外侍臣成久立，听得新歌乐意同。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d44036bec3','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"马群弥野草连云，当年玉帐度秋春。迎日捷书频送喜，内间赐与出金银。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d445b3ed52','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"雨过平沙不染泥，彩索凌风御帐齐。宫臣报道晓寒浅，有个黄鹂深树啼。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d44aec37f1','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"羽猎长年从翠华，麋鹿生茸草茁芽。射得黄羊充内膳，更喜江南新贡茶。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d4729f755b','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"果熟冰盘进御黄，秘殿挥毫对日长。元臣补衮应无阙，新赐宫衣自上方。\"]',NULL,NULL,NULL,NULL),('c16655df1df0b795bd5a79d491c412a4','竹枝词和歌韵自扈跸上都自沙岭至滦京所作','吴当','c16655df1df0b795286674e3cf9a242b','元',NULL,'[\"殿头云气尽成龙，水晶帘影散高春。阿母传杯池上宴，帝子吹箫月下逢。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa13c27f9710850718','踏云行','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"无相形容，灵明心手。清虚岸上栽金柳。剪除魍魉跨鹪螟，希夷俯啸空山陡。\",\"劫外飞腾，妙中行走。杳冥乡里寻知友。饮馀恬淡醉真风，乾坤旷朗绝纤有。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa160adbb6b03741d6','鹧鸪天·夏','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"弄舌闲禽向郁林。涤蒸散发趁松阴。清风习习来冰簟，陶写真情取次吟。\",\"天似水，柳如金。火云叠翠出遥岑。危楼安枕王孙趣，静室忘机逸士心。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa25665b36a8416b82','水龙吟','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"故乡何处栖迟，海山雾敛春风细。花浓石润，云娇烟淡，天容如水。芝桂香分，橘橙酒滟，锦茵摛翠。佩霓裳缥渺，飞琼羽盖，瑶池宴，赏佳丽。\",\"俯笑人间富贵。到头来、一场虚伪。桑田暗改，人生空老，谁能适意。虎战龙争，汉兴秦灭，今成何济。看蓬莱紫府，长春胜景，妙无加矣。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa26f8bd8f6a3512ba','粉蝶儿','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"欲说天机，奈尘寰、世人不信。只因他夙缘无分。恋浮华，贪火院，气神亏损。\",\"贩尸骸，迷了本来心印。悟后回头，乐清闲、莫劳方寸。\",\"好参求妙灵玄牝，炼纯阳，惊宇宙，一声雷震。恁时节，向蓬莱会中相等。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa27301596910850fc','鹧鸪天·秋','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"乍觉西风吹葛衣。萧萧秋色满华夷。催排荞麦花开日，点勘梧桐叶落时。\",\"鸦噪晚，客思归。一川禾黍正离离。蝉声莫向高枝叫，切恐宾鸿苦皱眉。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa49a3cc38bfca2bcd','瑞鹤仙·花开阆苑','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"武陵溪上看，睹阆苑瑶台，应是花绽。风流世间罕。喷清香鲜异，紫芝相间。东君不管。更别有、长春手段。向无为树上芬芳，光彩日常呈现。\",\"堪献。玄元根本，锦绣枝梢，是谁亲见。何方贵显。金莲会，任舒展。这天生雅艳，一种琼英，只许神仙作伴。若诸人，采得归来，玉京赏玩。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa4fd3ee2e109fc66e','鹧鸪天·春','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"小村隐居乐至闲。兴来吟首鹧鸪天。桃花笑日开红锦，门柳垂丝袅翠烟。\",\"扃筚户，扫苔钱。困时禅榻枕书眠。有人问我修何事，梦载华胥月一船。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa5765948474bc62a8','长相思','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"醉中醒。梦中醒。月浸松窗枕簟清。谁家捣练声。\",\"诉真情。乐真情。竹外流泉漱石鸣。云山叠翠屏。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa597ceb3e1539daf8','月中仙','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"历劫清凝，鉴无私莹澄，谁是宜表。冲虚浩渺。统万灵机变，恢恢临照。湛然包大道。体难测、穷通暗报。素碧谁能到。寂寥九阳，闲静至玄奥。\",\"乾元浩立青霄，覆三千大千，神用枢要。高明杳杳。应四时生化，清虚颠倒。慧风空外扫。更廓朗、群迷怎晓。我今深通了。归来赏玩逍遥好。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa59a1f3e553fc525f','江城子·令','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"庄周蝶梦几人醒。理圆明。了余生。老计田园，谷口傲其耕。落日西风吹败叶，云返岫，两初晴。\",\"数声高柳暮蝉鸣。淡烟横。野泉倾。千里关河，秋色满山城。晦迹人间青眼少，谁念我，养遐龄。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa706127c313a6355f','天香','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"若论修真，玄关直指，外缘心上都撇。静处跏趺，存神握固，莫辩是非优劣。任缘度日，除饱暖其馀忘绝。一味清闲价无穷，对下愚难说。\",\"端的至诚妙诀。叹学人、几曾休歇。默默昏昏，壶内玉花开彻。恍惚中间显现象，见宝珠、光耀似皎月。悟得之时，同游圣阙。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa706127c32bca5750','天香','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"过隙年光，如毛尘事，几人识破休矣。归去来兮，云耕水钓，林下澹然宁耳。翠微影里，别有些逍遥真味。一曲玄歌寄天香，叹物华荣悴。\",\"白茅半间而已。畅情怀、四时吟醉。返照人生，毕竟到头何济。坐看空华起灭，这混成消息越众美。太朴淳风，清虚妙理。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa7f968a4eff3940e8','鹧鸪天·冬','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"一夜严凝作苦寒。头明六出落人间。彤云掺粉蛾飞舞，柳絮随风蝶往还。\",\"梅影瘦，竹枝弯。一丘湖玉倚阑干。何人助我丹青力，写入屏山子细看。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa8a943637f3cb6225','小重山','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"自古高人远市廛。超然离俗表，隐林泉。曾闻宣圣有遗言。仁智者，山水窟中安。\",\"无事恼心间。猿鹤为朋友，养成丹。白云深处乐幽闲。明月下，一曲小重山。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa8f77d39adcfed8f8','烛影摇红','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"菊绽黄花，月生碧汉清光皎。霜前雨后正芬芳，四海圆明照。朵朵争开烂熳，吐香风、腾辉天表。槛边幽雅，空外婵娟，依时呈妙。\",\"阆苑仙葩，蒋公三径知音少。瑶台宝鉴照娑婆，悟者头头了。好对东篱赏玩，射江山、虚堂灵沼。数枝金蕊，万道银霞，浮生难晓。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa967af9d987d6ad78','上丹霄','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"叹人生如掣电，似浮沤。更何消苦苦贪求。荣枯得失，宿缘分定岂须忧。我今悟此养天倪，晦迹林丘。\",\"箪瓢乐，琴书味，闲中过，静中休。有恬淡真趣相酬。花朝月夜，大开口、笑展眉头。玄珠收得默然归，方外优游。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa97b5b8da2e84118b','满庭芳','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"决烈修真，殷勤办道，万缘识破皆空。太虚鼎内，默默养和冲。一颗灵珠灿烂，光辉似、月射千峰。长春景，五云台上，芝草吐香风。\",\"这些微妙理，有缘端的，千里相逢。纤尘不染，清净是真功。日用头头不昧，超生灭、法界难笼。还能此，逍遥自在，处处是仙宫。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa97b5b8da8ce7516a','满庭芳','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"老氏真机，如来祖印，明明象帝之先。不生不灭，端的至幽玄。一派清风匝地，迷云散、皓月当天。君知否，寥寥劫外，法鼓震三千。\",\"本来真个妙，无名无相，非道非禅。学人外觅，教我怎生传。不肯回光返照，区区地、走遍山川。归来也，人牛不见，芳草锁江烟。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa97b5b8dabc99ac49','满庭芳','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"本分家风，寻常日用，何方不是林泉。饥餐渴饮，任运度流年。行止非人所及，休思算、好弱由天。君知么，一身尚假，万事岂牢坚。\",\"些儿穷活计，寂寥潇洒，有口难言。箪瓢不置，那得买山钱。且恁腾腾兀兀，任从他螳战蜂喧。真清乐，松间步月，石上枕书眠。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa97b5b8daec0f6cc1','满庭芳','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"微妙家风，非空非色，卓然无去无来。超今越古，悟者免轮回。拈出长春圣境，琼花绽、铁树崔嵬。谁知觉，了翁夜半，震动九天雷。\",\"玄门真洒落，星楼月殿，愚昧难开。寥阳芝草，端的胜寒梅。方寸神机莫测，何消得、飞过蓬莱。玉霄闲客，未肯蹑苍苔。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa97b5b8daf2e5a284','满庭芳','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"大觉光明，不须外觅，人人各有如来。浮生迷昧，贩骨走千回。凿透灵源法海，禅河涨、风浪崔巍。泥牛吼，威音嘹亮，铁壁起云雷。\",\"菩提。无缝塔，林峦掩映，山色分开。任曹溪雪满，漏泄红梅。处处香严法界，休分别、只树蒿莱。真消息，孤峰顶上，石女绣莓苔。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffa9b878f1b552f1d6d','月上海棠','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"应时宇宙行慈善。向苦海横舟运方便。法界显威光，諕群魔、隐潜逃窜。真仙现。别有神通手段。\",\"上苍委我开仙院。把大道玄风任弘阐。香饵满满娑婆，钓金鳌、玉京游玩。琼瑶宴。俯啸人间蚁战。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffaa5e78230a2dd0ffb','瑞鹤仙·月照洞庭','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"素秋天似水，更夜色呈鲜，物华殊丽。山堂兴无际。\",\"见团圆月照，洞庭深邃。清风匝地，但处处、昭彰德被。\",\"遍恒沙朗射晴空，千古秀凝青桂。\",\"高贵。随形应现，鉴物无私，碧霄呈瑞。孤然爽异。辉法界，显灵慧。\",\"放清光万道，入户穿窗，皎皎昏蒙普济。愿凡夫，早悟圆明，尽知此理。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffaafe729f488e76206','瑞鹤仙·风入琼林','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"玉峰时暂歇，觉风入琼林，倍增真悦。烦蒸顿消灭。扫尘埃四海，廓然施设。八方静洁。布一味、清凉妙绝。这些儿冷淡生涯，等闲未曾轻泄。\",\"奇别。萧萧昼夜，飒飒乾坤，几人能瞥。随机应辙。寻知友，伴明月。绕群芳结秀，响报声闻，动用阴阳正诀。待浮生，吹散迷云，恁时细说。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffab649136675c46506','贺圣朝','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"春光明媚，黄莺出谷，紫燕来巢。见仙桃初放两三枝，间翠竹香茅。\",\"琴书高枕，柴门紧闭，莫放人敲。任松轩红日照三竿，更蝶梦花梢。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffab649136692c6a2b0','贺圣朝','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"道人幽趣，萧闲风味，林下云烟。夜横琴高隐翠微间，看月印寒泉。\",\"逍遥吟唱，还乡宫调，雪曲无弦。这玄珠收得未朝元，作住世神仙。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffab7fef213025e2983','华溪仄','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"华溪仄。春风也是人间客。人间客。飘零南北，几时休息。\",\"不如学个商山伯。石楼云殿销尘迹。销尘迹。丹丘闲看，老松千尺。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffab88f1c0d05cfa794','一剪梅','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"身若白云任卷舒。天涯海岸，自在无拘。太虚廓落是吾庐。不羡王侯，拱璧轩兴。\",\"一味闲闲乐有馀。从他活计，冷淡消疏。归来毕竟理何如。心月辉辉，光射蓬壶。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffabc32a2a513907f56','凤栖梧','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"逃暑绳床聊慰兴。翠箨森森，几处花阴衬。萱草池塘人不问。绿荷暗窃薰风信。\",\"懒把霜纨嫌力困。沉李嚼冰，煮茗清泉近。裸袒襟怀无郁闷。算来唯有神仙分。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffabd949cf9f3e5c2c3','一枝春','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"堪嗟肉傀儡，尘世争奇怪。昼夜火院煎苦，沉埋花酒丛中，孽重终难改。贩骨何时彻，头面翻腾，昧了灵源真宰。独我而今乐自在。\",\"放逸游寰海。逍遥天地间、笑开怀，一味清闲，万里金难买。九转功夫足，步月携云，稳赴金莲仙会。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffabe05afd514a95fc5','成功了','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"瞥然晓。便识破浮生，一场虚矫。利呼驱驰，光阴迅速，空惹物情衰老。自歌自笑。念好景、几人曾到。故园春色，海棠半开，绿杨轻袅。\",\"休休万事了。听乱山深处，杜鹃啼叫。归去来兮，长安古道，隐隐断霞残照。洞庭寂悄。叹门外、落花风扫。故人别后，青霄凤吟杳杳。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffabe05afd56b9bee0c','成功了','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"悟浮世。把万事纷纷，尽皆忘弃。藜杖云巾，麻绦纸袄，便是随身行李。玩山游水，要到处、乐然从己。兀兀腾腾，逍遥自在，快活谁比。\",\"同流看子细。是这些消息，恁般活计。一味真欢，君还会得，人静月明千里。畅哉而已。对小斋、几枝寒蕊。有谁知我，西山半轩晓翠。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffac69a825e35e6e757','二郎神','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"离尘俗，便点检林泉雅趣。竹杖芒鞋青箬笠，泛烟波、绿蓑柔橹。月夜江天无尽乐，品短笛、潇湘蓼渚。此消息，千金不卖，好对渔樵分付。\",\"归去。对晚翠风生小浦。勘破南华龟曳尾，尽教他衣冠豺虎。野水添杯谁似我，醉卧白云深处。任秋月春花，暗换桑田，明催今古。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffac69a825e8c4392e1','二郎神','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"诉衷情，为大地众生泪洒。晓夜忧煎贪活路，尽都被、妻男相挂。使万种机关图富贵，全不怕、犁耨高架。劝愚迷早悟，后世因缘，直言直写。\",\"幽雅。买玄妙不用钱价。只用他心灯常不昧，要万法灵通总压。待行满丹成归去日，把四假凡躯脱下。向桃源仙会，玉殿金楼，长春不夜。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffac69a825e9372348e','二郎神','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"叹平生，谩日夜劳劳攘攘。想一世浮华能几许，若云间、电光翻掌。梦幻吞侵何日省，恋火院、缠绵业网。这人身一失，万劫难逢，谁能回向。\",\"清爽。猛跳出迷津欲浪。彼岸风光真快乐，伴孤云野鹤飘荡。悟彻灵台无障碍，似月挂寥天朗朗。满十方三界，莹彻圆明，逍遥吟唱。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffac69a825ec226e607','二郎神','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"平生兴，有万顷云山野景。竹疃梅村蓬户悄，这幽闲、世间难胜。一曲无弦喧宇宙，对沉水、石炉绝听。向林下栖迟，养就懒散，烟霞情性。\",\"复命。披短褐玄通古圣。看鹭立鸥飞沙觜岸，笑醯鸡瓮中流梗。一枕华胥春梦觉，岂羡封侯列鼎。早回首归来，月照松溪，云岩苔径。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffac9a03f0579b0edb0','天香·梅','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"万木归根，三冬拔翠，晓来梅萼轻坼。妒雪精神，清人气焰，不许等闲攀摘。百花未发，独占得东君春色。庚岭斜横，秀孤芳，更妙机难测。\",\"西湖洒然至极。胜蜡黄愈增灵识。漏泄前村驿使，喜传消息。解引诗人雅咏，对一枝金蕾，兴自适。月浸寒梢，天香可惜。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffad56911fa56ce28f1','绛都春','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"心如江海。纳百川万派，澄清无碍。渺渺渊源几人知，包沙界。光明灿烂超三昧。諕小乘、邪魔潜退。混元风范，虚无秘密，下愚难解。\",\"宣扬亘初真载。统众妙之门，高下无赛。应物头头，显慈悲，消灾害。不生不灭非中外。这一曲、玄歌谁买。玉京仙子，吹动太虚天籁。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffad56911fa6da098a7','绛都春','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"侵寻妙景。看赤城秀稔，芝田千顷。物外闲闲，乐真情，搜佳兴。扁舟载月烟蓑冷。酒醒时、临流清咏。一声羌管，三幅晓风，洒然幽胜。\",\"徽音慢弹谁听。似野鹤腾空，生涯萍梗。放旷乾坤，与江湖，丝纶整。锦鳞钓得成馀庆。便归来、玄都驰骋。杖挑春色，逍遥太虚仙境。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffaed0c56ec4ec3423e','瑞鹤仙','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"岁华如转辇，叹世路忙忙，何日知足。锥刀竞蛮触。这浮名浮利，少荣多辱。谁能寡欲。解恬淡、天之美禄。把尘缘一旦纷飞，回首退藏岩谷。\",\"清福。松间步月，石上眠云，性如麋鹿。高歌一曲。闲吟罢，布棋局。听猿啼鹤唳，水绿山青，篱外风敲瘦玉。这真欢，夸古腾今，几人继续。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffafa71e3d61dc00226','倾杯','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"休休我省也，弃世俗、洞庭深处。好行步。大药烧成，宝珠收得，曲按无弦，音胜宫羽。洪杨道德，风范钟吕，许由巢父。向逍遥彼岸，寻个水云侣。\",\"谁能继圣徒，智若愚，六情断灭，九玄通悟。妙粹冲虚，炳焕灵明，物物般般，无疑无虑。一真莹彻，功行满足，顿超尘所。便归来洒落，方外了然去。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffafa71e3d67aa0392c','倾杯','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"物外回观，爱河滚滚，空劳攘、为何因故。恋惜妻男，竞争名利，念念随他，蒙昧真如。掀天富贵，倾城娇艳，总归何处。叹浮生下梢，终久尽成虚。\",\"伤嗟是非今古。百年欢宴，大都几许。梦想南柯，命遭北府，尚自贪婪，不寻归路。轮回贩骨，身伴狐狸，几番荒芜。谩赢得往来，欲浪苦流注。\"]',NULL,NULL,NULL,NULL),('c16655df2142dffafb40e7c2211bd8a3','瑞鹤仙·雪迷蓬岛','长筌子','c16655df2142dffad1457b72d1457b72','元',NULL,'[\"幸逢玄运到。正律吕推移，北辰宣号。须臾瑞祥报。\",\"见同云凝雪，晓迷蓬岛。天公德茂。但处处、琼花映罩。\",\"向长空片片轻飘，显扬圣功微妙。\",\"深奥。虚无造化，秘密神机，对谁分表。三山路杳。真潇洒，洗尘扰。\",\"看琼宫宝殿，素净楼台，出入仙姿窈窕。这冰清玉洁家风，几人顿了。\"]',NULL,NULL,NULL,NULL),('c16655df218501defa8e8f345ec0f144','西湖竹枝词二首','熊进德','c16655df218501ded1457b72d1457b72','元',NULL,'[\"销金锅边玛瑙玻，争似侬家春最多。蝴蝶满园飞不去，好花红到剪春罗。\"]',NULL,NULL,NULL,NULL),('c16655df218501defa8e8f34f846d249','西湖竹枝词二首','熊进德','c16655df218501ded1457b72d1457b72','元',NULL,'[\"金丝络索双凤头，小叶尖眉未著愁。大姑昨夜苕溪过，新歌学得唱湖州。\"]',NULL,NULL,NULL,NULL),('c16655df21a4849303f658f524a3b968','满江红·己巳二月二十二日游北门，有感','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"试检春光，都不在，槿篱茅屋。荒城外，牯眠衰草，鸦啼枯木。黄染菜花无意绪，青描柳叶浑粗俗。忆繁华，不似少年游，伤心目。\",\"棠坞锦，梨园玉。燕衣舞，莺簧曲。艳阳天，输与午桥金谷。行处绮罗香不断，归时弦管声相逐。怕夕阳，影散近黄昏，烧银烛。\"]',NULL,NULL,NULL,NULL),('c16655df21a484930c5c2e7d0a8f805e','念奴娇','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"茂林修竹，自山阴散后，几番陈迹。修禊年年春故事，懊恨风流非昔。当日兰台，后来菊圃，苗裔江南北。睹君雅号，恍然旧事重忆。\",\"岁晚木落天寒，黑貂将敝，尚作新丰客。星斗胸中空灿烂，磨蝎空名何益。袁吕相逢，知音一笑，肉眼无人识。访予梅屋，谈天聊慰孤寂。\"]',NULL,NULL,NULL,NULL),('c16655df21a484930c5c2e7d2560da87','念奴娇','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"延年有术，飧古松根下，茯苓千岁。纵是延年如何益，命也道之将废。思古之人，词章节行，杲杲行当世。遗风流韵，渊然尚有余味。\",\"无奈先哲凋零，后生坦率，多以儒为戏。每笑唐人书不读，直把黄金买贵。山泽奇才，云林真隐，没齿何曾遇。人生如梦，江流日夜东去。\"]',NULL,NULL,NULL,NULL),('c16655df21a484930ca60953d4e14a3b','水龙吟·次药房韵','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"西州玉局飞仙，霓裳曾侍槐龙翠。飞花丽句，雅音犹在，有人赓未。千载峨峰，一江川练，又练清气。叹瀛洲路近，刚风吹断，漫自有，凌霄意。\",\"草碧寒窗静里。折琼枝、小栏同倚。新吟婉美，西施态度，慵梳洗。按羽调丝，雪儿薄相，为君心醉。恨高楼暮隔，江城花暗，碧云遥睇。\"]',NULL,NULL,NULL,NULL),('c16655df21a484931368c91425e34789','临江仙','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"听得雅歌珠一串，飒然吹动梁尘。尊前重见旧时人。主人情未重，情重是嘉宾。\",\"飞絮落花无定在，近前遮莫谁嗔。文园倦客最伤神。野亭何处泊，空忆画堂春。\"]',NULL,NULL,NULL,NULL),('c16655df21a484931b15461dafb77fd8','浣溪沙·次伯机','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"翠玉峰高鹭点明。縠纹波动鸭雏生。湖山宜雨又宜晴。\",\"越女荡舟莲叶碧，裴郎驻马柳阴青。折花调客诉衷情。\"]',NULL,NULL,NULL,NULL),('c16655df21a4849325665b36b023ff78','水龙吟','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"燕芹香老春深，微风飐动新篁翠。惊敲梦断，忙呼小玉，故人来未。香缕筛帘，游丝堕几，暖薰花气。问春随莺到，又随燕去，谁解得，东君意。\",\"涧水流红影里。小楼东、有人孤倚。残桃着雨，鬓容撩乱，未堪妆洗。冉冉年光，悠悠时事，不如沉醉。更韦娘一曲，司空惯见，也应回睇。\"]',NULL,NULL,NULL,NULL),('c16655df21a4849326cb276076039f31','金缕曲·代送同僚','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"乍到蓉城路。听儿童歌谣德政，感恩如父。好人西京循吏传，谁道今人非古。留遗爱、甘棠佳树。节操冰霜清凛凛，看和风、吹作阳春雨。程去速，遽如许。\",\"我来不见空怀伫。望彼美、碧云暮合，草萋南浦。信是有才供世用，敢择东西何处。又谁念、婴儿思乳。不恨使君吾不识，恨使君不与吾相辅。聊寄意，短长句。\"]',NULL,NULL,NULL,NULL),('c16655df21a484933b8d37915277c909','阮郎归·舟中赋所见','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"风吹一捻柳腰轻。春柔力未胜。眉儿喜学远山青。终朝画不成。\",\"娇滴滴，笑盈盈。雏莺叶底声。花梢雨过夕阳明。无情渐有情。\"]',NULL,NULL,NULL,NULL),('c16655df21a484933ff93346872e97b0','念奴娇·送幕职','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"残花剩柳，正啼鹃声里，邮亭别馆。三叠阳关听未彻，手执离杯引满。政坐诸君，久烦老子，今日才萧散。翩然归去，故园绿树春晚。\",\"人世苍白浮云，自舒自卷，不入高人眼。官事如麻何日了，输与闲中不管。翠柏台高，紫薇省近，别有清华选。功名岁晏，江城回首天远。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493663efd33dd5d2efe','念奴娇·洛阳耆英会二首其一','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"戴花刘监，算耆英会上，与吾同岁。伊洛山川今如古，人事几番兴废。梦枕初残，黄粱未熟，已换人间世。箪瓢钟鼎，看来一等滋味。\",\"天上赤白双凡，东来西往，出没真儿戏。惟有神仙长年诀，长似功名富贵。欲捣玄霜，难寻玉杵，何日蓝桥遇。裴郎老矣，云英那肯随去。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493839a00c1bac157f6','念奴娇·赠王道人性初归茅山','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"芙蓉城郭，有羽仙骑鹤，来从何处。曾拉茅君峰顶会，瑶佩随风吹去。玉笈偷开，青囊拾得，笑看人间世。藏身壶里，个中别有天地。\",\"共约手种蟠桃，缀花结实，已是三千岁。欲脍长鲸麟作脯，倒海聊供一醉。偶忆寒梅，更惭小草，拂袖怀归计。蓬莱清浅，云帆他日相遇。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493858bd8d13c9686ae','沁园春·送杨伯可','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"雨足江皋，月满中秋，使客将归。看扁舟空载，贫无长物，破囊收贮，富有新诗。清白传家，怀金不受，洁已从来畏四知。民何幸，尽相安南里，乐业熙熙。\",\"谁知经界良规。是三代相传古法遗。要讲明有素，施行不扰，宽严相济，表里无私。惭愧偏州，久淹老子，却怪朝家选用迟。公子去，定致身鹓序，接武龙墀。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493ac7788abbf683f9f','沁园春·送李同知之官邺都','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"东西二都，史载循良，不五六人。记南阳有召，颍川有霸，并州如郭，河内如恂。直比朱弦，清伴古镜，吏自秋霜民自春。如公者，守廉平二字，近古名臣。\",\"棠阴手种方新。又五马翩翩邺水滨。想台高铜雀，尚留遗迹，堂深画锦，空锁凝尘。琴瑟从容，雅歌缓带，美政遥知达紫宸。期年后，看快行宣召，班冠廷绅。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493c73ac1480302f51e','唐多令·寄远','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"明艳注秋波。轻松绾髻螺。怕逢人、先敛双蛾。怯雨羞云情未稳，佳会少，远离多。\",\"梁绕忆清歌。兰舟肯再过。为他垂泪染香罗。欲倩鳞鸿将锦字，知别后，意如何。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493dc9229f18e52fcc1','点绛唇·情景四首','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"玉体纤柔，照人滴滴娇波溜。填词未就。迟却窗前绣。\",\"一幅花笺，适与何人手。还知否。孤灯坐守。渐入黄昏后。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493eeb04e9dd31e2873','唐多令','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"花下笑声微。莺喉高又低。怪穿花、粉蝶成围。唯有禅心清似水，相对坐，两忘机。\",\"莫道絮沾泥。狂风也解飞。恨残春、九十将归。回首阳台云缥缈，愁薄暮，雨霏霏。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493f29ff18e6c04bb48','点绛唇','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"笑靥多羞，低头不觉金钗溜。凭媒将就。凤枕回双绣。\",\"月地云阶，何日重携手。心坚否。齐眉相守。愿得从今后。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493f29ff18e7936da5e','点绛唇','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"闷托香腮，泪痕一线红膏溜。将身错就。枉把鸳鸯绣。\",\"柳带青青，攀向行人手。天知否。白头相守。破镜重圆后。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493f29ff18e8d094a3a','点绛唇','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"永夜无聊，更堪点滴听檐溜。枕寒难就。堆乱床衾绣。\",\"人面桃红，还忆擎将手。君知否。倚门独守。又是清明后。\"]',NULL,NULL,NULL,NULL),('c16655df21a48493f29ff18ee66e6793','点绛唇','陆文圭','c16655df21a48493a284df113d779cae','元',NULL,'[\"小立娉婷，歌声低遏行云住。不胜珠翠。玉面慵梳洗。\",\"除却姚黄，魏紫谁堪比。君描取。卷中人美。得似崔徽未。\"]',NULL,NULL,NULL,NULL),('c16655df21c8279f9f8c434a7eb209a1','殿前欢','赵显宏','c16655df21c8279fd1457b72d1457b72','元',NULL,'[\"楚云闲，任他孤雁叫苍寒。去留舒卷无心惯，聚散之间。\",\"趁西风出远山，随急水流深涧，为暮雨迷霄汉。阳台事已，秦岭飞还。\"]',NULL,NULL,NULL,NULL),('c16655df2261e7fa815c57ae4d5da536','鹦鹉曲','吕济民','c16655df2261e7fad1457b72d1457b72','元',NULL,'[\"朱颜绿鬓难留住。调弄了几拙讷的儿父。算光阴咫尺风波，恍着暮晴朝雨。\",\"怎禁他地久天长，睚不过暗来明去。望桃源雾杳烟迷，梦觉也玉人那处。\"]',NULL,NULL,NULL,NULL),('c16655df2261e7fa9067bf2fd46339f8','鹦鹉曲·寄故人和韵','吕济民','c16655df2261e7fad1457b72d1457b72','元',NULL,'[\"心猿意马羁难住。举酒处记送别那梁父。想人生碌碌纷纷，几度落红飞雨。\",\"瞬息间地北天南，又是便鸿书去。问多娇芳信何期，笑指到玉梅吐处。\"]',NULL,NULL,NULL,NULL),('c16655df2293968f25665b36550f38c2','水龙吟','曹光辅','c16655df2293968fd1457b72d1457b72','元',NULL,'[\"世间清苦禅和，了心才到安闲地。藜床兀兀，经年打坐，颓然假寐。却甚床边，偶闻牛斗，不知喧蚁。怪藤条临济，饥餐困卧，方会得、个中味。\",\"争似横江楼上，入帘栊、好山供翠。悠悠万事，从今都付，黄粱炊里。朝暮阴晴，定应不废，平生甘睡。笑傍人问我，何当梦觉，为苍生起。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b208b8630dad9144d8','水调歌头·迎送神二词为刘润之赋','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"双龙隐扶辇，千骑纵翱翔。云旌翠蕤摩荡，遥指白云乡。风驭飘飘高举，云驾攀留无处，烟雾杳茫茫。小立西风外，似听佩锵锵。\",\"暮天长，秋水阔，远山苍。归途正逢明月，醉语说丰穰。但愿明年田野，更比今年多稼，神贶讵能忘。君可多酾酒，吾复有新章。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b208b8630dc41274e8','水调歌头·迎送神二词为刘润之赋','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"清秋好天气，禾黍巳登场。群心思答神贶，吉日复辰良。神既来兮庭宇，飒飒西风吹雨，仙仗俨长廊。巫觋传神语，出户舞伥伥。\",\"刲肥羜，沥桂酒，奠椒浆。一年好处须记，此乐最难忘。风外渊渊箫鼓，醉饱满城黎庶，健倒卧康庄。夜久群动息，风散一帘香。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b20d10a9766c6513f0','蝶恋花·闻莺有感','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"鶗鴂一声春已晓。胡蝶双飞，暖日明花草。花底笙歌犹未了。流莺又复催春老。\",\"早是残红枝上少。飞絮无情，更把人相恼。老桧独含冰雪操。春来悄莫人知道。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b20eba3692c58a86bf','满江红·卫生衡之寿','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"春色三分，犹未一、元宵才过。行乐处、软红香雾，未收镫火。杨柳梢头青尚浅，梅花萼底红初破。待东风、吹绿满瀛洲，愁无那。\",\"无一物，为君贺。歌我志，君须和。问人生底事，必须奇货。好对青山倾白堕，休嗟事业违人些。怕他时、富贵逼人来，妨高卧。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b20f139c3464529d19','最高楼·寿卫生行之并序','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"贫而乐，天命复奚疑。儿女聚嬉嬉。东村邀饮香醪嫩，西家羞馔蕨芽肥。把年华，都付与，锦囊诗。\",\"白发青衣，是人所恶。金印碧幢，是人所慕。顾吾道、是耶非。山妻解煮胡麻饭，山童自制薜萝衣。问人生，须富贵，是何时。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b22271d1c2cd7d2b35','满庭芳·山居偶成每与文翰二三子论文把酒歌以侑觞亦足以自乐也','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"归去来兮，吾家何在，结茆水际林边。自无人到，门设不须关。蛮触政争蜗角，荣枯事、不到尊前。应堪叹，清溪流水，东去几时还。\",\"此身何处著，从教容与，木雁之闲。算躬耕陇亩，在我无难。便把锄头为枕，眠芳草、醉梦长安。烟波客，新来有约，要买钓鱼竿。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b232ad97e195ecff0a','渔家傲','段克己','c16655df22b2f8b2d1457b72d1457b72','元','此词从眼前春景，抒写惜春情绪，寄寓无限故国之思。上片写暮春时节，柳丝飞絮，落花成尘。栏杆倚遍，空无一语。惆怅满怀，无处诉说。下片春归无处寻觅，唯有闲愁将不去，依旧伴人住。春雨绵绵，直到黄昏。全词思绪缠绵，婉转工丽。含蓄蕴藉，寄寓殊深。结尾一句，为全词增添无限情韵。 ','[\"诗句一春浑漫与，纷纷红紫但尘土。楼外垂杨千万缕，风落絮，栏干倚遍空无语。\",\"毕竟春归何处所，树头树底无寻处。唯有闲愁将不去，依旧住，伴人直到黄昏雨。\"]',NULL,NULL,NULL,'[\"浑：简直，全。\",\"红紫：指落花。或当另有寄寓。 \"]'),('c16655df22b2f8b23f13d00ba0818077','南乡子·寿县大夫薛君宝臣','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"五福几人全。我见君侯得处偏。素著蔼然乡曲誉，喧传。尽道新官似旧官。\",\"五裤复歌廉。竹马儿童更可怜。万室春风和气里，鸣弦。好似今年胜去年。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b24314d27a2e8128f8','满江红·清明与诸生登西硙柏冈','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"欲把长绳，维白日、暂留春住。亲友面、一回相见，一回非旧。扰扰胶胶尘世事，不如人意十常九。向斜阳、无语倚危楼，空搔首。\",\"活国手，谈天口。都付与，尊中酒。这情怀又是，去年时候。风外纷纷飞絮乱，柳边湛湛长江去。问老来、还有几多愁，愁如许。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b248f9daa4732ce491','点绛唇·暮夜晨起书所见','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"爱酒渊明，无钱休对黄花语。一杯谁举。寂寞空归去。\",\"屋上青山，山上行云度。悠然处。是中真趣。欲写还无句。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b24af6cd7db3496c54','水龙吟·寿舍弟菊轩','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"天高秋气初清，姑山汾水增明秀。黄花红叶，输香泛滟，恰过重九。细捻金蕤，旋题新句，满斟芳酒。况人生自有，安排去处，须富贵、何时有。\",\"休说山中宰相，也不效、斜川五柳。锄犁自把，山田耕罢，双牛随后。经史传家，儿孙满眼，渐能承受。待与君坐阅，庄椿岁月，作皤然叟。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b24d9592fc39047ca2','西江月·久雨新霁秋气益清与二三子登高赋之','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"人与寒林共瘦，山和老眼俱青。琤然一叶不须惊。叶本无心入听。\",\"气爽云天改色，潦收烟水无声。夕阳洲外井禽鸣。涵泳一江秋影。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b25ee6d3827b82c054','汉宫春·纯甫生朝，且有弄璋之喜，赋此以贺','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"公子归来，笑平生湖海，豪气依然。黄金散尽，落魄谁识当年。世闲底物，解挽回、镜里朱颜。人共道，愁须殢酒，几回推向尊前。\",\"闻说梦熊初兆，喜一枝慰眼，岁晚流连。蟠桃会须结子，运偶三千。诗书旧业，要他时、分付青毡。□□□、庭阶照映，卧看玉树芝兰。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b260640171c7519119','诉衷情·初夏偶成','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"东风帘幕雨丝丝。梅子半黄时。玉簪微醒醉梦，开却两三枝。\",\"初睡起，晓莺啼。倦弹棋。芭蕉初绽，徙倚湖山，彩笔题诗。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2641ed81d9361517a','望月婆罗门引癸卯元宵与诸君各赋词以为乐寂寞山村无可道者因述其昔年京华所见以望月婆罗门引歌之酒酣击节将有堕开元之泪者','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"暮云收尽，柳梢华月转银盘。东风轻扇春寒。玉辇通宵游幸，彩仗驾双鸾。闻鸣弦脆管，鼎沸鳌山。\",\"漏声未残。人半醉、尚追欢。是处镫围绣毂，花簇离鞍。繁华梦断，醉几度、春风双鬓斑。回首处、不见长安。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2641ed81df1da2550','望月婆罗门引癸卯元宵与诸君各赋词以为乐寂寞山村无可道者因述其昔年京华所见以望月婆罗门引歌之酒酣击节将有堕开元之泪者','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"凤城春好，玉箫金管恣游盘。梅妆犹怯轻寒。一曲清平妙舞，掌上看回鸾。渐霓裳欲遍，翠敛春山。\",\"良宵易残。歌别鹤、惜馀欢。眼底浮华自满，尘浣吟鞍。瘴半私酒，若真是、京东夫子斑。身幸健、敢复求安。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b26e8d2e732012719c','大江东去和答卫生袭之','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"道人活计，本清虚挂壁，素琴而已。底事中心尝悁悁，不足一人之毁。人果何尤，天无可怨，政欲求诸己。愧生于内，赧然其颡流泚。\",\"圣道不远于人，步趋进退，谁复能违此。好把藩篱多剖却，看取成蹊桃李。笃敬忠诚，尚行蛮貊，岂不行州里。三薰三沐，为君准拟沈水。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b27b717ba93890a0f9','蝶恋花·寿卫生袭之','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"二月山城春尚未。柳弄东风，恰吐黄金蕊。占断溪头佳丽地。多君先得闲中趣。\",\"我为虚名相绊系。君自深藏，不识愁滋味。世事无劳深著意。年丰酒贱须勤置。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2841648322c76400a','大江东去送杨国瑞西行兼简仲宣生','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"悲哉秋气，觉天高气爽，淡然寥泬。行李匆匆人欲去，一夜征鞍催发。落叶长安，雁飞汾水，怕见河梁别。中年多感，离歌休唱新阕。\",\"暮雨也解留人，檐声未断，窗外还骚屑。满眼清愁吹不散，莫倚心肠如铁。面目沧浪，齿牙摇落，鬓发三分白。故人相问，请君还为渠说。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2921872ed344c24d2','满江红·登河中鹳雀楼','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"古堞凭空，烟霏外、危楼高矗。人道是、宇文遗趾，至今相续。梦断繁华无觅处，朱甍碧甃空陈迹。问长河、都不管兴亡，东流急。\",\"侬本是，乘槎客。因一念，仙凡隔。向人闲頫仰，已成今昔。条华横陈供望眼，水天上下涵空碧。对西风、舞袖障飞鸿，沧溟窄。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2981f1e35aa7e4fcb','满江红·重九日山居感兴','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"五柳成阴，三径晚、宦游无味。还自叹、迎门笑语，久须童稚。归去来兮尊有酒，素琴解写无弦趣。醉时眠、推手遣君归，吾休矣。\",\"富与贵，非吾事。贫与贱，宁吾累。步东篱遐想，昔人高致。霜菊盈丛还可采，南山依旧横空翠。但悠悠、一点会心时，君须记。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b298741ad3fdabaf2e','卜算子·正月上旬夜，梦寐闲闻雪作。诘旦起视，但云烟出没，晓山浓淡如画，西望长河仅一发耳，作长短句以写一时胜概','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"淡月晃书窗，夜静云撩乱。敧枕萧萧听雪声，落叶闲阶满。\",\"清晓独开门，淡荡东风软。诗句成时堕渺茫，眼底江天远。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b29c3767d33e95d88e','满江红·过汴梁故宫城二首','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"塞马南来，五陵草树无颜色。云气黯、鼓鼙声震，天穿地裂。百二山河俱失险，将军束手无筹策。渐烟尘、飞度九重城，蒙金阙。\",\"长戈袅，飞鸟绝。原厌肉，川流血。叹人生此际，动成长别。回首玉津春色早，雕栏犹挂当时月。更西来、流水绕城根，空呜咽。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b29c3767d3fa3c5266','满江红·过汴梁故宫城二首','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"尘满貂裘，依旧是、新丰羁客。还感慨、中年多病，惟堪眠食。方寸玉阶无地借，诗书勋业休重忆。况而今、双鬓已成丝，非畴昔。\",\"兴废事，吾能说。今古恨，空填臆。向南风望断，五弦消息。眯眼黄尘无处避，洗天风雨来何日。待酒酣、慷慨语平生，无人识。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2cbcc12c2cc96fe50','大江东去次韵答彦衡','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"无堪老懒，喜春来蔬笋，劝加餐食。底事东君留不住，忙似人闲行客。忧喜相寻，利名羁绊，心自无休息。不如闲早，付他妻子耕织。\",\"门外柳弄金丝，落花飞不起，东风无力。浊酒一杯谁送我，欢意都非畴昔。致主无心，苍颜白发，敢更希前席。功名蛮触，何须千里追北。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2cd66afebf665fbce','浣溪沙·元夜后一日史生仲恭久客初还酒闲喜为赋此','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"马上风吹醉帽偏。一川晴雪袅吟鞭。冷云堆里指家山。\",\"慈母已占乌鹊喜，佳人望月拜婵娟。今宵人月十分圆。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2d0d3fa00a829a392','浣溪沙·寿菊轩弟','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"白发相看老弟兄。闲身无辱亦无荣。儿孙已可代躬耕。\",\"了却文章千载事，不须谈笑话功名。青山高卧待升平。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2d5c6de2aee2531a8','满江红·遁庵主人植菊阶下秋雨既盛草莱芜没殆不可见江空岁晚霜馀草腐而吾菊始发数花生意凄然似诉余以不遇感而赋之因李生湛然归寄菊轩弟','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"雨后荒园，群卉尽、律残无射。疏篱下、此花能保，英英鲜质。盈把足娱陶令意，夕餐谁似三闾洁。到而今、狼藉委苍苔，无人惜。\",\"堂上客，头空白。都无语，怀畴昔。恨因循过了，重阳佳节。飒飒凉风吹汝急，汝身孤特应难立。漫临风、三嗅绕芳丛，歌还泣。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2d922008750593045','满江红·寿陈丈良臣','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"有妫之后，国于陈、庆流苗裔。还世有、异人闲出，簪缨不替。别派中分汾水右，英灵吸尽姑山气。看凤麟、来应太平期，为佳瑞。\",\"都未展，经邦志。医与卜，浑馀事。把活人手段，此中聊试。德义不孤朋友乐，田园粗了儿孙计。有鬓丝、禅榻老生涯，栖心地。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2d9e4a27bd2909aca','满江红·梦庵张君信夫生朝','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"腊尽春来，还又是、新年入手。人共喜、丹山仙桂，一枝初秀。转首黄金都散尽，酒酣弹铗蛟龙吼。想平生、豪气尚依然，冲星斗。\",\"红未透，花枝瘦。人不老，花依旧。老生涯正要，东山歌酒。翠壁撑空山玉立，长河泻浪风雷走。挽山河、胜概入金尊，为君寿。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2db1d2b6e2bdc1d1f','水调歌头·癸卯八月十七日逆旅平阳夜闻笛声有感而作','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"乱云低薄暮，微雨洗清秋。凉蟾乍飞破镜，倒影入南楼。水面金波滟滟，帘外玉绳低转，河汉截天流。桂子堕无迹，爽气袭征裘。\",\"广寒宫，在何处，可神游。一声羌管谁弄，吹彻古梁州。月自于人无意，人被月明催老，今古共悠悠。壮志久寥落，不寐数更筹。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2dbc1fc65fdabaf2e','卜算子·正月上旬夜梦寐闲闻雪作诘旦起视但云烟出没晓山浓淡如画西望长河仅一发耳作长短句以写一时胜概','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"淡月晃书窗，夜静云撩乱。敧枕萧萧听雪声，落叶闲阶满。\",\"清晓独开门，淡荡东风软。诗句成时堕渺茫，眼底江天远。\"]',NULL,NULL,NULL,NULL),('c16655df22b2f8b2f57cd49b13eaf4e1','满庭芳·雪夜用前韵','段克己','c16655df22b2f8b2d1457b72d1457b72','元',NULL,'[\"万籁收声，六花呈瑞，小桥路断江边。笑潮州刺史，匹马蓝关。漫有清名千载，一杯酒、孤负生前。争如我，围炉小酌，和气笑中还。\",\"梅梢新月上，芒鞋竹杖，烂赏林闲。便销金低倡，欲换应难。勋业何须看镜，蓬窗底、空卧袁安。君须记，游鱼失水，那可上长竿。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f21368c914417222c0','临江仙','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"打破这阳炉阴鼎，跃番黑汞红铅。龟蛇龙虎总虚言。六根并四大，阴物一齐捐。\",\"有个天然真一种，非心非肾非玄。不容口意识情传。修丹如此到，可谓大罗仙。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f21368c91469e62011','临江仙','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"服气咽精吞七曜，存思想化三车。飞罡噀水散名花。词章勤奏达，吟咏酌流霞。\",\"外事何干于内境。有为功力何赊。饶君作福等河沙。不明玄牝窍，特地隔天涯。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f21368c9147760e4b6','临江仙','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"午夜金乌从海底，丑初渐出扶桑。大明东向属三阳。韶华春满院，无地不生光。\",\"直驾河车朝帝阙，泥丸宫饮琼浆。华池神水灌中黄。婴儿未就母，地久更天长。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f21368c91483bf5706','临江仙','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"学道要明心地印，更须阴阳同行。不能及物只为身。小乘无智慧，大道几时成。\",\"一种贡高夸自会，妄言眩惑人情。只图博取利和名。不思生死苦，万劫路冥冥。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f21368c9148f93d62e','临江仙','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"促取一年中日月，来归片刻之中。坎离交媾虎龙通。三花时就鼎，一霎现神功。\",\"满地黄芽□白雪，辉腾南北西东。二弦合□甲庚同。骊珠初出水，焰慧自烧空。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f28d41f3874733a3a6','西江月','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"四大一身假合，六根五蕴阴邪。一元阳处是吾家。休认形骸野马。\",\"内院神京须出，天堂地狱空华。玄玄难测此幽遐。妙用有谁知者。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f28d41f38768413ace','西江月','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"大道不由别物，只由心上行持。念头才动汞铅飞。止要定心专气。\",\"杀盗淫邪不纪，利名恩爱休迷。无心无事是丹基。说破元来容易。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f28d41f3876f31f000','西江月','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"释氏禅经律论，儒家传记诗书。老君三六部真符。止论一心两字。\",\"了得一明心地，诸余土苴何须。忘形忘气总归虚。到此实非譬喻。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f28d41f3877d5c566a','西江月','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"一意中宫不动，四方四兽称臣。内丹外药合天真。现出一轮宝镜。\",\"此景此时此地，无心无我无人。不知谁是本来心。湛寂真常妙境。\"]',NULL,NULL,NULL,NULL),('c16655df23ee73f28d41f387ed9eb528','西江月','牧常晁','c16655df23ee73f2d1457b72d1457b72','元',NULL,'[\"多少外寻卦象，不知返照真元。此身元是易之门。昼夜坎离常运。\",\"乌兔马牛龙虎，无非借喻为根。屯蒙既未定晨昏。定意专心为本。\"]',NULL,NULL,NULL,NULL),('c16655df2443e68730686dfad243fb5f','沁园春·渔','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"不种田园，投闲江海，远绝尘踪。保一家性命，扁舟为屋，随机应舵，逐浪乘风。九曲江头，三元潭里，直把银钩堕水中。波深底，把金鳞钓出，回棹孤峰。\",\"三男三女和同。向砂锅净洗热炉烘。或敲冰煮茗，渴饮一碗，得鱼换酒，共酌三钟。蓑衣解开，箬笠放下，醉唱升平月满篷。江天阔，看一篙点处，粉碎虚空。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6873acdae5cecbc2dde','沁园春·虎','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"生在西山，常居东谷，出没无时。向枯树岩前，幽泉涧畔，饥餐渴饮，饱暖随宜。一任纵横，平生勇猛，走入丛林万木披。谁知得。但无忧无惧，断绝狐疑。\",\"等闲剔起双眉。有万里风生八面威。自踏叶巡山，不离元所，一灵不昧，百兽皈依。跳下悬崖，咆哮振地，月白山寒水满溪。收牙爪，且藏身遁迹，独步云归。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687571d611af1bed448','水调歌头·授混中子口诀','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"大道本无象，真性亦非空。其中造化有无，无有混玄同。透得玄关一窍，便好回头下手，静里要勤工。配合些儿妙，朝暮用屯蒙。\",\"采真铅，炼真汞，复真宗。阳生半夜，重关深锁倒骑龙。运起周天火候，流戊擒精就己，三性会元宫。朗朗超今古，神应妙无穷。\"]',NULL,NULL,NULL,NULL),('c16655df2443e68760f7731da224cdb0','百字令释宗','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"真宗顿悟，理幽微、了了无言可说。旷劫至今全体具，湛寂元无生灭。拂尘拈花，穿衣吃饭，觌面分明泄。头头皆是，何须特地差别。\",\"为言向上家风，纤锋快利，透石刚如铁。三界圆通无所住，随处应机明彻。芥纳须弥，珠含罔象，朗耀悬秋月。寸丝不挂，即同诸佛齐列。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6877951dfed5363a599','水调歌头·授回阳子口诀','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"性本无修证，命乃有施为。了明此理，道凭玄牝作根基。要得谷神不死，好住西南村里，更莫起狐疑。动静分双用，下手要知时。\",\"玩真空，调真息，运真机。铅生汞产，封闭丹炉炼紫芝。拨转银河斗柄，抽出坎中一画，直去补南离。行满功成日，飞步上天墀。\"]',NULL,NULL,NULL,NULL),('c16655df2443e68779cf26e753025ca3','沁园春·赠混真子只诀','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"道隐无名，包含万象，总在身心。若一言勘破，本来面目，不生不灭，耀古腾今。返照回光，存神绝念，直下承当莫外寻。忠言守，这些儿妙处，至理玄深。真中不许容针。\",\"好把阳炉炼浊阴。待阳生冬至，重门深锁，乌飞兔走。虎啸龙吟。片饷回旋，半时交姤，大地山河尽变金。超三界，作蓬莱仙子，寒暑无侵。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687821a45911434bb66','沁园春·耕','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"八口之家，五亩之宅，树之以桑。买吴牛十角，拖犁拽耙，耕翻大地，泼草开荒。下种依时，移苗应候，裁向中田灌水浆。灵苗长，看盈畴遍野，处处风光。\",\"秋来雨顺时康。有黍米凝珠滴露香。向六时勤顾，三更不睡，呼男呼女，采取回仓。击坏讴歌，烹鸡酌酒，妇乐夫欢饮一觞。无他事，但存诚守朴，稽首羲皇。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6878690bcd1effd2906','沁园春·全真家风','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"不恋功名，不求富贵，不惹闲非。盖一间茅屋，依山傍水，甘贫守道，静掩柴扉。读会丹经，烧残宝篆，终日逍遥任自为。真堪悦，遇饥来吃饭，冷即穿衣。\",\"个中消息谁知。自里面惺惺外若痴。且藏锋挫锐，先人后己，和光混俗，岂辨高低。处世随缘，乐天知命，白雪壶中配坎离。时来到，与十洲仙子，同驾云归。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6878d41f38716051f91','西江月','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"离位翻为姹女，坎宫却是婴儿。自因两个隔东西。始用黄婆通意。\",\"每遇阳生冬至，方能夫唱妻随。一时齐会赴瑶池。共饮刀圭欢戏。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6878d41f3879ab42f55','西江月','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"静里含光默默，玄中调息绵绵。真空洞彻量无边。亲见本来身面。\",\"虽是虚灵不昧，也须添汞抽铅。常加水火鼎炉煎。便有神光发现。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6878d41f387d04ecaaa','西江月','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"入室先知下手，安炉要会施工。癸生急采向黄中。逆把天轮旋动。\",\"赤子飞神入海，苍龙骑上南宫。片时啸咏下瑶空。十极神仙皈奉。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6878d41f387e3fda9a5','西江月','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"学道先明玄牝，修真须用工夫。三般药物不偏枯。也要坚刚一悟。\",\"月下擒来玉兔，海中捉出金乌。半时运火入坤炉。炼得神丹坚固。\"]',NULL,NULL,NULL,NULL),('c16655df2443e6878d41f387e8517096','西江月','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"悟性无生无灭，采铅有去有来。两般灵物结成胎。云气常浮宝盖。\",\"终日和光混俗，任时随处宽怀。清风明月满蓬莱。睹破虚空无碍。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687a35ee0da95da4f56','沁园春·真风子','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"自小颠狂，平生落魄，放浪飘蓬。把三教玄机，从根识破，包含万象，混沌家风。问释谈机，问儒说理，问道言丹守个中。无拘执，但闲来捉虎，怒后擒龙。\",\"不分南北西东。信步逍遥到处通。向太华峰头，瑶池会上，诗吟万字，酒饮千钟。足下云生，袖中雷起，剑吐寒光射九重。真风子，出乾坤之外，劈碎虚空。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687b27cdc1a15ad66a3','百字令道宗','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"修真立鼎，炼金丹、只用三般灵药。冬至阳生忙下手，采取也须斟酌。抽坎填离，流精进火，正轴旋匡廓。赤子乘龙，一时骑上南岳。\",\"乾宫交姤归来，虎龙降伏，夫妇同欢乐。巽户双开大火然，九转丹凝旁薄。玄谷风生，性天云散，万道神光烁。行功完满，他年回驾鸾鹤。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687c1a31b5464939fed','百字令','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"圣人传道，执其中、妙在惟精惟一。放则周流弥六合，卷则退藏于密。格物致知，正心诚意，静里包皇极。居仁由义，应机不费毫力。\",\"四时天地同参，火符合候，默默存真息。三五归元至德纯，保合太和冲溢。体用一原，显微无间，尽性穷端的。死生勘破，到头还是空寂。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687d8a8d00993663fab','沁园春·龙','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"静藏深渊，动游碧落，灵妙潜通。遇时至神知，乘阳便起，雷轰电掣，雾霭云从。展尾昂头，穿山透石，一举冲霄气势雄。神威烈，把黄河倒卷，白浪翻空。\",\"须臾霖雨漂洪。却都在天瓢一滴中。发通身欻火，飞光走焰，山精鬼怪，绝迹潜踪。变化无方，去来无碍，自得乾坤造化功。功成后，但归心明主，永效年丰。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687e04af516a0bb8dc0','沁园春·牧','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"赤脚蓬头，蓑衣箬笠，随处安然。守无角牛儿，不离左右。同行同住，同坐同眠。常在家山，匀调水草，拗性之时着一鞭。牧回处，看辽天鼻孔，软索低牵。\",\"闲来渴饮灵泉。把短笛横吹下岭前。喜牛儿安静，清风凉彻，放开心地，万事由天。彼此相忘，形神俱妙，花满前村水满川。真消息，有世人问我，起手擎拳。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687e5f1dfee6bff5e31','沁园春·樵','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"家住孤峰，门临曲涧，木作横桥。与世事相违，心求活计，蓑衣挂体，斧柄悬腰。出入玄关，往来幽径，直上高山采古樵。时攀就，把千枝攒簇，一担平挑。\",\"归来快乐逍遥。且放下千斤饮一瓢。这日用家常，随缘随分，今朝明日，自有新条。一曲高歌，三声短笛，遏断行云透碧霄。其中乐，有山猿献果，四皓相招。\"]',NULL,NULL,NULL,NULL),('c16655df2443e687e71cae3e6bca9b47','沁园春·三教一理','王玠','c16655df2443e687d1457b72d1457b72','元',NULL,'[\"道曰金丹，儒曰太极，释曰玄珠。矧三教之道，本来同祖，心存至德，性悟真如。阖辟机关，抽添运用，返照回光复本初。休分别，那些儿妙处，无字称呼。\",\"虚中状若蓬壶。寂静形忘一也无。问三教根宗，谷神不死，灵源澄彻，诚意如愚。五气朝元，五常合一，五眼圆明烁太虚。仙儒佛，派殊而理一，到底同途。\"]',NULL,NULL,NULL,NULL),('c16655df2489b5e19f8c434a55693d7c','殿前欢','卫立中','c16655df2489b5e1d1457b72d1457b72','元',NULL,'[\"碧云深，碧云深处路难寻。数椽茅屋和云赁。云在松阴。\",\"挂云和八尺琴，卧苔石将云根枕，折梅蕊把云梢沁。\",\"云心无我，云我无心。\"]',NULL,NULL,NULL,NULL),('c16655df250cbdb1617f15b94517b709','和西湖竹枝词','堵简','c16655df250cbdb1d1457b72d1457b72','元',NULL,'[\"港上蘋蒲翠叶齐，凫鸥鸿雁总来栖。劝郎得意且行乐，白苎樽前日易西。\"]',NULL,NULL,NULL,NULL),('c16655df2533e97625665b3670bd86d8','水龙吟','史药房','c16655df2533e976d1457b72d1457b72','元',NULL,'[\"等闲过了清明，草痕深一庭新翠。光风信息，牡丹初褪，茶醾犹未。燕语清圆，梅英松润，困人天气。笑文园倦客，诗才减尽，犹□有，伤春意。\",\"别有留春去里。小房栊、玉英双倚。天香浮动，朱衣乍试，铅华尽洗。一曲琵琶，轻拢挑拨，未觞先醉。又匆匆上马，蓝桥路隔，漫增凝睇。\"]',NULL,NULL,NULL,NULL),('c16655df267d70d0617f15b93490f18c','和西湖竹枝词','屠性','c16655df267d70d0d1457b72d1457b72','元',NULL,'[\"二八女儿双髻丫，黄金条脱银条纱。清歌一曲放船去，买得新妆茉莉花。\"]',NULL,NULL,NULL,NULL),('c16655df2736ab1c617f15b9260e1a13','和西湖竹枝词','燕不花','c16655df2736ab1cd1457b72d1457b72','元',NULL,'[\"湖头春满藕花香，夜深何处有鸣榔？郎来打鱼三更里，凌乱波光与月光。\"]',NULL,NULL,NULL,NULL),('c16655df273a41eeda75ed0db16f5bb0','上升花','吴真人','c16655df273a41eed1457b72d1457b72','元',NULL,'[\"紧锁心猿，悟光阴尘世，百年遄速。下手顿修元本，真灵此日，要住行屋。居家坑堑，先须跳将身己，使令孤宿。静无触。气财色酒，一齐须逐。\",\"俗景般般绝欲。更舍尽爷娘，共妻骨肉。自在逍遥，落魄清闲，认取里头金玉。琼英蕊，花心动，放香味、满空馥郁。异光簇。祥光灿烂，结成仙曲。\"]',NULL,NULL,NULL,NULL),('c16655df27bb5c994bde3461471c2a7c','三月廿日，陈浩然招游观音山，宴张氏楼。徐楚兰佐酒，以琵琶度曲《郯云台》为之心醉，口占《蝶恋花》一阕','顾瑛','c16655df27bb5c99535ab7660e3a37aa','元',NULL,'[\"春江暖涨桃花水，画舫珠帘，载酒东风里。四面青山青似洗，白云不断山中起。\",\"过眼韶华浑有几，玉手佳人，笑把琵琶理。狂杀云台标外史，断肠只合江州死。\"]',NULL,NULL,NULL,'[\"「春江暖涨桃花水」句：櫽括东坡《惠崇春江晚景》句：「竹外桃花三两枝，春江水暖鸭先知。」\",\"画舫（fǎng）：华美的游船。\",\"过眼韶华：人生中的美好时光。晁无咎《梁州令》：「过眼韶华如箭。」\",\"狂杀：夸张地形容游人沉迷于美景良辰。\",\"标外史：将「狂杀云台」一事载于史书逸闻。\",\"「断肠只合江州死」句：举白乐天作《琵琶行》事与「徐楚兰佐酒以琵琶度曲」对。白乐天《琵琶行》：「座中泣下谁最多，江州司马青衫湿。」\"]'),('c16655df27bb5c99fa8e8f34881c7f7b','西湖竹枝词二首','顾瑛','c16655df27bb5c99535ab7660e3a37aa','元',NULL,'[\"素云缺月挂秋河，听得临风《白苎》歌。湖水西来流不断，海潮东去是风波。\"]',NULL,NULL,NULL,NULL),('c16655df27bb5c99fa8e8f34fd6d1552','西湖竹枝词二首','顾瑛','c16655df27bb5c99535ab7660e3a37aa','元',NULL,'[\"陌上采桑桑叶稀，家中看蚕怕蚕饥。大姑要织回文锦，小姑要织嫁时衣。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb4e0b97688424f065','大江东去寿恽东阳丁亥正月十三日生推命者丙戌算因戏及之','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"东阳老子，问天公、乞得两番六十。初度孟陬春未到，却算去年丙戌。火树银花，先开三夜，照耀维摩室。祇园树下，争看无量寿佛。\",\"人道双岘峰头，乾坤清气，钟此弥天释。挂锡丛林三十载，玉检芝泥犹湿。岭上白云，尽堪怡悦，恐被风吹出。法身强健，摩挲时问铜狄。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb58dc9672ffb2c169','念奴娇·寄萧善之','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"绛河明月，到中秋、不比寻常三五。神女梦，寒生嫉妒，特地行云行雨。天上婵娟，人间阴晦，怅望成悽楚。金尊翠袖，澹然相对无语。\",\"遥想天柱峰头，通宵宴赏，此地今何处。争似银桥侵汉表，直入琼楼玉宇。桂树婆娑，羽衣凌乱，偷得霓裳谱。素娥应笑，醉来狂兴如许。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb5d219fcb96385d3c','唐多令·雪','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"春近柳依依。天台客未归。任东阳、减却腰围。手撚梅花吟未了，惊眼底、雪花飞。\",\"相对两忘机。青山陡觉非。想鹅池、夜令难违。谁道子猷豪富甚，银箬笠、玉蓑衣。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb7311c257574f5465','沁园春·赠郑伯洪','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"谷口仙田，瑶草谁栽，山中子真。向紫霞洞里，凿开丹室，素华台上，瞻望飙轮。王子吹笙，洪厓握手，烂醉桃花万树春。都休问、蓬莱清浅，天地氤氲。\",\"有时澡雪精神。诵蕊笈丹书小篆文。更佩联明月，剑寒秋水，或笞鸾凤，或翳麒麟。碧落空歌，紫虚郁秀，径挟飞仙拜玉宸。天门近，听履声直上，高步星辰。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb78cab9c155585280','桂枝香杨润之得家书','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"霜凝翠阁。谩独剪灯花，斜照罗幕。点检芳心旧事，几多成错。清愁正尔无聊赖，听梅花、数声残角。小窗风细，虚檐月转，怎禁寥寞。\",\"笑鬓底、年华老却。问前度刘郎，何处重约。流水桃花，别后几番开落。吾庐三径归来好，任缁尘暗迷京索。凤台人远，雁书频寄，喜占乌鹊。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb8264879b487c9e20','疏影·为刘架閤赋赠妓者','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"缟衣仙子。倚东风花信，先占春色。殢酒含嚬，脉脉无言，青鸟为传消息。暗香一点才浮动，早自有、东君怜惜。想前身、傅粉精神，化作飞琼肌骨。\",\"还向影娥池上，借霓裳一曲，徘徊歌席。清夜梨花，同梦方甘，又似楚云踪迹。新欢旧恨知多少，算檀板、金尊消得。折芳馨、欲寄相思，人在江南江北。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb863f063c235ea765','满江红·归故居答杨润之','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"袖拂西风，临古道、行行且止。遥指处、故园三径，归程十里。老虹青红疏雨外，远山紫翠斜阳里。更澄江、万顷白鸥轻，天连水。\",\"铜花锈，貂裘敝。空负了，平生志。慨几多往事，昨非今是。仙沈腰围缘底瘦，愁潘鬓影今如许。有元龙、百尺最高楼，君来倚。\"]',NULL,NULL,NULL,NULL),('c16655df281460fb8a5360ea8151029c','洞仙歌·漫兴','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"云间倦翼，向林边知倦。回首吾庐梦中见。霜催吟鬓，尘涴征衫，竟孤负、清夜猿惊鹤怨。\",\"绨袍空敝却，岁月无情，赢得当时故人恋。谩索笑，共梅花，弄粉吹香，怕肠断、东风庭院。奈芳草萋萋怨王孙，看江北江南，几回青遍。\"]',NULL,NULL,NULL,NULL),('c16655df281460fbb3e76098dddec2b4','满庭芳·己夘七月十一日得颖','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"露洗新秋，天浮灏气，桐孙初长庭隅。䙀裁红锦，门左记垂弧。白发萱亲笑道，于今见、四叶喜荣敷。天台路，吾儿知不，倩雁报安书。\",\"山中何所有，石田茅屋，菊径瓜区。更寒窗老砚，他年尽传渠。愿汝身如犊健，看书罢、更学把犁锄。称乡里，善人可矣，卿相又何如。\"]',NULL,NULL,NULL,NULL),('c16655df281460fbb90b1d301c4cf8de','满江红·天台道中','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"翠逼篮舆，天台路、树迷烟际。襟袖冷、朔风初定，露华才坠。珠斗横空孤嶂远，金波摇月寒潭碎。问刘郎、采药遇神仙，知何地。\",\"终不负，风云志。还有待，江山意。想琐窗应念，故人归去。翠羽梅花山下梦，青衫枫叶江头泪。算邮亭、一曲好姻缘，何时会。\"]',NULL,NULL,NULL,NULL),('c16655df281460fbdedfd82fa8f8e1b4','最高楼寄谢国芳','吴景奎','c16655df281460fb459a4ddcb8c27b7a','元',NULL,'[\"西池草，和梦泛晴晖。几度见春归。寒沙盟冷鸥先觉，秋江影落雁初飞。故园荒，征路远，信音稀。\",\"笑逆旅、光阴忙似瞥。更好染、髭须何用镊。惊夜杵、捣寒衣。桃花流水应无恙，小山丛桂更畴依。早归来，新酒熟，菊成围。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e5170683e7d3f5e','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"杉闸奔湍，一塘远接吴淞水，两行垂柳绿如云。今古送人行。买妻耻醮藏羞墓。\",\"秋茂邮亭递书处。路逢樵子莫呼名。惊起墓中灵。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e517068634da444','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"三塔龙潭，古龙祠下千年迹，几番残燬喜犹存。静胜独归僧。阴森一径松杉直。\",\"楼阁层层曜金碧。祈丰祷旱最通灵。祠下暮云生。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e5170686664e9c4','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"一掌春波，矗矗鹾帆闹如市，昔年烟雨最高楼。几度暮云收。三贤古迹通岐路。\",\"窣堵玲珑插濠罟。荷花袅袅间菰蒲。依约小西湖。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e5170686d6bd4da','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"湖合鸳鸯，一道长虹横跨水，涵波塔影见中流。终日射渔舟。彩云依傍真如墓。\",\"长水塔前有奇树。雪峰古甃冷于秋。策杖几经游。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e5170687bb1d6b8','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"百亩胥峰，道是子胥磨剑处，嶙峋白石几番童。时有兔狐踪。山前万个长松树。\",\"下有高人琴剑墓。周回苍桧四时青。红日战涛声。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e5170688943bd80','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"粉堞危楼，阑下波光摇月色，金鱼池畔草蒙茸。荒圃瞰楼东。亭亭遥峙梁朝桧。\",\"屈曲槎枒接苍翠。独怜天际欠青山。却喜水回环。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e517068dea8392d','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"万寿山前，屹立一亭名槜李，堂阴数亩竹涓涓。空翠锁风烟。骚人隐士留题咏。\",\"红尘不到苍苔径。子瞻三过见文师。壁上有题诗。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d1e517068ed5467bf','忆馀杭嘉禾八景空翠风烟','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"一甃幽澜，景德廊西苔藓合，茶经第七品其泉。清冽有灵源。亭间梁栋书题满。\",\"翠竹潇森映池馆。门前一水接华亭。魏武两其名。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154013b6a94','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"风搅长江浪搅风。鱼龙混亲一川中。\",\"藏深浦，系长松。直待云收月在空。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e15419669eaa','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"桃花波起五湖春。一叶随风万里身。\",\"钓丝细，香饵匀。元来不是取鱼人。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154205f7d67','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"如何小小作丝纶。只向湖中养一身。\",\"任公子，尔何人。枉钓如山截海鳞。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e1542a4ce417','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"极浦遥看两岸斜。碧波微影弄晴霞。\",\"孤舟小，去无涯。那个汀洲不是家。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e15443a68cf7','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"残霞返照四山明。云起云收阴复晴。\",\"风脚动，浪头生。听取虚篷夜雨声。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e15444fcdb18','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"五岭风光绝四邻。满川凫雁是交亲。\",\"云触岸，浪摇身。青草烟深不见人。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e15468b5dada','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"钓得鲜鳞拽水开。绿萍漾漾逐钩来。\",\"摇赪尾，噞红腮。不羡严陵坐钓台。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e1546d522c4c','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"残阳浦里漾渔船。青草湖中欲暮天。\",\"看白鸟，下平川。点破潇湘万里烟。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e1546d8476b2','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"雪色髭须一老翁。欲将短棹拨长空。\",\"微有雨，正无风。宜在五湖烟水中。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e1546e8f27e9','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"重整丝纶欲掉船。江头新月正明圆。\",\"酒瓶倒，岸花悬。抛却渔竿和月眠。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e1548cca9b23','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"绿杨湾里夕阳微。万里霞光浸落晖。\",\"击楫去，未能归。惊起沙鸥扑鹿飞。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154a30f02e6','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"舴艋舟人无姓名。葫芦提酒乐平生。\",\"香稻饭，滑莼羹。掉月穿云任性情。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154c853ad48','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"月移山影照渔船。船载山行月在前。\",\"山突兀，月婵娟。一曲渔歌山月边。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154e58957e8','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"无端垂钓空潭心。鱼大船轻力不任。\",\"忧倾倒，系浮沉。事事从轻不要深。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154fa8ea58c','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"舴艋为舟力几多。江头云雨半相和。\",\"殷勤好，下长波。半夜潮生不那何。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d4611e154ff25bb8b','渔父','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"目断烟波青有无。霜凋枫叶锦模糊。\",\"千尺浪，四腮鲈。诗筒相对酒胡芦。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d68e8bd607e128347','金字经梅边','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"雪冷松边路，月寒湖上村。缥缈梨花入梦云。巡。\",\"小檐芳树春。江梅信。翠禽啼向人。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d69a5ee28111d632c','沁园春·题画骷髅','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"漏泄元阳，爷娘搬贩，至今未休。吐百种乡音，千般扭扮，一生人我，几许机谋。有限光阴，无穷活计，汲汲忙忙作马牛。何时了，觉来枕上，试听更筹。古今多少风流。\",\"想蝇利蜗名谁到头。看昨日他非，今朝我是。三回拜相，两度封侯。采菊篱边，种瓜园内，都只到邙山土一邱。惺惺汉，皮囊扯破，便是骷髅。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d8f8c4ca03b1f3199','渔父三首','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"点点青山照水光。飞飞寒雁背人忙。\",\"冲小浦，转横塘。芦花两岸一朝霜。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d8f8c4ca0508a67ea','渔父三首','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"红叶村西夕照馀。黄芦滩畔月痕初。\",\"轻拨棹，且归与。挂起渔竿不钓鱼。\"]',NULL,NULL,NULL,NULL),('c16655df2897e56d8f8c4ca0b8119195','渔父三首','吴镇','c16655df2897e56dda11e8cdaf473271','元',NULL,'[\"醉倚渔舟独钓鳌。等闲入海即乘潮。\",\"从浪摆，任风飘。束手怀中放却桡。\"]',NULL,NULL,NULL,NULL),('c16655df2a0fca4df22e8d7a6999b348','西湖竹枝词','富恕','c16655df2a0fca4dd1457b72d1457b72','元',NULL,'[\"十里荷花锦一机，雨馀荷气扑人衣。满船游女蒙白苎，阵阵腥风鸥鹭飞。\"]',NULL,NULL,NULL,NULL),('c16655df2ba691040b7c13f455ba350b','风入松·和贝廷琚助教韵','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"谁教齿豁更头童。从唤作衰翁。惜花已自因花瘦，况飘零、万点随风。须信人生如梦，休言世事皆空。\",\"紫骝嘶过画桥东。犹记软尘红。重来绿遍西湖路，消魂是、杜宇声中。经眼倚妆飞燕，伤心照影惊鸿。\"]',NULL,NULL,NULL,NULL),('c16655df2ba691040d8a2c06ad45f5e8','木兰花慢·寿侄云逵时七月十四日也','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"问初秋明月，更一夕、十分圆。喜鸿雁行中，居然生子，眉目娟娟。只今年逾弱冠，遇生朝刚要赋诗篇。莫学汝家痴叔，蹉跎已傍中年。\",\"博山风袅水沉烟。绛蜡照华筵。有檀板歌词，金樽劝酒，银甲弹弦。阶庭旧时玉树，正朱颜绿鬓似神仙。丹桂不妨似窦，紫荆还见如田。\"]',NULL,NULL,NULL,NULL),('c16655df2ba691043186693a5b621aa7','风入松·寿郭仲和','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"传家应自郭汾阳。馀裔在钱唐。傍人莫笑儒酸在，亲曾接、鹓鹭班行。赢得身心稳住，须鬓却苍苍。\",\"中和相隐旧书堂。弦诵乐时康。梅花枝上春犹小，朋簪盍、次第称觞。莫问商瞿年纪，也须载弄之璋。\"]',NULL,NULL,NULL,NULL),('c16655df2ba691044334969f0f3649aa','蝶恋花','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"过雨春波浮鸭绿。草阁三间，人住清溪曲。旧种小桃多似竹。乱红遮断松边屋。\",\"有客抱琴穿翠麓。隔水呼舟，应是怜幽独。历历武陵如在目。几时同借仙源宿。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104618143341575e257','无俗念','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"等闲屈指，算今来古往，谁为英杰。耳目聪明天赋予，怎肯虚生虚灭。去燕来鸿，飞乌走兔，世事何时歇。风波境界，大川不用频涉。\",\"踏遍万户千门，五湖四海，一样中秋月。正面相看君记取，全体本来无缺。空里非空，梦中真梦，莫对痴人说。便须骑鹤，夜深朝礼金阙。\"]',NULL,NULL,NULL,NULL),('c16655df2ba6910464068b6f78da8e14','蝶恋花·杏庄为莫景行题','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"一色杏花三百树。茅屋无多，更在花深住。旋压小槽留客醉。举杯忽听黄鹂语。\",\"醉眼看花花亦舞。风卷残红，飞过邻墙去。恰似牧童遥指处。清明时节纷纷雨。\"]',NULL,NULL,NULL,NULL),('c16655df2ba6910477f3f1d490f32551','木兰花慢·赋白莲和宇舜臣韵','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"怅波翻太液，谁留住、蕊珠仙。向水殿云廊，玉容花貌，几度争鲜。人间延秋无计，掩霓裳、犹忆舞便娟。画里倾城倾国，望中非雾非烟。\",\"雁飞不到九重天。水调谩流传。柰花老房空，菂存心苦，藕断丝连。西风环佩轻解，有冰弦、谁复记华年。留得锦囊遗墨，魂消古汴宫前。\"]',NULL,NULL,NULL,NULL),('c16655df2ba6910484b499d6704c29bd','定风波·慢商角调赋崔莺莺传','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"把丽情、分付良工，传奇谩为重省。开户迎风，拂花动月，写尽西厢景。笑书生，最侥幸。刚道师婚胜琴聘。沉静。问姓名非是，近时三影。\",\"空思蔗境。画盐梅、不济调羹鼎。翻残金旧日，诸宫调本，才入时人听。减容光，懒窥镜。凤枕鸳衾与谁□。重赠。李绅歌意，续微之咏。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104860700215753f3ac','瑶华慢赋雪','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"喜深惊密。试问滕神，有多少寒力。如圆似莹，总疑是、满眼隋珠和璧。五更书幌，早已觉、寒光穿隙。料禽影、波上渐稀，径里悄无行迹。\",\"有人独倚危楼，望千里江山，高下同色。坡仙饮处，甚尚然、不减醉翁宾客。等闲评论，算柳絮、梨花非白。画堂深、弦管方调，一任低垂帘额。\"]',NULL,NULL,NULL,NULL),('c16655df2ba691049899e8b801453d86','渔家傲·寿杨复初','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"采芝步入南山道。道深宛似蓬莱岛。闻说村居诗思好。还被恼。苍苔满地无人扫。\",\"载酒亭前松合抱。客来便许同倾倒。玉兔已将灵药捣。秋意早。月华长似人难老。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104a45d9b8a9c0a7a8d','浪淘沙·赋元夕遇雨次俞紫芝韵','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"雨打上元灯。无处邀朋。何如窗下且瞢腾。谩忆牙旗穿夜市，铁马春冰。\",\"歌舞有人曾。倦与争能。越罗裁服换吴绫。尽道先生归隐也，乌帽乌藤。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104aae24bd900ac0cb0','凤凰台上忆吹箫·赋凤仙花','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"菊婢标名，凤仙题品，纷纷随处成丛。甚玉钗浑小，宝髻微松。依旧花分五彩，毗陵画、总付良工。谁为伴，鸡冠染紫，雁阵来红。\",\"玲珑。英英秀质，多想是花神，剪彩铺茸。却易分高下，难辨雌雄。疑把守宫同捣，端可爱、深染春葱。开还谢，从风乱飘，好上梧桐。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f82011e30fc','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"雾驾云軿，雨巾风帽，一剑凌风飞过。下视茫茫，遥观历历，不复更知天大。日月居诸，春秋代谢，几见授衣流火。记岳阳、三度曾游，未必世人知我。\",\"游戏处、柳树为徒，榴皮作字，聊说行藏如左。列豹重关，封狐千里，不满楚人哀些。瓦砾黄金，蓬莱别馆，归去有谁知那。不须分、天上人间，南北东西皆可。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f8208da8469','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"驴背驮诗，鸱夷盛酒，晓踏苏堤残雪。露阙云门，璇阶玉宇，照耀日华光洁。见说孤山，犹存老树，清兴一时超越。画船归、更有渔舟，此景顿成奇绝。\",\"还四顾、表里通明，高低一色，尘土不容毫发。却忆韩郎，花开顷刻，谁得染根仙诀。雪后园林，天开图画，眼界迥然俱别。待黄昏、约取林逋，湖上朗吟香月。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f820dc94f6e','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"君实园中，尧夫窝内，独乐正同安乐。维水泱泱，予怀渺渺，西望每思伊洛。草色随车，花香袭屦，风景至今犹昨。被杜鹃、啼破天津，便觉市朝萧索。\",\"谁复见、万古龙门，三春桃浪，冲岸紫鳞争跃。月到风来，水流山在，安得二贤同酌。心上经纶，鉴中治乱，叹息九原难作。笑红尘、逐利争名，总是蝇头蜗角。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f82134fa3eb','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"智不如愚，辩何如讷，大巧又何如拙。螳黠蝉痴，鹤长凫短，总是一番消歇。逝者如斯，挠之不浊，止水本来明彻。君试看、云散天开，见出自家秋月。\",\"谁种得、白雪黄芽，交梨火枣，不怕北风吹裂。头上簪花，手中拍板，稽首金银宫阙。见说麻姑，高骑鹏背，载酒过门相谒。便呼童、拗折松枝，扫取一庭残雪。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f82180fdf25','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"仙客难逢，白云谁扫，知是采芝何处。皓鹤归来，金鸡啼罢，梦断阆风玄圃。松下高歌，橘中残著，饥后自餐龙脯。待偷桃，曼倩重来，为问木公金母。\",\"君不见、洞里桃花，花间啼鸟，又是一番春暮。弱水三千，巫山十二，指点虚无归路。君抱琴来，我邀君饮，我醉欲眠君去。向人间、游遍朱门，静看只如蓬户。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f822ead78ec','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"身在云间，目穷天际，一带远山如隔。隐隐迢迢，霏霏拂拂，蔓草寒烟秋色。数著残棋，一声长啸，谁识洞庭仙客。对良宵、明月清风，意味少人知得。\",\"君记取、黄鹤楼前，紫荆台上，神有青蛇三尺。土木形容，水云情性，标韵自然孤特。碧海苍梧，白蘋红蓼，都是旧时行迹。细寻思、离乱伤神，莫厌此生欢剧。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f823b587321','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"破帽多情，布帆无恙，兴尽便寻溪转。明月来时，浮云飞尽，千古翠屏宜晚。桂棹兰桨，荷衣芰制，偏称药炉经卷。看轻鸥、点破长烟，一望水连天远。\",\"尝记得、雪满山阴，舟回剡曲，何必过门相见。浩气难消，哀情谁会，万里碧空横剑。一道虹桥，半天龙驾，尚忆幔亭开燕。要长生、须出人间，未卜道缘深浅。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f825fca25a3','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"醉里闲吟，兴来独往，山静悄无人语。两岸桃花，一溪春水，似忆仙源无路。花上莺啼，云间犬吠，偶到洞仙琳宇。便相留、闲话长生，嗟我委形非故。\",\"图画里、昔日天台，当年刘阮，此说荒唐无取。避世秦人，放舟渔子，却恐偶然相与。岭日将沉，林风忽动，吹落半帘红雨。待少焉、月出东方，拄个瘦藤归去。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f827ca2c8ae','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"芳草纤纤，游丝冉冉，可爱地晴江碧。世事浮云，人生大梦，岐路谩悲南北。漉酒春朝，步蟾秋夜，却忆旧时巾舄。问故园、何日归欤，松菊已非畴昔。\",\"谁似我、十亩柔桑，千头佳橘，饱有绿阴朱实。溉釜烹鱼，饭疏饮水，胜咀绛霞琼液。鸟倦知还，水流不竞，乔木且容休息。喜闲来、事事从容，睡觉半窗晴日。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f8292dd9fa9','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"有酒无殽，柰何良夜，客有鲈鱼相惠。如此山川，几何日月，三国竟成何事。目断风帆，江空岁晚，觅句谩思无已。怕多情、笑我华颠，甚矣吾衰久矣。\",\"客知夫、水与月乎，盈亏如彼，逝者有如斯水。舟放中流，听其所止，夜久江流声细。鹤为予来，客辞予去，予亦就眠篷底。忽羽衣、过我林皋，惊觉一番残醉。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f82ad4f07c5','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"玉砌雕阑，朱门紫陌，争似道人茅宇。樽有新醪，盘无兼味，自傍小溪垂缕。富贵何淫，贱贫亦乐，此外更无他语。有时将、明月为家，或共白云为侣。\",\"还俯视、六合之间，茫茫何物，不入灵台丹府。绿水青山，野花啼鸟，已把此心留住。梦熟黄粱，尘飞沧海，转首便为千古。问雪堂、归去何时，江上一犁春雨。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104d9a01f82c156e61a','鸣鹤遗音苏武慢并序','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"云缕虹竿，月钩星饵，海上金鳌曾钓。蓬岛连根，昆仑无外，不比人间嵩少。返本还源，成功满行，坐待紫皇飞诏。笑令威、千岁来归，知换几番华表。\",\"谁曾伴、阿母重游，蟠桃再结，目断西飞青鸟。林屋无扃，洞天不老，铁笛一声云杪。皎皎灵台，荧荧明镜，尘土等闲昏了。看火轮、飞出扶桑，万户千门皆晓。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104e1388e35a498a25c','狮儿词赋梅和仇山村韵','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"蹇驴破帽，知是几度寻春，山南山北。惆怅亭荒仙远，苔枝空绿。村醪正熟。为花醉、何妨留宿。春光似、怕人冷落，先回空谷。\",\"潇洒生意自足。有高标、不厌矮篱低屋。与雪相期，侧耳隔窗虫扑。晚晴纵步，又还信、一枝筇竹。莫嫌独。月在画阑东曲。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104f3d4df82efd3069d','一剪梅·寿俞子中紫芝','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"苍颜白发称乌纱。瘦似黄花。清似梅花。窗吟山月乐无涯。知是儒家。知是仙家。\",\"紫芝服食胜胡麻。不爱繁华。却爱年华。词成笔底是龙蛇。寿酒流霞。寿气成霞。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104fa585360ba9da3d9','沁园春·嘲昆季析居','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"树上凌霄，堂前紫荆，秋来尚芳。柰牝鸡晨语，鹡鸰憔悴，妖狐昼啸，鸿雁分张。仁智匪周，喜忧非舜，一旦天伦忍遂忘。如何好，望松楸感泣，桑梓悲伤。\",\"古今祸起专房。总一国、犹然况一乡。家有妇人，岂无长舌，世无男子，谁有刚肠。树大枝分，瓜熟蒂落，此语应非是义方。聊书此，要惩鉴诫，不在文章。\"]',NULL,NULL,NULL,NULL),('c16655df2ba69104fc86ce475b04359e','满江红·咏梨花鸟图','凌云翰','c16655df2ba69104d1457b72d1457b72','元',NULL,'[\"谁写琼英，空惊讶、年华虚度。依约似、清明池馆，粉容遮路。蝴蝶又来丛里闹，鹪鹩还占枝头语。向东阑、惆怅几回看，愁如许。\",\"疑有月，光摇树。疑是雪，香生处。自洗妆人去，凄凉非故。白发宫娃歌吹远，青旗酒舍诗吟古。记黄昏、灯暗掩重门，听春雨。\"]',NULL,NULL,NULL,NULL),('c16655df2d1d3ddd85f1647f3a8bb94c','玉楼春·宴河中瑞云亭','冯延登','c16655df2d1d3ddda7d8ae45e034fb6b','元',NULL,'[\"长原迤逦孤麋卧。野色微茫河界破。草承行屦绿云深，花触飞丸红雨妥。\",\"高亭初试煎花火。醉玉渐哗春满座。行杯莫压转筹频，佳节等闲飞鸟过。\"]',NULL,NULL,NULL,NULL),('c16655df2d6a4ca125665b369d7a94cb','水龙吟','杨载','c16655df2d6a4ca1c1e39d914671aeaf','元',NULL,'[\"鸿沟定约东归，又谁遣赤龙回指。青娥舞罢，重瞳饮泣，断肠声里。半壁酸风，两淮寒月，古今兴废。眇乌江满眼，惊涛卷雪，分明总是英雄泪。\",\"木末招招舟子，载何人断烟流水。平沙尽处，青山数点，江东千里。长啸风前，无人会我，登临此意。但黄芦古木，夕阳回照，有渔歌起。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af991ce2f87b0451b59f','金字经','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"絮飞飘白雪，鲊香荷叶风。且向江头作钓翁。\",\"穷，男儿未济中。风波梦，一场幻化中。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af994d108839434baef3','水仙子','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"春风骄马五陵儿，暖日西湖三月时，管弦触水莺花市。\",\"不知音不到此，宜歌宜酒宜诗。山过雨颦眉黛，柳拖烟堆鬓丝，可喜杀睡足的西施！\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af9964ad72482f63005d','落梅风','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"因他害，染病疾，相识每劝咱是好意。相识若知咱就里，和相识也一般憔悴。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af9964ad724867fb24c3','落梅风','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"人初静，月正明。纱窗外玉梅斜映。梅花笑人偏弄影，月沉时一般孤零。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af9964ad724897372dbe','落梅风','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"实心儿待，休做谎话儿猜。不信道为伊曾害。\",\"害时节有谁曾见来？瞒不过主腰胸带。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af9964ad7248a3bc7410','落梅风','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"天教富，莫太奢。无多时好天良夜。看钱奴硬将心似铁，空辜负锦堂风月。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af9964ad7248fabd14c4','落梅风','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"心间事，说与他。动不动早言两罢。罢字儿碜可可你道是耍，我心里怕那不怕？\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af9964ad7248fc0cfe68','落梅风','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"蔷薇露，荷叶雨，菊花霜冷香庭户。梅梢月斜人影孤，恨薄情四时辜负。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af996a10f508561b2324','风入松','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"眼前红日又西斜，疾似下坡车。晓来清镜添白发，上床与鞋履相别。\",\"莫笑鸠巢计拙，葫芦提一向装呆。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af997ba7ded029a9f17e','小桃红','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"画堂春暖绣帏重，宝篆香微动。此外虚名要何用？醉乡中。\",\"东风唤醒梨花梦。主人爱客，寻常迎送，鹦鹉在金笼。\"]',NULL,NULL,NULL,NULL),('c16655df2ea1af99b9bd94cc816de64a','庆宣和','马致远','c16655df2ea1af9981e5f81df9be311e','元',NULL,'[\"投至狐踪与兔穴，多少豪杰。鼎足三分半腰折，魏耶？\",\"晋耶？\"]',NULL,NULL,NULL,NULL),('c16655df2ff5ea57022a570306ccdff6','春草碧','钱霖','c16655df2ff5ea57d1457b72d1457b72','元',NULL,'[\"客窗闲理清商谱。弹到断肠声，伤今古。自怜素发无多，犹记纹疏夜深语。空剩旧时踪。迷南浦。\",\"梨花燕子清明，谁家院宇。没个好情怀，杯慵举。天涯行李萧萧，还是新愁老羁旅。那更落花深，红如雨。\"]',NULL,NULL,NULL,NULL),('c16655df2ff5ea57597e81a9464bf292','台城路次邵复孺韵','钱霖','c16655df2ff5ea57d1457b72d1457b72','元',NULL,'[\"碧云深处遥天暮，经年雁书沉影。雨散梅魂，风醒草梦，还见春回乡井。花明柳暝。念贾箧香空，谢池诗冷。流水斜阳，旧家那是旧风景。\",\"怀思横泖雅趣，故人吟啸里，得意酬领。谱缀台城，缄传茜水，肯把俊游重省。凭高倚迥。纵老兴犹浓，不堪驰骋。隔断相思，浦潮波万顷。\"]',NULL,NULL,NULL,NULL),('c16655df2ff5ea57ab28846fbdec4d7b','锁窗寒题玉山草堂','钱霖','c16655df2ff5ea57d1457b72d1457b72','元',NULL,'[\"书带生香，忘忧弄色，四窗虚悄。茅茨净覆，栋宇洗空文藻。卷珠帘，雨痕暮收，绮罗静隔红尘岛。对纸屏素榻，拂潭烟树，扫檐风条。深窈。\",\"西园晓。似日照炉峰，数声啼鸟。璚莲倚盖，晓水靓妆孤袅。浣花溪，尚余旧春，秾芳剩馥吟未了。望东林，小径斜通，梦约香山老。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be002b427f233c404b','定风波','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"功炼心开智藏圆。灵明一点迈婵娟。物外峥嵘惟独步，玄玄。不空不有貌难诠。\",\"触处全伸无碍相，虚空打破应无边。喜脱轮回今度了，功迁。九玄超上太微天。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be002b427f33b90f47','定风波','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一寸丹心碎炼摩。挥开智海息风波。林下乐投甘养素，中和。放开心月照娑婆。\",\"澄净内全三聚戒，安闲身富六波罗。隐显神游浑飘逸，无何。青霄物外恣蹉跎。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be002b427f38f076a5','定风波','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"悟结清凉浩劫因。生涯淡薄出嚣尘。物物般般心上离，思纯。克勤虚谷养元神。\",\"接物和光循德柄，无方应变任天真。豁落圆通精三昧，怀珍。道人才调特清新。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be0a4cff890f62fb46','雨霖铃','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"乾元资始。偶坤柔成物，默契天癸。推移造化清虚，运三要、五行神水。九气腾空六阴静，潜生烟紫。显降升消息还元，玉窟三花吐金蕊。\",\"虚皇宝藏风琴美。送青娥、离宫宴赤子。螺杯滟滟香浮，恣洪饮、写真情喜。物外巍巍，阳体露、出离生死。相旷代、贩骨轮流，了了今生止。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be0a4cff899fcd7be5','雨霖铃','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"万绿心歇。放神情和畅，痼疾蛰窟。清虚道业修崇，蠕神息、运无仓卒。七曜迎真虚室洁，冷辉冰骨。镇九天、十二楼前，五气腾空帐金阙。\",\"高提慧剑仙童谒。迸寒光、尖锋挑日月。收藏炳耀精华，聚壶鼎、激玄霜发。有物生成，无上妙、总归化恍惚。廓性海、浸润无为，绰约三空越。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be13b85d3cd413fe48','烛影摇红·警善恶','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"大道门开，正当甲子天元数。三清仙院列名科，精选须超度。此出幽人理悟。弃荣华、浮云不顾。凿开圆觉，打破虚空，梯登云路。\",\"爱海愚流，乐归觳觥沉诸趣。冥冥深夜昧三光，手足浑无措。缧绁酆都难诉。恨修迟、迷途失步。相跻真境，动扯清风，何时能遇。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be13ef4484983908a9','送入我门来','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"云水闲情，松筠雅操，根穷入道真诀。扫雾驱烟，磨炼志如铁。慨然不改箪瓢乐，廓愚谷，聪明养素拙。放胸襟，取兴狂吟，右笔楮面挥彻。\",\"虎龙发怒，盘旋万丈，艳喷词翰，摛锦清绝。静里功夫，神用至灵泄。如如性月。光全露，表耀古明今圆若缺。混希夷一气，纵横八表，真体无别。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be16b90946114e5fd4','望海潮·真空','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"真空根蒂，全凭神息，分开五蕴精华。和畅六神，氤氲三岛，天河浪泛灵槎。乾鼎煮丹砂。正云驾坤牛，风送羊车。密运黄金井，成灿烂，结琼华。\",\"滋彰极妙生涯。放空圆显理，玄览无瑕。纯粹一真，通天透地，恢弘无障无遮。方寸括河沙。恣兴陪风月，醉卧烟霞。个里真消息，偏分付，道人家。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be1988be79f30749e4','江梅引气邪归正','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"浩然气动混元功。契修崇。运清风。透彻三关，上下自然充。腠外浮游收入内，尽一指，都归玉路中。\",\"神迎天谷傲晴空。养冲融。得疏通。体了无萦，澄湛证参同。物我两忘真豁畅，无为理，至圆成显圣功。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be19d7fa37fd55cfd2','江城子·慢伪坐','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"谷神死不作。蛰窟彼、心迷用功错。妄穿凿。搜玄妙、水谷云根为托。谩消索。扭捏一身常把捉。相专著、地关无锁钥。\",\"天门不折弥封，岂留气，有无若。难为三宫升降，执一性返失，西来糟粕。道情薄。乖魂魄、动静龙虎盘礴。昧橐龠。怎炼玉炉金水结，无丹体、何能云步乐。傥还剔透玄机，会蓬莱约。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be1b7ea21aa0a688ee','南乡子·坎离气变还元','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"癸府隐银铅。朱汞神宫袅瑞烟。变化游魂成风动，枢旋。运入黄庭造化权。\",\"离去水为乾。坤体无阳厚德全。二气经营仙药就，功圆。出世名标闻九天。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be25665b36228dce7b','水龙吟','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"乾坤利判阴阳位。朴散氤氲成器。母怀子腹，儿因母产，杳冥分瑞。体化成纯粹。廓神室、云收尘累。现风清月朗，金童飞舞，傲嘉山，景奇异。\",\"宴罢归来沉醉。到华胥，玉关犹闭。劈开璧帐，主人邀我，密传祖意。岂有三空，体显清净，圆明不二。放纵横自在，无萦无系，与虚空类。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be25665b365f62ab10','水龙吟','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"至人主掌修真柄。格外玄言究竟。澄澄沉虑，绵绵至息，氤氲真性。一混炎风劲。五门开、三奇光并。动盈虚时候，阴阳变泰，显真造，契天命。\",\"整顿神情中正。现长春、风光卓夐。烂霞万缕，烟岚一派，盈空澄净。远近楼台，映奋云步，逍遥歌咏。傲清凉法界，安闲活计，证无为圣。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be25665b367fd44944','水龙吟','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"慧驱茅塞开心地。气养氤氲纯粹。三奇聚鼎，炎炎火里，丹花吐瑞。海底上清吹。异香喷、芳姿肥腻。被木童拈弄，飞行无碍，宝光衬，彩霞炽。\",\"奥室清凉常媚。廓胸襟、冰霜光被。烟消火灭，云收雾敛，顿成不二。头角逍遥，事混河沙，光尘不异。这元初妙有，清虚法体，证菩提位。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be25665b36a8937a19','水龙吟','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"冲和牧养循天数。暗契运行躔度。阴阳恋态，风雷云电，相凝作澍。点滴灵源注。遍浇溉、宿尘难污。显曲江莹静，乌擒玉兔，桂柯宿，吐红雾。\",\"二气氤氲结聚。散金光、三曲密布。炼成健体，童颜滋润，飞行馀裕。神变超尘，趣表清净，真容全露。是劫来一段，通天活计，在人人悟。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be25f1f7cd1c730adb','神光灿','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"神功造化，斡转天轮，闲中默契真修。照耀三光，推移五气添抽。北斗南辰围绕，卷银河、万扔波流。玉关启，运三车衮衮，齐驾金牛。\",\"霹雳轰惊宇宙，红光迸出山川，电掣无休。布设黄芽，霞罩凤阙龙楼。定结圆珠无价，木童喜、秘密牢收。这模样，拉虚空证果到头。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be25f1f7cd3ec4c71b','神光灿','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"悲欢歇念，动静刳心，无人野上恢恢。秀气融融，巧酝鼓润风雷。发挥地天变泰，作甘霖、法种莲开。显嘉瑞，向黄庭锦帐，妙产神胎。\",\"霭霭祥光罩体，九龙水浴出，捧上瑶台。笑傲长春，笙歌闹送金罍。景簇琼英灿烂，醉乘风、飞舞徘徊。得真乐，料前途生灭自摧。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be2dac80e005b5b992','浣溪沙·饮刀圭','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"离坎升沉气不迷。天门减息饮刀圭。月宫飞出日中鸡。\",\"东北朋生调玉兔，两情和气吐虹霓。烹成火枣玉童携。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be2ee238bf3d5b398f','摸鱼儿','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"养疏慵梦惊俗态，浮云富贵虚矫。高提宝剑霜姿冷，蛮触是非齐剿。猿马夭。世务起、心头绊迷相萦扰。冰襟洞晓。乐净境行藏，善芽茂养，饮水识多少。\",\"真氤氲，定海心珠皦皦。凛然云外寂悄。离尘瑞孕丹基造，丽螮蝀神光，晶真了了。破千古昏衢，放彻禅天皎。魂飞杳杳。恣盘礴清微，游扬法界，出露仪表。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be3219200acf60970c','瑞鹤仙·五行生数至成数','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"王行真气集。通玄牝、谷神应时出入。绵绵定呼吸。显平调荣卫，蠕分和辑。脉方动十。得火候、熏蒸自给。\",\"荡百骸、九窍关开，上下溯流相袭。功及三天两地，九六阴通，七八阳立。降升气揖。推造化，宝瓶缉。配刚柔合体，抽添循数，养就神丹一粒。变阳魂、法界恢弘，跨云步急。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be3350ed1907fd0cb3','醉桃源禄存文曲','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"禄存文曲附神光。驱驱逐色忙。昧禅徒乱角和商。如何作道场。\",\"双帘掩，琐明堂。仙童夺正阳。神功机在性昭彰。冥冥混八方。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be383c1c022211a90d','行香子·木去金','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"木隐金形，金色浮存。木森森、有像称尊。全凭火力，逗引游魂。自客先还，独存主，各归根。\",\"去住棋分，性命云屯。乐天真、默默昏昏。养成阳体，霞步高奔。得傲玄玄，跻寿域，出乾坤。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be3cd4b8966143c274','采桑子·赠王姑','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"性弘定慧空消息，虚谷传神。无象天真。拍塞乾坤貌至纯。\",\"历年无碍圆明相，不变长春。祖意明伸。阴粹阳精身外身。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be3ea3606286c0d030','南乡子·水火升降因土','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"戊己媾阴阳。斡运专权摄四傍。驱驾天元随呼吸，非常。来往绵绵造化昌。\",\"气聚结玄霜。壶鼎冰凝透体香。神变灵通归物物，清凉。无碍真空置道场。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be43bb3ffeffdc5436','武陵春·欲界','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"欲界谷神初剖判，情逸混冲融。太一云兴匝地风。岳顶雨蒙蒙。\",\"造化阴阳升降应，八脉醴泉通。琴韵琅琅聒耳聪。绝念酝奇功。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be4f0009ab59a373c0','行香子·木金间隔','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"木性慈仁，体顺融金。混难分、反复浮沉。还元祖性，造化幽深。得木抽金，金去木，罢相侵。\",\"精变游魂，湛入禅心。杳冥时、踪迹难寻。无中显有，透古通今。自魄安魂，魂胜魄，鬼神钦。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be4f7f90fc7a7a6b08','月中仙·九转功成','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"道泄冲和，运开基杳冥，生发元首。推排七返，配二三成六，再三成九。九还真造化，分八卦、妻男自偶。默默真情契，凝然气神，如一镇相守。\",\"盈盈动静归元，证不空不有。头面仍旧。无为性普，应地天神化，视之能久。任从劫运变，太空体、绵绵不朽。次了无生灭，恢弘妙用真乐受。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be52be8b79a1905eb8','南乡子·水火交互既济','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"坎内一阳停。本性澄清照物形。真火炎炎功足显，叮咛。既济氤氲缉宝瓶。\",\"神用奋威灵。气证升沈聚戊癸。日耀飞腾烹药鼎，兰馨。造化功成入杳冥。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be57cac2cde31b09a9','绣定针','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"道忠告。想射日回天，到头虚耗。旷劫沉埋，名利气财识破，馀风一扫。寸心清操。会得过、个中三盗。斡旋星斗，银河运转，浪倾丹灶。\",\"玄黄气相导。结地髓天精，蠢然腾倒。烹玉烧金，神化至虚，体合希夷深奥。彩云高蹈。露妙体、真空长傲。这些风味，咫尺世人不到。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be57cac2cde6889036','绣定针','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"酿金醴。灌上下三田，劫尘一洗。养浩师轲，贫乐慕回情逸。泛湖袭蠡。内摅恺悌。傲妙苑、道风济济。涤除心照，无离抱一，致柔循礼。\",\"胸襟昌云陛。盗月殿蟾精，日宫乌髓。二物相资，烹炼玉霜，满室丹霞光启。变成玄体。谅作个、阳魂头抵。步风超越，不在九霄境底。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be591294abdb1118ba','瑶台第一层','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"暑往寒来，被日月循环进退忙。春秋更变，枯荣迭运，几换风光。人生催促，断送沉腰，皤貌俱尪。至无常，有满籯金玉，救护无方。参详。\",\"轮回猛悟，豁开心地养元阳。手搏天地，身生万化，空界游扬。死生挥断，跳出世网、真相清凉。道情昌。证三时了了，千古堂堂。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be597ceb3edc2e2269','月中仙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"志士穷元，派浮沉动静，体用分别。神初象客，藉氤氲为主，应时提挈。杖头挑日月，斡天地、推移气泄。北斗南辰转，辉辉洞天，深处木童悦。\",\"而今主掌专权，证阳极返还，阴降时节。真功造化，显本来体段，清辉冰洁。焕然无点污，罄三界、十方透彻。一混希夷化，不空不有无生灭。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be5a1cdbbdb1e6eb09','黄鹤洞中仙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"性命根基奥。道本阴阳造。孤守灵光一点清，殆失观天道。\",\"三盗宜深讨。四象当逆考。夺得乾坤造化权，结就圆明宝。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be5bfdfca4783e2896','月华清','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一点灵光，千回沦落，四生六道游遍。苦海淹流，几许改头更面。谅四大、虚幼无根，出没甚、流星飞电。无限。递来往奇臭，枯荣迁变。\",\"识破般般不恋。悟梦蝶灯蛾，超生专擅。密扣玄关，摧挫寸心千炼。炼得显、元初洒落，展入地、升天体现。一贯。傲神通挥霍，清凉风扇。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be5e4c507b2f882cf2','双双燕','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"循环日月，春秋变荣枯，几多相缀。唐朝汉阙，楚苑秦宫兴替。爱海沦流何定，尽荡荡、无生淹滞。惊回浩劫尘情，返照推穷根蒂。明慧。通三抱一。\",\"真心运乾坤，坎离相济。氤氲结括，紫蕴素华藩卫。珠孕玄冥焕赫。显魄炼、云英蝉蜕。出入杳冥。无碍混通三际。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6181433431ce8d1d','无俗念','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一呼一吸，昧谷神、玄牝绵绵踵至。聚散忙忙乖造化，根蒂悬悬远离。上下无归，往来失御，不向三宫施。内迷归腠，意因执定一位。\",\"外灌四体皮肤，浮行散乱，蠢蠢难安置。壮观浑身多震动，变易千般何啻。意不灰寒，形不木槁，性命云泥异。禅功郁结，怎成阳体纯粹。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6314b2b1026e1ec9','苏幕遮·乾坤卦象明道','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"序乾坤，分宾主。匹配周天，甲己局分五。六十时归五日聚。五六三十，六六依策补。\",\"离中己，坎藏戊。离坎交媾，二土刀圭祖。减息绵绵自饮取。镇日忘情，定里神归姥。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be63849687140c7c4c','酹江月','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"鸿蒙一气，运天枢魁斗，斡旋星象。日月躔移分进退，定立朝昏弦望。四序循环，阴阳调燮，造化功无上，含弘光大，匠成品汇咸畅。\",\"神用剖判乾坤，生成应候，七九功归向。虎绕龙蟠吟啸曲，江岸声鸣风浪。将养神胎，奔腾圣变，法界真情放。玄中珠显，廓开光像无量。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be63849687715c3188','酹江月','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"真心径寸，括太极分瑞，玄黄交结。造化中间消息圣，日月虎龙罗列。釜下红光，勤交文武，进退依时节。烹成金蕊，密攒丹体冰洁。\",\"冲和国里人安，天根默契，沉静真如悦。珠鉴晶光牛斗射，耿耿清辉霜雪。万劫云收，多生尘敛，心法浑双灭。无言可说，慨然脱底明彻。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be65c7169d82977a16','永遇乐·色界','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"心虑灰凝，妙圆德备，威仪无缺。杳杳禅安，冥冥色碍，壶里风光泄。云霞来往，虎龙吟啸，捧弄木童神悦。廓胸臆、洪饮冲和，坦荡醉吟时节。\",\"笙歌鼎沸，鸾凰风舞，纵横自在行列。满目祥光，盈空瑞霭，隐隐飘红雪。拍怀冰莹，充襟月冷，壮贯道情明彻。傲真境、真乐真欢，出尘妙绝。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6da7adaccdcf60e8','江梅引神邪归正','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"灵襟澡雪蔑尘污。幻形刳。外容愚。恬淡场中，驰骤道情娱。一气制神神酩酊，乘醉舞，金童玉女扶。\",\"玄机真造志勤劬。蜕凡躯。入虚无。恢廓圆明，性月一轮孤。冷浸禅天风雾静，冰肌彩，傲澄澄晃玉壶。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6e59c9819b6c3bd6','蓦山溪','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"元来一点，本也无生灭。逐景落阴阳，匠成形、头面转别。五浊恶代，八苦镇交煎，生还死，死还生，生死何时彻。\",\"教君省悟，志夺玄黄诀。七耀贯三奇，斡珠玑、推移气泄。九还七返，药配按时烹，黄金窟，绽琼英，五气飞红雪。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6fe976b703e6c1b8','江梅引','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"猿颠马劣论修真。最艰辛。道难亲。要个惺惺，慷慨脱凡尘。推倒恩山心裂碎，乐云水，归依德有邻。\",\"坎离真造养元神。曲江滨。药苗新。酝就琼田，富国已安民。壮观九阳玄体健，挥法界，表巍巍，道化纯。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6fe976b790f0032e','江梅引','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"玄珠吐瑞电光飞。透帘帏。绕璇玑。倒卷黄河，风浪涌神威。子后六爻阴复降，成七返，仙童展羽衣。\",\"浩然恬养道巍巍。主无违。客同归。不见元初，万象境依稀。湛人希夷常默默，全体露，证鸿蒙，一点辉。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6fe976b79c99e63a','江梅引','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"景忘情灭万缘疏。览涤除。道情舒。把握权舆，神息动徐徐。将养灵台山岳静，常默默，调神定有馀。\",\"森罗真境傲华胥。显元初。妙如如。无像堂堂，模样契冲虚。飞舞游嬉无挂碍，狂歌咏，恣欣欣类接舆。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6fe976b7cb66dc04','江梅引','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"万斜不作一灵孤。妙容模。契虚无。觉海纵横，独耀越骊珠。云静水寒无点翳，清净体，穿山透壁殊。\",\"无人野上傲双凫。奋天衢。玩仙都。一点圆明，变化不能拘。腹实冲和怀美玉，密藏匮，更不求善价估。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6fe976b7d34e5e17','江梅引','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"黄裳元吉纵金风。击疏桐。退残红。万物归根，祖意证圆空。兀兀时当明西令，黄菊绽，秋香满玉丛。\",\"登高圣会兴无穷。燕金翁。聚仙童。爽气盈襟，沉寂内灵通。高傲无为真自乐，显本体，振巍巍，万古雄。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be6fe976b7fb28d75c','江梅引','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"无明咬断契真修。度春秋。志清幽。江月光飞，冰彩耀神州。万劫云收明祖意，眉毛剔，齐偕万事休。\",\"混融性命妙机投。两绸缪。恣神游。湛湛真空，天地彀难收。通变罔极非内外，太虚体，廓元初，气艳浮。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be73f48536ab2f5a6c','水调歌头','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"冲和养真命，神耀破昏衢。水山明秀雷泽，深处孕玄珠。七曜北辰枢纽，灿烂金华气结，槁木得重苏。灵腑风烟静，岳顶月轮孤。\",\"透三天，滋九鼎，饮琼酥。华胥笑傲，陶陶沉醉玉童扶。窈窕芝輧鹤驾，驰骤纯阳圣境，宇宙不能拘。寂阒先天貌，隐显混虚无。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be762dc63635a6b12b','望海潮','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"形神俱妙，灵通无碍，卓然独立玄玄。显理简情，空色无阂，揭为究意因缘。祖意密空传。自心境俱冥，智解都蠲。一段真清净，廓神变，出坤乾。\",\"恢弘妙体空圆。露如如无像，高傲禅天。□赫慧光，峥嵘实相，森罗法界无边。契道奋威权。已打破虚空，劫力难煎。擘碎轮回壳，拉真一，度流年。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be767e8a49214f51e4','望蓬莱','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"澄神久，消息入无为。终日怕兮其未兆，如婴动止乐熙熙。必固总心离。\",\"不断妄，真亦不修持。真妄之心属尘翳，翳尘不染性希夷。珠莹露摩尼。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be767e8a4969d88ffb','望蓬莱','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"真火运，五藏气煎烹。阳伏六阴潜变化，化精为髓血为精。白乳体生成。\",\"元神聚，朝现保身荣。金骨换形形不朽，光飞万缕烂霞明。云步越蓬瀛。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be767e8a49b9b65094','望蓬莱','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"推妙理，藏用显诸仁。神定杳冥天地合，勿疑恍惚惠风纯。五气保天真。\",\"金鼎沸，灿烂玉琼新。养就劫来无碍相，灵源三觉证全身。清净免沉沦。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be767e8a49dca9e556','望蓬莱','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"潜龙动，刚健海源蟠。黑雾三关深掩映，红霞九曲密巑岏。玉鼎炼金丹。\",\"宝瓶就，雪浪滚漫漫。八牖四扉辉日月，六神豁畅地天宽。真境写真欢。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be804d9eaa0ac26a21','醉落魄','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"齐心养浩。斡颠倒五行真造。灵风上下牙三岛。功契周天，真一内深抱。\",\"有形资养无形道。无情酝就有情宝。扶持体健神永保。独露禅天，心照月光皓。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be82f4af843377df79','醉桃源武曲廉贞','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"武廉幽贯地天根。绵绵息若存。六天交战定朝昏。丹炉火力温。\",\"驱神窟，养生门。玄功主掌尊。牝关天闭炼阳魂。灵霁性命屯。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be920cd93690668a61','留客住','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"悟生灭。顿剿断、马颠猿劣。万缘齐放，感得玄黄真诀。归依洗心清净，一向调燮。阴阳功决烈。呼精吸粹，盗虚无、真秀养成愚拙。\",\"息分别。气会神交，忘形真悦。聒耳清音，万派无中幽噎。寥阳化成仙域，捧出纯白。风光冰玉洁。神超物外，恣翱翔、坦荡步虚清绝。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be93259bbcf25c137e','月中仙·宾主互换','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"索妙修真，分元神作宾，一气为主。玄机不昧，凭坎离精粹，权衡三五。浩然成造化，销尸魄、纯阳结聚。七返真阴降，功全摄迹，归本认元祖。\",\"炼神合道冥冥，始真空体露。实相难睹。无穷变化，显性珠如意，恢弘光吐。独超三界外，令清净、腾今跨古。反换宾为主，首提究竟功德普。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be95f0e1c54912a70a','减字木兰花·九还阳丹进道','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"长男尊女。甲帐辛堂同宴处。一返游魂。闪灿炉开火力温。\",\"风雷酷烈。威赫云深明电掣。烟雨蒙蒙。飞下金乌宿桂宫。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be97c69562245ce128','降中央','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"盗天地冲和，一气造化丹基。玄牝辅弼枢机。四象三奇。消息盈虚否泰，铅汞抽添坎离。进退循环，玉关风透九霄吹。\",\"云行雨施，遍浇溉、黄芽雪肌。五彩腾辉低荫，二八香姿。六阳鼎沸，运火候、周天数随。引养形全，雾收云敛入无为。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be9a6b54e87a8488b3','醉桃源左辅右弼玄牝','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"司分木火地天通。玄关主掌雄。鹿羊神辂逗真风。都归保运功。\",\"枢九窍，贯三宫。熏蒸药鼎红。万神辐辏性疏通。无形廓太空。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be9bee8ea0b91205a3','醉桃源破军','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"破军雄镇北方阴。牢封地户深。真风高卷锁猿心。氤氲气制禽。\",\"三田贯，五神钦。元胎体变金。群魔离位九阳临。功成耀古今。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5be9c84202cab2cd1e1','瑞鹤仙·成数造化金丹','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"金丹真造化。在进火、功夫抽添相亚。绵绵息全藉。配天关地轴，器炉形借。铅消汞泻。引彩凤、金乌自下。\",\"入黄庭、展转相交，降升两情迎迓。多暇月娥捧酒，间簇笙簧，喜连夙夜。仙童力霸。乘空舞，彩云跨。向三天深处，放怀如意，五色光辉紧射。料玉壶、九转金成，殆难估价。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea29841805ad14103','万年春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"浩气流行，绵绵拥簇真禅定。颐神兴。杳冥难醒。凝结玄黄孕。\",\"造化圆空，剔透灵台莹。能常应。木童形胜。稳步云霞径。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2984180786a9b9c','万年春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一气冲和，心猿紧琐难奔走。调柔受。密封关口。太一氤氲守。\",\"二者相融，默默昏昏久。成奇偶。炼须九九。丕享虚空寿。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f612046100','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"乾坤呈瑞。六合三才八卦备。四返归真。一粒神丹气像新。\",\"见前必定。酩酊沉沉浑未醒。子母团圆。云步长春法界天。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f61282ff65','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"坤乾情睦。十二周天明二陆。八卦循环。驱率阴阳造化间。\",\"时当交泰。昧爽氤氲神气会。体炼纯阳。轻健冰姿散异香。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f63ff6709c','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"己从戊室。药味烹煎成玉质。六返金光。烈焰辉辉满洞房。\",\"内全养浩。壮观酡颜能永保。体透金香。捧出阳魂乐道场。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f655bc11ba','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"巽荣春谷。辛水溶溶滋震木。五返云雷。玉桂蟾宫应候开。\",\"月娥清写。风震庚金调木马。功显强兵。火衮深渊四海平。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f65829e8f4','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"返还成数。八卦爻抽同体聚。药祖完全。运就三三九九天。\",\"归根复命。鼎冷丹凝常药净。□赫金光。透体明连满室香。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f6877a4634','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一阳光启。戊室盘旋离合体。二返铅葩。永鼎温温进火车。\",\"深连巨海。踊出神鳌飞五彩。洞琐烟霞。密罩金童弄月华。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f6d08c113b','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"泽山同器。一点精华天癸至。七返丹成。远放流霞射玉清。\",\"气安神定。涤荡尘烦心月莹。独露禅天。万派波澄一样圆。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea2d534f6dd518616','减字木兰花','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"山通泽气。乱卷云腾风浪沸。三返炉煎。炽焰炎炎映洞天。\",\"奇莲焕吐。五叶香浮霞万缕。信手拈来。捧弄轻便上月台。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea339bbda4623033f','武陵春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"仙道古今人即是，恐尔自心迷。尔说天高我道低。三寸上天梯。\",\"十九这些人不到，相隔似云泥。妙诀倾心肯若稽。功德与天齐。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea339bbdaa8a4b55a','武陵春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"二八药苗阴岭采，采得雪金英。四脚无为鼎铸成。三性聚真晶。\",\"海底款吹无焰火，煅炼数研精。烹就冰肌耀素琼。魂圣貌纵横。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea339bbdab0e34d61','武陵春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"嗜爱顿忘情不纵，去智道颐形。心迹交时醉半醒。子母聚黄庭。\",\"九气天中鸣玉韵，昼夜不曾停。断送仙童入杳冥。变态奋威灵。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea339bbdabb7b6125','武陵春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"水火互交南北位，金木换西东。胎结源流帝旺宫。消息造真功。\",\"一马骤归三岛外，九曲六牛通。酝就玄珠透体红。轻健混冲融。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea339bbdac1876a86','武陵春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"心乐澄澄时静应，阴里媾阳精。飒飒和风海底生。六腑九宫盈。\",\"一气混挑罡斗转，天谷至神迎。虑念俱无净德成。咫尺傲鹏程。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea339bbdaf431c067','武陵春','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一气始方离混沌，八卦九宫期。罗络壶中媾坎离。消息浩充肌。\",\"斡转璇玑神寂定，龙虎甲庚颐。性普三宫造化奇。默默入希夷。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea3e4c85df6b2dce3','江梅引神邪','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"四肢气散失根芽。作神邪。发空华。异物千般，目下乱如麻。一切圣贤随意现，总变化，都因一念差。\",\"不知心病尚强夸。景无涯。事为嘉。特昧玄通，道眼被尘遮。不作真禅成魔境，阴阳隔，看如何炼玉砂。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bea53111523bea22bb','醉桃源','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"暗藏两曜抚坤乾。绵绵细斡旋。扶持来往贯三田。还元五气全。\",\"河车运，玉炉煎。炎炎结汞铅。彩霞光衬晃禅天。明明性月圆。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beb1a976a1b51d53ef','春从天外来','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"名利如何。叹六朝七国，五霸山河。几多兴废，一梦南柯。光阴暗里消磨。悟云山烟水，好归去、策杖披蓑。任春秋，乐调神养气，行止蹉跎。\",\"家园雪梅火枣，酝玉壶潋滟，满泛金波。烂饮醺醺，神游云外，真乐自在吟哦。伴清风明月，通天爽、飞舞婆娑。泄冲和。证鸿蒙体段，超彼波罗。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beb1a976a1fdaf5af0','春从天外来','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"返本修真。待一阳初动，五气全伸。金童施义，玉女弘仁。刚柔消息成姻。会黄庭仙馆，慧风引、宴拉诸神。瑞烟浓，泛琼壶雪浪，烹点铅银。\",\"冰肌烂霞帐锦，荫三奇光射，琼萼清新。体腻兰香，魂飞羽健，陪从道德为邻。傲太虚寥廓，真清净、不变长春。性中珍。辉十方三界，元迈冰轮。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beb2d0ff553b17135a','黄鹤洞中仙·顽空','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"深染顽空病。未识修真命。徒昧阴阳变化机，密翳真心镜。\",\"念断为见性。正是沉空净。不炼成丹妙体全，久久难归圣。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beb88f1c0dbc766ca2','一剪梅','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"一气升腾宝鼎煎。相交水火，玉路飞铅。不能神定两团圆。隔若云泥，难契重玄。\",\"切要澄心剿万缘。自然性命，混合归禅。阳魂变化恣升迁。百日千朝，云步壶天。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f23ca41d13','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"灵腑诸尘净，民山一气生。三田昼夜铁牛耕。鼓润风雷，金井长琼英。\",\"火力凭加减，冰肌应候烹。圆明方寸鬼神惊。悦畅仙童，饵讫恣飞行。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f23e78f51b','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"固蒂恢机柄，深根植化权。拈花摘叶总除蠲。引养冲和，上下贯三田。\",\"江岸和乌兔，丹炉煮汞铅。数成九转圣功圆。体透金光，晃耀照禅天。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f24253d37b','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"绝念驱群丑，澄心剿万缘。内枢橐龠息绵绵。一贯三宫，经纬契周天。\",\"火力温金鼎，明循六百篇。烹成二八体方圆。隐映祥光，绰约傲飞仙。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f258d1f919','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"洞锁猿驯静，缰收马不颠。气凝神聚契空圆。浩劫云收，长傲自家天。\",\"皓月清风爽，祥光罩瑞莲。木人石女共谈玄。变态神通，物外证金仙。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f288636d27','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"气射秋光冷，胸藏丽色风。腾身宛转离龙宫。趋造兰庭，笄岁喜和同。\",\"跨虎清溪里，驱乌皓月中。攒完鼎炼药成功。挥霍神通，无碍傲真空。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f2a2b89131','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"烛点心光吐，云收性月辉。一壶冰莹振风威。万象参罗，魁斗斡璇玑。\",\"造化无为理，恢恢道化巍。冥冥杳杳契天机。鸠僝神功，法界步云归。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f2c27e0612','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"极品轻肥贵，金童列鼎雄。掀天声价暂时中。电转星飞，身没气随风。\",\"唾手惊前拙，装愚不作聪。元阳真秀紧修崇。九九功圆，平步玩蓬宫。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec06ac9f2f36ba085','南歌子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"大药西南采，调和东北隅。火龙水虎奋天衢。七政枢机，斡转运琼酥。\",\"月窟喷香麝，冰壶迸玉珠。金童拈弄谒仙都。喜动天颜，赐寿等虚无。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec15e548e21386071','一剪梅·断空','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"夺得周天火候煎。丹炉昼夜，煮汞烹铅。海源江岸气喷烟。继日奔腾，通贯三田。\",\"不悟冥冥入定禅。神离气散，子母难圆。阴阳失媾昧玄玄。九载三年，不见壶天。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bec6c356d9c658b6e5','木兰花慢·坤卦老阴之象','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"剖坤柔立象，太阴体，换三阳。因六变心虚，关开实腹，应候呈祥。冲和易灵变态，运圣功、旋转斡天罡。消息栽培道化，六宫毂辏中央。\",\"融融品物含章。归土德，密收藏。性与命咸通，个中将养，气孕玄霜。香浮彩霞烜赫，混浩然、纯粹散诸方。造化冥冥极妙，超凡不离玄黄。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beca757b271b13cf94','满江红·慢','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"勘破荣华，提智剑、急逃生死。要黜聪、隳体虚室，内专穷理。绿水青山成伴侣，清风明月为知己。得自歌自乐乐天真，真常美。\",\"胸襟静，神情喜。从舒卷，恣行止。肖孤云野鹤，往来难拟。释氏真风勤整顿，净除诸障如顽鄙。醒圆明、一点证菩提，功超彼。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5becaae651ea511a507','江城子·慢真坐','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"修真万缘撇。心地下、功夫要刚烈。育愚拙。放四大、自在安闲不惙。志如铁。强斩马猿双足折，罢颠劣、都收归寂灭。八关六腑三宫，总和畅，万神悦。\",\"龟息玄通无间，得澄湛子母，团圆交结。两情泄。显阳体、炼就飞行时节。意冰洁。造化真空归物外，无为道、圆成还若缺。放开性月辉辉，贯乾坤彻。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed1ce5f2141abdd15','醉桃源贪狼巨门','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"杓枢贪巨秉天真。玄皇附耳亲。扶持五蕴已通神。都收万劫尘。\",\"寂然定，体清新。温存一性纯。冥冥物外傲长春。逍遥月作邻。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed465f5b34a574f1a','六幺令·观天造化','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"观天造化，进取慕希夷。刚柔中正扶持，五蕴乐无为。灵育九阳嘉瑞，坤殿坎交离。日中彩凤，翱翔江曲，万重水底觅乌龟。\",\"紫雾弥漫岳顶，消息结冰肌。巍巍体段清凉，风韵六神颐。豁爽三光洞照，海角恣栖迟。云间偃仰，陶陶无系，朗然恢廓旧容仪。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514caf0003499e','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"水府火龙藏。烟雾朦胧琐桂光。气动随风成造化，中央。聚变刀圭药味香。\",\"不见坎中阳。虚室无阴本体彰。育出朝元真浩气，琼浆。攒簇丹炉结玉霜。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514caf03875f73','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"火里六阴培。炽艳心藏点黑煤。功表清冷真水象，神胚。中气抽添自往来。\",\"赫赫振金雷。烟雨弥漫□斗魁。造化自家天地合，心灰。拍塞乾坤道体恢。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514caf12a596f1','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"符火应周天。加减无差达四禅。潜养铁牛喷玉蕊，功圆。换骨回阳证妙玄。\",\"般若有因缘。超上波罗意坦然。稳驾神舟归极乐，尘捐。清净心摅出水莲。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514caf84f9750e','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"进退兔和乌。驱入乾坤造化炉。少减多添循度运，情娱。巧炼丹凝体不枯。\",\"性养点尘无。放荡空圆傲玉壶。应物头头无挂碍，形殊。密蕴摩尼不夜珠。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514caf862829f1','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"大道在三田。知一都休执一边。通贯两间方寸内，幽玄。三一分形象帝先。\",\"钻则愈弥坚。仰亦弥高廓化权。视听不知无极旨，空圆。耀古腾今妙体全。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514cafe03d59b6','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"萍梗涉天隅。遣累刳情物物无。漱石枕流甜养浩，勤劬。拈弄龟蛇入玉炉。\",\"黑水产金珠。曲岸红霜润点酥。体□寸丸丹品妙，无虞。驰骤神通日月壶。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514cafe9523eaa','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"默默有如愚。一气氤氲日月炉。玄牝驱驰三性会，凝酥。醉饮醺醺性自娱。\",\"风月不能拘。天复天中运斗枢。消息玄黄形妙趣，情愉。一派红光晃太无。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed8514cafef00056f','南乡子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"离苑绿波飞。坎殿澄清奋火威。变化降升凝真象，功归。上德恢弘斡妙机。\",\"梨枣体娇肥。玉女殷勤献紫微。香衬彩霞光日月，巍巍。无像圆明法界辉。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed86a9bc406a95d62','无俗念火候不正','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"周天火候，要升沉飞伏，无差无错。气运前升穿绛阙，透上九天熏烙。两目难开，终为大患，急急须臾拨。迁延岁月，变成魔境沦落。\",\"六神失御猖狂，三尸放荡，五贼俱成恶。要镇炎炎归玉路，深入神宫恢廓。三盗循环，相交水火，锻炼成丹药。驱驰归正，得醇贤圣糟粕。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bed952140e9bcdfd4d','行香子·金去木','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"金体坚刚，木性柔和。辩宾主、火力功多。刚为主掌，柔像销磨。显各还元，归本性，息风波。\",\"透入神炉，霞罩嵯峨。放银铅、花绽婆娑。辉光满室，神变无何。镇玩蓬宫，游紫府，恣蹉跎。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beda08344883d4ef06','木兰花慢·乾卦老阳之象','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"判乾元刚象，德纯贞，利元亨。运一气氤氲，施生品物，分布形成。调和四时简易，正始终、机动应经营。日月东西出没，总因元气流行。\",\"参罗列曜循环，晃寥廓，体轻清。尽变化无方，杳冥真造，内隐精诚。酝就命基性烛，混太虚、模样貌峥嵘。了了真空无碍，八纮挥斥难名。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bedb230b5f48d350d9','烛影摇红·赠高莹蟾','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"心冷狙徒，志循教父寻宗祖。上天秘诀始恢弘，冰雪充灵腑。封闭庚金土釜。定阴阳、斡旋子午。应时升降，造化希夷，三光明吐。\",\"性命相资，地天交泰分宾主。湛然昏默契神功，法像周沙普。此理君休轻侮。莫贪爱、纵心逾矩。一朝功满，三岛同归，名超今古。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bee508b910ab2d0b52','醉落魄八卦配九宫','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"阴阳气结。乾坤派、六爻交设。四分九数明罗列。两遁推移，周岁用相迭。\",\"殷勤进取功无缺。抽添加减应时节。烹成二八丹威烈。吸海吞山，方寸道明彻。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bee78f813031f97042','行香子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"震乙情柔，酷爱庚金。角商和、律应清音。木人鼓掌，石女调琴。显巧施仁，勤行义，道情深。\",\"二物相融，交互浮沉。火煎烹、体变而金。开蒙既济，阳炼胜阴。得复根元，分魂魄，识真心。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bee78f81308bfe5313','行香子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"震内藏金，金乙成姻。假冲和、雷电交陈。龙吟虎啸，幽谷澄神。向杳冥中，风云里，傲全身。\",\"烟雨蒙蒙，芝草芽新。正东君、经纬长春。岷山水衮，一而藏真。已泯抽添，无间隔，返归纯。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beece5e19960e4c868','柳梢青','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"苦海渺茫流荡。似阔远、北溟洪浪。汨没群生，类鱼游戏，往来趋向。\",\"漂沦逐队情畅。几个化鹏形状。一去天池，垂云腾翥，顿超无上。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beed0c56ec39e9f9fb','瑞鹤仙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"衷情崇淡薄。荡浮萍、综迹逐波沦落。聪明总不作。念掀天富贵，梦惊为错。猛挥爱索。访馀裕、真风绰绰。\",\"谷神宁、缓息通真，元气动，符橐龠。挥霍玉炉金鼎，火衮云翻，妙娠丹药。神魂剔拨。成纯粹，体恢廓。向冥冥杳杳，湛彻壶天，领略清风逸乐。混虚无、真净真清，了空一着。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beed0c56ec52e42f27','瑞鹤仙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"炼丹成数究。进水火、抽添六阳时候。严严虎龙开，卷清风匝地，两情精媾。雷轰宇宙。渍玉蕊、灵芝吐秀。待药垆、火冷冰凝，方是雪肌成就。\",\"身富香喷兰麝，内放金光，夜明如昼。冲虚德懋。表清净，果无漏。罄色空都泯，魄纤魂圣，地厚天高远透。向三清、妙选丛中，已争领袖。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5beef03276583a2cad2','醉蓬莱','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"混元形一气，位析乾坤，朴分呈瑞。旷劫难逃，被阴阳相累。顿悟回、光合和金，木簇坎离精粹。瘴海风高，云行雨施，丹基匀渍。\",\"九会三期，六爻数足，奇偶相乘，谷神离器。透上昆仑，拉清风游嬉。偃仰安闲，白云堆里，任陶陶沉醉。看破虚空，行藏绰约，神珠如意。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef02891a48157d783','采桑子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"志枢动静勤朝暮，特玩屯蒙。消息玄通。橐龠吹嘘契太空。\",\"匠成伏火纯阳体，法界冲融。造化真功。宇宙都归掌握中。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef02891a48c91224e','采桑子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"慧光剔透茅心鄙，绝虑忘情。形静神清。捧出圆空一点明。\",\"驱云扫雾神机胜，出入纵横。实相难明。挥斥光华耀八弦。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef02891a4c7591573','采桑子','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"性珠莹彻禅天月，三界流光。四海呈祥。气会神交降玉霜。\",\"灵芽匀渍金花绽，透体清香。麝喷明堂。历劫宗风道化昌。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef32f182eaa1d2af7','浣溪沙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"三八为刀息缓留。圭凭戊己吸呼修。飞伏十干证添抽。\",\"造化结成仙药体，依时饵讫御神游。日飞天外得优游。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef32f182eabced278','浣溪沙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"勤饮刀圭炼极阳。薰蒸药味透天香。旋喷甘露返魂浆。\",\"烂饮醺醺长日醉，婆娑飞舞应笙簧。陶陶真乐上天方。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef32f182efdd0909e','浣溪沙','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"饮息时时药味加。五神运起海金沙。天河仙子度灵槎。\",\"水满金城凝露冷，人牛不见月空华。清风捧拥笑归家。\"]',NULL,NULL,NULL,NULL),('c16655df2fffb5bef87e94bb9a48b7a6','万年春玉琐金关互换封闭','王吉昌','c16655df2fffb5bed1457b72d1457b72','元',NULL,'[\"玉琐金关，阴阳击拆牢封闭。相凝制。变通无系。满腹清风细。\",\"九气天中，云步无纤翳。真根蒂。道情潜契。湛湛通三际。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf001d788813b4f1b9','瑞鹧鸪·过龙泉峪','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"西山一带好人烟。曲水环山若洞天。无是无非无宠辱，有花有果有林泉。\",\"家家奉道养闲客，户户钦坛礼大仙。如此勤心行吉善，太平快乐过丰年。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf04ef0d69f716439f','江城子·义州作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"纷纷世梦暂无安。蚁循环。少开颜。悟道修真，要脱死生关。认得元初无一物，除妄想，断高攀。\",\"老来佚乐正宜闲。罢轮竿。钓舟还。物外清游，历遍古仙坛。更待东君传信息，度辽水，看闾山。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf052d581c907a5138','巫山一段云·寄天长道众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"山后春将暮，龙阳景渐佳。东园芭榄正开花。随分有生涯。\",\"道友坚心长久。相见时时稽首。自然恬淡日和平。物外有余清。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf0bdc1eb368b1cf80','道无情·别西山道友','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"行过天涯海角。未似西山心乐。兴尽复东还。鬓多斑。\",\"彼此年过耳顺。别后有何凭准。唯愿各心休。永无忧。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf0d7a7f79054afeb2','南乡子·赠西路道人','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"幸遇好良因。枉惹浮名受苦辛。只为目前些子事，因循。趖过中间换了身。\",\"默坐守灵根。空外闲搜空外真。认得亘初微妙物，堪亲。唤作神仙不唤人。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf10a9dab005a6aedb','巫山一段云·携杖上禅房山','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"自在三山客，逍遥西海宾。孤身到处自全真。风月永为邻。\",\"识破浮华虚假。谁羡望云星马。一条拄杖胜龙骖。稳步上高岑。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf12f4d5daa8ed217d','卜算子','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"真性休空走。二物长相守。这些消息阿谁知，便是无中有。\",\"实鼎琼花放，玉户金光透。作个清凉彼岸人，清静工夫就。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf12ff8599eae8ba54','巫山一段云·劝世','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"道显清虚妙，释明智慧深。仲尼仁义古通今。三圣一般心。\",\"不认忘名默悟。只解分门别户。一朝合眼见前程。悔恨不圆成。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c9142bdd5120','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"有相有中容易觅，无为无处难为。终朝作伴影相随。愚人寻不见，达者已先知。\",\"此理勤参搜获正，一条大路无迷。劝君速悟莫迟疑。常常忘视听，步步入希夷。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c9142cea430a','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"十载饱谙沙漠景，一朝复到都门。如今一想一伤魂。休看苏武传，莫说汉昭君。\",\"过去未来都拨置，真师幸遇长春。知君道念日添新。皇天宁负德，后土岂亏人。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c91445d4dee2','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"参透全真清静法，何劳相上经求。好于此处觅踪由。莫生迷执见，休要不回头。\",\"真性如何能寂灭，真空空外闲搜。其中无欲认真修。随机常寂定，应物自圆周。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c9144cd31a00','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"数载崎岖天下遍，而今幸遇林泉。饥来吃饭困来眠。离城几一舍，别是小壶天。\",\"坐上水山俱秀发，池中开满青莲。老来功行卒难全。未能三岛去，先作五华仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c914bd1ba176','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"自别都门人事少，心头不挂微尘。闲歌闲咏默颐神。有中皆妄想，无内却全真。\",\"取性逍遥云水客，无情淡泊闲人。随缘安乐绝疏亲。三阳将欲遍，五气自朝元。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c914c841164b','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"一别潍阳过十载，天涯历遍西戎。归来燕国住仙宫。立言明祖教，演法继师踪。\",\"天意如何非易测，人情到了难穷。予心本自爱疏慵。却教居海北，未许过山东。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1368c914da77b7be','临江仙','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"本是一般孤另物，被他染着难知。迷云消散慧风吹。何劳身外觅，端坐是天西。\",\"万语千言终未悟，悟来一字成非。竿头进步勿生疑。般般都撒手，种种自皆离。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf13c27f971eb2d897','踏云行','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"暗地高真，明加保祐。白云仙迹当成就。吾门七祖镇燕山，金莲万朵芬芳秀。\",\"众会宁心，道人长久。同修清德过星斗。他年功满去朝元，先师应赐长生酒。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf13c27f9750c00a33','踏云行','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"静里喧喧，闹中寂寂。灵源妙用谁能识。两般颠倒入虚无，自然显个真消息。\",\"气过三关，神游八极。绵绵来往无踪迹。此时休歇罢参寻，前程路上忘疑惑。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1ac5a93ad29edf64','瑞鹧鸪·咏西山','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"西山深处道人家。养道修真何处加。九夏高眠无暑气，三秋结实有新瓜。\",\"乱山坡下宜禾黍，浑水河边长桑麻。四季平和人事少，三餐终日是生涯。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1b9919eb0f96c9e4','巫山一段云·清水作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"应运开正教，随时见宝坛。众真降迹在人间。却要列仙班。\",\"化度群生无数。个个尽教开悟。一朝功满到蓬莱。劫运免三灾。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf1d7cdf15e563893a','江城子·仲冬','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"龙祥踪迹古仙坛。水连山。径弯环。玉殿朱门，高耸白云间。疑是蓬宫临白溆，真福地，可开颜。\",\"仲冬游历苦无寒。道人攒。子文欢。看尽三峰，别后两闲闲。何日复来林下笑，散后易，聚时难。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf22f384af4f282614','西江月·入西山路','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"石径云梯路险，攀萝揽葛难行。洞天深处水山明。远却尘寰视听。\",\"土洞安眠稳坐，松岩耳静心清。不须更羡许由名。便是超凡入圣。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf260e48f3f5b4e1df','西江月·赠儒士王子正','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"本性元虚不二，奈何情欲交加。人能顿悟道生涯。世态分明是假。\",\"更要深通玄奥，须当拂去矜夸。含光默默养灵芽。便是无为造化。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf2715c37283dbcc76','瑞鹧鸪','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"为人幸遇教门兴。折莫因循着世荣。节食减眠神自爽，忘情去欲气偏清。\",\"暂时戒慎离灾苦，久远行持出死生。奉劝高明宜省悟，闻身强健早修行。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf2715c372c938c4c7','瑞鹧鸪','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"六年动作小功成。固蒂深根渐次荣。东院西宫闲客坐，南来北往道人行。\",\"十方和气精神爽，四海同心道眼明。天运玄风来此地，燕山路上好前程。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf277e95536b10d706','西江月·秋阳观作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我爱秋阳地僻，松岩来往人稀。不劳打坐自忘机。兀兀陶陶似醉。\",\"坐上有山有水，心间无是无非。朝朝常见白云飞。可以留连适意。\"]',NULL,NULL,NULL,'[\"打坐：僧道修行方法的一种。闭目盘腿，手放在一定的位置上，断除妄想。也可泛指静坐。\",\"忘机：消除机巧之心。常用以指甘于淡泊，与世无争。\",\"兀兀陶陶：醉酒貌。此处指与醉酒相似的感受。\",\"坐上：座席上。\",\"可以：表示有某种用途。\",\"适意：称心；宽心。\"]'),('c16655df30cb0ddf2bb5195296e91a99','江城子·龙阳观冬至作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"六阴消尽一阳生。暗藏萌。雪花轻。九九严凝，河海结层冰。二气周流无所住，阳数足，化龙升。\",\"归根复命性灵明。过天庭。入无形。返复天机，升降月华清。夺得乾坤真造化，功行满，赴蓬瀛。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf33472416820fe8b5','西江月·赠田道人','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"动处休将性动，静中莫使心疑。目前端正没关机。真静真清便是。\",\"气满三田自实，神通八脉方知。了然顿觉入无为。到处安心稳地。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf3470c669fd7a91d2','虞美人·劝世','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"迩来似觉精神涌。识破人间梦。皆因恬淡乐清和。与物无私，光照遍山河。\",\"如今普劝修真众。莫待空无用。一朝归去恨身卑。悔不当时，勤苦学修持。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e4463142cd93e','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"九九严凝冰结。山后朔风凛冽。西去意如何。啰凌啰。\",\"道友一声珍重。慎勿使他心动。志气要平和。道无魔。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e44632c39939f','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"但愿身安心静。谁羡往来迎请。正教满天涯。过流沙。\",\"师意真慈普度。应系群生开悟。积行要无边。性方圆。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e446366994d4f','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"每夜三更问话。说破人情虚假。个个悟真宗。道心通。\",\"要望秋阳回意。莫纵高眠春睡。指日复归来。笑颜开。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e44638bc25a94','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"不避春风迢递。来往默通真意。那个是吾家。信天涯。\",\"本性玄通无托。到处一身安乐。了了没西东。住云峰。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e4463a292a367','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"深谢将书来请。才到西岩春令。百鸟正喧呼。乐无余。\",\"道众满川和气。醮罢更看天意。缘契合虚谷。任西东。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e4463a9905c46','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"身逐东川道友。有客西山专候。真意是如何。啰凌啰。\",\"七十古为稀少。今我六旬还到。浮世不坚牢。醉陶陶。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e4463c1638735','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"随处道人道友。勿使人情浓厚。凡百在平常。少灾殃。\",\"别后应当西迈。接待往来宁耐。他日要相参。望终南。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e4463da9b5dcf','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"十月小春时令。万物归根安静。独我水云间。过东山。\",\"直指古燕无定。老也敢违天命。回首望终南。再来参。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf395e4463f1152bf9','道无情','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"三老共全清福。终日游山不足。坐卧白云中。看青峰。\",\"饭罢东坡石上。谈笑开怀豁畅。妙景可忘忧。对清流。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf3b9f99ff35adca56','西江月·常足畅怀','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"尽日观山玩水，终朝常足心休。老来佚乐更何求。保我阳生九九。\",\"幸得为人通道，道中别有归投。功成云步看瀛洲。万古名传不朽。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf410a4ea8d51a7c37','一剪梅·咏前高山','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"新得前高一簇山。清虚洞府，亘古仙坛。皇天怜我苦奔驰，佚我年高，教我开颜。\",\"暗谢高穹赐我闲。松间石上，断了追攀。忻然自得乐无穷，行出堪观。坐也堪观。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf43414ce2c8f6b920','一剪梅·寄蔚州道友','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"越过灵仙壶水傍。西河饯送，诚厚难忘。未能款话便登程，度岭严寒，来意匆忙。\",\"幸得和平达道乡。浑源丰足，诸事安康。有人东去寄新词，报与知音，表我行藏。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf46411b9dd42d182c','减字木兰花·怀仁县','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"怀仁抱义。五帝三皇因此治。抱义怀仁。天下生灵一体亲。\",\"勤参道德。建国成家为法则。道德勤参。更与修身作指南。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4a1c46696c495635','减字木兰花·赠龙虎夫人','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"昏昏默默。世智聪明孰可测。默默昏昏。无欲能开众妙门。\",\"全真抱朴。无虑无思无做作。抱朴全真。人圣超凡只在人。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4a4b1c10b9d2b94e','青玉案·自遣','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"扬眉瞬目分明露。认得元来处。清静无为香满炷。十方通彻，遍闻三界，一任年将暮。\",\"些儿妙处堪人顾。关外栖真忘来去。更有云霞三四侣。清吟野兴，固穷乐道，此外非他取。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abe0939c177','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我有林泉兴，君无补缺心。一方圣境理幽深。物外结知音。\",\"还到春时圆备。合是全真一会。功成行满待他年。携手上云天。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abe619d5c4d','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"溪水迎霜冷，山花带露鲜。良朋共赏玉蟾圆。高会兴无边。\",\"长记西湖万里。素魄澄澄一体。今宵相隔正三年。浑似梦游仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abe646ae931','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"万叠山横翠，千盘河曲长。居民安土乐农桑。流水落花香。\",\"卜此安居养老。一任形枯心槁。不摇不动寄残年。何处觅神仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abe674d2f3a','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"塞北三年转，天西万里回。飘飘云驾渐东来。无不笑颜开。\",\"道德峥嵘功大。行满决超三界。十方仙子行功圆。得见太平年。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abe7f9ee785','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"性烛通天眼，心灯放慧光。湛然灵物自真常。无处不圆方。\",\"住世荣华不恋。举措常行方便。一朝功满化云归。独步赴瑶池。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abeac4bc88e','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"太上临关日，吾宗受道时。七雄戈戟斗相持。何处起慈悲。\",\"直至西天行化。大教普闻天下。古今今古类皆同。方显道家风。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf4c2d8abef05e7348','巫山一段云','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"北海修真士，西岩放旷人。别师迢递过关津。物外不沾尘。\",\"学道须凭决裂。一片真心如铁。九年功满步云霞。三岛是吾家。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf51262b3ff223f682','减字木兰花·赠燕京万莲会众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"回头返照。世事无穷堪失笑。返照回头。得意归来便好休。\",\"休休省也。莫纵贪痴狂野马。省也休休。一任年光春复秋。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf52461c5ef5bda6e2','巫山一段云·先天观作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"涿鹿今朝到，蚩尤往日休。坂泉冬夏水长流。今古几春秋。\",\"因此强兵战胜。直到而今无定。一生一杀做冤仇。早晚是程头。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf5390b2d12e5cd601','凤栖梧劝世','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"大教普闻天下转。返朴全真，日用垂方便。方便真慈清静愿。心通渐觉人情远。\",\"上士高明容易劝。既悟玄风，早早忘知见。世事安能长久恋。不如认证元初面。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf578b87238357d585','踏云行·自咏','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"薄利虚名，谁人能戒。前头路险犹贪爱。惟予不肯着浮华，中心有愿超三界。\",\"广布慈风，休生懈怠。心怀忍辱常宁耐。豁开心地若虚空，分明见个真难坏。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf604b85df0c686366','凤栖梧先天观作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"山后风光何处好。上谷灵踪，自古轩辕庙。涌出清流方曲绕。森森绿桧知多少。\",\"云水闲游今日到。信笔狂吟，自在开怀笑。万景难侵心合道。全真妙体圆明了。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf606d191e45ef9800','青玉案','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"心头远恶常修善。自得真方便。至道夷然容易见。目前端正，是非休论，堪作长生伴。\",\"玄言悟彻无分辩。默默颐真内光现。保养神丹成九转。化身空外，六铢天赐，换了如今面。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf606d191e5ecc73ed','青玉案','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"群魔散尽真何乱。万事无萦绊。一点光同青玉案。纤尘不挂，永无障碍，得见虚空面。\",\"劝君早把尘情拚。下手速修转头晚。前有风波深不浅。神舟稳驾，云朋相伴，笑指芦花岸。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf606d191eb802eb77','青玉案','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"浮华莫恋心归道。渐得通玄妙。清静功圆心地了。高登彼岸，清凉永占，更没闲烦恼。\",\"长春真境仙无老。咫尺洞天谁能到。心上尘清都一扫。琳宫仙院，乘风清兴，游宴蓬莱岛。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf613b56011ca5e52f','点绛唇·暮春游五华山继先师韵','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"昨日春游，一行恰似乘鸾凤。知音共。适怀心纵。直入桃源洞。\",\"好向清溪，闲听琴三弄。真欢动。任他迎送。管甚庄周梦。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf636e4f53960593e6','西江月·赠万莲会众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"事事谙来心足，般般舍去身轻。本初一点要圆成。妙语玄言堪听。\",\"纵意不知有我，收心自觉无明。无明灭尽绝尘情。便是识心见性。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf6753eab4d9a45b9f','道无情·别蔚州道众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"十月蔚罗行化。去送来迎车马。和气满山川。教风传。\",\"别后何时再遇。唯愿各心开悟。功积要三千。化灵仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf67648aa2b4df9c5a','踏云行·赞师父仙体不朽','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"玉骨元清，金丹已就。长春不老何年朽。圆成一性遍河沙，遗骸全体仍依旧。\",\"万代稀逢，一时罕有。天涯门弟来奔凑。安居处顺庆真容，焚香十万连三昼。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf683b236b7cee9a89','瑞鹧鸪·劝世','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"长春教法付清和。普劝高明出爱河。道德勤修天赐乐，尘情苦恋世生魔。\",\"目前放下游真境，身后功深赴大罗。我有忠言新说破，看君听了意如何。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf68b938bfd94fb1ea','巫山一段云·秋阳观作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"十九游仙子，随师历八荒。西临回纥大城隍。到处见农桑。\",\"一种灵瓜甚美。赤县几人知味。大千沙界有多般。鹤驾复东还。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf698e0a2bc57cf4a2','西江月·寄京师道友','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"只为功亏行阙，故教不免东西。自从别后二年期。路转风尘万里。\",\"词寄燕山道众，听予至嘱休疑。外缘虽干内忘机。免却前头懊悔。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf6a0681a0a91a9dd1','巫山一段云·题通仙观','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"列翠峰峦秀，仙都气象清。三阳端正玉鳌形。一气自天成。\",\"万叠云山深处。别是洞天幽圃。何人能得久闲居。清静乐无余。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf6d25cafe201f265d','无梦令·龙阳观春分其间作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"道友三冬炼睡。炼做陈抟苗裔。咫尺过新春，昼夜须当加瑞。加瑞。加瑞。点点直教着地。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf6db59f6dd7e9e68a','一剪梅·示众复用前韵','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"外积阴功内固阳。长春示众，此语偏长。还能顿悟入无为，宿业犹如，赫日消霜。\",\"不假澄心六欲忘。功成行满，越过炎凉。腾腾兀兀任西东，觉彻人间，且恁韬光。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf79bb001364f164b9','江城子','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"法光普照道门昌。出天长。入龙阳。宝殿圆成，朝暮爇心香。霞友云朋休外觅，心地稳，是天堂。\",\"燕南河北好悲伤。正穷荒。受风霜。劫运推移，何日到仙乡。谁解闲居山后意，□□秀，小蓬庄。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf79bb00138ad77738','江城子','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"玉河别后出西山。晓霜寒。过重关。书疏相邀，须索赴清坛。度死超生虽好事，年已暮，鬓成斑。\",\"修真未就积功难。炼心宽。悟真闲。造化功夫，都在片时间。斡转玄机全至德，无可欲，列仙班。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf7b9fb0d21aeaa1ab','巫山一段云·催衲袄','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"昨夜西风紧，今朝秋意深。道人虽则是降心。寒暑亦难禁。\",\"衲袄宜教宽大。且要昼披夜盖。一衣犹恐不销任。此外更何寻。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf7fec56d366728338','临江仙·偶得','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"谈妙谈玄人易悟，真清真静难依。人间世事不堪为。十方明眼汉，学我没操持。\",\"既悟淳风须返朴，终朝朴实相随。含光默默外如痴。心明如朗月，性静合刀圭。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf80964137f68bb8a1','瑞鹧鸪·与延真观小贾师','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"道人起念不寻常。宿业牵缠惹祸殃。朴实修真方得久，浮虚学道岂能长。\",\"旋生旋灭心难定，不动不摇意易防。早悟知常功累足，恁时许尔志坚刚。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf85e4bec1a6f40efe','巫山一段云·龙阳观九日','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"白酒宽怀抱，黄花喷鼻香。此般真味庆重阳。性月自圆方。\",\"山后三秋无别。幽槛几丛开彻。两轩各各斗芳鲜。雅胜洞中天。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf877666aadd9369cf','道无情·怀仁县作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我爱怀仁冬月。炕暖窗明堪歇。天意不教闲。过南山。\",\"东去西来非愿。海角天涯将遍。道果尚难成。又登程。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f38700f93e5e','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我爱西岩气势，连山带土丰肥。青松绿柳嫩依依。更有名花异卉。\",\"春水涓涓声细，秋阳烨烨光辉。此中不用上天梯。便是仙家景致。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f3872679fbbf','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"莫羡喧哗京市，休辞淡薄山家。猿啼鹤唳兴还加，心地清凉无价。\",\"夜伴清风皓月，晓观绿桧云霞。蓬头丫髻做生涯，堪向幽屏图画。\"]',NULL,NULL,NULL,'[\"心地：心境。\",\"清凉：清静，不烦扰。\",\"丫髻：谓梳着丫形发髻。\",\"幽屏：隐僻之处。\"]'),('c16655df30cb0ddf8d41f3874d3393c9','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"人事虽然久厌，道家争忍空归。不因勤苦到关西。焉得重阳庆会。\",\"说与门中志士，祖庭已见根基。终南山色古今奇。老者堪为久计。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f3875d86ac77','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"浅见有知有识，深通无悟无迷。了然顿觉入希夷。此是男儿正智。\",\"达道岂离方寸，明心何在天西。终朝闲坐细寻思。裂转机关便是。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f38781b6c18c','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"进道须凭笃志，收心慎勿狂游。听予苦口劝同流。莫待因循皓首。\",\"绿发朱颜易改，青春白昼难留。争如速悟便回头。认取忠言善诱。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387949f2b39','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"山后重兴道院，燕南仍自干戈。道人功行累来多。免却非灾横祸。\",\"每日诵经报国，终朝念道降魔。福生祸灭养冲和。真静真清证果。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f3879e56718b','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"有欲难超老病，无情易变童颜。虚心实腹六神安。步步清凉彼岸。\",\"九载能除四相，十年决到三山。蓬壶阆苑恣游闲。免却人间流转。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387aab85d16','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我爱秋阳道众，人人谦让温和。终朝豁畅恣高歌。日用不分彼我。\",\"福地安居自在，松间闲步烟萝。先人后己行功多。永没非灾横祸。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387afcd36be','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"九夏天长暑热，三秋山后清凉。一川禾黍正苍苍。了见西成有望。\",\"论甚天涯海角，尽他关外山荒。目前无事即仙乡。且恁随缘豁畅。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387b42e3641','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我爱秋阳天气，一指云路无迷。何劳身外觅曹溪。了见三身四智。\",\"莫问天机深远，休寻大道无为。目前认得这些儿。便是全真苗裔。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387bc3f48b0','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"非爱青山绿水，惟图隐迹埋石。粗衣粝食绝人情。养就元初本柄。\",\"气结神凝久视，虚心实腹长生。一朝功满现三清。此是男儿正性。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387ccfe4479','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"我爱西岩秀丽，仙园地发三阳。山形曲屈卧龙冈。只此人间天上。\",\"云舍茅庵掩映，仙花异果馨香。市朝客到见清凉。举手应须瞻仰。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387d94c84ae','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"窗外横山入画，门前流水堪听。洞天幽处少人行。不是尘寰路径。\",\"占得静中风月，却回闹里人情。湛然六识自安宁。一任闲歌闲咏。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387f33eb33d','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"月里金乌报晓，日中玉兔方眠。谁知万法倒颠颠。此理非深非浅。\",\"认得元初这个，须明无事真禅。人人有分性周圆，只为使他不转。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf8d41f387f67a7ad2','西江月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"天上仙无懵懂，人间性有顽愚。门中有幸看经书。性性堂堂开悟。\",\"达理真明妙有，观空体合虚无。慧通灵宝证元初。谁解无文不度。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf905f9b6de712a5a9','减字木兰花·秋阳观作劝世','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"堆金积玉。日日悭贪心未足。足上何求。直待荒郊卧土丘。\",\"回头有路。争奈愚人迷不悟。若悟回头。免了前程无限愁。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf97a570cc64e44638','贺圣朝·太平兴国观中秋赏月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"海清山静，素秋光满，凉夜人圆。恣开怀赏玩过三更。更谈论幽玄。\",\"圆明顿悟，无私普照，善行周全。向今宵觉彻性皆通，证陆地神仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf982bdc63f7a4b6fd','一剪梅·述怀赠程老先生','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"学道无成天不悭。虚名滥占，实得应难。从今志气更重加，东望都休，不下西山。\",\"深谢知交忻崞间。四时不阙，助我衣餐。有时庆会上朝元，功行双全。日赴千坛。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddf9cd3af932a4970fc','减字木兰花·秋阳道友见过','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"金波玉液。除却妫川无处觅。玉液金波。一饮能教五气和。\",\"还丹大药。服了令人常快乐。大药还丹。福薄众生要见难。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa29da1c27ab3652a','巫山一段云·戒无明','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"一点光明大，三千业障深。劝君闻早细搜寻。莫待祸来侵。\",\"触处将身回转。慧性能生方便。这些消息汝还知。便是上天梯。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f607c4e12f','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"同尘混世。谁解忘言无酒醉。混世同尘。落魄逍遥甘分贫。\",\"灵源不昧。胜积无涯真宝贝。不昧灵源。一气清成要上天。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f607e59f57','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"身心裂转。便是元初真目面。裂转身心。一志如山超古今。\",\"些儿若悟。免却波波来与去。若悟些儿。万劫希逢此一时。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f63bf2f1d8','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"损人利己。贪爱欺瞒何日已。害众成家。富贵人前仍自夸。\",\"阴功暗记。死上头来谁肯替。天道无私。报应分明各有时。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f643327ebf','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"劳生丧命。谁解衣餐皆分定。恣欲贪痴。积业如山犹不知。\",\"一朝有报。铁胆铜心人也懊。受罪无休。恨不当时闻早修。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f6ac7dbae3','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"清河画士。处顺存心堪发志。画士清河。早早来时意若何。\",\"全真宗祖。画向白云传万古。宗祖全真。永镇燕山日日新。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f6c122fb7d','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"安禅聚落。难免区区闲把捉。聚落安禅。佛祖常忧恐落缘。\",\"山林纵卧。无虑无思真快活。纵卧山林。一点灵光无物侵。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f6ef9a8aa1','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"天堂岂远。举世迷人寻不见。地狱非亲。无限众生寒断门。\",\"聪明上士。幸得为人勤发志。志在心平。片善无遗道自成。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f6f2f0bd5e','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"顽愚不省。祸福还如身逐影。劫运天灾。都是人人心上来。\",\"若明此理。视物应当同自己。了见天真。善恶临时全在人。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa2d534f6f930e9ae','减字木兰花','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"三阳道士。坐对云峰谈不二。万法如通。尽在忘机冷笑中。\",\"无明实性。此理难诠如何证。要证元初。认取高空秋水孤。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfa78e26c847bdbed4','西江月·龙阳观冬至作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"月魄光通四海，龙阳气满三田。一声雷动震山川。迸出飞光闪电。\",\"法雨常加有道，慈云广布无边。此时方显太平年。遂我一生本愿。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfab4f71958296de78','一剪梅·太平兴国观示众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"天赐平遥好歇心。琳宫寂寂，古柏森森。清阴密锁洞天幽，聚落相邻，静胜山林。\",\"清静无为孰得寻。太平兴国，觅个知音。劝君速悟莫迟疑，言下承当，贤圣加临。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfad0971a2792b1498','昭君怨·泉州洞真观书于东壁','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"节令重阳闲步。直至武清南渡。极目看嘉祥。水茫茫。\",\"连日阴沉微雨。正在芦花深处。游乐水云间。望西山。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfaf111f973e1b8d06','踏云行·赠长春宫道众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"智慧男儿，聪明上士。修真幸遇先师指。葆光殿阁对青山，白云堪伴长春子。\",\"乐道明心，胜看经史。神仙籍上书名字。大家勤苦守灵踪，皇天肯负清高志。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfaf8e082f0bc0c272','西江月·赠张先生','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"日用灶头然火，时教突里亡烟。始终如一志精专。也胜往来打转。\",\"放下纷纷尘事，明通朗朗玄言。一朝功到性灵圆。便是蓬莱中选。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb2d8b554e8697f3c','南乡子·宝玄堂偶得','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"本性爱疏慵。不厌无名不厌穷。落魄随缘无所碍，心通。观透人间事事空。\",\"得失本来同。动静何劳问吉凶。兀兀前途真自得，成功。都在忘言冷笑中。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb4c4ddec33d55c13','江城子·沁州神霄宫赏月','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"素光皎洁暮天晴。十分明。称幽情。赏玩随时，高会尽云朋。今夜澄澄垂普照，无此夕，得和平。\",\"碧空如水月盈盈。性珠停。保长生。四海清通，何处不圆成。更谢高明恩德重，临万国，教门兴。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb64913667b87a82b','贺圣朝','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"夜深人静，披衣闲坐，琴听无弦。罢高谈逸论默通玄。任龟息绵绵。\",\"阴阳升降，冲和四大，骨壮神全。抱元初一点行功圆。看歌舞胎仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb6491366a612bd54','贺圣朝','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"古宜缘重，善根深种，志士心坚。感吾门洪教广开缘。共谈论功圆。\",\"中宵不寐，云朋高会，参透玄玄。向五千文内契忘筌。见自己神仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb88f1c0d2915ef58','一剪梅','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"新得前高孰可知。世间清绝，诗笔难题。朝元峰上试闲窥，未赴蓬莱，先到瑶池。\",\"一派松风入耳吹。仙音缥缈，洞达幽微。兴来策杖步云溪，行也相宜。坐也相宜。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb88f1c0d3fc79eae','一剪梅','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"新得前高自发扬。峰峦峭拔，松桧成行。道人归计出红尘，碧洞高眠，无事牵肠。\",\"白发垂垂两鬓霜。青松影里，描邈难妆。往来游戏日当斜，行也清凉。坐也清凉。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfb88f1c0d5230eb91','一剪梅','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"九九方终暖日阳。春和崇义，无梦天长。十方三界恣游行，炎夏还逢，不觉秋霜。\",\"悟得真闲万事忘。自然佚乐，心地清凉。本来一点纵宽舒，散尽群魔，独现威光。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfbc32a2a54f0829f1','凤栖梧','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"一点灵明当内究。散尽群魔，赶退西山兽。万树琼花如锦绣。长春永占无根朽。\",\"四海云朋来往凑。上下冲和，渐觉阳生六。一道灵光通宵透。清香换了胞囊臭。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfbc32a2a55c4cb687','凤栖梧','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"天下风光何处好。山海潜通，无比燕中道。四海云朋来往绕。先师默悟知还到。\",\"既入玄门须悟早。谁解修真，此事非草草。天意分明容起庙。燕山千里来怀抱。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfbc32a2a55e388f8c','凤栖梧','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"世事无涯何日了。争似忘机，学我归山早。山下林间常独笑。人心未悟谁能到。\",\"终日忘言人悄悄。暮去朝来，更没闲烦恼。惜取真阳身内宝。不须服饵容颜好。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfbc32a2a5a68e6960','凤栖梧','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"岩上春光将欲暮。可惜春光，无计留连住。春去秋来分四序。一来一去成寒暑。\",\"岁计谩人人不悟。得丧悲欢，尽是轮回趣。我欲留言叮嘱付。人心未解无生路。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfbc32a2a5b37c57c7','凤栖梧','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"山后三阳都历遍。宣德朝元，不晚须相见。数载师前人事倦。区区独自随风转。\",\"几度天长曾发愿。粝食粗衣，隔断人情面。富贵荣华无意恋。不如岩下成修炼。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfbc32a2a5c4e04b4a','凤栖梧','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"问道参禅都不会。境上闲游，只要心无昧。踏碎虚空离六对。松岩一觉和衣睡。\",\"何用人前夸口快。一点灵明，不许纤尘盖。法性堂堂观自在。归根复命超三界。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfc1c267d82f77d5f8','减字木兰花·示众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"年深学道。虽未通灵神气好。学道年深。万景丛中无物侵。\",\"同流听劝。薄利虚名深可远。听劝同流。放下诸缘宜速修。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfc6406ea88d965782','点绛唇·重九后五日作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"体入虚空，犹然未可为凭据。真空路。气神相聚。现世阳神去。\",\"若遇知音，此事堪分付。君听取。莫生疑误。行满天将护。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfc67a609a7c808478','悟南柯','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"莫觅他人短，唯思自己长。处身谦让性和光。与物无私，心地得清凉。\",\"会灭无明火，能生智慧香。何劳远去觅天堂。任运安闲，处处是仙乡。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfc67a609aa0a19e3a','悟南柯','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"莫要欺人重，须防报应深。群真降世布清吟。劝化愚迷，灾祸免相侵。\",\"更悟真常性，勤修吉善心。长生路上听仙音。正道无疑，稳步赴瑶岑。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfc67a609ae7433f42','悟南柯','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"生日无明重，死时地狱深。寸阴可惜胜黄金。速悟无生，上士做知音。\",\"转眼迷他趣，回头认本心。功成自是出阳阴。物外真闲，坦荡赋清吟。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfc6c09fe05a37297e','西江月·劝众减睡','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"夏日勤修宝殿，冬天好炼顽心。三更不纵睡魔侵。便是先生戒禁。\",\"学道休迷妄想，修真莫失丛林。无为功满恣歌吟。自在高眠稳枕。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfca04e9a32d2058c6','巫山一段云·自咏','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"湿热燕南地，清凉山后天。阴阳难晓倒颠颠。宿业自牵缠。\",\"二载登程酷暑。终日无言无语。闲中功行未能圆。使我忘心坚。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfca2083e59415b3c7','一剪梅·述怀赠柳先生','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"一点灵明本寂然。随通随感，应赴诸缘。头头认得这些儿，动也方圆。静也方圆。\",\"日用无私不着边。清虚消息，此法堪传。指人顿悟入无为，行也忘言。坐也忘言。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfd4f6c671328465e4','江城子·和苍盖郭秀才韵','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"乘车坐马走东西。论玄规。入幽微。天下峥嵘，随分立阶梯。吾教流通天地祐，无不喜，尽归依。\",\"其中消息与谁期。解忘机。识无为。霞友云朋，心地要平夷。抱朴全真明日用，神灿灿，性辉辉。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfd571477e27fbc8f4','悟南柯劝世','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"着假求仙果，迷真觅佛心。爇香礼拜恐徒寻。不断无明，空惹业根深。\",\"苦口如良药，忠言似美金。人逢善者作知音。正教依凭，万祸不能侵。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfd70c90e11c85146d','江城子·别樊山先天观道友','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"先天欲别意沉吟。就清阴。散幽襟。酷暑全无，蚊蚋不相侵。清静安居堪久计，住一日，胜千金。\",\"此方道友果坚心。日相寻。演清音。访道崇真，通古更明今。九夏待予无以报，临去也，赠荒吟。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfd8bcc76c1d7aea63','临江仙·西山静坐','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"静坐西山深有益，等闲人事稀逢。终朝饭饱恣疏慵。养成清静体，化出主人公。\",\"三十年来都乱觅，如今认证真容。狂情灭尽法皆空。本来无一事，刚恁费千功。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfd95a1b3bd7e49dfb','江城子·榆次县真常观作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"真常应物要方圆。且随缘。行功全。眼正心明，认得化工权。日用无私当直养，浩气满，现胎仙。\",\"一时庆会兴无边。透重玄。永绵绵。照彻人间，幻境岂能坚。便约故园堪久计，观八水，玩三川。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfda24374656639c4f','临江仙·示众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"自对千差无可取，心闲一境堪凭。真常不昧谷神清。群魔从此灭，一点自圆成。\",\"物外清吟唯独乐，人间宠辱何惊。劝君速悟问前程。要求真实相，休论假聪明。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfdae89f2031eb1c6d','凤栖梧太平兴国观作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"古迹琳宫堪作计。桧柏青青，宛有长春气。直待功圆方济会。无心默默符天意。\",\"幸得安然清静地。终日逍遥，方寸无萦系。老大不堪心自退。纷纷世事何时已。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfdcd2ab090230a427','江城子·咏腊雪','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"琼花缭乱玉尘飞。满长堤。拥柴扉。暖焙窗明，安用绣帘帏。夜静云收银鉴满，看雪月，斗光辉。\",\"季冬真瑞古今奇。助寒威。显丰期。感动诗人，魂魄入希夷。谢女多才咏不尽，留与俺，道人知。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfe018d456550dc466','减字木兰花·西山作','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"修真近俗。初地难当心境熟。近俗修真。谁是居尘不染尘。\",\"林泉恣兴。便是修真无可证。恣兴林泉。且做逍遥陆地仙。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfe42f6f639563f2af','一剪梅·下山','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"为爱前高自觉过。天生阻隔，不放冲和。前头何处道缘深，无福清闲，有分奔波。\",\"老也功亏一任魔。长春教语，慎勿蹉跎。自来达道学仙人，功也无多。行也无多。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfe51ba1faaf7dcddf','江城子·方壶过冬','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"严冬共喜小方壶。称幽居。论元初。话到三更，皆似觉清虚。万事有心求不得，凭象罔，获玄珠。\",\"一言了了性如如。乐无为。驾云车。朝拜高真，功满赴仙都。速悟人人俱有分，听予劝，莫痴愚。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfe821d9337cdc85c5','道无情·依韵别沁州长官杜帅','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"未达难通天令。谁解洞中生静。道在有无间。隔千山。\",\"去住绝疑心定。任运逍遥随命。终日不迷南。更何参。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddfee1c3d911672ece1','减字木兰花·一道士出示杜甫游春诗卷','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"游春畅饮。万物熙熙花似锦。畅饮游春。迷了从来多少人。\",\"儒流道士。几个能通天外志。道士儒流。谁肯忘情言下休。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddff0de0348434b3c50','巫山一段云·赠龙虎夫人','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"北海功还未，赤城行已周。一身清静永忘忧。何处得闲愁。\",\"四十天当不惑。事事谙来明白。乐知知命保安和。真理本无多。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddff1f9fe31446adeb9','瑞鹧鸪·示众','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"论道谈禅斗捷机。朦胧合眼便昏迷。贪名竞利心犹在，损己安人行岂知。\",\"益友不侵贤圣远，良朋难近虎狼随。一朝数满无由悔，闻早收心莫纵欺。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddff29ff18e1259fcef','点绛唇','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"闻说修行，千差万别难依据。真空路。何人得趣。撒手堪归去。\",\"行积无边，功要三千数。心开悟。逍遥云步。应过天仙举。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddff29ff18e82a8c5f2','点绛唇','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"学道难成，无明触处生烟火。招殃祸。时光虚过。生死如何躲。\",\"早悟前愆，更不生人我。还真个。时时明破。下手修仙果。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddff29ff18ed8fc5da9','点绛唇','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"学道寻真，宁心耐意搜求正。刀圭柄。群真相庆。一颗明珠莹。\",\"照见元初，认得头和影。神通圣。内容清兴。物外闲吟咏。\"]',NULL,NULL,NULL,NULL),('c16655df30cb0ddff29ff18ee0345087','点绛唇','尹志平','c16655df30cb0ddf1e1d1841838e8afb','元',NULL,'[\"晔晔重阳，素秋气爽添清兴。仙歌咏。黄华相称。朵朵如如性。\",\"小槛芬芳，满座人钦敬。真清静。香风纯正。万户千门庆。\"]',NULL,NULL,NULL,NULL),('c16655df31159d95b69548b353af1f8f','临江仙·河山亭留别钦叔裕之','辛愿','9c41898531159d95d1457b726c14da10','元',NULL,'[\"谁识虎头峰下客，少年有意功名。清朝无路到公卿。萧萧华屋，白发老诸生。\",\"邂逅对床逢二妙，挥毫落纸堪惊。他年联背上蓬瀛。春风莲烛，莫忘此时情。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e1368c914591c2808','临江仙','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"爱煞林泉风物好，羡他归去来兮。世缘相挽又还思，功名当壮岁，疏懒记当时。\",\"肝胆自知尘辈异，凤池麟阁须期。风云满目任时宜，东山高卧处，丝竹醉吴姬。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e1368c914f77d88fb','临江仙','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"千古武陵溪上路，桃花流水潺潺。可怜仙侣剩浓欢，黄鹂惊梦破，青鸟唤春还。\",\"回首旧游浑不见，苍烟一片荒山。玉人何处倚阑干，紫箫明月底，翠袖暮云寒。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e1fb294df01d6abfb','鹧鸪天·围襄阳','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"铁甲珊珊渡汉江，南蛮犹自不归降。东西势列千层厚，南北军屯百万长。\",\"弓扣月，剑磨霜，征鞍遥日下襄阳。玉门今日功劳了，好去临江醉一场。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e516492f11224fb79','清平乐·四首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"天南地北，何日兵尘息，四海升平归老忆，凤远岐山空碧。\",\"衣冠滚滚争春，谁能卧辙攀轮，一剑风云未遂，几回怒发冲巾。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e516492f14260fd37','清平乐·四首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"高眠窗北，偃卧喧雷息，依约关山归路忆，梦绕池塘春碧。\",\"功名负我青春，匆匆日月奔轮，且把琴书归去，山林道发儒巾。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e516492f14706eb86','清平乐·四首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"关河南北，有雁无消息，落日楼头人正忆，啼鸟一声山碧。\",\"莺莺燕燕争春，红尘马足车轮，惟有新丰豪客，东风老泪沾巾。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e516492f185d20182','清平乐·四首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"穷冬冷落，客思添萧索，浊酒沽来须强酌，要把闲愁推却。\",\"时间荣辱何惊，胸中气象休更，且匣风云长剑，天容两鬓青青。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e61a09a1fe1b95b80','木兰花慢·题亳州武津关','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"忆谯都风物，飞一梦，过千年。羡百里溪程，两行堤柳，数万人烟。伤心旧家遗迹，谩斜阳，流水接长天。冷落故祠香火，白云泪眼潸然。\",\"行藏好向故人传，椽笔舞蛮笺。总纠纠貔貅，秋风江上，高卧南边。功名笑谈尊俎，问锦江何必上楼船。他日武津关下，春风骄马金鞭。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e6831784d2fb742fc','天净沙·梅梢月','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"西风落叶长安，夕阳老雁关山，今古别离最难。故人何处，玉箫明月空闲。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e6831784d66bcc488','天净沙·梅梢月','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"黄昏低映梅枝，照人两处相思，那是愁肠断时，弯弯何似，浑如弓样眉儿。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e8162d02ccbe0bf98','喜春来','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"金装宝剑藏龙口，玉带红绒挂虎头。旌旗影里骤骅骝，得志秋，喧满凤凰楼。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4e9156e5e86fc140d8','青玉案·寄刘仲泽','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"西风天际征鸿去，问曾过，燕山路，叶落虚庭空绿树。一川秋意，满怀愁绪，楼外潇潇雨。\",\"天涯望断行云暮，好著蛮笺寄情句，底是相思断肠处。吟风赋月，论文说剑，无个知音侣。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ea1cbcbf6418a142e','木兰花慢·征南三首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"混鱼龙人海，快一夕，起鹍鹏。驾万里长风，高掀北海，直入南溟。生平许身报国，等人闲、生死一毫轻。落日旌旗万马，秋风鼓角连营。\",\"炎方灰冷已如冰，馀烬淡孤星。爱铜柱新功，玉关奇节，特请高缨。胸中泠然冰雪，任蛮烟瘴雾不须惊。整顿乾坤事了，归来虎拜龙庭。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ea1cbcbf677bdd0fb','木兰花慢·征南三首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"功名归堕甑，便拂袖，不须惊。且书剑蹉跎，林泉笑傲，诗酒飘零。人间事、良可笑，似长空、云影弄阴晴。莫泣穷途老泪，休怜儿女新亭。\",\"浩歌一曲饭牛声。天际暮烟冥，正百二河山。一时冠带，老却升平。英雄亦应无用，拟风尘、万里奋鹏程。谁忆青春富贵，为怜四海苍生。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ea1cbcbf6cd683e51','木兰花慢·征南三首','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"乾坤秋更老，听鼓角，壮边声。纵马蹙重山，舟横沧海，戮虎诛鲸。笑入蛮烟瘴雾，看旌麾、一举要澄清。仰报九重圣德，俯怜四海苍生。\",\"一尊别后短长亭。寒日促行程，甚翠袖停杯。红裙住舞，有语君听。鹏翼岂徒高举，卷天南地北日升平。记取归来时候，海棠风里相迎。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4eac8a4d39b6deaf71','太常引','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"晚凉庭院锁黄昏，鼓角静谯门，酒兴恋诗魂。清绕断，梅梢月痕。\",\"胸中豪气，壶中春色，醉眼小乾坤，今古不须论。且更尽，花前几尊。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4eb0d9d103f8a16bd0','殿前欢襄阳战','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"鬼门关，朝中宰相五更寒。锦衣绣袄兵十万，拔剑摇环，定输嬴，此阵间。\",\"无辞惮舍性命，争功汗，将军战敌，宰相清闲。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ecfbfadad71b210ee','满江红·襄阳寄顺天友人','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"奔驿南来，拥貔貅，且趋江右。良自愧，劣才微渺，圣恩洪厚。万里长江今我有，百年坚壁非他守。看虎牙，飞上万山头，诛群丑。\",\"风雨梦，乡关友。南北事，君知否。寄一缄梅信，小春时候。夜静戟门严鼓角，月明莲幕闲诗酒。怕故人，相忆问归期，平蛮后。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed0e9b1630305b84f','点绛唇·咏海棠','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"星斗文章，词源落落倾胸臆。十年南北，几度空相忆。\",\"把酒留君，后会知何夕。愁如织，一鞭行色，春雪梅花驿。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed0e9b16334bd9042','点绛唇·咏海棠','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"醉脸匀红，向人无语夸颜色。一枝春雪，犹染嵬坡血。\",\"庭院黄昏，燕子来时节。芳心折，露垂香颊，羞对开元月。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed0e9b1634a1d7fbf','点绛唇·咏海棠','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"独上高楼，恨随春草连天去。乱山无数，隔断巫阳路。\",\"信断梅花，惆怅人何处。愁无语，野鸦烟树，一点斜阳暮。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed0e9b1639ad51a0e','点绛唇·咏海棠','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"春日前村，一枝香彻江头路。月明风度，清煞西湖句。\",\"昨夜幽欢，梦里谁呼去。愁如许，觉来无语，青鸟啼芳树。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed0e9b163f96fb8ed','点绛唇·咏海棠','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"庭院黄昏，子规啼破开元梦。晚风吹动，似舞霓裳弄。\",\"有色无香，好著诗人讽。和谁共，月廊烟重，烧彻兰膏凤。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed8514caf40781f81','南乡子','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"深院日初长，万卷诗书一炷香。竹掩茅斋人不到，清凉，茶罢西轩读老庄。\",\"世事莫论量，今古都输梦一场。笑煞利名途上客，乾忙。千丈红尘两鬓霜。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed8514caf6660dd5c','南乡子','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"天地萃英灵，秀出人龙问世生。不只文章为第一，峥嵘，气吐虹霓万丈横。\",\"白褐黑头卿，埋灭黄尘气未平。昨夜长庚高似月，分明，光照乾坤彻五更。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed8514cafb63a4ea4','南乡子','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"浅淡汉宫妆，扇底春风玉有香。特地向人歌一曲，非常，纵使无情也断肠。\",\"宝髻绣霓裳，云雨巫山窈窕娘。好著千金携得去，何妨，丝竹东山醉玉觞。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed8514cafe2b28800','南乡子','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"音信怪来稀，世态时情固自宜。莫比红尘儿女辈，须知，义士交情死不移。\",\"应是占花期，箫鼓东城醉玉姬。谁念书生寒屋底，伤悲，忍泪窗前听子规。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ed8514cafe3b63ec6','南乡子','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"烟草入重城，马首关山接去程。几度留君留不住，伤情，一片秋蝉雨后声。\",\"无语泪纵横，别酒和愁且强倾。后会有期须记取，叮咛，莫负中秋夜月明。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ef32f182e47c777d9','浣溪沙','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"山掩人家水绕坡，野猿岩鸟太平歌，黄鸡白酒兴偏多。\",\"幸自琴书消日月，尽教名利走风波，钓台麟阁竟如何。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ef32f182e6013595c','浣溪沙','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"一片西风画不成，无人来此结茅亭，野猿山鸟乐升平。\",\"名利著人浓似酒，肝肠熟醉不能醒，黄尘奔走过浮生。\"]',NULL,NULL,NULL,NULL),('c16655df312bde4ef32f182ebd4c23bf','浣溪沙','张弘范','c16655df312bde4ed3890178da11e8cd','元',NULL,'[\"新卜西山崦下庄，疏篱编竹草苫堂，门前流水柳成行。\",\"满目烟岚诗酒地，十年鞍马是非场，虚名半纸几多忙。\"]',NULL,NULL,NULL,NULL),('c16655df3140d296f22e8d7ac6df0951','西湖竹枝词','赵奕','c16655df3140d296d1457b72d1457b72','元',NULL,'[\"湖头日日水光波，两两吴娃打桨过。笑隔芙蓉不相识，向人犹自唱吴歌。\"]',NULL,NULL,NULL,NULL),('c16655df32497005afb9d93c5ce6673d','和西湖竹枝词二首','李元圭','c16655df32497005d1457b72d1457b72','元',NULL,'[\"三月湖边花正开，江边望船郎未回。燕子来时春又去，心酸不待吃青梅。\"]',NULL,NULL,NULL,NULL),('c16655df32497005afb9d93cbe20e95f','和西湖竹枝词二首','李元圭','c16655df32497005d1457b72d1457b72','元',NULL,'[\"郎去远过江上山，望郎江上几时还。只怕郎归不相识，湖边日日照容颜。\"]',NULL,NULL,NULL,NULL),('c16655df33a0e238617f15b97683ba58','和西湖竹枝词','张渥','c16655df33a0e238d1457b72d1457b72','元',NULL,'[\"长簪高髻画双鸦，多在湖船少在家。黄衣少年不相识，白日敲门来索茶。\"]',NULL,NULL,NULL,NULL),('c16655df341c9c499f7691bc5f638dc7','太常引·仙吕·饯齐参议回山东','刘燕歌','c16655df341c9c49b49c63fab49c63fa','元',NULL,'[\"故人别我出阳关，无计锁雕鞍。今古别离难，兀谁画蛾眉远山。\",\"一尊别酒，一声杜宇，寂寞又春残。明月小楼间，第一夜相思泪弹。\"]',NULL,'故人别我而去遥远的他乡，没有办法能把他留住。人生离别是古今一大难事，从今后还有谁与我画眉？\n饮下这一杯饯行的别酒，听着杜鹃的一声声啼叫，落花流水，春已将尽，空留下一片寂寞。在这月夜小楼中独居，第一夜便相思流泪，以后的日子如何打发？','此曲为酒别离曲，情感真挚、深沉。表现出元代社会下层妇女的真情实感，再现一代才女的创作水平。《古今词话》、《词苑萃编》都注“传唱一时，脍炙人口。”\n此曲系一首别离曲，情感真挚、深沉。特别是“无计锁雕鞍”句把不忍离别的心情刻画得淋漓尽致，表现出下层妇女重情笃义的美好情怀。用“别酒”、“杜宇”、“明月小楼”等传统吟别意象却又能写出新意新境。在晚春时节，离别之人在杜鹃的悲啼中对酒相望泪眼。此种离别情景是离人别后的回忆，为虚境。而此时的“明月小楼”及倚楼“相思泪弹”则为实境，虚实相映，和前句“无计锁雕鞍”相呼应。通过此曲，读者可以看出一代才女的艺术水准。','[\"阳关：送别之地。\",\"锁雕鞍：锁住鞍马，意为留住离人。雕鞍，借指坐骑。\",\"\\u201C无计锁雕鞍\\u201D句：没法锁住坐骑留下行人。\",\"兀谁：谁个，哪一个。\",\"蛾眉远山：指美女的秀眉。远山，以山比喻女子美丽的眉毛。\",\"杜宇：即杜鹃，啼声悲切。\",\"\\u201C明月小楼间，第一夜相思泪弹\\u201D句：第一难遣是月夜之相思。\"]'),('c16655df34b9e0140bbeea5f5fc60fe7','生查子·三两信凉风','莫崙','c16655df34b9e014d1457b72d1457b72','元',NULL,'[\"三两信凉风，七八分圆月。愁绪到今年，又与前年别。\",\"衾单容易寒，烛暗相将灭。欲识此时情，听取鸣蛩说。\"]',NULL,NULL,NULL,NULL),('c16655df34b9e0145d454f125d15b6ae','摸鱼儿·听春教燕颦莺诉','莫崙','c16655df34b9e014d1457b72d1457b72','元',NULL,'[\"听春教、燕颦莺诉。朝朝花困风雨。六桥忘却清明后，碧尽柳丝千缕。蜂蝶侣。正闲觅闲花，闲草闲歌舞。最怜西子。尚薄薄云情，盈盈波泪，点点旧眉妩。\",\"流红记，空泛秋宫怨句。才色何处娇妒。落红无限随风絮。诗恨有谁曾遇。堪恨处。恨二十四番，花信催花去。东君暗苦。更多嘱多情，多愁杜宇，多诉断肠语。\"]',NULL,NULL,NULL,NULL),('c16655df34b9e01469aed2cdb237d078','卜算子·红底过丝明','莫崙','c16655df34b9e014d1457b72d1457b72','元',NULL,'[\"红底过丝明，绿外飞绵小。不道东风上海棠，白地春归了。\",\"月笛曲栏留，露舃芳池绕。争得闲情似旧时，遍索檐花笑。\"]',NULL,NULL,NULL,NULL),('c16655df34b9e014b1ce629b654a0c26','水龙吟·镜寒香歇江城路','莫崙','c16655df34b9e014d1457b72d1457b72','元',NULL,'[\"镜寒香歇江城路，今度见春全懒。断云过雨，花前歌扇，梅边酒盏。离思相欺，万丝萦绕，一襟销黯。但年光暗换，人生易感，西归水、南飞雁。\",\"也拟与愁排遣。奈江山、遮拦不断。娇讹梦语，湿荧啼袖，迷心醉眼。绣毂华茵，锦屏罗荐，何时拘管。但良宵空有，亭亭霜月，作相思伴。\"]',NULL,NULL,NULL,NULL),('c16655df34b9e014e9db4ca83ca4191e','玉楼春·绿杨芳径莺声小','莫崙','c16655df34b9e014d1457b72d1457b72','元',NULL,'[\"绿杨芳径莺声小，帘幕烘香桃杏晓。馀寒犹峭雨疏疏，好梦自惊人悄悄。\",\"凭君莫问情多少，门外江流罗带绕。直饶明日便相逢，已是一春闲过了。\"]',NULL,NULL,NULL,'[]'),('c16655df35048440f22e8d7aaf37b962','西湖竹枝词','文信','c16655df35048440d1457b72d1457b72','元',NULL,'[\"湖西日脚欲没山，湖东新月牙梳弯。\",\"南北两峰船里看，却比阿侬双髻鬟。\"]',NULL,NULL,NULL,NULL),('c16655df3545588902a275eea2ad3ca8','浣溪沙·退道','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"剑树刀山雪刃横。千磨百拷死还生。哀声流血苦难登。\",\"针刺著身犹害痛，钢铓刳性莫非疼。如何淫放不修行。\"]',NULL,NULL,NULL,NULL),('c16655df354558890a6dfc2d587dc9cf','好离乡','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"独坐向南溪。一事无能百不知。所爱冥冥烟雨后，东西。云绽峨峨列翠微。\",\"苍骨太虚齐。冉冉寒光映日飞。何事中心看不足，忘归。似有膏肓病著肌。\"]',NULL,NULL,NULL,NULL),('c16655df354558890a6dfc2d9d5b4433','好离乡','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"乱草独弯跧。鼓腹高歌自在闲。一枕游仙清梦断，怡颜。笑傲声喧碧嶂间。\",\"日午启柴关。雀跃徘徊望远山。山下有人来问道，知难。雀跃无言笑却还。\"]',NULL,NULL,NULL,NULL),('c16655df354558890acf32c812aadd60','解冤结觅饭','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"北方一日，南方一日，共东西、四方交日。梦寐沉沉，且往来、游行销日。待佗年、道心开日。\",\"百年短景，都来几日。暗推排、今朝明日。不觉推排，到圣贤、嘉音来日。洞天开、是吾归日。\"]',NULL,NULL,NULL,NULL),('c16655df354558890b18dedd884af8a6','万年春惊睡','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"秋夜沉沉，漏长睡酷多思想。须依仗。道情和畅。不纵魔军王。\",\"打叠神情，物物离心上。虚空帐。慧灯明放。坐待金鸡唱。\"]',NULL,NULL,NULL,NULL),('c16655df354558890b3c51af3a2d23ac','恣逍遥','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"忙里偷闲，师前取意。胜如那、苦求庸昧。连朝抵暮，贪生不已。也道我、为人过了一世。\",\"假使狂图，兼能巧智。多方便、蕴成家计。儿孙有靠，金珠没底。终比□、精神较些憔悴。\"]',NULL,NULL,NULL,NULL),('c16655df354558890b3c51af45261c3e','恣逍遥','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"昔种良因，今生福地。虚空感、上真加卫。开坛阐化，垂恩普济。凡一月、于中建成三会。\",\"至日相呼，临时莫避。乘斋且、散心游戏。家中不足，眉头长系。也则是、浮生过了一世。\"]',NULL,NULL,NULL,NULL),('c16655df354558890bb43d08690021b6','凤栖梧寄东方学道者','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"天下风光何处好。八水三川，自古长安道。锦树屏山方曲绕。天涯海角谁能到。\",\"既是抛家须早早。云水登程，莫恋闲花草。直至潼关西岳庙。教君廓尔清怀抱。\"]',NULL,NULL,NULL,NULL),('c16655df354558890cd2fd099f077a5d','玉炉三涧雪自咏','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"夜宿磻溪古庙，晓登竹径荒村。日中无事馁巡门。淡饭求佗一顿。\",\"不会深穷造化，随缘且度朝昏。是非人我绝谈论。复返生前混沌。\"]',NULL,NULL,NULL,NULL),('c16655df354558890cf31f7bc42bed70','梅花引·磻溪旧隐','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"无名客。无牵迫。无桑无梓无田宅。古岩前。老松边。长歌隐几，徐徐考太玄。玄中默论无生死。实际何曾分彼此。贯千经。协三灵。包含万化，都归一念冥。行不劳坐不倦。\",\"任行任坐随吾便。晚风轻。暮天晴。逍遥大道，南溪上下平。溪东幸获忘形友。月下时斟消夜酒。酒杯停。月华清。披补救散发，欣欣唱道情。\"]',NULL,NULL,NULL,NULL),('c16655df354558890d22264b09d3946b','木兰花慢·转轮','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"叹灵源旷代，本无极，信优游。自朴散形生，销磨混沌，落入行囚。竛竮四方宛转，向迷津、大海苦淹留。法界群情扰扰，梦魂千古悠悠。\",\"沦流。贩骨如山，知何日，是程头。好锻炼真空，三光慧照，万劫云收。终须舍身拚命，更惜头护面几时休。裂碎中间一点，便超得岸神舟。\"]',NULL,NULL,NULL,NULL),('c16655df35455889101f36be50202a30','桃源忆故人丹阳屡传教诲寄答','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"虚空照耀明如镜。好弱头头皆应。随逐状同形影。稍错还提正。\",\"佗人谗报浑虚佞。远道狂言无证。切告后来休听。默默依贤圣。\"]',NULL,NULL,NULL,NULL),('c16655df3545588911b6a56988c6a564','报师恩虢县渭南泺里','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一方胜景满川稀。水竹弯环四面围。簇槛名花红冉冉，当门幽桧绿依依。\",\"争歌稚子春风舞，斗巧灵禽晓树啼。社内人家三十户，崇真修道压磻溪。\"]',NULL,NULL,NULL,NULL),('c16655df3545588911d50c8c0de46bbf','蓬莱阁述怀','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"栖霞客。西游栖在南溪侧。南溪侧。千寻赤岸，万株苍柏。\",\"无心只有轻云白。举头不见繁华色。繁华色。空华杂乱，世人贪得。\"]',NULL,NULL,NULL,NULL),('c16655df3545588916ef7b0a5ea745dc','忍辱仙人妙用','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"千古圣贤皆一轨。亘初得得从心起。除此逍遥安稳地。无馀理。自然消息恬然美。\",\"不在劳神并苦己。般般放下头头是。选甚花街并柳市。虚空体。本来一物无凝滞。\"]',NULL,NULL,NULL,NULL),('c16655df354558891749ff73f0b918c8','水龙吟·夜晴','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"夜晴寥廓初寒，淡天莹彻琉璃翠。无阴树下，长安楼上，月明风细。百祸潜销，万家同赏，一般清味。见金星朗朗，银河耿耿，交光灿，满天地。\",\"流转碧空如水。任纵横、略无凝滞。冲山拍海，倾光腾秀，绵绵吐瑞。达了从兹，宝瓶坚固，玉浆时泥。把衷情欲诉，何人会得，且陶陶醉。\"]',NULL,NULL,NULL,NULL),('c16655df3545588925665b3655302e46','水龙吟','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"洞天春色盈盈，乱山秀出千堆锦。云收雨敛，晓晴烟淡，碧空横枕。高卧怡怡，顿开怀抱，释迷忘寝。看仙花瑞草，迎风照日，腾光彩，异凡品。\",\"欢庆时丰岁稔。万邦宁、百邪俱禁。太平国里，长安陌上，纵横有甚。大道无疑，傍门斜径，不须详审。是从来浩劫，神仙过路，但曾经恁。\"]',NULL,NULL,NULL,NULL),('c16655df3545588925f1f7cd64099de9','神光灿','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"修真门户，大道家风，长春境界无边。秀气盈盈，闲里别有壶天。天中自然快乐，运三光、日月周旋。忘伎巧，任淳风坦坦，圣道平平。\",\"一念还乡寂处，三宫罩、清灵万派归源。浩浩神光，来去透骨绵绵。行人顿除造作，待功成、指日登仙。未行者。向词中、明取一言。\"]',NULL,NULL,NULL,NULL),('c16655df3545588925f1f7cd9ae37eef','神光灿','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"推穷三教，诱化群生，皆令上合天为。慕道修真，行住坐卧归依。先须保身洁净，内常怀、悯物慈悲。挫刚锐，乃初心作用，下手根基。\",\"款款磨砻情性，除贪爱、时时剪拂愚迷。福慧双全，开悟自入希夷。灵台内思不疚，任纵横、出处何疑。彻头了，尽虚空、裁断是非。\"]',NULL,NULL,NULL,NULL),('c16655df3545588925f1f7cdcfb8dbbc','神光灿','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"悲欢绝念，视听忘怀，从初号曰希夷。不晓根源，刚强说是谈非。百般拈花摘叶，谩徒劳、使尽心机。这些事，算人人易悟，个个难依。\",\"不在唇枪舌剑，人前斗、惺惺广学多知。上士无争，只要返朴除疑。冥冥放开四大，把尘劳、一旦纷飞。认得后，管教贤、拍手笑归。\"]',NULL,NULL,NULL,NULL),('c16655df3545588926f6a6aac4f07c73','忍辱仙人春泽','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"数载田苗长亢旱。今春雨雪何滋漫。嘉兆分明知过半。将来看。掀天大熟歌讴满。\",\"二月花开成片段。千株柳发排堤岸。又待教人装好汉。相呼唤。提壶挈榼争跳窜。\"]',NULL,NULL,NULL,NULL),('c16655df35455889277a03f5d312a21c','报师恩赠众道友','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"神仙缥缈太虚私。世俗无由得见之。幸遇门庭开教化，临逢斋醮莫推辞。\",\"担家造孽常终日，作福治心只暂时。更到时来心不谨，终身何以报恩慈。\"]',NULL,NULL,NULL,NULL),('c16655df35455889293710f3c174e209','水龙吟·春兴','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"昊天空阔初晴，气回万物欣欣茂。亭台俯仰，山川高下，妆成锦绣。碧洞清泉，响闻迢递，一声长溜。更时时注目，悠悠远看，青峰上，白云凑。\",\"无限灵禽异兽。慰闲心、不辞柴瘦。含风翠柏，双崖争长，千株竞秀。耀日丹台，四时为伴，百年随寿。任寒来暑往，星移物换，得高眠昼。\"]',NULL,NULL,NULL,NULL),('c16655df354558893094b557270a5c15','望江南·四时四首鸣鹤馀音卷之二','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"山中好，最好是春时。红白野花千种样，间关幽鸟百般啼。空翠湿人衣。茶自采，笋蕨更同薇。百结布衫忘世虑，几壶村酒适天机。一醉任东\"]',NULL,NULL,'《望江南四时四首鸣鹤馀音卷之二》是元代诗人丘处机诗词中的一首。',NULL),('c16655df3545588932ad97e193ecc146','渔家傲','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"夜来又见银河绽。碎剪鹅毛交加乱。染画素山如粉面。路难辨，迢迢万里如铺练。\",\"密洒高楼风似箭。江边听得渔翁唤。玉作棹竿银索缆。船行慢。水流冰结梨花绽。\"]',NULL,NULL,NULL,NULL),('c16655df35455889354a405adee4b309','望蓬莱·南溪竹磻溪旧隐也','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"南溪竹，腾秀入青冥。直节虚心功未显，深根固蒂道先明。霜雪岂凋零。\",\"休怅恨，大器晚圆成。自有孤高栖凤质，能教倜傥化龙形。佗日看超升。\"]',NULL,NULL,NULL,NULL),('c16655df354558893684079e6c462d64','爇心香','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"征雁回时，野菊斑斓。向深溪、古洞弯跧。孤吟静境，独炼还丹。被夜萧条，埪局促，坐艰难。\",\"一性参差，数载留连。到如今、方露因缘。琼珠达地，宝月通天。便出玲珑，忘机构，没孜煎。\"]',NULL,NULL,NULL,NULL),('c16655df354558893684079ecb9c1fca','爇心香','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"大道无形。方寸何凭。在人人、智见高明。能降众欲，解断群情。作闹中闲，忙中静，浊中清。\",\"情态如婴。怀抱如冰。自蒙笼、觉破前程。吾言至嘱，君耳深听。下十分功，十分志，十分诚。\"]',NULL,NULL,NULL,NULL),('c16655df354558893730bf96b7184567','浣溪沙·土埪避暑','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"仙院深沉古柏青。森森寒影拂苔轻。萧条终日爽人情。\",\"洞冷不知门外暑，心闲唯觉腹中清。绕身浑似积冰凌。\"]',NULL,NULL,NULL,NULL),('c16655df35455889388c45640ec83dfa','桃源忆故人','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"故人别后闲吟罢。寂寞云溪潇洒。百尺孤松影下。独弄周天卦。\",\"清风皓月虽无价。妙手奇工难画。欲向世间夸诧。谁是分真假。\"]',NULL,NULL,NULL,NULL),('c16655df354558893917b50810ad4315','水龙吟·西虢','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"凤鸣南邑清佳，大仙降迹行鸾地。琳宫宝阁，星坛月馆，槐阴竹翠。烟盖云幢，影摇寒殿，往来呈瑞。向虚亭东望，平川似锦，洪波泛。渺天际。\",\"山秀水甜人义。遍坊村、各生和气。我来不忍，轻归刘蒋，天心地肺。须待他时，暗淘真秀，育成丹桂。去长安路上，眠冰卧日，作终身异。\"]',NULL,NULL,NULL,NULL),('c16655df35455889392d79f7a89d639c','六幺令·法性','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"浑沦朴散，天地始玄黄。乌飞兔走渐生，群物类开张。一点如如至性，扑入臭皮囊。游魂失道，随波逐浪，万年千载不还乡。\",\"错了鸿蒙体段，憎爱日相望。却认父母形骸。做我好容光。劫劫轮回贩骨，受尽苦和殃。何人闻早，寻他归路，莹然恢廓旧嘉祥。\"]',NULL,NULL,NULL,NULL),('c16655df354558893ccc33b04cf60806','水龙吟·警世','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"算来浮世忙忙，竞争嗜欲闲烦恼。六朝五霸，三分七国，东征西讨。武略今何在，空凄怆，野花芳草。叹深谋远虑，雄心壮气，无光彩，尽灰槁。\",\"历遍长安古道。问郊墟、百年遗老。唐朝汉市，秦宫周苑，明明见告。故址留连，故人消散，莫通音耗。念朝生暮死，天长地久，是谁能保。\"]',NULL,NULL,NULL,NULL),('c16655df3545588946bc153ef201ef45','金莲出玉花法门寺李生求','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一团臭肉。千古迷人看不足。万种狂心。六道奔波浮更沉。\",\"天真佛性。昧了如何重显证。宝范仙宗。觉后凭君豁蔽蒙。\"]',NULL,NULL,NULL,NULL),('c16655df3545588949665ad3b57cb681','上丹霄赠京兆府统军夹谷龙虎','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"感皇恩，承天诏，控西南。大门敞、高对烟岚。双权再任，过期无代复登三。晏然军国，事和平，灾割封缄。\",\"年将暮，心归道，搜玄路，访清谈。降尊宠、谦下无惭。山人放旷，本来无得有何参。但能慈忍，戒荒淫、名挂仙衔。\"]',NULL,NULL,NULL,NULL),('c16655df354558894af0cdcd33ca7413','无俗念·竹','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"虚心翠竹，禀天然、一气生来清独。月下风前堪赏玩，嘲谑令人无俗。嫩叶萧骚，隆冬掩映，秀出千林木。英姿光润，状同玄辅寒玉。\",\"好事东里田侯，南溪新种，使我开青目。尽日高吟窗外看，风飐筠梢摇绿。冉冉幽香，萧萧疏影，坐卧清肌肉。云龛闲伴，雅怀惟称仙福。\"]',NULL,NULL,NULL,NULL),('c16655df354558894c11909ddad57d71','武陵春·渭南杨五生朝','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"新岁才交生万物，时令近元宵。瑞气蒙蒙降碧霄。方诞谪仙苗。\",\"貌态堂堂殊胜敏，归道厌凡嚣。石烂松枯寿更遥，龟鹤算都饶。\"]',NULL,NULL,NULL,NULL),('c16655df35455889591294ab70d4446b','瑶台第一层','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"宝运龙飞。当四海、群仙降迹时。万机多暇，三灵协赞，不动枪旗。玉楼金殿广，更月台风榭临池。静无为，泛彩舟鸣棹，凉簟枰棋。\",\"深惟。前王创业，太平难遇道难期。会逢天祐，遐荒入贡，玄教开迷。坐朝垂听暇，伴赤松、谈论希夷。胜驱骋。向人间一度，天外空归。\"]',NULL,NULL,NULL,NULL),('c16655df3545588959f8a1ea6b4aaacc','万年春衲衣','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"衲袄秋来，著身渐觉时相称。霜天净。畅怀游兴。不怕西风劲。\",\"百片千条，上下穿联定。宽还正。外疏狂性。内放明珠莹。\"]',NULL,NULL,NULL,NULL),('c16655df354558895a1cdbbd373e01f3','黄鹤洞中仙','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"都要奔波走。谁肯坚心守。南北东西总一般，此外无佗有。\",\"踏尽铁鞋迷，不出庵门透。水到渠成本自然，行满功成就。\"]',NULL,NULL,NULL,NULL),('c16655df354558895aacbaa999a8db3a','汉宫春·苦志','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"二十年间，大魔交正阵，约度千重。狂弓迸箭暗窗，零落无穷。因心睡觉，透历年、无碍真宗。兴慧剑，群魔自然消散，独聘威雄。\",\"出入锐光八表，算神机莫测，天网难笼。驱云扫雾荡摇，法界无踪。飞腾变化，任太虚、萧瑟鸣风。巡四海，峥嵘□□，往来几个人同。\"]',NULL,NULL,NULL,NULL),('c16655df35455889654f3740636aab59','望蓬莱·游兴','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"飘蓬客，天赐水云闲。自在行时无日月，相随到处有蓑蛮。风雨亦开颜。\",\"修炼事，地轴锁天关。出有入无三尺剑，长生不死一丸丹。名列上仙班。\"]',NULL,NULL,NULL,NULL),('c16655df35455889660ea3b4d4bfd0a3','解冤结','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"山河已定，干戈不起，太平时、八方和义。斋醮频修，盛答报、虚空天地。谢洪恩、暗中慈惠。\",\"千年一遇，神仙出世。幸遭逢、莫生轻易。供养精严，但一岁、胜如一岁。遇良辰、大家沉醉。\"]',NULL,NULL,NULL,NULL),('c16655df35455889663e85870b09bc7e','无俗念·灵虚宫梨花词','丘处机','c16655df35455889df0aab05c4851e8e','元','上片写梨花开放的时间、风姿、周围的环境。前两句点出梨花开于晚春的寒食节前后，“春游浩荡”表明是芳草萋萋、漫天飞花的暮春季节。“白锦”二句，以白锦和白雪比喻梨花的洁白无瑕与香花烂漫的盛开模样，反用岑参的“忽如一夜春风来，千树万树梨花开”，形象真实而鲜明。“静夜”至上片末，写梨花开放的环境，暮霭沉沉天地阔的静夜里，梨花悄悄地绽放在月光溶溶的夜色中。“冷”字，生动地烘托出宁静、微寒的夜景，使读者想到曹雪芹的“冷月葬花魂”。人间与天上都溶浸在皓月与花色交织而成的氛围中，月光皎洁、花似雪明，给人一种高洁、脱俗的感受。\n下片前三句用典，继续以“天姿灵秀”的姑射真人来比喻梨花，《庄子·逍遥游》云：“藐姑射之山，有神人居焉，肌肤若冰雪，绰约若处子。”这三句就是用这典故，这位神人，修养已经达到神化难测的境界，暗写梨花的冰清玉洁与静若处子的仙姿。“万化”诸句写作者的体会与想法。由上句的“神人无功”，这位藐姑射山神人虽有大功，却无意于求功，这正是作者所追求的志向；作者又由梨花的清高绝俗、不与群芳同伍，感慨世俗之人未能欣赏，就好像俗人对作者的清高傲骨未能赏识。毕竟，世人多醉我清醒，这种不食人间烟火、超尘拔世的清凉境界，也只有能进入洞天福地修行的有道者，才能心领神会。\n此词虽是咏物，实即咏自己，这正是文人咏物词的寄寓手法。此词把梨花独特超群的气质，写得生动鲜明，让人有清新脱俗、灵秀异常的感觉，虽结尾不免落入谈道论教的范围，但仍不失为一首佳作。\n词人所选取的歌咏之物，往往是个人内在性情与艺术风格的表征，因此丘处机歌咏浩气清英、仙材卓荦的梨花，可以看出其清新脱俗的风格、灵秀异常的神人气息，而表现于作品中，则呈现了清拔脱俗的风格特色。','[\"春游浩荡，是年年、寒食梨花时节。白锦无纹香烂漫，玉树琼葩堆雪。静夜沉沉，浮光霭霭，冷浸溶溶月。人间天上，烂银霞照通彻。\",\"浑似姑射真人，天姿灵秀，意气舒高洁。万化参差谁信道，不与群芳同列。浩气清英，仙才卓荦，下土难分别。瑶台归去，洞天方看清绝。\"]',NULL,NULL,'此词表面上虽是咏梨花，实则是作者借不食人间烟火的梨花，以寄托自身的超尘拔世之志。','[\"无俗念：词牌名，著名的词有长春真人的梨花词《无俗念》。\"]'),('c16655df35455889698b87ffc4ba75de','炼丹砂','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"守分莫强图。遣日闲居。乐天知命忍萧疏。万事休论成与败，兀兀前途。\",\"失去本来虚。得也何如。百年反覆乃须臾。不似中心存道念，贤圣相扶。\"]',NULL,NULL,NULL,NULL),('c16655df354558896facdaa3b3c15c83','解冤结自咏','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"当初学道、凭空炼己，志冲天、人间无比。放旷山林，次后复、逍遥云水。过夷门、又临秦地。\",\"飘蓬十载，游程万里。度关津、崎岖迢递。事事谙来，但悟了、般般总弃。只随缘、布裘芒履。\"]',NULL,NULL,NULL,NULL),('c16655df3545588974871374e6dc7981','黄鹤洞中仙·虢县渭南泺里','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"此地风光胜。人物俱相应。水竹深藏数十家，户户知天命。\",\"我爱清虚景，策杖寻幽径。每日巡村转一遭，信步闲吟咏。\"]',NULL,NULL,NULL,NULL),('c16655df35455889767e8a495be3cc2b','望蓬莱','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"听咨告，小事要君知。万事苦求终害己，得便宜处落便宜。伶俐不如痴。\",\"真休炼，心外莫行持。只具眼前为见在，自然烦恼不相随。步步入无为。\"]',NULL,NULL,NULL,NULL),('c16655df35455889767e8a49d0e4066f','望蓬莱','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"王乔地，一曲甚清嘉。古道弯环连水石，垂杨□□带烟霞。桃李间桑麻。\",\"其中有，崇道两三家。知命固穷皆淡薄，乐天清俭不奢华。随分保生涯。\"]',NULL,NULL,NULL,NULL),('c16655df35455889787c3b98e371095f','凤栖梧述怀','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"西转金乌朝白帝。东望银蟾，皓色笼青桂。渐扣南华排菊会。满斟北海醺醺醉。\",\"醉卧终南山色翠。山色清高，夜色无云蔽。一鸟不鸣风又细。月明如昼天如水。\"]',NULL,NULL,NULL,NULL),('c16655df3545588979b0620cd7273a4f','金莲出玉花青峰','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"云收雨霁。露出青峰寒骨势。野静天空。岌岌高横碧落中。\",\"南溪无景。与尔炎天销日永。永日题诗。不赋闲愁只赋伊。\"]',NULL,NULL,NULL,NULL),('c16655df354558897dce8e0881fe113f','贺圣朝·静夜','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"夕阳沉后，陇收残照，柏锁寒烟。向南溪独坐，顺风长听，一派鸣泉。\",\"迢迢永夜，事忘闲性，琴弄无弦。待云中、青鸟降祥时，证陆地神仙。\"]',NULL,NULL,NULL,NULL),('c16655df35455889824bf6e5b87be209','雨中花令','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一种灵苗体性殊。待秋风、冷透根株。散化开、百亿黄金嫩，照天地清虚。\",\"九日持来满座隅。坐中观、眼界如如。类长生、久视无凋谢，称作伴间居。\"]',NULL,NULL,NULL,NULL),('c16655df35455889838e09bb19c98592','瑶台月自咏','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"平生懒堕。只赢得、无忧一枕高卧。蓬头垢面，不管形骸摧挫。任三光、日夜奔驰，放四大、林泉担荷。深溪畔，幽岩左。青山拥，白云锁。灾祸。雷轰电掣，无由近我。\",\"日午起行了还坐。把旧习般般打破。清闲处，唯有这些儿个。倦贪心、乐受贫穷，爱恣意、慵兴烟火。粮无贮，丹无货。萧然唱，洒然和。堪可。神仙未了，优游且过。\"]',NULL,NULL,NULL,NULL),('c16655df3545588985eb08c7b1c9746b','凤栖梧道友见访于磻溪','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"孤僻嵁岩清净界。凿土安身，抱道忘知解。道友相看唯莫怪。贫闲守拙无相待。\",\"富贵功名堪倚赖。多是多非，尖凿多成败。玉食馨香终不耐。箪瓢寂淡常安泰。\"]',NULL,NULL,NULL,NULL),('c16655df35455889878eba2c4b973156','玉炉三涧雪暮景','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"杲日西沉远陇，轻飙南起洪崖。飘飘逸兴爽情怀。吹断愁思俗态。\",\"渐渐放开心月，微微射透灵台。澄澄湛湛绝尘埃。莹彻青霄物外。\"]',NULL,NULL,NULL,NULL),('c16655df354558898d41f387a1d9552e','西江月','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"屈指追思前世，低头省悟令望。今生若不做修行。又与轮回作争。\",\"幸遇直常要妙，点头莫故昏蒙。便挥宝剑杀三朋。諕得龟蛇火遁。\"]',NULL,NULL,NULL,NULL),('c16655df354558898f47a83938e2062f','沁园春·示众','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"世事纷纷，似水东倾，甚时了期。叹利名千古，争驰虎豹，丘原一旦，总伴狐狸。枳棘丛中，桑榆影里，乱冢堆堆谁是谁。君知否，谩徒劳百载，空皱双眉。\",\"争如归去来兮。放四大、优游无所为。向碧岩古洞，完全性命，临风对月，笑傲希夷。一曲玄歌，千钟美酒，日月循环不老伊。童颜在，镇龟龄鹤寿，罢喝黄鸡。\"]',NULL,NULL,NULL,NULL),('c16655df354558898fb1d6b65386fe4d','忍辱仙人','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"天下周游身不动。人间照了心无用。闲把虚空频趯弄。行云从。八方上下轻放纵。\",\"兔角锥儿钻骨痛。龟毛拂子敲山重。扫荡邪魔灵物空。清风送。太平国里乘鸾凤。\"]',NULL,NULL,NULL,NULL),('c16655df354558898fb1d6b66806da6b','忍辱仙人','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"物外天机唯不审。人间世事无过恁。纵你英雄官极品。身如赁。贪饕逼迫应图甚。\",\"我自饥餐并渴饮。布裘不羡披绫锦。饱暖之馀邪僻禁。虚堂任。曲肱展脚和衣寝。\"]',NULL,NULL,NULL,NULL),('c16655df354558898fb1d6b6fc5f7acd','忍辱仙人','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一泽天恩齐庆贺。群生地著无饥饿。愁态眉间都蹴破。还真个。盈街堆亩收田课。\",\"酝酒邀宾时唱和。排筵看食重堆棰。醉饱腥膻心不挫。骄矜过。却忧福里潜生祸。\"]',NULL,NULL,NULL,NULL),('c16655df3545588990ce65895cc815fc','金莲出玉花自述','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"蓬头垢面。不管形骸贫与贱。抱朴颐神。恬淡无忧乐本真。\",\"冰姿玉体。到了难趋趖土底。子羽潘安。泉下骷髅总一般。\"]',NULL,NULL,NULL,NULL),('c16655df35455889954b6e00a3d7b4b3','忆江南','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"山中好，长夏正相宜。修竹万竿金锁碎，飞流千尺玉帘垂。何处有炎曦。\",\"松影下，散诞更无拘。沉李浮瓜供枕簟，苍松白石伴琴棋。一醉任风吹。\"]',NULL,NULL,NULL,NULL),('c16655df35455889967af9d9c5d7c7df','上丹霄','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"洞天清，神山秀，少人行。尽贪恋、世梦峥嵘。仙风瑞景，眼前虽顿却如盲。爱河终日，竞浮沉、来往纵横。\",\"东方出，西方没，南方死，北方生。四方转、异类翻腾。区区甚日，道眸开阐欲心更。愿将灵质，悟空华、彼岸高登。\"]',NULL,NULL,NULL,NULL),('c16655df3545588997b5b8dac4f2fc9f','满庭芳','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"幼稚抛家，孤贫乐道，纵心物外飘蓬。故山坟垄，时节轻修崇。幸谢乡豪并力，穿新圹、起塔重重。遗骸并，同区改葬，迁入大茔中。\",\"人从。关外至，皆传盛德，悉报微躬。耳闻言，心下感念无穷。自恨无由报德，弥加志、笃进玄功。深回向，虔诚道友，各各少灾凶。\"]',NULL,NULL,NULL,NULL),('c16655df354558899838dd4804c39897','蓬莱阁','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"蓬莱阙。漫漫巨海深难越。深难越。洪波激吹，怒涛翻雪。\",\"玉霄东畔曾闻说。虚无一境天然别。天然别。鳌山不动，蜃楼长结。\"]',NULL,NULL,NULL,NULL),('c16655df35455889999fa22665d65930','报师恩','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一横嘉景日常新。古柏森森四季春。福地清高稀俗事，名坛时复会仙宾。\",\"人人尽喜生中国，户户虔心敬上真。唯愿诸公皆省悟，同登无漏出红尘。\"]',NULL,NULL,NULL,NULL),('c16655df35455889999fa2267167b496','报师恩','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"不僧不道不温柔。九伯人前不害羞。觉性一时超法界，知身亿劫是吾囚。\",\"改头换面人难悟，走骨行尸我不忧。得意忘形还朴去，从教人笑不风流。\"]',NULL,NULL,NULL,NULL),('c16655df354558899ae02d7d7f325f4b','满庭芳·述怀','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"漂泊形骸，颠狂踪迹，状同不系之舟。逍遥终日，食饱恣遨游。任使高官重禄，金鱼袋、肥马轻裘。争知道，庄周梦蝶，蝴蝶梦庄周。\",\"休休。吾省也，贪财恋色，多病多忧。且麻袍葛屦，闲度春秋。逐疃巡村过处，儿童尽、呼饭相留。深知我，南柯梦断，心上别无求。\"]',NULL,NULL,NULL,NULL),('c16655df354558899d47d6d20511f99a','上丹霄答陇州防御裴满镇国','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"厌尘劳，抛家计，慕清闲。向物外、观照人间。须臾变灭，蜃楼歌侧海涛翻。暂时光景，转身休、百岁如弹。\",\"掀天富，倾城丽，过人勇，彻心奸。尽逐境、颠倒循环。纷纷醉梦，往来争夺苦摧残。不如闻早，伴烟霞、高卧云山。\"]',NULL,NULL,NULL,NULL),('c16655df354558899d96e1d45046a7ca','忍辱仙人声色','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"豪气冲天居列鼎。笙歌聒地排淫境。玉镫飞龙衫帽整。风流骋。不知扑入琉璃井。\",\"滑壁千寻光似镜。交加出路无门径。饶你玲珑机巧性。难逃命。与他送却头皮影。\"]',NULL,NULL,NULL,NULL),('c16655df354558899f9e04ea5e09d18b','喜迁莺·炼心','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"要离生灭。把旧习般般，从头磨彻。爱欲千重，身心百炼，炼出寸心如铁。放教六神和畅，不动三尸颠蹶。事猛烈。仗虚空一片，无情分别。\",\"关结。除缧绁。方遇至人，金口传微诀。顿觉灵风，吹开魔阵，形似木雕泥捏。既得性珠天宝，勘破春花秋月。恁时节。鬼难呼，唯有神仙提挈。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a0fee9e8048797f4','玉炉三涧雪勤劳','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"物外虽明端的，天心未放玲珑。区区陌上走西东。也学浮生作梦。\",\"梦寐更劳数载，岩龛复度三冬。待佗消息显真功。放出凌云螮蝀。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a298418085410ed6','万年春','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"土穴秋来，温温渐觉阳和胜。幽栖兴。道家偏称。疏懒多贫病。\",\"凛冽天寒，叶落山川净。窗前竞。雪飘风劲。热焙闲吟咏。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a2be0afca8a781c6','金莲出玉花夏旱','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"时当正热。正值天高时雨阙。万里晴晖。云欲生来风旋吹。\",\"如炉天地。尽日炎炎熔暑气。物困人疲。忆得前春嫌雨时。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a2d534f6e53f4748','减字木兰花','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"重阳师父。昔日甘河曾得遇。大道心开。设教东游海上来。\",\"天涯回首。挈得吾乡三四友。魏国升遐。惊动秦川百万家。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a46bdf1f9a81255d','瑶台月劝酒','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"浮名浮利。叹今古、悠悠颠倒人泥。茫茫宇宙，多少含灵愚智。尽劳生、终日贪图，竞抵死、奔波沉滞。观乌兔，嗟身世。百年寿，一春寐。虚费。争如满酌，流霞送醉。\",\"助四大聊壮神气。辨万化休论富贵。时时访，出谷道人游戏。效猖狂、物外高吟。庆滑辣、杯中美味。开怀抱，忘愁系。解其忿，挫其锐。遥致。青松皓鹤，绵绵度岁。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a56c4c575b30c8fa','沁园春·赞佛','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"净梵王宫，太子殷勤，雪山六期。把世情我态，丝毫断念，云根水谷，麻麦充饥。芥纳须弥，毛吞大海，自古男儿了悟时。超生灭，任循环宇宙，不管东西。\",\"圆成无得无知。信法界、空空寂灭机。又勿劳习定，安禅作用，偷闲终日，打坐行治。大理无时，真功非相，动静昏昏合圣规。无高下，但能通般若，总证牟尼。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a6f59de5dc8bbd2d','黄鹤洞中仙·自述','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"故里在天涯，海上无名士。因遇终南陆地仙，挈我来游此。\",\"素爱断蓬飞，野鹤孤云志。顶笠披蓑人不知，便是风狂子。\"]',NULL,NULL,NULL,NULL),('c16655df35455889a8441ddbb63eee3e','望海潮·脱俗','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"坚牢基址，清闲门户，生涯不比尘缘。幽旷大山，通明上士，逶迤仿效神仙。勤动向岩前。缓植桂栽松，药圃芝田。万仞高峰下，伴龟鹤，度流年。吾常志僻心颠。\",\"爱箪瓢淡溥，词翰嘲掀。幽洞小溪，开怀取兴，时成短句长篇。驰马胜花笺。任奋笔狂吟，走雾飞烟。放荡如如性。混终日，恣乾乾。\"]',NULL,NULL,NULL,NULL),('c16655df35455889af53dbcb48b83787','青莲池上幽栖','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一从东别长安道。西住磻溪庙，渐扣南山名迹杳。洪沟冷淡，土龛潇洒，北府何曾到。\",\"夜深陌上行人悄。独听岩前子规叫。切切松梢啼到晓。声声相劝，不如归去，争奈功夫少。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b079a9b772bf92f4','诉衷情令','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"孤城寒角韵悠扬。风送入斜阳。池塘菡萏无色，兰畹有馀香。\",\"秋日短，暮天长。月华昌。空空寂照，荡荡虚心，一片清凉。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b079a9b7888d0fff','诉衷情令','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"长安风景古今奇。吾道少人知。天心地肺时正，生杀按枢机。\",\"灵物秀，玉芝肥。射虹霓。山头凝望，目下三川，压尽华夷。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b079a9b7ca24001a','诉衷情令','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"纷纷霜叶乱飘扬。时令过重阳。黄花烂漫依槛，犹自吐清香。\",\"秋渐老，夜弥长。道情昌。云庵入定，法界游仙，不动凄凉。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b205697f679744c9','沁园春·九日虢县傅宅作朝真醮','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"晔晔重阳，秀气飘飘，廓周大千。正故庵交会，宾朋浩浩，青霄依约，鸿雁翩翩。是处登高，衔杯逸兴，放旷犹如陆地仙。朝真会，赞金风淡荡，玉露新鲜。\",\"黄花嫩蕊堪怜。散袅袅、清香满坐传。使众人得味，皆明至道，群莺无语，独王秋天。艳杏妖桃，繁华春景、莫与迎霜敢斗坚。乘佳趣，对芳丛烂饮，一醉千年。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b28ae7fa05d0a664','水龙吟·道运','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"混元南岳初开，瑞云透出昆仑表。星移电转，阴升阳降，红光缥渺。鹤舞鸾翔，看乌龟共，赤龙蟠绕。尽鸿蒙一气，烹成造化，神仙道，片时了。\",\"云散十方三岛，洞天深、月明风袅。此时占得，长生门户，遐龄体调。吟咏从佗，海移山变，石枯松老。伴烟霞独向，非非境外，恨知音少。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b3135ec00d7251af','醉蓬莱·九月十八日西虢刘氏醮','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"乍三清鹤降，万里云收，昊天空翠。玉磬琅琅，动乾坤声锐。尽日修斋，虚堂设醮，庆大仙恩惠。满献霞觥，长歌妙曲，留连师意。\",\"二九良辰，菊花开遍，正是重阳，素秋佳气。烂漫香风，健飘飖丹桂。秀景稀逢，上真难遇，幸一时相际。雁影沉沙，蟾光照夜，醺醺同醉。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b4717c6a4ea89d5d','齐天乐·忆法眷','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"自东离海上，元本三洲，四人同契。异域殊乡，同行并坐，终日相将游戏。谈玄论妙，究方外清虚，道家真味。唱和从容，一时法眷情何义。\",\"如今分显回然，苦志勤心，磨炼各逃倾逝。既昌飘零，难为会合，幽僻关山迢递。乾坤间隔，望落落犹如，晓星之势。再遇何年，驾云朝上帝。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b563c7c5f71ebe7f','金莲出玉花西虢南村','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"南村地胜。曲水横斜穿柳径。是处池塘。拍塞荷花映粉墙。\",\"高堂大厦。户户如屏堪入画。峻岭崇冈。日日生云遥降祥。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b64913665014a5e0','贺圣朝','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"断云归岫，长空凝翠，宝鉴初圆。大光明宏照，亘流沙外，直过西天。\",\"人间是处，梦魂沈醉，歌舞华筵。道家门、别是一船清，暗开悟心田。\"]',NULL,NULL,NULL,NULL),('c16655df35455889b64913667daacf6b','贺圣朝','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"洞天深处，良朋高会，逸兴无边。上丹霄飞至，广寒宫悄，掷下金钱。\",\"灵虚晃辉，睡魔奔送，玉兔婵娟。坐忘机、观透本来真，任法界周旋。\"]',NULL,NULL,NULL,NULL),('c16655df35455889bc32a2a541c0cc3f','凤栖梧','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"日月循环无定止。春去秋来，多少荣枯事。五帝三皇千百祀。一兴一废长如此。\",\"死去生来生复死。轮回变化，□□何时已。不到无心休歇地。不能清净超于彼。\"]',NULL,NULL,NULL,NULL),('c16655df35455889bc32a2a55725960f','凤栖梧','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"得好休来休便是。赢取逍遥，免把身心使。多少聪明英烈士。忙忙虚负平生志。\",\"造物推移无定止。昨日欢歌，今日愁烦至。今日不知明日事。区区著甚劳神思。\"]',NULL,NULL,NULL,NULL),('c16655df35455889bc32a2a55df68e04','凤栖梧','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"今日思量当日故。知我前程，迢递时难度。福祐不弘天不助。匆匆欲去无门去。\",\"走骨行尸心已悟。魂梦悠悠，且向磻溪住。幸谢街坊豪杰户。时时蓦谓来相顾。\"]',NULL,NULL,NULL,NULL),('c16655df35455889bc32a2a5fa972e54','凤栖梧','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一点灵明潜启悟。天上人间，不见行藏处。四海八荒惟独步。不空不有谁能睹。\",\"瞬目扬眉全体露。混混茫茫，法界超然去。万劫轮回遭一遇。九元齐上三清路。\"]',NULL,NULL,NULL,NULL),('c16655df35455889bdcbe49adc55e2b9','忍辱仙人春兴','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"春日春风春景媚。春山春谷流春水。春草春花开满地。乘春势。百禽弄古争春意。\",\"泽又如膏田又美。禁烟时节堪游戏。正好花间连夜醉。无愁系。玉山任倒和衣睡。\"]',NULL,NULL,NULL,NULL),('c16655df35455889bf5d07758f80c5ed','青莲池上客','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"重阳羽化登仙路。兄弟如何措。各各勤修生觉悟。通无入有，静思忘念，密考丹经祖。\",\"一时浩劫真容露。放荡情怀任诗句。直待人间功行具。云朋霞友，爽邀风月，笑指蓬瀛去。\"]',NULL,NULL,NULL,NULL),('c16655df35455889c2ebbe3b4e0f126d','鹧鸪天','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一片顽心要似飞。引入千古不思归。幸遇明师因晓了，肯教熟境再相随。\",\"你来时，我却西。我今省悟即从伊。万劫轮回皆为汝，百般魔障更因谁。\"]',NULL,NULL,NULL,NULL),('c16655df35455889c671cb0fdb763b84','心月照云溪','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"阴阳变化，万古同于此。得失暂时间，又何必、欣生恶死。存亡寿夭，都在百年中，回头看，北邙山，累累皆相似。\",\"身如赁舍，性假权居止。何处是家乡，任六道、循环驱使。觉来放下，不受苦孜煎，非眷属，莫忧佗，且要堤防自。\"]',NULL,NULL,NULL,NULL),('c16655df35455889c67a609a0e5d8be9','悟南柯','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"烂漫黄金蕊，轻盈白玉枝。重阳留得下元时。醮谢星官，特地献真师。\",\"牒秦三天主，声闻九地司。存亡福庆已潜资。大道洪恩。兼付出家儿。\"]',NULL,NULL,NULL,NULL),('c16655df35455889c67a609a1a8cf704','悟南柯','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"浩浩尘埃境，翩翩幻化躯。中情不解了须臾。任意奔波，颠倒走崎岖。\",\"逗引中丹坏，销靡内藏虚。悲愁灾患共萦纡。百便千方，医疗不能除。\"]',NULL,NULL,NULL,NULL),('c16655df35455889c67a609a4fe6fb23','悟南柯','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"白露三秋尽，清霜十月初。群花零落共萧疏。唯有重阳，嘉景独魁梧。\",\"烂漫真堪爱，馨香不可辜。人人皆插满头敷。试问乔公，簪著一枝无。\"]',NULL,NULL,NULL,NULL),('c16655df35455889c8aeae303477a5a5','望海潮·学道','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"神仙风范，长生门户，从来道德为基。馀外万般，留心一念，颠狂造作皆非。真教示开迷。自上古轩辕，龙驾齐飞。代代相传授，至今日，尽归依。\",\"虚无千圣同规。盖摧残嗜欲，剖判天机。贪利喻信雠，观身似梦，婪耽不整容仪。恬素返希夷，任垢面蓬头，纸袄麻衣。行满都抛却，泛寥廓，步云霓。\"]',NULL,NULL,NULL,NULL),('c16655df35455889ce17b792956f4e4a','留客住·修道','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"四元遇。过华山、共临秦地，咏歌谈笑，暗阐重阳佳趣。无为自令人化，有幸天使，官磨相间阻。东连海上，奋三公高义，大开门户。教行普。\",\"叹我离群，忘形慵举。内省无愆，外患何忧何惧。三光盛衰，交变万化，恩害相生天地数。留身且住。待青霄得志，坦然行步。\"]',NULL,NULL,NULL,NULL),('c16655df35455889cf4bd5042797bf82','满庭芳·警世','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"百尺危楼，千间峻宇，艳歌出入从容。幻身无赖，何异烛当风。旧日掀天富贵，当时耀、绝代英雄。百年后，都归甚处，一旦尽成空。\",\"诸公。闻早悟，抽身退迹，跳出樊笼。念本初一点，牢落无穷。幸遇时平岁稔，偷闲好、消息圆融。忘机处，灵波湛湛，独镇水晶宫。\"]',NULL,NULL,NULL,NULL),('c16655df35455889d95d4063a32bf9e1','如梦令·诫奢','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"陕右人人听我。福地好修因果。天下不如斯，贫富一般行坐。\",\"轻可。轻可。轻可骄奢太过。\"]',NULL,NULL,NULL,NULL),('c16655df35455889d989c918e547ad9e','离苦海/步蟾宫','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"知君好事从来慕。争奈染浮华难去。虽然欲意学飘蓬，被系脚绳儿缚住。\",\"匆匆顶上旋乌兔。切莫把光阴虚度。神仙咫尺道非遥，但只恐人心不悟。\"]',NULL,NULL,NULL,NULL),('c16655df35455889d9e5a226ac86ccd8','望蓬莱·秦川','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"秦川好，一片锦纹华。日出雨晴山色秀，月明风急水声嘉。千里净无涯。\",\"余到此，喜庆复难加。天祐时丰堪养道，地灵人杰不生邪。时复伴烟霞。\"]',NULL,NULL,NULL,NULL),('c16655df35455889dc41f87c3b77ad9c','木兰花慢','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"伫登临旷望，涌云气，北山兴。旋恍惚阴阳，虚无变化，寥廓充盈。奇峰状如太华，霭棱层、峻极染空青。急雨翻盆泼墨，迅雷激电飞声。\",\"威灵。大骋神通，三伏暑，结凝冰。叹是处飘风，良田遇害，厦屋遭倾。唯斯道乡幸感，屡涔涔至也忽然轻。深信皇天辅德，善因恶果分明。\"]',NULL,NULL,NULL,NULL),('c16655df35455889e0483df86c3c5fac','万年春杜鹃','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"春暖烟晴，杜鹃永日啼芳树。声声苦。劝人归去。不道归何处。\",\"我欲东归，归去无门路。君提举。有何凭据。空设闲言语。\"]',NULL,NULL,NULL,NULL),('c16655df35455889e36daca63aa9897e','望远行','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"九夏疲天旱，万物伤时热。算都为人心，分外生枝节。斗衣鲜马，壮社火班行引拽。小兄弟虚耗村村结。\",\"下士无邪正，上帝分优劣。□咱心不同，彼志胡漂撇。启虔诚修斋念善，因循岁月。望贤圣、空里相提挈。\"]',NULL,NULL,NULL,NULL),('c16655df35455889e429ae79cf626e1d','如梦令','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"皇统年时饥饿。万户愁生眉锁。有口却无餐，滴泪谩成珠颗。\",\"灾祸。灾祸。灾祸临头怎躲。\"]',NULL,NULL,NULL,NULL),('c16655df35455889e8ef4a7c86b48d66','戚氏','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"梦游仙。分明曾过九重天。浩气清英，素云缥渺贯无边。森然。似朝元。金童玉女传宣。当时万圣齐会，大光明罩紫金莲。群仙谣唱，诸天欢乐，尽皆得意忘言。流霞泛饮，蟠桃赐宴，次第留连。□□皆秉道德威权。神通自在，劫劫未能迁。\",\"冲虚妙，昊天网极，象帝之先。透重玄。命驾恍惚神游，掷火万里回旋。四维上下，八表纵横，鸾鹤不用挥鞭。□□应念随时到，了无障碍，自有根源。看尽清都绛阙，迈瀛洲紫府笔难传。瑶台阆苑花前。瑞云掩映，百和香风散，四时不夜春长暖，处处觉闲。想因缘，是一点功圆。混太虚，浩劫永绵绵。任阎浮地，山摧洞府，海变桑田。\"]',NULL,NULL,NULL,NULL),('c16655df35455889eb1c9c8c51277753','沁园春','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"智慧男儿，速悟尘劳，勿将性疲。但此身彼物，皆名幼化，多虚少实，不可追随。万种缠绵，千般汨没，荏苒光阴老却伊。争如向，太玄真教法，讨论希夷。\",\"乾坤荡荡无依。似一片、闲云出世奇。悟性宗合道，恩山易挫，神舟得岸，苦海难迷。行满功成，仙游羽化，物外何如土底归。无佗事，要升天入地，俱在心为。\"]',NULL,NULL,NULL,NULL),('c16655df35455889eb1c9c8c7c05b912','沁园春','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"列鼎雄豪，兔走乌飞，转头悄然。似电光开夜，云中乍闪，晨霜迎日，草上难坚。立马文章，题桥名誉，恍惚皆如作梦传。争如我，效忘机息虑，返朴归原。\",\"壶中异景堪怜。是别有风花雪月天。玩四时时见，祥云瑞气，三光光罩，玉洞琼筵。满泛流霞，高吟古调，骨健神清丹自圆。真堪爱，待功成一举，永镇飞仙。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f0f5c8b6bed92f59','满庭芳·九日','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"寒雁声回，园林色变，暮秋别是风光。练波横地，锦树映天长。过雨云山磊落，迎霜茂、金菊芬芳。佳辰会，千门万户，欢笑庆重阳。\",\"嘉祥。谁得遇，吾门四友，极味先尝。乃频沾清露，时倒霞浆。饮罢醍醐灌顶，归来后、月满虚堂。无愁思，陶陶快乐，酩酊入仙乡。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f170cfa83cc216b1','芰荷香乞食','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"日奚为。信腾腾绕村，觅饭充饥。拦门饿犬，撑突走跳如飞。张牙怒目，待操心、活龈人皮。是则是教你看家，宁分善恶，不辨高低。\",\"可叹狺狺此物，盖多生乖劣，一性昏迷。谈科演教，丁宁掐耳难知。虽然太上，驾亲临、无处慈悲。为人早早修持。还到恁时，发愤应迟。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f1d75c414a4d22ea','双双燕·春山','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"春烟淡荡，青山媚，行云乱飘空界。花光石润，秀出洞天奇怪。户牖平高万丈，尽耳目、临风一快。多生浩劫尘情，旷朗浑无纤芥。堪爱。\",\"逍遥自在。疏枷锁，抛离业根冤债。风邻月伴，道合水晶天籁。无限峥嵘胜景，尽赐与、山堂教卖。千圣宝珠，酬价问君谁解。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f29ff18eeaebd215','点绛唇','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"昨夜醺醺，醉中似觉乘丹凤。笙歌共。四天飞纵。□入桃源洞。\",\"正向深溪，闲把明珠弄。□晨动。一声风送。惊断游仙梦。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f316e4d138b9f6e4','金莲出玉花','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"登莱潍密。四海皆闻头插笔。爱诤多词。不肯饶人些子儿。\",\"余今向道。非似从前生计较。好弱都休。脑后如今没笔头。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f32f182e22af23f0','浣溪沙','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"云水飘飘物外吟。醍醐默默醉中斟。神仙活计道人心。\",\"容易肯争三寸气，寻常不贮一文金。清贫柔弱祸难侵。\"]',NULL,NULL,NULL,NULL),('c16655df35455889f5c58d6e22edf44d','报师恩疏慵','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"懒看经教懒烧香。兀兀腾腾似醉狂。日月但知生与落，是非宁辨短和长。\",\"客来坐上心慵问，饭到唇边口倦张。不是故将形体纵，养成贫病疗无方。\"]',NULL,NULL,NULL,NULL),('c16655df35455889fda18bb6daeed3aa','沁园春·景金本题作心通','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"大智闲闲，放荡无拘，任其自然。寄雅怀幽兴，松间石上，高歌沉醉，月下风前。玉女吹箫，金童舞袖，送我醺醺入太玄。玄中理，尽浮沉浩浩，来去绵绵。\",\"奇哉妙景难言。算别是、人间一洞天。傲立身敦厚，山磨岁月，从佗轻薄，海变桑田。神气冲和，阴阳升降，已占逍遥陆地仙。无烦恼，任开怀纵笔，端写灵篇。\"]',NULL,NULL,NULL,NULL),('c16655df35455889fdccfdbb1b829665','玉炉三涧雪','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"日落风生古洞，夜深月照寒潭。澄澄秋色净烟岚。独弄圆明宝鉴。\",\"认得心田要妙，咄回世俗贪婪。自欣山谷卧松岩。情愿被粗食淡。\"]',NULL,NULL,NULL,NULL),('c16655df35455889fdccfdbb72889fbd','玉炉三涧雪','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"一性昭彰乍显，二仪混合初融。飘飘法界任西东。到处神光覆拥。\",\"万籁寒泉凑顶，八方瑞霭腾空。怡然独向九霄中。坐看浮生作梦。\"]',NULL,NULL,NULL,NULL),('c16655df35455889fdccfdbb8352f735','玉炉三涧雪','丘处机','c16655df35455889df0aab05c4851e8e','元',NULL,'[\"最苦三冬冰雪，难当万里风尘。天涯海角不离身。何处参同心印。\",\"况是中丹宛转，徒劳外景因循。争如作伴到青春。看我行藏远近。\"]',NULL,NULL,NULL,NULL),('c16655df35580913617f15b92141a3b8','和西湖竹枝词','周溥','c16655df35580913d1457b72d1457b72','元',NULL,'[\"西湖西畔上清家，美人有如绿萼华。七星道冠拜星斗，万一琼台乘紫霞。\"]',NULL,NULL,NULL,NULL),('c16655df358454316f11c16c118a1703','喜迁莺·贺人生第三子','王特起','c16655df35845431d1457b72d1457b72','元',NULL,'[\"古今三绝。唯郑国三良，汉家三杰。三俊才名，三儒文学，更有三君清节。争似一门三秀，三子三孙奇崛。人总道，赛蜀郡三苏，河东三薛。\",\"欢惬。况正是，三月风光，好倾杯三百。子并三贤，孙齐三少，但笃三馀事业。文既三冬足用，名即三元高揭。亲朋庆，看宠加三锡，礼膺三接。\"]',NULL,NULL,NULL,NULL),('c16655df3584543178945c2e3b0c4004','喜迁莺·别内','王特起','c16655df35845431d1457b72d1457b72','元',NULL,'[\"东楼欢宴。记遗簪绮席，题诗纨扇。月枕双欹，云窗同梦，相伴小花深院。旧欢顿成陈迹，翻作一番新怨。素秋晚，听阳关三叠，一尊相饯。\",\"留恋。情缱绻。红泪洗妆，雨湿梨花面。雁底关河，马头星月，西去一程程远。但愿此心如旧，天也不违人愿。再相见，把生涯分付，药炉经卷。\"]',NULL,NULL,NULL,NULL),('c16655df35845431a696344e897f4a8c','喜迁莺','王特起','c16655df35845431d1457b72d1457b72','元',NULL,'[\"登山临水。正桂岭瘴开，苹洲风起。玄鹤高翔，苍鹰远击，白鹭欲飞还止。江上层波似练，沙际行人如蚁。目断处，见遥峰簇翠，残霞浮绮。\",\"千里。关塞远，雁阵不来，犹把阑干倚。数叠悲笳，一行征旆，城郭几番成毁。白塔前朝陵寝，青嶂故都营垒。念往事，但寒烟满目，愁蝉盈耳。\"]',NULL,NULL,NULL,NULL),('c16655df35845431d7d92007a2be4db5','梅花引','王特起','c16655df35845431d1457b72d1457b72','元',NULL,'[\"山之麓。河之曲。一湾秀色盘虚谷。水溶溶。雨蒙蒙。有人行李，萧萧落叶中。人家篱落炊烟湿。天外云峰迷淡碧。野云昏。失前村。溪桥路滑，平沙没旧痕。\",\"丹枫下。潇湘夜。横披省见王维画。画无声。惨经营。何如幻我，清寒此道行。马头风急催行色。疑是山灵嫌俗客。钓鱼矶。绿蓑衣。有人坐弄，沧浪犹未归。\"]',NULL,NULL,NULL,NULL),('c16655df35845431e7b8b6ef9f6c4877','喜迁莺·题郝仙女庙壁','王特起','c16655df35845431d1457b72d1457b72','元',NULL,'[\"汀洲苹满。记翠笼采采，相将邻媛。苍渚烟生，金支光烂，人在雾绡鲛馆。小鬟顿成云散，罗袜凌波不见。翠鸾远，但清溪如镜，野花留靥。\",\"情眷。惊变现。身后神功，丝满吴蚕茧。汉女菱歌，湘妃瑶瑟，香动倚云层殿。彤车载花一色，醉尽碧桃清宴。故山晚，叹流年一笑，人间飞电。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c101f03aeb3f873aff','使牛子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"精亏魄散游漾。把天真、变成虚妄。逐境性昏沉，苦海忙忙任流浪。\",\"前程路险如何向。劝汝等、早修无上。无上遍知明，明照离数焕师旷。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10b5deb4a703414aa','四犯令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一点皎然冰玉洁。浩劫无生灭。跨古腾今无暂歇。遍极目，真空摄。\",\"此法方知通妙诀。玄理人难别。两路曹溪分关节。映水照，天空月。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10b5deb4a979018b4','四犯令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"假里寻真真本别。慧照寥天彻。莹日晶空灵光摄。印碧落，凝翠色。\",\"达此心开通妙诀。物我俱休歇。无了了时离生灭。应别有，长天月。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10dab71bddca8d51d','红窗迥','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"弃凡情，归真理。击双关、玄门高启。吐神光、射透帘帏，现灵阳休体。\",\"金乌精，玉兔髓。晃乾坤、清华光炜。红窗迥，炳耀圆明，照逢壶彻底。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531b09b5ce04','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"大道孤然独立。善用本元辙迹。寰海好云游。玩清秋。\",\"月色扶空澄瑞，微现桂红轻翠。嘉景最奇幽。赛瀛洲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531b1cb0b1a5','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"海底凝珠鲜洁。天上莹然清绝。中道太虚宽。滚神丹。\",\"光胜一轮秋月。照鉴本无圆缺。清爽逼人寒。透琅玕。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531b420cb744','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"道契希夷恍惚，辩正其中有物。物物自然通。尽包容。\",\"月白风清满目。宝篆玲珑馥郁。寥落遍精空。有无中。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531b44a6a732','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"万象冲开寥廓。元气混然虚托。海月晏晶金。照天心。\",\"睫外威神辉豁。冰体雪肌绰约。稳稳过琼琳。上瑶岑。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531b650a0373','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"摆脱尘情冤业。此是利名休歇。清净谨威仪。宴瑶池。\",\"水静澄澄冰洁。显焕碧潭秋月。月里悟丹枝。性光辉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531ba30c0557','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"不觉形躯衰老。受尽许多萦恼。谁肯死前休。苦贪求。\",\"识破修行早早。认取自家珍宝。珍宝价无酬。永存留。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531bd5cb290b','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"运动一阳初出。照破太空微密。万象总潜形。炫然明。\",\"凝化鼎中灵质。丹就刹那功毕。仙子上云英。玩蓬瀛。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531bdb677e88','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"顶笠披莎归去。渐入芦花深处。绿岸缆渔舟。钓纶收。\",\"轻泛壶中一味。野鹤孤云相济。横管韵清幽。乐无愁。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531bdb798226','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"大道本无一物。遍纳十方充塞。无外亦无心。不容针。\",\"要见阐开清目。认得本元虚谷。非浅亦非深。满壶金。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c10f61531bf3f6c03e','一叶舟','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"照海凝珠光灿。贵贱不分弹雀。可惜本来珍。污泥津。\",\"认取勿令差错。莹净免繁萦缚。决正出嚣尘。玩长春。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c132ad97e124e70158','渔家傲','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一笠一蓑堪倚靠。波光曲岸垂钩钓。醁□浮卮自频告。高吟笑。尽教人道渔家傲。\",\"独弄无弦琴一操。个中始觉知音少。江月盈山空相照。谁登晃。蟾宫唯我亲曾到。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c132ad97e1383d14bb','渔家傲','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"地魄天魂勤烹炼。日精月髓情相恋。两曜交光如激电。分明见。玉炉万朵金花现。\",\"款款捞来捶作片。团团结就玲珑面。晃耀太虚烹玉霰。神光眩。飞丹透入通明殿。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c132ad97e153c10a65','渔家傲','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"百岁光阴如奔骑。浮华眩目飞尘隙。玄览涤除运神力。通消息。无生路上知端的。\",\"雾散瑶瞳升天奕。阳光渐著纯阴匿。万点灵砂晃虚璧。明皪皪。丹瑛一色符金碧。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c132ad97e168ce025f','渔家傲','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"日照阳魂空中炼。璇玑运斡天网转。咫尺眼前人不见。通一线。炉开鼎裂天光现。\",\"瑞散琼花飞片片。香风暗拂灵雯面。冷淡清虚情雅宴。真堪羡。玉宸扶御寥阳殿。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c132ad97e1cc99b416','渔家傲','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"混沌杳冥经寰宇。彩霞散尽明玉兔。西落咸池复回顾。心开悟。凝魂晦魄金乌趣。\",\"美盼扶彝寒光露。氤氲三色淳清素。直遇瑶天携女婺。无点污。丹书永系琳宫住。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c132ad97e1ec9ce9df','渔家傲','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"大道至真无言诘。韬光退隐藏深密。保炼元精无走失。隳阴质。瑞云捧出灵阳日。\",\"照破昏冥威天佖。混然恍惚空中实。莹净纯白相辅弼。通真一。丹书玉笈仙功毕。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca1015af220','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"人心刚硬，不肯回头。深沉苦海淹留。酒色财气不舍，甚日方休。闻早寻归出路，乐陶陶、云水闲游。无萦系，也无烦无恼，无喜无愁。\",\"渐入清凉活计，抱天真纯粹，一气和柔。配合铅霜，摄化鼎中频收。迤逦金丹，结就后，功成夺起仙俦。堪归去，跨鸾鹤，升入紫府瀛洲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca1126abe69','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"凿开混沌，撞透昆仑。斗柄斡转天轮。曲岸乌蟾，交腾走魄飞魂。汞鼎铅炉赫奕，炼金精、木液朱辰。星光显，散中元一气，万国霞絪。\",\"莹静清无尘翳，振山川林苑，万□璘璘。高下无殊，缅平一色精纯。对面料斯孰悟，谩徒劳、背觉合尘。休嗔道，似盲夫摸象，误失天真。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca11eceb6b2','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"贤愚间异，曲直丛林，清浊事急难分。多少无知，开口信意胡荤。鹓□呼为斥鴳，把灵椿、唤作朝菌。便假饶，是随何陆贾，辩正无门。\",\"牛缺谩施仁义，想肩吾锁目，自昧天真。摸象盲夫，昼夜晓甚明昏。玉罄权为煮料，把瑚琏、假作齑盆。最苦处，爨瑶琴烹羽，误杀麒麟。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca15592d727','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"光阴迅速，暑往寒来，俄然又报新年。物换形移，都被暗里催残。谁省人人越旧，改青春、皓发苍颜。虚过了，似落花飞絮，逝水无边。\",\"若也回光返照，运灵枢一撞，击碎双关。耀海玄珠，晞煌焰接昆山。宝性因明贯体，换凡躯、养就仙丹。玉辰显，向中天游奕，物外翛然。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca16058397b','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"韶光迅速，似蚁循环。往复不断相连。过了一岁，不觉又早一年。忽然无常限到，向前程、着是推延。修行事，道他人有分，自己无缘。\",\"若也回头省悟，弃功名富贵，物外翛然。保养元精，玉炉片雪烹煎。恍惚乾坤莹彻，灿玲珑、日耀辉鲜。三光秀，混一灵真性，送上瑶天。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca17c5372cf','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"心浮易动，熟景难忘。驱驰镇日忙忙。已过中年，有若日暮之光。恰如残灯怕晓，似风飘、败叶凋黄。最苦处，叹星移物换，人被无常。\",\"识破寻归出路，向玄门恳切，讨论仙方。付汝亲传，宁心静室消详。拟拭冰台莹彻，现神光、混入灵阳。圆明显得，长生久视，永劫安康。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca18e4a7748','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"忙郎痴呆，不识人抬。狂图捏怪胡来。执戆无智，空恁乱觅胡猜。口急虚脾厮□，斗无明、驰骋奇乖。强作坏，把他人障闭，自己沉埋。\",\"若悟天机指诀，叩玄关一击，宝洞门开。玉姹金婴出入，左右相偎。自然阴阳匹配，向空中、产个仙胎。真人显，越鳌宫日殿，恣意徘徊。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca1aa5dadfd','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"公邻烟水，我弊嵩峰。天涯杳隔难通。悟理非遥，目前不二清风。宝月孤高洞鉴，印千潭、普照玄洞。无彼我，混华元仙景，一体真空。\",\"如此知心志友，更何须贡马，驿路尘红。念某形疲，不须远远相躬。稽首同流勿罪，施宽慈、意莫忡悦。开青眼，用神光体入，不动之宗。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca1e0340959','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"元初一性，甚处安排。虚无恍惚中来。受气成形，父精母血凝胚。迤逦胎完降诞，未成能、长大情乖。自作坏，向途中役役，慢惹尘埃。\",\"虚使一生颠倒，昧天然至宝，万古沉埋。若悟生前，通玄鼎裂心开。混日凝阳纯秀，聚五方、霞彩曛回。玉辰显，便忘形遗照，直御瑶台。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c137d81ca1f3e102bd','声声慢','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"刚强柔弱，善恶分明，天地报应不同。滥浊荣家，贞烈志德长贫。清净无为妙道，被愚徒、返谤邪门。怎理会，见修真上士，有若雠人。\",\"高道猜为贼盗，把凶顽小辈，却做贤人。马饰衣襟，具礼晓甚卑尊。大家学些打懂，也胡来、讲甚人偷。抹截了，换云袍簪髻，搭个头巾。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1576594841aa9bfed','长相思','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"恣遨游。玩清秋。掩映曹溪细水流。琼花两岸秋。\",\"满浮沤。饮忘愁。日落星稀月上楼。沉沉醉玉洲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c15765948420fef559','长相思','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"道心宁。慧灯明。玩宝童儿启道经。清如佩玉声。\",\"湛然澄。焕然凝。燕静华池吞玉瑛。还纯体自贞。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c157659484bdb0ce9a','长相思','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"道无边。性光圆。认得龟蛇两下缠。阴阳合地天。\",\"汞和铅。气神全。光迸流霞耀晃然。方知玄又玄。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c157659484ffe8fb69','长相思','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"秘精精。满盈盈。密扣金关逆上行。玄珠颗颗倾。\",\"内灵灵。外明明。内外相交一点星。飞腾上玉瑛。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c16384968744bd99a9','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"无形道体，运阳光、密布通微恢鄂。炫惚昏衢烹浩气，冲塞英华盈落。莹日千寻，清风万里，显焕神光烁。夷门羽化，太玄一派灵廓。\",\"孤然脱洒无双，于中清净，不许纤尘著。养就胎仙神貌雪，出入无间绰约。紫府游行，瑶宫饮宴，赐我逍遥乐。金童捧盏，妙音引上龙阁。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1638496874b6815b8','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"太玄正教，离繁华、修整真清真静。清静之中开道眼，舒展银霞流井。溢落精空，冲盈法界，满满收金鼎。烹煎日月，混纯天地齐永。\",\"寂寥独立无双，孤然不改，万法心俱省。变化飞升朝上帝，返入清阳真境。不夜长春，神仙聚会，授命灵童请。玉婴吟送，一杯长寿酩酊。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1638496874eda5042','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"茅庵潇洒，静无尘、独坐垂帘孤僻。镇日闲闲清澹泊，别有恬然活计。握固存真，心无杂念，忘我兼忘世。玉婴神变，自然与道相契。\",\"光明照耀十方，凝空灿灿，大阐非为细。遍纳无极通造化，物物头头俱济。掌握乾坤，完容法体，玉貌真清丽。超凡出世，上朝三暻神帝。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c16384968753a73c11','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"长江素静，夜潇潇、天如玉案冰洁。秀色氤氲寥象廓，极目瑕琳光摄。独坐颐神，帘帏光透，显出玲珑雪。凝然澄湛，太虚一点通彻。\",\"神飞两翼翱翔，蟾宫殿里，夜宿琼枝歇。得遇真筌无损益，永受真人提挈。功行双全，金童玉诏，受命寥阳阙。玉霄宫里，永排仙位罗列。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1638496876896f0d1','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"玄元至道，大无隅、寂寂寥寥独立。遍应诸方通上下，极目天光微密。隐显存亡，穿无贯有，善用无辙迹。琅然泄化，太虚一点灵质。\",\"登林透人真阳，圆明普照，不许纤尘匿。理必期然仍未悟，说与门人端的。日殿祥光，蟾宫瑞彩，万派琼波益。神凝形释，玉岩依旧横碧。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1638496879750ae39','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"贪财竞色，利名牵、萦系何时休歇。镇日忙忙如梦想，颠倒心猿马劣。昧地谩天，多能已会，以巧翻为拙。万般虚假，积成宿世冤业。\",\"劝君省悟回头，攀缘弃舍，做个逍遥客。访道求真寻妙指，早愿超离生灭。不悟玄机，剔开毛塞，付耳亲传说。壶天景色，现前秋夜明月。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c163849687977e7f4e','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"幽微精粹，混阳光、一点殷红清绝。上下融和开慧目，跃出心珠凝结。阐化灵明，神光显现，内外相通彻。浮云消散，放开天外心月。\",\"虚无运转孤然，圆明寂照，万古无纤缺。与道同元包象法，物物头头俱摄。遍应诸方，无极太始，混一无分别。古今贤圣，尽归此处休歇。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c163849687a5344d4c','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一天清翠，混阳和微密，寂寥无物。致静颐心开上窍，通化灵明虚谷。育养元神，真人出现，放旷无拘束。皇天道父，赐余无限清福。\",\"天心正法盟威，紫虚道德，上品三乘箓。金简玄科封慧剑，心印清光无欲。己病先除，心邪摄正，百怪皆潜伏。不繁法信，自然披褐怀玉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c163849687a77ac097','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"玄元道体，大奂方、无隅无形无物。湿化胎生皆受气，感应通玄潜伏。包括乾坤，含容天地，日月皆收蓄。回光返照，旷然摄化盈目。\",\"分明顿著心珠，冲虚豁朗，灿灿开神谷。养就胎仙肌貌雪，出入随风香馥。暮嗅灵芝，朝餐仙桂，夜隐蟾宫宿。怡然欢乐，求同先圣真福。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c163849687cd32e350','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"浮生似梦，奈光阴百岁，都能几许。富贵荣华时暂过，刚甚厮牵厮系。总为儿孙，娇妻女婿，生死难为替。回头省也，瞥然不染尘翳。\",\"逍遥物外无拘，风邻月伴，便是余生计，石上溪边猿送果，酒满十方吟缀。醉卧烟霞，今宵还醒，意欲归何致。玉霄宫里，月中攀折仙桂。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c163849687f0d72ce9','酹江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"寂虚妙道，混杳冥恍惚，通微清绝。出化先天明象帝，满目流霞光屑。浊质消亡，琅函滋益，宝鼎灵砂结。丹华凝秀，素玄一色澄彻。\",\"精暚□抧盈空，清如秋夜，海桂无圆缺。耀古辉今冲皓气，满满玉壶春雪。莹日纯精，贞心幽粹，保益真仙悦。丹书迎诏，上朝三景罗列。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c16e59c98174fe3d51','蓦山溪','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"男儿一志，莫把初心退。决正处贞廉，把狂心、一齐割弃。随缘任运，云水作生涯，青松下，翠筠中，别有真滋味。\",\"清虚恬淡，便是余生计。气定自神和，迸玄珠、出离坎位。殷红璨璨，仿佛入离宫，通上下，照十方，摄境归心地。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1701c23eafbec76ba','上平西','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"启玄精，明真气，洞元中。间日魂月魄相融。内分三色，混然一体若芙蓉。玉辰圆鉴，瑞云浮、光射瑶宫。\",\"散阴氛，凝纯粹，归神化，运灵空。象悬明，灿耀玲珑。中天游奕，郁罗霄景玉音清。绛节霓旌，捧琼舆、宝梵仙风。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1702ef3c4bd361d60','连理枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"挫锐无羞耻。过宠无欢喜。好弱平常，随缘且过，坦然无系。步林泉、深入翠云中，乐清闲一世。\",\"欲要离生死。达彼真常理。炼气成神，神超碧落，太虚同体。任天宽地阔永无忧，得长生久视。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c179bb0013b8c94492','江城子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"丹华虚托假形骸。久沈埋。惹非灾。冗冗蜉蝣，都被死生催。顿觉悟知亡彼我，任空色，眼中来。\",\"海底金乌初出现，碧天开。绝纤埃。密布天光，光奕面前回。交灿瑞童颜胜玉，似雪映。一枝梅。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c17e8de16c361df12a','惜奴娇','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"猛悟回头，把尘事都忘了。急收心、速归大道。空里寻真，向无中传明教。最好。现丹华，回光返照。\",\"探□精微，表天奕幽深奥。明内外、浮沉颠倒。一气双关，混百神，分三要。玄妙。瑞云扶，玉辰容貌。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c186b912026005831e','虞美人','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"洞天一点天光密。莹净如千日。银霞潋滟贯寰瀛。万神攒簇，恍惚旷然明。\",\"空中产个胎仙出。绰约风飘逸。撞开天顶上云瑛。宝阙琼楼，玉簿挂仙名。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c187f71ee5c44f37b5','阮郎归·厌世忆朝元','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"云收雾敛天风洁。显出玲珑月。形释心凝澄彻。亘古无圆缺。\",\"千潭现，寒光摄。洞济溟阳哲。仙丹炼就真欢悦。异日朝金阙。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18a94363718c944fb','小重山','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"阴阳颠倒一声雷。忽然惊睡觉，梦中回。迷云拂尽慧光开。随风去，抱个日头来。\",\"温养结仙胎。空中明显现，越灵台。四维上下看蓬莱。堪归玉洞作仙才。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18bbed9d12936e9e6','捣练子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"真藏假，假藏真。释然独体绝疏亲。混元中，无我人。\",\"神通道，道通神。澄澄虚璧若冰轮。辗天河，出六尘。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18bbed9d1eee99922','捣练子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"说金翁，论黄婆。自然匹配炼冲和。铸红砂，满鼎多。\",\"神光显，照天河。胎光一点出洪波。现三清，上大罗。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18bbed9d1fce59573','捣练子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"真铅汞，坎和离。两般颠倒自相宜。炼金丹，耀日辉。\",\"通玄妙，得幽微。姹婴常把虎龙骑。上仙都，合圣机。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387063ee86b','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"杖上一瓢春酝，匣中三尺琅玡。眉间宝鉴镇妖邪。坐守更深半夜。\",\"金鼎烧成白雪，玉炉炼就黄芽。丹光潋滟泛琼华。天地氤氲相射。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387087c5c15','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"迷则身沉苦海，悟来月出西江。微风吹动桂花香。惊觉游仙一梦。\",\"鹤引鸾随甚处，家童笑指寥阳。玉牌金字号南昌。别是人间天上。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f3873191142c','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"顾我有身有患，窥真无作无为。自然斗柄运璇玑。日月周盈天地。\",\"点著冰灰雪炭，烧成火枣交梨。一时分付与峨嵋。益我聪明智惠。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387637ce41e','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"小隐不容沙芥，大通遍满诸方。混然灵廓查无疆。一性执其大象。\",\"火里烧成白雪，水中养就红阳。玉婴神变跨鸾凤。飞入西江月上。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f3876a6c1fa0','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"迷览千经亦少，悟知一字犹多。太虚鼎里炼冲和。养就玄珠万颗。\",\"顺理径游宝界，冲玄道上星河。万神聚会透烟萝。一体无分彼我。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387895bce91','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"割断铜绳铁网，敲开玉锁金枷。波涛苦海遇灵槎。游奕无阴村下。\",\"不在忘标指月，何须折桂拈花。太虚鼎里炼丹砂。结就玄珠无价。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f3878e19d013','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"务本颐生至理，迷源入死之机。亡邪守正更何疑。自有玄玄妙趣。\",\"赤子嬉游阆苑，青童笑指瑶池。西江月里折丹枝。保益精神久视。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387b0caf1f5','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"不肯内寻诸己，坚刚外觅多求。傍门小法久淹留。此辈难为拯救。\",\"把似守析待兔，争如冒雪骑牛。华胥国里恣遨游。午夜能令返昼。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387c1cc0b0a','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"莫恋金银财宝，休窥玉貌花容。百年终是落顽空。背了无生法认。\",\"欲识玄元正理，须明太上家风。至神至怪悟无穷。此个逍遥自分。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387c8281a87','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"不在瞻星礼月，何须诵戒看经。今朝与汝付叮咛。生死须凭性命。\",\"道启遍知无上，神通法界圆明。不劳作用自功成。志在无为清净。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387d0840374','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"浩气结成宝篆，清风吹散云蒙。一轮明月照虚空。好把精神舞弄。\",\"撞透玉京天上，冲开金阙门中。元神独坐广寒宫。率领群仙宗奉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387e85b37d0','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"眉间交枝琥珀，睛分脱质龙涎。鼎中烟袅彻禅天。一颗灵光出现。\",\"太上明兹玉象，如来证此金仙。清流异派本同源。悟理方知上善。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387f72da0da','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"寂静茅庵潇洒，危峰密锁烟霞。朝阳轩外一枝斜。待客清茶淡话。\",\"默坐翛然净洁，不占半点尘沙。冰台心似白莲花。长在西江月下。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387ff00f287','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"尽被家缘担阁，都因业障兰遮。离微孰举正兼邪。背了灵尝道话。\",\"云绽家家儿月。时来处处开花。睛童深入翠烟霞。引至长春不夜。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c18d41f387ff9a7bf7','西江月','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"不恋风花雪月，屏除富贵功名。刳心去志黜聪明。洗涤无知之病。\",\"破尽人门幻影，方通物外圆成。忘形遗照气神凝。务本乐天知命。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c190bdbd822713c560','满江红','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"昂簪策杖，赏月披风信途然。抱一冲和，养刀圭汞铅。金精木液润华田。密固三灵归火府，五云覆地，光彻梵天。虎龙交，宇宙明珠夺得，气象涓涓。宝霞辉日灿，显阳光赫奕，返照洞玄。\",\"混内晞曂昼夜连。法万象清圆，与物争鲜。湛寂泠源，常在目前。无中潋滟泛精渊。九色黄云笼沆瀣，三峰青霭，远接万川。道备纯精粹，阴氛荡散，万物生全。宝象含灵质，向六尘堆里，捧出个瑞仙。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c190bdbd82cf2a8433','满江红','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"春花秋月，古往今来暂时间。贵贱贤愚，曾经换几番。死生似蚁任循环。咫尺仙乡人不到，出尘径路，如隔万山。省悟归，真正恬然养素，自乐清闲。世事俱无染，一身清净，无为心定观。\",\"耀璧辉金向外看。迸万斛明珠，推出二关。太极宫中，凝结内丹。纯风馥奕透琅玕。万气周流归一体，千真妙用，情无两般。混入中元界，笙鹤缥渺，瑞气盘桓。玉诏符空碧，名充仙举，乘车驾凤鸾。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da1851597a','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"生喜亡悲，虚劳尘世，许年多少倾危。是非成败，荣辱两亏衰。富足于身大患，贫穷又、遣我寒饥。急如个，不来不去，父母未生时。\",\"虚无中有象，神风显焕，物炫星晕。同天澄瑞，同地地幽微。至道包盈法界，归元首、永劫无疑。离生灭，不增不灭，常与太虚齐。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da1e903686','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"盥腹斋心，冲霜冒雪，为求神宝精瑛。凭君一举，眸子见青冥。四朗天无浮翳，冰轮坼、悬象俱明。辰宵馆，玉牌金字，谁是系芳名。\",\"途中人自昧，便心跂径，有误前程。回耹返故久，动复徐生。宝梵仙宫路稳，琼林宴、光散霞轻。神功妙，纯阳炼就，朝现紫微星。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da2c954da6','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"去质贪华，还淳复古，本源浊以徐清。卑谦处厚，能弊不新成。知足无荣无辱，彝然御、非躁非轻。还知否，存亡得失，进退若亏盈。\",\"如斯全可道，心中出屑，眼里抽钉。瞳蒙奕羽，虚谷旷然明。三气氤氲密降，纯阳焕、形释心凝。归来处，风恬浪净，明月照寰瀛。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da4eefb400','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"独立星坛，高穹仰望，志诚修设威仪。名香频爇，谨谨奏青词。唯愿众生慕道，心通解、无执无迷。归真正，玄门隐密，一志永无移。\",\"都缘心返复，狂图捏怪、纽班胡为。色情增长，道德渐亏衰。似恁拖泥带水，全不念、先圣根基。经中道，人能清净，天地悉皆归。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da5e2071bd','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"祖道无修，真常妙用，悟来表里相通。灵光现处，照见杳冥中。晃耀清虚浩大，无遮障、拍塞冲融。希夷甫，从来面目，返本复归空。\",\"真空。明皎洁，唯然旷大，难测难穷。一天吾道，寂湛出凡笼。妙化方生正祖，通元首、返本归宗。超三界，移神脱壳，物外显阳踪。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da60d083e3','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"粮畚担簦，云山水陆，远途不避风霜。为求天宝，与汝诉衷肠。至理先明一性，二仪现、三殿恢彰。玄关外，临空奕奕，杲日布红光。\",\"群灵皆受命，谁能保守，玉府元阳。万神交灿，九气混中央。结就玄珠一颗，辉神彩、光列南昌。寥阳馆，华房出入，齐唱满庭芳。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da6f265a08','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"太极精微，天真郁秀，混元溢目无涯。玄珠升降，辉耀迸光砂。聚入壶中托化，归真正、不染嚣华。回头省，清虚妙体，返照秀灵芽。\",\"汞铅凝渐结，纯阳丹就，永劫无差。一瓢春酝，三尺斩妖邪。信步纵横自在，龟鹤引、深入烟霞。逍遥处，长春洞里，时复玩仙葩。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da77e3292e','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"腊正风交，琼梅初绽，水寒冰洁严凉。雀临高树，鱼入碧潭藏。海净天空浩浩，玄珠降、睒烁凝祥。胎星现，清名播世，道德自昭彰。\",\"群仙皆喜聚，坛前谨谨，祝寿玄阳。金虬浚液，玉兽旋喷香。唯愿遐龄满寿，同天地、永劫延长。升仙处，鸾迎凤引，云步现虚皇。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da82073a2c','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"太古真风，玄元宗祖，阐扬灵宝符文。金章玉篆，词简义光新。演道精宏浩大，通天地、细入微尘。修家国，臣忠子孝，民业自安淳。\",\"门人还知否，穷通妙指，拨物归真。涤除玄览，焕现外其身。昊境云瑛作伴，浮空驾、日月为邻。擎天翼，金门羽化，京廓现阳神。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da9e338467','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"静室颐神，宁心默运，内观一点真阳。洞虚豁朗，焕焕透明堂。照见虚空法界，先天地、独立无双。三光秀，圆明实相，浩劫应时长。\",\"回头谁问己，眼前顼著，不用思量。自然契道，物我两俱忘。唯有清风皓月，通神化、无极之光。升玄路，混元一气，拍塞遍诸方。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8da9e3f42d2','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"海角天涯，寻朋访友，悄无一个知音。斗争人我，财色利名侵。闻说邪淫妄语，拚身命、一向钻寻。真常教，余言直劝，返报怨还深。\",\"休休宁奈也，澄清默运，摄养真心。混元一气，升降定浮沉。迤逦收归宝鼎，纯阳就、点铁成金。通玄妙，群魔消散，方显鬼神钦。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8dab03a3d60','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"万物之中，为人最贵，内存一点精微。至灵至圣，日用有谁知。隐显存亡顺化，方圆应、曲直投机。从伊变，行居坐卧，动止镇相随。\",\"焕然光透体，真人出现，别有威仪。玉簪朱履，环佩锦缨衣。宝盖香车凤辇，云瑛覆、窈窕真妃。仙音奏，金童捧盏，吃得醉如泥。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8dab0735824','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"迅速韶光，俄然过了，算来刚甚劬劳。红颜衰朽，绿鬓渐疏焦。由自争名竞利，全不念、生死难逃。轮回处，似环蝼蚁，来往几千遭。\",\"君还心省悟，明通周象，拨易穷爻。地天通泰，离坎自相交。阴返阳生运化，纯精粹、清气冲霄。胎光现，神风绰约，飞步玩琼瑶。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8dadf723892','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"斗柄回旋，紫微初现，缅平一色淳清。云瑛捣尽，天宝耀华婴。玉桂琼林素羽，升玄御、光璨流精。泠然妙，丹黄金碧，炜烨化微星。\",\"星徽清雅宴，纯风隐韵，忘相忘形。愿吾流开悟，心释浮荣。勿念云山路隔，高著眼、莫昧灵明。如斯理，青童飞去，点破大兰城。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8dae86c9f4c','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"玉屑琳霜，云瑛幽粹，混沌三宝持盈。盈还而已，日月透昆明。□抧圆通昊界，氤氲降、天宝华婴。于中妙，五陵飞雪，人在水晶城。\",\"灵瑶开古道，神风易御，孰保遐龄。念吾流久视，玄毕终诚。密付丹须换质，从斯致、游宴华清。金童诏，玉辰宫里，身属少微星。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c197b5b8dae8821363','满庭芳','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"夜去明来，光阴迅速，四时催促人忙。浮生不久，谁肯细诮详。一向痴心妄想，贪冤业、不觉无常。形归土，三魂七魄，逐景任飞扬。\",\"谁无生与死，一人一个，难躲难藏。忽然省悟，因此做风狂。管甚前街后巷，行歌舞、坦荡宽肠。逍遥处，哩凌啰哩，落魄有何妨。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c19b5019917572b027','浪淘沙令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"访道与参师。设誓投词。慎终如始谨修持。退己进人常忍辱，诸事宽慈。\",\"该破上仙机。守正无疑。持行元始玉清微。慧日当空凭谁照，星眼芝眉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c19c70a6d8e7e1fd83','踏莎行','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"养气全神，至精坚固。琅函浊质奚能悟。心珠跃入太虚中，玉婴神变通玄户。\",\"赶退金乌，趁回玉兔。凝阳点化升仙去。清音香送别瑶台，真人醉卧蓬莱务。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f62e4ff490','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"功名富贵。致使沉沦空手去。改面回头。一失人身永劫休。\",\"归纯返朴。背境涤尘真正觉。一颗圆光。焕焕煌煌入大方。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f65a3627fe','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"淫声美色。底死谩生难弃舍。艳态娇妻。儿女恩情总送伊。\",\"除情去欲。惜气存神如珠玉。一点精金。失了难为复再寻。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6984f6826','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"深根固蒂。天宝流霞光满济。返照连天。冉冉程程玄又玄。\",\"无生无灭。湛湛孤然明皎洁。昕散云寥。点破清虚上九霄。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6a2d2a4a1','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"初心奉道。谨守精严光返照。元始扶真。真物相生恍惚中。\",\"凝空落落。明月清风相倚托。达彼无忧。回首烟霞物外游。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6a6ee2562','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"五行四象。戊己攒盈如何样。黑水红云。白马背牛两下分。\",\"金车宝辇。大道夷然烹玉线。碾路如银。搬载婴儿出世尘。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6a70eed74','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"居家学道。两事不成担阁了。欲要修真。不染浮华半点尘。\",\"虚心实腹。只此登仙真轨则。功满朝元。金阙寥阳化羽仙。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6ab7475da','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"无情苦海。壤壤众生由贪爱。争似余家。寂寞林泉别世华。\",\"颐真养浩。肌肉冰清如玉貌。出入无间。绰约随风飘上天。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6ac932272','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"秋风浩浩，极目霞琳澄瑞绕。隔岸芦花。隐映烟笼三两家。\",\"天真秀郁。谪降寰中光满室。保佑千春。朱顶青松延寿同。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6ecebab88','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"浮生苦恼。尘世忙忙何日了。一旦无常。福致他人业自当。\",\"君还省悟。弃舍冤亲寻出路。万事都休。性命之中子细搜。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a2d534f6f3ed3d87','减字木兰花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"今生不悟。指望来生何处去。六道交加。一步危亡永劫差。\",\"君还识破。尘世般般都打过。云水逍遥。稳泛虚舟物外漂。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a339bbda46542531','武陵春','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一叶梧桐窗外落，金菊出疏篱。策杖昂簪信步移。迷我曲江池。\",\"鹤驾匆匆归旧路，回首赴仙期。云映青童笑问伊。家住五陵溪。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a339bbda5058b28c','武陵春','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"太上混元真一气，莹净若琼琚。清腾白云不卷舒。明见有中无。\",\"冷淡灵光空荡漾，变现总无拘。上下周流出六虚。一颗夜明珠。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0a30cd682b','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"内阐规函致本宗。道相通。寂寥常在混元宫。悟真空。\",\"上下观之无一物，与谁同。逍遥独坐月明中。伴清风。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0a6475ed8f','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"野鹤孤云去复还。欲留难。断肠恩爱两情关。泪阑珊。\",\"秋后凄凉归路远，速跻攀。洛西烟浦柳摽间。望家山。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0a7cff75e0','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"宝性完容上下冲。内推穷。氤氲三色射瑶宫。焰飞空。\",\"洞济玉阳澄宝瑞，自和同。谷神翔翥混元中。玩仙风。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0aa5b1d3a1','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"鹤性佋佋望远攀。过潼关。秋深雨霁乍衣单。水云寒。\",\"争忍昔年名利染，早回还。夕阳村落杏摽间。望家山。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0aab11faa6','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"偃息团圞玩宝窥。目无疵。涤除玄览似婴儿。正容仪。\",\"欲问今宵何处也，赴仙期。满船空载月明归。上瑶池。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0ab2d7d37c','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"孰见西明画锦妆。景非常。烟波柳岸映斜阳。水云乡。\",\"咫尺家山人不到，好风光。步虚升入碧云房。洞天长。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0abae86f81','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"丱岁飘蓬住远山。水云闲。须知千载厌人间。欲回还。\",\"换骨脱胎归旧路，返童颜。步虚升入古仙坛。泛云鸾。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1a40cee0af06d0a66','添声杨柳枝','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"顿识圆明象易冲。妙无穷。祥烟飞入玉清宫。月临空。\",\"物性自然纯古道，与谁同。杨柳枝插宝瓶中。好家风。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b716bf6271','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一天清秀一溪云。风月近为邻。琴剑酒棋书画，随分乐天真。\",\"竹篱映，草堂新，静无尘。朝菌不见，满目琼花，间隔灵椿。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b717570666','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"爱河深处好抽头。名利系人愁。回首去游江上，烟锁一孤舟。\",\"琴一弄，酒三瓯。乐清幽。风邻月伴，醉卧琼林，梦入瀛洲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b72de2d9d3','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"林泉退隐作生涯。寂寞弃繁华。中山翠微仙子，满目泛灵砂。\",\"凝素液，混丹霞。坼瑶葩。云涛烟浪，岛外方壶，那里人家。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7546e843e','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"玉炉金鼎虎龙蟠。红焰满周天。温凉自然水火，昼夜转烹煎。\",\"精郁郁，气绵绵。养胎仙。功成迁转，宝辇香车，捧上瑶天。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7569f4888','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"阴阳返复勿令差。守正莫心邪。常进药炉丹鼎，龙虎动三车。\",\"收神水，炼精华。结灵砂。水晶宫里，玉兔金鸡，吐秀琼花。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b76d1a0fb5','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"逍遥无系乐清闲。信步且随缘。人来问归何处，遥指曲江边。\",\"杨柳岸，半笼烟。乍晴天。潇潇清夜，悄悄无人，月满空船。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b76e2a0d97','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"中朝金国圣皇明。有庆贺清平。天元运周甲子，正是好修行。\",\"归真静，体真清。气神灵。玄门阐化，太上真风，接引群生。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b79bf1b817','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"太虚寥廓杳无涯。元气混中华。回眸斡旋鼎鼐，玉液泛金砂。\",\"辉神彩，迸光霞。晃琼葩。云收波渺，海净天空，广漠之家。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7beb148ee','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"英公清列出尘缘。不系利名牵。逍遥任居云水，知命乐天然。\",\"穷妙理，悟幽玄。谨修仙。功成归去，引汝朝元。稳步金莲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7c1b5d70d','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"神仙妙诀古今传。谁会此幽玄。龟蛇自然呼吸，玄牝气绵绵。\",\"铅中汞，汞中铅。两俱全。抽添加减，进火功夫，炼就胎仙。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7c91a6ce3','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"初开江月透双林。隐映素波深。风恬湛然莹净，清夜好知音。\",\"白醪酒，自然斟。信时吟。无萦无系，豁畅开怀，一任浮沉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7ca4c508e','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"古今多少利名人。弃命斗争功。英豪尽归何处，都总落沉空。\",\"争如我，洞庭中。遣家童。金觥轻泛，醉卧当轩，一枕清风。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7e76daf85','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"众生薄福乐真闻。精进谨持功。修斋坐禅礼念，终久落顽空。\",\"真实相，有无中。好家风。玄元宗祖，太始门开，体照圆通。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b079a9b7f1d78e4d','诉衷情令','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一天秋色翠如描。鸿雁啭声高。山秀水明相照，烟树映溪桥。\",\"倾雪浪，泛云涛。拂轻袍。归来沈醉，直到黄昏，月上松梢。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b88f1c0d071159c5','一剪梅','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"一个尘劳一个忙。一自别离，一得真常。一天精秀一天凉。一点清光。一带凝阳。\",\"一气相交一气张。一结神丹，一命延长。一灵透入一云房。一对金童，一引仙乡。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1b88f1c0dab5bfc38','一剪梅','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"出俗轻枝翠嫩长。不比松筠，独耐风霜。岁寒心以表嘉祥。小蕾初生，压尽群芳。\",\"勿遣娉婷越粉墙。尽任桓伊，品弄何妨。天然姿秀坼□黄。绝艳清奇，一剪梅香。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c716702010db37f9','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"踏破尘劳网，冲天志决贞。手中持剑转环睛。日耀争光，睒烁莹天明。\",\"觉海千寻底，灵波万里倾。群魔消散鬼神惊。点化纯阳，永永住蓬瀛。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c71670201f93f8a3','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"不晓身中宝，宁知物外篇。不通真教乱狂颠。指望蓬莱，仰卧摸青天。\",\"欲识铅中汞，须知汞里铅。两般颠倒自如然。放出灵飞，空外舞胎仙。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c716702027a6909f','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"匹配阴阳理，婴公伴细腰。调和铅汞要终朝。水底金精，移入火中烧。\",\"炼就通神剑，修成智慧刀。降龙伏虎斩邪妖。夜吐光芒，谁敢语声高。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c71670202cdd467c','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"坐看华胥景，神游玩宝方。一双童子抱龙光。焕焕煌煌，撮聚入中央。\",\"产出灵仙子，功成入帝乡。玉霄榜上应科场。夺取仙标，宝洞执圭璋。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c716702050a2f8ac','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"瞬目迷千劫，扬眉隔万山。一真分瑞出双关。混入华元，到此有何难。\",\"隳质飞金翼，凝阳透玉环。惺然洗觉梦魂间。云去无心，物外独清闲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c716702089129e49','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"炼质心猿退，焚真意马疏。一灵圆觉顿真如。洞达渊玄，光鉴照元初。\",\"坎虎形如玉，离龙色似珠。云奔电激贯虚无。霹雳峥嵘，舞弄戏玄珠。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c716702097aaa29b','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"欲问修真理，君还会得么。鲸鹏出海透星罗。夜宿蟾宫，飞上桂枝柯。\",\"吉善灵禽少，凶邪怪鹏多。一群一队谩蹉跎。荆棘为巢，不见凤凰窠。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020a8c074f8','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"厌静曾居市，嫌喧却住山。通真何处不清闲。内外逍遥，不备利名攀。\",\"蜕质空中显，忘形物外观。二神凝素出阳关。鹤性佻佻，一去古长安。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020aabb9265','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"太上玄元祖，生居浩劫先。首初传教五千言。演正无疑，谁晓此幽玄。\",\"尽吃迷心药，谁餐续命丹。邪门曲教自缠绵。死堕阴囚，永劫镇黄泉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020abed5ade','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"决列心清操，真刚志气真。超然不落断常坑。眼界宽舒，焕焕照华清。\",\"锦绣乾坤莹，玲珑世界明。澄澄虚璧顿圆成。蜕壳精暚，一点碧天星。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020d125c71f','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"雨过春将晚，风来又早秋。百年光景转头休。富贵荣华，浑似水中沤。\",\"劝汝修身早，玄门志意搜。勿交尘事系心头。秘息三田，莫遣贼人偷。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020d628279b','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"凤去飞还远，鹤随翼羽皋。石中隐玉蛤珠韬。木长沉丁，草秀紫芝苗。\",\"兽变狮麟象，蛇渝化海蛟。鱼分水谷变鲸鳌。人道相通，云步越丹霄。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020e7868b11','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"大道亲之远，玄机退未疏。太虚凝秀自如如。一派灵源，潋滟贯从初。\",\"北海鸾渝黑，南山凤染朱。翱翔云外出虚无。日月交光，夺取夜明珠。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1c7167020f0183170','南柯子','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"未悟千般有，心开一物无。有无争觉顿清虚。混内倾阳，一点透灵乌。\",\"瑞气盘金鼎，祥烟玩玉壶。玉壶金鼎炼神珠。光灿流霞，冉冉上天吴。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1d7ee034e009fdf76','益寿美金花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"景风初现。宝适祥云乘九变。始信清然。游奕南陵泻素圆。\",\"冥灵才见。又早新春通一线。象外游仙。看尽瑶池五色莲。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1d7ee034e01eb4e69','益寿美金花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"默然内守。混里倾阳无中有。有里何如。独体圆通照海珠。\",\"神清气秀。游奕丹天明如昼。上下无拘。密布灵华满太虚。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1d7ee034e0b0d9cf0','益寿美金花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"玄元微密。寂寂寥寥无缝入。探赜功奇。易象先天合圣机。\",\"混然一气。拍塞冲虚无不济。极目清光。顿觉融风天地凉。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1d7ee034e1b3fe634','益寿美金花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"净无瑕翳。握固存神颐浩气。润谷玄霜。养就神砂满鼎香。\",\"清如赫日。瑞气凝空盈满室。混一先天。易御星仪达妙玄。\"]',NULL,NULL,NULL,NULL),('c16655df366b37c1d7ee034e25e0495c','益寿美金花','侯善渊','c16655df366b37c1d1457b72d1457b72','元',NULL,'[\"适来相