INSERT INTO `shi_wudaishiguo` VALUES ('6ce235a800113168b0ac603659dd3b02','题全义分水岭','伍彬','6ce235a800113168d1457b72d1457b72','五代十国',NULL,'[\"前贤功及物，禹后杳难俦。\",\"不改古今色，平分南北流。\",\"寒冲山影岸，清绕荻花洲。\",\"尽是朝宗去，潺潺早晚休。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515a52df9f980cb90d9d','九华望庐山','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"九江山势尽峥嵘，惟有匡庐最得名。\",\"万叠影遮残雪在，数峰岚带夕阳明。\",\"冷侵醉榻铺秋色，高亚吟龙送水声。\",\"只待丹霄酬志了，白云深处是归程。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515a53d18e4d7b3517b8','早行','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"结束何妨早，将行四顾频。\",\"山前犹见月，陌上未逢人。\",\"远树动宿鸟，危桥怯病身。\",\"渐明恒自慰，应免复迷津。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515a5f0dcf6db4e716e6','赠胥尊师','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"绿发童颜羽服轻，天台王屋几经行。\",\"云程去速因风起，酒债还迟待药成。\",\"房闭十洲烟浪阔，箓开三洞鬼神惊。\",\"他年华表重归日，却恐桑田已变更。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515a67b4ad1d7c9af0f6','游嵩山','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"独背焦桐访洞天，暂攀灵迹弃尘缘。\",\"深逢野草皆疑药，静见樵人恐是仙。\",\"翠木入云空自老，古碑横水莫知年。\",\"可怜幽景堪长往，一任人间岁月迁。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515a93faacb61f26e635','怀三茅道友','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"尘事何年解客嘲，十年容易到三茅。\",\"长思碧洞云窗下，曾借黄庭雪夜抄。\",\"丹桂有心凭至论，五峰无信问深交。\",\"杏坛仙侣应相笑，只为浮名未肯抛。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515ac91e5fb56ded0787','湘江晓望','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"笙歌欢罢散离筵，水色朦胧蘸宿烟。\",\"山响疏钟何处寺，火光收钓下滩船。\",\"微云过岛侵微月，古岸平江浸远天。\",\"归梦已阑风色动，孤帆仍要住无缘。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515aca1acecd4bd8e678','冬日原居酬光上人见访','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"吾道丧已久，吾师何此来。\",\"门无尘事闭，卷有国风开。\",\"野迥霜先白，庭荒叶自堆。\",\"寒暄吟罢后，犹喜话天台。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515ade259154bc3e199f','谪居海上','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"家临泾水隔秦川，来往关河路八千。\",\"堪恨此身何处老，始皇桥畔又经年。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515ae473e8a26b0f4639','早梅','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"江南近腊时，已亚雪中枝。\",\"一夜欲开尽，百花犹未知。\",\"人情皆共惜，天意欲教迟。\",\"莫讶无浓艳，芳筵正好吹。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515ae6cc899b1d7a4862','月中桂','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"断破重轮种者谁，银蟾何事便相随。\",\"莫言望夜无攀处，却是吟人有得时。\",\"孤影不凋清露滴，异香常在好风吹。\",\"几回目断云霄外，未必姮娥惜一枝。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515aeb6a53bf8521fa32','道傍松','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"偃盖当衢莫记年，独含苍翠鹤应怜。\",\"垂阴独向笙歌地，有韵自成风雨天。\",\"尘□路岐分夜月，烧侵根脚起残烟。\",\"论功只合行人赏，销得烦蒸古道边。\"]',NULL,NULL,NULL,NULL),('6ce235a800a3515aedd5a5f063440bf9','祖龙词','熊皦','6ce235a800a3515ad1457b72d1457b72','五代十国',NULL,'[\"平吞六国更何求，童女童男问十洲。\",\"沧海不回应怅望，始知徐福解风流。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd00432d9ec2ade6ae','仙都山留题','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"林下事无非，尘中竟不知。\",\"白云深拥我，青石合眠谁。\",\"山静捣灵药，夜闲论古诗。\",\"此来亲羽客，何日变枯髭。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd197d96162a0bd1c7','送人红花栽','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"世上红蕉异，因移万里根。\",\"艰难离瘴土，潇洒入朱门。\",\"叶战青云韵，花零宿露痕。\",\"长安多未识，谁想动吟魂。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd2219ec0b68c4c741','闻蝉','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"一雨一番晴，山林冷落青。\",\"莫侵残日噪，正在异乡听。\",\"孤馆宿漳浦，扁舟离洞庭。\",\"年年当此际，那免鬓凋零。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd5b1806ea2ff2e490','忆天台山','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"常记游灵境，道人情不低。\",\"岩房容偃息，天路许相携。\",\"霞散曙峰外，虹生凉瀑西。\",\"何当尘役了，重去听猿啼。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd695b8d4e5faab555','伤雨后牡丹','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"废功看不已，醉起又持杯。\",\"数日帘常卷，中宵雨忽来。\",\"凄凉无戏蝶，零落在苍苔。\",\"造化根难问，令人首可回。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd80a4a3ea14e468c9','送僧','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"草履初登南客船，铜瓶犹贮北山泉。\",\"衡阳旧寺秋归去，门锁寒潭几树蝉。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd83ae37af76414cfb','石笋','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"千古海门石，移归吟叟居。\",\"窍腥蛟出后，形瘦浪冲馀。\",\"工语宁无玉，僧知忽有书。\",\"好期仙者叱，变化向庭隅。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd83e0c7db2637ed7e','怀华山隐者','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"先生入太华，杳杳绝良音。\",\"秋梦有时见，孤云无处寻。\",\"神清峰顶立，衣冷瀑边吟。\",\"应笑干名者，六街尘土深。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd8cec9caf505e174c','冬日暮国清寺留题','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"天台山下寺，冬暮景如屏。\",\"树密风长在，年深像有灵。\",\"高钟疑到月，远烧欲连星。\",\"因共真僧话，心中万虑宁。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bd917339fde478edc6','灵溪观','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"鳌海西边地，宵吟景象宽。\",\"云开孤月上，瀑喷一山寒。\",\"人异发常绿，草灵秋不干。\",\"无由此栖息，魂梦在长安。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bda92f8f1df0053d0e','晚霁望岳?','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"湘西斜日边，峭入几寻天。\",\"翠落重城内，屏开万户前。\",\"崖崚危溅瀑，林罅静通仙。\",\"谁肯功成后，相携扫石眠。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bddc0d59deb55a493a','赠惠律大师','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"秋是忆山日，禅窗露洒馀。\",\"几悬华顶梦，应寄沃洲书。\",\"风月资吟笔，杉篁笼静居。\",\"满城谁不重，见著紫衣初。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bdecc77ca4fb1677b2','经费冠卿旧隐','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"节高终不起，死恋九华山。\",\"圣主情何切，孤云性本闲。\",\"名传中国外，坟在乱松间。\",\"依约曾栖处，斜阳鸟自还。\"]',NULL,NULL,NULL,NULL),('6ce235a8017621bdef299735fb881ac3','括苍','刘昭禹','6ce235a8017621bdd1457b72d1457b72','五代十国',NULL,'[\"尽日行方半，诸山直下看。\",\"白云随步起，危径极天盘。\",\"瀑顶桥形小，溪边店影寒。\",\"往来空太息，玄鬓改非难。\"]',NULL,NULL,NULL,NULL),('6ce235a801acceeb7cd23f6f00875255','孤雁','张子明','6ce235a801acceebd1457b72d1457b72','五代十国',NULL,'[\"只影翩翩下碧湘，傍池鸳鹭宿银塘。\",\"虽逢夜雨迷深浦，终向晴天著旧行。\",\"忆伴几回思片月，蜕翎多为系繁霜。\",\"江南塞北俱关念，两地飞归是故乡。\"]',NULL,NULL,NULL,NULL),('6ce235a80209bcaa5a7fe1f8ff79a466','随驾游青城','李舜弦','6ce235a80209bcaad1457b72d1457b72','五代十国',NULL,'[\"因随八马上仙山，顿隔尘埃物象闲。\",\"只恐西追王母宴，却忧难得到人间。\"]',NULL,NULL,NULL,NULL),('6ce235a80209bcaa9b8012858847a943','蜀宫应制','李舜弦','6ce235a80209bcaad1457b72d1457b72','五代十国',NULL,'[\"浓树禁花开后庭，饮筵中散酒微醒。\",\"蒙蒙雨草瑶阶湿，钟晓愁吟独倚屏。\"]',NULL,NULL,NULL,NULL),('6ce235a80209bcaac4b790dcd6a82e5a','钓鱼不得','李舜弦','6ce235a80209bcaad1457b72d1457b72','五代十国',NULL,'[\"尽日池边钓锦鳞，芰荷香里暗消魂。\",\"依稀纵有寻香饵，知是金钩不肯吞。\"]',NULL,NULL,NULL,NULL),('6ce235a802d9ded19ce449ed83213cea','建州节帅更代筵上献诗','王感化','6ce235a802d9ded1d1457b72d1457b72','五代十国',NULL,'[\"旌旗赴天台，溪山晓色开。\",\"万家悲更喜，迎佛送如来。\"]',NULL,NULL,NULL,NULL),('6ce235a802d9ded1a0fe53395475c45a','奉元宗命咏苑中白野鹊','王感化','6ce235a802d9ded1d1457b72d1457b72','五代十国',NULL,'[\"碧岩深洞恣游遨，天与芦花作羽毛。\",\"要识此来栖宿处，上林琼树一枝高。\"]',NULL,NULL,NULL,NULL),('6ce235a805bf81b7b46462d19d237377','题广爱寺楞伽山','李琪','6ce235a805bf81b7d1457b72d1457b72','五代十国',NULL,'[\"善高天外远，方丈海中遥。\",\"自有山神护，应无劫火烧。\",\"坏文侵古壁，飞剑出寒霄。\",\"何似苍苍色，严妆十七朝。\"]',NULL,NULL,NULL,NULL),('6ce235a805bf81b7d4118116a37e0231','奉试诏用拓拔思恭为京北收复都统','李琪','6ce235a805bf81b7d1457b72d1457b72','五代十国',NULL,'[\"飞骑经巴栈，鸿恩及夏台。\",\"将从天上去，人自日边来。\",\"此处金门远，何时玉辇回。\",\"早平关右贼，莫待诏书催。\"]',NULL,NULL,NULL,NULL),('6ce235a809fc39735b1d815c80e7a795','立木海上刻诗','李赞华','6ce235a809fc3973d1457b72d1457b72','五代十国',NULL,'[\"小山压大山，大山全无力。\",\"羞见故乡人，从此投外国。\"]',NULL,NULL,NULL,NULL),('6ce235a80dc2c716079427bedee6b4c6','追和秦隐君辞荐之韵上陈侯乞归凤山','詹琲','6ce235a80dc2c716d1457b72d1457b72','五代十国',NULL,'[\"谁言悦口是甘肥，独酌鹅儿啖翠微。\",\"蝇利薄于青纸扇，羊裘暖甚紫罗衣。\",\"心随倦鸟甘栖宿，目送征鸿远奋飞。\",\"击壤太平朝野客，凤山深处□生辉。\"]',NULL,NULL,NULL,NULL),('6ce235a80dc2c716545775cd8335199f','永嘉乱衣冠南度流落南泉作忆昔吟','詹琲','6ce235a80dc2c716d1457b72d1457b72','五代十国',NULL,'[\"忆昔永嘉际，中原板荡年。\",\"衣冠坠涂炭，舆辂染腥膻。\",\"国势多危厄，宗人苦播迁。\",\"南来频洒泪，渴骥每思泉。\"]',NULL,NULL,NULL,NULL),('6ce235a80dc2c716b3f73085a6727956','癸卯闽乱从弟监察御史敬凝迎仕别作','詹琲','6ce235a80dc2c716d1457b72d1457b72','五代十国',NULL,'[\"一别几经春，栖迟晋水滨。\",\"鹡鸰长在念，鸿雁忽来宾。\",\"五斗嫌腰折，朋山刺眼新。\",\"善辞如复我，四海五湖身。\"]',NULL,NULL,NULL,NULL),('6ce235a80de7fb3edce940c0f0fb555d','寄麻姑仙坛道士','查文徽','6ce235a80de7fb3e8a3016d7dcc8d586','五代十国',NULL,'[\"别后相思鹤信稀，郡楼南望远峰迷。\",\"人归仙洞云连地，花落春林水满溪。\",\"白发只应悲镜镊，丹砂犹待寄刀圭。\",\"方平车驾今何在，常苦尘中日易西。\"]',NULL,NULL,NULL,NULL),('6ce235a80faee635c191ef65cdef4f77','辞郡守李公恩命','恒超','6ce235a80faee635352407223cef96dc','五代十国',NULL,'[\"虚著褐衣老，浮杯道不成。\",\"誓传经论死，不染利名生。\",\"厌树遮山色，怜窗向月明。\",\"他时随范蠡，一棹五湖清。\"]',NULL,NULL,NULL,NULL),('6ce235a81215d6bebe17a21a7b1b9276','题蜀宫壁','黄万祜','6ce235a81215d6bed1457b72d1457b72','五代十国',NULL,'[\"莫交牵动青猪足，动即炎炎不可扑。\",\"鸷兽不欲两头黄，黄即其年天下哭。\"]',NULL,NULL,NULL,NULL),('6ce235a812f7c7b6073bbd08f4bae02a','碾驴鞍判','罗绍威','6ce235a812f7c7b6d1457b72d1457b72','五代十国',NULL,'[\"邺城大道甚宽，何故驾车碾鞍。领鞴驴汉子科决，待驾车汉子喜欢。\"]',NULL,NULL,NULL,NULL),('6ce235a812f7c7b6224a09b83a233a7b','柳','罗绍威','6ce235a812f7c7b6d1457b72d1457b72','五代十国',NULL,'[\"妆点青春更有谁，青春常许占先知。\",\"亚夫营畔风轻处，元亮门前日暖时。\",\"花密宛如飘六出，叶繁何惜借双眉。\",\"交情别绪论多少，好向仁人赠一枝。\"]',NULL,NULL,NULL,NULL),('6ce235a812f7c7b69c061546b4e47eee','咏白菊','罗绍威','6ce235a812f7c7b6d1457b72d1457b72','五代十国',NULL,'[\"虽被风霜竞欲催，皎然颜色不低摧。\",\"已疑素手能妆出，又似金钱未染来。\",\"香散自宜飘渌酒，叶交仍得荫香苔。\",\"寻思闭户中宵见，应认寒窗雪一堆。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce00c5f8ddeda74009','文明殿受册封闽王','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"龙墀班听漏声长，竹帛昭勋扑御香。\",\"鸣佩洞庭辞帝主，登车故里册闽王。\",\"一千年改江山瑞，十万军蒙雨露光。\",\"吟寄短篇追往事，留文功业不寻常。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce0e53328e954e2c4f','御命归乡蒙赐锦衣','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"九重宣旨下丹墀，面对天颜赐锦衣。\",\"中使擎来三殿晓，宝箱开处五云飞。\",\"德音耳聆君恩重，金印腰悬已力微。\",\"更待临轩陈鼓吹，星轺便指故乡归。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce161e45f003a94175','松','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"倚涧临溪自屈蟠，雪花销尽藓花乾。\",\"幽枝好折为谈柄，入手方知有岁寒。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce2178b843640d4c63','甲子岁衔命到家至榕城册封次日闽王降旌旗于新丰市堤饯别','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"登庸楼上方停乐，新市堤边又举杯。\",\"正是离情伤远别，忽闻台旨许重来。\",\"此时暂与交亲好，今日还将简册回。\",\"争得长房犹在世，缩教地近钓鱼台。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce22e9a58f6ed95154','奉使封王次宜春驿','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"微宦淹留鬓已斑，此心长忆旧林泉。\",\"不因列土封千乘，争得衔恩拜二天。\",\"云断自宜乡树出，月高犹伴客心悬。\",\"夜来梦到南台上，遍看江山胜往年。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce2fe04e9f617adeef','蒙闽王改赐乡里','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"乡名文秀里光贤，别向钧台造化权。\",\"阀阅便因今日贵，德音兼与后人传。\",\"自从受赐身无力，向未酬恩骨肯镌。\",\"归阙路遥心更切，不嫌扶病倚旌旃。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce343141f685bab2fd','天祐元年以右拾遗使册闽王而作','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"蓬莱宫阙晓光匀，红案舁麻降紫宸。\",\"鸾奏八音谐律吕，凤衔五色显丝纶。\",\"萧何相印钧衡重，韩信斋坛雨露新。\",\"得侍丹墀官异宠，此身何幸沐恩频。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce3c0d883f4a420a20','书斋谩兴二首','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"官事归来衣雪埋，儿童灯火小茅斋。\",\"人家不必论贫富，惟有读书声最佳。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce3c0d883f8f818bfe','书斋谩兴二首','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"池塘四五尺深水，篱落两三般样花。\",\"过客不须频问姓，读书声里是吾家。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce418d13e1c95fa6ab','擢探花使三首','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"探花时节日偏长，恬淡春风称意忙。\",\"每到黄昏醉归去，纻衣惹得牡丹香。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce418d13e1e9e63d53','擢探花使三首','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"九重烟暖折槐芽，自是升平好物华。\",\"今日始知春气味，长安虚过四年花。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce418d13e1ea53d211','擢探花使三首','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"洪崖差遣探花来，检点芳丛饮数杯。\",\"深紫浓香三百朵，明朝为我一时开。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce48ee0028e966d1f5','擢进士','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"霓旌引上大罗天，别领新衔意自怜。\",\"蝴蝶流莺莫先去，满城春色属群仙。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce5b746443f27ee1f4','华下霁后晓眺','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"结茅幽寂近禅林，霁景烟光著柳阴。\",\"千嶂华山云外秀，万重乡思望中深。\",\"老嫌白发还偷镊，贫对春风亦强吟。\",\"花畔水边人不会，腾腾闲步一披襟。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce5be653d690e8727d','万寿寺牡丹','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"烂熳香风引贵游，高僧移步亦迟留。\",\"可怜殿角长松色，不得王孙一举头。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce5fd901fd37a7152e','题故居','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"一为鹅子二连花，三望青湖四石斜。\",\"惟有岭湖居第五，山前却是宰臣家。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce63a9f47524f979a4','寄舍弟承裕员外','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"江花岸草晚萋萋，公子王孙思合迷。\",\"无主园林饶采伐，忘情鸥鸟恣高低。\",\"长江月上鱼翻鬣，荒圃人稀獭印蹄。\",\"何事斜阳再回首，休愁离别岘山西。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce8006c859685dd517','隋堤柳','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"春半烟深汴水东，黄金丝软不胜风。\",\"轻笼行殿迷天子，抛掷长安似梦中。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce867bd83172d41ab0','寄示儿孙','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"力学烧丹二十年，辛勤方得遇真仙。\",\"便随羽客归三岛，旋听霓裳适九天。\",\"得路自能酬造化，立身何必恋林泉。\",\"予家药鼎分明在，好把仙方次第传。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfce868f0e523ee51aa3','题景祥院','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"一溪拖碧绕崔嵬，瓶钵偏宜向此隈。\",\"农罢树阴黄犊卧，斋时山下白衣来。\",\"松多往日门人种，路是前朝释子开。\",\"三卷贝多金粟语，可能心炼得成灰。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcea4cf84014d1961a2','辞闽王归朝寄倪先辈','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"时人莫讶再还乡，简册分明剑佩光。\",\"驷马高车太常乐，登庸门下忆贤良。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcea53539977b71abd2','访建阳马驿僧亚齐','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"萧萧风雨建阳溪，溪畔维舟访亚齐。\",\"一轴新诗剑潭北，十年旧识华山西。\",\"吟魂惜向江村老，空性元知世路迷。\",\"应笑乘轺青琐客，此时无暇听猿啼。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcea71cce6ad81666c4','题槐','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"雨中妆点望中黄，句引蝉声送夕阳。\",\"忆昔当年随计吏，马蹄终日为君忙。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcea74c7f4e528b65c0','题壶山','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"井邑斜连北，蓬瀛直倚东。\",\"秋高岩溜白，日上海波红。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfceab372b71384f63ff','汉上登舟忆闽','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"汉皋亭畔起西风，半挂征帆立向东。\",\"久客自怜归路近，算程不怕酒觞空。\",\"参差雁阵天初碧，零落渔家蓼欲红。\",\"一片归心随去棹，愿言指日拜文翁。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfceb4a5b3aa455ad817','奉使封闽王归京洛','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"泥缄紫诰御恩光，信马嘶风出洛阳。\",\"此去愿言归梓里，预凭魂梦展维桑。\",\"客程回首瞻文陛，驿路乘轺忆故乡。\",\"指日还家堪自重，恩荣昼锦贺封王。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfceb84d2eba113063cf','题莒潭安闽院','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"祧宗营祀舍，幽异胜珠林。\",\"名士穿云访，飞禽傍竹吟。\",\"窗含孤岫影，牧卧断霞阴。\",\"景福滋闽壤，芳名亘古今。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcec8a5d27c44577869','对雨述怀示弟承检','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"淋淋霎霎结秋霖，欲使秦城叹陆沈。\",\"晓势遮回朝客马，夜声滴破旅人心。\",\"青苔重叠封颜巷，白发萧疏引越吟。\",\"不有惠连同此景，江南归思几般深。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcedc6608eb81a3a169','喜弟承检登科','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"两篇佳句敌琼瑰，怜我三清道路开。\",\"荆璞献多还得售，桂堂恩在敢轻回。\",\"花繁不怕寻香客，榜到应倾贺喜杯。\",\"知尔苦心功业就，早携长策出山来。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcee924f429a7ec01c0','晨兴','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"晨起竹轩外，逍遥清兴多。\",\"早凉生户牖，孤月照关河。\",\"旅食甘藜藿，归心忆薜萝。\",\"一尊如有地，放意且狂歌。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcee924f429f15618c4','晨兴','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"鼓绝天街冷雾收，晓来风景已堪愁。\",\"槐无颜色因经雨，菊有精神为傍秋。\",\"自爱鲜飙生户外，不教闲事住心头。\",\"披襟徐步一萧洒，吟绕盆池想狎鸥。\"]',NULL,NULL,NULL,NULL),('6ce235a81488cfcef61731e30f24cf03','晓望','翁承赞','6ce235a81488cfced1457b72d1457b72','五代十国',NULL,'[\"清霜散漫似轻岚，玉阙参差万象涵。\",\"独上秦台最高处，旧山依约在东南。\"]',NULL,NULL,NULL,NULL),('6ce235a8175ce1dc2219ec0b99044ad8','闻蝉','廖凝','6ce235a8175ce1dcd1457b72d1457b72','五代十国',NULL,'[\"一声初应侯，万木已西风。\",\"便感异乡客，先于离塞鸿。\",\"日斜金谷静，雨过石城空。\",\"此处不堪听，萧条千古同。\"]',NULL,NULL,NULL,NULL),('6ce235a8175ce1dcf8d745afb3cb8919','彭泽解印','廖凝','6ce235a8175ce1dcd1457b72d1457b72','五代十国',NULL,'[\"五斗徒劳谩折腰，三年两鬓为谁焦。\",\"今朝官满重归去，还挈来时旧酒瓢。\"]',NULL,NULL,NULL,NULL),('6ce235a817fea07dad64cfa51a48a2dc','同徐学士咏笋','刘羲叟','6ce235a817fea07dd1457b72d1457b72','五代十国',NULL,'[\"徐徐出土非人种，枝叶难投日月壶。\",\"为是因缘生此地，从他长养譬如无。\"]',NULL,NULL,NULL,NULL),('6ce235a81844a4f916bf851148f4f0f6','柳堤诗','詹敦仁','6ce235a81844a4f9d1457b72d1457b72','五代十国',NULL,'[\"种稻三十顷，种柳百馀株。\",\"稻可供饘粥，柳可爨庖厨。\",\"息耒柳阴下，读书稻田隅。\",\"以乐尧舜道，同是耕莘夫。\"]',NULL,NULL,NULL,NULL),('6ce235a81844a4f9724d50176383890e','复留侯从效问南汉刘岩改名龑字音义','詹敦仁','6ce235a81844a4f9d1457b72d1457b72','五代十国',NULL,'[\"伏羲初画卦，苍氏乃制字。点画有偏旁，阴阳贵协比。\",\"古者不嫌名，周公始称讳。始讳犹未酷，后习转多忌。\",\"或援他代易，或变文回避。滥觞久滋蔓，伤心日益炽。\",\"孙休命子名，吴国尊王意。????僻，{壴臣}温??异。\",\"梁复踵已非，时亦迹旧事。?杰自其一，蜀闯是其二。\",\"鄙哉化䁈名，陋矣?䵎义。大唐有天下，武后拥神器。\",\"私制迄无取，古音实相类。???囝〇，??{厓厂=}?地。\",\"?国及照?，作史难详备。唐祚值倾危，刘龑怀僭伪。\",\"吁嗟毒蛟辈，睥睨飞龙位。龑岩虽同音，形体殊乖致。\",\"废学愧未弘，来问辱不弃。奇字难雄博，摛文伏韩智。\",\"因诵鄙所闻，敢布诸下吏。\"]',NULL,NULL,NULL,NULL),('6ce235a81844a4f9b19be981ac5576fa','遣子访刘乙','詹敦仁','6ce235a81844a4f9d1457b72d1457b72','五代十国',NULL,'[\"扫石耕山旧子真，布衣草履自随身。\",\"石崖壁立题诗处，知是当年凤阁人。\"]',NULL,NULL,NULL,NULL),('6ce235a81844a4f9bb53809d8c872b6b','劝王氏入贡宠予以官作辞命篇','詹敦仁','6ce235a81844a4f9d1457b72d1457b72','五代十国',NULL,'[\"争霸图王事总非，中原失统可伤悲。\",\"从来宾主如邮传，胜负干戈似局棋。\",\"周粟纵荣宁忍食，葛庐频顾谩劳思。\",\"江山有待早归去，好向鹪林择一枝。\"]',NULL,NULL,NULL,NULL),('6ce235a81844a4f9c2dc76f1ab31fc16','留侯受南唐节度使知郡事辟予为属以诗谢之','詹敦仁','6ce235a81844a4f9d1457b72d1457b72','五代十国',NULL,'[\"晋江江畔趁春风，耕破云山几万重。\",\"两足一犁无外事，使君何啻五侯封。\"]',NULL,NULL,NULL,NULL),('6ce235a81844a4f9ca115e698fb7e083','余迁泉山城留侯招游郡圃作此','詹敦仁','6ce235a81844a4f9d1457b72d1457b72','五代十国',NULL,'[\"当年巧匠制茅亭，台馆翚飞匝郡城。\",\"万灶貔貅戈甲散，千家罗绮管弦鸣。\",\"柳腰舞罢香风度，花脸妆匀酒晕生。\",\"试问亭前花与柳，几番衰谢几番荣。\"]',NULL,NULL,NULL,NULL),('6ce235a818f575017fabae9bbf2b8c6d','奉诏赋蜀主降唐','牛希济','6ce235a818f5750143feaeeed1457b72','五代十国',NULL,'[\"满城文武欲朝天，不觉邻师犯塞烟。\",\"唐主再悬新日月，蜀王难保旧山川。\",\"非干将相扶持拙，自是君臣数尽年。\",\"古往今来亦如此，几曾欢笑几潸然。\"]',NULL,NULL,NULL,NULL),('6ce235a81a4ffe2726c2256406640abb','失题','潘佑','6ce235a81a4ffe27b7892fb3c22abfa3','五代十国',NULL,'[\"谁家旧宅春无主，深院帘垂杏花雨。\",\"香飞绿琐人未归，巢燕承尘默无语。\"]',NULL,NULL,NULL,NULL),('6ce235a81a4ffe272b234c4978c9f224','七岁吟','潘佑','6ce235a81a4ffe27b7892fb3c22abfa3','五代十国',NULL,'[\"朝游沧海东，暮归何太速。只因骑折白龙腰，谪向人间三十六。\"]',NULL,NULL,NULL,NULL),('6ce235a81a4ffe277fbd61b4da8de223','送人往宣城','潘佑','6ce235a81a4ffe27b7892fb3c22abfa3','五代十国',NULL,'[\"江畔送行人，千山生暮氛。\",\"谢安团扇上，为画敬亭云。\"]',NULL,NULL,NULL,NULL),('6ce235a81a4ffe27f13130e388497b78','送许处士坚往茅山','潘佑','6ce235a81a4ffe27b7892fb3c22abfa3','五代十国',NULL,'[\"天坛云似雪，玉洞水如琴。\",\"白云与流水，千载清人心。\",\"君携布囊去，路长风满林。\",\"一入华阳洞，千秋那可寻。\"]',NULL,NULL,NULL,NULL),('6ce235a81c41d2bde96e39b8fbcf18e6','歌头·赏芳春','李存勖','6ce235a81c41d2bd0efe3284cbb6a3b8','五代十国',NULL,'[\"赏芳春，暖风飘箔。莺啼绿树，轻烟笼晚阁。杏桃红，开繁萼。灵和殿，禁柳千行斜，金丝络。夏云多，奇峰如削，纨扇动微凉，轻绡薄。梅雨霁，火云烁。临水槛，永日逃繁暑，泛觥酌。\",\"露华浓，冷高梧。雕万叶，一霎晚风。蝉声新雨歇，惜惜此光阴，如流水，东篱菊残时，叹萧索。繁阴积，岁时暮。景难留，不觉朱颜失却。好容光，且且须呼宾友。西园长宵，宴云谣。歌皓齿，且行乐。\"]',NULL,NULL,NULL,NULL),('6ce235a81cc46de7aef1b16cd2a10e8c','赠乔尊师','张鸿','6ce235a81cc46de7d1457b72d1457b72','五代十国',NULL,'[\"长忌时人识，有家云涧深。\",\"性惟耽嗜酒，贫不破除琴。\",\"静鼓三通齿，频汤一味参。\",\"知师最知我，相引坐柽阴。\"]',NULL,NULL,NULL,NULL),('6ce235a8210f6e4cd1a2f9d9e825b12f','清风楼上','师鼐','6ce235a8210f6e4cd1457b72d1457b72','五代十国',NULL,'[\"清风楼上赵官斋，此日平生眼豁开。\",\"放心普通年事远，不从葱林带将来。\"]',NULL,NULL,NULL,NULL),('6ce235a822ea6219166bebb4a5aecef7','奉和圣制元日大雪登楼','张义方','6ce235a822ea6219d1457b72d1457b72','五代十国',NULL,'[\"恰当岁日纷纷落，天宝瑶花助物华。\",\"自古最先标瑞牒，有谁轻拟比杨花。\",\"密飘粉署光同冷，静压青松势欲斜。\",\"岂但小臣添兴咏，狂歌醉舞一家家。\"]',NULL,NULL,NULL,NULL),('6ce235a8245c6f3aaa561fd46ad0daa6','咏蒲鞋','刘章','6ce235a8245c6f3ad1457b72d1457b72','五代十国',NULL,'[\"吴江浪浸白蒲春，越女初挑一样新。\",\"才自绣窗离玉指，便随罗袜上香尘。\",\"石榴裙下从容久，玳瑁筵前整顿频。\",\"今日高楼鸳瓦上，不知抛掷是何人。\"]',NULL,NULL,NULL,NULL),('6ce235a8256542c701c067ded74088c5','秋残','翁宏','6ce235a8256542c7d1457b72d1457b72','五代十国',NULL,'[\"又是秋残也，无聊意若何。\",\"客程江外远，归思夜深多。\",\"岘首飞黄叶，湘湄走白波。\",\"仍闻汉都护，今岁合休戈。\"]',NULL,NULL,NULL,NULL),('6ce235a8256542c7b015fa13b063504a','宫词','翁宏','6ce235a8256542c7d1457b72d1457b72','五代十国','诗写女子春末怀人。首句点题，写来不拘一格。一句中，「又」字开头，「也」字结尾，连用一个副词和一个语气词，这在诗中是不多见的。然而作者用得很自然，使起句突兀，加强了语气，强化了诗中女主人公的哀怨之情，并有笼盖全篇的作用，算得上写法的出新。「又」字还与下面的「经年」相应，暗示这女子与情人离别，正是去年此时，故对物候变化特别敏感。第二句「如何出翠帏」，「如何」，有不堪的意思。联系第一句看，这位女子正是在去年此时此地，经受着别离的苦痛。时隔一年，记忆犹新，而且，现在还是在这一时间和这一地点，她怎敢再身临其境，重新经受这样的苦痛呢！所以说不敢出翠帏。再联系下联看，不敢出来实际上还是出来了，人在极端苦闷的时候，往往就是处在这样的自我矛盾中。这又活画出了这位女子梦魂牵惹、如痴似醉的神态，从而烘托出她的思念之情是如何的镂心刻骨。\n以下几联均写其院中所见所感。主要是说她如何触景伤怀，忧思难解，但反复抒写，意多重复，用语平常。惟独第二联两句，融情入景，写得工丽自然，不失为精彩之笔。\n「落花人独立，微雨燕双飞」。既是春残，自然落花无数，而无数落花又很容易引起人们韶华易逝、青春难再之感。现在，这位女子，正当芳龄，却独立庭院，青春在消逝，欢娱难为继，她的命运和这春残的落花，不是一模一样吗！作者将落花与思妇互相映衬，倍觉凄然。暮春天气，微雨蒙蒙，给人的感觉本是抑郁沉闷的，何况是心事重重、愁思郁积的女子呢！偏偏在这时，一双不知趣的燕子，在细雨中穿去穿来，显出很自得的样子，这就使她更加难堪了。燕子无知，尚能比翼双飞；人属多情，只能黯然独立，此情此景，怎堪忍受！诗人以燕双飞反衬人独立，把女子的内心愁苦之情推到了顶点。花、雨、人、燕，本是纯粹的「景语」，作者通过映衬、反衬，融情入景，把它们连缀成一幅和谐统一的艺术画面，从而烘托出诗中女子忧思难解的内心世界，使「景语」完全变成了「情语」。这两句写得细腻深刻而委婉含蓄，对偶工丽而无雕琢之嫌，堪称佳句。\n北宋词人晏几道名篇《临江仙》中，创造性地借用了翁宏这两句诗，他写道：「梦後楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。记得小蘋初见，两重心字罗衣。琵琶絃上说相思。当时明月在，曾照彩雲归。」这两句恰恰是词中的精华所在，成了谭献誉为「千古不能有二」的「名句」。','[\"又是春残也，如何出翠帷。\",\"落花人独立，微雨燕双飞。\",\"寓目魂将断，经年梦亦非。\",\"那堪向秋夕，萧飒暮蟾辉。\"]','[\"北宋·王舉《雅言系述》：翁宏字大举，桂岭人。隐居韵贤间，不仕能诗。《宫词》云：「又是春残也，如何出翠帷\\u2026\\u2026」\",\"明·胡应麟《诗薮》：（翁宏）《宫词》「落花人独立，微雨燕双飞」最佳。\"]','又到了春天快要结束之时，这让人怎么走出翠绿的帷帐？\n人独自站在落花面前，小雨中燕子成双飞去。\n观看此景魂魄像要失去，经过很多年梦境也不一样了。\n怎么能够忍受如此愁苦的夜晚，蝉在凄凉萧索的夕阳下映出光辉。','《宫词》是唐末五代诗人翁宏创作的五言律诗。该诗写女子春末怀人，反复抒写其触景伤怀，忧思难解之情。诗中颔联“落花人独立，微雨燕双飞”两句，融情入景，写得工丽自然，成为千古名句。','[\"宫词：一作「春残」。\",\"翠帏（wéi）：绿色的帷帐。帷，四周相围而无顶的篷帐。独立：独自一人站立。\",\"微雨：小雨。\",\"寓目：观看，过目。语出《左传·僖公二十八年》：「请与君之士戏，君凭轼而观之，得臣与寓目焉。」\",\"断：断绝。\",\"经年：经过一年或若干年。\",\"梦亦非：梦境也不同了。\",\"那堪向：怎能忍受。那，通「哪」；向，语气助词。\",\"秋夕：一作「愁夕」。\",\"萧飒（sà）：萧条冷落、萧索。\",\"暮蟾：一作「暮蝉」。\"]'),('6ce235a8256542c7cca133febe8aea01','送廖融处士南游','翁宏','6ce235a8256542c7d1457b72d1457b72','五代十国',NULL,'[\"病卧瘴云间，莓苔渍竹关。\",\"孤吟牛渚月，老忆洞庭山。\",\"壮志潜消尽，淳风竟未还。\",\"今朝忽相遇，执手一开颜。\"]',NULL,NULL,NULL,NULL),('6ce235a82620d6c823c3941a8c9a5877','题僧院','李涛（信臣）','6ce235a82620d6c8a64c94bad707329b','五代十国',NULL,'[\"走却坐禅客，移将不动尊。\",\"世间颠倒事，八万四千门。\"]',NULL,NULL,NULL,NULL),('6ce235a82620d6c82fa6e49943e79c59','春社从李昉乞酒','李涛（信臣）','6ce235a82620d6c8a64c94bad707329b','五代十国',NULL,'[\"社公今日没心情，为乞治聋酒一瓶。\",\"恼乱玉堂将欲遍，依稀巡到第三厅。\"]',NULL,NULL,NULL,NULL),('6ce235a82620d6c8504c7e66d65cddd1','题处士林亭','李涛（信臣）','6ce235a82620d6c8a64c94bad707329b','五代十国',NULL,'[\"帝里高人宅，苍苔绕径深。\",\"卷帘山入户，摘果鸟移林。\",\"石沼养龟水，月台留客琴。\",\"生涯一樽酒，名利不关心。\"]',NULL,NULL,NULL,NULL),('6ce235a82620d6c86508a4cb38c9e6a3','赠山翁','李涛（信臣）','6ce235a82620d6c8a64c94bad707329b','五代十国',NULL,'[\"松萝深处住，闲野不生愁。\",\"鸟语烟岚静，水声门户秋。\",\"花间归洞路，山下钓鱼舟。\",\"沐浴圣王化，自怜丝满头。\"]',NULL,NULL,NULL,NULL),('6ce235a82697a9523621c834ac545967','棋','高辇','6ce235a82697a952d1457b72043c3d7e','五代十国',NULL,'[\"野客围棋坐，支颐向暮秋。\",\"不言如守默，设计似平雠。\",\"决胜虽关勇，防危亦合忧。\",\"看他终一局，白却少年头。\"]',NULL,NULL,NULL,NULL),('6ce235a82c3ca0987436323a8afe7a14','下狱贡诗','黄崇嘏','6ce235a82c3ca098d1457b72d1457b72','五代十国',NULL,'[\"偶辞幽隐在临邛，行止坚贞比涧松。\",\"何事政清如水镜，绊他野鹤在深笼。\"]',NULL,NULL,NULL,NULL),('6ce235a82c3ca098eb5eb4fcad207775','辞蜀相妻女诗','黄崇嘏','6ce235a82c3ca098d1457b72d1457b72','五代十国',NULL,'[\"一辞拾翠碧江湄，贫守蓬茅但赋诗。\",\"自服蓝衫居郡掾，永抛鸾镜画蛾眉。\",\"立身卓尔青松操，挺志铿然白璧姿。\",\"幕府若容为坦腹，愿天速变作男儿。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a75722605a9b96270ba8','寄韩侍郎','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"谋身谋隐两无成，拙计深惭负耦耕。\",\"渐老可堪怀故国，多愁翻觉厌浮生。\",\"言诗幸遇明公许，守朴甘遭俗者轻。\",\"今日况闻搜草泽，独悲憔悴卧升平。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a7572d22bdcb48fb21a4','江上晚泊','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"寒云淡淡天无际，片帆落处沙鸥起。\",\"水阔风高日复斜，扁舟独宿芦花里。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a7573acea021d2899842','寄庐山白上人','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"潦倒门前客，闲眠岁又残。\",\"连天数峰雪，终日与谁看。\",\"万丈高松古，千寻落水寒。\",\"仍闻有新作，懒寄入长安。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a7577166329427a913a8','汉宫词','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"寒烛照清夜，笙歌隔藓墙。\",\"一从飞燕入，便不见君王。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a75776a3a2e2c2201696','言怀别同志','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"惭老将谁托，劳生每自惭。\",\"何当重携手，风雨满江南。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a7577842d4e7b60a04fc','送君去','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"关河月未晓，行子心已急。\",\"佳人无一言，独背残灯泣。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a757a42f0c4dbec90e4e','郊原晚望怀李秘书','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"归鸟入平野，寒云在远村。\",\"徒令睇望久，不复见王孙。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a757adbd7d388bb8266a','寄鉴上人','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"一从携手阻戈鋋，屈指如今已十年。\",\"长记二林同宿夜，竹斋听雨共忘眠。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a757e02d3eb6160576e3','秋晚野望','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"倚筇聊一望，何处是秦川？\",\"草色初晴路，鸿声欲暮天。\"]',NULL,NULL,NULL,NULL),('6ce235a82db9a757e076dadf0992ebeb','送人','左偃','6ce235a82db9a757d1457b72d1457b72','五代十国',NULL,'[\"一茎二茎华发生，千枝万枝梨花白。\",\"春色江南独未归，今朝又送还乡客。\"]',NULL,NULL,NULL,NULL),('6ce235a82e90a509603d1456b4042de1','哭徐夤','王延彬','6ce235a82e90a509d1457b72d1457b72','五代十国',NULL,'[\"延寿溪头叹逝波，古今人事半销磨。\",\"昔除正字今何在，所谓人生能几何。\"]',NULL,NULL,NULL,NULL),('6ce235a82e90a509680b6df4538281cf','春日寓感','王延彬','6ce235a82e90a509d1457b72d1457b72','五代十国',NULL,'[\"两衙前后讼堂清，软锦披袍拥鼻行。\",\"雨后绿苔侵履迹，春深红杏锁莺声。\",\"因携久酝松醪酒，自煮新抽竹笋羹。\",\"也解为诗也为政，侬家何似谢宣城。\"]',NULL,NULL,NULL,NULL),('6ce235a83249df613ad12a1e8a1dfaae','郊庙歌辞汉宗庙乐舞辞观德舞','张昭','6ce235a83249df61d1457b72d1457b72','五代十国',NULL,'[\"高庙明灵再启图，金根玉辂幸神都。\",\"巢阿丹凰衔书命，入昴飞星献宝符。\",\"正换熏弦娱赤子，忽登仙驾泣苍梧。\",\"荐樱鹤馆笳箫咽，酌鬯金楹剑佩趋。\",\"星俎云罍兼鲁礼，朱干象箾杂巴渝。\",\"氤氲龙麝交青琐，仿佛锡銮下蕊珠。\",\"荐豆奉觞亲玉几，配天合祖耀璿枢。\",\"受釐饮酒皇欢洽，仰俟馀灵泰九区。\"]',NULL,NULL,NULL,NULL),('6ce235a83249df618f7e8f8028acda2a','郊庙歌辞汉宗庙乐舞辞显仁舞','张昭','6ce235a83249df61d1457b72d1457b72','五代十国',NULL,'[\"运极金行谢，天资水德隆。\",\"礼神鄜畤馆，布政未央宫。\",\"诘旦备明祀，登歌答茂功。\",\"云轩临降久，星俎荐陈丰。\",\"霭霭沈檀雾，铿锵环佩风。\",\"荧煌升藻藉，肸蚃转珠栊。\",\"尊祖咸韶备，贻孙书轨同。\",\"京坻长有积，宗社享无穷。\"]',NULL,NULL,NULL,NULL),('6ce235a83249df619367298154fddfd5','郊庙歌辞汉宗庙乐舞辞章庆舞','张昭','6ce235a83249df61d1457b72d1457b72','五代十国',NULL,'[\"罘罳晓唱鸡人，三牲八簋斯陈。雾集瑶阶琐闼，香生绮席华茵。\",\"珠佩貂珰熠爚，羽旄干戚纷纶。酌鬯既终三献，凝旒何止千春。\",\"阿阁长栖彩凤，郊宫叠奏祥麟。赤伏英灵未泯，玄圭运祚重新。\",\"玉斝牺樽潋滟，龙旂凤辖逡巡。瞻望月游冠冕，犹疑苍野回轮。\"]',NULL,NULL,NULL,NULL),('6ce235a83249df61991f2b7e1f596b08','郊庙歌辞汉宗庙乐舞辞积善舞','张昭','6ce235a83249df61d1457b72d1457b72','五代十国',NULL,'[\"黍稷斯馨，祖德惟明。\",\"蛇告赤帝，龟谋大横。\",\"云行雨施，天成地平。\",\"造我家邦，斡我璿衡。\",\"陶匏在御，醍盎惟精。\",\"或戛或击，载炮载烹。\",\"饮福受胙，舞降歌迎。\",\"滔滔不竭，洪惟水行。\"]',NULL,NULL,NULL,NULL),('6ce235a83249df61b288a964670b001e','郊庙歌辞汉宗庙乐舞辞灵长舞','张昭','6ce235a83249df61d1457b72d1457b72','五代十国',NULL,'[\"天降祥，汉祚昌。火炎上，水灵长。建庙社，洁蒸尝。罗钟石，俨珩璜。\",\"陈玉豆，酌金觞。气昭感，德馨香。祗洛汭，瞻晋阳。降吾祖，福穰穰。\"]',NULL,NULL,NULL,NULL),('6ce235a83249df61d0b4acfb25fe3de7','郊庙歌辞汉宗庙乐舞辞武德舞','张昭','6ce235a83249df61d1457b72d1457b72','五代十国',NULL,'[\"明明我祖，天集休明。\",\"神母夜哭，彤云昼兴。\",\"笾豆有践，管籥斯登。\",\"孝孙致告，神其降灵。\"]',NULL,NULL,NULL,NULL),('6ce235a833b0f1490dc6993a53a5e357','宿郑谏议山居','昙域','6ce235a833b0f149d1457b72d1457b72','五代十国',NULL,'[\"堂开星斗边，大谏采薇还。\",\"禽隐石中树，月生池上山。\",\"凉风吹咏思，幽语隔禅关。\",\"莫拟归城计，终妨此地闲。\"]',NULL,NULL,NULL,NULL),('6ce235a833b0f14925040c75f995a3e4','怀齐己','昙域','6ce235a833b0f149d1457b72d1457b72','五代十国',NULL,'[\"鬓髯秋景两苍苍，静对茅斋一炷香。\",\"病后身心俱澹泊，老来朋友半凋伤。\",\"峨眉山色侵云直，巫峡滩声入夜长。\",\"犹喜深交有支遁，时时音信到松房。\"]',NULL,NULL,NULL,NULL),('6ce235a833b0f149cc902d0a31cd47ed','赠岛云禅师','昙域','6ce235a833b0f149d1457b72d1457b72','五代十国',NULL,'[\"远庵枯叶满，群鹿亦相随。\",\"顶骨生新发，庭松长旧枝。\",\"禅高太白月，行出祖师碑。\",\"乱后潜来此，南人总不知。\"]',NULL,NULL,NULL,NULL),('6ce235a834687c79f86d2a87f8740ba9','献陈陶处士','莲花妓','6ce235a834687c79d1457b72d1457b72','五代十国',NULL,'[\"莲花为号玉为腮，珍重尚书遣妾来。\",\"处士不生巫峡梦，虚劳神女下阳台。\"]',NULL,NULL,NULL,NULL),('6ce235a835863ab6a4bf6acd6ee78a5f','叙怀','徐月英','6ce235a835863ab6d1457b72d1457b72','五代十国',NULL,'[\"为失三从泣泪频，此身何用处人伦。\",\"虽然日逐笙歌乐，长羡荆钗与布裙。\"]',NULL,NULL,NULL,NULL),('6ce235a835863ab6e076dadfdb9a97ad','送人','徐月英','6ce235a835863ab6d1457b72d1457b72','五代十国',NULL,'[\"惆怅人间万事违，两人同去一人归。\",\"生憎平望亭前水，忍照鸳鸯相背飞。\"]',NULL,NULL,NULL,NULL),('6ce235a83653e9d538c649b4db8964b3','批州符','萧结','6ce235a83653e9d5d1457b72d1457b72','五代十国',NULL,'[\"秧开五叶，蚕长三眠。\",\"人皆忙迫，划甚闲船。\"]',NULL,NULL,NULL,NULL),('6ce235a8374e1b1e6f2597a60353988a','谢武肃王','无作','6ce235a8374e1b1ed1457b72d1457b72','五代十国',NULL,'[\"云鹤性孤单，争堪名利关。\",\"衔恩虽入国，辞命却归山。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe3817313850c2da2cf2','题西林寺水阁','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"竹翠苔花绕槛浓，此亭幽致讵曾逢。\",\"水分林下清泠派，山峙云间峭峻峰。\",\"怪石夜光寒射烛，老杉秋韵冷和钟。\",\"不知来往留题客，谁约重寻莲社踪。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe381e0a4463a58924e2','观华夷图','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"别手应难及此精，须知攒簇自心灵。\",\"始于毫末分诸国，渐见图中列四溟。\",\"关路欲伸通楚势，蜀山俄耸入秦青。\",\"笔端尽现寰区事，堪把长悬在户庭。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe382bc7e21e7f58c872','僻居谢何明府见访','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"公退琴堂动逸怀，闲披烟霭访微才。\",\"马嘶穷巷蛙声息，辙到衡门草色开。\",\"风引柳花当坐起，日将林影入庭来。\",\"满斋尘土一床藓，多谢从容水饭回。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe382e4cce53773f2446','僻居酬友人','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"僻居虽爱近林泉，幽径闲居碧藓连。\",\"向竹掩扉随鹤息，就溪安石学僧禅。\",\"古琴带月音声亮，山果经霜气味全。\",\"多谢故交怜朴野，隔云时复寄佳篇。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe384de7c29d2f2098fb','冬日道中','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"去去天涯无定期，瘦童羸马共依依。\",\"暮烟江口客来绝，寒叶岭头人住稀。\",\"带雪野风吹旅思，入云山火照行衣。\",\"钓台吟阁沧洲在，应为初心未得归。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe384faa0ac1155f2b79','寄史处士','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"长羡闲居一水湄，吟情高古有谁知。\",\"石楼待月横琴久，渔浦经风下钓迟。\",\"僻坞落花多掩径，旧山残烧几侵篱。\",\"松门别后无消息，早晚重应蹑屐随。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe385978ae0018a5d615','林居喜崔三博远至','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"几日区区在远程，晚烟林径喜相迎。\",\"姿容虽有尘中色，巾屦犹多岳上清。\",\"野石静排为坐榻，溪茶深煮当飞觥。\",\"留连话与方经宿，又欲携书别我行。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe385c0ca254e2a28902','观山水障子','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"功绩精妍世少伦，图时应倍用心神。\",\"不知草木承何异，但见江山长带春。\",\"云势似离岩底石，浪花如动岸边蘋。\",\"更疑独泛渔舟者，便是其中旧隐人。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe385d6d383384c6b4a9','送江少府授延陵后寄','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"五老云中勤学者，遇时能不困风尘。\",\"束书西上谒明主，捧檄南归慰老亲。\",\"别馆友朋留醉久，去程烟月入吟新。\",\"莫因官小慵之任，自古鸾栖有异人。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38674ace68942504f3','庐山书堂送祝秀才还乡','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"束书辞我下重巅，相送同临楚岸边。\",\"归思几随千里水，离情空寄一枝蝉。\",\"园林到日酒初熟，庭户开时月正圆。\",\"莫使蹉跎恋疏野，男儿酬志在当年。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38682d605b0f5f25b2','宿灊山','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"一入仙山万虑宽，夜深宁厌倚虚栏。\",\"鹤和云影宿高木，人带月光登古坛。\",\"芝术露浓溪坞白，薜萝风起殿廊寒。\",\"更陪羽客论真理，不觉初钟叩晓残。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38773ca06abc9c61b3','晚秋同何秀才溪上','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"闲步秋光思杳然，荷藜因共过林烟。\",\"期收野药寻幽路，欲采溪菱上小船。\",\"云吐晚阴藏霁岫，柳含馀霭咽残蝉。\",\"倒尊尽日忘归处，山磬数声敲暝天。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe388b07f2276386fa3a','游西禅','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"远岫当轩列翠光，高僧一衲万缘忘。\",\"碧松影里地长润，白藕花中水亦香。\",\"云自雨前生净石，鹤于钟后宿长廊。\",\"游人恋此吟终日，盛暑楼台早有凉。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe388d14ae25d5d1fca2','九江旅夜寄山中故人','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"弱柳风高远漏沈，坐来难便息愁吟。\",\"江城雪尽寒犹在，客舍灯孤夜正深。\",\"尘土积年粘旅服，关山无处寄归心。\",\"此时遥羡闲眠侣，静掩云扉卧一林。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe3895bd370a859c6fbd','游西山龙泉禅寺','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"叠巘层峰坐可观，枕门流水更潺湲。\",\"晓钟声彻洞溪远，夏木影笼轩槛寒。\",\"幽径乍寻衣屦润，古堂频宿梦魂安。\",\"因嗟城郭营营事，不得长游空鬓残。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38b3d4c316ded60cd3','龙潭张道者','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"碧洞幽岩独息心，时人何路得相寻。\",\"养生不说凭诸药，适意惟闻在一琴。\",\"石径扫稀山藓合，竹轩开晚野云深。\",\"他年功就期飞去，应笑吾徒多苦吟。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38b42c2750827cee93','暮冬送何秀才毗陵','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"匹马嘶风去思长，素琴孤剑称戎装。\",\"路涂多是过残岁，杯酒无辞到醉乡。\",\"云傍水村凝冷片，雪连山驿积寒光。\",\"毗陵城下饶嘉景，回日新诗应满堂。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38b72d534592240304','闻杜牧赴阙','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"旧隐匡庐一草堂，今闻携策谒吾皇。\",\"峡云难卷从龙势，古剑终腾出土光。\",\"开翅定期归碧落，濯缨宁肯问沧浪。\",\"他时得意交知仰，莫忘裁诗寄钓乡。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38b9a177a1d7c16a85','寄落星史虚白处士','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"白云峰下古溪头，曾与提壶烂熳游。\",\"登阁共看彭蠡水，围炉相忆杜陵秋。\",\"棋玄不厌通高品，句妙多容隔岁酬。\",\"别后相思时一望，暮山空碧水空流。\"]',NULL,NULL,NULL,NULL),('6ce235a83864fe38fd8d95d795aabd72','僻居秋思寄友人','伍乔','6ce235a83864fe38d1457b72d1457b72','五代十国',NULL,'[\"门巷秋归更寂寥，雨馀闲砌委兰苗。\",\"梦回月夜虫吟壁，病起茅斋药满瓢。\",\"泽国旧游关远思，竹林前会负佳招。\",\"身名未立犹辛苦，何许流年晚鬓凋。\"]',NULL,NULL,NULL,NULL),('6ce235a839e03c14693a8c573fe03abc','金屑眼中翳','文偃','6ce235a839e03c14d1457b723cef96dc','五代十国',NULL,'[\"金屑眼中翳，衣珠法上尘。\",\"己灵犹不重，佛视为何人？\"]',NULL,NULL,NULL,NULL),('6ce235a839f408632556817fa67cd429','塞下三首','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"月冷榆关过雁行，将军寒笛老思乡。\",\"贰师骨恨千夫壮，李广魂飞一剑长。\",\"戍角就沙催落日，阴云分碛护飞霜。\",\"谁知汉武轻中国，闲夺天山草木荒。\"]',NULL,NULL,NULL,NULL),('6ce235a839f408632556817fb2fb94ef','塞下三首','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"贵主和亲杀气沉，燕山闲猎鼓鼙音。\",\"旗分雪草偷边马，箭入寒云落塞禽。\",\"陇月尽牵乡思动，战衣谁寄泪痕深。\",\"金钗谩作封侯别，劈破佳人万里心。\"]',NULL,NULL,NULL,NULL),('6ce235a839f408632556817fd7900e11','塞下三首','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"塞叶声悲秋欲霜，寒山数点下牛羊。\",\"映霞旅雁随疏雨，向碛行人带夕阳。\",\"边骑不来沙路失，国恩深后海城荒。\",\"胡儿向化新成长，犹自千回问汉王。\"]',NULL,NULL,NULL,NULL),('6ce235a839f408632a9ace2fe510bc69','忆仙谣','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"白榆风飒九天秋，王母朝回宴玉楼。\",\"日月渐长双凤睡，桑田欲变六鳌愁。\",\"云翻箫管相随去，星触旌幢各自流。\",\"诗酒近来狂不得，骑龙却忆上清游。\"]',NULL,NULL,NULL,NULL),('6ce235a839f408633ba628e1b97b7264','吊边人','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"杀声沈后野风悲，汉月高时望不归。\",\"白骨已枯沙上草，家人犹自寄寒衣。\"]',NULL,NULL,NULL,NULL),('6ce235a839f4086350fd0aebb2c6f090','都门送别','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"岸柳萧疏野荻秋，都门行客莫回头。\",\"一条灞水清如剑，不为离人割断愁。\"]',NULL,NULL,NULL,NULL),('6ce235a839f4086372e04d91341f23ac','秋日','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"秋含砧杵捣斜阳，笛引西风颢气凉。\",\"薜荔惹烟笼蟋蟀，芰荷翻雨泼鸳鸯。\",\"当年酒贱何妨醉，今日时难不易狂。\",\"肠断旧游从一别，潘安惆怅满头霜。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863749bbf4c17c08c0a','金陵杂题二首','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"王气生秦四百年，晋元东渡浪花船。\",\"正惭海内皆涂地，来保江南一片天。\",\"古树著行临远岸，暮山相亚出微烟。\",\"千征万战英雄尽，落日牛羊食野田。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863749bbf4cd32edfb1','金陵杂题二首','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"暮潮声落草光沉，贾客来帆宿岸阴。\",\"一笛月明何处酒，满城秋色几家砧。\",\"时清曾恶桓温盛，山翠长牵谢傅心。\",\"今日到来何物在，碧烟和雨锁寒林。\"]',NULL,NULL,NULL,NULL),('6ce235a839f4086374f1f1ebb71aa9ae','题苏仙山','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"眼穿林罅见郴州，井里交连侧局揪。\",\"味道不来闲处坐，劳生更欲几时休。\",\"苏仙宅古烟霞老，义帝坟荒草木愁。\",\"千古是非无处问，夕阳西去水东流。\"]',NULL,NULL,NULL,NULL),('6ce235a839f408638127a100cf26233e','横吹曲辞其一入塞曲','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"欲为皇王服远戎，万人金甲鼓鼙中。\",\"阵云暗塞三边黑，兵血愁天一片红。\",\"半夜翻营旗搅月，深秋防戍剑磨风。\",\"谤书未及明君爇，卧骨将军已殁功。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863934238b1ff846209','杂曲歌辞结客少年场行','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"重义轻生一剑知，白虹贯日报雠归。\",\"片心惆怅清平世，酒市无人问布衣。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863b1e230883aa92bc8','洪州解至长安初举纳省卷梦仙谣','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"□□□□□□□，□□□□□□□。\",\"玉殿大开从客入，金桃烂熟没人偷。\",\"凤惊宝扇频翻翅，龙惧金鞭不转头。\",\"□□□□□□□，□□□□□□□。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863b6388df7a3f669bb','赠王定保','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"仙桂曾攀第一枝，薄游湘水阻佳期。\",\"皋桥已失齐眉愿，萧寺行逢落发师。\",\"废苑露寒兰寂寞，丹山云断凤参差。\",\"闻公已有平生约，谢绝女萝依兔丝。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863bf443c6e47d399d1','入塞二首其二','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"苦战沙门卧箭痕，戍楼闲上望星文。\",\"生希国泽分偏将，死夺河源答圣君。\",\"鸢觑败兵眠白草，马惊边鬼哭阴云。\",\"功多地远无人纪，汉阁笙歌日又曛。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863d1dd32ec4414490b','阳朔碧莲峰','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"陶潜彭泽五株柳，潘岳河阳一县花。\",\"两处争如阳朔好，碧莲峰里住人家。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863d9a39daeb8fa5e51','麻姑山','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"绀殿松萝太古山，仙人曾此话桑田。\",\"闲倾云液十分日，已过浮生一万年。\",\"花洞路中逢鹤信，水帘岩底见龙眠。\",\"我来游礼酬心愿，欲共怡神契自然。\"]',NULL,NULL,NULL,NULL),('6ce235a839f40863f68bb99017605baa','纳省卷赠为首刘象','沈彬','6ce235a839f40863d1457b72d1457b72','五代十国',NULL,'[\"曾应大中天子举，四朝风月鬓萧疏。\",\"不随世祖重携剑，却为文皇再读书。\",\"十载战尘销旧业，满城春雨坏贫居。\",\"一枝何事于君借，仙桂年年幸有馀。\"]',NULL,NULL,NULL,NULL),('6ce235a83cd367931cba19e3263126e1','投谒齐己','释乾康','6ce235a83cd36793d1457b72d1457b72','五代十国',NULL,'[\"隔岸红尘忙似火，当轩青嶂冷如冰。\",\"烹茶童子休相问，报道门前是衲僧。\"]',NULL,NULL,NULL,NULL),('6ce235a83cd367939e0c612879290e67','赋残雪','释乾康','6ce235a83cd36793d1457b72d1457b72','五代十国',NULL,'[\"六出奇花已住开，郡城相次见楼台。\",\"时人莫把和泥看，一片飞从天上来。\"]',NULL,NULL,NULL,NULL),('6ce235a83e58ae77f312981bca787258','送德林郎中学士赴东府','乔舜','6ce235a83e58ae77a64c94bac22abfa3','五代十国',NULL,'[\"掺袂向江头，朝宗势未休。\",\"何人乘桂楫，之子过扬州。\",\"飒飒翘沙雁，漂漂逐浪鸥。\",\"欲知离别恨，半是泪和流。\"]',NULL,NULL,NULL,NULL),('6ce235a83ea1dcbbf819547ae159c8ec','芳草','扈载','6ce235a83ea1dcbbd1457b72d1457b72','五代十国',NULL,'[\"幽芳无处无，幽处恨何如。\",\"倦客伤归思，春风满旧居。\",\"晚烟迷杳霭，朝露健扶疏。\",\"省傍灵光看，残阳少皞区。\"]',NULL,NULL,NULL,NULL),('6ce235a83fcfd6e3631dbe455cff5338','留题座主和凝旧阁','李瀚','6ce235a83fcfd6e3d1457b72d1457b72','五代十国',NULL,'[\"座主登庸归凤阙，门生批诏立鳌头。\",\"玉堂旧阁多珍玩，可作西斋润笔不。\"]',NULL,NULL,NULL,NULL),('6ce235a83ff97a2b517b11968fc87a90','题县令范传真化洽亭','沈颜','6ce235a83ff97a2bd1457b72d1457b72','五代十国',NULL,'[\"前有浅山，屹然如屏。\",\"后有卑岭，缭然如城。\",\"跨池左右，足以建亭。\",\"斯亭何名，化洽而成。\"]',NULL,NULL,NULL,NULL),('6ce235a83ff97a2bdbb2b1f51b639a6f','书怀寄友人','沈颜','6ce235a83ff97a2bd1457b72d1457b72','五代十国',NULL,'[\"江湖劳遍寻，只自长愁襟。\",\"到处慵开口，何人可话心。\",\"登楼得句远，望月抒情深。\",\"却忆山斋后，猿声相伴吟。\"]',NULL,NULL,NULL,NULL),('6ce235a8403ad6781250e6aff752e0fb','冬日与群公泛舟焦山其二','王瓒','6ce235a8403ad678d1457b72bea5955b','五代十国',NULL,'[\"纵步不知远，夕阳犹未回。\",\"好花随处发，流水趁人来。\"]',NULL,NULL,NULL,NULL),('6ce235a8403ad678c39b94f2ec11b459','冬日与群公泛舟焦山其一','王瓒','6ce235a8403ad678d1457b72bea5955b','五代十国',NULL,'[\"江外水不冻，今年寒复迟。\",\"众芳且未歇，近腊仍裌衣。\",\"载酒适我情，兴来趣渐微。\",\"方舟大川上，环酌对落晖。\",\"两片青石棱，波际无因依。\",\"三山安可到，欲到风引归。\",\"沧溟壮观多，心目豁暂时。\",\"况得穷日夕，乘槎何所之。\"]',NULL,NULL,NULL,NULL),('6ce235a840838a5477ef8ec9c67663f1','述国亡诗','花蕊夫人','6ce235a840838a549c25cd16cbb6a3b8','五代十国','徐氏（一说姓费），青城（今四川都江堰市）人，因才貌双全，得幸于后蜀主孟昶，拜贵妃，别号花蕊夫人。她曾仿王建作宫词百首，为时人称许。孟蜀亡国后，被掳入宋。宋太祖久闻其诗名，召她陈诗。徐氏就诵了这首“述亡国之由”的诗。诗泼辣而不失委婉，不亢不卑，从题材到风格，都与作者所擅长的“宫词”大不相同，当时就获得宋太祖的赞赏（事据《十六国春秋·蜀志》）。\n此诗破题就直述国亡之事：“君王城上竖降旗”。史载后蜀君臣极为奢侈，荒淫误国，宋军压境时，孟昶一筹莫展，屈辱投降。诗句只说“竖降旗”，遣词含蓄。下语只三分而命意十分，耐人玩味。\n次句“妾在深宫那得知”，纯用口语，而意蕴微妙。大致有两重含义：首先，历代追咎国亡的诗文多持“女祸亡国”论，如把商亡归咎于妲己，把吴亡归咎于西施等等。而这句诗则象是针对“女祸亡国”而作的自我申辩。语似轻声叹息，然措词微婉，而大有深意。其次，即使退一步说，“妾”及时得知投降的事也照样于事无补，一个弱女子不可能有回天之力。不过，“那得知”云云毕竟还表示了一种廉耻之心，比起甘心作阶下囚的“男儿”们终究不可同日而语。这就为下面的怒斥预留了地步。\n第三句照应首句“竖降旗”，描绘出蜀军“十四万人齐解甲”的投降场面。史载当时破蜀宋军仅数万人，而后蜀则有“十四万人”之众。以数倍于敌的兵力，背城借一，即使面临强敌，当无亡国之理。可是一向耽于享乐的孟蜀君臣毫无斗志，闻风丧胆，终于演出众降于寡的丑剧。“十四万人”没有一个死国的志士，没有一星半点丈夫气概，当然是语带夸张，却有力写出了一个女子的羞愤：可耻在于不战而亡。\n至此，作者的羞愤痛切之情已酝酿充分，于是爆发出一句热骂：“更无一个是男儿！”“更无一个”与“十四万人”对比，“男儿”与前面“妾”对照，可谓痛快淋漓。“诗可以怨”，其实岂但可怨而已，这里已是“嬉笑怒骂，皆成文章”了。\n此诗写得很有激情，表现出亡国的沉痛和对误国者的痛切之情；更写得有个性，活现出一个活泼泼有性格的女性形象。诗人以女子身份骂人枉为男儿，就比一般有力，个性色彩鲜明。就全诗看，有前三句委婉含蓄作铺垫，虽泼辣而不失委婉，非一味发露、缺乏情韵之作可比。\n据宋吴曾《能改斋漫录》，花蕊夫人作此诗则有所本。“前蜀王衍降后唐，王承旨作诗云：‘蜀朝昏主出降时，衔璧牵羊倒系旗。二十万人齐拱手，更无一个是男儿。’”对照二诗，徐氏对王诗几处改动都很好。原诗前二句太刻意吃力，不如改作之含蓄有味，特别是改用第一人称“妾”的口气来写，比原作多一重意味，顿添神采。这样的改作实有再造之功。就诗人陈诗一事而论，不但表现了廉耻之心，而且有几分胆气。这行为本身就足为孟蜀“男儿”羞。所以，此诗得到一代雄主赵匡胤的赏识，不是偶然的。','[\"君王城上竖降旗，妾在深宫那得知。\",\"十四万人齐解甲，更无一个是男儿。\"]','[\"王士禛《五代诗话》引《稗史汇编》：\\u201C蜀既破，其亡国诗云：\\u2018君王城上竖降旗······\\u2019亦愤而悲矣。\\u201D\",\"薛雪《一瓢诗话》：\\u201C落句云：\\u2018十四万人齐解甲，更无一个是男儿。\\u2019何等气魄？何等忠愤？当令普天下须眉一时俯首。\\u201D\"]','后蜀国的君王城楼上竟然竖起了白旗，我被封锁在了这冷清清的行宫里哪里知道这件事？\n守卫君王的十四万人一起脱下了金闪闪的铠甲，其实这些人中没有一个是守卫国家的男子！','《述国亡诗》是五代时期女诗人花蕊夫人创作的一首七言绝句。这首诗第一句是叙述蜀国降宋的事实，第二句使诗意更为深入，同时也展示出了这位宫妃的内心活动和对蜀国灭亡的看法。第三句具体讲述蜀国投降的惨状，第四句用了反问句式并运用反语斥责了蜀主的无能，又嘲笑了文武群臣甘当俘虏的丑态，从而表明了这位宫妃的爱国之心，报国之志。','[\"妾：花蕊夫人自称。\",\"解甲：解除武装，指投降。\",\"更无：一作\\u201C宁无\\u201D。\"]'),('6ce235a841e3fb326145c7d5f3d362b3','都堂试贡士日庆春雪','李景','6ce235a841e3fb32d1457b72d1457b72','五代十国',NULL,'[\"密雪分天路，群才坐粉廊。\",\"霭空迷昼景，临宇借寒光。\",\"似暖花消地，无声玉满堂。\",\"洒池偏误曲，留砚忽因方。\",\"几处曹风比，何人谢赋长。\",\"春晖早相照，莫滞九衢芳。\"]',NULL,NULL,NULL,NULL),('6ce235a842fe95555c8b32d68a3e265c','赠进士李守微','韩屿','6ce235a842fe9555d1457b72d1457b72','五代十国',NULL,'[\"一定童颜老岁华，贫寒游历贵人家。\",\"炼成正气功应大，养得元神道不差。\",\"舄曳鹤毛乾毾?，杖携筇节瘦槎牙。\",\"如何蓬阆不归去，落尽蟠桃几度花。\"]',NULL,NULL,NULL,NULL),('6ce235a8437664571c028bc9ccc9fc25','秋词','徐锴','6ce235a8437664576d0f84634311359e','五代十国',NULL,'[\"井梧纷堕砌，寒雁远横空。\",\"雨久莓苔紫，霜浓薜荔红。\"]',NULL,NULL,NULL,NULL),('6ce235a8437664572194b05d5081f5b0','太傅相公与家兄梅花酬唱许缀末篇再赐新诗俯光拙句谨奉清韵用感钧私伏惟采览','徐锴','6ce235a8437664576d0f84634311359e','五代十国',NULL,'[\"重叹梅花落，非关塞笛悲。\",\"论文叨接萼，末曲愧吹篪。\",\"枝逐清风动，香因白雪知。\",\"陶钧敷左悌，更赋邵公诗。\"]',NULL,NULL,NULL,NULL),('6ce235a8437664573d68fd5cbd733784','送程德琳郎中学士','徐锴','6ce235a8437664576d0f84634311359e','五代十国',NULL,'[\"瓜步妖氛灭，昆冈草树青。\",\"终朝空望极，今日送君行。\",\"报政秋云静，微吟晓月生。\",\"楼中长可见，特用灭离情。\"]',NULL,NULL,NULL,NULL),('6ce235a843766457d4343f75be7e79b2','同家兄哭乔侍郎','徐锴','6ce235a8437664576d0f84634311359e','五代十国',NULL,'[\"诸公长者郑当时，事事无心性坦夷。\",\"但是登临皆有作，未尝相见不伸眉。\",\"生前适意无过酒，身后遗言只要诗。\",\"三日笑谈成理命，一篇投吊尚应知。\"]',NULL,NULL,NULL,NULL),('6ce235a843766457f2f05cb446856754','太傅相公以东观庭梅西垣旧植昔陪盛赏今独家兄唱和之馀俾令攀和辄依本韵伏愧斐然','徐锴','6ce235a8437664576d0f84634311359e','五代十国',NULL,'[\"静对含章树，闲思共有时。\",\"香随荀令在，根异武昌移。\",\"物性虽摇落，人心岂变衰。\",\"唱酬胜笛曲，来往韵朱丝。\"]',NULL,NULL,NULL,NULL),('6ce235a84413a713a239a8fb48c3e98f','观棋','李从谦','6ce235a84413a713c8c41c4a2bcab9d9','五代十国',NULL,'[\"竹林二君子，尽日竟沉吟。\",\"相对终无语，争先各有心。\",\"恃强斯有失，守分固无侵。\",\"若算机筹处，沧沧海未深。\"]',NULL,NULL,NULL,NULL),('6ce235a845601d6dcb9c443367d74082','题九天使者庙','孙咸','6ce235a845601d6dd1457b72d1457b72','五代十国',NULL,'[\"独入玄宫礼至真，焚香不为贱贫身。\",\"秦淮两岸沙埋骨，湓浦千家血染尘。\",\"庐阜烟霞谁是主，虎溪风月属何人。\",\"九江太守勤王事，好放天兵渡要津。\"]',NULL,NULL,NULL,NULL),('6ce235a84571954d731baf767ecc81a6','画山水图答大愚','荆浩','6ce235a84571954dd1457b72d1457b72','五代十国',NULL,'[\"恣意纵横扫，峰峦次第成。\",\"笔尖寒树瘦，墨澹野云轻。\",\"岩石喷泉窄，山根到水平。\",\"禅房时一展，兼称苦空情。\"]',NULL,NULL,NULL,NULL),('6ce235a845bf305e664085beeeb30d80','荆台','孙光宪','6ce235a845bf305e892c91e08f468c87','五代十国',NULL,'[\"百尺荆台草径荒，如何前日谓云阳。\",\"古今不尽迁移恨，依旧台边水渺茫。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70f1a3c3ecea3287182','晚眺','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"倚杖残秋里，吟中四顾频。\",\"西风天际雁，落日渡头人。\",\"草色衰平野，山阴敛暮尘。\",\"却寻苔径去，明月照村邻。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70f2844bea53963cff2','磻','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"良哉吕尚父，深隐始归周。\",\"钓石千年在，春风一水流。\",\"松根盘藓石，花影卧沙鸥。\",\"谁更怀韬术，追思古渡头。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70f3d4f859709b7d745','耒阳杜工部祠堂','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"南游何感思，更甚叶缤纷。\",\"一夜耒江雨，百年工部文。\",\"青山当日见，白酒至今闻。\",\"惟有为诗者，经过时吊君。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70f65a0108ba1c3ef39','怀连上旧居','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"闲思连上景难齐，树绕仙乡路绕溪。\",\"明月夜舟渔父唱，春风平野鹧鸪啼。\",\"城边寄信归云外，花下倾杯到日西。\",\"更忆海阳垂钓侣，昔年相遇草萋萋。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70f6f855ad111ba416e','献主司','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"那堪雨后更闻蝉，溪隔重湖路七千。\",\"忆昔故园杨柳岸，全家送上渡头船。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70f95abd1581ca77fae','第四举','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"失意从他桃李春，嵩阳经过歇行尘。\",\"云僧不见城中事，问是今年第几人。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70fcfdf1cb40f6e596d','湘江亭','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"独宿大中年里寺，樊笼得出事无心。\",\"寒山梦觉一声磬，霜叶满林秋正深。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70fe10c1ccfd6c3e224','题梅仙馆','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"仙界路遥云缥渺，古坛风冷叶萧骚。\",\"后来岂合言淹滞，一尉升腾道最高。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70fe807865ddd1821ed','公子行','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"锦衣红夺彩霞明，侵晓春游向野庭。\",\"不识农夫辛苦力，骄骢蹋烂麦青青。\"]',NULL,NULL,NULL,NULL),('6ce235a84668c70ffa9954d54c187b91','题颜氏亭宇','孟宾于','6ce235a84668c70fd1457b72d1457b72','五代十国',NULL,'[\"园林萧洒闻来久，欲访因循二十秋。\",\"今日开襟吟不尽，碧山重叠水长流。\"]',NULL,NULL,NULL,NULL),('6ce235a846971608fb1c120e44ee7ab6','题酒户修孔庙状','冯道幕客','6ce235a846971608d1457b72d1457b72','五代十国',NULL,'[\"槐影参差覆杏坛，儒门子弟尽高官。\",\"却教酒户重修庙，觅我惭惶也不难。\"]',NULL,NULL,NULL,NULL),('6ce235a847067d4528d4d1a20d38d29b','百舌鸟二首','无则','6ce235a847067d45d1457b72d1457b72','五代十国',NULL,'[\"千愁万恨过花时，似向春风怨别离。\",\"若使众禽俱解语，一生怀抱有谁知。\"]',NULL,NULL,NULL,NULL),('6ce235a847067d4528d4d1a25fa98a74','百舌鸟二首','无则','6ce235a847067d45d1457b72d1457b72','五代十国',NULL,'[\"长截邻鸡叫五更，数般名字百般声。\",\"饶伊摇舌先知晓，也待青天明即鸣。\"]',NULL,NULL,NULL,NULL),('6ce235a84a54bb4fb50c37d5ba4b0bc2','塞上','赵延寿','6ce235a84a54bb4fd1457b7258e4d44e','五代十国',NULL,'[\"黄沙风卷半空抛，云动阴山雪满郊。\",\"探水人回移帐就，射雕箭落著弓抄。\",\"鸟逢霜果饥还啄，马渡冰河渴自跑。\",\"占得高原肥草地，夜深生火折林梢。\"]',NULL,NULL,NULL,NULL),('6ce235a84d4d4194ac5e0f327d51021c','赠廖融','王正己','6ce235a84d4d4194d1457b72d1457b72','五代十国',NULL,'[\"病起正当秋阁迥，酒醒迎对夜涛寒。\",\"炉中药熟分僧饭，枕上琴闲借客弹。\"]',NULL,NULL,NULL,NULL),('6ce235a84e92236535d097bedebccdcb','五门街望有题','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"我皇开国十馀年，一辈超升炙手欢。\",\"闲向五门楼下望，衙官骑马使衙官。\"]',NULL,NULL,NULL,NULL),('6ce235a84e92236553026aa9952d8e0f','谢郎中惠茶','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"三斤绿茗赐贻恭，一种颁沾事不同。\",\"想料肠怀无答处，披毛戴角谢郎中。\"]',NULL,NULL,NULL,NULL),('6ce235a84e92236563e4727eec608ba8','咏蚕','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"辛勤得茧不盈筐，灯下缫丝恨更长。\",\"著处不知来处苦，但贪衣上绣鸳鸯。\"]',NULL,NULL,NULL,NULL),('6ce235a84e9223657d159b2fae76e360','咏金刚','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"扬眉斗目恶精神，捏合将来恰似真。\",\"刚被时流借拳势，不知身自是泥人。\"]',NULL,NULL,NULL,NULL),('6ce235a84e9223658201fc14aff73223','咏王给事','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"厥父元非道郡奴，允光何事太侏儒。\",\"可中与个皮裈著，擎得天王左脚无。\"]',NULL,NULL,NULL,NULL),('6ce235a84e922365954f17228b819db1','咏安仁宰捣蒜','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"安仁县令好诛求，百姓脂膏满面流。\",\"半破磁缸成醋酒，死牛肠肚作馒头。\",\"帐生岁取餐三顿，乡老盘庚犯五瓯。\",\"半醉半醒齐出县，共伤涂炭不胜愁。\"]',NULL,NULL,NULL,NULL),('6ce235a84e922365c73037992275ed09','题张道隐太山祠画龙','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"世人空解竞丹青，惟子通玄得墨灵。\",\"应有鬼神看下笔，岂无风雨助成形。\",\"威疑喷浪归沧海，势欲拿云上杳冥。\",\"静闭绿堂深夜后，晓来帘幕似闻腥。\"]',NULL,NULL,NULL,NULL),('6ce235a84e922365d1dda362c8fbab2a','住名山日陈情上府主高太保','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"名山主簿实堪愁，难咬他家大骨头。\",\"米纳功南钱纳府，只看江面水东流。\"]',NULL,NULL,NULL,NULL),('6ce235a84e922365d32fced9591c41d0','咏伛背子','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"出得门来背拄天，同行难可与差肩。\",\"若教倚向闲窗下，恰似箜篌不著弦。\"]',NULL,NULL,NULL,NULL),('6ce235a84e922365f962cc30b661dcab','咏虾蟆','蒋贻恭','6ce235a84e922365d1457b72d1457b72','五代十国',NULL,'[\"坐卧兼行总一般，向人努眼太无端。\",\"欲知自己形骸小，试就蹄涔照影看。\"]',NULL,NULL,NULL,NULL),('6ce235a84f4aaf791379e03b64105727','中秋月','可朋','6ce235a84f4aaf79d1457b72d1457b72','五代十国',NULL,'[\"登楼仍喜此宵晴，圆魄才观思便清。\",\"海面乍浮犹隐映，天心高挂最分明。\",\"片云想有神仙出，回野应无鬼魅形。\",\"曾向洞庭湖上看，君山半雾水初平。\"]',NULL,NULL,NULL,NULL),('6ce235a84f4aaf7936b06fd044620e0b','赠方干','可朋','6ce235a84f4aaf79d1457b72d1457b72','五代十国',NULL,'[\"盛名传出自皇州，一举参差便缩头。\",\"月里岂无攀桂分，湖中刚爱钓鱼休。\",\"童偷诗稿呈邻叟，客乞书题谒郡侯。\",\"独泛短舟何限景，波涛西接洞庭秋。\"]',NULL,NULL,NULL,NULL),('6ce235a84f4aaf7987f064b69da942d3','赋洞庭','可朋','6ce235a84f4aaf79d1457b72d1457b72','五代十国',NULL,'[\"周极八百里，凝眸望则劳。\",\"水涵天影阔，山拔地形高。\",\"贾客停非久，渔翁转几遭。\",\"飒然风起处，又是鼓波涛。\"]',NULL,NULL,NULL,NULL),('6ce235a84f4aaf79dbb5eebbdb197e66','耕田鼓诗','可朋','6ce235a84f4aaf79d1457b72d1457b72','五代十国',NULL,'[\"农舍田头鼓，王孙筵上鼓。击鼓兮皆为鼓，一何乐兮一何苦。\",\"上有烈日，下有焦土。愿我天翁，降之以雨。令桑麻熟，仓箱富。\",\"不饥不寒，上下一般。\"]',NULL,NULL,NULL,NULL),('6ce235a84f4aaf79f5c00c09f06dd682','桐花鸟','可朋','6ce235a84f4aaf79d1457b72d1457b72','五代十国',NULL,'[\"五色毛衣比凤雏，深花丛里只如无。\",\"美人买得偏怜惜，移向金钗重几铢。\"]',NULL,NULL,NULL,NULL),('6ce235a85209d809610f729788480bfe','感秃鹙潜吟','顾夐','6ce235a85209d809d1457b72d1457b72','五代十国',NULL,'[\"昔日曾看瑞应图，万般祥端不如无。\",\"摩诃池上分明见，仔细看来是那鹕。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f05c0134a848e4209','秋宿湘江遇雨','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国','这首诗通过描写湘江秋雨的苍茫、寂寥景色，抒发诗人的怀才不遇之感，语意极为沉痛、悲凉。\n诗歌一开篇便起得雄浑壮阔。“湘上阴云锁梦魂”，这一句不但形象地描绘出暴雨将至之时的那种风急浪高、阴云密布的景色，而且点明题意，交代因雨受阻、夜宿湘江的缘由。一个“锁”字，流露出诗人被迫困于此地的无奈和怅惘。不过，由于整句取景壮阔，无奈怅惘之中并无衰飒之气，反倒更加激发了诗人的豪情壮志，所以接下来的“江边深夜舞刘琨”一句，诗人即用刘琨闻鸡起舞的故事，表明自己奋发振作、立志救时的抱负和决心。\n颔联则转为描写湘江的秋雨景色。“秋风万里芙蓉国，暮雨千家薜荔村”，这两句既对仗工整，又写景壮丽。秋风暮雨之中，诗人眺望远方，只见湘江两岸，满目所见，尽是花枝繁密的芙蓉和随风摇曳的薜荔，煞是美丽。描写“芙蓉”的诗句最早见于中唐柳宗元《芙蓉亭》：“新亭俯朱槛，嘉木开芙蓉。”名之以“芙蓉国”和“薜荔村”，本已极言其多、其盛，再加上“万里”和“千家”的修饰语，则显得更加幅员辽阔，展现出一片广阔无边的花海和绿色原野，面对这样的壮丽景色，诗人的心境也不由得更加开阔了。\n颈联由写景转入抒情。其中“乡思不堪悲橘柚”一句，主要抒发诗人的思乡之情和生不逢时之悲。橘树生长于南方，一旦移植到北方栽种，因为气候、土壤等条件的变化，结出的果实味道就会有所不同，所以有“橘生淮南则为橘，生于淮北则为枳”之说。湖南地处南方，故盛产橘柚，何况眼下又是橘柚成熟的季节，湘江两岸必定是金黄一片、硕果累累。诗人见此情景，对于橘树的适得其所，不禁心向往之。相形之下，自己滞留此地，已是身不由己，再加上生不逢时、有志难骋的遭遇，不觉更加凄苦、悲凉。于是，只能将满腹辛酸化为一声“旅游谁肯重王孙”的呼喊。“王孙”语出淮南小山《楚辞·招隐士》，意为隐居的贤士，后来也用来称呼游子，此处则二意并存，借指诗人自己。诗人怀抱经世之才和救世之志，然而却始终不被重视，只能到处漂泊，这其中的苦楚却无人明白。\n尾联则以写景作结。“渔人相见不相问，长笛一声归岛门”两句，暗用屈原与渔父之事。屈原虽然不被世人理解，但是尚有渔父与之对话，而自己竟然连屈原都不如，一种世无知音的悲愤溢于言表，令人感慨万千。\n此诗写景抒情，章法严密。一方面情景分明，二者相问写来；另一方面又能融情人景，表现出由雄壮到悲愤的巨大变化，因此极易打动人心。','[\"湘上阴云锁梦魂，江边深夜舞刘琨。\",\"秋风万里芙蓉国，暮雨千家薜荔村。\",\"乡思不堪悲橘柚，旅游谁背重王孙。\",\"渔人相见不相问，长笛一声归岛门。\"]',NULL,'湘江上阴云笼罩，使人心情郁闷。深夜里徘徊江岸，想起刘琨的远大抱负。\n秋风吹动，万里芙蓉花摇曳生姿。暮雨洗尘，千村的薜荔枝苍翠可爱。\n乡思难耐，看见橘柚更令人悲叹。羁旅他乡，如被弃的山野之人无人看重。\n就是打鱼人和我相见，也不与我说一句话。自管吹着长笛回岛去了。','《秋宿湘江遇雨》是五代诗人谭用之创作的七言律诗。这首诗描绘了秋天湘江夜雨的景色，表现了作者的羁旅乡愁，但不颓唐。全诗寓情于景，借景以抒情，二者融合无间。同时语言凝炼，对仗工整。','[\"湘江：即湘水，发源于广西兴安县海阳山，北流至湖南，注入洞庭湖，是湖南省最大的河流。\",\"锁：束缚，封住。\",\"梦魂：梦乡之魂，指思乡之情。\",\"刘琨：晋朝人，少怀壮志，与祖逖相互激励，常闻鸡鸣而起来舞剑，准备为国家作一番事业。后来常用这个故事以表示胸怀壮志。\",\"芙蓉国：湖南省内因广种荷花，故有芙蓉国之称。\",\"薜荔：又名木莲，一种常绿蔓生植物，多生于田野间。古人把它看香草。屈原《九歌·山鬼》：\\u201C若有人兮山之阿，被薜荔兮带女萝。\\u201D\",\"橘、柚：这两种水果都盛产于南方，在秋冬成熟。橘一向被称为\\u201C嘉树\\u201D。\",\"旅游：离家旅行在外。\",\"王孙：本意是贵族子弟，有时也指隐士，这里是作者自指。《楚辞·招隐士》：\\u201C王孙游兮不归，春草生兮萋萋。\\u201D\",\"岛门：岛上。\"]'),('6ce235a854cfa04f06492014887c8d2d','江上闻笛','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"谁为梅花怨未平，一声高唤百龙惊。\",\"风当阊阖庭初静，月在姑苏秋正明。\",\"曲尽绿杨涵野渡，管吹青玉动江城。\",\"临流不欲殷勤听，芳草王孙旧有情。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f1e47f6c0fd8e8d93','河桥楼赋得群公夜宴','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"芙蓉帘幕扇秋红，蛮府新郎夜宴同。\",\"满座马融吹笛月，一楼张翰过江风。\",\"杯黏紫酒金螺重，谈转雕珰玉麈空。\",\"深荷良宵慰憔悴，德星池馆在江东。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f333493aa150ea75b','幽居寄李秘书','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"几年帝里阻烟波，敢向明时叩角歌。\",\"看尽好花春卧稳，醉残红日夜吟多。\",\"印开夕照垂杨柳，画破寒潭老芰荷。\",\"昨夜前溪有龙斗，石桥风雨少人过。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f36041fa3ad4bbedd','月夜怀寄友人','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"剑气徒劳望斗牛，故人别后阻仙舟。\",\"残春谩道深倾酒，好月那堪独上楼。\",\"何处是非随马足，由来得丧白人头。\",\"清风未许重携手，几度高吟寄水流。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f434aae856df5ed0f','送丁道士归南中','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"孤云无定鹤辞巢，自负焦桐不说劳。\",\"服药几年期碧落，验符何处咒丹毫。\",\"子陵山晓红云密，青草湖平雪浪高。\",\"从此人稀见踪迹，还应选地种仙桃。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f46360b6b850fa8ee','别江上一二友生','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"国风千载务重华，须逐浮云背若耶。\",\"无地可归堪种玉，有天教上且乘槎。\",\"白纶巾卸苏门月，红锦衣裁御苑花。\",\"他日成都却回首，东山看取谢鲲家。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f46ef44679b897b01','感怀呈所知','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"十年流落赋归鸿，谁傍昏衢驾烛龙。\",\"竹屋乱烟思梓泽，酒家疏雨梦临邛。\",\"千年别恨调琴懒，一片年光览镜慵。\",\"早晚休歌白石烂，放教归去卧群峰。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f65da0e98529c604a','贻钓鱼李处士','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"罢吟鹦鹉草芊芊，又泛鸳鸯水上天。\",\"一棹冷涵杨柳雨，片帆香挂芰荷烟。\",\"绿摇江澹萍离岸，红点云疏橘满川。\",\"何处邈将归画府，数茎红蓼一渔船。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f676a67e7b1ede96b','赠索处士','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"不将桂子种诸天，长得寻君水石边。\",\"玄豹夜寒和雾隐，骊龙春暖抱珠眠。\",\"山中宰相陶弘景，洞里真人葛稚川。\",\"一度相思一惆怅，水寒烟澹落花前。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f73daf811103a9af1','寄孟进士','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"依旧池边草色芳，故人何处忆山阳。\",\"书回科斗江帆暮，曲罢驺虞海树苍。\",\"吟望晓烟思桂渚，醉依残月梦馀杭。\",\"别来南国知谁在，空对襜褕一断肠。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f910e1efabf0eefe1','塞上其二','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"钵略城边日欲西，游人却忆旧山归。\",\"牛羊集水烟黏步，雕鹗盘空雪满围。\",\"猎骑静逢边气薄，戍楼寒对暮烟微。\",\"横行总是男儿事，早晚重来似汉飞。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04f9e0c19f3ccb7e7e1','寄阎记室','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"织锦歌成下翠微，岂劳西去问支机。\",\"未开水府珠先见，不掘丰城剑自辉。\",\"鳌逐玉蟾攀桂上，马随青帝踏花归。\",\"相逢半是云霄客，应笑歌牛一布衣。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04fcd866d2af03a72ff','别雒下一二知己','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"金鼎光辉照雪袍，雒阳春梦忆波涛。\",\"尘埃满眼人情异，风雨前程马足劳。\",\"接塞峨眉通蜀险，过山仙掌倚秦高。\",\"别来无限幽求子，应笑区区味六韬。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04fd19ce70a99fddd59','寄友人','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"病多慵引架书看，官职无才思已阑。\",\"穴凤瑞时来却易，人龙别后见何难。\",\"琴樽风月闲生计，金玉松筠旧岁寒。\",\"早晚烟村碧江畔，挂罾重对蓼花滩。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04fd8903fb8fa4cf850','送友人归青社','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"雕鹗途程在碧天，彩衣东去复何言。\",\"二千宾客旧知己，十二山河新故园。\",\"吟看桂生溪月上，醉听鲲化海涛翻。\",\"好期圣代重相见，莫学袁生老竹轩。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04fdccfbf24819d4d5a','塞上其一','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"秋风汉北雁飞天，单骑那堪绕贺兰。\",\"碛暗更无岩树影，地平时有野烧瘢。\",\"貂披寒色和衣冷，剑佩胡霜隔匣寒。\",\"早晚横戈似飞尉，拥旄深入异田单。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04fde40f5829bec9a7b','闲居寄陈山人','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"闲居何处得闲名，坐掩衡茅损性灵。\",\"破梦晓钟闻竹寺，沁心秋雨浸莎庭。\",\"瓮边难负千杯绿，海上终眠万仞青。\",\"珍重先生全太古，应看名利似浮萍。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04fedadc71518f7adde','忆南中','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"碧江头与白云门，别后秋霜点鬓根。\",\"长记学禅青石寺，最思共醉落花村。\",\"林间竹有湘妃泪，窗外禽多杜宇魂。\",\"未棹扁舟重回首，采薇收橘不堪论。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04ff1f513b40f8368ca','约张处士游梁','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"莫学区区老一经，夷门关吏旧书生。\",\"晋朝灭后无中散，韩国亡来绝上卿。\",\"龙变洞中千谷冷，剑横天外八风清。\",\"好携长策干时去，免逐渔樵度太平。\"]',NULL,NULL,NULL,NULL),('6ce235a854cfa04ffacb43f295c8d455','寄岐山林逢吉明府','谭用之','6ce235a854cfa04fd1457b72d1457b72','五代十国',NULL,'[\"岐山高与陇山连，制锦无私服晏眠。\",\"鹦鹉语中分百里，凤凰声里过三年。\",\"秦无旧俗云烟媚，周有遗风父老贤。\",\"莫役生灵种杨柳，一枝枝折灞桥边。\"]',NULL,NULL,NULL,NULL),('6ce235a85552997e182a8ee4933c0058','遣歌妓','杨玢','6ce235a85552997ed1457b72d1457b72','五代十国',NULL,'[\"垂老无端用意乖，谁知道侣厌清斋。\",\"如今又采蘼芜去，辜负张君绣靸鞋。\"]',NULL,NULL,NULL,NULL),('6ce235a85552997e35c1d68ee4ef25ad','登慈恩寺塔','杨玢','6ce235a85552997ed1457b72d1457b72','五代十国',NULL,'[\"紫云楼下曲江平，鸦噪残阳麦陇青。\",\"莫上慈恩最高处，不堪看又不堪听。\"]',NULL,NULL,NULL,NULL),('6ce235a85552997ec5dcd023e9939f87','批子弟理旧居状','杨玢','6ce235a85552997ed1457b72d1457b72','五代十国',NULL,'[\"四邻侵我我从伊，毕竟须思未有时。\",\"试上含元殿基望，秋风秋草正离离。\"]',NULL,NULL,NULL,NULL),('6ce235a8592b1c1e643ca13a572985b0','送钟员外','萧彧','6ce235a8592b1c1ed1457b72d1457b72','五代十国',NULL,'[\"丽汉金波满，当筵玉斝倾。\",\"因思频聚散，几复换亏盈。\",\"光彻离襟冷，声符别管清。\",\"那堪还目此，两地倚楼情。\"]',NULL,NULL,NULL,NULL),('6ce235a8592b1c1ef312981b3e5a0105','送德林郎中学士赴东府','萧彧','6ce235a8592b1c1ed1457b72d1457b72','五代十国',NULL,'[\"离情折杨柳，此别异春哉。\",\"含露东篱艳，泛香南浦杯。\",\"惜持行次赠，留插醉中回。\",\"暮齿如能制，玉山甘判颓。\"]',NULL,NULL,NULL,NULL),('6ce235a859739c42d88ac227913385fd','乞荆浩画','大愚','6ce235a859739c42d1457b72d1457b72','五代十国',NULL,'[\"六幅故牢健，知君恣笔踪。\",\"不求千涧水，止要两株松。\",\"树下留盘石，天边纵远峰。\",\"近岩幽湿处，惟藉墨烟浓。\"]',NULL,NULL,NULL,NULL),('6ce235a85b36c7460bc14a36a11ecfa0','咏灯','李昪','6ce235a85b36c746078719152f885d0f','五代十国',NULL,'[\"一点分明值万金，开时惟怕冷风侵。\",\"主人若也勤挑拨，敢向尊前不尽心。\"]',NULL,NULL,'李昪这首诗吟给徐温听，十分贴切。一点分明值万金，表面上是说，灯火明亮贵值万金，实际是说我这个灯火是很有价值的；开时惟怕冷风侵，表面上是说，灯火怕风、容易被吹熄灭，实际是说我很弱小，需要你的照拂才能生存；主人若也勤挑拨，敢向尊前不尽心。表面是说，灯芯需要主人勤来挑拨，才能持续明亮（古代那灯就是这样）；实际是说您多关注照拂我，我是一定会竭诚以报答的。这首其实是向徐温表心意的诗，温听后大悦。',NULL),('6ce235a85c2cbab70f4646f0c4d5ed80','再次前韵代梅答','汤悦','6ce235a85c2cbab7d1457b72d1457b72','五代十国',NULL,'[\"托植经多稔，顷筐向盛时。\",\"枝条虽已故，情分不曾移。\",\"莫向阶前老，还同镜里衰。\",\"更应怜堕叶，残吹挂虫丝。\"]',NULL,NULL,NULL,NULL),('6ce235a85c2cbab75282700004e2f9c9','鼎臣学士侍郎楚金舍人学士以再伤庭梅诗同垂宠和清绝感叹情致俱深因成四十字陈谢','汤悦','6ce235a85c2cbab7d1457b72d1457b72','五代十国',NULL,'[\"人物同迁谢，重成念旧悲。\",\"连华得琼玖，合奏发埙篪。\",\"馀蘖虽无取，残芳尚获知。\",\"问君何所似，珍重杜秋诗。\"]',NULL,NULL,NULL,NULL),('6ce235a85c2cbab7568ec0ab55f7150f','送季大夫牧舒州','汤悦','6ce235a85c2cbab7d1457b72d1457b72','五代十国',NULL,'[\"却下乌台建隼旟，侯封归去袭龙舒。\",\"严霜尚满辞天阙，甘雨看随入境车。\"]',NULL,NULL,NULL,NULL),('6ce235a85c2cbab7892da2b7ed593e65','鼎臣学士侍郎以东馆庭梅昔翰苑之毫末今复半枯向时同僚零落都尽素发垂领兹唯二人感旧伤怀发于吟咏惠然好我不能无言辄次来韵攀和','汤悦','6ce235a85c2cbab7d1457b72d1457b72','五代十国',NULL,'[\"忆见萌芽日，还怜合抱时。\",\"旧欢如梦想，物态暗还移。\",\"素艳今无几，朱颜亦自衰。\",\"树将人共老，何暇更悲丝。\"]',NULL,NULL,NULL,NULL),('6ce235a85c2cbab7e5216555a10822ab','奉和圣制送邓王牧宣城','汤悦','6ce235a85c2cbab7d1457b72d1457b72','五代十国',NULL,'[\"千里陵阳同陕服，凿门胙土寄亲贤。\",\"曙烟已别黄金殿，晚照重登白玉筵。\",\"江上浮光宜雨后，郡中远岫列窗前。\",\"天心待报期年政，留与工师播管弦。\"]',NULL,NULL,NULL,NULL),('6ce235a85c7d495e895f8cb3f8e0e632','题青草湖神祠','刘山甫','6ce235a85c7d495ed1457b72d1457b72','五代十国',NULL,'[\"坏墙风雨几经春，草色盈庭一座尘。\",\"自是神明无感应，盛衰何得却由人。\"]',NULL,NULL,NULL,NULL),('6ce235a86320f3e7df2da566407387dd','咏鹰','高越','6ce235a86320f3e7d1457b72d1457b72','五代十国',NULL,'[\"雪爪星眸世所稀，摩天专待振毛衣。\",\"虞人莫谩张罗网，未肯平原浅草飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67224a09b80344481c','柳','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"茏葱二月初，青软自相纡。\",\"意态花犹少，风流木更无。\",\"影繁晴陌上，烟重古城隅。\",\"炀帝河声里，几番荣又枯。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d672e312341fbd56ef8','湖上望庐山','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"辍棹南湖首重回，笑青吟翠向崔嵬。\",\"天应不许人全见，长把云藏一半来。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d674e94d4734718d406','春苔','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"底物最牵吟，秋苔独自寻。\",\"何时连夜雨，叠翠满松阴。\",\"湘岸荒祠静，吴宫古砌深。\",\"侯门还可惜，长被马蹄侵。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d675ef720476c863b64','看牡丹二首','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"莫将红粉比秾华，红粉那堪比此花。\",\"隔院闻香谁不惜，出栏呈艳自应夸。\",\"北方有态须倾国，西子能言亦丧家。\",\"输我一枝和晓露，真珠帘外向人斜。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d675ef7204774d25558','看牡丹二首','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"看花长到牡丹月，万事全忘自不知。\",\"风促乍开方可惜，雨淋将谢可堪悲。\",\"闲年对坐浑成偶，醉后抛眠恐负伊。\",\"也拟便休还改过，迢迢争奈一年期。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d675f64c3fd5e8a7308','看桑','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"簇簇互相遮，闲看实可嗟。\",\"藉多虽是叶，栽盛不如花。\",\"春绿暗连麦，秋乾暮立鸦。\",\"旧乡曾种得，经乱属谁家。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d6765dc784e359f4769','庐山瀑布','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"有山来便有，万丈落云端。\",\"雾喷千岩湿，雷倾九夏寒。\",\"图中僧写去，湖上客回看。\",\"却羡为猿鹤，飞鸣近碧湍。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d679aa5288d892018bf','主人司空后亭牡丹','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"佳卉挺芳辰，夭容乃绝伦。\",\"望开从隔岁，愁过即无春。\",\"体物真英气，馀花似庶人。\",\"蜂攒知眷恋，鸟语亦殷勤。\",\"况在豪华地，宁同里巷尘。\",\"酷怜应丧德，多赏奈怡神。\",\"忌秽栽时土，尝甜折处津。\",\"绕行那识倦，围坐岂辞频。\",\"入梦殊巫峡，临池胜洛滨。\",\"乐喧丝杂竹，露渍卯连寅。\",\"饮兴尤思满，吟情自合新。\",\"怕风惟怯夜，忧雨不经旬。\",\"栏槛为良援，亭台是四邻。\",\"虽非能伐性，争免碍还淳。\",\"斗艳何惭蜀，矜繁未让秦。\",\"私心期一日，许近看逡巡。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d679fa8203e96407c46','题未开牡丹','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"青苞虽小叶虽疏，贵气高情便有馀。\",\"浑未盛时犹若此，算应开日合何如。\",\"寻芳蝶已栖丹槛，衬落苔先染石渠。\",\"无限风光言不得，一心留在暮春初。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d679fb5f53c800be48f','牡丹','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"意态天生异，转看看转新。\",\"百花休放艳，三月始为春。\",\"蝶死难离槛，莺狂不避人。\",\"其如豪贵地，清醒复何因。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67a0fca8e0094486d0','甘露寺紫薇花','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"蜀葵鄙下兼全落，菡萏清高且未开。\",\"赫日迸光飞蝶去，紫薇擎艳出林来。\",\"闻香不称从僧舍，见影尤思在酒杯。\",\"谁笑晚芳为贱劣，便饶春丽已尘埃。\",\"牵吟过夏惟忧尽，立看移时亦忘回。\",\"惆怅寓居无好地，懒能分取一枝栽。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67af83359e71301fc8','题梅岭泉','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"梅岭旧闻传，林亭势峞然。\",\"登临真不易，幽胜恐无先。\",\"楚野平千里，吴江曲一边。\",\"标形都大别，洞府岂知焉。\",\"飞阁横空去，征帆落面前。\",\"南雄雉堞峻，北壮凤台连。\",\"烂熳三春媚，参差百卉妍。\",\"风桃诸处锦，洛竹半溪烟。\",\"燕入晴梁语，莺从暖谷迁。\",\"石根朝霭碧，帘际晚霞鲜。\",\"径柳行难约，庭莎醉好眠。\",\"清明时更异，造化意疑偏。\",\"不独宜韶景，尤须看暑天。\",\"药苗繁似结，萝蔓猛如编。\",\"珠亚垂枝果，冰澄汲井泉。\",\"粉墙蜩蜕落，丹槛雀雏颠。\",\"炎气微茫觉，清飙左右穿。\",\"云峰从勃起，葵叶岂劳扇。\",\"又见秋天丽，浑将夏日悬。\",\"红鬒著霜树，香老卧池边。\",\"菱芡谁铺绣，莓苔自学钱。\",\"暗虫依砌响，明月逗帘圆。\",\"小砌滋新菊，高轩噪暮蝉。\",\"雨声寒飒飒，雁影晓联联。\",\"释此何堪玩，深冬更可怜。\",\"窗中看短景，树里见重川。\",\"冈阜分明出，杉松气概全。\",\"讴成白雪曲，吟是早梅篇。\",\"创制谁人解，根基太守贤。\",\"或时留皂盖，尽日簇华筵。\",\"谁咏忧黎庶，狂游泥管弦。\",\"交加丰玉食，来去迸金船。\",\"侍从非常客，俳谐像列仙。\",\"画旗张赫奕，妖妓舞婵娟。\",\"罢宴心犹恋，将归兴尚牵。\",\"只应愁逼夜，宁厌赏经年。\",\"孤贱今何幸，跻攀奈有缘。\",\"展眉惊豁达，徐步喜周旋。\",\"讽咏虽知苦，推功靡极玄。\",\"聊书四十韵，甘责未精专。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67b22c3e6645779645','又题牡丹上主人司空','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"一年芳胜一年芳，爱重贤侯意异常。\",\"手辟红房看阔狭，自张青幄盖馨香。\",\"白疑美玉无多润，紫觉灵芝不是祥。\",\"只恐梦徵他日去，又须疑向凤池傍。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67c76349c5d813ee23','主人司空见和未开牡丹辄却奉和','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"把笔临芳不自怡，首徵章句促妖期。\",\"已惊常调言多鄙，遽捧高吟愧可知。\",\"绝代贞名应愈重，千金方笑更难移。\",\"狂歌狂醉犹堪羡，大拙当时是老时。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67dcb2c8f68040d784','甘露寺','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"寒暄皆有景，孤绝画难形。\",\"地拱千寻崄，天垂四面青。\",\"昼灯笼雁塔，夜磬彻渔汀。\",\"最爱僧房好，波光满户庭。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67e0b41bec72575eff','牡丹落后有作','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"未发先愁有一朝，如今零落更魂销。\",\"青丛别后无多色，红线穿来已半焦。\",\"蓄恨绮罗犹眷眷，薄情蜂蝶去飘飘。\",\"明年虽道还期在，争奈凭栏乍寂寥。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67e13ab1af555fdbb6','题金山寺','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"万古波心寺，金山名目新。\",\"天多剩得月，地少不生尘。\",\"过橹妨僧定，惊涛溅佛身。\",\"谁言张处士，题后更无人。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67e90d415eecbdbc5f','老松','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"郁郁复苍苍，秋风韵更长。\",\"空心应有□，老叶不知霜。\",\"子落生深涧，阴清背夕阳。\",\"如逢东岱雨，犹得覆秦王。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67f819547acdb13269','芳草','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"何处不相见，烟苗捧露心。\",\"萋萋绿远水，苒苒在空林。\",\"野吹闲摇阔，游人醉卧深。\",\"南朝古城里，碑石又应沉。\"]',NULL,NULL,NULL,NULL),('6ce235a8651f4d67fdd4dde6c1516c07','柳絮咏','孙鲂','6ce235a8651f4d67d1457b72d1457b72','五代十国',NULL,'[\"年年三月里，随处自悠扬。\",\"雨过浑疑尽，风来特地狂。\",\"入花蜂有碍，遮水燕无妨。\",\"苦是添离思，青门道路长。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9800dd2b87d5fff2de','晚次','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"烟郭遥闻向晚鸡，水平舟静浪声齐。\",\"高林带雨杨梅熟，曲岸笼云谢豹啼。\",\"二女庙荒汀树老，九疑山碧楚天低。\",\"湘南自古多离怨，莫动哀吟易惨悽。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98136717709eb8429b','长安道中早行','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"客离孤馆一灯残，牢落星河欲曙天。\",\"鸡唱未沈函谷月，雁声新度灞陵烟。\",\"浮生已悟庄周蝶，壮志仍输祖逖鞭。\",\"何事悠悠策羸马，此中辛苦过流年。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9836da68b43c6e923c','芍药','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"香清粉澹怨残春，蝶翅蜂须恋蕊尘。\",\"闲倚晚风生怅望，静留迟日学因循。\",\"休将薜荔为青琐，好与玫瑰作近邻。\",\"零落若教随暮雨，又应愁杀别离人。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9837ffe6d3723ecdd4','边上','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"戍楼吹角起征鸿，猎猎寒旌背晚风。\",\"千里暮烟愁不尽，一川秋草恨无穷。\",\"山河惨澹关城闭，人物萧条市井空。\",\"只此旅魂招未得，更堪回首夕阳中。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9843641d20986e4d09','春江雨','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"雨溟溟，风泠泠，老松瘦竹临烟汀。\",\"空江冷落野云重，村中鬼火微如星。\",\"夜惊溪上渔人起，滴沥篷声满愁耳。\",\"子规叫断独未眠，罨岸春涛打船尾。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9858197e860558dbdd','春夕言怀','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"风透疏帘月满庭，倚栏无事倍伤情。\",\"烟垂柳带纤腰软，露滴花房怨脸明。\",\"愁逐野云销不尽，情随春浪去难平。\",\"幽窗谩结相思梦，欲化西园蝶未成。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98632e7905f556f522','春日旅泊桂州','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"暖风芳草竟芊绵，多病多愁负少年。\",\"弱柳未胜寒食雨，好花争奈夕阳天。\",\"溪边物色堪图画，林畔莺声似管弦。\",\"独有离人开泪眼，强凭杯酒亦潸然。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c986a56837ea746f884','经旧游','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"暂到高唐晓又还，丁香结梦水潺潺。\",\"不知云雨归何处，历历空留十二山。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9887af2fd50063b36a','惆怅吟','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"秋风丹叶动荒城，惨澹云遮日半明。\",\"昼梦却因惆怅得，晚愁多为别离生。\",\"江淹彩笔空留恨，庄叟玄谭未及情。\",\"千古怨魂销不得，一江寒浪若为平。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c9892e146c1a68e7956','春晚谣','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"雨微微，烟霏霏，小庭半拆红蔷薇。\",\"钿筝斜倚画屏曲，零落几行金雁飞。\",\"萧关梦断无寻处，万叠春波起南浦。\",\"凌乱杨花扑绣帘，晚窗时有流莺语。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98a340fcbd0234f373','秋晚过洞庭','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"征帆初挂酒初酣，暮景离情两不堪。\",\"千里晚霞云梦北，一洲霜橘洞庭南。\",\"溪风送雨过秋寺，涧石惊龙落夜潭。\",\"莫把羁魂吊湘魄，九疑愁绝销烟岚。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98b432baee28b71d17','惜花','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"蝶散莺啼尚数枝，日斜风定更离披。\",\"看多记得伤心事，金谷楼前委地时。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98c711bbce9a623ee8','题华严寺木塔','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"六街晴色动秋光，雨霁凭高只易伤。\",\"一曲晚烟浮渭水，半桥斜日照咸阳。\",\"休将世路悲尘事，莫指云山认故乡。\",\"回首汉宫楼阁暮，数声钟鼓自微茫。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98d81ccf204d02474b','碧户','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"碧户扃鱼锁，兰窗掩镜台。\",\"落花疑怅望，归燕自裴回。\",\"咏絮知难敌，伤春不易裁。\",\"恨从芳草起，愁为晚风来。\",\"衣惹湘云薄，眉分楚岫开。\",\"香浓眠旧枕，梦好醉春杯。\",\"小障明金凤，幽屏点翠苔。\",\"宝筝横塞雁，怨笛落江梅。\",\"卓氏仍多酒，相如正富才。\",\"莫教琴上意，翻作鹤声哀。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98d8b5bb8b622175cb','所思','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"空塘水碧春雨微，东风散漫杨柳飞。\",\"依依南浦梦犹在，脉脉高唐云不归。\",\"江头日暮多芳草，极目伤心烟悄悄。\",\"隔江红杏一枝明，似玉佳人俯清沼。\",\"休向春台更回望，销魂自古因惆怅。\",\"银河碧海共无情，两处悠悠起风浪。\"]',NULL,NULL,NULL,NULL),('6ce235a865eb5c98fa8270f487534d0e','洞庭湖','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"空江浩荡景萧然，尽日菰蒲泊钓船。\",\"青草浪高三月渡，绿杨花扑一溪烟。\",\"情多莫举伤春目，愁极兼无买酒钱。\",\"犹有渔人数家住，不成村落夕阳边。\"]',NULL,'江面上浩荡荡景色萧萧然，终日里只看见菰蒲钓鱼船。\n青草湖里浪高三月从此过，杨花飞絮扑面江上风烟满。\n多情怀绪莫要观看伤春景，无法消愁只因没有买酒钱。\n极远处还布那几户渔家在，不成村落散住在那夕阳边。',NULL,'[\"空江：洞庭湖水面横无际涯，尽日因风被阻，所以显得江面空旷。浩荡：指风吹浪涌，白波浩荡记。景萧然：这是同无风时比较，无风时江面百舸争流，异常热闹，而今船只避风，江面显得萧然。\",\"菰（gū）：即艾白；蒲（pú）：水草，孤与蒲，皆生长在浅水处。泊钓船：钓船停在港湾中。\",\"青草：湖名，今在湖南省岳阳县西南，接湘阴县界，因湖南省有青草山，而且湖中多青草，故名。青草湖向来就和洞庭湖并称。一湖之内，有沙洲间隔，一名青草，一名洞庭。\",\"情多：指多愁善感的人。莫举伤春目：不要放眼看那使人伤心的春天的景象。\",\"兼：又加上。\",\"渔人：打渔的人。夕阳边：在夕阳洒落的岸边。\"]'),('6ce235a865eb5c98fc636e5b22efc0f1','寄人二首','张泌（佖）','6ce235a865eb5c981cc3633cd1457b72','五代十国',NULL,'[\"【其一】\",\"别梦依依到谢家，小廊回合曲阑斜。\",\"多情只有春庭月，犹为离人照落花。\",\"【其二】\",\"酷怜风月为多情，还到春时别恨生。\",\"倚柱寻思倍惆怅，一场春梦不分明。\"]',NULL,NULL,NULL,NULL),('6ce235a866bfcc4f1bca23def9f0b87b','寄禅月大师','张格','6ce235a866bfcc4fd1457b72d1457b72','五代十国',NULL,'[\"龙华咫尺断来音，日夕空驰咏德心。\",\"禅月字清师号别，寿春诗古帝恩深。\",\"画成罗汉惊三界，书似张颠直万金。\",\"莫倚名高忘故旧，晓晴闲步一相寻。\"]',NULL,NULL,NULL,NULL),('6ce235a8672b702b643ca13aa24dd312','送钟员外','孙岘','6ce235a8672b702bd1457b72d1457b72','五代十国',NULL,'[\"万物中萧洒，修篁独逸群。\",\"贞姿曾冒雪，高节欲凌云。\",\"细韵风初发，浓烟日正曛。\",\"因题偏惜别，不可暂无君。\"]',NULL,NULL,NULL,NULL),('6ce235a8675812887264890a3e789396','郊庙歌辞梁郊祀乐章庆融','张衮','6ce235a867581288d1457b72d1457b72','五代十国',NULL,'[\"导和气兮袭氤氲，宣皇使兮彰圣神。\",\"服遐裔兮敷质文，格苗扈兮息烟尘。\"]',NULL,NULL,NULL,NULL),('6ce235a8675812887f512126afa8b6db','郊庙歌辞梁郊祀乐章庆和','张衮','6ce235a867581288d1457b72d1457b72','五代十国',NULL,'[\"烟燎升，礼容彻。诚感达，人神悦。灵贶彰，圣情结。玉座寂，金垆歇。\"]',NULL,NULL,NULL,NULL),('6ce235a86758128895bb0913d9f4125b','郊庙歌辞梁郊祀乐章庆熙','张衮','6ce235a867581288d1457b72d1457b72','五代十国',NULL,'[\"哲后躬享，旨酒斯陈。\",\"王恭无斁，严祀惟寅。\",\"皇祖以配，大孝以振。\",\"宜锡景福，永休下民。\"]',NULL,NULL,NULL,NULL),('6ce235a867581288a50274e034075858','郊庙歌辞梁郊祀乐章庆隆','张衮','6ce235a867581288d1457b72d1457b72','五代十国',NULL,'[\"恭祀上帝，于国之阳。\",\"爵醴是荷，鸿基永昌。\"]',NULL,NULL,NULL,NULL),('6ce235a867581288c7a33d02a241ea33','郊庙歌辞梁郊祀乐章庆休','张衮','6ce235a867581288d1457b72d1457b72','五代十国',NULL,'[\"大业来四夷，仁风和万国。\",\"白日体无私，皇天辅有德。\",\"七旬罪已服，六旬师方克。\",\"伟哉帝道隆，终始常作则。\"]',NULL,NULL,NULL,NULL),('6ce235a867581288ff27b4be3711d2c9','郊庙歌辞梁郊祀乐章庆肃','张衮','6ce235a867581288d1457b72d1457b72','五代十国',NULL,'[\"笾豆簠簋，黍稷非馨。\",\"懿兹彝器，厥德惟明。\",\"金石匏革，以和以平。\",\"由此无疆，期乎永宁。\"]',NULL,NULL,NULL,NULL),('6ce235a86c82c1348645a3e4c15fc87f','赏牡丹应教','谦光','6ce235a86c82c134d1457b72d1457b72','五代十国',NULL,'[\"拥衲对芳丛，由来事不同。\",\"鬓从今日白，花似去年红。\",\"艳异随朝露，馨香逐晓风。\",\"何须对零落，然后始知空。\"]',NULL,NULL,NULL,NULL),('6ce235a86e5ccf263b50ae5f9e97db19','过衡山留赠廖融','张观','6ce235a86e5ccf26d1457b72d1457b72','五代十国',NULL,'[\"未向漆园为傲吏，定应明代作徵君。\",\"传家奕世无金玉，乐道经年有典坟。\",\"带雨小舟横别涧，隔花幽犬吠深云。\",\"到头终为苍生起，休恋耕烟楚水濆。\"]',NULL,NULL,NULL,NULL),('6ce235a875db7e71a6e072b5eeab7297','述怀','崔氏','6ce235a875db7e71d1457b72d1457b72','五代十国',NULL,'[\"不怨卢郎年纪大，不怨卢郎官职卑。\",\"自恨妾身生较晚，不及卢郎年少时。\"]',NULL,NULL,NULL,NULL),('6ce235a87641e4a4da1d4a4b808be2fe','山中早起','刘乙','6ce235a87641e4a4d1457b72d1457b72','五代十国',NULL,'[\"鸡调扶桑枝，秋空隐少微。\",\"阔云霞并曜，高日月争辉。\",\"若厥开天道，同初发帝机。\",\"以言当代事，闲辟紫宸扉。\"]',NULL,NULL,NULL,NULL),('6ce235a87641e4a4f61731e35c15e36c','晓望','刘乙','6ce235a87641e4a4d1457b72d1457b72','五代十国',NULL,'[\"地祇逃秀境，神化或殷雷。\",\"裂汉娲补合，高峰剑跃开。\",\"即今新定业，何世不遗才。\",\"若是浮名道，须言有祸胎。\"]',NULL,NULL,NULL,NULL),('6ce235a8777e4905f4578269f0e98770','歌','白衫举子','6ce235a8777e4905d1457b72d1457b72','五代十国',NULL,'[\"执板狂歌乞个钱，尘中流浪且随缘。\",\"直饶到老常如此，犹胜危时弄化权。\"]',NULL,NULL,NULL,NULL),('6ce235a87807d4e0e72f76298400cfbb','感怀诗','刘义度','6ce235a87807d4e0d1457b72d1457b72','五代十国',NULL,'[\"昨日方髽髻，如今满颔髯。\",\"紫阁无心恋，青山有意潜。\"]',NULL,NULL,NULL,NULL),('6ce235a87a0ab30ec336dc5cf0e94335','荆台道院','梁震','6ce235a87a0ab30ed1457b72d1457b72','五代十国',NULL,'[\"桑田一变赋归来，爵禄焉能浼我哉。\",\"黄犊依然花竹外，清风万古凛荆台。\"]',NULL,NULL,NULL,NULL),('6ce235a87aed643873391f35022af20a','陪游凤皇台献诗','宋齐丘','6ce235a87aed64387ce3284b0f840be9','五代十国',NULL,'[\"嵯峨压洪泉，岝峉撑碧落。\",\"宜哉秦始皇，不驱亦不凿。\",\"上有布政台，八顾背城郭。\",\"山蹙龙虎健，水黑螭蜃作。\",\"白虹欲吞人，赤骥相?㩧。\",\"画栋泥金碧，石路盘硗埆。\",\"倒挂哭月猿，危立思天鹤。\",\"凿池养蛟龙，栽桐栖鸑鷟。\",\"梁间燕教雏，石罅蛇悬壳。\",\"养花如养贤，去草如去恶。\",\"日晚严城鼓，风来萧寺铎。\",\"扫地驱尘埃，剪蒿除鸟雀。\",\"金桃带叶摘，绿李和衣嚼。\",\"贞竹无盛衰，媚柳先摇落。\",\"尘飞景阳井，草合临春阁。\",\"芙蓉如佳人，回首似调谑。\",\"当轩有直道，无人肯驻脚。\",\"夜半鼠窸窣，天阴鬼敲啄。\",\"松孤不易立，石丑难安著。\",\"自怜啄木鸟，去蠹终不错。\",\"晚风吹梧桐，树头鸣嚗嚗。\",\"峨峨江令石，青苔何淡薄。\",\"不话兴亡事，举首思眇邈。\",\"吁哉未到此，褊劣同尺蠖。\",\"笼鹤羡凫毛，猛虎爱蜗角。\",\"一日贤太守，与我观橐籥。\",\"往往独自语，天帝相唯诺。\",\"风云偶不来，寰宇销一略。\",\"我欲烹长鲸，四海为鼎镬。\",\"我欲取大鹏，天地为矰缴。\",\"安得生羽翰，雄飞上寥廓。\"]',NULL,NULL,NULL,NULL),('6ce235a87aed643895917b67131f66b0','赠仰山慧度禅师','宋齐丘','6ce235a87aed64387ce3284b0f840be9','五代十国',NULL,'[\"初闻如自解，及见胜初闻。\",\"两鬓堆残雪，一身披断云。\",\"道应齐古佛，高不揖吾君。\",\"稽首清凉月，萧然万象分。\"]',NULL,NULL,NULL,NULL),('6ce235a87aed6438bd569f20ea8be76d','陪华林园试小妓羯鼓','宋齐丘','6ce235a87aed64387ce3284b0f840be9','五代十国',NULL,'[\"切断牙床镂紫金，最宜平稳玉槽深。\",\"因逢淑景开佳宴，为出花奴奏雅音。\",\"掌底轻璁孤鹊噪，枝头乾快乱蝉吟。\",\"开元天子曾如此，今日将军好用心。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f05fc0edcbb1f3713','宿故人江居','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"渡口树冥冥，南山渐隐青。\",\"渔舟归旧浦，鸥鸟宿前汀。\",\"静榻悬灯坐，闲门对浪扃。\",\"相思频到此，几番醉还醒。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f195e4288fbf2accd','送吴梦訚归闽','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"瓯闽在天末，此去整行衣。\",\"久客逢春尽，思家冒暑归。\",\"海云添晚景，山瘴灭晴晖。\",\"相忆吟偏苦，不堪书信稀。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f1eeadb304b8ef14e','怀果上人','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"拥锡南游去，名香几处焚。\",\"别来无远信，多恐在深云。\",\"好月曾同步，幽香省共闻。\",\"相思不相见，林下叶纷纷。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f25797f6d4a70e920','山中访人不遇','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"负琴兼杖藜，特地过岩西。\",\"已见竹轩闭，又闻山鸟啼。\",\"长松寒倚谷，细草暗连溪。\",\"久立无人事，烟霞归路迷。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f341a8a7faafc49aa','夏日寄史处士','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"掩关苔满地，终日坐腾腾。\",\"暑气冷衣葛，暮云催烛灯。\",\"寂寥知得趣，疏懒似无能。\",\"还忆旧游否，何年别杜陵。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f401bcefc71a85fcc','过王','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"谷口何时住，烟霞一径深。\",\"水声离远洞，山色出疏林。\",\"雪彩从沾鬓，年光不计心。\",\"自言人少到，犹喜我来寻。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f413f45c300c1aba3','早秋吟眺','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"新秋初雨后，独立对遥山。\",\"去鸟望中没，好云吟里还。\",\"长年惭道薄，明代取身闲。\",\"从有西征思，园林懒闭关。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f4c3ce9483a6358af','夏日登瀑顶寺因寄诸知己','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"曾于尘里望，此景在烟霄。\",\"岩静水声近，山深暑气遥。\",\"杖藜青石路，煮茗白云樵。\",\"寄语为郎者，谁能访寂寥。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f511772af78599b91','春江送人','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"春江多去情，相去枕长汀。\",\"数雁别湓浦，片帆离洞庭。\",\"雨馀沙草绿，云散岸峰青。\",\"谁共观明月，渔歌夜好听。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f6825ac48801bacdf','寄故园兄弟','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"久与乡关阻，风尘损旧衣。\",\"水思和月泛，山忆共僧归。\",\"林想添邻舍，溪应改钓矶。\",\"弟兄无苦事，不用别庭闱。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f7fb24d90027a9584','寄李处士','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"僧话磻溪叟，平生重赤松。\",\"夜堂悲蟋蟀，秋水老芙蓉。\",\"吟坐倦垂钓，闲行多倚筇。\",\"闻名来已久，未得一相逢。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8f9f36ad8f10a0800a','赠隐者','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"世路争名利，深山独结茅。\",\"安情自得所，非道岂相交。\",\"百尺松当户，千年鹤在巢。\",\"知君于此景，未欲等闲抛。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8fb43dbf62dd711927','过秦岭','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"古今传此岭，高下势峥嵘。\",\"安得青山路，化为平地行。\",\"苍苔留虎迹，碧树障溪声。\",\"欲过一回首，踟蹰无限情。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8fbaa3be9deed37b46','寄伍乔','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"蹉跎春又晚，天末信来迟。\",\"长忆分携日，正当摇落时。\",\"独游饶旅恨，多事失归期。\",\"君看前溪树，山禽巢几枝。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8fc61ddff128927bd0','江边闲步','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"闲来南渡口，迤逦看江枫。\",\"一路波涛畔，数家芦苇中。\",\"远汀排晚树，深浦漾寒鸿。\",\"吟罢慵回首，此情谁与同。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8fcd59a8d2c28b1406','寄暹上人','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"闻罢城中讲，来安顶上禅。\",\"夜灯明石室，清磬出岩泉。\",\"欲访惭多事，相思恨隔年。\",\"终期息尘虑，接话虎溪边。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8fe749e46c4a64a027','赠栖隐洞谭先生','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"先生双鬓华，深谷卧云霞。\",\"不伐有巢树，多移无主花。\",\"石泉春酿酒，松火夜煎茶。\",\"因问山中事，如君有几家。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8fe9cfb66c564d15cc','归雁','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"春至衡阳雁，思归塞路长。\",\"汀洲齐奋翼，霄汉共成行。\",\"雪尽翻风暖，寒收度月凉。\",\"直应到秋日，依旧返潇湘。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8feae8906f7925d174','宿山寺','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"溪山尽日行，方听远钟声。\",\"入院逢僧定，登楼见月生。\",\"露垂群木润，泉落一岩清。\",\"此景关吾事，通宵寐不成。\"]',NULL,NULL,NULL,NULL),('6ce235a87bf6be8ff888459f6f13c27e','山中夏日','孟贯','6ce235a87bf6be8fd1457b72d1457b72','五代十国',NULL,'[\"深山宜避暑，门户映岚光。\",\"夏木荫溪路，昼云埋石床。\",\"心源澄道静，衣葛蘸泉凉。\",\"算得红尘里，谁知此兴长。\"]',NULL,NULL,NULL,NULL),('6ce235a87debdbf132964cb03804d34b','雪晴','杨凝式','6ce235a87debdbf198d6f58a6395ebd0','五代十国',NULL,'[\"春来冰未泮，冬至雪初晴。\",\"为报方袍客，丰年瑞已成。\"]',NULL,NULL,NULL,NULL),('6ce235a87debdbf1b428d6a29bd91265','题壁','杨凝式','6ce235a87debdbf198d6f58a6395ebd0','五代十国',NULL,'[\"院似禅心静，花如觉性圆。\",\"自然知了义，争肯学神仙。\"]',NULL,NULL,NULL,NULL),('6ce235a87debdbf1d16daf7246d71c74','赠张全义','杨凝式','6ce235a87debdbf198d6f58a6395ebd0','五代十国',NULL,'[\"洛阳风景实堪哀，昔日曾为瓦子堆。\",\"不是我公重葺理，至今犹是一堆灰。\"]',NULL,NULL,NULL,NULL),('6ce235a87debdbf1f71754ec6eab5ea1','题怀素酒狂帖后','杨凝式','6ce235a87debdbf198d6f58a6395ebd0','五代十国',NULL,'[\"十年挥素学临池，始识王公学卫非。\",\"草圣未须因酒发，笔端应解化龙飞。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d704eeffa6517228af','咏后主出降','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"蜀朝昏主出降时，衔璧牵羊倒系旗。\",\"二十万人齐拱手，更无一个是男儿。\"]',NULL,NULL,'后蜀何光远撰《鉴诫录》卷五《徐后事》：“故兴圣太子随军王承旨（原注：失名），有《咏后主出降》诗曰：‘蜀朝昏主出降时，衔璧牵羊倒系旗。二十万军齐拱手，更无一个是男儿。’”何是后蜀前期人。《舆地碑记目》卷四《普州碑记》载：“《聂公真龛记》，在灵居山，军事判官何光远撰，广政四年（942年）建。”今此碑尚在安岳圆觉洞第八十二龛，今人刘长久《中国西南石窟研究》有引。《鉴诫录》一书今存宋本，上海图书馆从海外购归，已影印。其成书时间不晚于后蜀后主中后期，所以此诗原作者肯定不是花蕊夫人徐（费）氏。若宋人所载属实，则花蕊夫人徐（费）氏只是略改王诗以答宋太祖，也属急智。',NULL),('6ce235a87e5a57d71b738b5774cb0aa6','和蜀后主题剑门','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"孟阳曾有语，刊在白云棱。\",\"李杜常挨托，孙刘亦恃凭。\",\"庸才安可守，上德始堪矜。\",\"暗指长天路，浓峦蔽几层。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7386ce1dc47c7fe00','荆南席上咏胡琴妓二首','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"玉纤挑落折冰声，散入秋空韵转清。\",\"二五指中句塞雁，十三弦上啭春莺。\",\"谱从陶室偷将妙，曲向秦楼写得成。\",\"无限细腰宫里女，就中偏惬楚王情。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7386ce1dcc307f113','荆南席上咏胡琴妓二首','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"红妆齐抱紫檀槽，一抹朱弦四十条。\",\"湘水淩波惭鼓瑟，秦楼明月罢吹箫。\",\"寒敲白玉声偏婉，暖逼黄莺语自娇。\",\"丹禁旧臣来侧耳，骨清神爽似闻韶。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d73b7deb5211895555','放猿','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"放尔丁宁复故林，旧来行处好追寻。\",\"月明巫峡堪怜静，路隔巴山莫厌深。\",\"栖宿免劳青嶂梦，跻攀应惬白云心。\",\"三秋果熟松梢健，任抱高枝彻晓吟。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d74a4ba452d16523cc','遇放猿再作','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"嶓冢祠前汉水滨，饮猿连臂下嶙峋。\",\"渐来子细窥行客，认得依稀是野宾。\",\"月宿纵劳羁绁梦，松餐非复稻粱身。\",\"数声肠断和云叫，识是前时旧主人。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d74bf04532a8b27fb7','题麦积山天堂','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"蹑尽悬空万仞梯，等闲身共白云齐。\",\"檐前下视群山小，堂上平分落日低。\",\"绝顶路危人少到，古岩松健鹤频栖。\",\"天边为要留名姓，拂石殷勤身自题。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d74f6b207963082da2','示诸门生','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"二百一十四门生，春风初长羽毛成。\",\"掷金换得天边桂，凿壁偷将榜上名。\",\"何幸不才逢圣世，偶将疏网罩群英。\",\"衰翁渐老儿孙小，异日知谁略有情。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d771e46f2aeb393e0d','过平戎谷吊胡翙','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"立马荒郊满目愁，伊人何罪死林丘。\",\"风号古木悲长在，雨湿寒莎泪暗流。\",\"莫道文章为众嫉，只应轻薄是身雠。\",\"不缘魂寄孤山下，此地堪名鹦鹉洲。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d79755973e014b17ed','从蜀后主幸秦川上梓潼山','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"彩仗拂寒烟，鸣驺在半天。\",\"黄云生马足，白日下松巅。\",\"盛德安疲俗，仁风扇极边。\",\"前程问成纪，此去尚三千。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d799f1d707d46d5aa1','与诸门生春日会饮繁台赋','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"柳阴如雾絮成堆，又引门生饮古台。\",\"淑景即随风雨去，芳樽宜命管弦开。\",\"谩夸列鼎鸣钟贵，宁免朝乌夜兔催。\",\"烂醉也须诗一首，不能空放马头回。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7aacdd717bcc3eeb8','题斗山观','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"霞衣欲举醉陶陶，不觉全家住绛霄。\",\"拔宅只知鸡犬在，上天谁信路岐遥。\",\"三清辽廓抛尘梦，八景云烟事早朝。\",\"为有故林苍柏健，露华凉叶锁金飙。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7b618f59ca04a551c','贺王溥入相','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"一战文场拔赵旗，便调金鼎佐无为。\",\"白麻骤降恩何极，黄发初闻喜可知。\",\"跋敕案前人到少，筑沙堤上马归迟。\",\"立班始得遥相见，亲洽争如未贵时。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7bf267535c6c484df','和韩昭从驾过白卫岭','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"龙旆飘飖指极边，到时犹更二三千。\",\"登高晓蹋巉岩石，冒冷朝冲断续烟。\",\"自学汉皇开土宇，不同周穆好神仙。\",\"秦民莫遣无恩及，大散关东别有天。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7df6c53d196db3c00','题孤云绝顶淮阴祠','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"一握寒天古木深，路人犹说汉淮阴。\",\"孤云不掩兴亡策，两角曾悬去住心。\",\"不是冕旒轻布素，岂劳丞相远追寻。\",\"当时若放还西楚，尺寸中华未可侵。\"]',NULL,NULL,NULL,NULL),('6ce235a87e5a57d7fa9cba881591d6f2','奉诏赋剑州途中鸷兽','王仁裕','6ce235a87e5a57d71f50893f168908dd','五代十国',NULL,'[\"剑牙钉舌血毛腥，窥算劳心岂暂停。\",\"不与大朝除患难，惟馀当路食生灵。\",\"从将户口资嚵口，未委三丁税几丁。\",\"今日帝王亲出狩，白云岩下好藏形。\"]',NULL,NULL,NULL,NULL),('6ce235a87fe02bc8a59b40ea99968233','石城怀古','刘洞','6ce235a87fe02bc8d1457b7292977ae4','五代十国',NULL,'[\"石城古岸头，一望思悠悠。\",\"几许六朝事，不禁江水流。\"]',NULL,NULL,NULL,NULL),('6ce235a880334690b46462d1475bd3c3','题广爱寺楞伽山','净显','6ce235a880334690d1457b72d1457b72','五代十国',NULL,'[\"灵异不能栖鸟雀，幽奇终不着猿猱。\",\"为经巢贼应无损，纵使秦驱也谩劳。\",\"珍重昔贤留像迹，陵迁谷变自坚牢。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e1379e03bee0e8f3c','中秋月','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"王母妆成镜未收，倚栏人在水精楼。\",\"笙歌莫占清光尽，留与溪翁一钓舟。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e161e45f0ef26611c','松','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"大夫名价古今闻，盘屈孤贞更出群。\",\"将谓岭头闲得了，夕阳犹挂数枝云。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e20dae9e9265697eb','除夜','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"铜龙看却送春来，莫惜颠狂酒百杯。\",\"吟鬓就中专拟白，那堪更被二更催。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e27be7b70f481e2a1','元日','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"戴星先捧祝尧觞，镜里堪惊两鬓霜。\",\"好是灯前偷失笑，屠苏应不得先尝。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e3b92d4e7f281a0f3','夜夜曲','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"自从君去夜，锦幌孤兰麝。\",\"欹枕对银缸，秦筝绿窗下。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e411b1f95fcc61b2b','新燕','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"才离海岛宿江滨，应梦笙歌作近邻。\",\"减省雕梁并头语，画堂中有未归人。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e47e0d0f6bc340ad4','夕','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"台榭沈沈禁漏初，麝烟红蜡透虾须。\",\"雕笼鹦鹉将栖宿，不许鸦鬟转辘轳。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e48b9839a368a8fd9','村行','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"暧暧村烟暮，牧童出深坞。\",\"骑牛不顾人，吹笛寻山去。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e55f0e269e5fa7c22','暮春日宴溪亭','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"寒食寻芳游不足，溪亭还醉绿杨烟。\",\"谁家花落临流树，数片残红到槛前。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372e73f0dc135fc0cecb','游紫阳宫','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"古殿烟霞簇画屏，直疑踪迹到蓬瀛。\",\"碧桃满地眠花鹿，深院松窗捣药声。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372ea754aa7dd48fc946','煎茶','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"岳寺春深睡起时，虎跑泉畔思迟迟。\",\"蜀茶倩个云僧碾，自拾枯松三四枝。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372eaee9e211593a9a73','杜鹃花','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"杜鹃花与鸟，怨艳两何赊。\",\"疑是口中血，滴成枝上花。\",\"一声寒食夜，数朵野僧家。\",\"谢豹出不出，日迟迟又斜。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372eb183da42894fab20','会友不至','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"王孙还是负佳期，玉马追游日渐西。\",\"独上郊原人不见，鹧鸪飞过落花溪。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372eb432baeeec14f7bd','惜花','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"忘餐为恋满枝红，锦障频移护晚风。\",\"客散酒酣归未得，栏边独立月明中。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372ebf2916fd16fd89c8','寒夜吟','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"洞房脉脉寒宵永，烛影香消金凤冷。\",\"猧儿睡魇唤不醒，满窗扑落银蟾影。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372edb074953759ea97c','晓','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"列宿回元朝北极，爽神晞露滴楼台。\",\"佳人卷箔临阶砌，笑指庭花昨夜开。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372eeaa820754dc4dcf7','江上枫','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"江枫自蓊郁，不竞松筠力。\",\"一叶落渔家，残阳带秋色。\"]',NULL,NULL,NULL,NULL),('6ce235a8805b372eecdeed799e3e3b15','露','成彦雄','6ce235a8805b372ed1457b72d1457b72','五代十国',NULL,'[\"银河昨夜降醍醐，洒遍坤维万象苏。\",\"疑是鲛人曾泣处，满池荷叶捧真珠。\"]',NULL,NULL,NULL,NULL),('6ce235a881028f09bb376af8ffd335f1','元白席上作','李升','6ce235a881028f09d1457b72d1457b72','五代十国',NULL,'[\"生在儒家遇太平，悬缨垂带布衣轻。\",\"谁能世路趋名利，臣事玉皇归上清。\"]',NULL,NULL,NULL,NULL),('6ce235a88130e2cf0e227119e091e8ff','渔父引二首','李梦符','6ce235a88130e2cfd1457b72d1457b72','五代十国',NULL,'[\"渔弟渔兄喜到来，波官赛却坐江隈。\",\"椰榆杓子木瘤杯，烂煮鲈鱼满案堆。\"]',NULL,NULL,NULL,NULL),('6ce235a88130e2cf0e227119e3446908','渔父引二首','李梦符','6ce235a88130e2cfd1457b72d1457b72','五代十国',NULL,'[\"村寺钟声度远滩，半轮残月落山前。\",\"徐徐拨棹却归湾，浪叠朝霞锦绣翻。\"]',NULL,NULL,NULL,NULL),('6ce235a88130e2cfea7bbfe092149247','答常学士','李梦符','6ce235a88130e2cfd1457b72d1457b72','五代十国',NULL,'[\"罢修儒业罢修真，养拙藏愚春复春。\",\"到老不疏林里鹿，平生难见日边人。\",\"洞桃深处千林锦，岩雪铺时万草新。\",\"深谢名贤远相访，求闻难博凤为邻。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a760f27dd4e1502e75e','送客','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"明月孤舟远，吟髭镊更华。\",\"天形围泽国，秋色露人家。\",\"水馆萤交影，霜洲橘委花。\",\"何当寻旧隐，泉石好生涯。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a765a8633a41b2c0143','旅怀','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"迢迢江汉路，秋色又堪惊。\",\"半夜闻鸿雁，多年别弟兄。\",\"高风云影断，微雨菊花明。\",\"欲寄东归信，裴回无限情。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a766afee675c199e552','登润州城','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"天末江城晚，登临客望迷。\",\"春潮平岛屿，残雨隔虹霓。\",\"鸟与孤帆远，烟和独树低。\",\"乡山何处是，目断广陵西。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a768006c85995db5d81','隋堤柳','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"锦缆龙舟万里来，醉乡繁盛忽尘埃。\",\"空馀两岸千株柳，雨叶风花作恨媒。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a76a3487de5ede059d2','塞下曲','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"万里黄云冻不飞，碛烟烽火夜深微。\",\"胡儿移帐寒笳绝，雪路时闻探马归。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a76b2b91c963f47fb66','临刑诗','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"街鼓侵人急，西倾日欲斜。\",\"黄泉无旅店，今夜宿谁家。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a76d2166c1fcaa3bd0b','岳阳楼','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"倚楼高望极，展转念前途。\",\"晚叶红残楚，秋江碧入吴。\",\"云中来雁急，天末去帆孤。\",\"明月谁同我，悠悠上帝都。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a76da2cab73fac2f19e','江行','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"越信隔年稀，孤舟几梦归。\",\"月寒花露重，江晚水烟微。\",\"峰直帆相望，沙空鸟自飞。\",\"何时洞庭上，春雨满蓑衣。\"]',NULL,NULL,NULL,NULL),('6ce235a881532a76eb46310491b7e76b','瀑布','江为','6ce235a881532a76d1457b72d1457b72','五代十国',NULL,'[\"庐山正南面，瀑布古来闻。\",\"万里朝沧海，千寻出白云。\",\"寒声终自远，灵派孰为分。\",\"除却天台后，平流莫可群。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd611078d8b33f42639','送人宰邑','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"官小任还重，命官难偶然。\",\"皇恩轻一邑，赤子病三年。\",\"瘦马稀餐粟，羸童不识钱。\",\"如君清苦节，到处有人传。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd61379e03b5545121b','中秋月','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"一岁几盈亏，当轩重此期。\",\"幸无偏照处，刚有不明时。\",\"色静云归早，光寒鹤睡迟。\",\"相看吟未足，皎皎下疏篱。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6158ddf6fcb7268d7','寄僧知乾','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"貌高清入骨，帝里旧临坛。\",\"出语经相似，行心佛证安。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6187f61193abe5bb3','赠衡山令','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"君吟十二载，辛苦必能官。\",\"造化犹难隐，生灵岂易谩。\",\"猿跳高岳静，鱼摆大江宽。\",\"与我为同道，相留夜话阑。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd61e7a1982e6d9b06a','春暖送人下第','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"相送短亭前，知君愚复贤。\",\"事多凭夜梦，老为待明年。\",\"春树添山脊，晴云学晓烟。\",\"雄文有公道，此别莫潸然。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd620ccd4f4f1ed40c4','过洞庭湖','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"浪高风力大，挂席亦言迟。及到堪忧处，争如未济时。\",\"鱼龙侵莫测，雷雨动须疑。此际情无赖，何门寄所思。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6285fc38fbe0f710d','春日山中竹','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"数竿苍翠拟龙形，峭拔须教此地生。\",\"无限野花开不得，半山寒色与春争。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd62d933064dd871e0d','冬日作','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"粝食拥败絮，苦吟吟过冬。\",\"稍寒人却健，太饱事多慵。\",\"树老生烟薄，墙阴贮雪重。\",\"安能只如此，公道会相容。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd635f4510abd644f10','见王贞白','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"共贺登科后，明宣入紫宸。\",\"又看重试榜，还见苦吟人。\",\"此得名浑别，归来话亦新。\",\"分明一枝桂，堪动楚江滨。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd63621c8344a56b9c7','棋','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"十九条平路，言平又崄巇。\",\"人心无算处，国手有输时。\",\"势迥流星远，声乾下雹迟。\",\"临轩才一局，寒日又西垂。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6449d1a461931ac15','春早寄华下同人','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"正是花时节，思君寝复兴。\",\"市沽终不醉，春梦亦无凭。\",\"岳面悬青雨，河心走浊冰。\",\"东门一条路，离恨镇相仍。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd65115d10be3e92fa8','终南山','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"九衢南面色，苍翠绝纤尘。\",\"寸步有闲处，百年无到人。\",\"禁林寒对望，太华净相邻。\",\"谁与群峰并，祥云瑞露频。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd66014f7f52e19a802','寄曹松','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"莫怪苦吟迟，诗成鬓亦丝。\",\"鬓丝犹可染，诗病却难医。\",\"山暝云横处，星沈月侧时。\",\"冥搜不可得，一句至公知。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd665caf058d498e321','岳阳兵火后题僧舍','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"十年兵火真多事，再到禅扉却破颜。\",\"唯有两般烧不得，洞庭湖水老僧闲。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd665d603af6784c836','乱中偷路入故乡','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"愁看贼火起诸烽，偷得馀程怅望中。\",\"一国半为亡国烬，数城俱作古城空。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd668a23e3681fba1d8','上岳守','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"入郭宽万里，岳阳堪画图。\",\"及窥贤太守，不见洞庭湖。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6699b8731ea574ad8','塞上曲','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"极目望空阔，马羸程又赊。\",\"月生方见树，风定始无沙。\",\"楚水辞鱼窟，燕山到雁家。\",\"如斯名利役，争不老天涯。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6742f585ad34ad238','赠宾贡','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"惟君怀至业，万里信悠悠。\",\"路向东溟出，枝来北阙求。\",\"家无一夜梦，帆挂隔年秋。\",\"鬓发争禁得，孤舟往复愁。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd679f21ca59ccc063e','蔷薇','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"一架长条万朵春，嫩红深绿小窠匀。\",\"只应根下千年土，曾葬西川织锦人。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd68fd880d97b3226df','怀素台歌','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"我呼古人名，鬼神侧耳听。杜甫李白与怀素，文星酒星草书星。\",\"永州东郭有奇怪，笔冢墨池遗迹在。笔冢低低高如山，墨池浅浅深如海。\",\"我来恨不已，争得青天化为一张纸。高声唤起怀素书，搦管研朱点湘水。\",\"欲归家，重叹嗟。眼前有三个字，枯树槎，乌梢蛇，墨老鸦。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd693c88f0b70403fb4','游洞庭湖','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"楚云团翠八百里，澧兰吹香堕春水。\",\"白头渔子摇苍烟，鸂鶒眠沙晓惊起。\",\"沙头龙叟夜叹忧，铁笛未响春风羞。\",\"露寒紫藟结新愁，城角泣断关河秋。\",\"谪仙欲识雷斧手，刬却古今愁共丑。\",\"鲸游碧落杳无踪，作诗三叹君知否。\",\"瀛洲一棹何时还，满江宫锦看湖山。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd697ef67351da400f8','南中县令','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"寂寥虽下邑，良宰有清威。\",\"苦节长如病，为官岂肯肥。\",\"山多村地狭，水浅客舟稀。\",\"上国搜贤急，陶公早晚归。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6a78b78d849708c4c','夏日即事','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"僻居门巷静，竟日坐阶墀。\",\"鹊喜虽传信，蛩吟不见诗。\",\"笋抽通旧竹，梅落立闲枝。\",\"此际无尘挠，僧来称所宜。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6b0afe96c88c935d0','汉南邮亭','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"高阁水风清，开门日送迎。\",\"帆张独鸟起，乐奏大鱼惊。\",\"骤雨拖山过，微风拂面生。\",\"闲吟虽得句，留此谢多情。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6c57d35f4e760d519','送进士苏瞻乱后出家','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"因乱事空王，孤心亦不伤。\",\"梵僧为骨肉，柏寺作家乡。\",\"眼闭千行泪，头梳一把霜。\",\"诗书不得力，谁与问苍苍。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6c6a185e60606c7f5','闻砧','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"深闺乍冷鉴开箧，玉箸微微湿红颊。\",\"一阵霜风杀柳条，浓烟半夜成黄叶。\",\"垂垂白练明如雪，独下闲阶转凄切。\",\"只知抱杵捣秋砧，不觉高楼已无月。\",\"时闻寒雁声相唤，纱窗只有灯相伴。\",\"几展齐纨又懒裁，离肠恐逐金刀断。\",\"细想仪形执牙尺，回刀剪破澄江色。\",\"愁捻银针信手缝，惆怅无人试宽窄。\",\"时时举袖匀红泪，红笺谩有千行字。\",\"书中不尽心中事，一片殷勤寄边使。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6cfcf602d265c58c3','湖外送何崇入阁','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"因诗相识久，忽此告临途。\",\"便是有船发，也须容市沽。\",\"精吟五个字，稳泛两重湖。\",\"长短逢公道，清名振帝都。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6d4c6727cd43a8a7b','冬日后作','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"寂寞掩荆扉，昏昏坐欲痴。\",\"事无前定处，愁有并来时。\",\"日影才添线，鬓根已半丝。\",\"明庭正公道，应许苦心诗。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6e0725a5266d70b2f','喜友人再面','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"一别几寒暄，迢迢隔塞垣。\",\"相思长有事，及见却无言。\",\"静坐将茶试，闲书把叶翻。\",\"依依又留宿，圆月上东轩。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6e21076dc576fac43','秋日送河北从事','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"北风沙漠地，吾子远从军。\",\"官路虽非远，诗名要且闻。\",\"蝉悲欲落日，雕下拟阴云。\",\"此去难相恋，前山掺袂分。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6e756c066662b7253','对雪','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"大片向空舞，出门肌骨寒。\",\"路岐平即易，沟壑满应难。\",\"兔穴归时失，禽枝宿处乾。\",\"豪家宁肯厌，五月画图看。\"]',NULL,NULL,NULL,NULL),('6ce235a884e92cd6ef9731e9707817bd','经杜工部坟','裴说','6ce235a884e92cd6d1457b72d1457b72','五代十国',NULL,'[\"骚人久不出，安得国风清。\",\"拟掘孤坟破，重教大雅生。\",\"皇天高莫问，白酒恨难平。\",\"悒怏寒江上，谁人知此情。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a201716f28522175ef378','题中条静观','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"松顶留衣上玉霄，永传异迹在中条。\",\"不知揖遍诸仙否，欲请还丹问昨宵。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a201718048ef3955ebea9','哭张道古','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"曾陈章疏忤昭皇，扑落西南事可伤。\",\"岂使谏臣终屈辱，直疑天道恶忠良。\",\"生前卖卜居三蜀，死后驰名遍大唐。\",\"谁是后来修史者，言君力死正颓纲。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a20175cd5fd98e3879ee3','与罗隐之联句','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"一壶天上有名物，两个世间无事人。\",\"醉却隐之云叟外，不知何处是天真。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a20175f2a381802cad4c0','山居','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"闷见有人寻，移庵更入深。\",\"落花流涧水，明月照松林。\",\"醉劝头陀酒，闲教孺子吟。\",\"身同云外鹤，断得世尘侵。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a20175f2a38181b57ecf5','山居','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"冥心栖太室，散发浸流泉。\",\"采柏时逢麝，看云忽见山。\",\"夏狂冲雨戏，春醉戴花眠。\",\"绝顶登云望，东都一点烟。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a20175f2a3818b1478776','山居','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"不求朝野知，卧见岁华移。\",\"采药归侵夜，听松饭过时。\",\"荷竿寻水钓，背局上岩棋。\",\"祭庙人来说，中原正乱离。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a20176a4be4b57704ea05','宿洞庭','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"月到君山酒半醒，朗吟疑有水仙听。\",\"无人识我真闲事，赢得高秋看洞庭。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a201773caeebb20f22c76','富贵曲','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"美人梳洗时，满头间珠翠。\",\"岂知两片云，戴却数乡税。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a201787da82db85900245','茶诗','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"嫩芽香且灵，吾谓草中英。\",\"夜臼和烟捣，寒炉对雪烹。\",\"惟忧碧粉散，常见绿花生。\",\"最是堪珍重，能令睡思清。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a201789758bef0b96f996','景福中作','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"闷见戈鋋匝四溟，恨无奇策救生灵。\",\"如何饮酒得长醉，直到太平时节醒。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017ac1af11e36a865af','伤农','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"一粒红稻饭，几滴牛颔血。\",\"珊瑚枝下人，衔杯吐不歇。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017be72b15916b66d67','偶题','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"帆力劈开沧海浪，马蹄踏破乱山青。\",\"浮名浮利浓于酒，醉得人心死不醒。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017be72b159a6462d8a','偶题','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"似鹤如云一个身，不忧家国不忧贫。\",\"拟将枕上日高睡，卖与世间荣贵人。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017c8e1a439d4d1903e','题霍山秦尊师','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"老鹤玄猿伴采芝，有时长叹独移时。\",\"翠娥红粉浑如剑，杀尽世人人不知。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017e73951eed96bee0a','招友人游春','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"难把长绳系日乌，芳时偷取醉工夫。\",\"任堆金璧磨星斗，买得花枝不老无。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017eac30e867bd91283','思山咏','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"因卖丹砂下白云，鹿裘惟惹九衢尘。\",\"不如将耳入山去，万是千非愁杀人。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017ef1e1806a2a70229','题病僧寮','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"佛前香印废晨烧，金锡当门照寂寥。\",\"童子不知师病困，报风吹折好芭蕉。\"]',NULL,NULL,NULL,NULL),('6ce235a88b6a2017ff7681cd173f3c61','咏西施','郑遨','6ce235a88b6a2017d1457b72d1457b72','五代十国',NULL,'[\"素面已云妖，更著花钿饰。\",\"脸横一寸波，浸破吴王国。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c11215dd1a9dbb19f','郊庙歌辞梁郊祀乐章其五庆和','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"惟德动天，有感必通。\",\"秉兹一德，禋于六宗。\",\"钦夤宝命，恭肃礼容。\",\"来顾来享，永穆皇风。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c11215dd1abac925d','郊庙歌辞梁郊祀乐章其五庆和','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"天惟佑德，辟乃奉天。\",\"交感斯在，昭事罔愆。\",\"岁功已就，王道无偏。\",\"于焉报本，是用告虔。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c343e9c82c60446b8','郊庙歌辞梁郊祀乐章庆顺','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"圣皇戾止，天步舒迟。\",\"乾乾睿相，穆穆皇仪。\",\"进退必肃，陟降是祗。\",\"六变克协，万灵协随。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c71ac42acac3bde50','郊庙歌辞梁郊祀乐章庆平','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"天命降鉴，帝德惟馨。\",\"享祀不忒，礼容孔明。\",\"奠璧布币，荐纯献精。\",\"神祐以答，敷锡永宁。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c7f51212622bdc7ba','郊庙歌辞梁郊祀乐章庆和','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"就阳位，升圜丘。佩双玉，御大裘。膺天命，拥神休。万灵感，百禄遒。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c7f51212634067d21','郊庙歌辞梁郊祀乐章庆和','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"秉黄钺，建朱旗。震八表，清二仪。帝业显，王道夷。受景命，启皇基。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c7f512126afecde5c','郊庙歌辞梁郊祀乐章庆和','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"膺宝图，执左契。德应天，圣飨帝。荐表衷，荷灵惠。寿万年，祚百世。\"]',NULL,NULL,NULL,NULL),('6ce235a88ced4a1c7f512126db331132','郊庙歌辞梁郊祀乐章庆和','赵光逢','6ce235a88ced4a1cd1457b72d1457b72','五代十国',NULL,'[\"开九门，怀百神。通肸蚃，接氤氲。明粢荐，广乐陈。奠嘉璧，燎芳薪。\"]',NULL,NULL,NULL,NULL),('6ce235a892593edc5f2a381806cea3f3','山居','颜仁郁','6ce235a892593edcd1457b72d1457b72','五代十国',NULL,'[\"柏树松阴覆竹斋，罢烧药灶纵高怀。\",\"世间应少山间景，云绕青松水绕阶。\"]',NULL,NULL,NULL,NULL),('6ce235a892593edca874ca894687db70','农家','颜仁郁','6ce235a892593edcd1457b72d1457b72','五代十国',NULL,'[\"夜半呼儿趁晓耕，羸牛无力渐艰行。\",\"时人不识农家苦，将谓田中谷自生。\"]',NULL,NULL,NULL,NULL),('6ce235a896535892e78c6a92af405232','别诸同志','钟茜','6ce235a896535892d1457b7292977ae4','五代十国',NULL,'[\"随阳来万里，点点度遥空。\",\"影落长江水，声悲半夜风。\",\"残秋辞绝漠，无定似惊蓬。\",\"我有离群恨，飘飘类此鸿。\"]',NULL,NULL,NULL,NULL),('6ce235a89b4ecdb939cdd7d896289b67','郊庙歌辞后唐宗庙乐舞辞雍熙舞','卢文纪','6ce235a89b4ecdb967d16d001c1d4df5','五代十国',NULL,'[\"仁君御宇，环海谧清。\",\"运符武德，道协文明。\",\"九成式叙，百度惟成。\",\"金门积庆，玉叶传荣。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df924d491b4c080824c','赠王正己','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"吟高鄙俗流，傲逸访巢由。\",\"古寺寻僧饭，寒岩衣鹿裘。\",\"园桃山鼠啮，崖蜜猎人偷。\",\"遂这个清性，浮生不苟求。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df948021318a91e6c1e','赠狄涣','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"高卧白云乡，崖泉对阁凉。\",\"守贫无属道，多病数求方。\",\"耕犊惊雷毙，寒芜入圃荒。\",\"如何帝未梦，吟苦顶铺霜。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df98c8bb9a94cc64cfa','赠天台逸人','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"移桧托禅子，携家上赤城。\",\"拂琴天籁寂，欹枕海涛生。\",\"云白寒峰晚，鸟歌春谷晴。\",\"又闻求桂楫，载月十洲行。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df9a295750c7bce5ebe','梦仙谣','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"琪木扶疏系辟邪，麻姑夜宴紫皇家。\",\"银河旌节摇波影，珠阁笙箫吸月华。\",\"翠凤引游三岛路，赤龙齐驾五云车。\",\"星稀犹倚虹桥立，拟就张骞搭汉槎。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df9c3951e1b6a7ef29f','谢翁宏以诗百篇见示','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"高奇一百篇，造化见工全。\",\"积思游沧海，冥搜入洞天。\",\"神珠迷罔象，瑞玉匪雕镌。\",\"休叹不得力，离骚千古传。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df9cbcf457b9c05225e','退宫妓','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"神仙风格本难俦，曾从前皇翠辇游。\",\"红踯躅繁金殿暖，碧芙蓉笑水宫秋。\",\"宝筝钿剥阴尘覆，锦帐香消画烛幽。\",\"一旦色衰归故里，月明犹梦按梁州。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df9dd40ffde12cb6482','题伍彬屋壁','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"圆塘绿水平，鱼跃紫莼生。\",\"要路贫无力，深村老退耕。\",\"犊随原草远，蛙傍堑篱鸣。\",\"拨棹茶川去，初逢谷雨晴。\"]',NULL,NULL,NULL,NULL),('6ce235a89ca18df9feade6458fe8c033','古桧','廖融','6ce235a89ca18df9d1457b72d1457b72','五代十国',NULL,'[\"何人见植初，老树梵王居。\",\"山鬼暗栖托，樵夫难破除。\",\"声高秋汉迥，影倒月潭虚。\",\"尽日无僧倚，清风长有馀。\"]',NULL,NULL,NULL,NULL),('6ce235a89d6ad813a0228395db71a315','睹木平和尚','文益','6ce235a89d6ad8130efe3284d240e3d3','五代十国',NULL,'[\"木平山里人，貌古年复少。\",\"相看陌路同，论心秋月皎。\",\"怀衲线非蚕，助歌声有鸟。\",\"城阙今日来，一讴曾已晓。\"]',NULL,NULL,NULL,NULL),('6ce235a8a47a9571989f618f6e8a1892','梦白衣妇人歌词其一','卢绛','6ce235a8a47a9571d1457b72d1457b72','五代十国',NULL,'[\"清风明月夜深时，箕帚卢郎恨已迟。\",\"他日孟家坡上约，再来相见是佳期。\"]',NULL,NULL,NULL,NULL),('6ce235a8a524d4c2472bcef1cdf33d23','北使还京作','冯道','6ce235a8a524d4c26c3cf77d6395ebd0','五代十国',NULL,'[\"去年今日奉皇华，只为朝廷不为家。\",\"殿上一杯天子泣，门前双节国人嗟。\",\"龙荒冬往时时雪，兔苑春归处处花。\",\"上下一行如骨肉，几人身死掩风沙。\"]',NULL,NULL,NULL,NULL),('6ce235a8a524d4c2a05bda1e2d0fda59','天道','冯道','6ce235a8a524d4c26c3cf77d6395ebd0','五代十国',NULL,'[\"穷达皆由命，何劳发叹声。\",\"但知行好事，莫要问前程。\",\"冬去冰须泮，春来草自生。\",\"请君观此理，天道甚分明。\"]',NULL,NULL,NULL,NULL),('6ce235a8a524d4c2e3872325f84e9536','偶作','冯道','6ce235a8a524d4c26c3cf77d6395ebd0','五代十国',NULL,'[\"莫为危时便怆神，前程往往有期因。\",\"须知海岳归明主，未必乾坤陷吉人。\",\"道德几时曾去世，舟车何处不通津？\",\"但教方寸无诸恶，狼虎丛中也立身。\"]',NULL,NULL,NULL,NULL),('6ce235a8a524d4c2eb72f240714d8747','赠窦十','冯道','6ce235a8a524d4c26c3cf77d6395ebd0','五代十国',NULL,'[\"燕山窦十郎，教子有义方。\",\"灵椿一株老，丹桂五枝芳。\"]',NULL,NULL,NULL,NULL),('6ce235a8a524d4c2f838070b5a3c928b','放鱼书所钥户','冯道','6ce235a8a524d4c26c3cf77d6395ebd0','五代十国',NULL,'[\"高却垣墙钥却门，监丞从此罢垂纶。\",\"池中鱼鳖应相贺，从此方知有主人。\"]',NULL,NULL,NULL,NULL),('6ce235a8a63ba1688454396b59ca4d45','赠禅月大师','王锴','6ce235a8a63ba168d1457b72d1457b72','五代十国',NULL,'[\"长爱吾师性自然，天心白月水中莲。\",\"神通力遍恒沙外，诗句名高八米前。\",\"寻访不闻朝振锡，修行唯说夜安禅。\",\"太平时节俱无事，莫惜时来话草玄。\"]',NULL,NULL,NULL,NULL),('6ce235a8a7e8c00eb388c14c4c046bc4','红蔷薇','牛峤','6ce235a8a7e8c00ed1457b72d1457b72','五代十国',NULL,'[\"晓啼珠露浑无力，绣簇罗襦不著行。\",\"若缀寿阳公主额，六宫争肯学梅妆。\"]',NULL,NULL,NULL,NULL),('6ce235a8a842bb1032590ed365ee4691','题游帷观真君殿后','伊用昌','6ce235a8a842bb10d1457b72d1457b72','五代十国',NULL,'[\"日日祥云瑞气连，侬家应作大神仙。\",\"笔头洒起风雷力，剑下驱驰造化权。\",\"更与戎夷添礼乐，永教胡虏绝烽烟。\",\"列仙功业只如此，直上三清第一天。\"]',NULL,NULL,NULL,NULL),('6ce235a8a842bb1038c5c70096701181','留题阁皂观','伊用昌','6ce235a8a842bb10d1457b72d1457b72','五代十国',NULL,'[\"花洞门前吠似雷，险声流断俗尘埃。\",\"雨喷山脚毒龙起，月照松梢孤鹤回。\",\"萝幕秋高添碧翠，画帘时卷到楼台。\",\"两坛诗客何年去，去后门关更不开。\"]',NULL,NULL,NULL,NULL),('6ce235a8a842bb10ae74008d3d20f714','题茶陵县门','伊用昌','6ce235a8a842bb10d1457b72d1457b72','五代十国',NULL,'[\"茶陵一道好长街，两畔栽柳不栽槐。\",\"夜后不闻更漏鼓，只听锤芒织草鞋。\"]',NULL,NULL,NULL,NULL),('6ce235a8a842bb10b413b9b8960cd183','题酒楼壁','伊用昌','6ce235a8a842bb10d1457b72d1457b72','五代十国',NULL,'[\"此生生在此生先，何事从玄不复玄。\",\"已在淮南鸡犬后，而今便到玉皇前。\"]',NULL,NULL,NULL,NULL),('6ce235a8a842bb10c4747a2a84294b64','湖南闯斋吟','伊用昌','6ce235a8a842bb10d1457b72d1457b72','五代十国',NULL,'[\"谁人能识白元君，上士由来尽见闻。\",\"避世早空南火宅，植田高种北山云。\",\"鸡能抱卵心常听，蝉到成形壳自分。\",\"学取大罗些子术，免教松下作孤坟。\"]',NULL,NULL,NULL,NULL),('6ce235a8b0034cf287c9caecddce328e','迎孙刺史','朱元','6ce235a8b0034cf2c4015b7fcc1aa436','五代十国',NULL,'[\"昔日郎君今刺史，朱元依旧守朱门。\",\"今朝竹马诸童子，尽是当时竹马孙。\"]',NULL,NULL,NULL,NULL),('6ce235a8b003e5320a11e8a9226853ba','题兴化高田院桥亭','郑良士','6ce235a8b003e532d1457b72d1457b72','五代十国',NULL,'[\"到此溪亭上，浮生始觉非。\",\"野僧还惜别，游客亦忘归。\",\"月满千岩静，风清一磬微。\",\"何时脱尘役，杖履愿相依。\"]',NULL,NULL,NULL,NULL),('6ce235a8b003e53215acf26f763fd67a','寄富洋院禅者','郑良士','6ce235a8b003e532d1457b72d1457b72','五代十国',NULL,'[\"画破青山路一条，走鞭飞盖去何遥。\",\"碍天岩树春先冷，锁院溪云昼不销。\",\"霅上茗芽因客煮，海南沈屑为斋烧。\",\"谁能学得空门士，冷却心灰守寂寥。\"]',NULL,NULL,NULL,NULL),('6ce235a8b003e5324a8df15bbe0d7c09','游九鲤湖','郑良士','6ce235a8b003e532d1457b72d1457b72','五代十国',NULL,'[\"仄径倾崖不可通，湖岚林霭共溟蒙。\",\"九溪瀑影飞花外，万树春声细雨中。\",\"覆石云闲丹灶冷，采芝人去洞门空。\",\"我来不乞邯郸梦，取醉聊乘郑国风。\"]',NULL,NULL,NULL,NULL),('6ce235a8b0204b182e6c7db95732bdb9','答诸姊妹戒饮','蒋氏','6ce235a8b0204b18d1457b72d1457b72','五代十国',NULL,'[\"平生偏好酒，劳尔劝吾餐。\",\"但得杯中满，时光度不难。\"]',NULL,NULL,NULL,NULL),('6ce235a8b0f35b7ea2d6992020068877','即事','杨鸾','6ce235a8b0f35b7ed1457b72d1457b72','五代十国',NULL,'[\"白日苍蝇满饭盘，夜间蚊子又成团。\",\"每到更深人静后，定来头上咬杨鸾。\"]',NULL,NULL,NULL,NULL),('6ce235a8b1c42afaf4676dbbf29fbbbd','郊庙歌辞后唐宗庙乐舞辞武成舞','崔居俭','6ce235a8b1c42afa22fb0cee3df1d4b9','五代十国',NULL,'[\"艰难王业，返正皇唐。\",\"先天在造，却日重光。\",\"汉绍世祖，夏资少康。\",\"功成德茂，率祀无疆。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc05f9d6fd792dbde6','泊姑熟口','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"杳杳金陵路，难禁欲断魂。\",\"雨晴山有态，风晚水无痕。\",\"远色千樯岸，愁声一笛村。\",\"如何遣怀抱，诗毕自开尊。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc07a8fc7357783fa7','自喻','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"予念天之生，生本空疏器。\",\"五岁禀慈训，愤悱读书志。\",\"七岁辨声律，勤苦会诗赋。\",\"九岁执公卷，倜傥干名意。\",\"乞荐乡老书，幸会春官试。\",\"折桂愧巍峨，依莲何气味。\",\"性拙绝不佞，才短无馀地。\",\"前年会知己，荐章实非据。\",\"宁见民说平，空荷君恩寄。\",\"瞿塘抵巴渝，往来名揽辔。\",\"孤舟一水中，艰险实可畏。\",\"群操百丈牵，临难无苟避。\",\"濆向江底发，水在石中沸。\",\"槌鼓称打宽，系纫呼下纬。\",\"善恶胡可分，死生何足讳。\",\"骑衡与垂堂，非不知前喻。\",\"临渊与履冰，非不知深虑。\",\"我今縻搢绅，善地谁人致。\",\"城狐与社鼠，巧佞谁从庇。\",\"奴颜与婢膝，丑直谁从媚。\",\"妻儿夐限越，容颜几憔悴。\",\"致身霄汉人，呃嚱尽贤智。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc0c1818072d0b7c34','宿疏陂驿','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"秋染棠梨叶半红，荆州东望草平空。\",\"谁知孤宦天涯意，微雨萧萧古驿中。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc1133f1b2934589c4','赠怤师','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"水中有片月，照耀婵娟姿。\",\"庭前有孤柏，竦秀岁寒期。\",\"坚然物莫迁，寂焉心为师。\",\"声发响必答，形存影即随。\",\"雪花安结子，雪叶宁附枝。\",\"兰死不改香，井寒岂生澌。\",\"晨炉烟袅袅，病发霜丝丝。\",\"丈室冰凛冽，一衲云离披。\",\"顾此名利场，得不惭冠绥。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc2391d0f0a5315185','齿落词','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"己卯至庚辰，仲夏晦之暮。\",\"吾齿右排上，一齿脱而去。\",\"呼吸缺吾防，咀嚼欠吾助。\",\"年籥惜不返，日驭走为蠹。\",\"唇亡得无寒，舌在从何诉。\",\"辅车宜长依，发肤可增惧。\",\"不须考前古，聊且为近喻。\",\"有如云中雨，雨散绝回顾。\",\"有如枝上叶，叶脱难再附。\",\"白发非独愁，红颜岂私驻。\",\"何必郁九回，何必牵百虑。\",\"开尊复开怀，引笔作长句。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc2588d3c8fc9ddf2c','题厅壁','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"永日无他念，孤清吏隐心。\",\"竹声并雪碎，溪色共烟深。\",\"数息闲凭几，缘情默寄琴。\",\"谁知同寂寞，相与结知音。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc285ce58a7482dde9','公居','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"公居门馆静，旅寄万州城。\",\"山共秋烟紫，霜并夜月清。\",\"无愁干酒律，有句入诗评。\",\"何必须林下，方驰吏隐名。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc30da9f0bba980f1d','金口步','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"两山斗咽喉，群石矗牙齿。\",\"行客无限怨，横吞一江水。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc3196d11225759d37','春答','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"花枝千万趁春开，三月瓓珊即自回。\",\"剩向东园种桃李，明年依旧为君来。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc332a8c7f2179cdf8','渡溪','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"渡溪溪水急，水溅罗衣湿。\",\"日暮犹未归，盈盈水边立。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc33d585827cae5133','金盘草诗','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"今春从南陵，得草名金盘。\",\"金盘有仁性，生在林一端。\",\"根节岁一节，食之甘而酸。\",\"风俗竞采掇，俾人防急难。\",\"巴中蛇虺毒，解之如走丸。\",\"巨叶展六出，软干分长竿。\",\"摇摇绿玉活，袅袅香荷寒。\",\"世云暑酷月，郁有神物看。\",\"天之产于此，意欲生民安。\",\"今之为政者，何不反此观。\",\"知彼苛且猛，慎勿虐而残。\",\"一物苟失所，万金惟可叹。\",\"莫并蒿与莱，岂羡芝及兰。\",\"勤渠护根本，栽植当庭栏。\",\"寄言好生者，休说神仙丹。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc3d74aee4059a0fd4','志峡船具诗梢','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"少尝侍先君，馀闲诵白氏。\",\"始得入峡诗，深味作诗旨。\",\"云有万仞山，云有千丈水。\",\"自念坎壈时，尤多兢慎理。\",\"山束峡如口，水漱石如齿。\",\"孤舟行其中，薄冰犹坦履。\",\"孱颜屹焉立，汹涌勃然起。\",\"百丈为前牵，万险即平砥。\",\"破之以筼筜，续之以麻枲。\",\"砺之坚以节，引之直如矢。\",\"杼轴连半空，长短随两涘。\",\"铁锁枉驰名，锦缆谩称美。\",\"长绳岂能系，朽索何足拟。\",\"苟非?之为，胡可力行此。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc3d74aee45beda4eb','志峡船具诗梢','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"箭飞峡中水，锯立峡中石。\",\"峡与水为隘，水与石相击。\",\"濆为生险艰，声发甚霹雳。\",\"三老航一叶，百丈空千尺。\",\"苍黄徒尔为，倏忽何可测。\",\"篙之小难制，戙之独有力。\",\"猗嗟戙之为，彬彬坚且直。\",\"有如用武人，森森矗戈戟。\",\"有如敢言士，落落吐胸臆。\",\"拯危居坦夷，济险免兢惕。\",\"志彼哲匠心，俾其来者识。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc3d74aee479d485e5','志峡船具诗梢','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"制之居首尾，俾之辨斜正。\",\"首动尾聿随，斜取正为定。\",\"有如提吏笔，有如执时柄。\",\"有如秉师律，有如宣命令。\",\"守彼方与直，得其刚且劲。\",\"既能济险难，何畏涉辽夐。\",\"招招俾作主，泛泛实司命。\",\"风乌愧斟酌，画鹢空辉映。\",\"古人存丰规，猗欤聊引证。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc3d74aee4f7f0311d','志峡船具诗梢','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"用之大曰橹，冠乎小者楫。\",\"通津既能济，巨浸即横涉。\",\"身之使者颊，虎之拿者爪。\",\"鱼之拨者鬣，弩之进者筴。\",\"此实为相须，相须航一叶。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc43457c2aa0cae61c','巫山公署壁有无名氏戏书二韵','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"南陵直上路盘盘，平地凌云势万端。\",\"堪笑巴民不厌足，更嫌山少画山看。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc54eeee3c59c89f66','富池口','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"扁舟闲引望，望极更盘桓。\",\"山密碍江曲，雨多饶地寒。\",\"短莎烟苒苒，惊浪雪漫漫。\",\"难写愁何限，乡关在一端。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc67921b22386cd123','泊巴东','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"偶泊巴东古县前，宦情乡思两绵绵。\",\"不堪蜡炬烧残泪，雨打船窗半夜天。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc6a2146453a316fe3','岳州众湖阻风','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"众湖湖口系兰船，睡起中餐又却眠。\",\"风伯如何解回怒，数宵樯倚碧芦烟。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc6a2146459a0a711a','岳州众湖阻风','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"偶系扁舟枕绿莎，旋移深处避惊波。\",\"晓来闲共渔人话，此去巴陵路几多。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc71c70698000efe44','再经秭归','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"总角曾随上峡船，寻思如梦可悽然。\",\"夜来孤馆重来宿，枕底滩声似旧年。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc71c7069849b8965b','再经秭归','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"秭归城邑昔曾过，旧识无人奈老何。\",\"独有凄清难改处，月明闻唱竹枝歌。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc7a295a18d1dbacbd','夔州病中','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"隐几经旬疾未痊，孤灯孤驿若为眠。\",\"郡楼昨夜西风急，一一更筹到枕前。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc7cd4d4c25cea6b90','过武宁县','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"行过武宁县，初晴物景和。\",\"岸回惊水急，山浅见天多。\",\"细草浓蓝泼，轻烟疋练拖。\",\"晚来何处宿，一笛起渔歌。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc9a54ea5e71edc733','使风','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"风起即千里，风回翻问津。\",\"沉思宦游者，何啻使风人。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abc9fa5311bccedf3f0','道中未开木杏花','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"粉英香萼一般般，无限行人立马看。\",\"村女浴蚕桑柘绿，枉将颜色忍春寒。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abca037842bc03f72a1','西塞山','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"西塞名山立翠屏，浓岚横入半江青。\",\"千寻铁锁无由问，石壁空存道者形。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abca037842bf26a07e9','西塞山','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"匹妇顽然莫问因，匹夫何去望千春。\",\"翻思岵屺传诗什，举世曾无化石人。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abca504957b5e828cb8','西山晚景','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"公局长清淡，池亭晚景中。\",\"蔗竿闲倚碧，莲朵静淹红。\",\"半引弯弯月，微生飋飋风。\",\"无思复无虑，此味几人同。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcb5d43167bf828647','早春西园','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"引步携筇竹，西园小径通。\",\"雪攲梅蒂绿，春入杏梢红。\",\"静意崖穿溜，孤愁笛破空。\",\"如何将此景，收拾向图中。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcc40b26e57bdbc04b','巫庙','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"巴水走若箭，峡山开如屏。\",\"汹涌匹练白，?崒浓蓝青。\",\"崖空蓄云雨，滩恶惊雷霆。\",\"神仙宅幽邃，庙貌横杳冥。\",\"隐约可一梦，缥缈馀千龄。\",\"名利有所役，舟楫无暂停。\",\"悉窣垂肸蚃，祠祷希安宁。\",\"鸦鸦尔何物，飞飞来庙庭。\",\"纷纷扬寥泬，远近随虚舲。\",\"铁石砺觜爪，金碧辉光翎。\",\"翔集托阴险，鹐啄贪膻腥。\",\"日既恃威福，岁久为精灵。\",\"依草与附木，诬诡殊不经。\",\"城狐与社鼠，琐细何足听。\",\"况乎人假人，心阔吞沧溟。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcc5b4767a1138210d','问春','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"游丝垂幄雨依依，枝上红香片片飞。\",\"把酒问春因底意，为谁来后为谁归。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcc6786282a1c69c73','采桑女','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"采桑知蚕饥，投梭惜夜迟。\",\"谁夸罗绮丛，新画学月眉。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcc6786282e26fcabb','采桑女','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"渡水采桑归，蚕老催上机。\",\"札札得盈尺，轻素何人衣。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcca565bef2f71c35e','道院','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"白日人稀到，帘垂道院深。\",\"雨苔生古壁，雪雀聚寒林。\",\"忘虑凭三乐，消闲信五禽。\",\"谁知是官府，烟缕满炉沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcd84c52d162bf0208','自和','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"一片残阳景，朦胧淡月中。\",\"兰芽纡嫩紫，梨颊抹生红。\",\"琴阮资清格，冠簪养素风。\",\"烟霄半知足，吏隐少相同。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcd9caccf111c3362f','和程刑部三首公会亭','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"飋飋笼清籁，萧萧锁翠阴。\",\"向高思尽节，从直美虚心。\",\"迥砌滋苍藓，幽窗伴素琴。\",\"公馀时引步，一径静中深。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcd9caccf162c40810','和程刑部三首公会亭','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"公事公言地，标名姓必臧。\",\"江山如得助，谈笑若为妨。\",\"均赋乡原肃，详刑郡邑康。\",\"官箴居座右，夙夜算难忘。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcd9caccf17476557b','和程刑部三首公会亭','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"照影翻窗绮，层纹滉额波。\",\"丝青迷岸柳，茸绿蘸汀莎。\",\"片雪翘饥鹭，孤香卷嫩荷。\",\"凭栏堪入画，时听竹枝歌。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcde7faa98eb6a66c2','淘金碛','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"画船晚过淘金碛，不见黄金惟见石。\",\"犹恐黄金价未高，见得锱铢几多力。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abce1414ee889fd25b3','霞','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"拂拂生残晖，层层如裂绯。\",\"天风剪成片，疑作仙人衣。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abceb237aa067e8db1b','落叶','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"素律铄欲脆，青女妒复稀。\",\"月冷天风吹，叶叶乾红飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abced74c843444be822','无题','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"冰雪肌肤力不胜，落花飞絮绕风亭。\",\"不知何事秋千下，蹙破愁眉两点青。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abced74c843fe37e413','无题','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"梨花如雪已相迷，更被惊乌半夜啼。\",\"帘卷玉楼人寂寂，一钩新月未沉西。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcf832dbd39d2f4d9b','巴江','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"巴江江水色，一带浓蓝碧。\",\"仙女瑟瑟衣，风梭晚来织。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5e19abcfe0dac61f351806b','路次覆盆驿','王周','6ce235a8b5e19abcd1457b7258e4d44e','五代十国',NULL,'[\"曾上青泥蜀道难，架空成路入云寒。\",\"如何却向巴东去，三十六盘天外盘。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5f6095301f332bf162938aa','献周世宗','钟谟','6ce235a8b5f60953d1457b72d1457b72','五代十国',NULL,'[\"三年耀武群雄服，一日回銮万国春。\",\"南北通欢永无事，谢恩归去老陪臣。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5f6095329ff5426c04dfa25','贻耀州将','钟谟','6ce235a8b5f60953d1457b72d1457b72','五代十国',NULL,'[\"翩翩归尽塞垣鸿，隐隐惊开蛰户虫。\",\"渭北离愁春色里，江南家事战尘中。\",\"还同逐客纫兰佩，谁听缧囚奏土风。\",\"多谢贤侯振吾道，免令搔首泣途穷。\"]',NULL,NULL,NULL,NULL),('6ce235a8b5f60953db6eaa42f1f2b69b','代京妓越宾答徐铉','钟谟','6ce235a8b5f60953d1457b72d1457b72','五代十国',NULL,'[\"一幅轻绡寄海滨，越姑长感昔时恩。\",\"欲知别后情多少，点点凭君看泪痕。\"]',NULL,NULL,NULL,NULL),('6ce235a8b83962bd19d0a53a5f690ad7','咏卧牛','李家明','6ce235a8b83962bdd1457b72d1457b72','五代十国',NULL,'[\"曾遭宁戚鞭敲角，又被田单火燎身。\",\"闲向斜阳嚼枯草，近来问喘为无人。\"]',NULL,NULL,NULL,NULL),('6ce235a8b83962bd5065579b292f4c54','元宗钓鱼无获进诗','李家明','6ce235a8b83962bdd1457b72d1457b72','五代十国',NULL,'[\"玉甃垂钩兴正浓，碧池春暖水溶溶。\",\"凡鳞不敢吞香饵，知是君王合钓龙。\"]',NULL,NULL,NULL,NULL),('6ce235a8b83962bd73476c3be164329a','题纸鸢止宋齐丘哭子','李家明','6ce235a8b83962bdd1457b72d1457b72','五代十国',NULL,'[\"安排唐祚革强吴，尽是先生作计谟。\",\"一个孩儿?不得，让皇百口合何如。\"]',NULL,NULL,NULL,NULL),('6ce235a8b83962bdcbe34888043f55b0','咏皖公山','李家明','6ce235a8b83962bdd1457b72d1457b72','五代十国',NULL,'[\"龙舟轻飐锦帆风，正值宸游望远空。\",\"回首皖公山色翠，影斜不到寿杯中。\"]',NULL,NULL,NULL,NULL),('6ce235a8b8b8b2b12f6edc00d1de126c','题建造','王继勋','6ce235a8b8b8b2b1d1457b72d1457b72','五代十国',NULL,'[\"曾看画图劳健羡，如今亲见画犹粗。\",\"减除天半石初泐，欠却几株松未枯。\",\"题像阁人渔浦叟，集生台鸟谢城乌。\",\"我来一听支公论，自是吾身幻得吾。\"]',NULL,NULL,NULL,NULL),('6ce235a8b8b8b2b1ee79666a3eaea20a','赠和龙妙空禅师','王继勋','6ce235a8b8b8b2b1d1457b72d1457b72','五代十国',NULL,'[\"白面山南灵庆院，茅斋道者雪峰禅。\",\"只栖云树两三亩，不下烟萝四五年。\",\"猿鸟认声呼唤易，龙神降伏住持坚。\",\"谁知今日秋江畔，独步医王阐法筵。\"]',NULL,NULL,NULL,NULL),('6ce235a8ba1d688c37cdf6e51feafbc0','登祝融峰','李徵古','6ce235a8ba1d688cd1457b720f840be9','五代十国',NULL,'[\"欲上祝融峰，先登古石桥。\",\"凿开巇崄处，取路到丹霄。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70064f2721317fa8f','庭苇','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"品格清于竹，诗家景最幽。\",\"从栽向池沼，长似在汀洲。\",\"玩好招溪叟，栖堪待野鸥。\",\"影疏当夕照，花乱正深秋。\",\"韵细堪清耳，根牢好系舟。\",\"故溪高岸上，冷淡有谁游。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb700962dedf58c481a','哭故主人陈太师','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"十年孤迹寄侯门，入室升堂忝厚恩。\",\"游遍春郊随茜旆，饮残秋月待金尊。\",\"车鱼郑重知难报，吐握周旋不可论。\",\"长恸裴回逝川上，白杨萧飒又黄昏。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb700967e1a5a9ddacb','送黄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"雨馀飞絮乱，相别思难任。\",\"酒罢河桥晚，帆开烟水深。\",\"蟾宫须展志，渔艇莫牵心。\",\"岐路从兹远，双鱼信勿沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70108db5648055e15','冬日','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"自别吾师后，风骚道甚孤。\",\"雪霜侵鬓发，音信隔江湖。\",\"扰扰悲时世，悠悠役梦途。\",\"向公期尽节，多病怕倾壶。\",\"贱迹虽惭滞，幽情忍使辜。\",\"诗成天外句，棋覆夜中图。\",\"落壁灯花碎，飘窗雪片粗。\",\"煮茶烧栗兴，早晚复围炉。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb702111eebcdf026b7','秋日途中','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"信步腾腾野岸边，离家都为利名牵。\",\"疏林一路斜阳里，飒飒西风满耳蝉。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70243d083d2017c0c','送仙客','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"危言危行是男儿，倚伏相牵岂足悲。\",\"莫向汀洲时独立，悠悠斜日照江蓠。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb704817ec68f67347e','送绍明上人之毗陵','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"忽起毗陵念，飘然不可留。\",\"听蝉离古寺，携锡上扁舟。\",\"月出沙汀冷，风高苇岸秋。\",\"回期端的否，千里路悠悠。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70514853616258514','己未岁冬捧宣头离下蔡','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"诏下如春煦，巢南志不违。\",\"空将感恩泪，滴尽冒寒衣。\",\"覆载元容善，形骸果得归。\",\"无心渐季路，负米觐亲闱。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7055a4e84582c427b','安福县秋吟寄陈锐秘书','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"县庭事简得馀功，诗兴秋来不可穷。\",\"卧听寒蛬莎砌月，行冲落叶水村风。\",\"愁髭渐去人前白，醉面犹怜鉴里红。\",\"苦恨交亲多契阔，未知良会几时同。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb707aa6489cbcb9d73','舟次彭泽','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"飘泛经彭泽，扁舟思莫穷。\",\"无人秋浪晚，一岸蓼花风。\",\"乡里梦渐远，交亲书未通。\",\"今宵见圆月，难坐冷光中。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb709ff393441c53aa7','蛩','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"月冷莎庭夜已深，百虫声外有清音。\",\"诗情正苦无眠处，愧尔阶前相伴吟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70aa630e4c9e95107','春夕偶作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"早是春愁触目生，那堪春夕酒初醒。\",\"贯珠声罢人归去，半落桃花月在庭。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70b41eacf5b0ab5ea','吉水县依韵酬华松秀才见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"官况萧条在水村，吏归无事好论文。\",\"枕欹独听残春雨，梦去空寻五老云。\",\"竹径每怜和藓步，禽声偏爱隔花闻。\",\"诗情冷淡知音少，独喜江皋得见君。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70bb4db323568f476','徐司徒池亭','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"亭榭跨池塘，泓澄入座凉。\",\"扶疏皆竹柏，冷淡似潇湘。\",\"萍嫩铺波面，苔深锁岸傍。\",\"朝回游不厌，僧到赏难忘。\",\"最称收残雨，偏宜带夕阳。\",\"吟堪期谢朓，醉好命嵇康。\",\"奢侈心难及，清虚趣最长。\",\"月明垂钓兴，何必忆沧浪。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70bd0216c726db7db','读蜀志','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"鼎分天地日，先主力元微。\",\"鱼水从相得，山河遂有归。\",\"任贤无间忌，报国尽神机。\",\"草昧争雄者，君臣似此稀。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70bf9dae88ef11430','哭舍弟二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"鸿雁离群后，成行忆日存。\",\"谁知归故里，只得奠吟魂。\",\"虫蠹书盈箧，人稀草拥门。\",\"从兹长恸后，独自奉晨昏。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70bf9dae8cf3dc23c','哭舍弟二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"浮生多夭枉，惟尔最堪悲。\",\"同气未归日，慈亲临老时。\",\"旧诗传海峤，新冢枕江湄。\",\"遗稚呜呜处，黄昏绕穗帷。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70c161507b3f8bba0','剑客','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"恩酬期必报，岂是辄轻生。\",\"神剑冲霄去，谁为平不平。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70ca4a600fda66f59','访章禅老','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"比寻禅客叩禅机，澄却心如月在池。\",\"松下偶然醒一梦，却成无语问吾师。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70d06fd39c6c6fb5c','赠东林白大师','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"虎溪久驻灵踪，禅外诗魔尚浓。\",\"卷宿吟销永日，移床坐对千峰。\",\"苍苔冷锁幽径，微风闲坐古松。\",\"自说年来老病，出门渐觉疏慵。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70d892eda303693cc','海城秋日书怀寄朐山孙明府','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"槐柳蝉声起渡头，海城孤客思悠悠。\",\"青云展志知何日，皓月牵吟又入秋。\",\"鉴里渐生潘岳鬓，风前犹著卜商裘。\",\"鸣琴良宰挥毫士，应笑蹉跎身未酬。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70db88ef267cb69e9','赠史虚白','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"致主嘉谋尚未伸，慨然深志与谁论。\",\"唤回古意琴开匣，陶出真情酒满樽。\",\"明月过溪吟钓艇，落花堆席睡僧轩。\",\"九重梦卜时终在，莫向深云独闭门。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70e33cb716fe525d7','送张惟贞少府之江阴','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"相送烟汀畔，酒阑登小舟。\",\"离京梅雨歇，到邑早蝉秋。\",\"俗必期康济，诗谁互唱酬。\",\"晚凉诸吏散，海月入虚楼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb70ece057a2a57d716','题吉水县厅前新栽小松','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"斸开幽涧藓苔斑，移得孤根植砌前。\",\"影小未遮官舍月，翠浓犹带旧山烟。\",\"群花解笑香宁久，众木虽高节不坚。\",\"输我婆娑栏槛内，晚风萧飒学幽泉。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71023d5154940fa1f','和彭正字喜雪见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"千门忻应瑞，偏称上楼看。\",\"密洒虚窗晓，狂飘大野寒。\",\"园深宜竹树，帘卷洽杯盘。\",\"已作丰年兆，黎民意尽安。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71044ccb1d040d8c3','都下再会友人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"谁言多难后，重会喜淹留。\",\"欲话关河梦，先惊鬓发秋。\",\"浮云空冉冉，远水自悠悠。\",\"多谢开青眼，携壶共上楼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb710ad31ca8a3c02a7','游北山洞神宫','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闷见尘中光景促，仙乡来礼紫阳君。\",\"人居淡寂应难老，道在虚无不可闻。\",\"松桧稳栖三岛鹤，楼台闲锁九霄云。\",\"羡师向此朝星斗，一炷清香午夜焚。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7119d4dd600ffbe69','和浔阳宰感旧绝句五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"追感古今情不已，竹轩闲取史书看。\",\"欲亲往哲无因见，空树临风襟袖寒。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7119d4dd69e7abdb2','和浔阳宰感旧绝句五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"园林春媚千花发，烂熳如将画障看。\",\"惟爱松筠多冷淡，青青偏称雪霜寒。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7119d4dd6a26157e8','和浔阳宰感旧绝句五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"浔阳物景真难及，练泻澄江最好看。\",\"曾上虚楼吟倚槛，五峰擎雪照人寒。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7119d4dd6dbb29b93','和浔阳宰感旧绝句五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"昔岁寻芳忻得侣，江堤物景尽情看。\",\"就中吟恋垂杨下，撼起啼莺晚吹寒。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7119d4dd6dd13c9f0','和浔阳宰感旧绝句五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"知君百里鸣琴处，公退千山尽日看。\",\"政化有同风偃草，更将馀力拯孤寒。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb711a669aa32f051c6','送王道士游东海','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"巨浸常牵梦，云游岂觉劳。\",\"遥空收晚雨，虚阁看秋涛。\",\"必若思三岛，应须钓六鳌。\",\"如通十洲去，谁信碧天高。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71230911df4278fde','送虞道士','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"烟霞聚散通三岛，星斗分明在一壶。\",\"笑说馀杭沽酒去，蔡家重要会麻姑。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7134e5d20c5ca0859','柳絮','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"年年二月暮，散乱杂飞花。\",\"雨过微风起，狂飘千万家。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7162eecdf19bf3d4e','所思代人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"巫峡云深湘水遥，更无消息梦空劳。\",\"梦回深夜不成寐，起立闲庭花月高。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb716d4cb0dbf7c4988','晋陵县夏日作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"事简公庭静，开帘暑气中。\",\"依经煎绿茗，入竹就清风。\",\"至论招禅客，忘机忆钓翁。\",\"晚凉安枕簟，海月出墙东。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71722c06fb8f2baa9','题柴司徒亭假山','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"叠石峨峨象翠微，远山魂梦便应稀。\",\"从教藓长添峰色，好引泉来作瀑飞。\",\"萤影夜攒疑烧起，茶烟朝出认云归。\",\"知君创得兹幽致，公退吟看到落晖。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb718c49249ab3302d8','悼怀王丧妃','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"花绽花开事可惊，暂来浮世返蓬瀛。\",\"楚宫梦断云空在，洛浦神归月自明。\",\"香解返魂成浪语，胶能续断是虚名。\",\"音容寂寞春牢落，谁会楼中独立情。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71a83a92815123930','杪秋夕吟怀寄宋维先辈','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"江岛穷秋木叶稀，月高何处捣寒衣。\",\"苦嗟不见登龙客，此夜悠悠一梦飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71aa7e03d9ab3bcc4','暮春吟怀寄姚端先辈','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"无奈诗魔旦夕生，更堪芳草满长汀。\",\"故人还爽花前约，新月又生江上亭。\",\"庄梦断时灯欲烬，蜀魂啼处酒初醒。\",\"何时得见登龙客，隔却千山万仞青。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71b558de3687a08c3','客中寒食','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"旅次经寒食，思乡泪湿巾。\",\"音书天外断，桃李雨中春。\",\"欲饮都无绪，唯吟似有因。\",\"输他郊郭外，多少踏青人。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71c31f65bbdc29c27','下蔡春暮旅怀','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"柳过春霖絮乱飞，旅中怀抱独悽悽。\",\"月生淮上云初散，家在江南梦去迷。\",\"发白每惭清鉴启，心孤长怯子规啼。\",\"拜恩为养慈亲急，愿向明朝捧紫泥。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71caeff96cefbab2e','和易秀才春日见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"每恨多流落，吾徒不易亲。\",\"相逢千里客，共醉百花春。\",\"小槛山当面，闲阶柳拂尘。\",\"何时卜西上，明月桂枝新。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb71efed6d9694c7a60','送夏侯秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"江村摇落暮蝉鸣，执手临岐动别情。\",\"古岸相看残照在，片帆难驻好风生。\",\"牵吟一路逢山色，醒睡长汀对月明。\",\"况是清朝至公在，预知乔木定迁莺。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb722f3d9c5822a1f98','江村晚秋作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"高秋水村路，隔岸见人家。\",\"好是经霜叶，红于带露花。\",\"临罾鱼易得，就店酒难赊。\",\"吟兴胡能尽，风清日又斜。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7230bfdea5fe39a9d','再游洞神宫怀邵羽人有感','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"重向烟萝省旧游，因寻遗迹想浮丘。\",\"峰头鹤去三清远，坛畔月明千古秋。\",\"泉落小池清复咽，云从高峤起还收。\",\"自惭未得冲虚术，白发无情渐满头。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7250082fc9823d62b','魏夫人坛','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"仙坛遗迹在，苔合落花明。\",\"绛节何年返，白云终日生。\",\"旋新芳草色，依旧偃松声。\",\"欲问希夷事，音尘隔上清。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb726261146be2e11c9','秋江夜泊寄刘钧','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"万里江山敛暮烟，旅情当此独悠然。\",\"沙汀月冷帆初卸，苇岸风多人未眠。\",\"已听渔翁歌别浦，更堪边雁过遥天。\",\"与君共俟酬身了，结侣波中寄钓船。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72673734fd8a8be74','燕','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"豪家五色泥香，衔得营巢太忙。\",\"喧觉佳人昼梦，双双犹在雕梁。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb729ddb4942912137e','献乔侍郎','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"位望谁能并，当年志已伸。\",\"人间传凤藻，天上演龙纶。\",\"贾马才无敌，褒雄誉益臻。\",\"除奸深系念，致主迥忘身。\",\"谏疏纵横上，危言果敢陈。\",\"忠贞虽贯世，消长岂由人。\",\"慷慨辞朝阙，迢遥涉路尘。\",\"千山明夕照，孤棹渡长津。\",\"杜宇声方切，江蓠色正新。\",\"卷舒唯合道，喜愠不劳神。\",\"蝉客陪清论，渔翁作近邻。\",\"静吟穷野景，狂醉养天真。\",\"格论思名士，舆情渴直臣。\",\"九霄恩复降，比户意皆忻。\",\"却入鸳鸾序，终身顾问频。\",\"漏残丹禁晓，日暖玉墀春。\",\"鉴物心如水，忧时鬓若银。\",\"惟期康庶事，永要叙彝伦。\",\"贵贱知无间，孤寒必许亲。\",\"几多沈滞者，拭目望陶钧。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72a4c679ee9e94680','新秋有感','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"门巷凉秋至，高梧一叶惊。\",\"渐添衾簟爽，顿觉梦魂清。\",\"暗促莲开艳，乍催蝉发声。\",\"雨降炎气减，竹引冷烟生。\",\"戍客添归思，行人怯远程。\",\"未逢征雁下，渐听夜砧鸣。\",\"张翰思鲈兴，班姬咏扇情。\",\"音尘两难问，蛩砌月空明。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72a7e0d7a90712534','七夕','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"星河耿耿正新秋，丝竹千家列彩楼。\",\"可惜穿针方有兴，纤纤初月苦难留。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72b25a6d7add49225','寄左偃','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"萧条陋巷绿苔侵，何事君心似我心。\",\"贫户懒开元爱静，病身才起便思吟。\",\"闲留好鸟庭柯密，暗养鸣蛩砌草深。\",\"况是清朝重文物，无愁当路少知音。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72b25a6d7bbc5acf8','寄左偃','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"每病风骚路，荒凉人莫游。\",\"惟君还似我，成癖未能休。\",\"舍寐缘孤月，忘形为九秋。\",\"垂名如不朽，那恨雪生头。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72cf1b457017f5658','落花','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"残红引动诗魔，怀古牵情奈何。\",\"半落铜台月晓，乱飘金谷风多。\",\"悠悠旋逐流水，片片轻黏短莎。\",\"谁见长门深锁，黄昏细雨相和。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72e3e8b7e319134f9','海上和柴军使清明书事','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"清明时节好烟光，英杰高吟兴味长。\",\"捧日即应还禁卫，当春何惜醉朐阳。\",\"千山过雨难藏翠，百卉临风不藉香。\",\"却是旅人悽屑甚，夜来魂梦到家乡。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72ff775af61c896e4','渔父二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"偶向芦花深处行，溪光山色晚来晴。\",\"渔家开户相迎接，稚子争窥犬吠声。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb72ff775afb82bdb58','渔父二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"偶向芦花深处行，溪光山色晚来晴。\",\"渔家开户相迎接，稚子争窥犬吠声。\",\"雪鬓衰髯白布袍，笑携赪鲤换村醪。\",\"殷勤留我宿溪上，钓艇归来明月高。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb731dd8d9b6a8f1e02','庐山栖隐洞谭先生院留题','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"坛畔归云冷湿襟，拂苔移石坐花阴。\",\"偶然醒得庄周梦，始觉玄门兴味深。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb736dea6de7ad77270','秋夜吟寄左偃','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"与君诗兴素来狂，况入清秋夜景长。\",\"溪阁共谁看好月，莎阶应独听寒螀。\",\"卷中新句诚堪喜，身外浮名不足忙。\",\"会约垂名继前哲，任他玄发尽如霜。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb737789ec40c59700d','江行值暴风雨','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"风狂雨暗舟人惧，自委神明志不邪。\",\"投得苇湾波浪息，岸头烟火近人家。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb73b99cfab647818d7','晚春客次偶吟','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"暂驻征轮野店间，悠悠时节又春残。\",\"落花风急宿酲解，芳草雨昏春梦寒。\",\"惭逐利名头易白，欲眠云水志犹难。\",\"却怜村寺僧相引，闲上虚楼共倚栏。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb73c0ff891db6ad57e','寄庐山庄隐土','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"烟萝拥竹关，物外自求安。\",\"逼枕溪声近，当檐岳色寒。\",\"药苗应自采，琴调对谁弹。\",\"待了浮名后，依君共挂冠。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb73ef48a517af050a3','得故人消息','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"多难分离久，相思每泪垂。\",\"梦归残月晓，信到落花时。\",\"未必乖良会，何当有后期。\",\"那堪楼上望，烟水接天涯。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb73f9ccd6be7e14eca','莺','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"羽毛特异诸禽，出谷堪听好音。\",\"薄暮欲栖何处，雨昏杨柳深深。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7403cef202f2f372a','感事呈所知','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"竞爱松筠翠，皆怜桃李芳。\",\"如求济世广，桑柘愿商量。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb741274a23bd42364f','访蔡文庆处士留题','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"幽人栖息处，一到涤尘心。\",\"藓色花阴阔，棋声竹径深。\",\"篱根眠野鹿，池面戏江禽。\",\"多谢相留宿，开樽拂素琴。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7416d5791e1dcc0c2','和友人喜雪','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"腊雪频频降，成堆不可除。\",\"伴吟花莫并，销瘴药何如。\",\"谢女诗成处，袁安睡起初。\",\"深迷樵子径，冷逼旅人居。\",\"惹砌催樽俎，飘窗入簿书。\",\"最宜楼上望，散乱满空虚。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb741d56e348ed03eeb','吉水春暮访蔡文庆处士留题','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"无事无忧鬓任苍，浊醪闲酌送韶光。\",\"溟蒙雨过池塘暖，狼藉花飞砚席香。\",\"好古未尝疏典册，悬图时要看潇湘。\",\"恋君清话难留处，归路迢迢又夕阳。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74277eeb73e935ea4','书王秀才壁','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"茅舍何寥落，门庭长绿芜。\",\"贫来卖书剑，病起忆江湖。\",\"对枕暮山碧，伴吟凉月孤。\",\"前贤多晚达，莫叹有霜须。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74326efa12f6e189b','送人南游','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"浪迹天涯去，南荒必动情。\",\"草青虞帝庙，云暗夜郎城。\",\"越鸟惊乡梦，蛮风解宿酲。\",\"早思归故里，华发等闲生。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7444ca0dcd04dae08','献中书张舍人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"仙桂从攀后，人间播大名。\",\"飞腾谐素志，霄汉是前程。\",\"持宪威声振，司言品秩清。\",\"帘开春酒醒，月上草麻成。\",\"丹陛凌晨对，青云逐步生。\",\"照人裴玉莹，鉴物宪陂明。\",\"下直无他事，闲游恣逸情。\",\"林僧开户接，溪叟扫苔迎。\",\"煮茗山房冷，垂纶野艇轻。\",\"神清宜放旷，诗苦益纵横。\",\"馀刃时皆仰，嘉谋众伫行。\",\"四方观启沃，毕竟念孤平。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb746468d62e0fbd63b','寄庐山简寂观重道者','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"忆昔采芝庐岳顶，清宫常接绛霄人。\",\"玉书闲展石楼晓，瑶瑟醉弹琪树春。\",\"惟恨仙桃迟结实，不忧沧海易成尘。\",\"似醒一梦归凡世，空向彤霞寄梦频。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb748142f62b97af8f9','酒醒','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"睡觉花阴芳草软，不知明月出墙东。\",\"杯盘狼藉人何处，聚散空惊似梦中。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb748a5cf0dfa8c8b5d','海上载笔依韵酬左偃见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"都城分别后，海峤梦魂迷。\",\"吟兴疏烟月，边情起鼓鼙。\",\"戍旗风飐小，营柳雾笼低。\",\"草檄无馀刃，难将阮瑀齐。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74ac06ef2bbac8cda','旅夜闻笛','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"长笛起谁家，秋凉夜漏赊。\",\"一声来枕上，孤客在天涯。\",\"木末风微动，窗前月渐斜。\",\"暗牵诗思苦，不独落梅花。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74b53736e41860e1a','早春','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"一种和风至，千花未放妍。\",\"草心并柳眼，长是被恩先。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74c4fb32ef3f4f120','梅花','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"群木方憎雪，开花长在先。\",\"流莺与舞蝶，不见许因缘。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74d0e2020af52d258','吉水作尉时酬阎侍御见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"谬佐驱鸡任，常思赋鵩人。\",\"未谐林下约，空感病来身。\",\"锁径青苔老，铺阶红叶新。\",\"相思不可见，犹喜得书频。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74d5a9caf8f353987','春日野望怀故人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"野外登临望，苍苍烟景昏。\",\"暖风医病草，甘雨洗荒村。\",\"云散天边野，潮回岛上痕。\",\"故人不可见，倚杖役吟魂。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74db00c7fef6a0eaa','闲居言怀','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"未达难随众，从他俗所憎。\",\"闲听九秋雨，远忆四明僧。\",\"病后倦吟啸，贫来疏友朋。\",\"寂寥元合道，未必是无能。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74dcfe1cb65a53db9','送阎侍御归阙','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"羡君乘紫诏，归路指通津。\",\"鼓棹烟波暖，还京雨露新。\",\"趋朝丹禁晓，耸辔九衢春。\",\"自愧湮沈者，随轩未有因。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74e94d4738886312a','春苔','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"春霖催得锁烟浓，竹院莎斋径小通。\",\"谁爱落花风味处，莫愁门巷衬残红。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb74eecd5b8f2ec37f6','书夏秀才幽居壁','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"永巷苔深户半开，床头书剑积尘埃。\",\"最怜小槛疏篁晚，幽鸟双双何处来？\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb750064b404449be03','竹','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"森森移得自山庄，植向空庭野兴长。\",\"便有好风来枕簟，更无闲梦到潇湘。\",\"荫来砌藓经疏雨，引下溪禽带夕阳。\",\"闲约羽人同赏处，安排棋局就清凉。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7508d73be14076f77','献中书汤舍人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"庆云呈瑞为明时，演畅丝纶在紫微。\",\"銮殿对时亲舜日，鲤庭过处著莱衣。\",\"闲寻竹寺听啼鸟，吟倚江楼恋落晖。\",\"隔座银屏看是设，一门清贵古今稀。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb750adafed86e1875e','题庐山东寺远大师影堂','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"远公遗迹在东林，往事名存动苦吟。\",\"杉桧已依灵塔老，烟霞空锁影堂深。\",\"入帘轻吹催香印，落石幽泉杂磬音。\",\"十八贤人消息断，莲池千载月沈沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7512f8e16f536bb30','送圆','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"莲宫旧隐尘埃外，策杖临风拂袖还。\",\"踏雪独寻青嶂下，听猿重入白云间。\",\"萧骚红树当门老，斑驳苍苔锁径闲。\",\"预想松轩夜禅处，虎溪圆月照空山。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb753c1a27831074e10','祀风师迎神曲','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"太皞御气，勾芒肇功。\",\"苍龙青旗，爰候祥风。\",\"律以和应，□以感通。\",\"鼎徂修蚃，时惟礼崇。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7542fe3b5c5957a14','春云','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"阴去为膏泽，晴来媚晓空。\",\"无心亦无滞，舒卷在东风。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75435bc2bf508b56d','暮春怀故人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"池馆寂寥三月尽，落花重叠盖莓苔。\",\"惜春眷恋不忍扫，感物心情无计开。\",\"梦断美人沈信息，目穿长路倚楼台。\",\"琅玕绣段安可得，流水浮云共不回。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb756c592e182687d88','听蝉寄朐山孙明府','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"忽听新蝉发，客情其奈何。\",\"西风起槐柳，故国阻烟波。\",\"垄笛悲犹少，巴猿恨未多。\",\"不知陶靖节，还动此心么。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb759f61864cfc94370','赠朐山杨宰','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"讼闲徵赋毕，吏散卷帘时。\",\"听雨入秋竹，留僧覆旧棋。\",\"得诗书落叶，煮茗汲寒池。\",\"化俗功成后，烟霄会有期。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75a8066dba45cce81','宿庐山白云峰重道者院','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"绝顶松堂喜暂游，一宵玄论接浮丘。\",\"云开碧落星河近，月出沧溟世界秋。\",\"尘里年光何急急，梦中强弱自悠悠。\",\"他时书剑酬恩了，愿逐鸾车看十洲。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75ad594875a0a2b67','春日书怀寄朐山孙明府','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"一作边城客，闲门两度春。\",\"莺花深院雨，书剑满床尘。\",\"紫阁期终负，青云道未伸。\",\"犹怜陶靖节，诗酒每相亲。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75b00ed7416e3693a','怀王道者','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闲思王道者，逸格世难群。\",\"何处眠青嶂，从来爱白云。\",\"酒沽应独醉，药熟许谁分。\",\"正作趋名计，如何得见君。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75b87bd709d7f2f9f','泉','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"潺潺青嶂底，来处一何长。\",\"漱石苔痕滑，侵松鹤梦凉。\",\"泛花穿竹坞，泻月下莲塘。\",\"想得归何处，天涯助渺茫。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75bfc8ce29da22e2a','赠蒯亮处士','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"著得新书义更幽，负琴何处不遨游。\",\"玄宫寄宿月华冷，羽客伴吟松韵秋。\",\"满户烟霞思紫阁，一帆风雨忆沧洲。\",\"吾君侧席求贤切，未可悬瓢枕碧流。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75ed0751c04c6a718','言志寄刘钧秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"童稚亲儒墨，时平喜道存。\",\"酬身指书剑，赋命委乾坤。\",\"秋爽鼓琴兴，月清搜句魂。\",\"与君同此志，终待至公论。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb75f4ab482ee5a292d','江馆秋思因成自勉','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"江边候馆幽，汀鸟暝烟收。\",\"客思虽悲月，诗魔又爱秋。\",\"声名都是幻，穷达未能忧。\",\"散逸怜渔父，波中漾小舟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7649ce95735c2143e','和毗陵尉','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"决狱多馀暇，冥搜万家空。\",\"卷帘疏雨后，锁印夕阳中。\",\"还往多名士，编题尚古风。\",\"宦途知此味，能有几人同。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb76713ea568a74c67d','红花','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"红花颜色掩千花，任是猩猩血未加。\",\"染出轻罗莫相贵，古人崇俭诫奢华。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb767736a65d83e4acd','思旧游有感','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"长忆衔杯处，酕醄尚未阑。\",\"江南正烟雨，楼上恰春寒。\",\"好树藏莺密，平芜彻野宽。\",\"如今无处觅，音信隔波澜。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7679fce3e271a2a48','途中柳','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"翠色晴来近，长亭路去遥。\",\"无人折烟缕，落日拂溪桥。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb767b6c2d507ac0c5f','春晚招鲁从事','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"衮衮利名役，常嗟聚会稀。\",\"有心游好景，无术驻残晖。\",\"南陌草争茂，西园花乱飞。\",\"期君举杯酒，不醉莫言归。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb76dd074eaa95755b0','送庐阜僧归山阳','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"山阳旧社终经梦，容易言归不可留。\",\"瓶贮瀑泉离五老，锡摇江雨上孤舟。\",\"鱼行细浪分沙觜，雁逆高风下苇洲。\",\"遥想枚皋宅边寺，不知凉月共谁游。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb770e09ede03793c14','赠海上观音院文依上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"烟霞海边寺，高卧出门慵。\",\"白日少来客，清风生古松。\",\"虚窗从燕入，坏屐任苔封。\",\"几度陪师话，相留到暮钟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb771741c46aa4be4c4','钟陵禁烟寄从弟','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"落絮飞花日又西，踏青无侣草萋萋。\",\"交亲书断竟不到，忍听黄昏杜宇啼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77179141009bd0091','经古寺','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"殿宇半隳摧，门临野水开。\",\"云凝何代树，草蔽此时台。\",\"绕塔堆黄叶，沿阶积绿苔。\",\"踟蹰日将暮，栖鸟入巢来。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7758eae9385ba2665','书小斋壁','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"其谁肯见寻，冷淡少知音。\",\"尘土侵闲榻，烟波隔故林。\",\"竹风醒晚醉，窗月伴秋吟。\",\"道在唯求己，明时岂陆沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb775ba455fb8ae0468','暮春有感寄宋维员外','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"杜宇声中老病心，此心无计驻光阴。\",\"西园雨过好花尽，南陌人稀芳草深。\",\"喧梦却嫌莺语老，伴吟惟怕月轮沈。\",\"明年才候东风至，结驷期君预去寻。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb775f56f1d7c3ac681','贻毗陵正勤禅院奉长老','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"随缘驻瓶锡，心已悟无生。\",\"默坐烟霞散，闲观水月明。\",\"竹深风倍冷，堂迥磬偏清。\",\"愿作传灯者，忘言学净名。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7762aef6e41ffdea8','贻庐山清溪观王尊师','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"霞帔星冠复杖藜，积年修鍊住灵溪。\",\"松轩睡觉冷云起，石磴坐来春日西。\",\"采药每寻岩径远，弹琴常到月轮低。\",\"鼎中龙虎功成后，海上三山去不迷。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77708069b8218bf0b','吉水作尉酬高援秀才见赠','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"佐邑惭无术，敢言贫与清。\",\"风骚谁是主，烟月自关情。\",\"卷箔当山色，开窗就竹声。\",\"怜君惠嘉句，资我欲垂名。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7788f416373220cf0','春闺辞二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"卷帘迟日暖，睡起思沈沈。\",\"辽海音尘远，春风旅馆深。\",\"疏篁留鸟语，曲砌转花阴。\",\"寄语长征客，流年不易禁。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7788f4163ca42bcff','春闺辞二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"不得辽阳信，春心何以安。\",\"鸟啼窗树晓，梦断碧烟残。\",\"绿鉴开还懒，红颜驻且难。\",\"相思谁可诉，时取旧书看。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb778a013e4e7c4f3c4','广陵寒食夜','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"广陵寒食夜，豪贵足佳期。\",\"紫陌人归后，红楼月上时。\",\"绮罗香未歇，丝竹韵犹迟。\",\"明日踏青兴，输他轻薄儿。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77994795b6f43e7b4','吉水县酬夏侯秀才见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"启鉴悠悠两鬓苍，病来心绪易凄凉。\",\"知音不到吟还懒，锁印开帘又夕阳。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb779e218800fe4485c','赠永真','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"蓝袍竹简佐琴堂，县僻人稀觉日长。\",\"爱静不嫌官况冷，苦吟从听鬓毛苍。\",\"闲寻野寺听秋水，寄睡僧窗到夕阳。\",\"骞翥会应霄汉去，渔竿休更恋沧浪。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77a918f815872c7c2','寄庐岳鉴上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"岳寺栖瓶锡，常人亲亦难。\",\"病披青衲重，晚剃白髭寒。\",\"烘壁茶烟暗，填沟木叶乾。\",\"昔年皆礼谒，频到碧云端。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77b0a61444f1e42fc','赠上都先业大师','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"懒向人前著紫衣，虚堂闲倚一条藜。\",\"虽承雨露居龙阙，终忆烟霞梦虎溪。\",\"睡起晓窗风淅淅，病来深院草萋萋。\",\"有时乘兴寻师去，煮茗同吟到日西。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77c6f1083f0b5044d','送孙孔二秀才游庐山','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"庐山多胜景，偏称二君游。\",\"松径苍苔合，花阴碧涧流。\",\"倾壶同坐石，搜句共登楼。\",\"莫学天台客，逢山即驻留。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77d92fc09e90d0ccb','又送赴关','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"心似白云归帝乡，暂停良画别龚黄。\",\"烟波乍晓浮兰棹，魂梦先飞近御香。\",\"一路伴吟汀草绿，几程清思水风凉。\",\"想应敷对忠言后，不放乡云离太阳。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77e0ac4f8e43d766d','海上春夕旅怀寄左偃','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"柳过清明絮乱飞，感时怀旧思悽悽。\",\"月生楼阁云初散，家在汀洲梦去迷。\",\"发白每惭清鉴启，酒醒长怯子规啼。\",\"北山高卧风骚客，安得同吟复杖藜。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77e3e2624d0c0f188','和夏侯秀才春日见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"绵蛮黄鸟不堪听，触目离愁怕酒醒。\",\"云散碧山当晚槛，雨催青藓匝春庭。\",\"寻芳懒向桃花坞，垂钓空思杜若汀。\",\"昼梦不成吟有兴，挥毫书在枕边屏。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb77eaba84b61cfdbf2','江边吟','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"风暖汀洲吟兴生，远山如画雨新晴。\",\"残阳影里水东注，芳草烟中人独行。\",\"闪闪酒帘招醉客，深深绿树隐啼莺。\",\"盘桓渔舍忘归去，云静高空月又明。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7816ab490d9a8f577','甲子岁罢吉水县过钟陵时暮春维舟江渚谒柴太尉席上作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"公侯延驻暂踟蹰，况值风光三月初。\",\"乱落杯盘花片小，静笼池阁柳阴疏。\",\"舟维南浦程虽阻，饮预西园兴有馀。\",\"却笑田家门下客，当时容易叹车鱼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb781819ac8192fdaa3','送汪涛','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"知君别家后，不免泪沾襟。\",\"芳草千里路，夕阳孤客心。\",\"花飞当野渡，猿叫在烟岑。\",\"霄汉知音在，何须恨陆沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb783cd5c2080d14457','送戴秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"已是殊乡客，送君重惨然。\",\"河桥乍分首，槐柳正鸣蝉。\",\"短棹离幽浦，孤帆触远烟。\",\"清朝重文物，变化莫迁延。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb784b20bb6fbdf7ab4','送孙霁书记赴寿阳辟命','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"辟命羡君赴，其如怆别情。\",\"酒阑汀树晚，帆展野风生。\",\"淮静寒烟敛，村遥夜火明。\",\"醉沈朐岭梦，吟达寿春城。\",\"旧友摇鞭接，元戎扫榻迎。\",\"雪晴莲幕启，云散桂山横。\",\"王粲从军画，陈琳草檄名。\",\"知君提健笔，重振此嘉声。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb785e20929b418ae80','宿韦校书幽居','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"溪上高眠与鹤闲，开樽留我待柴关。\",\"园林月白秋霖歇，一夜泉声似故山。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7865f05833ef40af1','思朐阳春游感旧寄柴司徒五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"南陌风和舞蝶狂，惜春公子恋斜阳。\",\"高歌饮罢将回辔，衣上花兼百草香。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7865f058371f77ac5','思朐阳春游感旧寄柴司徒五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"昔年常接五陵狂，洪饮花间数十场。\",\"别后或惊如梦觉，音尘难问水茫茫。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7865f05837540ac92','思朐阳春游感旧寄柴司徒五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"王孙昔日甚相亲，共赏西园正媚春。\",\"醉卧如茵芳草上，觉来花月影笼身。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7865f0583828eafe0','思朐阳春游感旧寄柴司徒五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"春郊饮散暮烟收，却引丝簧上翠楼。\",\"红袖歌长金斝乱，银蟾飞出海东头。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7865f0583f38500c0','思朐阳春游感旧寄柴司徒五首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"烟铺芳草正绵绵，藉草传杯似列仙。\",\"暂辍笙歌且联句，含毫花下破香笺。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7877e5d23b29d9444','寄赠致仕沈彬郎中','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"鹤氅换朝服，逍遥云水乡。\",\"有时乘一叶，载酒入三湘。\",\"尘梦年来息，诗魔老亦狂。\",\"莼羹与鲈鲙，秋兴最宜长。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb787a0fda6f3bb2153','寄杨先生','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"仙翁别后无信，应共烟霞卜邻。\",\"莫把壶中秘诀，轻传尘里游人。\",\"浮生日月自急，上境莺花正春。\",\"安得一招琴酒，与君共泛天津。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb788b61ccb7ad1cacc','遥赋义兴潜泉','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"见说灵泉好，潺湲兴莫穷。\",\"谁当秋霁后，独听月明中。\",\"溅石苔花润，随流木叶红。\",\"何当化霖雨，济物显殊功。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb789fb69f56a58a691','柴司徒宅牡丹','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"暮春栏槛有佳期，公子开颜乍拆时。\",\"翠幄密笼莺未识，好香难掩蝶先知。\",\"愿陪妓女争调乐，欲赏宾朋预课诗。\",\"只恐却随云雨去，隔年还是动相思。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb78b64d6aa178a7a02','游茅山二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"绿藓深迎步，红霞烂满衣。\",\"洞天应不远，鸾鹤向人飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb78b64d6aa9a5b1258','游茅山二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"茅许仙踪在，烟霞一境清。\",\"夷希何许叩，松径月空明。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb78d105d2de1bd0c09','献张义方常侍','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"雄飞看是逼嵓廊，逸思常闻不暂忘。\",\"公署静眠思水石，古屏闲展看潇湘。\",\"老来酒病虽然减，秋杪诗魔更是狂。\",\"乘兴有时招羽客，横琴移月启茅堂。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb792fed0d8ed8ce6a9','邮亭早起','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"邮舍残灯在，村林鸡唱频。\",\"星河吟里晓，川陆望中春。\",\"旧友青云贵，殊乡素发新。\",\"悠悠念行计，难更驻征轮。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb793c6cfa2314b1268','贻青阳宰','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"徵赋常登限，名山管最多。\",\"吏闲民讼少，时得访烟萝。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7956b31c4f25b22f9','宿山中寺','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"寄宿深山寺，惟逢老病僧。\",\"风吹几世树，云暗暮秋灯。\",\"瞑目忘尘虑，谈空入上乘。\",\"明晨返名路，何计恋南能。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb795b953557dc63f0c','思湓渚旧居','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"昔岁曾居湓水头，草堂吟啸兴何幽。\",\"迎僧常踏竹间藓，爱月独登溪上楼。\",\"寒翠入檐岚岫晓，冷声萦枕野泉秋。\",\"从拘宦路无由到，昨夜分明梦去游。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb796d0212f87e4713c','春日作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"和气来无象，物情还暗新。\",\"乾坤一夕雨，草木万方春。\",\"染水烟光媚，催花鸟语频。\",\"高台旷望处，歌咏属诗人。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb797917648890ecf8c','春日书怀','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"千峰雪尽鸟声春，日永孤吟野水滨。\",\"霄汉路岐升未得，花时空拂满衣尘。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb79a1c07a12a9dbd0e','吉水寄阎侍御','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"何处怀君切，令人欲白头。\",\"偶寻花外寺，独立水边楼。\",\"不得论休戚，何因校献酬。\",\"吟馀兴难尽，风笛起渔舟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb79d43227a9ed6623f','王昭君','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"蛾眉翻自累，万里陷穷边。\",\"滴泪胡风起，宽心汉月圆。\",\"飞尘长翳日，白草自连天。\",\"谁贡和亲策，千秋污简编。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb79d91dcbd02f66d65','悼亡','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"巷深芳草细，门静绿杨低。\",\"室迩人何处，花残月又西。\",\"武陵期已负，巫峡梦终迷。\",\"独立销魂久，双双好鸟啼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a0586077c705c5b5','夜泊江渚','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"水乡明月上晴空，汀岛香生杜若风。\",\"不是当年独醒客，且沽村酒待渔翁。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a05b3e32974b6346','题徐五教池亭','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"多士池塘好，尘中景恐无。\",\"年来养鸥鹭，梦不去江湖。\",\"泉脉通深涧，风声起短芦。\",\"惊鱼跳藻荇，戏蝶上菰蒲。\",\"泛泛容渔艇，闲闲载酒壶。\",\"涨痕山雨过，翠积岸苔铺。\",\"花影沈波底，烟光入座隅。\",\"晓香怜杜若，夜浸爱蟾蜍。\",\"步逸心难厌，看吟兴不辜。\",\"凭君命奇笔，为我写成图。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a0e39c779d2a277f','送姚端先辈归宁','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"知君归觐省，称意涉通津。\",\"解缆汀洲晓，张帆烟水春。\",\"牵吟芳草远，贳酒乱花新。\",\"拜庆庭闱处，蟾枝香满身。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a13495074c043fe5','访洞神宫邵道者不遇','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闲来仙观问希夷，云满星坛水满池。\",\"羽客不知何处去，洞前花落立多时。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a22b14778fb30066','秋夕病中作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"卧病当秋夕，悠悠枕上情。\",\"不堪抛月色，无计避虫声。\",\"煎药惟忧涩，停灯又怕明。\",\"晓临清鉴里，应有白髭生。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a252ae8d8066d7fd','送姚端秀才游毗陵','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"毗陵嘉景太湖边，才子经游称少年。\",\"风弄轻帘沽酒市，月明红袖采莲船。\",\"若耶罨画应相似，越岫吴峰尽接连。\",\"此去高吟须早返，广寒丹桂莫迁延。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a399191fb714e843','旅次闻砧','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"砧杵谁家夜捣衣，金风淅淅露微微。\",\"月中独坐不成寐，旧业经年未得归。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a45fb6bb6c5ca679','云','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"悠悠离洞壑，冉冉上天津。\",\"捧日终为异，从龙自有因。\",\"高行四海雨，暖拂万山春。\",\"静与霞相近，闲将鹤最亲。\",\"帝乡归莫问，楚殿梦曾频。\",\"白向封中起，碧从诗里新。\",\"冷容横钓浦，轻缕绊蟾轮。\",\"不滞浓还淡，无心卷复伸。\",\"非烟聊拟议，干吕在逡巡。\",\"会作五般色，为祥覆紫宸。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a4e77b6d74fed7ed','再到山阳寻故人不遇二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"维舟登野岸，因访故人居。\",\"乱后知何处，荆榛匝弊庐。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a4e77b6daaf07eca','再到山阳寻故人不遇二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"欲问当年事，耕人都不知。\",\"空馀堤上柳，依旧自垂丝。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a59a55085ace95df','经古观有感','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"古观寥寥枕碧溪，偶思前事立残晖。\",\"漆园化蝶名空在，柱史犹龙去不归。\",\"丹井泉枯苔锁合，醮坛松折鹤来稀。\",\"回头因叹浮生事，梦里光阴疾若飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a61464db3af07aee','客中春思','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"又听黄鸟绵蛮，目断家乡未还。\",\"春水引将客梦，悠悠绕遍关山。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a94139c05e6dbf28','思简寂观旧游寄重道者','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闲忆当年游物外，羽人曾许驻仙乡。\",\"溪头烘药烟霞暖，花下围棋日月长。\",\"偷摘蟠桃思曼倩，化成蝴蝶学蒙庄。\",\"俗缘未断归浮世，空望林泉意欲狂。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7a9fea7fe263247c3','赠夏秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"轩车紫陌竞寻春，独掩衡门病起身。\",\"步月怕伤三径藓，取琴因拂一床尘。\",\"明时傥有丹枝分，青鉴从他素发新。\",\"况是青云知己在，原思生计莫忧贫。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7aa7d581db2993c98','哭柴郎中','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"昔岁遭逢在海城，曾容孤迹奉双旌。\",\"酒边不厌笙歌盛，花下只愁风雨生。\",\"棋接山亭松影晚，吟陪月槛露华清。\",\"音尘自此无因问，泪洒川波夕照明。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7aac775725c801ba8','江次维舟登古寺','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"辍棹因过古梵宫，荒凉门径锁苔茸。\",\"绿阴满地前朝树，清韵含风后殿钟。\",\"童子纵慵眠坏榻，老僧耽话指诸峰。\",\"吟馀却返来时路，回首盘桓尚驻筇。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ac587c6085e8396b','访庐山归章禅伯','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"沈沈石室疏钟后，寂寂莎池片月明。\",\"多少学徒求妙法，要于言下悟无生。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ad6fbd09a040401f','思九江旧居三首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"门前烟水似潇湘，放旷优游兴味长。\",\"虚阁静眠听远浪，扁舟闲上泛残阳。\",\"鹤翘碧藓庭除冷，竹引清风枕簟凉。\",\"犬吠疏篱明月上，邻翁携酒到茅堂。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ad6fbd09bf22cd96','思九江旧居三首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"结茅曾在碧江隈，多病贫身养拙来。\",\"雨歇汀洲垂钓去，月当门巷访僧回。\",\"静临窗下开琴匣，闷向床头泼酒醅。\",\"游宦等闲千里隔，空馀魂梦到渔台。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ad6fbd09f131343e','思九江旧居三首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"无机终日狎沙鸥，得意高吟景且幽。\",\"槛底江流偏称月，檐前山朵最宜秋。\",\"遥村处处吹横笛，曲岸家家系小舟。\",\"别后再游心未遂，设屏惟画白蘋洲。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ae2c9698db77f715','依韵和蠡泽王去微秀才见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"咫尺风骚客，难谐面继酬。\",\"相思对烟雨，一雁下汀洲。\",\"花影谁家坞，笛声何处楼？\",\"支筇朗吟罢，搔首独迟留。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ae9c6f84b1410d4a','赠朐山孙明府','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"县庭无事似山斋，满砌青青旋长苔。\",\"闲抚素琴曹吏散，自烹新茗海僧来。\",\"买将病鹤劳心养，移得闲花用意栽。\",\"几度访君留我醉，瓮香皆值酒新开。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7af30294556be1479','海上从事秋日书怀','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"悠悠旅宦役尘埃，旧业那堪信未回。\",\"千里梦随残月断，一声蝉送早秋来。\",\"壶倾浊酒终难醉，匣锁青萍久不开。\",\"唯有搜吟遣怀抱，凉风时复上高台。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7afe2144a489655dc','腊中作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"冬至虽云远，浑疑朔漠中。\",\"劲风吹大野，密雪翳高空。\",\"泉冻如顽石，人藏类蛰虫。\",\"豪家应不觉，兽炭满炉红。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b06278f48be79d36','春晏寄从弟德润','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"相思禁烟近，楼上动吟魂。\",\"水国春寒在，人家暮雨昏。\",\"朱桥通竹树，香径匝兰荪。\",\"安得吾宗会，高歌醉一尊。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b0b38a4fdc5661bf','怀旧夜吟寄赵杞','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"长笛声中海月飞，桃花零落满庭墀。\",\"魂销事去无寻处，酒醒孤吟不寐时。\",\"萱草岂能忘积恨，尺书谁与达相思。\",\"悠悠方寸何因解，明日江楼望渺弥。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b0c2321a293d3dbb','送智雄上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"忽起游方念，飘然不可留。\",\"未知携一锡，乘兴向何州。\",\"古岸春云散，遥天晚雨收。\",\"想应重会面，风月又清秋。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b2eac595750163e9','姑苏怀古','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"苏台踪迹在，旷望向江滨。\",\"往事谁堪问，连空草自春。\",\"花疑西子脸，涛想伍胥神。\",\"吟尽情难尽，斜阳照路尘。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b2eac595b9ec6222','姑苏怀古','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"阖闾兴霸日，繁盛复风流。\",\"歌舞一场梦，烟波千古愁。\",\"樵人归野径，渔笛起扁舟。\",\"触目牵伤感，将行又驻留。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b438a01ff2f62b20','宿山店书怀寄东林令图上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"一宿山前店，旅情安可穷。\",\"猿声乡梦后，月影竹窗中。\",\"南楚征途阔，东吴旧业空。\",\"虎溪莲社客，应笑此飘蓬。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b4c22e3f6149fa9f','赠王道者','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"混俗从教鬓似银，世人无分得相亲。\",\"槎流海上波涛阔，酒满壶中天地春。\",\"功就不看丹灶火，性闲时拂玉琴尘。\",\"仙家变化谁能测，只恐洪崖是此身。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b52ea60000bc3ee5','代别','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"明日鸣鞭天一涯，悠悠此夕怯分离。\",\"红楼有恨金波转，翠黛无言玉箸垂。\",\"浮蚁不能迷远意，回纹从此寄相思。\",\"花时定是慵开鉴，独向春风忍扫眉。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b535587cfb099c3d','赠致仕沈彬郎中','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"自言婚嫁毕，尘事不关心。\",\"老去诗魔在，春来酒病深。\",\"山翁期采药，海月伴鸣琴。\",\"多谢维舟处，相留接静吟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b5473a2bb94b0f9c','送相里秀才之匡山国子监','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"气秀情闲杳莫群，庐山游去志求文。\",\"已能探虎穷骚雅，又欲囊萤就典坟。\",\"目豁乍窥千里浪，梦寒初宿五峰云。\",\"业成早赴春闱约，要使嘉名海内闻。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b5fc01cfc3ab8300','献徐舍人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"清名喧四海，何止并南金。\",\"奥学群英伏，多才万乘钦。\",\"秩参金殿峻，步历紫微深。\",\"顾闻承中旨，丝纶演帝心。\",\"褒雄饶义路，贾马避词林。\",\"下直无他事，开门对远岑。\",\"轩窗来晚吹，池沼歇秋霖。\",\"藓点生棋石，茶烟过竹阴。\",\"希夷元已达，躁竞岂能侵。\",\"羽客闲陪饮，诗人伴静吟。\",\"自渐为滞物，多幸辱虚襟。\",\"此日重遭遇，心期出陆沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b62206d430f86787','依韵和友人秋夕见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"夕风庭叶落，谁见此时情。\",\"不作关河梦，空闻砧杵声。\",\"会须求至理，何必叹无成。\",\"好约高僧宿，同看海月生。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b6b37af619bc0aa6','书郭判官幽斋壁','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"不妨公退尚清虚，创得幽斋兴有馀。\",\"要引好风清户牖，旋栽新竹满庭除。\",\"倾壶待客花开后，煮茗留僧月上初。\",\"更有野情堪爱处，石床苔藓似匡庐。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b7a373d081c40e3a','秋雨','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"竟日散如丝，吟看半掩扉。\",\"秋声在梧叶，润气逼书帏。\",\"曲涧泉承去，危檐燕带归。\",\"寒蛩悲旅壁，乱藓滑渔矶。\",\"爽欲除幽簟，凉须换熟衣。\",\"疏篷谁梦断，荒径独游稀。\",\"偏称江湖景，不妨鸥鹭飞。\",\"最怜为瑞处，南亩稻苗肥。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b801879c583630f1','依韵和智谦上人送李相公赴昭武军','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"暂别庙堂上，雄藩去豁情。\",\"秋风生雁渚，晚雾湿龙旌。\",\"吟里过侯服，梦中归帝城。\",\"下车军庶乐，千里月华清。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b859627750ff377d','赠','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"五更初起扫松堂，瞑目先焚一炷香。\",\"念彻莲经谁得见，千峰岩外晓苍苍。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b8a5f2f66a500ecd','宿临江驿','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"候馆寥寥辍棹过，酒醒无奈旅愁何。\",\"雨昏郊郭行人少，苇暗汀洲宿雁多。\",\"干禄已悲凋发鬓，结茅终愧负烟萝。\",\"篇章早晚逢知己，苦志忘形自有魔。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7b920f64cafdaf9ac','寄黄鹗秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"长忆狂游日，惜春心恰同。\",\"预愁花片落，不遣酒壶空。\",\"草软眠难舍，莺娇听莫穷。\",\"如今千里隔，骚首对秋风。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ba0fc49316016b13','游玄真观','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闲吟游古观，静虑想神仙。\",\"上景非难度，阴功不易全。\",\"醮坛松作盖，丹井藓成钱。\",\"浩浩红尘里，谁来叩自然。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7bb053e8ba5c4b1f6','赠别','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"行杯酌罢歌声歇，不觉前汀月又生。\",\"自是离人魂易断，落花芳草本无情。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7bb24223425bad076','病中','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闲斋病初起，心绪复悠悠。\",\"开箧群书蠹，听蝉满树秋。\",\"诗魔还渐动，药债未能酬。\",\"为忆前山色，扶持上小楼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7bc7e2ab89f28a8c8','子规','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"暮春滴血一声声，花落年年不忍听。\",\"带月莫啼江畔树，酒醒游子在离亭。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7bca5792d7334e2eb','海城秋夕寄怀舍弟','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"鸟栖庭树夜悠悠，枕上谁知泪暗流。\",\"千里梦魂迷旧业，一城砧杵捣残秋。\",\"窗闲寂寂灯犹在，帘外萧萧雨未休。\",\"早晚莱衣同著去，免悲流落在边州。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7bdd7fc167bb8d557','秋雨二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"竟日声萧飒，兼风不暂阑。\",\"竹窗秋睡美，荻浦夜渔寒。\",\"地僻苔生易，林疏鸟宿难。\",\"谁知苦吟者，坐听一灯残。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7bdd7fc16c9268185','秋雨二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"飘洒当穷巷，苔深落叶铺。\",\"送寒来客馆，滴梦在庭梧。\",\"逼砌蛩声断，侵窗竹影孤。\",\"遥思渔叟兴，蓑笠在江湖。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7be029a1db1f682e1','喜春雨有寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"青春终日雨，公子莫思晴。\",\"任阻西园会，且观南亩耕。\",\"最怜滋垄麦，不恨湿林莺。\",\"父老应相贺，丰年兆已成。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7be0a5befa2675225','书情寄诗友','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"默默谁知我，裴回野水边。\",\"诗情长若旧，吾事更无先。\",\"芳草人稀地，残阳雁过天。\",\"静思吟友外，此意复谁怜。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c3097ed309b3fac2','清溪逢张惟贞秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"洞隐红霞外，房开碧嶂根。\",\"昔年同鍊句，几夜共听猿。\",\"考古书千卷，忘忧酒一樽。\",\"如今归建业，雅道喜重论。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c3aa25e18f642bcd','放鹭鸶','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"池塘多谢久淹留，长得霜翎放自由。\",\"好去蒹葭深处宿，月明应认旧江秋。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c478d4d18712b112','送朐山孙明府赴寿阳幕府辟命','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"堪羡元戎虚右席，便承纶綍起金台。\",\"菊丛憔悴陶潜去，莲幕光辉阮瑀来。\",\"好向尊罍陈妙画，定应书檄播雄才。\",\"预愁别后相思处，月入闲窗远梦回。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c4aa2adafc843fa4','宿钟山知觉院','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"宿投林下寺，中夜觉神清。\",\"磬罢僧初定，山空月又生。\",\"笼灯吐冷艳，岩树起寒声。\",\"待晓红尘里，依前冒远程。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c7ce8bafa2b682a6','隔墙花','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"颜色尤难近，馨香不易通。\",\"朱门金锁隔，空使怨春风。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c8c57717ef99ab82','赠海上书记张济员外','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"鹏霄休叹志难伸，贫病虽萦道且存。\",\"阮瑀不能专笔砚，嵇康唯要乐琴尊。\",\"春风满院空欹枕，芳草侵阶独闭门。\",\"剑有尘埃书有蠹，昔年心事共谁论。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7c8d968ed18d159d0','维舟秋浦逢故人张矩同泊','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"卸帆清夜碧江滨，冉冉凉风动白蘋。\",\"波上正吟新霁月，岸头恰见故乡人。\",\"共惊别后霜侵鬓，互说年来疾逼身。\",\"且饮一壶销百恨，会须遭遇识通津。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ca9a088f32a48491','新喻县偶寄彭仁正字','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"经年离象魏，孤宦在南荒。\",\"酒醒公斋冷，雨多归梦长。\",\"志难酬国泽，术欠致民康。\",\"吾子应相笑，区区道未光。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7cbee23823d474af7','壬申岁承命之任淦阳再过庐山国学感旧寄刘钧明府','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"三十年前共苦辛，囊萤曾寄此烟岑。\",\"读书灯暗嫌云重，搜句石平怜藓深。\",\"各历宦途悲聚散，几看时辈或浮沉。\",\"再来物景还依旧，风冷松高猿狖吟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7cfc3b64cf94bb046','访澄上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"寻师来静境，神骨觉清凉。\",\"一饷逢秋雨，相留坐竹堂。\",\"石渠堆败叶，莎砌咽寒螀。\",\"话到南能旨，怡然万虑忘。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d0b7c8420acf7ff0','赠重安寂道者','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"寒松肌骨鹤心情，混俗陶陶隐姓名。\",\"白发只闻悲短景，红尘谁解信长生。\",\"壶中日月存心近，岛外烟霞入梦清。\",\"每许相亲应计分，琴馀常见话蓬瀛。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d41eb9ad5d4751e8','烈祖孝高挽歌二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"谁解叩乾关，音容去不还。\",\"位方尊北极，寿忽殒南山。\",\"凤辇应难问，龙髯不可攀。\",\"千秋遗恨处，云物锁桥山。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d41eb9adf8b90f8b','烈祖孝高挽歌二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"仙驭归何处，苍苍问且难。\",\"华夷喧道德，陵垄葬衣冠。\",\"御水穿城咽，宫花泣露寒。\",\"九疑消息断，空望白云端。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d4ae6cf9b3106f25','送刘恭游庐山兼寄令上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"松桂烟霞蔽梵宫，诗流闲去访支公。\",\"石堂磬断相逢夜，五老月生溪影空。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d5436735af5c978a','庭竹','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"偶自山僧院，移归傍砌栽。\",\"好风终日起，幽鸟有时来。\",\"筛月牵诗兴，笼烟伴酒杯。\",\"南窗睡轻起，萧飒雨声回。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d6fc44e1360e8e77','庐山','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"控厌浔阳景，崔嵬古及今。\",\"势雄超地表，翠盛接天心。\",\"湓浦春烟列，星湾晚景沈。\",\"图经宜细览，题咏卒难任。\",\"靖节门遥对，庾公楼俯临。\",\"参差含积雪，隐映见归禽。\",\"峭拔推双剑，清虚数二林。\",\"白莲池宛在，翠辇事难寻。\",\"天近星河冷，龙归洞穴深。\",\"谷春攒锦绣，石润叠琼琳。\",\"玄鹤传仙拜，青猿伴客吟。\",\"泉通九江远，云出几州阴。\",\"冬有灵汤溢，夏无炎暑侵。\",\"他年如遂隐，五老是知音。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d89bc494f0945fc9','对雨寄朐山林番明府','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"竟日如丝不暂停，莎阶闲听滴秋声。\",\"斜飘虚阁琴书润，冷逼幽窗梦寐清。\",\"开户只添搜句味，看山还阻上楼情。\",\"遥知公退琴堂静，坐对萧骚饮兴生。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d8b5bb8b0f148a85','所思','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"解佩当时在洛滨，悠悠疑是梦中身。\",\"自从物外无消息，花谢莺啼近十春。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d8b5bb8b78080da5','所思','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"离思春来切，谁能慰寂寥。\",\"花飞寒食过，云重楚山遥。\",\"耿耿梦徒往，悠悠鬓易凋。\",\"那堪对明月，独立水边桥。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d8b5bb8b8ba97e56','所思','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"门掩残花寂寂，帘垂斜月悠悠。\",\"纵有一庭萱草，何曾与我忘忧。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d936383b9c3212bf','春晚过明氏闲居','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"寥寥陋巷独扃门，自乐清虚不厌贫。\",\"数局棋中消永日，一樽酒里送残春。\",\"雨催绿藓铺三径，风送飞花入四邻。\",\"羡尔朗吟无外事，沧洲何必去垂纶。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d93c5210b3bdb20e','海上太守新创东亭','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"使君心智杳难同，选胜开亭景莫穷。\",\"高敞轩窗迎海月，预栽花木待春风。\",\"静披典籍堪师古，醉拥笙歌不碍公。\",\"满径苔纹疏雨后，入檐山色夕阳中。\",\"偏宜下榻延徐孺，最称登门礼孔融。\",\"事简岂妨频赏玩，况当为政有馀功。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7d984dc95c0c836c9','和朐阳载笔鲁裕见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"燕台多事每开颜，相许论交淡薄间。\",\"饮兴共怜芳草岸，吟情同爱夕阳山。\",\"露浓小径蛩声咽，月冷空庭竹影闲。\",\"何事此时攀忆甚，与君俱是别乡关。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7dac7724c745fc3cd','秋日登润州城楼','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"虚楼一望极封疆，积雨晴来野景长。\",\"水接海门铺远色，稻连京口发秋香。\",\"鸣蝉历历空相续，归鸟翩翩自著行。\",\"吟罢倚栏深有思，清风留我到斜阳。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7db49282f1585de9c','寒江暮泊寄左偃','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"维舟芦荻岸，离恨若为宽。\",\"烟火人家远，汀洲暮雨寒。\",\"天涯孤梦去，篷底一灯残。\",\"不是凭骚雅，相思写亦难。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7dc79c1b2b93b1dee','寄刘钧秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"掩户当春昼，知君志在诗。\",\"闲花半落处，幽客未来时。\",\"野鸟穿莎径，江雪过竹篱。\",\"会须明月夜，与子水边期。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7dddb4979fa09d993','登毗陵青山楼','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"高楼闲上对晴空，豁目开襟半日中。\",\"千里吴山清不断，一边辽海浸无穷。\",\"人生歌笑开花雾，世界兴亡落叶风。\",\"吟罢倚栏何限意，回头城郭暮烟笼。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7de8a09e9b8adb503','春日招宋维先辈','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"瓮中竹叶今朝熟，鉴里桃花昨日开。\",\"为报广寒攀桂客，莫辞相访共衔杯。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e02629cf9e81404d','宿青溪米处士幽居','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"寄宿溪光里，夜凉高士家。\",\"养风窗外竹，叫月水中蛙。\",\"静虑同搜句，清神旋煮茶。\",\"唯忧晓鸡唱，尘里事如麻。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e23fd8b3336b23c5','鹤','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"九皋羽翼下晴空，万里心难驻玉笼。\",\"清露滴时翘藓径，白云开处唳松风。\",\"归当华表千年后，怨在瑶琴别操中。\",\"好共灵龟作俦侣，十洲三岛逐仙翁。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e23fd8b3c3400055','鹤','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"警露精神异，冲天羽翼新。\",\"千年一归日，谁识令威身。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e49b97088c3d77b7','桃花','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"只应红杏是知音，灼灼偏宜间竹阴。\",\"几树半开金谷晓，一溪齐绽武陵深。\",\"艳舒百叶时皆重，子熟千年事莫寻。\",\"谁步宋墙明月下，好香和影上衣襟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e6d7c1930e222eb6','江南重会友人感旧二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"江南重会面，聊话十年心。\",\"共立黄花畔，空惊素发侵。\",\"斜阳浮远水，归鸟下疏林。\",\"牵动诗魔处，凉风村落砧。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e6d7c193612136e8','江南重会友人感旧二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"长江落照天，物景似当年。\",\"忆昔携村酒，相将上钓船。\",\"狂歌红蓼岸，惊起白鸥眠。\",\"今日趋名急，临风一黯然。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7e8ae0201b9e8e81d','秋江夜泊寄刘钧正字','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"闲忆诗人思倍劳，维舟清夜泥风骚。\",\"鱼龙不动澄江远，云雾皆收皎月高。\",\"潮满钓舟迷浦屿，霜繁野树叫猿猱。\",\"此时吟苦君知否，双鬓从他有二毛。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ea11dc510eb5a70d','石棋局献时宰','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"得从岳叟诚堪重，却献皋夔事更宜。\",\"公退启枰书院静，日斜收子竹阴移。\",\"适情岂待樵柯烂，罢局还应屐齿隳。\",\"预想幽窗风雨夜，一灯闲照覆图时。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ea2e00e06e97ede6','新喻县酬王仲华少府见贻','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"事简开樽有逸情，共忻官舍月华清。\",\"每惭花欠河阳景，长愧琴无单父声。\",\"未泰黎元惭旷职，纵行谦直是虚名。\",\"与君尽力行公道，敢向昌朝俟陟明。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7eb3a214417d81567','江南春','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"千家事胜游，景物可忘忧。\",\"水国楼台晚，春郊烟雨收。\",\"鹧鸪啼竹树，杜若媚汀洲。\",\"永巷歌声远，王孙会莫愁。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7eb9022d272ba0a7e','舟中望九华山','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"排空苍翠异，辍棹看崔嵬。\",\"一面雨初歇，九峰云正开。\",\"当时思水石，便欲上楼台。\",\"隐去心难遂，吟馀首懒回。\",\"僧休传紫阁，屏歇写天台。\",\"中有忘机者，逍遥不可陪。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ed86e153fbe69c12','感秋书事','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"宦途憔悴雪生头，家计相牵未得休。\",\"红寥白蘋消息断，旧溪烟月负渔舟。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ef6f7a99c42b27cf','怀庐岳旧游寄刘钧因感鉴上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"昔年庐岳闲游日，乘兴因寻物外僧。\",\"寄宿爱听松叶雨，论诗惟对竹窗灯。\",\"各拘片禄寻分别，高谢浮名竟未能。\",\"一念支公安可见，影堂何处暮云凝。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f085341cbbff7ba3','送致仕沈彬郎中游茅山','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"挂却朝冠披鹤氅，羽人相伴恣遨游。\",\"忽因风月思茅岭，便挈琴樽上叶舟。\",\"野寺宿时魂梦冷，海门吟处水云秋。\",\"华阳洞府年光永，莫向仙乡拟驻留。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f163092f9dacdde7','献中书潘舍人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"运叶半千数，天钟许国臣。\",\"鹏霄开羽翼，凤阙演丝纶。\",\"顾问当清夜，从容向紫宸。\",\"立言成雅诰，正意叙彝伦。\",\"朴素偕前哲，馨香越搢绅。\",\"褒辞光万代，优旨重千钧。\",\"公退谁堪接，清闲道是邻。\",\"世间身属幻，物外意通津。\",\"吊往兼春梦，文高赋复新。\",\"琴弹三峡水，屏画十洲春。\",\"庭冷铺苔色，池寒浸月轮。\",\"竹风来枕簟，药气上衣巾。\",\"茶谱传溪叟，棋经受羽人。\",\"清虚虽得趣，献替不妨陈。\",\"杞梓呈才后，神仙入侍频。\",\"孤寒皆有赖，中外亦同忻。\",\"有士曾多难，无门得望尘。\",\"忙忙罹险阻，往往耗精神。\",\"寻果巢枝愿，终全负米身。\",\"遭逢敦孝治，蹇塞值通津。\",\"最庆清朝禄，还沾白发亲。\",\"甘柔心既遂，虚薄报何因。\",\"远宦联绵历，卑栖夙夜勤。\",\"良时空爱惜，末路每悲辛。\",\"骨立驹犹病，颜凋女尚贫。\",\"而今谐顾遇，尺蠖愿求伸。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f46ddc2f3baa73b0','投所知','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"孤琴尘翳剑慵磨，自顾泥蟠欲奈何。\",\"千里交亲消息断，一庭风雨梦魂多。\",\"题桥未展相如志，叩角谁怜宁戚歌。\",\"唯赖明公怜道在，敢携蓑笠钓烟波。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f5efc327d0538db2','寄庐山白大师','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"长忆寻师处，东林寓泊时。\",\"一秋同看月，无夜不论诗。\",\"泉美茶香异，堂深磬韵迟。\",\"鹿驯眠藓径，猿苦叫霜枝。\",\"别后音尘隔，年来鬓发衰。\",\"趋名方汲汲，未果再游期。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f600bfb19234d92d','下蔡春偶作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"旅馆飘飘类断蓬，悠悠心绪有谁同。\",\"一宵风雨花飞后，万里乡关梦自通。\",\"多难不堪容鬓改，沃愁惟怕酒杯空。\",\"采兰扇沈何时遂，洗虑焚香叩上穹。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f677b10baf85cfa3','夏云','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"如峰形状在西郊，未见从龙上泬寥。\",\"多谢好风吹起后，化为甘雨济田苗。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f819547a91597d53','芳草','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"二月正绵绵，离情被尔牵。\",\"四邻初过雨，万里正铺烟。\",\"眷恋残花惹，留连醉客眠。\",\"飘香是杜若，最忆楚江边。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f907207c8d763c2b','吹笛儿','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"陇头休听月明中，妙竹嘉音际会逢。\",\"见尔樽前吹一首，令人重忆许云封。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f95852e14512251f','晋陵罢任寓居依韵和陈锐秀才见寄','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"掩门三径莓苔绿，车马谁来陋巷间。\",\"卧弃琴书公干病，笑迎风月步兵闲。\",\"当秋每谢蛬清耳，渐老多惭酒借颜。\",\"济物未能伸一术，敢于明代爱青山。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7f9974314b1689f90','旅馆秋夕','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"寥寥山馆里，独坐酒初醒。\",\"旧业多年别，秋霖一夜听。\",\"砌蛬声渐息，窗烛影犹停。\",\"早晚无他事，休如泛水萍。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fa22392368886338','对酒招陈昭用','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"花开叶落堪悲，似水年光暗移。\",\"身世都如梦役，是非空使神疲。\",\"良图有分终在，所欲无劳妄思。\",\"幸有一壶清酒，且来闲语希夷。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fa4e527a9c7b2b25','叙吟二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"成僻成魔二雅中，每逢知己是亨通。\",\"言之无罪终难厌，欲把风骚继古风。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fa4e527a9df2d460','叙吟二首','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"往哲搜罗妙入神，隋珠和璧未为珍。\",\"而今所得惭难继，谬向平生著苦辛。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fbd69fd69417ef76','览友人卷','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"新诗开卷处，造化竭精英。\",\"雪霁楚山碧，月高湘水清。\",\"初吟尘虑息，再味古风生。\",\"自此寰区内，喧腾二雅名。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fcb6e311795bde62','泊秋浦','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"苇岸风高宿雁惊，维舟特地起乡情。\",\"渔儿隔水吹横笛，半夜空江月正明。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fcf605f7c6c4b518','海上和郎戬员外赴倅职','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"宋玉逢秋何起悲，新恩委寄好开眉。\",\"班升鸳鹭频经岁，任佐龚黄必暂时。\",\"乍对烟霞吟海峤，应思蘋蓼梦江湄。\",\"一朝凤诏仲徵入，鹏化那教尺鴳知。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fdafa384e6132cde','赠谦明上人','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"虽寄上都眠竹寺，逸情终忆白云端。\",\"闲登钟阜林泉晚，梦去沃洲风雨寒。\",\"新试茶经煎有兴，旧婴诗病舍终难。\",\"常闻秋夕多无寐，月在高台独凭栏。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fe3699878ff0d0f2','献张拾遗','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"官资清贵近丹墀，性格孤高世所稀。\",\"金殿日开亲凤扆，古屏时展看渔矶。\",\"酒醒虚阁秋帘卷，吟对疏篁夕鸟归。\",\"献替频陈忠誉播，鹏霄万里展雄飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fe7d7213089b2d82','舟次吉水逢蔡文庆秀才','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"别后音尘断，相逢又共吟。\",\"雪霜今日鬓，烟月旧时心。\",\"舣棹夕阳在，听鸿秋色深。\",\"一尊开口笑，不必话升沈。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7fea4fd33c909bc76','赠钟尊师游茅山','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"筇杖担琴背俗尘，路寻茅岭有谁群。\",\"仙翁物外应相遇，灵药壶中必许分。\",\"香入肌肤花洞酒，冷侵魂梦石床云。\",\"伊予亦有朝修志，异日遨游愿见君。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ff08b01cbccdd07a','春日途中作','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"干禄趋名者，迢迢别故林。\",\"春风短亭路，芳草异乡心。\",\"雨过江山出，莺啼村落深。\",\"未知将雅道，何处谢知音。\"]',NULL,NULL,NULL,NULL),('6ce235a8bb402bb7ffed8acb35451739','留题胡参卿秀才幽居','李中','6ce235a8bb402bb7d1457b72d1457b72','五代十国',NULL,'[\"竹荫亭除藓色浓，道心安逸寂寥中。\",\"扣门时有栖禅客，洒酒多招采药翁。\",\"江近好听菱芡雨，径香偏爱蕙兰风。\",\"我惭名宦犹拘束，脱屣心情未得同。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd2545948424401c5371844f','戏张道人不饮酒','颜荛','6ce235a8bd254594d1457b72d1457b72','五代十国',NULL,'[\"言自云山访我来，每闻奇秘觉叨陪。\",\"吾师不饮人间酒，应待流霞即举杯。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c920dcac13b567517c','谢杨尚书惠樱桃','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"满合虚红怕动摇，尚书知重赐樱桃。\",\"揉蓝尚带新鲜叶，泼血犹残旧折条。\",\"万颗真珠轻触破，一团甘露软含消。\",\"春来老病尤珍荷，并食中肠似火烧。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9349187236152d245','八月十六夜月','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"十六胜三五，中天照大荒。\",\"只讹些子缘，应号没多光。\",\"桂老犹全在，蟾深未煞忙。\",\"难期一年事，到晓泥诗章。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c937c541395a05394e','哭亡将诗','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"自是碙砂发，非干骇石伤。\",\"牒高身上职，盌大背边创。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c938d1776faaf7a529','哭李郢端公','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"军门半掩槐花宅，每过犹闻哭临声。\",\"北固暴亡兼在路，东都权葬未归茔。\",\"渐穷老仆慵看马，著惨佳人暗理筝。\",\"诗侣酒徒消散尽，一场春梦越王城。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c940e7a610cb8d7766','冬除夜书情','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"兀兀坐无味，思量谁与邻。\",\"数星深夜火，一个远乡人。\",\"雁翥天微雪，风号树欲春。\",\"愁章自难过，不觉苦吟频。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c97835d322ecf8ed2a','松寺','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"山寺取凉当夏夜，共僧蹲坐石阶前。\",\"两三条电欲为雨，七八个星犹在天。\",\"衣汗稍停床上扇，茶香时拨涧中泉。\",\"通宵听论莲华义，不藉松窗一觉眠。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c981762804462df5ac','赠僧','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"浮世浮华一断空，偶抛烦恼到莲宫。\",\"高僧解语牙无水，老鹤能飞骨有风。\",\"野色吟馀生竹外，山阴坐久入池中。\",\"禅师莫问求名苦，滋味过于食蓼虫。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c996534eba4aa0ed55','寒食日戏赠李侍御','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"十二街如市，红尘咽不开。\",\"洒蹄骢马汗，没处看花来。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9c4fccd8d639463fb','雪','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"瑞雪落纷华，随风一向斜。\",\"地平铺作月，天迥撒成花。\",\"客满烧烟舍，牛牵卖炭车。\",\"吾皇忧挟纩，犹自问君家。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9cae5d7e27389ece3','樊川寒食二首','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"鞍马和花总是尘，歌声处处有佳人。\",\"五陵年少粗于事，栲栳量金买断春。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9cae5d7e2deb4eb97','樊川寒食二首','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"寒食权豪尽出行，一川如画雨初晴。\",\"谁家络络游春盛，担入花间轧轧声。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9cc81bec8cada0587','苦吟','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"莫话诗中事，诗中难更无。\",\"吟安一个字，撚断数茎须。\",\"险觅天应闷，狂搜海亦枯。\",\"不同文赋易，为著者之乎。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9e86c0051ec9fb93f','逢友人赴阙','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"正当天下待雍熙，丹诏徵来早为迟。\",\"倚马才高犹爱艺，问牛心在肯容私。\",\"吏开黄阁排班处，民拥青门看入时。\",\"却笑郡人留不得，感恩唯拟立生祠。\"]',NULL,NULL,NULL,NULL),('6ce235a8bd68c4c9ef51874da6fb95d8','观新岁朝贺','卢延让','6ce235a8bd68c4c9d1457b72d1457b72','五代十国',NULL,'[\"龙墀初立仗，鸳鹭列班行。\",\"元日燕脂色，朝天桦烛香。\",\"表章堆玉案，缯帛满牙床。\",\"三百年如此，无因及我唐。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a91a4971f342e441b0','钱塘鹤','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"人间路霭青天半，鳌岫云生碧海涯。\",\"虽抱雕笼密扃钥，可能长在叔伦家。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a93390f53bfb23880a','秋日听僧弹琴','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"金徽玉轸韵泠然，言下浮生指下泉。\",\"恰称秋风西北起，一时吹入碧湘烟。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a9379039b19f843c14','春雪','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"雪霁凝光入坐寒，天明犹自卧袁安。\",\"貂裘穿后鹤氅敝，自此风流不足看。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a93ed549a4e9c939bb','南徐题友人郊居','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"门前樵径连江寺，岸下渔矶系海槎。\",\"待到秋深好时节，与君长醉隐侯家。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a971bcd8bc39c37ab7','读度人经寄郑仁表','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"身虽一旦尘中老，名拟三清会里题。\",\"二午九斋馀日在，请君相伴醉如泥。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a97b41b1b3b5a49ee4','金钱花','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"浅绛浓香几朵匀，日镕金铸万家新。\",\"堪疑刘宠遗芳在，不许山阴父老贫。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a980bc2a3c8cfb19c9','投谢钱武肃','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"东门上相好知音，数尽台前郭隗金。\",\"累重虽然容食椹，力微无计报焚林。\",\"弊貂不称芙蓉幕，衰朽仍惭玳瑁簪。\",\"十里溪光一山月，可堪从此负归心。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a980c5fea236bb83c9','衰柳','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"金风渐利露珠团，广陌长堤黛色残。\",\"水殿狂游隋炀帝，一千馀里可堪看。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a9a3d67d1a150bc3b4','凤仙花','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"香红嫩绿正开时，冷蝶饥蜂两不知。\",\"此际最宜何处看，朝阳初上碧梧枝。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a9b50777a18a88843e','客路','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"人寰急景如波委，客路浮云似盖轻。\",\"回首故山天外碧，十年无计却归耕。\"]',NULL,NULL,NULL,NULL),('6ce235a8c03381a9dc1a924dae3fa646','贾谊','吴仁璧','6ce235a8c03381a9d1457b72892c91e0','五代十国',NULL,'[\"扶持一疏满遗编，汉陛前头正少年。\",\"谁道恃才轻绛灌，却将惆怅吊湘川。\"]',NULL,NULL,NULL,NULL),('6ce235a8c3791cd1e9dfa322efdb025c','赠廖凝','韦鼎','6ce235a8c3791cd1d1457b72d1457b72','五代十国',NULL,'[\"君与白云邻，生涯久忍贫。\",\"姓名高雅道，寰海许何人。\",\"岳气秋来早，亭寒果落新。\",\"几回吟石畔，孤鹤自相亲。\"]',NULL,NULL,NULL,NULL),('6ce235a8c57fa3da8263ea229d15e345','书从事厅屏上','刘坦','6ce235a8c57fa3dad1457b72d1457b72','五代十国',NULL,'[\"金殿试回新折桂，将军留辟向江城。\",\"思量一醉犹难得，辜负扬州管记名。\"]',NULL,NULL,NULL,NULL),('6ce235a8c7bddbbda7e39c982e595280','孔庙口号','李花开','6ce235a8c7bddbbdd1457b72d1457b72','五代十国',NULL,'[\"破落三间屋，萧条一旅人。\",\"不知负何事，生死厄于陈。\"]',NULL,NULL,NULL,NULL),('6ce235a8c7f5271d75ead4e7704660be','宫中作','钱俶','6ce235a8c7f5271d0d0871f09908279e','五代十国',NULL,'[\"廊庑周遭翠幕遮，禁林深处绝喧哗。\",\"界开日影怜窗纸，穿破苔痕恶笋芽。\",\"西第晚宜供露茗，小池寒欲结冰花。\",\"谢公未是深沈量，犹把输赢局上夸。\"]',NULL,NULL,NULL,NULL),('6ce235a8c97192a753b3c2cb0e4a56f9','感怀诗二章','韩熙载','6ce235a8c97192a7b6a1085a89fcd07f','五代十国',NULL,'[\"未到故乡时，将为故乡好。及至亲得归，争如身不到。\",\"目前相识无一人，出入空伤我怀抱。风雨萧萧旅馆秋，归来窗下和衣倒。\",\"梦中忽到江南路，寻得花边旧居处。桃脸蛾眉笑出门，争向前头拥将去。\"]',NULL,NULL,NULL,NULL),('6ce235a8c97192a753b3c2cb1fe605e9','感怀诗二章','韩熙载','6ce235a8c97192a7b6a1085a89fcd07f','五代十国',NULL,'[\"仆本江北人，今作江南客。\",\"再去江北游，举目无相识。\",\"金风吹我寒，秋月为谁白。\",\"不如归去来，江南有人忆。\"]',NULL,NULL,NULL,NULL),('6ce235a8c97192a7633305debf88a2dc','书歌妓泥金带','韩熙载','6ce235a8c97192a7b6a1085a89fcd07f','五代十国',NULL,'[\"风柳摇摇无定枝，阳台云雨梦中归。\",\"他年蓬岛音尘断，留取尊前旧舞衣。\"]',NULL,NULL,NULL,NULL),('6ce235a8c97192a7660999744238f5bc','送徐铉流舒州','韩熙载','6ce235a8c97192a7b6a1085a89fcd07f','五代十国',NULL,'[\"昔年悽断此江湄，风满征帆泪满衣。\",\"今日重怜鹡鸰羽，不堪波上又分飞。\"]',NULL,NULL,NULL,NULL),('6ce235a8c97192a776b1e8911069ea5d','溧水无相寺赠僧','韩熙载','6ce235a8c97192a7b6a1085a89fcd07f','五代十国',NULL,'[\"无相景幽远，山屏四面开。\",\"凭师领鹤去，待我挂冠来。\",\"药为依时采，松宜绕舍栽。\",\"林泉自多兴，不是效刘雷。\"]',NULL,NULL,NULL,NULL),('6ce235a8c9e18a9c1040e2ad3f92087f','示门生马侍郎胤孙','裴皞','6ce235a8c9e18a9cd1457b72d1457b72','五代十国',NULL,'[\"宦途最重是文衡，天与愚夫著盛名。\",\"三主礼闱年八十，门生门下见门生。\"]',NULL,NULL,NULL,NULL),('6ce235a8cb2a529dbe723c6f54a28a46','书壁','黄损','6ce235a8cb2a529dd1457b72d1457b72','五代十国',NULL,'[\"一别人间岁月多，归来人事已销磨。\",\"惟有门前鉴池水，春风不改旧时波。\"]',NULL,NULL,NULL,NULL),('6ce235a8cb2a529de807865d13589061','公子行','黄损','6ce235a8cb2a529dd1457b72d1457b72','五代十国',NULL,'[\"春草绿绵绵，骄骖骤暖烟。\",\"微风飘乐韵，半日醉花边。\",\"打鹊抛金盏，招人举玉鞭。\",\"田翁与蚕妇，平地看神仙。\"]',NULL,NULL,NULL,NULL),('6ce235a8cb2a529debffee25699b09d9','读史','黄损','6ce235a8cb2a529dd1457b72d1457b72','五代十国',NULL,'[\"逐鹿走红尘，炎炎火德新。\",\"家肥生孝子，国霸有馀臣。\",\"帝道云龙合，民心草木春。\",\"须知烟阁上，一半老儒真。\"]',NULL,NULL,NULL,NULL),('6ce235a8cb2a529df7b7ace64dceb3ef','出山吟','黄损','6ce235a8cb2a529dd1457b72d1457b72','五代十国',NULL,'[\"来书初出白云扃，乍蹑秋风马走轻。\",\"远近留连分岳色，别离呜咽乱泉声。\",\"休将巢许争喧杂，自共伊皋论太平。\",\"昨夜细看云色里，进贤星座甚分明。\"]',NULL,NULL,NULL,NULL),('6ce235a8cbd9cbdd161e45f027801a32','松','廖匡图','6ce235a8cbd9cbddd1457b72d1457b72','五代十国',NULL,'[\"曾于西晋封中散，又向东吴作大夫。\",\"浓翠自知千古在，清声谁道四时无。\",\"枝柯偃后龙蛇老，根脚盘来爪距粗。\",\"直待素秋摇落日，始将凡木斗荣枯。\"]',NULL,NULL,NULL,NULL),('6ce235a8cbd9cbdd4c03e7e8ef807770','九日陪董内召登高','廖匡图','6ce235a8cbd9cbddd1457b72d1457b72','五代十国',NULL,'[\"祝融峰下逢嘉节，相对那能不怆神。\",\"烟里共寻幽涧菊，樽前俱是异乡人。\",\"遥山带日应连越，孤雁来时想别秦。\",\"自古登高尽惆怅，茱萸休笑泪盈巾。\"]',NULL,NULL,NULL,NULL),('6ce235a8cbd9cbdd88edaea52808e511','和人赠沈彬','廖匡图','6ce235a8cbd9cbddd1457b72d1457b72','五代十国',NULL,'[\"冥鸿迹在烟霞上，燕雀休夸大厦巢。\",\"名利最为浮世重，古今能有几人抛。\",\"逼真但使心无著，混俗何妨手强抄。\",\"深喜卜居连岳色，水边竹下得论交。\"]',NULL,NULL,NULL,NULL),('6ce235a8cbd9cbddf06065b6eaf29e4a','赠泉陵上人','廖匡图','6ce235a8cbd9cbddd1457b72d1457b72','五代十国',NULL,'[\"暂把枯藤倚碧根，禅堂初创楚江濆。\",\"直疑松小难留鹤，未信山低住得云。\",\"草接寺桥牛笛近，日衔村树鸟行分。\",\"每来共忆曾游处，万壑泉声绝顶闻。\"]',NULL,NULL,NULL,NULL),('6ce235a8ccadd2da7cde088946e1bb49','景阳台怀古','陈贶','6ce235a8ccadd2dad1457b72d1457b72','五代十国',NULL,'[\"景阳六朝地，运极自依依。\",\"一会皆同是，到头谁论非。\",\"酒浓沈远虑，花好失前机。\",\"见此尤宜戒，正当家国肥。\"]',NULL,NULL,NULL,NULL),('6ce235a8cdb2c6d303de69ecffbcce83','从幸秦川过白卫献诗','韩昭','6ce235a8cdb2c6d3d1457b727fa732b5','五代十国',NULL,'[\"吾王巡狩为安边，此去秦亭尚数千。\",\"夜照路岐山店火，晓通消息戍瓶烟。\",\"为云巫峡虽神女，跨凤秦楼是谪仙。\",\"八骏似龙人似虎，何愁飞过大漫天。\"]',NULL,NULL,NULL,NULL),('6ce235a8cdb2c6d3912773e5e2ff1e50','和题剑门','韩昭','6ce235a8cdb2c6d3d1457b727fa732b5','五代十国',NULL,'[\"闭关防老寇，孰敢振威棱。\",\"险固疑天设，山河自古凭。\",\"三川奚所赖，双剑最堪矜。\",\"鸟道微通处，烟霞锁百层。\"]',NULL,NULL,NULL,NULL),('6ce235a8cf24927be0722252f703b999','游西湖','沈韬文','6ce235a8cf24927bd1457b72d1457b72','五代十国',NULL,'[\"□□□□□□□，菰米蘋花似故乡。\",\"不是不归归未得，好风明月一思量。\"]',NULL,NULL,NULL,NULL),('6ce235a8d3b5e65c143c329cf2e2c3d8','古镜','若虚','6ce235a8d3b5e65cd1457b72d1457b72','五代十国',NULL,'[\"轩后红炉独铸成，藓痕磨落月轮呈。\",\"万般物象皆能鉴，一个人心不可明。\",\"匣内乍开鸾凤活，台前高挂鬼神惊。\",\"百年肝胆堪将比，只怕看频素发生。\"]',NULL,NULL,NULL,NULL),('6ce235a8d3b5e65c6fae10f90ceaf681','乐仙','若虚','6ce235a8d3b5e65cd1457b72d1457b72','五代十国',NULL,'[\"乐氏骑龙上碧天，东吴遗宅尚依然。\",\"悟来大道无多事，真后丹元不值钱。\",\"老树夜风虫咬叶，古垣春雨藓生砖。\",\"松倾鹤死桑田变，华表归乡未有年。\"]',NULL,NULL,NULL,NULL),('6ce235a8d3b5e65cfc46852ca9813083','怀庐山旧隐','若虚','6ce235a8d3b5e65cd1457b72d1457b72','五代十国',NULL,'[\"九叠嵯峨倚着天，悔随寒瀑下岩烟。\",\"深秋猿鸟来心上，夜静松杉到眼前。\",\"书架想遭苔藓裹，石窗应被薜萝缠。\",\"一枝筇竹游江北，不见炉峰二十年。\"]',NULL,NULL,NULL,NULL),('6ce235a8d4645267340abba0c1e14cf8','重门曲','周濆','6ce235a8d4645267d1457b72d1457b72','五代十国',NULL,'[\"憔悴容华怯对春，寂寥宫殿锁闲门。\",\"此身却羡宫中树，不失芳时雨露恩。\"]',NULL,NULL,NULL,NULL),('6ce235a8d46452678018193837d56e6f','逢邻女','周濆','6ce235a8d4645267d1457b72d1457b72','五代十国',NULL,'[\"日高邻女笑相逢，慢束罗裙半露胸。\",\"莫向秋池照绿水，参差羞杀白芙蓉。\"]',NULL,NULL,NULL,NULL),('6ce235a8d4645267a02b4775b20789f2','山下水','周濆','6ce235a8d4645267d1457b72d1457b72','五代十国',NULL,'[\"背云冲石出深山，浅碧泠泠一带寒。\",\"不独有声流出此，会归沧海助波澜。\"]',NULL,NULL,NULL,NULL),('6ce235a8d4645267fa2fbcb61adfd9b0','废宅','周濆','6ce235a8d4645267d1457b72d1457b72','五代十国',NULL,'[\"牢落画堂空锁尘，荒凉庭树暗消春。\",\"豪家莫笑此中事，曾见此中人笑人。\"]',NULL,NULL,NULL,NULL),('6ce235a8d813ebecf99f5b2bd99ee0ad','大言诗','谭峭','6ce235a8d813ebecd1457b72d1457b72','五代十国',NULL,'[\"线作长江扇作天，靸鞋抛向海东边。\",\"蓬莱信道无多路，只在谭生拄杖前。\"]',NULL,NULL,NULL,NULL),('6ce235a8d9f2467f8f239f6da4254801','游后湖赏莲花','李璟','6ce235a8d9f2467f23ce1851d707329b','五代十国',NULL,'[\"蓼花蘸水火不灭，水鸟惊鱼银梭投。\",\"满目荷花千万顷，红碧相杂敷清流。\",\"孙武已斩吴宫女，琉璃池上佳人头。\"]',NULL,NULL,NULL,'[\"银梭：银质梭子。常用以比喻鱼。\",\"满目：充满视野。\",\"清流：清澈的流水。\"]'),('6ce235a8d9f2467faae4b3452e770cea','保大五年元日大雪同太弟景遂汪王景逖齐王景逵进士李建勋中书徐铉勤政殿学士张义方登楼赋','李璟','6ce235a8d9f2467f23ce1851d707329b','五代十国',NULL,'[\"珠帘高卷莫轻遮，往往相逢隔岁华。\",\"春气昨宵飘律管，东风今日放梅花。\",\"素姿好把芳姿掩，落势还同舞势斜。\",\"坐有宾朋尊有酒，可怜清味属侬家。\"]','[\"《江表志》：保大五年元日，天忽大雪。上诏太弟以下登楼展宴，咸命赋诗，令中使就私第赐李建勋。建勋方会中书舍人徐铉、勤政殿学士张义方于溪亭，即时和进。元宗乃召建勋、铉、义方同入，夜艾方散。侍臣皆有兴咏，徐铉为前后序，大弟合为一图，集名公图绘，曲尽一时之妙。\"]',NULL,NULL,NULL),('6ce235a8db567276e2b23371a6502a8f','奉和九月九日登慈恩寺浮图应制','王景','6ce235a8db567276078719151ce927f8','五代十国',NULL,'[\"玉辇移中禁，珠梯览四禅。\",\"重阶清汉接，飞窦紫霄悬。\",\"缀叶披天藻，吹花散御筵。\",\"无因銮跸暇，俱舞鹤林前。\"]',NULL,NULL,NULL,NULL),('6ce235a8dc65d0235279340a3523c20c','又咏蜀都城上芙蓉花','张立','6ce235a8dc65d023d1457b72d1457b72','五代十国',NULL,'[\"去年今日到城都，城上芙蓉锦绣舒。\",\"今日重来旧游处，此花憔悴不如初。\"]',NULL,NULL,NULL,NULL),('6ce235a8dc65d0235833dc6e619977f7','咏蜀都城上芙蓉花','张立','6ce235a8dc65d023d1457b72d1457b72','五代十国',NULL,'[\"四十里城花发时，锦囊高下照坤维。\",\"虽妆蜀国三秋色，难入豳风七月诗。\"]',NULL,NULL,NULL,NULL),('6ce235a8e0ea4aae8edd6281b3731fda','送贺秘监归会稽诗','于休烈','6ce235a8e0ea4aaedbe272bae57c6b95','五代十国',NULL,'[\"飞名紫府内，抗手白云乡。\",\"道与松乔匹，荣辞园绮行。\",\"夫君既鹤驾，幼子复霓裳。\",\"少别留宸藻，东南归路光。\"]',NULL,NULL,NULL,NULL),('6ce235a8e148fd7cf8434e0f3c24f586','题龙窝洞','张仁溥','6ce235a8e148fd7cd1457b72d1457b72','五代十国',NULL,'[\"折花携酒看龙窝，镂玉长旌俊彦过。\",\"他日各为云外客，碧纱笼却又如何。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c02909a28ecbfa89b','送邓王二十弟从益牧宣城','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"且维轻舸更迟迟，别酒重倾惜解携。\",\"浩浪侵愁光荡漾，乱山凝恨色高低。\",\"君驰桧楫情何极，我凭阑干日向西。\",\"咫尺烟江几多地，不须怀抱重凄凄。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c0941ac86be176f91','病起题山舍壁','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"山舍初成病乍轻，杖藜巾褐称闲情。\",\"炉开小火深回暖，沟引新流几曲声。\",\"暂约彭涓安朽质，终期宗远问无生。\",\"谁能役役尘中累，贪合鱼龙搆强名。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c20f77f892f236f5d','悼诗','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"永念难消释，孤怀痛自嗟。\",\"雨深秋寂莫，愁引病增加。\",\"咽绝风前思，昏蒙眼上花。\",\"空王应念我，穷子正迷家。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c3baf5b90194f503b','亡后见形诗','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国','后主对“钟山”这个词很有爱，自号钟隐、钟山隐者。“钟山”对后主来说，意味着出世、隐居、清逸。因此联系全文，“钟山”一次并非实指而是虚指。无乃见钟山并非说见不到钟山，而是说无法继续目前这种类似隐居的闲逸的状态。这首诗的中心思想应该是，有一件事他不想去做。不想做的理由是因为环境恶劣，无法像现在这样清逸闲散。\n他说“不想去”，意味着有选择的余地。又说不想去的理由是因为“不能这么安逸”，意味着他目前的生活状态是相当滋润的。如果是亡国前后期的作品，是否去汴京，是他可以选择的吗？他当前的生活状态，能用“安逸”来描述吗？显然不能。所以这不是亡国前后的作品。\n将这首诗和后主的生平相联系，可以作以下猜测：所谓异国，并非指某个国家，而是指自己灭亡的唐朝。所谓惊涛，并非是说渡江的情景，而是自己现状。所谓钟山，并非是说某座山，而是说隐居的生活。而在后主心目中，隐居不代表清苦，而代表清逸。\n他现在正过着十分安逸的生活，很舒服满足，不想放弃这种生活去过另外一种类似于“惊涛骇浪”般的，有别于现在的生活。','[\"异国非所志，烦劳殊清闲。\",\"惊涛千万里，无乃见钟山。\"]',NULL,'留在别国不是我的意愿，他国的烦劳远多余清闲。\n在千万里的惊涛骇浪中，没想到竟然还能见到钟山。',NULL,'[\"无：竟然；也可不翻译。异国：这里指自己覆灭的国家。\",\"殊：不同或超过。\"]'),('6ce235a8e188a71c4348f1cceb76874d','书琵琶背','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"侁自肩如削，难胜数缕绦。\",\"天香留凤尾，馀暖在檀槽。\"]',NULL,NULL,'《书琵琶背》是南唐后主李煜的诗作，这首诗是词人悼念亡妻昭惠周后而作的。从诗中可以看出词人见物思人，回忆起昭惠皇后生前弹琵琶的样子，表达对亡妻的无限思念之情。','[\"琵琶：弹拨乐器。初名批把，见《释名·释乐器》。此类乐器原流行于波斯、阿拉伯等地，汉代传入中国。后经改造，圆体修颈，有四弦、十二柱，俗称\\u201C秦汉子\\u201D。一说，中国秦末，百姓苦长城之役，弦鼗而鼓之，琵琶即始于此。（见晋·傅玄《<琵琶赋）序》）南北朝时又有曲项琵琶传入中国，四弦，腹呈半梨形，颈上有四柱，横抱怀中，用拨子弹奏，即现今琵琶的前身。唐宋以来经不断改进，柱位逐渐增多，改横抱为竖抱，废拨子，改用手指弹奏。现今民间的琵琶有十七柱，通常称四相十三品，革新的琵琶有六相十八品；后者能弹奏所有半音，技法丰富，成为重要的民族独奏乐器。\",\"侁：行貌。\",\"肩如削：语出曹植《洛神赋》：\\u201C肩若削成，腰如约素。\\u201D\",\"胜：禁得起。\",\"绦：用丝编织的带子。\",\"天香：芳香的美称。\",\"凤尾：指琵琶上端安放弦柱的部位，其形如凤尾。\",\"檀槽：琵琶上架弦的格子，以檀木做成。后也用以代指琵琶。\"]'),('6ce235a8e188a71c43e5a75d43d4fa83','书灵筵手巾','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国','“浮生共憔悴，壮岁失婵娟。”诗的起句总叹人生的虚浮无定，难以把握，无论愿意不愿意，生命还得继续，所以活着就已经是一种无奈。而这无奈的人生本来就已经令人悲哀，何况正在壮岁之年，又失去了相依相倚的妻子，这余下的人生途程将会是多么的漫长，多么的难行。诗中以“婵娟”代指妻子，以见妻子的青春美好，也就愈见痛失爱妻的深情。然后笔调一转，拈出手巾点题。手巾是古代女子的随身之物，攥在手中，用以拭汗，用以掩笑，用以擦去泪水，用以生风借凉，不可须臾而离。诗的首二句抒写悲痛，就人生落笔，有一种从根上说起的本质意味，起点既高，落笔滔滔。\n“汗手遗香渍，痕眉染黛烟。”人已离去，而手巾上犹可嗅到生前所用香膏的气味，画眉的黛烟也在手巾上留下了点点斑痕，睹物思人，自有无尽思念在其中。而“汗手遗香渍”一句，提示周氏生前将巾紧紧攥在手中的动作，以至于有手汗的浸渍而染香于巾。若不是相知极深之人，不会有这样细致的观察，而回忆之时，自然倍加痛心。诗言物不言人，所言之物又只是一条亡者生前的旧手巾，“汗手”、“痕眉”点到为止，便草草结束。\n整体上就诗章结构而言，有一种头重脚轻之感。鲁迅曾评说向秀悼念亡友嵇康的《思旧赋》说：“很奇怪他为什么只有寥寥的几行，刚开头却又煞了尾。”可见这也是一种抒写情感的方法。不过，向秀之“寥寥的数行”是有话不能说，李煜这里却是满腹的言语无法说出，悲痛至于极点的缘故。\n此诗是李煜在亡妻灵座前的随笔之作，写的都是眼前景、眼前物，因此也就信手拈来，十分自然，没有雕琢的痕迹。灵室气氛本来就让人十分伤心，正如晋代潘岳《寡妇赋》所写：“入空室兮望灵座，帷飘飘兮灯荧荧”，而亡妻的遗物更增添了一层忧伤。由具体遗物汗巾写起，从小处着笔，抒写壮岁失妻的痛苦，是此诗的一大特色。','[\"浮生共憔悴，壮岁失婵娟。\",\"汗手遗香渍，痕眉染黛烟。\"]','[\"王士祯《五代诗话》：触物寓意类如此。\"]','这一生与妻子共同分担着国难的痛苦以至于让人憔悴，正值壮年却又遇上丧妻之痛。\n手巾上犹可嗅到妻子生前所用香膏的气味，画眉的黛烟也在手巾上留下了点点斑痕。','《书灵筵手巾》是南唐后主李煜的五言绝句作品，怀念亡后，睹物思人，抒发哀感，诗里述说了自己壮年失妻的痛苦，描写了对亡妻遗物的无限爱惜之情。虽只有短短四句，但却内含巨大的悲恸和哀伤。','[\"灵筵：供亡灵的几筵。人死后，生者为祭奠死者而设立的几案，用以供奉灵位、衣物与酒食。《梁书·止足传·顾宪之》：\\u201C不须常施灵筵，可止设香灯，使致哀者有凭耳。\\u201D北齐颜之推《颜氏家训·终制》：\\u201C灵筵勿设枕几，朔望祥裨唯下白粥清水干枣，不得有酒肉饼果之祭。\\u201D王利器《颜氏家训·集解》：\\u201C灵筵，供亡灵之几筵，后人又谓之灵床，或日仪床。\\u201D\",\"手巾：即毛巾，又称拭手巾、净巾，是擦拭脸手皮肤的日常用品。在中国，手巾自古即被使用，后禅林备之于僧堂、浴室、后架等供大众使用。《毗尼母经》卷八提出净体巾、净面巾、净眼巾之别。《大比丘三千威仪》卷下说明手巾的用处：\\u201C当用手巾有五事：一者当拭上下头；二者当用一头拭手，以一头拭面止；三者不得持拭鼻；四者以用拭腻污当即浣之；五者不得拭身体，若澡浴各当自有巾。若著僧伽梨时，持手巾有五事：一者不得使巾头垂见，二者不得持白巾，三者当败色令黑，四者不得拭面，五者饭当用覆膝上，饭已当下去。\\u201D此处当是大周后净面巾。因为李煜和大周后都笃信佛教，此处\\u201C手巾\\u201D当是佛教仪礼上的\\u201C手巾\\u201D。\",\"浮生：指人生在世，虚浮不定，故称为\\u201C浮生\\u201D。语出《庄子·刻意》：\\u201C其生若浮，其死若休。\\u201D\",\"憔悴：忧戚，烦恼。\",\"壮岁：这里指李煜丧妻时的二十八岁。\",\"婵娟：泛指形态美好的女子。这里指大周后。\",\"黛烟：青黑色的颜料，古时女子用以画眉。\"]'),('6ce235a8e188a71c4c4fb32e10befbe5','梅花','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"失却烟花主，东君自不知。\",\"清香更何用，犹发去年枝。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c4c4fb32e8052175b','梅花','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"殷勤移植地，曲槛小阑边。\",\"共约重芳日，还忧不盛妍。\",\"阻风开步障，乘月溉寒泉。\",\"谁料花前后，蛾眉却不全。\"]',NULL,NULL,'此诗题为《梅花》，却不是一首咏物诗，而是咏叹与梅花相关的人和事。据《全唐诗》载：“后主尝与周后移植梅花于瑶光殿之西，及花时，而后已殂，因成诗见意。”\n此诗首联起句“殷勤移植”语，即指这次移植梅花之事；“移植地”便是“瑶光殿之西”的“曲槛小栏边”。李煜、周后（即昭惠皇后）都是极富雅趣之人，又凭着帝王皇后的特殊条件，便为自己的生活极力营造出优美的氛围。他们以销金红罗罩壁，以绿钿刷丝隔眼，糊以红罗，种梅花其外，兴之所至，便有了移植梅花之事。颔联写道“共约重芳日，还忧不盛妍”，意思是记得当时还曾担心，梅花“重芳日”，只恐“不盛妍”。唯其如此，颈联便接着说：“阻风开步障，乘月溉寒泉。”为了给梅花“阻风”，这两位形影相随的伴侣还特意为梅花牵开了漂漂亮亮、长长宽宽的“步障”；为了给梅花浇水，也还曾不辞“乘月”披星之劳。实指望来年能观赏到夫妻共同移植、一块浇灌的梅花的艳美风姿。可是，又有谁能料到花开前后，这正该供夫妻共赏同乐的美景良辰，而“蛾眉却不前”。尾联的这一慨叹，紧承在语流上逐层推进的前三联而发，于升至极高处的波峰浪尖，忽发哀音，跌入深潭，凄恻动人，给读者心灵以强烈冲击。','[\"殷勤：情意恳切深厚，亦指恳切深厚的情意。司马迁《报任少卿书》：\\u201C未尝衔杯酒，接殷勤之 欢。\\u201D《史记· 司马相如列传》：\\u201C相如乃使人重赐文君侍者，通殷勤。\\u201D\",\" 槛（jiàn）：窗户下或长廊旁的栏杆，也指井栏。\",\"重（chóng）：再次。\",\"妍（yán）：美，如\\u201C争妍斗艳\\u201D。李白《于阗采花》诗：\\u201C丹青能令丑者妍。\\u201D\",\"步障：用以遮蔽风尘或视线的一种屏幕。《晋书·石崇传》：\\u201C崇与贵戚王恺、羊琇之徒，以奢靡相尚；恺作紫丝布步障四十里，崇作锦步障五十里以敌之。\\u201D\",\"溉（gài）：灌、浇水。\",\"花：开花。名词活用为动词。\",\"蛾眉：女子长而美的眉毛。常作美女代称。这里借指周后。\"]'),('6ce235a8e188a71c5550e5061796dca7','感怀','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"层城无复见娇姿，佳节缠哀不自持。\",\"空有当年旧烟月，芙蓉城上哭蛾眉。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c5550e50697cdca51','感怀','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"又见桐花发旧枝，一楼烟雨暮凄凄。\",\"凭阑惆怅人谁会，不觉潸然泪眼低。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c591e3e72666614a1','咏扇','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"揖让月在手，动摇风满怀。\"]',NULL,NULL,'南唐后主李煜《咏扇》的这两句充满了文人小情调：揖让答礼的时候，团扇在手，如挥动一轮明月；扇子摇动，清风满怀，心旷神怡。两句诗把团扇写活了，尤其前一句形象生动，用语明净、精炼、准确、传神，并反映了作者潇洒自如的神态。\n据叶梦得《石林燕语》记载：江南李煜既降，太祖尝因曲燕问：“闻卿在国中好作诗。”因使举其得意一联，煜沉吟久之，诵其《咏扇》云：“揖让月在手，动摇风满怀。”上曰：“满怀之风，却有多少？”他日复燕煜，顾近臣曰：“好一个翰林学士！”从中可以看见虽然文人把扇、红袖添香，是古代雅士文人、商贾骚人所追求的一种浪漫的生活方式。但李煜作为一代君王，这就未免显得小家子气。难怪宋太祖赵匡胤对这位昔日的同行很是不屑：“好一个翰林学士！”这简直就带有对李煜的轻蔑之意了。',NULL),('6ce235a8e188a71c618dbccaae6e74e0','渡中江望石城泣下','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"江南江北旧家乡，三十年来梦一场。\",\"吴苑宫闱今冷落，广陵台殿已荒凉。\",\"云笼远岫愁千片，雨打归舟泪万行。\",\"兄弟四人三百口，不堪闲坐细思量。\"]',NULL,NULL,'《渡中江望石城泣下》是李煜作了俘虏北上渡江时所作的一首七律，抒发了亡国的悲痛心情。山重水尽、绝望无依的兄弟四人，都已不堪愁苦闲坐，再仔细思量，更是令人窒息的绝望。直到国破家亡，李煜才从梦中醒来，才体味到了做俘虏任人宰割的痛苦。梦醒了，国亡了，思量追悔也无济于事了。与北宋誓死抵抗未必能取胜，但总比坐以待毙要强得多。李煜尚拥有南唐半壁江山，如能发愤图强，还不知是谁做一统帝王。可恨的是他没有这种雄心壮志，只能坐在飘舟里“细思量”，去为自己家族的处境哀愁了。这是作者真实心情的流露和抒发，不粉饰，不矫造，有很强的感染力。','[\"吴苑：宫阙名\",\"岫：峰峦\"]'),('6ce235a8e188a71c6540fde05a482162','题金楼子后','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"牙签万轴裹红绡，王粲书同付火烧。\",\"不于祖龙留面目，遗篇那得到今朝。\"]',NULL,NULL,'此诗载于《全唐诗》卷八，是李煜读《金楼子》后题写其书后的一首诗。《金楼子》是以梁元帝萧绎为中心的文人集团作品，大多数文章是由萧绎本人撰写。作品是采用札记、随感的形式，或前引名言成句，后加自己的看法；或借题发挥以阐发自己的思想；或记述史实以劝诫子女；或追叙往事，聊以自慰；或转志奇事，欲广闻见；或记东交游，以叙友情。李煜此诗主旨是对《金楼子》中所说的两件焚书事件发表感慨。',NULL),('6ce235a8e188a71c8d42884dd5ad2454','病中书事','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"病身坚固道情深，宴坐清香思自任。\",\"月照静居唯捣药，门扃幽院只来禽。\",\"庸医懒听词何取，小婢将行力未禁。\",\"赖问空门知气味，不然烦恼万涂侵。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71c9daed43d756b5e40','赐宫人庆奴','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"风情渐老见春羞，到处消魂感旧游。\",\"多谢长条似相识，强垂烟态拂人头。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71ca369533d23398bd0','輓辞二首','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"【其一】\",\"珠碎眼前珍，花凋世外春。\",\"未销心里恨，又失掌中身。\",\"玉笥犹残药，香奁已染尘。\",\"前哀将后感，无泪可沾巾。\",\"【其二】\",\"艳质同芳树，浮危道略同。\",\"正悲春落实，又苦雨伤丛。\",\"秾丽今何在？飘零事已空。\",\"沈沈无问处，千载谢东风。\"]',NULL,NULL,NULL,'[\"题注：宣城公仲宣，後主子，小字瑞保，年四岁卒。母昭惠先病，哀苦增剧，遂至於殂。故後主挽辞，并其母子悼之。\"]'),('6ce235a8e188a71caf3d675c90e6553a','九月十日偶书','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"晚雨秋阴酒乍醒，感时心绪杳难平。\",\"黄花冷落不成艳，红叶飕飗竞鼓声。\",\"背世返能厌俗态，偶缘犹未忘多情。\",\"自从双鬓斑斑白，不学安仁却自惊。\"]',NULL,NULL,NULL,NULL),('6ce235a8e188a71cb8b4b82a2687f630','秋莺','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"残莺何事不知秋，横过幽林尚独游。\",\"老舌百般倾耳听，深黄一点入烟流。\",\"栖迟背世同悲鲁，浏亮如笙碎在缑。\",\"莫更留连好归去，露华凄冷蓼花愁。\"]',NULL,NULL,'《秋莺》是五代十国时南唐国君李煜的代表作之一。此诗纯用“赋体”，不事雕琢，以简驭繁，以明白晓畅的诗句表现了囚徒生活的苦楚，让人产生无限的同情悲悯，教人神思黯然。因之，本诗是咏物诗中的佳作。',NULL),('6ce235a8e188a71cdba11069fd6ab805','病中感怀','李煜','6ce235a8e188a71cb7892fb32ab56412','五代十国',NULL,'[\"憔悴年来甚，萧条益自伤。\",\"风威侵病骨，雨气咽愁肠。\",\"夜鼎唯煎药，朝髭半染霜。\",\"前缘竟何似，谁与问空王。\"]',NULL,NULL,NULL,NULL),('6ce235a8e22ad4a67f45c210d89b50f1','题鹰猎兔画','和凝','6ce235a8e22ad4a6a64c94baef4e3b77','五代十国',NULL,'[\"虽是丹青物，沉吟亦可伤。\",\"君夸鹰眼疾，我悯兔心忙。\",\"岂动骚人兴，惟增猎客狂。\",\"鲛绡百馀尺，争及制衣裳。\"]',NULL,NULL,NULL,NULL),('6ce235a8e22ad4a6a86f601eec0ec4f9','洋川','和凝','6ce235a8e22ad4a6a64c94baef4e3b77','五代十国',NULL,'[\"华夷图上见洋川，知在青山绿水边。\",\"官闲最好游僧舍，江近应须买钓船。\"]',NULL,NULL,NULL,NULL),('6ce235a8e22ad4a6bb567fb121bf2b8a','醴泉院','和凝','6ce235a8e22ad4a6a64c94baef4e3b77','五代十国',NULL,'[\"万山岚霭簇洋城，数处禅斋尽有名。\",\"古柏八株堆翠色，灵泉一派逗寒声。\",\"暂游颇爱闲滋味，久住翻嫌俗性情。\",\"珍重支公每相勉，我于儒行也修行。\"]',NULL,NULL,NULL,NULL),('6c